PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE [new] (
  [id] INTEGER PRIMARY KEY ON CONFLICT ABORT AUTOINCREMENT, 
  [matn] TEXT);
CREATE TABLE [category] (
  [matn] TEXT, 
  [id] INTEGER PRIMARY KEY ON CONFLICT ABORT AUTOINCREMENT);
CREATE TABLE IF NOT EXISTS "mysms" (
  [id] AUTOINC, 
  [matn] TEXT, 
  [cat_id] INTEGER, 
  [deleted] INT NOT NULL DEFAULT 0, 
  [sended] INT NOT NULL DEFAULT 0, 
  [shared] INT NOT NULL DEFAULT 0, 
  [ready] INT NOT NULL DEFAULT 0, 
  [liked] INT NOT NULL DEFAULT 0, 
  CONSTRAINT [sqlite_autoindex_New Text Document (5)_1] PRIMARY KEY ([id]));
CREATE TABLE [bio] (
  [id] INTEGER NOT NULL ON CONFLICT REPLACE PRIMARY KEY AUTOINCREMENT, 
  [bio] TEXT DEFAULT no, 
  [liked] INTEGER NOT NULL DEFAULT 0);
DELETE FROM sqlite_sequence;
COMMIT;
