INSERT INTO mysms VALUES(1,'همانطور که مایل نیستم بنده کسی باشم، حاضر نیستم آقای کسی باشم. کسانی که مخالف آزادی دیگرانند، خود لیاقت آزادی را ندارند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(2,'ازدواج نه بهشت است و نه جهنم، [ بلکه ] تنها یک برزخ است.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(3,'قدرت شگفت انگیز نیروهای خلاق و استعدادهای نهفته آدمی هنگامی آشکار می شود که نیازی حیاتی، تواناییهای او را به فعالیت وادارد و کشش آرزویی، سراپای وجودش را به جنب و جوش درآورد.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(4,'مردم به همان اندازه خوشبخت هستند که می‌خواهند باشند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(5,'شما می‌توانید هرچه را که می‌خواهید، داشته باشید؛ به شرطی که آن را به شدت بخواهید.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(6,'شما می‌توانید هرچه که می‌خواهید، بشوید، هر کامیابی که می‌خواهید، به دست آورید؛ به شرطی که با اراده‌ای استوار، از آن دست برندارید.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(7,'کسانی که دست به کار نمی‌شوند، به خواسته‌های خود می‌رسند، ولی فقط به باقی‌مانده‌ی خواسته‌های افرادی می‌رسند که پیش از آن‌ها شتاب کردند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(8,'همه ی مردم از تعارف خوششان می آید.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(9,'اگر بردگی عیب نیست، پس هیچ چیز عیب نیست.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(10,'حکومت مردم بر مردم و برای مردم هرگز محکوم به نابودی نخواهد بود.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(11,'اگر نمی خواهی در حق تو داوری شود، درباره دیگران داوری نکن.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(12,'با مردم باش! حق، همیشه با مردم است؛ آنها هرگز کسی را گمراه نمی کنند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(13,'اگر ما دیگران را در سختیها و گرفتاریها دلداری دهیم، خود نیز آرام می شویم.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(14,'مرد بی شهامت کسی است که در جایی که باید اعتراف کند، خاموش بنشیند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(15,'من دیده ام که درجه سعادت اشخاص بستگی به اراده و میل خود آنها دارد.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(16,'کشتن گنجشک ها، کرکس ها را ادب نمی کند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(17,'هیچ کس نمی تواند فرد ضعیف را با تضعیف فرد قوی تقویت کند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(18,'زمین خوردن، شکست خوردن نیست؛ از زمین بلند نشدن، شکست خوردن است.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(19,'کمابیش همه انسانها در برابر ناملایمات پایداری می کنند، اما اگر خواستید منش کسی را آزمایش کنید به او قدرت بدهید.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(20,'انسان همان اندازه شاد خواهد بود که فکرش را برای پذیرش آن مقدار از شادی آماده کرده است.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(21,'هیچ وقت نباید کسی را به دلیل عملش تحقیر و یا به جهت کاری که کرده توبیخ کنیم. چون او در محیطی رشد کرده که مقتضیات تربیتی و محیط و عادتهای اکتسابی و ارثی خود را داشته است.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(22,'رنج و غصه را برای خودت بپذیر و رفاه و آسایش را برای همه.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(23,'اگر بازده کار من خوب بود که دیگر، انتقادهای انجام شده نسبت به من اهمیتی نداشت و اگر نتیجه راندمان کاری من غلط و اشتباه می شد، حتی اگر ده فرشته آسمانی هم قسم می خوردند که عملکرد من درست و صحیح بوده باز هم کسی باور نمی کرد و نمی پذیرفت.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(24,'هرگاه بتوانیم بعد از شکست لبخند بزنیم شجاع هستیم.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(25,'مصمم به نیک بختی باش، نیک بخت می شوی.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(26,'به این نکته پی برده ام که بیشتر مردم آنقدر شاد هستند که ذهن شان را نیز وادار می سازند این گونه باشد.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(27,'لبخند، بیش از چند لحظه دوام ندارد، اما خاطره ی آن جاودانی است.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(28,'به یاد داشته باش که برای موفقیت، بیش از هر چیز به اراده و تصمیم نیاز داری.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(29,'هر پله ی تعالی روح برای انسان یک تولد دوباره است.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(30,'من نمی دانم پدر بزرگم که بوده، اما به این نکته بیشتر اهمیت می دهم که بدانم نواده او چه کسی خواهد شد.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(31,'آزادی یعنی اینکه می خواهم نه برده باشم و نه برده دار.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(32,'در بین مردم ما کم نیستند کسانی که به وجود کشورمان افتخار می کنند. اما ما در تلاشیم نسلی از مردمی را بسازیم که کشورمان به وجودشان افتخار کند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(33,'گامهای من رو به جلو همواره آرام بوده اند. این را خودم می دانم، اما هرگز هیچ گامی را رو به عقب برنداشته ام.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(34,'اندکی از مردم را می توان برای همیشه فریب داد. همه مردم را نیز می توان برای مدت اندکی فریب داد. اما نمی توان همه مردم را برای همیشه فریب داد.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(35,'من دریافته ام که انسانی هر اندازه مصمم به خوشبخت زیستن باشد، به همان اندازه خوشبخت و سعادتمند زندگی خواهد کرد.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(36,'هنگامی که شما پاهای فیلی را به زور نگه داشته‌اید و او تلاش می‌کند خودش را برهاند، بهتر است بگذارید برود.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(37,'آدمی ناتوان ترین موجود جهان است، بویژه در برابر خودش. هیچ کس بهتر از خود انسان نمی تواند خودش را فریب دهد. هر کس نیمی از عمرش را در فریب دادن خود تلف می کند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(38,'به همه اعتماد داشتن خطرناک است، به هیچ کس اعتماد نداشتن خطرناکترین است.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(39,'من مایلم امور دولت را به نحوی اداره کنم که اگر در پایان فرمانروایی خود همه دوستانم را از دست دادم، دست کم یکی از دوستانم باقی بماند و آن، وجدان و قلب من است.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(40,'انسان آنقدر فرصت ندارد که نیمی از عمرش را صرف جنگ و ستیزه کند. اگر کسی از حمله و بدگویی نسبت به من دست بکشد، هرگز گذشته را برای تلافی به خاطر نخواهم آورد.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(41,'اگر بدانیم کجا هستیم، کجا می رویم و به چه وسیله می رویم، یکی از مشکلات خود ما و اجتماع ما برطرف می شود.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(42,'چیزهایی که من مایل به دانستن آنها هستم در کتاب وجود دارد و بهترین رفیق من کسی است که کتابی به من بدهد که [ آن را ] نخوانده باشم.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(43,'چقدر سخت است که انسان بمیرد، در حالی که هیچ خدمتی به کشورش نکرده باشد.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(44,'تصمیم بگیرید که کاری را انجام دهید، آنگاه راه انجام آن را خواهید یافت.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(45,'پدرم به من کار کردن را آموخت، نه عشق و علاقه به کار را.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(46,'هر کاری که تصمیم آن را بگیرید، نصفش را انجام داده اید.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(47,'بدون حکومت می توان زندگی کرد، اما بدون کتاب و مطبوعات نه.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(48,'همه می توانند بدبختی را تحمل کنند. اگر می خواهید منش کسی را بیازمایید، به او قدرت بدهید.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(49,'چنانچه می خواهی منش کسی را بسنجی، به او قدرت بده.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(50,'بهترین خوبی آینده، این است که هر بار تنها یک روز از آن می آید.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(51,'بینش و آگاهی، آن است که دیگران را به گونه ای که خود، خود را می بینند، بیان داری.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(52,'قشنگ‌ترین زمان‌های زندگی شما زمان‌هایی است که با دوستانتان سپری می‌کنید.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(53,'ادعا نمی‌کنم که رویدادها را مهار کرده‌ام، بلکه با افسوس بی‌پایان، می‌گویم که رویدادها مرا مهار کرده‌اند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(54,'من تلاش خود را می کنم و به راه خود ادامه می دهم. اگر نتیجه ی دلخواه از کارم گرفتم، خرده گیری و سخن دیگران بر من کارساز نیست و اگر کارم نادرست باشد، سوگند ده فرشته ی آسمانی مبنی بر درستی آن، نمی تواند آن را نیکو کند.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(55,'شما نمی توانید با شانه خالی کردن از مسئولیت امروز، از مسئولیت فردا شانه خالی کنید.((آبراهام لینکلن))',1,0,0,0,0,0);
INSERT INTO mysms VALUES(56,'با مصلحت دیگران ازدواج کردن، در جهنم زیستن است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(57,'من دنیای پر از شیر و عسل را دوست نمی دارم. من دنیای کوچک و گرمی را دوست دارم که خودم با دست خود آن را ساخته باشم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(58,'رویدادهای خوش زندگی مثل درختان سرسبز و خرمی است که وقتی از دور به آنها می نگریم خیلی زیبا به نظر می رسند، ولی به مجرد آنکه نزدیکشان شده و وارد آنها می شویم، زیبایی شان هم از بین می رود. شما در این زمان نمی توانید بفهمید زیبایی اش به کجا رفته است. آنچه می بینید، چند درخت خواهد بود و بس.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(59,'انسان کامل کسی است که زندگی خود را به دست خود بسازد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(60,'کسی که روح شادی دارد، همیشه وسیله خوبی برای شادمانی و خنده پیدا می کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(61,'اسرار شخص، حال زندانیان را دارد که چون رها شوند، تسلط بر آنها غیر ممکن است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(62,'اصل و اساسی که تمام اخلاق گرایان به راستی در باب آن نظری یکسان دارند، این است : کسی را نیازار و هرچه می توانی به دیگران یاری برسان.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(63,'مهمترین توشه زندگی، تسلیم شدن در برابر وقایع و حوادث ناگزیر زندگی است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(64,'خیلی کم اتفاق می افتد که نسبت به آنچه داریم احساس رضایت و خشنودی کنیم و در عوض، همیشه برای آنچه که نداریم تاسف می خوریم و غصه دار هستیم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(65,'هوش و استعداد خود را پرورش بده، اما نه از راه مطالعه صرف، بلکه با تفکر توام با عمل. تمایل بیشتر دانشمندان به مطالعه شبیه تلمبه است؛ خالی بودن مغزهای خودشان موجب می شود که افکار مردم دیگر را به سوی خود بکشند؛ هرکس زیاد مطالعه کند به تدریج قدرت تفکر [ خود ] را از دست می دهد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(66,'هنرها تنها تقلید محض واقعیت خارجی نیستند و اگر برخی آثار هنری چنین بودند، در حقیقت در برابر رسالت عالی خود کاذب می نمودند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(67,'وظیفه هنر تجلی ایده ها است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(68,'مفاهیم سازه های مغزی اند، حال آنکه ایده ها مقدم بر فکر بشری هستند. در حقیقت، درک مغزی ما از ایده ها مفهوم را می سازند، لذا مفاهیم را راهی به قلمرو ذاتها نیست.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(69,'هرچه اکنون هست، لحظه ای دیگر می باید گفت بوده است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(70,'هر شبی که فرا می رسد معنایش این است که روزی را از دست داده ایم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(71,'تنها لحظه ی حال، حقیقت محض است و هر چیز دیگر صرفاً خیال پردازی و بازیگوشی اندیشه است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(72,'زندگی، نگاه داشتن یک نی بر نوک انگشت است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(73,'ما چنان زندگی می کنیم که گویی همواره در انتظار چیزی بهتر هستیم، حال آنکه اغلب آرزو می کنیم که ای کاش گذشته باز گردد و بر آن حسرت می خوریم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(74,'اکثر مردم هنگامی که به پایان کار می رسند و نظری بر گذشته می افکنند در می یابند که سرتاسر زندگی را چون چیزی گذرا زیسته اند و با حیرت مشاهده می کنند که آنچه بی اعتنا از کنارش گذشته اند و لذتی از آن نبرده اند همان زندگی شان بوده است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(75,'هر خشنودی که به دست می آید تخمه ی امیال دیگری را با خود دارد؛ زیرا امیال و خواهش های اراده ی شخصی را پایانی نیست.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(76,'اولین وظیفه، گرفتن حق خود از زندگی است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(77,'چه سبک سرانه است برای انسان که بر فرصتهای گذشته افسوس بخورد و دل بسوزاند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(78,'هرگز هیچ انسانی خشنودی و خوشحالی مطلق را لمس نکرده است؛ اگر [ لمس ] کرده بود، آن شادی دیوانه اش می ساخت.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(79,'در حقیقت، محنت و رنج، اولین شرط و اساس هستی است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(80,'شر و بلا هستی خود را از طریق امور مثبت به نمایش می گذارند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(81,'شعبده ها تنها برای یک بار دیدن جالب هستند و پس از آن تاثیر خود را از دست می دهند و دست استاد شعبده رو می شود.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(82,'برای کسی که از رنج روحی شدید در عذاب است، درد جسمانی تمامی مفهوم خود را از دست می دهد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(83,'مادام که نویسنده با هدف سیاه کردن و پر کردن کاغذ به نوشتن می پردازد، در حقیقت بر سر خواننده کلاه می گذارد، آن را تا روی چشمش پایین می کشد و فریبش می دهد، زیرا وانمود می کند که چیزی برای عرضه دارد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(84,'بهترین آثار نویسندگان بزرگ، درست هنگامی خلق می شوند که بی هیچ چشم داشتی و یا در ازای اندک مبلغی به نوشتن می پردازند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(85,'آنچه که نویسندگان اندیشمند را تحریک به فکر می کند خود موضوع است. به همین جهت اندیشه ایشان بی درنگ متوجه موضوع می شود. تنها در میان ایشان می توانیم نویسندگانی را بیابیم که نامشان جاودان خواهد شد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(86,'اثر هیچ کس جز آن نویسنده ای که سنگ معدن نوشته ی خود را به گونه ای مستقیم از معدن ذهن خود یعنی از تامل خود استخراج می کند ارزش خواندن ندارد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(87,'چه بسیار می آموخت مرد، اگر تنها آن میزان آموختنی که در کتب خودش موجود است می دانست!((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(88,'خطایی عظیم تر از این ممکن نیست که تصور رود آنچه اخیراً نوشته شده همواره کاملتر است؛ که آنچه جدیداً نوشته شده پیشرفتی است بر آنچه پیش تر نوشته شده؛ که هر تغییری به معنای پیشرفت است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(89,'دستاوردها به سرعت برق، یکدیگر را پشت سر می گذارند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(90,'آنچه جدید است به ندرت خوب است؛ زیرا آنچه خوب است تنها اندک زمانی جدید می ماند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(91,'مردی که به قدر کفایت، توان و ذوق ادبی ندارد که عنوانی بکر برای کتابش بسازد به یقین ناتوان تر از آن است که بخواهد مطلبی قابل تامل در آن عرضه کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(92,'مردان بزرگ تنها محصول ذهن خود را به دست می دهند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(93,'رابطه قلم با اندیشه، رابطه عصا با راه رفتن است، اما بدون عصا راحت تر می توان گام برداشت و آنگاه که قلمی هم در دست نیست بهتر می توان اندیشید. مرد تنها آن زمان که فرسودن آغاز می کند تمایلی به دست بردن به عصا و قلمش می یابد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(94,'فرضیه ای که جایی در ذهن یافته یا از آن زاده شده، چون موجودی زنده به زندگی می پردازد، تا آنجا که تنها موافق و هم رأی خود را از جهان خارج دریافت می دارد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(95,'لازم است بر سر نویسنده بی نام و نشان، یعنی یک شارلاتان ادبی فریاد زد که اگر شجاعت آن را نداری که آنچه را درباره مردم می گویی آشکارا تصدیق کنی بهتر است زبان تهمت خود را نگاه داری.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(96,'یک انتقاد بی نام و امضا چیزی در حد یک نامه بی نام و نشان است و بنابراین باید با همان بدگمانی براندازش کرد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(97,'نقل قول نادرست اغلب معلول یک نیت ناپاک است و در نهایت، بی شرمی نفرت انگیز و فرومایگی فطرت و بدذاتی و چنین شخصی همانند کسی که دست به جعل سند می زند دیگر در اختیار خودش نیست و تا ابد اخلاق صدق و راستی را از کف می دهد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(98,'سبک، چهره ی ذهن است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(99,'تقلید از سبک دیگران مانند صورتک بر چهره زدن است، اما صورتک به سرعت لوس و غیر قابل تحمل می شود، چرا که بی روح و بی دوام است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(100,'حتی زشت ترین صورت زنده نیز دارای روح است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(101,'همه جا سادگی و بی ریایی جذاب است و تزویر نفرت انگیز.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(102,'همه نویسندگان بزرگ می کوشند افکارشان را به حد ممکن خالصانه، قاطعانه، واضح و به ایجاز بیان کنند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(103,'نویسنده باید حرفی برای گفتن داشته باشد. آه! البته منظور حرفهای زیاد است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(104,'بسیار روشن است که همه ی نویسندگان مایلند این گونه به نظر بیایند که حرفی برای گفتن دارند، در حالی که هیچ چیز در چنته ندارند. این روش را فیلسوفان موذی و دغل دانشگاهی رواج داده اند و هم اکنون در همه جا به چشم می خورد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(105,'زندگی پرسش دشواری است، من ترجیح می دهم آن را صرف تامل بر خودش کنم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(106,'اراده حیات مطلقاً و در هر زمان خواهان زندگی است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(107,'انکار اراده حیات تنها با روا داشتن خشونتی دردآور بر خویش مقدور می گردد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(108,'ما به طور یقین برای پرداخت باج تولد و مرگ زندگی می کنیم و از همه ی لذتها و رنجهای زندگی بهره مند می شویم و هیچ یک نیز نمی توانند ما را رهایی بخشند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(109,'وحشت مرگ که ما را با وجود اینهمه درد، سخت به زندگی چسبانده، براستی موهوم و غیر واقعی است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(110,'آن عملی که اراده حیات به واسطه ی آن خود را اثبات می کند و انسان به هستی می آید، عملی است که همه در ژرفنای وجودشان از آن شرم دارند و به دقت بر آن سرپوش می گذارند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(111,'ما عادت کرده ایم که شاعران را بیشتر مشغول به شرح و توصیف عشق ببینیم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(112,'آن فرشته ای که امروز در تصنیفات و غزل های عاشقانه تصویر می کنند، اگر هجده سال پیش تر متولد شده بود کمتر می توانست توجه ایشان را به خود معطوف سازد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(113,'در حقیقت، عشق مطلقاً همان غریزه جنسی است منتها در شکلی سرکش تر، ویژه و شاید اگر دقیقش را بخواهیم، آشکارتر.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(114,'در جهان واقعی، پس از اراده حیات، این عشق است که خود را به عنوان قویترین و فعال ترین همه ی محرکها به نمایش می گذارد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(115,'عشق، همواره نیمی از توان و افکار جوانان را به خود مشغول می سازد و غایت نهایيِ تقریباً همه ی تلاشهای بشری است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(116,'عشق یعنی به راه انداختن نسل بعد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(117,'عشق امری است بس مهم، زیرا برخلاف سایر امور، سر و کاری با خوشبختی و بدبختی فرد حاضر ندارد؛ عشق باید هستی و ماهیت ویژه ی بشر آینده را تامین کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(118,'هدف حقیقی تمامی عشق و عاشقی ها، اگرچه خود افراد از حقیقت بی خبر باشند، این است که موجودی جدید به دنیا آید.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(119,'طبیعت، غریزه را دقیقاً در آنجا قرار داده که فرد فعال قادر نیست هدف را دریابد یا تمایلی به تعقیب آن ندارد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(120,'کم بودن غریزه در انسان در مقایسه با رده های فروتر جانداران معلول، توانایی عظیم مغز او است، اما همین اندک غریزه هم برای گمراه کردن او کفایت می کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(121,'آن حس زیبادوستی که مرد را در انتخاب همسر راهنمایی می کند، زمانی که به همجنس گرایی تنزل می یابد گمراه می گردد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(122,'عشقِ مرد پس از گذشت دوره ای مشخص به طور محسوس رو به کاهش می گذارد، چنانکه تقریباً هر زن دیگری بیش از زن خودش برایش جذابیت دارد؛ مرد متمایل به تغییر است، در حالی که عشق زن پس از انتخاب به گونه ای مداوم افزایش می یابد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(123,'اغلب اتفاق می افتد که زن عاشق مردی زشت رو شود، اما هرگز نمی تواند یک مرد زن صفت را دوست بدارد، زیرا قادر نیست عیبهای او را جبران کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(124,'ازدواج، اتحاد دلها است نه ذهنها.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(125,'هر مرد به آن چیزی عشق خواهد ورزید که خود فاقد آن است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(126,'در عشق، هر کس طبعی عکس طبع خود را ترجیح می دهد؛ اما تنها آنجا که دست کم اول مزاج خودش مشخص باشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(127,'عشق، شعبده ای بی مانند است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(128,'برای اینکه مردی عاشق شود به گذر زمان برای تامل و انتخاب نیازی نیست، بلکه همدلی و جاذبه ای شخصی که در نخستین نگاه، وجود هر دو طرف را سرشار می سازد کفایت می کند، یا همان که معمولاً کشش خون می نامندش و آن نیروی ویژه ای که ستارگان را می راند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(129,'عشق کامیاب نیز اغلب بیشتر به بدبختی می انجامد تا به خوشبختی.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(130,'ممکن است که عاشق عیبهای هولناک موجود در شخصیت محبوبه اش را ببیند و تشخیص دهد-کاستی هایی که زندگی رنج آوری را به وی نوید می دهد- و باز هم ترسی به دل راه ندهد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(131,'من نمی پرسم، هیچ اهمیت نمی دهم که آیا قلبت سرشار از گناه است یا نه؛ می دانم که تو را دوست می دارم، هر آنچه باشی.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(132,'مرد قادر است همزمان محبوبه ی خود را دوست بدارد و از او متنفر باشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(133,'کسانی که به خاطر عشق ازدواج می کنند همواره بدبخت هستند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(134,'اکثر ازدواجها نه محصول انتخاب و خواست حقیقی، که ثمره ی اندیشه های سطحی و شرایط اتفاقی اند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(135,'در پاره ای از موارد، عشق با احساسی کاملاً متفاوت در هم می آمیزد؛ یعنی رفاقت حقیقی.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(136,'زنان نه طاقت انجام کار سنگین جسمی را دارند و نه توان فعالیت شدید ذهنی؛ آنها دِین زندگی را نه با آنچه انجام می دهند که با آنچه تحمل می کنند، می پردازند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(137,'ما مردان هر گاه احساساتمان برانگیخته می شوند در اهمیت امور قدری اغراق می کنیم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(138,'نویسنده ای شایسته که ایده های بسیار در سر دارد، به زودی خواننده را مجاب می کند که براستی و حقیقتاً حرفی برای گفتن دارد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(139,'نویسنده شایسته از آنجا که براستی چیزی در سر دارد همواره نظرات خود را به ساده ترین و صریح ترین شکل بیان می کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(140,'در نویسندگی هرچه مفهومی خیالی تر و مجردتر باشد کمتر ادا کننده مقصود است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(141,'نویسنده خردمند هنگام نوشتن براستی با ما حرف می زند و به همین جهت قادر است در آن واحد، هم سرگرممان کند و هم به رقصمان در آورد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(142,'حضور مطلق هوش است که هماره و همه جا آثار نوابغ را برجسته می سازد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(143,'باید برای به زبان آوردن امور غیر معمول از کلمات معمول استفاده کرد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(144,'نویسندگانی که جملات سخت و پیچیده و مبهم به هم می بافند، براستی نمی دانند چه می خواهند بگویند... آنها اغلب مایلند از خودشان و دیگران پنهان سازند که حرفی برای گفتن ندارند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(145,'[ در نویسندگی ]، زیاده گویی عموماً تاثیری برخلاف آنچه مدنظر است به وجود می آورد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(146,'[ در نویسندگی ]، هر کلمه زائد مانعی بر سر راه رسیدن به مقصود است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(147,'مردمان زیادی هستند که تلاش می کنند فقر اندیشه شان را زیر انبوهی از کلمات بی مصرف پنهان کنند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(148,'نویسنده باید در وقت خواننده، در تمرکز حواس و پشتکار او صرفه جو باشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(149,'از قلم افتادن یک اندیشه خوب بسیار بهتر از بیان چیزی است که به هیچ وجه ارزش مطالعه ندارد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(150,'راز اضطراب و اندوه مدام در به زبان آوردن همه چیز است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(151,'بار کردن اندیشه بسیار بر کلمات اندک ویژگی ذهنهای برجسته است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(152,'حقیقت، زیباترین برهنگی است و ژرفنای تاثیر آن نتیجه سادگی بیانش است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(153,'[ در نویسندگی ]، هر کلمه زائد لکه ای بر دامن سبک است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(154,'ایجاز حقیقی بیان از آنِ کسی است که آنچه را که ارزش بیان دارد می گوید و از طول و تفصیل اموری که هر کس خود قادر است به آنها بیندیشد اجتناب می ورزد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(155,'ثروت و سنگینی اندیشه است که به سبک ایجاز می بخشد و زبده و آبستن معنایش می سازد، نه هیچ چیز دیگر.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(156,'اگر نظرات نویسنده ای با اهمیت، درخشان و در کل قابل ابلاغ باشند، به طور حتم مواد و مصالح کافی برای ساختن جملات بیانگر خود را مهیا می سازند و چه از لحاظ دستوری و چه از لحاظ لغوی به حد کمالشان می رسانند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(157,'[ در نویسندگی ]، انتخاب لغات همواره موجد کوتاه کردن و تراکم معنا است و به اندیشه رخصت می دهد تا بیان موافق و مفهوم خود را بیابد و به سلامت به مقصد برسد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(158,'نویسنده ای که خواهان ایجاز بیان است باید اندیشه خود را گسترش بخشد، نه برعکس.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(159,'یک مرد لاغر و بیمار برای به تن کردن لباس بزرگ باید خود را سلامت و هیکل مند سازد نه اینکه قیچی در کار لباس بیچاره اندازد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(160,'کلمات باید چنان در کنار یکدیگر بنشینند که خواننده را وادار سازند تا به طور دقیق همان گونه که نویسنده اندیشه کرده بیندیشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(161,'نویسنده باید به یاد داشته باشد که افکار و ملاحظات از قانون جاذبه تبعیت می کنند؛ یعنی از درون کاسه سر آسانتر به روی کاغذ می آیند تا از روی کاغذ به درون کاسه سر.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(162,'آن کس که با سهل انگاری می نویسد، در وهله اول ثابت می کند که خودش هم ارزش چندانی برای افکارش قایل نیست.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(163,'آنجا که مرد به صحت و اهمیت افکارش ایمان دارد، آن ندا و شوق لازم برای پشتکار و تلاش بی وقفه جهت کشف روشن ترین، سودمندترین و پرقدرت ترین بیان آن افکار در او طنین افکن می شود.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(164,'شلختگی اهانت به جامعه است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(165,'نگارش شتابزده و بی دقت، بی احترامی نفرت انگیز به خواننده است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(166,'تالیف محکم و سودمند باید بر این پایه بنا شود که انسان در هر لحظه تنها قادر است به یک امر به روشنی بیندیشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(167,'جهالت تنها آن زمان مایه خفت است که با ثروت همراه باشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(168,'نیاز و عسرت سد راه فقرا می شود؛ کار ایشان جای دانش را می گیرد و ذهن و افکارشان را به خود معطوف می سازد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(169,'ثروتمندان به این دلیل که از دولت و فرصتی که می تواند ارزشمندترین همه ارزشها را نصیبشان سازد استفاده نمی برند سزاوار سرزنش اند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(170,'هنگامی که مطالعه می کنیم شخص دیگری به جای ما فکر می کند؛ ما تنها جریان ذهنی او را تکرار می کنیم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(171,'گاه اتفاق می افتد که شخصی که فراوان - یعنی تقریباً تمام روز را – مطالعه می کند و در فواصل آن هم وقت خود را با سرگرمیهای خالی از تفکر تلف می کند، به تدریج توان خوداندیشی را از دست می دهد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(172,'شخصی که همواره سواری می کند، سرانجام راه رفتن را از یاد می برد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(173,'ذهن نیز تحت فشار مداوم افکار دیگران از کار می افتد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(174,'می توان با تزریق خوراک بیش از حد به ذهن آن را مسدود کرد و از کار انداخت.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(175,'هنگامی که شخص فقط مطالعه کند و سپس درباره آنچه مطالعه کرده به تفکر نپردازد، مطالعه اش عمق نمی یابد و بخش اعظم آن از دست می رود.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(176,'افکار روی کاغذ بیش از ردپاهایی بر ماسه نیستند؛ راهی را که فلان شخص پیموده می توان دید، اما برای دیدن آنچه وی در راه دیده باید چشمان او را داشت.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(177,'کتابهای مزخرف بیشمار همان علفهای هرز مزرعه ادبیات اند که غذا را از غله می گیرند و نابودش می سازند. این کتابها وقت و پول و دقتی را که به کتابهای سازنده و اهداف درخشانشان تعلق دارند، وابسته و منحصر به خود می سازند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(178,'مادام که مجلات ادبی، معلومات مختصر و پیش پا افتاده ی مردمان معمولی را چاپ می کنند، به ویژه ابزار مکارانه ای برای سرقت وقت مردم زیبایی شناس هستند؛ وقتی که باید برای پیشرفت فرهنگ به شاهکارهای اصیل هنری اختصاص یابد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(179,'آن که برای ابلهان می نویسد همواره مخاطب بسیار می یابد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(180,'کتابهای بد، سم روح اند و ذهن را نابود می کنند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(181,'شرط مطالعه کتاب خوب، نخواندن کتاب بد است؛ زیرا زندگی کوتاه است و فرصت و توان محدود.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(182,'اینکه شخصی بخواهد هرچه را می خواند حفظ کند مثل این است که سعی کند هر آنچه را که می خورد در معده نگاه دارد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(183,'ذهن، تنها آنچه را که برایش جالب باشد حفظ می کند؛ آنچه را که با نظام فکری وی سازگار باشد و مناسب برای نیل به اهدافش.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(184,'کتابهای مهم را باید دو مرتبه خواند؛ قدری به این دلیل که مطلب در مرتبه دوم به کمال، فهم می شود و آغاز، تنها زمانی درک می شود که پایان معلوم باشد؛ تا حدودی هم به این دلیل که در مرتبه دوم، دل و دماغ چنان تفاوت دارند که یکی دیگری را متاثر می سازد و بدین گونه شخص مطلب را به گونه ای دیگر می بیند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(185,'آثار، عصاره ذهن اند و بنابراین همواره ارزشی بیش از سخنان دارند، حتی اگر گوینده ی بزرگترینِ اذهان باشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(186,'ای کاش کسی می آمد و تاریخ تراژیک ادبیات را تهیه می کرد تا نشان دهد ملتهایی که بزرگترین نویسندگان و هنرمندان از آنها برخاسته اند و غرورآمیزترین دارایی هایشان هستند، در زمان حیات این بزرگان چگونه با آنها معامله کرده اند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(187,'کتابخانه می تواند بسیار بزرگ و در همان حال در هم ریخته و نامرتب باشد و در این صورت نسبت به کتابخانه ای کوچک و منظم کاربرد و بازده کمتری خواهد داشت.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(188,'فردی که دامنه ای وسیع از معلومات در ذهن دارد و با این حال بر آنها تامل نکرده و زمینه کاری برای آنها فراهم نیاورده، در مقایسه با فردی که معلومات کمتری دارد اما به کمال در آنها اندیشه کرده، از ارزش و توان کمتری برخوردار است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(189,'انسان نمی تواند چیزی را به ذهن بسپارد مگر اینکه آن را درک کند؛ بنابراین باید آن را فرا بگیرد؛ اما تنها زمانی می توان گفت درکش کرده که آن را به کار انداخته باشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(190,'برای متفکر به قدر کافی اندیشه های ارزشمند وجود دارد؛ اما تنها تعداد کمی از آنها قدرت کافی برای ایجاد پژواک و بازتاب دارند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(191,'جهان مشهود پیرامون انسان برخلاف مطالعه، اندیشه ای معین و مجرد را به ذهن وی تحمیل نمی کند، بلکه صرفاً دستمایه و مجالی را فراهم می آورد تا وی را به تفکر درباره ی آنچه اقتضای نهاد و حال و هوای فعلی او است رهنمون شود.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(192,'اهل فضل کسانی هستند که مطالعه ی خود را در میان صفحات کتابها انجام داده اند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(193,'اندیشمندان و نوابغ کسانی هستند که مستقیماً به سراغ کتاب طبیعت رفته اند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(194,'نسبت اندیشه ای که مطالعه می کنیم با آنچه که به ذهن خودمان می رسد، نسبت فسیل گیاهی ماقبل تاریخی است با گیاهی که در رستنگاه خود جوانه می زند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(195,'دست گرفتن کتاب برای دور کردن افکار شخصی، معصیتی در برابر روح القدس است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(196,'حقیقتی که صرفاً فرا گرفته شده باشد همچون عضوی مصنوعی است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(197,'مطالعه، اندیشیدن با ذهن دیگری است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(198,'مطالعه ی افکار یک مؤلف مانند خوردن ته مانده ی غذای ضیافتی است که به آن دعوت نشده ایم، یا مانند به تن کردن لباسی است که یکی از مهمانان به گوشه ای نهاده است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(199,'تنها آن که نبوغش راهنمای اوست، آن که خود می اندیشد و آن که دقیق و با طمانینه فکر می کند، قطب نمایی دارد که به وسیله ی آن راه راست را می یابد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(200,'نسبت یک متفکر با یک فیلسوفِ کتابی مانند نسبت شاهدی عینی به یک نفر تاریخدان است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(201,'افکار هم مثل انسانها هستند؛ همواره و به دلخواه نمی آیند؛ باید انتظار کشید.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(202,'مطالعه، ذهن را با افکار شخص دیگری پر می کند که شیوه ی تفکرش اغلب سوای شیوه ی تفکر ما است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(203,'زندگی حقیقيِ پیش روی انسان، موضوع طبیعی اندیشه است و در جایگاه خود به عنوان جوهر اصلی وجود، آسانتر از هر چیز دیگری قادر است تا ذهنی متفکر را تحریک کند و متاثر سازد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(204,'رابطه ی تجربه با اندیشه همان رابطه ی بلع با هضم و جذب است. اگر تجربه ادعا کند که دستاوردهایش برای پیشرفت نوع بشر کفایت می کند، مثل این است که دهان تمام سلامت بدن را ثمره ی کار خود بداند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(205,'یک ذهنِ حقیقتاً توانا همواره قاطعانه و به روشنی می داند که چه می خواهد بگوید، خواه بوسیله ی نثر، نظم و یا موسیقی.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(206,'نشان مخصوص ذهن والا این است که همواره قضاوت دست اول خود را دارد. هرچه می گوید ثمره ی خوداندیشی او بوده و به خاطر شیوه ی اعلام نظراتش همواره ممتاز است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(207,'در جهان ذهن، ما ارواحی جدا از جسم و آزاد از قانون و عسرت و اضطراب هستیم. بنابراین در زمین مسرتی وجود ندارد که با آنچه ذهنی والا و پرثمر در لحظه ای فرخنده در خود می یابد برابری کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(208,'اساتید برای کسب پول درس می دهند و نه برای خرد، که برای تظاهر و تفاخر و مشهور بودن به آن با یکدیگر رقابت می کنند. دانشجویان هم نه برای دانش و معرفت، بلکه برای روده درازی و باد به غبغب انداختن درس می خوانند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(209,'من کمترین ارزشی برای مطالعاتی که صرفاً به قصد امرار معاش باشد قایل نیستم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(210,'دانشجویان و فرهیختگان در اصل، بیشتر می خواهند معلومات کسب کنند تا معرفت. آنها به اینکه درباره ی هر امری چیزی بدانند افتخار می کنند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(211,'معلومات صرفاً وسیله ای برای رسیدن به معرفت است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(212,'تفکر است که از مرد فیلسوف می سازد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(213,'وقتی که وصف این اعجوبه های دانش و فضیلت دانشگاهی پرهیبت را می شنوم گاهی به خود می گویم: آه، برای اینکه بتوانند زیاد بخوانند ناچار بوده اند چقدر کم فکر کنند!((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(214,'هر چیزی که انسان برای خود آن چیز به دنبالش نباشد بی ارزش است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(215,'فضیلت حقیقی، مهم نیست در چه زمینه ای، تنها آنجا به دست می آید که اثر به خاطر خودش خلق شده باشد و نه به عنوان وسیله ای برای نیل به اهداف برتر.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(216,'اغلب، فقرا برای همدردی و دستگیری از دیگران آمادگی بیشتری دارند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(217,'برای چه آن چیزی که معمولی است باید با تحقیر همراه باشد؟((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(218,'هیچ کس اجازه نخواهد داد تا از او بلندتر شویم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(219,'چرا با وجود تمامی آیینه های جهان باز هم هیچکس قادر نیست حقیقتاً بداند به چه شکلی در نظر می آید؟((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(220,'یک دانشمند میانه حال با هدف تسلط بر تدریس و تالیف به مطالعه می پردازد. سر او به معده و روده هایی می ماند که غذا بدون هضم شدن از آنها عبور می کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(221,'کسی که دارای سمت استادی است در طویله غذا می خورد که بهترین مکان برای حیوانات نشخوارگر است. اما کسی که غذای خود را در دستان طبیعت می یابد در مراتع آسوده تر است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(222,'برای پرورش حقیقی در ادبیات باستانی مطلقاً لازم است که فرد چند بُعدی باشد و دیدگاه های مختلفی را بکار گیرد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(223,'مشاهده دقیق مردم حتی اگر ارزش مصاحبت نداشته باشند مهم و ارزشمند است. هر فرد به عنوان تصوری از طبیعت، ارزش مشاهده دارد؛ بنابراین بالاترین درجه متعلق به زیبایی است، زیرا تصویر کامل تر و متعالی تر طبیعت است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(224,'زیبایی، تصور اصلی و اساسی طبیعت است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(225,'یک رایحه تنها بار اول ما را متاثر می کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(226,'هر یک از امیال و افکار، در مدتی که حضور داشته تاثیر خود را بر چهره [ انسان ] به جا گذاشته است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(227,'هرگز نگذاریم هیچ چیزی زندگی فکری ما را متوقف سازد، هرچند ممکن است طوفان بی امان جهان محیط ما را آشفته سازد و در هم ریزد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(228,'نبوغ، خود به تنهایی به همان اندازه قادر است اندیشه های ناب تولید کند که یک زن به تنهایی می تواند طفلی به دنیا آورد. شرایط بیرونی باید نبوغ را بارور سازند و نقش پدر را برای فرزندش ایفا کنند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(229,'دانشمند آن است که چیزها آموخته؛ نابغه کسی است که از وی چیزی می آموزیم که خود هرگز از کسی نیاموخته است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(230,'آن که می خواهد کارهای بزرگ انجام دهد باید چشم به آیندگان بدوزد و با عزم راسخ برای نسلهای در راه فعالیت کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(231,'نابغه در شرایط مساعد و با تلاش پیگیر و اشتیاقی سستی ناپذیر، زندگی را وقف تولید، تحصیل، ساخت و ساز و باروی، طرح ریزی، بنیانگذاری، برقرارسازی و زیبایی بخشی می کند و همواره به این می اندیشد که برای خود کار می کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(232,'نابغه در خود دانش شادی و لذتی می یابد؛ در تحلیل هر مسئله، در هر اندیشه ی باریک، چه از آن خود وی باشد و چه از آن غیر.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(233,'اگر اثری بی همتا از یک نابغه به ذهنی معمولی پیشکش شود، آن ذهن ساده از آن اثر همان اندازه بهره مند می شود که یک نفر مبتلای به نقرس از دعوت شدن به مجلس رقص. یکی فقط به جهت تشریفات به مجلس می رود و آن دیگری تنها به جهت عقب نماندن از قافله کتاب را می خواند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(234,'هر اندازه هم که آیندگان، نویسنده ای را بزرگ و ستودنی و آموزنده بیابند باز هم در زمان حیاتش در چشم معاصران، ضعیف و زبون و بی مزه می آید.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(235,'یک اثر بزرگ و فوق العاده و اصیل فقط آنگاه خلق می شود که مؤلفش افکار، روش و عقاید معاصرانش را نادیده بگیرد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(236,'آنها که از میان اکثریت بیرون می آیند، آنها که نابغه نام می گیرند، تنها آنان والایان حقیقی نوع بشرند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(237,'ارزش آثار نابغه چنان است که در میان همه ی مردان تنها او می تواند چنین تحفه ای به جهان پیشکش کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(238,'خواندن زندگینامه یک فیلسوف به جای مطالعه ی آثارش مانند نادیده گرفتن یک نقاشی و توجه به ساخت قاب آن است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(239,'نابغه همنشین خود را فراموش می کند و بی توجه به اینکه آیا وی سخنان او را می فهمد یا نه، چنان به حرف زدن ادامه می دهد که گویی کودکی با عروسکش سخن می گوید.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(240,'اینکه مغز باید نوکر و کارگر شکم باشد در حقیقت عقیده ی آن کسانی است که نه حاصل دسترنج خود را می خورند و نه به سهم خود قناعت می کنند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(241,'استعداد بشر همواره با محدودیت روبه رو است و هیچ کس بدون داشتن برخی ضعفها نمی تواند نابغه گردد؛ این ضعفها حتی می تواند محدودیت های ذهنی باشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(242,'جماعت درس خوانده ی باسواد آنقدر افراط می کنند و گندش را بالا می آورند که افتخار و شرف نابغه را لکه دار می کنند؛ دقیقاً به همان صورت که ایمان به مقدسات به جهل و خرافه پرستی ابلهانه بدل می گردد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(243,'افتخار، بوقلمون صفت است و وانگهی اگر در آن دقیق شوید، کم ارزش؛ هرگز با تلاشی که کرده اید برابر نیست و یا آن لذتی که به شما می بخشد محصول خودش نیست، بلکه این تلاش است که افتخار را ارزش می بخشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(244,'هیچ کس خود را آنگونه که براستی هست نمی نمایاند، بلکه نقاب خود را بر چهره می زند و نقش خود را بازی می کند. در حقیقت، مراتب اجتماعی ما چیزی بیش از یک کمدی بی سر و ته نیست.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(245,'اگر کسی نشان دهد که تو را خوار می دارد و حقیر می شمارد، در نهایت نشان داده که توجه زیادی به شخص تو دارد و می خواهد کاری کند که از ارزش کمی که برایت قایل است آگاه شوی.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(246,'آنچه مردم را سنگدل می سازد این است که در هر انسان تنها آن میزان توان هست که بتواند دردها و رنجهای خود را تحمل کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(247,'هیچ کس نمی داند چه توانایی هایی برای تقلا و تحمل رنج در خود دارد، تا زمانی که اتفاقی می افتد و این نیروها را به جنبش در می آورد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(248,'هنگامی که توان روحی انسان نابود شود و تحلیل رود، زندگی در نظرش بس کوتاه و بی ارزش و زودگذر خواهد آمد و در آن هیچ چیز رخ نخواهد داد که ارزش انگیزش هیجان وی را داشته باشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(249,'یک پزشک همه کس را می تواند درمان کند جز خودش را؛ زمانی که بیمار شود، نزد یک همکار می رود.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(250,'خردها از اساس با یکدیگر اختلاف دارند، اما با ملاحظات صرفاً کلی نمی توان به این تفاوتها پی برد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(251,'اگر آموزش چیزی را پیش از پنج سالگی آغاز کنید و با وقار و آرامش فراوان آن را به گونه ای مداوم تکرار کنید برای ابد در ذهنتان باقی خواهد ماند. زیرا در مورد انسانها هم بسان حیوانات، آموزش تنها آنگاه با موفقیت همراه است که از دوران کودکی آغاز شود.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(252,'پس از مدتی طولانی امکان دارد که بر مرگ رقبا و دشمنانمان هم تقریباً به قدر مرگ دوستان و عزیزانمان غصه بخوریم؛ البته زمانی که ایشان را به عنوان شاهدان پیروزیهای درخشانمان از دست داده باشیم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(253,'اینکه حساب، نازلترینِ فعالیتهای ذهنی است با این حقیقت اثبات می گردد که تنها عملی است که با یک عدد ماشین هم انجام پذیر است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(254,'هر عدم تناسب بین اراده و خرد به بدبختی انسان منجر می گردد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(255,'زندگی تکنولوژیک دوران ما، با کمال بی سابقه اش و افزایش و ازدیاد اسباب تجمل، این فرصت را به ما داده تا میان فراغت و فرهنگ متعالی تر و زحمت و فعالیت و رفاه بیشتر دست به انتخاب بزنیم و البته عوام به فراخور شخصیت خودشان دومی را انتخاب می کنند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(256,'ازدواج یعنی نصف کردن حقوق و دوچندان کردن مسئولیت ها.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(257,'هر حقیقت از سه مرحله می گذرد: اول مورد تمسخر قرار می گیرد. دوم به شدت با آن مخالفت می شود و سوم به عنوان یک امر بدیهی مورد پذیرش قرار می گیرد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(258,'هر کس محدودیتهای دید خود را محدودیتهای دنیا می انگارد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(259,'ما سه چهارم [ وجود ] خودمان را به تاوان اینکه شبیه دیگران باشیم از دست می دهیم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(260,'هر قومی اقوام دیگر را مسخره می کند؛ در حالی که همه بر حق هستند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(261,'زندگی نامه انسان عبارت است از: مدهوش بودن از امیدها و آرزوها و پای کوبان به آغوش مرگ پناه بردن.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(262,'همه آرزوها از نیاز سرچشمه می گیرد، یعنی از کمبودها و رنجها.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(263,'هنر، گونه ای رستگاری است؛ ما را از خواستن، یعنی درد و رنج، آزادی می بخشد و تصاویر زندگانی را دلربا می سازد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(264,'برای نیل به خوشبختی هیچ راهی نادرست تر از لذت طلبی و کوشش برای درک عیش و نوش و خوشی های جهان نیست.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(265,'تمام حقایق سه مرحله را پشت سر گذاشته اند: اول، مورد تمسخر قرار گرفته اند. دوم، به شدت با آنها مخالفت شده است و سوم، به عنوان یک چیز بدیهی پذیرفته شده اند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(266,'ایده ها الگوهای ازلی ای هستند که در قلمرو ذاتها یا هستی های حقیقی وجود دارند و آدمیان تنها هنگامی که خود را از توجه به جزییات، در اینجا و اکنون (مکان و زمان) رها سازند وارد آن قلمرو می شوند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(267,'نگهداری دم ماهی و دل زن از دشواری ها است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(268,'وظیفه ی هنرها توصیف موارد خاصی از حقیقت نیست، بلکه نشان دادن امور مطلق و کلی ای است که در پشت این موارد خاص و جزیی قرار دارند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(269,'ازدواج یعنی با چشمان بسته به امید گرفتن یک مارماهی، دست فروبردن در جوالی [ =ظرفی از پشم بافته ] پر از مار.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(270,'عشق و عاشقی هر چند لطیف و پر احساس ابراز گردد باز هم ریشه در شهوت دارد و بس.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(271,'هر کتابی که به خواندنش می ارزد باید در آن واحد دوبار خوانده شود. رعایت دستور فوق دو علت دارد: یکی اینکه در مطالعه دوم، قسمت های مختلف کتاب بهتر درک می شود و قسمت اول کتاب زمانی نیک فهمیده می شود که از پایان آن نیز آگاه باشیم و دیگر اینکه در این دو مطالعه وضع روحی ما یکسان نیست، در مطالعه ی دوم ما نظر تازه ای نسبت به هر قسمت پیدا کرده و جور دیگری تحت تأثیر آن کتاب قرار می گیریم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(272,'کتابخانه تنها یادبود مطمئن و ماندگار نوع بشر است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(273,'انسانهای بزرگ مانند عقابند که آشیانه ی خود را بر فراز قله های بلندِ تنهایی می سازند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(274,'زبان، ارزشمندترین میراث یک ملت است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(275,'اشخاصی که هرگز وقت ندارند آنهایی هستند که کمتر کار می کنند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(276,'آنکه خود را حقیر می شمارد، در حقیقت، فرد متکبری است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(277,'ما به ندرت درباره آنچه که داریم فکر می کنیم، در حالی که پیوسته در اندیشه چیزهایی هستیم که نداریم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(278,'اگر با شخص مباحثه کنیم و تمام قدرت استدلال و بیان خود را به کار اندازیم، چقدر ناراحت و خشمگین خواهیم شد، زمانی که بفهمیم طرف [ مقابل ] نمی خواهد بفهمد و ما با اراده او سر و کار داریم؛ اینجاست که منطق بی فایده است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(279,'موسیقی اصیل آن است که در ورای خود بیانگر ایده ای باشد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(280,'اراده، آن مرد کور نیرومندی است که بر دوش خود مرد شل بینایی را می برد تا او را رهبری کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(281,'زیبایی اگرچه مایه شرافت است، اما در معرض هزاران شر و آفت است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(282,'اولین درسی که پدر و مادر باید به فرزندان خود بیاموزند، راستی و درستی است.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(283,'افراد پست و فرومایه از خطاهای اشخاص بزرگ لذت فراوان می برند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(284,'ایده ها در بیرون زمان قرار دارند و در نتیجه ابدی هستند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(285,'هر جدایی یک نوع مرگ است و هر ملاقات یک نوع رستاخیز.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(286,'اگر ما چیزی را می خواهیم، از آن جهت نیست که دلیلی بر آن پیدا کرده ایم، بلکه چون آن را می خواهیم، برایش دلیل پیدا می کنیم.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(287,'تجربه نشان داده است افرادی که دارای نبوغ هنری فوق العاده بوده اند، در ریاضیات استعداد نداشته اند. هیچ کس نمی تواند در هر دو رشته ممتاز گردد.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(288,'آیا [ این ] جهالت نیست که انسان ساعتهای شیرین امروز را فدای روزهای آینده کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(289,'نوابغ بر خلاف اشخاص عادی، تنها به فکر خود نیستند و منافع شخصی را در نظر نمی گیرند. بدین جهت در آثار نوابغ همیشه نظریاتی دیده می شود که دارای جنبه کلی و جهانی است و از حدود زمان فراتر می رود.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(290,'ممکن است انسان در مورد مسایل دیگران درست قضاوت کند، ولی در مسایل مربوط به خودش به خطا رود. زیرا هنگام قضاوت در امور خودمان، «اراده» به فعالیت پرداخته و «عقل» را از کار می اندازد. از این رو شخص باید با دوست خود مشورت کند.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(291,'نخستین و مهمترین سبب های نیک بختی انسان، عبارت است از خلق و خوی خود او.((آرتور شوپنهاور))',2,0,0,0,0,0);
INSERT INTO mysms VALUES(292,'یکی از راههای رسیدن به کامیابی و پیروزی، داشتن آگاهی و پول است.((آلبر کامو))',3,0,0,0,0,0);
INSERT INTO mysms VALUES(293,'بشر تنها آفریده ای است که نمی خواهد آن باشد که هست.((آلبر کامو))',3,0,0,0,0,0);
INSERT INTO mysms VALUES(294,'پیش روی من گام برندار که پشت سرت گام برنخواهم داشت. پشت سرم گام برندار که پیش روی تو گام برنخواهم داشت. تنها کنارم گام بردار و دوست من باش.((آلبر کامو))',3,0,0,0,0,0);
INSERT INTO mysms VALUES(295,'هیچکس مجبور نیست انسان بزرگی باشد؛ تنها، انسان بودن کافی است.((آلبر کامو))',3,0,0,0,0,0);
INSERT INTO mysms VALUES(296,'باید آزادی را همراه عدالت انتخاب کرد؛ یکی بدون دیگری بی معناست. اگر کسی نان شما را بگیرد آزادی شما را هم گرفته است و اگر کسی آزادی شما را برباید مطمئن باشید که که نان شما نیز در معرض تهدید است.((آلبر کامو))',3,0,0,0,0,0);
INSERT INTO mysms VALUES(297,'آزادی، تنها ارزش جاودانه ی تاریخ است.((آلبر کامو))',3,0,0,0,0,0);
INSERT INTO mysms VALUES(298,'تنها وظیفه ی انسان، عشق ورزیدن است.((آلبر کامو))',3,0,0,0,0,0);
INSERT INTO mysms VALUES(299,'باید تمدن در خدمت بشریت باشد، نه بشریت در خدمت تمدن.((آلبر کامو))',3,0,0,0,0,0);
INSERT INTO mysms VALUES(300,'بهترین برنامه ریزی برای آینده استفاده درست از زمان حال است.((آلبر کامو))',3,0,0,0,0,0);
INSERT INTO mysms VALUES(301,'وجود ما نویسندگان و هنرمندان اگر تنها یک علت داشته باشد، آن علت چیزی نخواهد بود جز سخن گفتن به جای همه کسانی که نمی توانند سخن بگویند.((آلبر کامو))',3,0,0,0,0,0);
INSERT INTO mysms VALUES(302,'به سختی می توان در بین مغز های اندیشمند جهان، کسی را یافت که دارای یک نوع احساس مذهبی مخصوص به خود نباشد؛ این مذهب با مذهب یک شخص عادی تفاوت دارد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(303,'من می‌پذیرم که اندیشه‌‌ها، روی بدن انسان، اثر می‌گذارند.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(304,'دشواری‌های بزرگی که ما با آن‌ها روبرو هستیم، نمی‌توان با همان دیدگاهی که آن‌ها را پدید آورده است، از بین برد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(305,'تخیل، همه چیز است. تخیل، پیش‌بینی جذابیت‌های آینده است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(306,'در مسیر کشف، هوش، نقش زیادی ایفا نمی‌کند. جهشی در هشیاری ایجاد می‌شود که می‌توانید آن را شم و شهود یا آرزو نام نهید؛ آن گاه راهکار، خود به خود به سوی شما می‌آید و نمی‌دانید چگونه یا چرا.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(307,'هیچ دشواری را نمی توان در همان سطحی که به وجود آمده از بین برد؛ باید یاد بگیریم که نگاهمان را به جهان تغییر دهیم.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(308,'مهم‌ترین مسأله این است که دست از پرسیدن برنداریم.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(309,'آنچه به راستی فراتر از فهم ماست، خودش را به والاترین شکل خرد نمایان می‌سازد و درخشان‌ترین زیبایی را دارد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(310,'پندار و گمان، به مراتب مهم‌تر از دانش است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(311,'در سقوط افراد در چاه عشق، قانون جاذبه تقصیری ندارد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(312,'دانش بدون دین لنگ است، دین بدون دانش کور.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(313,'تخیل بسیار مهم تر از دانش است؛ دانش محدود است، اما تخیل جهان را در بر می گیرد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(314,'هر چه بیشتر می آموزم بیشتر به نادانی خود پی می برم.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(315,'بهتر آن است که ذهن را برای اندیشیدن مورد استفاده قرار دهیم نه برای انبار کردن اطلاعات.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(316,'فرصت ها در دل مشکلات نهفته اند.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(317,'از دیروز بیاموز، برای امروز زندگی کن و به فردا امید داشته باش.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(318,'تنها دو راه برای زیستن در زندگی خود داری، اول اینکه هیچ معجزه ای را باور نکنی و دیگر اینکه همه چیز را معجزه بدانی.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(319,'لذت نگریستن و درک طبیعت برترین نعمت است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(320,'... از دیدگاه زندگی روزانه یک چیز است که آن را خوب می دانیم : انسان برای انسانهای دیگر در اینجاست.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(321,'زندگی هر شخص زمانی شروع می شود که بتواند بیرون از خودش زندگی کند.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(322,'مسایل مهمی که با آن روبرو هستیم، با همان ذهنیتی که آنها را ایجاد کردیم، حل شدنی نیستند.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(323,'هیچ کس به خرد غایی نرسد، مگر آن را در خود جست و جو کند.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(324,'هیچگاه چیزی را خوب نمی فهمی، مگر اینکه بتوانی آن را برای مادربزرگت شرح دهی!((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(325,'تفاوت بین نابغه و کودن بودن در این است که نابغه بودن محدودیت های خودش را دارد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(326,'دو چیز را پایانی نیست: یکی جهان هستی و دیگری حماقت انسان. البته در مورد اولی مطمئن نیستم!((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(327,'انسانهای باهوش مسائل را حل می کنند، نوابغ آنها را اثبات می کنند.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(328,'اندیشه های بزرگ همیشه با مخالفت شدید و خشونت بارِ اندیشه های مردم عادی روبرو بوده اند.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(329,'من می خواهم بدانم (خدا) چگونه این جهان را آفریده است. علاقه ای به این یا آن پدیده ندارم. در (طیف) این یا آن عنصر لطفی نمی بینم. من می خواهم اندیشه های ( او ) را بدانم؛ مابقی، جزییات است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(330,'تنها زندگی ای ارزشمند است که صرف دیگران شود.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(331,'سه قدرت بر جهان حکومت می کند: 1- ترس 2- آزمندی 3- نادانی.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(332,'من به این مسأله باور راسخ [ =محکم و استوار ] دارم که تنها یک راه برای رهایی از شر سرمایه داری وجود دارد: برقراری اقصاد ِ سوسیالیستی به یاری آموزش و پرورشی درخور ِ تحقق اهداف سوسیالیستی. در چنین ساختاری ابزار تولید و فرآورده های آن به کل جامعه تعلق داشته و مصرف آن نیز طبق برنامه خواهد بود.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(333,'من نمی دانم انسانها با چه سلاحی در جنگ جهانی سوم با یکدیگر می جنگند، اما در جنگ جهانی چهارم، سلاح آنها سنگ و چوب و چماق خواهد بود.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(334,'باید شرم کنند، کسانی که بدون کمترین تأمل و تفکر از پدیده های معجزه آسای دانش و فن بهره می گیرند و سفیهانه از درک مفهوم هوشمندانه ی آن عاجزند؛ همانند گاوی که از لذت نشخوار گیاهان برخوردار، اما از دانش گیاه شناسی بی خبر است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(335,'همزمان با گسترش دایره ی دانش ما، تاریکی ای که این دایره را در بر می گیرد نیز گسترده می شود.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(336,'اگر انسانها در طول عمر خویش، میزان کارکرد مغزشان یک میلیونیوم معده شان بود، اکنون کره ی زمین تعریف دیگری داشت.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(337,'سعادت یعنی افتخار به گذشته و تلاش برای حال و امید به آینده.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(338,'شتابنده ترین راه دریافت عشق، بخشیدن آن به دیگران است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(339,'حقیقت تنها یک توهم است، حتی اگر روی آن پافشاری شده باشد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(340,'تنها از دو راه می توان زندگی کرد: نخست، گویی هیچ چیز معجزه نیست؛ دوم، گویی همه چیز معجزه است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(341,'اگر کسی احساس کند که هرگز در زندگی دچار اشتباه نشده، این بدان معنی است که هرگز به دنبال چیزهای تازه در زندگیش نبوده است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(342,'زندگی مثل دوچرخه سواری می مونه ... واسه حفظ تعادلت همیشه باید در حرکت باشی ... .((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(343,'غذایی که مردم را سالم نگه می دارد آن نیست که می خورند، بلکه آن غذایی است که خوب هضم می کنند.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(344,'هر کس بخواهد در باب دانش و حقیقت بر کرسی داوری بنشیند، خنده ی خدایان او را خراب خواهد کرد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(345,'کمتر کسی می تواند بدون دغدغه و در کمال متانت به بیان باورهایی بپردازد که با تعصبات محیط اجتماعی او یکسان نباشد. بسیاری از مردم شهامت داشتن چنین باورهایی را هم ندارند.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(346,'من هرگز به آینده فکر نمی کنم، چرا که خودش به زودی خواهد آمد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(347,'به یقین بر این باورم که پول نمی تواند کاروان بشری را به سوی پیشرفت هدایت کند؛ حتی اگر در دست فداکارترین فرد بشر برای این مقصود باشد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(348,'به جای اینکه سعی کنید مرد موفقیت باشید، سعی کنید مرد ارزشها باشید.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(349,'هیچ کاری برای انسان سخت تر از فکر کردن نیست.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(350,'زیباترین چیز برای تجربه کردن، کارها و پیشامدهای اسرارآمیز و شگفت‌آور است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(351,'دانش چیز شگفت انگیزی است، مشروط بر آنکه کسی مجبور نباشد از راه آن امرار معاش کند.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(352,'ما در پیشگاه خداوند، همگی به گونه‌ای برابر، خردمند و نادان هستیم.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(353,'تصور [ =انگاره ]، مهمتر از دانش است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(354,'نحوه افکار و اندیشه های انسان به گونه ای است که امکان دارد تنها خواندن یک کتاب، پایه اندیشه ها و افکار او را بر مبنای جدید یا در مسیر خاص قرار دهد و چه بسا ممکن است کتابی، مسیر سرنوشت میلیونها انسان را در جاده مخصوصی بیندازد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(355,'پی بردن به اسرار اتم برای من آسانتر از درک مکنونات [ =نهان داشته های ] یک زن است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(356,'از نظر من، معادله [ =برابری و توازن ] مهمتر از سیاست است؛ زیرا سیاست برای اکنون است و معادله برای ابد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(357,'به طبیعت عمیق عمیق نگاه کنید، آن زمان، همه ی مسائل را بهتر درک خواهید کرد.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(358,'الهام، تنها چیز مهم زندگی است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(359,'وظیفه ی ما این است که خود را از زندانی که به گستره ی دلسوزی و در آغوش کشیدن تمام آفریده های طبیعت و زیبایی های آن است، برهانیم.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(360,'داشتن ابتکار و درک هر مطلبی [ =جستاری ]، مهمتر از داشتن دانش است.((آلبرت انیشتین))',4,0,0,0,0,0);
INSERT INTO mysms VALUES(361,'دو چیز تفاوت فاحشی را بین انسان و حیوان به وجود می آورد : قدرت بیان و دروغگویی.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(362,'برای کسب چیزهای بزرگ، نه تنها باید تلاش کنیم، بلکه باید رویا هم داشته باشیم، نه تنها باید برنامه‌ریزی کنیم، بلکه همچنین باید باور هم داشته باشیم.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(363,'تنها تهیدستان هستند که نقد داد و ستد می‌کنند، نه از سر درست کاری بلکه از سر ناگزیری، چون کسی به آنها نسیه نمی‌دهد.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(364,'در نظام دیکتاتوری، دارایی یکی از امور برازنده است. در نظام دموکراسی، دارایی تنها امر برازنده است.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(365,'خنده، بهترین اسلحه جنگ با زندگی است.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(366,'دانش و آگاهی کلیدی است که تمام درها با آن باز می شوند.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(367,'دانش از خطا به دور است، اما دانشمندان همواره خطا می کنند.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(368,'مرگ هرگز دشوارتر از تولد نیست.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(369,'قوه تخیل و خیالبافی، مردمان حساس را به هنرپیشگی می کشاند و اشخاص گستاخ و بی پروا را قهرمان می سازد.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(370,'خنده طبیعی، فریاد موتور نیرومند بدن است.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(371,'هرکس خود را محور جهان می انگارد.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(372,'زن رسماً مربی مرد و پیراینده ی اخلاق اوست.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(373,'مادر، جلوه حق است، رضایت خدا است؛ باید خدا را خشنود نگاه داشت.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(374,'از پشت عینک طلا، دیدگان، درخشش ندارند.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(375,'مذهب خدمت بزرگی به عشق نمود زمانی که آن را گناه نامید.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(376,'زیبایی دنیا بسته به هنرهای زیبا است؛ هنر، مایه ی غرور و اسباب دلداری و آسایش خاطر است.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(377,'تا زمانی که به قدرت نرسیده ای فرمانبردار باش و چون به آن رسیدی، فروتن.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(378,'اندیشه های خوب، تراویده ی روح پاک است و بوی خوش، تراویده ی گل خوشبو.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(379,'اگر پنجاه ملیون نفر به یک چیز احمقانه باور داشته باشند، آن چیز همچنان احمقانه است.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(380,'هرگز به احساساتی که در برخورد اول با کسی پیدا می کنید نسنجیده اعتماد نکنید.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(381,'ما ساخته و پرداخته کسانی می شویم که دوستمان داشته اند.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(382,'خطا کردن یک کار انسانی است، اما تکرار آن یک کار حیوانی.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(383,'اگر همین افکار و اندیشه پریشان فلاسفه نبود، هم اکنون بشر مانند هزاران سال پیش با ترس و وحشت در غارها زندگی می کرد.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(384,'هرگز کتابی به کسی امانت ندهید، چون به شما برگردانده نمی شود. من تمام کتابهای کتابخانه ام امانتی است که از دیگران گرفته ام.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(385,'از ویژگیهای طبیعی آدمی است که عاقلانه و منطقی فکر می کند، اما پوچ و بی معنی رفتار می کند.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(386,'انسان در کتاب از چیزهایی با خبر می شود که دیدن خود آنها امکان پذیر نیست؛ زیرا عین آنها فرسنگها و یا قرنها و سالها [ از ما ] فاصله دارند.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(387,'نقص مهم امپراتوریهای بزرگ دنیا این بود که قوانین را مغزهای قوی برای مردان قوی می نوشتند و ضعف بشر را در نظر نمی گرفتند.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(388,'هیچ عیب ندارد که دانا و فیلسوف باشید، ولی اگر هستید، طوری رفتار کنید تا کسانی که با شما حرف می زنند، تصور کنند مانند خودشان ساده اید.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(389,'دانش، کلیدی است که همه ی دربها به آن باز می شود.((آناتول فرانس))',5,0,0,0,0,0);
INSERT INTO mysms VALUES(390,'ما باید بتوانیم نشانه‌ها را حتی در دوردست‌ها ببینیم و دریابیم.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(391,'ضرورت‌هایی [=بایستگی‌] که پایه و بنیان یک پیشه را شکل می‌دهد، می‌تواند دنیا را دگرگون و غنی سازد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(392,'به راستی، هیچ چیز هرگز نخواهد توانست جای همسفر از دست رفته را بگیرد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(393,'کسی نمی‌تواند یک دوست کهن را بیافریند و از نو بسازد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(394,'شکوه و بزرگی یک پیشه، شاید پیش از هر چیز، در یکی شدن روح‌هاست و این، یک شکوه راستین است؛ شکوه مناسبات روابط انسانی.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(395,'فقط گامی که به پیش برمی‌داریم، می‌تواند رهایی‌بخش باشد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(396,'اگر انسان در طوفان رویدادها گرفتار شود، ترسش می‌ریزد. تنها ناشناخته‌هاست که لرزه به دل‌ها می‌افکند و سبب ترس و هراس آدمی می‌شود.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(397,'کار بزرگ مردمان بخشنده این است که با همه‌ی توان خود، افق‌های بیکران را در برمی‌گیرند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(398,'بی‌گمان، انسان بودن یعنی متعهد [=پایبند] بودن.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(399,'آنهایی که به سبب پیشرفت تکنیکی زیاد بشریت، دچار ترس و هراس شده‌اند، هدف و وسیله را با هم اشتباه گرفته‌اند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(400,'کسی که فقط با امید دستیابی به نعمت‌های مادی، دست به مبارزه می‌زند، خویشتن را از موهبتی [=دَهِش] که شایسته‌ی زندگی‌ست، بی‌بهره می‌سازد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(401,'بر سفره‌ای که در زیر درخت سیبی پهن شده باشد، جز سیب بر آن فرود نخواهد آمد و سفره‌ی گسترده‌ی زیر ستارگان، فقط پذیرای غبارات کوکب‌هاست.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(402,'این فاصله نیست که اندازه‌ی دوری را تعیین می‌کند؛ دیوار باغ خانه‌ی ما می‌تواند اسرار بیشتری را نسبت به دیوار چین در خود نگه دارد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(403,'فصل‌ها می‌گذرد و ما در عصرها مانده‌ایم... و نعمت‌های زمین، همچون شن ظریف صحرا که از میان انگشتان می‌لغزد، از کف ما بیرون می‌رود.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(404,'در صحرا، آدم‌ها همیشه آزاد هستند و از گنج‌های آشکار و دیدنی، جانبداری نمی‌کنند؛ چرا که صحرا برهنه است و فقط می‌توان از یک گستره‌ی پنهان و ناپیدا جانبداری کرد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(405,'دوست داشتن، تنها نگریستن به یکدیگر نیست، بلکه با هم نگریستن در یک جهت است.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(406,'بکوشید پیش از آنکه کسی را داوری کنید، او را درک کنید.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(407,'راستی و درستی برای انسان، همان چیزی‌ست که از او یک مرد می‌سازد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(408,'راستی و درستی، زبانی‌ست که همگان را آزادگی می‌بخشد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(409,'هنگامی که به نقش خود- هر اندازه هم مبهم باشد- خِرد کافی پیدا کنیم، فقط در آن صورت است که احساس شادمانی خواهیم کرد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(410,'بسیاری از کسانی که مدت‌ها با عشقی بزرگ سر کرده‌اند و سپس از آن، بی‌بهره گشته‌اند، بیشتر، از نجابت شگفت خویش به ستوه می‌آیند و آنگاه فروتنانه و با عشقی ساده و عادی، نیک‌بختی خود را می‌سازند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(411,'با مرگ یک انسان، دنیای ناشناسی با او می‌میرد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(412,'در تنهایی مرگ، هیچ چیز از چشم نمی‌افتد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(413,'تا زمانی که در این دنیا گیر نیفتیم، نمی‌توانیم آن را در گمان آورریم و پیش‌بینی کنیم.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(414,'ضرورت [=بایستگی] را نمی‌توان پیش‌بینی کرد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(415,'درد غربت، گرایشی‌ست که کس نمی‌داند از چیست… هوس است و میل؟… هرچه هست، برای بیان آن، هیچ واژه‌‌ای یافت نمی‌شود.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(416,'آنچه انسان را رهایی می بخشد، برداشتن گامی است که در پی آن، گامهای دیگر هم برداشته شود.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(417,'بالاترین خوشی ها در شور و شوقی است که انسان را به ماجراجویی ها و پیروزیهای بزرگ و فعالیتهای نوآورانه وادار می کند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(418,'عشق آن نیست که به هم خیره شویم، عشق آن است که هر دو به یک سو بنگریم.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(419,'هر آدم بزرگی هم روزی روزگاری بچه ای بوده؛ گیرم کمتر کسی از آنها این را به یاد می آورد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(420,'بزرگترها اگر به خودشان باشد هیچ وقت نمی توانند از چیزی سر در بیاورند. برای بچه ها هم خسته کننده است که همین جور مدام هر چیزی را به آنها توضیح بدهند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(421,'بچه ها باید نسبت به آدم بزرگها گذشت داشته باشند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(422,'دنیا برای پادشاه ها به گونه عجیبی ساده شده و تمام مردم تنها یک مشت رعیت به حساب می آیند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(423,'حق داریم توقع داشته باشیم چون اوامرمان عاقلانه است.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(424,'محاکمه کردن خود از محاکمه کردن دیگران خیلی مشکلتر است. اگر توانستی در مورد خودت قضاوت درستی بکنی، معلوم می شود یک فرزانه تمام عیاری.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(425,'برای خودپسندها دیگران تنها یک مشت ستایشگرند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(426,'خودپسندها جز ستایش خودشان چیزی را نمی شنوند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(427,'چیزی که زیبا باشد بی گفت و گو مفید هم هست.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(428,'شما را به خدا بیخودی وقت خودتان را سر این جریمه مدرسه به هدر ندهید؛ این کار بیهوده ای است.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(429,'به خود می گویم ستاره ها برای این روشن هستند که هر کسی بتواند یک روز مال خودش را پیدا کند!((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(430,'آدمها دیگر برای سر درآوردن از چیزها وقت ندارند. همه چیز را همین جور حاضر آماده از دکانها می خرند. اما چون دکانی نیست که دوست معامله کند، آدمها مانده اند بی دوست.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(431,'سرچشمه ی همه سوءتفاهم ها زیر سر زبان است.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(432,'جز با چشم دل هیچ چیز را چنان که باید نمی شود دید؛ نهاد و گوهر را چشم سر نمی بیند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(433,'ارزش گل تو به قدر عمری است که به پایش صرف کرده ای.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(434,'آدمیزاد هیچ وقت جایی را که هست خوش ندارد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(435,'تنها بچه ها هستند که می دانند پيِ چه می گردند. بچه ها هستند که کلی وقت صرف یک عروسک پارچه ای می کنند و عروسک برایشان آنقدر اهمیت پیدا می کند که اگر یکی آن را از آنها کش برود می زنند زیر گریه.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(436,'حتی اگر آدم دم مرگ هم باشد داشتن یک دوست، عالی است.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(437,'چیزی که کویر را زیبا می کند این است که یک جایی یک چاه قایم کرده است.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(438,'چه خانه باشد، چه ستاره، چه کویر، چیزی که اسباب زیبایی اش می شود نامرئی است.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(439,'آدمها!... می چپند توی قطارهای تندرو، اما نمی دانند دنبال چه می گردند؛ این است که بنا می کنند دور خودشان چرخک زدن.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(440,'عشق، تمرین نیایش است و نیایش، تمرین سکوت.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(441,'زندگی به ما آموخته که عشق در نگاه خیره به یکدیگر نیست، بلکه در یک سو نگریستن است.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(442,'معنای هر چیز در خودش نیست، بلکه در نوع برداشت ما از آن نهفته است.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(443,'شما هیچ به گل من نمی مانید. شما هنوز هیچ چیز نشده اید. کسی شما را اهلی نکرده و شما نیز کسی را اهلی نکرده اید. برای شما نمی توان مُرد. البته گل سرخ من هم در نظر یک رهگذر عادی به شما می ماند، اما او به تنهایی از همه ی شما برتر است؛ زیرا او گل سرخ من است...((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(444,'یک انسان، تنها می تواند با قلبش به درستی ببیند؛ چیزی که لازم و ضروری باشد، با چشم دیده نمی شود.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(445,'نگاهت رنج عظیمی است، وقتی به یاد من می آورد که چه چیزهای زیادی را هنوز به تو نگفته ام.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(446,'بینش حقیقی در قلب صورت می گیرد؛ امور اساسی با چشم دیده نمی شوند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(447,'انسان در مزه‌ی گیرای ماجراهای بلندپایه و پیروزمندانه و در کارهای نوآورانه است که، شادی نهایی را می‌یابد.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(448,'آنان که خرسندی بیشتری دارند، آرزوی کمتری دارند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(449,'آن که در درون آسایش دارد، همه ی دنیا را در آسایش می بیند.((آنتوان دوسنت اگزوپری))',6,0,0,0,0,0);
INSERT INTO mysms VALUES(450,'کامیابی، کلید دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(451,'من زمانی کامیاب هستم و توانگر می‌شوم که احساس کنم دارم کاری فراگیر و جهانی انجام می‌دهم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(452,'هرچه بیشتر ببخشید، بیشتر دریافت می‌کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(453,'اگر پول داشته باشید، به راه نادرست نرفتن، آسانتر است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(454,'ارزش هایی که برای خود برمی گزینید، بر همه ی تصمیم های شما در زندگی اثر می گذارند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(455,'افرادی دارای قدرت هستند که فلسفه ی زندگی و کنش های آنان یکی باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(456,'باید بدانیم، آنچه همچون نیرویی مغناطیسی، جهت زندگی ما را آشکار می کند، قدرت جادویی ارزشها است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(457,'ارزشها، نیروهایی هستند که در پیش روی ما قرار دارند و ما را به گرفتن تصمیم هایی وا می دارند که جهت و هدف نهایی زندگی ما را روشن می سازند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(458,'تنها راه دستیابی به نیک بختی درازمدت آن است که بر مبنای بزرگترین آرمانهای خود زندگی کنیم، یعنی هدف راستین خود را بشناسیم و بر پایه ی آن به کنش بپردازیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(459,'آنچه در زندگی بیشتر مردم، مایه ی اندوه است، این است که بیشتر، می دانند در زندگی خود خواهان چه چیزهایی هستند، ولی به درستی نمی دانند که چه کسی می خواهند باشند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(460,'تنها راه نیک بختی و خشنودی جاودانی این است که بر پایه ی ارزشهای راستین خود زندگی کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(461,'برای اینکه به خوشبختی راستین برسید، باید تفاوت میان وسیله و هدف را بدانید و به ناگزیر در جهت هدف یا ارزش پایانی به جنبش درآیید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(462,'اگر سلسله مراتب ارزشهای خود را بشناسید، دلیل کنش هایتان برای خودتان روشن تر می شود و می توانید در زندگی، ثبات بیشتری داشته باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(463,'کسانی که به مجردِ برخورد با سختی، کار را رها می کنند، هرگز به جایی نمی رسند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(464,'باید گفت که چه در درون شرکتها و چه در روابط بین افراد، مدیریت از هر چیزی مهمتر است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(465,'افراد، برای فرار از رنج، تلاش بیشتری می کنند تا برای کسب لذت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(466,'بزرگترین خشنودی های زندگی، زمانی به دست می آید که انسان بداند در راستای باورهای راستین خود، رفتار کرده است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(467,'بیشتر ما برای باز شناختن بهای هر چیز، قانونی داریم و آن اینکه، خواستار امور کمیاب هستیم و برای امور بسیار شگفت انگیزی که پیوسته در پیرامون مان رخ می دهد، ارزشی قائل نیستیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(468,'برای احساس شادکامی و خوشبختی، لزومی ندارد که پیشامد ویژه ای رخ دهد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(469,'می توانید هم اکنون، احساس شادی کنید، بدون اینکه هرگز به دلیل ویژه ای نیاز باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(470,'اگر زندگی خود را به گونه ای بنا کنیم که خوشبختی ما وابسته به اموری بیرون از دسترس ما باشد، در آن صورت، احساس رنج خواهیم کرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(471,'اینکه احساس کنید، ارزشهای مورد نظرتان برآورده می شود یا نه، بستگی به قوانین فردی شما دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(472,'زندگی، پدیده ای دگرگون شونده است، از این رو، قوانین ما باید به گونه ای باشد که به ما اجازه ی سازگاری، رشد و لذت را بدهد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(473,'بیشتر کامیابی من ناشی از این باور است که می دانم توانایی انجام بسیاری از کارها را دارم، هرچند که آن کارها را پیش تر انجام نداده باشم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(474,'اینکه دیگران، همه ی باورهای شما را بپذیرند، امری، نشدنی است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(475,'کسانی که خود را همیشه خوب و خوش احساس می کنند، رفتارشان با دیگران بهتر است و به عنوان یک فرد انسان، از توانایی های خود بیشتر بهره می گیرند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(476,'اگر کامروا نشوید، زندگی، خود به خود و به میزان کافی، شما را دچار رنج خواهد ساخت و نیازی نیست که با ایجاد قوانین سخت، پیوسته خود را دچار رنج و اندوه کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(477,'هر کسی دارای قوانین و ارزش های ویژه ای است که ممکن است با قوانین و ارزش های شما متفاوت [ =دیگرگون ] باشد؛ ارزش های آنها نه بهتر است و نه بدتر.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(478,'قوانین فردی، بر همه ی کنش های ما فرمانروایی می کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(479,'به یاد داشته باشید که هر گاه از دست کسی خشمگین و یا برآشفته شدید، این، قوانین فردی شما است که ناآرام تان کرده است، نه رفتار طرف مقابل.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(480,'اگر قواعد بازی را ندانید، امکان بُرد وجود ندارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(481,'اگر به دیگران، قوانین خود را نگویید، نباید امید داشته باشید که آنان بر پایه ی قوانین شما رفتار کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(482,'اگر نخواهید دست کم، برخی از قوانین دیگران را بپذیرید و پاس بدارید، نباید امیدوار باشید که دیگران، بر پایه ی قوانین شما رفتار کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(483,'اگر قرار باشد که در برابر هر چیزی، واکنش نشان دهیم، پیوسته از فشار روانی، رنج خواهیم برد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(484,'خودباوری انسان، برآمده از این است که خود را بر شرایط زمانه چیره ببیند، نه اینکه شرایط بر او چیره باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(485,'قوانین فردی خود را به گونه ای سامان دهید که به شیوه ها و راههای بسیار انبوه و گوناگون، احساس عشق و محبت کنید و لذت بردن را برای خویش بسیار آسان سازید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(486,'اگر قوانین دیگران را ندانید، بی شک، بازی را خواهید باخت، زیرا ناگزیر، دیر یا زود، آن قوانین را زیر پا خواهید گذاشت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(487,'یادتان باشد که نیروبخش ترین قانون این است: با وجود هر پیشامدی، از زندگی خود لذت ببرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(488,'اگر بخواهیم سبب رفتار انسانها را بدانیم، بررسی مهمترین و کارسازترین تجارب مرجع زندگی آنها، بی گمان، راهنمای خوبی است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(489,'هرچه تجارب زندگی ما بیشتر و کیفیت آنها بالاتر باشد، میزان حق گزینش ما بیشتر می شود و بهتر می توانیم معنای [ =چَم ] هر چیز را دریابیم و توان خود را برای انجام هر کار بسنجیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(490,'اگر بر این باورید که فرد باهوشی هستید، سبب آن، این است که چارچوبهای مرجع روشنی را در ذهن خود، پویا می کنید که این احساس اطمینان را نیرو می بخشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(491,'خوبی یا بديِ هر چیزی نسبی است و بستگی به این دارد که با چه چیز سنجیده شود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(492,'نیروی انگاره ی ما، ده بار توانمندتر از نیروی اراده است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(493,'اگر قدرت انگاره ی خود را از قید و بند رها کنیم، خودباوری و بینشی در ما ایجاد می شود که محدودیت های گذشته مان را در هم می شکند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(494,'اگر کسی در هر یک از زمینه های زندگی، بهتر از ما واکنش نشان دهد، ساده ترین دلیل اش آن است که راهی بهتر برای داوری و معنا [ =چَم ] کردن امور دارد و می داند که در هر مورد، چه واکنشی نشان دهد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(495,'کلید بنیادی داوری عالی این است که در هنگام ارزیابی امور و رویدادها، دارای روحیه ی نیرومند و حالت فکری و عاطفی مثبت باشیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(496,'چنانچه می‌خواهید در هر کاری به مهارت، چیرگی و استادی برسید، می‌بایست به درستی بدانید چه می‌خواهید و دریابید که برای دستیابی به آن، به چه چیزی نیاز دارید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(497,'اگر خواسته‌ی خود را بیان کرده باشید، بی‌گمان، به پاسخ خواهید رسید، ولی زود ناامید نشوید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(498,'بسیار پیش می‌آید که در زندگی، تجربه و احساس خوبی پیدا می‌کنید، ولی هرگز از خودتان نمی‌پرسید که چه امری، این احساس خوب و خوشایند را ایجاد کرد!((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(499,'هرچه چیزی را بیشتر اندازه‌گیری کنید و مقادیر کمّی آن را روشن کنید، در مورد آن، خودآگاه‌تر و هوشیارتر می‌شوید و هرچه خودآگاه‌تر شوید، آن تجربه‌ی احساسی را بهتر و خوشایندتر می‌کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(500,'حتی هراسناک‌‌ترین و دردناک‌ترین تجربه‌ها و احساسات را می‌توانید با میل و اراده‌ی خودتان دگرگون سازید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(501,'بیشتز زمانها ناچاریم کارهایی انجام دهیم که زیاد از آن خوشمان نمی‌آید، ولی می‌توانیم احساس خود را دگرگون سازیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(502,'در زندگی نمی‌توانید بی‌آنکه به راستی بخواهید، از بسیاری چیزها سرخوش باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(503,'به یاد داشته باشید، معنا [ =چَم ] و مفهوم زندگی، چیزی است که برمی‌گزینید و کنترل آن، همیشه در دست شماست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(504,'هریک از ما در زندگی، حق گزینش داریم و می‌توانیم اندیشه‌ها و احساسات خود را برگزینیم. چنانچه آگاهانه به گزینش نپردازیم، بهای آن را خواهیم پرداخت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(505,'همه‌ی پیشرفت‌ها و دستاوردهای انسان، نتیجه‌ی پیوستگی در تلاش و نیز پایبندی اوست؛ آن هم تلاشی هدفمند و پایدار.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(506,'همه‌ی ما این توانایی را داریم که با دشواری‌ها و بازدارنده‌ها برخورد کنیم، بر آنها پیروز شویم و بر ترس‌های خود چیره گردیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(507,'چنانچه انسانها همواره در حالت احساسی تردید، دودلی و دوگانگی بودند، به ندرت می‌توانستند به کامیابی درازمدت برسند یا به چیزی که به راستی ارزشمند است دست یابند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(508,'ترس، شک و تردید و نبود اطمینان و باور، هرگز نتایج چندان گهرباری ندارند و نخواهند داشت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(509,'اطمینان و باور، چیزی نیست که آن را در بیرون از خود بجوییم و به سوی آن، دست دراز کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(510,'همه‌ی رویاهای بشر دست یافتنی هستند و به جرأت می‌توان گفت، رویایی وجود ندارد که نتوانیم به آن دست یابیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(511,'بسیاری از ما در زندگی شخصی با چیزی به مراتب کمتر از آنچه سزاوارش هستیم، زندگی می‌کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(512,'چنانچه به راستی خواهان آن باشید که به خودباوری و بی‌گمانی راستین برسید، بی‌شک راه آن را خواهید یافت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(513,'تا زمانی که جستاری سبب دگرگونی رفتاری شما نشده است، هنوز آن را به خوبی فرا نگرفته‌اید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(514,'هیچ چیز در زندگی معنا ندارد، مگر معنایی که شما به آن بدهید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(515,'در جستجوی قهرمان نباشید، بلکه خود قهرمان بشوید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(516,'به هیچ دسته کلیدی اعتماد نکنید بلکه کلید سازی را فرا بگیرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(517,'هدف های بزرگ، انگیزه های بزرگ ایجاد می کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(518,'بزرگترین سد در برابر صمیمیت این است که فکر کنید دیگران نیز جهان را مانند شما نمی بینند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(519,'تصمیمات ما هستند که سرنوشت ما را تعیین می کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(520,'گام نخست پیروزی، آگاهی است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(521,'راه موفقیت، همیشه در حال ساخت است؛ موفقیت پیش رفتن است، نه به نقطه پایان رسیدن.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(522,'شاید به راستی تنها دشواری بزرگ و ناگشودنی، آن است که هرگز دشواری‌ نداشته باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(523,'چنانچه هنوز با دشواری‌هایی دست و پنجه نرم می‌کنید، نشانه‌ی آن است که زنده، تندرست و نیرومند هستید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(524,'آنچه شادمانی و خوشبختی شما را در زندگی رقم می‌زند، کامیابی‌ها و پیروز‌هایتان نیست. این، هویت، منش و ماهیت بنیادی شماست که باارزش است و می‌توانید به آن ببالید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(525,'مهم نیست که چه کار کرده‌اید، مهم این است که خود را چگونه ساخته‌اید و چگونه انسانی شده‌اید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(526,'می‌بایست به روشی زندگی کنید و به گونه‌ای با مسائل و دشواری‌هایتان روبرو شوید که در پایان زندگی، با افسوس نگویید، ای کاش چنین و چنان کرده بودم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(527,'چنانچه با مسائل و دشواری‌هایتان رویرو نشوید، در پایان زندگی، احساس خواهید کرد که تنها یک دهم از آن را زندگی کرده‌اید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(528,'چنانچه از دشواری‌ها نهراسید و به پیشباز آنها بروید، سربلند خواهید بود؛ آن هم با این آگاهی که از زندگانی، بهره‌ی کافی جسته و سراپا و با همه‌ی وجود، زنده بوده‌اید و زندگی کرده‌اید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(529,'زمانی که با دشواری‌‌ها و مسائل خود، برخورد مناسب می‌کنید و آنها را به فرصت‌هایی جهت رشد و دگرگونی شخصی بدل می‌سازید، زندگانی خود را به سفری زیبا، پر ماجرا و هیجان‌انگیز بدل خواهید کرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(530,'چنانچه نتوانید مفهوم و هدف ویژه‌ای برای زندگانی خود پیدا کنید، احساس خرسندی، شادمانی و خوشبختی راستین از زندگانی‌تان رخت برخواهد بست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(531,'شاید زمان آن رسیده باشد که دوباره بر روی چَم، مفهوم و هدف از زندگانی خود تمرکز و توجه کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(532,'هریک از ما انسان‌ها برای زندگی خود به دلیل و هدفی نیاز داریم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(533,'کامیابی‌ها و پیروزی‌ها هرگز نمی‌توانند خلاء روحی و روانی شما را پر کنند. ممکن است به گونه‌ای موقت سبب شادمانی‌ شما شوند، ولی هرگز نمی‌توانند در درازمدت، خوشبختی و شادمانی راستین را به شما هدیه کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(534,'چنانچه کامیابی‌ها و دستاوردهای شما به زندگی‌تان مفهومی نبخشند، همه بیهوده خواهند بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(535,'بی‌شک زندگی ما انسانها هدفی دارد. بدون باور پابرجا به این چَم، مفهوم و هدف، دیگر انگیزه‌ای برای زندگی باقی نمی‌ماند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(536,'آنچه سرانجام به زندگی انسان شکل می‌بخشد و سرنوشت او را رقم می‌زند، اراده‌ی او برای انجام کارهاست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(537,'احساسات و عواطف شما به نوبه‌ی خود، زندگی و سرنوشت و نقشی را که در آن ایفا خواهید نمود، رقم می‌زند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(538,'ممکن است خداوند گهگاهی در برخی از رویدادها و امور دیر کند، ولی بدان چَم نیست که به ما پشت کرده و ما را برای همیشه فراموش کرده است. مگر آنکه خودمان جا بزنیم و ناامید شویم و برای همیشه، به خودمان پشت کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(539,'چنانچه کسی را باور نکنید، خواهید مُرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(540,'در این زندگانی، هر چیزی که به‌ راستی ارزشمند باشد، مستلزم ایمان و باور پابرجاست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(541,'چنانچه شکاک و بدبین باشید، هرگز نمی‌توانید پیشه‌ای را راه‌اندازی کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(542,'بدون ایمان و باور پابرجا، نمی‌توانید زندگی کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(543,'سوءتفاهم میان افراد، هنگامی رخ می‌دهد که، در دو دنیای بسیار دیگرگون زندگی کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(544,'واژه‌ها و گفته‌ها، همواره نقش بسیار مهمی در احساسات ما دارند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(545,'هیچ‌کس دوست ندارد درباره‌اش پیشداوری کنند یا سرزنش شود، حتی خودتان؛ پس بکوشید هرگز چیزی را که برای خود نمی‌پسندید، به دیگران نیز روا ندارید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(546,'دشواری در این است که بیشتر ما فریادی را که به چَم درخواست کمک است، با فریادی دیگر که آن هم به چَم درخواست کمک است، پاسخ می‌دهیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(547,'چنانچه می‌خواهید روابطی پایدار و ماندگار داشته باشید، می‌بایست درباره‌ی هویت و ماهیت بنیادی نزدیکان و افراد زندگی خود یک تصمیم قلبی بگیرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(548,'چنانچه دیگران احساس خوبی نسبت به شما نداشته باشند، در درازمدت، شما نیز نمی‌توانید احساس خوبی نسبت به آنها و خودتان داشته باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(549,'به یاد داشته باشید، پیش از آنکه بکوشید با کسی ارتباط بهتری داشته باشید، با خودتان بهتر ارتباط برقرار کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(550,'چه بخواهید و چه نخواهید، می‌بایست با دشواری‌ها روبرو شوید؛ آنها همیشه وجود داشته و خواهند داشت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(551,'مردم، هنگامی بیشتر رشد می‌کنند که در زندگی، درد و رنج بیشتری را تاب آورده باشند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(552,'چنانچه شما نیز همان کارهایی را بکنید که دیگران می‌کنند، به همان چیزهایی خواهید رسید که دیگران رسیده‌اند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(553,'هنگامی که با آسودگی و باور، به دیگران اجازه می‌دهید به جای شما بیندیشند و تصمیم بگیرند، همواره ناگزیرید برای آن، بهایی بپردازید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(554,'اطمینان، باور و قطعیت، مانند تیغ دولبه‌ای است که می‌تواند نتایج وارونه و ناسازگاری ایجاد کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(555,'تا زمانی که کیفیت اندیشه‌ها و ذهنیات خود را دگرگون نکرده‌اید، هرگز نمی‌توانید کیفیت زندگی‌تان را دگرگون سازید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(556,'ایمان به همراه تلاش و تکاپو می‌تواند همه‌ی خواسته‌ها و رویاها را برآورده سازد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(557,'هرگز نمی‌توانید از پیش برای همه چیز آماده باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(558,'چنانچه چشم‌داشت‌ ما از دیگران، از پیش، روشن باشد، بردن در بازی زندگی برای همه‌ی ما ساده‌تر خواهد شد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(559,'هرگاه اراده کنید، می‌توانید با دوستانه رفتار کردن، احساس دوست داشتنی بودن نمایید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(560,'هنگامی که خشمگین و برآشفته هستید، در دشواری خود به دام می‌افتید. هرچه بیشتر خشمگین شوید و دست و پا بزنید، بیشتر در باتلاق دشواری‌ها فرو خواهید رفت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(561,'اگر پیوسته روی دشواری و نه راهکار تمرکز کنید، تنها دشواری‌ها را دیده و راهکارها را نخواهید یافت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(562,'هرگز نمی‌بایست از دشواری‌ها روی‌گردان باشیم. شاید بهتر باشد گاهی از وجود دشواری، هیجان‌زده شویم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(563,'من بر این باورم، دشواری، تنها درسی است که از ما می‌خواهد هرچه زودتر و باشتاب‌تر، آن را بیاموزیم تا دیگر رفوزه نشویم و ناگزیر نباشیم آن را تکرار کینم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(564,'تنها یک راه برای خوشبختی وجود دارد؛ اینکه به روشی با خودتان و دیگران ارتباط برقرار کنید که برای شما، توانایی، شادمانی، خودباوری، شایستگی و توانمندی به همراه داشته باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(565,'می‌بایست همواره مراقب باشید که نحوه‌ی برقراری ارتباط با دیگران به گونه‌ای باشد که دگرگونی مورد پسندی به وجود آورد و از ایجاد هر گونه احساس بد و منفی در دیگران، پیشگیری کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(566,'خندیدن، سبب خواهد شد که مرکز توجه خود را دگرگون سازید و زنجیره‌ی رفتاری را که همیشه به یک شکل ثابت و دگرگونی‌ناپذیر تکرار می‌شد، بشکنید و دگرگون سازید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(567,'هنگامی که دو نفر با هم رابطه ایجاد می‌کنند و دو نظر، باور و یا نگرش بسیار دیگرگون نسبت به امری مشترک دارند، از یکدیگر دوری می‌گزینند و نمی‌توانند به سوی دنیاهای یکدیگر پل بزنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(568,'چنانچه خواهان روابطی پسندیده و کامروا با دیگران هستید، نخستین باور هسته‌ای و مرکزی که می‌باید داشته باشید، آن است که بدانید هویت و منش راستین مردم با رفتارها، پاسخ‌ها و واکنش‌های آنها یکی نیست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(569,'هرگاه با کسی که به خشونت با شما رفتار کرده است، با گرمی و مهربانی و نیکویی رفتار می‌کنید، این رفتار خوب شما در یاد او می‌ماند و در روابط آینده با شما به نرمی رفتار خواهد کرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(570,'آنچه روابط را پایدار نگه می‌دارد، آن است که با وجود نگرش‌ها و باورهای گوناگون، بتوانید به گونه‌ای با هم ارتباط برقرار کنید و پلی میان دنیاهای خود بزنید که به روابط شما آسیبی نرساند و آن را پایدار و ماندگار نگه دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(571,'چنانچه همه‌ی مسئولیت‌های خود را می‌دانستید و به گردن می‌گرفتید و در زمان مناسب نیز آنها را انجام می‌دادید، نیازی نداشتید که از دست کسی، برآشفته یا خشمگین شوید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(572,'مردم همواره متوجه نیت و هدف خیر شما و تلاش‌های از روی راستی شما برای برقراری ارتباط سالم و درست با خودشان هستند و همه چیز را به خوبی می‌بینند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(573,'شادمان بودن، آن هم زمانی که شرایط خوب است، کار چندان سخت و دشواری نیست؛ همه در این شرایط می‌توانند شادمان باشند و لبخند بزنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(574,'زمانی که همه چیز خوب است و در مورد همه چیز، آسوده دل هستید، گوش‌تان بدهکار نیست، ولی هنگامی که در زندگی با دشواری روبه‌رو شدید یا درد و رنجی به سراغتان آمد، آنگاه گوش شنوا پیدا می‌کنید و پذیرش‌تان برای باورها و اندیشه‌های تازه بالاتر می‌رود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(575,'به هنگام رویارویی و روبرو شدن با دشواری‌ها، اندیشیدن را آغاز می‌کنیم و پوست می‌اندازیم و آب دیده می‌شویم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(576,'رشد و پیشرفت و آموختن، همواره، نتیجه و پیامد ژرف‌نگری و اندیشیدن است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(577,'بدون سختی‌ها، نمی‌توان رشد کرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(578,'همواره در آینده با شرایطی روبرو خواهید شد که در آنجا آموخته‌هایتان به شما کمک خواهد کرد و سودمند خواهد بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(579,'شگفت‌انگیز است که چه اندازه از دشواری‌ها و سختی‌های زندگی می‌آموزید و رشد می‌کنید، بی‌آنکه خودتان بدانید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(580,'گاهی، درد و رنج، بهترین دوست و آموزگار ماست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(581,'گاهی درد و رنج، چیزهایی به ما می‌آموزد که شادی و شادمانی هرگز نمی‌تواند بیاموزد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(582,'زمانی که شرایط آن گونه است که می‌خواهیم، تنها شادمان هستیم، ولی رشد نمی‌کنیم و نمی‌آموزیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(583,'به تجربه در زندگی دریافته‌ام که برای یک‌یک تلاش‌ها و دردسر‌های کوچک، پاداش‌هایی بسیار بزرگ در راه است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(584,'همواره در پی میان‌بُر بودن، نتیجه‌ی عکس می‌دهد. یعنی برخی از میان‌بُرها به راستی میان‌برهایی به سوی مرگ، بیماری و غم هستند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(585,'خداوند هرگز مشکلی را سر اهمان نمی‌گذارد، مگر آنکه بداند، شایستگی و گنجایش رویارویی با آن و توانایی از بین بردن آن را نیز داریم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(586,'در دل هر سختی و دشواری، دانش، سود و فرصت‌های باارزشی برای رشد و آموختن وجود دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(587,'به یاد داشته باشید، باور، اطمینان و قطعیت، یک مهارت و توانایی است که هرچه بیشتر آن را تمرین کنید، هر بار می‌توانید با سادگی بیشتری به آن دست یابید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(588,'بزرگترین درد و رنج‌های زندگی، درد و رنج‌هایی است که از درون خودمان و به سبب زیر پا گذاشتن استانداردها و قوانین‌مان احساس می‌کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(589,'هر بار که در زندگی خود برآشفته شده و از کسی رنجیده‌اید، رنجش و برآشفتگی شما به سبب رفتار یا کارهای آن شخص نبوده، بلکه سبب برآشفتگی و درد و رنج‌تان، تفاوت قوانین شما با آن فرد بوده است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(590,'زمانی که از استانداردها و قوانین دیگران آگاه می‌شوید، می‌توانید با کامیابی بیشتری، نیازها و خواسته‌هایشان را برآورده سازید و روابط بهتری را پایه‌ریزی کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(591,'اگر استانداردها و قوانین خود را با دیگران در میان بگذارید، آنها شناخت بیشتری از شما پیدا می‌کنند و در برآورده ساختن نیازها و خواسته‌هایتان، کامیا‌ب‌تر خواهند بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(592,'چنانچه می‌خواهید به کسی احساس عشق و دوست داشتنی بودن دهید، نباید بر پایه‌ی استانداردها و قوانین خودتان به کنش بپردازید، می‌بایست خواسته‌های او را برآورده سازید، نه خواسته‌های خودتان را.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(593,'زمانی که از آنچه ممکن است پیش بیاید، می‌ترسیم، آنچه را پیش آمده و وجود دارد (هر چند زیاد خوشایند هم نباشد)، به سادگی تاب می‌آوریم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(594,'بدانید که تفاوت استانداردها و قوانین شما با استاندارد و قوانین دیگران، هرگز به اندازه‌ی پایبندی، وفاداری، رشد و دگرگونی شخصی، عشق، محبت و دوستی میان شما و دیگران اهمیت ندارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(595,'آن گونه خودباوری‌ که برخاسته از احساس شایستگی، توانمندی و توانایی بی اندازه باشد، هرگز در درازمدت پابرجا و ماندنی نخواهد بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(596,'آنچه تفاوت میان زندگی ما و دیگران را رقم می‌زند، برخورد، نگرش و منش‌های ما در برخورد با دشواری‌هاست، نه خود دشواری‌ها یا اندازه و کوچک و بزرگی آن.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(597,'شایستگی‌ها و توانایی‌های شما در کمک و یاری رساندن به دیگران، سهیم شدن آنچه دارید با دیگران، شایستگی حل و فصل احساسات و کنار آمدن با آنهاست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(598,'دشواری‌ها را به وزنه‌هایی بدل سازید که عضلات شما را ورزیده‌تر کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(599,'دشواری‌ها را به فرصت‌هایی جهت آموختن، رشد و دگرگونی شخصی بدل سازید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(600,'به راستی، می‌توانید هر گونه دشواری را به فرصتی مثبت و سودمند بدل سازید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(601,'اگر بخواهید احساسات خود را دگرگون سازید، می‌بایست رفتار و کردارهای خود را دگرگون کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(602,'هر کاری که به گونه‌ای فیزیولوژی، بدن و حرکات جسم شما را دگرگون کند، می‌تواند احساسات و عواطف شما را نیز دگرگون سازد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(603,'هرگاه با دشواری روبرو شدید، آن را بپذیرید و کارهای سازنده انجام دهید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(604,'به یاد داشته باشید که دشواری‌ها هرگز از بین نمی‌روند و ناپدید نمی‌شوند، مگر کسی مسئولیت آنها را به گردن بگیرد و در نهایت، گره آن را بگشاید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(605,'بهترین روش برای گشودن گره دشواری، آن است که پیوسته و مداوم، مرکز توجه خود را بر راهکار متمرکز کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(606,'برای کامیابی هرچه بیشتر، می‌بایست برای رویارویی با دشواری‌ها آماده باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(607,'زمانی فرا می‌رسد که دشواری‌هایی که در گذشته ما را دیوانه می‌کردند، برایمان بسیار ساده و خنده‌دار شوند؛ زیرا شایستگی‌ها و توانایی‌های خود را در رویارویی با آنها نیرو و بهبود بخشیده‌ایم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(608,'بهترین نکته‌ی کلیدی در از بین بردن دشواری‌ها آن است که همواره آن را پیشگویی کنید و آماده باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(609,'می‌بایست این باور را در خود به وجود آورید که می‌توانید بر همه‌ی دشواری‌های خود چیره شوید و همه‌ی آنها را از بین ببرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(610,'ما انسانها بیشتر از ترس اینکه به بلای بزرگتری دچار نشویم، درد و بلاهای کنونی را به جان می‌خریم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(611,'حقیقت این است که هر اندازه هم مثبت و خوش‌بین باشیم، شرایطی در زندگی وجود دارد که طرح‌ها، روش‌ها و برنامه‌های پیش‌بینی شده‌ی ما کارساز نخواهد بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(612,'تنها با تکیه بر ستون‌ها و پایه‌هاست که یک ساختمان می‌تواند پابرجا بماند و فرو نریزد. البته برای ساختن آن پایه‌ها و ستون‌ها، زمان و تلاش لازم است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(613,'کلید کامیابی در زندگانی، آن است که مدیریت یادبود‌ها و سابقه‌های ذهنی گذشته‌ی خود را بیاموزیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(614,'همواره به یاد داشته باشید ممکن است آنچه را دشواری یا سختی می‌دانید، به راستی، یک هدیه یا بخشش ارزشمند و گرانبها باشد. این، تنها گذشت و مرور زمان است که این حقیقت را روشن خواهد کرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(615,'بکوشید از نگرانی‌ها و پریشانی‌های خود به هنگام و در جای درست بهره بگیرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(616,'شاید عشق و شور راستین این باشد که با چشم‌پوشی از اینکه چه رویدادی در زندگی‌تان رخ دهد، بی‌درنگ، کنترل شرایط و امور را در دست گیرید و به خود امکان دهید سراپا، با آنچه پیش آمده، درآمیزید و با آن، یکی شوید و با همه‌ی وجود، آن را احساس کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(617,'اگر درد و رنج شکست را به جان نخرید، هرگز نمی‌توانید برنده از زمین بیرون بیایید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(618,'درد و رنج، همان چیزی است که شما را وادار می‌سازد تلاش کنید و به پیش بروید و به کامیابی‌هایی برسید که هرگز گمانش را هم نمی‌بردید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(619,'زمانی که بُریده‌اید و ناتوان هستید و شکست خورده‌اید، یا احساس می‌کنید دیگران، نیرومندتر، ماهرتر یا حتی خوش شانس‌ترند، به درون خود دست یازید و درد و رنج را به میل، آرزومندی و انگیزه بدل کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(620,'فراموش نکنید که کامیابی در زندگی، تنها از انگیزه داشتن و بااراده بودن به دست نمی‌آید. کامیابی، نتیجه‌ی گام برداشتن و کاری انجام دادن است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(621,'هرچه بیشتر بترسید که از چیزی بی‌بهره شوید، بیشتر می‌خواهید آن را به دست آورید!((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(622,'چنانچه با همه‌ی وجود، چیزی را جست‌و‌جو کنید، به شما داده خواهد شد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(623,'چنانچه همیشه از چیزی خوشمان بیاید یا بدمان بیاید، همان چیزهایی را به دست می‌آوریم که همیشه به دست می‌آوردیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(624,'اگر هدف و سمت و سو نداشته باشید، هر بادی، باد موافق [ =سازوار ] خواهد بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(625,'بدون هدف، همواره دستخوش نیروهای موافق [ =سازوار ] و مخالف و شرایط پیرامونتان خواهید بود، نه گزینش‌های خودتان.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(626,'همیشه، راه‌های زیادی وجود دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(627,'هرگز نمی‌توانید امیدوار باشید که دنیا، به گونه‌ای خودکار، شرایط رسیدن به هدفتان را فراهم سازد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(628,'به یاد داشته باشید، همیشه از همان جایی که هستید، آغاز کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(629,'تنها با تلاش و کوشش است که می‌توانید کامروا شوید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(630,'همه‌ی رهبران بزرگ دنیا آموخته بودند که چگونه خودباوری و بی‌گمانی را پیش از هر کس و نخست از درون خودشان ببینند و باور کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(631,'چنانچه با آسایش و بی هیچ شک و تردیدی، آنچه را می‌خواهید در دنیای بیرون شاهد روی دادن آن باشید، به روشنی در ذهن‌تان ببینید، بی‌شک روی خواهد داد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(632,'انسان‌های بسیاری در این دنیا وجود دارند که از استعدادها، شایستگی‌ها و توانایی‌ها و مهارتهای چشم‌گیری برخوردارند، ولی چون خودباوری و آگاهی کافی ندارند، نمی‌توانند کامیاب شوند و به خواسته‌ها و اهداف خود برسند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(633,'چنانچه همیشه از نتایج همه چیز آسوده دل باشیم و آن را به روشنی ببینیم، زندگی، هیجان و گیرایی خود را از دست خواهد داد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(634,'ما انسانها در زندگی خود به اندکی گوناگونی و دگرگونی نیازمندیم؛ این همان امری است که زندگی را هیجان‌انگیزتر می‌سازد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(635,'چنانچه بیش از اندازه، نسبت به همه چیز آسوده دل باشید، دیگر نمی‌توانید رشد کنید و همواره سر جایتان می‌مانید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(636,'به فکر بودن، آماده بودن و دشواری‌ها را پیش‌بینی کردن، یک امر است و پریشانی، نگرانی و هراس، امری دیگر.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(637,'مسئولیت زندگانی خود را به دوش بگیرید. با خودباوری، آسودگی و پایداری تمام، با دشواری‌ها روبرو شوید تا به راهکارهای بسیار بهتری برسید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(638,'هرگز نمی‌توانید از شر دشواری‌ها رهایی یابید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(639,'تنها راه کامیاب بودن، آن است که خود را پنهان نکنیم و از دست دشواری‌ها نگریزیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(640,'می‌بایست تلاش کنیم پیش از هر کاری، پاسخ و راهکار مهم‌ترین دشواری و مسئله‌ی خود را بیابیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(641,'یکی از سست‌کننده‌ترین باورهایی که می‌توانیم داشته باشیم، آن است که گمان کنیم رویدادها، امور و پدیده‌ها در کنترل ما نیستند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(642,'تا زمانی که مسئولیت کاری را به دوش نگرفته‌ایم، هیچ چیز در کنترل ما نخواهد بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(643,'پاسخ و واکنش مناسب را هنگامی خواهید یافت که مسئولیت‌پذیر باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(644,'چنانچه تجربه‌های ملموس بیشتری از این دنیا (چه مثبت و چه منفی) داشته باشید و خودتان نیز حس کنید، قدر دهش‌‌های دیگر زندگی را بیشتر خواهید دانست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(645,'هرگاه از دست مسائل و دشواری‌هایتان به ستوه آمدید، کافی است این گفته را به خودتان یادآوری کنید که دشواری‌ها و مسائل، نشانه‌های زندگی و زنده بودن هستند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(646,'هنگامی که ترسیده‌اید، نمی‌توانید تصمیم درستی بگیرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(647,'باورها و ارزش‌ها، بهترین راهنما و مشاور زندگی شما هستند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(648,'هر مسئله و دشواری، بی‌شک، راهکاری دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(649,'ارزش‌ها و باورهایتان را خودتان برگزینید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(650,'دوست داشتن و عشق ورزیدن به گفتن نیست، بلکه می‌بایست آن را در خود احساس کنی و انجام دهی.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(651,'گاهی، کمی درد و رنج برای رشد و آموختن هرچه بیشتر لازم است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(652,'بهتر است استانداردها و قوانینی برای خود داشته باشیم که ما را به تلاش و پرکار بودن تشویق نماید، نه اینکه از هر گونه رویارویی، تلاش و مبارزه بازدارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(653,'چنانچه می‌خواهید سطح کیفیت زندگی خود را بهبود بخشید و سرنوشت خود را به گونه‌ای بهتر رقم بزنید، می‌بایست با دشواری‌ها روبرو شوید و کاری بکنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(654,'این باورهای ما هستند که سرنوشت‌مان را رقم خواهند زد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(655,'بکوشید اندیشه‌ای آزاد و سرشتی پرسش‌گر داشته باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(656,'مسئولیت را بپذیر؛ بگذار هرچه می خواهد پیش آید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(657,'حتی پزشکان در کمال راستی، امانت و پایبندی خود، اشتباه کرده‌اند و می‌کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(658,'پاسخ و واکنش شما در برابر دشواری‌ها، بیش از هر کار دیگری در زندگی، در زقم زدن سرنوشت، کامیابی و شکست‌تان تعیین کننده خواهد بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(659,'همواره نظر دیگران را جویا شوید و مشورت کنید، ولی تصمیم نهایی را خودتان بگیرید؛ چون این شما هستید که می‌بایست پیامدها و نتایج آن را بپذیرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(660,'ذهن خود را باز و پذیرا نگه دارید و همواره گنجایش راه دادن اندیشه‌ها و باورهای تازه را در خودتان ایجاد کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(661,'قانون احتمالات یادتان نرود، شاید روزی کسی بگوید: ((بله))((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(662,'هرگز نمی‌توانید به دشواری‌های خود پشت کنید، بلکه می‌بایست با آنها روبرو شوید و برای از بین بردن آنها کاری انجام دهید و تلاش کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(663,'هرگاه گرفتن تصمیم مهمی را دشوار یافتید بدانید که علتش تنها یک چیز است و آن اینکه تصور روشنی از ارزشهای خود ندارید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(664,'گاهی، گرایش ما به گسترش دادن و کلی‌گویی، برخاسته از این گرایش و نیاز درونی است که کمتر بیندیشیم و آسوده‌تر باشیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(665,'ترس از دودلی سبب می‌شود از حرکت بازایستیم، نه خودِ تردید و دودلی.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(666,'چنانچه بیاموزیم که از تردید و دودلی لذت ببریم و از آن به سود خود بهره بگیریم، زندگی به مراتب، ساده‌تر و زیباتر خواهد شد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(667,'تنها کارشناس چیره‌دست و کارآزموده‌ی زندگی شما، خودتان هستید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(668,'هرگز به دیگران چشم ندوزید و دنباله‌رو نباشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(669,'زندگی را همان گونه که هست بپذیرید؛ در این زمان است که آرزو محو می شود. سعی کنید همیشه شاد باشید، بدون اینکه برای این شادی دلیل خاصی وجود داشته باشد و اینگونه است که به آرامش می رسید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(670,'همواره خواسته‌ها و نیازهای خود را با کمک خواستن از دیگران و گردآوری دانسته‌ها و داده‌های بیشتری بخواهید، نه با محاکمه و بازخواست کردن دیگران.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(671,'کامیابی در زندگی، کامیابی در برقراری ارتباط با خودتان و آفرینش مفاهیمی است که پشتیبان‌تان بوده و به شما نیروی بیشتری ببخشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(672,'برآشفتگی‌ها و خشم‌های ما برخاسته از چَم و مفهومی است که به پیشامدها، رویدادها و رخدادها نسبت می‌دهیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(673,'زمانی که نمی‌دانید چه چیزی حال‌تان را خوب می‌کند، نمی‌توانید به حال خوب برسید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(674,'هرگز هدف دوستان، آشنایان و نزدیکان خود را زیر سؤال نبرید؛ بکوشید همواره به سوی دنیای آنها پل بزنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(675,'اگر مراقب سلامتی خود نباشید، به سختی می توانید از عواطف خود بهره مند گردید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(676,'همیشه به خود اعتماد داشته باشید. اگر یک بار کاری را با موفقیت انجام داده باشید، باز هم می توانید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(677,'به یاد داشته باشید که حواس خود را به هر چیزی متوجه کنیم همان را به دست می آوریم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(678,'نشاط و خوشدلی، اعتماد به نفس شما را تقویت و زندگی را دلپذیرتر می سازد و باعث می شود که اطرافیان شما شادی بیشتری را احساس کنند. خوشدلی به معنی خوش خیالی و فرار از مشکلات نیست، بلکه نشانه ی هوش و ذکاوت شما است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(679,'پیشداوری درباره‌ی هویت دیگران، هر گونه ارتباط میان شما و آنها را از بین خواهد برد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(680,'هر چیز را هر اندازه که نازک ببرید و ورقه ورقه کنید، باز هم ورقه‌های ایجاد شده، هر کدام دو رو و دو سو خواهند داشت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(681,'همواره روش دیگری برای نگریستن به آنچه پیش آمده، وجود دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(682,'زمانی که گمان نمی‌کنید به شما و هویت‌تان توهین شده است، می‌توانید نیرومندانه‌‌تر به کنش بپردازید و شرایط را در دست بگیرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(683,'وقتی به دنبال هدف تازه ای می روید و با مانعی مواجه می شوید، به یاد آورید که از این موانع ، پیش تر هم در زندگی تان بوده است و بر آنها چیره گشته و موفق شده اید!((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(684,'زمانی که دیگران از شما درخواست کمک می‌کنند، بهتر است به آنها کمک کنید، نه اینکه شما نیز از آنها کمک بخواهید!((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(685,'اگر توجه مان را به چیزی جلب کنیم که از آن وحشت داریم، به یقین همان بلایی که از آن می ترسیم، بر سرمان می آید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(686,'در زمانهای تصمیم گیری است که سرنوشت ما رقم زده می شود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(687,'من و شما نیز می توانیم زندگانی خود را به شکل یکی از این افسانه ها در آوریم، به شرط اینکه شهامت داشته باشیم و بدانیم که قادریم اختیار رخدادهایی را که در زندگیمان روی می دهد، به دست گیریم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(688,'استعداد های نهفته درون خود را کشف و شکوفا کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(689,'روابط میان انسانها زمانی از هم می‌پاشد که هر دو سوی درگیر در یک رابطه نسبت به امر، پیشامد، رویداد یا یک پدیده‌، دو نگرش بسیار متفاوت [=دیگرگون] داشته باشند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(690,'تنها با اتکا به یک رفتار نادرست، همه‌ی هویت و منش افراد را زیر سؤال نبرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(691,'بسیاری از ناامیدی‌هایی که احساس می‌کنیم، زاییده‌ی دیدگاه‌ها، باورها و ارزش‌های خودمان است و ارتباطی با آنچه پیش آمده ندارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(692,'اگر از رفتار کسی خوشتان نمی‌آید، سزاوار است به او بگویید از این رفتار تو برآشفته شدم. ولی هرگز به منش و هویت او نتازید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(693,'موفقیت نیز ناشی از آغازگری و ابتکار، پشتکار و بیان روشن عشق و محبت ژرف قلبی است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(694,'کوچکترین تحول همانند سنگ ریزه ای است که به درون برکه ای پرتاب می شود و امواج بسیاری به گرد آن شکل می گیرند. سنگ ریزه ی آگاهی نیز با برکه ی ذهن ما چنین کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(695,'موفقیت، نتیجه ی تشخیص درست است؛ تشخیص درست، نتیجه ی تجربه است؛ تجربه نیز اغلب نتیجه ی تشخیص نادرست است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(696,'همه ی ما ارزشمندیم و همگی هدایایی از جانب خداوند هستیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(697,'این خواست خداوند است که آرزوی درست دل هر انسانی را برآورد. چه بسیار پیش آمده که آدمی با دیدن عیبهای خودش در دیگران شفا یافته است. موفقیت پیش رفتن است، نه به پایان رسیدن.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(698,'راه موفقیت همیشه در دست ساختمان است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(699,'همه ی عواطف نیرو بخش، حالت هایی از بیان عشق و محبت هستند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(700,'برای اینکه تغییر، ارزش حقیقی داشته باشد، باید پایدار و ماندگار باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(701,'شور و هیجان شما ناشی از هدفهای زندگی تان است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(702,'کلید موفقیت در نگه داشتن حد و اندازه برای هر چیز است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(703,'آنچه سرنوشت ما را مشخص می کند، شرایط زندگی نیست، بلکه تصمیم های ما است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(704,'استعدادهای درون خود را کشف و شکوفا کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(705,'آنان که به بزرگی می رسند کسانی هستند که شکست برایشان قابل درک نیست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(706,'هدفی که به سوی آن می روید و آنچه دارید، معیار حقیقی موفقیت شما نیست. به نظر من، موفقیت، جریان مداومی است که طی آن، مشتاق و آرزومند موفقیت های بیشتری هستیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(707,'موفقیت، پیش رفتن است، نه به نقطه ی پایان رسیدن.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(708,'نیروی معجزه آسایی که بزرگترین رؤیاها و آرزوهای ما را برآورده می سازد، در درون همه ی ما نهفته است؛ وقت آن است که این نیرو را آزاد کنیم!((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(709,'اندیشه های خود را شکل دهید، وگرنه دیگران، اندیشه های شما را شکل می دهند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(710,'نهایت قدرت در این است که نتایج مورد نظر خود را به دست آورید و در عین حال به دیگران ارج بگذارید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(711,'ما در عصر اطلاعات [ =دانستنیها ] زندگی می کنیم؛ نمودار درجه اول فرهنگ ما دیگر صنعت نیست، بلکه ارتباطات است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(712,'در دنیای مدرن، دانستنیها، ابزار قدرتمندان است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(713,'اگر داشتن فکر و اندیشه ی مثبت به تنهایی کارساز بود، بایستی همه در کودکی اسبهای کوچک داشته و اکنون نیز زندگی رویایی داشته باشیم؛ هر موفقیت بزرگ با عمل به دست می آید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(714,'اگر جسم شما زنده، پر تحرک و شاد باشد، روحیات شما نیز از آن پیروی می کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(715,'آنچه نتیجه بخش است، عمل است. دانش فقط نیروی بالقوه ای است در دست آنان که می دانند چگونه می توان عمل مؤثری انجام داد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(716,'افرادی که موفق هستند، آمادگی تغییر و قابلیت انعطاف دارند؛ تا جایی که بتوانند زندگی دلخواه خود را فراهم نمایند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(717,'مردم در روابط خود، خواهان عشق هستند. عشق، به زندگی نیرو و رنگ و معنا می بخشد. هیچ عظمتی بدون عشقی بزرگ به دست نمی آید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(718,'باور ما در مورد آنچه که هستیم و آنچه که می توانیم باشیم، به درستی مشخص کننده ی آینده ی ما است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(719,'اگر به معجزه باور داشته باشیم، در زندگیمان رخ می دهد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(720,'برنامه ریزی، یعنی تشخیص این مطلب که بزرگترین استعدادها و آرزوها باید مسیر درست خود را پیدا کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(721,'ارزش، پیشداوری اساسی، اخلاقی و عملی است در مورد اینکه چه چیزهایی از اهمیت برخوردارند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(722,'ارزشها، نظامهای اعتقادی ویژه ی ما هستند که به ما می گویند چه چیزی در زندگی ما درست و چه چیزی نادرست است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(723,'درک ارزشها یکی از حساس ترین کلیدهای نیل به پیشرفت و کامیابی است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(724,'افراد ناموفق کسانی هستند که بدبختی های زندگی را به عنوان حقیقت و سرنوشت ناگزیر می پذیرند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(725,'آزمایش و خطا بسیار خوب است، تنها یک عیب دارد: مقدار زیادی از سرمایه ای که همه ی ما کم داریم، یعنی زمان، در این راه صرف می شود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(726,'فرق آدمهای موفق و ناموفق در آنچه که دارند نیست، بلکه در این است که با توجه به امکانات و تجربه های خود، چه می بینند و چه می کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(727,'برنامه هایی که دیگران تنظیم کنند همیشه به کار شما نخواهد آمد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(728,'آمادگی داشتن و استفاده از فرصتها رویهم رفته چیزی را به وجود می آورند که آن را خوشبختی می نامیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(729,'کسی که خانه اش شیشه ای است، سنگ پرتاب نمی کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(730,'روحیه، مجموعه ی میلیونها جریان عصبی است که در درون ما رخ می دهد؛ به عبارت دیگر، جمع کل تجربه های ما در هر لحظه از زمان است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(731,'افراد موفق می توانند خود را در چنان حالت روحی قرار دهند که آن حالت به آنها قدرت و دلگرمی بدهد تا به اهداف خود دست یابند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(732,'تصورات درونی ما مطابق با ثبت دقیق رخدادها نیست، بلکه رخدادهای مورد نظر، از صافی باورهای خاص فرد، برداشتها، ارزشها و پیش فرض های وی می گذرند تا ثبت گردند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(733,'اگر کاری را بدون نتیجه در نظر بگیریم، خواه ناخواه بدون نتیجه هم خواهد بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(734,'زندگی همچون رودخانه است؛ همیشه جریان دارد و اگر مسیر خود را آگاهانه و با اراده معین نکنید دستخوش امواج آن خواهید شد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(735,'اگر تخم های فکری و جسمی هدفی را که در نظر دارید نکارید، علفهای هرزه، خود به خود سبز خواهند شد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(736,'اساساً ایمان عبارت از هر گونه اصل راهنما، باور، یقین و یا گرایش است که به زندگی معنا بخشیده و به آن جهت می دهد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(737,'اگر ایمان به درستی به کار رود، بزرگترین نیرویی است که منشأ خیر و خوبی در زندگانی می شود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(738,'هیچ نیرویی برای هدایت انسان نیرومندتر از ایمان نیست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(739,'تغییرات رفتاری را باید از تغییر باورها آغاز کرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(740,'محیط را می توان نیرومندترین عامل مولد ایمان و باور دانست، اما تنها عامل آن نیست. در غیر این صورت، جامعه، پویایی خود را از دست می داد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(741,'تجربه ها چنان اثری در ما داشته اند که یادگار آنها تا ابد در ذهن ما جا گرفته است. این گونه رویدادها باورهایی در ما ایجاد می کنند که می تواند زندگی ما را تغییر دهد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(742,'گاهی افرادی به موفقیت های بزرگ می رسند، تنها به این دلیل ساده که نمی دانند کاری که به آن دست زده اند، بسیار دشوار، بلکه غیر ممکن است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(743,'در بطن هر بدبختی و حادثه ی ناخوشایند، دانه ای است که روزی به ثمر رسیده و فواید بسیار به بار خواهد آورد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(744,'مردان بسیار موفق کسانی نیستند که هرگز شکست نمی خورند، بلکه اگر نتیجه ی به دست آمده، با چشم داشت آنها یکسان نبود، از آن درس می گیرند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(745,'از دیروز تا اکنون، به اندازه ی یک روز به تجربه های شما افزوده شده است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(746,'به هر میزان هم که به چیزی باور داشته باشیم، باز هم لازم است که دریچه های ذهن خود را به روی باورها و امکانات دیگر باز بگذاریم و همیشه برای یادگیری مطالب تازه تر آمادگی داشته باشیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(747,'نتایج تازه ناشی از کارهای تازه است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(748,'بزرگترین عامل بازدارنده ی بیشتر مردم، ترس از شکست است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(749,'چیزی به نام شکست وجود ندارد؛ آنچه هست گونه ای نتیجه به شمار می رود. شما همیشه به نتیجه می رسید؛ اگر این نتیجه ها دلخواه شما نیستند نحوه ی عمل را عوض کنید تا به نتایج تازه ای برسید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(750,'پذیرش مسئولیت، یکی از بهترین معیارهای توانایی و بلوغ شخص است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(751,'یک فرد هر چقدر هم که باهوش باشد، مشکل است که به تنهایی بتواند با تمام استعدادهای یک گروه کارآمد برابری کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(752,'یکی از کلیدهای موفقیت این است که پیوندی سازنده بین کارهایی که انجام می دهیم و چیزهایی که دوست داریم به وجود آوریم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(753,'اگر فکر کنید که کار، یعنی زحمت و مشقت محض و وسیله ای برای تامین معاش خانواده، به احتمال زیاد، چیزی بیش از آن هم گیرتان نخواهد آمد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(754,'تفاوت ورزشکاران از لحاظ مهارتهای بدنی چندان مهم نیست. آنچه ورزشکاران خوب را از قهرمان های بزرگ متفاوت می کند میزان اراده و پشتکار است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(755,'هر موفقیت، آثاری از خود بر جای می گذارد؛ این ردپاها را دنبال کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(756,'به طور رایج، مشکل مردم در کمبود امکانات نیست، بلکه در نحوه ی اداره ی امکانات موجود است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(757,'ذهن می تواند به عقب بازگردد، اما زمان قابل بازگشت نیست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(758,'آموزش و پرورش ما تاکنون به فکر این بوده که دانش آموزان چه مطالبی باید بیاموزند نه اینکه چگونه بهتر می توانند مطالب را فرا گیرند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(759,'کمابیش هر چیزی را که در دنیا بخواهید، اگر از راه درست بروید و باور محکم و تصمیم بی چون و چرا داشته باشید، آن را به دست خواهید آورد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(760,'رسیدن به برخی چیزها ایمان محکم و انرژی و تلاش فراوان می خواهد، گرچه می توان به آنها رسید، اما باید به راستی کار کرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(761,'تا جسم نیرومند نباشد، برداشتن گام های نیرومند امکان پذیر نیست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(762,'اگر احساس قدرت کنیم، کارهایی از ما ساخته است که در حالت ترس و ضعف و خستگی از ما بر نمی آید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(763,'نمی توان یک رفتار ناخوشایند را ملاک داوری درباره ی منش افراد گرفت، زیرا رفتار فرد، همیشه بیانگر منش او نیست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(764,'داوری بهتر، زندگی بهتر می آفریند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(765,'اگر فکر می کنید که موفق می شوید یا شکست می خورید، در هر دو صورت درست فکر کرده اید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(766,'بیشتر ما برای ایجاد ویژگی های بد در خویشتن، راههای فراوانی آفریده ایم، ولی برای ایجاد ویژگی های خوب، راههای اندکی باقی گذاشته ایم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(767,'پرسشهای ما افکار ما را می سازند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(768,'اگر نسبت به خود، زیاد سخت گیر باشید، بی گمان، نسبت به دیگران نیز سخت گیر خواهید بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(769,'باید از گذشته درس بیاموزیم، نه اینکه در گذشته ها زندگی کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(770,'در پژوهش بر روی یک اثر خوب، نیرویی نهفته است که شما را وا می دارد همچون نویسنده بیندیشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(771,'در هر تجربه ای، نکته ای ارزشمند می توانم پیدا کنم و اگر از هر تجربه، نکته ای و نظریه ای را دریابم، در آن صورت، پیوسته رشد خواهم کرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(772,'تجربه ها بررسی های من همه نمایانگر این است که محدودیت های وجود ما (چه مثبت و چه منفی) بسیار کمتر از آن چیزی است که به ما باورانده اند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(773,'سرآغاز بهروزی این است که بدانیم باورهای ما ارادی است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(774,'داشتن ابزارهای خوب و نیرومند فایده ای نخواهد داشت، مگر آنکه بدانید به چه منظور از آنها استفاده کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(775,'ممکن است بزرگترین اره ای که در جهان اختراع شده است در دست شما باشد و در عین حال، در میان انبوهی از درختان جنگل، سرگردان بمانید و ندانید چه کنید. اگر بدانید که چه درختانی را باید برید و چرا؟ شرایط بر وفق مراد است، وگرنه ابزاری عالی در دست دارید که به هیچ نمی ارزد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(776,'هدفهای محدود، زندگی را محدود می سازند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(777,'هدف باید به سود شما و دیگران باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(778,'پیش از آنکه رویدادی در جهان بیرون رخ دهد باید در جهان درون پیش آید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(779,'اگر می خواهیم از حد توانایی های خود فراتر برویم، ابتدا باید آن خواسته را در ذهن تجربه کنیم، آنگاه زندگی ما به آن سمت می رود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(780,'تمام محدودیت هایی که دارید آنهایی هستند که خودتان برای خود ایجاد کرده اید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(781,'محدودیت ها فقط در ذهن وجود دارند؛ پس هر وقت محدودیتی به ذهنتان رسید، بی درنگ آن را از مغز خود بیرون کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(782,'اگر برای انجام کاری دلایل کافی در دست داشته باشید، ناگزیر به انجام آن وادار می شوید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(783,'یکی از راههای غلبه بر دشواریها و محدودیت هایی که خود، آنها را ایجاد می کنیم این است که آنها را به گونه ای دقیق بشناسیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(784,'به درستی، هر کس که به توفیق بزرگی نایل شده، نمونه و سرمشق یا آموزگار، راهنما و مشاوری داشته است تا راه درست را به او نشان دهد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(785,'اگر خودتان برای زندگی خود برنامه نداشته باشید، کسی دیگر از وجود شما برای برنامه های خود بهره نخواهد گرفت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(786,'بد نیست دفتر یادداشتی داشته باشید و رویدادهای مهم روزانه را در آن ثبت کنید. اگر زندگی انسان ارزش ادامه را داشته باشد ارزش ثبت کردن را نیز دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(787,'برای هر یک از ما در هر زمان، امکاناتی موجود است تا زندگی خود را بهتر سازیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(788,'هر چه دقیق تر باشیم می توانیم کارهای مؤثرتری انجام دهیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(789,'برای ایجاد تغییر در دیگران باید از تصورات درونی آنها درک درستی داشته باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(790,'پرمغزترین مطالب، زیباترین اندیشه ها و هوشمندانه ترین انتقادات به طور مطلق بی معنی هستند، مگر آنکه مخاطب، معنا و احساس آنها را درک کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(791,'اتومبیل می تواند هم به سمت بالای جاده حرکت کند و هم به سمت پایین؛ بستگی به این دارد که روی آن به کدام سمت باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(792,'یک رهبر راستین باید بتواند از دانسته هایی که از بیرون دریافت می کند به نحو مؤثر بهره گیرد، وگرنه کار رهبری به خود بزرگ بینی می انجامد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(793,'یک پزشک ممکن است در تشخیص بیماری بسیار استاد باشد، اما کار درمان به خوبی پیشرفت نمی کند، مگر آنکه احساس کنید پزشک به وجود شما اهمیت می دهد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(794,'ما باید خود را با زندگی، آنچنان که هست و نه آن گونه که می خواهیم باشد، سازگار کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(795,'لزومی ندارد که افراد به درستی مانند هم باشند تا با هم صمیمی شوند، بلکه باید به تفاوت دیدگاههای یکدیگر توجه و در عین حال به هم احترام بگذارند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(796,'نمی توان به اصطلاح با قدرت اراده با دیگران ارتباط برقرار کرد و با کمک چماق، آنها را وادار به پدیرفتن باورها و نقطه نظرهای خود کرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(797,'در بسیاری از موارد، اگر کاری را به دقت مانند گذشته انجام دهیم برایمان راحت تر است. اما آنچه آسان تر است بیشتر وقتها از همه بدتر است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(798,'برخی از ما بحث را با مشاجره و بوکس بازی های لفظی اشتباه می گیریم، در حالی که راههای ظریفانه تر و مؤثرتری برای از بین بردن مقاومت ها وجود دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(799,'یکی از راههای از بین بردن دشواریها و رفع گرفتاریها آن است که آنها را از نو تعریف کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(800,'اگر باور کنید که بر خود مسلط بوده و قادر به تغییر روحیات خود هستید، البته به این کار توانا خواهید شد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(801,'هیچ چیز به خودی خود دارای معنا نیست، بلکه احساسات، رفتارها و واکنش های ما نسبت به هر چیز بستگی به نحوه ی ادراک و تصور ما از آن چیز دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(802,'بدبختی، گونه ای نقطه نظر است. کسی که کارش فروش آسپیرین است از سر درد شما احساس خشنودی می کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(803,'ما دنیا را آنچنان که هست نمی بینیم، زیرا ماهیت امور از نقطه نظرهای گوناگون قابل تفسیر است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(804,'درست است که پرچم تنها یک تکه پارچه است، اما در عین حال نشانه ی تمام فضایل و ویژگی های یک ملت به شمار می رود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(805,'باید عواملی را که در شما روحیات منفی ایجاد می کند از محیط خود بزدایید و به جای آنها عوامل مثبت را در ذهن خود و دیگران جای دهید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(806,'اگر کسی به کار معینی دست بزند، اما در ژرفنای وجود خود خواهان چیز دیگری باشد، به خوشبختی و رضایت خاطر نخواهد رسید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(807,'هیچ موفقیت حقیقی به دست نمی آید، مگر اینکه با ارزش های اساسی ما مطابقت داشته باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(808,'گاهی باید بیاموزیم که چگونه ارزش های ناسازگار را با یکدیگر آشتی دهیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(809,'به کمک ارزش های والای انسانی و ارتباط دادن آنها با اخلاق پسندیده، می توان عادتهای ناپسند را ترک کرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(810,'بهترین راه پیوند افراد به یکدیگر آن است که ارزش های والای آنها را به هم نزدیک سازیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(811,'هر سیستمی که قابلیت انعطاف بالایی داشته باشد و تغییرات زیادتری را بپذیرد، کارایی بیشتری خواهد داشت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(812,'تنها کسانی که تغییر نمی کنند، آنهایی هستند که نفس نمی کشند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(813,'باید یاد بگیرید که با یأس مبارزه کنید؛ یأس، رؤیاها و آرزوها را نابود می کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(814,'دلسوزی، برداشت های مثبت را به منفی و قدرت روحی را به ضعف تبدیل می کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(815,'هر جا موفقیت بزرگی ببینید، انبوهی از ناکامی ها را در مسیر آن مشاهده می کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(816,'در مسیر هر موفقیت بزرگ، دشواریها و ناکامی های بزرگی قرار دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(817,'برای رفع فشارهای عصبی باید دو گام برداشت: گام نخست اینکه برای مسائل جزیی، خود را ناراحت نکنید، گام دوم اینکه بدانید همه ی مسائل جزیی هستند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(818,'موفقیت، سکه ای است که روی دیگر آن ناکامی است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(819,'این ماییم که فکر خود را محدود می سازیم و در نتیجه، زندگی ما محدود می شود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(820,'رضایت خاطری که از کمک دیگران به دست می آید به هیچ بهایی قابل خریداری نیست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(821,'زمانی می اندیشیم بهترین راه کمک به بیچارگان آن است که الگوی خوبی برای آنها باشیم تا بدانند شیوه های بهتری نیز برای زندگی وجود دارد و به آنها کمک کنیم تا بتوانند خودشان زندگی خود را اداره کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(822,'راحتی می تواند اثرات مخربی در زندگی انسان داشته باشد. کسی که زیاد احساس راحتی کند، از رشد، تلاش و خلاقیت باز می ماند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(823,'اگر در زندگی رو به بالا حرکت نکنید ناچار به سرازیری می افتید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(824,'بکوشید همیشه خود را با هدفهای خود مقایسه کنید، نه با دوستان و همقطاران؛ زیرا همیشه می توان افرادی را یافت که در سطحی پایین تر از ما باشند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(825,'همیشه بیش از آنچه می گیرید بدهید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(826,'راز زندگی در بخشش است؛ سخاوت به زندگی انسان معنا می بخشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(827,'در دنیایی که پر از افراد تبلیغات چی است شما نیز می توانید یکی از آنها باشید، یا اینکه بگذارید شما را تحت تاثیر پیامهای تبلیغاتی قرار دهند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(828,'افراد قوی، رمز نفوذ در دیگران را می دانند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(829,'امروزه، قدرت عبارت از توانایی ایجاد ارتباط و نفوذ در دیگران است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(830,'جنایت، مشکل بشر نیست، بلکه این رفتار انسانها است که جنایت را می آفریند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(831,'گاهی وقتها بر یک رشته عملیات نامی می نهیم و چون با نام مشخص می سازیم آنها را شی ء می نامیم، حال آنکه مجموعه ای از کارها هستند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(832,'یکی از گرفتاریهایی که بسیاری از کودکان (به ویژه در مناطق محروم) با آن مواجه هستند، دسترسی نداشتن به الگوهای قوی و مثبت است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(833,'اگر مغز خود را خوب به کار گیرید، عظمتی به زندگی شما می دهد که هرگز در رؤیاهای خود آن را نمی دیدید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(834,'لازمه ی رهبری این است که به قدر کافی قدرت و بصیرت داشته باشیم تا بتوانیم نتایج اقدامات خود را، چه کوچک باشند و چه بزرگ، از قبل پیش بینی کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(835,'کافی نیست گفتنی ها را بگوییم، باید گفتنی ها را عمل کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(836,'به آن گروه از افراد بپیوندید که به آنچه می گویند عمل می کنند. این افراد، الگوهای بزرگی هستند که سایر افراد جهان را به شگفتی وامی دارند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(837,'الهام بخش زندگی من، شرح حال کسانی بوده است که امکانات و استعدادهای خود را به کار می گیرند تا به موفقیت ها و نتایجی تازه، چه برای خود و چه برای دیگران دست یابند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(838,'تکرار، مادر مهارت است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(839,'شما هر قدر کامیاب باشید و هر اندازه خوب کار کرده باشید، کسانی پیدا می شوند که بهتر از شما باشند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(840,'بیشتر مردم می پذیرند، چیزهایی بسیار ارزشمندتر از پول را از دست بدهند تا پول بیشتری به دست آورند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(841,'رفتارهای ما به شدت تحت تاثیر باورهای ما هستند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(842,'هدفهای باشکوه، دریچه ای به دنیای نیروی بیکران است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(843,'هر ثروتی، از اندیشه ی انسانی ناشی می شود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(844,'خدمت راستین، زندگی را پر بار می کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(845,'چنانچه برنامه ی دقیقی برای سرمایه گذاری نداشته باشید، سرانجامی جز شکست مالی نخواهید داشت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(846,'اگر راههایی برای کمک به دیگران کشف کنید که متناسب با درآمدتان باشد، به یکی از بزرگترین دلخوشی های زندگی دست خواهید یافت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(847,'تندرستی ما بیشتر به گونه ی زندگی و تکاپوی مان بستگی دارد، تا به سن و سال.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(848,'در هر زمان که می اندیشید سطح مهارت شما به بالاترین اندازه رسیده است، کسی را می یابید که در سطحی بالاتر از شما قرار دارد و این، یکی از جنبه های زیبای زندگی است که مایه ی رشد و پیشرفت همیشگی می شود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(849,'آنچه در چشم ما، تاریک ترین روزهای زندگی به نظر می رسد، شاید پربارترین روزهای زندگی مان باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(850,'باید آگاهانه، نگهبانی بر دریچه ی ذهن خود بگماریم تا تنها به چیزهایی اجازه ی ورود دهد که زندگی ما را پربارتر سازد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(851,'اگر می خواهید زندگی تان فزونی یابد، در پی گسترش آن باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(852,'اگر بخواهیم دیگران را بهتر درک کنیم و به آنان ارج نهیم، یکی از بهترین راهها آن است که در برخی از تجربه های آنان شریک باشیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(853,'همه ی تجربه های انسانی، دارای ارزش هستند، ولی گاهی سال ها زمان می برد تا به ارزش آنها پی ببریم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(854,'فراموش نکنید که زندگی، مجموعه ای از لحظات است. می توانیم لحظاتی را برای خود بیافرینیم که مایه ی برآمدن ما باشد، نه اینکه محدودمان کند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(855,'دست به کنش بزنید و از کشف توانایی های تازه لذت ببرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(856,'کارهایی را که می توانیم یا نمی توانیم بکنیم و آنچه را که شدنی یا نشدنی می پنداریم، به ندرت، برآمده از توانایی راستین ما بوده و بیشتر از باورهایی که نسبت به خود داریم، سرچشمه می گیرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(857,'نه تنها منشی که داریم، بلکه منشی هم که نداریم، هویت ما را روشن می سازد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(858,'رفتار دیگران نسبت به شما، بر مبنای تصوری [ =انگاره ای ] است که از منش شما دارند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(859,'همه ی ما همیشه بر مبنای تصویری که از خویشتن داریم، به کنش می پردازیم، خواه این تصویر، دقیق باشد، خواه نباشد، زیرا یکی از توانمندترین نیروهایی که در ذات بشر وجود دراد، نیاز به ثبات است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(860,'هیچ اهرمی، نیرومندتر از هویت، در دگرگونی رفتار انسانی نیست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(861,'تغییر هویت، از شادترین، جادویی ترین و رهاننده ترین تجربه های زندگی است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(862,'هیچ چیز لذت بخش تر از این نیست که ببینیم کسی، منش و هویت خود را گسترش می دهد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(863,'ما در جهانی زندگی می کنیم که بایستی هویت هایمان پیوسته گسترده تر شوند تا کیفیت زندگی مان بهتر گردد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(864,'اگر اراده کنیم که شیوه ی اندیشه، احساس و کنش های ما شایسته ی آن کسی باشد که می خواهیم باشیم، تبدیل به آن فرد می شویم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(865,'به راستی، والاترین نیکویی و مهربانی ما، ایجاد ارتباط روحی با افراد دیگر است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(866,'بکوشید نسبت به فرد مقابل، بخشنده و دهنده باشید، نه اینکه بخواهید چیزی به دست آورید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(867,'روی به سوی هر رویدادی که بکنیم، همان به سرمان می آید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(868,'همیشه بخشی از درآمد خود را برای کمک به نیازمندان کنار بگذارید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(869,'از کامیابی دیگران به همان اندازه شادمان شوید که از کامروایی خود، احساس شادمانی می کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(870,'کارهای نادرست گذشته را فراموش کنید و به کامیابی های بزرگ آینده بیندیشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(871,'به اندازه ای در اندیشه ی درست کردن خود باشید که زمانی برای خرده گیری از دیگران نداشته باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(872,'خویشتن داری به انسان غرور می بخشد، زیرا احساس می کنید که عواطف خود را آگاهانه، روشن کرده و خویشتن را به سوی ارزشهایی برتر هدایت می کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(873,'آنچه در درون شما است، بستگی به اراده ی خودتان دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(874,'به یاد داشته باشید، هویتی که در طول روز از خود بروز می دهید، منش و خوی شما را در بر می گیرد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(875,'تا می توانید از تجربه های دیگران بهره مند شوید و آنچه را که می آموزید، به کار گیرید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(876,'توجه داشته باشید که هر تصمیمی، پیامدهایی دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(877,'ما با کنش های خود، ژرفترین ارزشها و باورهایمان را بیان می کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(878,'چه بسا کسانی که از رویارویی با هر مسأله و امر سختی می گریزند و توجه ندارند که دشواری ها، انسان را آبدیده می کند و ویژگی های مثبت او را آشکار می سازد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(879,'قهرمان بودن، در کمال نیست، بلکه در انسانیت است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(880,'به یاد داشته باشید که گامهای کوچک می تواند نتایج بزرگی در پی داشته باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(881,'همه ی کنش های ما برآمده از این باور بنیادی است که چه کسی باید باشیم و چه کارهایی را باید، یا نباید انجام دهیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(882,'با دگرگونی باورها، ارزشها، قوانین و هویت، می توان همه ی رفتارها را دگرگون نمود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(883,'هر تصمیمی دارای پیامدهایی است و اگر به آثار درازمدت کارهای خود بر روی این سیاره، بی توجه باشیم، تصمیم های شایسته ای نخواهیم گرفت.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(884,'بدبختانه، بیشتر مردم، تربیت را تنها در رفتن به مدرسه می دانند و همین که فارغ التحصیل [ =دانش آموخته ] شدند، دست از پژوهش و آموختن می کشند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(885,'سپاس گذاری و حق شناسی یک نفر، ممکن است زندگی انسانی دیگر را برای همیشه دگرگون سازد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(886,'خدمت به همنوع، تنها یک وظیفه نیست، بلکه فرصتی است تا بتوانیم بخشی از دِین خود را به جامعه ادا کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(887,'تنها کسانی که نیروی خدمت سخاوت مندانه و صمیمانه را آموخته اند، ژرفترین خوشی های زندگی و موقعیت های راستین را درک می کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(888,'هر روز به گونه ای زندگی کنید که گویی آن روز، مهمترین روز زندگی شما است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(889,'آنچه مهم است، کیفیت زندگی است، نه عمر دراز.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(890,'باید دگرگونی ها را دوست بداریم، زیرا دگرگونی، تنها چیزی است که قطعیت دارد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(891,'اگر نمی توانی، بایدت شاید، اگر بایدت شاید، پس می توانی.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(892,'می‌بایست بدانید آنچه در کنترل، گزینش و اراده‌ی شماست، رویدادها و پدیده‌ها نیستند؛ تنها، مفهومی که به رویدادها، امور و پدیده‌ها می‌‌دهید، در کنترل و به گزینش و اراده‌ی شماست.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(893,'همواره با چشم‌پوشی از اینکه چه پیشامدی در زندگی و در جهان بیرون روی می‌دهد، چَم و مفهومی که به آن نسبت می‌دهید، با شما و به میل و اراده و گزینش خودتان است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(894,'زمانی که ایمان و باور خود را از دست می‌دهید، زندگی، پوچ و بی‌معنا می‌شود و زمانی که زندگی، پوچ و بی‌معنا شود، دیگر زنده نخواهید بود و زندگی را از کف خواهید داد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(895,'اندیشه‌ها و ذهنیات شما با تندرستی فیزیکی و جسمانی‌تان، رابطه‌ی نزدیک و تنگاتنگ دارند و بر آن تاثیرگذارند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(896,'زمانی که پیشه‌ی سخت و توان فرسایی دارید و ایمان و باور پابرجایی نیز بدان ندارید و مفهوم و هدفی والا را در آن نمی‌بینید و نمی‌یابید، زندگی برای شما جهنم خواهد شد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(897,'انکار یا گریز از دشواری‌ها، هرگز نمی‌تواند زندگی بامفهوم و خرسند گونه‌ای را برای شما پدید آورد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(898,'ما انسان‌ها می‌بایست بدانیم که چیزی به نام کار نادرست وجود ندارد. هر کاری، چَم و مفهومی دارد، حتی اگر هنوز آن را نیافته باشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(899,'نکته‌ی غم‌انگیز این است که بیشتر مردم تا زمانی که تندرست هستند، چَم و مفهوم زندگی خود را کشف نمی‌کنند. می‌بایست بیماری كُشنده‌ای، زندگی‌شان را تهدید کند تا بخواهند و تلاش کنند که چَم و مفهوم زندگی را بیابند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(900,'آنچه را که پیش آمده، نمی‌توانیم دگرگون کینم، ولی می‌توانیم مرکز توجه خود و نیز چَم و مفهومی را که به رویداد پیش آمده نسبت می‌دهیم، دگرگون کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(901,'چنانچه سخن خوبی ندارید که از کسی بگویید، پس بهتر است هرگز از او سخن نگویید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(902,'مردم، حیوانات و همه‌ی موجودات زنده را دوست داشته باش و به آنها عشق بورز.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(903,'خوب، مهربان، خوشرو و گشاده‌رو باش تا دنیا را نیز به جای بهتری برای دیگران بدل کنی.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(904,'چنانچه بخواهیم از زندگی خود بهره‌ی بیشتری ببریم، شاید می‌بایست از تجربه‌ی نزدیکان بهره بگیریم و از آنها بیاموزیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(905,'می‌بایست بیاموزیم که در سراسر زندگی خود، مراقب نشانه‌های هشدار، الگوها و درس‌های پندآموز باشیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(906,'زندگی می‌بایست سفری شادمانه و شادی‌بخش باشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(907,'قدر چیزهای کوچک را می‌بایست به اندازه‌ی چیزهای بزرگ‌تر و مهم‌تر بدانیم و به هر دوی آنها به یک اندازه ارج نهیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(908,'دلیل شماره‌ی یک احساس خوشبختی نکردن و بازنده بودن در زندگی بسیاری از انسان‌ها این است که تصویر ذهنی روشنی از شادمانی و خوشبختی راستین و برنده بودن در زندگی ندارند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(909,'بدانید که چه بخواهید و چه نخواهید، همواره در حال دگرگونی و بهتر شدن هستید و خواهید بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(910,'مفهوم و هدف زندگی شما هرگز دگرگون نمی‌شود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(911,'وسایل و ابزار، ممکن است دگرگون شوند، ولی هدف همواره یک چیز است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(912,'به راستی، هر پیشامدی هم که روی دهد، همواره می‌بایست چَم و مفهوم نیروبخش و مثبتی را در آن بجویید و بیابید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(913,'دلیل اینکه بسیاری از انسان‌ها احساس بازنده بودن می‌کنند آن نیست که زندگی‌شان به راستی یک باخت بزرگ است. شاید گمان می‌کنند برنده بودن، آن است که بر روی کره‌ی ماه فرود آیند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(914,'بخش بزرگ زندگی ما انسان‌ها، صرف فرآیندها می‌شود، نه نتیجه، بهره و سرانجام.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(915,'اگر بنا بود تنها به هنگام رسیدن به هدف خود شادمان شویم و از فرآیند تلاش برای رسیدن به هدف، شاد نباشیم، تنها بخش کوچکی از زندگی خود را شادمان بودیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(916,'شما ماموریت و مسئولیتی ویژه را در برابر خودتان و دیگران به دوش دارید و پیش از هر چیز و هر کس در برابر خودتان مسئول هستید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(917,'چنانچه هدف از زندگانی خود را ندانید و نشناسید، چگونه می‌توانید در بازی زندگی برنده باشید؟((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(918,'می‌بایست در زندگی، هدفمند باشید. می‌بایست هدفی را دنبال کنید. می‌بایست برگزینید و تصمیم بگیرید؛ آن هم، هم‌اکنون.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(919,'چنانچه در زندگی، قوانین ناسازگار بسیاری دارید، درد و رنج زیادی را به جان خواهید خرید و ناامید و سرخورده خواهید شد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(920,'هنگامی که رویدادهای این جهان را با وجود دردناک بودن و ناخوشایند بودن آنها، با دید و نگرشی مثبت می‌نگرید و چَم و مفهوم دیگرگونی به آن می‌دهید، به راستی برنده می‌شوید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(921,'گاهی برای برنده شدن، دست به کارهایی می‌زنیم که با ارزش‌های درونی ما ناسازگار است. در این موارد، بازنده‌ی اصلی ما هستیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(922,'احساس شادمانی و خوشبختی راستین در زندگی، آن است که گاهی درد و رنج‌هایی کوتاه و گذرا را به جان بخرید، چرا که شادمانی برتر و نهایی، همان منش و ماهیتی است که برای خود رقم می‌زنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(923,'زمانی که رشد می‌کنید و در زندگی مردم، کارآیی مثبت دارید، زندگی را به گونه‌ای دیگر تجربه می‌کنید که همان خوشبختی و شادمانی راستین است.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(924,'چنانچه معنی و مفهوم و هدف زندگانی را ندانیم و ندانیم که چه چیزهایی در زندگی، ارزش و اهمیت دارند، به سادگی نمی‌توانیم تصمیم بگیریم و همیشه دودل و مردد خواهیم شد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(925,'چنانچه ندانیم چه می‌خواهیم، هر بادی، باد سازگار خواهد بود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(926,'چنانچه خواسته‌ها و اهداف خود را نشناسیم، کشتی بدون سکانی خواهیم بود که در اوج مثبت‌اندیشی، همواره مسیر خود را دگرگون می‌کنیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(927,'انسان‌های کامروا، قوانین دست و پاگیر کمتری دارند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(928,'انسان‌های خوشبخت و شادمان، قوانین ساده‌ای دارند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(929,'انسان‌های خوشبخت و شادمان، کسانی هستند که این شایستگی و توانایی را دارند که از تماشای یک لبخند زیبا، شاد شوند و احساس خوشبختی کنند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(930,'همواره به یاد داشته باشیم که هرگز ناگزیر نیستیم کارهای بزرگ انجام دهیم و چَم و مفهوم و هدفی بسیار بزرگ، آرمانی و آسمانی را در زندگی خود بیافرینیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(931,'من بر این باورم که کارهای ساده و پیش پا افتاده هم بسیار نیرومند و کارآمد هستند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(932,'توانایی‌ها و شایستگی‌های خود را گسترش بخشید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(933,'کمال‌گرایی راستین و بنیادی آن است که خود را همان گونه که هستیم، بپذیریم و دوست داشته باشیم.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(934,'تا زمانی که با دیگران درباره‌ی استانداردها و قوانین‌شان گفتگو نکرده‌اید، نمی‌توانید ادعا کنید که قوانین آنها را می‌دانید و نمی‌توانید این چشم‌داشت را از آنها داشته باشید که قوانین شما را ندیده و نشنیده بگیرند و با آن همساز باشند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(935,'زمانی که از استانداردها و قوانین بنیادی و باید و نبایدها و قوانین همیشه باید و... هرگز نباید... دیگران آگاهی یابید، روابط‌ تان با آنها ساده‌تر می‌شود.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(936,'هرگاه می‌خواهید دست به کاری بزنید و دل آسوده نیستید که چرا می‌خواهید چنین کاری بکنید، دوباره آن را با چَم و هدف زندگانی خود بسنجید و با آن مشورت کنید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(937,'گاهی، نرسیدن به یک رویا که با ناشکیبایی و مشتاقانه، خواهان آن هستید، آنچه را به راستی می‌خواهید و به آن نیاز دارید و برای آن ساخته شده‌اید، به شما می‌دهد و می‌بخشد.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(938,'شاید تلاش‌های شما تا به امروز ثمربخش نبوده‌اند، ولی سبب کامیابی‌های دیگری در آینده شده‌اند و زمینه‌ی خوبی را برای کامیابی نهایی‌تان فراهم ساخته‌اند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(939,'ناکامی‌ها و شکست‌های ظاهری، زمینه‌ساز و سکویی برای جهش‌ها و پرش‌های دیگر شما به سوی کامیابی نهایی هستند.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(940,'عشق بورزید، دوست داشته باشید، ببخشید و از خودتان مایه بگذارید.((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(941,'هدف زندگی خود را برآورده سازید و مهم‌تر از همه آنکه این کار را با عشق، شور، حرارت، شیفتگی و سرزندگی بسیار انجام دهید!((آنتونی رابینز))',7,0,0,0,0,0);
INSERT INTO mysms VALUES(942,'اگر دانش را به خاطر کسب درآمد فرا می گیرید، در حق آنهایی که به خاطر خودشان دانش اندوخته اند، تجاوز کرده اید.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(943,'هرگز برای بهره مندی از خوشبختی، امروز و فردا مکن.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(944,'هر کس باید راه زندگی خودش را پیدا کند و از راه زندگی خودش برود نه از راه زندگی دیگری.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(945,'من احساس می کنم پس هستم.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(946,'بکوش تا عظمت در نگاهت باشد نه در آنچه می نگری.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(947,'هیچ کس موفق نمی شود سرزمین جدیدی را کشف کند، مگر اینکه بپذیرد مدت زیادی رنگ خشکی را نبیند.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(948,'هرچه بالاتر روی، از نظر آنان که پرواز نمی دانند کوچکتر به نظر خواهی رسید.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(949,'بهتر است برای چیزی که هستی، مورد نفرت باشی تا اینکه برای چیزی که نیستی محبوب باشی.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(950,'داشتن احساس حقیقی از تظاهر به آن احساس، کمابیش غیر قابل تمییز [ =بازشناختن ] است.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(951,'این از ویژگی های عشق است که هیچ گاه ثابت نمی ماند؛ عشق، بدون وقفه رشد می کند، در صورتی که کاهش نیابد.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(952,'من بر این باورم که عشق به یک شخص ِبزرگوار، بیش از هر چیز دیگر به انسان فروتنی می آموزد.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(953,'خرد برخاسته از منطق نیست، برآمده ار عشق است.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(954,'ادبیات بد، نتیجه ی احساساتی است که در نگاه نخست بسیار هیجان انگیز و شکوهمند است.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(955,'هنر، همکاری میان خدا و انسان است و در این همکاری، زمانی که انسان کمتر دخالت می کند، نتیجه عالی تر است.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(956,'همه چیز پیش تر گفته شده، اما چون کسی [ آن را ] گوش نکرده، دوباره برمی گردیم و تکرارش می کنیم.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(957,'مجازاتی در جهنم وجود ندارد جز اینکه مجبوری کارهای ناتمام را تمام کنی.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(958,'زیباترین مطالب آنهایی است که با جنون شروع شده اند اما خردمندانه نوشته شده اند.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(959,'به کسی که حقیقت را جستجو می کند باور داشته باش و به کسی که مدعی دستیابی به حقیقت است شک کن.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(960,'کارهایی هست که دیگران هم می توانند انجام دهند، آن را انجام نده. حرفهایی هست که دیگران هم می توانند بزنند، آن را بیان نکن و چیزهایی هست که دیگران هم می توانند بنویسند، آن را ننویس! کاری را بکن که فقط تو می توانی انجامش بدهی.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(961,'هرگز برای خوشبختی، امروز و فردا نکن.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(962,'به نظر من، ما روزی خواهیم مرد که نخواهیم و نتوانیم از زیبایی لذت ببریم و درصدد نباشیم آن را دوست بداریم.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(963,'بزرگترین شادی، جدایی از عشق ورزیدن، بیان کردن و نشان دادن عشق است.((آندره ژید))',8,0,0,0,0,0);
INSERT INTO mysms VALUES(964,'هر بشری می تواند در ژرفای اندیشه ی خود پناهگاهی بسازد و در آن جای امن، هر گلوله ی سنگین و گران و هر سخن نابجا و نیشدار و مسمومی را ناچیز شمرد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(965,'یک روح آرامش جو که آرامش و مسالمت [ =سازواری ] را در سراسر وجود خود احساس می کند، چه ترسی ممکن است به خود راه دهد؟((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(966,'با تمام وجود نسبت به کسی که سزاوار و شایسته ی عشق شما به نظر می رسد سوگند وفاداری یاد کنید.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(967,'درد حقیقی پیری در ضعف جسمانی نیست، بلکه در سهل انگاری و بی باکی روحی است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(968,'به راستی تسلی بزرگی است که کسی بتواند در زمان های درد و تنهایی و فراق، دست کم یک خاطره ی کامل درخشنده را به یاد آورد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(969,'برای کار کردن کافی نیست که انسان پشت یک میز بنشیند، بلکه باید خود را در هنگام کار از همه ی اندیشه های گوناگون پاس داشته و در امان نگاه دارد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(970,'هدف آموزش و پرورش این نیست که متخصص و اهل فن تحویل بدهد، بلکه هدف آموزش این است که فکر و هوش عالی بسازد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(971,'به هر اندیشه و هوش و ادراکی، غذای ویژه ی خودش را باید تجویز کرد که مطابق با اشتهای آن اندیشه باشد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(972,'در ادبیات نیز مانند عشق نمی توان به گزینش دیگری اعتماد کرد. باید که مؤمن و معتقد و وفادار باشیم نسبت به آنچه که طبع و اندیشه ی ما می پسندد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(973,'صرف داشتن موهبت و استعداد کافی نیست، زیرا اگر آن را به حال خود رها کنند عقیم می ماند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(974,'شعر، گونه ای از حالت تاثر و هیجان است که در آن حالت، آرامش و سکون به انسان دست می دهد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(975,'کار، از بین برنده ی کسالت و عیب و نیاز است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(976,'افراد بشر هیچ گاه نمی توانند به اقدامی مفید مبادرت ورزند و به عملی مشترک دست زنند، مگر اینکه یکی از میان آنان برخاسته و تلاش همه را هر لحظه به سوی هدف مشترک رهبری کند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(977,'هر عمل دسته جمعی وقتی درست رهبری نگردد دچار آشفتگی و بی نظمی می شود.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(978,'بدون وجود وجود فرمانده ی شایسته و سزاوار، عیچ عمل نظامی، هیچ حیات ملی و هیچ زندگانی اجتماعی وجود ندارد و نمی تواند وجود داشته باشد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(979,'پیرمردان بسیارند و برای برگزیدن و بهره گیری از آنها نباید دنبال شناسنامه و تاریخ ولادت آنها رفت و کهن ترین آنها را برگزید، بلکه باید به دنبال بهترین آنها رفت.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(980,'هرم ها از رأس ساخته می شود و چیزی که در معماری بسیار خطرناک است در اداره ی امور عمومی آسان به نتیجه می رسد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(981,'مرد اگر نمی خواهد در مرحله ی عشق و محبت خسته شود، باید با حسن نیت، اهمیت مقامی را که عشق در زندگانی و حیات یک زن دارد درک کند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(982,'هیچ کاری ممکن نیست برای مملکت انجام گیرد، مگر اینکه هوی و هوس و خواهش های نفسانی و هیجان های روحی مانند عشق و خشم و جز آن در امور مملکتی راه نداشته باشد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(983,'نخستین ویژگی مورد نیاز یک مدیر، داشتن اراده است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(984,'در حقیقت، هیچ چیزی برای فرمانبران و زیردستان آنقدر باعث دلسردی و دلسوختگی از کار نمی شود که داشتن یک مدیر سست عنصر و دو دل و دورو.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(985,'نمود شخصی همان میزان حکومت می کند که خود شخص.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(986,'یک مدیر حقیقی هیچگاه گمان و باور ندارد که یک هدف که برآورده شد، دیگر همه ی کارها جاودانه و منظم و روبه راه شده است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(987,'باغی که خوب از آن مراقبت به عمل آمده است اگر مدت زمانی به حال خود رها شود، همه ی آن را علف های هرزه و بد فرا می گیرد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(988,'مدیر خوب می داند که هیچ نتیجه ی مطلوبی هیچ گاه به دست نخواهد آمد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(989,'ارتش بزرگ همیشه فرمانده ی خود را سرمشق عمل خویش قرار می دهد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(990,'خونسردی، بزرگترین صفت برای کسی است که امر فرماندهی را بر عهده گرفته است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(991,'گاهی عامل زمان آنقدر مهم است که باید تنها عامل روشن و فرضیه ی اساسی و اصلی برای پاسخ به مسأله قرار گیرد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(992,'در دنیا عده ی اندک شماری یافت می شوند که می توان به آنها اعتماد کرد؛ باید گشت و اینها را یافت.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(993,'مدیر واقعی و حقیقی به بشریت اعتماد نمی کند، اما به چند تن از مردان به تمامی اعتماد می کند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(994,'یک مدیر پر توقع و جدی همیشه بیش از یک مدیر سهل انگار و سست، مورد علاقه و محبت زیردستان است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(995,'راز اینکه انسان را دوست بدارند، این است که او دوست بدارد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(996,'بسیار اشخاص کم و نادری هستند که با سرشت های مشخص بتوانند در برابر قدرت بلامنازع [ =بی مدعی ] که به آنها داده شده مقاومت کنند و به بیان دیگر، خود را گم نکنند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(997,'آزادی، یک حق غیر قابل تصرف بشری نیست، بلکه یک پیروزی مورد پسند است که باید هر روز این پیروزی و فتح از نو آغاز گردد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(998,'هیچ رهبری نمی تواند قدرت خود را پاس دارد، مگر اینکه هر روز خود را سزاوار آن قدرت نشان دهد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(999,'طوفان، درد و تباهی را آشکار ساخته، اما او به هیچ وجه آفریننده ی تباهی و درد نبوده است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1000,'گرگ پیر تا زمانی که قدرت آن را دارد که طعمه ای به چنگ آورد و آن را از هم بدرد مورد احترام [ =بزرگ داشتن ] است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1001,'دگرگونی های پرشتاب و نوآوریهای شگفت انگیز دلیل پیروزی جوانی است و استقامت و پایداری، ثبات و استحکام سنن و عادت ها، دلیل بر حیثیت و شرف پیری است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1002,'زندگانی، یک نمایش و سینمای همیشگی است. تماشاچیان صحنه ی زندگانی یکی پس از دیگری برمی خیزند و از سینمای زندگانی بیرون می روند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1003,'رمز پاس داشتن چابکی و نرمی و ظرافت آن است که انسان هیچ گاه ورزش را ترک نکند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1004,'آنچه را که دیروز انجام شده است امروز نیز می توان انجام داد، اما آنچه که انجام نشده برای همیشه از دست به در شده است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1005,'فن پیر شدن عبارت است از جنگ و نبرد در برابر بدی ها و معایب پیری، که در پایان زندگانی ما، یعنی دوره ی نیکبختی و خوشبختی ما پا به عرصه ی ظهور می گذارد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1006,'هر عملی که برای جوان گردانیدن و بازدارندگی از آشکار شدن پیری باشد یک عمل و رفتار ناشی از تمدن و مدنیت است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1007,'عشق پیرانه سر نیز می تواند آن قدر مؤثر و گیرا و همان اندازه صادقانه و صمیمانه باشد که مهرورزی ایام جوانی، صادقانه و صمیمانه و مؤثر و گیرنده است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1008,'یک پیرمرد بدون اندوه و با نشاط هرگز بدون دوست و رفیق نخواهد ماند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1009,'اگر همه ی مردم می دانستند که همه ی مردم از همه ی مردم چه چیزها که نمی گویند، هیچ کس به هیچ کس سخنی نمی گفت.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1010,'عیب و علت واقعی و اصلی پیری در ضعف جسمانی نیست، بلکه در بی علاقگی روحی است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1011,'مردمی که آرام تر و کندتر پیر می شوند آنهایی هستند که با فرهنگ و فرزانگی، دلیل و برهان زندگانی را درک نموده اند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1012,'در بسیاری از زمانها، پیرمردان بهتر از جوانان، راه و رسم فرمان دادن و اداره کردن را می دانند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1013,'فن و هنر پیری آن است که انسان سالخورده خود را به منزله ی تکیه گاه زندگانی و به منزله ی مرد مورد اعتماد نسل های آینده قرار دهد، نه اینکه خود را در برابر جوانان به منزله ی یک دشمن و رقیب نمایان سازد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1014,'یک مرد خردمند، پس از آنکه زمانی را صرف کار و تلاش ویژه ای کرد، بقیه ی عمر را جز صرف آسایش خویشتن و فرهنگ و تربیت خاص خود نخواهد ساخت.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1015,'برای مردمانی که جز به تکمیل احساسات بشری نمی اندیشند، موسیقی پناهگاهی شایسته و ابزار سرگرمی مؤثری است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1016,'قدرت و توان بیش از آنکه مربوط به سن و سال باشد، به تندرستی و سلامت وابسته است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1017,'مسرت ها و خوشی های زندگانی در سنین پیری بسیار لذت بخش تر و گواراتر از دوران جوانی است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1018,'پیروزمندان! فراموش نکنید که پیروزی های بشری همیشه نسبی و موقتی است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1019,'هیچ پیروزی ای آینده ی ما را در نظر ما مشخص نمی سازد و نمی تواند باعث اعتماد ما به آینده ی دور و درازی باشد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1020,'هیچ انقلابی نتوانسته است نیکبختی و خوشبختی جاودانه ی اجتماعی را تضمین کند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1021,'ثروتها و نام آوریهایی که زاده ی یک لحظه از عمر اند در یک لحظه از زمان فنا می شوند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1022,'جنگ و نبرد زندگانی باعث پایداری و قوت شما خواهد شد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1023,'دانشمند کسی است که از بررسی ها و تجربه های خود، حدس ها و قیاس هایی [ =سنجش ] بدست آورد که دارای روابط همیشگی و ثابت از نظر فنون (حوادث حسی) باشند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1024,'امید و رویای یک مرد عمل این است که در زمانهای بی نهایت دشوار و در موارد پیچیده و سخت، نسبت به ادراک حیوانی و هوش فطری خویش اطمینان خاطر کامل به دست آورد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1025,'دانسته ها و آگاهی های ما جز در موارد لازم و مناسب از آنِ ما نیستند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1026,'یک مرد بزرگ عمل، به یک شاعر بایستی بسیار بیشتر نزدیک باشد تا به یک مؤلف دایره المعارف... .((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1027,'برای مرد عمل، اندیشه با عمل درهم است، همان گونه که برای یک شاعر، اندیشه با تصویر و نشانه های درهم و مربوط می باشد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1028,'کسی که بخواهد همه کاره باشد هیچ کاره است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1029,'به راستی عبث و بیهوده است که انسان از طبیعت چیزی را بخواهد که به او داده نشده است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1030,'تا زمانی که کارت به انجام نرسیده، نباید از راهی که برگزیده ای، از انحراف و تغییر جهت روی برتابی.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1031,'باید در هر کاری به امکان و عملی بودن آن کار ایمان داشت.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1032,'بهترین خلق و خوی، بردباری و متانت و به ویژه، خوش منشی و شوخ طبعی است که توانایی و قدرت آن را دارد که برای یک زن و شوهر خوشبختی فوق العاده به بار آورد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1033,'آگاه باشید! دوست داشتن و عشق ورزیدن را باید احساس کرد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1034,'عشق مانند آن بوته ی خاری است که هرچه بیشتر در کندن آن از زمین تلاش شود، آن خارها بیشتر در جسم و گوشت طرف مقابل نفوذ و رسوخ می نماید.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1035,'انسان همیشه در پی چیزی است که از او دوری می جوید و رد می کند چیزی را که به او تسلیم می گردد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1036,'کسی که در دسترس و معرض دید و همنشینی قرار ندارد آسان می تواند مورد تحسین و ستایش قرار گیرد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1037,'وقتی که غذایی به عشق و محبت نرسد و بی قوت بماند، یا به خواب می رود و یا فنا می شود و یا به علت نقص کمک از مبدأ کمال و فراوانی دچار کاستی می شود.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1038,'ستایش، خود گونه ای از هدیه و ارمغان است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1039,'اگر زن درصدد تلاش و کوشش مجدد برای دور کردن مرد از شغل اش برآید، جز اینکه کینه و بغض مرد را به خود متوجه سازد نتیجه ی دیگری نمی گیرد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1040,'بهترین کتابها و رمان های عاشقانه آنهایی هستند که به آنها که سزاوار حقیقی محبت و عشق اند، راه و رسم زندگانی را بیاموزند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1041,'یکی از موارد اساسی آیین دلبری این است که شخصی که می خواهد محبوب باشد باید وقت بیشتری را صرف تربیت و فرهنگ خویش نماید.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1042,'هر کاری که با عشق و محبت آغاز می گردد، لذت بخش و سرورآمیز است، اما عشقی که با کار آمیخته شود لذت اش بیشتر و مسرت و سرور آن افزونتر است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1043,'همان گونه که هر چیز تازه ای، جذابیت و تمایل بیشتری دارد، در برابر، از قابلیت فناپذیری بیشتری نیز برخوردار است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1044,'استعداد و ذوق همیشه برای زن و شوهر، تازگی و تجدد و تنوع پایدار و همیشگی ایجاد می کند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1045,'کسی که دیگری را دوست دارد و به راستی به او عشق می ورزد، هر روز تلاش می کند یک کشیش دهکده ای باشد که دوست دارد همه شب خیابان های تنگ و پرپیچ و خم خود را طی کند و در محیط اندیشه و هوش و استعداد محبوب خود گردش نماید.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1046,'بزرگترین رمز خسته نشدن، طبیعی بودن است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1047,'اگر ما دلدار خود را با ویژگی های خاص خودش برگزیده ایم، باید بگذاریم که او در حرکات و پرورش و نمو آزاد باشد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1048,'عشق نیاز به تجزیه کننده ندارد، بلکه برای بیان نیازمند شاعر است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1049,'کسی که با نیروی تمام دوست دارد و با قدرت کامل عشق می ورزد، می فهمد که اگر لازم باشد باید خود را تازگی مجدد بخشد و دم به دم خویش را نو و نوتر نماید.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1050,'یک عشق بزرگ، به موجودات بسیار ساده قدرت و قابلیت تیزهوشی، از خودگذشتگی و اعتماد و اطمینان خاص می بخشد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1051,'برای همگان، حکومت کردن بر خانه از هر جای دیگر و هر امر دیگری دشوارتر است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1052,'در عمل، کسی که خود را می گذارد تا بخورند اش، خورده می شود و می میرد، بدون اینکه اثری از خود بر جای گذارد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1053,'اگر یک معاون و یا قائم مقام به گونه ای قلبی نپذیرد که مدیر مافوق او، دانسته‌های تجربه‌هایش بالاتر از او است، هرگز نمی تواند خدمتگزاری درست باشد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1054,'تا زمانی که انسان گمان می کند وجودش می تواند برای جلوگیری از خطا و اشتباه مفید باشد باید در پست خود باقی بماند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1055,'در رازداری، مسرت و سرور ویژه ی خودش نهفته است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1056,'رازداری نقشی است که حاضر جوابی و دانایی ویژه ی خود را لازم دارد، اما از همه جهت، مسرت انگیز و شعف [ =شادمانی ] آمیز است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1057,'سرور و مسرت بسیار زیاد به دست آمده از کار خوب به حدی است که می تواند جانشین تمام مسرت ها و خوشی های جهان گردد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1058,'بهشت یک باغبان، باغ او است و بهشت یک نجار، میز کارگاه او می باشد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1059,'زن خوب، وزیر دارایی خوبی برای خانه است و در اثر لطف و مهربانی و بینایی و بینش او است که بودجه ی خانه همیشه در تعادل است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1060,'یک زن از نتایج زحمت های خود در تنظیم خانه که دنیای کوچک کاملی است باید به همان میزان مغرور و مفتخر باشد که یک رجل دولتی برجسته ای که توانسته مملکتی را نظم و ترتیب بخشیده و سازمان دهد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1061,'هیچ آموزشی بدون کاردانی مناسب نیست.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1062,'چیزی که بدون زحمت و رنج آموخته شود روزی فراموش می شود.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1063,'بهتر است انسان میزان و مقدار کمی از امور را کامل فرا گیرد تا اینکه به طور متوسط مقدار زیادی از مواد را بیاموزد.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1064,'اشخاص عاقل می کوشند تا خود را همرنگ محیط سازند و اشخاص دیوانه سعی می کنند محیط را به رنگ خود درآورند. به همین جهت تحولات و پیشرفتهای جامعه به دست اشخاص دیوانه بوده است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1065,'حسود را آزار مده که خود در آتشی بی پایان می سوزد. هر چیزی که مطابق میل و آرزوی ماست، حقیقت محض به نظرمان می رسد و تمام آن چیزها که بر خلاف میل مان است، خشمناکمان می کند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1066,'بشر خیلی زود به خوشبختی عادت می کند و خیلی زود هم فراموش می کند که خوشبخت است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1067,'برای اینکه دوست پیدا کنی، باید خود را سزاوار و آماده دوستی کنی.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1068,'هر چیزی که مطابق میل و آرزوی باطنی ما است، به نظر، حقیقت محض و هر آنچه بر خلاف میل ما باشد باعث خشم مان می شود.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1069,'بعد از کتابهای آسمانی، شریف ترین و ثمربخشترین کتابها بیوگرافی و شرح حال است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1070,'افراد دانا کوشش دارند خود را همرنگ محیط سازند، اما اشخاص دیوانه سعی می کنند محیط را به رنگ خود در آورند. به همین دلیل، تحولات و پیشرفت های اجتماع به دست دیوانگان بوده است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1071,'بشر به امید زنده است و امید همان آرزو است.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1072,'خوب اندیشیدن آن است که ما از مدل و نمونه ی کوچک داخلی دنیای وجود خودمان بر اساس دنیای بزرگ و حقیقی، تصویر درست و روشنی که سزاوار وجود خود ما است بسازیم.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1073,'اگر با شتاب وارد مرحله ی قضاوت بی دلیل نشویم و شتابزده تصمیم نگیریم، آن وقت توانسته ایم گامی بزرگ به سوی فرزانگی و خرد و احتیاط و پرهیزگاری برداریم.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1074,'هیجانات شدید نفسانی و عشق و شهوت و خشم نمی تواند یک بشر را، یک انسان را راهنمایی و رهبری کرده و به هدف و مراد خود برساند.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1075,'هیچ تجربه و آزمونی را نمی توان دلیل اعتبار گرفت.((آندره موروا))',9,0,0,0,0,0);
INSERT INTO mysms VALUES(1076,'مغز بشر در زمان خود می تواند بهشتی از جهنم و دوزخی از بهشت به وجود بیاورد.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1077,'هر کنشی، واکنشی برابر دارد.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1078,'تمام ذرات جهان دارای نیروی مرموزی به نام جاذبه هستند که باعث جذب آنها به یکدیگر می شود و به طور معمول ذرات هر توده مجذوب مرکز آن و توده ذکر شده، خود، مجذوب توده دیگری است.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1079,'دانش کلی مانند خلیجی است که من و همکارانم تنها چند دانه ریگ جالب از این پهنای گسترده برداشته ایم.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1080,'شاید مانند کودکی باشیم که در کنار دریا با سنگ ریزه و صدفهای زیبا بازی می کند، اما بی خبر از آنیم که دریایی بس بزرگ و اقیانوسی بیکران در مقابل دیدگانمان وجود دارد که در اعماق آن اسرار بزرگ و شگفت انگیزی نهفته است.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1081,'آنچه می دانیم یک قطره است، آنچه نمی دانیم یک اقیانوس.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1082,'در حیرتم که وجود ناچیز خودم را در مقابل عظمت آفریدگان و این دنیای بزرگی که در آن زندگی می کنم، به چه تشبیه کنم.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1083,'کسی که فکر نمی کند، به ندرت دم فرو می بندد.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1084,'حتی حقیرترین موجودات نیز از نعمت های خداوند بی بهره نیستند.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1085,'تا آنجا که ممکن است، امروز کارت را خوب انجام بده، باشد که فردا هم قدمی رو به جلو برداری و کارت را بهتر انجام دهی.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1086,'من مشهور نشدم مگر به واسطه کار و کوشش و اگر از عملی خسته می شدم، تفریح و رفع خستگی را در اشتغال به کار دیگری جستجو می کردم.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1087,'من نمی دانم که جهان مرا چه می داند؟ اما من خود را مانند کودکی می بینم که در کنار ساحل سرگرم بازی است و گاه و بیگاه با یافتن سنگ ریزه ها و یا گوش ماهیهای زیباتر و صافتر از حد معمول، خوشحال می شود؛ حال آنکه اقیانوس بزرگ حقیقت همچنان کشف نشده پیش رویم گسترده است.((آیزاک نیوتن))',10,0,0,0,0,0);
INSERT INTO mysms VALUES(1088,'بزرگترین ثروت یک ملت، آزادی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1089,'آزادی بدون دانایی به دست نمی آید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1090,'یک آموزگار ناپخته می تواند سالها شاگردان خود را گمراه و سرگردان کند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1091,'اگر برای رسیدن به خواهشها و آرزوهای خود زورگویی پیشه کنیم، پس از چندی کسانی را در برابرمان خواهیم دید که دیگر زورمان به آنها نمی رسد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1092,'رَد راستی، رَد خویشتن است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1093,'گمان مبر که دیگران تو را به آرمانت خواهند رساند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1094,'آن که برنامه ها را از پایان به آغاز مورد گفتگو و ارزیابی قرار می دهد، براستی در حال سرپوش گذاری بر روی چیزی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1095,'گفتگو با آدمیان ترسو، خواری در پی دارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1096,'آن که پیاپی سخنتان را می بُرد، دلخوش به شنیدن سخن شما نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1097,'اگر آغاز زندگیت با سپیده دم و روز همزاد گشت، همواره در جست و جوی چراغ و پناهگاهی برای شبانگاهان باش و اگر در شب و سیاهی آغاز شد، چراغی از امید در خود بیافروز که پگاه خوشبختی نزدیک است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1098,'جایی که شمشیر هست آرامش نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1099,'ریشه کارمند نابکار در نهاد سرپرست و مدیر ناتوان است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1100,'اگر به سخنی که گفته اید با تمام وجود پایبند هستید، دیگر نیازی نیست برای آن پوزش بخواهید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1101,'نرم دلی و نرمش، منش آدمی و سنگدلی و سخت سری، منش اهریمن است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1102,'فرمانروایانی که یک شبه توانمند می شوند با تلنگری فرو می ریزند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1103,'اندیشه برتر در روزهای طوفانی و آشوب و در همان حال خموشی و آرامش، توانایی برتر خویش را از دست نمی دهد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1104,'کسانی که با دیگران رو راست نیستند با خود نیز بدین گونه اند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1105,'اگر شناخت زن و مرد نسبت به ویژگی های درونی و بیرونی یکدیگر بیشتر گردد، کمتر دچار گسست می شوند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1106,'آدمهای آرمانگرا زمانی که به نادرست بودن آرزویی پی می برند، برای ادامه آن پافشاری نمی کنند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1107,'اگر کسی را با زیباترین منشها و صفات بخوانیم چیزی از ارزش ما نمی کاهد، بلکه او را دلگرم ساخته ایم آنگونه باشد که ما می گوییم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1108,'ارزش استاد را دانستن، هنر نیست، بلکه بایستگی و وظیفه است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1109,'عدم پایبندی به نظم گیتی، ویژگی انسانهای گوشه گیر است که عشق و احساس را سپر دیدگاههای نادرست خود می سازند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1110,'هنگام گسستن و بریدن از همه چیز می توانی بسیاری از نداشته ها را در آغوش بکشی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1111,'خودبینی، چنبره و محیط گیرایی اش درون است و افتادگی، پیرامون و بیرون ماست و این می تواند همگان را به سوی ما بکشاند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1112,'بدبخت کسی است که نمی تواند ناراستی خویش را درست کند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1113,'آنان که خویی جانور گونه دارند و تنها در پی زدودن گرفتاریهای خویش و یا گهگاه افزایش قلمرو زمینی خود هستند، بزهکاران روزگارند. باید گفت نشان آنها بر گیتی هم تراز ریگ کوچکی در کرانه دریای بشری نیز نخواهد بود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1114,'بسیاری از چیزهای را که می خواهیم داشته باشیم می توانیم با نشان دادن توانمندی خویش به آسانی به دست آوریم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1115,'پیش از آنکه با کسی پیمانی ببندید، درباره توانایی خود در اجرای آن دمی بیندیشید و سپس پاسخ گویید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1116,'دیدگاه خوب مردم، بهترین پشتیبان برگزیدگان است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1117,'آنگاه که سنگ خویشتن را به سینه می زنید، نباید امید داشته باشید که همگان فرمانبردار شما باشند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1118,'فرهنگ های هم ریشه انگیزه ای توانمند است که موجب همبستگی کشورها در آینده می شود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1119,'کسی که نسبت به سرنوشت میهن و مردم سرزمین خویش بی انگیزه است، ارزش یاد کردن ندارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1120,'آدم خودباور هیچ گاه برای رسیدن به مادیات، ارزشهای انسانی را زیر پا نمی گذارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1121,'اهل فرهنگ و هنر، سازندگان آینده اند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1122,'در دوی زندگی همیشه حریف را شانه به شانه ات بپندار و همیشه با خود بگو تنها یک گام پیش ترم، تنها یک گام.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1123,'اگر می خواهی کسی را بزرگ کنی، پیوسته از او به دشمنی یاد کن.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1124,'پوزش خواستن پس از اشتباه زیباست، حتی اگر از یک کودک باشد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1125,'هرگز به کودک تان نگویید پیشه آینده اش چه باشد. همواره به او ادب و ستایش به دیگران را آموزش دهید، چون با داشتن این ویژگیها همیشه او نگار مردم و شما در نیکبختی خواهید بود و اگر این گونه نباشد، هیچ پیشه ای نمی تواند به او و شما بزرگواری بخشد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1126,'آنان که سامان و پویندگی در کیهان را دروغ می پندارند، همواره در اندیشه کین خواهی و حمله به جهان پیرامون خویش هستند؛ زندگی خود و نزدیکانشان را تباه می سازند و سرانجام در برآیندی بزرگتر از هستی ناپدید می شوند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1127,'به کوهستان که می نگرم، درونم سرشار از نیرو می شود. کوهها سر فرود می آورند و می گویند: باز ما را درخواهی نورد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1128,'مردمان توانمند در میان جشن و بزم نیستند. آنها هر دم به آرمانی بزرگتر می اندیشند و برای رسیدن به آن در حال پیکارند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1129,'قهرمان های آدمهای کوچک همانند آنها زود گذرند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1130,'فرومایگان پس از پیروزی، حریف شکست خورده خویش را به ریشخند می گیرند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1131,'یک آموزگار می تواند با رفتار و گفتار خود، کشوری را دگرگون سازد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1132,'آنکه به خرد توانا شد، ترس برایش نامفهوم است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1133,'دانش امروز شکوه بسیاری در پی داشته، اما نیروی آرامش بخشیدن به روان ما را ندارد؛ امنیت را بزرگان خردمند به ما می بخشند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1134,'آسیب دیده همیشه درهای رویاهایش کوچک و کوچکتر می شود، مگر با امید که شرایط ما را دگرگون می سازد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1135,'بسیاری در پیچ و خم یک راه مانده اند و همواره از خویشتن می پرسند ما چرا ناتوان از ادامه راهیم؟ به آنها باید گفت می دانی در کجا مانده ای؟ همانجایی که خود را پرمایه دانسته ای.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1136,'برای نوشیدن شهد بهشت، همواره نگاهت به راستی و درستی باشد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1137,'ستایش گر همیشه در حال پیشی گرفتن از ستایش شونده است؛ او یاد می کند و دلدار برآورده می سازد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1138,'خرد ابتدا به اندیشه پناه می برد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1139,'تبار بی ریش سفید همچون خانه بی سقف است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1140,'آدمی با کینه، زندگی را بر دوستان نیز تنگ می کند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1141,'نتیجه گیری زود [ هنگام ] پس از رخدادهای مهم زندگی از بی خردی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1142,'بیچاره مردمی که فرمانروایانش رایزن و رایزنانشان فرمانروا هستند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1143,'رایزنی که کار اجرایی می کند، قابل اعتماد نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1144,'چه بسیار گردن کشانی که به آنی دچار زبونی شدند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1145,'نخستین گام بهره کشان کشورها نابودی بزرگان و ریش سفیدان است و سپس تاراج دارایی آنها.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1146,'در پشت هیچ در بسته ای ننشینید به امید اینکه روزی باز شود، [ بلکه ] در دیگری را جستجو کنید و اگر نیافتید، همان در را بشکنید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1147,'تا چیزی از دست ندهی، چیز دیگری به دست نخواهی آورد؛ این یک هنجار همیشگی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1148,'کسی که چند آرزوی درهم ورهم دارد، به هیچ یک از آنها نمی رسد، مگر [ اینکه ] در بین آنها با ارزش ترین را انتخاب کرده و آن را به عنوان هدف خویش برگزیند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1149,'اگر از خودخواهی کسی به تنگ آمده ای، او را خوار مساز؛ بهترین راه آن است که چند روزی رهایش کنی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1150,'هیچ گاه در برابر فرزند، همسرتان را بازخواست نکنید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1151,'اگر روانتان در گفتگویی مورد حمله قرار گرفت، نمی توانید با کلمات، بیزاری از واژه ها را بجویید. تنها سکوت و ترک گفتگو نجات دهنده روان آسیب دیده شماست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1152,'دوستان فراوان نشان دهنده کامیابی در زندگی نیست، بلکه بیانگر نابودی زمان به گونه ای گسترده است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1153,'گربه را آزار مده، او نمی تواند به تو وفادار بماند؛ این هنجار عاطفی اوست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1154,'اگر می خواهی دوستی ات پا برجا بماند، هیچ گاه با دوستت شریک مشو.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1155,'بدان، همواره آنکه برای رسیدن به تو از همه چیزش می گذرد، روزی تنهایت خواهد گذاشت؛ این هنجار دردناک زندگی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1156,'برای آنکه همواره دوستانمان را نگاه داریم، بهتر است همیشه فاصله و بازه ای میان خود و آنها داشته باشیم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1157,'ترس از جدایی، جدایی به بار می آورد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1158,'پرحرفی در هنگام اندرز از اثر آن می کاهد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1159,'نگاه آدمهای کوچک چه زود پر می شود و لبریز.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1160,'سربازی که می ترسد، جان خود و دیگر سربازان را به خطر می افکند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1161,'همواره آدمیان، محیط و دایره بدی و پلیدی را با دانش و اندیشه برتر خویش بسته و بسته تر می سازند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1162,'وارونگی آدمیان و جانوران در پویایی اندیشه و دانش است، اما آدمی هر دم می تواند به رفتار و خوی بسیار وحشی دست یابد و دست به هر جرمی بزند که پلیدترین جانوران هم در بایسته ترین هنگامه از انجام آن خودداری می کنند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1163,'اگر در بند زندگی روزمره تان شوید، نمی توانید گامی به سوی بهروزی بردارید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1164,'آنکه برای بهروزی آدمیان تلاش می کند و راه درست را نشان می دهد، بارها و بارها می زید و تا یاد و سخنش جاری است، او زاده می شود و باز هم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1165,'هر پیوستنی، آگاهی و ثمره ای نو به ارمغان می آورد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1166,'هنر، خوراک روان و هنرمند، آفریننده آن است. بارگاه هنر با هیچ جایگاهی سزاوار ارزیابی نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1167,'شورشهای آدمیان با امواج پر نیروی کیهان خیلی زود به سامانه درست خویش باز می گردد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1168,'راهی جز نرمش و بازی با زندگی نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1169,'کسی که می ماند و نمی پرد، به یک راز بزرگ آگاه گشته و آن فلسفه پرواز است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1170,'هنگامی که می خواهی وظیفه و بایسته خویش را انجام دهی، از کسی فرمان نگیر.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1171,'خودت را بشناس اما به آن مبال.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1172,'یاد اشک و شیفتگی، آویزه خاموش دلهاست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1173,'برای آنکه روانت را بپروری، ابتدا با خود یکی شو.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1174,'ارزش پیمان شکن به اندازه کفن هم نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1175,'گیتی همواره در حال زایش است و پویشی آرام در همه گونه های آن در حال پیدایش است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1176,'آن که نمی تواند از خواب خود برای فراگیری دانش و آگاهی بکاهد، توانایی برتری و بزرگی را ندارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1177,'تن پوشی زیباتر از سرشت و گفتار نیکو سراغ ندارم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1178,'کیهان دارای ساختاری هدفمند است. این ساختار به آن پویایی بخشیده و برآیندی شگرف در آن بر جای می گذارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1179,'بهترین آموزگار استاد، شاگرد اوست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1180,'راه آشتی را کسی باید بیابد که خود سبب جدایی شده است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1181,'دشمنی با خاندان یک بدکار، تنها نشان ترس است نه نیروی انسانهای خردمند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1182,'فزون خواهی برای داشته های ما زیانبار است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1183,'خوار نمودن هر آیین و نژادی، به کوچک شدن خود ما خواهد انجامید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1184,'بسیاری از جنگها و آوردهای مردمی از روی نبود شناخت و آگاهی آنها نسبت به یکدیگر بوده است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1185,'آن که زیبایی خرد را ندید، گرفتار زیبایی آدمیان شد و بدین گونه از هر چه داشت تهی گشت.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1186,'جشن های بزرگ، انگیزه افزایش باروری و پویایی آدمیان می شود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1187,'بخشیدن تخت و اورنگ به خویشاوندان از زبونی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1188,'شباهنگام برای خانواده و نزدیکانت نامه بنویس و در روز برای اربابان و سرپرستان.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1189,'گیتی در جنگ و آوردی بزرگ در گردش است. اندیشه و تلاش خردمندان از یک سو و پوزخند اهریمن و روانِ دیوپیشگان از سوی دیگر، معرکه این جهان گذارا است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1190,'پیش نیاز دلیر بودن، [ داشتن ] بدنی ورزیده نیست؛ گاهی آدمهای سبکبار، پشت کوهستان را هم به خاک مالیده اند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1191,'اگر دشمنت با روی خوش نزدیکت شد، در برابرش خموش باش و تنهایش بگذار.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1192,'دشمنی و پادورزی به انسان خردمند انگیزه زندگی می دهد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1193,'خود را برای پیشرفت مردم ارزانی دار تا مردم پشتیبان تو باشند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1194,'تبهکار همیشه نگران کیفر خویش است، حتی اگر بر زر و زور لمیده باشد و این بسیار درد آور است، چرا که سایه کیفر همواره در برابر دیدگانش است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1195,'آزمودگی انسانها از زر هم با ارزش تر است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1196,'آن که پرسشهای پراکنده در وادیهای گوناگون را همزمان می پرسد، تنها می خواهد زمان و نیروی استاد را تباه کند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1197,'هر چه بلندپروازتر باشید تپش قلبتان کمتر خواهد شد، فشار و دردهای روانی تان نیز.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1198,'دوستی که نومیدنامه می خواند، همیشه سوار بر تو و پیشدار دورخیزهای بلندت خواهد شد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1199,'گِره هایی که به هزار نامه دادگستری باز نمی شود، به یک نگاه و یا ندای ریش سفیدی گشاده می گردد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1200,'بی مایگی و بدکاری پاینده نخواهد بود. گیتی رو به رشد پویندگی است. با نگاهی به گذشته می آموزیم که آدمیان، اشتباهاتی همچون برده داری، همسر سوزی و ... را رها کرده اند. خردورزی! آدمی را پاک خواهد کرد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1201,'سامانه همه گیر هستی با آنکه یکنواخت پنداشته می شود، رو به پویش و پیشرفت است. گردش آرام هستی نباید ما را فریب دهد. ما بخشی از یک برنامه بزرگ و پیش خواسته در کیهان هستیم که پیشرفت را در نهاد خود دارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1202,'هیچ گاه عشق به همدم را پاینده مپندار و از روزی که دل می بندی این نیرو را نیز در خویش بیافرین که اگر تنهایت گذاشت نشکنی و اگر شکستی باز هم نامید مشو؛ چرا که آرام جان دیگری در راه است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1203,'فرزند نانجیب، آتش عمر پدر است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1204,'افزایش ناگهانی مردم سبب کاهش ادب، فرهنگ و فرهیختگی می گردد؛ آنهم از آن رو که نسل و تبار پیش از آن نمی تواند بایستگی ها و وظایف خویش را همچون آموزگار به درستی انجام دهد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1205,'خاموشی بیشه نبرد، فریادها در سینه دارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1206,'آغاز هر روز، نو شدنی دوباره و زمانی برای پویایی بیشتر است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1207,'مرد دلیر بهنگام ستیز و نبرد، همراهانش را نمی شمارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1208,'سرپرستانی که از ارزش سربازی می کاهند و پدر و مادرانی که مانع میهن داری فرزندان خویش می شوند، به کشورشان پشت کرده اند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1209,'پیامد دانایی، پذیرفتن بار ساماندهی دیگران است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1210,'آینده جوانان را از روی خواسته ها و گفتار ساده شان می توان حدس زد. مپنداریم که میزان دارایی و یا امکانات آنها دلیلی بر پیروزی و شکست آنهاست، تنها [ مسئله ی ] مهم، خواسته و آرزوی آنهاست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1211,'استخوان بندی شهرها در دیوار است و بزرگداشت.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1212,'آنگاه که شب فرا رسید و همه پدیدگان فرو خُفتند، ابر دریاها (اقیانوسها) به پا می خیزند، آیا تو هم بر می خیزی؟((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1213,'هر آن که خردمندتر است اهریمن بیشتر به او حمله می کند. در میدان جنگ و آوردِ او، هزاران فتنه و افسون کشته شده را خواهی دید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1214,'بدخو، عمرش کم است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1215,'اگر میرآب، به اندازه دهد، همه دشت سبز باقی خواهد ماند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1216,'چه نشانی از بد اندیش به جا است؟ هیچ.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1217,'تنها مادر و پدر خواسته های فرزند را بی هیچ چشم داشتی بر آورده می سازند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1218,'همواره تنهایی، توانایی به بار می آورد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1219,'برای اهل اندیشه هیچ دوستی بهتر از تنهایی نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1220,'کار، بهترین آرامبخش افکار پریشان و غم است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1221,'هنرمند و نویسنده مزدور از هر کشنده ای زیانبارتر است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1222,'سازگاری با زیستگاه و تلاش برای بهتر شدن جایگاه کنونی، ویژگی ناب آدمهای پاک است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1223,'ثمره کشتن، کشته شدن است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1224,'غروب جان آدمی، سپیده دمی به جهان دیگر است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1225,'خواسته مراد از مرید، خاموشی و ژرف نگری است و خواهش مرید از مراد، نشان دادن مسیر پیشرفت است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1226,'توان آدمیان را با آرزوهایشان می توان سنجید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1227,'تاراج و شورش هیچ گاه بهانه تاراج و شورش دیگر نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1228,'وزن یک تهمت، سنگین تر از هزار هزار دشنام است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1229,'آنکه کارش را دوست ندارد، خیلی زود پیر می شود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1230,'شناور بودن خرد آدم در جهان احساس، میدان بروز و رشد هنر را به او داده است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1231,'برخی از انسانها مانند گلهای لاله، زندگی کوتاه در هستی و نقشی ماندگار در اندیشه ما دارند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1232,'برای پویایی و پیشرفت، گام نخست از پشت درهای بسته برداشته می شود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1233,'رویش باغ سکوت در هنگامه خروش و همهمه ارزش خود را نشان می دهد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1234,'آنان که از رسیدن به ریشه ها هراس دارند، در روزمرگی دست و پا می زنند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1235,'خودخواهی، کاشی سادگی روانت را خواهد شکست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1236,'نگارنده و سخنگویی که دیگران را کوچک و خوار می دارد، خود چیزی برای نمایش و بروز ندارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1237,'برای کسب خرد، سختی را بر خود هموار کن.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1238,'کژی و ناراستی، شکاف و رخنه گاه اندیشه اهریمن خواهد شد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1239,'کسی که درد روشنگری و بازگویی تجربه را ندارد، خود نیز زمانی برای بهره بردن از آن نخواهد یافت.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1240,'جفتت اگر پرید، برای پریدن عجله نکن.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1241,'هنگامه رهایی اهریمن، هنگامه دربند شدن تو است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1242,'از آه و نفرین بزرگان و ریش سفیدان هر ایلی باید ترسید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1243,'ناتوان ترین آدمیان کسانی هستند که نیروی بدنی خویش را به رخ دیگران می کشند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1244,'بزرگواری، بی مهر و دوستی به دست نمی آید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1245,'بزرگترین کارخانه نابودی توانمندیها، آیین آموزشی نادرست است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1246,'تن پوشی زیباتر از سرشت نیکو نمی شناسم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1247,'پیرامونیان ما چه بخواهیم و یا نخواهیم، بر اندیشه های ما رخنه و اثر خواهند کرد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1248,'دارایی با ارزش تر از تندرستی نمی شناسم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1249,'ریگهای ساحل خرد، نشیمنگاه پندارهای پاک و شبانه توست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1250,'هیچ گاه دشمن خویش را کوچک مپندار، چون او بهترین دوستت نیز هست. او انگیزه پیشرفت و پویش بیشتر را به تو خواهد داد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1251,'برای رسیدن به نیک نامی، گواهی برتر از راستی نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1252,'برای شناخت آدمیان به جای کنکاش در اندیشه آنها، در پی شناخت پیشوای انگاره (فکر) آنها باشید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1253,'برای به دست آوردن و گسترش خرد، سختی را بر خود هموار کن.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1254,'پیران فریبکار، آتش زندگی جوانانند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1255,'پیامد دانایی، پذیرفتن بایسته هاست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1256,'تاریکی در زندگی ماندگار و ابدی نیست، برسان روشنایی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1257,'تنهایی برای جوان ارزشمند و برای پیر آزار دهنده است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1258,'با ترشرویی به میان مردم رفتن، تنها از بیماران ساخته است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1259,'آن که پندپذیر نیست، در حال افتادن در چاله سستی و زبونی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1260,'نامداری بی نیک نامی به پشیزی نمی ارزد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1261,'همرنگ دیگران شدن، باور هیچ کدام از بزرگان نبوده است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1262,'آدمهای بزرگ به خوشی های کوتاه مدت تن نمی دهند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1263,'آدم بی مایه، همه افراد را ابزار رسیدن به خواسته های خود می سازد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1264,'آن که دیگران را ابزار پرش خویش می سازد، تنها خواهد ماند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1265,'مردمان توانمند در خواب نیز رهسپار جاده پیشرفتند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1266,'نقد و ارزیابی بی کینه، پاداشی است که ارزش آن را باید دانست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1267,'دشمن، ابزار نابود ساختن آدمی را در درون سرای او جست و جو می کند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1268,'اندیشه و انگاره ای که نتواند آینده ای زیبا را نوید دهد، ناتوان و بیمار است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1269,'میهمانی های فراوان از ارزش آدمی می کاهد، مگر دیدار پدر و مادر.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1270,'در هنگام توانایی اگر بدهی دیگران را ندهی، بویژه هنگامی که او درمانده باشد، شکوه و جایگاه خویش را برای همیشه از دست می دهی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1271,'خویشتن و مردم را زمانی می شناسی که تنها شوی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1272,'خوارترین کارفرما کسی است که با ندادن و یا کم کردن دستمزد زیردست، فرمانروایی می کند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1273,'فرودستان در بهترین هنگامه هم بهانه های زیادی برای انجام ندادن کار های خود دارند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1274,'هیچ گاه از داشتن دشمن نترس، [ بلکه ] از انجام ندادن درست آرمانهای خویش بترس.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1275,'همای بخت بر شانه ات نخواهد نشست، مگر آنکه شانه ای به گستره و پهنای کوهستان داشته باشی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1276,'برای رسیدن به کامیابی نباید از شکست های پیشین خیلی ساده بگذرید، شناخت موشکافانه آنها، پیشرفت شما را در پی خواهد داشت.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1277,'آدمهای بزرگ و اندیشمند، بسیار اشک می ریزند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1278,'آنگاه که نمی دانم چه می گویم، جز راستی چیزی بر زبانم جاری نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1279,'همیشه به دنبال سرنوشتی بهتر برای خود باش و اگر آرمان تو کسب دانش است، به هر میزان در این مسیر رنج ببری، ارزشش را دارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1280,'هر آرزویی بدون پژوهش و تلاش به سرانجام نخواهد رسید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1281,'فرمانروایان نیرومند، مشاورانی باهوش و کارآمد در کنار خود دارند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1282,'الگوی انسانی شما هر چه بزرگتر باشد، سرنوشت زیباتری در برابر شماست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1283,'آنکه راستی نپوید، گرفتار آمیزش با اهریمن است. فرزند این آمیزش فتنه و شورش است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1284,'راز اندوختن خرد، یکرنگی و بخشش است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1285,'گاهی برای رسیدن به پیشرفت می بایست راه سخت کوهستان را برگزینیم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1286,'بخشیدن تخت و اورنگ به خویشاوندان، نتیجه ضعف و ناتوانی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1287,'امیدوار مباش که دیگران همراهیت کنند، تنها به درمان دردهای روزگار خویش بیاندیش.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1288,'مشورت با خردمندان، پیروزی در پی دارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1289,'پدر و مادر، زندگیشان را با فروتنی به فرزند می بخشند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1290,'اگر نتوانیم به تبار خویش سامانی درست ببخشیم، مانند این است که در خانه ای بی دیوار زندگی می کنیم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1291,'در برف، سپیدی پیداست. آیا تن به آن می دهی؟ بسیاری با نمای سپید نزدیک می شوند که در ژرفنای خود، نیستی به همراه دارند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1292,'هیچ پیر جهان دیده ای منکر برآیند زهرآلود دارایی حرام در زندگی آدمی نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1293,'نادانی، خودخواهی به بار می آورد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1294,'برای نزدیکی و همگرایی قوم خویش، کمک به یکدیگر و ستیز با نادانی و ناراستی را در پیش گیرید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1295,'آنان که تیشه به ریشه بزرگان و ریش سفیدان می زنند، خود و فرزندانشان را بی پناه خواهند ساخت.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1296,'آنکه می دزدد، جز حق خویش چیزی نمی ستاند. اما این ستاندن، نفرین و خواری ابدی در پی دارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1297,'اسنخوان بندی فریاد، پاسخ هزاران ستم بی صداست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1298,'آسودگی آدمی به گنج و دینار نیست، به خرد است و روشن بینی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1299,'اندرز پیران در بیشتر زمانها پر از خون و درد است که بدبختانه جوان از فهم آن ناتوان است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1300,'روز های خوش برای کژاندیش بسیار کوتاه و روز کیفر بسیار دراز است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1301,'آدم مادی گرا جاده های احساسش کم رفت و آمد است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1302,'سرآمد دشواری و سختی، دانایی است و دانا چشم خویش را بر بسیاری از زیبایی های زود گذر گیتی خواهد بست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1303,'اهل خرد و فرهنگ همیشه زنده اند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1304,'بداندیش همیشه کارش گره می خورد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1305,'نادان همیشه از طمع و فزون خواهی خویش خسته است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1306,'اگر جانت در خطر بود، به جای پنهان شدن، بکوش همگان را از گرفتاری خویش آگاه سازی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1307,'اگر دست تقدیر و سرنوشت را از یاد ببریم، پس از پیشرفت نیز افسرده و رنجور خواهیم شد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1308,'آدمی می تواند بارها و بارها به شیوه های گوناگون قهرمان شود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1309,'گاهی تنها راه درمان روانهای پریشان، فراموشی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1310,'آدمهای پاک نهاد درهای وجودشان را پس از ناسپاسی می بندند، نه پیش از آن.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1311,'صدها راه برای پند و اندرز دادن وجود دارد، اما در بیشتر موارد، بدترین گونه آن را که همان راستگویی است، برمی گزینیم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1312,'بدگویی، رسوایی در پی دارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1313,'کردار ناپسند خویش را با دارایی زیاد هم نمی توانی پنهان سازی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1314,'پیام آوران باورهای پست و خُرد، بزرگترین پیروزیهای تاریخ مردم خویش را با گفتاری پست به ریشخند گرفته اند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1315,'شکست های زندگی، درهای پیروزی را می گشاید و خودپسندی، درهای پیروزی را یکی پس از دیگری می بندد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1316,'شما با گذشت، چیزی را از دست نمی دهید، بلکه به دست می آورید.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1317,'شالوده و زیربنای گسترش هر کشور، فرهنگ است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1318,'همه آدمیان به شیوه های گوناگون، سختی های روزگار را می چشند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1319,'زیباترین خوی زن، نجابت اوست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1320,'آیا از بخشندگی و مهربانی که نخستین ویژگی های خداوند است، در ما نشانی هست؟((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1321,'هیچ کس و هیچ چیز نمی تواند مانع پویندگی ما شود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1322,'نمی توان امید داشت که آدم های کوچک رازهای بزرگ را نگاه دارند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1323,'با ولخرجی تنها مال از دست نمی رود، زمان ارزشش فراتر است و آن هم نابود می شود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1324,'انسان خردمند، تار و پودهای اصلی زندگی را می یابد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1325,'بسیاری به دلیل برآیند هنجارهای درونی شان، بین نمای سپید و پاکی در اشتباهند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1326,'ره آورد سفر در درون آدمی به جز خرد و پیشرفت نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1327,'رهایی و آزادی برآیند پرستش خرد و دانایی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1328,'جام عمر را جز با می دلدادگی به خرد و دانش پر مکن.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1329,'خموشی، دری به سوی نگاه ژرفتر است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1330,'به ارزش نگاه دوست زمانی پی می بری که در بند دشمن و بدخواهان باشی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1331,'تنها با از خودگذشتگی برای دیگر آدمیان می توان جاودانه شد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1332,'هرگز هنگام گام برداشتن به سوی آرمان بزرگ، نگاهت به کسانی که دستمزد خود را پیشاپیش می خواهند نباشد! تنها به توانایی های خود بیندیش.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1333,'اندرز جوان باید کوتاه، تازه و داستان وار باشد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1334,'شب زندگی برای خردمند همچون روز روشن است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1335,'هنگامه شادمانه، سرودن و نواختن چه زود گذر و کوتاه است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1336,'روشنایی روز، دلیل و برهان نمی خواهد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1337,'بی گمان اولین باج، آخرین باج نخواهد بود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1338,'سخن های پست، آدمهای حقیر را جذب و خردمندان را فراری می دهد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1339,'خارهای کوچک زخمی به جان نمی زند، بلکه برای روزهای سخت تر با آن می آمیزد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1340,'سرایش یک بیت درست از زندگی نیاز به سفری هفتاد ساله دارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1341,'با احساس می شود پند و اندرز داد، اما ساماندهی به کشور نیاز به هنجار و چهارچوبی توانمند دارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1342,'آدم خودبین، چاره ای جز فرود آمدن ندارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1343,'این دیدگاه اشتباه است که بپنداریم مرد توانا فرزندی مانند خود خواهد داشت.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1344,'رازها در هنگامه شادی و بازی آدمی است. نکته فراموش شده جهان اندیشه، تعریف درست این حالتها است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1345,'سخن بدون پشتوانه یعنی گزاف گویی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1346,'ساده باش، آهوی دشت زندگی خیلی زود با نیرنگ می میرد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1347,'نکات سخت هم اساسی ساده دارند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1348,'در زندگی نادان، سرانجام یک گره [ مانند ] صدها گره باز نشدنی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1349,'آن که درست سخن نمی گوید، داناترین هم که باشد، همگان بی سوادش می پندارند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1350,'فرمانروا با اندیشه وزیران توانمند خویش فرمانروایی می کند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1351,'آرمان و انگیزه روشن، ویژگی انسان کارآمد است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1352,'دست استاد خویش را ببوس، چون او هم پدر است هم پرورنده خرد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1353,'به کارگیری آشنایان در یک گردونه کاری، برآیندی جز سرنگونی زود هنگام سرپرست آن گردونه نخواهد داشت.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1354,'شهامت گله ناشی از چوپان بیدار است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1355,'اگر آماده نباشیم، ارزشمندترین زمانها را نیز از دست خواهیم داد و کسی که آماده نیست، بخت کمتری برای پیروزی خواهد داشت؛ آمادگی یعنی به روز بودن در هر حرفه و کاری.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1356,'بخش بزرگی از ادب آدمی برآیند ریشه نژادی و خانوادگی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1357,'جز ناامیدی و افسردگی هیچ بن بستی در زندگی آدمی نیست.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1358,'اگر می خواهی بزرگ شوی، از کردار نیک دیگران فراوان یاد کن.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1359,'نماز عشق ترتیبی ندارد؛ چرا که با نخستین سر بر خاک گذاردن، دیگر برخواستنی وجود ندارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1360,'سختی های بزرگ به آدمی نیرویی دو چندان می بخشد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1361,'اگر بر ساماندهی نیروهای خود توانا نباشیم، دیگران سرنوشت مان را می سازند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1362,'در خواب می توانی نیروی روان خویش را بنگری.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1363,'چهارچوب نگاه ما زمینی است، اما برآیند اندیشه ما جنبه آسمانی نیز پیدا می کند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1364,'اندیشه و سخن ریش سفیدان، برآیند بردباری، مردم داری و سرد و گرم چشیدگی روزگار است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1365,'یکی از بزرگترین خوشبختی ها، خدمت بیشتر به مردم است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1366,'دودمانی که بزرگان و ریش سفیدانش خوار باشند، به کالبد بیجانی ماند که خوراک جانوران دیگر شود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1367,'هنرمندان ناب، هر روز در برابر دیدگان مردم نیستند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1368,'نیرنگ پیران بدنهاد تنها با مرگ به پایان می رسد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1369,'اگر شیفته کارت نباشی، روانت بیمار می شود و در نهایت پیکرت از پای در خواهد آمد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1370,'چه دودمانی از بد اندیشان برجاست؟ هیچ.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1371,'تنبلی، خشم بدن را در پی دارد و بدن چه زود لرزش و جنبش آن را بروز می دهد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1372,'کردار ناپسند خویش را با زر هم نمی توانی پنهان سازی.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1373,'رد راستی، رد خویشتن است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1374,'زیبارویی که می داند زیبای ماندنی نیست، پرستیدنی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1375,'دریاها نماد فروتنی هستند؛ در نهاد خود کوههایی فراتر از خشکی دارند، اما هیچ گاه آن را به رخ ما نمی کشند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1376,'در دراز مدت هیچ نیرویی نمی تواند در برابر فرهنگ و هنر ایستادگی کند.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1377,'از روی خواسته ها و گفتارهای ساده جوانان می توان به آینده آنها پی برد؛ تنها خواسته و آرزوی آنها گواه آینده است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1378,'کارکشتگی آدمیان از زر هم با ارزش تر است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1379,'گفتگو با خردمندان و دانشوران، یک گوهر و پاداش کمیاب است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1380,'گیتی برای انسان به اندازه ای کوچک است که مجالی برای کژی و ناراستی باقی نمی گذارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1381,'خرد باید نخستین خواست آدمیان باشد؛ چرا که بی خرد، چیزی برای خود ندارد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1382,'خوشنامی، بزرگترین شکوه و افتخار هر انسانی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1383,'دل کیهان را که بگشاییم این سخن را خواهیم شنید : هر کنشی، واکنشی را در پی دارد. پس بر این باور باشید! همه کردار ما چه خوب و چه زشت، بی بازگشت نخواهد بود.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1384,'پیوند پاک، پیوندی ابدی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1385,'پرنده کوچک دلت را برای من در بند مکن.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1386,'برای پرشهای بلند گاهی لازم است چند قدمی به عقب برویم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1387,'هیچ گاه برای شروع دیر نیست، با خود بگوییم این بار، کار تمام نشده را به پایان می رسانم.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1388,'کودکی که بدون پرسش ما به گناه خویش اعتراف می کند، در حال گذراندن نخستین گامهای قهرمانی است.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1389,'هیچ وقت خود را بی نیاز از مشورت با بزرگان ندانید. هیچ انسان تاثیر گذاری را نمی شناسم که در طول حیات خود از مشورت و نظرخواهی سود نبرده باشد.((اُرد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1390,'آه و بردباری، ریشه هر دیوزاد و بدخویی را از بین خواهد برد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1391,'آن که در بیراهه قدم بر می دارد، آرمان و هدف خویش را گم کرده است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1392,'درون ما با تمام جزئیات از نگاه تیزبین اهل خرد پنهان نیست.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1393,'ریش سفید، داراترین به اندیشه است نه به زر.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1394,'کلید رازهای بزرگ در ژرفای کمی نیست.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1395,'درون ما از نگاه تیزبین خردمندان پنهان نیست.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1396,'کسی که کردار شایسته را زیر پا می گذارد، توانایی این را نمی یابد که قدم بر نخستین پله پیروزی بگذارد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1397,'عشق همچون توفان سرزمین غبار گرفته، وجود را پاک می کند و [ در پی آن ] انگیزه رشد و باروری روزافزون می گردد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1398,'زاد روز ما با تاری نادیدنی به هزاران زاد روز دیگر گره خورده است. مرگ هم زاد روزی است همانند زاده شدن که بدرودی به جهانی دیگر است... .((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1399,'اهل بازار بر این باور اشتباهند که فرهنگ را هم می توان با زمان بندی دگرگون ساخت!((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1400,'افراد پلیدی هستند که با زمان سنجی مناسب از نگرانی های همگانی بهره می برند و خود را یک شبه ناجی مردم معرفی می کنند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1401,'همیشه آن که با شما هم آوا می شود و سخن شما را تکرار می کند نمی تواند هم فکر شما نیز باشد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1402,'چه بسیار آدمیان نادانی که مهربانی شایستگان را بر نمی تابند. آنها در نهایت یا به بردگی تیزدندانان گرفتار آیند و یا چهره زشت تنهایی را آشکارا ببینند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1403,'نگاه مردان کهن ایستا نیست؛ آنها دورانهای آینده را نیز به خوبی می بینند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1404,'رسانه تنها می تواند پژواک ندای مردم باشد، نه اینکه به مردم بگوید شما چه بگویید که خوشایند ما باشد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1405,'اهل سیاست پاسخگو هستند! البته تنها به پرسشهایی که دوست دارند!!!.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1406,'هیچ رخدادی از اندیشه ما پاک نخواهد شد؛ چه زشت و چه زیبا. تنها گذر زمان است که آنها را کمرنگ می کند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1407,'رسیدن به راستی و درستی چندان سخت و پیچیده نیست؛ کافی است کمی به خوی کودکی برگردیم.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1408,'جریان های آلوده به مرداب خواهند رسید؛ [ از این رو ] سخن گفتن از آنها زندگی مان را تباه می سازد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1409,'آرمان ما نباید موجب نابودی دیگران شود. آرمانی ارزشمند است که بهروزی ما و دیگران را در پی داشته باشد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1410,'ناراستی هایمان را بپذیریم؛ چرا که بهانه آوردن، سنگینی آنها را دو چندان می کند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1411,'اندیشه همه گیر مردمی همیشگی نیست، زیرا همواره دستخوش دگرگونی به دست جوانان پس از خود است. ورود جوانان به تدریج آرمانهای نو پدید می آورد و اگر آرمان گذشتگان نتواند خود را بازسازی کند ناگریز نابود می شود.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1412,'میهن دوستی، دسته و گروه نمی خواهد! این خواستی همه گیر است؛ که اگر جز این باشد باید در شگفت بود.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1413,'ابله ترین آدمیان کسانی هستند که با مسخره کردن شایستگان شاد می شوند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1414,'آدمیانی اندک، تنها به هدف می اندیشند! و بسیار کسان که هدفشان جز خانه نشین کردن همان گروه و دسته ی کوچک نیست.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1415,'پیشرفت، تنها در سایه ی آمادگی همیشگی ما به دست می آید.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1416,'بزرگترین اشتباه و چاله ی زندگی یک [ فرد ] زیاده خواه در آن است که پیش از کسب آمادگی لازم، پشت میز مدیریت بنشیند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1417,'پادشاهی که مردمش از او فروتر بنشینند در فزونی و برتری نیست؛ او در ته چاه کبر و خودبینی فرو افتاده است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1418,'دوستی تنها برآیند نیاز ما نیست؛ از خودگذشتگی، نخستین پایه دوستی است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1419,'ستایشگران میهن، زنان و مردان آزاده اند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1420,'اندیشمندان را شاید بتوان نادیده گرفت و یا به زور خفه نمود، اما تاریخ، گواه هزاران سال فریاد رسای آنان بوده و هست.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1421,'هیچ گاه از راستی و درستی خویش آزرده مباش؛ چون همیشه در انتهای هر داستان تو برنده هستی!((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1422,'با فریب شاید بتوان چیزی بدست آورد، اما در نهایت همان دستاورد مایه تباهی خواهد بود.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1423,'نخستین گام در راه پیروزی، آموختن ادب و نکو داشت دیگران است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1424,'اسطوره ها زاینده اند! آنها همواره برای فرزندان سرزمین خویش امید به ارمغان می آورند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1425,'مردانی که بیشتر از حقوق و هنجار زنها پشتیبانی می کنند، خود بیشتر از دیگران به نهاد زن می تازند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1426,'در هر سرنوشتی، رازی مهم فرو نهفته است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1427,'سرود هستی آهنگی ملایم و کشیده دارد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1428,'خودآختگی قابلیتی است که بسیاری ندارند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1429,'در زمان نکوهش هنگامه ی باران، به بهره اش نیز بیاندیش.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1430,'روان مردگان و زندگان در یک ظرف در حال چرخش اند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1431,'در کنارمان دلبر هست و اگر نیست، در سفر رسیدن به اویم؛ پس تنهایی وجود ندارد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1432,'تنها آرمانهای بزرگ است که به ما بینشی فرا دنیوی می دهد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1433,'گذشتن از سختی های پیش رو، چندان سخت تر از آن چه پشت سر گذاشته ایم نخواهد بود.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1434,'جهان همواره در حال دگرگونی و رشد است؛ نباید این پویندگی را زشت دانست، باید همراه بود و سهمی از این رشد را بر عهده داشت.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1435,'امروز به شمار نویسندگان، وب نوشت های اینترنت، بنگاه سخن پراکنی و خبرگزاری ایجاد شده است. پنهان سازی خبری در جهان امروز بسیار خنده آور و کودکانه است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1436,'ناراستی ها پیشاپیش رو به مرگ و نیستی اند، مگر آنکه ما آنها را در اندیشه و روان خویش زنده نگاه داریم.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1437,'واژه ها سرشار از پندارها است؛ ارزش آنها همپای زندگی است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1438,'انتخاب امروز ما، برآیند اندیشه ها و راه بسیار درازی است که تاکنون از آن گذشته ایم. این انتخاب می تواند سیمای جدیدی را از ما به نمایش بگذارد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1439,'وقتی ناگهان رخداد مهمی از درون دستگاه اداری کشور به رسانه ها کشیده می شود، نکته بسیار مهمتری از دید رسانه ها پنهان می ماند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1440,'آنگاه که زایش راهی نو را در درون خویش احساس کردی، پای در راهی خواهی گذارد که پیش تر برای رسیدن بدان بسیار تلاش کرده ای.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1441,'گاهی آدمها تا جایی به هم نزدیک می شوند که دیگر یکدیگر را نمی بینند! شاید دوری بتواند دوباره موجب شناخت درست آنها از یکدیگر شود.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1442,'زندگی، میدان ادامه ی راه اشتباه نیست؛ هر گاه پی به ناراستی راه خود بردیم باید به ریشه و بن پاکی خویش باز گردیم، نه آنکه با اشتباهی دیگر آن را ادامه دهیم که برآیند آن، از دست دادن همه عمر است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1443,'به آرزوهای خود ایمان بیاورید و به گونه ای به آنها بیندیشید که گویی به زودی رخ می دهند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1444,'کسی که آدم پیش رویش را آن گونه که هست نمی بیند، خیلی زود به مرز جدایی می رسد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1445,'با گفتن واژه هایی همانند نمی توانم! و یا نمی شود! هر روز پس تر می روید.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1446,'تواناترین افراد در بیشتر زمانها خود را ناتوان می یابند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1447,'انسانها را آنگونه که هستند بخواهیم نه آنگونه که می خواهیم.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1448,'اندیشه و انگاره ی بیمار، آینده را تیره و تار می بیند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1449,'تنها راه ماندگاری هر مراوده ی دوستانه ای، درک درست حقایق شخص مقابل است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1450,'اگر هدف زندگی روشن باشد، دهها راه بن بست نیز نمی تواند ما را از پیش رفتن به سوی آن باز دارد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1451,'آنچه رخ داده را باید پذیرفت، اما آنچه روی نداده را می توان به میل خویش ساخت.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1452,'خویش را خوار نکنیم و ارزشمندش بداریم؛ بدین گونه است که در برابر یاوه گویان می ایستیم و پاسخ شان را می دهیم.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1453,'آن که مدام از کمبودها و ناراستی های زندگی خویش سخن می گوید، دوست خوبی برای تو نخواهد بود.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1454,'خیال پردازی که عملگرا هم باشد می تواند سرچشمه ی دگرگونی های بسیار گردد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1455,'با کسی گفتگو کن که رسیدن به خرد و آگاهی، اندیشه ی اوست نه خویشتن خویش.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1456,'زمانی می توانید کسی را از راهی بازدارید که ابتدا هدفش را دگرگون ساخته باشید. تا کسی هدفش دگرگون نگردد شما راه به جایی نخواهید برد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1457,'آتش خشم را با آب سکوت خاموش کن.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1458,'قوم بی نیا و مرد کهن، به هزار آیین اهریمنی اداره می شود.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1459,'نگاه زمینیان، تهی از انوار آسمانیان است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1460,'اگر نتوانیم به خاندان خویش آرایشی سامان یافته دهیم، همیشه در خانه ایی بی حصار زندگی می کنیم.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1461,'در برف، سپیدی آشکار است. آیا تن به آن می دهی؟ بسیاری با نمایی سپید، در ژرفنای [ وجود ] خود نیستی را پرورش داده اند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1462,'ریش سفیدان، زنجیر ارتباط نسلها هستند و قوم بدون ریش سفید، گذشته ای کمرنگ دارد و آیین های به جای مانده به هزار گونه تفسیر می شود.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1463,'سفر، نای روان است برای اندیشه و آرمان بزرگ.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1464,'برای مقدس شمردن، بیداری و آگاهی لازم است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1465,'تلاش های سیاسی برای جوانان، مردابی مرگبار است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1466,'نرمش و سازگاری با گیتی، ما را از هر کمین دلهره آوری نجات خواهد بخشید.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1467,'پشتیبانی از داشته دیگران، پشتیبانی از داشته خود ماست.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1468,'اهل خرد، پیشتاز روزگار خویش اند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1469,'نادانی و پستی یک نفر در گذشته نمی تواند میدان انتقام از خاندان او باشد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1470,'خودخواه، تجربه سخت تنهایی را پیش رو دارد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1471,'با بردباری همه چیز در چنگ توست.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1472,'بزرگترین نابکاری آن است که بپنداریم برای آنکه برترین باشیم باید دست به ویرانگری چهره دیگران بزنیم.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1473,'برای ربودن دلهای آدمیان باید بر هم پیشی بگیریم و این زیباترین مسابقه زندگی است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1474,'موشکافی در شکست، پیشرفت در پی خواهد داشت.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1475,'برای کامیابی همواره نگاهت به راستی باشد و درستی.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1476,'برای دلهره شبانگاهان، نسیم گرمابخش خرد را همراه کن.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1477,'برای به دست آوردن گنج خموشی، بارگاه دانش خود را بزرگتر ساز.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1478,'برای ماندگاری، پنداری جز پاکی روان نداشته باش.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1479,'برای گردش دو روزه، توشه ای سه روزه همراه ببر و اگر توان رسیدن به خواسته زندگی ات را دو هزار گام می دانی، خود را آماده پیمودن سه هزار گام بنما، چرا که شتاب در هنگام رهسپاری نباید کم شود.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1480,'برای آنکه به پایین پرتاب نشوی، دست گیر آدمیان شو.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1481,'برای آنکه پرواز کنی، پیکر خویش را به حال خود رها مکن.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1482,'پیش نیاز رسیدن به دلیری و بی باکی، یافتن آرمان و خواسته ای روشن است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1483,'پیران دنیا دیده، سدی برای گفتگو برپا نمی کنند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1484,'پرسش روشن شاگرد از پاسخ استاد ارزشمندتر است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1485,'پرتگاه می تواند برآورنده روان و یا کشنده تن باشد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1486,'پشیمانی، اولین گام برای پوزش است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1487,'کارمندان نابکار بیشتر از دزدان و آشوبگران به کشور آسیب می رسانند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1488,'جایگاه ارزیابی و نقد شما بر کارکرد دیگران می تواند شروع نخستین گام شما برای سازندگی باشد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1489,'نیکی برآیند خرد است در دل و روان آدمی.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1490,'از سفر کرده، ارزش سرزمین مادری را بپرس.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1491,'آب و هوا بر جهان بینی، پبوندهای مردمی و اندیشه ما تاثیرگذار است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1492,'هر قدر به دیگران احترام بگذاریم، به ما احترام خواهند گذاشت.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1493,'مستمند کسی است که دشواری و سختی ندیده باشد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1494,'در هستی، جنبش حشرات هم چهار چوبی دارد.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1495,'انسانهای ماندگار به چیزی جز آرمان نمی اندیشند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1496,'دل به همدلی خوش است نه به شکستن.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1497,'هنرمندان و خردمندان قهرمانان ناب روزگارند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1498,'هر آرزویی بدون پژوهش و تلاش، سترون (عقیم) باقی می ماند.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1499,'آزادی، دادن باج به مردم نیست! چرا که مال و داشته آنها است.((ارد بزرگ))',11,0,0,0,0,0);
INSERT INTO mysms VALUES(1500,'هر کس عادت کند که بدون دلیل هر حرفی را باور کند، از صورت انسانی خارج است.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1501,'موسیقی، صدای خداست.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1502,'انسان، هر زمان از پیشامدِ آینده درباره ی خود اندیشید و از آن، بیم و هراس در خاطرش نِشاند، آن خطر، زودتر او را تعقیب می کند.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1503,'نیک بخت ترین مردم کسی است که کردار به سخاوت بیاراید و گفتار به راستی.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1504,'برخی چنان سرگرم میراث علمی گذشتگانند که فرصت مراجعه به عقل خود را ندارند و اگر فرصتی هم به دست آورند حاضر نیستند اشتباه ها و لغزش های آنان را اصلاح و جبران کنند.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1505,'تعصب در دانش و فلسفه مانند هر تعصب دیگر نشانه ی خامی و بی مایگی است و همیشه به زیان حقیقت تمام می شود.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1506,'کسی که به تمرینهای بدنی می پردازد به هیچ دارویی نیاز ندارد، درمان او در جنبش و حرکت است.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1507,'هیچ چیز در دنیا اتفاقی نیست.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1508,'چیزی که دانش می آراید راستی است.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1509,'اگر توانایی دانشمندان ایران از جهان دانش گرفته شود چیزی جز سیاهی باقی نمی ماند.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1510,'هر که دنیا خواهد، دانش آموزد و هر که آخرت خواهد در عمل کوشد.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1511,'نشان دوست نیکو آن است که خطای تو را بپوشاند، تو را پند دهد و رازت را آشکار نسازد.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1512,'اگر می دانستید که یک محکوم به مرگ هنگام مجازات تا چه حد آرزوی بازگشت به زندگی را دارد، آنگاه قدر روزهایی را که با غم سپری می کنید، می دانستید.((ابوعلی سینا))',12,0,0,0,0,0);
INSERT INTO mysms VALUES(1513,'ن که دانست زبان بربست و آن که می گفت ندانست.((احمد شاملو))',13,0,0,0,0,0);
INSERT INTO mysms VALUES(1514,'عشق، سوءتفاهمی است که با یک متاسفم فراموش می شود.((احمد شاملو))',13,0,0,0,0,0);
INSERT INTO mysms VALUES(1515,'لیس خور کفش ستمگران به پهنای زبان کفش لیس ها است و قدرت ستم آنها با توان تاب آوردن ستمدیدگان برآورده می شود.((احمد شاملو))',13,0,0,0,0,0);
INSERT INTO mysms VALUES(1516,'دنیایی که انسان ناگزیر باشد برای اثبات ناچیزترین حقوق خویش، تا حد مرگ سرود بخواند، دنیای بسیار زشتی است؛ دنیایی وارونه با مفاهیم وارونه.((احمد شاملو))',13,0,0,0,0,0);
INSERT INTO mysms VALUES(1517,'با مشاهده ی یک در، بی درنگ لزوم دیوارها احساس می شود. آیا با مشاهده ی یک دیوار هم، به همان اندازه لزوم یک در را احساس می کنیم؟((احمد شاملو))',13,0,0,0,0,0);
INSERT INTO mysms VALUES(1518,'آرمان هنر اگر جغجغه ی رنگین به دست کودک گرسنه دادن یا رخنه ی دیوار خرابه نشینان را به پرده ای تزئینی پوشاندن یا به جهل و خرافه دامن زدن نباشد، پیشرفت انسان است... هنرمند، بالقوه می تواند منجی جهان باشد، چرا که با یقین کامل می توان حکم کرد که دل و دماغ جلاد شدن را ندارد.((احمد شاملو))',13,0,0,0,0,0);
INSERT INTO mysms VALUES(1519,'انسانی که با اجتماع تکامل پیدا کرده باشد، از همه جانوران بهتر است و اگر بدون قانون و عدالت زندگی کند، خطرناکترین جانور است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1520,'ما همان چیزی هستیم که پی در پی انجام می‌دهیم؛ بنابراین، تعالی، یک کردار نیست، بلکه یک عادت است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1521,'بگذارید به گونه‌ای زندگی کنیم که گویی، موجوداتی فناناپذیر و جاودانه‌ایم.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1522,'ذهن فرهیخته، ذهنی است که بتواند اندیشه‌ای را بدون پذیرفتن آن در سر بپروراند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1523,'چیزی را که انسان برتر، خوب می‌پندارد، به راستی خوب است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1524,'خردمند آنچه را می داند، نمی گوید و آنچه را که می گوید، می داند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1525,'عشق، حواس را از دیدن عیبها منع می کند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1526,'دشمنی کسی را در دل راه مده که تو را همیشه غمگین می کند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1527,'راستی مثل گنج است؛ هر قدر آشکار گردد، پیروانش بیشتر می شود و دروغ مثل شعله پنهان آتش است که وقتی آشکار شد، سوزش و تباهی بیشتری به همراه دارد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1528,'اعتقادی که به زور به دست بیاید، ارزشی ندارد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1529,'ما زمانی می‌توانیم ادعا کنیم کاری یا پیشه‌ای را آموخته‌ایم که آن را انجام دهیم.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1530,'سربلندی کسانی که به خوبی به بچه‌ها آموزش می‌دهند، به همان اندازه‌ی کسانی است که آنها را به دنیا می‌آورند: گروه دوم به آنها زندگی می‌بخشد و گروه نخست، راه خوب زندگی کردن را به آنها می‌آموزد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1531,'انسان واقعی کسی است که از کمک کردن به دیگران احساس رضایت می کند، اما از اینکه کسی به او کمک کند خجل و شرمسار می شود، زیرا نیکی کردن به دیگران نهایت ایثار و بزرگواری است و کمک گرفتن از دیگران بیانگر ضعف و حقارت است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1532,'دوستی میوه دیر رسی است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1533,'هر کاری که می کنیم به انگیزه‌ی پاداشی انجام می‌دهیم.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1534,'دو چیز اندوه را از بین می برد: یکی دیدار دوستان و دیگر، سخن دانایان و خردمندان.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1535,'کارهای تکراری ما نشان دهنده شخصیت ما است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1536,'در زندگی، زمانی که کاری برای انجام یا چیزی برای عشق ورزیدن یا بارقه ای برای امیدوار بودن داشتید، آن گاه بدانید فرد شادی خواهید بود.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1537,'هیچ چیز مانند نیکی، انسان را سیر نمی کند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1538,'انسان، دنیایی از شگفتی ها است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1539,'هدف نهایی از آموزش و پرورش، آموختن روش درست بهره گیری از اوقات فراغت است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1540,'انسان آزاده از کسی سلب آزادی نمی کند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1541,'آدم بخشنده عزیز است، اگر چه فقیر باشد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1542,'شادی، بهترین چیزهاست؛ آن قدر اهمیت دارد که سایر چیزها تنها برای کسب آن هستند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1543,'هیچ روح برتری نیست که آمیزه ای از دیوانگی و جنون را در خود نداشته باشد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1544,'هیچ چیز مانند بخشش و نیکی انسان را سیر نمی کند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1545,'خوشبختی به خود ما بستگی دارد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1546,'تجربه، میوه ای است که آن را نمی چینند، مگر پس از گندیدن.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1547,'خواسته های خود را در میان دو دیوار محکم اراده و عقل زندانی کنید.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1548,'معلم نفس خود و شاگرد وجدان خویش باش.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1549,'برتری باسواد بر بی سواد همانند برتری زندگان بر مردگان است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1550,'برای دوام دوستی باید برتری و ارزش یکدیگر را بشناسید و به یکدیگر با دیده احترام بنگرید.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1551,'بی نیازی را از دنیا با خرسندی بخواه؛ زیرا آنچه که انسان را بی نیاز می سازد، خرسندی است، نه دارایی.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1552,'بزرگی در احترام داشتن نیست، بلکه در شایستگی داشتن احترام است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1553,'عشق، یک روح است که در دو جسم دمیده شده.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1554,'یک دوست خوب می‌تواند پادزهر پنجاه دشمن باشد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1555,'شادمانی، معنا [ = چم ] و هدف زندگی است؛ کل هدف و پایان وجود انسانی است.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1556,'در همه ی جنبه‌های طبیعت، امری شگفت انگیز وجود دارد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1557,'بدترین چیز در مورد بردگی این است که دست آخر، برده ها به آن خو می گیرند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1558,'کارهایی را که بایستی بیاموزیم و زمان، صرف یادگیری آنها کنیم، می توانیم در زمان انجام، یاد بگیریم.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1559,'از راه کنش و کردار است که انسانها کامیاب می شوند یا شکست می خورند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1560,'انسانها به دلیل شخصیت هایشان آن چیزی هستند که هستند، اما در کردار است که آنها به خوشبختی و یا بدبختی دست می یابند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1561,'زیباترین رنگها، که به گونه ای اتفاقی بر روی بوم آمده باشد، لذت کمتری به ما می بخشد تا طرحی سیاه و سفید.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1562,'کار شاعر این نیست که رخدادهای حقیقی را نقل کند، بلکه کار او این است که آن چیزهایی را روایت کند که بنابر احتمال یا ضرورت ممکن بود و یا می توانست رخ دهد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1563,'ما نسبت به انسانی که سزاوار بدبختی نبوده است، احساس مهربانی و دلسوزی می کنیم.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1564,'بدتر از همه آن است که کسی آگاهی کامل داشته باشد و اراده کند عمل را انجام دهد و سپس آن را انجام ندهد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1565,'در میان نمایشنامه نویسانی که از استعدادی برابر برخوردارند، آنان که خود از نظر عاطفی حساس تر هستند، متقاعد مؤثرترند؛ کسی که خود افسرده است افسردگی را سرایت می دهد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1566,'نمایشنامه نویس در فرآیند نوشتن، حتی اگر داستان را آفریده باشد، نخست باید طرحی از داستان بریزد و سپس با رخداد های مناسب به پر کردن آن بپردازد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1567,'کامل بودن بیان در این است که روشن باشد بی آنکه عادی باشد.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1568,'زبان پر زرق و برق، هم شخصیت و هم اندیشه را از نظر پنهان می کند.((ارسطو))',14,0,0,0,0,0);
INSERT INTO mysms VALUES(1569,'انسان برای پیروزی آفریده شده است؛ او را می توان نابود کرد، اما نمی توان شکست داد.((ارنست همینگوی))',15,0,0,0,0,0);
INSERT INTO mysms VALUES(1570,'هیچ گلی عطر، رنگ و زیبایی مادر را ندارد.((ارنست همینگوی))',15,0,0,0,0,0);
INSERT INTO mysms VALUES(1571,'نوشته خوب آن است که مثل کوه یخ، یک هشتم آن آشکار و متکی به بخش دیگرش باشد که در زیر آب پنهان است.((ارنست همینگوی))',15,0,0,0,0,0);
INSERT INTO mysms VALUES(1572,'زنها جنگ را شروع می کنند و مردها آن را ادامه می دهند.((ارنست همینگوی))',15,0,0,0,0,0);
INSERT INTO mysms VALUES(1573,'تنها شجاعت است که انسان را از نادانی زندگی و خواری مرگ می رهاند.((ارنست همینگوی))',15,0,0,0,0,0);
INSERT INTO mysms VALUES(1574,'ستاره ی بخت هیچ کس شوم نیست؛ این ما هستیم که آسمان را بد تعبیر می کنیم.((ارنست همینگوی))',15,0,0,0,0,0);
INSERT INTO mysms VALUES(1575,'هر کس در دنیا باید یکی را داشته باشد که حرفهای خود را به او بزند؛ آزادانه و بدون رودربایسی و خجالت. به راستی انسان از تنهایی دق می کند.((ارنست همینگوی))',15,0,0,0,0,0);
INSERT INTO mysms VALUES(1576,'تو برای برآورده شدن آرزوی خودت بکوش و بگذار دیگران هم به آرزوی خود برسند.((ارنست همینگوی))',15,0,0,0,0,0);
INSERT INTO mysms VALUES(1577,'روش طرح ریزی هر نقاش تنها ابزار وی در بیان حالت درونی او نسبت به زندگی است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1578,'عشق، هم مسری [ =سرایت کننده ] است و هم محدود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1579,'مشت زنی که پای خود را در بند نهد، بدین اعتبار که اگر عمل مشت زنی منحصراً شامل بازوها و پنجه ها باشد، مقامی پاک تر می یابد، در حقیقت، امکانات بازوها و پنجه هایش را نیز فلج ساخته است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1580,'نقاشان انتزاع گر، سست و کم مایه اند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1581,'نبوغ، آنقدرها هم که به طور رایج گمان می رود، نایاب نیست، اما نباید آن را تنها عامل اصلی برای آفرینش آثار هنری بزرگ دانست.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1582,'نبوغ، برای آنکه به گونه ای شایسته بارود شود باید خود را با لحظه های مساعدی [ =سازوار ] که در تاریخ پیش می آید همزمان سازد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1583,'هر پرده ی نقاشی وجودی جدا افتاده دارد و به خود برپای است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1584,'چارچوب یک پرده ی نقاشی نه تنها تصویر را از چیزهای مجاورش جدا می کند، بلکه موجب می شود که شخص اصولاً به گونه ای دیگر بر آن بنگرد تا به اشیایی که در پیرامونش قرار دارند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1585,'ویژگی اصلی زیبایی آن است که خود همیشه غایتی است، نه آنکه ابزاری برای رسیدن به غایتی باشد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1586,'زیبایی، معجونی است که از ترکیب هر گونه تجربه ی بشری با چوب بستی از عناصر ظاهری به وجود می آید.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1587,'ماهیت زیبایی منحصراً آن است که بتواند انگیزه ای برای مشاهده باشد و به هیچ وجه رابطه ای با زندگی عمل ندارد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1588,'مرزبندی نبوغ در هر نقاش، بیشتر به نسبت محدودیت ظرفیت عاطفی آن تعیین می گردد، تا بر اثر محدودیت توانایی اش در بیان عواطف.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1589,'درون [هر] چارچوب، دنیایی ابدی توسط شخص دیگری آفریده شده است که از هر شائبه ی فنا به دور است؛ دنیایی که جریان ازلی علت و معلول، یا رنج و شادی را در آن راهی نیست.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1590,'جوهر وجود زیبایی همان بی ثمر بودن اش است. هر ارزش دیگری در زندگی دوست داشتنی است، زیرا ابزار رسیدن به چیزی بیشتر دوست داشتنی می گردد؛ تنها زیبایی است که در نفس خود دوست داشتنی است و تنها قوه ی مشاهده ی ذهن آدمی است که می تواند معیار دوست داشتنی بودن آن را تعیین کند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1591,'زیبایی مطلقاً فایده ای ندارد، زیرا هیچ گاه ابزار رسیدن به چیز دیگری نمی شود. زیبایی به طور مطلق خواستنی است، زیرا تمایل نهایی آدمی را برآورده می سازد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1592,'تجربه، هنری است که به ما اجازه می دهد زمان حال را در پرتو حافظه مان از زمان گذشته تعبیر کنیم.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1593,'هر چقدر حافظه مان ذخیره و مایه ی بیشتری داشته باشد، تعبیرمان دقیق تر و موشکافانه تر خواهد بود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1594,'تداعی، در ادراک ما از زیبایی سهم کلی دارد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1595,'از آنجایی که به طور رایج، تجربه ی زندگی مردمان به مراتب غنی تر از تجربه ی هنری شان است، طبعاً لایه های بیرونی یک نقاشی برای ایشان از حیث ارزشهای مبتنی بر تداعی، غنی تر می گردد تا لایه های درونی آن.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1596,'آنچه موجب بزرگی یک هنرمند می شود و آنچه کارش را زیبا می سازد، توانایی او در به لرزش درآوردن سیم حساس حافظه در تماشاگر یا توانایی اینکه خود را بفهماند است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1597,'به هر میزان اثر هنری متعلق به لایه های درونی تر باشد، بیشتر باید افراد استثنایی را به عنوان تنها داوران نیکوکار دستچین کرد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1598,'دوست داشتن، چیزی جز سهیم شدن نیست.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1599,'هر اثر بزرگ نقاشی این ویژگی را دارد که پس از دیدن آن، گوشه ای از زندگی در نظر انسان تغییر می یابد و دیگر هرگز آنچه بوده است باقی نمی ماند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1600,'هر چقدر تماشاگری بیشتر درباره ی هنر بداند، هنر برایش تابنده تر می شود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1601,'نقاشی که آفرینشی ابتکاری دارد، از نقاشان دیگر بسیار کم مایه می گیرد و از زندگی، بسیار زیاد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1602,'هنر، کمتر وابسته ی اجزاء به وجود آوردنده اش است تا چگونگی ترکیب نهایی اش.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1603,'زیبایی یعنی میانه روی کامل در میان احساس و ادراک؛ در میان حس شدنی و اندیشه کردنی.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1604,'بیطرفی، آرمانی است که هیچ تاریخ نگار واقعی را بر آن دسترس نیست، زیرا تنها از راه جانبداری است که می تواند میان روبدادهای موثر و آنها که ارتباط مستقیمی با موضوع بحث ندارند، تمییز و تفکیکی قائل شود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1605,'یکی از ویژگی های ذوق، قابلیت سرایت آن است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1606,'ذوق، انگاره ای متغیر است که در همه حال ترکیبات تازه به وجود می آورد و در همه حال، آمادگی پذیرش نفوذهای تازه را دارد و در همه حال، نفوذهای کهنه را از خود به دور می دارد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1607,'روح هر عصر توسط هنرمند آفریده می شود، اما خود هنرمند نیز آفریده ی روح عصرش است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1608,'هرگز بر خاطر عاشق نمی گذرد که معشوقش تنها بدان سبب در نظرش دوست داشتنی است که تمایل وی در آن لحظه او را دوست داشتنی یافته است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1609,'آنها که زیاد ثروتمندند، به طور رایج اشیایی را می پسندند و جمع آوری می کنند که نفاست شان [ =گرانبهایی ] زاده ی کمیابی آنها باشد، نه تابع تازگی شان.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1610,'ذوق سلیم که مسلماً بیشتر بانوان تربیت شده ی امروزی از آن برخوردارند، راههای گوناگونی برای به کار بردن نقش گلهای مزارع در ساختن طرحهای تزیینی به ایشان می آموزد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1611,'بینش عبارت است از حالت هیجان زده یا عاطفی ذهن و یا مجموعه ای از اشتیاق ها و آزردگی ها که تنها با عناصر دیدنی به بیان درمی آید.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1612,'ذوق سلیم چیزی نیست جز توانایی ذهن به درک اثر هنری و از راه آن ادراک به سهیم شدن در نوع خاصی از عشق که موجب آفرینش آن اثر هنری گردیده است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1613,'دنیای عین و دنیای ذهن، هر دو به یک اندازه مهم و به یک میزان معتبر هستند، حتی اگر اولی اندازه گرفتنی باشد و دومی نباشد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1614,'دنیای ذهن ناموجود است، مگر آنکه از راه حواس بدان دست یابیم.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1615,'بدون بینش، هنر بی بر می ماند، گرچه ممکن است چندی باب پسند روز شود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1616,'فهم ما از هنر به میزان گسترده ای تابع تجربه ی اندوخته شده ای است که درباره ی آن به دست آورده ایم.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1617,'حتی بی باک ترین مبتکران [ =نوآوران ] نمی توانند از تکیه کردن بر سنت سرباز زنند و چاره جز آن ندارند که بنای هنر خود را به طور عمده بر شالوده ی سنن گذشته استوار سازند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1618,'سنت، شیارهای عادتی در ذهن حفر می کند و تمایلات و بیزاری هایی در آدمی به وجود می آورد که درست برابر تمایلات و بیزاری های به دست آمده از تجربه ی مداوم ما از طبیعت است؛ یعنی همان عواملی که ملاکهای زیبایی را در نظرمان برقرار می سازند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1619,'حس تنفر طبیعی هر فرد عادی در برابر چیز نامانوس، از امور پیش پا افتاده در تاریخ هنر است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1620,'رمانتیسم در هنر الزاماً مرحله ای زودگذر است. عمر آن تنها تا زمانی ادامه می یابد که مضمون عاطفی نوظهوری، صورتهای کهنه را ورافتاده و بی ثمر بداند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1621,'هر اثر هنری در درون چارچوب خود، چون واحدی صوری [ =ظاهری ] می گردد که تابع مجموعه ای از قوانین دیدنی است؛ این قوانین همان اندازه سخت و ثابت اند که قوانین مبتنی بر خاصیت وجودی حاکم بر دنیای بیرون چارچوب.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1622,'واکنش تماشاگر در برابر اثر هنری به همان نسبت که دامنه ی تجربه اش تنگ تر باشد، ضعیف تر یا محدودتر است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1623,'ویژگی همه ی آفرینندگان بزرگ این است که در هر لایه ی هنری گفتنی ها دارند و تداعی ها برمی انگیزند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1624,'سراسر تاریخ پر است از نمونه های هنرمندان بزرگی که هیچ گاه به درک بزرگيِ هم عصران خود دست نیافته اند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1625,'آفرینندگان بزرگ در همه ی دوره ها نفوذ شدید خود را بر ذهن ها پاس می دارند، اما در هر دوره به گونه ای جداگانه.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1626,'اگر نقاشی نتواند از عهده ی اجرای این معجزه برآید که وصفی مادی را تبدیل به صورتی انتزاعی کند، وی نه مقام نقاشی دارد و نه مقام چهره سازی.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1627,'هنر، چیزی جز بیان تجربه ی آدمی نیست و درک کردن و لذت بردن از هنر، همانا توانایی تماشاگر در مرتبط ساختن اثر هنری با ذخیره ی تجربه های بر هم انباشته شده ی شخصی او است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1628,'نبوغ می تواند از میان هر طبقه ی اجتماع بال و پر گیرد، اما برای پی بردن به نبوغ، عمری دراز - همراه با آنچه که فراگیری نامیده می شود - لازم است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1629,'لذت را نمی توان چیزی جز احساس برآمده از آرزوی برآورده شده دانست.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1630,'این اندیشه که در جایی هنری خالص و به کلی جدا از زندگی یا دنیایی وجود داشته باشد که تشنگی ذاتی چشم را برآورد، بدون آنکه تایید و کمکی از ذهن یا حافظه بخواهد، مسلماً پذیرفتنی نیست.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1631,'آنچه موجب سنگینی وزن زیبایی که هر اثر هنری می تواند در بر داشته باشد می گردد، توانایی آن اثر است به داشتن بار تجربه ی بشری با خود؛ همچنان که اسکلتی سست می تواند بار عضلات و اعصاب و اعضاء درونی و پوست بیرونی بدن را بر خود تاب آورد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1632,'زیبایی، آن نمودی از پدیده است که اگر به کمک حواس دریافت شود و از آنجا به مرکز اندیشه یا مشاهده ی درونی ادراک کننده منتقل گردد، این هنر را دارد که واکنش هایی مبتنی بر تجربه های اندوخته شده اش برانگیزد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1633,'به هر میزان، تجربه های تماشاگر فراوانتر و توانایی او برای مشاهده ی درونی بیشتر باشد، تجهیزات و تدارکات وی برای درک زیبایی کامل تر و موثرتر است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1634,'تنها عشقی را که ممکن است در طبیعت پی جویی کنیم، عشق به کارایی است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1635,'همه ی تجربه های بشری را می توان با زبان عشق یا نفرت به بیان درآورد. عشق، عامل سازنده و نفرت، عامل خراب کننده است؛ وجود هر دو آنها لازم است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1636,'کاشتن گُل و از ریشه درآوردن علف، دو هنر وجودی باغبان است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1637,'هر فردی به سلیقه ی خود، فهرست جداگانه ای از اشیا را زیبا می داند و برای هر قلم آن نیز رقم شاخص یا ارزش هنری متفاوتی قائل می شود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1638,'عشق، نه تنها کلمه ای بارور است، بلکه چنان گنجایشی دارد که صدها مفهوم را در بر می گیرد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1639,'باغبان، فردی هنرمند است نه هنرشناس؛ او سازنده ی زیبایی است نه به کار برنده ی آن.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1640,'عاشق هرگز شک نمی کند که معشوقش - خواه گلی باشد یا انسانی یا خدایی - شایسته ی عشق او نباشد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1641,'آرزوها زاده ی تصادف نیستند؛ آنها از راه عادت در انسان تکوین می یابند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1642,'آرزوهای هر کس نتیجه ی تجربه های زندگی او است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1643,'هنر دارای ساختمانی به مراتب پیچیده تر از طبیعت است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1644,'افسونها حالتی دور افتاده دارند. آنها اندیشه ی آدمی را مورد اهانت قرار می دهند، زیرا آن را نادیده می انگارند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1645,'هیچ فرد دلبسته به موسیقی نیست که بتواند انگاره ی صوتی را تاب آورد که فاقد آن هنر باشد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1646,'دستخط هر کس هیچ گونه رابطه ای با معنای جمله هایی که می نویسد ندارد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1647,'مغز آدمی ذاتاً و در همه حال سرگرم بیرون آوردن دسته هایی از قوانین است و به هر میزان تجربه ای پخته تر باشد، قانون به دست آمده از آن پیچیده تر خواهد بود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1648,'خم هر ساقه نتیجه ی وجود تعادلی است در میان بلندی و نرمی اش و همچنین سنگینی تخمه ای که بر فرق آن ساقه می روید.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1649,'شهود در پی چیزی جدا از دانش است؛ شهود در پی خوشی است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1650,'زمانی که یک قانون نیروی برگزیده و برتر را در دست داشته باشد و قوانین دیگر را بی اثر بگذارد، نتیجه ی آفرینش چیزی ناهنجار و دل آزار خواهد بود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1651,'زیبایی عبارت است از کردار مبتنی بر ریاضی مکنون [ =پنهان داشته شده ] در پدیده ها که از راه شهود ادراک شده باشد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1652,'یکی از موجبات پیدایش زیبایی این است که در خمیره ی مأنوس و فهمیدنی، اندک مایه ای از نامأنوس و نافهمیدنی افزوده شود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1653,'صورت باید مطالعه شود، همچنان که کتابی مطالعه می شود؛ رنگ صرفاً می تواند تجربه یا دریافت شود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1654,'رنگهایی را که به دور خود می بینیم، چون حروف الفبایی هستند که بار معنایی با خود ندارند و صرفاً پس از برقراری نظم [در بین آنها] است که معنا می یابند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1655,'تجربه ی خالص وجود خارجی ندارد. ممکن است تجربه ی اصلی یا مرکزی وجود داشته باشد، اما همیشه شماری تجربه های فرعی در بر دارد که اثر آن را تشدید می کنند یا کاهش می دهند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1656,'یک سمفونی هرگز نمی تواند تنها گوش آدمی را مخاطب خود قرار دهد. معماری تالار موسیقی، راحتی یا عدم راحتی صندلی ما، شدت و رنگ نوری که تالار را روشن کرده است، نوع و درجه ی گرمای هوای تالار، همه دست اندر کارند و هریک به سهم خود جزیی بر تجربه ی اصلی ما می افزایند یا از آن می کاهند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1657,'هر چیز تنها بدان سبب که تابع ضوابط ریاضی پیچیده تر یا جالب تری است از چیز دیگری که کمتر آن ویژگی را دارد زیباتر می نماید.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1658,'اگر طبیعت، زیبایی را تولید می کند، مسلماً از آن جهت نیست که زیبایی در نفس خود چیزی خواستنی است، بلکه بدان سبب است که زیبایی به گونه ای برای نوعی از انواع موجودات سودمند افتاده است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1659,'آنچه را که ما زیبایی طاووس یا زیبایی گل ساعت می نامیم باید همان ویژگی باشد که بدون آن، طاووس و گل ساعت نتوانند چنانکه باید هنر وجودی خود را نمایان سازند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1660,'تنها تجربه است که می تواند زهدانی را که در آن اثری هنری نطفه بندی شده است بارور سازد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1661,'تجربه ی زندگی نیز آفریننده ی راستین هر اثر هنری است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1662,'هر اثر هنری به مثابه ی کودکی است که هنرمند، مادرش و محیط زندگی هنرمند، پدرش باشد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1663,'از وجود خود درخت است که ما بر شناخت زیبایی در درختان دست می یابیم.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1664,'یک اثر هنری، اقدامی عمدی از جانب هنرمند برای ایجاد یک حالت عاطفی معین، از راه مخاطب قرار دادن شمار محدودی از حواس مدرکه [ =قابل فهم ] به شمار می رود.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1665,'هنر تنها این نیست که هنرمند از درون انبان [ =توشه دان ] بزرگ و سردرگمی از خرده ریزها، پاره هایی از آن را - که به ویژه نظرش را جلب می کند - بیرون بکشد و آنها را به گونه ای که مورد پسندش است با هم ترکیب سازد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1666,'هنر برخلاف طبیعت، پر است از نیت ها و نظرها و توضیحات [ =بازنمودن ] و بیزاریها و پسندهای بشری و اگر قرار باشد که هنر فهمیده شود باید نخست همه ی آن عوامل فهمیده شوند.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1667,'نقاش در مقام نگاهبان شکل ظاهری سیب هیچ گونه شخصیتی ندارد، زیرا در آن هنگام وی بازیچه ی دست سیب و حس بینایی خویش است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1668,'به مجرد آنکه نقاش بخواهد چون فردی مستقل از حقوق خود بهره گیرد و حالتی نسبت به سیب بگیرد – سرخی آن را دوست بدارد یا از گِردیش ملول شود – نخستین گام را در جاده ی آفرینش برداشته است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1669,'هنر را نباید چون لایه هایی رویهم افتاده در نظر آورد؛ زیرا آن لایه ها چنان در درون یکدیگر رسوخ یافته اند که هر بیننده ای هنگام تماشای هر اثر هنری در یک لحظه بر وجود همه ی آن لایه ها هشیاری می یابد و بنابراین، باید گفت بر وجود هر یک از آنها به هیچ وجه هشیاری نمی یابد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1670,'عشق با بهره گیری از وجود هنرمند به اراده ی خود تحقق می بخشد و سرانجام، خویشتن را با ابزاری که به گونه ای ناچیز قابل اندازه گیری هستند به بیان درمی آورد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1671,'راز استادکاری در این است که هنرمند از واسطه ی هنریش تا حد امکان بهره برداری کند و آن را به زیر فرمان و خدمت اراده ی خود درآورد، بدون اینکه اراده ی واسطه ی هنری را به کرداری برخلاف طبیعت اش وادار ساخته باشد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1672,'آزادی هیچ نقاشی کامل نمی تواند بود، بلکه در میان حدودی بسیار تنگ دربند است.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1673,'هر نقاش فقط چیزهایی را می بیند که می خواهد ببیند و فقط چیزهایی را می خواهد ببیند که دوست می دارد.((اریک نیوتن))',16,0,0,0,0,0);
INSERT INTO mysms VALUES(1674,'چه پارادوکس شگفت‌انگیزی! هر زمان که اراده می‌کنم با نادیده گرفتن خواسته‌هایم به دیگران کمک کنم تا چیزهایی را که دوست دارند، به دست آورند، هم احساس شادمانی و خرسندی بیشتری می‌کنم و هم کامروا می‌شوم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1675,'زندگی دو نیمه است : نیمه اول در انتظار نیمه دوم و نیمه دوم در حسرت نیمه اول.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1676,'ماجراجو و اهل عمل باشید تا از طعم دلپذیر تغییرات تازه بهره مند شوید.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1677,'این ترس از انتخاب است که قدرت تصمیم گیری را از ما می گیرد و باعث نوعی ناتوانی در تصمیم گیری می شود.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1678,'انسانهای واقعاً کامیاب کسانی هستند که آنچه را که به آن نیاز دارند دنبال می کنند.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1679,'هرچه بهتر بدانی که نتایجی که سرانجام به آنها نیاز داری چگونه خواهند بود، آسانتر می توانی با رویدادهایی که در طول مسیر با آنها روبرو می شوی، کنار بیایی.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1680,'خواسته ها، آرزوهای ما هستند و نیازها، ضرورتهای ما؛ نیازها برای دستیابی به کامیابی و کامروایی، ضروری هستند.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1681,'اگر فقط بنشینی و چشم براه بمانی که گزینه ها به سوی تو بیایند، بندرت درباره ی آنها چیزی یاد خواهی گرفت.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1682,'تجربه های گذشته ی من بهترین مربیان من هستند.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1683,'صخره ها و مارها همیشه وجود دارند، اما اگر آنها را پیش بینی کرده و درباره ی همه چیز پیشاپیش بیندیشیم، می توانیم به خوبی از پس کارها برآییم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1684,'نتایج بهتر شبیه پروانه ها هستند؛ اگر آنها را دنبال کنی، خودت را خسته کرده ای و ممکن است نتایج از دست تو بگریزند.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1685,'روی نیازهای واقعی خودت متمرکز شو، از گزینه هایت آگاه باش و به طور کامل، درباره ی همه ی گزینه هایت اندیشه کن؛ سپس بگذار که بهترین نتایج به آسودگی به سوی تو بیایند.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1686,'زندگی ما با تصمیم هایی شکل می گیرد که ما در وقت مناسب خودشان، آنها را مهم نمی دانیم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1687,'ما غالباً تصمیمات نادرستی می گیریم، چون در ابتدا چند پرسش ساده را از خود نمی پرسیم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1688,'پرسیدن مثل ساعت کوکی است و ما را بیدار می کند.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1689,'بیشتر وقتها متوجه نیستیم که بهترین و سریع ترین راه رسیدن به اهداف بهتر این است که موانع سر راهمان را دور بزنیم و هیچ مانعی در دنیا بزرگتر از خود نیست.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1690,'تصمیمات من، باورهای مرا آشکار می سازند.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1691,'علت اینکه بسیاری از ما مشکل داریم این است که سر خودمان کلاه می گذاریم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1692,'من یاد گرفته ام که وقتی به خودم حقیقت را می گویم، برایم آسانتر است که با دیگران هم رو راست باشم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1693,'سریع ترین راه برای پیدا کردن حقیقت این است که نگاهی به آنچه گمان می کنیم حقیقت است و در واقع نیست، بیندازم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1694,'مردم غالباً اشتباهات ما را بسیار آسانتر از خود ما می بینند.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1695,'هرچه بیشتر واقعیت را ببینیم، تصمیم های بهتری می توانیم بگیریم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1696,'درست هنگامی که از آفتاب بالای سرمان غفلت می کنیم، پل یخی زیر پای ما که زمانی سفت و محکم بود، زیر پایمان آب می شود.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1697,'انسان نباید به این بیندیشد که بهتر از دیگران هست یا نیست، بلکه باید به این بیندیشد که می تواند بهتر از آن چیزی که خودش هست، باشد؛ هر کسی شایسته ی بهتر بودن نسبت به خودش است.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1698,'ما هرگز به خودمان اجازه نمی دهیم به چیزی بیشتر از آنچه که به راستی بر این باوریم که سزاوارش هستیم، دست پیدا کنیم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1699,'هنگامی که کردارهای تو بهتر می شوند، همه چیز بهتر می شود.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1700,'شاید در این اندیشه باشم که شایسته ی موقعیت بهتری هستم، اما کردارهایم نشان می دهند که گاهی اوقات به راستی به این مسئله باور ندارم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1701,'رمز تصمیم گیری های بهتر این است که باور کنم سزاوار وضعیت بهتری هستم و سپس برپایه ی این باور، عمل کنم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1702,'در درون هریک از ما یک راهنما وجود دارد که در واقع، استادی درونی است که به ما حکمت می آموزد و راه را نشانمان می دهد؛ راهنمای ما خودمان هستیم.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1703,'هرچه زودتر بیاموزیم که تصمیمات بهتری بگیریم، زودتر هم به نتایج بهتر می رسیم. اگر ما در محیط کار و در خانه تصمیمات بهتری بگیریم، سازمانها و اجتماعات ما نیز از این موضوع بهره مند خواهند شد.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1704,'همه ی ما برای رسیدن به تصمیمات بهتر، راهنمای خود هستیم و می توانیم به دیگران کمک کنیم که آنها هم این نکته را کشف کنند.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1705,'کامیابی، یعنی پیشروی به سوی هر آنچه که می اندیشی مهم است.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1706,'وقتی تمام توجه تو به کاری است که انجام می دهی، ذهن ات منحرف نمی شود و شادمان هستی؛ تو تنها به آنچه در آن زمان روی می دهد سخت توجه داری.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1707,'وقتی خودت را در یک موقعیت نامساعد می یابی، این اهمیت دارد که به دنبال چیزی بگردی که درست و خوب است، حتی اگر یافتنش دشوار باشد؛ سپس قدرش را بدان و آینده ات را بر پایه ی آن بنا کن.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1708,'مهم این است که موقعیت های دردناک را تجربه کنی و از آنها درس بگیری، نه اینکه بکوشی با چیز دیگری ذهن ات را از آن موقعیت ها دور کنی.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1709,'سخت است که گذشته را رها کنی؛ اگر از گذشته نیاموخته باشی.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1710,'هر وقت در زمان حال ناخرسند بودی یا احساس ناکامی کردی، زمانش فرا رسیده که از گذشته بیاموزی یا برای آینده برنامه ریزی کنی.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1711,'بر رویدادهای گذشته نظر کن؛ چیزی باارزش از آن بیاموز. از آنچه که آموختی بهره برگیر تا زمان حال را بهبود بخشی.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1712,'گذشته را نمی توانی تغییر دهی، اما می توانی از آن بیاموزی.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1713,'برنامه ریزی برای آینده، نگرانی و عدم اعتماد را کاهش می دهد.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1714,'برنامه ریزی، بسیاری از حدس و گمانها را درباره ی کاری که هر روز نیاز داری انجام دهی از بین می برد.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1715,'وقتی برنامه داشته باشی، باید مداوم، همچنان که دانسته ها و تجربه های بیشتری جمع آوری می کنی، در آن تجدیدنظر کنی تا برنامه ات حقیقی تر شود.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1716,'نکته ی مهم آن است که تو هر روز کاری را انجام دهی، حتی اگر فکر کنی که کار کوچکی است تا به تو کمک کند آن آینده ی عالی تحقق یابد.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1717,'کار کردن و زندگی کردن با هدف فقط یک طرح و نقشه ی بزرگ یا برنامه ی زندگی نیست، بلکه یک نگرش عملی برای هر روز از زندگی است.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1718,'کامیابی یعنی تبدیل شدن به آن کسی که می توانید باشید و پیش رفتن به سوی اهداف باارزش.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1719,'وقتی چیزی را می یابی که کارایی دارد، می خواهی هرچه زودتر افراد بیشتری از آن بهره گیرند.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1720,'وقتی مردم در کار و زندگی، شادمان و کامیاب اند، برای همه بهتر است.((اسپنسر جانسون))',17,0,0,0,0,0);
INSERT INTO mysms VALUES(1721,'آغاز هر کاری، همیشه کار دیگری را ایجاد می‌کند. این یک قانون طبیعت است که کار، کار می‌آورد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1722,'هر زمان احساس کردید برای انجام کار دوست داشتنی خود پیر شده‌اید، بدانید که پژواک منفی درون‌تان بلند شده است. این پژواک، یک پژواک دروغین است؛ یک دروغ محض است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1723,'در هر سنی، هر کاری که بخواهید، می‌توانید انجام دهید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1724,'هیچ هدفی بالاتر از این نیست که در زمان بیماری و مرگ، احساس کنید که خوب زندگی کرده‌اید و آنچه سبب شادمانی‌تان می‌شده است، انجام داده‌اید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1725,'هرچه بیشتر بخوانید و تمرین کنید، از نیروی خلاقیت و انرژی‌های مثبت بیشتری می‌توانید برخوردار شوید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1726,'زندگی ما با همه‌ی دگرگونی‌هایش، در دست خودمان است. اکنون می‌خواهید آن را بی‌رمق و بی‌روشنایی بگذرانید، یا پر از شور و الهام؟((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1727,'در زمان نوشتن- نوشتن آنچه در دنیای رویا و گمان پرورده‌ایم- باید از ته دل بنویسیم.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1728,'برای چیدن ستاره‌ها، گاهی نیز لازم است دیوانه‌وار رفتار کنیم. دلهای دیوانه هم گاهی می‌توانند شکسته شوند و ستاره‌های درخشان خود را دانه دانه فرو ریزند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1729,'آنچه انجام می‌دهید، بسیار مهم‌تر از آن چیزهایی است که مرتب از آن سخن می‌گویید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1730,'یک هدف سزاوار پذیرش، باید در حد بالا بردن نیروی هیجانات و انگیزه‌های انسان باشد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1731,'هدف، باید به راستی یک هدف باشد؛ به گونه‌ای به شمار رود که انگار به راستی وجود دارد، انگار نفس می‌کشد و زندگی می‌کند تا بتواند ایجاد جنبش و انرژی کند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1732,'هدف، اگر خوب به آن پرداخته شود، تبدیل به انگیزه‌ای می‌شود که بامدادان از خواب بیدارتان می‌کند، آن را احساس می‌کنید... عطرش را می‌بویید و مزه‌اش را می‌چشید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1733,'یک هدف توانمند، شما را به پیش می‌راند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1734,'همه‌ی کسانی که هدف و چرایی در زندگی خود دارند، مفهوم زندگی را بیش‌تر درک می‌کنند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1735,'اگر بخواهید آسوده دل شوید که هدف یا چشم‌انداز دلخواهی که پیش رو دارید، تا چه اندازه توانمند است، باید به کارآیی آن در مورد خودتان توجه کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1736,'روند خودسازی از زمانی آغاز می‌شود که ابتدا انسان بداند به کجا می‌خواهد برسد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1737,'هر نقشی را که بازی کنید، به همان نقش تبدیل خواهید شد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1738,'هر کسی می‌تواند با جای گرفتن در قالب نقشی که دوست دارد، به راستی، به آن بپیوندد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1739,'همه‌ی احساسات ما از اندیشه و ذهنیات‌مان ریشه می‌گیرند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1740,'بازیگران بزرگ، همیشه کسانی هستند که نقش و وجود خود را بازی می‌کنند و در نتیجه، به دل می‌نشینند و دوست داشته می‌شوند؛ در حالی که بازیگران ناکامروا به هیچ روی، در درون نقش نمی‌نشینند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1741,'در زندگی راستین، اگر ما نقش ایده‌آل خود را باور کنیم و از عهده‌ی اجرای آن خوب برآییم، بدون تردید، بازیگر کامیابی خواهیم شد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1742,'بنیاد دگرگونی هر کس، از خود و با خودش آغاز می‌شود، البته به شرط آنکه این ارتباط درونی، ژرف، همیشگی و بس باشد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1743,'بهترین روش برای درک راستین جهان، زمانی صورت می‌گیرد که خودتان را کمی از مسایل دور و بر خویش کنار بکشید و از راه دورتری به آن بنگرید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1744,'با گذاشتن زمان برای گوشه‌نشینی‌ها، به ندای درون‌تان گوش بسپارید؛ ندایی که کمتر آن را شنیده‌اید. این همان ندایی است که دنیا را معنا می‌کند و تکیه‌گاه شما در هستی است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1745,'به گونه‌ی بنیادی، انجام هر کاری را می‌توان با خوشی و شادمانی همراه کرد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1746,'روشن است که انسان‌های کامروا همیشه کسانی هستند که می‌توانند در گوشه‌های سلول زندان نیز سلول‌های سازنده‌ی مغز خود را پرورش دهند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1747,'به هر کاری که از سر خوشی و شادمانی بنگرید، شادمان و خوشایند می‌شوید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1748,'یکی از اصول دگرگونی، اهمیتی است که به زندگی امروز و اکنون خود می‌دهیم.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1749,'برای یافتن شادمانی، نیازی به جست‌و‌جو کردن آن در اینجا و آنجا نیست، چرا که شادمانی، در همین جاست! در درون خودتان... در لایه‌های زیرین سیستم‌های انرژی خودتان... در قلب و در روح‌تان... دریابیدش!((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1750,'شما بسیار بیشتر از آنچه می‌اندیشید، می‌توانید بر همه‌ی رویدادها و تلاش‌های روزانه‌ی خود کنترل داشته باشید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1751,'فراموش نکنید که با تمرکز بر هدف‌های کوچک و عینی، هدف‌های بزرگتری را در این راستا به دست خواهید آورد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1752,'یک قهرمان خوب، تمرین خود را هرگز به امید شانس و نیکبختی رها نمی‌کند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1753,'سودمندترین و مثبت‌ترین انرژی، زمانی به دست می‌آید که بتوانیم از محدوده‌ی دایره‌ها، خود را فراتر ببریم و زمان آفرینش امکانات بیشتری را به خود بدهیم و آینده‌ی خود را از درون قاب کهنه‌ی گذشته‌های دور تماشا نکنیم.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1754,'ریشه‌ی هر دگرگونی در اندیشیدن است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1755,'افراد بدبین هرگز دوست ندارند با مسایل روبرو شوند. از آنجا که گمان می‌کنند باید هر کاری را درست و بدون کاستی انجام دهند، به راستی هیچ کاری را نمی‌توانند انجام دهند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1756,'با اندیشیدن ژرف، همیشه راهکاری برای دشواری‌ها پیدا خواهد شد و با انجام هرچه بیشتر کارهای کوچک، انرژی‌های مثبت بیشتری به دست خواهید آورد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1757,'یک فرد خوش‌بین، با اندیشه‌ی بسیار، خود را دگرگون خواهد کرد. حال آنکه، یک فرد بدبین، نه می‌اندیشد و نه به کنش می‌پردازد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1758,'منفی‌گرایان، در ذهن خود، همیشه بدترین گونه‌ی سناریویی را می‌نویسند که با هیچ تلاشی، امیدی به بهبودی آن نیست.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1759,'اندیشیدن، همیشه انسان را به سوی خوش‌بینی می‌کشاند و خوش‌بینی، برابر با خودسازی و دگرگونی است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1760,'لازم است بدانید که نخستین گام، بسیار مهم است و گام دیگر، ساختن یک دورنمای مثبت است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1761,'ایجاد یک رابطه‌ی خوب، همیشه با یک رویداد آغاز می‌شود.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1762,'مشورت با خود، یکی از ثمربخش‌ترین مشورت‌هاست، چرا که هیچ مشاوری بیشتر از خودتان شما را نمی‌شناسد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1763,'داشتن یک استاد خوب در زندگی انسان بسیار سودمند است؛ استادی که بتواند شناخت درون را به ما بیاموزد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1764,'اگر همچنان گرفتار پاره‌ای از خوی‌های بد خود باشیم، رسیدن به مرحله‌ی دگرگونی و خودسازی برایمان دشوار است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1765,'یکی از روش‌های نیرومند کردن خود در برابر آنچه بدان خو گرفته‌اید، این است که نخست نیازهای خود را کشف کنید، سپس با یک ابزار بی‌گزند‌‌تر و کارسازتر، پاسخ‌گوی بنیادین آن نیاز خود باشید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1766,'یک ذهن پرکار نمی‌تواند در یک جسم بی‌تکاپو به زندگی خود ادامه دهد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1767,'با سپردن خود به دست یک مربی آگاه، نه تنها ناتوان‌تر نمی‌شوی، بلکه احساس توانمندی بیشتری خواهی کرد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1768,'از ترک کردن خانه‌ای که در آن سکونت دارید، هراس نداشته باشید. در ذهن خود، خانه‌ای بزرگتر، تازه‌تر و شادتر بسازید و برای زندگی کردن در آنجا به هیجان درآیید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1769,'بدانید که هدف‌های شما، شکل نهایی منش شماست.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1770,'مغز انسان به ماهیچه‌ی بازو بسیار شباهت دارد. اگر با آنها کار شود، آشکار است که نیرومند و ورزیده می‌شوند، وگرنه، سست و از کار افتاده خواهند شد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1771,'زمان را دریابید و از هم اکنون، زندگی تازه‌ای را آغاز کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1772,'به سوی خوشبختی دیگران بروید تا راه خوشبختی خود را نیز هموار کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1773,'باید دانست که جز امروزی که در آن هستید، هیچ آینده‌ای برای شما وجود ندارد. آینده‌ی شما در آن دوردست‌ها نیست.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1774,'چشم و هم‌چشمی، اگر سبب شور و هیجان شود، می‌تواند یکی از بزرگترین انگیزه‌های پیشرفت و دگرگونی باشد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1775,'اگر به راستی می‌خواهید دگرگون شوید، دگرگونی شخص دیگری را در پیش گیرید، به او نیرو دهید و از راهنمایی، پشتیبانی و تشویق‌های لازم برخوردارش کنید؛ آنگاه بنشینید و نتیجه‌ی همه‌ی این‌ها را در زندگی خودتان تماشا کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1776,'برای به دست آوردن چیزی که دوست دارید، هیچ کاری بهتر از گفتگو کردن با خود نیست؛ زیرا هیچ‌کس بیشتر از خودتان به مسایل زندگی‌تان آگاهی ندارد و باز هم هیچ‌کس بیشتر از خود شما، به مهارتها و توانایی‌های شما آگاهی ندارد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1777,'یکی از مهم‌ترین راههای پیشرفت و دگرگونی، دریافت تاییدها و پشتیبانی درونی است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1778,'برای شکست‌های زندگی، برنامه‌ریزی کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1779,'پیشرفت ما در راه رسیدن به هدف‌هایمان، همیشه در یک خط مستقیم انجام نمی‌گیرد و این خط، در بیشتر زمانها با پیچ و خم‌هایی همراه است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1780,'یک فرد خوش‌بین به خوبی می‌داند که زندگی، بالا و پایین دارد و از پایین افتادن‌ها هیچ هراسی ندارد و در آن زمان هم در اندیشه‌ی چاره‌جویی است تا بتواند خود را از سختی‌ها بیرون بکشد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1781,'اگر کاری را به راستی دوست داشته باشید، با انجام آن، ده برابر بیشتر از کارهای دیگر به نتیجه و کامیابی خواهید رسید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1782,'شادمانی، بیشتر از یک فنجان قهوه‌ی داغ در یک سپیده دم سرد زمستانی به انسان، نیرو و انرژی می‌دهد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1783,'اگر برای رسیدن به شادمانی و خشنودی تلاش نکنید، نه برای دیگران سودمند هستید و نه آن انرژی و نیروی آفریننده را برای رسیدن به هدف خود پیدا خواهید کرد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1784,'خودتان را از جرگه‌ی دوستانی که شما را برای دستیابی به هدف‌هایتان پشتیبانی نمی‌کنند، با خوشرویی کنار بکشید، چرا که دوستان نزدیک، همیشه می‌توانند در دگرگونی زندگی انسان کارساز باشند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1785,'برای نزدیک شدن و ایجاد رابطه‌ی تنگاتنگ با افراد، نخست کمی بیندیشید؛ همان گونه که باید هر کاری پیش از آغاز، نخست موشکافی شود.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1786,'روشن است کسانی که سبب شادمانی شما می‌شوند، همیشه بر کسانی که شما را دل‌تنگ می‌کنند، برتری دارند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1787,'از همنشینی و وقت‌گذرانی با کسانی که کارشان، تنها، نشستن و غیبت کردن است، دوری کنید و نیروهای مثبت درون خود را به هدر ندهید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1788,'ارتباط نزدیک با افراد مثبت، شاد و پرشور، نگاه ما را خود‌به‌خود به سوی امکانات بیشتر زندگی متوجه می‌سازد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1789,'با توجه به اینکه بازی‌های زندگی همیشه یا در حالت کنش است یا واکنش، شما باید پیوسته، زندگی خود را در راستای کنش بگذارید، نه واکنش.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1790,'در رویارویی با جوهر بنیادی زندگی، جهان را به واکنش در برابر خویش وادار کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1791,'با ایجاد پندار نیرومند، می‌توان به بالا رفتن امکان هر رویدادی کمک فراوان کرد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1792,'مهم‌ترین بخش تجسم پویا، نه تنها دستیابی به رویاست، بلکه ایجاد آن دگرگونی‌هایی است که نخست در درون انسان روی می‌دهد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1793,'برای به وجود آوردن عشق و دوستی، شادمانی و روی خوش، کارآیی بیشتری دارد و ترشرویی و ناخشنودی، هیچ گونه دشواری را از بین نمی‌برد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1794,'با گشاده‌رویی و لبخند است که می‌توانید همدلی و همدردی دیگران را جلب کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1795,'اگر کسی را دوست داشته باشید و شاد نباشید، به راستی، ارزش آن عشق را انکار و نفی کرده‌اید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1796,'فراموش نکنید که اگر با عشق به سوی انجام کاری بروید، نه از آن کار خسته می‌شوید و نه احساس رنج و افسردگی خواهید کرد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1797,'هدف بنیادی شما اگر نتواند پندارهای شما را برانگیزد، هرگز به جایی نخواهد رسید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1798,'زمانی که ایده‌های بزرگ به آرامی ساخته می‌شوند، شاید آدمهای بزرگ هم باید به همان گونه ساخته شوند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1799,'فراموش نکنید که با تمرکز روی انجام کارهای کوچک، به هدف‌های بزرگ دست پیدا می‌کنیم.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1800,'اگر بخواهید شاهد پیشرفت‌های بزرگ خود باشید، بدون تردید باید از دگرگونی‌های کوچک خود هم شادمان و هیجان زده شوید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1801,'باید دانست که هیچ دگرگونی کوچکی، هر اندازه که ناچیز هم باشد، بی‌اهمیت نیست.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1802,'خندیدن، همه‌ی مرزهای محدود اندیشه را ویران می‌کند و آمادگی انسان را برای پذیرش هر رویدادی بالا می‌برد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1803,'دوستی، یک گنجینه است؛ چرا باید به فراموشی سپرده شود؟((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1804,'اگر در باغچه‌ای می‌نشینیم، آیا بهتر نیست به جای دیدن خارها به زیبایی افسون کننده‌ی گل‌ها بنگریم؟((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1805,'اگر شما به خودسازی، نوآوری، پیشرفت و دگرگونی باور داشته باشید، باید بدانید که برای به دست آوردن آنها، هیچ چیز بهتر از چشم و هم‌چشمی نیست.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1806,'چشم و هم‌چشمی به شما درس باارزشی می‌دهد و آن اینکه هر اندازه شما خوب باشید، همیشه کسی پیدا می‌شود که از شما بهتر است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1807,'جوانان با هر شکستی بیشتر رشد می‌کنند و می‌آموزند که باختن، به چَم مرگ یا بی‌ارزشی آنها نیست، بلکه سوی دیگر پیروزی است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1808,'با شکست‌ها و پیروزی‌های خود، شاد باشید. بکوشید در بازی‌ها، برابر نشوید، بهتر شوید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1809,'به هر چیز که بنگرید و هر چیزی را که ببینید، در زندگی شما رشد می‌کند. هرچه را هم که رد کنید، به پشت سرتان می‌افتد. اگر همیشه به سایه‌ها بنگرید، بدون تردید، سایه‌ها تبدیل به زندگی‌تان می‌شوند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1810,'پذیرش دشواری‌ها و پذیرفتن و دست و پنجه نرم کردن با آنها، یکی از ویژگی‌های افراد خوش‌بین است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1811,'عشق، نه می‌آید و نه روی می‌دهد، بلکه عشق، آفریده می‌شود.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1812,'برای کشف گنجایش‌های خود باید پیوسته، بیشتر و بیشتر، به دورن خود سفر کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1813,'پیشداوری خود شما درباره‌ی میزان گنجایش، توانایی و استعدادی که دارید، از هر کسی درست‌‌تر است؛ همان توانایی‌ها و استعدادهایی که برای پدیدار شدن، چشم‌براه دگرگونی شماست.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1814,'بگذارید نیروهای مثبت تایید دیگران، چاشنی زندگی‌تان باشد، ولی غذای بنیادی زندگی خود را خودتان نپزید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1815,'برای شناسایی خودتان، به بیرون از خود نگاه نکنید، به درونتان بنگرید و آن چیزی را که در درون خود دارید، بیافرینید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1816,'در هر سنی که باشید، چه پیر و چه جوان، می‌توانید خود را دگرگون سازید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1817,'نخستین گام کامیابی در زمینه‌ی دگرگونی، به چگونگی باورهای انسان مربوط می‌شود و باورها، سکوی جنبش و کنش او هستند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1818,'اگر تاکنون گمان می‌کردید که اهل کنش نیستید، با انجام کارهای کوچکتر به مرحله‌ی کنش نزدیک شوید و بدانید که کنش شما سبب سازندگی‌تان می‌شود.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1819,'اگر به کار همچون سرگرمی بنگریم، انرژی و نیروی آفرینندگی خود را بالا می‌بریم.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1820,'برای دست یافتن به سطح بالاتری از تجربه برای نوآوری و خودسازی، به دنبال فرصت‌هایی بگردید که برقرارکننده‌ی ارتباط‌‌‌ های دوسویه باشد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1821,'کلید خودسازی هر کس، با انجام هر کار مثبت کوچکی، قفل کامیابی را باز می‌کند، ولی به شرطی که هدف همه‌ی کامیابی‌ها، همین امروز باشد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1822,'باید توجه داشته باشید که همین شکل زندگی امروز شما، الگوی زندگی پایانی‌‌تان است و نمایانگر تصویر آینده‌ی شماست.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1823,'اگر در این اندیشه باشیم که برای زندگی کردن، یک دنیا زمان داریم، دچار اشتباه محض شده‌ایم و زمانهای خود را از دست داده‌ایم.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1824,'پیدا کردن یک هدف روشن و بزرگ، نخستین کامیابی است که برانگیزاننده‌ی فرد و به آرزو درآورنده‌ی وی در آفرینش راهها و راهکارها می‌باشد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1825,'نوآوری و خودسازی، در پی حرکت خودجوش آدمی به دست می‌آید و ارتباط این دو سبب، انکارناپذیر است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1826,'فراموش نکنید که هر روز می‌توانید رویای روز پیش را دگرگون کنید و چیز دیگری در ذهن خود بسازید، ولی هرگز نباید از گمان به دور باشید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1827,'بسیاری از مردم حتی از اندیشه‌ی اینکه در آینده به کجا خواهند رسید، دچار هراس می‌شوند!((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1828,'اگر خودتان را در یک شرایط دلخواه تصور کنید، بی‌شک، در قالب آن جای خواهید گرفت.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1829,'اگر بتوانیم خود را در زیر جریان جاری ترس که مانند آبشاری بر سرمان می‌ریزد، بگذاریم، ترس‌مان با همان قطره‌های آب فرو می‌ریزد و با احساسی سرشار از یک انرژی بی‌مانند روبرو خواهیم شد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1830,'هر زمان که با شرایط منفی و ناامید کننده رویرو می‌شوید، در پی نابود کردن سرچشمه و ترس‌های ناشی از آن باشید، پیدایش کنید و شاهد نتیجه‌ی درخشان کار باشید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1831,'برخی از افراد گمان می‌کنند که نمی‌توانند انسان‌های آفریننده‌ای باشند و نوآوری، کار آنها نیست؛ حال آنکه درست نمی‌اندیشند و هر کس می‌تواند این ویژگی را داشته باشد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1832,'در رویارویی با هر گونه دشواری که جنبه‌ی ارتباطی دارد، می‌توانید با راهکارهای نامحدود، برای از بین بردن آن دشواری، نقشه بکشید و اگر اندیشه‌ی خود را به کار بیندازید و تنها احساساتی بشوید، آن دشواری برای همیشه باقی می‌ماند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1833,'در رویارویی با دشواری‌ها، با کمک نردبان اندیشه، بالا روید و با اندیشه و بینش، به ایجاد طرح‌ها و راهکارهای منطقی دست بزنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1834,'ارتباطات، هرگز به خودی خود به وجود نمی‌آیند و تنها ما آفریننده‌ی آنها هستیم.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1835,'دشمن پیروزی ما، چیزی جز نگرانی و ترس نیست!((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1836,'اگر روی همه‌ی آن چیزهایی که در زندگی دوست دارید، تمرکز کنید، آنها را بی‌گمان به دست خواهید آورد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1837,'قهرمانان، الگوهای ایده‌آل زندگی و روش دست یافتن به آنها را به انسان نشان می‌دهند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1838,'یکی از روش‌ها برای آسان کردن کارهای دشوار و پردردسر زندگی این است که کارهای غیر ضروری را بشناسیم و آنها را کنار بگذاریم.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1839,'اگر آغاز هر کاری از سوی شما باشد، از چیرگی خود در زندگی، شگفت‌زده خواهید شد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1840,'ذهن شما از آن خودتان است؛ باید آن را با آنچه دوست دارید، پُر کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1841,'در مورد آنچه در ذهن خود دارید، احساس مسئولیت داشته باشید تا بتوانید از یک ذهن مثبت و بزرگ برخوردار شوید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1842,'نگرانی، سبب رشد دشواری‌ها می‌شود.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1843,'برای از بین بردن نگرانی‌های خود ناگزیر باید کاری بکنید، حتی اگر آن کار، کوچک باشد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1844,'باید به یاد داشت که روند دگرگونی، از مسیر اندیشه می‌گذرد و نه از مسیر احساسات!((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1845,'هرچه بیشتر با خود و دیگران روراست باشیم، انرژی و تمرکز بیشتری به دست خواهیم آورد و دیگر نگران آن نخواهیم بود که به این چه گفته‌ایم و به دیگری چه!((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1846,'اشتباه ما در این است که گمان می‌کنیم تنها داوری دیگران اهمیت دارد نه داوری خودمان.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1847,'دانش، یک توانایی است. به دانشی که کسب کرده‌اید، ارج نهید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1848,'اگر به نیروی اراده و توان خود باور داشته باشید، تیشه به ریشه‌ی کامیابی‌های خودتان زده‌اید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1849,'نخستین گام برای بالا بردن نیروی اراده، باور کردن آن است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1850,'نیروی اراده و سامان شخصی را تنها خودتان می‌توانید در خودتان به وجود بیاورید؛ تنها باید اراده کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1851,'تا خودتان نخواهید، هیچ رویدادی پیش نخواهد آمد و هیچ‌کس به انجام کاری که دوست ندارید، ناچارتان نخواهد کرد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1852,'فراموش نکنید که با رد کردن هر وسوسه‌ی کوچک، کمی به توانا کردن اراده‌ی خود کمک کرده‌ایم، حال آنکه با رد کردن وسوسه‌های بزرگ، این نیرو چند برابر خواهد شد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1853,'در گذشته زندگی نکنید، از زندگی در آینده بپرهیزید و ذهن خودتان را در همین امروزی که از آن شماست، متمرکز کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1854,'همیشه یک دارایی بزرگ و دور از چشم‌داشت، در برابر یک تلاش بزرگ و به دور از چشم‌داشت، به وجود خواهد آمد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1855,'با پرسیدن پرسش‌های بسیار می‌توانید ارتباطات خود را پایدار کنید و پایه‌ی دگرگونی‌های درخشانی را در زندگی‌تان بنا نهید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1856,'اگر بتوانید بخشی از دانش و آگاهی خود را در زمینه‌ی خودسازی به دیگران نیز بدهید، بر دگرگونی‌های مثبت خویش خواهید افزود.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1857,'با بخشیدن تجربه‌های خود به دیگران، بی‌درنگ، تجربه‌های تازه‌تری کسب خواهید کرد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1858,'خودسازی و رشد هر کس، تنها به خود آن شخص، بستگی دارد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1859,'مهم‌ترین سبب سازندگی و رسیدن به مرتبه‌ی بالا، در کنترل و شیوه‌ی اندیشه‌ی خودتان است و بس.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1860,'اگر نتوانید دانش و آگاهی خود را در کنترل خویش درآورید، بدون شک دچار یک احساس بیگانگی و بیشتر از آن، بدبختی خواهید شد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1861,'از خودتان وجودی بسازید که زیاد دربند پیشداوری دیگران نباشید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1862,'از مخفی‌گاه درون خود بیرون بیایید و با بی‌باکی به میدان زندگی بروید تا دشواری‌‌ها و ترس‌ها را تجربه و با آنها مبارزه کنید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1863,'ارتباط با انسان‌هایی که به مرحله‌ی رشد و دگرگونی رسیده‌اند، نه تنها دلپسند و دلنشین است، بلکه بیشتر به مراحل مفید و نوآوری‌‌های ارزنده‌ای کشیده می‌شود.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1864,'اگر بخواهید به راستی به شور و وجد بیایید، باید به گونه‌ای وانمود کنید که این رویداد شورانگیز به راستی رخ داده است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1865,'آنچه انسان را به آواز خواندن برمی‌انگیزاند، همان آواز خواندن است.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1866,'انسان، بدون توجه و آگاهی نسبت به مرگ، که پایان بازی است، هرگز نمی‌تواند به زندگی خود، آن‌ گونه که باید نزدیک شود.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1867,'پذیرش وجودی مرگ، به چَم بی‌رنگ شدن انگیزه‌های زندگی نیست، بلکه این نوید را به ما می‌دهد که پس از مرگ نیز دوباره زاده خواهیم شد و دوباره برخواهیم خاست.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1868,'کسانی که در شادمانی و همیاری به ما سهمی دارند، دوستان راستین به شمار می‌آیند.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1869,'زیاد هم دربند به ثمر رسیدن مو‌ به موی رویاهایتان نباشید؛ چرا که مهم‌تر از آن، دگرگونی بزرگی است که با این روش، در منش راستین شما به وجود می‌آید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1870,'اگر پایه‌ی شادمانی خود را، دستیابی به هدف ویژه‌ای بدانید، در مسیر زندگی، به شدت احساس اندوه خواهید کرد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1871,'شادمان بودن، از همان روز زایش، حق طبیعی شماست و هیچ لازم نیست که وابسته به چیز دیگری باشد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1872,'یکی از اسرار مهم این جهان آن است که در پس هر هراسی، یک جایگاه امن و سودمند قرار دارد.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1873,'ترس از انجام هر کاری، با آغاز آن کار از بین می‌رود.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1874,'اگر شما احساس می‌کنید که پندارهای نوآور و آفریننده‌ای دارید، شکی نداشته باشید که هنرمند و آفریننده نیز هستید.((استیو چندلر))',18,0,0,0,0,0);
INSERT INTO mysms VALUES(1875,'زندگی کردن بدون هدف، آزادی بخش است.((استیون شَپیرو))',19,0,0,0,0,0);
INSERT INTO mysms VALUES(1876,'هنگامی که روی هدفهای نزدیک تمرکز می کنید و به آینده و اهداف دور و دراز نمی اندیشید، آسوده دل خواهید شد و فرصت های بسیار بزرگ و والا سر راهتان قرار خواهند گرفت.((استیون شَپیرو))',19,0,0,0,0,0);
INSERT INTO mysms VALUES(1877,'حوب است که بخواهید به هدف ویژه ای برسید، ولی آنقدر روی نتیجه ی دلخواه تان تمرکز نکنید که چشم تان به روی فرصت های دیگر بسته شود.((استیون شَپیرو))',19,0,0,0,0,0);
INSERT INTO mysms VALUES(1878,'هنگامی که ما با چشم بسته اهدافمان را در پیش می گیریم، بیشتر، فرصت های دور از ذهن و شگفت آور را از دست می دهیم.((استیون شَپیرو))',19,0,0,0,0,0);
INSERT INTO mysms VALUES(1879,'زندگی را با چوب خط و سنجه های خودتان اندازه گیری کنید.((استیون شَپیرو))',19,0,0,0,0,0);
INSERT INTO mysms VALUES(1880,'هنگامی که به زندگی روزمره تان از دید شخص دیگری می نگرید، چیزهایی می بینید که پیش تر هرگز نمی دیدید.((استیون شَپیرو))',19,0,0,0,0,0);
INSERT INTO mysms VALUES(1881,'برای رسیدن به یک هدف، باید بسیار تلاش کنید تا بر بازدارنده ها و سدهایی که در مسیرتان قرار گرفته اند، چیره شوید.((استیون شَپیرو))',19,0,0,0,0,0);
INSERT INTO mysms VALUES(1882,'عشق را در دل نگه دار؛ زندگی بدون آن همچون باغی‌ست بی‌بهره از آفتاب که گل‌های آن، همه پژمرده باشند.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1883,'امروزه جوانان تصور می‌کنند که پول، همه چیز است و زمانی که بزرگتر شدند، یقین پیدا می‌کنند که این چنین است.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1884,'شخصیت افراد را می توان با نفوذی که آنها بر دوستان خود دارند، سنجید.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1885,'مرد با اراده مردی است که شخصیت انفرادی خود را در اجتماع گم نمی کند و شجاعت آن را دارد که باورهای خاص خود را آشکار نماید.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1886,'وقتی خوشبخت هستیم همیشه خوب جلوه می کنیم، اما وقتی خوب باشیم همیشه خوشبخت نخواهیم بود.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1887,'در انتخاب دوست می توان دقت کرد، ولی دشمن را نمی توان به خوبی تشخیص داد.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1888,'آن قدر جوان نیستم که هر چیز را بدانم.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1889,'هیچ کس آن قدر ثروتمند نیست که بتواند گذشته ی خود را بخرد.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1890,'همیشه دشمنانت را ببخش؛ هیچ چیز بیش از این آنها را ناراحت نمی کند.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1891,'تا زنده هستید، دل خود را زنده و شاداب نگهدارید؛ فرصت برای دلتنگی و مرگ بسیار زیاد است.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1892,'از عجایب روزگار این است که زن در عین زیبایی، وفادار هم باشد.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1893,'آدم باید همیشه عاشق باشد، از این رو مرد نباید ازدواج کند.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1894,'تنها وظیفه ی ما در قبال تاریخ، بازنویسی آن است.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1895,'هرکسی می تواند تاریخ ساز باشد، اما تنها یک انسان بزرگ می تواند آن را رقم زند.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1896,'تنها تفاوت بین هوسبازی و عشق جاودان در این است که هوسبازی تا حدودی بادوام تر است.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1897,'یک مرد تا زمانی که عاشق نشده است می تواند با هر زنی خوشبخت زندگی کند.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1898,'کتاب غیراخلاقی به هیچ وجه وجود ندارد؛ کتاب یا خوب نوشته شده یا بد.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1899,'کتابهایی که دنیا آنها را «غیراخلاقی» معرفی می کند، کتابهایی هستند که ننگ دنیا را برملا کرده اند.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1900,'بهترین نوع حکومت برای یک هنرمند، بی حکومتی است.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1901,'تنها، باور مرد هنرمند قابل توجه است، حتی در مورد روش و اخلاق.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1902,'یک هنرمند بزرگ، چیزها را همان گونه که هستند نمی بیند، اگر می دید هنرمند نبود.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1903,'حافظه دفتر خاطراتی است که همیشه با خود به همراه داریم.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1904,'فقط کسی که حسابش را تسویه نکرده است می تواند امیدوار باشد که در یاد کسبه به حیات خود ادامه دهد.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1905,'بدترین چیز در زندگی، هراس در میدان جنگ و خیانت به دوست است.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1906,'تنها کسانی که بیش از ثروتمندان به پول می اندیشند فقرا هستند.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1907,'ما کارهای احمقانه ای را که در زندگی انجام داده ایم، به نام تجربه به رخ دیگران می کشیم.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1908,'خوشبختی چیزی نیست که آن را حس کنیم، تنها باید آن را به یاد آوریم.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1909,'هنر، کلید فهم زندگی است.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1910,'هنرپیشه باید هنر خود را آشکار و خود را پنهان کند. این هدف هنر است.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1911,'وظیفه، چیزی است که انتظار داریم دیگران آن را انجام دهند.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1912,'اختلاف نظر درباره خوبی یا بدی یک اثر هنری نشان می دهد که آن اثر پر معنی و جاندار است.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1913,'انسان در برگزیدن دشمنانش نمی‌تواند زیاد دقت به خرج دهد.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1914,'زندگی، پراهمیت ترین چیزی است که تاکنون مورد گفتگوی بشر بوده است.((اسکار وایلد))',20,0,0,0,0,0);
INSERT INTO mysms VALUES(1915,'زن و شوهر یک سال بعد از ازدواج به زیبایی صورت یکدیگر فکر نمی کنند، بلکه هر دو متوجه اخلاق و رفتار هم می شوند.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1916,'دارایی، در دست کسانی که دارای اراده ی نیرومند و تملک [ =داراشدگی ] نفس نیستند و یا نمی‌توانند امیال و هواهای نفسانی خویش را اداره کنند، به منزله‌ی دام و سرچشمه‌ی بلا و گرفتاری است.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1917,'زن برای آن آفریده نشده که وجودی پوچ و بیهوده باشد و تنها به درد زینت و تفریح مرد بخورد.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1918,'رضایت وجدان تنها پس از انجام تکلیف است.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1919,'نیرومندترین اهرمها اراده است.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1920,'سرگرم بودن به هر کاری، بهتر از تنبلی و بیکاری است.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1921,'اشخاص عاقل و با اخلاق هرگز کلامی بر زبان نمی آورند که احساسات دیگران را جریحه دار سازند.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1922,'اخلاق، سرمایه است؛ اخلاق، بهترین و عالی ترین دارایی ما به شمار می رود.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1923,'اراده بهترین راهنمای طبیعت است.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1924,'سختی ها و ناراحتی ها بهترین وسیله ی آزمایش زندگی زناشویی است، زیرا رنج و محنت، اخلاق حقیقی زن و مرد را آشکار می سازد.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1925,'به جرأت می توان ادعا کرد که زندگانی حقیقی برخی از مشاهیر و رجال بعد از مرگشان شروع شده است.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1926,'طول عمر انسان از روی کردار و اندیشه های او سنجیده می شود، نه از تعداد سالهایی که زندگی می کند.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1927,'عشق و سختی بهترین وسیله ی آزمایش زندگی زناشویی است.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1928,'کتاب خوب در حکم گور و مدفن جاودانی حیات انسانی است. زیرا زندگی حقیقی بشری فقط عبارت از تفکر است و اندیشه ها نیز در صفحات کتابهای پایدار و جاوید می ماند. بنابراین کتاب های خوب گنج های شاهواری از اندرز و کلمه های حکیمانه است.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1929,'همسر خوب بهترین یاور در صحرای سنگلاخ و پرمخاطره ی زندگی است.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1930,'انسان موظف است عاشق حقیقت باشد و بیان آن را به دلیل عشقی که نسبت به آن دارد، از تکالیف حتمی خود بداند.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1931,'کتاب خوب به منزله ی بهترین دوست و مونس انسان است. اخلاق و صفات کتاب مانند انسان تغییرپذیر نیست و هرچه از اول بوده است همیشه هم به همان حال باقی خواهد ماند. کتاب از تمام دوستان انسان ثابت قدم تر، باوفاتر و صبورتر است و هرگز در ایام بدبختی و محنت به انسان پشت نمی کند. برعکس آغوش مهربان و رأفت آن برای استقبال همیشگی گشوده است. در دوره جوانی انسان را سرگرم و مشغول می سازد و در روزگار پیری و شکستگی تسلیت و دلداری می دهد.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1932,'کتاب دارای جنبه های جاودانه و ابدی است؛ بتها و معبدهای باشکوه، نابود می گردند، پرده های نقاشی و مجسمه های گرانبها تباه می شوند، اما کتاب بی هیچ نقصی در طی زمانها و قرنها باقی می ماند.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1933,'کتاب، رشته ی اتصال و پیوند معنوی است و مردم غالبأ به وسیله ی آثار و نوشته های مؤلفین که مورد توجه و اقبال جامعه هستند با یکدیگر همفکر و مأنوس می شوند و بدین ترتیب وجه اشتراکی مابین خویش احساس می کنند.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1934,'کتاب، مونس و غمخوار اوقات حزن و خستگی است که غم و اندوه را به سرور و شادمانی تبدیل می کند؛ رنج و غم را از آینه ی خاطر زدوده و گنجینه ی ذهن را پر از گوهرهای گرانبها می کند.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1935,'همان گونه که اخلاق و سیرت اشخاص را به وسیله ی دوستان و معاشرین آنها می توان شناخت، به همان گونه هم از روی کتابهایی که می خوانند می توان به عادتهای روحی و ساختمان اخلاقی آنها پی برد.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1936,'مردم با اخلاق معمولاً دارای وجدانی پاک هستند.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1937,'به راستی که پیران جوان و جوانان پیر در دنیا بسیارند.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1938,'آری، برای شنا در جهت مخالف جریان رودخانه، قدرت و جرات لازم است، وگرنه هر ماهی مرده ای هم می تواند در جهت موافق جریان رودخانه حرکت کند.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1939,'کمک گرفتن از دیگران در امر تصمیم گیری، کاری بی فایده و غیر عقلانی است.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1940,'انسان برای خوشبختی آفریده شده و خوشبختی با کار مفید به دست می آید.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1941,'هیچ یک از گرایش های نفس انسانی خطرناکتر از گرایش به تنبلی نیست.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1942,'تنها سرمایه گرانبهای ما وقت است و اگر رفت دیگر بر نمی گردد.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1943,'نجام وظیفه، عالی ترین مقصد و منظور زندگی و اخلاق است و زندگی انسان در مرکز دایره وظایف عمومی قرار گرفته است.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1944,'حتی مرگ نیز بر شیرینی طعم حیات می افزاید؛ زیرا ما را به زندگانی وابسته و علاقه مند می سازد.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1945,'اشخاص جدی و فعال طوری به کار عادت می کنند که بیکاری باعث صدمه و عذاب آنان می گردد و اگر بر حسب اتفاق از شغل و رشته مخصوص به خود باز بمانند، به حکم اجبار، مشغله ای دیگر برای خویش تدارک می بینند.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1946,'هنگام مطالعه تنها دانشمندان طرف صحبت هستند. در غیر این صورت، دوری از احمقان غیر ممکن است.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1947,'تنبل ترین و سست اراده ترین افراد جامعه بیش از همه شکوه و شکایت دارند؛ بدترین اقسام چرخها چرخی است که سر و صدا و هیاهوی آن زیادتر است.((اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1948,'ما، بیشتر از شکست‌هایمان می‌آموزیم تا از کامیابی‌ها. بیشتر با دریافتن اینکه چه کاری نباید انجام دهیم، درمی‌یابیم که چه کاری باید انجام دهیم و شاید کسی که هرگز مرتکب اشتباهی نشده، هرگز چیزی کشف نکرده است.((ساموئل اسمایلز))',21,0,0,0,0,0);
INSERT INTO mysms VALUES(1949,'درصدد این نباشید که ردپای فرد خردمندی را بیابید و آن را دنبال کنید؛ آنچه را آنان جسته‌اند، بجویید.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1950,'آن دم که محسور ناشناخته شوی، دیگر پایانی بر این زیارت نیست، سفری است بی انتها، بی پایان، همیشه در راه؛ سفری خستگی ناپذیر.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1951,'هوشمندی خطرناک است. هوشمندی یعنی اینکه خود مستقل می اندیشی، خود مستقل پیرامون را می نگری، دیگر جزمیات را باور نداری، فقط و فقط تجربه خود را می پذیری.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1952,'تفکر، بیرون زدن است. عدم تفکر، درون زدن است. فکر کن و خواهی دید که از خود دورتر و دورتر خواهی شد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1953,'درون رفتن در حقیقت، درون رفتن نیست؛ این تنها بیرون رفتن است... و ناگهان خود را در درون می یابی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1954,'آزادی هدف زندگی است. بدون آزادی، زندگی ابدا معنایی ندارد. منظور از آزادی، آزادی سیاسی، اجتماعی یا اقتصادی نیست. آزادی یعنی آزادی از زمان، آزادی از ذهن و آزادی از زور.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1955,'انسان مذهبی سعادتمند است. هر جا که باشد در معبد است. انسان سعادتمند معبد خود را همراه دارد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1956,'نخستین گام آن است که زندگی را همان گونه که هست بپذیری.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1957,'هنگامی که خوشی بنا به دلیلی باشد، پایدار نخواهد بود. اگر خوشی بنا به هیچ دلیلی نباشد، پایدار و برای همیشه می ماند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1958,'عشق، ترس را دور می سازد، همچنان که نور، ظلمت را.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1959,'به یاد داشته باش که تنها تو نیستی که حقیقیت را می جویی، حقیقت نیز در جستجوی تو است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1960,'آسمان را طلب کن؛ همه از آن تو است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1961,'و من این را بلوغ ذهن می خوانم : آنگاه که بی هیچ پرسشی به زندگی نظر بیندازی و صرفا با شهامت و بی باکی در آن شیرجه روی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1962,'تنها مشکل ما با غم، بیچارگی، خشم، نومیدی، تشویش، دلهره و بدبختی آن است که می خواهیم از شر آنها راحت شویم؛ همین تنها مانع است. مجبوری با آنها زندگی کنی... همه همان موقعیت هایی هستند که زندگی با آنها به تمامیت می رسد و می بالد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1963,'معنویت را فضیلت های ساختگی به وجود نمی آورد. چون اگر فضیلت ساختگی باشد... دیگر فضیلت نخواهد بود.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1964,'معنایی در زندگی نیست؛ معنا را باید خلق کرد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1965,'زندگی یک معما نیست، یک راز است. می توان پاسخی برای معما یافت، اما راز به گونه ای است که هرگز نمی توان پاسخی بر آن یافت.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1966,'عشق حقیقی بسیار لحظه ای است، اما هیهات از این لحظه!((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1967,'جهان مشکلی ندارد، مشکل عدم آگاهی است. از پوسته ناآگاهی برون آی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1968,'من مخالف چشم پوشی از هر چیزی هستم به جز حماقت و خرافات.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1969,'دانش اکتسابی است، شناخت از آن توست، متعلق به خود توست و اصیل است. دانش اطلاعات است، شناخت دگرگونی است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1970,'هرگز معتقد مشو، هرگز پیرو مشو، هرگز بخشی از سازمان مشو. راست باش و صادق با خودت. به خود خیانت مکن.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1971,'اگر شاد باشی، زمان معنایی نخواهد داشت، زمان از میان خواهد رفت.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1972,'حقیقت چنان بیکران و واژگان چنان حقیر.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1973,'کهنسالان بر کودکان مسلط اند و می خواهند آنها را زودتر از زمانی که طبیعت مقدر داشته از دوران کودکی بیرون کشند. آنها می کشند و خرد می کنند؛ کودک، کودکی را برای همیشه از کف می دهد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1974,'این تو و فقط تویی که مسئول آن چیزی هستی که برایت پیش می آید.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1975,'اگر خنده را فلج کنی، گریه را نیز فلج کرده ای. تنها کسی که می تواند خوب بخندد می تواند خوب گریه بگرید و اگر بتوانی خوب بخندی و خوب گریه کنی، زنده ای!((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1976,'وقتی چیزی به کسی می دهی، شاید ارزش چندانی نداشته باشد، اما همین هدیه کردن و بخشیدن، بی نهایت رضایت بخش است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1977,'پیش از آنکه مرگ بر در بکوبد، هر آنچه داری تقسیم کن! آیا می توانی ترانه ای زیبا بخوانی؟ بخوان و آن را تقسیم کن. آیا می توانی تصویری را نقاشی کنی؟ نقاشی کن و آن را تقسیم کن. هر آنچه در کف داری... و هرگز کسی را ندیده ام که چیزی برای تقسیم کردن نداشته باشد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1978,'تنها انسان مرده تلاش می کند ثابت کند که زنده است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1979,'به آینده موکول نکن. فردا، فردا؛ این واژه را از واژگان ذهنت بیرون بینداز! فردا وجود ندارد. نمی تواند وجود داشته باشد؛ چنین مفهومی در ذات پدیده ها نیست. تنها امروز است که وجود دارد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1980,'نخست طبیعی باش، سپس در رودخانه «طبیعی» جاری خواهی شد. روزی می رسد که رودخانه به اقیانوس ماوراء طبیعی می پیوندد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1981,'وجود تنها یک زمان را می شناسد؛ زمان حال. زمان گذشته را نمی شناسد، چون دیگر وجود ندارد. زمان آینده را نیز نمی شناسد، چون هنوز نیامده است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1982,'بد نیست گهگاه غمگین باشی، غمین بودن زیبایی خود را دارد. فقط باید بیاموزی که از زیبایی غمین بودن لذت ببری، از سکوت آن و از ژرفای آن.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1983,'ذهن هرگز نمی گذارد آنچه را که هست ببینی؛ تنها اجازه می دهد چیزی را ببینی که ذهن می خواهد تو ببینی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1984,'مشکل بتوانی لغزشهای خود را ببینی. تنها کسی که خود را دوست می دارد می تواند این لغزشها را ببیند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1985,'به حرفهای دیگران درباره خودت بها نده، خودت ببین که هستی، کجا هستی و لغزش هایت کدامند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1986,'فرد دانا با علامت سوالی زندگی می کند، فردی که در حیرت و شگفتی به سر می برد، با علامت تعجب زندگی می کند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1987,'برای اینکه قلب عاشق داشته باشی، به سری نیاز داری که کمتر اهل حساب و کتاب باشد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1988,'ببین چه می کنی و چرا... چه می گویی و چرا... آن وقت تمام بازیهایی را که با دیگران و نه تنها با دیگران، که با خودت کرده ای خواهی دید.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1989,'تنها آن زمان که انسانی رشد یافته، پخته، هوشیار و آگاه شوی خواهی توانست به مردم خدمت کنی. بلی تنها در چنین حالتی می توانی خدمت کنی، چون چیزی داری که می توانی تقسیم کنی : عشق و مهربانی. چیزی داری که یاری رسان است : درک و خرد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1990,'این همه بر خود سخت مگیر، کمی بیشتر مهربان باش، کمی بیشتر عشق بورز.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1991,'هنگامی که خود را شناختی، به هر کجای دیگر می توانی سفر کنی. سپس به هر کجا که بروی، سعادت در کنار تو است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1992,'عشق چنان از احترام سرشار است که آزادی را هدیه می کند. اگر عشق، آزادی به همراه نیاورد، عشق نیست، چیز دیگری است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1993,'بگذار بگویم که در جامعه ای غیر آزاد می توانی آزاد باشی، در جهانی سیاه بخت، سعادتمند باشی. مانعی از سوی دیگران نمی تواند وجود داشته باشد، می توانی دگرگون شوی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1994,'آفریدن چنان ثروتی است که حرص و طمع را بی معنا می سازد. وقتی ثروت کافی باشد، فقر از میان خواهد رفت.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1995,'اگر خود را تغییر دهی، تغییر جهان را آغاز کرده ای. با تغییر تو، بخشی از جهان تغییر کرده است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1996,'به یاد داشته باش، وقتی به تخریب دست می یازی، خود را نیز تخریب کرده ای و آنگاه که می آفرینی، ترانه ای، رقصی، خود را نیز می آفرینی و ابعاد نوینی از وجود خود را کشف می کنی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1997,'لذت بردن از کاری که می کنی، هدفی که تمام توان خود را به آن معطوف می داری؛ آنچه که نمی خواهی از آن دیگری باشد؛ آنچه که می خواهی همان باشی، با طبیعت در آشتی، نقشی که کارگردان در این نمایشنامه به تو داده است و تو حاضر نیستی که این نقش را با ریاست جمهوری یا امپراتوری عوض کنی، تو را به خرسندی می رساند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1998,'خوب است که حقیقت کالا نیست و هیچ کس نمی تواند آن را در اختیار تو بگذارد، وگرنه ارزشش را از دست می داد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(1999,'اگر عشق نتواند تو را یک شاعر سازد، هیچ چیز دیگر نخواهد توانست.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2000,'ابر پر باران باید که ببارد؛ فرقی نمی کند که آیا زمین به باران نیاز دارد یا نه.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2001,'دانشمند در مورد خود دانشمند هیچ نمی داند، اما در مورد چیزهای دیگر همه چیز می داند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2002,'دانشی که از بیرون به دست آمده دروغین است. این نوع دانش بر نادانی تو سرپوش می گذارد، اما تو را خردمند نمی سازد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2003,'با رشد یقین و اطمینان، شادمانی رشد می یابد و با رشد شک و تردید، تنش، بی قراری و بدبختی. شک و تردید در نهایت به رنج و عذاب و نگرانی می انجامد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2004,'من هرگز مخالف دانش نیستم، اما دوست دارم انسان نخست در قلب خود ریشه بدواند و سپس، دانش را به عنوان یک وسیله به کار گیرد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2005,'دانش هرگز نی تواند هدف و مقصود باشد، نمی تواند ارباب باشد، تنها می تواند نوکری خوب باشد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2006,'دانش می تواند به تو راحتی و آسایش و استاندارد بالایی از زندگی ببخشد، اما نمی تواند به زندگی تو کیفیت بهتری ببخشد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2007,'اگر تو تمام کتابخانه های دنیا را نیز در خود جای داده باشی، یک بودا نخواهی شد. تو همان نادان و ابله پیشین باقی خواهی ماند که بار دانش را با خود حمل می کنی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2008,'زندگی هیشه همچون رودخانه است و تو در چیزی وامانده ای و متوقف شده ای : شوهر، زن، دوست. هرگاه کسی وامانده شود خشمگین می شود، زیرا هیچ کس دوست ندارد آزادی اش را از دست بدهد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2009,'بزرگترین دلخوشی انسان، آزاد بودن است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2010,'بزرگسالان الگوی یادگیری کودکان هستند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2011,'هرگز، هرگز حتی برای یک لحظه آزادی ات را از دست نده و هرگز آزادی کسی را سلب نکن؛ از نظر من دین یعنی همین.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2012,'انسان براستی دیندار همیشه آزاد می ماند و به کسانی که در سر راهش قرار می گیرند کمک می کند تا آزاد باشند. او هرگز بر کسی سلطه نمی جوید و هرگز به کسی اجازه نمی دهد که بر او سلطه جوید.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2013,'باید همیشه مراقب بود، زیرا ذهن پیوسته از ما می خواهد که اسیر و دلبسته شویم و دلبسته شدن باعث آشفتگی ما می شود.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2014,'تو به این دلیل از آن شخص که عاشقش هستی متنفر می شوی که او آزادی ات را سلب کرده است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2015,'آزادی طلبی ما به این دلیل نیست که عاشق آزادی هستیم، بلکه در حقیقت، ما خود آزادی هستیم و فقط در آزادی می توانیم رشد کنیم.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2016,'لذت راستی چنان است که هیچ کس دوست ندارد در رنج و دروغ فرو افتد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2017,'یک دروغ کوچک با خود هزار و یک دروغ دیگر می آورد، زیرا تو مجبور می شوی از آن دفاع کنی و از دروغ نمی توان با حقیقت دفاع کرد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2018,'مرد و زن به این دلیل در عشق هم می افتند که از تنهایی درآیند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2019,'تاریخ فقط وقایع را ثبت می کند، اما داستانها حقایق را.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2020,'زندگی در راه دروغ بی ارزش است و مردن در راه حقیقت یکی از بزرگترین برکات زندگی است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2021,'انسان، آزاد به دنیا می آید؛ او هیچ سرنوشتی ندارد. اگر سرنوشتی وجود داشت، آزادی انسان از بین می رفت و او تبدیل به یک دستگاه می شد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2022,'حقیقت را نمی توان خریداری کرد. هیچ راهی برای گرفتن حقیقت از دیگران نیست. حقیقت قابل انتقال نیست؛ تو باید خودت حقیقت را کشف کنی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2023,'عشق، دروازه ی دنیایی کاملاً متفاوت را به روی تو می گشاید. بدون عشق در دنیای محدود عقل محصور خواهی ماند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2024,'عقل همیشه عشق را به کوری و دیوانگی محکوم ساخته و به آن نسبت های ناروایی داده، به این دلیل ساده که از درک آن عاجز بوده است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2025,'عشق همیشه آفریننده است و دنیا بسیار ویرانگر بوده، زیرا به انسان آموخته که انرژی عشق را سرکوب کند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2026,'عشقِ سرکوب شده ویرانگر می شود و عشقِ ابراز شده، آفریننده.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2027,'اگر تو باوری داشته باشی دست از جست و جو برمی داری؛ اگر باوری داشته باشی گمان می کنی که از قبل می دانی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2028,'شک و تردیدها را نباید سرکوب کرد. از آنها باید به عنوان سکوی پرش استفاده نمود.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2029,'شک و تردید چیز بدی نیست، هیچ عیب و ایرادی ندارد. اما تو نباید همیشه در شک و تردید به سر بری. تو باید شک و تردیدها را در راه جستجوی حقیقت به کار گیری.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2030,'انسان راستین همواره جهان و هستی را غنی تر می کند و از خود چیزی به هستی می بخشد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2031,'تا زمانی که تو چیزی از خود نبخشی هرگز احساس شادمانی نخواهی کرد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2032,'مرداب راکد چیزی فراتر از خودش را نمی شناسد، زیرا هرگز از چارچوب خودش فراتر نمی رود.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2033,'راه درست زندگی، دل به دریا زدن است؛ همیشه در حال اکتشاف بودن، همیشه به ستاره ها رسیدن.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2034,'انسانی که در راه کشف حقیقت تلاش می کند، جامعه بی درنگ دشمن او می شود.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2035,'مردم فقط از حقیقت می ترسند و نه از هیچ چیز دیگر.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2036,'انسان حیوان عجیبی است! همه چیز را کاوش می کند، تا قله ی اورست می رود، تا قطب شمال و کره ی ماه می رود، اما هرگز به فکر نمی افتد که سری هم به درون خویش بزند! این بزرگترین بیماری ای است که انسان گرفتار آن است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2037,'یگانه گنجی که ارزش جست و جو دارد طبیعت خود تو است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2038,'یادگیری هر گونه مهارتی در ابتدا دشوار است. یادگیری فن شنا در ابتدا مشکل است، اما وقتی در آن ماهر شوی چنان سهل و آسان می شود که بعدها متعجب می شوی که چرا تا آن حد مشکل به نظر می رسید.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2039,'انسانی که به حقیقت دست یافته چگونه می تواند دروغ بگوید؟ برای چه باید دروغ بگوید؟ او هیچ دروغی برای گفتن ندارد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2040,'آن کلید مخفی که دروازه ی معجزه ها را می گشاید، خوش و خرم بودن است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2041,'شهامت، بزرگترین کیفیت دینی است و دیگر چیزها پیامد آن هستند. تو اگر شهامت نداشته باشی نمی توانی راستگو باشی. اگر شجاع نباشی نمی توانی عاشق باشی، نمی توانی با ایمان باشی و نمی توانی در راه جست و جوی حقیقت گام برداری.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2042,'ترسوها نمی توانند ساحل را ترک گویند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2043,'پدر و مادرت تنها جسم تو را متولد می سازند نه روحت را.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2044,'شادمانی پیامد خودآگاه شدن است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2045,'شهامت پیشه کن تا معجزه های ناگزیر رخ دهند. هر لحظه ی زندگی انسان شجاع با معجزه می گذرد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2046,'انسان شجاع پیوسته از گذشته، از کهنه و از آشنا دست می شوید و همیشه آماده است تا به استقبال ناآشنا و ناشناخته برود.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2047,'شادمانی فقط از آنِ شجاعان است. شادمانی یعنی پیوسته دست شستن از گذشته. شادمانی در گذشته می میرد و در هر لحظه زایشی تازه می یابد؛ این یعنی شادمانی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2048,'وقتی قلب تو گشوده شود حتی اگر در زندان باشی همچنان ارباب می مانی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2049,'تمایل تو به دوام و ثبات است که تو را به دردسر می اندازد. تو می خواهی زندگی بدون تغییری را در پیش بگیری و این ممکن نیست؛ تو به دنبال ناممکن هستی!((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2050,'ما کاملاً بی آلایش، پاک و ساده به دنیا می آییم، اما دنیا شروع به ترسیم طرح و نگارهایی در لوح سفید آگاهی ما می کند؛ شروع به شکل بخشیدن به ما می کند؛ همه کس را آلوده و مسموم می کند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2051,'تا وقتی کودک به اندازه ای رشد یابد که بتواند مستقل فکر کند دنیا او را ویران می سازد. تا آن زمان او را معلول و از کار افتاده می کند. زیر بغل او عصاهایی قرار می دهد تا قادر نباشد روی پاهای خودش بایستد، تا فراموش کند رأی و نظر خویش را بکار گیرد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2052,'خورشید طلوع کرده است اما تو در تاریکی هستی. طلوع خورشید به تلاش های تو بستگی ندارد. هر کاری بکنی نمی توانی باعث طلوع خورشید شوی، اما می توانی درهایت را بگشایی یا اینکه آنها را بسته نگاه داری.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2053,'انسان آگاه می داند که زندگی پیوسته در حال تغییر است؛ زندگی یعنی تغییر. تنها یک چیز همیشگی است و آن خود تغییر است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2054,'زندگی همچون رودخانه است؛ راکد نیست، روان است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2055,'البته که سیاستمداران باید از دادن حق رأی به کودکان بترسند، زیرا کودکان همه چیز را می بینند و به ماهیت همه چیز پی می برند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2056,'همه چیز تغییر می یابد و هیچ چیز حتی برای یک لحظه ثابت نمی ماند. اگر تو از این نکته آگاه شوی، میل و اشتیاق تا ابد ثابت نگاه داشتن امور در تو فروکش می کند و آنگاه آزاد و رها می شوی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2057,'اگر زندگی پیش بینی پذیر می بود، مکانیکی می شد. زندگی پیش بینی پذیر نیست، همیشه با شگفتی همراه است و تو هر قدر هشیارتر باشی با شگفتی بیشتری روبرو می شوی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2058,'سر از راه منطق عمل می کند. می توان قانعش کرد، می توان هندو یا مسیحی اش ساخت، می توان آن را کمونیست، فاشیست یا سوسیالیست گرداند. با سر می توان همه کار کرد؛ تنها یک نظام آموزشی زیرکانه و راهکارهایی مکارانه لازم است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2059,'قلب از راه عشق زندگی می کند و عشق را نمی توان در بند کشید. عشق اساساً سرکش است. هیچ گاه نمی دانی که تو را به کجا خواهد برد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2060,'زندگی کردن خطرآفرین است و مردن بسیار راحت و آسوده.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2061,'مردم، عاشق زندگی مرده، عاشق زندگی آرام و راحت هستند، اما به بهای آن، شور و هیجان، ماجراجویی و ذوق و شوق را از کف می دهند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2062,'نخستین اولویت و مقدم ترین چیز برای انسان هوشمند، جست و جوی شادمانی است. آنگاه که شادمانی را بیابی و مزه ی آن را بچشی دوباره متولد می شوی، زندگی راستین آغاز می شود و پی می بری زندگی برای چیست.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2063,'فقط یک شخص شادمان می تواند یاور دیگران باشد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2064,'یک شخص بدبخت فقط می تواند به دیگران بدبختی بدهد؛ ما فقط می توانیم آنچه را که داریم به دیگران عرضه کنیم.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2065,'شاید تو قصد یاری رسانی داشته باشی، اما اگر انرژی شادمانی در وجود تو نباشد، اگر تو از این انرِژی سرشار نباشی، ناگزیر آسیب خواهی رساند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2066,'مردم به این دلیل عشق می ورزند که غمگین اند؛ به این دلیل در جست و جوی دیگری اند که تنها هستند و عشق فقط زمانی امکان پذیر است که تو تنها نباشی، بلکه در یگانگی باشی؛ با خودت قهر نباشی، بلکه با خودت در شیفتگی و سرمستی باشی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2067,'معلومات یعنی هیچ چیز؛ یک دستگاه کامپیوتر می تواند معلوماتی بسیار بیشتر از تو اندوخته باشد، اما کامپیوتر هیچ گاه نمی تواند یک مسیح یا یک بودا شود.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2068,'این همان کاری است که میلیونها نفر از مردم در حال انجام آن هستند: همچون یک دستگاه، همچون یک کامپیوتر عمل می کنند؛ کلیشه هایی را تکرار می کنند؛ واژگانی زیبا اما مرده را تکرار می کنند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2069,'شاید تو تجهیزات بیشتری در اختیار داشته باشی، اما همان آدم گذشته باقی خواهی ماند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2070,'شادمانی همزمان با خودآگاهی شکوفا می شود؛ یا خودآگاهی شکوفاتر می شود و تو شادمان تر می شوی و یا شادمانی شکوفاتر می شود و تو خودآگاه تر می شوی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2071,'دانش، مرده است و دانستن، زنده و پویا.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2072,'دانش حقیقی یا خرد از راه آگاهی به دست می آید؛ نه با گردآوری معلومات، بلکه با از سر گذراندن تحول. آگاهی، تحولی بنیادین است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2073,'دانستن چیزهایی در مورد حقیقت، شناخت حقیقت نیست و دانستن چیزهایی در مورد عشق، شناخت عشق نیست.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2074,'ذهن یعنی گذشته؛ ذهن همیشه مرده است. چیزی نیست جز انباشتگی خاطرات بر روی هم. گرد و غبار چگونه می تواند روشن و درخشان باشد؟((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2075,'هر قدر احمق تر باشی از دانشت مطمئن تر می شوی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2076,'انسانهای احمق بسیار کوته فکرند، زیرا به نتایج قطعی رسیده اند؛ نه فقط برای خود، بلکه برای دیگران نیز! می خواهند این نتایج را به همه، به همه ی عالم تحمیل نمایند. گمان می برند که با این کار با مردم همدردی می کنند!((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2077,'همیشه به خاطر داشته باش هرگاه به قله رسیدی، همزمان دره ای عمیق نیز در کنارش دهان باز خواهد کرد. اگر می خواهی به بهشت دست پیدا کنی، ریشه های تو باید در خودِ جهنم پا بگیرند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2078,'زندگی به هیچ وجه اسرارآمیز نیست. بر هر برگ درخت، بر هر ریگ ساحل، زندگی را می خوانیم. این زندگی است که در هر پرتو آفتاب می رقصد؛ هر آنچه می بینی، خود زندگی است با تمام زیبایی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2079,'اگر هر نسلی با عشق و احترام نسبت به کودکان رفتار کند و به آنها آزادی بدهد تا رشد کنند، همه سخنان یاوه در ارتباط با شکاف نسل ها محو خواهد شد. اگر شما به فرزندانتان احترام بگذارید و با آنها رفیق باشید، امکان ندارد شکاف بین نسلی ایجاد شود.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2080,'اگر ایجاد پیوند آزاد بوده و با آزادی همراه باشد، شادی از راه خواهد رسید؛ چون آزادی ارزش غایی است و چیزی بالاتر از آن نیست. اگر عشق تو سوی آزادی رهنمونت کند، عشق تو عین برکت است، و اگر سوی بردگی برَدَت، نه برکت که لعنت است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2081,'زندگی ات را قربانی چیزی مکن، همه چیز را قربانی زندگیت کن.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2082,'ازدواج وسیله ای برای فرار از ترس تغییر است. ازدواج وسیله ای است تا پیوند را تثبیت کنی. اما عشق چنان پدیده ای است که به محض تلاش برای تثبیت آن، خواهد مرد. ایستایی در عشق همان و نابودی عشق همان. عشق حقیقی، تنهایی را به یگانگی مبدل می سازد.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2083,'عشق نیست، عشق ورزیدن است؛ پیوند نیست، پیوند یافتن است؛ ترانه نیست، ترانه خواندن اس؛ رقص نیست، رقصیدن است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2084,'تمام تأکید من نه بر نامها که بر افعال است؛ تا می توانی از نامها دوری کن. این کار در زبان امکان پذیر نیست، اما در عرصه ی زندگی می توانی؛ چه، زندگی خود یک فعل است. زندگی یک نام نیست، براستی «زندگی کردن» است و نه زندگی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2085,'زندگی، وابستگی متقابل است؛ هیچ کس مستقل نیست؛ حتی برای لحظه ای نمی توانی تنها زندگی کنی؛ به حمایت تمام هستی نیازمندی، هر آن دَم است و بازدَم؛ نه این یک پیوند نیست، این وابستگی متقابل محض است.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2086,'شهامت به معنای زندگی در پیوند با دیگران و در عین حال مستقل باقی ماندن است. انسان نوین، انسان با شهامت خواهد بود.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2087,'عشق با درد همراه است، چون رشد را موجب می شود؛ عشق با درد همراه است چون عشق چنین می طلبد؛ عشق با درد همراه است چون عشق دگرگون می کند؛ عشق با درد همراه است، چون در عشق از نو زاده می شوی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2088,'بزرگترین معجزه در جهان آن است که تو هستی، من هستم. بودن، بزرگترین معجزه است و مکاشفه، درهای این معجزه ی بزرگ را به روی تو می گشاید.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2089,'والاترین هنر در جهان آن است که مرید باشی؛ این موهبت با هیچ چیز دیگری قابل سنجش نیست... .((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2090,'هرگز فرصتی را برای شاد کردن دیگران از دست ندهید، چرا که نخست خود شما از این کار سود می برید.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2091,'تو آن امپراطوری هستی که به خواب رفته و در رویا می بیند که گداست؛ از خواب بیدار شو.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2092,'خود را همان گونه که هستی بشناس، بی هیچ ایده آلی، بی هیچ قضاوتی و بی هیچ سرزنشی.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2093,'زندگی رازی بی پایان است، از این رو کسانی که از دانش سرشار هستند، از زندگی محرومند.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2094,'عشق، نبردهای تازه به همراه می‌آورد. این نبرد‌ها، روح را به رزمگاه فرا می‌خواند و به بلوغ می‌رساند؛ از این نبرد‌ها نترس.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2095,'این بدن سرانجام با مرگ از هم می پاشد، پیش از آنکه از هم بپاشد، عشق را به آن هدیه دهید.((اشو))',22,0,0,0,0,0);
INSERT INTO mysms VALUES(2096,'زینت انسان [در] سه چیز است : علم، محبت و آزادی.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2097,'به دست گرفتن مال خود و انجام وظیفه‌ی خویش، عین دادگری است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2098,'اگر روزی شأن و مقامت پایین آمد ناامید مشو، زیرا آفتاب هر روز هنگام غروب پایین می رود تا بامداد روز دیگر بالا بیاید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2099,'بی صبری، شخص را از هیچ رنجی نمی رهاند، بلکه درد جدیدی را برای از پا در آوردن او به وجود می آورد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2100,'سرعت و تندی [ انجام ] کار را مجوی، بلکه بر خوبی و درستی آن سعی کن. زیرا مردم از تو نپرسند که در چه مدت کار را انجام داده ای، بلکه خوبی و بی نقصی آن را می جویند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2101,'نیرومندترین مردم کسی است که خشم خود را نگه دارد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2102,'گسترش شهر، تا زمانی که یکپارچگی آن را به خطر نیندازد، مجاز است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2103,'آموزش و پرورش، اگر به حد کمال فراهم شود، نهاد انسان را سامان می‌دهد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2104,'اگر بازی، قانون نداشته باشد، کودکان هم به بی‌قانونی خو می‌گیرند و زمانی که بزرگ شدند، مردان وظیفه‌شناس و درستکار نخواهند بود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2105,'هنگامی که زندگانی انسان، در نتیجه‌ی تربیت نخستین در راه معینی افتاد، زندگی آینده‌ی او هم همان راه را خواهد پیمود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2106,'کسانی که بتوانند به مقام زیبایی- آن گونه که مناسب آن است- پی برده و حقیقت آن را مشاهده کنند، کمیاب هستند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2107,'کسی که به راستی، عاشق دانش است، باید از همان آغاز کودکی، با همه‌ی توان، جویای راستی بی‌چون و چرا باشد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2108,'هرگاه انسان، تنها به یک جستار، به سختی دلبسته شود، از شدت دلبستگی او به چیزهای دیگر کاسته می‌شود؛ مانند سیلی که در یک مسیر جریان یابد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2109,'نفس زبون و فرومایه، آن سان که پیداست، از درک دانش راستین بی‌بهره است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2110,'هرگاه بخواهی سرشت حکیمانه را از سرشت مخالف آن بازشناسی، باید بررسی کنی که آیا شخص از آغاز کودکی، دادگر و سازگار بوده است، یا ستمگر و سرکش.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2111,'مردان جنگی ما نباید خواهان دارایی و رشوه باشند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2112,'اگر کسی بخواهد بیش از یک پیشه داشته باشد، در هیچ یک از آنها کامیابی شایانی به دست نخواهد آورد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2113,'دنباله‌روی، اگر در جوانی آغاز گردد و تا مدتی ترک نشود، مبدل به خوی و طبیعت دومین شده و اثر آن در صورت ظاهر و آهنگ صدا و اخلاق انسان خواهد ماند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2114,'برای دارندگان چشم دل، زیباترین منظره، مشاهده‌ی کسی است که دارای روحی زیبا باشد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2115,'عشق راستین، عشقی است متین و حکیمانه که جستار آن، نظم [=سامان] و زیبایی است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2116,'پرورش بدن باید از کودکی آغاز شود و در همه‌ی دوره‌ی زندگی ادامه یابد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2117,'تندرستی بدن، هر چند به درجه‌ی کمال رسد، به خودی خود سبب تندرستی روح نمی‌تواند شد و حال آنکه برعکس، تندرستی روح، همه‌ی نیکویی‌های ممکنه را در جسم ایجاد می‌تواند کرد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2118,'به راستی خنده‌آور است که نگاهبانان، خود نیازمند نگاهبانان دیگر باشند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2119,'پزشک، آن گونه از بیماری را که خود بدان دچار بوده، در بیماران دیگر بهتر باز می‌شناسد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2120,'مردمان نیک در جوانی ساده هستند و از بدکاران، به آسانی فریب می‌خورند، زیرا آنها در دل خود، نمونه‌ای از آنچه برای بدکاران می‌گذرد، نمی‌یابند که با آن بسنجند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2121,'کسی که پس از تربیت روحی به کار تربیت بدنی بپردازد، اگر بخواهد، می‌تواند خود را از رفتن به پزشک، بی‌نیاز سازد، مگر در موارد ضروری [=بایسته].((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2122,'انسان، چیزی را دوست می‌دارد که شایستگی آن را با شایستگی خویش، یکسان بداند؛ یعنی رونق آن را رونق خود و شکست آن را شکست خویش بپندارد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2123,'اگر انسان درباره‌ی هر چیز، باور درستی داشته باشد، راستی و درستی آن را درک نموده است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2124,'هیچ چیز ترسناک‌‌تر و شرم‌آورتر از این نیست که چوپان برای پاس داشتن گله‌، سگ‌های بی‌تربیتی نگاه دارد که به سبب نابسامانی یا گرسنگی یا هرزگی، آماده‌ی آزار گوسفندان باشند و به جای صفت سگ، صفت گرگ از خود نشان دهند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2125,'در شهر بد، ستم فرمانروایی می‌کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2126,'هر چیزی، مانند خود را می‌جوید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2127,'آرزوهای ساده و میانه‌ای که پیرو منطق و خرد و رای درست می‌باشد، تنها نزد شمار کمی از افراد یافت می‌شود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2128,'پرداختن به کار خود و پرهیز از دخالت در کار دیگران، عین دادگری است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2129,'اندیشیدن، خود، گونه‌ای گفتگوی روح با خویشتن است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2130,'پول و پرهیزکاری، دو کفه‌ی ترازو هستند؛ اگر یکی بالا رفت، بی‌شک، دیگری پایین می‌آید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2131,'انسان قدرشناس [ =سپاسدار ]، انسان بزرگی است که سرانجام، چیزهای بزرگی را در زندگی اش به دست می آورد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2132,'هیچ یک از امور بشری، سزاوار آن نیست که انسان، زیاد آن را جدی بگیرد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2133,'درباره‌ی مردم ستمگر چنین می‌گویم که بیشتر آنها اگر در جوانی گرفتار نشوند، در پایان کار، رسوا و روسیاه می‌شوند و در روزهای پیری و بیچارگی مورد سبک‌داشت بیگانه و هم میهن قرار می‌گیرند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2134,'گریز از مجازات، جنایتکار را از آن هم که هست، بدتر می‌کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2135,'آنجا که جویندگان زمامداری، گدایان و گرسنگان باشند که می‌خواهند از این راه کسب مال کنند، هرگز حکومت درستکار برپا نخواهد شد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2136,'این حقیقتی مسلم است که بنیان هیچ حکومتی دگرگون نمی‌شود، مگر آنگاه که میان اعضای طبقه‌ی حاکمه جدایی افتد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2137,'اگر در شهری، دارایی و توانگری ارجمند شمرده شود، البته به همان نسبت از بزرگداشت نیکی و نیکوکاران کاسته می‌شود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2138,'اگر در جایی از شهر، تهیدست یافت شود، در همان نزدیکی، دزد و جیب‌بُر و دشمن مقدسات و هر گونه بدکار دیگر هم پنهان خواهند بود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2139,'هر گونه زیاده‌روی، واکنشی در پی دارد، چه در فصول، چه در گیاه‌ها، چه در بدن و بیش از هر چیز در حکومت.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2140,'زیاده‌روی در آزادی، به ناچار منجر به زیاده‌روی در بندگی خواهد شد؛ چه در فرد، چه در حکومت.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2141,'امور مهمه هرگز به دور از خطر نیست و چنان که می‌گویند، دشواری، شرط هر چیز زیباست.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2142,'جای شگفتی نیست که بیشتر مردم، سخنان ما را نپذیرند؛ زیرا اندیشه‌ای که ما درباره‌ی آن گفتگو می‌کنیم، آنها هرگز اجرای آن را در کردار مشاهده ننموده‌اند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2143,'به راستی اگر انسان، اندیشه‌ی خود را متوجه به وجود راستین نماید، دیگر زمان آن نخواهد داشت که رفتار افراد را در نظر آورد و با مردم ستیزه کند و درباره‌ی آنها دشمنی و کینه در درون خود بپروراند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2144,'ادراک ناپخته، ابزار سنجش هیچ چیز نمی‌تواند بود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2145,'جستار والا‌ترین دانش، همانا رسیدن به اصل خیر است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2146,'اگر دارای همه چیز شویم، ولی دستمان از خیر تهی باشد، مانند آن است که هیچ نداشته باشیم.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2147,'اگر شخص همه‌ی دانش‌ها را درک کند، ولی از شناخت اصل خیر و شناخت زیبایی و نیکی بازماند، این سودی تواند داشت؟((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2148,'خیر در نظر همه‌ی مردم، همان درک خوشی‌ها و در نظر خواص [=ویژگان]، همان شناخت است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2149,'خرد، نه با گذشت سال‌ها، بلکه به همت انسان به دست می‌آید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2150,'اندیشه کردن، هم‌کلامی روح با خودش است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2151,'سزاوار نیست که من، احترام یک فرد را بر احترام راستی، مقدم شمارم، بلکه لازم است باور خود را بیان کنم.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2152,'چه بسا چشم ناتوان که دیده‌ی تیزبین را شکست می‌دهد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2153,'جز مرد خردمند، کسی نیست که خود به تنهایی، ضامن [=پذیرفتار] نیکبختی خویش باشد، یعنی در میان مردم، شخص خردمند کمتر از همه، نیازمند وجود دیگران است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2154,'آنگاه که جوش و خروش هوی و هوس فرو می‌نشیند، مانند این است که انسان از دست شماری ستمگر دیوانه رسته باشد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2155,'مرد بی‌خرد، هر اندازه هم توانگر باشد، هرگز نمی‌تواند در درون خود، آسایش و آرامشی ایجاد کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2156,'هر کس در نگهبانی چیزی مهارت داشته باشد، در دزدیدن آن هم ماهر خواهد بود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2157,'اگر به شخصی زیان رسانیم، آن شخص از جنبه‌ی ویژگی‌های انسانی پایین می‌آید و به بیان دیگر، از کمالات بشری او کاسته می‌شود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2158,'هر گاه به مردم بدی کنیم، بیشتر بر تباهی آنها می‌افزاییم.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2159,'بدی کردن، خواه با دوست و خواه با دیگران، کار آدم دادگر نیست؛ بلکه کار شخص ستمگر است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2160,'اگر ما در جستجوی طلا بودیم، هرگز نمی‌گذاشتیم که زمانمان به پاس‌داشت آداب و تعارفات از دست برود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2161,'چه جریمه‌ای بهتر و مناسب‌تر از آن است که به نادانان می‌دهند؛ یعنی اینکه از آنان درس می‌گیرند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2162,'بدن ناقص است و به خودی خود نمی‌تواند درد خویش را دوا کند و فن پزشکی برای این هدف بنا شده که آنچه به جهت بدن سودمند است، فراهم کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2163,'سبب به وجود آمدن شهر این است که هیچ فردی برای خود، بس نیست، بلکه به بسیاری از چیزها نیازمند است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2164,'طبیعت، در همه‌ی ما استعدادهای مشابه نیافریده، بلکه استعدادها دیگرگون است؛ برخی برای یک کار شایسته‌اند، برخی برای کار دیگر.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2165,'هر کاری زمانی دارد که اگر در آن زمان انجام نیابد، زمان، از دست رفته است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2166,'کاری که باید انجام گیرد، چشم‌براه آسایش و استراحت کارگر نمی‌شود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2167,'سبب جنگ، حس زیاده‌خواهی است که هر زمان بروز کند، هم برای جامعه و هم برای افراد، بزرگترین رنج است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2168,'جوانمردی، نیرویی است پایداری ‌ناپذیر و چون در نفسی بروز کند، ترس و آشفتگی را در برابر هر گونه خطر از بین می‌برد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2169,'مهم‌ترین بخش هر کار، آغاز آن است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2170,'تربیت در همان جوانی، کارساز بوده و بنای هر نقشی که در آن هنگام بریزد، پابرجا خواهد ماند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2171,'کودک نمی‌تواند استعاره را از جز آن، بازشناسد و آنچه در کودکی به او آموخته می‌شود، ممکن است همواره در ذهن وی پابرجا مانده، از بین نرود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2172,'باید تا می‌توانیم بکوشیم نخستین داستان‌هایی که برای کودکانمان گفته می‌شود، مشوق نیکویی اخلاق باشد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2173,'آنچه خوب باشد، زیان‌آور نمی‌تواند بود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2174,'آنچه سبب شر نباشد، مسبب شر نیز نخواهد بود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2175,'آنچه خوب باشد، مسبب همه چیز نمی‌تواند بود، بلکه تنها سبب خیر است، نه سبب شر.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2176,'هر اندازه چیزی کامل‌تر باشد، دگرگونی و امور بیرونی کمتری در آن نفوذ دارد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2177,'هیچ کس نمی‌خواهد شریف‌ترین جزء وجود خود را به دروغ بیالاید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2178,'مرد خردمند، مرگ را برای خردمند دیگر که دوست او باشد، امر ترسناکی نخواهد دانست.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2179,'اگر زمانی مقام تو پایین آمد، ناامید مشو. زیرا آفتاب هر روز هنگام غروب پایین می رود و بامداد روز دیگر بالا می آید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2180,'منتظر درخواست نیاز از سوی حاجت مند مباش، بلکه شایسته است که پیش از درخواست، کار او را انجام دهی و با همت خود نیازمندی او را برطرف کنی.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2181,'لذتی که از علم حاصل می شود، بی آلایش است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2182,'سخن اگر از دل خارج شود به دل هم وارد می شود و اگر با نیت گوینده یکسان نباشد، توقع مدار که تاثیر کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2183,'نادانی هر کس به دو چیز دانسته می شود : اول خبر دهد و بگوید چیزی را که از او نپرسیده اند. دوم سخن گفتن بیش از حد ضرورت.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2184,'هر کس به فکر خیر و سعادت دیگران باشد، سرانجام سعادت خودش را هم به دست خواهد آورد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2185,'عشق تنها مرضی است که بیمار از آن لذت می برد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2186,'عشق بلایی است که همه خواستارش هستند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2187,'آنان که می خواهند خوب زندگی کنند باید به حقیقت نزدیک شوند؛ زیرا پس از نیل به مقام حقیقت یابی است که دست از غم و اندوه دنیا برمی دارند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2188,'کامل ترین نوع بی عدالتی آن است که عادل به نظر برسیم، در حالی که عادل نیستیم.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2189,'بی عشق، زندگی محال است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2190,'از نزدیکی با کسی که قادر به حفظ اسرار و رموز زندگی خود نیست، بپرهیز.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2191,'هر چیز را که نگهبان بیشتر بود استوارتر گردد، مگر راز که نگهدار آن هر چه زیادتر باشد آشکارتر گردد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2192,'به دیدن کسی که با تو سرسنگین است نرو، با کسی که سخنت را انکار کند گفتگو مکن و برای کسی که گوش نمی دهد حرف مزن.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2193,'آغاز هر کار مهمترین قسمت آن است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2194,'عشق رمز بزرگی است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2195,'برای جامعه، فضیلت و اخلاق از نان شب هم واجب تر است. اگر جامعه ای شکست اخلاقی بخورد با هیچ نیرویی نمی توان شکست را جبران نمود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2196,'زندگی کوتاه تر از آن است که شما بخواهید وقت خود را صرف پریشانی و نگرانی کنید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2197,'من از گفتگو با سالخوردگان، در امان می‌مانم؛ زیرا آنها بیش از ما راهی را پیموده‌اند که ما هم به نوبه‌ی خود، از آن خواهیم گذشت. پس چه خوب است از آنها بپرسیم که آن راه چگونه است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2198,'نخستین و بالاترین پیروزی، تسلط بر خویش است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2199,'جویندگان جاه و مقام، اگر نتوانند بر همه‌ی قبیله‌ ریاست کنند، بر یک سوم قبیله را کافی می‌شمارند و اگر نتوانند نظر خرده‌گیران و بزرگواران را به خود جلب کنند، به خوش‌باوری زیردستان و فرومایگان بسنده می‌کنند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2200,'تا زمانی که پایه و بنیان شهرهای ما این است که هست، اگر نفسی در آن رهایی یابد و آنچنان شود که می‌باید بود، به جرأت می‌توان گفت که این از بخشش ویژه‌ی الهی است که وی را پاس داشته است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2201,'بزرگترین جنایتی که شخص بتواند نسبت به شهر خود مرتکب شود، همانا ستم است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2202,'در یک شهر به‌سامان، دادگری، بی‌شک وجود خواهد داشت.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2203,'سازگاری، عکس ناسازگاری است، آرزو، عکس نفرت و جلب شیئی، عکس دفع آن است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2204,'نداشتن آرزو و بی‌میلی، در حکم آن است که انسان، چیزی را رد کند و از خود براند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2205,'شخص را آنگاه دلیر می‌خوانیم که همواره، چه هنگام رنج و چه هنگام خوشی، پیروی از فرمان خرد نموده و به راهنمایی خرد، اموری را که باید از آن به دور بود، از آنچه جز آن است، تشخیص دهد؛ ما این شخص را خردمند هم می‌خوانیم.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2206,'درستکاری، سبب پیدایش نیکویی است و نادرستی، سبب بروز بدی.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2207,'برای مردم فرزانه، گفتگو، اندازه‌ای به جز پایان زندگی ندارد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2208,'زمانی که انسان، به درستی جستارهای خود باور داشته باشد، در پیشگاه مردمان زیرک و دوست، می‌تواند از جستارهای بزرگی که بدان دلبستگی دارد، با جرأت و باور سخن گوید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2209,'اگر انسان، کسی را جز به عمد بكُشد، این خطا کوچکتر از آن است که او را در جستار زیبایی و نیکی و دادگری و قانون فریب دهد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2210,'تمسخر، جز در مورد کردارهای زشت، نشانه‌ی سطحی بودن اندیشه است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2211,'اگر کسی در آب بیفتد، خواه در حوض افتاده باشد، خواه در میان دریا، به ناچار باید شنا کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2212,'زن و مرد، هر دو به حکم طبیعت، شریک در انجام همه‌ی وظایف می‌باشند، ولی در همه‌ی کارها زن ناتوان‌تر از مرد است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2213,'آنچه سودمند است، زیباست و آنچه زیان‌آور است، زشت.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2214,'بزرگترین سبب شر در یک شهر، آن چیزی است که سبب پراکندگی و پریشانی شهر می‌گردد و بزرگترین سبب خیر، آن چیزی است که سبب سازواری و یکپارچگی شهر می‌شود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2215,'چیزی که جدایی می‌آورد، همانا خودخواهی در شادی و اندوه است؛ به این چَم که هرگاه برای شهر یا افراد، رویدادی رخ دهد، برخی دلخون و برخی دیگر، شادمان شوند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2216,'از خطر نمی‌توان گریخت. آیا بهتر نیست که انسان آنگاه با خطر روبرو شود که در صورت کامیابی، سودهایی بهره‌ی او گردد؟((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2217,'چپاول، در گذشته بسی لشکرها را به نابودی رسانیده است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2218,'جنگ و درگیری اگر بین خویشاوندان بروز کند، آن را آشوب گویند و اگر بین بیگانگان باشد، آن را جنگ خوانند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2219,'با وجود عشق، هر انسانی سراینده می‌شود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2220,'شهری که زمامداران آن هیچ کوششی برای کسب مقام به خرج ندهند، به ناگزیر دارای حکومت درستکار و شایسته‌ای خواهد بود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2221,'بزرگواری، نیروی حیوانی نفس را فرمانبردار نیروی انسانی یا بهتر بگویم فرمانبردار نیروی الهی می‌سازد و حال آنکه فرومایگی، جزء انسانی نفس را محکوم جزء وحشی می‌کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2222,'بهتر است انسان در برابر سختی‌ها، بردباری به خرج دهد و زاری نکند؛ زیرا نسبت به خیر و شر این مسایل نمی‌توان باور داشت و سخت‌گیری در این موارد هم کمکی به آینده نمی‌کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2223,'نفسی که پیراسته و آراسته نباشد، خواهی نخواهی از ویژگی میانه‌روی هم بی‌بهره است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2224,'شخص نادان به کسب دانش نمی پردازد، زیرا خود را دانا می شمارد و بدبختی افراد نادان در همین است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2225,'آن که نه فضیلت دارد و نه دانش، به آنچه هست خرسند است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2226,'آنچه سبب شود که نیستی صورت هستی به خود گیرد، آفرینش و خلاقیت است. از این رو همه هنرها آفریدن است و استادان و هنرمندان همه خلاق و آفرینش گرند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2227,'عشق عبارت است از اشتیاق به دست آوردن خوبی برای همیشه.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2228,'بزرگترین خردمندی، خرد و معرفتی است که به تنظیم امور خانواده و اجتماع مربوط است و آن، تسلط بر خود و حق طلبی و اعتدال نامیده می شود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2229,'آنگاه که چشم عقل باز می شود، چشم سر رو به ضعف و ناتوانی می گذارد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2230,'بزرگترین خطای پزشکان این است که صرفاً به مداوای جسم بیمار اقدام می کنند، بدون اینکه لحظه ای هم به فکر در مان روح برآیند. در حالی که جسم و فکر با هم هستند و نمی توان آنها را از هم تفکیک کرد و به طور جداگانه درمان نمود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2231,'رفتاری که شهرها با مردمان دانشمند می‌کنند، به اندازه‌ای ناپسند است که هیچ کس در جهان، چنین رفتاری نمی‌بیند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2232,'اهل ریا و دورویی، هرگز از دانش راستین برخوردار نمی‌توانند شد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2233,'اگر راستی را پیش‌آهنگ بپنداریم، قابل تصور نیست که کاروانی از ستم و تبهکاری در پی آن روان یابیم.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2234,'اسباب فساد، بیشتر با چیزهای خوب دشمنی دارند تا با چیزهای بد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2235,'نفوسی که دارای کمال استعداد باشند، بر اثر سوء تربیت به پست‌ترین درجه‌ی فرومایگی، می‌رسند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2236,'رأیی که متکی به دانش نباشد، هیچ ارزشی ندارد، بلکه رأی کوران است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2237,'چه سزاوار است که آدمی در آمیزش با صاحبان جمال، خود را نیز به زیبایی و آراستگی بیاراید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2238,'نیکمردان به مهمانی زیردستان ناخوانده می روند، خوبان نیز ناخوانده به مهمانی خوبان می روند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2239,'نیکمرد نیست که به مهمانی زیردست می رود، بلکه زیردستی به مهمانی نیکمرد می رود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2240,'چه خوب بود اگر حکمت همانند آب می بود که چون از یک ظرف لبریز گردد بتواند به ظرف خالی دیگری ریخته شود تا هر دو به اندازه یکدیگر از هم بهره مند شوند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2241,'نگهبانی نیرومندتر از عشق وجود ندارد؛ نه مال و جلال و نه قوم و خویش و در میان مردمان کاری را که از توان عشق ساخته است نتواند کرد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2242,'نعمتی پرارزش تر از این برای یک جوان وجود ندارد که مورد محبت فردی شریف قرار گیرد و برای یک مرد نیز نعمتی بالاتر از عشق به معشوق وجود ندارد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2243,'آن اصلی که باید انسان را در راه رسیدن به یک زندگی باارزش و سعادتمند رهبری کند، نه نزدیکان و نه خویشان می توانند به کسی ببخشند و نه از ثروت و مال و مقام به دست می آید؛ بلکه تنها نیروی عشق است که آن را پدید می آورد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2244,'عاشقان فرومایه چون از راه درست منحرف گردند، موجب بدنامی عشق می شوند؛ به حدی که حتی برخی کسان جرات می کنند بگویند که کمر به خدمت عشق بستن ننگ است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2245,'هر کاری که با نظم عمومی و رسوم و آداب مطابقت داشته و از روی پرهیزگاری باشد، هرگز سزاوار ملامت نتواند بود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2246,'به راستی همان عشق به تنهایی کافی است که موجد اتحاد و توانایی افراد جامعه باشد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2247,'آنجا که تسلیم عشاق شدن در هر صورت ننگ به شمار می رود، علت این امر را باید در رفتار زشت و بدِ کسانی جستجو کرد که از این رابطه وحشت دارند و می کوشند تا آن را بدنام کنند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2248,'عشق آشکار از عشق ورزی پنهان، شریفتر و زیباتر است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2249,'مردمان هم، سوگند عاشق را سوگند نمی شمارند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2250,'این لطف عشق است که کار عاشق را پسندیده می نمایاند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2251,'عاشقی که با تن بشری نه با نفس انسانی عشق می ورزد، نه او را قدر و قیمتی هست و نه عشق او را دوام و ثباتی.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2252,'دوستدار خوی و خصال نیک و فرخنده همیشه بر میثاق محبت و عشق استوار می ماند. زیرا با مراعات انتظام و انسجام، دل به چیزی داده است که برای همیشه لایتغیر و بادوام خواهد ماند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2253,'هیچ کاری به خودی خود زشت یا زیبا نیست، بلکه هر کاری که به وجه زیبایی انجام گیرد، زیباست و اگر به شیوه زشت و ناپسندی انجام گیرد، زشت است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2254,'اگر معشوقی زود رام گردد زشت می نماید و پیوند عشقش چندان شرافتی ندارد. زیرا او باید زمان کافی داشته باشد تا عاشق را تمام و کمال بیازماید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2255,'انجام هر نوع خدمت و تحمل هر گونه سختی و مذلت را به راه معشوق، ننگ عاشق نشماریم، بلکه آن را از افتخاراتش دانیم و مایه آراستگی و برازندگی او شناسیم.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2256,'عشق، نزدیکترین دوست آدمیان است و شفابخش دردهایی است که راه خوشبختی و سعادت را بر بشر فرو بسته اند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2257,'هر کس که استادش عشق باشد در همه جهان مشهور می گردد، اما آنکه با عشق بیگانه بماند در تاریکی و گمنامی می افتد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2258,'اگر هنرمندان را بنگرید می بینید که از آنان کسانی بلندآوازه تر شده اند که شاگردی مکتب عشق را کرده اند و آن کس که دستش به دامان عشق رسید در تاریکی و گمنامی فرو نمی غلتد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2259,'عشق است که در میان مردمان صلح و آشتی برقرار می کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2260,'عشق، خوب رفتار است و نیک کردار.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2261,'عشق پدر امنیت، ملاطفت، نازکی، نرمی، شادی و رغبت است که بدو، خیرات عزت و همه بدیها ذلت و زوال یافته اند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2262,'عشق همیشه به چیزی تعلق دارد که فاقد آن است. عشق چیزی را می جوید که سوای خودش و دور از خودش بوده و هنوز آن به دست نیاورده است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2263,'عشق، هر یک از موجودات خدایی و انسانی را تفسیر نموده و آنها را به همدیگر پیوند می دهد و نیایشها و قربانیهای بشری را به خدایان و فرمانهای خدایی را به بندگان می رساند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2264,'مگر لازم است آنچه زیبا نیست زشت باشد؟((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2265,'باور درستی که نتواند منطق خود را اثبات کند، دانایی نیست، اما چون شناخت به حقیقت است، نادانی هم نمی باشد و این فاصله ی میان دانایی و نادانی است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2266,'کار قانونگذار این نیست که بیشترین نیکبختی را برای طبقه‌ی ویژه‌ای از اهالی شهر فراهم کند، بلکه این است که زندگانی شایسته‌ای را برای همه‌ی شهر فراهم نماید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2267,'نهالی که خود، روییده و به دست دیگری پرورش نیافته باشد، از این رو، دِینی به دیگران ندارد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2268,'از آنان که هیچ ندارند، چگونه می‌توان چیزی بیرون کشید؟((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2269,'هیچ یک از امور بشری ارزش تشویش ندارند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2270,'توقف در زندگی، مرگ تدریجی است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2271,'بدترین چیز این است که شخص خودش را تعریف کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2272,'فلسفه، متعالی ترین موسیقی ها است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2273,'با سرانگشت عشق، هر کسی شاعر می شود.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2274,'دانایان حرف می زنند چون چیزی برای گفتن دارند، احمق ها حرف می زنند چون باید چیزی بگویند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2275,'سگ، همان روحی را دارد که فیلسوف.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2276,'از شاعر نخواهید خودش را شرح دهد؛ نمی تواند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2277,'به دنیا نیامدن بهتر از آموزش نیافتن و نادان ماندن است؛ زیرا جهالت، ریشه ی همه بدبختی ها است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2278,'سزاوار است مرد عاقل هنگام خوردن چیزهای خوشمزه، به یاد آورد تلخی دارو را و زیاد نخورد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2279,'در دنیا دو نیرو وجود دارد: شمشیر و سیاست؛ بیشتر وقتها شمشیر مغلوب سیاست شده است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2280,'آنگاه که جزء خردمند و شریف و حاکم نفس انسان به خواب رود، جزء حیوانی و وحشی نفس که از خوردن و آشامیدن سیر شده، بنای جست و خیز می‌گذارد و از خواب گریخته، درصدد نیل به آرزوهای خود برمی‌آید.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2281,'مرد بی‌خرد و دیوانه، آهنگ آن می‌کند که هم بر آدمیزاد و هم بر خدایان حاکم شود و چنین می‌پندارد که این توانایی در او هست.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2282,'کسانی که دارای اخلاق استبداد بوده و بر نفس و کشور خود مستبدانه حکومت می‌کنند، بهره‌ای جز بدبختی و نکبت ندارند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2283,'سخن کسی که راستی و درستی را سرزنش می‌کند، از متانت و شناخت به دور است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2284,'پس فیلسوفان راستین کدامند؟ آنان که عاشقان دید حقیقتند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2285,'هرگز کسی را که پیوسته در حال پیشرفت است، هر چقدر هم که كُند حرکت کند، نومید مکن.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2286,'نیک بختی جامعه به مراتب مهمتر از نیک بختی فرد است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2287,'عوام، ثروتمندان را محترم می شمارند و خواص، دانشمندان را.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2288,'نغمه و وزن موسیقی تاثیر فوق العاده ای بر روح انسان دارد و اگر درست به کار رود، می تواند زیبایی را در رویاهای روح جایگزین کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2289,'در آینه نگاه کن؛ اگر صورت زیبا داری، کاری مناسب چهره ات انجام بده و اگر قیافه ات نامناسب است، زشتی کردار را به زشتی صورت میفزا.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2290,'لذتی که از علم حاصل می شود، بی عیب است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2291,'هیچ کوچکی را حقیر نشمارید، باشد که از شما فزونی یابد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2292,'بیازمایید مرد را به فعل او نه به قول او.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2293,'اشرار از کسانی هستند که عیبهای مردم را جستجو کنند و به آن تکیه نمایند و نیکی آنها را نادیده انگاشته یا فراموش کنند؛ مانند مگسی که در جاهای کثیف می نشیند و از جاهای تمیز دوری می کند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2294,'اگر پیش از هر کار، انگشت خود را آهسته به پیشانی خود بزنی، ناچار نخواهی شد در پایان کار با مشت محکم به فَرق خود بکوبی.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2295,'عشق ما نسبت به فرزندانمان از بزرگترین آرزوی روح برای فناپذیر شدن سرچشمه می‌گیرد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2296,'[دانش] ستاره‌شناسی، روح را وامی دارد که به آسمان بنگرد و ما را از این جهان به جهان دیگر بَرد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2297,'باید الگوها و نمونه‌هایی که انسان را از مرگ به هراس می‌اندازد، به دور انداخت.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2298,'آموزش موزیکال، شیوه‌ای نیرومند است، زیرا ریتم و هارمونی، راه خود را به درونی‌ترین نقاط روح می‌یابند.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2299,'تهیدستی، نداشتن دارایی نیست، زیاده خواهی خواسته های آدمی است.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2300,'زیبایی در مرحله ی نهایی تبدیل پذیر به ریاضیات است؛ درک زیبایی زمانی به دست می آید که شخص بر کردار مبتنی بر مبانی ریاضی جهان شناسایی یابد.((افلاطون))',23,0,0,0,0,0);
INSERT INTO mysms VALUES(2301,'خوشا به حال فردی که ساعت‌ها، روزها و سال‌های زندگی‌اش، با آرامش و سازش سپری می‌شود.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2302,'خشم، گرفتن انتقام اشتباه دیگران از خودمان است.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2303,'نظم، اولین قانون آسمانی است.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2304,'اندیشه های ما در غرفه های بیشمار مغز با زنجیر ناپیدا به یکدیگر پیوسته اند. چون یکی از آنها بیدار شود، هزار تای دیگر نیز سر بر می دارند.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2305,'بهترین راه برای اثبات صفای ذهنی ما نشان دادن خطاهای آن است؛ نهر آبی که ناپاکیهای بستر خود را نمایش می دهد به ما می فهماند که آب، پاک و صاف است.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2306,'هماره خواندن، هرگز خوانده نشدن!((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2307,'انسان به دور از اشتباه و خطا است؛ بخشاینده خدا است.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2308,'دانش ِ کم خطرناک است، یا باید آب ِ چشمه را تا قطره ی آخر بنوشید یا هرگز لب نزنید.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2309,'با هر ناراحتی کوچک خشمگین شدن و به دیگران پریدن، نشانه غرور بی اندازه یا کم عقلی است.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2310,'وضع و حال مردمان تنگ فکر به وضع و حال بطریهای تنگ دهان می ماند؛ هرچه کمتر در خود دارند، با سر و صدای بیشتری آن را به خارج پرتاب می کنند.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2311,'احمق ها تحسین می کنند و خردمندان تایید.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2312,'آن که دروغی می گوید، نمی داند که چه کار دشواری انجام می دهد؛ زیرا می بایست بیست دروغ دیگر نیز در ادامه آن دروغ اول از خود بگوید.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2313,'تفریح، همانا خوشبختی کسانی است که قادر به تفکر نیستند.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2314,'انسان راستگو برجسته ترین اثر خداوند است.((الکساندر پوپ))',24,0,0,0,0,0);
INSERT INTO mysms VALUES(2315,'هر مؤمنی که صدای مهمانی را بشنود و به آن شاد شود گناهش آمرزیده شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2316,'با زنان مشورت مکن، رأی ایشان ضعیف و عزم ایشان سست است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2317,'از عیادت کنندگان کسی ثوابش بیشتر است که زودتر برخیزد مگر آنکه بیمار نشستن او را خواهد و از او سؤال کند که بنشیند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2318,'هر که مؤمنی را حقیر شمارد خدا او را به آن مؤمن در بهشت جمع نکند مگر آنکه توبه کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2319,'حلال نیست مسلمان را که مسلمانی را بترساند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2320,'مسلمان تا بیماری او بر صحتش غالب نشود باید دوا نکنند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2321,'هرگاه خویشان با یکدیگر بدی کنند مال‌شان به دست بدکاران می‌افتد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2322,'باید که یاری کردن شما به یکدیگر در طاعت خدا باشد به درستی که اگر یاری یکدیگر بکنید همیشه با رفاهيّت و نعمت خواهید بود در طاعت خدا و باز ایستید از معصیت خدا.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2323,'هر مرد مؤمن و زن مؤمنه که دست بر سر یتیم بکشد از روی ترحّم، حق تعالی برای او به عدد هر مویی که دستش بر آن می‌گردد حسنه‌ای برای او بنویسد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2324,'دوستی با دوستان خود هموار مکن و خود را پُر مده به ایشان بلکه روزی دشمن تو شوند و دشمنی را با دشمنان خود هموار بکن شاید که روزی دوست تو شوند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2325,'هر که خود را در محل تهمت درآورد کسی را که گمان بد به او می‌برد ملامت نکند و هر که راز خود را پنهان دارد اختیار با خودش است و هر سخنی که از دو کس گذشت فاش می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2326,'هر که نام خدا بر اول طعام ببرد و حمد خدا را در آخر بکند هرگز از آن طعام از او سؤال نکنند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2327,'خدا را یاد کنید در هنگام طعام خوردن و حرف بسیار مگویید که آن طعام نعمت و روزی خداست، بر شما واجب است که در وقت صرف کردن آن شکر خدا و یاد او و حمد او بکنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2328,'هرگز از امتلای طعام آزار نکشیدم زیرا هیچ لقمه‌ای را به نزدیک دهان نبرده‌ام مگر آنکه نام خدا بر آن گفته‌ام.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2329,'بخورید آنچه از خوان بر زمین افتد که خوردن آن شفای هر دردی است به امر الهی برای کسی که به آن طلب شفا نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2330,'چون با کسی برادری کنی آنچه از او بینی بر محمل نیک حمل کن تا آنکه به حدّی برسد که دیگر محمل نیک نیابی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2331,'مشورت با جماعتی بکن که از خدا ترسند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2332,'برادران مؤمن را به قدر پرهیزکاری ایشان دوست بدار.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2333,'از زنان بد بپرهیز و از نیکان ایشان در حذر باش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2334,'جامه‌های نفیس بپوش و زینت کن که خدا نیکوست و نیکو را دوست دارد، اما باید که از حلال باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2335,'شستن جامه اندوه و غم را برطرف می‌کند و موجب قبولی نماز می‌گردد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2336,'آن قدر پینه بر جامه‌ی خود زدم که شرم کردم از آن کس که بر آن پینه می‌زد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2337,'نماز کسی که انگشتر عقیق در دست داشته باشد بر نماز جماعتی که غیر عقیق در دست داشته باشند به چهل درجه زیادتی دارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2338,'هر که کاسه‌ طعام را بلیسد ملائکه بر او صلوات فرستند و دعا کنند و به فراخی روزی و حسنات مضاعف برای او بنویسند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2339,'کسی که خواهد طعام او را ضرر نرساند، تا گرسنه نشود و معده پاک نشود چیزی نخورد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2340,'هر که خواهد خیر خانه‌اش بسیار شود دست را پیش از طعام خوردن بشوید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2341,'دست شستن پیش از طعام و بعد از طعام فقر را زایل می‌کند و روزی را زیاد می‌کند و چرک را از جامه دور می‌کند و چشم را جلا می‌دهد و دردها را از بدن دور می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2342,'نافع و مبارک‌ترین شیرها برای فرزند شیر مادر است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2343,'خواب کردن پیش از نماز طلوع آفتاب و پیش از نماز خفتن پریشانی و فقر می‌آورد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2344,'هر که از شبیخون عذاب خدا ترسد باید که کم خواب بکند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2345,'مصاحبت مکن در سفر با کسی که فضیلت تو را بر خود نداند آن قدر که تو از فضیلت او بر خود می‌دانی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2346,'مروّت و مردی در حضر خواندن قرآن است و همنشینی با علما کردن و تفکر در فقه و علوم کردن و محافظت بر نمازهای جماعت کردن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2347,'مروّت سفر، توشه‌ی خود را صرف کردن و مخالفت رفیقان نکردن و خدا را در هر بلندی و پستی و فرود آمدن و ایستادن و نشستن بسیار یاد کردن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2348,'طلب روزی نیکو نکرده است کسی که از برای تجارت به دریا سوار شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2349,'به درستی که جهاد کردن برای خدا دری است از درهای بهشت که حق تعالی برای مخصوصان دوستانش گشوده است و نعمتی است که برای ایشان ذخیره گردانیده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2350,'کسی را که فرمان نبرند چه رأی به کار برد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2351,'والله که دین و دنیا به اصلاح نمی‌آید مگر به جهاد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2352,'سه چیز است که حافظه را زیاد می‌کند: مسواک کردن، و روزه داشتن، و قرآن خواندن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2353,'هر که جهاد را با قدرت بر آن ترک کند حق تعالی بر او جامه‌ی مذلّت و خواری بپوشاند و بلا او را فرو گیرد و خشنودی از او دوری کند و در دیده‌ها حقیر و بی‌مقدار شود و راه اندیشه بر دلش بسته شود و مغلوب حق گردد و با او با انصاف و عدالت سلوک نکنند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2354,'بدا به حال شما و اندوه مهیا از برای شما که نشانه‌ی تیر دشمنان گردیده‌اید. بر شما غارت می‌آورند و شما بر ایشان غارت نمی‌برید و به جنگ شما می‌آیند و شما به جنگ ایشان نمی‌روید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2355,'خیری نیست در سگ‌ها مگر سگ شکاری یا سگ گله.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2356,'در همه جا خدا را یاد کنید که او با شماست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2357,'هلاک نمی‌شود کسی که مشورت کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2358,'خانه‌ای که در آن قرآن خوانده می‌شود و یاد خدا در آن خانه کرده می‌شود برکت آن خانه بسیار می‌شود و ملائکه حاضر می‌باشند و شیاطین دور می‌شوند و اهل آسمان را روشنی می‌دهد چنان که ستاره‌ها اهل زمین را روشنی می‌دهند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2359,'خانه‌ای که در آن قرآن خوانده نشود و یاد خدا در آن نکنند برکت آن خانه کم می‌باشد و ملائکه دوری می‌کنند و شیاطین در آن خانه حاضر می‌باشند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2360,'فریاد کردن خروس نماز است و بال زدنش رکوع و سجود است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2361,'کراهت دارد که آدمی در خانه شب به روز آورد که دری و پرده‌ای نداشته باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2362,'راه رفتن پیاده با سواره باعث غرور و فساد سواره و موجب مذلّت و خواری پیاده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2363,'هر که با نادانی تجارت کند در ربا خوردن فرو می‌رود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2364,'در بازار یاد خدا بسیار بکنید در وقتی که مردم مشغول دنیا باشند که کفاره‌ی گناهان شما باشد و باعث زیادتی حسنات شما شود و شما را از غافلان ننویسند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2365,'زینهار که قسم مخورید که اگر چه متاع را روا می‌کنید اما برکتش را می‌برد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2366,'صالحان را برای صلاح ایشان دوست دار و مدارا کن به ظاهر با فاسقان و در دل دشمن ایشان باش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2367,'هر که نزد توانگری برود و به نزد او شکستگی کند برای توانگری دو ثلث دینش می‌رود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2368,'هر که والی مردم را ممنوع سازد از آنکه کارهای خود را به او عرض کنند حق تعالی در قیامت حاجت او را برنیاورد و اگر چیزی به رسم هدیه بگیرد چنان است که از غنیمت دزدی کرده است که بدترین دزدی‌هاست و اگر رشوه بگیرد چنان است که به خدا شرک آورده باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2369,'ظلم کننده و یاری کننده‌ی او بر ظلم و کسی که راضی به آن ظلم باشد هر سه در گناه شریکند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2370,'زینهار که اجتناب کن از جاهایی که محل تهمت‌اند و از مجلسی که گمان بد به او ببرند که همنشین بد مصاحب خود را فریب می‌دهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2371,'هر که خود را در محل تهمت درآورد ملامت نکند کسی را که گمان بد به او می‌برد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2372,'بدترین مصاحبان تو کسی است که معصیت خدا را در نظر تو زینت می‌دهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2373,'بر تو باد به حاضر شدن در مجالسی که در آنجا یاد خدا کنند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2374,'زینهار مزاح مکنید که کینه‌ها به هم می‌رسد و دشنام کوچک است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2375,'برای فرزند بر پدر حقی است و برای پدر بر فرزند، حقی. حق پدر بر فرزند این است که: او را در همه چیز اطاعت کند، جز در معصیتِ خداوند سبحان. و حق فرزند بر پدر آن است که: نام زیبا بر او بگذارد و فرهنگِ سازنده‌ی او را نیکو کند و قرآن را به او تعلیم دهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2376,'چشم زدن واقعیت دارد و افسونگری و جادوگری راست است و فالِ نیک زدن صحیح است و فال بد زدن درست نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2377,'نزدیکی و ارتباط با اخلاق مردم، موجب اطمینان از گزند آنان می‌باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2378,'هر کس بر اشیاءِ دور از هم و متضاد اشاره کند و آنها را اراده نماید، چاره‌جویی‌ها او را تنها گذارد و رسوا کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2379,'چه نیکوست فروتنی توانگران به فقرا برای طلب پاداش الهی! و نیکوتر از آن، متانت و بی‌اعتنایی فقرا بر توانگران است به خاطر تکیه بر خدا.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2380,'خداوند عقل و خرد را نزد کسی به ودیعت ننهاد، جز اینکه با همان حقیقتِ شریف، روزی او را نجات داد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2381,'هر کس با حق گلاویز شود، حق او را می‌افکند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2382,'تقوا، فرمانروای اخلاق است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2383,'تندی و تیزيِ زبانت را برای کسی که تو را تعلیم سخن داده، قرار مده و بلاغت و زیبایی کلامت را به رُخ کسی که گفتار تو را بر مبنای قاعده و زیبایی راه انداخته است، مکش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2384,'در تأدبّ نفست این بس که از آنچه از دیگری نمی‌خواهی، خود نیز اجتناب کنی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2385,'هر کس صبر کرد، مانند صبر آزادمردان، [اوست که از اخلاق عالی برخوردار شده] و اگر نتوانست صبر کند، باید مانند جهّال فراموش کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2386,'مردم دنیا مانند کاروانیانی هستند که از همان هنگام فرود در این دنیا، راننده‌ی آنان بر آنان فریاد می‌زند: کوچ کنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2387,'از عقلی که داری، همین تو را بس که راه‌های گمراهی را از رستگاری‌ات روشن بسازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2388,'کار خیر را انجام بدهید و چیزی را کوچک مشمارید، زیرا کار نیکو کوچکش بزرگ است و اندکش فراوان، و هیچ کس از شما نگوید: کسی [دیگر] برای انجام کار نیکو از من شایسته‌تر است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2389,'برای هر یک از خیر و شرّ، اهلی است؛ اگر شما یکی از آنها را رها کردید، کسانی هستند که جای شما را بگیرند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2390,'هر کس دورن خود را اصلاح کند، خداوند هم آشکار او را اصلاح فرماید و هر کس برای دین خود عمل کند، خداوند برای امر دنیای او کفایت کند و هر کس رابطه‌ی بین خود و خدا را نیکو نماید، خداوند رابطه‌ی بین او و مردم را نیکو فرماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2391,'بردباری، پرده‌ای پوشاننده است و عقل شمشیری است برّان؛ پس عیوبِ اخلاقی خود را با بردباریت بپوشان و هوایت را با عقلت بكُش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2392,'هر کس احتیاجی را به مؤمن عرضه کند، مانند این است که آن را به خدا عرضه کرده و هر کس آن را با کافری مطرح کند، گویی از خدا شکایت کرده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2393,'بزرگ‌ترین حسرت‌ها در روز قیامت، حسرت مردی است که مالی را از راهی خلافِ اطاعتِ خداوندی بیندوزد؛ پس از وی مردی آن مال را از او ارث ببرد که آن را در اطاعت خداوند سبحان مصرف کند و از همین راه داخل بهشت شود و آن شخص اول را که مال اندوخته بود، به خاطر همان مال داخل آتش شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2394,'زیان‌کارترین مردم از حیث کالا و محروم‌ترین آنان از نظر تلاش، مردی است که بدن خود را در طلب مال ناتوان ساخت و فرسود، ولی مقدّرات، اراده‌ی او را یاری نکرد؛ پس با حسرت همان مال از دنیا خارج شد و با نتیجه‌ی فاسدِ آن، وارد آخرت شد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2395,'رزق (روزی) بر دو نوع است: روزی‌ای که تو را می‌جوید و روزی‌ای که تو آن را می‌جویی. پس هر کس دنیا را بجوید، مرگ او را طلب کند تا این که او را از دنیا بیرونش کند و کسی که آخرت را طلب کند، دنیا او را می‌جوید تا روزيِ او را کاملا~به او برساند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2396,'همواره پایان یافتن لذایذ و دوام و بقای نتایج سوء آنها را به یاد داشته باشید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2397,'[هنگامی که ظاهر یک شخص تو را به شگفتی وادارد]، تجربه کن، [باشد] که او را دشمن خواهی داشت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2398,'خداوند هرگز درِ شکرگزاری را بر بنده‌ای باز نمی‌کند که درِ افزایش را به روی او ببندد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2399,'شایسته‌ترین مردم به کرامت، کسی است که مردمانِ با کرامت به وسیله‌ی او شناخته می‌شوند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2400,'مردم، دشمن آن چیزی هستند که آن را نمی‌دانند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2401,'چه بسا تصمیم‌های روزانه که خواب آنها را شکست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2402,'بهترین شهر یا کشور برای تو، جایی است که زندگی تو را تنظیم و آماده کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2403,'اندکی که دوام داشته باشد، بهتر است از بسیاری که مایه‌ی ملالت و تنگدلی باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2404,'اگر در مردی یک خصلت زیبا و با عظمت دیدید، نظایر آن خصلت را در او انتظار داشته باشید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2405,'خداوند درِ دعا را بر بنده‌ای باز نمی‌کند که درِ پذیرش دعا را به روی او ببندد و چنان است که درِ توبه را برای بنده‌ای باز کند و درِ بخشایش را بر او ببندد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2406,'هر کس بدون آشنایی با فقه به تجارت پرداخت، خود را در معرض ربا قرار داد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2407,'هر کس مصیبت‌های کوچک را بزرگ شمرد، شهوات و تمایلات برای او پست می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2408,'هیچ مردی شوخی نکرد، جز این که چیزی از عقلش را بیرون انداخت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2409,'اعراض کردنِ تو از آنچه رغبتی به تو دارد، کاهشی در نصیبِ توست و رغبت و میل تو درباره‌ی کسی که از تو رویگردان است، ذلّت و پستی نفس است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2410,'دو گرسنه (جوینده‌ی حریص) هرگز سیر نمی‌شوند: جوینده‌ی علم و جوینده‌ی دنیا.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2411,'از مختصّات ایمان است که صدق (راستی) را بر کذب مقدّم بداری، آنجا که صدق ضرری به تو برساند و دروغ به نفع تو باشد و در گفتارت برتری از عمل تو دیده نشود و هنگامی که درباره‌ی دیگری سخن می‌گویی، از خدا بترسی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2412,'بردباری و آرامش دو همتایند که بزرگی همّت، آن دو را به وجود می‌آورد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2413,'غیبت کردن، کوشش ناتوان است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2414,'دنیا برای حقیقتی دیگر آفریده شده است، نه برای خویشتن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2415,'چشم چون سربند است برای نشستگاه و وقتی خواب در چشم آید، بندِ بسته باز شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2416,'توحید آن است که خدا را در توهّم نیاوری و عدالت آن است که او را متهم نسازی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2417,'خیری در سکوت از حُکم نیست، چنان که خیری در سخن گفتن از روی نادانی وجود ندارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2418,'پاداش مجاهدی که در راه خدا شهید شده، با عظمت‌تر از کسی نیست که قدرت پیدا کند و عفو نماید؛ نزدیک است که عفیف (پاکدامن)، فرشته‌ای از فرشتگان خداوندی باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2419,'قناعت، مالی است تمام نشدنی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2420,'بدترین گناهان، آن گناه است که مرتکب شونده‌اش، آن را سبک شمارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2421,'خداوند، مردم نادان را برای تعلّم مسئول قرار نداد، جز این که نخست اهل علم را برای تعلیم آنان قرار داد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2422,'بدترین برادران، کسی است که برادران به خاطر او دچار تکلّف شوند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2423,'هنگامی که مؤمن، برادرش را به غضب آورد، در حقیقت از او مفازقت کرده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2424,'مردی که کارهای او را زنی تدبیر کند، ملعون است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2425,'کسی که از روی تقیه قسم بخورد برای آنکه دفع ضرری از خود بکند برای او گناه و کفاره نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2426,'چون با برادران مؤمن ملاقات نمایید با ایشان مصافحه کنید و پنداشت و خوشحالی نزد ایشان ظاهر کنید تا آنکه چون جدا شوید هیچ گناه بر شما نمانده باشد و با دشمن خود نیز مصافحه کنید هر چند او نخواهد که خدا چنین امر فرموده است و باعث دفع دشمنی او می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2427,'نگهداری آن‌چه در دست داری، بهتر از طلب کردن چیزی است که در دست دیگری می‌باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2428,'کسی که فراوان سخن بگوید، به سرسام‌گویی افتد و کسی که بیندیشد، بینا شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2429,'بپرهیز از تکیه کردن به آرزوها، زیرا آرزو متاع و کالای مردم احمق است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2430,'عقل، حفظِ تجربه‌هاست و بهترین تجربه‌ها چیزی است که پندی به تو دهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2431,'هیچ انگیزه‌ای برای خوش‌گمانی زمامدار درباره‌ی رعيّتش شدیدتر از احسان وی به رعيّت، و سبک کردن مشقت‌ها از دوش آنان، و تحمیل نکردن تکلیفی که متوجه آنان نیست، نمی‌باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2432,'لشکریان با اذن خداوندی نگهبانان رعيّت، و موجب افتخار و زینت زمامداران و عزت دین و طرق ایجاد امنیت در جامعه می‌باشند، و برای رعيّت، استقرار و اسقامتی نیست، جز با لشکریان.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2433,'هرگز حیثت و موقعیت چشمگیر یک انسان باعث نشود تو کوشش و تکاپوی اندكِ او را بزرگ بشماری [و برعکس] موقعیتِ حقیر یک انسان موجب نشود کوشش و تکاپوی بزرگِ او را کوچک به حساب بیاوری.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2434,'ویرانی زمین ناشی از فلاکت مردم آن است. و سببِ فلاکتِ مردم، روی آوردن کارگزاران به مال‌اندوزی، و بدگمانی (ناامیدی) از دوام حکومت‌شان، و عبرت نگرفتن از حوادث روزگار و جریانات اجتماعی و سیاسی اقوام و ملل گذشته است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2435,'کسی که به ارزش خویشتن نادان باشد، به ارزش دیگران نادان‌تر خواهد بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2436,'هر کس بردباری کند، در زندگی خود تفریط نکند و در میان مردم، پسندیده زندگی کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2437,'هر کس نزاع و تخاصمش بر مبنای نادانی باشد، کوريِ او درباره‌ی حق فراوان خواهد بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2438,'هر کس از جاده‌ی حقیقت بلغزد، نیکی نزد او بد، و بد نزد او نیکی تلقی می‌شود و به مستی گمراهی مبتلا گردد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2439,'هنگامی که سخن حکما صحیح باشد، دوای دردِ جهل می‌شود و اگر خطا باشد، دردی بر دردها می‌افزاید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2440,'سخن مانند شکار در حال فرار است، یکی آن را می‌زند و دیگری در زدنِ آن خطا می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2441,'ای فرزند آدم! غصّه‌ی روزی را که نیامده، بر غصّه‌ی روزی که آمده است، اضافه مکن، زیرا اگر از عمرت چیزی باقی مانده باشد، خداوند روزيِ تو را در همان روز عطا خواهد فرمود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2442,'مواظب باش دوستی تو درباره‌ی دوستت حدّی (اندازه) داشته باشد، زیرا باشد که روزی با تو خصومت بورزد. هم‌چنین، توجه کن دشمنی تو درباره‌ی دشمنت اندازه‌ای داشته باشد، زیرا ممکن است روزی فرا رسد که دوستت شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2443,'علم خود را به جهل و یقین‌تان را به شكّ مبدل مسازید. هنگامی که دانستید، عمل کنید و زمانی که یقین کردید، به عمل اقدام نمایید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2444,'طمع، وارد کننده است [به حوادث مبهم] و صادر کننده نیست (روشن‌کننده‌ی تکلیف و پایان کار نیست) و ضمانت‌کننده‌ای است که وفا نمی‌کند و بسا نوشنده‌ی آب، پیش از آنکه سیراب شود، گلویش می‌گیرد و هر اندازه ارزش چیزی که مورد رقابت است، بیش‌تر باشد، مصیبتِ گم شدنِ آن نیز بزرگ‌تر می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2445,'آن اندکی که دوام خواهد داشت، بیش‌تر مورد امید است از آن فراوانی که تنگدلی می‌آورد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2446,'در آن هنگام که مستحبّات به واجبات ضرر وارد کند، آن مستحبّات را رها کنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2447,'میان شما و نصیحت، پرده‌ای از غرور است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2448,'علم، راه پوزش را بر کسانی که می‌خواهند بر جهل خود اصرار بورزند و علت برای آن می‌آورند، بسته است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2449,'هر گاه خداوند بنده‌ای را پست و ذلیل گرداند، علم را برای او ممنوع سازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2450,'اگر خداوند در مقابل معصیت کردنش تهدید نمی‌فرمود، باز واجب بود به خاطر شکر نعمت‌هایش معصیت نشود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2451,'با نابخرد (احمق) هم‌صحبت مباش، زیرا او می‌خواهد کار خود را برای تو بیاراید و دوست دارد تو هم مثل او باشی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2452,'دوستان تو بر سه نوعند و دشمنان تو نیز بر سه نوع هستند: پس دوستان تو عبارتند از: دوستِ تو، دوستِ دوستِ تو، دشمن دشمن تو، و دشمنان تو عبارتند از: دشمن تو، دشمن دوستِ تو و دوستِ دشمن تو.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2453,'چه فراوان است وسایل عبرت گرفتن و چه اندک است بهره‌برداری از این وسایل برای عبرت‌گیری.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2454,'هر کس در دشمنی شدت به خرج دهد، مرتکب گناه شده و کسی که کوتاهی کند، ستم ورزیده است و هر کس در خصومت مبالغه کند، نمی‌تواند برای خدا تقوا داشته باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2455,'هیچ گناهی [قابل برگشت] برای من اهمیتی ندارد، اگر مهلتی پس از ارتکاب آن داشته باشم که دو رکعت نماز بخوانم و عافیت را از او مسئلت بدارم.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2456,'آن کس که به شدیدترین بلا مبتلاست، نیازش به دعا، بیش از آن بی‌بلایی نیست که از بلا در امان نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2457,'مردم فرزندان دنیا هستند و مرد به محبت مادرش توبیخ نمی‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2458,'محدودیت مدتِ زندگی، برای نگهبانی انسان از انحراف کافی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2459,'محبت پدران موجب خویشاوندی میان فرزندان است، و خویشاوندی به محبت بیش‌تر نیازمند است تا محبت به خویشاوندی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2460,'از حدس و گمان‌های مردمانِ با ایمان بترسید، زیرا خداوند متعال حق را در زبان آنان قرار داده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2461,'ایمان هیچ بنده‌ای مطابق واقع نمی‌شود، جز اینکه به آنچه در اختیار خداست، مطمئن‌تر از آن باشد که در اختیار خودِ اوست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2462,'مسکین و بینوا، فرستاده‌ی خداست. پس هر کس او را محروم برگرداند، در حقیقت خدا را ممنوع نموده و هر کس به آن بینوا عطا کند، پس به خدا عطا کرده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2463,'برای دل‌ها اقبالی (روی‌آوردنی) است و ادباری (روی‌گرداندنی). پس هنگامی که روی آورد، مستحبات را [هم] بر او عرضه کنید و اگر روی گرداند، تنها به واجبات قناعت کنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2464,'سنگ را به همان‌جا که از آن آمده، برگردانید، زیرا شرّ را چیزی جز شرّ دفع نمی‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2465,'فقر عامل کاهش دین است و عقل را به دهشت می‌اندازد و برای عداوت انگیزه می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2466,'جاهلی که یادگیرنده است، مانند عالِم است و عالِمی که که در علم کجروی می‌کند، شبیه به جاهلی است موذی و لجوج.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2467,'از معصیت ورزیدن به خدا در خلوت‌ها تقوا بورزید و اجتناب کنید، زیرا شاهد، خودِ حاکم است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2468,'پیروز نشد هر کس که گناه بر او پیروز شد و آن کس که شرّ بر او غلبه کند، مغلوب است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2469,'خداوند سبحان در اموالِ توانگران، معاش فقرا را قرار داده است. در نتیجه، هیچ فقیری گرسنه نماند، جز اینکه توانگری به همان اندازه از مالِ فقیر بهره‌مند شد و خداوند متعال آنان را در مورد این ظلم بازخواست خواهد کرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2470,'بی‌نیازی از عذر خواستن، بهتر از عذرخواهی راستین است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2471,'کم‌ترین حقی که خداوند بر گردن شما دارد، این است که از نعمت او برای گناهان کمک نگیرید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2472,'خداوند سبحان اطاعت را غنیمتِ هشیارانِ خردمند قرار داده، در آن هنگام که مردم ناتوان قصور نمایند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2473,'حکومت و قدرتِ سلطان، نگهبان خداست در روی زمین.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2474,'سُرور و شادمانی مؤمن در چهره‌ی اوست و اندوهش در قلبش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2475,'اگر بنده‌ی خدا اجل و سرنوشتِ نهایی‌اش را ببیند، آرزو و فریبندگی‌ آن را دشمن می‌دارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2476,'هر کسی دو شریک در مال دارد: وارث و حوادث.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2477,'کسی که چیزی از وی خواسته شده، تا وقتی وعده نداده، آزاد است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2478,'کسی که دیگران را به کاری خواند که خود به آن عمل نکند، مانند تیراندازی است که می‌خواهد از کمانِ بی‌زه، تیر بیندازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2479,'علم بر دو نوع است: یک نوع از آن دو، طبیعی [و استعدادی] است، نوع دوم اکتسابی است و نوع اکتسابی بدون طبیعی نفعی ندارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2480,'درستی آن رأی که با دولت پیش آید، با رفتن آن می‌رود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2481,'پاکدامنی، زینت فقر است و شُکر زینتِ توانگری.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2482,'روز عدالت بر ظالم، سخت‌تر است از روز ظلم بر مظلوم.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2483,'بزرگ‌ترین توانگری، ناامیدی است از آنچه در دست مردم است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2484,'سپاسگزاری بیش از شایستگی، چاپلوسی است و کوتاهی ورزیدن از اندازه‌ی شایستگی، یا از ناتوانی است یا از حسادت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2485,'بدترین گناهان، گناهی است که مرتکب شونده‌ی آن، کوچکش بشمارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2486,'آگاه باشید! احتیاج و بینوایی بلاست و شدیدتر از بینوایی، بیماری بدن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2487,'هر کس عملش او را به عقب بیندازد، نَسَب (نژادش) او را جلو نیندازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2488,'هر کس شرافت و حیثیتِ نفس خویشتن را از دست داده باشد، شرافت پدرانش سودی به حال او ندارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2489,'کسی که در عیب خود بنگرد، از عیب جویی دیگران رویگردان می‌شود و هر کس به روزيِ خداوندی خشنود شود، از آنچه که از او فوت شده، اندوهگین نمی‌گردد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2490,'هر کس شمشیر ظلم بکشد، با همان شمشیر کشته می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2491,'قناعت، مالی است فناناپذیر.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2492,'هر کس بداند سخنش از گروه اعمالش است، سخن را جز در آنچه برای او اهمیت دارد، کم کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2493,'ظالم سه علامت دارد: به کسی که بالاتر از اوست، با نافرمانی او ظلم می‌کند و به آن که پایین‌تر از اوست، با غلبه و زورگویی؛ و ستمکاران را یاری کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2494,'بزرگ‌ترین عیب آن است که آنچه را در خود توست، در دیگران عیب محسوب کنی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2495,'هر کس در تنگنای زندگی قرار بگیرد و آن را آزمایش نداند، پاداشی را که امیدِ آن می‌رفت، ضایع نموده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2496,'کسی که به دنیا تکیه کند، او را نترساند، جز مشاهده‌ی به هم ساییده شدنِ دندان‌های مصیبت‌ها.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2497,'ای مردم! تأدیب و تربیتِ نفوس خویش را خود به عهده بگیرید و نفس‌های خود را از عاداتِ پلید منصرف کنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2498,'از حماقت و خشونت است شتاب کردن پیش از امکان یک چیز و صبر و تحمّل بعد از فرصت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2499,'فکر، یک آیینه‌ی صاف است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2500,'تجربه و عبرت گیری، تبلیغ کننده‌ای است خیرخواه.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2501,'برای ادب کردنِ شایسته‌ی نفس خویشتن کافی است که پرهیز نمایی از ارتکاب آنچه که برای دیگری کراهت داری.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2502,'علم به عمل دعوت می‌کند. اگر عمل آن را اجابت کرد، با آن خواهد بود، وگرنه کوچ می‌کند و از صحنه‌ی وجود آدمی خارج می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2503,'ای مردم! کالای دنیا خس و خاشاکی است وبا آلود، پس از چراگاه آن اجتناب کنید. عدم آرامش در این دنیا سعادت آمیزتر از اطمینان و سکونت در آن است و رمقی از معاش، پاکیزه‌تر از ثروت آن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2504,'انسان با ایمان به این دنیا به چشم عبرت و تجربه می‌نگرد و از توشه به اندازه‌ی سیر شدن شکم یک مضطرّ بهره‌مند می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2505,'خداوند سبحان پاداش را در مقابل عبادت و عذاب را در مقابل معصیت قرار داده است برای جلوگیری از عذاب بندگانش و تحریک آنان به بهشتش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2506,'شرفی بالاتر از اسلام وجود ندارد و نه عزّتی بالاتر از تقوا، و نه تعقلی بهتر از پرهیزکاری.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2507,'هیچ یار و یاوری موفق‌تر از توبه وجود ندارد و هیچ گنجی بی‌نیاز کننده‌تر از قناعت، و هیچ مالی بالاتر از خشنودی، از بین برنده‌ی فقر نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2508,'هر کس کفایت ورزید به حدّ کفایت از معیشت، به آسودگی دست یافت و در گشایش راحتی مستقر شد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2509,'تمایلات، کلیدِ مشقت‌هاست و مَرکبِ زحمت و آزار، طمع و کبر و حسد انگیزه‌هایی برای تجاوز به سوی گناهان و شرّ در بر گیرنده‌ی همه‌ی بدی‌های عیوب است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2510,'از مردم با ایمان کسی است که عمل زشت را با دست و قلبش منکر می‌شود و ناپسند می‌شمارد، این است تکمیل کننده‌ی خصلت‌های خیر.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2511,'بخل، همه‌ی عیوب را در بر دارد. این صفتِ خبیث، افساری است که آدمی به وسیله‌ی آن به هر بدی کشیده می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2512,'هر کس چیزی را طلب کند، یا آن را و یا [حداقل] اندازه‌ای از آن را پیدا می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2513,'هیچ خیری که پشتش آتش است، خیر نیست و هیچ شرّی شر نیست که پشتش بهشت است و هر نعمتی جز بهشت، محقّر است و هر بلایی جز آتش، عافیت است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2514,'هر کس نعمت‌های خداوندی به او فراوان باشد، احتیاجات مردم هم به او بسیار باشد و هر کس در آن نعمت‌ها- در آنچه که خداوند واجب فرموده- قیام کند، آن نعمت‌‌ها را در معرض دوام و بقا قرار داده و هر کس در آن نعمت‌ها به ادای حقوق لازم در آن‌ها قیام نکند، آن نعمت‌ها را در معرض زوال و فنا قرار داده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2515,'هر کس ببیند در جامعه ستمی صورت می‌گیرد و به عمل زشتی خوانده می‌شود، و او با قلبش آن را انکار کند، پس دینش سالم و خودش از گناه برکنار است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2516,'سخن در گرو (در بند) توست، تا زمانی که آن را نگفته باشی. وقتی آن سخن را ابراز کردی، تو در گرو آن خواهی بود. پس زبانت را محفوظ دار، همان گونه که طلا و نقره‌ات را، زیرا چه بسا سخنی که نعمتی را سلب نموده و نقمتی را جلب کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2517,'بترس از این که خدا تو را در ارتکاب معصیتش ببیند و در اطاعتش نیابد که در نتیجه از زیانکاران باشی. پس وقتی نیرومند شدی، بر اطاعت خداوندی قوی باش و اگر ناتوان گشتی، از معصیت خداوندی ناتوان باش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2518,'تکیه بر دنیا- با آنچه از این دنیا می‌بینی- نادانی است و تقصیر در عمل نیکو با اطمینان به پاداش آن، زیانکاری است و اطمینان به هر کسی پیش از آزمایش او، ناتوانی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2519,'سخن بگویید تا شناخته شوید، زیرا مرد زیر زبان خود مخفی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2520,'آنچه از دنیا به سوی تو آید، آن را بگیر و از آنچه از تو رویگردان شود، چشم بپوش. پس اگر نتوانستی، در طلب دنیا، از نیکی و شایستگی تجاوز مکن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2521,'بسا سخنی که نافذتر و تیزتر از حمله است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2522,'هر آنچه بتوان به آن قناعت کرد، برای زندگی کافی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2523,'روزگار تو ای آدمی! بر دو نوع است: روزی به نفع تو و روزی به ضرر توست. در آن دوران که روزگار به سود تو می‌گردد، به خودکامگی مپرداز و در آن دوران که به ضرر توست، صبر و شکیبایی پیشه کن!((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2524,'افتخار و به خود بالیدن را کنار بگذار و کبر و خودپسندی‌ات را ساقط کن و به یاد قبرت باش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2525,'اگر انسان‌ها در عظمت خداوند و بزرگی نعمتِ او می‌اندیشیدند، قطعاً به راه راست – [که از آن منحرف شده‌اند] – برمی‌گشتند. ولی دل‌ها بیمار است و دیده‌ها مختل.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2526,'ای مردم! شما را توصیه می‌کنم به تقوای الهی و کثرتِ ستایش خداوند برای عطایایی که به شما نازل نموده و نعمت‌هایی که بر شما عنایت فرموده و آزمایشی که نزد شماست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2527,'به یاد مرگ باشید و غفلت از آن را کم کنید. شما چگونه غفلت می‌ورزید از چیزی که هرگز از شما غافل نیست و به کسی (ملک‌الموت) طمع بسته‌اید که مهلتی به شما نخواهد داد؟((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2528,'سبقت بجویید، ای مردم- خدا شما را رحمت کناد- به آن جایگاه‌های خود که به آباد کردن آن‌ها مامور شده و ترغیب به ورود و به سوی آن‌ها دعوت شده‌اید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2529,'قطعی است که امر ما (اهل بیت) سخت است و بس دشوار. آن را تحمل نمی‌کند، جز مؤمنی که خداوند قلب او را برای ایمان آزمایش نموده است و حدیث ما را جز دل‌های امین و عقول و خِردهای متین نگاه نمی‌دارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2530,'برای خداوند سبحان و متعال، هیچ چیزی از آن‌چه که بندگان در شب و روزهای‌شان در آن اختلاف دارند، پنهان نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2531,'اعضای شما شهود خداوندی هستند و اندام‌هایتان لشکریانش و وجدان‌هایتان چشمانش و خلوت‌های شما برای او آشکار است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2532,'مردم باتقوا در میان آن مردم اهل فضیلت‌ها هستند: منطق آنان درست و صحیح، پوشاک‌شان بر مبنای میانه‌روی، حرکت‌شان بر اساس فروتنی است. چشمان خود را از آن‌چه که به آنان تحریم فرموده، پوشیدند و گوش‌های خود را به آن علمی که برای آنان سودمند است، فرا دادند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2533,'نفوس انسان‌های متّقی هنگام بلا، همان گونه است که در حالات آسایش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2534,'روشنایی چشم انسانِ باتقوا در حقیقتی است که زوال و فنا راهی به آن ندارد و پارسایی و امتناعش درباره‌ی چیزی است که زوال و نابودی بر سرش تاختن خواهد آورد. بردباری را با علم، و قول را با عمل درمی‌آمیزد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2535,'هیچ حجابی شما را از خدا نبریده و هیچ دری برای ورود به پیشگاه‌اش برای شما بسته نشده است و قطعاً او در هر مکان و در هر زمان و وقتی موجود است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2536,'ای بندگان خدا! شما را به تقوای الهی توصیه می‌کنم. زیرا تقواست که مهار حقیقی از آلودگی‌ها و انحرافات است و استحکام‌بخش [حیات معقولِ] انسان‌ها.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2537,'در جهاد با دشمن‌تان صدق نيّت داشته باشید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2538,'اطاعت خداوندی نگهدارنده است از مهلکه‌های محیط بر شما و از موقعیت‌هایی که در انتظار است و از گرمی آتش‌های شعله‌ور.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2539,'نفس خود را برای عبادت خداوندی رام و مطیع بسازید و حرکت کنید به سوی خداوندی با اطاعتی که شایسته‌ی اوست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2540,'قرآن معدن ایمان است و متن آن، و سرچشمه‌های علم است و دریاهای آن، و باغ‌های دادگری است و غدیرهای (گودی‌های آب) آن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2541,'تقوا شما را به زندگی با رفاه و آسایش برمی‌گرداند که [امروز] برای شما ناپدید می‌نماید و شما را به وطن‌های وسیع – که فراتر از مادّه و مادّیات است و جایگاه‌های امن و مقامات عزّت – رهنمون می‌شود، در روزی که چشم‌ها باز و خیره می‌ماند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2542,'هر کس تقوا را پیشه ساخت، سختی‌ها از وی دور و ناپدید شد پس از آن‌که نزدیک بود، و امور برای او شیرین شد پس از تلخی‌هایش. امواج از وی باز شد پس از حمله‌ی انبوهش، و دشواری‌ها برای او آسان گشت پس از مشقت‌هایی که در آن بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2543,'قرآن عزّت و شرفی است برای کسی که آن را [برای حیات معقول خود] یاور اتخاذ کرده و امان است برای کسی که وارد آن شود و هدایتی است برای شخصی که از آن پیروی نماید و عذر است برای آن انسان که خود را به آن نسبت بدهد و دلیلی است روشن برای کسی که در سخن گفتن به آن استناد کند و شاهدی است [راستین] برای انسانی که به وسیله‌ی آن احتجاج کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2544,'به امر نماز ملتزم شوید و در حفظ آن بکوشید و این [فریضه‌ی بااهمیت] را فراوان به جای بیاورید و با این عبادت بزرگ به خدا نزدیک شوید، زیرا نماز برای مردمِ باایمان، واجبِ متعيّن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2545,'مردانی از مؤمنان، حقّ نماز را شناختند که نه زر و زیور مالِ دنیا آنان را به خود مشغول می‌دارد و نه آن‌چه که از مال و فرزند باعث چشم‌روشنی می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2546,'دوست بدار بر دیگری آن‌چه را که برای خود دوست داری و آن‌چه برای تو ناملایم و ناگوار است، بر دیگران نیز کراهت‌بار تلقی کن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2547,'هر کس زکات را بدون خشنودی نفْس ادا کند و امید به آن داشته باشد که به عوضی بهتر از آن برسد، چنین شخصی به سنت اسلام نادان است و پاداش حقیقی را از دست داده، دارای عملی گمراه است و پشیمانی دراز.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2548,'دل‌های خود را از دنیا بیرون کنید، پیش از آن‌که بدن‌هایتان از دنیا خارج شود. شما در این دنیا در عرصه‌ی آزمایش قرار گرفته‌اید و برای غیر این دنیا آفریده شده‌اید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2549,'مرگ در رابطه با شما چنان است که گویی چنگال‌های خود را در شما فرو برده و حوادث شدید و دشواری‌های ترسناک، شما را احاطه کرده است. پس علایق خود را از دنیا قطع و بر توشه‌ی تقوا تکیه کنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2550,'در یاری خداوند برای کسی که از او بی‌نیازی بخواهد، کفایتی است، و برای کسی که شفا می‌جوید، شفایی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2551,'هر انسانی باید کرامت و خیر را با پذیرش آن استقبال کند و از هر حادثه‌ی کوبنده، پیش از آن‌که وارد شود، برحذر باشد. و هر کسی باید بنگرد در کوتاهی روزها و اقامت اندک در منزلگاهی که [با سرعت] آن را به منزلگاه دیگر تبدیل خواهد کرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2552,'حق، گسترده‌ترین اشیاء است در توصیف، و محدودترین و تنگ‌ترین اشیاء است به هنگام اجرای منصفانه.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2553,'حق، چیزی است که به نفع کسی به جریان نمی‌افتد، جز این‌که روزی دیگر به ضرر او سراغش را خواهد گرفت [و برعکس]، و به ضرر کسی جاری نمی‌شود، جز این‌که روزی دیگر به سود او به جریان می‌افتد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2554,'خداوند سبحان برای بندگان خود چنین مقرر فرموده که او را اطاعت کنند و پاداش آنان را از روی احسان، افزایش ثواب قرار داده و گسترش پاداش به آنان به خاطر آن است که خداوند شایسته‌ی افزودن و گستردنِ آن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2555,'هر حقی در مقابل حقی است، که هر حقی تکلیفی را ایجاب می‌کند و هر تکلیفی، حقی را، و هیچ یک از این امور بدون دیگری تحقق پیدا نمی‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2556,'باعظمت‌ترین حقوق الهی، حق حاکم بر مردم و حق مردم بر حاکم است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2557,'هرگز امور مردم جامعه بدون صلاحيّت حُكّام، اصلاح نشود و امور والیان جامعه بدون نظام صحیح بر مردم اصلاح نگردد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2558,'در آن هنگام که مردم جامعه حق حاکم را به حاکم ادا کردند و زمامدار نیز حق مردم را به آنان ادا کرد، حق در میان آنان عزیز شود و مسیرهای روشن دین هموار، و نشانه‌های عدالت معتدل و برپا، و سنّت‌ها در مجرای خود به جریان می‌افتند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2559,'ای مردم! بر شما باد خیراندیشی و خیرخواهی در اجرای حقوق درباره‌ی یکدیگر و کمک و یاوری بر ادای آن‌ها.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2560,'هیچ‌کس نمی‌تواند به حقیقتِ آن اطاعتی نایل شود که شایسته‌ی خداوند سبحان است، هرچند اشتیاق او برای تحصیل خشنوديِ خداوندی شدید و تلاش و کوشش او در عمل طولانی باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2561,'آن‌چه که از حقوق خداوندی بر بندگانش واجب است، خیراندیشی و خیرخواهی به اندازه‌ی طاقت و همیاری برای برپا داشتن حق در میان‌شان می‌باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2562,'قطعی است، برای کسی که جلالِ خداوند سبحان در جان او و موقعیت آن مقام ربوبی در دلش بزرگ باشد، سزاوار است که هرچه جز خداوند بزرگ است، نزد وی در مقابل عظمتِ ربوبی کوچک باشد و سزاوارترین مردم برای چنین موضعی- به طور حتم- کسی است که نعمت خداوندی برای او بزرگ و احسان او برای آن کس لطیف است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2563,'از زشت‌ترین حالات زمامداران نزد مردمان صالح، این است که مردم درباره‌ی آنان گمان فخرفروشی و غرور ورزی داشته باشند و وضع شخصیتی آنان بر کبر مبتنی شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2564,'آن کس که وقتی حق به او گفته شود، یا عدالت به او عرضه شود، از شنیدن و پذیرفتن آن‌ها سنگینی احساس کند، عمل به آن دو برای او سنگین‌تر خواهد بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2565,'سعادتمندان سرای آخرت، با دعا به بارگاه خداوند، عطر جان‌فزای بخشش خداوندی را استشمام کرده و در گرو احتیاج به لطف و احسان خداوندی، اسیران خضوع به عظمتِ ربّانی‌اند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2566,'نفْست را برای خودت مورد محاسبه قرار بده، زیرا برای نفوس دیگر حسابگری است جز تو.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2567,'بسا خیرخواهی که درباره‌ی تو و برای تو خیراندیشی و خیرخواهی می‌کند، ولی نزد تو متهم است. و بسا راستگویی که خیر دنیا را به تو راست می‌گوید، ولی از طرف تو تکذیب شده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2568,'چه خانه‌ی خوبی است این دنیا برای کسی که به خانه (حقیقی) بودنِ آن خسنود نباشد و چه محل خوبی است برای کسی که آن را برای خود محل وطن اتخاذ نکرده است. یقینی است که سعادتمندان دنیا در ابدیت کسانی هستند که امروز از آن گریزانند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2569,'در آن هنگام که زمین، لرزه‌ی بزرگ را بگیرد و قیامت با حوادث بزرگش محقق گردد و هرکس به عملی که کرده، ملحق شود و به هر معبودی آن‌که عبادتش نموده، برسد و اهل هر طاعتی به مطاع خود نایل آید، در آن روز، در سنجش عدالت و قسط خداوندی یک نگاه در هوا و صدای آرام حرکتِ قدم در زمین به نتیجه و جزای خود نرسد، جز بر مبنای حق خود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2570,'این دنیا خانه‌ای است پیچیده در بلا و معروف به فریبکاری؛ حالات متنوّع آن دوامی ندارد و ساکنانش از حوادث آن سالم نباشند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2571,'تقوای الهی کلید رستگاری است، و ذخیره برای قیامت، و آزادی از هر عادتِ ثابت، و نجات از هرگونه هلاکت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2572,'به وسیله‌ی تقواست که هر جوینده به مقصود خود رسد و هر گریزان نجات پیدا کند و با این صفتِ شریف است که برای هر آرمان، توفیق وصول حاصل آید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2573,'راویان علم فراوانند و رعایت کنندگانش اندک.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2574,'کسی که از جهاد کراهت دارد، نبودنش بهتر از حضورش در معرکه است و نشستن‌اش بی‌نیاز کننده‌تر از حرکت و برخاستن اوست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2575,'سکوت و آرامش بهترین مانع شکست و اضطراب است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2576,'کسی که در راه حق شهید شده است، منزلگه نهایی او بهشت و کسی که در راه باطل کشته شده، سرنوشتش رو به آتش است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2577,'به درستی که مرگ، ویران کننده‌ی لذت‌های شماست و تیره‌ کننده‌ی شهوات و دور کننده‌ی مقاصد شما. دیدار کننده‌ای است ناخوشایند. متّصلی است به انسان که مغلوب نمی‌شود و کشنده‌ای (کینه‌ورزی) است که نتوان جستجویش کرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2578,'برحذر باشید از دنیا، زیرا این دنیا به شدّت مکرپرداز و مغرور کننده و فریباست. روزی دهنده است و روزی ممتنع (چیزی می‌دهد و از چیزی دیگر جلوگیری می‌کند)، می‌پوشاند و می‌كَنَد. آسایشش دوامی ندارد و مشقتش پایان نپذیرد و بلایش متوقف نشود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2579,'آگاه باشید! زبان، پاره‌ای از انسان است. هنگامی که زبان امتناع کند (بند آید)، از سخن گفتن ناتوان شود و زمانی که زبان به فعالیت بیفتد، سخن گفتن به آن مهلت ندهد. و قطعی است ماییم فرمانروایان کلام که ریشه‌ها و رگ‌های آن در ما روییده و شاخه‌هایش بر ما خمیده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2580,'آن که خوش منظر است، عقلش ناقص می‌باشد و کسی که کشیده قامت است، کوتاه همّت است. کسی که دارای عمل پاکیزه است، چهره‌ای زشت دارد و آن که قامتی کوتاه دارد، زیرک و هشیار است. انسانی که طبیعتش احسان‌گر است، تصنّع کردنش ناخوشایند است و کسی که قلبش سرگشته است، تعقّلش آشفته می‌باشد و آدم خوش‌زبان، دارای دلی آهنین است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2581,'آن کس را که به تو معصیت کند، به وسیله‌ی آن کسی که تو را اطاعت کند، بکوب، و با کسی که از تو پیروی کند و در انقیاد تو باشد، از کسی که از فرمان تو سرپیچی کند، بی‌نیاز باش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2582,'جز این نیست که انسان به آن‌چه در زندگی اندوخته، مستحق پاداش یا عقاب می‌شود و به وصول آن‌چه پیش انداخته، گام برمی‌دارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2583,'اندوهگین باش به آن‌چه از آخرتت از دست داده‌ای و به خاطر آن‌چه از دنیا به دست آوردی، شادی فراوان نداشته باش و به خاطر آن‌چه از دنیا از دست داده‌ای، غصه مخور و ناله مکن و همه‌ی اهتمام خود را به پس از مرگ منحصر کن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2584,'هرگز بنده‌ی دیگری مباش، در حالی که خدا تو را آزاد آفریده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2585,'هیچ چیزی را به خدا شریک قرار ندهید و سنّت محمد (ص) را تباه نسازید. این دو ستونِ محکم دین (توحید و محافظت بر سنت پیامبر) را برپا دارید و این دو چراغ را روشن نگه دارید، تا در نتیجه توبیخ از شما دور باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2586,'هر کس پنهان و آشکارش و کردار و گفتارش، مخالف یکدیگر نباشد، قطعاً امانتِ انسان بودن را ادا کرده و در عبادت اخلاص ورزیده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2587,'حقوق مردم مستحق را ادا کن و اگر ادا نکنی، قطعاً روز قیامت از کسانی خواهی بود که بیش از همه دشمن دارند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2588,'بدا به حال کسی که فقرا و بینوایان و دریوزه‌کنان و رانده‌شوندگان از جامعه و وامداران و در راه‌ماندگان، دشمن او نزد خدا باشند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2589,'کسی که امانت را ناچیز شمارد و به آن خیانت کند و نفس و دین خود را از خیانت پاک نگرداند، قطعاً ذلّت و رسوایی را در دنیا به خود وارد ساخته و او در آخرت ذلیل‌تر و رسواتر خواهد بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2590,'به یقین، خیانت بر امت، بزرگ‌ترین خیانت است و فریبکاری‌ِ پیشوایان، زشت‌‌ترین فریبکاری‌هاست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2591,'از مشورت با زنان بپرهیز، زیرا رأی و نظر آنان سست و تصمیم‌شان بی‌اساس است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2592,'هیچ‌کس به خیر نمی‌رسد، جز کسی که عمل به خیر می‌کند و به هیچ‌کس پاداش شرّ داده نمی‌شود، جز به مرتکب شرّ.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2593,'در هنگام برخورداری از نعمت‌ها خودکامه مباش و در هنگام سختی‌ها، از سستی و احساس شکست پرهیز کن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2594,'این دنیا در برابر چشمم پست‌تر و موهون‌تر از دانه‌ی تلخ ناچیزی است که به کار دبّاغی برآید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2595,'ای بندگان خدا! از مرگ و نزدیکی آن برحذر باشید و توشه‌ی آن را مهیا سازید، زیرا مرگ حقیقتی با عظمت و حادثه‌ای بزرگ با خود خواهد آورد: یا خیر [و سعادتی] که هرگز شرّی پس از آن وجود نخواهد داشت و یا شرّ [و شقاوتی] که هرگز خیری پس از آن نخواهد بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2596,'برای اطاعت، نشانه‌های روشنی است و راه‌هایی واضح و جاده‌ی هموار و غایتی مورد جستجو. هشیاران به آن وارد می‌شوند و کودن‌های نگون‌بخت با آن مخالفت می‌نمایند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2597,'خویشتن‌داری در زمانِ حیرتِ گمراهی، بهتر از سوار شدن بر مَرکبِ حوادثِ هولناک است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2598,'بهترین سخن آن است که سودی بدهد. علمی که خیری در آن نیست، سودی ندارد و از علمی که شایسته‌ی تعلّم نیست، نفعی نتوان بُرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2599,'قلبِ انسانِ جوان، مانند زمین خالی از کشت است که هرچه در آن پاشیده یا کاشته شود، آن را می‌پذیرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2600,'مَثَل کسی که فریب این دنیا را بخورد، مانند قومی است که در جایگاهی دارای وسایل رفاه و آسایش زندگی می‌کردند، سپس مجبور شدند به جایگاهی بی‌خیر و فاقد رفاه و آسایش کوچ کنند. قطعی است برای چنین مردمی، چیزی ناگوارتر و مصیبت‌بارتر از جدایی از آن منزلِ پر آسایش و حرکت به سوی جایگاهِ فاقدِ رفاه و آسایش نمی‌باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2601,'آن‌چه را نمی‌دانی، مگو، اگرچه آن‌چه که می‌دانی، اندک باشد. و درباره‌ی کسی دیگر مگو آن‌چه را که نمی‌خواهی درباره‌ی تو گفته شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2602,'خودپسندی، ضد درستی و آفتِ عقل‌هاست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2603,'گاهی اجابت دعا برای بزرگ‌تر نمودنِ پاداش دعا کننده و فراوان بودن عطای آرزو کننده به تاخیر می‌افتد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2604,'بسا، باشد که چیزی را از خدا مسئلت نمایی، ولی آن خواسته به تو داده نمی‌شود، ولی در مقابل، بهتر از آن خواسته، یا در این دنیا و یا در آخرت نصیبت می‌شود. یا آن خواسته به خاطر آن‌چه برای تو بهتر است، از تو برمی‌گردد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2605,'بر فرصت‌ها پیشدستی کن، پیش از آن‌که فوتِ آن موجب اندوه شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2606,'هرگز دشمن دوستت را برای خودت دوست اتخاذ مکن که به خصومت با دوستت منجر شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2607,'همواره غضب را به تدریج بیاشام، زیرا من جرعه‌ای که شیرین عاقبت‌تر و دارای پایانی لذیذتر از آن ندیده‌ام.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2608,'دوست واقعی کسی است که هويّتِ دوستی را در نهان حفظ کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2609,'هر کس از حق تجاوز کرد، راه برای او تنگ می‌شود و هر کس به اندازه‌ی خود کفایت کند، برایش پایدارتر است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2610,'چه بسا آدمِ بینا در تشخیص مقصد به خطا رود و انسان نابینا به رشد و مقصد کمالِ خود نایل آید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2611,'هر کس به زمانه اطمینان کند، زمانه به او خیانت ورزد و هر کس آن را تعظیم کند، پست و خوارش نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2612,'پیش از حرکت در راه، از همراه و پیش از قرار گرفتن در خانه درباره‌ی همسایه تحقیق نما.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2613,'هر کس نسبت به تو خوش‌گمان باشد، گمانش را تصدیق کن و هرگز حق برادرت را به خاطر دوستی‌ای که بین تو و اوست، تباه مساز، زیرا برادر تو نیست کسی که حق او را ضایع کنی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2614,'ظلم هر کسی که به تو ستمی بورزد، در نظرت بزرگ جلوه نکند، زیرا چنین شخصی در ضرر بر خویشتن و سود رساندن به تو خواهد کوشید و پاداش کسی که تو را شاد کند، این نیست که با او زشتی نمایی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2615,'زنان را در حجاب نگهدار تا از نگریستن به مردان بازمانند، زیرا سخت گرفتن حجاب برای بقای عفّت آنان بهتر است و بیرون رفتن‌شان از خانه، بدتر نیست از این‌که بیگانه‌ای را که به او اطمینان نداری، نزد آنان به خانه آوری. و اگر بتوانی کاری کنی که هیچ کس جز تو را نشناسد، آن کار را انجام بده.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2616,'خداست بهترین داور میان بندگانش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2617,'ظلم و ارتکابِ خلافِ واقع، انسان را در دنیا و آخرتش هلاک می‌کند و نقص و اختلالِ وجوديِ او را نزد عیب‌جوی او پدیدار می‌نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2618,'دنیا آدمی را از همه چیز منصرف نموده، به خود مشغول می‌دارد و انسان‌ِ دنیادار چیزی از آن را به دست نمی‌آورد، جز این‌که طمع و اشتیاق به آن را برای خود (دنیا) بگشاید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2619,'دنیادار با آن‌چه از دنیا به دست آورده، از آن‌چه که به آن نرسیده است، بی‌نیاز نمی‌شود و پس از آن، نوبتِ از دست دادن آن چیزهایی که انباشته و شکستن آن‌چه محکم کرده است، می‌رسد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2620,'لازم است بر حاکم که اگر به امتیازی نایل شد و یا نعمتی به او اختصاص یافت، آن امتیاز یا نعمت موجب دگرگونی او درباره‌ی رعيّتش نشود و آن نعمت‌هایی که خداوند متعال قسمت او کرده، وسیله‌ی نزدیکی بیش‌تر به بندگانش و مهربانی به برادرانش شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2621,'نفْست را از اقدام به آن‌چه برای تو حلال نیست، به شدّت بازدار، زیرا انصاف و عدالت درباره‌ی نفس، جلوگیری جدی از اقدام نفس است در هر چه که بخواهد یا نخواهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2622,'هیچ چیزی بیش از حرکت بر مبنای ظلم موجبِ دگرگونی نعمت خداوندی و سرعتِ انتقام او نیست، زیرا خداوند شنونده‌ی دعای ستمدیدگان و در کمین ستمکاران است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2623,'آن‌چه از دنیا وجود دارد، پس از اندک زمانی، گویی وجود نداشته است و آن‌چه از آخرت وجود دارد، پس از اندک زمانی (پس از مرگ)، گویی ابدی است. هر قابل شمارشی، پایان‌پذیر و هر چه مورد انتظار است، آینده و هر آینده‌ای، نزدیک و در حال رسیدن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2624,'عالِم کسی است که اندازه و ارزش خود را بشناسد. برای نادانی مرد همین کافی است که ارزش خود را تشخیص ندهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2625,'قطعاً از مبغوض‌ترین مردان نزد خدا، آن بنده‌ای است که خدا او را به خودِ او واگذاشته است. این بنده (رها شده به حال خود) از راه حق متجاوز، و بدون رهبری که دلیل راه است، در حرکت است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2626,'ای مردم! خداوند شما را برکنار فرموده از آن‌که به شما ظلم کند، ولی شما را از آن‌که مبتلا و آزمایش کند، دور نساخته است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2627,'سوگند به خدا! من از جمله‌ی رانندگان (طرد کنندگان) جاهلیت از جامعه بوده‌ام، تا آن‌گاه که لشکر جاهلیت همگی پشت گرداندند، و زیر ذلت سلطه مجتمع شدند. من اکنون ناتوان نشده‌ام و نمی‌ترسم و خیانتی نکرده‌ام و سست نشده‌ام. و سوگند به خدا! قطعاً باطل را می‌شکافم، تا حق را از پهلوی آن درآورم.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2628,'این دنیا خانه‌ی فنا و زحمت است و جایگاه دگرگونی‌ها و عبرت‌گیری‌ها.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2629,'بهترین وسیله‌ای که توسّل‌کنندگان به خداوند- سبحانه و تعالی- انتخاب نموده‌اند، ایمان به او، و به فرستاده‌ی او، و جهاد در راه اوست. زیرا جهاد در راه خدا، مقام اعلای اسلام است، و کلمه‌ی اخلاص، مبنای فطرت اصلی انسان بر آن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2630,'برپا داشتن نماز است که اصل دین است و پرداخت زکات که همانا فریضه‌ای واجب است. و روزه‌ی ماه رمضان که سپری است از عذاب و حجّ بیت‌اللّه و عمره که همانا آن دو در آن‌که فقری را منتفی نموده و گناه را می‌شویند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2631,'وصله‌ی رحم که بر مال می‌افزاید، و اجل را به تأخیر می‌اندازد؛ و صدقه‌ی پنهانی که کفّاره‌ی گناه است؛ و صدقه‌ی آشکار که مرگ زشت و سخت را دفع می‌نماید؛ و انجام کارهای نیکو که از سقوط‌‌‌های پست جلوگیری می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2632,'قرآن را بیاموزید، زیرا آن بهترین گفتار است. در آن قرآن تدبّر و تفقّه نمایید، زیرا بهار دل‌هاست و از نور قرآن شفا بطلبید که قرآن شفای سینه‌هاست، و آن را نیکو تلاوت کنید، زیرا سودبخش‌ترین سرگذشت‌هاست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2633,'قطعی است عالِمی که به غیر علمش عمل می‌کند، مانند جاهل متحيّری است که از جهلش رها نشده و بیدار نمی‌شود. حجّت بر علیه او، از حجّت بر علیه جاهل بزرگ‌تر است، و حسرت و تأسف از او، جدایی‌ناپذیرتر و نزد خدا برای ملامت، شایسته‌تر.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2634,'من شما را از دنیا برحذر می‌دارم، زیرا این دنیا شیرین و سبز و خرّم و با شهوات پیچیده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2635,'این دنیا سرای ابدی است برای کسی که آن را متّهم نسازد و برحذر از آن نباشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2636,'شما را از دنیا برحذر می‌دارم، زیرا این دنیا شایسته‌ی استقرار برای سکوت نیست و خانه‌ای نیست که بتوان امیدِ عاملِ حیاتِ [همیشگی] به آن بست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2637,'هیچ انسانی از این دنیا شادمان نشد، جر این‌که اشکی به دنبالش فرا رسید. به احدی با سود و خیرات و شادی‌های خود روی نشان نداد، جز این‌که با ضررهایی که به او وارد کرد، به او پشت گرداند (و راه خود را پیش می‌گیرد). بارانی اندک از رفاه بر کسی نبارید، جز این‌که رگباری از ابر بلا و ناگواری‌ها بر او فرو ریخت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2638,'دنیایی بسیار فریبنده: هرچه در آن است، فریب و هرچه بر روی آن است، رو به فنا. در هیچ یک از زاد و توشه‌ی این دنیا خیری نیست، جز تقوا.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2639,'خیر این دنیا ناچیز است، و شرّ آن همیشه حاضر. جمع شده‌اش رو به فناست و مُلکش ربوده شدنی، و آبادش رو به ویرانی. پس چیست نفع آن خانه‌ای که از بنیادش شکسته می‌شود، و چه منفعتی است در عمری که در این دنیا مانند فنای زاد و توشه‌اش رو به زوال است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2640,'ای مردم! آن‌چه را که خداوند بر شما مقرر فرموده، مطلب و مطلوب خود تلقی نکنید، و از ادای حقّ خداوندی، آن‌چه را که از شما خواسته، مسئلت نمایید. و دعوت مرگ را پیش از آن‌که شما را [برای عبور از پل زندگی] بخوانند، به گوش‌های خود بشنوانید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2641,'دل‌های پارسایان در این دنیا می‌گرید، اگرچه بخندند، و اندوه‌شان سخت می‌شود، اگرچه شادمان باشند. خصومت آنان با نفس‌های امّاره‌شان فراوان است، هر چند به آن‌چه به آنان روزی شده باشد، مورد غبطه باشند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2642,'جز این نیست که شما بر مبنای دین خداوندی، برادران یکدیگرید. میان شما جدایی نینداخته، جز پلیديِ درون‌ها و بدی نيّت‌هایتان. به این خاطر است که به کمک هم نمی‌شتابید و خیرخواه یکدیگر نیستید و به همدیگر نمی‌بخشید و مهر نمی‌ورزید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2643,'ای بندگان خدا! شما را به تقوای الهی توصیه می‌کنم که تنها توشه‌ی معاد است و تنها وسیله‌ی پناهندگی به خدا. توشه‌ای است رساننده به مقصد و پناهی است واصل کننده به هدفِ مطلوب.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2644,'تا آن‌جا که بتوانم، از شعله‌ور ساختن آتش جنگ خودداری خواهم کرد. و آن‌گاه که چاره‌ای ندیدم، دوای آخرین، داغ کردن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2645,'ای بندگان خدا! به درستی که تقوای الهی، اولیاء خدا را از ارتکاب محرّماتش بازداشت، و خوف الهی را به دل‌های آنان ملازم نمود تا آنان را به بیداری در شب و به تشنگی در روزهای گرم وادار کرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2646,'از جریان (نشانه) دنیا در مسیر فنا شدن این‌که: تیر در کمان کرده است و تیرهایش به خطا نمی‌رود و جراحت‌هایش مرهم نمی‌پذیرد. زنده را با تیر مرگ می‌زند و تندرست را با بیماری و نجات یافته [از سختی را] با هلاکت و مشقت شَدید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2647,'از موارد عبرت‌گیری از این دنیا، این‌که: آدمی نزدیكِ وصل به آرزویش می‌شود، در آن حال، حضور مرگش آرزو را از او قطع می‌کند. پس نه آرزویی یافت می‌شود و نه آن‌چه رها شده، او را رها می‌سازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2648,'هر چیزی از دنیا، شنیدنش باعظمت‌تر از دیدن آن است و هر چیزی از آخرت، دیدنش باعظمت‌تر از شنیدن آن است. پس کفایت کند شما را از دیدن امور دنیوی شنیدنش، و از غیب، خبری که از آن داده می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2649,'آن‌چه که برایتان حلال شده، بیش‌تر از آن است که برای شما حرام گشته است. پس رها کنید اندک را برای وصول به بسیار و ترک کنید آن‌چه را که ننگ است برای به دست آوردن آن‌چه وسیع است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2650,'خداوند متعال روزی‌تان را تعهّد نموده و شما به عمل کردن مأمورید. پس نباشد طلب کردنِ چیزی که برای شما تضمین شده، شایسته‌تر از [کوشش] درباره‌ی عملی که برای شما مقرّر گشته است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2651,'ای مردم! هر کس راه روشن را پیش گیرد، به آب برسد و هر کس راهی مخالفِ راهِ راست درنوردد، در بیابان بی‌سر و ته گمراه شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2652,'ای مردم! جز این نیست که دنیا گذرگاهی است ناپایدار، و آخرت اقامت‌گاهی است برقرار. از گذرگاه خود بگیرید، برای اقامت‌گاه پایدار.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2653,'خدا رحمت کند مردی را که حقی را ببیند و به آن کمک نماید، یا ستمی را ببیند و آن را منتفی بسازد و یاور حق باشد تا آن را به صاحبش برساند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2654,'خودپسندی آدمی، یکی از حسدکنندگانِ عقل اوست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2655,'در صف‌‌آرایی و حمله بر دشمن، زره‌پوش را بر بی‌زره مقدّم بدارید، و دندان‌ها را بر هم بفشارید، زیرا در زمانِ وارد کردن نیزه‌ها بر بدن دشمن، این حالت بهتر می‌تواند شمشیرها را از فرق سر برگرداند و بپیچد، زیرا این حرکت و پیچیدن، زخم را کاری‌تر، و شکاف را عمیق‌تر می‌سازد، و پلک‌ها را روی هم گذارید و چشم را نیم‌بسته نگهدارید. زیرا این حالت، دل را محکم‌تر و آرام‌تر می‌سازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2656,'سوگند به خدا! اگر شما از شمشیر این دنیا فرار کنید، توانایی سالم ماندن از شمشیر آخرت را ندارید، و شما سروران عرب و بزرگ‌ترین برجستگان هستید. قطعی است که غضب خداوندی و ذلّت و پستی پایدار و ننگ و عار جاودان، در فرار از دشمن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2657,'آن کس که از جهاد بگریزد، بر طول عمر خود نمی‌افزاید، و بین او و روزی که مرگش فرا خواهد رسید، ممنوعيّتی ایجاد نخواهد شد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2658,'قرآن [که به صورت کتاب مدوّن پیش روی ماست]، خطّی است نوشته شده که از دو طرف در میان دو جلد قرار گرفته است. این خطوطِ نوشته شده زبانی ندارد که سخن بگوید، تا چاره‌ای جز این نیست که باید مفسّر و ترجمانی باشد که قرآن را تسیر کند و توضیح بدهد. همانا این مردم‌اند که به عنوان دركِ مقاصدِ قرآن، سخن می‌گویند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2659,'بافضیلت‌ترین مردم نزد خداوند کسی است که برای او عمل به حق- اگرچه موجب کاهش [ظاهری] و سختی باشد- محبوب‌تر از باطل بوده، هرچند آن باطل برای او فایده را جلب کند و موجب افزایش وی شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2660,'عطای مال در غیر موردش، افراط در خرج و اسراف است و این افراط‌ گری و اسراف، صاحبش را در دنیا بالا می‌برد و در آخرت ساقطش می‌کند، و در میان مردم عزیز می‌دارد و نزد خدا، پست و خوارش می‌سازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2661,'درباره‌ی من دو گروه از مردم به هلاکت می‌افتند: دوست افراط گر که محبت او را به سوی غیر حق (باطل) می‌کشاند، و دشمن تفریط گر که عداوت با من، او را هم به سوی غیر حق (باطل) می‌کشاند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2662,'به درستی، کسی که از مردم کناره گرفت و تنها شد، او از آنِ شیطان خواهد بود، چنان که گوسفندی که از گلّه کناره گرفت، نصیب گرگ می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2663,'هر کس مردم را به شعار خوارج دعوت کند، او را بکشید، اگرچه زیر این عمامه‌ی من باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2664,'نباید یک انسان بخیل بر نوامیس و نفوس و اموال و احکام و زمامداری مسلمین ولایت داشته باشد که حریص به اموال آنان باشد و نباید شئونِ حیاتی مسلمانان به ولایتِ جاهل واگذار شود، که آنان را با نادانی خود گمراه سازد. و نیز نباید والی مسلمانان جفاکار باشد که آنان را از روی جفا از حقوق خویش محروم کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2665,'کتاب خداوندی اینک در میان شماست. گوینده‌ای است که زبانش عاجز نمی‌شود و خانه‌ای است که ارکانش ویران نمی‌گردد و عزّتی است که یارانش شکست نمی‌خورند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2666,'جز این نیست که دنیا، دیدگاه نهایی انسانِ نابیناست: او بعد از این دنیا و بالاتر از آن را نمی‌بیند. ولی شخص بینا کسی است که نگاهش از این دنیا به اعماق آن نفوذ می‌کند و بعد از آن و بالاتر از آن را می‌بیند، و می‌داند قرارگاه ابدی در ورای آن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2667,'آگاه باشید! نیست میان حق و باطل، جز چهار انگشت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2668,'خداوند سبحان توبه را به سببِ فراوانی روزی و رحمت بر مخلوقاتِ خود قرار داده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2669,'هرگز به خدا شریک قرار ندهید و سنّت پیامبر اکرم (ص) را ضایع نکنید. این دو ستون را برپا دارید و این دو چراغ را روشن نگاهدارید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2670,'کسی به بهشت داخل نمی‌شود، جز این‌که پیشوایان الهی را بشناسد و آنان نیز او را بشناسند و کسی به دوزخ داخل نشود، جز این‌که آنان را منکر و آنان نیز او را منکر شوند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2671,'پس هر کسی که خدا به او مالی داد، به نزدیکان خود بپردازد و مهمان‌نوازی کند و اسیر را آزاد کند و زجر دیده را با آن مال نوازش دهد و از آن مال به مستمند بدهد و وامِ وام‌داران را بپردازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2672,'خداوند سبحان بندگان خود را در هنگام ارتکاب اعمال زشت مبتلا می‌سازد به کاهش میوه‌ها، و حبس برکات، و بستن در خزانه‌های خیرات، تا کسی که بخواهد، به طرف خدا برگردد و گنهکار دل از گناه بركَنَد. و کسی که می‌خواهد به یاد خدا بیفتد، و کسی که می‌خواهد، به خاطر نهی از لغزش‌ها، از آن‌ها امتناع بورزد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2673,'نباید ولایت مسلمین به کسی سپرده شود که بعضی را بر بعضی دیگر ترجیح بدهد و نباید زمامداری مسلمین را در اختیار کسی گذاشت که در حُکم رشوه بگیرد و حقوق مردم را ضایع کند و آن را چنان که باید، ادا نکند (از اجرای احکام خداوندی خودداری کند). هم‌چنین، والی مسلمین، نباید سنّت را متوقف کند و امت اسلامی را از این راه به هلاکت بیندازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2674,'جز این نیست که شما در این دنیا هدفِ تیرهای مرگ قرار گرفته‌اید. با هر جرعه‌ای که از این دنیا می‌نوشید، اندوهی است گلوگیر، و در هر لقمه‌ای که از آن می‌خورید، ناگواری‌هایی است شدید. به هیچ نعمتی از این دنیا دست نمی‌یابید، جز با از دست دادن نعمتی دیگر، و هیچ کسی از شما روزی عمر نمی‌کند، جز این‌که یک روز از مدتِ عمر او از بین می‌رود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2675,'هر انسانی از شما به مقدار توانایی‌اش تکلیف شده و باید برای ادای آن بکوشد. و به مردم نادان تخفیف داده شده است (خداوند متعال در مراعات قدرت برای تکلیف، بندگان خود را مورد ترحّم قرار داده است.)((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2676,'انسان عاقل با چشم دل، غایت و هدفِ نهایی خود را می‌بیند، و فراز و نشیب و پایین و بالای زندگی خود را می‌شناسد. دعوت کننده [در عرصه‌ی هستی] دعوت به سوی حق نموده، و مسئول تزکیه‌‌ی نفوس انسان‌ها، کار خود را انجام داد. پس اجابت کنید دعوت کننده را و از پیشوایان پیروی کنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2677,'کسی که با بینایی دلش می‌نگرد و با بصیرت عمل می‌کند، آغاز کارش این است که باید بداند آیا عمل او به ضرر اوست، یا به نفع او. اگر به سود اوست، حرکت خود را ادامه بدهد و اگر به زیان اوست، از حرکت در مسیری که به زیان او می‌انجامد، بازایستد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2678,'کسی که بدون علم حرکت می‌کند، مانند کسی است که بیراهه می‌رود. چنین شخصی هر اندازه که از راه واضح دور شود، از هدفی که به آن نیازمند است، دورتر می‌شود. و آن کس که به علم عمل کند، مانند کسی است که در راهِ آشکار و روشن حرکت می‌نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2679,'برای هر ظاهری، باطنی است بر مثال خود. آن‌چه که ظاهرش پاک است، باطنش نیز پاک می‌باشد و آن‌چه که ظاهرش پلید است، باطنش نیز پلید است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2680,'برای هر عملی گیاهی است، و هیچ گیاهی از آب بی‌نیاز نیست. و آب‌ها مختلف‌اند. آن آب که سیراب کردنش پاک باشد، كِشتش پاک و میوه‌ی آن شیرین خواهد بود و آن‌چه سیراب شدنِ آن پلید باشد، كِشتش پلید و میوه‌اش تلخ خواهد بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2681,'با ایمان است که می‌توان دلیل برای اعمال صالح ارائه داد. و با اعمال صالح است که به ایمان می‌توان دلیل آورد. و با ایمان است که علم آباد می‌شود. و با علم است که بیم از مرگ به وجود می‌آید. و با مرگ، دنیا به پایان می‌رسد. و با دنیاست که آخرت احراز می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2682,'خدا را در نظر بگیرید، بندگان خدا، خدا را، زیرا دنیا شما را از راهی می‌گذراند و شما و قیامت به یک ریسمان بسته‌اید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2683,'نماز، گناهان را از گردن گنهکاران، مانند برگ از درختان می‌ریزد و گناهان را از گردن‌ها باز می‌کند، همان‌گونه که مهارها از گردن حیوانات باز می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2684,'کسی که امین نباشد و امانت را برنگرداند، خسارت خواهد دید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2685,'قطعی است که امر به معروف و نهی از منکر، دو خُلق از اخلاق خداوندی است. و این دو صفت و فعالیت، نه مرگی را نزدیک می‌کنند و نه از روزی کسی می‌کاهند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2686,'تمسّک کنید به کتاب خداوندی، زیرا «این کتاب طنابی محکم، و نوری است آشکار» و شفایی است سودمند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2687,'«فراوانی ورود قرآن به گوش، آن را کهنه نمی‌کند.» هر کس به این کتاب الهی قائل شد، «راست گفت و راست گردید و هر کس به آن عمل کرد، بر دیگران سبقت جُست.»((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2688,'روزگار بر آیندگان چنان می‌گذرد که بر گذشتگان. آن‌چه از زمان پشت گرداند، برنمی‌گردد و آن‌چه در جویبار زمان است، ابدی و سرمدی نخواهد ماند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2689,'محبوب‌ترین بندگان نزد خدا کسی است که از پیامبرش تبعیت کند و دنبال کار او را بگیرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2690,'ای بندگان خدا! شما را به تقوای خداوندی و اطاعت او توصیه می‌کنم، زیرا تنها تقواست که نجات دهنده در فردا (قیامت) و رهایی‌بخش در ابديّت است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2691,'این دنیا، نزدیک‌ترین خانه به غضب خداوندی و دورترین خانه از رضای پروردگاری است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2692,'برترین بندگان خدا نزد خداوند سبحان، امامی است عادل که هدایت شده و مردم به وسیله‌ی او هدایت شوند. سنّت معلوم را برپا بدارد و بدعت مجهول را بمیراند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2693,'باید خردسال‌تان از بزرگسال‌تان پیروی کند و بزرگسال‌تان به خردسال‌تان محبت بورزد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2694,'واجبات را مراعات کنید، واجبات را. آن‌ها را برای خدا ادا کنید، تا شما را رهسپار بهشت نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2695,'خداوندا! اگر ما را بر دشمنان پیروز ساختی، ما را از ظلم و تعدّی بر کنار فرما و برای پیروی از حقّ موفّق فرما. و اگر آنان را بر ما غالب فرمودی، شهادت را نصیب ما فرما و از فتنه و آشوب ما را محفوظ بدار.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2696,'خاصیت حق این است که می‌توانی آن را بگیری و می‌توانی آن را رها کنی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2697,'من با دو نفر جنگ خواهم کرد: مردی که چیزی را ادعا کند که شایسته‌ی آن نیست و مردی که از ادای حقّی (امانتی) که به عهده دارد، امتناع بورزد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2698,'وصيّت به تقوا بهترین وصيّتی است که مردم درباره‌ی یکدیگر انجام می‌دهند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2699,'این را بدانید که این قرآن، آن نصیحت دهنده‌ی خیرخواه است که فریب نمی‌دهد و آن راهنماست که گمراه نمی‌کند و آن گوینده‌ی حدیث است که دروغ نمی‌گوید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2700,'برای دردهای خود از قرآن شفا بطلبید و در مشقت‌ها از آن یاری بخواهید، زیرا درمان بزرگ‌ترین دردها (کفر و نفاق و انحراف و گمراهی)، در آن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2701,'ظلمی که رهایی ندارد، ظلمی است که بندگان خدا به یکدیگر روا می‌دارند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2702,'تقوا بورزید برای خدا که در دیدگاه او هستید و پیشانی‌های شما به دست اوست و همه‌ی دگرگونی‌ها و تحرّکات شما در قبضه‌ی قدرتِ اوست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2703,'هر کس برای خدا تقوا بورزد، خداوند برای او راه نجات از فتنه‌ها و نوری رها سازنده از تاریکی‌ها را قرار می‌دهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2704,'به تقوای الهی چنگ بزنید، زیرا برای تقوا طنابی است که دستگیره‌ی آن محکم و پناهگاهی که بلندی آن، بالاتر از دسترس است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2705,'قرآن است شفاعت کننده‌ی پذیرفته شده و گوینده‌ی تصدیق شده. و هر کس که روز قیامت قرآن شفاعت او را کند، شفاعت قرآن برای او پذیرفته شود. و کسی که در روز رستاخیز، قرآن بدی او را گوید، گواهی قرآن بر علیه او مورد تصدیق قرار گیرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2706,'اهتمام بورزید به کار، برخیزید برای کار، سپس کار را ناتمام نگذارید و به پایان برسانید. و در تصمیمی که گرفتید، استقامت بورزید، استقامت. شکیبا باشید، شکیبا. پرهیزکاری پیشه کنید، پرهیزکاری.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2707,'سوگند به خدا! من بنده‌ای را نمی‌بینم که تقوا ورزیدن سودی به حال او داشته باشد، جز این‌که زیان خود را حفظ کند. قطعاً زبان شخص باایمان در پشت قلب او و قلب منافق پشت زبان اوست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2708,'وقتی مؤمن بخواهد سخنی بگوید، نخست درباره‌ی آن در نفس [و شخصیت] خود می‌اندیشد؛ اگر خیر و صلاح باشد، آن را آشکار می‌کند و اگر شرّ و فسادی در آن باشد، آن سخن را می‌پوشاند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2709,'کسی که خداوند به وسیله‌ی آزمایش و تجارب، نفعی به او نرساند، از هیچ اندرزی سود نمی‌برد و نتیجه‌ی تقصیر او از پیش رویش برآید، تا بشناسد آن‌چه را انکار کرده بود و انکار کند آن‌چه را که شناخته بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2710,'جز این نیست که مردم بر دو صفتند: پیرو قانون شرع، یا بدعت‌گزار و پیرو بدعت که با او هیچ برهان قانونی و روشنایی دلیل از خداوند وجود ندارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2711,'آگاه باشید! ظلم بر سه قسم است: ظلمی که بخشوده نشود، و ظلمی که رها نگردد، و ظلمی که قابل بخشش باشد و مورد تعقیب قرار نگیرد. اما ظلمی که بخشوده نشود، شرک ورزیدن به خداست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2712,'برای هر پیمان‌شکنی، فجور (گناهی) است و هر گناهی، موجب تیرگی قلب می‌گردد. «و برای هر عهدشکنی در روز قیامت پرچمی است که با آن شناخته می‌شود.»((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2713,'کسانی که تقوا ورزیدند، گروه گروه رهسپار بهشت شدند در امنیت از عذاب و انقطاع از ملامت. آنان از آتش برکنار شدند و در جایگاه امن و آرامش قرار یافتند و به آن منزلگه ابدی و قرارگاه جاودانی خشنود گشتند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2714,'ای بندگان خدا! مراعات کنید آن‌چه را که با رعایت آن به سعادت نایل خواهید شد و با ضایع کردن آن، تبهکارانِ شما در خسارت خواهند افتاد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2715,'تقوا حقّ خداوندی بر ذمّه‌ی شماست و بیان کننده‌ی حقّ شما بر خدا. برای توفیق یافتن به تقوا، از خدا یاری بجویید و برای ارتباط عبوديّت با خدا، از تقوا استمداد کنید، زیرا تقوا امروز نگه‌دارنده و سپری است در برابر آلودگی‌ها و فردا راهی است به سوی بهشت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2716,'مسیر تقوا روشن است و رونده‌ی آن راه، بَرنده‌ی سود و امانتدار آن، حافظ آن از نابودی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2717,'به ابر برق‌زننده‌ی دنیا ننگرید و در انتظار آن نباشید که برای شما بارانی ببارد، و گوش به کسی که برای دنیا سخن می‌گوید، فرا ندهید و کسی را که برای دنیاگرایی فریاد می‌زند، اجابت نکنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2718,'آگاه باشید! این دنیا مانند زنی است نابکار که خود را بر مردان عرضه کند [و سپس امتناع کند] و چارپایی است سرکش و ستیزه‌گر و دروغ‌گویی است خیانت پیشه. همواره در حال انکار و کفرورزی و معاندی است متمرّد، دارای تمایل زیاد و اضطرار و دگرگونی فراوان.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2719,'تواضع و فروتنی را بر سر نهید و تکبر و نخوت را زیر پا محو و نابود بسازید و خودپرستی و خودستایی را از گردن‌هایتان درآورید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2720,'اگر ناچار از تعصّب هستید، عصبيّت خود را برای اخلاق نیکو و کردارهای پسندیده و امور زیبا قرار بدهید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2721,'آنچه چشمت را بیازارد و نیز درد را، تحمل کن، در غیر این صورت هرگز از حوادث زندگی خرسند نخواهی بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2722,'تعصّب بورزید به خصلت‌ها و عادات پسندیده، مانند حمایت و حفظِ حقوق همسایگی و وفا به پیمان و اطاعت نیکوکار و مخالفت با خودخواهی و فراگرفتن فضل و فضیلت و بزرگ شمردن معصیتِ قتل نفس، و انصاف به خلق و فرو بردن غضب و پرهیز از فساد در روی زمین.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2723,'کسی که درخت وجودش نرم و باطراوت باشد، شاخه‌هایش زیاد شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2724,'اختلاف، رأی را ویران می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2725,'هر کس عطا کند، به موقعیتی بالا می‌رسد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2726,'در دگرگونی حالات است که گوهرهای اصلی مردم شناخته می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2727,'حسادت رفیق، از بیماريِ دوستی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2728,'بیش‌تر هلاکت و سقوطِ عقل‌ها زیر برق‌های طمع‌هاست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2729,'از عدالت نیست، حُکم دادن با اطمینان بر مبنای گمان.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2730,'دشمنی با بندگان، توشه‌ی بدی است برای معاد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2731,'از باارزش‌ترین اَعمالِ آدم با کرامت، چشم‌پوشی است از آنچه می‌داند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2732,'برترین اعمال، عملی است که نفس [امّاره‌ات] را با وجود اکراهش به انجام آن وادار کنی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2733,'تلخی دنیا، شیرینی آخرت است و شیرینی دنیا تلخی آخرت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2734,'تندخویی نوعی از جنون است، زیرا شخص تندخو از کار خود پشیمان می‌شود و اگر ندامتی حاصل نکرد، پس جنونِ او مستحکم است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2735,'تندرستی بدن از کمی حسادت است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2736,'هنگامی که فقیر شدید، به وسیله‌ی صدقه، با خدا تجارت کنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2737,'وفا به مردم غدّار (حیله‌گر و بی‌وفا)، بی‌وفایی و مکرپردازی نزد خداست، و حیله‌گری و بی‌وفایی با مردم غدّار، وفاست در نزد خدا.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2738,'خداوند هیچ‌کس را مانند کسی که با امتیازات و خواستنی‌های دنیا اشباع شود (همانند مهلت دادن)، مبتلا (آزمایش) نکرده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2739,'برای پدیده‌ی خصومت و عداوت، هلاکت‌هاست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2740,'هنگامی که زنان به مرحله‌ی نهایت ادراک رسیدند، خویشاوندان پدری مانند برادران و عموها، به امر زنان شایسته‌تر از مادرند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2741,'ایمان مانند نقطه‌ای [نورانی، سفیدی] در قلب آشکار می‌شود، سپس هر اندازه ایمان زیادتر شود، بر آن نقطه‌ی نورانی می‌افزاید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2742,'هم‌نشین سلطان، مانند سواره‌ی شیر است [که] موقعیت او مورد رشک و غبطه است، ولی خودِ او موقعیتِ خود را بهتر می‌شناسد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2743,'به اولاد دیگران نیکویی کنید تا احترام شما در فرزندان‌تان محفوظ بماند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2744,'در هنگام خوشی و رغبت، چنان عمل کنید که در زمان ترس و وحشت عمل می‌کنید. هشیار باشید! من ندیده‌ام مانند کسی را که جویای بهشت باشد و بخوابد و گریزان از آتش باشد و در خواب فرو رود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2745,'هشیار باشید! کسی را که حق سودش نبخشد، باطل ضررش خواهد زد و کسی را که هدایت راستش نسازد، گمراهی به هلاکتش خواهد کشید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2746,'وحشتناک‌ترین هراسی که درباره‌ی شما دارم، دو چیز است: پیروی از هوا و درازای آرزو.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2747,'انسان ذلیل و خوار، از ورود ستم بر خود جلوگیری نمی‌کند. حقّ بدون جدّیت قابل وصول نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2748,'چه سوداگری بدی است که دنیا را قیمت خود بینی و آن را عوض آن‌چه نزد خدا برای تو آماده است، تلقی کنی!((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2749,'کسانی که به پستی و خواری تن دادند، مغلوب شدند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2750,'سوگند به خدا! با قدرتی که به وسیله‌ی شمشیر بر پراندن تارک دشمن و قطع بازوها و پاهای او دارم، سلطه را به دست دشمن نخواهم داد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2751,'نافرمانی و سرپیچی از نصیحتِ ناصح مهربان و عالِم تجربه‌دیده، موجب حسرت می‌شود و پشیمانی به دنبال می‌آورد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2752,'شقی آن کسی است که از هوا و غرور خود فریب خورد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2753,'وجود حاکم و زمامدار برای مردم ضروری است، چه حاکم نیکوکار چه بدکار؛ که اگر زمامدار بدکار و منحرف باشد، شخص با ایمان در زمامداريِ او عملِ صالح خود را انجام می‌دهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2754,'در زمامداری شخص صالح، انسانِ باتقوا به اعمالِ صالحه‌ی خود مشغول می‌شود و از زمامداريِ انسانِ منحرف، شخص شقی بهره‌مند می‌گردد، تا مدت زمامداریش به سر آید و مرگش فرا رسد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2755,'ای مردم! وفا همراه صدق است و من سپری نگهدارنده‌تر از وفا (صدق) نمی‌بینم و کسی که بداند سرنوشت نهایی حیاتِ او چیست، مکر نمی‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2756,'ما در زمانی به سر می‌بریم که بیش‌تر مردمش مکر را هشیاری تلقی کرده‌اند و نادانان این مکرپردازی را به مهارت در چاره‌جویی نسبت می‌دهند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2757,'برای هر یک از دنیا و آخرت، فرزندانی (علاقه‌مندانی) است. شما از فرزندان آخرت باشید و از فرزندان دنیا مباشید، زیرا هر فرزندی در روز قیامت به پدرش ملحق خواهد شد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2758,'حقیقت این است که امروز دوران عمل است و حسابی در کار نیست (حساب محسوسی دیده نمی‌شود) و فردا زمان حساب است و عملی در کار نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2759,'دنیا جایگاهی است که فنا بر آن مقدّر و خروج از آن به اهلش اجباری شده است. این دنیا بر مذاق فرزندانش شیرین و بر خیره‌شوندگانش سبز و با طراوت است. این دنیا برای جوینده‌اش شتابان است، و بر دلِ نظاره‌کننده‌اش مخلوط کننده و فریبا.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2760,'از این دنیا بهترین زاد و توشه که اندوخته‌اید، کوچ کنید، و بیش از حدّ کافی از این دنیا مخواهید، و بیش از آن‌چه برای معاش زندگی لازم است، مجویید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2761,'خداوند باعظمت‌تر است از تخيّلات پوچ و بی‌اساسِ تشبیه‌کنندگانش و والاتر است از اوهام باطل منکرانش، عظمتی بس والا.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2762,'مرگ و نابودی شما در آن زندگی است که از دشمن شکست بخورید و ذلیل شوید. زندگی فناپذیرِ شما، در آن مرگی است که با پیروزی بر دشمن از این جهان رخت بربندید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2763,'مردم در این دنیا با فتنه‌ها آزمایش می‌شوند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2764,'آن بنده‌ای که تقوا به پروردگارش ورزید، از اندرز به خود بهره‌مند و خیرخواه خویشتن شد و توبه و بازگشت به سوی خدایش را پیش انداخت و به شهوتش پیروز گشت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2765,'فرار از جهاد، ننگ برای نسل‌های آینده‌ی شما و آتشی است در روز حساب. به رها کردن روح از کالبدتان رضایت دهید و دل‌خوش باشید و سبک‌بال به سوی مرگ حرکت نمایید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2766,'از این دنیا چیزی نمانده، جز ته‌مانده‌ی آبی در ظرف دستشویی، یا چیزی جز جرعه‌ی آبی اندک که اگر تشنه‌ای آن را بمکد، سیراب نشود. ای بندگان خدا! به کوچ از این دنیا تصمیم بگیرید که فنا سرنوشت همه‌ی مردم آن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2767,'از جمله تماميّت شرایط قربانی، بلندی و استقامت گوش آن و سالم بودن چشم آن است. پس اگر گوش و چشم قربانی سالم باشد، قربانی سالم و کامل است و اگر شاخ قربانی شکسته باشد و پای خود را به قربانگاه بکشد، کفایت می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2768,'آگاه باشید! دنیا جایگاهی است که نمی‌توان از عذاب و مجازات گناهان و فتنه‌ها و شرارت‌ها و شرورش سالم و در امان بود، جز در همین دنیا و از هیچ رویدادی که در طبیعتِ دنیاست، نمی‌توان نجات پیدا کرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2769,'این دنیا در نظر کسانی که دارای خِرد هستند، مانند سایه‌ی در حال برگشت است؛ در همان زمان که آن را در حال گسترش می‌بینی، رو به جمع شدن و زوال است و در همان هنگام که آن را رو به افزایش می‌بینی، رو به نقص و کاهش است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2770,'اجل آدمی از او پوشیده و آ رزویش فریبنده و شیطان آماده‌ی اغوای اوست، که معصیت را برای او می‌آراید تا مرتکبش گردد، و توبه را به حالت آرزو در درونش درمی‌آورد تا آ ن را به تاخیر بیندازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2771,'پروردگارا! بر من ببخشا آن‌چه را که درباره‌ی من از من داناتری. اگر بار دیگر به خطایی که کرده بودم، برگردم، بار دیگر با بخشش خود بر من عنایت بفرما. خداوندا! بر من ببخشا آن‌چه را که از نفْس خود وعده کردم و تو درباره‌ی آن وعده از من وفا ندیدی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2772,'ایمان زنان ناقص است، برخورداری زنان از سهم‌الارث ناقص و عقول آنان ناقص است. دلیل نقصان ایمانِ زنان، بازنشستِ آنان در روزهای قاعدگی از نماز و روزه است. دلیل نقصانِ عقولِ آنان، تساوی شهادتِ دو زن با یک مرد است. دلیلِ نقصانِ برخورداريِ آنان از سهم‌الارث این است که نصفِ سهم‌الارثِ مردان است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2773,'سپاسگزاری‌تان را در هنگام برخورداری از نعمت‌ها فراموش نکنید. خداوند با حجت‌های آشکار و روشن و کتاب‌های آسمانيِ واضح که موارد عذر را بیان نموده، جای پوزشی برای شما نگذاشته است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2774,'چه توصیفی کنم درباره‌ی دنیایی که آغازش مشقّت است و پایانش فنا؟ در حلالش حساب است، و در حرامش عِقاب. هر کس در این دنیا احساس بی‌نیازی کند، گمراه و مفتون گردد و اگر مبتلا به فقر شود، اندوهگین گردد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2775,'کسی که برای این دنیا به تلاش بیفتد، او را ترک گوید و کسی که درباره‌ی دنیا بی‌اعتنایی کند، دنیا به سراغ او آید و مطیعش شود. کسی که از این دنیا وسیله‌ی بینایی جوید، او را بینا سازد و کسی که در او بنگرد و خیره شود، نابینایش نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2776,'آبشخور این دنیا تیره، و چشمه‌سارش گل‌آلود و دارای منظری فریباست، و بی‌اعتنایی به آزمایش‌هایش مهلک. فریبنده‌ای است در حال دگرگونی‌ها ( مانع از دریافت حقیقت) و روشنایی است در معرض زوال سریع، و سایه‌ای است در حال برچیده شدن، و تکیه‌گاهی است مایل به سقوط.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2777,'به خدا تقوا بورزید، تقوای کسی که شنید و خشوع کرد، گناه اندوخت و اعتراف کرد، ترسید و عمل نمود، حذر کرد و به انجام اعمالِ نیکو مبادرت ورزید. به مقام یقین رسید و به نیکوکاری پرداخت، وسیله‌ی عبرت بر او عرضه شد، عبرت گرفت. برحذر داشته شد، حذر نمود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2778,'من برای تبلیغ رسالت‌های خداوندی و اتمام وعده‌ها و کلمات تعلیم شده‌ام. و نزد ما اهل بیت است، ابواب حکمت و روشنایی همه‌ی امور.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2779,'هزار ضربه‌ی شمشیر برای من آسان‌تر است از آن مرگ در رختخواب که در اطاعت خداوندی نباشم.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2780,'به کدامین مسیر منحرف می‌شوید و به کدامین سوی برمی‌گردید؟ یا به کدامین پشتیبان مغرور می‌شوید؟ جز این نیست که نصیب هر یک از شما از زمین، خوابگاهی به طول و عرض قامتِ شماست که گونه بر خاکش خواهید نهاد؟((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2781,'گویی (چنان زندگی نمایید که احساس کنید) چنگال‌های مرگ در زندگی شما فرو رفته و علایق امیدها و آرزوها از شما بریده شده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2782,'ای مردم! خدا را خدا را در نظر بگیرید (بترسید از خدا) درباره‌ی آن‌چه از شما در حفظ و عمل به قرآن خواسته و در مراعات عمل به حقوقی که نزد شما به ودیعت نهاده است، زیرا خداوند سبحان شما را بیهوده نیافریده و به حال خودتان وانگذاشته و شما را در جهل و کوری رها ننموده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2783,'خداوند عزیز، همه‌ی آثار اعمالِ شما را [از خیر و شر] معيّن فرموده و همه‌ی اعمال شما را دانسته، و اجل‌های شما را ثبت نموده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2784,'تملّق و چرب‌زبانی نکنید که شما را به ارتکاب معصیت می‌کشاند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2785,'خیرخواه‌ترین مردم درباره‌ی خویشتن، مطیع‌ترین مردم با پروردگارش، و خائن‌ترین مردم درباره‌ی خویشتن، معصیت‌کارترین آنان به خدایش می‌باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2786,'شایسته‌ی غبطه و سودبرنده کسی است که دین او برای او سالم باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2787,'سعادت از آنِ کسی است که از دیگران برای خود وعظ و عبرت بگیرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2788,'بدانید که اندک ریا، شرک است و هم‌نشینی با هواپرستان، جایگاهِ فراموشی ایمان و حضور شیطان است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2789,'از دروغ اجتناب کنید، زیرا دروغ، بیگانه و دور از ایمان است. انسان راستگو بر کناره‌ی نجات و کرامت قرار گرفته، و دروغگو در پرتگاهی از سقوط و پستی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2790,'هرگز حسادت نورزید، زیرا «حسد ایمان را می‌خورَد، چنان که آتش هیزم را.»((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2791,'«با یکدیگر عداوت نورزید، زیرا عداوت تراشنده [و محو کننده‌ی] ایمان است.»((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2792,'بدانید آرزو عقل را پوشانده و ذکر حداوندی را به فراموشی می‌سپارد. آرزو را تکذیب کنید، زیرا آرزو فریبنده و صاحبش فریب خورده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2793,'ای بندگان خدا! از محبوب‌ترین بندگان خدا در پیشگاه ربوبی، بنده‌ای است که خداوند [سبحان] او را در شناختِ نفْس خود و ساختن آن یاری فرماید. [این انسانِ مورد عنایتِ خداوندی]، لباسی از اندوه بر تن نمود و پوشاکی ار بیم بر خود پوشید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2794,'خداوند سبحان هرگز جبّاران دورانی را شکست نداده، مگر پس از مهلت و آسایشی که در آن غوطه‌ور بوده‌اند و استخوانِ شکسته‌ی هیج یک از امّت‌ها را جبران نفرموده است، مگر پس از قرار دادن آنان در تنگنای زندگی و آزمایش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2795,'در مشقّت و سختی‌هایی که رو به سوی آن‌ها می‌روید و در آن حوادث بزرگی که پشت سر گذاشته‌اید، عواملی برای تجربه و عبرت وجود دارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2796,'هر صاحب قلبی از خِرَد برخوردار نیست، [چنان که] هر دارنده‌ی گوشی شنوا نبوده و هر ناظری بینا نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2797,'هر کسی که به خود اهمیتی ندهد (به خود یاری نکند) که از نفْس خود واعظ و مانعی [از آلودگی‌ها] داشته باشد، برای او غیر از نفْسش، نه مانعی خواهد بود و نه پند دهنده‌ای.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2798,'نه دهر و زمان بر خدا جریان داشته است که آن جریان حالات مختلفی برای او ایجاد کند و نه در مکانی جایگزین بود که انتقال برای او امکان‌پذیر باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2799,'آگاه باشید! مَثَل اهل بیت محمد (ص) مَثَل ستارگان است. هر گاه ستاره‌ای از آنان غروب کند، ستاره‌ای دیگر طلوع نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2800,'به همان حدّ و اندازه‌ای که خداوند تحصیل علمِ آن را برای تو تجویز فرموده، کفایت بوَرز و عظمت خداوند سبحان را با موازین عقليِ خود اندازه‌گیری مکن، که این خطا تو را از گروه‌ِ هلاک‌شدگان می‌نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2801,'اوست آن خداوند توانا، هنگامی که اوهام بشری قصد پیشرفت به سوی درک و شناختِ نهایتِ قدرتِ او نمایند و اندیشه‌ی مبرّا از خطرهای وسوسه‌ها را که بخواهد در اعماق غیوبِ ملکوتِ الهی با آن به ذات اقدس ارتباط پیدا کند، مردود می‌نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2802,'اگر هم خداوند به ستمکار مهلت بدهد، گرفتن و کیفر خداوندی از او فوت نمی‌شود و خداوند در گذرگاه ستمکار در کمین و در موضع ماده‌ای گلوگیر در مسیر فرو بردن آب دهان است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2803,'همه‌ی اقوام و اُمَم از ظلم امرای خود می‌ترسند و من از ظلم رعيّتِ خود بیمناکم! شما را به جهاد بسیج کردم، به راه نیفتادید و سخنان [سازنده] به گوش‌تان خواندم، نشنیدید و شما را نهانی و آشکار دعوت کردم، پاسخ مثبت ندادید و شما را نصیحت نمودم، نپذیرفتید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2804,'ای اهل کوفه! من از شما به سه خصلت و دو خصلت مبتلا شده‌ام: ناشنوایی گوش‌دار! لال‌هایی سخنگو! نابینایانی چشمدار! [دو خصلتِ نکوهیده‌ی دیگر که در شماست و مرا با آن دو مبتلا کرده‌اید:] نه آزادمردانِ راستین هستید در هنگام رویارویی با دشمنان و نه برادران قابل اطمینان در زمان آزمایش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2805,'برای به دست آوردن عزت و فخر دنیا به رقابت ناشایست نپردازید و به زینت و نعمت‌های دنیا دل خوش نکنید، و از دشواری‌ها و سختی‌هایش شیون به راه نیندازید، زیرا عزّت و فخر دنیا رو به انقطاع و زینت و نعمت‌هایش رو به زوال، و دشواری‌ها و سختی‌های آن رو به فنا و نابودی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2806,'شادی دنیا با اندوه آمیخته و قدرت مردان در آن، رو به ناتوانی و سستی است. پس فراوانی آن‌چه در این دنیا برای شما خوشایند است، فریب‌تان ندهد، زیرا آن‌چه که از آن‌ها همراه شما خواهد بود، اندک است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2807,'خدا رحمت کند مردی را که اندیشید و عبرت اندوخت، و از عبرتِ اندوخته، بینایی یافت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2808,'مسلمان کسی است که مسلمانان از زبان و دستش سالم باشند، جز در مسیر حق. و اذیت و آزار مسلم صحیح نیست، جز برای وجوب.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2809,'به درستی که چیزی بدتر از شرّ نیست، جز کیفر آن و چیزی بهتر از خیر نیست، جز پاداش آن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2810,'امید به چیزی شایسته است که می‌آید و یأس از چیزی درست است که گذشته است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2811,'آگاه باشید که برای هر خونی، خونخواهی و برای هر حقّی، طلب کننده‌ای است. خونخواهِ خون‌های ما، مانند حکم کننده درباره‌ی خویشتن است و آن خونخواه، خداوندی است که هیچ مطلوبی او را ناتوان نسازد و هیچ فرار کننده‌ای نمی‌تواند از او فوت شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2812,'ای مردم! از شعله‌ی چراغ واعظی که خود وعظش را پذیرفته، فروغ بگیرید و از آبِ زلالِ چشمه‌ای بکشید که از تیرگی تصفیه شده باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2813,'ای بندگان خدا! به جهالت خویشتن تکیه ننمایید، و مطیع هواهای خود نباشید، زیرا کسی که به چنین موقعیتی سقوط کند، به لبه‌ی رودخانه‌ای افتاده که سیل آن را برده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2814,'خدا را، خدا را در نظر بگیرید از این‌که شکایت پیش کسی ببرید که نتواند اندوه را از شما زایل کند و نتواند با رأی خود چیزی را بشکند که برای شما محکم شده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2815,'پیشدستی کنید برای علم، پیش از آن‌که روییدنی آن بخشکد و پیش از آن‌که از استفاده‌ی علمی از دارندگان علم منصرف و به غوطه خوردن در حوادث و فتنه‌ها مشغول شوید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2816,'مردم را از کار زشت نهی کنید و خود نیز از آن دور شوید، زیرا جز این نیست که شما مأمور شده‌اید به نهی کردن مردم، پس از آن‌که خود از ارتکابِ امور زشت اجتناب کنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2817,'رازهای مخفی برای بینایان آشکار و جادّه‌ی حقّ برای کسی که از آن جادّه منحرف شده است واضح، و قیامت پرده از روی خود برداشته و علامت آن روز بزرگ برای کسی که دارای فراستِ نافذ باشد، ظاهر گشته است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2818,'برای هر مدّتی (در هر حادثه و نوع زندگی) کتابی است، و برای هر غیابی، بازگشتی. بشنوید حکمت را از شخصيّت ربّانی که در میان شماست و دل‌هایتان را برای پذیرش از او حاضر کنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2819,'ماییم درختِ [پُر بار] نبوّت و محل فرود رسالت و جایگاه تردّد فرشتگان و معادن علم و منابع جوشانِ حُکم، یار و دوستدار ما، در انتظار رحمتِ خداست، و دشمن ما و کسی که با ما کینه‌توزی دارد، در انتظار غضب الهی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2820,'پیشدستی کنید برای آمادگی به استقبال امری که فراگیر همه و در بر گیرنده‌ی هر یک از شما با کیفیت مخصوص به شماست؛ این امر همان مرگ است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2821,'کامل‌ترین فقیه کسی است که مردم را از رحمت خداوندی ناامید نسازد و از محبت او مأیوس‌شان ننماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2822,'پایین‌ترین علم همان است که منحصر در زبان است و بالاترین علم آن است که در اعضاء و ارکانِ وجودِ آدمی آشکار می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2823,'خیر، فراوانی مال و فرزند نیست، بلکه خیر آن است که دانشت فراوان باشد و بردباريَت با عظمت و عبادتی که به پروردگارت می‌کنی، مردم آن را عامل سرافرازی تلقی کنند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2824,'خیری در این دنیا نیست، جز برای دو نفر: شخصی که از گناهانی که مرتکب شده، توبه کند و آن‌ها را جبران نماید و شخصی که برای وصول به خیرات بشتابد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2825,'قطعاً شایسته‌ترین مردم به پیامبران، داناترین آنان به چیزی است که پیامبران آورده‌اند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2826,'خواب رفتن در حالت یقین، بهتر از نماز در حالت شک و تردید است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2827,'راویان علم فراوانند و دقت‌کنندگان و مراعات‌کنندگان آن اندکند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2828,'مردم برای اصلاح دنیای خود، چیزی از امر دین‌شان را رها نمی‌کنند، جز اینکه خداوند برای آنان آنچه را که ضررش بیشتر است، پیش آورد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2829,'بسا عالِمی که جهلش او را کشت و با اینکه علمش با او بود، ولی سودی به حال او نکرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2830,'جلب محبت، نصفِ خِرد است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2831,'غصّه، نصف پیری است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2832,'به اندازه‌ی مصیبت، صبر نازل می‌شود و هر کس در هنگام مصیبت دست خود را به روی ران‌هایش بزند، ثواب عملش پوچ گردد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2833,'ایمان‌تان را با صدقه نگهدارید و اموال‌تان را با زکات تضمین نمایید و امواج بلا را به وسیله‌ی دعا از خود دور کنید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2834,'هیچ‌کس نمی‌تواند قانون خداوندی را برپای دارد، جز کسی که تصنّع نکند و خود را به مردم خارج از جاده‌ی حق تشبیه نکند و دنبال انگیزه‌ی طمع و تمایلات نرود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2835,'هیچ مالی سودمندتر از عقل نیست و هیچ تنهایی وحشتناک‌تر از خودپسندی و هیچ تعقّلی مانند تدبیر و هیچ كَرَمی مانند تقوا و هیچ هم‌نشینی مانند اخلاق نیکو نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2836,'هیچ میراثی مانند فرهنگ سازنده و هیچ عامل حرکتی مانند توفیق و هیچ تجارتی مانند عمل صالح و هیچ سودی مانند پاداش نیکو و هیچ پرهیزکاری مانند توقف در زمان اشتباه وجود ندارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2837,'هیچ زهد و پرهیزکاری مانند زهد و پرهیزکاری به هنگام قرار گرفتن در مقابل حرام نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2838,'هیچ علمی مانند اندیشه، و عبادتی مانند ادای واجبات و هیچ ایمانی مانند شرم و حیا و هیچ شکیبایی و حیثيّتی مانند تواضع و فروتنی و هیچ شرفی مانند علم و هیچ عزتی مانند متانت و بردباری، و هیچ یاری و یاوری محکم‌تر از مشورت نمی‌باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2839,'در آن هنگام که صلاح و نیکوکاری بر زمان و مردمش غالب باشد، سپس مردی به مرد دیگر بدگمان شود، با اینکه گناهی مرتکب نشده، در این صورت ظلم کرده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2840,'مَثَل دنیا، مانند مَثَل ماری است که دارای پوست و گوشتی نرم است، در حالی که زهر کشنده‌ای درون آن است، شخص نادان به آن میل کرده و خردمند عاقل از آن اجتناب نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2841,'تفاوت زیادی است بین دو عمل: عملی که لذتش می‌گذرد و نتیجه‌ی زشتش می‌ماند، و عملی که زحمتش می‌رود و پاداش نیکویش می‌ماند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2842,'غیرتِ زن کفر است و غیرت مرد، ایمان.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2843,'هر کسی در عمل کوتاهی نماید، به اندوه مبتلا می‌شود. خداوند ارتباط ندارد با کسی که خدا را در مال و جانِ او سهمی نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2844,'در اوایل سرما، از خود حفاظت کنید و در آخرش آن را پذیرا باشید، زیرا سرما در بدن‌های مردم همان کار را می‌کند که در درخت‌ها: آغازش می‌سوزاند و آخرش شکوفا می‌سازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2845,'عظمت خالق در نزد تو، مخلوقات را در چشمت کوچک می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2846,'مردم دشمن چیزی هستند که آن را نمی‌دانند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2847,'دنیا جایگاهی است راستین برای کسی که با واقعیت آن درست رویاروی شد و خانه‌ی عافیت و گوارایی است برای کسی که آن را شناخت (معرفت از آن حاصل نمود). این دنیا خانه‌ی بی‌نیازی‌ست برای کسی که از آن توشه برداشت و جایگاه پندگیری‌ست برای کسی که از آن نصیحت پذیرفت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2848,'دنیا گذرگاه است، نه خانه‌ی پایدار ماندن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2849,'مردم در این دنیا بر دو گروهند: مردی که نفس خود را فروخت و خود را به هلاکت انداخت و کردی که نفس خود را خرید و آن را آزاد کرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2850,'نماز وسیله‌ی نزدیکی هر انسانِ باتقوا به خداست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2851,'حج، جهاد هر ناتوانی است و برای هر چیزی زکاتی است و زکات بدن روزه می‌باشد و جهادِ زن، شوهرداريِ نیکوست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2852,'روزی را، به وسیله‌ی صدقه برای خود فرود آورید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2853,'هر کس به نتیجه‌گیری و پاداش یقین داشته باشد، برای بخشش دست می‌گشاید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2854,'یاری و کمک خداوندی، به اندازه‌ی نیاز نازل می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2855,'کميِ عائله (نان‌خور) یکی از دو توانگری است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2856,'دوستی حقیقی هنگامی است که دوست برادرِ خود را در سه چیز مراعات کند: در هنگام سختی، در غیابش و در وفاتش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2857,'بسا روزه‌داری که از روزه گرفتنش جز تشنگی و گرسنگی سودی نبرد و بسا قیام کننده در شب که از قیامش جز شب بیداری و مشقت بهره‌ای نبرد. چه خوب است خوابِ هشیاران و افطارشان.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2858,'علم بهتر از مال است، [زیرا] علم تو را حفظ کند و تو مال را حراست کنی. مال را خرج کردن رو به تمامی می‌برد و علم با انفاق و تعمیمش افزوده می‌شود. آنچه از مال ساخته است، با رفتن مال از بین می‌رود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2859,'علم حاکم است و مال محکوم.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2860,'هلاک شد کسی که ارزش خود را نشناخت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2861,'برای هر انسانی عاقبتی است، یا شیرین و یا تلخ.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2862,'هیچ آدم شکیبایی پیروزی را از دست نمی‌دهد، اگرچه زمان به درازا بکشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2863,'هر کس به کردار قومی خرسند باشد، مانند کسی است که در آن کردار با آنان همراه است و برای هر کس که در یک باطل وارد شده، دو گناه است: گناه عمل به آن باطل و گناه رضایت به آن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2864,'بر شما باد اطاعت کسی که در جهل به او معذور نمی‌باشید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2865,'برای شما عوامل و وسایل بینایی آماده شده، اگر ببینید و جاده‌ی هدایت برای شما هموار شده، اگر هدایت شوید و حقایق را به شما شنوانده‌اند، اگر بشنوید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2866,'هر کس نفس خویشتن را در موارد تهمت قرار بدهد، کسی را که به او بدگمان باشد، ملامت نکند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2867,'هر کس خودرأیی را انتخاب کرد، هلاک گشت و هر کس با مردم مشورت کرد، با عقولِ آنان شریک شد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2868,'هر کس راز خود را مخفی داشت، اختیار آن را به دست خود دارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2869,'فقر و تنگدستی، بزرگ‌ترین مرگ است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2870,'هر کس حق کسی را ادا کند که حق او را ادا نمی‌کند، او را پرستیده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2871,'هیچ مخلوقی را با معصیتِ خالق نمی‌توان اطاعت کرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2872,'هیچ مردی به تأخیر در دریافتِ حقّش ملامت نمی‌شود، بلکه از این ملامت می‌شود که آنچه را که حق او نیست، بگیرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2873,'خودپسندی، مانع افزایش امتیازات است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2874,'برای کسانی که چشمِ بینا دارند، بامداد روشن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2875,'اجتناب از گناه، آسان‌تر از زحمت جبران و توبه‌ی آن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2876,'هر کس سر نیزه را برای خدا (در راه خدا) تیز کند، بر قتل شدیدترین حمایت‌گرانِ باطل پیروز شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2877,'اگر از چیزی ترسیدی، در همان چیز وارد شو، زیرا شدتِ ناراحتی پرهیز و خویشتن‌داری از آن، بزرگ‌تر از آن است که می‌ترسی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2878,'وسیله‌ی ریاست و سروری، شکیبایی و فراخی سینه است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2879,'با پاداش نیکو دادن به نیکوکار، بدکار را بیازار.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2880,'شرّ و بدی را از سینه‌ی دیگران برکن، با درآوردن آن از درون خویشتن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2881,'لجاجت و مقاومتِ بی‌دلیل، تدبیر و اندیشه را از بین می‌برد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2882,'طمع‌کاری، بردگی جاودانی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2883,'پشیمانی، میوه‌ی تفریط است، و سلامت محصول احتیاط.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2884,'هیچ خیری در سکوت از حکمِ به‌جا نیست، چنان که هیچ خیری در سخن بر مبنای نادانی نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2885,'هیچ دو دعوایی با یکدیگر مخالف نمی‌شوند، جز اینکه یکی از آن دو گمراهی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2886,'برای آن ظالم که ابتدا به ظلم کرده، فردا [از پشیمانی] پشتِ دست به دندان گزیدن در پی دارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2887,'هر کس با حق به ستیزه برخیزد، هلاک شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2888,'هر کس صبر و بردباری نجاتش ندهد، اضطراب و داد و فریاد او را بکشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2889,'در این دنیا هیچ بنده‌ای نعمتی را به دست نمی‌آورد، جز اینکه از نعمتی دیگر جدا شود، و به روزی از عمر خود روی نمی‌آورد، جز اینکه با نزدیکی به اجل، روزی دیگر را از دست دهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2890,'ای فرزند آدم! آنچه را فوق معاش لازم اندوختی، تو در آن خزانه‌دار دیگری هستی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2891,'برای دل‌های فرزندان آدم، تمایل و روی‌آوردنی است و روی‌گرداندنی. پس دل‌ها را به هنگام تمایل و روی آوردنِ آن‌ها به کار گیرید، زیرا هنگامی که دل به اکراه وادار به کاری شد، نابینا می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2892,'هر مالی که به تو پندی داده باشد، از دستت نرفته است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2893,'هر ظرفی از محتوایی که در آن قرار داده می‌شود، پر می‌گردد، جز ظرف علم که هرچه محتوا در آن افزوده شود، بر ظرفیتش بیفزاید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2894,'نخستین پاداشی که آدم بردبار از بردباريِ خود می‌برد، این است: مردم یاران او هستند، به ضرر نادان.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2895,'اگر آدم بردباری نیستی، خود را به شکیبایی و بردباری وادار کن، زیرا اندک است تعداد مردمی که خود را به قومی تشبیه کنند و از افراد آن قوم نشوند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2896,'هر کس به محاسبه‌ی خویشتن پردازد، سود بَرَد و هر کس از خویشتن غفلت بورزد، زیانکار شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2897,'هر کس ترسد (احتیاط کند)، در امان باشد و هر کس عبرت بگیرد، بینا گردد و هر شخصی که بینا شود، به مقام فهم رسد و کسی که بفهمد، به علم نایل گردد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2898,'بخشش و احسان، نگهبان حیثيّت است و بردباری دهن‌بندِ احمق، و عفو زکات پیروزی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2899,'باارزش‌ترین توانگری، ترک آرزوهاست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2900,'دوستی، خویشاوندی اکتسابی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2901,'هر کس حیا به او لباس بپوشاند، مردم عیب او را نبینند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2902,'با کرامت و بخشش است که ارزش‌ها عظمت پیدا می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2903,'آدم طمع‌کار همواره در گرو ذلّت و پستی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2904,'ایمان، معرفت قلبی است و اقرار به زبان و عمل به اعضا و جوارح.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2905,'هر کس نزدیک توانگر وارد شود و به خاطر توانگريِ او فروتنی کند، دو سوم دین خود را از دست داده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2906,'هر کس دلش به محبت دنیا شیفته شد، دل او به سه چیز از دنیا می‌چسبد: اندوهی که از او ناپدید نشود و طمعی که رهایش نسازد و آرزویی که به آن نخواهد رسید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2907,'قناعت، برای به دست آوردن مُلک کافی است و نیز برای وصول به نعمت‌ها، اخلاق نیکو کفایت می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2908,'با کسی که روزی به او روی آورده، شرکت (همکاری) کنید، زیرا او شایسته‌تر به توانگری و سزاوارتر به روی آوردن بهره و منفعت است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2909,'هر کس با دستی کوتاه (ناتوان) عطا کند، با دستی بلند (توانا) به او عطا شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2910,'هرگز کسی را به مبارزه دعوت نکن و اگر به مبارزه دعوت شدی، بپذیر، زیرا کسی که دعوت به مبارزه کند، ظالم است و ظالم همواره شکست خورده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2911,'بهترین خصلت‌های زنان، بدترین خصلت‌های مردان است: کبر، ترس و بخل. پس اگر زن متکبّر باشد، در برابر هیچ‌کس جز همسرش سر فرود نیاورد و اگر بخیل باشد، مال خود و مال شوهرش را حفظ می‌کند و اگر ترسو باشد، از هر چیزی که حالت تعرّض به او داشته باشد، وحشت می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2912,'عاقل کسی است که هر چیز را در محل خود قرار می‌دهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2913,'زن [با توجه به طبیعت شهوانیش]، شرّ است و بدترین چیزی که در اوست، این است که چاره‌ای از او نیست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2914,'هر کسی سستی را اطاعت کند، حقوق را ضایع سازد و هر کس سخن‌چین را اطاعت نماید، دوست را تباه کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2915,'سنگِ غضب در بنای خانه، ضامن ویرانی آن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2916,'روز محاسبه‌ی مظلوم بر ظالم، سخت‌تر است از روز ستم ظالم بر مظلوم.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2917,'به خدا تقوا بورز، اگرچه اندک باشد و بین خود و خدا پرده‌ای قرار بده، اگرچه نازک باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2918,'برای خدا در هر نعمتی حقّی است. پس هر کس آن حق را ادا کرد، به آن نعمت اضافه شد و اگر در ادای آن حق کوتاهی ورزید، به خطر زوالِ نعمت دچار شد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2919,'کرامتِ نفس؛ مهرانگیزتر از خویشاوندی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2920,'هر کس درباره‌ی تو گمان خوش داشت، گمان او را تصدیق کن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2921,'کسی که دستِ کرامتِ خود را از دودمانش برمی‌دارد، یک دستِ او از آنان برداشته می‌‍‌شود و در مقابل، دست‌های فراوانی از او برداشته خواهد شد. هر که خوی نرم دارد، از محبت همیشگی دودمانش برخوردار خواهد بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2922,'بار خدایا! من این مردم را دچار ملالت کرده‌ام، آنان نیز مرا گرفتار ملالت نموده‌اند. من اینان را افسرده ساخته‌ام، آنان هم مرا افسرده و ناراحت کرده‌اند. بهتر از آنان را به جای آنان بر من عنایت فرما، و بدتر از من را به جای من نصیب آنان بنما.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2923,'شعله‌های جنگ زبانه می‌کشد و روشنایی شعله‌های جنگ سر بر کشیده است. صبر و بردباری برای استقبالِ رویدادهای کارزار را دریابید، زیرا شکیبایی و تحمل، بهترین انگیزه برای پیروزی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2924,'جهاد [با دشمنان خدا] دری از درهای بهشت است که [خداوند این در را] به روی خواصّ اولیاء خود باز کرده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2925,'سوگند به خدا! هیچ قومی در خانه‌ی خود مورد حمله و هجوم قرار نگرفت، مگر این‌که ذلیل شد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2926,'کسی که اطاعت نمی‌شود، رأیی ندارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2927,'هشیار باشید! شما در این زندگی، روزگار آرزو را می‌گذرانید که اجل را به دنبال دارد. کسی که در روزگار آرزو و پیش از آن‌که اجلش فرا رسد، عمل نماید، عمل برای او سودمند خواهد بود و مرگی که به سراغش آمده است، ضرری برای او وارد نخواهد ساخت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2928,'از زنان پلید بترسید و از خوبان آنان برحذر باشید. در کارهای نیکو از زنان اطاعت مکنید، تا در کارهای زشت طمعی نورزند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2929,'زهد، کوتاه کردن آرزوست، و شکر هنگام برخورداری از نعمت‌ها، و پرهیزکاريِ شدید در زمان رویارویی با محرّمات.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2930,'ای بندگان خدا، از گناهان مهلک و عیوبی که خدا را به سخط و غضب می‌آورد، بپرهیزید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2931,'مردانِ کارآزموده‌ی اجتماعی و سیاسی با حالات تصنّعی می‌توانند خود را بر فراستِ زمامداران عرضه کنند، و خوش خدمتی خود را به رخ آنان بکشند، در صورتی که پشت این نقاب ساختگی، نه خیرخواهی و خیراندیشی دارند و نه امانت‌داری برای انجام وظایف خود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2932,'تو به بهانه‌ی اشتغال به کارهای بزرگ از ضایع کردن حق کوچک معذور نیستی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2933,'عمل هر روز را در همان روز به جای بیاور، زیرا برای هر روز موقعیت و قانون خاصی حکم‌فرماست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2934,'هیچ چیزی به انتقام خداوندی نزدیک‌تر و از نظر نتایج وخیم‌تر و برای نابود کردن نعمت خداوندی مؤثرتر و برای منقرض ساختن روزگار دولت، قاطع‌تر از خونریزی به ناحق وجود ندارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2935,'خداوند سبحان [در روز قیامت] حکم میان بندگان خود را از حکم درباره‌ی خون‌هایی که ریخته‌اند، آغاز خواهد کرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2936,'بپرهیز از خودپسندی و بالیدن به خود، و از اطمینان به آن‌چه که از نفس خود، تو را به خودپسندی وادار کرده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2937,'اگر نفس خود را از مقداری فراوان از چیزهایی که دوست می‌داری، به خاطر ترس از یک ناگواری، بازنداری، هوی و هوس‌های نفسانی تو را به مقداری فراوان از ضررها می‌کشاند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2938,'دنیا جایگاه عمل در آزمایش‌هاست. کسی که در این دنیا زندگی می‌کند، هرگز ساعتی در فراغت به سر نمی‌برد، جز این‌که همان فراغتش روز قیامت برای او حسرتی خواهد بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2939,'برترین چیزی که از این دنیا به آن نایل می‌شوی، خاموش کردن باطلی یا زنده کردن حقی باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2940,'شادی تو در آن اندوخته‌های نیکو باشد که برای ابدیت از پیش فرستاده‌ای و تاسف و حسرتت درباره‌ی چیزی باشد که پس از خود به جای می‌گذاری و تمامی اهتمام تو درباره‌ی پس از مرگ باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2941,'جز این نیست که مَثَل دنیا مانند ماری است که لمس آن نرم و زهرش کشنده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2942,'هرگاه دنیادار به خوشی آن اطمینان داشته باشد، دنیا او را به نتایج وحشتناک می‌کشاند، یا اگر در آن به انس گرفتن آرمید، او را به وحشت می‌رساند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2943,'از گذشته‌ی دنیا برای آیندهچی آن عبرت بگیر و تجربه بیندوز، زیرا بعضی از امور دنیا همانند بعضی دیگر است و آخر آن به اول آن ملحق و ضمیمه می‌شود و همه‌ی امور دنیا حجابی بین انسان و هدفِ اعلای او بوده و در آخر کار از او جدا می‌گردد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2944,'بپرهیز از هر عملی که در نهان انجام داده می‌شود، ولی در آشکار مورد شرمندگی می‌باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2945,'بپرهیز از هر عملی که اگر درباره‌ی آن سؤال شود، صاحب آن منکر شود یا از آن پوزش بطلبد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2946,'از رفاقت با کسی که رأیش سست و بی‌پایه و عملش زشت است، پرهیز کن، زیرا محاسبه‌ی ارزش هم‌نشین با هم‌نشین صورت می‌گیرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2947,'بپرهیز از آن‌که مرگ بر سرت تاختن آورد و تو در طلب دنیا از پروردگارت گریخته باشی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2948,'غضب خود را فرو بَر و تحمّل داشته باش و هنگام توانایی، بخشش و چشم‌پوشی کن و هنگام خشم، بردبار باش و هنگام قدرت، عفو و اغماض کن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2949,'با کرامت باش، نه اسراف‌گر، حسابگر باش، نه ممسک و لئیم.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2950,'باارزش‌ترین بی‌نیازی ترک آرزوست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2951,'هر کس آرزو را طولانی کند، به بدکرداری دچار شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2952,'بهترین و بافضیلت‌ترین مؤمن کسی است که جان و دودمان و مالش را در راه خدا تقدیم بدارد، زیرا هر چیزی که تو از پیش می‌فرستی، ذخیره‌اش برای تو می‌ماند و آن‌چه پشت سر خود می‌اندازی، خیرش برای دیگری است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2953,'بپرهیز از هم‌نشینی مردم فاسق، زیرا همواره شرّ به شرّ ملحق می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2954,'از غضب بپرهیز، زیرا غضب، لشکری بزرگ از لشکریان شیطان است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2955,'روزگار آدمی با دو روز می‌گذرد: یک روز به سود تو و روز دیگر به ضرر توست. و به یقین این دنیا خانه‌‌ای است دگرگون شونده. پس آن‌چه از آنِ توست، سراغ تو خواهد آمد، اگرچه ناتوان باشی، و آن‌چه به ضرر توست، نمی‌توانی آن را از خود دفع کنی، اگرچه قدرتمند باشی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2956,'فقر، آدم باهوش را از اقامه‌ی دلیل لال می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2957,'انسان تنگدست در شهر خویش بیگانه است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2958,'ناتوانی آفتی است و شکیبایی، دلاوری و پارسایی ثروت و پرهیزکاری سپری است و چه هم‌نشین خوبی است رضایت در زندگی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2959,'سینه‌ی انسان عاقل، صندوق راز اوست و گشاده‌رویی طناب محبت و بردباری مخفی‌کننده‌ی عیوب است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2960,'مسالمت و مدارا، نهانگاه عیوب است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2961,'هر کس از خود راضی باشد، ناراضیان وی فراوان خواهند بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2962,'در آن هنگام که دنیا به کسی روی آورد، نیکویی‌های دیگران را به او عاریت می‌دهد و اگر دنیا از کسی رویگردان شود، نیکویی‌های خود را از او می‌گیرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2963,'با مردم چنان بیامیزید که اگر در آن حال بمیرید، برای شما بگریند و اگر زنده ماندید، همواره به شما مهر و محبت بورزند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2964,'هنگامی که بر دشمنت پیروز شدی، عفو و بخشش را شُکر غلبه‌ات بر او قرار بده.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2965,'ناتوان‌ترین مردم کسی است که از به دست آوردن دوستان عاجز است و ناتوان‌تر از آن، کسی است که دوستی را پیدا کند و او را از دست بدهد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2966,'هنگامی که مقدّمات نعمت‌ها به شما رسید، با کمی ِ سپاسگزاری، دنباله‌ی آن‌ها را از دست مدهید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2967,'هر کس نزدیکانش او را ضایع کنند، دوستی و الفت با دیگران را به دست آورد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2968,'امور چنان تسلیم مقدّرات است که حتی گاهی تدبیر موجب مرگ می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2969,'ترس و هراس، همراه نومیدی است و شرمندگی با محروميّت، و فرصت‌ها مانند ابر در گذر است و فرصت‌های خیر را غنیمت بشمارید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2970,'هر کس عقلش او را عقب بیندازد، نژاد و نَسَبش نمی‌تواند او را پیش ببرد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2971,'از کفّاره‌های گناهان بزرگ، رسیدن به فریادِ فریادخواه و برطرف کردنِ اندوهِ آدم غمگین است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2972,'تا زمانی که دردت با تو می‌سازد، تو هم با دردت بساز.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2973,'بهترین پارسایی، پنهان داشتن پارسایی است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2974,'ایمان بر چهار پایه مستحکم است: صبر، یقین، عدل و جهاد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2975,'هر کس به بهشت اشتیاق دارد، شهوات را از دل بزداید و هر کس از دوزخ بترسد، از محرّمات اجتناب نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2976,'هر کس در دنیا پارسایی پیشه کند، مصیبت‌ها را ناچیز یابد و هر کس در انتظار مرگ باشد، سبقت به خیرات نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2977,'هر کس عناد ورزید، راه‌ها برای او سنگلاخ و ناهموار گشت و شئون زندگی‌اش دچار مشکلات و راه رهایی برای او تنگ شد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2978,'آدم نیکوکار بهتر از خودِ کار، و عامل شر از خود شر، بدتر است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2979,'بی‌نیازترین بی‌نیازی‌ها عقل است و بزرگ‌ترین نیازمندی حماقت و وحشتناک‌ترین تنهایی خودپسندی است و بهترین و باکرامت‌ترین حیثیت، اخلاق نیکو.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2980,'بپرهیز از رفاقت آدم احمق، زیرا او می‌خواهد سودی به تو برساند، [ولی] ضرر وارد می‌سازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2981,'بپرهیز از شخص بخیل، زیرا او آنچه را که تو به آن از همه چیز محتاج‌تری، از تو دور می‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2982,'بپرهیز از رفاقت با انسان تبهکار، زیرا او تو را به هیچ و پوچ می‌فروشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2983,'بپرهیز از دروغگو، زیرا او مانند سراب است که دور را به تو نزدیک و نزدیک را به تو دور می‌نماید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2984,'زبان عاقل در پشتِ قلبِ او و قلب احمق، در پشت زبانش قرار دارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2985,'قلب احمق در دهان اوست و زبان خردمند در قلب اوست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2986,'زشتی که تو را غمگین کند، بهتر از کاری است که تو را به نخوت کشاند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2987,'حیثیت انسان به اندازه‌ی همّت اوست و صدق و صفای او به اندازه‌ی رادمردی‌اش، و شجاعت او به اندازه‌ی عظمتِ طبعش و پاکدامنی او به اندازه‌ی غیرتش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2988,'پیروزی با حسابگری‌ست و حسابگری با کوشش فکر، و فکر با راز داری.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2989,'عیب تو پوشیده است، تا زمانی که دنیا به تو روی آورد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2990,'شایسته‌ترین مردم به عفو و بخشیدن، تواناترین آنان به کیفر دادن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2991,'سخاوت آن است که پیش از سؤال و تقاضا انجام بگیرد و اما پس از خواستن، یا از شرم است و یا برای فرار از توبیخ.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2992,'هیج بی‌نیازی مانند عقل و خرد نیست و هیچ فقری مانند نادانی و هیچ ارثی مانند فرهنگِ شایسته و هیچ پشتیبانی مانند مشورت نمی‌باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2993,'صبر و شکیبایی بر دو قسم است: شکیبایی در برابر آنچه از آن کراهت داری و شکیبایی در برابر آنچه دوست داری.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2994,'بی‌نیازی در غربت، وطن است و نیازمندی در وطن، غربت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2995,'قناعت، مالی است فناناپذیر.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2996,'مال، اصل و مایه‌ی شهوت‌هاست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2997,'کسی که تو را از یک ناگواری بترساند، مانند کسی است که به تو مژده داده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2998,'زبان، حیوانی است درّنده که اگر رها شود، می‌گزد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(2999,'زن، عقربی است دارای گزیدن شیرین.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3000,'شفاعت کننده، بالِ جوینده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3001,'مردم دنیا مانند کاروانی هستند که آنان را به حرکت درآورده‌اند و می‌رانند، در حالی که آنان در خوابند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3002,'از دست رفتن دوستان، غربت است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3003,'فوت شدن حاجت، بهتر از مطالبه‌ی آن از دستِ شخص ناشایست است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3004,'از عطا کردن اندک شرمگین مباش، زیرا ناامیدی از آن کم‌تر است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3005,'پاکدامنی زینت فقر است و سپاسگزاری زینت توانگری.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3006,'هنگامی که درودی به تو گفته شد، با درودی بهتر آن را پاداش بده و هنگامی که دستی به تو عطایی کند، تو با بیش از آن عطا، مقابله کن و با این حال برتری از آنِ کسی است که آغاز کرده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3007,'هنگامی که عقل کامل شد، سخن کم می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3008,'هر کس خود را بر مردم پیشوا قرار داد، نخست پیش از تعلیم دیگران به تعلیم خویشتن بپردازد و تأدیب خویشتن را پیش از تأدیب زبانش انجام دهد و آن کس که به تعلیم و تربیت خویشتن بپردازد، از معلم و مربّی مردم، به تجلیل و تعظیم شایسته‌تر است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3009,'هر آن چه قابل شمارش است، پایان می‌پذیرد و هر آن چه مورد انتظار است، فرا می‌رسد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3010,'امور زندگی وقتی به ابهام و اشتباه افتاد، آخر آن‌ها را با ابتدای آن‌ها باید محاسبه نمود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3011,'حکمت را هر جا باشد، بگیر، زیرا حکمت گاهی در سینه‌ی منافق به حرکت درمی‌آید تا از آن بیرون آید و نزد آنان که شایسته‌ی آن هستند، می‌آرامد و در سینه‌ی مؤمن جایگیر می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3012,'حکمت، گمشده‌ی مؤمن است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3013,'بگیر حکمت را، اگرچه حامل آن از اهل نفاق باشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3014,'ارزش هر انسانی، ناشی از آن امتیاز نیکوست که در اوست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3015,'نسبت صبر بر ایمان، مثل نسبت سر است بر بدن و خیری در آن جسد نیست که سر ندارد و همچنین خیری در آن ایمان نیست که از صبر برخوردار نباشد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3016,'من در شگفتم از کسی که توانایی توبه و استغفار دارد، با این حال مأیوس می‌شود!((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3017,'هر کس بین خود و خدا را اصلاح کند، خداوند بین او و مردم را اصلاح نماید و هر کس امر آخرت خود را اصلاح کند، خداوند امر دنیای او را اصلاح نماید و هر کس پند دهنده‌ای از ذات خود برای خویشتن داشته باشد، برای او از طرف خدا نگهبانی به وجود می‌آید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3018,'بخیل در این دنیا، مانند فقرا زندگی می‌کند و در آخرت، مانند توانگران محاسبه می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3019,'آن کس که در برابر حق عرض اندام نماید، آینده‌ای جز نابودی ندارد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3020,'انسانی که به شناخت خویشتن نایل نشده، در پست‌ترین تاریکی نادانی غوطه‌ور است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3021,'اصل ریشه‌ای که بر تقوا روییده، هلاک نمی‌شود و زراعت قومی که در زمین تقوا کاشته شده باشد، از تشنگی نخواهد خشکید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3022,'ظاهر قرآن زیبا و شگفت‌انگیز است و باطن آن عمیق. شگفتی‌های قرآن فناناپذیر است و معانی بی‌نظیرش را پایانی نیست و تاریکی‌ها بدون استمداد از آن مرتفع نخواهد شد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3023,'آغاز و اساس دین، معرفت خداست و کمالِ معرفتِ او تصدیقش، و غایت تصدیق او توحیدش، و حدّ اعلای توحید او اخلاص به مقام کبریایی‌اش و نهایت اخلاص به او، نفی صفات از ذات اقدسش.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3024,'آن کس که خداوند سبحان را توصیف کند، همسان برای ذات بی‌همتایش سازد، و دویی در یگانگی‌اش درآورد و مقام والای احديّت را تجزیه نماید. پندار تجزیه در وحدتِ ذاتش نشان نادانی است که خدا را قابل اشاره انگارد و با آن اشاره محدودش سازد و چون معدودها، به شمارشش درآورد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3025,'تحصیل علم و یقین به برخی از مطالب قرآنی، مطابق پیمانی که از بندگان گرفته شده، لازم است و در برخی دیگر که اسرار و رموز قرآنی محسوب می‌شوند، مردم را به تنگنای لزوم معرفتِ آن‌ها مجبور ننموده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3026,'سیلِ انبوهِ فضیلت‌های انسانی- الهی، از قلّه‌های روح من به سوی انسان‌ها سرازیر می‌شود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3027,'ارتفاعاتِ سر به ملکوت کشیده‌ی امتیازاتِ من، بلندتر از آن است که پرندگان دورپرواز بتوانند هوای پریدن روی آن ارتفاعات را در سر بپرورانند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3028,'دمساز طبع درشت‌خو، چونان سوار بر شتر چموش است که اگر افسارش را بکشد، بینی‌اش بریده شود و اگر رهایش کند، از اختیارش به‌در می‌رود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3029,'دنیای شما نزد من، از اخلاط دماغ یک بُز ناچیزتر است!((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3030,'شما ای مردم! به وسیله‌ی ما دودمان محمد (ص) از تاریکی‌های جهل و فساد رها شده، راه هدایت را در پیش گرفتید و گام بر فراز اعتلا نهادید. شب‌های تیره و تار جاهليّت را به وسیله‌ی تکاپوها و ارشادهای ما پشت سر گذاشتید، تا فروغ بامدادِ اسلام، بر عقول و دل‌های شما درخشیدن گرفت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3031,'ناشنوا باد، گوشی که نصایح رسا و سازنده را درنیابد! کسی که فریاد بلند، گوشش را کوبیده و كَر ساخته، چگونه از صدای ضعیف تأثر خواهد پذیرفت؟((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3032,'من نگهبانيِ شما را در راه‌های حق که در میان جاده‌های ضلالت کشیده شده، به عهده گرفته‌ام.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3033,'اندیشه‌ی کسی که از من تخلّف کند، از دریافت حق و حقیقت به دور افتاد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3034,'ما امروز راه حق و باطل را تفکیک نمودیم و هر کسی راه خود را پی گرفت. کسی که اطمینان به آب دارد، سوز عطش طاقت از دستش برنخواهد گرفت.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3035,'ای مردم! امواج توفانی فتنه‌ها را که حیات شما دریانوردان اقیانوس هستی را به تلاطم انداخته است، با کشتی‌های نجات بشکافید و پیش بروید. از راه عداوت و نفرت از یکدیگر برگردید، و طریق مهر و محبت پیش بگیرید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3036,'آن کس به مقصود خویشتن نایل گشت که پر و بالی داشت و به پرواز درآمد، یا فاقد قدرت بود و از هجوم به مخاطرات خودداری کرد و آسوده شد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3037,'من همواره به کمكِ انسان حق‌طلب، اِعراض‌کنندگان از حق را، و با انسانِ حق‌شنو که مطیع وظایف الهی است، گنهکارِ آشوبگر را خواهم کوفت تا آنگاه که واپسین روز حیاتم فرا رسد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3038,'سوگند به خدا! من از آن هنگام که خداوند متعال پیامبرش را به پیشگاه خود خوانده، تا به امروز، همیشه از حقّ قانونی خود رانده شده‌ام و دیگران در قلمرو اختصاصی من به تاختن درآمده‌اند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3039,'گشایش کارهای اجتماع در عدالت است و کسی که عدالت برای او تنگی ایجاد کند، ستم برای او تنگناتر خواهد بود.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3040,'آن کس که بهشت و دوزخ را پیش رو دارد، [از بازیچه‌ها و آرزوهای دور و دراز و بی‌تفاوتی‌ها] رویگردان است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3041,'آن مدعی که ادعایش اساسی ندارد، هلاکت در انتظار اوست، و کسی که افترا به واقعیات زند، به استقبال نومیدی و سقوط می‌شتابد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3042,'به شما هشدار می‌دهم: خطاهایی که مردم مرتکب می‌شوند، چونان اسب‌های چموشند که خطاکاران بر آن‌ها سوار گشته، با افسارهایی از دست رفته، در بیراهه‌ها و سنگلاخ‌ها می‌تازند. پایانِ این تاخت و تاز طغیانگرانه، آتش است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3043,'هواخواهانِ باطل از قدیم در اکثريّت‌اند و اگر در اقلّيّت باشد، [نقصی برای حق نیست] با این حال اجرایش امکان‌پذیر و قابل تحقّق است. چه اندک است بازگشتِ آن‌چه که روی گردانده، به گذشته خزیده است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3044,'[مردم بر سه گروه‌اند:] تکاپوگر پویا که نجات پیدا می‌کند و جوینده‌ی كُندرو که برای او امیدی هست، و تقصیرکننده‌ی تبهکار که سقوطش در آتش است. انحراف به راست و چپ (افراط و تفریط) گمراهی، و جاده‌ی اصلی راه وسط است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3045,'از خانه‌هایتان برای تشکیل اجتماعاتِ ویرانگر بیرون نیایید. آشتی و الفت در میان خود به وجود بیاورید، توبه و بازگشت به سوی خدا در دسترس شماست.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3046,'اگر شما می‌دیدید آن‌چه را که افراد نوع شما پس از عبور از دالان اسرارآمیز مرگ دیدند، شیون می‌کردید و به اضطراب می‌افتادید و به شنیدن حقایق تن درمی‌دادید و اطاعت از حقایق می‌کردید.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3047,'از طرف خداوند متعال، بعد از رسولان آسمانی، کسی جز بشر تبلیغ نمی‌کند.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3048,'مال و فرزندان، كِشتگاه دنیاست و عمل صالح، محصولی از كِشتگاه دنیا برای آخرت است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3049,'هیچ کسی چون دودمان آدمی پریشانی‌های او را به آسایش مبدّل نمی‌کند. دودمان آدمی مهربان‌ترین مردم در هنگام فرود آمدن حوادثِ سخت و ناگوار است.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3050,'زمانی که خداوند برای یک انسان زبان راستین در میان مردم قرار می‌دهد، برای او بهتر از مالی است که دیگران از او ارث خواهند برد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3051,'بپرهیز از دوست داشتن مدّاحی و تملّق دیگران، زیرا این صفات پلید از محکم‌ترین فرصت‌ها برای شیطان است که نیکی نیکوکاران را در آن محو می‌سازد.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3052,'بپرهیز از شتابزدگی در امور، پیش از رسیدنِ زمانِ آ ن‌ها، و از سستی و درماندگی در موقعیت مقتضی آن امور.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3053,'از هیجان و تندی برحذر باش و آن‌ها را مهار کن، تا غضب فرو نشیند و اختیار خود را مالک شوی.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3054,'در هر صبح و شام به خدا تقوا بورز و بر نفس خود از فریب دنیا بترس و در هیچ حالی از آن، احساس امن و امان مکن.((امام علی (ع)))',25,0,0,0,0,0);
INSERT INTO mysms VALUES(3055,'هر که زنی به مؤمنی بدهد که به او انس گیرد حق تعالی ملکی را در قبر مونس او گرداند به صورت محبوب‌ترین اهل او نزد او.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3056,'هر که در بیماری عیادت مؤمنی بکند ملائکه او را از همه جهت فرو گیرند و گویند خوشا به حال تو و گوارا باد بهشت از برای تو.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3057,'گرامی‌ترین مردم بر مردم کسی است که خیرش به ایشان برسد و از ایشان مستغنی باشد و از ایشان سؤال نکند.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3058,'هر که جامه‌ی زیادتی داشته باشد و داند که برادر مؤمنش به آن محتاج است به او ندهد حق تعالی او را سرنگون در آتش جهنم اندازد.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3059,'هر که سیر بخوابد و نزدیک او مؤمن گرسنه باشد حق تعالی به ملائکه فرماید که شما را گواه می‌گیرم بر این بنده که من او را امری کردم و نافرمانی من کرد و اطاعت دیگران کرد بدانید که او را به عمل خود گذاشتم و هرگز او را نیامرزم.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3060,'هر که حاجت برادر مؤمن خود را برآورد حق تعالی صد حاجت او را برآورد که یکی بهشت باشد.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3061,'هر که غمی از مؤمنی بردارد حق تعالی غم‌های آخرت او را بردارد.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3062,'هر که مؤمنی را اعانت کند بر ظالمی خدا او را یاری کند در گذشتن بر صراط در وقتی که قدم‌ها بلغزد.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3063,'هر که مؤمنی را آب دهد در وقت تشنگی حق تعالی از شراب بهشت او را سیراب کند.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3064,'هر که بر مؤمنی جامه‌ای بپوشاند در وقتی که عریان باشد خدا او را دیبا و حریر بهشت بپوشاند و هر که جامه‌ای بر او بپوشاند در غیر عریانی پیوسته در ضمان خدا باشد از بلاها تا از آن جامه برای مؤمن تاری باقی باشد.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3065,'هر که بعد از مردن کفن بپوشاند بر مؤمنی چنان باشد که او را از هنگام ولادت تا وقت مردن جامه داده باشد.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3066,'هر که خود را از بدگویی در عرض مسلمانان نگاه دارد خدا گناهش را در قیامت بیامرزد.((امام علی بن الحسین (ع)))',26,0,0,0,0,0);
INSERT INTO mysms VALUES(3067,'از جمله‌ی ضعف و سستی آن است که کسی به تو احسانی کند و تو به عوض مکافات او نکنی.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3068,'چون مهمان بیاید او را اعانت و یاری کنید در فرود آمدن و وقتی که خواهد بار کند و برود او را مدد نکنید بر رفتن که این دلیل بر خسّت نفس است و در رفتن توشه همراهش بکنید و توشه‌ی نیکو و خوشبو به عمل آورید که این، دلیل جوانمردی است.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3069,'یک برادر مؤمن را که از برای خدا با او برادری کنم و سیر کنم نزد من بهتر است از آنکه ده مسکین را سیر کنم.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3070,'هرگاه شما را به عروسی بطلبند دیر بروید زیرا که دنیا را به یاد شما می‌آورد و چون شما را به جنازه بخوانند زودتر بروید چون آخرت را به یاد شما می‌آورد.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3071,'رازی به زنان مگویید و درباره‌ی خویشان شما آنچه گویند اطاعت مکنید.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3072,'لعنتی که از دهان شخصی بیرون می‌آید می‌گردد اگر صاحبش را بیابد آنجا قرار می‌گیرد اگر نه به گوینده‌اش برمی‌گردد.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3073,'هر که طعنی بکند در روی مؤمنی به بدترین مردن‌ها بمیرد سزاوار است که عاقبتش به خیر نباشد.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3074,'هر مسلمانی که به دیدن مسلمانی بیاید یا پی کاری بیاید او در خانه باشد و او را رخصت ندهد و بیرون نیاید پیوسته در لعنت خدا باشد تا به یکدیگر ملاقات کنند.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3075,'سه چیز است که هر که مرتکب آنها شود پیش از مردن عقوبت آنها را می‌یابد: ظلم و قطع صله‌ی رحم و قسم دروغ و بسیار است که جماعتی بدکردارند و به سبب صله‌ی رحم مال و فرزندان ایشان بسیار می‌شود و قسم دروغ و قطع صله‌ی رحم خانه‌ها را از اهلش خالی می‌کند و نسل را منقطع می‌کند.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3076,'هر که بر غلامی یا کنیزی به قدری از حدود الهی بزند بی‌آنکه جرمی از او صادر شده باشد که به حسب شرع مستحق آن حد شده باشد گناه او را کفاره‌ای نیست به غیر از آزاد کردن او.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3077,'واجب است از برای مؤمن بر مؤمن که هفتاد کبیره‌ی او را بپوشاند.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3078,'تبسّم کردن در روی برادر مؤمن حسنه است و خاشاکی از او برداشتن حسنه است و خدا هیچ عبادتی را دوست‌تر از شاد گردانیدن مؤمن نمی‌دارد.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3079,'یک حج بهتر است از هفتاد بنده آزاد کردن و خرج کشیدن و نفقه دادن اهل یک خانه از مسلمانان که ایشان را سیر کند و بپوشاند و روی ایشان را از سؤال کردن حفظ کند بهتر است از هفتاد حج.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3080,'مؤمنی که برادر مؤمنش حاجتی به نزد او بیاورد و او قادر نباشد بر برآوردن حاجت او دلش غمگین شود به سبب این خدا بهشت را بر او واجب گرداند.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3081,'هر که در انگشتر خود آیه‌ای از قرآن نقش کند آمرزیده شود.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3082,'سه چیز است که خدا به هیچ وجه در آن رخصتی نداده است: اول پس دادن امانت از هر که باشد خواه نیکوکار و خواه بدکار، دوم وفا به عهد و پیمان کردن خواه با نیکوکار و خواه با بدکار، سوم نیکی با پدر و مادر خواه نیکوکار باشند و خواه بدکردار.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3083,'کسی که از روی خشم و غضب به سوی پدر و مادر نظر کند در وقتی که بر او ظلم کنند خدا هیچ نماز او را قبول نکند.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3084,'یک شب تب برابر است با عبادت یکساله و دو شب تب برابر است با عبادت دو ساله و سه شب تب برابر است با عبادت هفتاد ساله.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3085,'اگر مصاحبت کنی با مثل و مانند خود مصاحبت و رفاقت بکن و با کسی رفیق مشو که خرج تو را کشد که این موجب خواری مؤمن است.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3086,'کسی که به کشتی سوار می‌شود برای تجارت دین خود را در معرض تلف درآورده است.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3087,'هر که خشمی را فرو خورد و قادر باشد که آن را به عمل آورد حق تعالی دلش را از ایمنی و ایمان پر کند در روز قیامت.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3088,'مذاکره‌ی علم ثواب نماز مقبول دارد.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3089,'از مشقت زندگانی است خانه‌ی تنگ.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3090,'هر اهل خانه‌ای که نزد ایشان گوسفند شیردهی باشد هر روز دو مرتبه ملائک به ایشان گویند خدا شما را برکت دهد.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3091,'چون به طلب حاجت بیرون بروید در روز بروید که در شب حاجت برآورده نمی‌شود.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3092,'کسی که طلب دنیا کند برای آنکه از مردم سؤال نکند و روزی عیال خود را فراخ گرداند و با همسایگان خود احسان کند در روز قیامت روی او مانند ماه شب چهارده باشد.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3093,'بهشت بر سخن‌چینان حرام است.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3094,'هر که به سوی پادشاه ظالمی برود و او را امر به پرهیزکاری بکند و پند و نصیحت بگوید او را مثل ثواب جن و انس بوده باشد.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3095,'یک نشستن با کسی که اعتماد بر او داشته باشم نزد من بهتر است از عبادت یک سال.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3096,'عطسه نیکو چیزی است نفع می‌رساند به بدن و خدا را به یاد می‌آورد.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3097,'خدا دوست می‌دارد کسی را که خوش طبعی کند میان مردم مادامی که فحش نگوید.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3098,'مردم یا مؤمن‌اند یا جاهل پس مؤمن را آزار نباید کرد و با جاهل بی‌خردی نباید کرد و اگر نه مثل او خواهی بود.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3099,'با منافق با زبان خوش سازگاری بکن و محبت قلبی خود را خالص گردان برای مؤمنان و اگر یهودی با تو همنشینی کند نیکو مجالست کن با او.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3100,'در نمک شفای هفتاد نوع از انواع درد هست و اگر مردم بدانند که چه منفعت‌ها در نمک هست مداوا نکنند مگر به آن.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3101,'بدنی که بیماری نکشد طغیان به هم می‌رساند و در چنین بدنی خیری نیست.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3102,'اگر کسی با گبری مصافحه کند دستش را بشوید.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3103,'هر که ميّتی را غسل بدهد و آنچه از عیب‌های آن ميّت بر او ظاهر شود به کسی نقل نکند گناهانش آمرزیده شود.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3104,'هر که کفن کند مؤمنی را چنان است که ضامن پوشش او شده باشد تا روز قیامت و هر که بکند از برای مؤمنی قبری چنان باشد که او را در خانه‌ی موافقی جا داده باشد تا روز قیامت.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3105,'هر مؤمنی که به قصد دیدن برادر مؤمن خود از خانه بیرون رود حق او را شناسد حق تعالی به هر گامی حسنه‌ای در نامه‌ی عملش بنویسد و گناهی از او محو کند و درجه‌ای برای او بلند کند.((امام محمد باقر (ع)))',27,0,0,0,0,0);
INSERT INTO mysms VALUES(3106,'در قیامت سه کس در سایه‌ی عرش الهی باشند در روزی که سایه‌ای به غیر آن نباشد: کسی که برادر مسلمان خود را زنی بدهد و کسی که خادمی به او بدهد و کسی که راز او را پنهان دارد.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3107,'چون کسی بیمار شود مردم را رخصت دهد که به عیادت او بیایند به درستی که هیچ کس نیست مگر آنکه او را دعای مستجابی هست.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3108,'پرهیز آن نیست که در اصل، چیزی نخوری بلکه آن است که بخوری و کمتر بخوری.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3109,'مؤمن می‌باید از پنج چیز خالی نباشد: مسواک و شانه و جانماز و تسبیحی از تربت که سی و چهار دانه باشد و انگشتر عقیق.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3110,'بهترین عیش دنیا فراخی خانه و بسیاری دوستان است.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3111,'مردم در طعام خوردن میانه‌رو باشند همیشه بدن ایشان صحیح باشد.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3112,'شوخی و کج خلقی طفل در کودکی علامت آن است که در بزرگی دانا و بردبار خواهد بود.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3113,'خدا غضب نمی‌کند برای چیزی مثل آنکه غضب برای ظلم بر زنان و طفلان می‌کند.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3114,'عیال آدمی اسیران اویند. پس کسی را که خدا نعمتی به او کرامت کند بر اسیران خود توسعه کند اگر نکند به زودی آن نعمت از او زایل می‌شود.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3115,'دیده‌ی خود را به خواب عادت مده که هیچ عضوی در بدن شکر خدا کمتر از دیده نمی‌کند.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3116,'هر که مست شود از شراب و بعد از آن تا چهل روز بمیرد نزد خدا مانند بت پرست باشد.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3117,'بسیار پرده از میان خود و برادر مؤمن خود برمدار که باعث برطرف شدن حیاست.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3118,'هیچ چیز شیطان و لشکرهای او را مجروح نمی‌کند مثل رفتن برادران مؤمن به دیدن یکدیگر از برای خدا.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3119,'نیکی زندگانی در گشادگی خانه و بسیاری خدمتکاران است.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3120,'در خانه‌ای که صورتی در برابر تو باشد نماز مکن مگر آنکه چاره نداشته باشی پس سر آن صورت را قطع کن و نماز بکن.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3121,'هر بالی که کبوتر می‌زند باعث نفرت و گریختن شیطان می‌شود.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3122,'بامداد به سوی چیزی که موجب عزّت توست برو یعنی بازار.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3123,'کسی که نخرد مگر به قسم و نفروشد مگر به قسم حق تعالی در قیامت نظر رحمت به سوی او نمی‌فرماید.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3124,'در مزد گرفتن برای نوشتن قرآن باکی نیست.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3125,'مسلمان نباید که با گبر در یک کاسه چیزی بخورد یا با او در یک فرش بنشیند یا با او مصاحبت کند.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3126,'اگر محتاج شوی به طبیب ترسا باکی نیست که بر او سلام کنی و او را دعا کنی که دعای تو نفعی به او نمی‌رساند.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3127,'سزاوار آن است که آدمی مصاحبان پدر خود را نگاه دارد که نیکی او به ایشان مانند نیکی با پدر است.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3128,'چون سه نفر در مجلسی باشند دو تای ایشان با یکدیگر سرگوشی نکنند که موجب اندوه و ایذای آن رفیق دیگر است.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3129,'مزاح مکن که نور ایمانت را می‌برد و مروّت مردیت را سبک می‌کند.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3130,'گرامی‌ترین شما نزد خدا کسی است که تقیه بیشتر کند.((امام موسی (ع)))',28,0,0,0,0,0);
INSERT INTO mysms VALUES(3131,'برای پول درآوردن یا گرد‌آوردن آن، باید بتوانید با آن شاد باشید.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3132,'برای کامروا بودن و شاد زندگی کردن، سخن های ناخوشایند را نشنیده بگیر و ستایش‌ها را در ذهن نگه دار.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3133,'اگر بیش از اندازه وابسته به پول باشید، پول درآوردن و نگه داشتن آن برایتان دشوار خواهد بود.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3134,'اگر من زیاد پول داشته باشم، دیگران بی‌پول می‌مانند! این نابخردانه‌ترین باور است.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3135,'هر کس باید بپذیرد که ارزش کمک – وام، قرض گرفتن – را دارد. زیرا توانایی شما برای پذیرش کمک دیگران، تعیین کننده‌ی توانایی شما برای کسب دارایی است.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3136,'پول، یک بازی است و شما با بازی کردن برنده می‌شوید نه با زجر کشیدن.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3137,'توانگران و تهیدستان، دو نگرش کاملاً متفاوت [ =دیگرگون ] نسبت به پول داشته‌اند. تهیدستان، آرزو می‌کرده‌اند پول به دست آورند و توانگران، باور داشته‌اند که به دست می‌آورند.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3138,'احساس توانگر بودن، دارایی راستین را در پی خواهد داشت.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3139,'خطاهایی که برنده‌ها انجام می‌دهند به مراتب بسیار بیشتر از خطاهایی است که بازندگان انجام می‌دهند و به همین دلیل است که آنها برنده‌اند.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3140,'توانگر بودن یک هدف نیست، بلکه یک فرآورده ی جنبی است.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3141,'برای توانگر بودن باید بتوانید نسبت به توانگران دیگر احساس خوب و مثبت داشته باشید.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3142,'کتابهای دینی، ما را به دستگیری از تهیدستی تشویق [=آرزومند] می‌کنند، نه به پیوستن به آنها.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3143,'ترس خرج کردن پول را از خود دور کن که این برای توانگر شدن دارای اهمیت است. گاه و بی‌گاه برای خود پول خرج کن.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3144,'مسیر حرکت افراد کامروا به سوی کامیابی است، در صورتی که تلاش شکست ‌خوردگان، گریز از شکست است.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3145,'اگر پذیرش کامیابی برای شما نگرانی و آشفتگی به همراه بیاورد، همیشه خود را دور از کامیابی نگاه خواهید داشت.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3146,'مهم نیست که از کجا آغاز می‌کنی، مهم، آن نقطه‌ای است که برای پایان دادن بر می‌گزینی.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3147,'ما ایستادگی را از طبیعت می آموزیم؛ رودی که از رفتن باز بایستد بوی گند می گیرد.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3148,'هر جا که هستید، همانجا نقطه آغاز است؛ تلاش بیشتر امروز سازنده فردای متفاوت شماست.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3149,'دنیا به تلاش پاداش می دهد نه به بهانه.((اندرو متیوس))',29,0,0,0,0,0);
INSERT INTO mysms VALUES(3150,'وقتی زنها دوستمان می دارند، ما را از هر لحاظ به دیده عقل می نگرند؛ حتی جنایات ما را. ولی وقتی علاقه ای به ما نداشته باشند، ارزشی برای فضایل مان نیز قایل نمی شوند.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3151,'عشق ورزیدن شغل بزرگی است. افرادی که می خواهند به خوبی از عهده این کار برآیند، باید کاری جز این نکنند.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3152,'زن خوش آیند و دوست داشتنی زنی است که همه او را می بینند. به همین علت است که هنرپیشگان این همه محبوبیت دارند.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3153,'یک زن بدون عشق هیچ است؛ زیبایی بدون کامرانی پشیزی ارزش ندارد.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3154,'بزرگترین مانع بر سر راه ابراز اخلاص به دیگران، کنار نیامدن با خود است.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3155,'قلب مادر به اندازه‌ای گسترده است که همیشه می‌توانید بخشش و گذشت را در آن بیابید.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3156,'امید، بهترین پشتوانه و عالی ترین نقطه اتکاء است. همیشه امیدوار باشید و روح ناامیدی و یاس را از خود دور سازید.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3157,'استعداد بزرگ بدون اراده بزرگ وجود ندارد.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3158,'خانه بدون زن، گورستان است.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3159,'سعادت بسته به کار و جسارت است.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3160,'هنر سخن گفتن چیزی جز هنر زندگی کردن نیست.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3161,'هنرمند راستین باید طرفدار اصل هنر برای هنر باشد.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3162,'دوست داشتن بدون امید، باز هم یک خوشبختی است.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3163,'هیچ گاه در زندگی نمی توانید محبتی بهتر، بی پیرایه تر و حقیقی تر از محبت مادر خود بیابید.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3164,'عشق، سپیده دم ازدواج است و ازدواج، شامگاه عشق.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3165,'اساس خوشبختی های مادی بر اعداد و ارقام بنا شده است.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3166,'هدر دادن زمان گرانبها ترین هزینه ها به شمار می رود.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3167,'ازدواج های غیر متناسب مانند پارچه های ابریشمی و پشمی است که سرانجام ابریشم، پشم را خواهد برید.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3168,'ایده ها سرمایه ای هستند که فقط وقتی به دست افراد با استعداد و نابغه می افتند، ثمربخش می شوند.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3169,'حساسیت، عشق، تحمل و فداکاری، زندگی زن را تشکیل می دهد.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3170,'عشق، غریزه ی خاص خود را دارا است و می داند چگونه در قلبی نفوذ کند.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3171,'مادر، یگانه موجودی است که حقیقت عشق پاک را می شناسد.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3172,'سنت در هنر به منزله ی کتاب الفبا است، اما جز این ارزشی ندارد.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3173,'سنت در هنر به منزله ی کتاب الفبا است، اما جز این ارزشی ندارد.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3174,'بخشندگی را از گل بیاموز؛ زیرا حتی ته کفشی را که لگدمالش می کند خوشبو می سازد.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3175,'کمال زن در مادر بودن است. زنی که مادر نباشد موجود ناقصی است.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3176,'وظیفه هنر، پیروی از طبیعت نیست، بلکه بیان آن است.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3177,'به همان میزان که بقال به کاسبی علاقه دارد، هنرمند از معامله بیزار است.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3178,'اتلاف وقت، گرانترین خرجهاست.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3179,'نابغه کسی است که پیوسته افکارش را از قوه به فعل در می آورد.((اونوره دو بالزاک))',30,0,0,0,0,0);
INSERT INTO mysms VALUES(3180,'چنان باش که بتوانی به هر کس بگویی : مثل من رفتار کن.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3181,'اخلاق باید بر هنر حکومت کند.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3182,'دو چیز در من شگفتی و ستایش همیشگی و فزاینده برمی‌انگیزد: آسمان پرستاره‌ی فراز سرم و حکم اخلاقی درونم.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3183,'آگاهی، دانش سازمان یافته است و خرد، زندگی سازمان یافته.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3184,'آنچنان رفتار کن که رفتار تو بتواند به صورت قانون کلی درآید.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3185,'شیرین ترن نتیجه ی دقت و نظم، پیروزی است.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3186,'خدا را باور کنید، برای اینکه به چنین باوری محتاجید.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3187,'شاعری، والاترین هنرها است.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3188,'هرگز با دیگران مثل ابزار کار رفتار نکنید، بلکه آنان را چون غایت و هدف بشمارید.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3189,'موسیقی لذت بخش ترین هنرها است، اما چیزی به ما نمی آموزد؛ آنچه که فکر و روح آدمی را تغذیه می کند و آموزنده است، شعر و شاعری است.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3190,'وظیفه ی اصلی دولتهای حقیقی آن است که نگذارند به انسانها مانند شیء نگریسته شود و دست کم، محدودیت و نگاهبانی را به حداکثر آزادی افراد اعمال کنند.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3191,'وجه تمایز انسان نه جسم او بلکه روحش است.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3192,'اگر از انسان آرزو و خواب گرفته شود، بیچاره ترین موجود روی زمین است.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3193,'در دنیا دو چیز از همه زیباتر است؛ آسمان پرستاره و وجدان آسوده.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3194,'بهترین خوشی ها استراحت پس از کار است.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3195,'روشنگری، پایان دوران کودکی خود تحمیل کرده ی نوع بشر است.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3196,'شعار روشنگری این است که توانایی دنبال کردن فهم خود را داشته باش.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3197,'اگر بتوان این پرسش را بیان کرد که آیا ما در حال حاضر در یک عصر به روشنگری رسیده زندگی می کنیم؟ پاسخ باید این باشد که خیر، اما یقیناً در یک عصر روشنگری زندگی می کنیم.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3198,'آرزوهای بشر پایان ناپذیر است. هرگاه به آرزویی رسید، آرزوی دیگری دارد.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3199,'حتی خشم آسمان برای زمین، فراوانی است.((ایمانوئل کانت))',31,0,0,0,0,0);
INSERT INTO mysms VALUES(3200,'هنگامی که امیدوارید دیگران هیجان را به زندگی شما بازگردانند، برای ایجاد عشق و شور و نشاط به آنان وابسته می شوید و تماس خود را با سرچشمه عشق درون خود از دست می دهید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3201,'عشق، مغناطیسی است که ما را به مبدأ خود جذب می کند.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3202,'عشق از آنجا که با معشوق پیوند می خورید آغاز می گردد و تا حرکت موزون و هماهنگی روحهایتان امتداد می یابد. هنگامی که جانهایتان یکی می شوند، آنگاه با تمامی وجود عشق خواهید ورزید. دیگر چیزی بین شما نیست که از جنس عشق نباشد. پیوند مقدس همین است؛ شور و شیفتگی، وجد و شادمانی راستین هم این است.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3203,'تنها با عشق میان دلهایتان است که عشق شما عمق و استحکام حقیقی خود را نشان خواهد داد.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3204,'عشق ماندگار هرگز بر جاذبه ی جسمانی میان شما و معشوق که همواره در حال تغییر است متکی نیست.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3205,'در آغاز تنها عشق بود؛ حتی زندگی و پیدایش شما بر روی این کره ی خاکی نیز برخاسته از عشق است. این عشق بوده که در یک لحظه، مرد و زنی را آنچنان به سوی هم جذب کرده است تا از تلفیق و اتحاد عاشقانه ی بدن آنها بذر شما متولد گردد.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3206,'شور زندگی، ترانه ای است که عشق می سراید؛ شور زندگی همان عشق است که به حرکت در آمده است. عشق و شور زندگی زمانی نصیب تان می شود که آتش عشق را گرامی داشته و بیاموزید که همواره آن را روشن و فروزان نگاه دارید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3207,'چنانچه می خواهید راه خود را به سوی عشق و شور زندگی بازیابید، باید راه دل و قلب خود را بیابید. آنجا، در مرکز وجودی شما، تمامی آنچه هستید سکونت دارد. شما خود نور هستید؛ شما شور و شادمانی هستید؛ شما از جنس عشق هستید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3208,'موهبت عشق و شور زندگی، دست به دست و سینه به سینه خواهد گشت، بارها و بارها. آیا به راستی این معجزه ای کم نظیر نیست؟ هرگز نمی توان برای تاثیری که عشق شما بر جهان هستی خواهد گذشت پایانی متصور شد...((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3209,'عشق، ویژگی ای نیست که به کلی نسبت به آن بیگانه باشید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3210,'عشق، ویژگی و یا کیفیتی نیست که برخی با آن زاده شده باشند، اما برخی دیگر محکوم هستند تا پایان عمر بدون آن سر کنند.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3211,'تسلیم عشق خود شدن، همان تسلیم شدن به چیزی یا کسی بیرون از شما نیست، بلکه تسلیم شدن در برابر قدرت عشق و شور و شعف درونی خود شماست. این نوع تسلیم شدن همان تسلیم شدن در برابر خود حقیقی تان است.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3212,'عشق به مراتب بزرگتر و فراتر از جاذبه ی فیزیکی و جسمانی ای است که نسبت به شخص دیگری در خود احساس می کنیم. عشق حتی به مراتب فراتر از ایمان به آرمان و غایتی برتر یا علاقه ی شور و اشتیاق نسبت به روابط، کار و یا حتی خانواده است.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3213,'هر چه تعهد خود را نسبت به چیزی بیشتر کنید، عشق و شور و حال بیشتری نسبت به آن در خود احساس خواهید کرد.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3214,'عشق، آهنربایی است که ما را به سوی آغاز خود می کشاند.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3215,'عشق، تنها سرچشمه ی نیرو و توان شماست.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3216,'هر چه عشق و شور زندگی بیشتری از خود نشان دهید، برای دیگران نیز بیشتر مقاومت ناپذیر خواهید شد و آنها دیگر نخواهند توانست شما را نادیده بگیرند.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3217,'شما این توان را دارید که یک زندگانی‌ سرشار از عشق و خرسندی بیافرینید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3218,'زندگی با عشق، نیازمند جرأت، دلیری و بی باکی روحی بزرگی است.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3219,'عشق، همان چیزی است که به شما امکان می دهد بارها و بارها زاده شوید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3220,'عشق، همان جوهره و هویت راستین شما است .((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3221,'عاشق بودن به همان اندازه طبیعی است که نفس کشیدن و زنده بودن.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3222,'هر آغاز دوباره، هر رشد و دگرگونی درونی و هر تغییر مسیری که همواره از عشق و نیاز و گرایش درونی آغاز شده باشد، به سمت راستی، شادمانی، خوشبختی و آزادی بیشتر است.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3223,'در سرتاسر زندگی، باز، همان عشق است که تنها پشتیبان و آزادی بخش شماست.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3224,'هرچه بیشتر عشق بورزید، عشق و شور زندگی بیشتری به شما روی خواهد آورد.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3225,'این همان عشق است که شما را در راه خودکاوی و خودشناسی، یک دم تنها نمی گذارد، آن هم زمانی که نمی دانید به کدامین سو روانید و در پی کدامین گمگشته‌اید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3226,'عشق و شور زندگانی را در درون خودتان جست و جو کنید. سرزندگی و زنده بودن را تنها از درون خودتان جویا شوید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3227,'هر کاری را با پایبندی انجام دهید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3228,'عاشق هر که هستید، با وفاداری به او عشق بورزید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3229,'هر گزینشی که می کنید، به آن پایبند باشید؛ آنگاه هر کاری که از شما سر بزند، سرشار از عشق و شور زندگی خواهد بود.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3230,'به گونه ای از عشق و شور خود نگاهبانی و پاسداری کنید که گویی گرانبها ترین دارایی شماست.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3231,'نگران نباشید که دیگران درباره ی شما چگونه می اندیشند. به این نیندیشید که آنچه را می خواهید انجام بدهید، چقدر عملی، سودمند یا کارساز است. شوخ باشید، عاشق باشید، با شور و حال زندگی کنید؛ خودتان باشید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3232,'گردن نهادن به عشق خود، همان گردن نهادن به چیزی یا کسی بیرون از شما نیست، بلکه گردن نهادن به نیروی عشق و شور و شادمانی درونی خود شماست .این گونه گردن نهادن، همان گردن نهادن در برابر خود راستین تان است.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3233,'چنانچه می خواهید راه خود را به سوی عشق و شور زندگی بازیابید، باید [نخست] راه دل و قلب خود را بازیابید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3234,'عشق راستین از روح شما سرچشمه می گیرد. این گونه از عشق ناب، هنگامی که خود را در دل و جان دیگری می یابید، شکل می گیرد و پیوند اعجازگونه‌ی این دو را به جشن و سرور می نشیند.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3235,'چشم براه نمانید که عشق، شما را پیدا کند؛ زمین حاصلخیزی [ =بَروَری ] بیافرینید تا بذر عشق به سادگی در آن جوانه زند و رشد کند.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3236,'خود را تمام و کمال و تا آنجا که در توان دارید، با همه ی راستی و درستی، به رابطه‌تان پایبند کنید. قدرت پایبندی، بذر عشق میان شما و معشوق را آبیاری خواهد کرد و به آن، این امکان را خواهد داد تا در قلب شما به بار بنشیند.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3237,'زمانی از عشق و شور زندگی، بهره مند می شوید که آتش عشق را گرامی بدارید و بیاموزید که همواره آن را روشن و فروزان نگه دارید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3238,'هرگاه زندگی با شور و عشق را بر می‌گزینید، زندگی نیز عشق و شور خود را برای شما بر خواهد گزید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3239,'کارتان را با عشق انجام دهید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3240,'هرگاه خود را با همه ی وجود پیوسته وقف پیشه، کار و نیز زندگی تان کنید، آنگاه کامیابی راستین را تجربه خواهید کرد و خرسندی و خشنودی راستین را احساس خواهید نمود.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3241,'هنگامی که عاشق زندگی می شوید، دنیا را درست همچون عشاق از پس دیدگان عشق خواهید نگریست.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3242,'مهم نیست چه کسی هستید، چه پیشه ای دارید یا چگونه زمان خود را می گذرانید. هر روز فرصت‌های بی شماری در دست دارید تا بر زندگی انسانهای پیرامون خود تاثیرگذار باشید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3243,'ایمان، تنها زاده‌ی عشق و شور زندگی است.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3244,'هرگاه به خود اجازه دهید تا هستی و آفرینش را با همه ی شکوه و بزرگی آن بنگرید، آنگاه به توانایی، زیبایی و عشق و شور زندگی در خودتان پی خواهید برد؛ زیرا شما نیز بخشی از همین شکوه و بزرگی هستید.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3245,'عشق، نور است که هرچه را در مسیرش قرار بگیرد - از جمله قلبها را - از خود روشن می سازد.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3246,'هرچه بیشتر عشق بورزید، بر دیگران نیز رواست تا عشق بیشتری به شما و نیز دیگران بورزند.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3247,'هرگز نمی توان برای کارآمدی عشق شما در جهان هستی، پایانی متصور شد .((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3248,'زندگی هر یک از شما این توانایی و شایستگی را دارد که والا و بی اندازه باشد. هر یک از شما نیز این توانایی و شایستگی را در خود دارید که از زندگی خود تا سرحد کمال لذت ببرید. کلید این معما در زندگی همراه با عشق و شور زندگی نهفته است.((باربارا دی آنجلیس))',32,0,0,0,0,0);
INSERT INTO mysms VALUES(3249,'اشک چشمی را خشکاندن، بیش از آن مایه شهرت و افتخار است که دریای خون به راه انداختن.((بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3250,'به آنها که مرا دوست دارند لبخند می زنم و برای آنها که از من متنفرند آه می کشم، آسمان هر جور که بالای سر من قرار دارد بی تفاوت است، زیرا در درون من قلبی است که برای مواجهه با هر سرنوشت و تقدیری آماده است.((بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3251,'از نظر یک مرد، برای یک زن، مردن آسانتر است تا با وی زندگانی کردن.((بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3252,'هر که شعر را با حقیقت و حکمت آشتی دهد، شاعر حقیقی است.((بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3253,'انتقام شیرین است بویژه برای زنان.((لرد بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3254,'همه ی خوشبختی ها و موفقیت هایی که به من روی آورده، از درهایی وارد شده که آنها را به دقت بسته بودم.((لرد بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3255,'آغاز وجود انسان از سینه ی زنان حیات می یابد، آغازین کلمات را لبان او می آموزاند، آغازین گریه ها را نوازش انگشتان او آرام می دهد و واپسین آه ها را هم اغلب گوشِ زنی می شنود، آنگاه که مردان از تحمل بار اندوه جانگزای دیدن واپسین دم حیات مردی که ایشان را پیشوا بوده، تن می زنند.((بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3256,'عشق مرد قسمتی از زندگی او و عشق زن همه زندگی اوست.((لرد بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3257,'تمام کسانی که شاد هستند، پیروز خواهند شد؛ باید شادی ها را تقسیم نمود؛ خوشبختی ارزش دو برابر شدن را دارد.((لرد بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3258,'هرچه ایمان مرد به هوشش بیشتر شود، زن بهتر می تواند او را فریب دهد.((لرد بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3259,'اگر قلبی شکست، بهتر است با شکستگی هایش به راه عشق ادامه دهد.((جرج گاردون، لرد بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3260,'امیدوارم در تمام طول زندگی چنان همدیگر را دوست داشته باشیم که انگار هرگز ازدواج نکرده ایم.((جورج گوردون بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3261,'اگر می توانستیم جزئیات حوادث را بشناسیم شاید در هنگام موازنه ی سود و زیان، جنگ را عملی سودمند نمی یافتیم زیرا فتوحات در قبال خساراتش ارزش ندارد.((بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3262,'تنها در زمان صلح است که اهالی یک کشور می توانند بار سنگین جنگ را احساس کنند.((بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3263,'نابود شدن نفوس بشری هنگام جنگ باعث می شود که یکی از طرفین جشن هایی برپا کند.((بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3264,'سربلندی در خشک کردن قطره اشک است نه در جاری ساختن سیل خون.((بایرون))',33,0,0,0,0,0);
INSERT INTO mysms VALUES(3265,'شما آهن‌ربایی زنده هستید؛ در مورد هر چه بیندیشید، همان را به سمت خود می‌کشید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3266,'زندگی همچون قفلی پیچیده است؛ تنها کاری که باید بکنیم این است که شماره‌های درست و ترتیب درست آن‌ها را پیدا کنیم تا بتوانیم هرآنچه می‌خواهیم، به دست آوریم.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3267,'نخست در مورد هدف روشن و بنیادی زندگی‌تان تصمیم بگیرید و سپس، همه‌ی تلاش‌های خود را پیرامون آن، سازماندهی کنید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3268,'بدانید که براستی چه هدفی را دنبال می‌کنید، ولی برای دستیابی به آن انعطاف نشان بدهید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3269,'درستی، صفتی است که سایر ارزش ها را در وجود شما نگاه می دارد و سبب می شود زندگی تان را بر پایه ی آنها استوار کنید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3270,'درستی، زیربنای منش است و ساختن منش، مهمترین وظیفه ی ما است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3271,'تنها انسانی که با والاترین ارزش ها و فضایل، زندگی می کند، به راستی، درستی و راستگویی رسیده است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3272,'کنش های شما، منش شما را می سازند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3273,'اگر هر چه بیشتر، کنش ها، گفتار و کردار شما بر پایه ی والاترین ارزش هایتان باشند، بیشتر به خودتان دلبسته شده و احساس بهتری نسبت به خودتان پیدا می کنید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3274,'رو راست بودن با خود، یعنی رو راست بودن با همه ی کسانی که در زندگی شما نقش دارند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3275,'درستی شما در میزان پایبندی تان به گرامی ترین ارزش هایتان جلوه می کند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3276,'در هر شرایط مالی که هستید، زمانی که بخواهید دیگر کار نکنید، باید بتوانید یک سال آینده را با همان شرایطی که اکنون دارید، سپری کنید؛ بدون هیچ‌گونه درآمدی.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3277,'باورهای شما، بیانگر منش و انسانیت شما است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3278,'مشکلات ما را متوقف نمی کنند، بلکه به ما آموزش می دهند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3279,'یادگیری مداوم حداقل شرط لازم برای موفقیت در هر زمینه ای است که در آن فعالیت می کنید؛ هر روز چیز جدیدی بیاموزید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3280,'هر فعالیت یا عدم فعالیتی مستلزم انتخابی است بین آنچه بیشتر اهمیت دارد و آنچه کمتر اهمیت دارد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3281,'پاداش هایی که در زندگی می گیرید بستگی به این دارد که چه کاری می کنید، چقدر خوب آن را انجام می دهید و چقدر مشکل است که بتوان کسی را جایگزین شما کرد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3282,'قانون کاشت و برداشت، اصل و اساسی زندگی بشر است. شما امروز محصولی را برداشت می کنید که در گذشته بذر آن را کاشته اید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3283,'دقت و تمرکز، رموز عملکرد عالی هستند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3284,'مدیریت زمان وسیله ای است که می تواند شما را از جایی که هستید به جایی که می خواهید باشید برساند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3285,'خوشبختی زمانی به دست می آید که شما به کاری که انجام می دهید باور داشته باشید، آن را خوب بشناسید و دوست بدارید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3286,'قانون حد توانایی می گوید: همیشه برای انجام ضروری ترین کارها زمان کافی وجود دارد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3287,'قانون حذف گزینه ها می گوید: انجام یک کار مستلزم صرف نظر از انجام کارهای دیگر است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3288,'بهره وری بالا نتیجه ی بالا بردن کارآیی وقت است؛ به این ترتیب که تنها مهمترین کارها را انجام دهید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3289,'توانایی شما در به تعویق انداختن خوشی های کوتاه مدت، تعیین کننده میزان پیشرفت مالی شما در دراز مدت است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3290,'برای مدیریت زمان خود باید خوشبختی را هدف اصلی زندگی تان قرار دهید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3291,'پیشرفت های جزیی در استفاده بهتر از وقت می تواند در مجموع منجر به دگرگونی های اساسی در زندگی شما شود.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3292,'هنگام کار کردن، وقت تلف نکنید، بی وقفه کار کنید و از خودتان بیشتر مایه بگذارید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3293,'سطح دانش و مهارتی که در حال حاضر دارید حداکثر توان فعلی شما را تعیین می کند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3294,'ارزش هر چیزی را می توان با مقدار زمانی که حاضرید صرف آن کنید اندازه گیری کرد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3295,'توانایی شما در مدیریت زمان برای دستیابی به بالاترین نتایج، مهارت اصلی در کارآیی فردی است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3296,'آینده متعلق به افراد با قابلیت است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3297,'اکثر مردم سرگرم فعالیت هایی هستند که بیشتر آرامش بخشند تا هدفمند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3298,'بخش قابل توجهی از زمان خود را با کسانی بگذرانید که در زندگی، بیشترین اهمیت را برای شما دارند و نگذارید که در طول این مدت کسی مزاحمتان شود.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3299,'برای آنکه درآمدتان بیشتر شود باید بیشتر چیز یاد بگیرید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3300,'بدترین نوع هدر دادن وقت این است که کارهایی را که هرگز نیازی به انجام دادنشان نیست به بهترین نحو ممکن انجام دهیم.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3301,'انجام کاری که براستی از آن لذت می برید رمز عملکرد عالی است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3302,'کلید آینده رشد مداوم شخصی و حرفه ای شماست.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3303,'جمع آوری هر ثروتی با فروش خدمات فردی یا در حقیقت زمان شخصی آغاز می شود.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3304,'کلید آینده، رشد مداوم و شخصی شماست.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3305,'یک روش عالی برای موفقیت در کار این است : افکارتان را روی کاغذ بیاورید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3306,'کلید دستیابی به موفقیت بزرگ، پیروزی، احترام و خوشبختی در زندگی این است که بتوانی ذهنت را تمام و کمال روی مهمترین کار یا هدفی که داری متمرکز کنی، آن را درست انجام دهی و تا زمانی که تمام نشده، دست از کار نکشی.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3307,'مدیریت زمان در حقیقت مدیریت شخصی، مدیریت زندگی و مدیریت خودتان است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3308,'اگر افراد موفق، هم تاثیر گذارند و هم کار آمد، به این دلیل است که هم کارهای درست را انجام می دهند و هم کارهایشان را به درستی انجام می دهند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3309,'با یک مدیریت زمان عالی، کیفیت زندگی شما بهتر می شود و برای افراد و چیزهایی که از آنها لذت می برید، فرصت بیشتری به دست می آورید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3310,'برای آنکه بتوانید به مهارت های عالی در مدیریت زمان دست یابید، سعی کنید در آن واحد، تنها روی تغییر یکی از رفتار های خود تمرکز کنید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3311,'اهمیتی ندارد که از کجا آمده اید، مهم این است که به کجا می روید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3312,'داشتن اهداف روشن و از پیش تعیین شده برای پیشرفت و موفقیت مطلقا ضروری است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3313,'زمان، با ارزش ترین سرچشمه مالی و تمام موجودی شما برای خرید چیزهایی است که در زندگی خواهان آن هستید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3314,'زمان از بین رفتنی است؛ نمی توان آن را پس انداز کرد، تنها می توان آن را به روشهای گوناگون خرج کرد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3315,'مدیریت زمان بیش از هر چیز نیاز به انضباط فردی، خویشتن داری و تسلط بر نفس دارد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3316,'کیفیت زندگی شما بستگی به کیفیت مدیریت شما در استفاده بهتر از زمان دارد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3317,'به هر اندازه مهارت های خود را بالا ببرید، می توانید در مدت زمان کمتر، کارهای بیشتری انجام دهید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3318,'یکی از بهترین راههای استفاده از زمان این است که قابلیت های خود را در زمینه هایی که نتایج کلیدی را برای شما به بار می آورد، افزایش دهید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3319,'همه چیز به حساب می آید. هر کاری که انجام دهید، یا به شما کمک می کند و یا آسیب می رساند؛ هیچ چیزی خنثی نیست.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3320,'نقطه شروع تصمیم های بهتر، نقطه پایان تصمیم های بدتر است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3321,'زندگی شما تنها زمانی بهتر می شود که خودتان بهتر شوید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3322,'اینکه چه کسی باشید و در چه موقعیتی قرار داشته باشید، بر عهده شما و نتیجه انتخابها و تصمیم گیری های خودتان است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3323,'ما در رشته ای به کمال می رسیم که به آن عشق می ورزیم.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3324,'ریشه همه شکست ها و پریشانیها در احساس منفی است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3325,'احساس، نیروی برانگیزنده ذهن است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3326,'عوامل ایجاد تحول، عشق و نیاز است، اما عشق از نیاز مهمتر است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3327,'ذهن به هر حال خالی نمی ماند؛ آن را با افکار مثبت پر کنید تا پریشانی و نگرانی در آن جای نگیرد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3328,'خداوند به هر یک از ما قدرت داده است که در یک رشته به کمال برسیم که آن را حیطه کمال می گوییم.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3329,'ما باید از مرز متوسط بودن خارج شویم، ما حق نداریم متوسط باشیم.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3330,'اگر محدودیتی وجود دارد در ذهن ماست نه در دنیای خارج.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3331,'خوشبختی، تحقق تدریجی هدف و آرمانی ارزشمند است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3332,'نوآوریها به وسیله کسانی به وجود می آید که حتی در آن رشته کار نکرده اند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3333,'شما همان می شوید که درباره اش فکر می کنید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3334,'آنچه اکنون هستیم نتیجه افکار ما تا امروز است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3335,'احساس منفی، ترمز موفقیت ماست؛ ترمزهای خود را رها کنید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3336,'سرنوشت آینده ما از کوشش جسمی ما نیست، از طرز فکرها و دیدگاه های ما است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3337,'پذیرش مسئولیت مهمترین نشانه رشد و بلوغ یک انسان است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3338,'برای کامیابی همواره باید چند احساس مثبت در زندگی ما حاکم باشد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3339,'افراد ناموفق و ناخشنود همواره یک گونی از عادتهای منفی را بر پشت خویش می کشند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3340,'هیچ کس مسئول شکست ما نیست.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3341,'حدود سه درصد مردم دارای هدف هستند و تنها یک درصد مردم آن را روی کاغذ می آورند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3342,'ادیسون در اختراع 1097 پدیده، بیشترین شکست ها را تحمل کرده است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3343,'ادیسون برای پرورش گیاه ماده خام لاستیک سازی، هزار و هفتصد بار آن را تجربه کرد؛ یعنی هزار و هفتصد بار شکست خورد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3344,'ادیسون می گوید من در هر شکست توانسته ام راههای به نتیجه نرسیدن آن پدیده را کشف کنم.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3345,'راز برتری، بزرگی، افتخار و شهرت در خدمت به دیگران است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3346,'انسان نابغه به دنیا می آید، اما بنا به عادت از ذخیره خلاقیت خود استفاده نمی کند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3347,'مهمترین مهارتی که در تمام عمر خود می توانیم کسب کنیم، روابط انسانی برتر با سایر مردم است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3348,'هشتاد و پنج درصد خوشی و خشنودی ما از زندگی از رابطه نشاط انگیز با دیگران به دست می آید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3349,'هنر ارتباط برتر به استعداد و مهارت سازش با دیگران مربوط می شود.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3350,'سه درصد از درآمدتان را برای یادگیری مداوم روی خودتان سرمایه گذاری کنید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3351,'باارزش ترین سرمایه شما خودتان هستید؛ برای بالا بردن ارزش خود تمامی آموزش های لازم را فرا گیرید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3352,'هیچ کس جز خودتان نمی تواند مانع شود که در کارتان جزء بهترین ها باشید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3353,'با افراد مناسب یعنی انسانهای مثبت و هدفمندی که شما را تشویق می کنند و الهام بخش هستند، نشست و برخاست کنید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3354,'با افکاری که برای شما محدودیت ایجاد می کنند به مقابله برخیزید. بسیاری از آنها هرگز درست نیستند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3355,'آرامش فکری را بالاترین هدف زندگی خویش قرار دهید و بر اساس آن برای زندگی خود برنامه ریزی کنید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3356,'زندگی را آن گونه که هست ببینید، نه آن گونه که شما دوست دارید باشد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3357,'ایستادگی در برابر سختی ها در شما اعتماد به نفس ایجاد می کند و توانایی شما را نیز افزایش می دهد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3358,'انسان به طور طبیعی تمایل به آسانترین راه دارد. اگر در برابر این تمایل ایستادگی کنید، نیرومندتر و تواناتر می شوید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3359,'تلاش و مبارزه با مشکل های کوچکتر، توانایی مبارزه با مشکل های بزرگتر را به شما می دهد و دستاوردهای بزرگتری را نصیب شما می کند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3360,'زندگی شما بازتاب افکارتان است؛ اگر افکارتان را تغییر دهید، زندگی شما دگرگون می شود.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3361,'در هر زمینه ای هر چه بیشتر یاد بگیرید و دانش خود را بالا ببرید، اعتماد به نفس تان نیز بیشتر می شود.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3362,'تمرین و تکرار زیاد در هر زمینه ای، برگ برنده را به دست شما می دهد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3363,'راحت طلبی، دشمن شماره یک شهامت و اعتماد به نفس است.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3364,'آنچه به نظر می رسد بزرگترین مشکل زندگی شماست ممکن است تبدیل به بزرگترین موقعیت شما شود.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3365,'شما را به این جهان آورده اند تا در زندگی خود کاری شگفت انگیز انجام دهید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3366,'یاداشت هدف هایتان بر روی کاغذ، احتمال دستیابی به آنها را 1000% افزایش می دهد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3367,'هر چقدر کارتان را بهتر انجام دهید، خودتان را بیشتر دوست خواهید داشت و اعتماد به نفس تان نیز بیشتر خواهد شد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3368,'هر موفقیت بزرگی نتیجه هزاران تلاش کوچک و عادی است که مورد توجه و ستایش دیگران قرار نمی گیرد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3369,'دم را غنیمت شمارید. اگر از زمان حال استفاده کنید می توانید قدرتی را که می خواهید به دست آورید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3370,'تاریخ بشر حکایت انسانهای عادی است که بر ترس خود چیره شده و کارهای شگفت انگیزی انجام داده اند.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3371,'پیش از شروع هر کاری از خودتان بپرسید: انجام دادن یا ندادن این کار چه تاثیرات بلندمدتی بر زندگی من خواهد داشت؟((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3372,'اگر دو کار مهم را پیش رو دارید، اول کاری را که بزرگتر، سخت تر و مهمتر است، انجام بدهید. خود را موظف کنید که بلافاصله دست به کار شوید و آنقدر ایستادگی به خرج دهید تا کار تمام شود، پیش از آنکه دست به انجام کار دیگری بزنید.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3373,'یک انسان عادی اگر بتواند این عادت را در خود ایجاد کند که کارهایش را به گونه ای روشن اولویت بندی کند و کارهای مهم را به سرعت انجام دهد، می تواند از یک نابغه که زیاد حرف می زند و نقشه های عالی می کشد، اما کمتر کاری را به انجام می رساند، پیشی بگیرد.((برایان تریسی))',34,0,0,0,0,0);
INSERT INTO mysms VALUES(3374,'عطش قدرت، خطرناک ترین پدیده خود سری مدیر است.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3375,'از بین ده زن، نه زن پیش از به کار بردن تازیانه، آن را از دست مردان می گیرند.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3376,'اگر تمامی ما قدرت جادویی خواندن افکار یکدیگر را داشتیم، نخستین چیزی که در دنیا از بین می رفت عشق بود.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3377,'احساس وظیفه در کار، نیکو و در روابط، آزاردهنده است. انسان ها تشنه محبت اند نه مراقبت.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3378,'ترس از عشق، ترس از زندگی است و آنان که از عشق دوری می کنند مردگانی بیش نیستند.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3379,'زندگی خوب، زندگی شاد است. البته قصد من این نیست که اگر شما خوب باشید حتما شاد خواهید بود. منظور من این است که اگر شما شاد باشید خوب زندگی خواهید کرد.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3380,'حاضر نیستم در راه باورهایم کشته شوم، چون ممکن است برخطا باشم.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3381,'اینکه نظری را همه می پذیرند، نمی تواند دلیلی بر درست بودن آن نظر باشد. در حقیقت، با توجه به نادانی اکثریت نوع بشر، امکان نادرست بودن نظری که همگان آن را می پذیرند بیشتر است تا عکس آن.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3382,'تا چهل سالگی که مغزم خوب کار می کرد، به ریاضیات و پژوهش پرداختم. از چهل تا شصت سالگی که ذهنم ضعیف شده بود به فلسفه روی آوردم و در اواخر که به کلی مغزم کار نمی کرد به سیاست!((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3383,'راستش را بخواهید هر تقلیدی خطرناک است.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3384,'گرچه تقلید محض همیشه مردود است، از آشنایی با نثر خوب، ثمره ی بسیاری به دست می آید؛ به ویژه در پروراندن زیبایی وزن نثر.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3385,'انگیزه هایی که مردمان را به فلسفه کشانده گوناگون است. با حرمت ترین انگیزه ها میل به شناختن جهان بوده است.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3386,'فلسفه باید خطاپذیری ذهن بشر را به ما بفهماند.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3387,'ما متوجه خواهیم شد که محکمترین و تعصب آمیزترین باورهای ما آنهایی هستند که کمترین دلیل ها برای درستی شان در دست است.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3388,'اگر آدمی آنقدر به غذا بی اعتنا باشد که زرد و ناتوان شود، ما نباید او را تحسین کنیم؛ اما مردی که از آگاهی به نیازهای خویش به مرحله ی همدردی با گرسنگان رسیده باشد سزاوار تحسین ما خواهد بود.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3389,'فلسفه نباید از زندگانی شخصی بکاهد، بلکه باید بر آن بیفزاید.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3390,'اگر امید و آرزوی شما منحصر به خودتان یا کشورتان یا طبقه تان یا پیروان باورتان باشد خواهید دید که به موازات لطف و مهر و محبت شما عواطف خصمانه وجود دارد.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3391,'اگر بنا باشد که جهان از فاجعه هایی که آن را تهدید می کنند جان به در برد مردمان باید بیاموزند که دامنه ی همدردی شان را گسترده تر سازند.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3392,'همکاری جهان با فن و صنعتی که ما امروز داریم می تواند فقر را از میان بردارد و می تواند مرتبه ای از سعادت و رفاه را برای همه ی نوع بشر فراهم کند که بشر هرگز به خود ندیده است.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3393,'دلایل این ناتوانی مسخره و اندوهبار در اینکه چنان رفتار کنیم که منافع همگان حکم می کند، در امور خارجی نیست، بلکه در طبیعت عاطفی خود ماست.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3394,'مردم متوحش مردم سخت گیرند.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3395,'کمتر رخ می دهد که ترس، عمل عاقلانه ای را باعث شود و بیشتر منجر به عملی می شود که آن خطری را که ترس از آن برخاسته افزایش می دهد.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3396,'انسانی که امید و آرزویش از حدود زندگانی شخصی فراتر رفته باشد مانند انسانی که آرزوهایش محدود باشد در معرض ترس نیست.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3397,'جوهر عرفان چیزی نیست جز گونه ای قوت و عمق احساس نسبت به باورهایی که انسان درباره ی جهان دارد.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3398,'آرمانهای ما فقط با درآمیختن با جهان به ثمر می رسند و چون از جهان جدا شوند عقیم می مانند. اما آرمانی که از حقیقت بترسد یا از پیش بخواهد که جهان موافق خواهشهای او باشد توانایی این آمیزش را ندارد.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3399,'بینش نسنجیده و نیازموده برای فراهم آمدن حقیقت کافی نیست، هر چند که بسیاری از مهمترین حقایق نخست به این صورت تجلی کرده اند.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3400,'کسانی که عقلشان ضعیف است حاضر نیستند این را در مورد خودشان بپذیرند؛ هر چند همه این را در مورد دیگران می پذیرند.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3401,'در هر لحظه، یک چیز یگانه و تازه نهفته است.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3402,'خواندن آثار فلان نویسنده تنها با هدف رد کردن سخنان او، راه شناخت آن نویسنده نیست. خواندن کتاب طبیعت نیز با این باور که سراسر این کتاب خرافات است به همان ترتیب، راه شناخت طبیعت نیست.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3403,'اگر خوب زندگی کنی به حتم شاد نمی شوی، اما اگر شاد باشی خوب زندگی خواهی کرد.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3404,'بذر اندیشه و فکر بپاش، عمل و نتیجه درو کن.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3405,'گرفتاری این دنیا از این است که نادان از کار خود اطمینان دارد و دانا از کار خود مطمئن نیست.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3406,'راز شادمانی در این است: بگذارید دلبستگی‌های شما تا حد امکان گسترده باشند. بگذارید واکنش‌هایتان نسبت به چیزها و اشخاصی که به آنها دلبستگی دارید، به جای دشمنانه بودن، تا حد امکان دوستانه باشد.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3407,'زندگی کردن بدون برخی از چیزهایی که می‌خواهید، بخش ضروری شادمانی است.((برتراند راسل))',35,0,0,0,0,0);
INSERT INTO mysms VALUES(3408,'بهبودی، زمان می برد، ولی در برخی از زمانها به فرصت هم نیاز دارد.((بقراط))',36,0,0,0,0,0);
INSERT INTO mysms VALUES(3409,'فرومایگی شخص از دو چیز روشن می شود : بیهوده سخن گفتن و نپرسیده جواب دادن.((بقراط))',36,0,0,0,0,0);
INSERT INTO mysms VALUES(3410,'انسان باید خود را به دو چیز عادت دهد : جفای ایمان و ستم بشریت.((بقراط))',36,0,0,0,0,0);
INSERT INTO mysms VALUES(3411,'انسان باید در دنیا مانند میهمانی باشد که او را به ضیافت دعوت کرده اند؛ آن چه را پیش روی او آورند بخورد و آنچه ندهند درخواست نکند.((بقراط))',36,0,0,0,0,0);
INSERT INTO mysms VALUES(3412,'میانه روی را در همه چیز پاس بدار، بوئیدن هوای تازه و نظافت و ورزش روزانه را فراموش مکن، سر را خنک و پا را گرم نگه دار. دردهای جزیی خود را به مدد روزه و نه با دارو درمان کن!((بقراط))',36,0,0,0,0,0);
INSERT INTO mysms VALUES(3413,'دارو خوراک است و خوراک، دارو است.((بقراط))',36,0,0,0,0,0);
INSERT INTO mysms VALUES(3414,'هر بیماری که با دارو درمان نشود، چاقوی جراحی آن را مداوا می کند؛ اگر چاقو درمان نکرد داغ، چاره ی آن است، اگر داغ هم مؤثر نبود، دردی است کان را دوا نباشد.((بقراط))',36,0,0,0,0,0);
INSERT INTO mysms VALUES(3415,'سخن نیکو، شکارچی دلها و خط زیبا صفابخش چشمها است.((بقراط))',36,0,0,0,0,0);
INSERT INTO mysms VALUES(3416,'چهار چیز قوه بینایی را زیان رساند : طعام شور خوردن، آب سوزان بر سر ریختن، به چشمه آفتاب نگریستن و روی دشمن دیدن.((بقراط))',36,0,0,0,0,0);
INSERT INTO mysms VALUES(3417,'بهره من از دانش و حکمت تنها این بود که بر جهل خود دانا شدم.((بقراط))',36,0,0,0,0,0);
INSERT INTO mysms VALUES(3418,'همه‌ی گرفتاری‌های انسان از این است که نمی‌تواند تنها در اتاقی بنشیند، آرام گیرد و سکوت کند.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3419,'ناتوانی یک انسان، از آنجا سرچشمه می‌گیرد که نمی‌تواند برای دراز مدت با خود خلوت کند و در یک اتاق تنها بنشیند. این امر سبب می‌شود که خودِ این انسان، نه تنها پاره‌ای از دشواری‌ها، بلکه همه‌ی دشواری‌ها باشد.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3420,'احساس ارزندگی، آنچنان دلبند آدمی می‌باشد که هر کسی خواهان ستایش است؛ حتی من که این را می‌نویسم و شما که می‌خوانید.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3421,'همه‌ی ابهت مقام برای کسانی که در جستجوی معنا هستند، شکوه و بزرگی ندارد.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3422,'زمانی که در یک خطابه، کلمه های تکراری یافته می شوند و متوجه می شویم که کوشش برای درست کردن آنها، خطابه را خراب خواهد کرد، باید آنها را به جای خود باقی گذاشت، این کلمه ها نشانه و نمایانگر خطابه اند.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3423,'زندگانی در صورتی با نیکبختی و خوشبختی همراه است که با عشق آغاز شود و با جاه طلبی و بلندپروازی به پایان رسد.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3424,'آیا می خواهید حرفهای شما را باور کنند؟ از خود سخن نگویید.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3425,'دل منطقی دارد که عقل از آن بی خبر است.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3426,'ما دوست نداریم که دیگران ما را فریب دهند، ولی از فریب دیگران هم عبرت نمی گیریم.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3427,'اندیشه و تفکر، پشتوانه ای بزرگ در سراسر حیات بشر است و انسان بی اندیشه و بی تفکر به ماده ای بی روح می ماند.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3428,'تمامی شأن و عظمت انسان در اندیشه ی اوست.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3429,'سرچشمه ی همه فسادها بیکاری است؛ شیطان برای دست های بیکار، کار پیدا می کند.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3430,'هرچه اندیشه ی شما بزرگ باشد به همان اندازه بیشتر به اندیشه های دیگران احترام می گذارید.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3431,'سعادتمند کسی است که در هجوم و مقابله با سختی ها خود را نبازد.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3432,'محبت برتر از عقل است و بنیاد دانش و یقین در روشنی قلب است.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3433,'ثروت فرد دانا، همان علم و دانش اوست.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3434,'تمامی مقام و بزرگی انسان در اندیشیدن است.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3435,'متولد شدم و ندانستم برای چه؟ زندگی کردم و ندانستم چگونه زندگی کنم؟ اکنون می میرم و نمی دانم چرا؟((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3436,'ریشه تمام مفاسد، بیکاری است. هر حکومت و دولتی که بخواهد این [ مشکل ] را حل کند، باید تا حد امکان مردم را به کار گمارد.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3437,'اگر مردم می دانستند که پشت سر یکدیگر چه می گویند، به طور حتم چهار نفر دوست در دنیا باقی نمی ماند.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3438,'باید اندازه ی خود را بشناسیم؛ آری ما در این دنیا چیزی هستیم، ولی نه همه چیز.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3439,'باید کار خود را بر مبنای خوب اندیشیدن متکی سازیم.((بلز پاسکال))',37,0,0,0,0,0);
INSERT INTO mysms VALUES(3440,'کسی که حفظ جان را مقدم بر آزادی بداند، لیاقت آزادی را ندارد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3441,'ازدواج مثل شهر محاصره شده است. کسانی که داخل شهرند سعی می کنند از آن خارج و آنها که خارج [ آن ] هستند، تلاش می کنند تا به آن داخل شوند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3442,'کسی که امیدی را وسیله کسب درآمد خود قرار دهد، ممکن است از گرسنگی بمیرد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3443,'آب قطره قطره می چکد و با پایداری و سماجت، سنگ بزرگ را سوراخ می کند؛ موش با پشتکار و استقامت موفق به پاره کردن رشته محکمی از سیم می شود و ضربه های پی در پی تبری کوچک، درخت کهن را از پای در می آورد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3444,'دهان خردمند در قلب اوست.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3445,'حرف حق بر شما تاثیر خواهد گذاشت، حتی اگر آن را قبول نداشته باشید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3446,'عروس زندگانی نامش کار است. اگر شما داماد این عروس شوید، فرزند شما سعادت نام خواهد داشت.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3447,'پیش از ازدواج چشمهایتان را خوب باز کنید و بعد از ازدواج آنها را کمی روی هم بگذارید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3448,'فقیر برای سیر کردن شکمش در عذاب است و ثروتمند برای معالجه ناراحتی.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3449,'کسانی را می شناسیم که برای آرایش ظاهری و داشتن لباسی گرانمایه، شکم خود را گرسنه نگاه داشته و خانواده خود را در تنگدستی می گذارند و برای فریب دادن چشم دیگران، خود را دچار رنج و محنت می کنند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3450,'سه چیز خیلی سخت است : فولاد، الماس و خود شناسی.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3451,'اشخاص زیرک از بدبختی ها و گرفتاری های دیگران آگاه و بیدار می شوند، ولی اشخاص نادان از بلاهای خود عبرت نمی گیرند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3452,'یک عمر طولانی ممکن است به اندازه کافی خوب نباشد، ولی یک زندگی مفید حتماً به اندازه کافی طولانی است.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3453,'اگر می خواهی رازی را از دشمنان پنهان بداری، آن را هرگز به دوستانت فاش مکن.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3454,'اگر به تغییرات پایان دهید، زندگی خود را پایان داده اید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3455,'شما ممکن است تعلل کنید، اما زمان هرگز.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3456,'اگر می خواهی پس از مرگ فراموش نشوی، یا چیزی بنویس که قابل خواندن باشد، یا کاری کن که قابل نوشتن باشد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3457,'تلاش و پشتکار و میانه روی، برای انسان ایجاد خوشبختی خواهد کرد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3458,'آن چه با خشم آغاز می شود، با شرم به پایان می رسد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3459,'تجربه مدرسه ای است که دانش آموزان خود را با بهایی گران بار می آورد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3460,'دشمنان خود را دوست بدارید؛ چرا که آنها موارد ناتوانی‌تان را به شما می‌گویند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3461,'استعدادهای خود را پنهان نکنید. استعداد برای این خلق شده است که به کار گرفته شود. گذاشتن ساعت آفتابی در سایه چه فایده ای دارد؟((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3462,'فراموش نکنید که نه تنها باید سخن درست را در جای درست بیان کنید، بلکه دشوار‌تر آن است که در جایی وسوسه‌انگیز، جلوی زبان خویش را بگیرید و سخن نادرستی نگویید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3463,'بیشتر بدبختی انسانها ناشی از این است که قیمت و ارزش واقعی چیزها را نمی دانند و قیمتی بیشتر از ارزش واقعی آن را پرداخت می کنند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3464,'وقتی شما در رابطه با دیگران مهربان و خوب هستید بیشتر از همه به خودتان خوبی می کنید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3465,'آنان که آزادی را فدای امنیت می کنند، نه شایستگی آزادی را دارند و نه لیاقت امنیت را.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3466,'هیچ وقت به گمان اینکه وقت دارید ننشینید، زیرا در عمل خواهید دید که همیشه زمان کم و کوتاه است.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3467,'انسان، محل اشتباه و فراموشکاری است.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3468,'یک امروز، ارزشِ دو فردا را دارد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3469,'نصیحت؟ عاقل ها به آن احتیاجی ندارند و احمق ها آن را نمی پذیرند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3470,'دشمنانت را خوب گوش بسپار که هیچ کس بهتر از آنها عیبهای تو را گوشزد نمی کند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3471,'دشمن کوچک وجود ندارد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3472,'آنجا که ازدواجی بدون عشق صورت می گیرد به یقین، عشقی بدون ازدواج در آنجا رخنه خواهد کرد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3473,'اگر مردم با این همه پول که در راه جنگ خرج می کنند زمین بخرند، ارزانتر از زمین هایی می شود که به وسیله ی جنگ و خونریزی به دست می آورند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3474,'با یک لبخند، همه ی درها به سوی انسان گشوده خواهد شد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3475,'زندگی راستین شما زمانی است که کاری برای کسی انجام دهید که توان جبران محبت شما را نداشته باشد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3476,'پیش از آنکه دنبال هوی و هوس بروی، به کیسه پول خودت نگاه کن.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3477,'برای ثروتمند شدن تنها دانستن راه درآمد کافی نیست، باید راه خرج کردن را هم به خوبی دانست.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3478,'زود خوابیدن و زود برخاستن، مرد را سلامت، ثروتمند و عاقل می کند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3479,'کسی که ثروتی ندارد، خود را دارد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3480,'تجربه به من ثابت کرده است که در طول بیست و چهار ساعت، کسی نیست که دست کم پنج دقیقه گرفتار حماقت نشود. فرد عاقل و خوش بخت کسی است که نگذارد این حماقت از این میزان بیشتر شود.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3481,'ارزش چه چیزی بیشتر از طلاست؟ الماس، چه چیزی از الماس گرانبهاتر است؟ اراده!((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3482,'انسان می تواند دو دقیقه بدون هوا، دو هفته بدون آب، سه هفته بدون غذا و یک عمر بدون پول زندگی کند، ولی نمی تواند یک لحظه بدون اندیشه و فکر زندگی کند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3483,'آنکه از شنیدن رفتار نیک همسایه اش رنج می برد، از شنیدن عکس آن شاد می شود و آنان که از برتری دیگران ناامید هستند، از اینکه دیگران به حدی کوچک شوند که با آنها برابر گردند، شاد خواهند شد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3484,'اگر به شما عشق ورزیده می شود، عشق بورزید و شایسته این عشق باشید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3485,'در زیر خاک به قدر کافی زمان برای خوابیدن داریم. اگر زمان گرانبهاترین چیزهاست، بیهوده صرف کردن آن بدترین کارهاست. زیرا زمانی که از دست رفت دیگر باز نخواهد گشت. هیچ وقت به گمان آنکه زمان کافی دارید، ننشینید. در عمل خواهید دید که همیشه زمان، کوتاه و ناچیز است.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3486,'نیکی مقدس ترین وظیفه انسان است؛ بویژه [ نسبت ] به کسانی که روزی از آنها بدی دیده است.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3487,'رشته محکم سیم موفق می شود و ضربات پی در پی تبر کوچک درخت کهن را از پای در می آورد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3488,'بی نظمی در کار بدتر از بی خبری از آن است. در کار های زندگی باید به خود اعتماد داشت و بس.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3489,'پنبه را از آتش و جوان را از قمار دور نگه دارید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3490,'از هزینه های کوچک و غیر ضروری دوری کن. زیرا یک سوراخ کوچک موجب غرق شدن یک کشتی بزرگ می شود.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3491,'چشم دیگران چشمی است که ما را ورشکست می کند. اگر همه غیر از خودم کور بودند، من نه به خانه باشکوه احتیاج داشتم و نه به مبل عالی.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3492,'وقتی شما نسبت به دیگران مهربان هستید، بدین معناست که نسبت به خودتان مهربانترید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3493,'تنبلی آنقدر آهسته راه می رود که فقر و خواری برای رسیدن به آن دچار مشکل نمی شوند.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3494,'برای پیدا کردن عیبهای یک دختر، از او در نزد دوستانش تعریف کنید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3495,'خردمند کیست؟ کسی که از همه می‌آموزد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3496,'چه کسی توانگر است؟ کسی که به سهم خود خرسند باشد.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3497,'دارایی آن نیست که داری، بلکه چیزی است که با آن خوش باشی.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3498,'اگر آدمی بتواند نیمی از آرزوهای خود را برآورده سازد، دو برابر بر گرفتاری هایش می افزاید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3499,'سحرخیزی یک گنج است.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3500,'اگر انسان، سرمایه‌اش را در مغزش بگذارد، نه در کیف پولش، هیچ‌کس نمی‌تواند آن را از او برباید.((بنجامین فرانکلین))',38,0,0,0,0,0);
INSERT INTO mysms VALUES(3501,'آنکه خوشی خود را در رنج دیگران ببیند، هرگز روی خوشی را نمی بیند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3502,'نفرت، ویران می‌کند؛ عشق، شفا می‌دهد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3503,'هیچ‌گاه با نفرت نمی‌توان نفرت را زدود؛ نفرت، با عشق زدوده می‌شود؛ این، قانونی برگشت‌ناپذیر است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3504,'هر آنچه که ما هستیم، نتیجه‌ی آن چیزی‌ست که بدان اندیشیده‌ایم.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3505,'برای اینکه هیچ‌گاه دچار درد و رنج نشوی، به هیچ چیز به عنوان من و یا مال من نچسب.((یک استاد بودایی))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3506,'هیچ چیز را باور نکنید؛ مهم نیست آن را کجا می‌خوانید یا چه کسی آن را گفته است؛ حتی اگر من، آن را گفته باشم، مگر این که با منطق و احساس شما هماهنگی داشته باشد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3507,'آدمی که از سر نادانی، با من به نادرست رفتار کند، از جوانمردانه‌ترین عشق من بهره‌مند خواهد شد؛ و هرچه بیشتر به من شر رساند، خیر من بدو بیشتر خواهد رسید.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3508,'کینه هرگز کینه را بازنایستاند: قاعده‌ی گذشته اینکه عشق، کینه را باز ایستاند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3509,'آن که اندیشه آموخته است، چون گاو کهنسال گردد، تنش ببالد، ولی دانش او نه.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3510,'جهان نداند که اینجا همه به فرجام خواهیم رسید؛ ولی آنها که این بدانند، ستیزه‌هایشان بی‌درنگ بازایستد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3511,'آنان که راستی را در ناراستی پندارند و ناراستی را در راستی بینند، هرگز به راستی نرسند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3512,'رها کردن را بیاموزید که کلیدخوشبختی است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3513,'هر کس زنجیر طمع را دور بیندازد، بدبختی از او دور می شود؛ مانند قطره آب که روی برگ گل می لغزد و می ریزد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3514,'اگر به دنیای درونت حکومت کنی، خوشبخت هستی.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3515,'باران همیشه از سقف های سست به داخل اتاق می چکد و شهوت از مغز پوک.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3516,'یار اگر نادان باشد، تنهایی بهتر است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3517,'یک روز زندگی به روشن بینی، بهتر از صد سال عمر در تاریکی است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3518,'آنکه در درون آرامش دارد، پندار و گفتار و کردار آرام نیز دارد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3519,'مهم نیست که دیروز چقدر بر شما سخت گذشته است؛ همیشه می توانید از نو آغاز کنید.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3520,'اعتبار، درست مثل شهرت است؛ سخت و دیر به دست می آید، آسان و زود از دست می رود.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3521,'احسان کننده خود را تملق می گوید.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3522,'نپذیرید، باور نکنید، از هیچ‌کس، حتی از من، مگر آنکه با خرد بی‌گزند خودتان در هماهنگی و سازگاری باشد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3523,'آزادی در بی آرزویی است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3524,'همچنان که کماندار، تیرهای خود را می تراشد و هموار می کند، هر انسانی می تواند افکار آشفته خود را جهت دهد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3525,'اشک های دیگران را مبدل به نگاه های پر از شادی نمودن، بهترین خوشبختی ها است.((گوتاما بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3526,'آنچه به روح آرامش می بخشد همانا دانش است و بس.((گوتاما بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3527,'هستی ما به ناپایداری ابرهای پاییز، تماشای تولد و مرگ موجودات، همچون نظاره ی شعله های آتش یک عمر، مانند جرقه ی رعدی در آسمان و چون سیلابی پر شتاب و روان از سراشیبی کوهی [است].((گواتما بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3528,'آنان که به راه قدسی نرفته و در جوانی گنجی اندوخته نکرده اند، مرغان ماهیخوار پیر را مانند که در دریاچه ای بی ماهی از میان می روند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3529,'هیچ کس جز خود ما مسئول بدبختی ها و یا خوشبختی های ما نیست.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3530,'در تن نحیف می تواند اندیشه ای بزرگ مسکن گیرد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3531,'حیات بشر همچون شبنمی است که روی برگ گلی می لغزد و می افتد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3532,'چشم گریان دیگران را به نگاههای سرور آمیز تبدیل کردن، لذت بخش ترین خوشبختی ها است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3533,'آرامش فکر، بزرگترین خوشبختی است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3534,'آن‌کس که تنها در پی خوشی‌ها زید، حواسش لجام‌گسیخته باشد و خوراکش بیرون از اندازه‌ی میانه‌روی باشد و تنبل و سست باشد، مارَه (وسوسه‌گر) بی‌تردید او را بیفکند، چنان که باد، درخت سست را بیفکند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3535,'همچنان که باران از سقفِ سوراخ در خانه تراود، هوس نیز در ذهنی که نیندیشد، راه یابد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3536,'بدکار در این جهان شیون کند و در جهان دیگر نیز گریه کند که در هر دو جهان ناله و زاری کند. چون نتیجه‌ی بدِ کارهایش را ببیند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3537,'نکوکار در این جهان شاد است و در جهان دیگر نیز شاد است که در هر دو جهان، شادمان است. شادمان است چون به نکوکاریهایش بیندیشد و چون به راه نیک رود، شادمان‌تر گردد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3538,'نااندیشمند گرچه بهره‌ی بزرگی از قانون را از بر بخواند، ولی بر آن کار نکند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3539,'آرزومندان نمیرند، نااندیشمندان، خود، گویی مرده‌اند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3540,'اگر آرزومندی بیدار گشته باشد و فراموشکار نباشد و بنابر داد زيَد، شکوه‌اش فزونی گیرد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3541,'خردمند با بیدار کردن خوداشتیاقی و خویشتنداری، تواند برای خویش جزیره‌ای سازد که هیچ سیلی آن را غرق نتواند کرد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3542,'نادانان از پی بیهودگی روند، خردمند اشتیاق را چون گرانبهاترین گوهر خویش نگاه دارد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3543,'آن‌کس که آرزومند و اندیشمند بود، به خوشی بسیار رسد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3544,'رام کردن اندیشه، که گریزپا است و گرفتن آن دشوار است و هرجا خواهد شتابان رود، نیک بُوَد، که اندیشه‌ی رام، شادمانی آورد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3545,'گر کسی را ایمان پابرجا نباشد و داد راستین نداند و ذهنش آشفته باشد، هرگز دانش وی کمال نپذیرد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3546,'کینه‌ور [ =کینه دار ] به کینه‌ور و دشمن به دشمن، آن بد نتواند کرد که اندیشه‌ی نیک راهنمون شده به كَس کند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3547,'بگذار فرزانه در روستایش مسکن کند، چون زنبور عسل که شهد فراهم آرد و رود، بی‌آنکه گل یا رنگ و بوی آن را گزند رساند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3548,'فرزانه نباید به گژیهای [ =کجی ] دیگران، گناهان یا کوتاهی‌هایشان پردازد، بلکه باید به بدکاریها و ناآگاهی های خویش بنگرد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3549,'آن که سخنان نیک، ولی بی‌ثمر گوید و به آنها عمل نکند، چون گل زیبا و رنگارنگ است که عطر ندارد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3550,'بوی گل یا چوب صندل یا گل‌های تگره و مَلّیکا ( که از این دو گل، گرد خوشبوی سازند) خلاف وزش باد نرود، ولی بوی خوش نیکان، خلاف باد نیز رود؛ که نیک مرد همه جا را فرا گرفته است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3551,'گر راهرو بهتر از خویش یا هم‌ارز خود را به راه نبیند، همان بهتر که تنها رود، چه نادان به همراهی نَيَرزَد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3552,'نادان که داند نادان است، دستِ‌کم تا همین اندازه بخرد است. ولی نادانی که خود را دانا انگارد، به راستی، نادان خوانده شود.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3553,'گر نادان به همه‌ی عمر نیز همنشین دانا گردد، زود باشد که راستی را دریابد، چنان که با زبان، مزه ی آتش را بچشد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3554,'آن‌کس که شادی خویش جوید و باشندگانی را که شادی آرزو کنند شکنجه کند یا كُشَد، پس از مرگ، شادی نیابد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3555,'چون شبانی که به چوبدستی خود، گاوان را سوی اصطبل رانَد، سال و مرگ نیز زندگی مرمان را رانَند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3556,'نادان نداند چه هنگام کارهای بد کند، ولی بدکار، کردار خویش گویی به آتش می‌سوزاند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3557,'گردونه‌های درخشان شهریاران تباه گردند، تن نیز به تباهی رسد، ولی برتری نیکان هرگز سوی نابودی نرود و نیکان، نیکان را چنین گویند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3558,'آنچه کس از کردنش پشیمان گردد، نیک نیست و پادافراه [ =سزای ] وی شیون و چهره‌ی اشکبار باشد. آن کرده‌ی نیک است که کس از آن پشیمانی نخورد و پادافراه آن شادمانه و شادمان نبیند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3559,'تا کار بد بَر ندهد، نادان اندیشد بَر او چون انگبین است، ولی چون بَر دهد، نادان غم خورَد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3560,'بگذار خردمند پند دهد و آموزش دهد و آنچه را ناشایست است، از آن بازدارد. فرزانه، محبوب [ =پسند ] نیکان باشد و منفور [ =ناپسند ] بدان.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3561,'بدکاران را یار مگیر و دوست خسان [ =فرومایگان ] مباش. با نیکان دوستی کن و بهترین مردمان را دوست گیر.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3562,'مقنیان [ =چاه کن ها ] هرجا خواهند آب برند؛ کمانگیران تیر در کمان كُنند؛ درودگران [ =نجاران ] از چوب چیزها كَنند؛ خردمندان خود را سازند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3563,'چنان که سنگ استوار را باد نلرزاند، خردمندان نیز از سرزنش و ستایش، تردید به دل راه ندهند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3564,'برخیز! سُستی مکن! پی دادِ راستی رو! راستان در این جهان و جهان دیگر به شادی بیارمند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3565,'نیکان در همه حال با دوراندیشی گام بردارند؛ نیکان از برای برآوردن خواهش شهوانی، سخن نگویند؛ خردمندان در غم و شادی هرگز به هیجان نیایند و افسرده نگردند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3566,'گر کس از برای خویش یا دیگران، پسر، ثروت، و سروری آرزو نکند و پیروزی خویش را به شیوه‌های نادرست نخواهد، نیک، خردمند و پارسا باشد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3567,'یک کلمه‌ی با معنی [ =با چَم ]، بهتر از هزار کلمه‌ی بی‌معنی است که آن کلمه‌ی بامعنی را گر کسی شنود، خاموش گردد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3568,'بر خود چیره شدن، بهتر از چیرگی بر همه‌ی مردمان است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3569,'اگر کس، صدهزار سال، هر ماه هزاران بار قربان کند و اگر یک دم کسی را بزرگ دارد که دانش راستین پابرجای دارد، آن بزرگداشت، بهتر از صد سال قربان کردن است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3570,'هرچه کسی در این جهان در یک سال، پیشکش و نثار قربان کند تا مُزد یابد، این همه پشیزی نیرزد؛ بزرگداشت پارسایان بهتر است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3571,'آن که صد سال بدکار و ناخویشتندار زيَد، زندگی یکروزه‌ی مرد فرزانه و اندیشمند بهتر از آن صد سال است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3572,'آن که صد سال تنبل و سست زيَد، زندگی یکروزه‌ی آن که نیروی استوار یافته، بهتر از آن صد سال است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3573,'آن که صد سال زيَد و آغاز و انجام را نبیند، زندگی یکروزه‌ی آن که آغاز و انجام را بیند، بهتر از آن صد سال است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3574,'آن که صد سال زيَد و برترین داد را نبیند، زندگی یکروزه‌ی آن که برترین داد را بیند، بهتر از آن صد سال است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3575,'اگر کس در انجام نیکی سُستی ورزد، اندیشه‌اش از بدی شادمان گردد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3576,'اگر کس گناه کند، مگذار دیگر چنان کند؛ مگذار به گناه کردن، دل خوش دارد که انباشتن بدی، دردناک است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3577,'بدکار تا کردار بَدش بَر ندهد، شادی بیند، ولی چون کردار بدش بَر دهد، بدی بیند. نیکوکار نیز تا کردار نیک او بَر ندهد، بدی بیند، ولی چون کردار نیک او بَر دهد، نیکی بیند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3578,'مگذار كَس، نیکی را خُرد انگارد و در دل گوید مرا نخواهد رسید. ظرف آب نیز از ریزش قطرات آب پُر گردد. دانا پُر از نیکی گردد، گرچه اندک اندک نیکی خویش را گرد آورد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3579,'آن که دستش زخمی نباشد، شاید به زهر دست زند، ولی زهر آن را که زخمی نباشد، اثر نکند و او را که بدی نکند، بدی نباشد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3580,'نه در آسمان، نه در میان دریا، نه در شکاف کوه، در جهان هیچ‌جا نیست که شاید آدمی از کردار بد رها باشد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3581,'همه‌ی مردم از جزای کار بد بر خود لرزند و همه از مرگ هراسند. به یاد دار که تو نیز چون آنانی. مَكُش و مایه‌ی كُشتار مَشو.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3582,'مگذار كَس، بدی را خُرد انگارد و در دل گوید مرا نخواهد رسید. ظرف آب نیز از ریزش قطرات آب پُُر گردد. نادان پُر از بدی گردد، گرچه اندک اندک بدی خویش را گرد آورد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3583,'با هیچ‌کس به دُرُشتی سخن مگوی، چه آنان که با ایشان سخن گویی، تو را همچنان پاسخ گویند. گفتار خشم‌آمیز دردناک است: ضربات یکی پس از دیگری بر تو فرود آید.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3584,'بگذار هرکس خود را سوی آنچه درست است، بَرَد و آنگاه بگذار دیگران را بیاموزد؛ این چنین، فرزانه آزرده نگردد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3585,'اگر کسی دیگران را بیاموزد تا چون او گردند، چون اختیار خود را دست گیرد، اختیار دیگران را دست تواند گرفت، چه اختیار خود را دست گرفتن، دشوار است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3586,'انجام کارهای بد آسان است و این کردارها خود را گزند رسانند. کردارهایی نیک و سودمندند که انجام‌شان بس دشوار است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3587,'به جهان چنان نگر که به حباب یا سراب نگری، شهریار مرگ آن را که به جهان چنین نگرد، نبیند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3588,'آن که پیش از این به خطرها نمی‌اندیشید و سپس هشیار گشت، این جهان را روشنی بخشد، چون ماه که از پشت ابرها بیرون آید.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3589,'تنها، نادانان، بخشندگی را نستایند. خردمند از بخشش شاد گردد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3590,'هیچ گناه نکردن، نیکی کردن، اندیشه‌ی خود پاک گرداندن، آموزه‌ی همه‌ی بیداران است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3591,'از پیروزی دشمنی زاید، چه شکست خورده، ناشاد است. آن که پیروزی و شکست را واگذارد، خرسند و شادمان است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3592,'هیچ چیز چون دشمنی، بازدارنده از رهایی نیست.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3593,'شادی، برتر از آرامش است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3594,'گرسنگی، بدترین ناخوشی‌ها است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3595,'آن که شیرینی تنهایی و آرامش را چشید، از ترس و گناه رسته [=رهایی یافته] است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3596,'دیدار گزیدگان، نیک است و با آنها به‌سر بردن، همواره شادی بخش است. اگر کس نادانان را نبیند، راستی شادمان است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3597,'آن که همراه نادانان شود، به راهی دراز رنجه گردد. همراهی نادانان چون همراهی دشمنان همواره دردناک است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3598,'مگذار هیچ‌کس به آنچه خوش یا آنچه ناخوش است، دل بندد. ندیدن خوشی درد است و دیدن ناخوشی نیز درد است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3599,'باشد که آدمی خشم خویش فرونشاند و خودخواهی را فراموش کند و از بُن رهایی یابد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3600,'آن که در نام و صورت نباشد و هیچ چیز را از آن خویش نخواند، آزرده نگردد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3601,'آن که خشم خویش را که چون گردونه‌ای شتابان پیش رود، بازدارد، او را گردونه‌ران راستین خوانم؛ دیگران تنها زمام به دست دارند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3602,'راست گو و تسلیم [=گردن نهادن] خشم مشو؛ اگر از تو اندک خواهند، بده.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3603,'فرزانگان که تن و زبان و اندیشه نگاه دارند، به راستی خویش را نیک نگاه داشته‌اند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3604,'آفت دعاها، باز ناگفتن است. آفت خانه‌ها، بازنساختن است. آفت منش، تنبلی است. آفتِ نگاهبان، بی‌فکری است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3605,'بدرفتاری، آفتِ زن است. فرومایگی، آفت نیکوکاران است. در این جهان و آن جهان، همه‌ی روش‌های بد، آفت است، ولی آفتی هست که بد‌ترین آفت‌هاست و آن، نادانی است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3606,'آتشی چون هوس نیست، کوسه‌ای چون دشمنی نیست، دامی چون نادانی نیست و سیلی چون آز نیست.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3607,'کسی که بدیهای همسایه‌اش را چون گندم وجین شده [=پاک شده ار علف هرز] در برابر باد بالا می‌افکند، ولی بدیهای خویش می‌پوشد، چون نیرنگ‌بازی است که مُهره و طاس دروغین از بازیگر دیگر پنهان دارد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3608,'اگر کس در انجام کاری دُرُشتی [=پرخاش] کند، دادگر نیست.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3609,'کس به سبب پُر سخنی، دانا نیست. دانا کسی است که شکیبا و رها از دشمنی و ترس باشد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3610,'کس به سبب پُر سخنی، پشتیبان قانون نیست. اگر کسی اندک آموزد، ولی به چشم سر، قانون را بیند و هرگز آن را به فراموشی نسپارد، چنین کسی پشتیبان قانون است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3611,'در آن کس که راستی، نیکویی، آمرزش، پاکدامنی، خویشتنداری، میانه‌روی، بی‌آلایشی و خردمندی باشد، چنین کسی پیر است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3612,'آن‌کس که رشک ورزد، خسیس و نادرست باشد، تنها به مِس سخنی یا دیدار زیبا، اَرجمند نگردد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3613,'دانش، به شوق به دست آید و بی‌آن، از دست بشود.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3614,'تا آرزوی مردان نسبت به زنان، حتی اندک ترین آنها، از میان نرود، اندیشه‌ی مرد در بند است، چنان که گوساله که شیر مادرش خورد، به او وابسته است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3615,'اگر کسی با وانهادن خوشی اندک، خوشی بزرگ بیند، پس فرزانه نیز شاید که خوشی اندک وانهد و به خوشی بزرگ نِگَرَد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3616,'آن‌کس که مایه‌ی رنج دیگران گردد تا از برای خویش خوشی خواهد، در بند دشمنی گرفتار است و از دشمنی رهایی نیابد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3617,'آنچه باید انجام شود، به فراموشی سپرده شده و آنجه نباید انجام گیرد، انجام شده است. آرزوهای سرکشان و بی‌تدبیران همواره رو به فزونی دارد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3618,'آن که نیک باورمند باشد، گر از نیکی و شکوه برخوردار باشد، هرجا سکنی گزیند همچنان گرامی باشد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3619,'نیکان از دور بدرخشند، چون کوههای پوشیده از برف. بَدان دیده نشوند، چون تیرهایی که به شب اندازند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3620,'فرو بردن گوی آهن گداخته به کام بهتر است تا کسی بد و گناهکار با خیرات و صدقه ی (درستکاری آیینی مردمان در برابر روحانیان گدا) مردمان به‌سر برد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3621,'چونان برگ علف که اگر درست به کف گرفته نباشد، دست را ریش کند، پارسایی ناراست نیز دیندار و پارسا را به دوزخ برد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3622,'کار بد بهتر آنکه انجام نپذیرد، چه پشیمانی آورد. کار نیک بهتر آنکه انجام گردد، چه کس از انجام آن پشیمانی نخورد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3623,'آنان که از آنچه نباید شرمگین باشند، شرم دارند و از آنچه باید شرمگین باشند، شرم ندارند، اینان در پی آموزه‌های دروغین بوده و به راه بد روند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3624,'آنچه را پیش و پس و میان است، وا نِه و آنگاه سوی دیگر کران هستی رو. اگر اندیشه‌ات همه آسوده باشد، دیگر به زادن و تباهی درنیایی.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3625,'بی‌دانش، ژرف‌اندیش نباشد و بی‌ژرف‌اندیشی، دانش نباشد.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3626,'ای نادان، گیسوی بافته را چه سود! جامه‌ی پوست بُز را چه سود؟ تو را در اندرون، میل به غارت و دریدن است، ولی بیرون‌ خود را پاک نشان می‌دهی.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3627,'اگر کس در بند تردید، سرشار از آرزوهای شدید و تنها آرزومند خوشی باشد، تشنگی وی فزونی گیرد و به راستی بندهای خویش گرانتر کند.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3628,'علف هرز، کشتزار را زیان دارد و دشمنی، آدمی را گزند رساند؛ از این رو هدیه‌ای که ارزانی کسانی گردد که دشمنی نورزند، آنان را پاداش بزرگی است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3629,'می‌خواهم در این دنیای تاریک، طبلی به آواز در بیاورم که از این پس دیگر آگاه کردن از مرگ به گوش نرسد.((گوتاما بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3630,'رهایی قطعی هر کس در نابودی است.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3631,'تنها راه برقراری آرامش در زمین، این است که بیاموزیم زندگی خود را سرشار از آرامش سازیم.((بودا))',39,0,0,0,0,0);
INSERT INTO mysms VALUES(3632,'مشتریان ناراضی همیشه موجب نگرانی هستند. آنها در عین حال فرصت طلایی شما هستند.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3633,'بهترین کاری که می کنم این است که اشتیاقم را با دیگران قسمت می کنم.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3634,'در زندگی، همه چیز دادگرانه نیست؛ بهتر است با این حقیقت کنار بیایید.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3635,'دنیا برای شرافت شما اهمیتی قائل نیست. در این دنیا از شما انتظار می رود که پیش از آن که نسبت به خودتان احساس خوبی داشته باشید، کار مثبتی انجام دهید.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3636,'پس از فارغ التحصیل شدن از دبیرستان و استخدام، کسی به شما رقم بسیار زیادی پرداخت نخواهد کرد. به همین ترتیب، پیش از آن که بتوانید به مقام معاون ارشد، با خودرو مجهز و تلفن همراه برسید، باید برای مقام و مزایایش زحمت بکشید.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3637,'اگر فکر می کنید آموزگارتان سخت گیر است، بسیار در اشتباه هستید. پس از استخدام شدن متوجه خواهید شد که رئیس شما خیلی سخت گیرتر از آموزگارتان است؛ چون امنیت شغلی آموزگارتان را ندارد.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3638,'آشپزی در رستورانها با غرور و شأن شما در تضاد نیست. پدر بزرگ های ما در مورد این کار نظر دیگری داشتند، از نظر آنها این کار، یک فرصت بود.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3639,'اگر در کارتان موفق نیستید، پدر و مادر خود را سرزنش نکنید، از نالیدن دست بکشید و از خطاهای خود درس بگیرید.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3640,'پیش از آنکه شما متولد بشوید، پدر و مادر شما هم جوانان پرشوری بودند و تا به آن اندازه که اکنون به نظر شما می رسد، موجب آزردگی خاطر نبودند.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3641,'ثروتمندترین مرد دنیا دارنده ی چیزهای ملموس نیست. نه زمین دارد، نه طلا، نه نفت، نه کارخانه، نه فعالیت صنعتی دیگر و نه ارتش و نه سپاهی. این نخستین بار در تاریخ بشر است که ثروتمندترین مرد دنیا تنها دانش دارد و بس.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3642,'هر گاه خبرهای بد را به عنوان یک نیاز به تغییر و نه یک خبر منفی پذیرفتید، شما از آن شکست نخورده اید، بلکه چیزهای تازه ای از آن آموخته اید.((بیل گیتس))',40,0,0,0,0,0);
INSERT INTO mysms VALUES(3643,'غرور می‌تواند برانگیزاننده ی خوبی باشد. پول نیز می‌تواند، ولی هرگز نباید آنها را هدف بدانیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3644,'انسان باید سرنوشت خود را برگزیند، نه اینکه آن را بپذیرد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3645,'برای ایمان داشتن باید حصار پیشداوری ها را برچید.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3646,'دو اشتباه بسیار بزرگ وجود دارد: یکی اینکه قبل از موعد اقدام به عمل کنیم و دیگری اینکه فرصت مناسب را از دست بدهیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3647,'انسان زمانی خود را می شناسد که به مرزهای خود برسـد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3648,'افتادن از طبقه ی سوم همان اندازه صدمه می زند که افتادن از طبقه ی صدم؛ پس اگر قرار بود بیفتم، چه بهتر که از فاصله ی زیادی بیفتم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3649,'قلب آدمها گاهی شکوه می کند، چرا که آدمها می ترسند بزرگترین رؤیاهایشان را برآورده کنند، به این دلیل که یا فکر می کنند لیاقتش را ندارند و یا اینکه نمی توانند از عهده ی آن برآیند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3650,'ما قلبها از ترس می میریم؛ تنها از اندیشیدن به عشق های مدفون شده و یا لحظه هایی که می توانستند خیلی زیبا باشند و نبودند، یا گنج هایی که می توانستند کشف شوند، اما برای همیشه در زیر خاک مدفون ماندند. چون اگر هریک از این پیشامدها رخ هد، ما رنج وحشتناکی می کشیم؛ قلب من از رنج کشیدن می ترسد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3651,'همیشه به قلبت بگو که ترس از رنج از خود رنج بدتر است. تاریکترین لحظه ی شب لحظه ی پیش از برآمدن آفتاب است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3652,'لازم نیست آدم از کوهی بالا رود تا بفهمد بلند است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3653,'کشتی در ساحل بسیار امن تر است، اما برای این [ کار ] ساخته نشده است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3654,'این ما هستیم که سرعت گذر زمان را تعیین می کنیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3655,'روحی که در پیکر ما وجود دارد، کم و بیش تکامل نیافته است. با هر گامی که در فراز و نشیب زندگی می نهیم، در پی ذرات گم شده ی وجود خویش در کاوشیم. اگر در این پویش، لحظه ای بخت یارمان باشد، آن گوهر نایافته را در وجود انسانی دیگر می یابیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3656,'نتوان گفت: بهارا، به امید دیدن و ماندن تو! می توان گفت: بهارا، تو بیا دانه ی امید به قلبم بنشان! و سفر کن به کجاآبادی، هر زمانی که تو را دلخواه است!((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3657,'زندگی همواره چشم به راه شرایط بحرانی می ماند تا قدرتش را نشان دهد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3658,'شتاب زمان به اندازه ای است که در چند لحظه می تواند مردم را از بهشت به دوزخ بفرستد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3659,'هیچ کس نمی داند زندگی برای ما چه اندوخته است؛ بسیار خوب است که همیشه راه گریز را بدانیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3660,'من می توانم انتخاب کنم که قربانی دنیا باشم یا یک ماجراجو در جستجوی گنج؛ همه چیز بستگی به روش نگاه من به زندگی دارد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3661,'مردم جوری حرف می زنند که [ گویی ] همه چیز را می دانند، اما اگر جسارت داشته باشی و سؤالی بپرسی آنها هیچ چیز نمی دانند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3662,'انسان همیشه خواهان دگرگونی و تحول است، اما همزمان، سکون و ثبوت را آرزو می کند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3663,'شنیدن یک توهین و پاسخ ندادن به آن، آسانتر از درگیر نبرد شدن با شخصی نیرومندتر از خود است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3664,'پذیرفتن نیک سرشتی خود همیشه آسانتر از رویارویی با دیگران و جنگیدن برای حقوق خویش است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3665,'همواره می توانیم بگوییم سنگی که دیگران به سوی ما انداخته اند، به ما نخورده است، اما تنها در شب می توانیم برای قلب ضعیف خود بگرییم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3666,'وعده ها را باور نداشته باش، چون دنیا پر از وعده است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3667,'خداوند دعای کسانی را می شنود که می خواهند نفرت از آنها گرفته شود، اما نسبت به آنهایی که می خواهند از عشق بگریزند ناشنواست.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3668,'هنگامی که آرزوی چیزی را دارید، سراسر کیهان همدست می شوند تا بتوانید این آرزو را تحقق بخشید.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3669,'هر انسانی حق دارد به رسالتش شک کرده و گاه آن را رها کند، اما هرگز حق ندارد آن را از یاد ببرد!((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3670,'عشق حقیقی اجازه می دهد که هر کس راه خود را در پیش بگیرد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3671,'عشق، نیرویی است که ما را بار دیگر به هم می پیوندد تا تجربه ای را که در زندگیهای گوناگون و در مکانهای مختلف جهان پراکنده شده است، بار دیگر متراکم کند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3672,'آگاهی از مرگ نمی گذارد آنچه را که باید امروز از آن لذت ببریم به فردا واگذار کنیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3673,'در شادی گناهی نیست.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3674,'همواره می توانیم بگوییم سنگی که دیگران به سوی ما انداخته اند، به ما نخورده است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3675,'راز زندگی این است که بفهمیم هر روز، یک معجزه است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3676,'دانستن هدف زندگی یا دانستن بهترین راه خدمت به خدا کافی نیست، افکار خودت را به عمل درآور؛ راه، خودش را نشان می دهد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3677,'فقط خودت می دانی که چه چیزی برایت بهترین است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3678,'حقیقت همواره همان جایی است که ایمان هست.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3679,'جوانی که فراموش می کند شاخه گلی به محبوبش بدهد، سرانجام وی را از دست خواهد داد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3680,'مهم این نیست که چقدر زندگی می کنیم مهم این است که چگونه زندگی می کنیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3681,'بسیاری از اوقات ما خودمان استعدادهایمان را می کشیم، چرا که نمی دانیم با آنها چه کنیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3682,'هرگز نگذار آگاهی از عیب هایت تو را بترساند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3683,'از دست دادن بخشی از چیزی، بهتر است تا از دست دادن کل آن چیز.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3684,'انسان باید از گذشته رها شود و از میان راههایی که به او پیشنهاد می شود، بهترین را برگزیند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3685,'برای آگاهی از استعدادها و محدودیت هایتان، هیچ کس مناسب تر از خودتان نیست.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3686,'هر رحمتی که نادیده گرفته شود، تبدیل به لعنت می شود.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3687,'حسد ورزان و شکست خوردگان تنها در حالی می توانند به تو صدمه بزنند که خود، توان این کار را به آنان بدهی.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3688,'اگر نسبت به دیگران صبور باشیم، پذیرش خطاهای خودمان ساده تر می شود.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3689,'بهترین راه شناخت و نابودی یک دشمن این است که خود را دوست او بدانیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3690,'در جوانی آنگاه که رویاهایمان با تمام قدرت در ما شعله ورند، خیلی شجاعیم، اما هنوز راه مبارزه را نمی دانیم. وقتی پس از زحمات فراوان مبارزه را می آموزیم، دیگر شجاعت آن را نداریم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3691,'تکبر و خودپسندی در بسیاری از موارد، ماسکی بی ارزش از ترس و وحشت است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3692,'هرگز نباید به پشت سرمان نگاه کنیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3693,'در زندگی عشق باید باشد، حتی اگر بازتاب و پاسخ فوری نداشته باشد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3694,'دیوانه بمانید، اما مانند عاقل ها رفتار کنید. خطر متفاوت بودن را بپذیرید، اما بیاموزید که بدون جلب توجه مفید باشید.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3695,'این که بدانی در راه درستی هستی یک چیز است، اما اگر فکر کنی راه درست تنها همین است چیز دیگری است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3696,'به خاطر ترس از دست دادن، چه چیزهایی را از دست داده ایم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3697,'در عشق هیچ خطری وجود ندارد. هزاران سال است که انسانها یکدیگر را جستجو کرده اند و یکدیگر را یافته اند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3698,'لحظاتی هست که جز غم و رنج چیزی رخ نمی دهد و نمی توانیم از آن دوری کنیم. تنها زمانی دلیلش را می فهمیم که بر آن غلبه کرده باشیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3699,'هرگز نمی توانیم درباره زندگی دیگران قضاوت کنیم، چون هر کس رنجها و درماندگی های خودش را دارد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3700,'روح جهان از خوشبختی انسانها سیراب می‌شود و یا از بدبختی، ناکامی و حسادت آنها.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3701,'انسان هرگز نمی‌تواند از رویا دست بکشد، رویا خوراک روح است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3702,'براستی، این موجودات نیستند که چیزی را آشکار می‌کنند، خود مردم هستند که با نگریستن به موجودات، روش نفوذ به روح جهان را می‌یابند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3703,'معمولا مرگ سبب می‌شود آدم زندگی را بیشتر احساس کند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3704,'چیزهای ساده خارق العاده ترین چیزها هستند و تنها خردمندان می توانند آنها را ببینند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3705,'آدم می تواند همیشه دوستان جدیدی پیدا کند، بی آنکه مجبور باشد هر روز آنها را ببیند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3706,'زمانی که ما همواره در پیرامون خود افراد مشخصی را ببینیم احساس می کنیم که آنها بخشی از زندگی ما هستند و چون بخشی از زندگی ما می شوند سرانجام تصمیم می گیرند که زندگی ما را تغییر دهند و اگر آن گونه که آنها آرزو دارند نباشیم از ما ناخشنود می شوند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3707,'هر کسی گمان می کند که دقیقاً می داند ما باید چگونه زندگی کنیم، اما هیچ کس هرگز نمی داند که چگونه باید زندگی خاص خودش را داشته باشد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3708,'در زندگی ما لحظه ای فرا می رسد که تسلط بر زندگی را از دست می دهیم و از آن پس، سرنوشت بر هستی ما مسلط می شود و این بزرگترین گزافه ی جهان است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3709,'وقتی با آدمها حرف می زنی، یک چیزهایی به تو می گویند که نمی دانی چگونه به گفتگو ادامه دهی.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3710,'وقتی تو چیزی را می خواهی همه ی جهان دست به یکی می کند تا تو آرزویت را برآورده سازی.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3711,'انسان همیشه امکان برآورده ساختن رویاهایش را ندارد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3712,'انسانها بسیار زود از علت زندگی خویش آگاه می شوند، شاید برای همین هم هست که بسیار زود از آن چشم می پوشند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3713,'هر چیز در زندگی بهایی دارد؛ این آن چیزی است که مبارزین روشنایی می کوشند بیاموزند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3714,'بهتر است گاهی چیزها را به حال خود رها کنیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3715,'[ گاهی انسان ] می بایست بین چیزی که به آن عادت کرده و چیزی که بسیار دلش می خواهد داشته باشد، یکی را برگزیند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3716,'وقتی همه ی روزها به هم شبیه هستند، انسان دیگر متوجه پیشامدهای خوبی که در طی روز رخ می دهد نمی شود.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3717,'هیچ مانعی وجود ندارد، مگر خود [ انسان ].((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3718,'یک چوپان می تواند سفر را دوست داشته باشد، اما هرگز میش هایش را فراموش نمی کند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3719,'پول، چیزی جادویی است؛ با پول، انسان هیچ وقت کاملاً تنها نیست.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3720,'در مدتی کوتاهتر از یک فریاد همه چیز در زندگی [ انسان ] تغییر می کند، پیش از آنکه حتی به موقعیت جدیدش عادت کند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3721,'من هم مثل همه هستم؛ دنیا را آنگونه می بینم که دلم می خواهد باشد، نه آنگونه که به راستی هست.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3722,'تلاش کن نشانه ها را دریابی و به آنها احترام بگذاری.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3723,'مطالبی هست که نباید درباره ی آنها کنجکاوی کرد، چون نباید از سرنوشت گریخت.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3724,'هم می توان دنیا را با چشمان یک غارت شده ی بدیخت نگریست و هم با چشمان یک ماجراجوی در جستجوی گنج.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3725,'مردم، بسیار از نشانه حرف می زنند، اما نمی دانند که به راستی درباره ی چه چیز حرف می زنند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3726,'وقتی که شانس با ما است باید از آن بهره گیریم و به آن کمک کنیم، همان گونه که شانس به ما کمک می کند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3727,'همه نمی توانند رویاهایشان را به یک شکل ببینند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3728,'به یاد داشته باش که همیشه باید بدانی که چه می خواهی.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3729,'آنچه بیش از همه انسانها را شیفته می کند زیبایی است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3730,'من نمی خواهم تغییر کنم و چیزی را تغییر بدهم چون نمی دانم چگونه باید این کار را کرد؛ من کاملاً به خودم عادت کرده ام.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3731,'گاهی نمی توان جلوی جریان رودخانه ی زندگی را گرفت.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3732,'هرگز از رویاهایت چشم پوشی نکن؛ چشم براه نشانه ها باش.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3733,'تصمیم ها فقط آغاز یک ماجرا هستند. وقتی کسی تصمیمی می گیرد، خود را در جریانی تند پرتاب می کند که او را به سوی هدفی خواهد برد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3734,'همه چیز در زندگی نشانه است. جهان به زبانی ساخته شده که همه می توانند بشنوند، اما آن را فراموش کرده اند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3735,'پیشامد و امر ناگهانی وجود ندارد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3736,'کسی که به سفر عادت دارد، می داند که همیشه لحظه ای فرا می رسد که باید رفت.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3737,'احساس پیش از وقوع، شیرجه ی سریع روح در جریان جهانی حیات است که تاریخچه ی زندگی همه ی انسانها در آغوش آن به هم می پیوندد و یگانه می گردد، به گونه ای که می توانیم همه چیز را بدانیم، چون همه چیز در آن نوشته است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3738,'پیچ و خم ها برای کاروان اهمیت ندارد، چون هدف همواره ثابت است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3739,'هیچ کس نباید از ناشناخته بترسد، چون هر انسانی می تواند آنچه را که می خواهد به دست آورد و آنچه را که نیاز دارد فراهم کند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3740,'تنها ترس ما این است که آنچه را داریم از دست بدهیم، خواه زندگیمان باشد و خواه مزارعمان. این ترس زمانی از بین می رود که بفهمیم داستان زندگی ما و داستان جهان، هر دو را یک دست مشترک رقم زده است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3741,'کسی که به صحرا می رود نمی تواند عقب گرد کند و هنگامی که نمی شود به عقب بازگشت، فقط باید در جستجوی بهترین راه برای پیش رفتن بود.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3742,'هرچه در سطح زمین است به گونه ای مداوم در حال تغییر است، چون زمین هم زنده است و زمین هم روح دارد و ما به ندرت می دانیم که زمین در جهت منافع ما کار می کند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3743,'همه ی چیزها تجلی یک چیز واحد است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3744,'جهان، روحی دارد و کسی که بتواند آن روح را درک کند می تواند زبان همه چیز را بفهمد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3745,'هر کسی راهی برای آموختن دارد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3746,'اگر در زمان حال باشی، زندگی تبدیل به جشنی همیشگی می شود، به عیدی بزرگ؛ چون همیشه در لحظه ای که در آن زندگی می کنیم جریان دارد و تنها در آن لحظه.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3747,'شاید خداوند صحرا را آفریده است تا انسان از دیدن نخلها لذت ببرد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3748,'در دنیا همواره کسی هست که چشم براه دیگری است، چه در میان صحرا و چه در قلب یک شهر بزرگ.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3749,'تپه ها با حرکت باد تغییر شکل می دهند، اما صحرا همیشه همان که بوده می ماند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3750,'اگر من بخشی از افسانه ی تو باشم تو روزی باز خواهی گشت.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3751,'وقتی آدم عاشق است، همه چیز بیشتر معنا دارد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3752,'اشیاء به خودی خود چیزی را آشکار نمی کنند، این انسانها هستند که با نگاه کردن به اشیاء، راه ورود به روح جهان را درمی یابند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3753,'آنچه که بتوانی تغییر دهی، آینده ی تو نبوده است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3754,'اگر تو به حال توجه کنی، آن را بهتر خواهی کرد و اگر حال را بهتر کنی، آنچه از پس آن می آید بهتر خواهد شد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3755,'هر روز، ابدیت را در خود دارد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3756,'هرگز نباید سست شد، حتی اگر راه زیادی را پیموده باشی.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3757,'باید صحرا را دوست داشت، اما هرگز کاملاً به آن اعتماد نکن؛ چون صحرا محک مردان است. هر گام آنها را احساس می کند و کسی را که سربهوا و گیج باشد خواهد کشت.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3758,'شر در آن چیزی نیست که از دهان به درون می رود، شر در آن چیزی است که از دهان بیرون می آید.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3759,'شترها خائن هستند؛ هزاران کیلومتر راه می روند، بدون اینکه هیچ نشانه ای از خستگی در آنها نمایان شود. سپس ناگهان به زانو درمی آیند و می میرند. اسبها کم کم خسته می شوند و همیشه می دانی چقدر از آنها توقع داشته باشی و چه زمانی ممکن است بمیرند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3760,'آدم دوست دارد، چون دوست دارد؛ همین؛ هیچ دلیلی برای دوست داشتن وجود ندارد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3761,'به آنچه که پشت سر گذاشته ای نیندیش.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3762,'تنها یک راه برای آموختن وجود دارد و آن، عمل است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3763,'قلب تو هر جا باشد گنج تو هم همانجا است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3764,'این طبیعی است که از مبادله ی همه ی آنچه که توانسته ایم به دست آوریم، با یک رویا بترسیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3765,'هیچ زمان نمی توانی قلبت را خاموش کنی، حتی اگر وانمود کنی که به حرفهایش گوش نمی دهی، او آنجا در سینه ی تو خواهد بود و مدام آنچه را که درباره ی زندگی و دنیا می اندیشد تکرار خواهد کرد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3766,'اگر تو قلبت را خوب بشناسی، هیچ وقت تو را غافلگیر نخواهد کرد؛ چون تو آرزوها و رویاهایش را خواهی شناخت و می توانی آنها را به حساب آوری.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3767,'هیچ کس نمی تواند از دل خود بگریزد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3768,'آدمها می ترسند که بزرگترین رویاهایشان را برآورده کنند، چون یا به این می اندیشند که سزاوار آن نیستند و یا اینکه از عهده ی آن برنمی آیند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3769,'ترس از رنج از خود رنج بدتر است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3770,'هیچ قلبی وقتی در پی رویاهایش بوده، هرگز رنج نکشیده است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3771,'نیک بختی را می توان در یک شن ساده ی صحرا یافت. چون یک دانه ی شن، یک لحظه از آفرینش است و جهان برای آفریدن آن میلیونها سال وقت گذاشته است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3772,'بیشتر آدمها دنیا را چیز تهدیدکننده ای می بینند و به همین دلیل هم دنیا برای آنها تهدیدکننده می شود.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3773,'وقتی که ما گنجینه های حقیقی در دست داریم، هرگز متوجه آن نمی شویم؛ چون آدمها به وجود گنج باور ندارند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3774,'هیچ کس از نتایج آنچه که زیر آسمان می گذرد در امان نیست.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3775,'چشم ها قدرت روح را نشان می دهند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3776,'همه چیز در کیهان در حال دگرگونی است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3777,'وقتی چیزی در حال دگرگونی و تغییر است، هرچه در پیرامون آن است نیز دگرون می شود.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3778,'خودت را تسلیم ناامیدی نکن؛ این امر تو را از گفتگو با قلبت باز می دارد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3779,'تنها یک چیز هست که برآورده شدن رویایی را ناممکن می سازد و آن، ترس از شکست است.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3780,'عشق، زمانی است که قوشی بر فراز شنهای صحرا به پرواز درمی آید. چون برای او، تو دشتی سرسبزی و او هرگز بدون شکار از نزد تو بازنمی گردد. او صخره ها و تپه های تو را می شناسد و تو نسبت به او دست و دلبازی.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3781,'بادها همیشه همه چیز را می دانند؛ آنها همه جا روانند، بی آنکه زادگاه یا مکانی برای مردن داشته باشند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3782,'وقتی انسان دوست می دارد، می تواند جزیی از آفرینش باشد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3783,'وقتی انسان دوست می دارد، هیچ نیازی ندارد که بفهمد چه پیشامدی در حال رخ دادن است. چون همه چیز در درون ما روی می دهد و انسان می تواند به باد بدل شود.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3784,'زمینی که روی آن زندگی می کنیم بهتر خواهد شد اگر ما بهتر باشیم و بدتر خواهد شد اگر ما بدتر باشیم.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3785,'هرچه یکبار پیش می آید ممکن است دیگر هرگز روی ندهد، اما آنچه که دو بار پیش آید، بدون شک، بار سوم هم رخ خواهد داد.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3786,'هر کس هر کاری که بکند، در روی زمین اصلی ترین نقش تاریخ جهان را ایفا می کند و معمولاً خودش این را نمی داند.((پائولو کوئیلو))',41,0,0,0,0,0);
INSERT INTO mysms VALUES(3787,'حقیقت واقعی در سکوت نهفته است.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3788,'برای جوانمردی و مروتی که در حق هر کس می کنی، امید هیچ پاداشی نداشته باش.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3789,'اگر آینه نبود، من همیشه خود را جوان می دانستم.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3790,'من جستجو نمی کنم، پیدا می کنم.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3791,'هنگام کار کردن راحت هستم؛ چیزی که مرا خسته می کند بیکاری و دیدار اشخاص است.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3792,'فرق نقاشی با عکس در احساس آرامشی است که نقاشی به انسان می دهد.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3793,'هیچ بهاری بی‌خزان نیست، جز بهار هنر و ادب.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3794,'تمایل و کشش یک هنرمندِ آفریننده آن است که بشریت را در کنار پرتگاه متوقف کند.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3795,'هر رقابتی، اسلحه‌ی ویژه ی خود را دارد؛ گاهی زور، گاهی زر، گاهی هوش و گاهی مهربانی.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3796,'کار بی‌هدف، راهی‌ است که به پایان نمی‌رسد.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3797,'آنچه در آینده خواهم کرد، دلنشین‌‌تر از آنچه در گذشته کرده‌ام، خواهد بود.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3798,'نقاشی، یک نیاز فوری به خالی کردن بار احساس‌ها و تصویرهاست.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3799,'یک نقاش باید آن‌ چیزی را بیافریند که تجربه می‌کند.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3800,'مادر، تابلوی آفریدگار است.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3801,'زمان حال همیشه بر گذشته برتری دارد؛ برای تو، این پیروزی ا‌ست.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3802,'هیچ‌چیز جز در تنهایی انجام‌پذیر نیست. در این دوران، تنها بودن و در تنهایی زیستن کار دشواری ا‌ست.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3803,'آرامش کامل تنها در آغوش طبیعت امکان‌پذیر است.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3804,'تا آمادگی اداره خانواده‌ای را نداری، کاری نکن.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3805,'آزمونی که در جوانی داده‌ای، نتیجه‌اش در پیری، آشکار خواهد شد.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3806,'هنر، غبار هر روزه‌ی زندگی را از جان می‌زداید.((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3807,'آسایش، ناموس طبیعت است. همه به آسایش نیاز دارند، حتی گیاهان!((پابلو پیکاسو))',42,0,0,0,0,0);
INSERT INTO mysms VALUES(3808,'ما در مدرسه، چگونگی انجام هر چیزی، به جز درست زندگی کردن را می آموزیم.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3809,'تنها، شخص کنجکاو است که خواهد آموخت و تنها، انسان با اراده، بر موانع [ =بازدارنده های ] راه علم و دانش چیره خواهد شد.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3810,'میزان شیفتگی و دلبستگی برای آموختن، مهمتر از میزان هوش و زیرکی شخص برای یادگیری است.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3811,'هدف از زندگی، آموختن است.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3812,'آموختن هیچ چیز با شانس و برخورد به دست نمی آید. بایستی با شیفتگی به دنبال اش رفت و با جدیت به آن پرداخت.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3813,'اگر زندگی را به درستی معنا [ =چَم ] نکنیم، هر آنچه را که از ابتدا می آموزیم و یا تجربه می کنیم، بی ثمر خواهد بود.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3814,'انسان های خوب، به این دلیل، خوب هستند که ناکامی های زیادی را از سر گذرانده اند و خِرد و شناختی هم که به آن نایل گشته اند، نتیجه ی شکست هایی است که تجربه کرده اند.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3815,'از هر فرصتی، برای لذت بردن از زندگی استفاده کنید.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3816,'بیاموزید که از روند یادگیری، لذت ببرید.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3817,'سراسر زندگی، یک کلاس درس است.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3818,'هر کنش، بی تردید، نتیجه ای در پی دارد؛ همواره مراقب کاری که می کنید، باشید.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3819,'تجربه، بهترین آموزگار است؛ البته با این شرط که ما هم دانش آموزان خوبی باشیم.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3820,'حقیقت این است که زمانی دانا و درس خوانده می شویم که دریابیم چیزهایی را که آموخته ایم، کمترین میزان دانش ممکن بوده است.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3821,'تو تنها زمانی خشنود می شوی که بدانی کیستی؛ آن وقت زندگی ات را به گونه ای برمی گزینی که نیازهای راستین تو را برآورده کند.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3822,'بهترین روش پند دادن به فرزندانتان این است که نخست، آنچه را آنها می خواهند و مورد نظرشان است، کشف کنید. سپس برای انجام آن چیز، آنها را راهنمایی کنید.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3823,'از هر چیزی برای پیشرفت، یادگیری و رشد خود بهره بگیر.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3824,'زندگی، بسیار کوتاه است و درس های زیادی هست که باید آموخته شوند. کسانی که همه چیز را پشت گوش می اندازند، بازنده هستند.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3825,'اگر بتوانید خودتان را باور کنید، بزرگترین راز زندگی را آموخته اید.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3826,'هنگامی که شما توانایی پذیرفتن مسائل را ندارید، یادگیری، بسیار دشوار می شود.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3827,'همان گونه که مشت بسته قادر به دریافت هدیه نیست، به همان ترتیب هم، اندیشه و ذهن بسته نمی تواند هیچ درسی را بیاموزد.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3828,'از آنچه زندگی به شما می بخشد، لذت ببرید، ولی همیشه آزاد و رها باقی بمانید.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3829,'یک مشکل، فرصتی است که به تو داده می شود تا با آن، خودت را بهتر کنی.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3830,'اشتباهات، به روشنی، آنچه را که برای بهتر کردن خود و درست کردن شرایط، لازم است، به ما نشان می دهند.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3831,'همه ی انسان ها دارای شایستگی هستند. آنچه کمیاب است، جسارت لازم برای بهره گیری از این شایستگی ها می باشد؛ زمانی که شما را به مکان های ناشناخته راهنمایی می کنند.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3832,'زندگی، چیزی مانند ترومپت است؛ اگر درون آن ندمی، پژواکی از آن نمی شنوی.((پیتر مک ویلیام))',43,0,0,0,0,0);
INSERT INTO mysms VALUES(3833,'نُه دهم خردمند بودن این است که در دم خرد بورزی.((تئودور روزولت))',44,0,0,0,0,0);
INSERT INTO mysms VALUES(3834,'همه‌ی منابع مورد نیاز ما در ذهن ماست.((تئودور روزولت))',44,0,0,0,0,0);
INSERT INTO mysms VALUES(3835,'نه منتقدان زیاد اهمیت دارند، نه کسی که زمین خوردن آدمهای توانمند را نشان می دهد. حتی این هم خیلی مهم نیست که کاری را می توانستیم بهتر انجام دهیم. آفرین بر انسان هایی که در صحنه حضور دارند.((تئودور روزولت))',44,0,0,0,0,0);
INSERT INTO mysms VALUES(3836,'مهمترین عنصر کامیابی، داشتن راه و رسم همدلی و همراهی با مردم است.((تئودور روزولت))',44,0,0,0,0,0);
INSERT INTO mysms VALUES(3837,'در کارنامه زندگی من چیز برجسته یا درخشانی نیست، جز شاید یک مورد و آن اینکه آنچه را به انجام آن باور دارم، انجام می دهم... و لحظه ای که تصمیم به انجام کاری می گیرم، عمل می کنم.((تئودور روزولت))',44,0,0,0,0,0);
INSERT INTO mysms VALUES(3838,'هر جا هستی و با هر چه داری، آنچه را می توانی بکن.((تئودور روزولت))',44,0,0,0,0,0);
INSERT INTO mysms VALUES(3839,'در هر جا که هستید و با هر چه که در اختیار دارید، کاری بکنید.((تئودور روزولت))',44,0,0,0,0,0);
INSERT INTO mysms VALUES(3840,'بهترین پاداش زندگی، بهره مندی از کار و تلاش در زمینه ای است که ارزش آن را دارد.((تئودور روزولت))',44,0,0,0,0,0);
INSERT INTO mysms VALUES(3841,'من کار را از همان جایى که دیگران رهایش کرده‏اند، آغاز کرده و دنبال مى‏کنم.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3842,'من به هیچ وجه اجازه نمی دهم که شرایط مرا ناامید کند. سه چیز لازم برای رسیدن به یک هدف با ارزش عبارت است از : کار ، استقامت و عقل سلیم.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3843,'چیزهای ضروری در زندگی بر این پایه‌اند: کار کردن، عشق ورزیدن و امید داشتن.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3844,'هرگز اختراعی را بدون در نظر گرفتن خدماتی که برای دیگران ارائه می‌دهد، کامل نکردم. ابتدا نیازهای دنیا را شناسایی کردم، سپس به اختراع ادامه دادم.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3845,'ناتوانی شدید ما در گردن نهادن نهفته است. مطمئن‌ترین راه کامیابی آن است که همیشه یک بار دیگر هم تلاش کنیم.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3846,'بزرگترین ناتوانی ما، کوتاه آمدن است. امن ترین راه برای کامیابی، تلاش برای یک بار دیگر است.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3847,'مادرم کلید کامیابی من بود. او با همه‌ی وجودش به من ایمان داشت و من احساس می‌کردم کسی را دارم که برایش زندگی کنم؛ کسی که نباید ناامیدش کنم.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3848,'ما یک میلیونیم درصد درباره‌ی هیچ چیز نمی‌دانیم.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3849,'نود و هشت درصد موفقیتها عرق روی پیشانی است و تنها دو درصد الهام از درون است.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3850,'برای پیروزی یک راه آسان وجود دارد: هیچ فرصتی را برای کار کردن از دست ندهید و اگر وقت شما کم است، وقت اختراع کنید.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3851,'همچون اسفنج که آب را در بر می گیرد، شما هم اندیشه های خوب را در بر گیرید و آن را در خدمت هدف خویش قرار دهید.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3852,'من شکست نخورده ام، تنها هزار راه حل پیدا کرده ام که به کار نمی آیند.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3853,'تمام پیشرفت های جهانگیر خود را مدیون اندیشه منظم و یادداشت برداری دقیق هستم.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3854,'هر پیشرفت و موفقیتی از اندیشه سرچشمه می گیرد.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3855,'نام نیک پیراهنی است که هرگز کهنه نمی شود.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3856,'طعم بسیاری از شکست های زندگی را کسانی چشیده اند که در دو قدمی کامیابی از تلاش خود دست کشیده اند.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3857,'افکار من بلند، اما زمان من کوتاه است. امیدوارم خود را به حدود صد سالگی برسانم.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3858,'اگر تنها شور و شوق و عشق و علاقه را برای فرزندان خود به جای بگذاریم، سرمایه ای بزرگ به آنها داده ایم.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3859,'در دنیا دیگر وسیله ای نیست که بشر برای فرار از تحمل و زحمت فکر کردن، به آن متوسل نشده باشد!((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3860,'من هفتصد بار اشتباه نکردم، من یک بار اشتباه نکردم. من زمانی موفق شدم که هفتصد راهی را که موفقیت آمیز نبود اصلاح کردم. هر گاه راهی را که عمل نمی کرد حذف کردم راهی را پیدا کردم که کار می کرد.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3861,'انسان ها مانند خطوط انگشتان، هیچ کدام به هم شبیه نیستند.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3862,'هیچ گاه اثرات خوب تعلیم و تربیت مادرم را از دست نخواهم داد.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3863,'زنها اختراعهای بزرگی نکرده اند، اما مخترعان بزرگ را پرورش داده اند.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3864,'نیایش اگر به گونه ی تهاجمی و با پافشاری و پی در پی انجام گیرد، به اجابت می رسد.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3865,'من نمی گویم هزار بار شکست خوردم، بلکه می گویم من کشف کردم که هزار راه برای شکست خوردن وجود دارد.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3866,'آنان که در زندگی پیروز و کامیاب شده اند، نخست از نظر فکر و روح، پیروز و کامیاب بوده اند.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3867,'انسان هر چه بالاتر برود، احتمال دیده شدن وصله شلوارش بیشتر می شود.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3868,'انسان در هر سنی که از کار باز ماند، بهتر است سال بعد بی درنگ بمیرد.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3869,'من در زندگیم حتی یک روز هم کار نکرده ام. آنچه انجام داده ام، تفریح بوده است.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3870,'حجب و حیا نه تنها آرایش است، بلکه نگهبان دانش است.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3871,'می خواهم برق را به اندازه ای ارزان کنم تا تنها توانگران بتوانند پول شمع بپردازند.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3872,'اگر هر کاری را که توان انجامش را داریم بکنیم، براستی، خود را سرگشته خواهیم ساخت.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3873,'من دلسرد نمی شوم، زیرا هر تلاش نادرستی که کنار گذاشته شود، گامی به سوی پیش است.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3874,'اگر هر یک از ما، کارهایی را که شایستگی انجام آن را داریم، انجام می دادیم، به راستی شگفت زده می شدیم.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3875,'هزار بار آزمایشی که در ساختن لامپ نتیجه نداد، هرگز برایم شکست به شمار نرفت. سرانجام، هزار و یکمین آزمایش، در ِکامیابی و روشنایی را به روی من گشود.((توماس ادیسون))',45,0,0,0,0,0);
INSERT INTO mysms VALUES(3876,'آزادی دریایی متلاطم و طوفانی است. مردان بزدل، آرامش استبداد را بر این طوفان ترجیح می دهند.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3877,'یک دَم، دست از تلاش برندارید. این گونه، زمانی برای شکایت کردن پیدا نمی‌کنید. اگر همیشه در حال انجام دادن کاری باشیم، به کامیابی ‌های باور نکردنی می‌رسیم.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3878,'هرگز پیش نیامده که به هنگام برآمدن خورشید، من هنوز در رختخواب باشم.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3879,'شناخت و آگاهی به شرایط، به کردار درست می‌انجامد و کردار درست، بدون داشتن شناخت و دانش، ناممکن است.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3880,'هیچ عاملی نمی تواند افرادی را که دارای ذهنیت درست هستند از رسیدن به هدف و مقصود باز دارد و هیچ عاملی نمی تواند به کسانی که ذهنیت نادرست دارند کمک کند.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3881,'شادترین لحظه های زندگی ام اندک زمانی بود که در خانه و در آغوش خانواده ام سپری کردم.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3882,'جایی که مردم از دولتشان بترسند، ستم حاکم است و جایی که دولت از مردم بترسد، آزادی وجود دارد.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3883,'درخت آزادی را باید گهگاه با خون میهن پرستان و ستمکاران آبیاری کرد.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3884,'بانکها از ارتش آماده به جنگ، خطرناک ترند.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3885,'هیچ چیز نمی تواند مانع از رسیدن مردی به هدفش شود که نگرش ذهنی درستی دارد و هیچ چیز نمی تواند به مردی کمک کند که نگرش ذهنی نادرستی دارد.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3886,'ذهن مشغول، همواره شادمان است.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3887,'راستی و درستی، نخستین بخش کتاب عشق است.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3888,'من تنها یک ترس دارم و آن این است که زندگی ام بیش از اندازه در این دنیا به طول انجامد و در آخر عمر مانند گلیمی پوسیده، دیگران را از وجود خویش در رنج و زحمت اندازم.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3889,'همه ی انسانها برابر آفریده شده اند.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3890,'لحظه های شاد زندگی من کم بوده‌اند و این لحظه ها زمانی بوده که در آغوش خانواده‌ام بوده‌‌ام.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3891,'خورشید، پزشک پر قدرت من است.((توماس جفرسون))',46,0,0,0,0,0);
INSERT INTO mysms VALUES(3892,'آدمی را باور او نسبت به چیزی، زنده نگه می‌دارد، نه گفتگو بر سر چیزهای بسیار.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3893,'انسان، حیوان ابزارمند است.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3894,'هر جاده ای، حتا جاده ی مشرف به دریاچه ی مرغابی هم به انتهای جهان می رسد، اما اگر انسان جاده ی مشرف به دریاچه ی مرغابی را تا انتها دنبال کند، باز از دریاچه ی مرغابی سر در می آورد، از همان دریاچه ای که پیش تر در آنجا بودیم؛ بنابراین آنجایی که ما در جستجویش بودیم، پایان جهان است.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3895,'وظیفه و کار ما این نیست که دلواپس فاصله های دور و مبهم و ناشناخته ای که در پشت ابر و تاریکی پنهان شده اند باشیم، بلکه باید وقت خود را صرف آن چیزهایی بکنیم که در دسترس ما قرار گرفته اند.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3896,'سرنوشت خود را با اندیشه ها [ یتان ] تعیین کنید.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3897,'هرگاه بتوانیم از نیروی تخیل به همان اندازه بهره ببریم که از نیروی بصری استفاده می کنیم، هر کاری انجام پذیر است.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3898,'بشر، حیوان به کار برنده ابزار است. در هیچ نقطه ای او را بدون ابزار نمی توان یافت. با نداشتن ابزار در حکم هیچ است و با داشتن آن، همه چیز.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3899,'ای بشر گرچه خلق شده ای، برخیز و ثابت کن که اشرف مخلوقاتی.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3900,'ویژگی های هرکس مربوط به خوبیها و بدیهای اخلاقی دوستان او است.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3901,'برای آنکه در زندگی دچار لغزش نشوی، همواره قلب خود را پاک نگاه دار.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3902,'اخلاق، مهمترین درس است؛ هر چند دستمزد آن گران باشد.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3903,'بزرگترین عیب آن است که از عیب خویش آگاه نباشیم.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3904,'اگر ندانید که به کجا می روید، هر راهی شما را با خود خواهد برد.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3905,'تجربه بهترین درس است، هر چند که دستمزد آن گران باشد.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3906,'هر اقدام بزرگ ابتدا محال به نظر می رسد.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3907,'تاریخ، سیر خود را بر زندگی مردان بزرگ بنا نموده است.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3908,'بزرگی مردان بزرگ از طرز رفتارشان با مردان خرد آشکار می شود.((توماس کارلایل))',47,0,0,0,0,0);
INSERT INTO mysms VALUES(3909,'انسان به بودن در هر جایی عادت می کند و دیگر برایش سخت است که از آنجا برود. نحوه فکر کردنش هم بعد از مدتی عادت می شود و عوض کردنش سخت است.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3910,'سرشت آدمی‌ این است که همچنان که بر سن او افزوده می‌شود، از دگرگونی نافرمانی کند، به ویژه با بهبود یافتن.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3911,'بزرگترین امید آن است که امید را به قصد کشت، کتک بزنیم.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3912,'نویسنده، وظیفه دارد، گنجایش پذیرفته شده ی انسان را برای شکوه و بزرگی روح و روان نشان دهد و آن را بستاید.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3913,'نویسنده ای که به کمال انسان، باور پابرجا نداشته باشد، نه احساس بخشش و پایبندی به ادبیات دارد و نه هرگز عضو خانواده ی ادبیات است.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3914,'گاهی برخلاف شکست و نابوديِ کمابیش قطعی، باید نابخرد و بزدل باشیم که در آستانه ی بزرگترین پیروزی نهفته مان، میدان را ترک کنیم.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3915,'ادبیات به دیرینگی گفتار است. ادبیات از دل نیاز بشری به آن سرچشمه گرفته و هیچ تغییری نیافته است، جز این که نیاز بشر به آن بیشتر شده است.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3916,'اگر شاهراه زندگی، تنها دو راه بنیادی داشته باشد و می بایست تنها یکی از آنها را برگزید، چه کسی می تواند ادعا کند که کدام یک بهتر است؟((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3917,'در زندگی، مواردی هست که حتی از دوستان آدم هم کاری ساخته نیست.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3918,'انسان در دنیا تنها است و در میان همنوعان خود، تنها است و چشم داشتِ هیچ گونه آسایش و دلداری از هیچ جا ندارد.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3919,'آدم نمی تواند همه چیز را با هم داشته باشد.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3920,'از هر رویدادی که رخ می دهد، باید درسی گرفت.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3921,'چه نیکو است که آدمی مورد اعتماد باشد.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3922,'کسی که داستانی می نویسد، ناچار است بهترین دانستنی ها و بهترین احساسات خود را در آن بگنجاند.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3923,'نویسنده در فضایی از بیم و ارج نهادن به واژه زندگی می کند، زیرا واژه ها می توانند ستمگر یا مهربان باشند و نیز می توانند درست در برابر چشمان شما، معانی [ =چَم ها ] را دگرگون کنند.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3924,'نویسندگانِ نادرستی هم هستند که برای مدتی کوتاه، کارشان را از پیش می برند، ولی نه برای مدتی دراز، نه برای مدتی دراز.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3925,'وظیفه ی نویسنده این است که تعالی ببخشد، بگستراند و نیرو بخشد.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3926,'یک گلف باز نمی تواند در کار خود کامیاب شود، مگر اینکه، ضربه زدن به یک توپ کوچک را برای خود، مهمترین چیز دنیا بداند.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3927,'من به این نتیجه رسیده ام که گاهی گزینش یک شخص برگزیده به گونه ی مخاطب، بسیار کارساز است؛ شخصی حقیقی که او را می شناسی، یا شخصی فرضی؛ باید برای آنها بنویسی.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3928,'یک کتاب، مثل یک انسان است: باهوش و کودن، دلیر و ترسو، زیبا و زشت.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3929,'زمان، تنها خرده گیری است که جاه طلبی ندارد.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3930,'به یک خرده گیر اگر رو بدهی، خودش را دانای روزگار می داند.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3931,'هر انسانی، برای خود بهایی دارد، ولی بهای نویسنده - نویسنده ای راستین - را به سادگی نمی توان روشن کرد و کمابیش، نمی توانید به این بها تحقق ببخشید.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3932,'آدمی تنها زمانی می کوشد چیزی را ثابت کند که نسبت به آن چیز آسوده نباشد.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3933,'نخستین آرزوی یک نویسنده این است که بگذارد کسی کتابش را بخواند.((جان اشتاین بک))',48,0,0,0,0,0);
INSERT INTO mysms VALUES(3934,'اندیشه، دانش یا باور ما نتیجه‌ی چندانی به دست نمی‌دهد؛ فقط کاری که می‌کنیم، پیامدهایی به همراه دارد.((جان راسکین))',49,0,0,0,0,0);
INSERT INTO mysms VALUES(3935,'بر این باورم که نخستین نشان انسان‌های به راستی بزرگ، فروتنی است.((جان راسکین))',49,0,0,0,0,0);
INSERT INTO mysms VALUES(3936,'با پول هرگز نمی توان یک سرباز خوب، آموزگار خوب، هنرمند خوب و یا کارگر خوب ساخت ... .((جان راسکین))',49,0,0,0,0,0);
INSERT INTO mysms VALUES(3937,'باید بخندید، اما نه با هدف مسخره کردن.((جان راسکین))',49,0,0,0,0,0);
INSERT INTO mysms VALUES(3938,'خواندن کتابهای خوب، تفکر در راز آفرینش، همنشینی با دوستان و گردش در طبیعت برای خوشبختی ما کافی است.((جان راسکین))',49,0,0,0,0,0);
INSERT INTO mysms VALUES(3939,'اگر به بچه کمی محبت نشان بدهید، او چندین برابر به شما مهر خواهد ورزید.((جان راسکین))',49,0,0,0,0,0);
INSERT INTO mysms VALUES(3940,'همه‌ی خوبی هنر به کمال جانی است که با جانی دیگر سخن می گوید و ارزشمندی آن برابر با بزرگی و شکوه جانی است که آن را بیان می‌دارد.((جان راسکین))',49,0,0,0,0,0);
INSERT INTO mysms VALUES(3941,'من کتاب می نویسم؛ زیرا می خواهم به مردم کمک کنم تا در کارشان موفق شوند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3942,'به گمان من انسان برای موفقیت در زندگانی باید بتواند در چهار زمینه استاد شود : مناسبات، تدارکات، نگرش و رهبری.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3943,'مسئله این نیست که بر دیگران اثر می گذاریم یا نه، بلکه آنچه اهمیت دارد چگونگی اثرگذاری است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3944,'موفقیت، کم و بیش در دسترس همه هست، اما دامنه توفیق در زندگانی شخص، بدون توان رهبری محدود است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3945,'توان رهبری آمیزه ای از مهارتهای گوناگون است که کمابیش می توان همه آنها را آموخت و اصلاح کرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3946,'رهبر برای موفقیت در کار باید از آسیاب روزگار عبور کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3947,'تا کسی نداند که نمی داند، رشد نمی کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3948,'رهبران موفق هیچ گاه دست از آموختن بر نمی دارند. فرایند آموختن هرگز تمام نمی شود و پیوسته آموختن پیامد چیرگی بر خویشتن و پشتکار است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3949,'اگر کسی استعداد طبیعی داشته باشد باز هم باید برای موفقیت تمرین کند و آماده شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3950,'قدرت حقیقی رهبری را نمی توان به کسی بخشید. قدرت فقط و فقط نتیجه نفوذ شخصی است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3951,'رهبری چیزی جز داشتن قدرت نفوذ و اثرگذاری بر دیگران نیست.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3952,'تفاوت اصلی بین رهبری و مدیریت این است که رهبران با استفاده از نفوذ خویش دیگران را به پیروی وامی دارند، اما کار اصلی مدیران، حفظ سیستم ها و فرایندها است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3953,'برای آگاهی از توان رهبری فرد، بهترین روش این است که از او بخواهیم تغییر مثبتی ایجاد کند. مدیران توانایی حفظ سمت و سوی حرکت را دارند، اما از تغییر دادن عاجزند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3954,'در دنیای کسب و کار، ابزار رهبری حقوق و درآمد است. بیشتر پیروان وقتی پای گذران زندگی به میان می آید، از رهبر خود به خوبی پیروی می کنند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3955,'در سازمان های داوطلبانه نمی توان پیروان را به زور وادار به همکاری کرد. اگر رهبر نفوذ نداشته باشد، کسی خواسته هایش را پاسخ نمی دهد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3956,'آن کس که مدعی رهبری است، اما پیرو ندارد، آبروی خود می برد و زحمت ما می دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3957,'وقتی انسان با دیگران به گونه ای ثمربخش ارتباط برقرار کند، شناسایی به وجود می آید و این شناسایی به نوبه خود منجر به نفوذ می شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3958,'رئیس، کارکنان را وادار به کار می کند، رهبر آنها را راهنمایی می کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3959,'رئیس متکی به قدرت رسمی است، رهبر متکی به خیرخواهی است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3960,'رئیس ترویج دهنده ترس است، رهبر تشویق کننده شور و دلبستگی به کار است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3961,'رئیس، عیب و ایراد را سرزنش می کند، رهبر آن را برطرف می کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3962,'کارکنان کاری ندارند که رهبر به چه میزان آگاه است، مگر اینکه مطمئن باشند رهبر به فکر آنها است. رهبری از قلب آغاز می شود نه از مغز.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3963,'بدون عشق به مردم نمی توان آنها را رهبری کرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3964,'هر پله روی پله قبلی بنا می شود و اگر پله قبلی را از زیر آن بکشند، فرو می ریزد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3965,'برای ثمربخش کردن رهبری، رهبر باید دیگر همکاران با نفوذ گروه را با خود از نردبان نفوذ بالا ببرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3966,'هر کسی به نوبه خود رهبر است، زیرا دست کم بر یک نفر نفوذ دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3967,'هر کسی نمی تواند رهبر بزرگی شود، اما می تواند در رهبری بهتر شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3968,'پروردگارا زندگانی مرا در خدمت به خلق قرار بده تا آنها را درک کنم!((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3969,'اگر می خواهید در همه شئون زندگانی موفق شوید، یکی از نخستین وظیفه ها مبارزه با هر نوع گرایش به بهانه تراشی است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3970,'اگر به جای توجه به نتیجه و دستاورد کار، برای سختی ها و مشکلات آن ماتم بگیرید، کم کم ناامید و دلسرد می شوید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3971,'اگر استعداد دارید، اما تلاش و تحرک شما به جایی نمی رسد، احتمالا بر خود مسلط نیستید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3972,'ما چیزهایی را آموزش می دهیم که می دانیم، ما مانند خودمان را بازسازی می کنیم. گندم از گندم بروید، جو ز جو.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3973,'زیاد کار کردن هنر نیست، هوشمندانه کار کردن هنر است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3974,'آدم همه کاره هیچ کاره است. آن زندگانی که همه چیزش مثل ساعت با دقت کار می کند، وجود خارجی ندارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3975,'بهترین کار آن است که از انجام آن لذت می بریم.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3976,'شغلی را بپذیر که عاشق آن باشی.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3977,'کارایی اساس بقا است، اما لازمه موفقیت ثمربخش بودن است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3978,'وقتی انسان اعتبار خود را هزینه می کند، نمی تواند رهبر بماند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3979,'رهبر به هر اندازه هم سابقه رهبری داشته باشد نمی تواند نسبت به رای و نظر همکاران بی توجه بماند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3980,'کارکنان لغزش های اتفاقی را به شرطی که بر اساس توانایی رهبر باشد و او را در حال رشد و بالندگی ببینند، می بخشند. اما به کسانی که در منش خود مرتکب لغزش می شود اعتماد نمی کنند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3981,'آرمان را نمی توان قرض گرفت، خرید یا گدایی کرد، بلکه باید از درون انسان بجوشد و بالا بیابد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3982,'هنرمند، بدون توجه به ماده سازنده، از آنچه در دست دارد اثری گرانبها پدید می آورد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3983,'بحران لزوما شخصیت انسان را نمی سازد، اما شکی نیست که آن را آشکار سازد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3984,'هر بار که راهی را انتخاب می کنیم، گامی در راه ایجاد شخصیت خود بر می داریم.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3985,'مردم از کسی لذت می برند که از زندگی لذت ببرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3986,'هیچ کس رهبری کسی را که خود را از دیگران بهتر می داند، نمی پذیرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3987,'اگر خود به خویشتن اعتماد ندارید، دیگران هم به شما اعتماد نخواهند کرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3988,'اگر هر لحظه حال و هوایی دیگر دارید، رفتارتان برای مردم غیر قابل پیش بینی می شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3989,'مردم به کسی که درصدد انجام کار در حد عالی باشد، احترام می گذارند، اما از توقعات و انتظارات غیر واقع بینانه بیزارند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3990,'مردم از کسی که به همه چیز و همه کس بدگمان باشد، دوری می جویند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3991,'آموزشگران، مطلب های ساده را می گیرند و پیچیده می کنند، ارتباط گران، مطلب های پیچیده را می گیرند و ساده می کنند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3992,'رشدتان می گوید که چه کسی هستید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3993,'کسی که پا را از مرز زندگی عادی فراتر می نهد، آرزویی بزرگ در دل دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3994,'آدم عادی منتظر می ماند تا فرد دیگری در او انگیزه ایجاد کند و تصور می کند که شیوه تفکر او نتیجه شرایط و زندگی اوست.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3995,'به هر قهرمان ورزش حرفه ای که بنگرید، استعداد فراوان می بینید. اما آنچه بهترین ها را به بالاترین سطح می رساند، فکر و ذهن است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3996,'رهبران را با مسایلی که درگیر می شوند بسنجید. هر کس لباسش را به اندازه خود می دوزد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3997,'مردم زمانی به دانایی شما اهمیت می دهند که بدانند به آنها اهمیت می دهید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3998,'رهبران می توانند از هر چیزی دست بکشند، جز از مسئولیت نهایی خویش.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(3999,'اگر نیازمند مردم باشید، امکان رهبری آنها را از دست می دهید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4000,'کسی که کار خود را تنها زمانی انجام می دهد که سر حال باشد یا انجام آن راحت باشد، توفیقی پیدا نخواهد کرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4001,'برای دستیابی به سبک زندگی منضبط، نخستین گام این است که عادت بهانه تراشی خود را ترک کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4002,'رهبران برخوردار از بینش و بصیرت می دانند که کدام نیمه را باور کنند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4003,'رمز کامیابی در این است که بر توانایی های خود تکیه کنید و آنها را بپرورانید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4004,'خیر و شر شما در دست خود شماست.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4005,'تنها راه برنده شدن با پول این است که دربند آن نباشید و با بزرگواری و گشاده دستی با پول خود کارهای باارزش انجام دهید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4006,'راه حفظ بزرگ منشی تنها این است که خود را به بذل و بخشش عادت دهید. از وقت، پول، توجه و منابع خود به دیگران ببخشید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4007,'بی خیالی در صدر چیزهایی است که رهبران باید از آن بترسند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4008,'امروز باید گامی شجاعانه بردارید تا فردا به اوج توانایی خود برسید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4009,'رهبر خوب پیروان خود را تشویق می کند تا چیزی را به او بگویند که نیاز به داشتن آن دارد، نه چیزی را که خوشایند اوست.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4010,'رهبران پیش از آنکه دست مردم را بفشارند، قلب آنها را تسخیر می کنند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4011,'رهبری که با شور و شوق به میدان آید، معمولا با شور و شوق روبرو می شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4012,'اگر کار دیروز به نظرتان بزرگ می رسد، امروز کار مهمی انجام نداده اید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4013,'هیچ کس نمی تواند هم مغرور باشد و هم خواستار آموختن.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4014,'شهامت رهبران بزرگ در تخقق آرمانشان زاییده شوق و علاقه آنهاست نه پست و مقام شان.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4015,'اگر نقاط ضعف یکدیگر را به رخ هم نکشیم زندگی زناشویی بهتر می شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4016,'خیلی ها غافل از نقاط قوت یکدیگر راهی دادگاه طلاق می شوند. این زوجها تابع افراط و تفریط هستند. اول دنبال نقاط قوت هم و برجسته کردن آن می روند و در نهایت تنها نقاط ضعف را می بینند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4017,'رمز برخورداری از نگرش خوب، داشتن شوق به تغییر است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4018,'اینکه امروز که هستیم، پیامد گزینش دیروز ما است. فردا هم همان خواهیم شد که امروز برمی گزینیم.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4019,'تغییر کردن یعنی انتخاب و میل به تغییر.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4020,'مشکل را زمانی می توان برطرف کرد که آن را به درستی شناخته باشیم.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4021,'بپذیرید که قدرت ایمان از ترس بیشتر است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4022,'اعتقاد یعنی یقین درونی، ایمان یعنی عمل بیرونی. اگر عزم و اراده خود را به زبان بیاورید علاوه بر تشویق شدن، احساس مسئولیت خواهید کرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4023,'فرق بین انسان دانا و انسان نادان در واکنشی است که به دانسته های خود نشان می دهند. دانا بر اساس آنچه می شنود عمل می کند و نادان می داند و عمل نمی کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4024,'عاشق تغییر باشید تا این عشق، شما را دگرگون کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4025,'عواطف انسان ناشی از افکار او است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4026,'با تغییر الگوهای فکری می توان احساس و عاطفه را تغییر داد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4027,'معمولاً روزهای اول، مشکل ترین دوران تغییر عادت است. در هم شکستن و تغییر دادن عادتهای ناپسند بسیار دشوار است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4028,'بزرگترین نبرد انسان علیه ناکامی نبردی است درونی نه بیرونی.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4029,'مهم نیست که هدفهای انسان در حوزه هنر، کسب و کار، مذهب، ورزش یا ارتباط با مردم قرار دارند. تنها راه پیشرفت این است که خطاها در مراحل اولیه باشند، انسان خطا را بپذیرد و در اثر خطا کردن، پس نزند و پیش برود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4030,'انسان به هر جایی که می رسد مدیون راهی است که می رود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4031,'انسانها به طور طبیعی میل به لَختی و سکون دارند. به همین علت، خودسازی کاری است نفس گیر.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4032,'در دل هر موفقیت، تحمل سختی و ناملایمات نیز وجود دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4033,'پیروزیهای انسان نتیجه شکست های مکرر و تلاش مستمر برای بالندگی است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4034,'کسی که خطا نمی کند و ناکام نمی شود، چه بسا هرگز حرکت نمی کند و به جلو نمی رود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4035,'من معتقدم موفقیت زمانی به دست می آید که یک قدم جلوتر برویم؛ یعنی ضمن استقبال از ناملایمات و سختی ها شکست را بخشی از زندگانی خود بدانیم.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4036,'دیکته نانوشته غلط ندارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4037,'در رندگانی هیچ چیز به اندازه سختی و ناکامی، انسان را بهبودپذیر نمی کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4038,'ناملایمات به شرطی که اختیار خود را به دست آنها نسپاریم، به جای آسیب رساندن سبب بهبود و تغییر وضع کلی انسان می شوند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4039,'انسان موفق کسی است که شکست می خورد، اما خود را شکست خورده نمی داند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4040,'زیانبارترین احساس پس از شکست، از دست دادن اعتماد به نفس و مشکوک شدن به توانایی و صلاحیت خویش است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4041,'همه آدمهای موفق، شکست را تجربه کرده اند، اما هرگز خود را شکست خورده و وامانده نپنداشته اند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4042,'ملاک موفقیت یا شکست، نگرش است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4043,'قدرتی که در اختیار یک انسان شریف باشد بسیار سودمند است، اما قدرت خودکامگان سرچشمه ویرانیهای هولناک است. قدرت به خودی خود نه سازنده است و نه ویرانگر، نه سبب امنیت می شود و نه سبب موفقیت.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4044,'همه خودکامگان- حتی آنها که خیرخواه هستند- سرانجام قدرت را از دست می دهند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4045,'موفقیت، مساوی با رسیدن به هدف نیست، بلکه خود سفر است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4046,'انسان هر کاری را می تواند انجام دهد، اما همه ی کارها را نمی تواند بکند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4047,'نگرش، حقیقت هر کس را برملا می کند و تظاهر آن، کارها و حرکات او است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4048,'نگرش، حالتی ذهنی و درونی است. چه بسا کسی که نگرش نادرست دارد مرتکب کارهای غیر اخلاقی و غیر قانونی نشود، اما نگرش نادرست او سبب از هم پاشاندن تیمی خواهد شد که در آن عضویت دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4049,'هیچ انسانی کامل نیست، اما آدمهایی که خیال می کنند کامل هستند هرگز به درد کار تیمی نمی خورند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4050,'کینه ورزیدن هرگز کاری سازنده و مناسب نیست و اگر در تیمی حالت گذشت نباشد، به زودی آن تیم دچار آسیب و زیان خواهد شد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4051,'یکی از بینش هایی که به انسانها لطمه می زند، آرزوی برابری است؛ این آرزو سرچشمه حسادت و تنگ نظری است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4052,'نگرش انسان با استعداد و ظرفیت او رابطه مستقیم دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4053,'نگرش، احساسی درونی است که رفتار، نماد بیرونی آن است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4054,'گاهی وقتها می توانیم طرز فکر خودمان را در ظاهر پرده پوشی کنیم و کسانی که ما را می بینند گول بزنیم. اما عمر پرده پوشی چندان دراز نیست. نگرش انسان آنقدر با حجاب می جنگد تا به طور طبیعی جلوه کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4055,'انسان نمی تواند در درون دچار تلاطم و حرکت باشد، اما در بیرون آرام و بی حرکت بماند. چیزی نمی گذرد که تلاطم های درونی بر جلوه ها و نمودهای بیرونی اثر می گذارند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4056,'برخی از نردبان نگرش سازنده ترقی می کنند و برخی به دلیل داشتن نگرش نادرست سقوط می کنند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4057,'ما از جهتی مثل هواپیما هستیم. اگر بینی ما رو به بالا باشد رو به صعود داریم و اگر رو به پایین باشد خطر سقوط در پیش است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4058,'وقتی طرز فکر ما به پنیر بدبو آغشته شود، تمام دنیا بوی بد می دهد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4059,'انسانها به جای پذیرش مسئولیت رفتار خویش به انتظار تغییر دیگران نشسته اند و یا منتظرند دنیا عوض شود!((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4060,'بزرگترین دستاوردهای تاریخ، کار انسانهایی بوده که تنها اندکی از انبوه همتایان خود پیشتر بوده اند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4061,'تفاوت آدمها با هم جزیی است، اما همان تفاوت جزیی سبب اختلافهای کلی می شود. تفاوت جزیی آنها هم همان نگرش و نوع نگاه آنها است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4062,'بزرگی و کوچکی اختلاف آدمها در این است که نگرش آنها سازنده است یا تباه کننده.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4063,'زندگانی را می توان به سنگ چاقو تیزکنی تشبیه کرد. تیز شدن چاقو یا بریدن دست نتیجه ی نیت و قصد ما است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4064,'دارندگان دید بلند به هنگام رویارویی با مشکلات با تحمل جنبه های ناگوار و دفع آنها، مشکلات را به فرصت مبدل می کنند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4065,'وقتی خدا می خواهد کسی را تربیت کند او را به مدرسه های پر زرق و برق نمی فرستد، بلکه او را در مدرسه احتیاج تعلیم می دهد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4066,'رهبران بزرگ در هنگامه گرفتاریها پدید می آیند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4067,'نگرش سازنده و قوی، انسان را در دژی مستحکم قرار می دهد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4068,'بچه همیشه از محیط خود، اولویتها، نگرشها و علایق و اصول اعتقادی را می گیرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4069,'مسلم بدانید که باورهای انسان بر نگرش او مؤثر است؛ حتی اگر آن باورها درست نباشند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4070,'ما کمتر به یاد می آوریم که معلم ما به ما چه آموخته، اما کمتر از یاد می بریم که ما را چقدر دوست داشته است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4071,'پیش از درک و فهم درس، انسان به علاقه و توجه معلم توجه می کند. مدتها می گذرد و آموخته ها از یاد می روند، اما احساس خوشایند ناشی از پذیرش و تایید از یاد نمی رود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4072,'دیگران ما را آنگونه می بینند که ما خودمان را می بینیم.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4073,'نمی توان بچه ها را در حصار گذاشت، به گونه ای که با تجربه های جدید آشنا نشوند؛ تجربه هایی که ممکن است ناگوار هم باشند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4074,'وقتی شریک زندگانی ناجور باشد و میل به تغییر نداشته باشد، انسان کمابیش زندانی ناسازگاری می شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4075,'ما با آنچه به دست می آوریم زندگانی می کنیم، اما با آنچه می دهیم زندگانی می بخشیم.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4076,'هرچه رهبر بزرگتر باشد میزان از خودگدشتگی او باید بیشتر شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4077,'خلاقیت باعث پرورش قدرت انطباق پذیری می شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4078,'به این فکر نکنید که چرا نمی توان این کار را انجام داد، به این بیندیشید که چگونه می توان این کار را انجام داد. در برخورد با هر چالش جدید منتظر راه حل های غیر متعارف شوید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4079,'کارهای عظیم مستلزم کار گروهی عظیم هستند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4080,'هم گروه خود را نه به چشم رقیب، که به چشم کسی ببینید که در سعی و کوشش با شما شریک است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4081,'برای آنکه شراکت را جایگزین رقابت کنید، باید سوء ظن را کنار بگذارید و در مقام حمایت از یکدیگر برآیید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4082,'باور داشتن به کاری که انجام می دهید یک مطلب است و متعهد بودن کامل به آن کار، مطلبی دیگر.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4083,'تعهد، خصلتی شخصی است که به ما امکان می دهد به هدفهای خود دست پیدا کنیم. احساسات انسان پیوسته در نوسان است، اما تعهد باید موضوعی ثابت و پابرجا باشد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4084,'تعهد اغلب در دل مشکلات کشف می شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4085,'دست و پنجه نرم کردن با مشکلات بر عزم راسخ اشخاص می افزاید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4086,'باید دانست که تعهد و استعداد با هم ارتباطی ندارند، مگر اینکه شما آنها را به هم ربط دهید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4087,'روزی متوجه می شوید ارزشهایی دارید که نسبت به آنها پایبند نیستید؛ خود را به این ارزشها پایبند و متعهد سازید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4088,'شما نمی توانید نسبت به افراد غیر متعهد، متعهد باشید و انتظار داشته باشید که از آنها تعهد ببینید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4089,'اغلب مشکلات ارتباطی با نزدیک شدن اشخاص به یکدیگر از میان می رود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4090,'بدون ارتباط مناسب، اتحادی شکل نمی گیرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4091,'قابل اعتماد بودن فراتر از توانمندی تنها است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4092,'اگر کسی پیوسته دستور کارش این باشد که منافع خودش را مقدم بر منافع تیم بداند، غیر قابل اعتماد بودن خود را ثابت می کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4093,'قابل اعتماد بودن چیزی فراتر از پذیرش مسئولیت است. پذیرش مسئولیت باید با قضاوت درست همراه باشد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4094,'هرگز فواید بلندمدت قابل اعتماد بودن را دست کم نگیرید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4095,'نظم و ترتیب یعنی انجام دادن کارهایی که به آن علاقه ندارید تا سرانجام بتوانید کارهایی را که به راستی دوست دارید، انجام دهید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4096,'انضباط، پرداختن بهای چیزهای کوچک است تا بتوانید چیزهای بزرگتری بخرید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4097,'برای اینکه از مغزتان استفاده کنید، نیازی به این نیست که نابغه باشید. کافی است از ذهنی که خداوند به شما ارزانی کرده، استفاده کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4098,'اگر گاه واکنش های احساسی تند نشان می دهید، اولین کاری که باید بکنید این است که حرفهایی را که نباید بزنید، نزنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4099,'انسان بدون انضباط در زندانی نامرئی محبوس است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4100,'اعضای هر تیمی همیشه ستایشگر بازیکنی هستند که بتواند به آنها کمک کند تا به سطح جدیدی برسند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4101,'کسی که به دیگران بها می دهد، قادر است آنها را به سطح کاملاً جدیدی ارتقا دهد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4102,'پیش از ْآنکه دیگران شما را باور کنند، شما آنها را باور کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4103,'پیش از اینکه دیگران به شما خدمت کنند، شما به آنها خدمت کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4104,'به دیگران ارزش دهید، پیش از اینکه آنها به شما ارزش دهند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4105,'اشخاص معمولاً به کسی که به آنها توجه کرده است، علاقه نشان می دهند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4106,'کاری به اندازه غنا بخشیدن به زندگی دیگران اغنا کننده و ارزشمند نیست.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4107,'نمی توانید دست روی دست بگذارید و انتظار داشته باشید که بر بی حالی و رخوت خویش غلبه کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4108,'برای غلبه بر ترس باید ترس را احساس کنید و با این حال، دست به کار شوید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4109,'تنها راه برای درهم شکستن ترس آن است که با ترس خود روبرو شوید و به آن کاری که از آن می ترسید، اقدام کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4110,'برای غلبه بر ترس باید آستین بالا بزنید و از یک جایی شروع کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4111,'اگر منتظر بمانید که ابتدا احساس را به دست آورید و بعد دست به کار شوید، ممکن است هرگز اشتیاقی در خود نیابید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4112,'بعد از آنکه جرقه اشتیاق در وجودتان زده شد، تنها کاری که برایتان باقی می ماند این است که این آتش را شعله ور نگه دارید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4113,'هدفمند بودن یعنی اینکه در هر لحظه و در هر روز، کار درستی انجام دهیم.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4114,'اشخاص موفق هدفمند هستند، پراکنده فکر نمی کنند، می دانند که چه کار می کنند و چرا آن کار را انجام می دهند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4115,'بیشتر پیروزیهای زندگی با پیروزیهای کوچک به دست می آید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4116,'درباره طول مدت عمرمان کاری از ما ساخته نیست، اما می توانیم درباره نحوه گذراندن آن تصمیم بگیریم.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4117,'اگر موفقیت تیم با از خودگذشتگی شما کسب می شود، چه بهتر که این از خودگذشتگی را نشان بدهید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4118,'با آنکه می دانید به کجا می خواهید برسید، اگر مسیر درستی انتخاب نکنید، هرگز به مقصد نمی رسید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4119,'بزرگترین متحد و هم پیمان آمادگی، شجاعت و بزرگترین دشمن آن، ترس است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4120,'آماده شدن مستلزم آینده نگری است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4121,'یکبار غافلگیر شدن مجاز است، اما اگر این اتفاق دوباره بیفتد، نشانه آن است که از آمادگی لازم برخوردار نیستید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4122,'ممکن است آماده شدن تضمینی برای پیروزی نباشد، اما بی شک شما را در این موقعیت قرار می دهد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4123,'هر کسی که عقایدش را بیش از سایر افراد گروهش دوست بدارد، کارش را از پیش می برد، اما به تیمش لطمه می زند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4124,'ما در جامعه ای زندگی می کنیم که بیماری هدف دارد. بسیاری از مردم تلاش می کنند تا به جایی برسند، اما وقتی رسیدند، همانجا متوقف می شوند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4125,'بیشتر اشخاص در سه موقعیت، اقدام به تغییر خود می کنند: از بس که رنج می برند به این کار تن می دهند؛ از بس که آموخته اند مایل به این کار می شوند؛ یا از بس که عایدی دارند از توان انجام این کار برخوردارند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4126,'از خودگذشتگی ساده نیست، اما ضرورت دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4127,'عادت کنید که به دیگران کمک کنید، خواهید دید که نمی توانید از این کار دست بکشید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4128,'همه چیز به طرز برخورد و نگرش شما بستگی دارد. مسایل می توانند شما را با مشکل و یا با موفقیت روبرو سازند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4129,'از آنچه در اختیار دارید مایه بگذارید، اما بیشتر از آن خیر.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4130,'با عزم راسخ کار کنید، اسیر دست سرنوشت نشوید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4131,'به وقت کوشش باید کوشش کرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4132,'وقتی کارتان تمام شد، دست از تلاش بردارید، نه وقتی که خسته شدید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4133,'اگر همیشه چشم به عقربه های ساعت دارید تا زمان کار تمام شود، در این عادت خود تجدید نظر کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4134,'این باور که یک شخص می تواند به تنهایی کار بزرگی انجام دهد، افسانه ای بیش نیست.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4135,'افراد عملاً در یک بازی شرکت می کنند، اما تیمها هستند که مقامهای قهرمانی را به دست می آورند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4136,'برای انجام کاری به حقیقت درست، از آرزوی خویش دست بکشید و خود را آماده کنید تا عضو یک تیم شوید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4137,'برای برآوردن میزان هوشمندی یک حاکم، نخستین روش این است که ببینید چه افرادی را به دور خود گرد آورده است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4138,'تنها رهبرانی که احساس امنیت می کنند به دیگران قدرت می دهند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4139,'در فرهنگی که مدالهای طلای فردی را ستایش می کنند و شخص به جای پذیرفتن مسئولیت، برای حقوق خود می جنگد، مردم به طور رایج از درک دیدگاه کلی ناتوانند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4140,'هر کسی که عادت دارد آدمها را به دور خود جمع کرده و از آنها تنها برای منافع خودش بهره گیرد، سازنده ی یک تیم نیست، بلکه یک دیکتاتور است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4141,'تیمهای برنده بازیکنانی دارند که منافع تیم را بر منافع خودشان مقدم می شمارند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4142,'تنها در صورتی افراد یک تیم می توانند فداکاری کرده و با یکدیگر همکاری کنند که بدانند به سوی چه هدفی در حرکت هستند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4143,'یک تیم تنها هنگامی می تواند به سطح بالا صعود کند که بازیکنان با هم متحد شوند و امیال شخصی شان را رها کنند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4144,'رویدادهای خوب برای تیمی پیش می آید که هر بازیکن در جایگاهی قرار بگیرد که بیشترین کارآیی را در آن دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4145,'اگر رویایی دارید، به یک تیم نیازمندید تا آن را تحقق بخشید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4146,'رویای بزرگ با یک تیم بد چیزی فراتر از یک کابوس نیست.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4147,'حقیقت این است که تیم شما باید به اندازه ی رویای شما بزرگ باشد. در غیر این صورت نمی توانید به رویای خود جامه ی عمل بپوشانید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4148,'اگر می خواهید به کوه اورست صعود کنید، باید تیمی به عظمت کوه اورست در دست داشته باشید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4149,'اگر تیمی با عظمت و با یک رویای کوچک داشته باشید بسیار بهتر از رویایی عظیم با یک تیم کوچک است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4150,'وقتی تیمی که در اختیار دارید با تیم رویاهای شما هماهنگی ندارد، در این صورت تنها دو راه پیش رو دارید: یا از رویای خود دست بکشید و یا تیم خود را رشد دهید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4151,'توانایی یک زنجیر می تواند متاثر از ضعیف ترین حلقه ی آن باشد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4152,'توانایی یک تیم تحت تاثیر ضعیف ترین حلقه ی آن است. مهم نیست که چه میزان افراد تیم تلاش می کنند تا آن را توجیه کنند، جبران نمایند و یا پنهان کنند؛ حلقه ی ضعیف سرانجام برملا خواهد شد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4153,'در حقیقت، یکی از تفاوتهایی که بین پیروان و رهبران وجود دارد، عملکرد است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4154,'اگر خودتان پیشرفت نکرده باشید نمی توانید تیم خود را به سوی پیشرفت و ترقی سوق دهید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4155,'از محدوده ی آسایش خود خارج شوید. تا وقتی از آنچه در گذشته انجام داده اید پا را فراتر نگذارید، آگاه نخواهید شد که چه توانایی هایی دارید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4156,'آنجا که درستی به بینش سوخت می رساند، شور و اشتیاق آتش را به ارمغان می آورد. آتش حقیقی دلبستگی و باور پایدار، تنها از درون برمی خیزد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4157,'یک بینش اصولی باید بر مبنای گذشته ساخته شود، نه اینکه گذشته را نادیده بگیرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4158,'افراد تا از گذشته آگاهی نداشته باشند، به آینده دسترسی نخواهند داشت.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4159,'بینش بدون راهبرد کمی بیش از خواب و خیال است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4160,'نگرشهای خوب در بین بازیکنان، پیروزی تیم را تضمین نمی کند، اما نگرشهای بد، شکست آن را تضمین می کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4161,'هر کدام از ما منحصر به فرد آفریده می شویم و عملکرد جداگانه ای داریم و در نتیجه، باید به گونه ای متفاوت با ما برخورد شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4162,'بهترین تحسینی که می توانید دریافت کنید این است که روی شما حساب شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4163,'برخی فکر می کنند که اگر دیگران سخت کار کنند، می توان از توانایی آنها بهره برداری کرد، اما این مطلب به هیچ وجه حقیقت ندارد. اگر کسی بهای پیروزی را نپردازد، با شکست، بهای آن را خواهد پرداخت.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4164,'قهرمان شدن بهای سنگینی دارد، اما در اوج ماندن بهایی بیش از قهرمانی دارد و بهتر از پیش شدن حتی بیش از دو مورد پیش بها دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4165,'هیچ کس با هدف باختن، هیچ کاری را آغاز نمی کند. مشکل بیشتر پذیرفتن این باور نادرست است که زمانی فرا می رسد که ناگهان موفقیت ارزانتر از گذشته به دست خواهد آمد؛ اما به طور رایج، زندگی این گونه عمل نمی کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4166,'بیشتر مردم در دامنه ی کوه، صعود را رها نمی کنند، بلکه آنها در نیمه ی راه صعود را رها می کنند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4167,'کار تیمی در یک چشم بر هم زدن نمی تواند توسعه یابد. تیمها آرام آرام و با حرکت کند قدرت می یابند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4168,'تیم شما تنها در صورتی به توانایی خود دست می یابد که شما به توانایی شخصی خود دست یابید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4169,'همیشه باید چیزی را از دست داد تا پیشرفت کرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4170,'یک علامت تجاری یک هویت زنده است و به طور فزاینده ای در طول زمان، تضعیف یا تقویت می شود که نتیجه ی هزار حرکت جزیی است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4171,'هیچ چیز بیشتر از رهبری که از تصمیم گیری عاجز است، نمی تواند یک تیم را دلسرد کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4172,'تلاش نکنید همگان را با هوشیاری خود گیج کنید، بلکه با روشنی [ بیان ] خود، افراد را تحت تاثیر قرار دهید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4173,'هر کسی بدون توجه به شغل خود و یا سابقه ای که از او دارید، شایستگی آن را دارد که مورد احترام قرار گیرد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4174,'تیمها همواره منعکس کننده ی رهبرانشان هستند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4175,'بهترین رهبران گوش می دهند، دعوت می کنند و سپس به مشارکت تشویق می کنند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4176,'تیمها مثل جوامع کوچک هستند و تنها وقتی توسعه می یابند که افراد آن جوامع برای یکدیگر قیافه نمی گیرند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4177,'در وحدت، قدرت عجیبی وجود دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4178,'رهبری مایه ی تفاوت دو تیم با استعداد یکسان است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4179,'اگر به هر تیمی که موفقیت بزرگی به دست آورده نگاهی بیندازید متوجه می شوید که رهبری قدرتمند دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4180,'رهبران ابتدا خودشان را به سطح بالاتری سوق می دهند، سپس اطرافیان خود را ترقی می دهند؛ الگو بودن مقدم بر رهبری است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4181,'همه مهم هستند، اما همه با هم برابر نیستند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4182,'اگر نگاهی به پشت صحنه های هر کار بزرگی بیندازید، همواره یک رهبر مقتدر را پیدا می کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4183,'وقتی دارید پیروز می شوید هیچ چیز آزار دهنده نیست.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4184,'وقتی تیم روحیه ی بالایی دارد، به جای تمرکز روی مشکلات، روی توانایی خود متمرکز می شود.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4185,'هیچ چیز ناخوشایندتر از آن نیست که در تیمی باشید که هیچ کس نمی خواهد در آن باشد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4186,'تیم تنها زمانی تغییر می کند که افراد آن به خودشان ایمان داشته باشند؛ به عنوان یک رهبر باید آن ایمان را آغاز کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4187,'میل به تغییر، بدون انرژی برای تغییر، تنها افراد را دلسرد می کند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4188,'مردم آنچه را که می بینند انجام می دهند. بهترین راه برای آنکه افراد یاد بگیرند که شما چه چشم داشتی از آنها دارید این است که خودتان الگوی آن شوید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4189,'پیروزی های کوچک به اعضای کم استعدادتر تیم کمک خواهد کرد تا اطمینان کسب کرده و موفق شوند.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4190,'هنگامی که تیم را به راستی به حرکت درآورید، آنگاه می توانید آن را هدایت کنید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4191,'اگر می خواهید عرض خیابان را طی کنید، یک یا دو درجه تغییر مسیر، آسیبی به شما نخواهد زد. اما اگر می خواهید از عرض اقیانوس بگذرید چند درجه خطا در محاسبه می تواند شما را به دردسر بیندازد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4192,'ایثار [ =برگزیدگی ] و اشتیاق شما، به عملکردتان کمک خواهد کرد و به برخی از هم تیمی های شما الهام خواهد بخشید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4193,'پیشرفت دادن مردم در هر راهی نتیجه ی خوب در پی دارد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4194,'وقت، پول و تلاش مورد نیاز برای توسعه ی اعضای تیم در عرض یک شب تیم را تغییر نمی دهد، اما توسعه ی آنها همواره نتیجه های ثمربخش به بار می آورد.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4195,'تشخیص این امر که افراد تیم ارزش توسعه را دارند، نخستین گام در تشکیل یک تیم بهتر است.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4196,'اگر شما پیشوا هستید، گناه را به گردن بگیرید، اما هرگز امتیاز را به خود نسبت ندهید.((جان ماکسول))',50,0,0,0,0,0);
INSERT INTO mysms VALUES(4197,'عشق به زندگی از راه کار، به معنای دوستی و یکدلی با درونی‌ترین راز زندگی‌ست.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4198,'تا هنگامی که کاری با شور و اشتیاق درنیامیزد، پوچ و میان‌تهی خواهد بود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4199,'هنگامی که به بالای کوه رسیدید، تازه آغاز بالا رفتن است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4200,'همه آنچه در خلقت است، در درون شماست و هر آنچه درون شماست، در خلقت است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4201,'آرامش گهواره ای است بر دامن خاک و سنگ پله هایی به جانب افلاک.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4202,'شادمانی، اسطوره ای است که در جستجویش هستیم.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4203,'گنجی که در اعماق نامحدود شما حبس شده است، در لحظه ای که خود نمی دانید، کشف خواهد شد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4204,'ما دو چیز را می‌توانیم برای فرزندانمان به ارث بگذاریم: نخست، ریشه‌هایی که آنها را بر زمین پابرجا نگه دارد و دوم، بال‌هایی که آنها را در آسمان به پرواز دربیاورد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4205,'انسانها بر دو گونه اند: یکی، او که در تاریکی بیدار است و دیگری، او که در روشنایی خواب است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4206,'زندگی روزمره شما پرستشگاه و نیز دیانت شماست.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4207,'قلب شما در سکوت و آرامش به اسرار روزها و شبها شناخت می یابد، اما گوشهایتان در حسرت و آرزویند که آوای چنین شناختی را که بر قلبهایتان فرود می آید، بشنوند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4208,'جهت الهی شما دریایی پهناور و بی ساحل است. ذات الهی شما از ازل پاکیزه بوده و تا ابد نیز خالص و پاک خواهد ماند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4209,'اگر از دوست خود جدا شدی، مبادا که بر جدایی اش افسرده و غمین گردی؛ زیرا آنچه از وجود او در تو دوستی و مهر برانگیخته است، ای بسا که در غیابش روشن تر و آشکارتر از دوران حضورش باشد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4210,'به روزگار شیرین رفاقت سفره خنده بگسترید و نان شادمانی قسمت کنید. به شبنم این بهانه های کوچک است که در دل، سپیده می دمد و جان تازه می شود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4211,'ایمان از کردار جدا نیست و عمل از پندار.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4212,'کار، تجسم عشق است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4213,'اگر در مورد شما بد گفتند سکوت کنید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4214,'دهش (بخشش)، آنگاه که از ثروت است و از مکنت، هر چه بسیار، باز اندک باشد، که واقعیت بخشش، ایثار از خویشتن است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4215,'سزاوار است دوستی را بخواهی که به روزهایت تلاش و به شبهایت آرامش بخشد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4216,'گروهی از مردم اندکی از ثروت کلان خویش را می بخشند و آرزویی جز شهرت ندارند. این خودخواهی و این شهرت پرستی که به طور ناخودآگاه گرفتارش هستند بخشش آنها را تباه می سازد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4217,'اگر گام در معبدی نهادی تا اوج فروتنی و هراس خود را نشان دهی، برای همیشه برتری کسی را نسبت به کس دیگر نخواهی یافت. برای تو کافی است گام در معبدی نهی، بی آنکه کسی تو را ببیند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4218,'آن چه امروز از آنِ شما است، یک روز به دیگری سپرده می شود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4219,'دین تحقق پندار است و پهنه پندار.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4220,'ای که در رنج و عذابی! تو آنگاه رستگار می شوی که با ذات و هویت خویش یکی گردی.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4221,'هشدار! تنها به عزم نیاز اگر به معبد درون شوید، هرگز هیچ نیابید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4222,'چون عاشقی آمد، سزاوار نباشد این گفتار که : خدا در قلب من است، شایسته تر آنکه گفته آید : من در قلب خداوندم.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4223,'شما می توانید بانگ طبل را مهار کنید و سیم های گیتار را باز کنید، اما کدامیک از فرزندان آدم خواهد توانست چکاوک را در آسمان از نوا باز دارد؟((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4224,'اگر به دیدار روح مرگ مشتاقید، هم به جسم زندگی روی نمایید و دروازه های دل بدو برگشایید که زندگانی و مرگ یگانه اند؛ همچنان که رودخانه و دریا.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4225,'کسی که بخشش می کند، زمانی به نشاط حقیقی دست می یابد که پس از جستجوی فراوان نیازمندی را پیدا کند که عطای وی را بپذیرد. تلاش برای یافتن چنین شخصی، لذت بخش تر از ایثار است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4226,'درختان، شعرهایی هستند که زمین بر آسمان می نویسد و ما آنها را بریده و از آنها کاغذ می سازیم تا نادانی و تهی مغزی خویش را در آنها به نگارش درآوریم.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4227,'اندوه و نشاط همواره دوشادوش هم سفر می کنند و در آن هنگام که یکی بر سفره ی شما نشسته است، دیگری در رختخوابتان آرمیده باشد. شما پیوسته چون ترازویی بی تکلیف در میانه ی اندوه و نشاط هستید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4228,'در پهنه پندار و ربودگی خیال، فراتر از پیروزیهای خود و فروتر از شکستهای خود نروید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4229,'سخاوت، بخشیدن بیشتر از توان است و غرور، ستاندن کمتر از نیاز.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4230,'ارزش انسان به داشته هایش نیست، به چیزی است که آرزوی به دست آوردنش را دارد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4231,'ما همگی رزم آوران پیکار زندگی هستیم، اما برخی پیشرویم و برخی دنباله رو.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4232,'توان زندگی به چگونگی نگرش ما به زندگی وابسته است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4233,'اگر زیبایی را آواز سر دهی، حتی در تنهایی بیابان، گوش شنوا خواهی یافت.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4234,'خطاست اگر بیندیشیم که عشق، نتیجه ی همنشینی درازمدت و با هم بودنی مجدانه است. عشق، ثمره ی خویشاوندی دو روح آشناست و اگر این خویشاوندی در لحظه ای تحقق نیابد، در طول سالیان هم تحقق نخواهد یافت.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4235,'فضایی بین پندار و عمل وجود دارد که با پشتکار پیموده می شود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4236,'اگر سزاوار است که دوست با جزر زندگی ات آشنا شود، بگذار تا با مد آن نیز آشنا گردد، زیرا چه امیدی است به دوستی که می خواهی در کنارش باشی ، تنها برای ساعتها و یا قلمرو مشخصی؟((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4237,'هیچ رابطه ی انسانی، وجود دیگری را به تملک خود در نمی آورد. در دوستی یا عشق، هر دو نفر در کنار هم دستهای خود را برای یافتن چیزی که یک دست به تنهایی قادر به یافتن آن نیست دراز می کنند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4238,'زناشویی برای هیچ کس هیچ حقی جز آنکه به دیگری می دهد پدید نمی آورد. زناشویی برای هر کس همان قدر آزادی می آورد که آن کس به دیگری می دهد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4239,'در میان مردمان فهمیده، استوارترین اساس زناشویی، دوستی و رفاقت است؛ اشتراک در علایق یکدیگر و توانایی در به سامان رساندن باورها و درک اندیشه ها و رویاهای یکدیگر به گونه ای مشترک.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4240,'چه تفاوتی دارد که در شهر بزرگی زندگی کنی یا در شهری کوچک؛ زندگی راستین در درون ماست.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4241,'قلبت را دنبال کن...قلب تو برای هر کار بزرگی راهنمای درستی است. تقدیر هر کاری که انجام می دهی با آن عنصر قدسی که درون هر یک از ماست تعیین می شود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4242,'به من می گویند اگر خودت را بشناسی همه ی انسانها را شناخته ای.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4243,'جامهای یکدیگر را پر کنید، اما از جام واحد ننوشید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4244,'گرگها شبانگاه به گوسفندان حمله می کنند، اما آثار خون گوسفندان تا دمیدن نور آفتاب همچنان بر روی زمین باقی خواهد ماند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4245,'در رنجی که ما می بریم، درد نه تنها در زخمهایمان، که در اعماق قلب طبیعت نیز حضور دارد. در تغییر هر فصل، کوهها، درختان و رودها ظاهری دگرگونه می یابند؛ همان گونه که انسان در گذر عمر با تجربه ها و احساساتش دگرگون می شود. در دل هر زمستان، تپشی از بهار و در پوشش سیاه شب، لبخندی از روشنایی نمایان است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4246,'آن که گرفتار رنج و عذاب شده، اما با ذات و هویتش همنوایی دارد، همانند کشتی است که سکانش درهم شکسته و در گوشه و کنار جزایر و دریاها سرگردان است و از هر سو در بر گرفته شده با خطرها؛ ای بسا که غرق نشود و به ته دریا فرو نرود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4247,'مردم! هشدار! که زیبایی، زندگانی است، آن زمان که پرده گشاید و چهره برنماید؛ اما زندگی، شمایید و حجاب خود، شمایید. زیبایی، قامت بلند ابدیت است، نگران منتهای خویش در زلال آینه؛ اما روشنی آینه شمایید و نهایت جاودانه شمایید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4248,'خداوندا، نمی توانیم از تو چیزی بخواهیم، که تو نیازهای ما را می دانی، پیش از اینکه در ما پدیدار شود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4249,'سکوت، دردناک است؛ اما در سکوت است که همه چیز شکل می گیرد و در زندگی ما لحظه هایی هست که تنها کار ما باید انتظار کشیدن باشد. درون هر چیز در اعمال هستی، نیرویی هست که چیزی را می بیند و می شنود که هنوز قادر به درکش نیستیم، هر آنچه امروز هستیم، از سکوت دیروز زاده شده است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4250,'و کدامین ثروت است که نگاه دارید تا ابد؟ آنچه امروز شما راست، یک روز به دیگری سپرده شود. پس امروز به دست خویش بخشش کنید، باشد که شهد گوارای سخاوت، نصیب شما گردد، نه مرده ریگی وارثانتان.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4251,'این کودکان فرزندان شما نی اند، آنان پسران و دختران اشتیاق حیاتند و هم از برای او. از شما گذر کرده و به دنیا سفر کنند، اما از شما نیایند. همراهی تان کنند، اما از شما نباشند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4252,'نفرت به همان اندازه ی دوست داشتن، خوب است. یک دشمن می تواند به خوبی یک دوست باشد. برای خود زندگی کن، زندگیت را بزیی، سپس به راستی دوست انسان خواهی شد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4253,'درون من جایی است که تنها در آن زندگی می کنم.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4254,'حسود بر من حسد می ورزد، بی آنکه خود بداند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4255,'دوست برای آن است که نیازت را برآورد، نه اینکه تهی بودنت را پر کند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4256,'ای شکست من، جسارت جاویدان من! من و تو به طوفان می خندیم.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4257,'نفرین بر او که با بدکاری که به اندرز خواهی آمده همدستی کند. زیرا هم رأیی با بدکار مایه ی رسوایی و گوش دادن به دروغ، خیانت است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4258,'هیچ کس نمی تواند چیزی را بر شما آشکار سازد، مگر آنچه که از پیش در طلیعه ی ناخودآگاه معرفت تان قرار داشته است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4259,'اندیشه ها جایگاهی والاتر از دنیای ظاهری دارند. چه حقیر است و کوچک ، زندگی آنکه دستانش را میان دیده و دنیا قرار داده و هیچ نمی بیند جز خطوط باریک دستانش؛ در خانه ی نادانی، آینه ای نیست که روح خود را در آن به تماشا بنشیند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4260,'براستی آیا این خداوند است که انسان را آفریده است یا عکس آن؟ خداوند، درهای فراوانی ساخته که به حقیقت گشوده می شوند و آنها را برای تمام کسانی که با دست ایمان به آن می کوبند، باز می کند. نیکی در انسان باید آزادانه جریان و تسرِی یابد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4261,'همه آنچه در خلقت است، در درون شماست و هر آنچه درون شماست، در خلقت است. طبیعت با آغوشی باز و دستانی گرم از ما استقبال کرده و می خواهد که از زیبایی اش لذت بریم. چرا انسان باید آنچه را در طبیعت ساخته شده از بین ببرد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4262,'دوستی با کار، عشق به زندگی است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4263,'زندگی بدون عشق مثل درختی بدون شکوفه و بدون برگ و بار است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4264,'آن هنگام که روحم عاشق جسمم شد و جفت گیری این دو سر گرفت من بار دیگر متولد شدم.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4265,'آرزوهایت را در آسمان بجوی، محبوبت را به تو خواهد داد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4266,'دیروز، فرمانبردار سلطان ها بودیم و سر برآستان ِ امپراطوران داشتیم؛ امروز، حقیقت را می ستاییم و راه عشق می پوییم.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4267,'فرزندانتان از آنِ شما نیستند! آنها پسران و دخترانی هستند که از خودشیفتگی زندگی، جان گرفته اند. آنها به وسیله ی شما و نه از شما شکل می گیرند، گرچه در کنار شما آسوده اند اما در تملک شما نیستند. شما مجازید که عشق خود را به ایشان هدیه کنید، نه افکارتان را، که آنها خود متفکرند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4268,'هرگز در پاسخ عاجزانه ای در نمانده ام، مگر در برابر کسی که از من پرسید: تو کیستی؟((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4269,'برای پی بردن به قلب و ذهن کسی، به آرزوهای آینده‌ی او بنگرید، نه به دستاوردهای گذشته‌اش.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4270,'لذت ترساندن، ژرف و پایدار است. به همین دلیل، مترسک از ایستادن در دشت خلوت هیچ گاه خسته نمی شود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4271,'خوشبختی یعنی هر روز مژده ی خوبی شنیدن.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4272,'به یکدیگر مهر بورزید، اما از مهر، بند نسازید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4273,'اندرز من به زوجهای جوان این است که به هنگام شادی، همگام با یکدیگر نغمه ساز کنید و پای بکوبید و شادمان باشید، اما امان دهید که هر یک در حریم خلوت خویش آسوده باشد و تنها؛ چون تارهای عود که تنهایند هر کدام، اما به کار یک ترانه ی واحد در ارتعاش.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4274,'شما چون فصلهای سال هستید، زیرا در زمستان خود بهار را انکار می کنید، در حالی که بهار سرسبز هرگز شما را انکار نمی کند، بلکه در سنگین ترین خواب بی خبری به روی شما لبخند می زند، بی آنکه خشمگین شود و یا با شما ستیز کند و صفا و یکرنگی را نادیده بگیرد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4275,'اندیشه ام در من، عشق به آرامش و خواهش وابسته نبودن را جای می دهد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4276,'هرگاه توانستیم به همان راحتی که می خندیم از صمیم دل بگرییم، انسانی میانه رو شده ایم. گریه بارانی است که از طوفان درون برمی خیزد یا شبنمی است که نمایانگر لطافت روح است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4277,'به هنگام باز ایستادن تنفس، نفس از تکرار پی در پی آزاد می شود و تلاش برای آزادی از زندانی مخوف و اوج گرفتن در فضایی گسترده و پر از آثار حیات به سوی پروردگار ادامه می یابد تا بی پرده به وصال برسد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4278,'چون [ =اگر ] بخشنده ای به فرد نیازمند بخشش و کرم کرد، در دل احساس شادی و سرور کند و ببالد. اما نیکی کردن به کسی که نیاز ندارد روا نیست، چه او هرگز ارزش نیکی را نمی شناسد و همچنان که از خار خشک گل نمی روید، ارج نهادن به نیکی را نمی داند... .((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4279,'به پزشک خود ایمان داشته باشید و به گفته هایش، که جز دارویی شفا بخش نیست، اعتماد کنید و جرعه ی تلخ او را با خاطری آسوده سرکشید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4280,'گروهی دریای زیبای حقیقت را به درون خود نهان دارند و زلال آن را در پیاله ی کوچک کلام نمی کنند؛ به گرمای مهربان سینه ی آنان باشد که جان به سکوتی موزون آرام گیرد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4281,'شکنجه ها عشق به شما روا می دارد تا به رازی که در دلهایتان نهفته است پی برید و بدین وسیله جزیی از حیات باشید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4282,'چون بمیرم بگویید، غریبی مشتاق به سوی وطن خویش هجرت کرد؛ در دنیا اسیر مراد خود بود و اکنون در آسمانها رهاست.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4283,'عبادت، گستردن جان است بر کرانه هستی و آمیزش انسان است با اکسیر حیات.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4284,'از خیالتان خلوتگاهی در بیابان بسازید، پیش از آنکه خانه ای در میان دیوارهای شهر بنا کند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4285,'در تعلیم و تربیت، ذهن به تدریج از تجربه های علمی به نظریه های عقلانی، تجربه معنوی و سپس به سوی خدا رهسپار می شود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4286,'تعلبم و تربیت، دانه ای در درون شما نمی کارد، اما دانه های شما را می رویاند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4287,'تعلیم و تربیت، دانه را آبیاری می کند، اما از پیش خود دانه ای به شما نمی دهد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4288,'من سکوت را از آدم پر حرف آموختم، بردباری را از نابردبار و مهربانی را از نامهربان. اما شگفت آور است که قدرشناس این آموزگاران نیستم.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4289,'اگر دانشت چیزی درباره ارزش اشیا و امور به تو نمی آموزد و تو را از اسارت ماده رهایی نمی بخشد، پس هرگز به عرش حقیقت نزدیک نمی شوی.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4290,'بیچاره ترین آدمها کسی است که رویاهای خویش را در پیکر زر و سیم تعبیر می کند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4291,'دوستی با آدم نادان به اندازه بحث کردن با آدم مست، احمقانه است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4292,'دوستی مسئولیتی شیرین است، نه یک فرصت.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4293,'اگر دوستت را در تمامی شرایط درک نکنی، او را هرگز درک نخواهی کرد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4294,'عشق برای همیشه از زیبایی می هراسد، با وجود این، زیبایی برای همیشه با عشق دنبال خواهد شد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4295,'از یک خود کامه، یک بدکار، یک گستاخ و یا کسی که سرفرازی درونی اش را رها کرده، چشم نیک اندیش نداشته باش.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4296,'چه حقیر و کوچک است، زندگی آن که دستانش را میان دیده و دنیا قرار داده و هیچ نمی بیند، جز خطوط باریک دستانش. در خانه نادانی، آینه ای نیست که روح خود را در آن به تماشا بنشیند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4297,'کاملترین پاداش برای انسان، بخششی است که بتواند امیال هستی اش را تغییر دهد و خواستهای دو لب خشکیده از عطش او را دگرگون سازد و تمام زندگی اش را به چشمه های همیشه جوشان و ابدی تبدیل کند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4298,'چه ناچیز است زندگی کسی که با دستهایش چهره خود را از جهان جدا ساخته و چیزی نمی بیند جز خطوط باریک انگشتانش را.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4299,'دوستی همواره یک مسئولیت شیرین است، نه یک فرصت.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4300,'شما دل به یار خود بسپارید، اما نه برای نگهداری آن؛ زیرا تنها گرمی زندگی است که می تواند دلها را حفظ کند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4301,'چه بسیارند گلهایی که از زمان زاده شدن، بویی برنیاورده اند! و چه بسیارند ابرهای سترونی که در آسمان گرد هم آمده، اما هیچ دُری نمی افشانند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4302,'کنار یکدیگر بایستید، اما نه چندان نزدیک؛ چنان که ستون های معبد از هم جدا می ایستند و درختان سرو و بلوط در سایه یکدیگر نمی بالند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4303,'بخشش زودگذر توانگران بر تهیدستان تلخ است و همدردی نیرومندان با ناتوانان، بی ارزش؛ چرا که یادآور برتری آنان است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4304,'بسیاری از دین ها به شیشه پنجره می مانند؛ راستی را از پس آنها می بینیم، اما ما را از راستی جدا می کنند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4305,'زندگی روزانه شما پرستشگاه شما و دین شماست. آنگاه که به درون آن پای می نهید، همه هستی خویش را همراه داشته باشید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4306,'مردم هرگز نمی دانند پیشوا جز سرشت بزرگ آنها که به سوی آسمان سیر می کند، شکاری ندارد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4307,'هیچ کس نمی تواند چیزی را به شما بیاموزد، جز آنچه که در افق دید و خرد شما وجود داشته و شما از آن بیخبر بوده اید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4308,'زمانی مردم را درست می ببینی که در بلندیهای سر به آسمان کشیده حضور داشته باشی و نیز در منزلگاههای دور.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4309,'شاید بتوانید دست و پای مرا به غل و زنجیر بکشید و یا مرا به زندانی تاریک بیافکنید، اما افکار مرا که آزاد است نمی توانید به اسارت در آورید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4310,'عشق زمانی که شما را می پرورد، شاخ و برگ فاسد شده را هرس می کند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4311,'طبیعت با آغوشی باز و دستانی گرم از ما استقبال کرده و می خواهد که از زیبایی اش لذت ببریم. چرا انسان باید آنچه را که در طبیعت ساخته شده است، از بین ببرد؟((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4312,'به فرزند عشق خود توانید داد، اما اندیشه تان را هرگز، که وی را افکاری دیگر به سر است؛ تفکراتی از آن خویشتن.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4313,'رابطه قلبی دو دوست نیاز به بیان الفاظ و عبارات ندارد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4314,'با سالخوردگان و افراد با تجربه مشورت کنید که چشمهایشان، چهره سالها را دیده و گوشهایشان، نوای زندگی را شنیده است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4315,'رنجدیده! اگر بکوشی تا چیزی از مال خود را به مردم ببخشی، بی تردید رستگار هستی.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4316,'تاسف، ابر سیاهی است که آسمان ذهن آدمی را تیره می سازد، در حالی که تاثیر جرایم را از بین نمی برد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4317,'هنگامی که در سکوت شب گوش فرا دهی، خواهی شنید که کوهها و دریاها و جنگلها با خود کم بینی و هراس ویژه ای نیایش می کنند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4318,'چشمه ساری که خود را در اعماق درون شما پنهان ساخته است، روزی قد خواهد کشید و فوران خواهد کرد و با ترنم و نغمه، راه دریا را در پیش خواهد گرفت.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4319,'حاشا که آواز آزادی از پس میله و زنجیر به گوش تواند رسید و از گلوگاه مرغان اسیر.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4320,'پند آموز است ماجرای مردی که زمین را می کاوید تا ریشه های بی ثمر را از دل آن بیرون کشد، اما ناگاه گنجی بزرگ یافت؟!((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4321,'انسان خردمند با مشعـل دانش و حکمت، پیش رفته و راه بشریت را روشن می سازد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4322,'چه زیباست هنگامی که در اوج نشاط و بی نیازی هستی، دست به دعا برداری.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4323,'به رویاها ایمان بیاورید که دروازه های ابدیت اند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4324,'هر ضعیف و ناچیزی که در میان شما عذاب دیده و نابود گشته است، نیرومندترین و ایستاده ترین چیزی است که در هستی شماست.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4325,'آموختن تنها سرمایه ای است که ستمکاران نمی توانند به تاراج ببرند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4326,'حیات درختان در بخشش میوه است. آنها می بخشند تا زنده بمانند، زیرا اگر باری ندهند، خود را به تباهی و نابودی کشانده اند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4327,'اگر بکوشی و در پی نصیبی حتی برای خود باشی، بدان که صالحی.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4328,'دوست بدارید، اما عشق را به زنجیر بدل نکنید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4329,'وقتی از دارایی خود چیزی می بخشی چندان بخشش نکرده ای، بخشش حقیقی آن است که وجود خود را به دیگری بخشی.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4330,'گمان نبرید که زمان عشق در دست شماست، بلکه این عشق است که هر زمان شما را شایسته بداند، هدایت می کند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4331,'بهترین مردم کسانی هستند که اگر از آنها تعریف کنید، خجل شوند و چون بد گفتید، سکوت کنند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4332,'شیمیدانی که از قلب خود، عناصر شفقت، احترام، آرزو، توجه، تأسف، شگفتی و بخشایش را گرفته و آنها را با هم ترکیب می کند، اتمی را می سازد که عشق نام دارد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4333,'شاهین، لانه‌ی خود را با خود نمی‌برد، بلکه تنها پرواز می‌کند و در آسمان، اوج می‌گیرد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4334,'هرگاه عشق به سوی شما اشاره کند، از او پیروی کنید، هر چند که را‌ههایش دشوار و پرنشیب باشد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4335,'اگر عشق با شما سخن گوید، او را باور کنید، هر چند پژواک‌اش، رویاهایتان را آشفته سازد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4336,'عشق، چیزی جز خودش را نمی‌دهد و چیزی جز از خود نمی‌ستاند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4337,'به یکدیگر عشق بورزید، ولی عشق را به بند نکشید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4338,'هر یک از شما دل خود را به همسرش دهد، ولی مبادا چنین بخششی برای در بند کردن باشد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4339,'فرزندانتان، فرزندان شما نیستند. آنها فرزندان زندگی‌اند که آرزومند خویشتن خویش‌اند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4340,'می‌توانید بکوشید تا مانند آنها [ (فرزندانتان) ] شوید، ولی بیهوده است که بکوشید آنها را مانند خود کنید. زیرا زندگی به گذشته باز نمی‌گردد و در خانه‌ی دیروز به سر نمی‌برد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4341,'تا هنگامی که پاره‌ای از وجود خویش را نبخشید، بخشش‌تان بی‌ارزش و بی‌مقدار خواهد بود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4342,'برخی از مردم بسیار دارند، ولی اندک می‌بخشند و می‌بخشند برای آنکه نام آور شوند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4343,'برخی اندک دارند، ولی همه را می‌بخشند، آنها به زندگی و به گشاده‌دستی زندگانی باور دارند. گنجینه‌هایشان هرگز تهی نمی‌شود و تا ابد لبریز است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4344,'برخی از مردم با شادی می‌بخشند و شادی برای آنها پاداش همان بخشش است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4345,'به نیازمندان بخشیدن چه زیبا است؛ و زیباتر از آن، به کسی بخشیدن است که از ما نمی‌خواهد، ولی نیاز او را می‌دانیم.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4346,'آن کس که دست و دل خود را برای بخشش بگشاید و در پی نیازمندان باشد، شاديِ بالاتر و لذت‌آورتر از بخشش به دست می‌آورد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4347,'بی‌شک، روزی خواهد رسید که دارایی‌تان را از دست می‌دهید! اکنون بخشش کنید تا بخشش، فصلی از فصلهای زندگی‌تان باشد و نه از آنِ بازماندگان شما.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4348,'کار کنید تا خود را با زمین و روح آن هماهنگ و همساز نمایید. زیرا تنبل و بیکار با فصلهای زمین بیگانه است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4349,'به راستی که زندگی سیاه است، اگر جنبشی در آن نباشد. حرکتِ بی‌شناخت، کور است و برکتی ندارد. شناخت بی عمل نیز نازا و بیمار است. کردار بی‌عشق، بیهوده و بی‌ثمر است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4350,'اگر با عشق کار کنید، خود را با دیگران پیوند می‌زنید و چنین پیوندی سبب پیوندتان با پروردگار خواهد شد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4351,'اگر نتوانید با عشق کار کنید و رنجیده و افسرده باشید، بهتر است کارتان را رها کنید و بر پلکان پرستشگاه بنشینید و از آنهایی که با آرامش و شادی کار می‌کنند، گدایی کنید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4352,'اگر نان بپزید، ولی لذتی نیابید، به راستی، نان تلخی پخته‌اید که تنها نیمی از گرسنگی انسان را فرو می‌نشاند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4353,'آسایش‌خواهی، خواهش‌های جان را در دل قربانی می‌کند و می‌كُشد، آنگاه با شدت می‌گرید و در پی جنازه‌اش می‌رود.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4354,'آن که در شما نامحدود است و در بند نیست، در آسمان خانه دارد؛ خانه‌ای که درش از بامداد و پنجره‌هایش از سکون و آواز شب است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4355,'شرم، سپری محکم در برابر نگا‌ههای ناپاکان است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4356,'بهتر است که داد و ستد را با مهر و داد انجام دهید تا به آزمندی و گرسنگی نینجامد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4357,'اگر یکی از شما بخواهد با نام دادگری بر درخت بدی تیشه زند، در آغاز باید نگاهی به ریشه‌ی آن بیندازد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4358,'درباره‌ی آن که جانی بكُشد، درحالی که مردم جان او را هزار باز کشته‌اند، چه مجازاتی خواهید نوشت؟((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4359,'چگونه می‌توانید آنان که عذاب وجدان‌شان بیش از گناهان‌شان است را مجازات کنید؟((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4360,'شمایی که با باد سفر می‌کنید! کدامین بادنما می‌تواند شما را رهنمون سازد؟ کدامین قانون بشری می‌تواند برایتان سودمند باشد؟ درحالی که بندهای زندانتان را نشکسته‌اید؟((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4361,'نمی‌توانید آزاده باشید، مگر آنکه برای به دست آوردن آزادی بکوشید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4362,'چگونه ستمگر می‌تواند بر آزادمردان حُکم براند، مگر آنکه ستم، بنیان آزادی و ننگ، پایه‌ی بزرگی‌شان باشد؟((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4363,'اگر بخواهید ترسی را از خویش برانید، بدانید که جایگاه ترس در دلتان ریشه دارد و نه در کسانی که از آنها می‌ترسید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4364,'خرد و عشق، سکان و بادبان روح اند و در دریای جهان در حال تکاپو هستند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4365,'از شما می‌خواهم خرد و عشق را گرامی بدارید، زیرا میان دو میهمان نباید فرق بگذارید و اگر از یکی بیش از دیگری مهمان نوازی کنید، دوستی و باور، هر دو را از دست می‌دهید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4366,'دلهایتان اسرار روزها و شبها را در سکون و آرامش می‌شناسند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4367,'آموزگاری که در سایه‌ی پرستشگاه راه می‌رود و پیروانش او را در بر گرفته‌اند، چیزی از دانش خود نمی‌دهد، بلکه باور و مهربانی و عشق خود را می‌بخشد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4368,'ستاره‌شناس می‌تواند چیزی از دانستنی‌هایش درباره‌ی نظم آسمانی بگوید، ولی نمی‌تواند آگاهی‌اش را به شما دهد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4369,'موسیقی‌دان می‌تواند زیباترین سروده‌ها و نغمه‌های جهان را برایتان بنوازد، ولی نمی‌تواند گوشی [به شما] ببخشد تا بتوانید با آن بشنوید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4370,'کوه در نزد کوهنورد، بزرگتر و بهتر آشکار می‌شود تا اینکه آن را از دشتی دور ببیند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4371,'اگر از دوست جدا شدید، اندوهگین نباشید، زیرا عشق شما نسبت به او بیش از خود او است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4372,'مبادا از روی دلسوزی، در برابر آنان که می‌لنگند بلنگید!((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4373,'ای کاش آهوان می‌توانستند شتاب و چابکی را به لاک‌پشت‌های كُندرو بیاموزند!((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4374,'اگر برای دیگران وارد پرستشگاه شوید، دعاهایتان برآورده نخواهد شد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4375,'من نمی‌توانم چگونه نیایش کردن با الفاظ را به شما بیاموزم. زیرا خداوند به اینگونه نیایش‌ها گوش فرا نمی‌دهد، مگر آنکه با دهان و زبان شما سخن بگوید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4376,'حقیقت پشیمانی، ابری تیره است که بر اندیشه سایه می‌افکند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4377,'سرود آزادی از میان میله‌های زندان بیرون نمی‌آید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4378,'جغدی که چشمان خود را تنها در تاریکی می‌گشاید، در روز نابیناست.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4379,'شما نمی‌توانید آواز بخوانید، مگر آنکه از رود سکوت بنوشید و نیز نمی‌توانید به قله برسید، مگر آنکه از کوه بالا روید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4380,'روح غمگین، در تنهایی آرام می‌گیرد و از انسانها می‌هراسد؛ چون آهویی زخمی که گله را به جا نهاده و به زندگی غار رو می‌آورد تا بهبود یافته یا بمیرد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4381,'روح من! زندگی چونان گذشتن از شب است، هر چند تندتر روی، زودتر به سپیده‌دم می‌رسی.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4382,'روح باید چون گُل از خاک، نازکی و بوی خوش برگیرد، از زندگی فرزانگی یابد و با پیروزی بر دنیا، از بند آن برهد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4383,'تبه کار را برای آن دوست دارم که بدی‍هایش از روح پست و فرومایه‌ی او سرچشمه نگرفته، بلکه از ناتوانی روح و ناامیدی اوست.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4384,'هدف روح در دل نهفته است و از نماهای بیرونی نمی‌توان درباره‌اش داوری کرد.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4385,'هیچ چیز را زشت مخوان! مگر هراس روح را در برابر یادبودهای گذشته‌اش.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4386,'مرگ در زیر شاخه‌های فروشکننده از طوفان، خوشتر از جان دادن بر بازوان پیری است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4387,'شگفتا که از مرگ می‌هراسیم، ولی آرزوی خفتن و رویاهای زیبا را داریم.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4388,'سخت است زندگی، برای کسی که آرزوی مرگ دارد، ولی بهر دلبندانش می‌زید.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4389,'هنر، زمانی شکوفا می‌شود که بینش نهانی هنرمند و روشنی طبیعت در یافتن شکل‌های تازه با هم سازگار باشند.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4390,'هر زیبایی و شکوه در این دنیا، تنها با اندیشه یا عاطفه ی درون یک انسان پدید می اید. هر آنچه امروز می بینیم و نسلهای گذشته آن را ساخته اند، پیش از پدید آمدن، یک اندیشه در ذهن مردی یا یک انگیزه در قلب زنی بوده است.((جبران خلیل جبران))',51,0,0,0,0,0);
INSERT INTO mysms VALUES(4391,'معنی حقیقی آزادی، مسئولیت است و به همین دلیل است که اغلب از آن می ترسند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4392,'ملت از راه آزادی به تعلیم و تربیت دست می یابند، نه برعکس.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4393,'هرگز برای پول ازدواج نکنید، چون برای پول در آوردن راههای آسانتری هم وجود دارد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4394,'یک بطری که تا نیمه آب داشته باشد، از نظر یک فرد خوشبین نیمه پر است، اما از نظر یک فرد بدبین نیمه خالی به شمار می رود.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4395,'گذراندن زندگی در جهت هدفی که آن را بزرگ و شکوهمند می‌دانید، منجر به شادی و سرور راستین می‌شود.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4396,'یک عمر اشتباه کردن، نه تنها افتخارآمیز است، که بسیار سودمندتر از یک عمر نشستن بیهوده است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4397,'همچنان که عنکبوت چشم براه گرفتار شدن مگس است، زن، چشم براه و در پی به دام افکندن مرد است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4398,'علت بدبختی و بیچارگی ما داشتن فرصتهای زیادی است که صرف اندیشیدن درباره خوشبختی و بدبختی می گردد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4399,'از مکالمه و پرگویی بیجا نجات پیدا خواهید کرد، اگر به خاطر بیاورید که مردم هرگز نصایح شما را قبول نمی کنند، مگر اینکه وکیل مدافع و یا دکتر باشید و آنها برای شنیدن صحبت های شما پول خرج کرده باشند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4400,'سعی کنید آنچه را که دوست دارید به دست آورید، وگرنه زمانی خواهد رسید که ناچارید آنچه را به دست آورده اید، دوست داشته باشید.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4401,'زن زشت در دنیا وجود ندارد. تنها برخی از زنان هستند که نمی توانند خود را زیبا جلوه دهند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4402,'عشق، خطای آشکار فرد در تمایز یک آدم عادی از دیگر آدمهای عادی است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4403,'مردم موفق کسانی هستند که بر می خیزند و به دنبال خواسته خود حرکت می کنند و اگر آن را نیافتند با تلاش و کوشش آن را به وجود می آورند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4404,'نخستین وظیفه‌ی ما نسبت به خودمان این است که نگذاریم تهیدست و بی‌پول شویم.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4405,'جوانی، زمانی است که شما پدر و مادر خود را بابت هر چیزی گناهکار می‌دانید و پختگی، زمانی است که می‌آموزید همه‌ی گناه‌ها به گردن بی‌تجربگی نسل جوانتر است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4406,'راز بدبختی و بیچارگی ما اوقات فراغتمان است که آن را با فکر کردن در مورد خوشبختی و بدبختی می گذرانیم.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4407,'به ندرت اشخاصی را پیدا می کنید که در سال، بیش از دو یا سه بار فکر کنند. من خودم با هفته ای یکبار یا دو بار فکر کردن به شهرت جهانی رسیده ام.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4408,'انقلاب، ستمدیدگان را آزاد نمی کند، [ بلکه ] تنها استثمارگران را تغییر می دهد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4409,'آزادی یعنی مسؤولیت؛ برای همین است که بیشتر انسانها از آن وحشت دارند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4410,'انسان ها به نسبت ظرفیتی که برای کسب تجربه دارند عاقل اند نه به نسبت تجاربی که اندوخته اند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4411,'هر چقدر توانایی های افراد کمتر باشد؛ ادعای آنها بیشتر خواهد بود.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4412,'همیشه راست بگو، اما هر راستی را نگو.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4413,'هر کسی نمی تواند حافظ، سعدی یا مولانا شود؛ هر کس به جای خود و برای خود کسی است؛ هر کس دارای سبکی است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4414,'هیچ عشقی صادقانه تر از عشق به غذا نیست.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4415,'تمام حقایق بزرگ در ابتدا کفر به نظر می رسند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4416,'پیشروان، انسانهایی هستند که برای رسیدن به مطلوب خویش به پا می خیزند و اگر بدان دست نیابند به ساختن آن همت می گمارند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4417,'انسانها نه به نسبت تجربه های خود، بلکه به نسبت ظرفیتی که برای تجربه کردن دارند عاقل هستند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4418,'دو تراژدی دردناک در زندگی وجود دارد: یکی اینکه در عشقت ناکام شوی و دیگر اینکه به وصال عشقت برسی.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4419,'مرد موفق کسی است که با جهان سازگار می شود و بازنده کسی است که با پافشاری می خواهد جهان را با خویشتن سازگار کند. بنابراین، همه ی پـیشرفت های جهان، کار بازندگان است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4420,'بهترین مکان برای یافتن پروردگار، باغ وجودت است. می توانی او را در آنجا جستجو کنی.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4421,'و اما طول عمر سعادت! هیچ انسان زنده ای قادر نخواهد بود در برابر آن تاب بیاورد؛ ممکن است دوزخی بر روی زمین باشد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4422,'تو چیزها را می بینی و می پرسی: چرا؟ من خواب چیزهایی را که وجود ندارند می بینم و می گویم: چرا که نه؟((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4423,'اصل و نسب مردان زمانی مشخص می شود که آنها بر سر مسایل کوچک با هم مشکل پیدا می کنند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4424,'سکوت بهترین تحقیر است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4425,'یک خانواده شاد چیزی جز یک بهشت زودرس نیست.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4426,'بیشتر مردم دعا نمی کنند، تنها التماس می کنند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4427,'به تجربه آموختم که انسانها هرگز از تجربه چیزی نمی آموزند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4428,'آن که حقیقت را پنهان می کند، می خواهد با دست روی خورشید را بپوشاند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4429,'بهترین مصلح کسی است که اصلاح را از خود شروع کند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4430,'حسب و نسب زن و شوهر از رفتاری که هنگام پرخاش و جدال با هم دارند، نمایان می شود.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4431,'برای اینکه دانش ملکه شود، آموختن کافی نیست عمل لازم است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4432,'مغز ابلهان، دانش را به پنداشته ها، فلسفه را به نادانی و هنر را به خودنمایی مبدل می کند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4433,'تنها دانشی باقی می ماند و فراموش نمی شود که همراه با عمل باشد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4434,'مردی که در نبرد زندگی می خندد، سزاوار ستایش است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4435,'او هیچ چیز نمی داند، اما تصور می کند و تظاهر می کند که همه چیز را می داند، این تعریف کوتاه و یک نماینده ی مجلس است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4436,'برای اینکه بفهمیم کسی خوب است یا بد بهترین راه آن است که او را سزار رم کنیم.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4437,'قوانین طبیعت با رسوم قبیله ی شما تفاوت دارد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4438,'وقتی آدم بفهمد که به هرچیزی - هر چقدر چندش آور- می تواند عادت کند به این ضرورت پی می برد که باید همه ی چیزهای عادی زندگی اش را وارسی کند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4439,'مرگ برای ما دروازه ی دوزخ است، اما دروازه ی خروجی نه دروازه ی ورود به آن.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4440,'در هرچیز بامزه ای، حقیقتی وجود دارد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4441,'خوش بین ها و بدبین ها هر دو به بشر خدمت کرده اند؛ اولی با آفریدن هواپیما و دومی با آفریدن چتر نجات!((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4442,'پول گرسنگی را درمان می کند، ولی درمان اندوه و درد نیست.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4443,'وقتی انسان ببری را بکشد، نامش را تمرین مردانگی می گذارند. ولی وقتی ببری انسان را بدرد، نام این کار را توحش و آدمخواری می نهند؛ تفاوت جنایت و عدالت هم در قاموس بشر از این بیشتر نیست.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4444,'وقتی جوان بودم متوجه شدم از هر ده عملی که انجام می دهم، نُه تای آن اشکال دارد. من نمی خواستم آدم بدنامی باشم، بنابراین ده مرتبه بیشتر کار می کردم.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4445,'تنها زمانی باید از فکر کردن به آینده غافل شد که در حقیقت، آینده ای وجود نداشته باشد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4446,'آن که می تواند، انجام می دهد و آن که نمی تواند، انتقاد می کند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4447,'بزرگترین نعمت خدا هستی و حیات است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4448,'دانش زمانی خطرناک است که فکر کند به هدف خود رسیده است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4449,'دانش در دو صورت خطرناک می شود : اول اینکه در دست آدم نادانی قرار گیرد و دوم اینکه فکر کند به هدف خود رسیده است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4450,'یک محصل باید کتابهای خوب بخواند، اما یک نویسنده باید کتابهای بیهوده را هم مطالعه کند. من مزخرف ترین کتابها را می خوانم و با اینکه جواهر قیمتی اغلب در خزانه هاست، گاهی در ویرانه ها گنجهایی پیدا می شود که آدم را بسیار دگرگون می کند. آدم باید از هر سوراخ و سنبه ای خبر داشته باشد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4451,'آرزو دارم که تا آخرین لحظه، وجودم ثمره بخش باشد و هنگامی بمیرم که هیچ کاری از من برنیاید.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4452,'اگر راهش را بدانی هر سخنی می توان گفت، وگرنه هیچ؛ ظرافت در دانستن آن راه است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4453,'روح من به دنیا کمک خواهد کرد که به خودش کمک کند.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4454,'هیچ گاه در اندیشه ی مرگ نباشید، بی گمان، مرگ در اندیشه ی شماست.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4455,'از شگفتی های زندگی این است که مرگ، درست زمانی ما را درمی‌یابد، که آماده شده‌ایم تا از یک زندگی شیرین برخوردار شویم.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4456,'مزه‌ی زندگی به سبب مرگ از دست نمی‌رود، همان گونه که خنده ما از جدی بودن زندگی نمی‌کاهد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4457,'بدون هنر، ظریف نبودن واقعیت، دنیا را مکانی تحمل‌ناپذیر می‌سازد.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4458,'من به آوازه ای بین المللی دست یافتم، چون یک یا دو بار در هفته می اندیشم.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4459,'هرچه بیشتر کار می کنم، بیشتر زندگی می کنم.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4460,'زندگی، پُر از ماجراهای دلنشین و هیجان انگیز است؛ هرگز شانس خود را برای یافتن آنها از دست ندهید.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4461,'تهیدستی، ریشه ی تمام پلیدی ها است.((جرج برنارد شاو))',52,0,0,0,0,0);
INSERT INTO mysms VALUES(4462,'رقابت [=چشم و هم‌چشمی]، پایه و بنیان کسب و کار است.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4463,'هر اندازه هم که باهوش باشید، به هر روی، به تنهایی نمی‌توانید کاری از پیش ببرید.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4464,'هماهنگی و پیوستگی، هر دو برای دارندگان تکنولوژی‌های بزرگ، مهم هستند.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4465,'گاهی، اندیشه‌ی استراتژیک این است که با کمترین‌ها، بیشترین کار را انجام دهید.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4466,'آینده، همیشه هم به شما خوشامد نمی‌گوید و بدون پروراندن آن، ممکن است برای شما درنگ نکند.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4467,'یک همه‌چیزدان هم به ناگزیر همه چیز را نمی‌داند؛ حتی اگر یک نابغه‌ی همه‌چیزدان باشد.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4468,'گاهی، پیاده کردن آنچه که در ذهن دارید، به کمی دقت نیاز دارد تا نتیجه‌ی خوبی داشته باشد.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4469,'گاهی، آنچه را که آزموده‌اید و درست است، بهتر از همه‌ی گزینه‌های شناخته شده است.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4470,'اگر به فردی که سابقه‌ی خیانت کاری دارد، راه دادید، بسیار مراقب باشید که جایتان را نگیرد.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4471,'بهتر است تکه‌تکه، ثمره‌ی کارتان را نشان دهید، تا اینکه به سبب کامل نشدن، برای پرده‌برداری از آن، زمان بسیاری درنگ کنید.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4472,'هنگامی که شما از سابقه‌ی فردی آگاه هستید و می‌دانید چه گونه آدمی‌ست، دیگر نباید از او رودست بخورید.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4473,'در پناه کسی بودن، به راستی، زیباست؛ ولی از آن هم زیباتر، بودن در پناه کسی‌ست که ما را دوست دارد و همیشه در کنار ماست.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4474,'پیمانی که نتوان بر سر آن ماند، یک دروغ است و هیچ‌کس دوست ندارد به او دروغ گفته شود.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4475,'زمانی که یک غریبه، رفتار بدی در حق ما بکند، زیاد به آن اهمیت نمی‌دهیم، ولی خدا نکند که دوستی نزدیک، یا یکی از اعضای خانواده، یا کسی که دوستش داریم، در حق ما بدی کند.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4476,'اغراق کردن [=گزافه‌گویی] بیش از اندازه، می‌تواند بدترین گونه‌ی پیمان‌شکنی باشد.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4477,'آینده‌نگری را با حقیقت [=راستی و درستی]، هماهنگ کنید.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4478,'اگر نتوانید آسمان آبی را ببینید، به پایان راه رسیده‌اید.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4479,'از ایجاد دگرگونی در شرایط خود نترسید، حتی اگر در شرایط کنونی، آسوده باشید.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4480,'همه‌ی پل‌ها را پشت سر خراب نکنید.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4481,'اگر روزی ناگزیر شوید با دشمن‌تان، دوست شوید، بهترین کار، تنبیه کردن او به روش بخردانه و هوشیارانه است.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4482,'بودن یک نفر معمولاً بهتر از نبودنش است.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4483,'مدیر عامل‌های ناتوان، مدیر عامل‌های ناتوان‌‌تری را به وجود می‌آورند.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4484,'مدیران خوب، شمشیرهای خود را تاب می‌دهند، ولی دشمنی‌هایشان را نیز کنار می‌گذارند.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4485,'یکی از بدترین کارهایی که یک مدیر می‌تواند انجام دهد، این است که بین کارمندانش، تبعیض [=برتری یکی بر دیگری] به وجود بیاورد. اگر مزایایی وجود دارد، باید برای همه باشد.((جفری کروکشنک))',53,0,0,0,0,0);
INSERT INTO mysms VALUES(4486,'دنیای ما مثل خانواده ی بزرگی از ملتهاست.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4487,'اگر بخواهیم چیزی درباره ی دنیای خودمان بدانیم نباید تنها به کشور کوچکی که ممکن است در آن به دنیا آمده باشیم بیندیشیم بلکه باید به تمام کشورها و تمام مردمی که بر روی زمین هستند فکر کنیم.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4488,'پنداشتن بسیار شیرین است زیرا ما با پنداشته های خود می توانیم افسانه های پریان را هم بسازیم.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4489,'تنها راه حقیقی برای درک داستان زمین این نیست که در این باره در کتابهای مردم دیگر، مطالبی بخوانیم بلکه بهتر این است که به خود کتاب طبیعت باز گردیم.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4490,'هر قطعه سنگی که می بینی در کنار راه یا در دامنه ی کوهی افتاده است می تواند صفحه ی کوچکی از کتاب طبیعت باشد و می تواند برایت چیزی بگوید البته به شرط آن که خواندنش را بدانی.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4491,'نیروی فکر، آدمیزاد را از حیوانات متمایز ساخت. همین نیرو آنان را از نیرومندترین و بزرگترین حیوانات وحشی هم نیرومندتر ساخت.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4492,'فیل بسیار بزرگ و نیرومند است، بسیار نیرومندتر از فیلبان کوچکی که روی گردنش می نشیند. با این همه فیلبان می تواند بیندیشد و چون بهتر از فیل می اندیشد ارباب و آقای فیل شده است.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4493,'مردمی که در یک کشور زندگی می کردند و به هم نزدیکتر بودند طبعاً با یکدیگر بیشتر آشنا می شدند و تصور می کردند که بهتر از مردمان دیگری هستند که در کشورهای دیگر زندگی می کنند و در کمال نادانی با یکدیگر می جنگیدند.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4494,'جنگیدن مردم با هم و کشتن یکدیگر نابخردانه ترین کاری است که انسان می تواند بکند؛ این کار برای هیچ کس ثمری ندارد.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4495,'کار نکردن و دیگران را برای خود به کار واداشتن، مایه ی افتخار و غرور آدمی نیست.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4496,'اکنون در هر کشوری مردم تصور می کنند که خودشان بهتر و باهوش ترین مردمانند و دیگران به خوبی و شایستگی ایشان نیستند.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4497,'هر کس خودش و کشورش را خوبتر و بهتر از دیگران می شمارد، اما در حقیقت هیچ کسی نیست که در خود میزانی خوبی و میزانی هم بدی نداشته باشد و به همین شکل، هیچ کشوری هم نیست که در آن چیزهای بد و از برخی نظرها خوب نباشد.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4498,'ما باید خوبی را در هر جا پیدا می کنیم بگیریم و بکوشیم تا بدی را هم از هر جا که هست دور سازیم.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4499,'ما باید ببینیم که در رفتار و آداب ما چه چیزهای خوبی هست و بکوشیم تا آنها را پاس داریم و هر چه را هم بد است باید به دور افکنیم. همچنین اگر در کشورهای دیگر هم چیزهای خوبی هست مسلما باید آنها را فرا گیریم.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4500,'ما که هندی هستیم باید در هند زندگی کنیم و برای هند کار کنیم، اما نباید فراموش کنیم که در عین حال در پیوند با خانواده ی بزرگتری که در سراسر جهان است قرار داریم و سرانجام، مردمی هم که در سایر کشورهای جهان زندگی می کنند خویشاوندان و پسر عموهای ما هستند.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4501,'ما باید بکوشیم تا سراسر جهان به گونه ای در آید که بتوان در آن شادمان تر و خوشبخت تر زندگی کرد.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4502,'هرچند مرد تفنگدار به اندازه ی یک مرد بی سلاح متمدن نباشد، نباید به او گفت که متمدن نیست زیرا در غیر این صورت با تفنگی او را از پا در خواهد آورد.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4503,'بهترین نشانه ی تمدن، وجود انسان بزرگوار و نیک است که خودخواه نباشد و همراه با دیگران برای خوشبختی همه کار کند.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4504,'کار کردن با هم برای خیر و خوشی مشترک و همگانی از هر چیزی بهتر است.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4505,'اکنون روزگاری است که شخصی که هیچ کار نمی کند پولهای اضافی هنگفتی به دست می آورد، در حالی که بیشتر کسانی که با زحمت و سختی، کار زیادی انجام می دهند هیچ سهمی از آن به دست نمی آورند!((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4506,'بیشتر مردم معمولاً دلیل کارهای خود را از یاد می برند و بعدها بدون اینکه بفهمند چرا، از کارهای دیگران پیروی می کنند.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4507,'باید به یاد داشته باشیم که پول به خودی خود ثمره ای ندارد و فقط ابزاری است که به ما کمک می کند تا با آن، کالاها را مبادله کنیم.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4508,'اگر پول برای تهیه ی چیزهایی که مورد نیازمان است صرف نشود هیچ ثمره ای ندارد.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4509,'تاریخ به معنی جنگها و شرح زندگی چند نفر اندک شمار نیست که شاه شده یا سرداران بزرگی بوده اند. تاریخ باید درباره ی مردم یک کشور برای ما سخن بگوید که چگونه زندگی می کردند، چه کارهایی انجام می دادند و چگونه می اندیشیدند. تاریخ باید از رنجها و شادی های آنان و از دشواری ها و مشکلاتشان برای ما سخن بگوید و بگوید که چگونه بر این دشواری ها چیره شدند.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4510,'برگرداندن تصاویر آویخته بر دیوار، مسیر تاریخ را دگرگون نمی کند.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4511,'در زندگی چیزی بدتر و خطرناک تر از بی نظمی نیست.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4512,'به گذشته خود هرگز نمی اندیشم، مگر آنکه بخواهم از آن نتیجه ای بگیرم.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4513,'میدان جنگ به کسی فرصت مطالعه نمی دهد؛ هرکس هر چه می داند باید به کار ببرد.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4514,'چشم داشت احترام از هیچ کس نداشته باش تا احترامی که به تو می گذارند، شیرین تر جلوه کند.((جواهر لعل نهرو))',54,0,0,0,0,0);
INSERT INTO mysms VALUES(4515,'وقتی نهال آزادی ریشه گرفت، به سرعت رشد و نمو می کند.((جورج واشنگتن))',55,0,0,0,0,0);
INSERT INTO mysms VALUES(4516,'کسانی که حاضر نیستند برای آزادی بهایی بپردازند، لیاقت آزادی را ندارند.((جورج واشنگتن))',55,0,0,0,0,0);
INSERT INTO mysms VALUES(4517,'نسبت به همه مهربان و صمیمی باش و به یاد داشته باش که جامه نو به انسان شخصیت نمی دهد، بلکه مانند پر مرغان زیباست که آنها را می پوشاند و به آنها زیبایی می دهد.((جورج واشنگتن))',55,0,0,0,0,0);
INSERT INTO mysms VALUES(4518,'نهال دوستی حقیقی آهسته رشد می کند.((جورج واشنگتن))',55,0,0,0,0,0);
INSERT INTO mysms VALUES(4519,'آماده شدن برای جنگ یعنی نگهداری صلح.((جورج واشنگتن))',55,0,0,0,0,0);
INSERT INTO mysms VALUES(4520,'وجدان مانند آینه است که نه چاپلوسی می کند و نه تحقیر می نماید.((جورج واشنگتن))',55,0,0,0,0,0);
INSERT INTO mysms VALUES(4521,'از قمار دوری کن؛ زیرا قمار بازی کار شیطان، کودک حرص و آز، برادر بی عدالتی و بالاخره پدر فرومایگی است.((جورج واشنگتن))',55,0,0,0,0,0);
INSERT INTO mysms VALUES(4522,'محبت حقیقی شبیه گیاهی است که آهسته رشد می کند و مادام که در برابر مشکلات و سختیها پایداری نکند، سزاوار اینکه آن را محبت حقیقی بخوانند، نخواهد بود.((جورج واشنگتن))',55,0,0,0,0,0);
INSERT INTO mysms VALUES(4523,'در وظیفه خود ایستادگی کردن و خاموش ماندن، بهترین پاسخ به تهمت است.((جورج واشنگتن))',55,0,0,0,0,0);
INSERT INTO mysms VALUES(4524,'احساسات، همانند افکار، این گنجایش را دارند تا ما را به سمت آینده‌ای درخشان و یا آینده‌ای ناخوشایند به پیش برند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4525,'داشتن منش درست، پیش‌نیازی ضروری [=بایسته] برای کامیابی و خوش‌بختی‌ست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4526,'احساساتی که داریم، منش ما را به وجود می‌آورند و این منش ماست که در پایان، کیفیت زندگی را تعیین می‌کند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4527,'اگر بتوانیم درس‌های فرا گرفته از گذشته را چراغ پیش روی خود کنیم و آنها را در زندگی خود به کار بندیم، آنگاه می‌توانیم دوازده ماه آینده را تا حد چشمگیری دگرگون سازیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4528,'ایجاد یک بینش تازه درباره‌ی گذشته، کلید دگرگونی منش آینده‌ی ماست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4529,'تا بینش خود را درست نکنیم، نمی‌توانیم منش خود را دگرگون سازیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4530,'تا زمانی که در را به روی تاریکی‌های گذشته نبندیم، نمی‌توانیم به سوی آینده‌ای بهتر و درخشان‌تر به پیش رویم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4531,'اکنون، آنجایی‌ست که آینده‌ی ما از آن آغاز می‌شود.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4532,'اهداف و آرزوهای گذشته، فرصت‌ها و سودمندی‌های اکنون را به ما پیشکش می‌کنند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4533,'امروز هیچ ربطی به کار نادرست دیروز یا پشیمانی فردا ندارد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4534,'آنچه که در آینده بناست روی دهد، یک تصویر آینه‌ای از بینش اکنون و منش ما در رابطه با زندگی‌ست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4535,'رؤیاهای ما، به ما روح اندیشه، کردار و احساس می‌بخشند و سبب می‌شوند که ما به درستی، همان فردی شویم که رؤیاهایمان بدان می‌اندیشند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4536,'هیچ چیز بیرون از اراده‌ی ما نیست و هیچ چیز هم بیرون از تصور ما وجود ندارد و تصور، نقطه‌ی آغاز همه‌ی پیشرفت‌هاست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4537,'آنچه که ما را از شناسایی شایستگی‌ها و موهبت‌های ذاتی خویش باز می‌دارد، رفتار نه چندان خوبی‌ست که با خود داریم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4538,'اگر ما به درستی، خود را بشناسیم و نیروها، توانایی‌ها، منابع [=سرچشمه‌ها]، عمق [=ژرفای] احساس، حس شوخ‌طبعی و نیکی‌های بی‌همتای خود را کشف کنیم، آنگاه، هرگز نسبت به توانایی خود در پایه‌گذاری آینده‌ای بهتر شک نخواهیم کرد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4539,'هر کدام از ما به نوبه‌ی خود، بی‌همتا و بی‌مانندیم. شما در دنیا هیچ کس را نمی‌توانید مشابه خود پیدا کنید.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4540,'ما برای نگاهبانی از آینده‌ی بهتر خود، بایستی این جُربُزه و دلیری را داشته باشیم تا در صورت نیاز، خود را از جمع دوستان ناباب کنار بکشیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4541,'ما باید همواره به یاد داشته باشیم که کژی‌های بزرگ، از کارهای نادرست کوچک آغاز می‌شوند و کارهای نادرست کوچک به مرور زمان تبدیل به کژی می‌شوند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4542,'تکرار و تمرین، مادر مهارت است.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4543,'کامیابی به سراغ کسانی خواهد رفت که در پی پیشرفت هستند، نه کسانی که به آن نیاز دارند یا فقط می‌خواهند از مواهب [=دهش‌های] آن بهره‌مند شوند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4544,'زندگی، به شایستگی‌ها پاسخ می‌دهد، نه به نیازمندی‌ها.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4545,'منش، یک سرمایه است؛ گنجی باارزش که باید از آن به درستی نگاهبانی کرد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4546,'داشتن منش درست، یکی از پایه‌های کامیابی‌ست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4547,'زندگی به گونه‌ای طراحی نشده است که سهم ما را با توجه به نیاز ما بدهد؛ زندگی به نسبت شایستگی به ما می‌دهد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4548,'بینش متوازن [=هم‌سنگ]، منش ژرف و دیدگاه پسندیده درباره‌ی زندگی، زمانی ارزشمند خواهد بود که به کار آمده و سبب پیشرفت ما گردند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4549,'همواره در نبودِ نیکی، شر و بدی به وجود می‌آید.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4550,'اگر گاهی به نظر می‌آید که زندگی، دادگرانه نیست، نباید کسی جز خود را گناهکار بدانیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4551,'گفتگو درباره‌ی هدف، یک چیز است و به ثمر رساندن آن، چیز دیگری‌ست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4552,'ما هر روز می‌توانیم یک نظم ویژه را جایگزین یک بی‌تدبیری کنیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4553,'آنچه که به دست می‌آوریم، سبب ارزشمندی ما نمی‌شود، بلکه چیزی که در فرآیند کنش‌ها می‌شویم، ارزش را به زندگی ما می‌آورد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4554,'این تلاش و کوشش است که رؤیاهای انسان را برآورده می‌سازد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4555,'اگر دگرگونی برای ما دشوار باشد، همان گونه که هستیم، می‌مانیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4556,'ما، هم توانایی و هم مسئولیت داریم تا بهترین گزینه‌ها را برای آغاز امروز برگزینیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4557,'کسانی که در جستجوی زندگی خوب هستند، فقط به راستی و چیزی جز آن، نیاز ندارند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4558,'تلاش و تکاپوی بدون اندیشه، می‌تواند ویرانگر باشد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4559,'باید برای آینده برنامه‌ریزی کرد و فقط در رؤیای آن به سر نَبُرد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4560,'داشتن اهداف روشن، بخش ضروری [=بایسته‌ی] هر زندگی‌ست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4561,'شبیه هر چیز دیگر، کامیابی، نیاز به گسترش نظم [=سامان] دارد تا بتوان با آن، به رؤیاها دست یافت؛ کار آسانی‌ست، با وجود این، می‌تواند برای انجام، کار دشواری باشد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4562,'با تمرین بر روی کوچک‌ترین نظم‌های روزانه، می‌توان به شکلی باور نکردنی، زندگی خود را برای همیشه دگرگون ساخت.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4563,'همه‌ی پاداش‌های زندگی، در دسترس همه‌ی ماست؛ مشروط بر اینکه در مراحل نخست رشد، نظم را حکمفرما کنیم و از ناآگاهی، دوری گزینیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4564,'ما حتی برای کوچک‌ترین کارها هم بایستی همه‌ی دانسته‌هایمان را بکار گیریم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4565,'ما باید برای تبدیل رؤیا به طرح، طرح به هدف و اهداف به کنش‌های عملی روزانه، خود را سامان دهیم تا بتوانیم با گام‌هایی آرام، به سوی آینده‌ای بهتر به پیش رویم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4566,'کارهای هوشمندانه، برنامه‌ریزی شده، سخت کوشانه و پاپا، موجی از انرژی‌های تَر و تازه را می‌آفرینند و ما را به سمت آینده‌ای مهیج [=برانگیزاننده] که اندیشه‌ها و خواسته‌های ما را در بر می‌گیرند، هدایت می‌کنند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4567,'دستاوردها، همان برداشت‌هایی هستند که از تلاش‌های گذشته‌ی ما به دست می‌آیند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4568,'اگر کشاورزی، مُشتی دانه را در بهار بکارد، نباید چشم‌داشت برداشتی بزرگ را در پاییز داشته باشد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4569,'کسی که در گذشته، کارهای اندکی کرده است، نباید چشم‌داشت دستاوردهای بزرگی را داشته باشد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4570,'دستاوردها، پاداش کسانی‌ست که با آینده‌نگری، از فرصت‌ها، بیشتر بهره گرفته‌اند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4571,'فرصت‌ها، دیر به دست می‌آیند و زود، از دست می‌روند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4572,'همه‌ی آنچه که ما انجام می‌دهیم، مبنای دستاوردهای آینده‌ی ماست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4573,'درس‌های زندگی باید به خدمت ما درآیند، نه اینکه ما را ناامید کنند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4574,'اگر دستاوردهای کنونی، خرسند کننده باشند، آینده هم به همان اندازه، برداشتی پُر ثمر خواهد داشت.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4575,'اگر فردی، بذر خوبی بکارد، ثمره‌ی خوبی هم برداشت می‌کند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4576,'هرچه که به دست می‌آوریم، ثمره‌ی دیدگاه‌ها و تلاش‌های گذشته است.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4577,'اگر از آنچه که شده‌ایم، شادمان نیستیم، پس بودن‌مان را باید دگرگون سازیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4578,'هیچ چیز دگرگون نمی‌شود، مگر اینکه ما دگرگون شویم؛ این، یکی از مبانی زندگی‌ست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4579,'هر چیزی که در زندگی داریم، نتیجه‌ی مستقیم از آن چیزی‌ست که هستیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4580,'زندگی خوشایند، آن است که بیش از آنچه که هستیم، شویم تا بیش از آنچه که داریم، داشته باشیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4581,'دستاوردها همیشه پیش‌بینی پذیر هستند، چرا که دستاوردها همیشه نتیجه‌ی فرآیند آن چیزی‌ست که هستیم و شده‌ایم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4582,'با بهتر شدن ما، زندگی هم بهتر می‌شود. تا بیش از آنچه که هستیم، نشویم، نمی‌توانیم بیشتر داشته باشیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4583,'ارزش‌های بالا، پاداش‌های بالا نیز به همراه دارند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4584,'باید به جستجوی ارزش‌های بیشتر بود، نه ارزشمندی‌های بیشتر.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4585,'با توجه دقیق به بینش، منش و کارهای خود، می‌توانیم مشارکتی مثبت با آنچه که داریم می‌شویم، داشته باشیم و بر این پایه است که می‌توانیم نسبت به اکنون، بهتر شویم و بیشتر از اکنون داشته باشیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4586,'دستاوردهای بهتر، از بودن بهتر، به دست می‌آیند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4587,'بیشتر داشتن، به ما ارزش بیشتر نمی‌دهد؛ بلکه فقط ما را از نظر مادی، نسبت به اکنون، بزرگتر می‌کند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4588,'کسانی که نمی‌توانند با درآمد کم، چند پِنی پس‌انداز کنند، هرگز نمی‌توانند در فرصت‌های آتی، چندین دلار پس‌انداز کنند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4589,'تعهدات [=پیمان‌ها]، زمانی ارزش دارند که اجرا گردند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4590,'ما همه می‌توانیم به آن جایگاهی که می‌خواهیم، برسیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4591,'هیچ رؤیایی دست نیافتنی نیست، مشروط بر اینکه ما در وهله‌ی نخست، جرأت [=یارای] باور آن را داشته باشیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4592,'در هیچ کجای دنیا، فرصت‌ها کم نیستند. کمبود، فقط در افرادی‌ست که اصولاً خود را وقف کامیابی نمی‌کنند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4593,'آنچه که روی می‌دهد، تعیین کننده‌ی کیفیت زندگی ما نیست، بلکه چگونگی روبرو شدن با آن رویداد، از اهمیت برخوردار است.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4594,'دستاوردها، به نیاز پاسخ نمی‌دهند، بلکه به تلاش، کار و تکاپو، پاسخ می‌دهند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4595,'اگر ما به سهم خود تلاش کنیم، دستاوردها هم در زمان خود، نمایان خواهند شد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4596,'کمتر از حد توان، کار کردن، کارایی اندوه‌‌باری در پی دارد؛ چرا که سبب از بین رفتن خودباوری و شرافت ما می‌شود.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4597,'زمانی که زندگی، وارونه است، می‌توانیم با انجام کارهای حتی کوچک، ولی ارزشمند، گامی برای دگرگونی و بهتر شدن برداریم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4598,'دستاوردها، بیشتر و مهم‌تر از اهداف هستند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4599,'تجربه‌ی ما از هر دستاوردی، با چشم‌پوشی از کوچکی یا بزرگی آن، گامی‌ استوار به سوی کامیابی در زندگی‌ست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4600,'همواره، انتخاب [=گزینش]، برکت است؛ ولی زمانی که این گزینش، با توانایی‌ها و فرصت‌ها همسو نشود، رنج است تا برکت.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4601,'ما بیشتر خواهان این هستیم که در سایه‌ی درخت، لَم دهیم تا به خود دردسر دیدن مبارزه‌ی درخت برای یافتن بزرگی و شکوه را ببینیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4602,'دستاوردها، بهترین سَنجه برای اندازه‌گیری پیشرفت انسان‌ هستند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4603,'همیشه بزرگ‌ترین پاداش‌ها برای کسانی‌ست که ارزش بیشتری به خویشتن و جهان هستی می‌دهند و کاینات [=موجودات] هم آنها را به سمت آن چیزی که می‌خواهند بشوند، رهنمون می‌سازد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4604,'بسیاری از افراد آموخته‌اند که چگونه درآمدِ خوب داشته باشند، ولی هنوز یاد نگرفته‌اند که چگونه خوب زندگی کنند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4605,'صادقانه [=از روی راستی] دادن یک شاخه گُل رُز، ارزشی بیشتر از چندین شاخه گُل اُرکیده دارد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4606,'همه‌ی ما می‌توانیم بهتر زندگی کنیم؛ این بستگی به ارزشی دارد که ما در زندگی به آن می‌پردازیم و قدردان [=سپاس‌دار] آن هستیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4607,'شادمانی، یک هنر است، نه یک رویداد. شادمانی، چیزی نیست که از آسمان بیفتد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4608,'شادمانی، هم سبب کامیابی و هم، نتیجه‌ی آن است و ما می‌توانیم شادمانی را هر زمان که بخواهیم، صرف نظر [=با چشم‌پوشی] از شرایط موجود، تجربه کنیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4609,'شادمانی، می‌تواند هر کجا که هستیم، آغاز شود و چون نمی‌توانیم پدیده‌ها را دست کاری کنیم، باید خود، احساس شادمانی را بیافرینیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4610,'شادمانی، چیزی نیست که بتوان از حساب بانکی برداشت کرد. شادمانی را از زندگی و محیط پیرامون‌مان می‌توانیم برداشت کنیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4611,'ما به جای گرفتن چیزی از امروز، از آن می‌گذریم. در جریان همه چیز هستیم، جز در جریان زندگی امروز.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4612,'بین رفتن به پاریس و تجربه کردن پاریس، زمین تا آسمان تفاوت وجود دارد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4613,'تنها با خواستن، نمی‌توانیم زندگی بهتری داشته باشیم. ما با تلاش و کوشش می‌توانیم به زندگی بهتر دست یابیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4614,'اگر می‌خواهیم شادمان باشیم، باید شادمانه بیندیشیم، احساس کنیم و به کنش بپردازیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4615,'اگر می‌خواهیم توانگر باشیم، باید توانگرانه بیندیشیم، احساس کنیم و به کنش بپردازیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4616,'بینش، سنگ بنای پازل زندگی‌ست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4617,'خُلق و خوی‌ و رفتارهای ما، برگرفته از بینش ماست و از این جاست که به راستی، همه چیز آغاز می‌شود.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4618,'در مسیر زندگی، بادهای شرایط در یک جریان بی‌پایان، زندگی ما را دگرگون می‌کنند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4619,'روشی که برای درست کردن بادبان قایق خود برمی‌گزینیم، ما را به اهداف گوناگون زندگی هدایت می‌کند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4620,'روش اندیشیدن ما دگرگونی شگرفی در رسیدن به هدف نهایی به وجود می‌آورد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4621,'بزرگ‌ترین چالش در زندگی، مهار روند چگونه اندیشیدن است.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4622,'با دگرگون ساختن روش درک، داوری و تصمیم‌گیری‌ها، می‌توان زندگی را به گونه‌ی چشم‌گیری دگرگون ساخت.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4623,'کارآمدترین سبب برای برخورد با فرصت‌های جدید، چگونگی و جنس اندیشه‌ی ماست، نه شرایط محیطی.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4624,'یکی از بهترین روش‌های گسترش دانستنی‌ها، بازبینی جدی تجربه‌های گذشته است.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4625,'یکی از روش‌های یادگیری انجام درست یک کار، انجام نادرست آن است.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4626,'ما به همان اندازه که از کامیابی یاد می‌گیریم، از شکست نیز می‌توانیم بیاموزیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4627,'کارهای نادرست و شکست‌های گذشته به ما کمک می‌کنند تا رفتار کنونی را درست کنیم و حال و آینده‌ی ما، تکرار گذشته‌ها نباشد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4628,'باید به شفافیت تجربه‌های گذشته، خواه خوب یا بد، باور پیدا کرد تا بتوان آنها را برای ایجاد آینده‌ی بهتر از گذشته، به خدمت گرفت.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4629,'هر کس در دنیای شخصی خود، شاید فقط درختان را بتواند ببیند، در حالی که یک دوست توانا و واقع‌بین، جنگل را می‌بیند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4630,'همه‌ی ما باید شاگردان مکتب کارهای نادرست دیگران باشیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4631,'اگر درس‌های گذشته را به کناری نهیم و از آنها پند نگیریم، بی‌گمان کارهای نادرست‌مان سبب نابودی خود ما خواهند شد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4632,'همه‌ی آن رویدادهایی که ما را دگرگون می‌کنند، نشانه‌ای از آن چیز یا کسی هستند که ما روزی خواهیم بود و بر پایه‌ی آن زندگی خواهیم کرد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4633,'بیشتر، فرصت‌های فوق‌العاده در میان رویدادهای نه چندان مهم زندگی، پنهان می‌شوند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4634,'ما پیش از آنکه بتوانیم یک سخنران خوب باشیم و سخنان کارآمدی را برای دیگران بیان کنیم، باید هنر خوب شنیدن را فرا گرفته باشیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4635,'بهترین روش یاد دادن حرف شنوی [=سربراه کردن] به فرزندان، گوش فرا دادن به گفته‌های آن‌هاست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4636,'آنها که خواهان زندگی بهتر هستند، نمی‌توانند از کتاب‌هایی که کارآمدی بسیار در دگرگونی زندگی آنان داشته است، دور بمانند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4637,'آنها که در جستجوی زندگی بهتر هستند، پیش از هر چیز باید انسانی بهتر شوند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4638,'زمانی که باورهای ما دگرگون می‌شود، گزینش‌های ما نیز دگرگون خواهد شد. گزینش‌های بهتر، دستاوردهای بهتری را به همراه خواهند داشت.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4639,'بین آن کس که بی‌سواد است و توانایی خواندن ندارد و آن کس که کتاب نمی‌خواند، تفاوت [=دیگرگونی] چندانی وجود ندارد. هر دوی این‌ها نتیجه‌ی نادانی و آسان‌گیری‌ ا‌ست!((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4640,'گذشته، اگر به درستی نگارش شود و برای آینده به صورت مکتوب [=نوشته شده] درآید، یکی از بهترین راهبردها برای چاره‌اندیشی‌های خوب امروز است که به بهتر شدن فردا می‌انجامد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4641,'همه‌ی کارهای گذشته‌ی ما، ما را به زمان کنونی رسانیده‌اند. به همین سبب، کارهایی که هم‌اکنون انجام می‌دهیم، سبب شادمانی یا پشیمانی ما در آینده خواهند شد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4642,'در زمان‌های سرنوشت ساز انتخاب [=گزینش]، دانش و آگاهی‌هایی که کسب کرده‌ایم و بینشی که از این دانش آموخته‌ایم، یا به ما کمک می‌کند و یا ما را نابود می‌سازد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4643,'شکست، چیزی جز تکرار کارهای نادرست، در داوری‌‌های روزمره‌ی ما نیست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4644,'بدتر از کار نخواندن کتاب، ندانستن اهمیت خواندن آن است.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4645,'بدبختانه، شکست هیچ‌گاه بانگ برنمی‌آورد و هشدارهایش را همانند پدر و مادر، فریاد نمی‌زند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4646,'پاداش‌ها و افسوس‌های ناگزیر اکنون، نتیجه‌ی کارهایی هستند که در گذشته انجام داده‌ایم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4647,'ما برای دگرگون کردن زندگی، باید نخست، خُلق و خو‌های فکری خود را دگرگون کنیم.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4648,'ما برای دگرگون کردن خود از آنچه که اکنون هستیم، به آن چیزی که می‌خواهیم بشویم، بایستی با آن بنیادهایی آغاز کنیم که بر چگونگی اندیشه‌ی ما تاثیر می‌گذارند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4649,'ما ناگزیر به دگرگونی همه چیز، برای کسب نتایج، نیستیم و دگرگونی‌های کوچک، با شتاب، دگرگونی را ایجاد می‌کنند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4650,'شادمانی و کامیابی، آسان به دست می‌آید.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4651,'اگر نخستین گام برداشته شود، رسیدن به کامیابی و شادمانی، دیگر چندان دشوار نیست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4652,'ناکامی، نتیجه‌ی ناآگاهی‌ست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4653,'نادانی‌‌‌های کوچک، راهی به سوی مرتکب شدن کارهای نادرست بزرگ و هدر رفتن زمان هستند.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4654,'نادانی و بی‌خبری، شبیه عفونت است؛ اگر بررسی نشود، به کل نظام‌مندی‌ها وارد شده و سبب فروپاشی کامل زندگی خوش و سرشار از کامیابی آدمی می‌شود.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4655,'انجام ندادن چیزهایی که ما به انجام دادن‌شان باور داریم، سبب می‌شود احساس گناه به ما دست دهد و این احساس گناه است که سبب می‌شود آدمی، خودباوری‌ خویش را از دست بدهد.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4656,'آنچه که می‌دانیم، نشانه‌ی بینش ماست؛ و آنچه را که درباره‌ی دانسته‌های خود احساس می‌کنیم، نشانه‌ی منش ماست.((جیم رآن))',56,0,0,0,0,0);
INSERT INTO mysms VALUES(4657,'آدمیزاد، به راستی همان است که خود می‌پندارد.((جیمز آلن))',57,0,0,0,0,0);
INSERT INTO mysms VALUES(4658,'امروز به هر جایی که رسیده‌اید، دیدگاه‌تان شما را به آنجا رسانده است و فردا هم همان جایی خواهید بود که دیدگاه‌تان شما را می‌برد.((جیمز آلن))',57,0,0,0,0,0);
INSERT INTO mysms VALUES(4659,'انسان نمی تواند به گونه ای مستقیم حوادث زندگی خودش را انتخاب کند، ولی می تواند افکار خود را انتخاب کند و با این عمل به طور غیر مستقیم، شکل حوادث زندگی خود را تعیین نماید.((جیمز آلن))',57,0,0,0,0,0);
INSERT INTO mysms VALUES(4660,'انسان مو به مو همان چیزی است که می پندارد؛ سرشت او جمع کل اندیشه های او است.((جیمز آلن))',57,0,0,0,0,0);
INSERT INTO mysms VALUES(4661,'آدمی وقتی که فکر و اندیشه خود را در مورد اشیا و اشخاص تغییر بدهد می بیند که آن اشیا و اشخاص هم نسبت به او تغییر روش می دهند.((جیمز آلن))',57,0,0,0,0,0);
INSERT INTO mysms VALUES(4662,'راز قدرت حقیقی در این است : با تمرینِ مداوم یاد بگیرید که چگونه توانایی های خود را هدر ندهید و در هر لحظه آنها را بر یک نقطه متمرکز کنید.((جیمز آلن))',57,0,0,0,0,0);
INSERT INTO mysms VALUES(4663,'کسی که کار کوچکی انجام می دهد، باید مقدار ناچیزی فداکاری کند؛ کسی که می خواهد موفقیت بزرگی را به دست آورد، باید به میزان زیادی فداکاری کند.((جیمز آلن))',57,0,0,0,0,0);
INSERT INTO mysms VALUES(4664,'شما به همان اندازه که بخواهید کوچک و به همان اندازه که آرزو کنید بزرگ می شوید.((جیمز آلن))',57,0,0,0,0,0);
INSERT INTO mysms VALUES(4665,'اندیشه همچون باغی است که هم می توان در آن كِشت و هم می توان به علف های هرز، اجازه ی رویش داد.((جیمز آلن))',57,0,0,0,0,0);
INSERT INTO mysms VALUES(4666,'آرمان و ایده آل آدمی، پیشگویی همان چیزی خواهد بود که سرانجام، روزی آشکار و پدیدار خواهد شد.((جیمز آلن))',57,0,0,0,0,0);
INSERT INTO mysms VALUES(4667,'کامیابی و پیروزی، بسان کرم شب‌تاب از دور می‌درخشد، ولی از فاصله‌ی نزدیک نه سوی دارد و نه حرارتی.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4668,'خدا آزادی را به کسانی می دهد که در جستجوی آن هستند.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4669,'هیچ چیز مثل نامه نمی تواند اصطلاحات ادبی و استعداد و قدرت تخیل شخص را برساند.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4670,'گاهی یتیم بودن هم مزایایی دارد.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4671,'عیب کار اینجاست که در دانشکده انتظار دارند آدم همه چیز را بداند.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4672,'بسیار مشکل است که آدم بخواهد تمام وقت مراقب خود باشد تا آنچه را احساس می کند نگوید.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4673,'چقدر دشت و صحرا پرماجرا است و لذت زندگی درآمیختن با این ماجراها است نه نوشتن آنها در کتاب.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4674,'ناراحتی های بزرگ نیست که صبر و بردباری نیاز دارد، بلکه این ناراحتی های خرد خرد و جگر سوراخ کن را با تبسم برگزار کردن، به راستی نیازمند روحیه است.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4675,'من با این فلسفه که ناامیدی و نکبت و غم، نیروی اخلاقی را تقویت می کند مخالفم. افراد نیکبخت و شادمان هستند که مهر و محبت می بخشند.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4676,'آن روز که ما زنها حقوق خود را به دست بیاوریم شما مردها باید حسابی مراقب خودتان باشید که کلاه سرتان نرود.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4677,'من زمستان را دوست دارم که با برف باشد نه با باران.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4678,'با مردها سر و کله زدن به راستی نیازمند تدبیر است. اگر آدم نقطه ی حساس آنها را بیابد، مثل گربه خرخر می کنند و اگر برخلاف میل آنها رفتار کنی چنگ می زنند.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4679,'وقتی که شما یک دختربچه ی نه ساله ی گرسنه ای را در آبدارخانه بگذارید که کاردها را جلا دهد و یک ظرف نان شیرینی هم کنار دستش باشد و تنهایش بگذارید، چه انتظاری [ =چشم داشت ] دارید؟((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4680,'همه کس آرزو دارد که به رویدادهای غیر منتظره برخورد کند؛ این در زندگی بشر طبیعی است.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4681,'مهمترین ویژگی آدمی، قوه ی تخیل و تصور او است. برای اینکه انسان می تواند خودش را به جای دیگری در نظر بگیرد. این ویژگی، انسان را فهمیده و دلسوز می کند.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4682,'بچه ها باید یاد بگیرند که هر کاری را با عشق و دلبستگی انجام دهند نه از روی وظیفه شناسی.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4683,'دنیا پر از شادی است، اگر، هر چه پیش آید انسان بپذیرد. تنها راز کامیابی در این است که انسان چشم داشت زیادی نداشته باشد.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4684,'وقتی انسان به شخصی، مکانی و یا روش ویژه ای در زندگی عادت کند و ناگهان آن را از دست بدهد، یک جای خالی در دل انسان باقی می گذارد.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4685,'زن به هر چه توجه کند و دلبسته باشد، خواه بچه، میکروب، همسر، شعر، کلفت و نوکر، گلکاری، افلاطون، متوازی الاضلاع یا بازی بریج، در هر حال و اساساً به لباس و قر و فر دلبسته است.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4686,'شوخی های بزرگ مهم نیستند، اصل این است که انسان بتواند از یک موضوع کوچک شاد باشد.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4687,'افسوس گذشته را خوردن و به امید آینده به سر بردن نادرست است؛ باید از این لحظه بیشترین بهره را برد.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4688,'می خواهم هر ثانیه از زندگیم را خوش باشم و می خواهم وقتی که خوش هستم بدانم که خوش هستم.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4689,'برخی از افراد زندگی نمی کنند، مسابقه ی دو گذاشته اند، می خواهند به هدفی که در افق دوردست دارند برسند و در حالی که نفسشان به شماره افتاده، می دوند و زیبایی های پیرامون خود را نمی بینند.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4690,'انسان هرگز چیزهایی که مزه اش را نچشیده هوس نمی کند، اما پس از اینکه یکبار مزه ی نعمتی را چشید، آن وقت دیگر محرومیت از آن دشوار است.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4691,'مسافرت و دیدن دنیا خود یک نوع آموختن و پروردن است.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4692,'زندگی بدون پیشگویی های خود یکنواخت است، مرتب باید خورد و خوابید، وای به روزی که انسان همه چیز را بداند و هیچ رویداد غیرمنتظره ای روی ندهد.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4693,'افتادگی یا گردن نهادن و خشنود بودن و یا هرچه می خواهید نامش را بگذارید، نشانه ی سستی و درماندگی است.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4694,'جوانی، کاری به سال و ماه ندارد، مهم این است که انسان زنده دل و پرنشاط باشد. ممکن است موی شما سفید باشد، اما مانند پسربچه ها بیندیشید.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4695,'به ناراحتی ها (حتی دندان درد) مانند تجربه ی جالبی می نگرم و از اینکه از تجربه ی تازه ای بیخبر نمانده ام، شادمانم. آسمان بالای سر من به هر رنگی باشد، من برای هر گونه سرنوشتی آماده ام.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4696,'انسان وقتی در مورد موضوعی که از آن آگاهی دارد سخن بگوید یا بنویسد، می تواند سخن خود را به دیگران بقبولاند و موثر واقع شود.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4697,'انسان وقتی یک چیزی را با تمام توان بخواهد تلاش می کند و سرانجام آن را به دست می آورد.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4698,'اگر عاشق بوده اید که نیازی به بیان و روشن کردن آن نیست و اگر نبوده اید که من نمی توانم برایتان بیان کنم.((جین وبستر))',58,0,0,0,0,0);
INSERT INTO mysms VALUES(4699,'خودپسندی زنها بزرگترین علت بدبختی ایشان و نابودی خانواده هاست. هیچ چیز به اندازه خودپسندی زنها بنیان خانواده ها را ویران نکرده است.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4700,'درخشانترین تاجی که مردم بر سر می نهند، در آتش کوره ها ساخته شده است.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4701,'اگر شاد بودی آروم بخند تا غم بیدار نشه و اگر غمگین بودی آروم گریه کن تا شادی ناامید نشه.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4702,'انسان اگر فقیر و گرسنه باشد، بهتر از آن است که پست و بی عاطفه باشد.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4703,'از دشمن خود یکبار بترس و از دوست خود هزار بار.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4704,'از لحظه ای که بلیط سینما را می خرید وارد دنیای دیگری می شوید.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4705,'فیلمسازان باید به این نیز بیندیشند که فیلمهایشان را در روز رستاخیز با حضور خودشان نمایش خواهند داد.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4706,'وقتی زندگی صد دلیل برای گریه کردن به تو نشون می ده، تو هزار دلیل برای خندیدن به اون نشون بده.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4707,'من دریافته ام که ایده های بزرگ هنگامی به ذهن راه می یابند که اراده کنیم چنین ایده هایی را داشته باشیم.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4708,'در دنیا جای کافی برای همه هست. پس به جای اینکه جای کسی را بگیری، سعی کن جای خودت را پیدا کنی.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4709,'حتی تظاهر به شادی نیز برای دیگران شادی بخش است.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4710,'شاید زندگی آن جشنی نباشد که آرزویش را داشتی، اما حال که به آن دعوت شده ای، تا می توانی زیبا برقص.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4711,'خوشبختی، فاصله این بدبختی تا بدبختی دیگر است.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4712,'شکست خوردن ناراحتی ندارد. آدم باید شجاع باشد تا بتواند از خودش یک احمق بسازد!((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4713,'این یکی از تضادهای زندگی ما است که آدم همیشه کار اشتباه را در بهترین زمان ممکن انجام می دهد.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4714,'نقاش کامل آن است که از هیچ برای خود سوژه بسازد.((چارلی چاپلین))',59,0,0,0,0,0);
INSERT INTO mysms VALUES(4715,'انسان موجودی است که به همه چیز عادت می کند.((داستایوسکی))',60,0,0,0,0,0);
INSERT INTO mysms VALUES(4716,'حقیقت ترکیبی است از گریه ها و خنده ها.((داستایوسکی))',60,0,0,0,0,0);
INSERT INTO mysms VALUES(4717,'من معتقدم که همه ما باید عشق به حیات را دریابیم و زندگی را دوست بداریم.((داستایوسکی))',60,0,0,0,0,0);
INSERT INTO mysms VALUES(4718,'کسی که به خودش دروغ می گوید و به دروغ خودش گوش می دهد، کارش به جایی خواهد رسید که هیچ حقیقتی را نه از خودش و نه از دیگران تشخیص نخواهد داد.((داستایوسکی))',60,0,0,0,0,0);
INSERT INTO mysms VALUES(4719,'زنان به خوبی مردان می توانند اسرار را حفظ کنند، ولی به یکدیگر می گویند تا در حفظ آن شریک باشند.((داستایوسکی))',60,0,0,0,0,0);
INSERT INTO mysms VALUES(4720,'تنها آرامش و سکوت، سرچشمه ی نیروی ابدی و جاوید است.((داستایوسکی))',60,0,0,0,0,0);
INSERT INTO mysms VALUES(4721,'دانش بیمناک ترین بلای جان آدمی است... رنج و بدبختی آن از طاعون و قحطی و جنگ بیشتر است.((داستایوسکی))',60,0,0,0,0,0);
INSERT INTO mysms VALUES(4722,'سراسر زمین از بالا به پایین، آکنده از اشک بشر است.((داستایوسکی))',60,0,0,0,0,0);
INSERT INTO mysms VALUES(4723,'اگر یک روز، یک ساعت، ناگهان همه چیز بخواهد به یک چیز تبدیل شود، برترین آنها مهر ورزیدن است.((داستایوسکی))',60,0,0,0,0,0);
INSERT INTO mysms VALUES(4724,'بر این باور باش که عشق و دستاوردهای عظیم، در برگیرنده ی خطرهای بزرگ است. آنگاه که شکست می خوری، از شکست خود درس بگیر.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4725,'در برابر دگرگونیها انعطاف پذیر باشید، ولی معیارها و ارزشهای خود را زیر پا نگذارید.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4726,'تنها مقصود ما در زندگی، عشق ورزیدن به یکدیگر است. اگر از عهده این مهم بر نمی آییم، دست کم بکوشیم تا یکدیگر را نیازاریم.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4727,'عشق پاک نسبت به اطرافیان باید بی غرض باشد، چرا که بی غرض بودن لازمه آن است.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4728,'برای قضاوت در مورد موفقیت خودت، ببین چه چیزی به دست آورده ای و در قبال آن چه چیزی از دست داده ای.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4729,'به یاد داشته باش، دست نیافتن به آنچه می خواهی، گاهی از اقبال بیدار تو سرچشمه می گیرد.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4730,'اصول و بنیادها را فرا گیر تا به چگونگی شکستن آنها به گونه ای شایسته آگاه باشی.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4731,'نگذار ستیزه ای خُرد بر ارتباطی پر قدر، خللی وارد سازد.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4732,'هرگاه به اشتباه خویش پی بردی، بی درنگ گامهایی برای درست کردن آن بردار.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4733,'سه اصل را دنبال کن: محترم داشتن خود، محترم داشتن دیگران و پاسخگو بودن در قبال تمام کنش های خود.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4734,'هر روز مجالی را صرف خلوت کردن کن.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4735,'آغوشت را به سوی دگرگونی بگشای، اما از ارزش های خود دست بر ندار.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4736,'به یاد داشته باش، خاموشی گاهی بهترین پاسخ است.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4737,'نیکو و آبرومند زندگی کن، آنگاه، به وقت سالخوردگی، هنگامی که به گذشته بی اندیشی، دیگر بار از زندگی ات لذت خواهی برد.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4738,'فضای عشق در خانه ی تو شالوده ای برای زندگی ات به شمار می رود.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4739,'در ناسازگاری ها با افراد مورد علاقه ات، تنها به وضعیت کنونی بپرداز، گذشته را بزرگ نکن.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4740,'دانش خود را به دیگران ببخش که روشی برای دستیابی به جاودانگی است.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4741,'با زمین مهربان باش.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4742,'سالی یکبار به جایی برو که پیش تر هرگز در آنجا نبوده ای.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4743,'به یاد داشته باش، بهترین رابطه، رابطه ای است که عشقتان به یکدیگر بر نیازتان به یکدیگر فزونی یابد.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4744,'کامیابی خود را به داوری بنشین، از این روش که بدانی چه واگذارده ای تا کامیابی را به دست آوری.((دالای لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4745,'برای اداره کردن خویش از سرت استفاده کن و برای اداره کردن دیگران از قلبت.((دالایی لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4746,'اگر کسی یک بار به تو خیانت کرد، این اشتباه او است و اگر کسی دوبار به تو خیانت کرد، این اشتباه تو است.((دالایی لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4747,'دیروز تاریخ است، فردا راز است، امروز یک هدیه است.((دالایی لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4748,'هر کجا که هستی، هر کجا خودت را یافتی، از هر آنچه که داری لذت ببر، به تمامی لذت ببر. هر جا که هستی و از هر چه که در دسترس است، احساس سپاس و نیایش داشته باش.((دالایی لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4749,'یوگا پیام خرد است و بشریت وارث این پیام.((دالایی لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4750,'سه اصل را فراموش نکن؛ احترام به خویشتن، احترام به دیگران و پذیرش مسئولیت تمامی اعمالی که انجام می دهی.((دالایی لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4751,'به یاد داشته باش که بهترین رابطه، رابطه ای است که میزان عشق طرفین، از میزان نیازشان به یکدیگر تجاوز کند.((دالایی لاما))',61,0,0,0,0,0);
INSERT INTO mysms VALUES(4752,'نیت نخستینِ کسی که عمل می کند، در هر کاری که باشد، آشکار ساختن تصویر خویش است.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4753,'همیشه شعله های بزرگ ناشی از جرقه های کوچک است.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4754,'به یاد داشته باش که امروز طلوع دیگری ندارد.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4755,'احساس شرم کمتر نتیجه گناهی بزرگتر است.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4756,'حتی زمانی هم که بدون امید زندگی می کنیم، آرزوهایی داریم.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4757,'بهشت نیز در تنهایی دیدنی نیست.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4758,'عاشق آن کسی باش که بر دو سویه بودن عشق پافشاری می کند.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4759,'طبیعت، هنر خداوند است.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4760,'تنها هدیه دادن مهم نیست، سلیقه در انتخاب هم مهم است.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4761,'هیچ کدام از نعمت های خدا نیست که گروهی از آفریده های خدا از آن بهره نگیرند.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4762,'بزرگداشت نویسندگان کهن مستلزم به کار بردن زبان آنها نیست، بلکه انجام همان کاری است که آنها انجام داده اند؛ یعنی پدید آوردن یک سبک ادبی به همان زبانی که هم میهنانشان سخن می گفته اند.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4763,'یک زبان ادبی مشهور، اساسی، زیبا و ظریف باید از پالایش مس بی ارزش گفتار روزمره به دست آید.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4764,'آسایش، پاداشی است که پس از هرکار و تلاش، چشم براه بشر است.((دانته))',62,0,0,0,0,0);
INSERT INTO mysms VALUES(4765,'در بین تمامی مردم تنها عقل است که عادلانه تقسیم شده، زیرا همه فکر می کنند به اندازه کافی عاقلند.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4766,'از آنچه شنیده و دیده و خوانده اید، هیچ چیز را قبول نکنید، مگر آنکه درستی و صحت آن بر خودتان آشکار شده باشد.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4767,'انسان نباید هیچ امری را به عنوان حقیقت قبول کند، مگر آنکه به راستی در نظر او حقیقت باشد.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4768,'حقیقت را با بیطرفی مطلق و با روحی آزاد از هر گونه تعصب جستجو کنید.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4769,'مطالعه، یگانه راهی است برای آشنایی و گفتگو با بزرگان روزگار که قرن ها پیش در دنیا به سر برده و اکنون در زیر خاک منزل دارند.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4770,'انسان، مالک و ارباب است، در حالی که حیوان فقط یک ماشین خودکار است، یک ماشین جاندار است. وقتی جانوری ناله می کند، نشانه ی شِکوه و زاری نیست، بلکه سر و صدای ابزار ماشینی است که خوب کار نمی کند.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4771,'من در هر چه شک کنم، در این شک نمی توانم کرد که شک می کنم و چون شک می کنم، می اندیشم؛ می اندیشم، پس هستم.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4772,'به جای تسلط بر جهان، باید بر خویشتن مسلط شد.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4773,'خواندن کتابهای خوب، [ در حکم ] مکالمه با انسانهای شرافتمند گذشته است.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4774,'تنها، داشتن اندیشه ی خوب کافی نیست؛ مسئله ی بنیادی در این است که آن را خوب به کار ببریم.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4775,'مسافرت چیزی است کم و بیش مثل حرف زدن با آدمیان قرنهای گذشته.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4776,'من می اندیشم پس هستم.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4777,'زمانی که خداوند ما را خلق می کرد، تصور فطری خویش را در ذهن ما مهر نمود.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4778,'ما می توانیم مدعی شویم که جسم وجود ندارد و همچنین ادعا کنیم که جهانی نیز وجود ندارد... اما هرگز نمی توانیم ادعا کنیم که من وجود ندارم؛ زیرا من می توانم در حقیقت سایر اشیاء شک کنم.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4779,'از هیچ اندیشه جدیدی بدون فهم آن حمایت نکنید. بلکه با شک و تردید در آن وارد شده و حقیقت را کشف کنید.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4780,'[ من ] می اندیشم پس هستم، هستم چون فکر می کنم، فکر می کنم چون شک می کنم.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4781,'رقابت تا زمانی پسندیده است که کار را به حسادت نکشاند.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4782,'باید بخواهیم تا بتوانیم!((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4783,'کسانی که آهسته می روند، اگر همواره در راه راست قدم بردارند، از آنانکه می شتابند و به بیراهه می روند بسیار بیشتر خواهند رفت.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4784,'برای اینکه بتوانم در مسیر زندگانی با اطمینان خاطر گام بردارم و عمل کنم، میل فوق العاده ای دارم که بدانم چگونه می توان بین حقیقت و غیر حقیقت، راست و دروغ و درستی و نادرستی تفاوت قایل شد.((دکارت))',63,0,0,0,0,0);
INSERT INTO mysms VALUES(4785,'هیچ چیز اضافه‌ای در جهان هستی وجود ندارد. هر کسی پا به عرصه‌ی وجود گذاشته، چون جایگاهی دارد که باید پُر کند. هر جزئی باید جایگاه مناسب خود را در این پازل بزرگ پیدا کند.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4786,'اندیشه‌های شاد، مولکول‌های شاد می‌آفریند.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4787,'همه‌ی ریشه‌های رنج انسان، از این توهم آغاز می‌شود که گمان می‌کند او در جهان می‌زید، حال آنکه، جهان در او می‌زید.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4788,'هشیاری، چیزی جز آگاهی نیست؛ مجموعه ی چیزهایی است که به آن توجه می کنیم.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4789,'پول، یک انرژی مثبت و حیاتی است که انسان‌ها با هم مبادله می‌کنند و به راستی، پاداشی است که در برابر تلاش خود، از هستی دریافت می‌دارید.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4790,'حقیقت زندگی این است که همه ی انسانهایی را که با آنها در تماس هستیم شاد کنیم.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4791,'از پیشداوری کردن دست بکش تا آرامش را تجربه کنی.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4792,'جهان فقط چیزهایی را به ما می دهد که باور داریم می توانیم داشته باشیم.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4793,'اگر محبت می خواهید، بیاموزید که محبت کنید.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4794,'اگر شادی می خواهید، به دیگران شادی بدهید.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4795,'برای تغییر جسم خود ابتدا آگاهی خود را تغییر دهید.((دیپاک چوپرا))',64,0,0,0,0,0);
INSERT INTO mysms VALUES(4796,'کسانی که ازدواج کرده اند، خود را ناراضی نشان می دهند و می گویند بد است. زیرا می خواهند فقط و فقط خودشان از این موهبت برخوردار شوند.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4797,'توجه کردن به بی توجهی به حقایق، گامی بزرگ به سوی دانایی است.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4798,'رمز موفقیت، پایبندی به هدف در زندگی است.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4799,'جوانی اشتباه است، کهولت، مبارزه و پیری پشیمانی.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4800,'کسانی که دنیا را تکان داده اند در استعدادهای طبیعی نابغه نبوده اند، بلکه بر عکس، قوای عقلی آنها از حد معمول و متوسط تجاوز نمی کرده است، اما به یک صفت ممتاز بوده اند: ثبات و استقامت.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4801,'همه به خاطر عشق زاده شده ایم؛ عشق، پایه و اساس هستی و تنها پایان آن است.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4802,'عمر آنقدر کوتاه است که نمی ارزد آدم، حقیر و کوچک بماند.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4803,'هیجانها همیشه شادی برانگیز هستند، اما هیچ شادی ای نیست که بدون هیجان باشد.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4804,'مردم موفق امروز، کودکان جسور دیروز هستند.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4805,'جوانی که به بالا نمی نگرد، نظرش به پایین می افتد و کسی که به راه کمال نمی رود، به پستی می گراید.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4806,'وقت گرانبها است، اما حقیقت گرانبهاتر است.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4807,'سلامتی، زیربنای خوشبختی، قدرت و روحیه است.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4808,'انسان به وجود آورنده ی شرایط نیست، بلکه شرایط، آفریننده ی انسان است.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4809,'مردان بزرگ کسانی هستند که تا رسیدن به هدف، آسایش ندارند.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4810,'زندگی، کوتاهتر از آن است که دست کم بگیرید اش.((دیزرائیلی))',65,0,0,0,0,0);
INSERT INTO mysms VALUES(4811,'دیگران را همانگونه که هستند، بپذیرید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4812,'ما به وسیله چهار چیز با مردم در تماس هستیم. ارزش موقعیت ما از روی این چهار چیز معلوم می شود : آنچه انجام می دهیم، آنچه جلوه می کنیم، آنچه می گوییم، آنطور که می گوییم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4813,'برای جلب علاقه باید اظهار علاقه مندی کرد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4814,'شش راه برای جلب محبت مردم :1- صمیمانه نسبت به غیر، علاقه مند باشید. 2- تبسمی بر لب داشته باشید. 3- به یاد بیاورید که نام هر کس برای او شیرین ترین و مهمترین لغت قاموسهاست.4- شنیدن را بیاموزید، طرف خود را به شوق آورید که از خود سخن بگوید.5- با مخاطب از آنچه دوست دارد صحبت کنید.6- صمیمانه و صادقانه اهمیت او را برا ی خودش آشکار سازید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4815,'فرصت را سفت بچسبید! زندگی، سراسر یک فرصت است. آنها که بیشترین پیشروی‌ها را می‌کنند، کسانی هستند که خواسته‌اند چنین کنند و دلش را هم داشته‌اند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4816,'بگذارید هر کس بر مبنای باور، فکر، آرزو، مطالعه و دانسته های خود قضاوت کند، نه اینکه شخص طوطی صفت گفته دیگران را بازگو کند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4817,'اگر می دانید گناهکار هستید، بی درنگ و از صمیم قلب، گناه خود را به گردن بگیرید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4818,'تا به پل نرسیده ای از آن عبور نکن و برای شیر ریخته شیون به راه نیانداز.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4819,'به یاد آورید که طرف شما صد برابر بیشتر به فکر گمانها و آرزوهای خود می باشد تا اندیشه و گمانهای شما.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4820,'اگر ما به دنبال سعادت و خوشبختی هستیم باید از فکر کردن درباره حق شناسی یا حق ناشناسی خودداری کنیم و محبت و خوبی را تنها برای شادی درون انجام دهیم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4821,'وقتی که خود را برای بدترین وضع آماده کنیم، نگرانی در ما اثر ندارد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4822,'اگر اشخاص خودخواهی تلاش دارند شما را بفریبند، نامشان را از لیست نامهای همنشینان خود خط بزنید، اما هرگز درصدد انتقام جویی برنیایید. زیرا اگر چنین کاری بکنید، خود را بیشتر از آن اشخاص آزار داده اید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4823,'حس غرور و خودبینی در تمام مردم یکسان است؛ تفاوتش تنها در نحوه ظهور آن است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4824,'برخی از خنده ها از گریه بالاتر است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4825,'نعمتهای خود را بشمار نه محرومیت ها را.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4826,'برای زندگی فکر کنید، اما غصه نخورید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4827,'ما چقدر دیر متوجه می شویم که زندگی و حیات یعنی همان دقایق و ساعاتی که با کمال شتابزدگی و بیرحمی انتظار گذشتن آنرا داشتیم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4828,'با دیگران بخند نه بر دیگران.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4829,'دو نفر از پنجره زندان به بیرون می نگریستند؛ یکی گل و لای را می دید و دیگری ستاره های درخشان را.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4830,'اگر می خواهید فردی را با رای و نظر خود همراه کنید، بگذارید در کمال آزادی حرفش را بزند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4831,'برای راه یافتن به دل هر کس باید از چیزی سخن گفت که نزد او عزیزتر است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4832,'بگذار مخاطب خیال کند فکری که تو به او تلقین کرده ای از خود اوست.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4833,'بهترین وسیله غلبه در مشاجره و مباحثه، جلوگیری از ورود به مشاجره و مباحثه است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4834,'بهترین شکل خوش آمد گویی این است که درباره هر کس حرفی را به زبان بیاورید که او در مورد خود فکر می کند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4835,'شما با دوست داشتن دیگران می‌توانید در طول دو ماه دوستان زیادی به دست بیاورید؛ دوستانی که اگر تلاش کنید دیگران شما را دوست داشته باشند، در طول دو سال هم به دست نخواهید آورد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4836,'بدون درک مطلب هر قدر هم در جهت رفع و حل مشکل تلاش کنیم نتیجه ای جز بهم ریختگی و اغتشاش فکری در بر نخواهد داشت.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4837,'اگر شخصی وقت خود را به روش علمی و کاملاً بیطرفانه و بی غرض صرف به دست آوردن حقیقت کند نگرانیش در اثر اطلاع یافتن بر حقایق موجود از میان خواهد رفت.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4838,'برای حل مشکل ابتدا باید حقیقت را دریابیم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4839,'طبق نظریه روانشناسی فرد هر چقدر هم که دارای قدرت فکری بالایی باشد قادر نخواهد بود که در یک زمان فکرش را متوجه دو موضوع کند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4840,'حسهای بد و منفی مثل کینه، حسد، غم و غصه چنان با قدرت و سرعتی غیرعادلانه راه خود را در افکار آدمی باز می کند که به ناچار افکار خوب و خوشایند مجبور به عقب نشینی می شوند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4841,'کار، ارزانترین دارویی است که برای رفع نگرانی در دسترس است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4842,'وقت خود را با کار پر کنید. آنان که دچار نگرانی هستند دارویی مناسب تر و ارزانتر از این برای مقابله با نگرانی نمی توانند بیابند. اگر خود را در کار غرق نکنید قطعاً در غم و ناامیدی غرق خواهید شد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4843,'اگر شما خواستار آرامش خیال هستید نگرانی را کوچک و حقیر بشمارید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4844,'همه ما در طول زندگیمان با حوادث و وقایع ناخوشایند روبرو می شویم و چاره ای جز پذیرفتن نداریم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4845,'وقتی از مبارزه بیهوده و کشمکش های بی ثمر با حوادث غیر قابل تغییر دست برداریم، در حقیقت نیروهایی که قادرند ما را به سوی سعادت سوق دهند آزاد کرده ایم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4846,'هیچ کس را سراغ ندارم که دارای آنچنان توانایی باشد که در آن واحد هم قادر به گلاویز شدن و دست و پنجه نرم کردن با مشکلات چاره ناپذیر باشد و هم در فکر یک زندگی تازه و جدید برای خود باشد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4847,'اگر دنیای حقیقی را رها کنیم و به سوی دنیای رویایی که ساخته ذهن خودمان است پیش برویم، آن زمان است که دیوانگی را برای خود خریده ایم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4848,'در این دنیا تنها راه استفاده از گذشته این است که با حوصله و تامل به بررسی و تجزیه و تحلیل اشتباهات گذشته بپردازیم و دیگر آن را تکرار نکنیم و از اشتباهات گذشته درس عبرت بگیریم و پس از آن فراموششان کنیم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4849,'مدتهاست به این امر پی برده ام که آموزش دادن یک کار خوب به بیست نفر راحت تر از این است که خودم یکی از آن بیست نفر باشم که می بایست به دستورات خودم عمل کنم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4850,'ضرب المثل های ساده، نتیجه تجربیات ملتهاست که از طریق نسلها سینه به سینه نقل شده و به ما رسیده است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4851,'هدف من از نگارش کتاب، آموختن مطالب تازه و جدید به شما نیست، بلکه هدف این است که شما را مجبور کند از آن چیزهایی که می دانید استفاده کرده و به آن عمل کنید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4852,'اصلی ترین مشکل ما انتخاب افکار صحیح و درست است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4853,'حوادثی را که هنوز اتفاق نیفتاده زیاد بزرگ نکنید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4854,'ما دارای روح آسمانی و علو طبعی نیستیم که قادر باشیم دشمنان خود را دوست بداریم، اما دست کم می توانیم برای سلامت و نشاط خود، آنها را ببخشیم و خطاهایشان را فراموش کنیم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4855,'به جای اینکه نسبت به دشمنان خود کینه توزی کنیم، خداوند را شاکر باشیم که زندگی، ما را همچون آنها بار نیاورده است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4856,'به طور کلی، ذات و نهاد جهان هستی ناسپاس و غیر قابل تغییر است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4857,'فراموش کردن اظهار تشکر و حق شناسی، جزیی از نهاد بشری است. به این ترتیب اگر ما همیشه منتظر باشیم که مورد تقدیر و تشکر قرار بگیریم، در حقیقت خود را به سمت یک دنیای پر از غم و ناراحتی هدایت کره ایم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4858,'چرا از فرزندانی که خود آنها را قدر ناشناس پرورش داده و تربیت کرده ایم، گله مندیم؟((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4859,'فرزندان ما همان گونه می شوند که تربیت شده اند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4860,'ثروتهای وجودی هر کدام از ما از گنجهای افسانه ای علی بابا بیشتر است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4861,'همیشه نعمتهایی را که داری بشمار، نه محرومیتها و ناکامیهایت را.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4862,'انسانهایی که صرفاً مقلد هستند، در نهایت یک کپی و یا درجه دومی از مورد تقلید خود خواهند شد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4863,'شما باید باغچه کوچک زندگیتان را مطابق میل و سلیقه خود بیارایید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4864,'هر کسی در دنیا ساز خودش را می نوازد و تنها با همان سازی که در اختیار دارد قادر به نواختن است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4865,'همواره در تاریخ دنیا خوشبختی و سعادت نصیب کسانی شده است که از زیر بار مشکلات و ناهنجاریهای زندگی خود شانه خالی نکرده و در برابر هر گونه حادثه ای چه خوب و چه بد ایستادگی کرده اند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4866,'نباید یکی از مصلحان بزرگ باشید تا در راه اصلاح دنیا قدم بردارید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4867,'خوشبختی و سعادت ما متکی به خوشبختی و سعادت دیگران است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4868,'با نشان دادن علاقه و علاقه مند شدن به دیگران، خود را از یاد ببرید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4869,'روزانه یک کار نیک انجام دهید و پس از انجام دادن یک کار خوب لبخند بزنید و شاهد لبخند دیگران باشید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4870,'زندگی و وجود ما سراسر راز و معما است که ما قادر به درک و فهم آن نیستیم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4871,'تازه می فهمم که مردم به من و شما و چیزهایی که در مورد ما گفته می شود توجهی نشان نمی دهند. آنها شبانه روز به فکر حال و احوال خود هستند. آنها هزاران مرتبه به سردردهای جزئی خود حتی بیشتر از واقعه مرگ من و شما اهمیت می دهند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4872,'کار خودت را به بهترین شکل انجام بده و سپس چتر کهنه ات را بالای سر بگیر تا تو را از باران انتقادها در امان نگاه دارد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4873,'ما مخلوق استدلال و دلیل و برهان نیستیم، بلکه مخلوق احساسات هستیم. عقل و منطق ما همچون قایقی کوچک در دریای عمیق و متلاطم احساسات به این طرف و آن طرف می رود.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4874,'خستگی، خود عامل تولید کننده نگرانی است و یا دست کم فرد را برای نگران شدن آماده می کند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4875,'ما به این دلیل خسته می شویم که احساساتمان موجب تولید فشارهای عصبی در بدنمان می شود.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4876,'از این نترسید که بهترین و بیشترین تلاش خود را صرف کارهای به ظاهر جزیی کنید. هر گاه کاری را به گونه موثر انجام دهید، قویتر می شوید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4877,'اگر کارهای کوچک را به خوبی انجام دهید، کارهای بزرگ خود به خود به جایی که باید برسند، می رسند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4878,'به خاطر بسپاریم که تنها راه تامین خوشبختی این نیست که متوقع حق شناسی از دیگران باشیم، بلکه خوبیهایی که به آنها می کنیم باید صرفاً به منظور تامین مسرت باطن خودمان باشد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4879,'چاپلوسی، هم گوینده و هم شنونده را تباه می کند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4880,'هرچیز را پیش از وقوع، دیدن و عاقبت اندیش بودن، کار خوبی است؛ اما برای مشکلات احتمالی آینده، از امروز اندوهگین شدن، شایسته نیست.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4881,'هر حرکتی که چیزی به وجود شما نیفزاید، چیزی از آن خواهد کاست و حرکتی که اثرش خنثی باشد وجود ندارد!((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4882,'از هر فرصتی استفاده کن؛ تمام زندگی، خود، فرصتی است. فردی که فراتر از همه می رود، معمولاً کسی است که جرأت بیشتری به خرج می دهد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4883,'مرور زمان به خودی خود بسیاری از نگرانی ها را از بین می برد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4884,'بیاموزید بی آنکه شما را آموزنده بدانند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4885,'هرگاه لیموترشی دارید بکوشید از آن لیمونادی بسازید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4886,'هیچ چیز جز خودت نمی تواند برایت آرامش بیاورد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4887,'تلاش منظم، پاداش چند برابر دارد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4888,'زندگی یعنی هر روز و هر ساعت را زیستن.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4889,'موفقیت در آرزوها، نسبت مستقیم با قدرت اراده ی ما دارد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4890,'کسانی که کار امروز را به فردا موکول می کنند، آنهایی هستند که کار فردا را هم به پس فردا موکول خواهند کرد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4891,'برای رسیدن به آرامش باید دری آهنین بر روی گذشته و آینده کشید و تنها به زمان حال اندیشید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4892,'از حق نشناسی دیگران نرنجید، چون ناسپاسی برای بشر یک امر طبیعی است. از این رو اگر ما به امید حق شناسی باشیم، بی جهت خود را به بیماریهای مختلفی دچار خواهیم کرد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4893,'فراموش نکنیم که حق شناسی نشانه ای از آموزش درست است. پس اگر از فرزندانمان توقع حق شناسی داریم، باید آنها را حق شناس بار بیاوریم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4894,'همان کاری را که ژنرال آیزنهاور می کند ما هم بکنیم؛ به این معنی که هرگز دقیقه ای از عمر خود را در اندیشه کسانی که دوستشان نمی داریم، هدر ندهیم.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4895,'مردم سه دسته اند : یا نوکر پول، یا رفیق پول و یا ارباب پول.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4896,'فرد چاپلوس هم گوینده را فاسد می کند و هم شنونده را.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4897,'تشخیص عیب و نقص ها خیلی بهتر از دیدن هنرهاست.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4898,'از دیگران تقلید نکن، خود را بشناس و آنچه هستی باش؛ بدان که در دنیا کسی مثل تو نیست.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4899,'بدبختی آدم از جهل نیست، از تنبلی است.(( دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4900,'نظم، اولین قانون طبیعت است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4901,'مشکلی که خوب بیان و موشکافی شده باشد، نیمی از آن حل شده است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4902,'عده بسیار کمی از مردم، سرزنش های مفید را به تعریفهای دروغین که از ایشان به عمل می آید، ترجیح می دهند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4903,'عزت و جلال مردان بزرگ از طرز رفتارشان با مردمان کوچک آشکار می شود.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4904,'شما با دلبسته شدن به دیگر مردم، می توانید طی دو ماه دوستان بیشتری بیابید، تا اینکه طی دو سال بکوشید مردم را وادار نمایید به شما دلبسته شوند.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4905,'بیایید آنقدر سرگرم نشویم یا با شتاب زندگی نکنیم که نتوانیم به موسیقی چمنزار یا آن سمفونی که جنگل را می ستاید، گوش فرا دهیم. برخی چیزها در جهان خیلی مهمتر از دارایی‌اند. یکی از آنها توانایی خوش بودن با چیزهای ساده است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4906,'اگر دو ماه شیفته‌ی دیگران باشید، دوستان بیشتری به دست خواهید آورد، تا اینکه دو سال بکوشید دیگران را شیفته‌ی خود کنید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4907,'اگر می خواهید پر شور و شوق باشید، مشتاقانه کار کنید.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4908,'به یاد داشته باشید که خوشبختی انسان به مقام یا دارایی او بستگی ندارد، خوشبختی، تنها به اندیشه ی او بستگی دارد.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4909,'حتماً کاری را انجام بدهید که از انجام آن، هراس و پروا دارید... این شتابنده ترین و امن ترین روشی است که تاکنون برای چیرگی بر ترس کشف شده است.((دیل کارنگی))',66,0,0,0,0,0);
INSERT INTO mysms VALUES(4910,'آزادی هیچ وقت انتظار ما را نمی کشد، ما باید در بدست آوردنش، از دل و جان دریغ نکنیم. چون در هیچ جایی از زوایای تاریخ سراغ نداریم که آزادی و دموکراسی، سهل و آسان به دست آمده باشد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4911,'خود را بزرگ شمردن نشانه بدبختی است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4912,'صاحبان اخلاق، روح جامعه ی خویش اند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4913,'هنگامی که اراده کنید، جهان هستی تبانی می‌کند تا آن اراده، به کردار درآید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4914,'اندیشه، شکوفه است؛ زبان، غنچه است و کردار، میوه‌ای‌ست که در پشت سر آنها قرار دارد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4915,'اجتماع، نام‌ها و آداب و رسوم را دوست دارد و از واقعیت‌ها و افراد آفریننده و خوداتکا بیزار است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4916,'انسان در هر شرایط و جایگاهی، نباید دنباله‌رو باشد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4917,'هیچ چیز به غیر از یکپارچگی ذهن نهایتاً مقدس نیست.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4918,'من از اندیشیدن به این نکته که ما چه آسان تسلیم نام‌ها و درجه‌های اجتماعی می‌شویم و خود را به آداب و رسوم آن می‌بازیم، شرمنده‌ام.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4919,'یافتن نیکویی‌ها در همه جا، معیار سلامت روانی است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4920,'یکی از زیباترین پاداش‌های زندگی این است که هیچ انسانی نمی‌تواند بدون اینکه به خودش کمک کند، خالصانه به دیگری کمک کند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4921,'ما سرانجام به آن جایی می‌رسیم که در همه‌ی روز، به آن می‌اندیشیم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4922,'هنگامی که کاری را آغاز می‌کنید، آن را با همه‌ی توان انجام دهید؛ همه‌ی‌ وجود خود را روی آن بگذارید؛ آن را با شخصیت خود درآمیزید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4923,'پرتلاش، پرانرژی و آرزومند باشید و ایمان داشته باشید تا به هدف خود برسید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4924,'هیچ هدف بزرگی بدون شیفتگی و دلبستگی به دست نمی‌آید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4925,'یکی از نیرومندترین موتورهای کامیابی، آرزومندی است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4926,'به آدم‌ها باور داشته باش تا با تو روراست باشند و با آنها بزرگوار باش تا بزرگی نشان دهند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4927,'اگر در جست‌و‌جوی زیبایی، جهان را هم سراسر درنوردیم، ولی حس آن را با خود نداشته باشیم، آن را نخواهیم یافت.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4928,'از درستی و راستی خود، همانند یک چیز مقدس نگاهبانی کنید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4929,'هر اندوهی، برانگیزاننده و اندرزی ارزشمند است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4930,'به خود باور داشته باش؛ به سخن دل ات توجه داشته باش.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4931,'آنچه را که ما نتیجه می دانیم، آغاز است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4932,'در برابر خورشید و ماه، بر انجام دادن کاری اراده می کنم که از درون، شادمان ام می کند و دل ام بر آن گواهی می دهد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4933,'خردمندان، همیشه همه چیز را نمی‌دانند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4934,'نشان همیشگی خرد، دیدن معجزه [=کاری شگفت‌انگیز] میان چیزهای مرسوم است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4935,'عقل، نیرویی است که دنیا را اداره می کند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4936,'هر چه ما را محدود می کند، نامش را می گذاریم بخت و اقبال.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4937,'وقتی انسان دوست واقعی دارد که خودش هم دوست واقعی باشد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4938,'از سلامتی خود مراقبت می کنیم، برای روز مبادا پول ذخیره می کنیم، سقف خانه را تعمیر می کنیم و لباس کافی می پوشیم، ولی کیست که در بند آن باشد که بهترین دارایی یعنی دوست را به گونه ای عاقلانه برای خود فراهم کند؟((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4939,'[ بگو ] با چه کسانی دوست و رفیقی، تا بگویم چگونه آدمی هستی.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4940,'دوست، کسی است که من می توانم با او صمیمی باشم و جلو او با صدای بلند فکر کنم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4941,'زیبایی در زن بدون وقار مانند تور ماهیگیری فرسوده و سوراخ سوراخ است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4942,'انسانی که عزم به تنها زیستن نموده است، باید از خانه و کاشانه خویش به همان نسبت دوری جوید که از جامعه. من به هنگام مطالعه و نوشتن، احساس تنهایی نمی کنم، هرچند که در اطرافم هیچ کس نباشد. اما اگر کسی می خواهد تنها باشد، ستارگان آسمان را به او نشان بده.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4943,'دانستن یعنی پی بردن به اینکه نمی توانیم بدانیم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4944,'سالها، چیزهای زیادی به ما می آموزد که روزها از آن بیخبر است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4945,'من در طبیعت وحشی، احساس ارزشمند و آشنایی را درک کرده ام که در خیابانها و دهکده ها هرگز به آن دست نیافته ام. در دشت های آرام، بویژه در مرزهای افق دوردست، انسان شاهد دل انگیزترین و باشکوه ترین زیبایی هایی است که با سرشت او همگون است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4946,'هیچ چیز برای همیشه به روندی نادرست تن در نمی دهد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4947,'رفتار و منش هر فرد، کتاب مصور اندیشه ها و باورهای اوست.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4948,'آنگاه که هوا کاملا تاریک است، می توانی ستاره ها را ببینی.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4949,'هر لحظه بزرگ و ماندنی در تاریخچه ی جهان در حقیقت ثمره ی پیروزی اشتیاق ها است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4950,'اگر اراده کنید و پایداری داشته باشید، بی شک موفق خواهید شد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4951,'بروید و به آنچه ایمان دارید، عمل کنید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4952,'جهان هستی به کسی که می داند به کجا می رود، راه نشان می دهد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4953,'مشورت هنری است که به کمک آن می توانید همه را به همکاری با خود وادارید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4954,'صدای آنچه انجام می دهید، به قدری بلند است که آنچه می گویید، شنیده نمی شود.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4955,'آنگاه که انسان در برابر خود می ایستد، به نظر می رسد همه چیز سد راه اوست.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4956,'هر انسانی آفریده اندیشه های روزانه خویش است. اگر دگرگونی در زندگی انسان روی دهد، باید از دل و جان آغاز شود تا اندیشه ها و احساسات، شخص را تحت تاثیر قرار دهد و دگرگون سازد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4957,'بیشترین تأثیر افراد خوب زمانی احساس می شود که از میان ما رفته باشند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4958,'هر انسانی را باید به دلیل خوبی هایش گرامی داشت.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4959,'تنها راه داشتن دوست این است که با او یکی شوید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4960,'خرد مانند الکتریسیته است. هیچ فردی نیست که همیشه خردمند باشد، ولی افراد سزاوار خردمندی، اگر در مسیر درست قرار بگیرند، اندک زمانی خردمند می‌شوند؛ مانند شیشه، که اگر آن را مالش دهی، مدتی کوتاه به نیروی الکتریسیته مجهز می‌شود.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4961,'توانگر راستین کسی است که مالک روزش باشد. هیچ پادشاهی، هیچ انسان پولداری و هیچ پری و دیوی، دارای چنین نیرویی نیستند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4962,'زندگی هر کسی افکاری است که او را در بر گرفته است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4963,'چرا باید احساس شخصی من، بر پایه‌ی اندیشه‌های دیگران ساخته شود؟((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4964,'بدون شور و اشتیاق هرگز کار بزرگی صورت خارجی پیدا نکرده است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4965,'چه آسان می توان مطابق عقاید دیگران زندگی کرد، چه آسان می توان در خلوت و انزوا مطابق نظرات خود رفتار نمود، اما بزرگ آن کسی است که در میان مردم، استقلال رای خود را در عین صمیمیت و مهربانی حفظ کند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4966,'خاموش باشیم، زیرا در آن زمان است که صدای نجوای خدا را خواهیم شنید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4967,'بزرگترین موفقیت عبارت است از اعتماد یا سازش کامل بین اشخاص صمیمی.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4968,'آن که در آرزوی دیدن زیبایی های جهان هستی عزم سفر کند، زمانی به گمشده خویش دست خواهد یافت که آن را در بطن خود پرورده باشد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4969,'اگرچه جنگ برای اصلاح حال ملت و افزایش آبادانی آغاز می گردد، اما حقیقت این است که در طی جنگ، بحران عظیمی در حیات اجتماعی و سیاسی پدیدار می شود.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4970,'اگر زنجیری به گردن دیگری بیندازید، سر دیگر آن به گردن خودتان گره می خورد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4971,'اگر هرکس با فکر خود و بدون نفوذ خارجی برای رسیدن به هدفش کوشش کند، سرانجام، طبیعت تسلیم او شده و دنیا به او روی می آورد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4972,'اولین ناآگاهی [ در مورد ] سیگار این است که هنگام کشیدن آن در عین بیکاری تصور می کنیم کاری انجام می دهیم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4973,'تمدن، نتیجه نفوذ زنان پارسا است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4974,'زندگی انسان یعنی اندیشه روزانه او.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4975,'کسی را که هیچ حقی ندارد، زودتر از کسی که نیمی از حق به جانب اوست، می توان قانع کرد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4976,'گلها تبسم زمین هستند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4977,'ما مالک اسباب مادی نمی شویم، بلکه بنده ی آن می گردیم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4978,'کسی که از تمام کمک های خارجی چشم بپوشد و آن را دور اندازد و شخصأ وارد میدان شود و به نیروی خود برپا ایستد، کسی است که نیرومندی و موفقیت را برایش پیش بینی می کنم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4979,'وقتی که پیر می شویم، صورت زیبا به سیرت زیبا می پیوندد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4980,'هر انقلابی ابتدا جرقه ای از اندیشه های یک انسان بوده است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4981,'هرکسی را می بینم از یک جهت بر من برتری دارد؛ از این جهت که من از هرکس پندی می گیرم و چیزی می آموزم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4982,'هر شیرینی به تلخی و هر تلخی به شیرینی آمیخته است و هر زشتی از زیبایی نیز بهره دارد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4983,'آنچه را که به دنبالش هستیم خواهیم یافت و آنچه را که از آن می گریزیم از ما خواهد گریخت.......حال که می دانیم به خواسته هایمان می رسیم شایسته است که اهداف و آرزوهای ارزشمند را طلب کنیم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4984,'باور خود را در عملکردتان پیاده کنید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4985,'اقبال، نام دیگر پایداری در هدف است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4986,'مردان بزرگ کسانی هستند که می دانند اندیشه های بزرگ بر جهان حکم می رانند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4987,'وقتی فرد در فشار درد و رنج باشد و مانند افراد شکست خورده به نظر برسد، تازه فرصت پیدا می کند چیزی یاد بگیرد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4988,'در قانون هستی، شادی، آرامش و آزادی زمانی تحقق می یابند که آنها را ببخشیم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4989,'چیزی ساده تر از بزرگی نیست؛ آری ساده بودن همانا بزرگ بودن است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4990,'انسان های بزرگ آنهایی هستند که روان، قوی تری از هر نیروی مادی می دانند و بر این باورند که انگار بر حکومت دارند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4991,'به ازاء هر دقیقه عصبانیت، شصت ثانیه شادی را از دست می دهید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4992,'شادی را گسترش دهید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4993,'زینت خانه به دوستانی است که در آنجا تردد می کنند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4994,'یگانه راه دوست پیدا کردن این است که اول خودمان اظهار دوستی کنیم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4995,'مقیاس ارزش انسان اهمیتی است که به وقت خود می دهد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4996,'به آدمها اعتماد کن تا با تو صادق باشند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4997,'یک انسان متعالی به اندازه ای که بزرگ می اندیشد، بهتر نیز زندگی می کند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4998,'یک دوست شاد، همچون شمعی در محفل یاران است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(4999,'کسی که نتواند بر ترس غلبه کند، هنوز اولین درس زندگی را نیاموخته است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5000,'پاداش کاری که خوب انجام گرفته همان انجام آن کار است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5001,'تار و پود روح مادر را از مهربانی بافته اند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5002,'برده فقط یک آقا دارد، اما شخص طمعکار نسبت به هر کسی که او را یاری کند، برده است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5003,'توان با هم خندیدن، لازمه‌ی عشق است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5004,'عشق بورز، به تو عشق خواهند ورزید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5005,'عشق مانند دو بخش یک فرمول جبری، تعادل ریاضی دارد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5006,'یکی ار خوبی‌های دوستی این است که شما می‌توانید نزد دوستانتان، نادان باشید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5007,'باید در کارها وجدان خود را راهنما قرار دهیم و همان [ راهی ] را که وجدان به ما نشان می دهد، در پیش بگیریم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5008,'نوابغ بزرگ زندگینامه بسیار کوتاهی دارند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5009,'هدف خود را باید روی واقعیت ها بنا سازیم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5010,'چیزی را که ما در باطن خود نداریم در بیرون هم نمی توانیم ببینیم. اگر در نفس شما بزرگی هست، آن را در فرد عامی هم پیدا خواهید کرد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5011,'دوستان، زینت بخش خانه‌ی ما هستند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5012,'فردا، غریبه‌ای، استادانه، درست آنچه را در همه‌ی زندگی به آن اندیشیده‌ایم و احساس کرده‌ایم، خواهد گفت.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5013,'ما همه نماد بوده و در دل نمادها ساکن هستیم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5014,'انسانهای بزرگ کسانی هستند که دریافته‌اند نیروی معنوی بسیار تواناتر از نیروی مادی است و اندیشه و خرد است که بر دنیا حکم می‌راند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5015,'یک ستایش کوچک، نتایجی عالی در پی دارد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5016,'برخی از دیدگاهها، ما را جوان می‌بینند و جوان نگاه می‌دارند. چنین دیدگاههایی، عشق‌های جهانی و زیبایی‌های جاودانه‌اند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5017,'هر اندازه که دنیا را برای یافتن زیبایی زیر پا بگذاریم، باز هم باید آن را با خود ببریم، وگرنه زیبایی را نخواهیم یافت.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5018,'یک دوست می تواند به راستی، شاهکار طبیعت به شمار آید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5019,'شکوه دوستی در دستان دراز شده به سوی هم، لبخند با محبت و شادی همنشینی نیست، بلکه الهامی روحانی است که هر زمان فرد کشف می کند شخصی دیگر باورش دارد و می‌خواهد به او باور داشته باشد، به او دست می‌دهد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5020,'طبیعت، پرده‌ای بسیار نازک است، زیرا حضور مطلق شکوه و بزرگی خداوندی از میان همه چیز پدیدار می شود.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5021,'به خانه‌ی دوست خود زیاد رفت و آمد کن، چرا که اگر به او سر نزنی، علف‌های هرز، مسیر روبروی خانه‌ی او را خواهند بست.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5022,'زمانی که برای نخستین بار چشمانم را به روی چمنزار های صبحگاهی باز کردم و به دنیای زیبا نگریستم، برای زنده بودن از خداوند سپاسگزاری کردم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5023,'با طبیعت همگام باشید، زیرا راز او بردباری و شکیبایی است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5024,'برای ذهنی کدر و تاریک، طبیعت اندوهگین است. برای ذهنی روشن، کل دنیا در حال درخشیدن و سرشار از نور است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5025,'آسمان؛ خوراک روزانه‌ی چشمان است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5026,'آفرینش هزار جنگل، در یک میوه‌ی درخت بلوط آشکار است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5027,'طبیعت، سرشار از قدرت پروردگاری است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5028,'زمین در گلها می‌خندد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5029,'علف هرز چیست؟ گیاهی که نیکویی‌های آن هنوز کشف نشده است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5030,'بر روی قلب خود بنویسید که هر روز، بهترین روز سال است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5031,'نیرویی که در انسان نهفته است، طبیعتی تازه دارد و هیچ کس جز او نمی داند که چه می تواند انجام دهد و خودش هم تا زمانی که آن را نیازماید از آن آگاهی ندارد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5032,'جان را به هیجان آورید، آنگاه آب و هوا و دهکده و شرایط شما در دنیا همگی ناپدید می شوند، دنیا استواری خود را از دست می دهد، هیچ نمی ماند جز جان و وجود پاک خداوندی که در آن زندگی می کند.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5033,'هیچ چیز زیبا‌تر از شاد کردن و پرهیز از آزار دیگران نیست.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5034,'ثبات نابخردانه، زاییده ی مغزهای کوچک است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5035,'شادی، عطری است که نمی توانی به دیگران بزنی، مگر آنکه قطره هایی از آن را به خودت بزنی.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5036,'مردان کم مایه به شانس باور دارند، مردان بااراده و خردمند، به علت و معلول.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5037,'به خودتان باور داشته باشید؛ به گفته ی دیگران اهمیت ندهید.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5038,'ترس، همواره از نادانی زاده می شود.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5039,'هیچ دانشی وجود ندارد که با قدرتی همراه نباشد.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5040,'کارهایی را انجام بده که از آنها می ترسی... در این صورت، مرگ ترس را حتمی بدان. ابتدا از پرتگاه به پایین بپر تا در بین راه، بال پرواز خود را بسازی.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5041,'درک زندگی به اندازه ی یک نفس، بسیار آسوده تر است، زیرا شما زندگی می کنید و این کار، نتیجه ی کامیابی ها است.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5042,'ما تنها همان چیزی را می‌توانیم ببینیم که هستیم.((رالف والدو امرسون))',67,0,0,0,0,0);
INSERT INTO mysms VALUES(5043,'شکست باید انرژی خفته را بیدار کند.((رومن رولان))',68,0,0,0,0,0);
INSERT INTO mysms VALUES(5044,'دوستی که شما را درک می کند، شما را می سازد.((رومن رولان))',68,0,0,0,0,0);
INSERT INTO mysms VALUES(5045,'مرد با اصالت اگر خوار بشود پست و فرومایه نمی شود.((رومن رولان))',68,0,0,0,0,0);
INSERT INTO mysms VALUES(5046,'اگر هنر و حقیقت نمی توانند با هم زنده بمانند، بگذار هنر بمیرد.((رومن رولان))',68,0,0,0,0,0);
INSERT INTO mysms VALUES(5047,'هیچکس حق ندارد وظایف خود را فدای تمایل دل خویش کند، اما دست کم باید این حق را به دل داد که هنگام عمل به وظایف خویش خشنود نباشد.((رومن رولان))',68,0,0,0,0,0);
INSERT INTO mysms VALUES(5048,'پس از ازدواج، دیگر عشق نیست؛ تنها زندگی است.((رومن رولان))',68,0,0,0,0,0);
INSERT INTO mysms VALUES(5049,'فقر و بیماری دردناکترین چیز نیست، بلکه از همه بدتر بیرحمی مردم نسبت به یکدیگر است.((رومن رولان))',68,0,0,0,0,0);
INSERT INTO mysms VALUES(5050,'ما بسیار تواناتر از آنیم که می پنداریم. لحظه هایی هست که در آنها تنها با دست نزدن به هیچ ابتکاری و انجام ندادن هیچ کاری، می توانیم بیاموزیم، زیرا در این زمانهای خاموشی بخش نهان هستی ما به کار افتاده و پویا می گردد.((رومن رولان))',68,0,0,0,0,0);
INSERT INTO mysms VALUES(5051,'هیچ رنجی بالاتر از رنج کودکی نیست که برای اولین بار طعم ستم و سنگدلی را چشیده باشد.((رومن رولان))',68,0,0,0,0,0);
INSERT INTO mysms VALUES(5052,'مردان آفریننده کارهای مهم اند و زنان به وجود آورنده مردان.((رومن رولان))',68,0,0,0,0,0);
INSERT INTO mysms VALUES(5053,'دیر یا زود، کسانی برنده می‌شوند که می‌پندارند می‌توانند برنده شوند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5054,'برای اینکه به سرعت اندیشه پرواز کنید و هر جایی که می‌خواهید، باشید، نخست باید به گونه‌ای آغاز کنید که گویی به آنجا وارد شده‌اید.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5055,'هر دشواری که در زندگی شماست، در نهان، هدیه‌ای برای شما دارد.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5056,'آنچه شما را به خانواده‌تان پیوند می‌دهد، رابطه‌ی خونی‌تان نیست، بلکه ارج و لذتی است که در زندگی با یکدیگر احساس می‌کنید.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5057,'برای بسیاری از مرغان، تنها خوردن غذا مهم است و پرواز اهمیتی ندارد.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5058,'اکنون دلیلی برای زیستن داریم، زیستن برای آموختن، برای اکتشاف و برای رهایی!((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5059,'بهشت، یک مکان نیست و یک زمان هم نیست؛ بهشت یعنی کامل شدن.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5060,'همیشه عملی است زمانی که می فهمی چه می کنی.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5061,'ما آزادیم به هر کجا که مایلیم برویم و آنچه که هستیم باشیم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5062,'ما می توانیم خود را از اسارت نادانی آزاد کنیم. می توانیم خود را به عنوان آفریده هایی با شعور و با فضیلت و دارای مهارت بازشناسیم. می توانیم رهایی یابیم! می توانیم پرواز را بیاموزیم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5063,'تنها قانون درست آن است که به آزادی منتهی شود؛ قانون دیگری وجود ندارد.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5064,'چرا دشوارترین کار در جهان این است که به پرنده ای این باور را بدهی که آزاد است؟((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5065,'تو نیاز داری که به شناختن خود ادامه دهی؛ هر روز کمی بیشتر از روز پیش.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5066,'آنچه که دیدگانت به تو می گویند باور نکن. همه ی آنچه که می توانی ببینی محدود است. با ادراک خود بنگر، آنچه را که آموخته ای بشناس.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5067,'من افتخار نمی خواهم؛ آرزوی رهبری ندارم؛ فقط می خواهم آنچه را یافته ام با دیگران در میان بگذارم و افق های آینده را که به همه ی ما تعلق دارد به آنها نشان دهم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5068,'یگانه راه دوری از گزینش های هراس آور، ترک مردمان و گوشه نشینی است که خود، گزینشی هراس آور است.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5069,'دگرگونی ناچیز امروز به فردایی سراسر دیگرگون راه می گشاید. به آنان که در زندگی راههایی دشوار و والا پی می گیرند، پاداشی برتر ارزانی خواهد شد، هر چند پس از گذشت سالها.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5070,'هر چه اوج می گیریم، چشم اندازمان گسترده تر می شود و گزینش ها و دوراهی ها و میان برها را بهتر می بینیم و هر چه فرود بیاییم، چشم اندازمان را از دست می دهیم و هنگام فرود، درک و دریافتمان از گزینش های دیگر بر باد می رود. حواسمان پی جزئیات می رود: جزئیات روزمره ی ساعت به ساعت و دقیقه به دقیقه و به این ترتیب، زندگی های همتا را فراموش می کنیم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5071,'از خداحافظی کردن نترسید؛ یک خداحافظی، پیش از آنکه بتوانید دوباره یکدیگر را ببینید لازم است و دیدار دوباره، پس از دقایق دوره های زندگی، مسلماً برای کسانی است که دوست هستند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5072,'نشانه ی نادانی شما، ژرفای باور شما به نبودن دادگری و تراژدی است. آنچه کرمِ صدپا زندگی می نامد، استاد به آن پروانه می گوید.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5073,'هر آنچه که یک استاد می گوید، واژه هایی زیبا هستند که نمی توانند او را رهایی بخشند؛ آنهم از نخستین حمله ی یک سگ هار در یک چراگاه.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5074,'مردن مانند شیرجه زدن در یک دریاچه ی ژرف در یک روز گرم است. در این شیرجه، شوكِ آن تغییر سرد و برنده وجود دارد، درد آن یک ثانیه است و سپس، پذیرش شناوری در حقیقت است.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5075,'در درون هر یک از ما توان سازگاری با تندرستی و بیماری، توانگری و فقر، آزادی و بردگی نهفته است. این ما هستیم که آنها را در سلطه داریم نه دیگران.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5076,'زندگی من یک زندگی آزاد است، اما برخی اوقات احساس تنهایی در آن رشد می کند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5077,'می خواهم باور کنم که در سطحی دیگر، اصلی دیگر هم هست.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5078,'ما برای پرواز به هواپیما نیاز نداریم. لازم نیست از دیوار بگذریم و یا به سیاره های دیگر سفر کنیم. اگر بخواهیم می توانیم بیاموزیم که چگونه بدون بهره گیری از ماشین ها به هرجا می خواهیم برویم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5079,'تنها آموزه ی مهم این است که در راه خود گام برمی دارم و آنچه می خواهم انجام می دهم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5080,'کودکان، مشتاقند و بزرگسالان، هوشیار.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5081,'مسلماً می توان خود را کنار کشید! چنانچه دیدگاهت درباره ی چگونگی اجرای هر کاری که دوست داری تغییر کند، می توانی آن کار را رها کنی.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5082,'راهی را که شادمانه به پایان می رسانی، به طور رایج، افراد ناآگاه درک نمی کنند و به همین دلیل تو را دیوانه خواهند خواند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5083,'دانش، پی بردن به آن چیزی است که از پیش می دانستی؛ انجام دادن، نشان دادن آن چیزی است که می دانی؛ آموختن، یادآوری به دیگران است که خودِ آنها به همان حد خوبی که تو می دانی، می دانند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5084,'آسانترین پرسش ها، ژرفترین آن است. کجا به دنیا آمده ای؟ خانه ات کجاست؟ به کجا می روی؟ چه کار می کنی؟ گاهی در این باره بیندیش و ببین که پاسخها تغییر می کنند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5085,'آنچه را که بیش از هر چیز به یادگیریش نیازمندی بهتر تدریس می کنی.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5086,'زندگی کن؛ هرگز از این موضوع که در مورد آنچه می کنی و یا می گویی، در سراسر جهان مطالبی انتشار یابد، شرمنده نباش؛ حتی اگر آنچه انتشار یافته حقیقت نداشته باشد.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5087,'دوستانت در نخستین لحظه ی دیدار، تو را خواهند شناخت؛ بهتر از آشنایانت که تو را در طی هزار سال بشناسند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5088,'بهترین راه فرار از مسئولیت این است که بگویی، من مسئولیت هایی دارم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5089,'از آینده های امکان پذیر روی نگردان، مگر آنکه مطمئن شوی هیچ چیزی برای یادگیری از آنها نداری.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5090,'همیشه مختاری که باورت را تغییر دهی و آینده ای متفاوت را برگزینی.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5091,'چیزی به نام یک مشکل وجود ندارد که در دستهایش برایت هدیه ای نداشته باشد.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5092,'انسان به تنها بودن عادت می کند، اما تنها برای یک روز آن را می شکند و سپس دوباره به آن عادت می کند، دوباره از نو.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5093,'جایی که اندیشه ی تو هست، تجربه ی تو نیز هست؛ انسان همان گونه است که می اندیشد؛ از هرچه که می ترسم به سرم می آید.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5094,'از جمعیت به اندازه ی کافی کشیده ام، حتی زمانی که شادمان هستند... و اگر بترسند، شخصی را به دار می آویزند و یا او را پرستش می کنند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5095,'حلقه ای که خانواده ی حقیقی تو را بهم می پیوندد، هم خونی نیست، بلکه احترام و شادمانی است که نسبت به زندگی یکدیگر دارند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5096,'حقیقتی را که بازگو می کنی، نه گذشته ای دارد و نه آینده ای، آن هست و این تمام چیزی است که برای بودن نیاز دارد.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5097,'آنچه را که شعبده باز می داند بیاموز، آنگاه دیگر شعبده ای نخواهد بود.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5098,'در مورد جمعیت مردم نگران نباش؛ تا زمانی که نخواهی نمی توانند تو را لمس کنند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5099,'کمی بزرگنمایی در همه ی ما هست، وگرنه هدفی برای استاد شدن نداشتیم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5100,'نمی توانم بدون آموختن هر آنچه در طی دوره ی زندگیم برای آموختن آن آمده ام، بروم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5101,'وقتی که می دانی هیچ چیز در این سیاره نمی تواند مزاحمتی برایت ایجاد کند... دیگر ماجراجویی وجود ندارد.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5102,'اگر شادی تو در گرو چیزی است که دیگری دارد، حدس می زنم که تو نیز یک مشکل داری.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5103,'برای محدودیتهای خود استدلال بیاور و مسلماً آنها از آنِ تو هستند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5104,'شاگردان، چیزهای ساده را دشوار می کنند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5105,'اگر به راستی می خواهی یک ابر را از زندگیت بزدایی، نباید یک توده ی بزرگ از آن بسازی؛ فقط آسوده باش و آن را از اندیشه ات پاک کن؛ این تنها کاری است که باید انجام داد.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5106,'هرگز آرزویی به تو داده نشده، مگر آنکه توانایی به حقیقت درآوردن آن هم داده شده باشد. هر چند باید برای رسیدن به آن کار کنی.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5107,'زمانی که پرسش هایت را به خوبی بیان کنی، خودشان پاسخ خود هستند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5108,'اگر برای مدتی تمرین کنید که تخیلی باشید، خواهید دانست که شخصیت های تخیلی گاهی وقتها از مردمی با بدنها و ضربان قلب، حقیقی تر هستند.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5109,'وجدان شما، ابزار سنجش درست شما از خودخواهی تان است؛ با دقت به آن گوش دهید.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5110,'خود ما برمی گزینیم که صدمه بزنیم و یا صدمه ببینیم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5111,'همه ی ما آزادیم هر آنچه که می خواهیم انجام دهیم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5112,'هر شخصی، همه ی پیشامدهای زندگی شما اینجا هستند، زیرا شما آنها را به اینجا کشانده اید.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5113,'تنها همان که هستی، باقی بمان؛ آرام و شفاف و درخشان.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5114,'من وجود ندارم که جهان را تحت تاثیر قرار دهم، وجود دارم که زندگیم را به شیوه ای بگذرانم که مرا شادمان سازد.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5115,'نود درصد از مخاطبین خود را از دست می دهی، مگر آنکه یاد بگیری سخنت را کوتاه نگاه داری.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5116,'کسی نمی تواند ما را از آنچه که مایل به انجامش هستیم بازدارد.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5117,'همه ی ما در این جهان، فریبکار هستیم، همه ی ما وانمود به بودن چیزی می کنیم که نیستیم.((ریچارد باخ))',69,0,0,0,0,0);
INSERT INTO mysms VALUES(5118,'این که دیگران اندیشه ات را می پسندند به این معنی نیست که بدنت را هم از آن خود کنند.((ریچارد براتیگان))',70,0,0,0,0,0);
INSERT INTO mysms VALUES(5119,'خیلی خوبه که صبح ها تنهای تنها از خواب بیدار شی و مجبور نباشی به یه عده بگی که دوست شون داری، در حالی که دیگه دوست شون هم نداری.((ریچارد براتیگان))',70,0,0,0,0,0);
INSERT INTO mysms VALUES(5120,'تا زمانی که در خوابی برای شاد کردنت کاری از من ساخته نیست.((ریچارد براتیگان))',70,0,0,0,0,0);
INSERT INTO mysms VALUES(5121,'اندیشه ای که هم اکنون در سر دارم مفت سگ هم نمی ارزد، چون کاملاً به هم ریخته ام.((ریچارد براتیگان))',70,0,0,0,0,0);
INSERT INTO mysms VALUES(5122,'هنگامی که رویاها می روند، زندگی از دست رفته است.((ریچارد براتیگان))',70,0,0,0,0,0);
INSERT INTO mysms VALUES(5123,'اگر بترسی، تنها می شوی؛ کارهای زیادی انجام می دهی که هیچ کدام از وجود تو هستی نگرفته است.((ریچارد براتیگان))',70,0,0,0,0,0);
INSERT INTO mysms VALUES(5124,'من شعر گفتن را بی نهایت دوست دارم، اما مانند یک رابطه ی عاطفی پر دردسر که در پایان به یک ازدواج خوب می انجامد، من و شعر هم بس زمان بُرد تا همدیگر را شناختیم.((ریچارد براتیگان))',70,0,0,0,0,0);
INSERT INTO mysms VALUES(5125,'من هفت سال شعر گفتم تا یاد بگیرم چگونه یک جمله ی درست بنویسم.((ریچارد براتیگان))',70,0,0,0,0,0);
INSERT INTO mysms VALUES(5126,'چهره ای که سر آمده از ته مانده های دیگر چهره هاست، به آینه ای نیاز دارد؛ از تکه های به هم آمده ی دیگر آینه های شکسته.((ریچارد براتیگان))',70,0,0,0,0,0);
INSERT INTO mysms VALUES(5127,'سرانجام، جایی دارد باران می بارد و برنامه ی گلها را تنظیم می کند و حلزونها را شاد نگه می دارد؛ تمام چیزی که ارزش اندیشیدن را دارد این است.((ریچارد براتیگان))',70,0,0,0,0,0);
INSERT INTO mysms VALUES(5128,'هرگز پول قرض ندهید، مگر آنکه آماده باشید از پس گرفتن آن چشم پوشی کنید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5129,'اگر چیزی برایتان ارزشمند است، در وهله ی نخست، آن را قرض ندهید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5130,'شما نمی توانید یک بچه ی بد را دگرگون کنید، ولی رفتار بد را می توانید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5131,'در کنار کسانی که دوست شان دارید، شاد و سرحال باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5132,'شما وظیفه دارید در برابر پدر و مادر خود، با ادب، سنجیده، شکیبا و دارای روحیه ی همکاری باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5133,'بخشنده بودن به این معنا [ =چَم ] نیست که به هر فرمانی گردن بنهیم.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5134,'به یاد داشته باشید که اگر کسی به شما لگد زد، شاید پیش از برخورد با شما، ماجرای بدی را از سر گذرانده باشد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5135,'بکوشید همواره بهترین باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5136,'اگر همه ی ما این سختی را به خود بدهیم که گاهی به غریبه ها لبخند بزنیم، جهان می تواند روزهای خود را بر پایه ی زد و خوردهای کمتری آغاز کند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5137,'اگر خواستار یک اجتماع هستید، باید بخشی از آن باشید و از آن جانبداری کنید؛ وگرنه راکد می شود و می میرد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5138,'پرسش ها به مردم کمک می کنند تا اندیشه ی خود را روشن سازند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5139,'نشست و برخاست با کسانی که حس خوبی به شما نمی دهند، بی ثمر است، مگر آنکه دلتان بخواهد افسرده شوید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5140,'برای نفس دانستنی ها به گردآوری آنها نپردازید، بلکه ببینید با آنها چه کار می توانید بکنید که ارزشمند باشد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5141,'شما هرگز نمی دانید چه الهام هایی ممکن است به دیگران بدهید، چه شعله هایی را ممکن است روشن کنید و بدون آنکه خود بدانید، چه دلگرمی هایی می توانید بدهید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5142,'کامرواترین افراد در زندگی، کسانی هستند که همواره در پی یاد دادن دانسته های خود و آوردن دیگران به دنبال خویشند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5143,'در گستره ی اخلاق، دست بالا را داشته باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5144,'کین خواهی، ویژه ی بازندگان است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5145,'اگر برنامه نداشته باشید، در هر جایی می توانید نقطه ی پایان بگذارید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5146,'کار خود را مهم بدانید و برای انجام دادن هرچه بهتر آن با همه ی توان بکوشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5147,'همواره در پی یافتن راههایی برای افزایش سهم همه باشید، نه فقط خودتان.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5148,'پابرجا و استوار باشید، بی آنکه مهاجم باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5149,'در پی کسب سرافرازی باشید، نه تباهی.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5150,'بخشی از راه حل باشید، نه مسأله.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5151,'اگر می خواهید در زندگی، احساس خوبی داشته باشید، خوش و کامروا باشید و زندگی تان معنایی [ =چَمی ] داشته باشد، باید چیزی بر جای گذارید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5152,'هر یک از ما به تنهایی، می توانیم کار کوچکی انجام دهیم، ولی اگر گردهم آییم، می توانیم دستاورد بزرگی داشته باشیم.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5153,'نمی توانیم به این امید باشیم که همه چیز، به همان اندازه که ما دوست داریم، سبز باشد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5154,'در هر زمینه ای سخاوتمند باشید، حتی در سخاوتمندی تان.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5155,'همه ی ما گاهی شکست می خوریم و هیچ کس کامل نیست.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5156,'شما بزرگتر می شوید، ولی ناگزیر، خردمندتر نمی شوید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5157,'خردمند بودن به معنای [ =چَم ] گناه نکردن نیست، بلکه به چم یادگیری رهایی از چنگال پیامدهای آن با پاس داشتن شرافت و تندرستی است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5158,'اگر بخواهید، می توانید هر گونه احساس بیزاری، افسوس و خشم را از خود دور کنید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5159,'آنچه پیش آمده، پیش آمده و شما نیاز به آن دارید که با شرایط پیش آمده، پیش بروید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5160,'شما ناگزیر نیستید چیزی را دگرگون کنید یا بهبود بخشید یا برای رسیدن به کمال تلاش کنید. کاملاً به عکس؛ تنها، [آنچه را که هستید] بپذیرید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5161,'بپذیرید که انسان هستید و برخی از زمانها، شکست می خورید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5162,'بر خود، خرده مگیرید که روزگارتان را دشوار خواهد کرد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5163,'درباره ی کارهای مهمی که انجام می دهید، بیندیشید و این گونه کارها را بیشتر انجام دهید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5164,'برای آنکه بدانید چه چیز اهمیت دارد و چه چیز بی اهمیت است، باید بدانید زندگی خود را وقف چه کاری می کنید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5165,'در اندیشه ی خود، انعطاف پذیر باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5166,'گاهی بهتر است کار را صرفاً برای لذتی که از آن می بریم، انجام دهیم.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5167,'اندیشیدن، همواره برای هر کس و درباره ی هر چیز خوب است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5168,'تلاش کنید، زندگی را نه مانند دشمن، بلکه در قالب یک دوست و رقیب تمرینی ببینید؛ اگر انعطاف پذیر باشید، تفریح خواهید کرد و اگر در زمین خود بایستید، ظرف مدت کوتاهی، ضربه فنی خواهید شد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5169,'با شریک زندگی تان، بهتر از بهترین دوستانتان رفتار کنید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5170,'خوشبختی، پایان یک طیف است که بدبختی، سر دیگر آن است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5171,'همواره باید با هدف دستیابی به چیزی بزرگتر، به پیش بروید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5172,'اگر می خواهید دوست باشید، یک دوست خوب باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5173,'در قبال پدر و مادر خود، کمی احترام و بخشندگی داشته باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5174,'شما می توانید بالاتر از پرورش دهندگان خود قرار بگیرید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5175,'باید زندگی را مانند زنجیره ای از ماجراها دید. هر ماجرایی، فرصتی برای آسودگی خاطر، یادگیری، کشف جهان، افزایش دایره ی تجربه و دوستان و گسترش بخشیدن به افق های دید است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5176,'بازیگران کامیاب [زندگی]، در جزئیات زندگی شخصی خود غرق نمی شوند و در یک حباب کوچک، در بند نمی مانند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5177,'شادترین، ترازمندترین و کامیاب ترین افراد در زندگی، آنهایی هستند که بخشی از چیزی هستند؛ بخشی از جهان، نه بریده از آن.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5178,'جالب ترین و برانگیزاننده ترین افراد، آنهایی هستند که به آنچه در پیرامون شان رخ می دهد، توجه زیادی دارند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5179,'اگر همه چیز آسان بود، ما نیرومند نمی شدیم.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5180,'بکوشید هر عقب نشینی را زمانی برای پیشروی ببینید. این عقب نشینی ها، شما را نیرومندتر می کنند، نه سست تر.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5181,'همان اندازه بار بردارید که می توانید به دوش بکشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5182,'زندگی، همین است و قرار نیست چیزی جز این باشد؛ زنجیره ای از نبردها و آسودگی ها.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5183,'آدمهای آرام، قابل اعتمادند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5184,'با هرچه که از آن می ترسید، روبرو شوید و آن را شکست دهید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5185,'اگر بیش از اندازه بیندیشی، هیچ گاه به پیش نخواهی رفت.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5186,'هنگامی که خود را به یک جریان، مسیر یا نقشه پایبند می کنید، آن را به تمامی دنبال کنید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5187,'افراد کامیاب در زندگی، کسانی هستند که عصاره ی آخرین قطره های رضایت و انرژی را از زندگی، بیرون می کشند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5188,'اگر زمان را در مبارزه برای اموری هدر دهید که آشکارا تغییر ناپذیرند، زمان، با شتاب برق از کنارتان می گذرد و آن را از دست می دهید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5189,'خود را وقف چیزهایی کنید که می توانید آنها را دگرگون سازید؛ پهنه هایی که بودن شما می تواند تفاوت ایجاد کند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5190,'هدف، بهترین بودن در هر زمینه ای است که می توانید، نه دوم بودن.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5191,'گمان نکنید که با رویاپردازی های خود هرگز به جایی نخواهید رسید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5192,'شماری از کامیاب ترین افراد، کسانی بوده اند که جسارت رویاپروری های دور و دراز را داشته اند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5193,'اگر بخواهید در زندگی خود کامیاب باشید، باید توجه خود را به آنچه هم اکنون برایتان پیش آمده، معطوف کنید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5194,'تنها راه زندگی کردن، بودن در همین زمان است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5195,'تلاش کنید گذشته را اتاقی ببینید که با آنچه اکنون در آن زندگی می کنید، تفاوت دارد. می توانید به آن سر بزنید، ولی دیگر نمی توانید در آن زندگی کنید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5196,'این جا، اکنون و در این زمان زندگی کنید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5197,'رویاها باشکوه اند، ولی واقعیت هم خوب است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5198,'اگر می خواهید زندگی تان کامروا، شاد، سرشار، معنادار و لبریز از ماجرا باشد، به سادگی با آن همراه شوید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5199,'باید با زندگی همراه شوید، ولی اگر هدف و برنامه ای نداشته باشید، به آسانی به گِل می نشینید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5200,'اگر می خواهید بهترین ثمره را از زندگی بگیرید، باید کمی بیندیشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5201,'اگر به هر روز، چنان که گویی روز مهمی است، درود بگویید، بر سرافرازی، احترام [ =بزرگ داشتن ] و خودباوری شما تاثیری شگرف بر جای خواهد گذاشت.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5202,'اگر بگذارید چیزهای دیرینه، دوباره در زندگی، سر برآورند، با شتاب در جریانهای آبی پایین دست غرق خواهید شد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5203,'اگر برای هدفهای خود برنامه نداشته باشید، صرفاً یک رویا باقی خواهند ماند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5204,'باور کنید، هر کاری که انجام می دهید و به هر صورتی که این کار را می کنید، به میزان زیادی به خودتان باز خواهد گشت.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5205,'زندگی برای زیستن است و لذت بردن از تابش آفتاب و سایر چیزهای بزرگ، نه گرفتار شدن در پریشانی هولناک، به این دلیل که چند تخم مرغ در سوپر مارکت از دستمان به زمین افتاده است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5206,'یاد بگیرید که وجه خنده دار هر چیزی را ببینید. این کار، بهترین شیوه برای رها شدن فوری از چنگ تنش و از بین رفتن پریشانی و تردید است؛ آن را بیازمایید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5207,'شما می توانید آدم بامزه ای باشید، ولی از خودتان یک دلقک نسازید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5208,'وقار به معنای [ =به چَم ] نشان دادن ارج نهادن به خود و داشتن خودباوری است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5209,'بهتر است بپذیرید که همیشه چیزهایی وجود دارند که غیر قابل درک اند و آنها را رها کنید؛ چقدر آسان است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5210,'مردم همواره قابل درک نیستند و زندگی همواره قابل فهم نیست.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5211,'به جای کین خواهی، هیچ کاری بهتر از آن نیست که موضوع را به کلی، به دست فراموشی بسپارید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5212,'در همه ی پهنه ها، خوش رفتار باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5213,'رعایت [ =پاس داشتن ] آداب، هزینه ای ندارد، در عین حال، خوش نیتی فراوانی ایجاد می کند و زندگی همه را بسیار دلپذیرتر می سازد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5214,'هر اندازه در مرزهای خود، امنیت بیشتری پیدا کنید، قدرت دیگران در تاثیرگذاری بر شما کمتر خواهد شد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5215,'مادام که خود را بزرگ نمی دارید، نمی توانید به این امید باشید که دیگران، شما را بزرگ دارند و ارج نهند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5216,'شما نمی توانید به خود ارج نهید، مگر اینکه از آنچه هستید و آن که هستید، تصویر روشنی داشته باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5217,'افراد کامیاب، ارزش خود را می دانند و زمان خود را هدر نمی دهند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5218,'جوان ماندن، آزمودن چیزهای تازه است، نه غرغر کردن و گفتن همه ی چیزهایی که آدمها در زمان پیری می گویند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5219,'جوان ماندن، پیش رفتن به سوی گزینه های امن نیست، بلکه بودن در جریان رویدادهایی است که رخ می دهد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5220,'برای آنکه مستقل بیندیشید، باید در مورد اینکه چه کسی هستید و در اندیشه ی خود و عمل کردن به آن، روشن باشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5221,'اگر همه چیز خوب بود، زندگی به طرز هولناکی، پوچ ستوه آور می شد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5222,'اگر نمی توانید سازی بنوازید، بد نیست به یک قطعه ی موسیقی گوش کنید که همواره می تواند شما را سبک و آسوده دل کند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5223,'تنها، آدمهای خوب، احساس گناه می کنند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5224,'احساس گناه کاملاً بیهوده است، مگر آنکه به دگرگونی در جهت بهبود منجر شود.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5225,'با چشم پوشی از اینکه یک آدم تا چه اندازه ترسناک باشد، همواره می توان چیز خوبی هم در او پیدا کرد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5226,'مردم دوست دارند در پیرامون کسانی باشند که خوشبین، مثبت، شاد و دارای خودباوری هستند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5227,'گاهی نگریستن به یک موضوع از زاویه ای متفاوت [ =دیگرگون ]، ارزشمند است.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5228,'به شریک زندگی خود، فضای لازم را بدهید تا خودش باشد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5229,'کامرواترین رابطه ها، آنهایی است که زوجها کاملاً به یکدیگر نزدیک و کاملاً جدا از هم هستند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5230,'بهترین رابطه ها، آنهایی است که طرفین از دلبستگی های یکدیگر جانبداری می کنند، حتی اگر مخالف دلبستگی های خودشان باشد.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5231,'نخستین کسی باشید که می گوید ببخشید.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5232,'گفتگو کردن به از بین بردن دشواری ها کمک نموده و سکوت، تنها آنها را بزرگ می کند.((ریچارد تمپلر))',71,0,0,0,0,0);
INSERT INTO mysms VALUES(5233,'شادی راستین از درون ما و از شناخت خودمان سرچشمه می گیرد، نه از مالکیت [ =ازآنِش ] چیزهایی که بیرون از وجود ما هستند.((ریچارد گرهم))',72,0,0,0,0,0);
INSERT INTO mysms VALUES(5234,'نخستین گام برای داشتن یک زندگی شادتر و کامیاب تر این است که مالکیت [ =ازآنِش ] وضعیت کنونی زندگی تان را به دست گیرید.((ریچارد گرهم))',72,0,0,0,0,0);
INSERT INTO mysms VALUES(5235,'هیچ فرد دیگری، زندگی شما را مهار نمی کند، مگر اینکه به او اجازه دهید.((ریچارد گرهم))',72,0,0,0,0,0);
INSERT INTO mysms VALUES(5236,'در هر دم از زندگی تان می توانید اراده کنید راه تازه ای را در پیش بگیرید و خودتان را از شیوه ی اندیشه ی فلج کننده ای که شما را تاکنون، واپس نگه داشته است، رها سازید.((ریچارد گرهم))',72,0,0,0,0,0);
INSERT INTO mysms VALUES(5237,'به جای گردن نهادن، تصمیم بگیرید زندگی تان را بهتر کنید؛ اگر بخواهید، می توانید.((ریچارد گرهم))',72,0,0,0,0,0);
INSERT INTO mysms VALUES(5238,'کسانی که پیوسته ناآرام اند، عزت نفس شان کم است و خودشان را دست کم می گیرند.((ریچارد گرهم))',72,0,0,0,0,0);
INSERT INTO mysms VALUES(5239,'اگر شمار بیشتری از مردم، همان میزان انرژی را که برای سرزنش، گلایه و خرده گیری از دیگران بکار می برند، برای به دست گرفتن مالکیت [ =ازآنش ] زندگی شان به کار می گرفتند، تا چه اندازه زندگی شان بهتر می شد.((ریچارد گرهم))',72,0,0,0,0,0);
INSERT INTO mysms VALUES(5240,'هیچ چیز با ارزشی در زندگی، ساده به دست نمی آید.((ریچارد گرهم))',72,0,0,0,0,0);
INSERT INTO mysms VALUES(5241,'از گناهان خود، درس بگیرید تا از تکرار آن جلوگیری کنید.((ریچارد گرهم))',72,0,0,0,0,0);
INSERT INTO mysms VALUES(5242,'با تصمیماتی که در زندگی خود می‌گیریم، خوشبختی و کامروایی خود را رقم می‌زنیم.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5243,'اگر به دیگران کمک کنید تا به خواسته هایشان برسند، شما هم به همه ی خواسته هایتان خواهید رسید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5244,'اینکه می گویند استاد بیش از شاگرد می آموزد، تنها یک اندیشه نیست.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5245,'اگر چگونگی برخورد با خرده گیری را بدانید، هرگز آن را دشواری به شمار نخواهید آورد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5246,'من این نکته را می پذیرم که هرچه در زندگی می خواهیم، به شرطی به دست خواهیم آورد که به دیگران کمک کنیم تا آنچه می خواهند به دست آورند و به مراد دل خود برسند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5247,'اخموها، تهی دست ترین افراد دنیا هستند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5248,'در مسیر رسیدن به اهداف خود، اگر با بازدارنده ای روبرو شدید، مسیر حرکت خویش را دگرگون سازید، نه هدفتان را.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5249,'شما با سنجش خود با دیگران، نمی توانید میزان کامیابی تان را بازشناسید، بلکه میزان کامیابی خود را از سنجش شایستگی هایتان با آنچه انجام داده اید، می توانید به دست آورید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5250,'به یاد داشته باشید که تمام آبهای جهان نمی تواند قایق شما را غرق کند، مگر آنکه به درون آن راه یابد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5251,'گاهی بر هم زدن سبک و روش عادی، سبب کامیابی است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5252,'حتی اگر در شرایطی، کاری از دست تان ساخته نیست، دست کم، نگرش خود را در مورد آن شرایط تغییر دهید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5253,'در درون هر کس، تا اندازه ای استعداد یافت می شود؛ اگر من می توانم، تو نیز خواهی توانست.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5254,'آنچه کامیابی شما را در زندگی تعیین می کند، رویدادهای زندگی شما نیست، بلکه برداشت شما از آن رویدادها است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5255,'تکرار کردن، پایه ی یادگیری و ابزاری نیرومند است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5256,'در بازی زندگی، آنچه تعیین کننده ی پیروزی یا شکست، خوشبختی یا بدبختی است، بیشتر، چیزهای کلی نیستند، بلکه جزئیات اند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5257,'نگران چیزی نباشید که نمی توانید آن را تغییر دهید، در برابر، انرژی خود را در موارد مثبت و سازنده بکار برید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5258,'نگرانی مانند صندلی گهواره ای است که شما را تکان می دهد، ولی با این همه جنبش، شما را به جایی نمی برد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5259,'اگر ما آماده ی بهره گیری از فرصت ها باشیم، فرصت ها همیشه در دسترس ما است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5260,'پیش از به حرکت درآوردن زبان، مغز خود را به کار اندازید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5261,'بنا کردن زندگی بر پایه های راستی و درستی، خواهانِ دانستن تفاوت درست و نادرست و به کار بستن این دانش است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5262,'در هر شرایط، خود را نبازید و دو ریسمان امید و ایمان را رها نکنید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5263,'پاداش های بزرگ، پیشامدهایی هستند که به زودی فراموش می شوند، ولی موهبت های کوچک زندگی، پیوسته هستند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5264,'امروز شاید سرشار از خوشی های بزرگ نباشد، ولی سرشار از لذتهای کوچک است. کافی [ =بس ] است که چشمانتان را باز کنید و آنها را ببینید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5265,'شما بی نظیر و خاص هستید. کاری را که شما برای نوع بشر انجام می دهید، هیچ کس نمی تواند انجام دهد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5266,'آنچه داری یا آنچه می کنی، مهم نیست؛ مهم چیزی است که هستی.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5267,'شکست، زشت و شرم آور نیست، بلکه زشتی، در این است که از تلاش کردن دست برداری.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5268,'مراقب دیدگاه های خود باشید، ممکن است هر دَم به گفتار تبدیل شوند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5269,'بیشتر، موانعی [ =بازدارنده هایی ] را که سر راه وجود دارد، خودمان ایجاد کرده ایم.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5270,'دنیا از آنِ کسانی است که می دانند، از هم اکنون و از همین جا می توان به تکاپو درآمد و به اوج رسید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5271,'هیچ چیز بدون شور و شوق به وجود نمی آید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5272,'اگر می خواهید در زندگی کامیاب شوید، بدانید چه می کنید، به کار خود باور داشته باشید و بدان عشق بورزید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5273,'هرکس راهی را در پیش می گیرد، سرانجام آن را نیز می پذیرد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5274,'مهربانی در حق دیگران، هرگز به هدر نمی رود، بلکه به خودت باز می گردد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5275,'هنگامی که سخن می گویید، چیزی را می گویید که از پیش می دانستید و هنگامی که به کسی گوش می کنید، چیزی را که دیگری می داند، می آموزید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5276,'هیچ گاه برای یادگیری، آرزو کردن یا دگرگونی دیر نیست.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5277,'بیشتر زمانها، پیشامدی که بسیار اندوه بار و ناامید کننده به نظر می رسد، سبب بروز بزرگترین دگرگونی در زندگی می شود.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5278,'کسانی که سرانجام، کامیاب می شوند، آنهایی هستند که از تلاش نمی هراسند. آنان می دانند که شکست، یکی از نتایج احتمالی تلاش است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5279,'آنچه شما را خوشبخت می کند، داشته های شما نیست، بلکه آنچه هستید، سبب می شود احساس خوشبختی کنید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5280,'شرافت، آن است که انسان با دلیری در برابر سختی ها بایستد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5281,'واژه ها، توانمندترین نیروهای جهان هستند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5282,'رو به خورشید بایستید، سایه ها پشت شما خواهند بود.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5283,'کامیابی، نتیجه ی پیروزی های کوچک روزانه است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5284,'به یاد داشته باشید، زمان، تنها چیزی است که باید به خود یا دیگران بفروشید. هرچه از زمان خود، بهتر بهره بگیرید، بهای آن بالاتر خواهد بود.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5285,'تنها با اندیشیدن به هدف ها، نمی توان به آنها رسید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5286,'برای اینکه با کامیابی به هدف خود برسید، داشتن پشتکار، بس نیست، بلکه باید با دقت به سوی آن پیش بروید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5287,'تهیدست ترین انسان، کسی است که رؤیایی در سر ندارد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5288,'انرژی مورد نیاز برای برنامه ریزی یک هدف، برابر است با انرژی مورد نیاز برای آرزوی آن.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5289,'من گمان می کنم، دلیل اینکه مردم به خواسته هایشان نمی رسند، این نیست که برنامه ی ساده ای دارند، بلکه آنها نمی خواهند این برنامه ی ساده را دنبال کنند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5290,'در بیشتر موارد، تنها عشق است که تفاوت میان پیروزی و شکست را تعیین می کند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5291,'امروز، روز مهمی است! کاری که امروز می کنید، مهم نیست؛ مهم این است که یک روز از زندگی خود را برای آن سپری می کنید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5292,'تا آنجا که می بینی، پیش برو، هنگامی که به آنجا رسیدی، یقین داشته باش که دورتر را خواهی دید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5293,'اگر خواهان شادی هستید، باید با تلاش بسیار کار کنید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5294,'بهترین راه برای کامیابی در هر کاری، این است که با آمادگی تمام و تلاش، به ژرفای آن بروید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5295,'همواره اندیشه های تازه وجود دارند که باید کشف شوند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5296,'خوشبخت، کسی است که آرزوهای بسیار دارد و آرزومند است که برای برآوردن آنها گام هایی بردارد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5297,'اگر مردم می توانستند به جای تمرکز بر نگرانی ها، از صمیم قلب بر کارشان تمرکز کنند، کامیابی آنها تضمین [ =پایندان ] می شد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5298,'اندیشه های شما، کردارهایتان را مهار می کنند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5299,'سخنانی به دل می نشینند که سخنران به گفته هایش باور داشته باشد و آنها را با شور و شوق بیان کند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5300,'فرستادن یک کارت تبریک، کار چندان بزرگی نیست، ولی دریافت یک کارت از جانب یک دوست، ممکن است روز شادی را برای ما ایجاد کند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5301,'خرده گیری، به ندرت، اما کار، همیشه، شما را به جایی می رساند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5302,'بسیاری از آدمها به دلیل داشتن ترس از شکست، هرگز کامیاب نمی شوند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5303,'زیبایی، نیازی به پیچیدگی ندارد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5304,'گرفتاری شما هر اندازه هم که باشد، غیر ممکن است که نتوانید هر روز یک کار خیر برای کسی بکنید یا سخن نیکی برای کسی بیان کنید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5305,'عشق، آموختنی است و برای همین است که برخی، در درس عشق، رد می شوند؛ آنها هرگز عشق ورزی را نیاموخته اند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5306,'عشق بورزید تا عاشقتان باشند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5307,'دانستن قدر و ارزش داشته هایمان، تنها نیمی از راه زندگی است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5308,'هنگامی که ذهن شما آزاد و تهی از هر گونه نفرت است، هر کاری را که باید بکنید، بهتر انجام می دهید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5309,'خودخواهی، در بالاترین حد خود، کار چندانی نمی کند، اما عشق، در جزیی ترین حد خود، بیشترین کار را می کند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5310,'اگر از آنچه در دست دارید، بهره برداری نکنید، از چیزی که ندارید، بهره ای نخواهید گرفت.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5311,'در سخت ترین سفرها نیز باید گام به گام پیش رفت، اما نباید درنگ کرد.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5312,'رمز کامیابی، داشتن پشتکار و دلیری، با وجود ناکامی ها است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5313,'تا زمانی که دستهایتان را در جیب ها گذاشته اید، نمی توانید از نردبان کامیابی بالا بروید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5314,'تنها کسانی می توانند پس از زمین خوردن، بلند شوند و به راه خود ادامه دهند، که بدانند به کجا می روند و چرا؟((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5315,'اگر پایبندی شما چندان جدی نباشد، کامیابی شما غیر ممکن است، زیرا هنگامی که شرایط، سخت می شود و تلاش بسیار لازم است، نیرویی برای تلاش باقی نمی ماند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5316,'تفاوت بین کامیابی و شکست، بیشتر، تفاوت بین پایبندی کامل و تلاش نه چندان جدی است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5317,'خوش شانسی، یعنی شناسایی فرصت های پیش آمده و به کارگیری آنها.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5318,'پایداری، عامل کلیدی در رسیدن به کامیابی، پس از هر تلاش است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5319,'شکست، بیشتر، نشانه ی کم بودن پایداری است.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5320,'پایبندی برخی از آدمها به گونه ای است که هنگامی که پیشامد بدی رخ می دهد، بی درنگ، راه شان را کج کرده و از پایبند بودنشان، شانه خالی می کنند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5321,'هنگامی که خسته می شوید، اگر پایبند باشید، نخستین اندیشه ای که به ذهن تان می رسد، این است که چگونه آن دشواری را از بین ببرید. ولی اگر پایبند نباشید، نخستین اندیشه ای که به ذهن تان می رسد، این است که چگونه از کار بگریزید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5322,'در بدگویی و خبرچینی، شنونده بیشتر از گوینده، گناهکار است، چون اگر شما گوش ندهید، هیچ کس نزد شما بدگویی نمی کند. بی گمان این کار را نخواهند کرد؛ پس گوش ندهید!((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5323,'هنگامی که سرسختانه کارهایتان را انجام می دهید، مردم از سر راهتان کنار می روند و شما را از کار باز نمی دارند.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5324,'در هر روز از زندگی تان به گونه ای کار کنید که انگار می خواهید روز دیگر به مرخصی بروید. به این ترتیب می توانید در هر روز کار بیشتری انجام دهید.((زیگ زیگلار))',73,0,0,0,0,0);
INSERT INTO mysms VALUES(5325,'برای زنده ماندن، باید خودت را از درون بكُشی؛ از این رو بیشتر آدمها همه چیز را رها کرده اند، چون می دانند که هر اندازه هم تلاش کنند، سرانجام می بازند و زمانی که به این نقطه رسیدی، هر گونه مبارزه ای بیهوده است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5326,'شاید بزرگترین دشواری، همین باشد؛ زندگی چنان که ما می شناختیم پایان یافته، ولی هنوز کسی نیست که بداند چه چیزی جای آن را گرفته است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5327,'هنگامی که آغاز به نوشتن موضوعی [ =جستاری ] می کنم، ناگهان پی می برم که درک من از آن چه اندازه اندک است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5328,'مردم از هر چیزی سخن می گویند، به ویژه از آنچه درباره اش هیچ نمی دانند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5329,'ناامیدی کامل می تواند در کنار درخشان ترین نوآوری ها به سر بَرَد. از کار افتادگی و شکوفایی، یکدیگر را جذب می کنند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5330,'هر کس نیازمند دوستانی است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5331,'اینکه چه چیزی در کجا گفته شود، تفاوتی ندارد، اینکه آنچه نخست گفته شود، دومی باشد یا دومی، واپسین.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5332,'بی دانش، نه می توان امیدوار بود نه ناامید.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5333,'همه نیاز به زمان دارند. آدم نباید پیش از اینکه بداند چه می گوید، نتیجه گیری کند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5334,'هیچ کس نمی داند در زمانهای بحرانی، وفاداری به کدام سو رو می کند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5335,'دمی بی خبری کافی است تا همه چیز را از دست بدهی و ناگهان هر چه را که با تلاش به دست آورده ای، نابود می شود و از دست می رود.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5336,'هر بار که می پنداری پاسخ پرسشی را یافته ای، پی می بری که آن پرسش، هیچ مفهومی ندارد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5337,'مسئله تنها این نیست که چیزها ناپدید می شوند، بلکه پس از آن، یادشان نیز نابود می شود.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5338,'همه ی چیزهای زیبایی را که می بینی، به ذهن بسپار، تا همیشه با تو باشند، حتی در زمانهایی که آنها را نمی توانی ببینی.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5339,'هرگز دری را نزن، مگر اینکه بدانی در آن سویش چه می گذرد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5340,'تا زمانی که مطمئن [ =آسوده دل ] نشوم، امیدم را از دست نمی دهم.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5341,'تصمیم هایی وجود دارد که هیچ کس نباید ناگزیر به گرفتن شان شود.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5342,'هنگامی که چیزی از دست می رود، دیگر نمی توان آن را پس گرفت.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5343,'از همه اندوهگین تر شخصی است که از همه بیشتر می خندد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5344,'فقرا نمی دانند که تنها دلیل آنها برای زندگی، تمایل ما به تظاهر در برخورداری از فضیلت سخاوت است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5345,'در عشق، یک نفر به اضافه ی یک نفر مساوی یک است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5346,'لجن، زجر آب است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5347,'تنها کسانی که پارو نمی زنند فرصت تکان دادن قایق را دارند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5348,'انسان روش مند از آزاداندیشی به دور است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5349,'در عشق، یک و یک می شود یک.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5350,'انسان محکوم است که آزاد باشد، چرا که به محض پرتاب شدن به این دنیا، مسئول کارهای خویش است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5351,'همه چیز کشف شده است، مگر چگونه زیستن.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5352,'وقتی ثروتمندان جنگ به پا می کنند، این فقرا هستند که می میرند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5353,'زمانی که (توهم جاودان بودن) را از دست بدهی، زندگی هم معنایش را از دست می دهد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5354,'بشر هیچ نیست، مگر آنچه از خود می سازد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5355,'هیچ چیز برای ما خوب نمی تواند بود، مگر آنکه برای همگان خوب باشد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5356,'کسی که دروغ می گوید و با گفتن اینکه همه ی مردم چنین نمی کنند، برای خود عذری می تراشد، کسی است که با وجدان خود بر سر ستیز است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5357,'کار جهان بر این مدار است که گویی تمام آدمیان، چشم بر رفتار هر یک از افراد دوخته اند و روش خود را طبق رفتار همین یک نفر تنظیم می کنند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5358,'بشر، محکوم است، زیرا خود را نیافریده و در عین حال، آزاد است، زیرا همین که پا به جهان گذاشت مسئول همه ی کارهایی است که انجام می دهد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5359,'شما آزاده اید؛ راه خود را برگزینید؛ یعنی بیافرینید.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5360,'گوشه گیری و کاهلی، روش کسانی است که می گویند: آنچه را که من نمی توانم کرد دیگران می توانند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5361,'حقیقتی وجود ندارد، جز در عمل.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5362,'بشر وجود ندارد، مگر در حدی که طرح های خود را تحقق می بخشد. بنابراین، جز مجموعه ی اعمال خود، جز زندگانی خود، هیچ نیست.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5363,'عشقی جز آنچه به مرحله ی تحقق درمی آید، وجود ندارد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5364,'امکان عشق، چیزی جز آنچه از عشق تجلی می کند نیست.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5365,'آنچه آدمی را سست عنصر می سازد، عمل گریز یا تسلیم است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5366,'شخص قهرمان، خود، خویشتن را قهرمان می کند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5367,'آنچه به بشر امکان زندگی می دهد، تنها عمل است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5368,'فلسفه ی متکی به احتمالات که وابسته به حقیقتی نباشد، محکوم به نیستی است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5369,'من همیشه می توانم آزادانه انتخاب کنم، اما باید بدانم که اگر انتخاب نکنم، باز هم انتخابی کرده ام.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5370,'بشر از ابتدا موجود ساخته و پرداخته ای نیست؛ بلکه با برگزیدن اخلاق خود، خویشتن را می سازد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5371,'هیچ کس نمی تواند آزادی خود را هدف خویش سازد، مگر اینکه آزادی دیگران را نیز به همان گونه هدف خود قرار دهد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5372,'من هنگامی آزادم که همه ی جهانیان آزاد باشند؛ تا هنگامی که یک نفر اسیر در جهان هست، آزادی وجود ندارد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5373,'همیشه باید اخلاق را آفرید و ابداع کرد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5374,'بشر آفریننده ی ارزشها است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5375,'امروز آدمی ناگزیر به دیروز تبدیل خواهد شد، اما امکان دارد فردای شما هرگز امروز نشود.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5376,'انسان مجموعه ای از آنچه دارد نیست، بلکه مجموعه ای است از آنچه هنوز ندارد، اما می تواند داشته باشد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5377,'تاریخ یک ماشین خودکار و بی راننده نیست و به تنهایی استقلال ندارد، بلکه تاریخ همان خواهد شد که ما می خواهیم.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5378,'نبوغ، جوهر تفکر است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5379,'نگذارید بچه ها گریه کنند، زیرا باران هم غنچه را تباه می کند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5380,'همه می گویند آرزو بر جوانان گناه نیست و من می گویم بر پیران هم!((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5381,'آدم باید به این امر خو بگیرد که به کمترین ها خرسند باشد. هرچه کمتر بخواهی، با چیزهای کمتری خشنود می شوی و هر اندازه نیازهایت را کم کنی، امور زندگی ات بهتر خواهد شد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5382,'هنگامی که امید می میرد، هنگامی که می بینی کمترین امکان امیدوار بودن را از دست داده ای، فضای خالی را با رؤیا، اندیشه های کوچكِ بچگانه و داستانها پر می کنی تا بتوانی به زندگی ادامه بدهی.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5383,'اگر بخواهی از راه دویدن، خودکشی کنی، ابتدا باید خوب دویدن را یاد بگیری.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5384,'مرگ، تنها چیزی است که حسی را در ما برمی انگیزد. مرگ، شکل هنری ما و تنها راه ابراز درون ناپیدا است.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5385,'هیچ چیز ساده تر از قلب نمی شکند.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5386,'کمابیش کسی نیست که بخش کوچکی از زندگی را چنان که در گذشته بود، در وجود خود به دوش نکشد.((ژان پل سارتر))',74,0,0,0,0,0);
INSERT INTO mysms VALUES(5387,'انسان، حیوانی بیمار است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5388,'قلب، دانشمندترین فیلسوفان و دانایان است. کسی که دارای قلب پاک باشد به پند و اندرز فیلسوفان نیازی ندارد.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5389,'به طور کلی اشخاصی که زیاد می دانند، کم حرف می زنند و کسانی که کم می دانند، پر حرف هستند.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5390,'فحش دلیل کسانی است که حق ندارند.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5391,'ساده ترین درس زندگی این است : هرگز کسی را میازار.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5392,'در زنان هوش و در مردان استعداد بیشتر است. آنچه را زن با چشم دقت و بررسی می بیند، مرد با دیده عقل و اندیشه می نگرد.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5393,'عموما اشخاصی که زیاد می دانند، کم حرف می زنند و کسانی که کم می دانند، پرحرف هستند.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5394,'انسان آزاد آفریده شده است اما همه جا در زنجیری است که خود بافته است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5395,'درمان فقر جسمانی، ورزش؛ فقر روحانی، دین؛ فقر مالی، تلاش و فقر معنوی، دانش است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5396,'بزرگترین تفاوت انسان و حیوان، فهم و اندیشه نیست، بلکه اراده و اختیار او است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5397,'انسان همیشه برای چیزهایی که آرزو می کند قدرت کافی دارد؛ فقط سستی اراده است که نمی گذارد او در راه رسیدن به آرزوی خود گام بردارد.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5398,'مردم بدون عشق [ همچون ] کورانی هستند که به هیچ وجه به منزل نخواهند رسید.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5399,'می توان حقیقتی را دوست نداشت، اما نمی توان منکر آن شد.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5400,'هر عیش و لذتی را که در دنیا می بینید، دارای رنج و زحمت است و اگر همان زمان برای ما زحمت ایجاد نکند، در آینده اسباب دردسر می شود. فقط دو لذت وجود دارد که هرگز نتیجه ناگوار و پشیمانی دربر ندارند: نیکی کردن به مردم و انجام وظیفه.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5401,'افراد سست اراده همیشه منتظر معجزه ها و رویدادهای شگفت انگیزند، اما افراد قوی، خود، آفریننده ی معجزه ها و رویدادهای شگفت انگیز هستند.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5402,'امید و آرزو آخرین چیزی است که دست از گریبان بشر برمی دارد.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5403,'بیش از سخن گفتن گفتارت را بسنج، چنان که بیش از کاشتن تخم شخم می زنی.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5404,'احساس دل، بالاتر از منطق است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5405,'آزادی را فقط در مجسمه ی آن یافتم.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5406,'آنچه بار زندگی را بر دوش ما سنگین تر می سازد، عموما زیاده روی در خود زندگی است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5407,'آنچه در دنیا برای انسان اهمیت دارد، انجام وظیفه است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5408,'مذهب چیز ثمربخشی است حتی برای انسانها هم لازم است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5409,'انسان از مادر، آزاد آفریده شده است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5410,'اندیشه های صرفاً ذهنی و کلی، منشأ بزرگترین خطاهای بشرند.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5411,'زندگی، آنچه را که بخواهید به شما می دهد؛ به این شرط که خواسته هایتان مشخص و روشن باشد. آنچه را که می خواهید پیوسته بجویید و مطمئن باشید که به آن می رسید.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5412,'در زیر فشار خواهشهای نفس، بَرده ام و به ندای وجدان، آزادم.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5413,'هر پدیده ای زمانی که از دست آفریدگارش بیرون می آید خوب است و همین که به دست انسانها می افتد به تباهی می گراید.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5414,'پول، زاده پول است. گاهی به دست آوردن اولین لیره دشوارتر از به دست آوردن میلیون لیره دوم است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5415,'ایمان داشتن به تواناییهای خویش، نیمی از موفقیت و کامیابی است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5416,'کسانی که دیر قول می دهند، خوش قول ترین مردان دنیا هستند.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5417,'سعی کنید همان طوری باشید که می خواهید دیگران شما را [ آنگونه] ببینند.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5418,'ای وجدان، تو عامل بزرگی وجود انسان و اخلاقی بودن کارهای او هستی و بدون تو چیزی در وجود خود احساس نمی کنم که مرا در جهانی برتر از حیوانات قرار دهد.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5419,'وجدان بزرگترین فیلسوف است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5420,'عشق حقیقی آتشی مخرب است که شعله خود را به سایر احساسات نیز می کشاند و آنها را با یک نیروی جدید، زنده می سازد. اینجاست که گفته اند عشق، قهرمانهایی را خلق می کند.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5421,'بهترین وسیله برای جلب محبت دیگران، نیکی کردن به آنهاست.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5422,'در مقابل همه چیز می توان مقاومت کرد جز نیکی و خوبی.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5423,'مرد آنچه را می داند، می گوید و زن آنچه را که خوش آیند شنونده است.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5424,'دوران جوانی، زمانی است برای به دست آوردن خرد؛ دوران کهنسالی، زمانی است برای به کار بستن آن.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5425,'هر کس لطف بهار می خواهد، باید دشواری زمستان را تاب آورد.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5426,'برای اینکه بتوانیم کسی را دوست داشته باشیم، به زمان و آگاهی نیاز داریم. برای دوست داشتن، ابتدا باید داوری کرد و برای برتری دادن، باید نخست سنجید.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5427,'آسایش، زمان نمی شناسد. هرگاه خسته شدی، بیاسای.((ژان ژاک روسو))',75,0,0,0,0,0);
INSERT INTO mysms VALUES(5428,'انسان باید به هنگام اظهار نظر درباره شخص خود، بسیار سختگیر و انعطاف‏ناپذیر باشد.((ژرژ برنانوس))',76,0,0,0,0,0);
INSERT INTO mysms VALUES(5429,'از نظر من، انسان انسان است و ویژگیها و ارزش او از دوران شرک و بت پرستی تاکنون چندان تفاوتی نکرده است. از این گذشته مسئله مهم، ارزیابی ارزش انسان نیست، بلکه باید دید چه نیرویی بر او حاکم است.((ژرژ برنانوس))',76,0,0,0,0,0);
INSERT INTO mysms VALUES(5430,'حقیقت، ابتدا انسان را نجات می‏دهد، آنگاه آرامش می‏بخشد.((ژرژ برنانوس))',76,0,0,0,0,0);
INSERT INTO mysms VALUES(5431,'مرگ آرزویی جز این ندارد که به وعده‏ای که در صبح زندگی داده است وفا کند. به بیان جدّی‏تر شیرینی و گرمی لبخند مرگ کمتر از لبخند زندگی نیست.((ژرژ برنانوس))',76,0,0,0,0,0);
INSERT INTO mysms VALUES(5432,'احساس ترحم، احساسی پرقدرت و آزمند است؛ نمی‏دانم به چه دلیل همواره آن را احساسی ضعیف، کودکانه و ناشی از طرز تفکری درهم و برهم معرفی می‏کنند، حال آنکه یکی از نیرومندترین احساسات انسان است.((ژرژ برنانوس))',76,0,0,0,0,0);
INSERT INTO mysms VALUES(5433,'مسئله اجتماعی در درجه اول مسئله شرف، آبرو و حیثیت است. تحقیر نابجا و غیر منصفانه فقیر باعث پیدایش تیره روزان می‏شود.((ژرژ برنانوس))',76,0,0,0,0,0);
INSERT INTO mysms VALUES(5434,'دوزخ این است که انسان دیگر دوست نداشته باشد.((ژرژ برنانوس))',76,0,0,0,0,0);
INSERT INTO mysms VALUES(5435,'نافرمانی این است که انسان دیگر قادر به درک چیزی نباشد.((ژرژ برنانوس))',76,0,0,0,0,0);
INSERT INTO mysms VALUES(5436,'کارهای [ نیک ] کوچک که به ظاهر چیزی نیستند، اما آرامش ایجاد می‏کنند، حکم گلهای صحرایی را دارند؛ انسان فکر می‏کند که این گلها عطری ندارند، اما همه آنها با هم فضا را معطّر می‏کنند.((ژرژ برنانوس))',76,0,0,0,0,0);
INSERT INTO mysms VALUES(5437,'ازدواج در حقیقت پیوندیست که از درختی به درخت دیگر بزنند؛ اگر خوب گرفت، هر دو بارور می شوند و اگر بد شد، هر دو خشک می شوند.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5438,'نه طوطی باش که گفته دیگران را تکرار کنی و نه بلبل باش که گفته خود را به هدر دهی.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5439,'عقیده ای که مردم از ترس جانشان بپذیرند، پایدار نیست.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5440,'تو می کوشی که آسوده تر باشی، من می کوشم که دیگران آسوده تر از من باشند.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5441,'نیک بخت کسی است که نه از مردن کسی شاد شود و نه کسی از مردن او شاد گردد.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5442,'هر کس چیزی را که ندارد، رسیدن به آن را خوشبختی می داند.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5443,'گذشته برای آن است که انسان، توشه ای از عبرت برای آینده خود بردارد.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5444,'توجه نسبت به آینده باعث می شود که انسان همیشه به نیروی خویش بیفزاید و برای فردای خود، بیش از آنچه امروز به کار برده است، توانایی به دست آورد.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5445,'برای همه کس خوشبختی یک معنی بیشتر ندارد! رسیدن به آرزوی خیالی.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5446,'آزادی باده ای است که هزاران تن آن را برای بدمستی می نوشند و یک تن برای سرمستی.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5447,'آزادی برای بسیاری از مردم سرمایه ی دکان است و برای یک تن از هزاران، سرمایه ی جان.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5448,'تنها کسانی که دل داده ی دانشند بزرگند، نه آنها که سوداگر دانشند.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5449,'زندگی خانوادگی [ مانند ] شرکتی است که هرچه سهم دو شریک در آن بیشتر باشد، قدرت آن شرکت و پایداری آن بیشتر خواهد بود.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5450,'تنها، کسی که هنر دارد و کاری از او ساخته است می تواند به فردای خویش اطمینان کند.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5451,'در اندیشه ی آنچه کرده ای مباش، در اندیشه ی آنچه نکرده ای باش.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5452,'بسیاری از استعدادهای بشری در نتیجه بیخبری و نادانی پدر و مادر از میان می رود. بسا کودکانی که ممکن بود اگر ایشان را آزاد بگذارند و دنبال فنی بفرستند، مردان بزرگی در فنّ خود بشوند و اما استبداد رأی و یک نوع خودخواهی که در مربیان و راهنمای ایشان بوده، آنها را بر رشته ای راهنمائی کرده است که با کمال بی میلی و اکراه آن را پذیرفته اند.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5453,'جهان بزرگتر از آن است که بتوانی آن را به خود اختصاص دهی.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5454,'بالاترین رمز کامیابی اینست که انسان چیزی بگوید و کاری بکند و چیزی را بخواهد که دیگران معمولاً آنرا نمی گویند و نمی کنند و نمی خواهند، یا در گفتن و کردن و خواستن آن تردید دارند.((سعید نفیسی))',77,0,0,0,0,0);
INSERT INTO mysms VALUES(5455,'تمام محبت خود را به یکباره برای دوستت ظاهر مکن؛ زیرا هر وقت اندک تغییری مشاهده کرد تو را دشمن می پندارد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5456,'اگر می خواهی هستی را بشناسی، خود را بشناس.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5457,'من یک شهروند جهانی‌ام و نه یک شهروند آتنی.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5458,'تنها یک خیر وجود دارد که نام آن، دانش است و تنها یک شر وجود دارد که نام آن، نادانی است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5459,'من تا آنجا که می‌توان، حق‌شناسی می‌کنم، ولی چون پول ندارم، جز ستایش، کاری از دستم برنمی‌آید.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5460,'دانش پاک در دلهای ناپاک قرار نمی گیرد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5461,'سعادتمند کسی است که از هر اشتباه و خطایی که از او سر می زند، تجربه ای جدید به دست آورد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5462,'هرگاه تمام بلاها و سختی های بشر را در یک جا جمع کنند و در میان مردم تقسیم نمایند، بدبخت ترین مردم چون سهم خود را ببینند، از سهم نخستین خویش راضی شده، لب از شکایت می بندند.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5463,'خشم و غضب را به درگاه مردان با اراده راهی نیست.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5464,'من جز یک چیز، چیز دیگری نمی دانم و آن این است که هیچ نمی دانم.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5465,'هیچ کس از قلب شما به شما نزدیکتر و راستگو تر نیست. بنابراین از کسانی که قلب پاک شما آنان را به خود نمی پذیرد، دوری کنید.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5466,'اگر خاموش باشی تا دیگران به سخنت آرند، بهتر که سخن گویی تا دیگران خاموشت کنند.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5467,'ابله ترین دوستان ما خطرناکترین دشمنان هستند.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5468,'پیش تمام دشمنان به نزدیکترین دشمن خود که زبان است توجه داشته باش.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5469,'مردم هر کدام آرزویی دارند؛ یکی مال می خواهد، یکی جمال و دیگری افتخار. ولی به نظر من دوست خوب از تمام اینها بهتر است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5470,'هیچ کس از قلب شما به شما نزدیکتر و راستگوتر نیست. بنابراین از کسانی که قلب پاک شما ایشان را به خود نمی پذیرد، دوری کنید.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5471,'سخن گفتن به جا و سکوت نمودن به جا نشانه عقل است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5472,'در تکاپو، تا بهتر از آن باشیم که هستیم؛ بهتر از این شیوه ای برای زیستن نیست.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5473,'می دانم که هیچ نمی دانم.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5474,'هیچ کس حق ندارد راضی شود که در گمراهی و نادانی بماند و نیز کسی نباید حقیقت را پنهان کند.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5475,'من حقیقت را نمی دانم و از راه مباحثه با اشخاص می خواهم آن را کشف کنم و کسب دانش نمایم.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5476,'من دانش و هنری ندارم، تنها هنر من این است که مانند مادرم فن قابلگی می دانم، با این تفاوت که مادرم زنها را در وضع حمل مدد می کرد و من، عقلها و ذهنها را مدد می کنم که زاینده شوند؛ یعنی علمی که در نهاد ایشان هست پیدا شود و به آن آگاه گردند.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5477,'فاش نکردن اسرار مردم دلیل کرامت و بلندی همت است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5478,'مرد عاقل کسی است که کم گوید و زیاد شنود.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5479,'شیرینی یک بار پیروزی به تلخی صدبار شکست می ارزد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5480,'روح درونی خود را زیبا کنید تا شخصیت درونی و بیرونی شما یکی شود.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5481,'هرکسی که بداند که نداند از همه داناتر است. یک چیز را خوب می دانم و آن این است که هیچ نمی دانم. هر که بداند درست چیست، دست به نادرست نمی زند.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5482,'ای مردم آتن، چه مرا بی گناه شمارید و آزاد کنید و چه نکنید، من هرگز از راه خویش باز نمی گردم حتی اگر صدبار کشته شوم.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5483,'هرجا که بشر را دوست بدارند، فرهنگ را هم دوست خواهند داشت.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5484,'تفاوت انسان با حیوان در کنترل هوای نفس و هوس ها است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5485,'ازدواج کنید، به هر وسیله ای که می توانید. زیرا اگر زن خوبی گیرتان آمد بسیار خوشبخت خواهید شد و اگر گرفتار یک همسر بد شوید فیلسوف بزرگی خواهید شد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5486,'ازدواج کردن و ازدواج نکردن هر دو موجب پشیمانی است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5487,'من داناترین انسان ها هستم، زیرا یک چیز می دانم که دیگران نمی دانند، و آن این که هیچ چیز نمی دانم.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5488,'در دوستی درنگ کن، اما وقتی دوست شدی ثابت قدم و پایدار باش.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5489,'دانش حقیقی این است که همه بدانیم که نادانیم.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5490,'زندگی بدون تحقیق و جستار ارزش زیستن ندارد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5491,'آنگونه باش که می خواهی به نظر برسی.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5492,'باید بخوری تا زنده باشی، نه آنکه زنده باشی تا بخوری.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5493,'نمی توانم چیزی به دیگران بیاموزم؛ فقط می توانم وادارشان کنم که بیندیشند.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5494,'ادب و حکمت را شعار خود ساز تا بهترین اهل زمان شوی و به نیکان بپیوندی.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5495,'ازدواج کنید؛ به هر وسیله ای که می توانید؛ اگر زن خوب داشته باشید بسیار خوشبخت خواهید شد و اگر گرفتار زن بدی شوید؛ فیلسوف از آب در می آیید و این هر دو برای هر مردی خوب است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5496,'آن انسانی عاقل تر است که می داند عقلش کمتر است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5497,'آن قدر بر مال دنیا حریص مباش که برای از دست دادنش اندوهناک شوی.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5498,'انتقام، دلیل سبکی عقل و پستی روح است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5499,'اثر حکمت آنگاه در شخص حکیم پدیدار شود که خویشتن را حقیر و ناچیز شمارد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5500,'هیچ گنجی به از هنر نیست و هیچ هنری بزرگوارتر از دانش نیست و هیچ پیرایه ای بهتر از شرم نیست و هیچ دشمنی بدتر از خوی بد نیست.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5501,'نخستین گام برای از میان برداشتن یک ملت، پاک کردن حافظه ی آن است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5502,'داروی خشم، خاموشی است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5503,'اندیشیدن به سرانجام هر کار باعث رستگاری است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5504,'خدمت خود را بی آنکه برای پاداش باشد به مردم عرضه کنید و پاداش آن را در رضایت خاطر خویش بیابید.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5505,'ممکن است مقداری زهر در کوزه ای ریخت و آن کوزه را شست و اثرات زهر را از بین برد، ولی چیزی را که در ذهنتان جای دارد، خوب یا بد، بر شما فرمانروایی خواهد کرد و هرگز از آن رهایی نتوانید یافت.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5506,'با پدر و مادرت چنان رفتار کن که از فرزندان خود توقع داری.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5507,'خشم و دشمنی را به درگاه مردان با اراده راهی نیست.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5508,'یک زندگی مطالعه نشده، ارزش زیستن ندارد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5509,'زندگی کوتاه است و فانی، اما هنر طولانی و ماندگار.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5510,'جامعه زمانی حکمت و سعادت می یابد که مطالعه، کار روزانه اش باشد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5511,'از مرگ نترسید، زیرا تلخی آن به دلیل ترس از آن است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5512,'فردی که در امری بزرگ جان می سپارد، مثل کسی است که تازه زخم برداشته و در گرمی گیر و دار، جراحت خویشتن را احساس نمی کند. پس کسانی که همت خود را وقف راه خیر می کنند از زحمت و آزار مرگ به دورند و شیرین ترین مرگها متعلق به کسانی است که کارهای بزرگ از آنان سر زده و آرزوهای بزرگ آنان بر آورده شده است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5513,'مرگ ترس ندارد، زیرا خوابی آرام است که خیالات آشفته در آن وجود ندارد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5514,'در لذتی که آمیخته به فساد است، خوشحال نباشید و [ به این ] فکر کنید که لذت نمی ماند و فساد می ماند.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5515,'با عاقل مشورت کن، چون فکر او به دور از هوای نفسانی است و با نادان مشورت مکن، زیرا که او تابع هوای نفس باشد. مشورت مکن با آن که محاط زمان است، بلکه مشورت کن با آن که محیط به زمان باشد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5516,'لازمه قضاوت، شکیبایی به هنگام شنیدن، اندیشیدن به هنگام گفتن، بینش به هنگام رسیدگی و بیطرفی به هنگام قضاوت است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5517,'مرد کامل آن است که دشمنان از او در امان باشند، نه آنکه دوستان از او بهراسند.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5518,'گریز از مرگ، دشوار نیست، گریز از بدی دشوار است، زیرا بدی تندتر از مرگ می رود.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5519,'مردی نیک بخت است که از هر کار نادرستی که از او سر بزند، تجربه ای تازه به دست آورد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5520,'شیرین‌ترین مرگ‌ها از آنِ کسانی است که کارهای بزگ از آنها سرزده و آرزوهای بزرگ آنها برآورده شده است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5521,'از مرگ نترسید که تلخی آن، از ترس از آن است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5522,'هیچ کس نمی‌داند، شاید مرگی که از او چون دشمنی سخت و زیانکار می‌گریزند، براستی، ره‌آوردی بزرگ است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5523,'تا زمانی که انسان زنده است، فهم این نکته برای او دشوار است که برای جاویدان شدن بایستی بمیرد؛ پس از آن مرگ نباشد.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5524,'سرایندگان، یگانه مفسران [ =بیان کنندگان ] خدایان هستند.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5525,'تنها خوبی موجود در جهان، شناخت و دانش؛ و تنها شر و زشتی، نادانی است.((سقراط))',78,0,0,0,0,0);
INSERT INTO mysms VALUES(5526,'برای شخصی که نمی‌داند به کجا می‌خواهد برود، باد موافق [=سازوار] وجود ندارد.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5527,'در زندگی، به اندازه‌ی کافی زمان هست؛ اگر از زمان خود، به گونه‌ای کارآمد برای انجام کاری بزرگ بهره بگیرید. ولی اگر زمان را برای بی‌بند و باری و تنبلی هدر دادید و برای چیز باارزشی زندگی نکردید، متوجه می‌شوید که بسیار دیر کرده‌اید.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5528,'اگر خردمندی، با این محدودیت به من پیشنهاد می‌شد که باید آن را سربسته نزد خودم نگه می‌داشتم و با دیگران در میان نمی‌گذاشتم، این هدیه را نمی‌پذیرفتم.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5529,'هیچ‌کس تا به حال بر اثر بخت و اقبال، خردمند نشده است.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5530,'فقیر آن نیست که کم دارد، بلکه آن است که بیشتر خواهد.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5531,'اگر نمی دانید بپرسید؛ لحظه ای نادان به نظر می رسید ولی تا پایان عمر دانا خواهید بود.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5532,'درنگ، بهترین درمان خشم است.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5533,'کسی که تنها به فکر خودش باشد و بخواهد همه چیز را به سود خودش تمام کند، نمی تواند خوشبخت باشد. اگر می خواهید به سود خود کار کنید باید به دیگران سود برسانید.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5534,'پس خیر شخصی خود را نگاه خواهی داشت، آنگاه که ببینی خوشبختان ناشادترینِ همگان هستند.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5535,'بهترین روش درمان خشم، به تاخیر انداختن است.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5536,'تا هنگامی که زنده هستی، یاد بگیر که چگونه زندگی کنی.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5537,'بخشاینده باش تا در حق تو بخشایش کنند.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5538,'کسانی به خرد دست یافته‌اند که گمان نمی‌برده‌اند دارای آن هستند.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5539,'به جای آنکه چندین کتاب بخوانی، کتابهای خوب را چندین بار بخوان.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5540,'کسی که از آنچه دارد راضی نیست، اگر صاحب تمام دنیا هم بشود، باز ناراضی و بینواست.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5541,'خاک، با چشم پوشی از بی نیازی آن، تا چیزی در آن کاشته نشود، پرورنده نخواهد بود.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5542,'چه موهبت بزرگی است، داشتن یک دوست با قلبی رازدار؛ چرا که می‌توانید با خیال آسوده، همه‌ی اسرار خود را در قلب او دفن کنید.((سنکا))',79,0,0,0,0,0);
INSERT INTO mysms VALUES(5543,'انجام کارهای بزرگ تنها بوسیله ی رأی و اراده و مشیت و شور و قدرت و اختیار و آمریت و عقل پخته و رسایی که زاده ی پیری و تجربت است به مرحله ی اجرا درمی آید.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5544,'اعتراف به ندانستن چیزی که نمی دانم باعث خجالت و شرم من نمی شود.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5545,'اعتراف به نادانی نسبت به چیزی که نمی دانم، مرا شرمگین نمی سازد.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5546,'دوستان حقیقی اگر از دیده بروند از دل نخواهند رفت.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5547,'جنگ، کشتارگاه کسانی است که همدیگر را نمی شناسند، به سود کسانی است که یکدیگر را می شناسند، اما همدیگر را نمی کشند؛ جنگ قوانین را خاموش می کند.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5548,'احمق هیچ گاه سعادتمند نمی شود.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5549,'اجر نیکی شما در عمل شما نهفته است.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5550,'برای آنکه عمر طولانی باشد، باید آهسته زندگی کنیم.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5551,'آنچه را که با خوشی و لذت آموخته ایم، هرگز فراموش نمی کنیم.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5552,'نفرت همان خشم و غضب است که روی هم انباشته شده است.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5553,'هرگز نمی توان با آدمهای کوچک کارهای بزرگ انجام داد.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5554,'پاکان و نیکان، خوبی را تنها برای خود نمی خواهند و از خودپرستی گریزانند. آنها می خواهند و می کوشند که نیکی سراسر جهان را فرا گیرد؛ یعنی همه را دوست می دارند.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5555,'هرگز به بزرگی نمی رسی، اگر در آن راه زحمت بسیار نکشی.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5556,'مرد بزرگ کسی است که در سینه خود قلبی کودکانه داشته باشد.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5557,'بسا کسان که در زندگی مورد نفرت و بیزاری بودند، ولی پس از مرگ، پیشوای اهل دل و محبوب [=دوست داشته شده‌ی] همگان شدند.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5558,'ناچیز شمردن درد و مرگ، نخستین وظیفه ی مرد است.((سیسرون))',80,0,0,0,0,0);
INSERT INTO mysms VALUES(5559,'خشم، شما را خوار می‌کند، در حالی که بخشایش، شما را وادار می‌کند تا فراتر از آنچه هستید، رشد کنید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5560,'در زندگی، هر چیزی را درست همان گونه که بخواهید، به دست می‌آورید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5561,'پابرجا ماندن در درستی و راستی، به معنای [ =چَم ] دور ماندن از بی خبری و نادانی است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5562,'هدف پایانی راه زندگی، رسیدن به خرد است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5563,'خرد، بالاترین و ژرفترین درجه ی دانش، بینش و فهم است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5564,'خرد، کیفیتی نیست که باید به آن رسید، بلکه کیفیتی است که باید به یاد آورده شود.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5565,'دگرگون کردن هیچ چیز آسان نیست و به هنگام گذر از این فرایند، باید با خودتان مهربان و شکیبا باشید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5566,'هیچ بخشی از زندگی نیست که درس نباشد. اگر زنده هستید، درسهایتان را نیز باید بیاموزید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5567,'کسانی که منش بزرگی دارند، گامهای گذر زندگی را آسوده تر بر می دارند. این افراد از درون می درخشند؛ درخششی که از چشم دیگران پنهان نمی ماند.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5568,'آزمایش های ناکام نیز به همان اندازه ی آزمایش های کامیاب، بخشی از فرآیند رشد هستند.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5569,'به جای اینکه کار نادرست خودتان را شکست و کار نادرست دیگران را ناچیز شمارید، می توانید آنها را به چشم فرصتی برای آموزش بنگرید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5570,'هر زمان که نتوانید به خواسته ای برسید، بدانید که فرصتی برای آموختن نکته ای مهم از اندیشه ها و رفتارتان به دست آورده اید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5571,'گاهی برای پاس داشتن خودمان، لازم است که در برابر بازدارنده های احساسی، خونسردی خود را پاس بداریم.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5572,'داوری ها همیشه منفی نیستند.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5573,'داوری، همان چیزی است که ما را از گشتن در پیرامون چیزی همانند پرده ی باز، باز می دارد؛ پرده ای که بر روی هر گونه داده ای که می خواهید با آن ارتباط برقرار کنید، باز است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5574,'تنها پادزهر داوری سخت، مهربانی است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5575,'ایستادگی در رنجش و پروراندن رویای انتقام در سر، تنها، باقی ماندن در دام نقش قربانی است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5576,'تنها با بخشیدن [دیگران] است که می توان کنش نادرست را فراموش کرد و پرونده را بست.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5577,'راستی و درستی، بسیار مهم است؛ چون بدون آن، با احساس تقسیم شدن در خودمان، زندگی می کنیم؛ احساس ناتمام بودن و در تعارض بودن با خودمان می کنیم.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5578,'خندیدن با کسی دیگر، معجزه آفرین است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5579,'درسها آنقدر تکرار می شوند تا آموخته شوند.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5580,'تنها راه رهایی از دست الگوها و مسائل دشواری که به تکرار آن گرایش دارید، جابه جایی نقطه نظرها و توجه به الگوها و یاد گرفتن آموختنی هایشان است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5581,'شما زمانی به آگاهی می رسید که دست از تکرار درسها [ی زندگی] بردارید و بتوانید به سمت دروس تازه تر گام بردارید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5582,'خنده، بدبختی را از بین می برد. خنده به انسان می آموزد که حتی در سخت ترین شرایط، خونسرد بماند و وجود خود را چندان جدی نگیرد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5583,'دگرگونی، همیشه آسان نیست.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5584,'نمی توانید درسی را بیاموزید، مگر اینکه از آن آگاه شوید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5585,'اگر به راستی می خواهیم دگرگون شویم، باید دگرگونی را برگزینیم و به انجام دگرگونی، پایبند باشیم.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5586,'هیچگاه نمی توانید تمام دروس خود را به پایان برسانید؛ چون تا زمانی که زنده اید، همیشه درسی برای آموختن وجود دارد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5587,'هرچه بر خردتان افزوده می شود، به همان نسبت، گنجایش تان نیز گسترش می یابد و به شما اجازه ی ادامه و رفع آسان تر مبارزات بزرگتر را می دهد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5588,'زندگی، مدرسه ای سالیانه است که هیچ گاه از آن، دانش آموخته نخواهید شد. از این رو فرایندی آموزشی است که ارزش راستین را به هستی می بخشد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5589,'شاگرد بودن، از مقام تان نمی کاهد. به راستی، شاگرد بودن در دنیا، توانایی هایی به شما می دهد که برای افرادی که آماده ی پذیرش این نقش نیستند، نادیدنی است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5590,'انسان فروتن، کسی است که در فضیلت خود، هم دارای باور است و هم فروتنی دارد، ولی همچنین، توانایی درک محدودیت هر یک را نیز دارا است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5591,'الگوها با گذشت زمان دگرگون می شوند، شما هم بایستی دگرگون شوید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5592,'پذیرش دگرگونی، به شما این اجازه را می دهد که به جای کورکورانه رد شدن، آماده ی هر پیچ و خمی باشید که زندگی برایتان پیش می آورد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5593,'هنگامی که به آنچه نداریم و می خواهیم داشته باشیم، می اندیشیم، از آْنچه داریم، بی خبر می مانیم.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5594,'اگر به آنچه داریم، بها ندهیم، ارزش داشته هایمان را دست کم می گیریم.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5595,'در هر دشواری، به جنبه های مثبت آن توجه کنید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5596,'زمانی به روشنی می بینید که بتوانید چشم انداز خود را دگرگون کنید و شرایط را در روشنایی تازه ای ببینید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5597,'بهترین راه رسیدن به روشنی، توجه کردن به زمانهایی است که آن را تجربه نمی کنید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5598,'هنگامی که در امر پشتیبانی از دیگران کوتاهی می کنید، بیشتر نشانه ی آن است که پشتیبانی از خویش را رها می سازید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5599,'هنگامی که می بینید نمی توانید از کسی پشتیبانی کنید، به درون خود بنگرید، شاید آنجا چیزی هست که از آن، پشتیبانی نمی کنید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5600,'این سرچشمه های بیرون از ما نیستند که کامیابی یا شکست ما را رقم می زنند، بلکه باور ما به خودمان است که زندگی مان را آن گونه که می خواهیم، شکل می دهد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5601,'آگاهی، نخستین گام برای فراهم آوردن زمینه ی دگرگونی هایی است که آرزو دارید در خودتان به وجود آورید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5602,'هنگامی که سر رشته ی زندگی تان را در دست خود بگیرید، دنیا پاسخ مثبت می دهد و هوش سرشار، برافروخته می گردد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5603,'به یاد داشته باشید که زندگی، فرصت های بسیاری برای بهره گیری در دسترس تان قرار می دهد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5604,'رها کردن و دست کشیدن را زمانی فرا می گیرید که این کار را آگاهانه و نه به عنوان یک کنش انفعالی، برای گریز از مسئولیت انجام دهید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5605,'دست برنداشتن از آثار برجای مانده از پیشامدهای ناخوشایند، بیش از خود پیشامد، به شخص زیان می رساند.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5606,'انسان چنان آفریده شده که می تواند اگر بخواهد و اراده کند، بدترین تجربه ها را از یاد ببرد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5607,'هنگامی که بدانید چه موانعی بر سر راهتان قرار دارند و سپس، خواهان رها کردن آنها باشید، به جهان هستی پیام می دهید که می خواهید به آن زندگی که شایسته ی آن هستید، دست یابید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5608,'ترس ها را به کنار بگذارید تا درس دلیری را بیاموزید وزندگی تان را به گونه ای که آرزو دارید، بیافرینید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5609,'زندگی بدون ماجرا، شاید امن و بی خطر باشد، ولی این نوع زندگی، نه بافت دارد و نه رنگ.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5610,'شما خود به تنهایی، مدبرترین آموزگار خود هستید. در ژرفای وجودتان، آنچه را که باید بدانید، می دانید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5611,'زندگی مان، زمانی صاف و هموارتر می شود که به پیام های درون خود توجه کنیم.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5612,'تنها کاری که باید انجام دهید، این است که به درون خود بنگرید، گوش فرا دهید و به خود باور داشته باشید. در بیرون، سرچشمه ی فراستی وجود ندارد که بتواند به پرسش های شما پاسخ دهد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5613,'خرد، با هوش تفاوت دارد. خرد، کاری به بهره ی هوشی شما و یا اینکه در مدرسه چه می کرده اید، ندارد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5614,'خرد، جایی است که در آن، ژرفترین نقطه ی درک شما با تجربه های روزانه، هم کوشی پیدا می کند.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5615,'زیبایی راستین خرد در آن است که زمانی که آن را به یاد می آورید، آن را منتقل می سازید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5616,'هرچه بیشتر به مردم، عشق بدهید، عشق بیشتری دریافت می کنید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5617,'هر بار که دیگران را از خرد خود بهره مند می سازید، گنجایش تان برای خرد بیشتر افزایش می یابد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5618,'کسانی که به خردمند بودن، نام آورند، کسانی هستند که به رشد دیگران کمک کرده اند.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5619,'هیچ کاری نیست که نتوانید انجام دهید، جایی نیست که نتوانید به آن برسید و چیزی نیست که نتوانید داشته باشید؛ همه در دسترس شما است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5620,'هر درسی که در زندگی تان بیاموزید، درها را به روی احساس بی پایان تان می گشاید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5621,'رشد و پیشرفت شما بستگی به این دارد که از درسهایی که آموخته اید، كِی و چگونه بهره بگیرید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5622,'تنها نتیجه ی پایداری در برابر درسها این است که آنقدر تکرار می شوند تا آنها را بیاموزید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5623,'هنگامی که درسی را آموختید، به سادگی به دیگران منتقل می شود و شما به سراغ درسهای دشوارتر و در ضمن، جالب تر پس از آن می روید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5624,'دیرور به تاریخ پیوست، از فردا هم چیزی مشخص نیست، ولی امروز، زمانی است که باید آن را دریافت.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5625,'با لحظه ی اکنون همراه باشید و به آنچه تجربه می کنید، توجه داشته باشید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5626,'هنگامی که درسهای زندگی را می پذیرید، هرچند هم که دروسی ناخواسته و مبارز طلب باشند، تازه نخستین گام بنیادی را به سوی یافتن خودِ راستین و هدف از زندگی برداشته اید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5627,'درس زندگی هر کسی همیشه برای خودش، آشکار و روشن است؛ بستگی به این دارد که شخص در آن زمان چه لنزی بر چشم گذاشته باشد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5628,'گاهی درسهایی با جذابیت کمتر که چه بسا مورد پسندتان هم نباشد، در عمل می توانند نقش دریافت بزرگترین هدایا را برای شما داشته باشند.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5629,'بنیاد زندگی، هم خط شدن با راهتان است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5630,'هر کس، هدف و راه جداگانه ی خود را دارد؛ راهی یگانه و جدا از راه دیگران.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5631,'هر کنش و یا پیشامدی، درسی به همراه دارد که باید آموخته شود.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5632,'هیچ شیء، محبت، عشق یا توجه نمی تواند خلاء درون انسان را پر کند. این خلاء را تنها می توان با نگاه به درون پر کرد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5633,'ارج نهادن به نفس، چیزی نیست که آن را به دست آورید و سپس تا ابد از آن نگاهبانی کنید، بلکه فرایندی است که همیشه نیاز به جستجو و پرورش و کار دارد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5634,'سرگرمی، همانند روغنی است که حرکت موتور زندگی را به نرمی نگاه می دارد. بدون روغن، دنده ها گیر می کنند و از کار می افتند.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5635,'برای شما چه چیز لذت آور است؟ آن را انجام دهید، پی در پی انجام دهید، چون قلبتان را سبک می کند و معجزه گر وجودتان می شود.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5636,'زندگی در زمان اکنون، فراهم آورنده ی چیزی است که بیشتر مردم برای رسیدن به آن تلاش می کنند و چیزی جز آرامش نیست.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5637,'اگر از وابستگی به آنچه می خواهیم در زندگی مان رخ دهد، دست برنداریم، بی شک از زندگی لذت نخواهیم برد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5638,'ثروتمند شدن یا زناشویی، هیچگاه تضمینی [ =پایندانی ] برای رهایی از نگرانی نیست. حتی ممکن است از میزان آسودگی شما بکاهد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5639,'بیدار هدایای جهان هستی باشید، زیرا گاه، شکل های غیر منتظره [ =دور از چشم داشت ] را می نمایانند.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5640,'زندگی برای هر کس معنایی [ =چَمی ] دیگرگون از دیگری دارد.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5641,'زندگی، شما را با درسهای بی شماری رو در رو قرار می دهد؛ درسهایی که از هیچ کدام سود نخواهید برد، مگر آنکه آنها را بشناسید و پذیرای ارزشهای ذاتی شان باشید.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5642,'هر انسانی در روی زمین بایستی درسهای مقرر خود را بیاموزد؛ درسهایی که جدا و منفرد از درسهای دیگران است.((شری کارتر اسکات))',81,0,0,0,0,0);
INSERT INTO mysms VALUES(5643,'هر که شایستگی دریافت زیبایی را داشته باشد، هرگز پیر نخواهد شد.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5644,'از یک نقطه مشخص، دیگر راهی برای بازگشت وجود ندارد؛ به این نقطه مشخص می توان دست یافت.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5645,'در جدال با دنیا، یار دنیا باش.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5646,'آنچه را آفریده ام، فقط ثمره تنهایی است.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5647,'امکانات برای من وجود دارد، اما در کدام خراب آبادی پنهان شده اند؟((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5648,'من درد انتظار را حس نمی کنم، از این رو وقت شناس نیستم و مانند یک گوساله چشم به راه می مانم.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5649,'کتاب باید تبری برای درهم شکستن دریای منجمد وجودمان باشد.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5650,'من فکر می کنم آدم باید وقت خود را تنها صرف خواندن کتاب هایی نماید که با چنگ و دندان دل خواننده را ریش می کنند.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5651,'قدرت فریادها به اندازه ای است که می تواند خشونت فرمانهای صادر شده بر ضد انسان را درهم بشکند.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5652,'جایی که اعتماد نیست، سخن بیهوده است.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5653,'رسیدن به قله پیروزی آسان است، به شرط اینکه راه رسیدن به قله را بشناسی و توانش را هم داشته باشی.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5654,'شعر، پویشی جاودانه در جستجوی حقیقت است.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5655,'عشق، خودرو بی نقصی است؛ راننده و سرنشین ناقص اند و راه، ناهموار.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5656,'هراس، تیره بختی است، اما شهامت را نمی توان نیکبختی دانست؛ نهراسیدن سعادت است.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5657,'آفریدگار اگر کمی عاقلانه رفتار می نمود، کافکا را در قاره آسیا خلق می کرد تا میلیونها انسان سخنانش را آویزه گوش قرار داده و تا ابد در احوالش به اندیشه و پژوهش بپردازند.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5658,'دوست، پیوندی است پدر و پسر را؛ او مهمترین نقطه ی عطف آنها است.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5659,'لازم نیست گوش کنید، فقط منتظر شوید. حتی لازم نیست منتظر شوید، فقط بیاموزید که آرام و ساکن و تنها باشید. جهان آزادانه خود را به شما پیشکش خواهد کرد تا نقاب از چهره اش بردارید. انتخاب دیگری ندارد؛ مسرور گِرد پای شما خواهد گشت.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5660,'عمری چکش برداشتم و بر سر میخی که روی سنگ بود کوبیدم. اکنون می فهمم که هم چکش خودم بودم، هم میخ و هم سنگ.((فرانتس کافکا))',82,0,0,0,0,0);
INSERT INTO mysms VALUES(5661,'انسان در همان لحظه که تصمیم می گیرد آزاد باشد، آزاد است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5662,'ما کودکانی هستیم که چون به خواب می رویم رویای سعادت می بینیم و چون بیدار می شویم تعبیری جز تیره روزی نمی یابیم.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5663,'بیاموز که باغ خودت را بکاری.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5664,'از روی سوالهای فرد، بهتر از جوابهای او می توانید درباره اش قضاوت کنید.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5665,'شک حالت خوش آیندی نیست، ولی ادعای یقین خنده دار است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5666,'در این دنیا هیچ کس نمی داند که در پشت پرده ظاهری حوادث چه می گذرد. امروز به خوبی بر من ثابت شده که درباره هیچ چیز نباید از روی اسناد و ظواهر قضاوت کرد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5667,'تصور می کنم که اگر هر کس تنها یک ربع ساعت به فکر زندگی خویش باشد و بیاندیشد که آن را اصلاح کند، هر ماه زندگی او از ماه پیش بهتر خواهد شد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5668,'فرصت بدی کردن در هر روز صدها بار به دست می آید و فرصت خوبی کردن در هر سال، یکبار.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5669,'پیوند عشق حقیقی حتی به مرگ گسیخته نمی شود چه برسد به دوری.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5670,'حقیقت را دوست بدار، ولی اشتباه را عفو کن.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5671,'اگر بر ناتوان خشمگین شوی، دلیل بر این است که قوی نیستی.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5672,'چرب زبانان اغلب نیتی ناپاک در قلب می پرورانند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5673,'خطاهای بشری اول رهگذر، بعد میهمان و چندی نمی گذرد که صاحبخانه می شوند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5674,'زندگی مانند کودکی است که اگر می خواهید به خواب نرود، پیوسته باید او را سرگرم کرد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5675,'تصور می کنم که اگر کسی یک ربع ساعت تنها به فکر زندگی خود باشد و بیندیشد که آن را اصلاح کند، هر ماه از زندگی او بهتر از ماه قبل خواهد شد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5676,'در بازی زندگی، انسان در ابتدا گول خور است و در پایان گول زن. به عبارت دیگر، او بره به دنیا می آید و روباه از دنیا می رود.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5677,'هیچ خدمتی صادقانه تر از خدمت عاشق به معشوق نیست.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5678,'بگذار که دوستی کم کم به اوج خود برسد. اگر این رسیدن برق آسا باشد، ناگهان از نفس می افتد و متوقف می شود.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5679,'ما باید در زندگی دارای هدف عالی بوده و افق بالاتری را بنگریم زیرا بدون هدف زیستن ثمره ای جز خستگی ندارد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5680,'عشق، نقاشی رنگ و روغنی است که طبیعت، آن را تدارک می‌بیند و ذهن، به آن شاخ و برگ می‌دهد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5681,'صفت، دشمن اسم است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5682,'زر اندوزانی که برای مال دنیا کیسه دوخته اند، بدانند که لباس آخرت جیب ندارد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5683,'در جامعه ای که کارها برحسب استعداد تقسیم نشود، در حقیقت همه بیکارند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5684,'از بین بردن یک نظریه رایج عمومی، نیازمند [ گذشت ] قرنها است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5685,'کار و کوشش ما را از سه عیب دور می دارد: افسردگی، دزدی و نیازمندی.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5686,'هر کس باید باغ خود را پرورش دهد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5687,'هر چه افراد ازدواج کرده بیشتر شوند، جنایتها کمتر خواهد شد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5688,'ازدواج مجموعه ای ازمزه هاست؛ هم تلخی و شوری دارد و هم تندی و ترشی و شیرینی و بیمزگی.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5689,'پرسش های هر کس بیش از جواب هایش او را می شناساند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5690,'سرنوشت یک ملت غالبا بستگی به خوب کار کردن و یا خوب کار نکردن دستگاه گوارش نخست وزیران ملت داشته است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5691,'آدمی هرگز بدکار و پلید به دنیا نمی آید.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5692,'بر سر دوراهی های زندگی گاهی اتفاق می افتد که عاطفه و وظیفه با هم تماس پیدا می کنند؛ کسانی که از عاطفه پیروی کنند، همیشه از خود شادمانند، اما مردمی که در این گونه از مراحل، تبعیت عقل و وجدان را ترجیح داده و انجام وظیفه نمایند، غالبأ از کرده ی خود پشیمان هستند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5693,'دو چیز برای من به معنای زندگی است: آزادی و بانویی که او را دوست دارم.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5694,'سعادت جز رؤیا چیز دیگری نیست، فقط به نظر حقیقت دارد؛ چه وقتی انسان می خواهد میزان زندگی را حساب کند نباید اندازه ی لذتها را بسنجد بلکه باید رنجهایی را که از آنها رهایی یافته است نیز در نظر بگیرد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5695,'ادب، چیزی غیر از گفتار و رفتار توام با مهربانی نیست.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5696,'زنها مثل ماهی هستند؛ به دست آوردن آنها آسان و نگهداشتن آنها مشکل است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5697,'کسانی که می توانند حرفهای پوچ و بی معنی را در باور تو بگنجانند، می توانند تو را مجبور به انجام کارهای وحشیانه کنند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5698,'از کشوری که بهای خدمتکاران و دانشمندان را نداند و از آنان سپاسگزاری ننماید، نباید امید پیشرفت داشت.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5699,'گل را می توان زیر پا له کرد، ولی بوی عطر آنرا نمی توان در فضا كُشت.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5700,'کسی که آرزوهایش سودمند باشد، ثروتمند است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5701,'وقتی که حرف پول پیش می آید، همه پیرو یک کیش و آیین می شوند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5702,'همه ما در یک مذهب مشترکیم و آن، ثروت است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5703,'آنهایی که در زبان بازی مهارت به خرج می دهند، بیشتر، نیت پلید را در قلب خود می پرورانند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5704,'هر چه بیشتر فکر می کنم بهتر می فهمم که هیچ نمی دانم.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5705,'شما ممکن است بتوانید گلی را زیر پا لگد کنید، اما محال است بتوانید عطر آن را در فضا محو سازید.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5706,'آدم باید اول انسان باشد، بعد دکتر.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5707,'پاپ می میرد، اما دستگاه او پابرجاست.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5708,'تصادف، کلمه ای بی معنی است؛ هیچ چیز بدون دلیل به وجود نمی آید.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5709,'جنگ تهاجمی، جنگ وحشی ها است؛ آن که از خود دفاع می کند، سزاوار آن است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5710,'دوستی، ازدواج دو روح است و در آن، ترک کردن و طلاق جایز نیست.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5711,'زمان، همه زخمها را بهبود می بخشد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5712,'پارسایی و پرهیزگاری در قلب نشسته است، نه جای دیگر.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5713,'سرزمینی در جهان نیست که [ در آن ] عشق از عاشقان، شاعر نسازد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5714,'کتابهای مفید، آنهایی هستند که خواننده را تشویق به تکمیل آن کنند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5715,'ما فقیر هستیم، اما خوش سلیقه ایم.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5716,'یکی از دلایل نوینی که موجب فقر ما است، نیازهای جدید ما است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5717,'آخرین درجه فساد، به کار بردن قوانین برای ستم است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5718,'آن که امید دارد هر چهار فصل سال بهار باشد، نه خود را می شناسد، نه طبیعت را و نه زندگی را.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5719,'آیا در دنیا کسی آنقدر عاقل است که از تجارب دیگران عبرت گیرد؟((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5720,'اختیار افکار ما نیز در دست خودمان نیست، زیرا کمابیش همیشه بی اختیار فکر می کنیم.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5721,'افرادی که بزرگترین خدمت را به دانش و فرهنگ نموده اند، نویسندگان و پژوهشگرانی بوده اند که در انزوا می زیسته اند و هرگز در گفتگوهای دانشگاهی شرکت نکرده و حقایق صد در صد اثبات نشده را در آکادمی ها ابراز نداشته اند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5722,'برگهای کتاب به منزله بالهایی هستند که روح ما را به جهان روشنایی پرواز می دهند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5723,'بهترین راه برای جلب نیکی، رفع بدی است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5724,'تاریخ تنها تصویر جنایتها و تیره بختی های بشر است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5725,'تمام هنرها برادر یکدیگرند؛ هریک از هنرها بر هنرهای دیگر روشنایی می افکند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5726,'تمام مردم آزادی را دوست می دارند، اما مهارت عجیبی در نابود کردن آن به کار می برند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5727,'حقایق را بگویید و مردم را آگاه سازید، هرچند مطمئن باشید که کشته خواهید شد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5728,'حکومت کشوری که در آن گدایی به صورت حرفه در آید، بد و فاسد است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5729,'خانم! زیبایی شما تنها چشم را لذت می بخشد، اما زیبایی اخلاق و فهم شما، روح را نوازش می دهد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5730,'خدا امید و خواب را برای جبران غم های زندگی به ما ارزانی داشته است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5731,'خوشبختی در دنیا وجود ندارد. زندگی ما عبارت است از محاکمه یا تنبیه کردن، پاداش گرفتن و یا انجام اعمال احتیاطی.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5732,'خوشبختی کالایی است که طبیعت به قیمت بسیار گران، آن را به ما می فروشد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5733,'دوست مثل درشکه در روز بارانی کمیاب است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5734,'سعادت مثل هر فضیلتی از دو چیز به وجود می آید، صرفه جویی و کوشش.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5735,'سعادت وکامیابی در زندگانی تابع رویدادها نیست، بلکه از خود ما سرچشمه می گیرد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5736,'عشق، نیرومند ترین سپاه است، زیرا در یک لحظه بر قلب و مغز و جسم حمله می کند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5737,'عشق ابزاری است که تمام دردسرهای کوچک را به یک دردسر بزرگ تبدیل می کند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5738,'عملی اگر کاشتید، عادتی درو خواهید کرد. عادتی اگر کاشتید، اخلاقی درو خواهید کرد. اخلاقی اگر کاشتید، سرنوشتی درو خواهید کرد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5739,'قلم روزنامه نگار مانند تیشه ای است که نمی شود آن را شکست؛ این تیشه بدین جهت ساخته شده که دنیای قدیم را ویران کند و دنیای تازه ای به وجود آورد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5740,'کتاب بر دنیای تمدن فرمانروایی می کند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5741,'کسانی که از حیث استعداد و طبیعت، مافوق دیگران هستند، غالبأ به خطا و گناه نزدیکترند، زیرا هیچ دلیلی ندارد که استعداد، اشخاص را مافوق یکدیگر قرار دهد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5742,'سرگرم نبودن با زنده نبودن یکی است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5743,'من دشمن تو و باورهای تو هستم، اما آماده ام در راه آزادی باورهایت، جان خود را فدا کنم.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5744,'نیش های چند مگس هرگز اسب چابک را از تاختن باز نمی دارد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5745,'زمانی یک جامعه دارای فضیلت اخلاقی است که قانون، همه افراد جامعه را با یک چوب براند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5746,'هر زمان احساس می کنم که درد و رنج و بیماری می خواهد مرا رنجه کند و آزار دهد، به کار پناه می برم. کار، بهترین درمان دردهای من است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5747,'بدبختانه همنوعان ما به گونه ای ساخته شده اند که افرادی که در راههای صاف و هموار راه می روند، پیوسته در مسیر آنهایی که می کوشند راه جدیدی را باز کنند، سنگ می اندازند.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5748,'اگر می خواهید [ انسان ] شایسته ای شوید، از هوای نفس [ خود ] بکاهید.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5749,'هر کس دو بار می میرد : یکبار آنگاه که عشق از دلش می رود و بار دیگر آنگاه که زندگی را بدرود می گوید. اما مرگ زندگی در برابر مرگ عشق ناچیز است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5750,'آنچه که در یک شب نمایش می آموزیم، از عهده یک کتاب بزرگ بیرون است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5751,'خرابی کار یک مملکت از دو چیز است : نداشتن مردان عالم و لایق و نبودن آنها بر سر کارهای مملکت.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5752,'کسی که از مرگ می ترسد، از زندگی هم می ترسد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5753,'برای آنکه بتوان خوب نوشت باید در یک کشور آزاد زندگی کرد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5754,'هر چه بیشتر کتاب بخوانید، بیشتر از زندگی حقیقی برخوردار خواهید شد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5755,'میوه ای که در دسترس ماست از میوه بالای شاخه درخت لذیذتر است، ولی میوه بالای درخت از این جهت در نظرمان جلوه می کند که دستمان به آن نمی رسد.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5756,'اگر بر ناتوان خشمگین شوی، دلیلش این است که قوی نیستی.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5757,'عشق، پارچه ی گلدوزی شده ای است که طبیعت، آن را آراسته و تخیل، نقش و نگار آن را ترسیم کرده است.((فرانسوا ولتر))',83,0,0,0,0,0);
INSERT INTO mysms VALUES(5758,'بد گمانی در افکار انسان مانند خفاش در میان پرندگان است که همیشه در سپیده دم یا هنگام غروب که نور و ظلمت به هم آمیخته است بال فشانی می کند.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5759,'بیان پرسشی بجا، یعنی به نیمه‌راه خردمندی رسیدن.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5760,'آدمی نمی تواند به طبیعت فرمان بدهد، مگر آنکه قوانین ابدی آن را بپذیرد. تنها با وجود چنین شناختی است که طبیعت فرمانبردار آدمی می گردد.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5761,'دانش یعنی قدرت.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5762,'ما باید برای خوشبخت زیستن، موقعیت های مناسب ایجاد کنیم، نه اینکه در انتظار آن باشیم.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5763,'شعر، قسمتی از دانش بشری است.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5764,'سرشت و بخت یک انسان در دستان خود اوست.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5765,'مطالعه اجمالی و جزیی در فلسفه، فرد را به تفکر و بی دینی وا می دارد، اما اگر به فلسفه عمیق بیندیشد او را به سمت دین متوجه می سازد.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5766,'انسانیت نیازمند حقیقت است.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5767,'بشر به طور معمول بر اساس سیرت خود فکر می کند و بر اساس دانش خود و افکار عمومی رایج سخن می گوید، اما عموماً بر مبنای عادت عمل می کند.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5768,'عطر گلها زمانی که در باغچه هستند، لذت بخش تر است، تا زمانی که آنها را می چینی.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5769,'دوست از دوست حق گله گذاری دارد. گله گذاری دلیل دوستی و علاقه به دوام روابط نیکو است.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5770,'گشادن عقده های درون در پیش دوستان دو تأثیر دارد؛ یکی آنکه شادی را دو برابر می کند و دیگر آنکه غم را دو نیم می سازد. زیرا آن کس که دوستان را در شادی خویش شریک می کند، سرور خاطرش بیشتر می شود و آن که غم دل به یاری همراه می گوید، بار اندوه خویش را سبک تر خواهد یافت.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5771,'دهان زشتگوی را باید با خاموشی وقار بست.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5772,'در طبیعت و اخلاق انسان هیچ ضعف و انحرافی نیست که با آموزش مناسب درست نشود.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5773,'بدتر از بیماری، وسیله ی مداوای آن است.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5774,'کتاب، سفینه ای است که اقیانوس بیکران زمان را درمی نوردد.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5775,'مطالعه، انسان را کامل می کند، سخنرانی به او حضور ذهن و سرعت انتقال می دهد، اما نوشتن، انسان را دقیق می کند.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5776,'در جهان هیچ عیبی آدمی را شرمگین تر از آن نمی کند که دیگران دروغش را کشف کنند.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5777,'پول مانند کود است؛ نیک است در صورتی که پراکنده باشد.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5778,'همه چشم امید به محبت دیگران دوخته اند و بیخبر از آنند که خود نیز می توانند عامل محبت برای دیگران باشند.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5779,'انسان دانا به جای آنکه در انتظار یک فرصت خوب در زندگی بنشیند، خود، آن را به وجود می آورد.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5780,'آنچه مردم را دانشمند می کند، مکاتبی که می خوانند نیست، بلکه چیزهایی است که یاد می گیرند.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5781,'خطاکارترین افراد کسانی هستند که عیب دیگران را می بینند.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5782,'برخی کتب را باید چشید، برخی دیگر را باید بلعید و تعداد محدودی را هم باید جوید و هضم کرد.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5783,'مرگ، این لطف را دارد که کاخ نیکنامی را بهره‌ی انسان می‌سازد و رشک و تنگ‌نظری کسان را نسبت به وی نابود می‌کند.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5784,'همان گونه که بچه‌ها از رفتن به تاریکی می‌ترسند، مردم نیز از مرگ هراسانند.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5785,'پروردگار، در آغاز یک باغچه آفرید و براستی، این باغچه ناب‌ترین شادمانی‌های انسان است.((فرانسیس بیکن))',84,0,0,0,0,0);
INSERT INTO mysms VALUES(5786,'کسی که بر جایگاه خویش منم زد بخت از وی روی بر خواهد تافت.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5787,'به این زمین گرد که به سرعت می گردد بنگر که درمان ما در آن است و درد ما نیز. نگاه کن و ببین که نه گردش زمانه آن را می فرساید و نه رنج و بهبودی حال بشر آن را به آتش می کشد، نه آرام می شود و نه همچون ما تباهی می پذیرد.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5788,'هنر برتر از گوهر آمد پدید.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5789,'نخستین مرحله ی شناخت آفرینش همانا خرد است؛ چشم و گوش و زبان سه نگهبان اویند که ناگزیر هر چه نیکی و شر است از همین سه ریشه می گیرد.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5790,'افسوس که جویندگان خرد اندکند. باید که به سخن دانندگان راه جست و باید جهان را کاوش نمود و از هر کسی دانشی آموخت و یک دم را هم برای آموختن نباید از دست داد.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5791,'دانایی توانایی به بار می آورد و دانش، دل پیران را جوان می سازد.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5792,'گوش شنونده همیشه در جست و جوی سخن خردمندانه و حکیمانه است.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5793,'خرد، برترین هدیه الهی است.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5794,'خرد و دانش مرد دانا در گفتار او آشکار است.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5795,'کسی که خرد ندارد، همواره از کرده های خویش پشیمان و در رنج است.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5796,'بی خردی، اسارت در پی دارد و خرد، موجب آزادی و رهایی است.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5797,'خرد مانند چشم هستی و جان آدمی است و اگر آن نباشد، چگونه جهان را به درستی خواهی گذراند.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5798,'خداوند درهای هنر را بر روی دانایان دادگر گشوده است.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5799,'کسی به فرجام زندگی آگاه نیست، خداوند هم نیازی به عبادت بنده ندارد.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5800,'دانش، ارزش آن را دارد که برای آن رنجها بکشی.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5801,'چراغ مایه راندن تاریکی است و بدی، جوهر تاریکی در زندگی آدمی است که باید از آن دوری جست.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5802,'بی خردی است که بگویم کسی بدی را بی دلیل انجام می دهد.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5803,'رنج منتهی به گنج را کسی خریدار نیست.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5804,'اگر برای انجام کاری بزرگ زمان نداری، بهتر است بی درنگ آن را به دیگران بسپاری.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5805,'کتاب زندگی گذشتگان، جان تاریک را روشنی می بخشد.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5806,'این جهان سراسر افسانه است و جز نیکی و بدی، چیزی باقی نیست.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5807,'کسی که به آبادانی می کوشد، جهان از او به نیکی یاد می کند.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5808,'آن که آفریننده و باشکوه است، در این جهان رنجهای بسیار خواهد دید.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5809,'جایگاه پرستش گران، کوهستان است.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5810,'آزادگان را کاهلی بنده می سازد.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5811,'فرمانروایانی که گوش به فرمان مردم دارند، در زندگی جز رامش و آوای نوش نخواهند شنید.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5812,'خسروی بزرگتر، بندگی بیشتر می خواهد.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5813,'ابلیس مانند نیکخواهان پیش می آید؛ ابتدا عهد و پیمان می گیرد، سپس راز می گوید.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5814,'آنگاه که هنر خوار می گردد، جادو ارجمند می شود.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5815,'جز مرگ را هیچ کسی از مادر نزاد.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5816,'از بزرگان تنها رنجهاست که باقی می ماند.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5817,'فرمان ایزد به جهانداران، عطا و بخشش است.((فردوسی))',85,0,0,0,0,0);
INSERT INTO mysms VALUES(5818,'تنها اثر بزرگ و کامل اثری است که هیچ وقت شخص نمی تواند تحقق آن را تصویر کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5819,'دور از ذهن ترین چیز در همه ی انسانها، بی اهمیت بودنشان در تمام عرصه هاست.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5820,'انسان درگیر عشق بی آنکه چیز خاصی از رویداد بداند می تواند پیروز باشد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5821,'نمی شود پیشاپیش هیچ رفتار ممتازی را نشان داد که پشت درهای بسته عملی نشود و نیز هیچ آرزوی عینی وجود ندارد که به راستيً بیهوده باشد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5822,'رویایی که غیر ممکن ها را به ما وعده می دهد، به همین سبب نیز از ما کناره می گیرد، اما رویاهایی که ممکن ها را به ما وعده می دهد، وارد زندگی می شود و تنها در این زندگی راه خود را می یابد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5823,'هرگونه تغییر در برنامه ی روزمره، روح انسان را آکنده از شادابی می کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5824,'هر کسی خود را در رویا برای لحظه ای سر کرده ی ارتش می پندارد که از نفربَرها گریخته است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5825,'همه ی انسان های کم قابلیت آرزو می کنند که شخصیت برتر و انسان مُهلک [ =نیست کننده ] بشوند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5826,'درک فوق العاده ضرورتی است تا در مواجهه با روزهای تیره بتوان ترس را حس کرد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5827,'آنچه کسی احساس می کند، هرگز نمی توان به درستی بازگو کرد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5828,'اگر مرا همتای خود می پندارید، پس بر بند کفاره ام بکشید. اگر به گونه ای دیگر [مرا] تصور نمی کنید، می خواهم که بر دارم زنید.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5829,'هر چیزی که روزی از آنِ ما بوده، حتا اگر به شکرانه ی همزیستی تصادفی یا دلبستگی های مشترک بوده باشد، به خود ما تبدیل می شود، برای این که از آنِ ما بوده است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5830,'همه ی چیزهایی که از دید ما ناپدید می شوند، در درون ما هم ناپدید می شوند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5831,'گربه جلو آفتاب پهن می شود و می خوابد. انسان درون زندگی با تمام پیچیدگی هایش پهن می شود و می خوابد. نه این و نه آن، خود را از قوانین سرنوشت ساز رها نمی سازد، تا آن گونه که هستند، باشند. هیچ کس تلاش نمی کند تا وزنه ی هستی را از جا بردارد. در جمع انسانها بزرگ ترین ها عاشق شهرت اند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5832,'تنهای تنها بودن چه خوب است؛ انسان می تواند با خود بلند بلند سخن بگوید و قدم بزند، بی آنکه نگاه ها مزاحم اش شوند، می تواند درون رویایی ناخواسته به پشتی تکیه دهد!((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5833,'زندگی روستایی حتا آنها را که توجهی بدان نمی کنند به خود جلب می کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5834,'تنها در عشق و درگیری ها است که حقیقت ما روشن می شود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5835,'آینه ای وجود ندارد که بتواند ما را به خود ما به هیات موجودی خارجی نشان دهد، چرا که آینه ای نیست که بتواند ما را از درونمان بیرون بکشد. پس روانی دیگر و نظمی دیگر برای نگریستن و اندیشیدن دیگر لازم است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5836,'زندگی بیش تر با یاد سرما ناآرام است تا با خود سرما.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5837,'آرامش تنها از آرامش نشات می گیرد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5838,'اگر مردم بستایند، بهتر از آن چه احساس می کنند، تاثیر می گذارد، چرا که در ستایش، خویشتن را فراموش می کنند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5839,'نوشتن بهتر از بی باکانه زیستن است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5840,'من در شناخت خویشتن خویش دریانوردم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5841,'این انسانها که همواره مرا در بر می گیرند، ارواحی هستند که مرا نمی شناسند و روز به روز با همنشینی و شیوه ی سخنوری خود وانمود می کنند که مرا می شناسند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5842,'زندگی برای ما همان چیزی است که از زندگی جدی برداشت می کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5843,'ما تنها دارنده ی ادراک شخص خود هستیم و باید از این پس، بر آن و نه بر آنچه که می بینیم واقعیت زندگی مان را بنا کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5844,'هر چیز وقتی پدید می آید، به خوبی می توانیم پدید آمده را به سان میرنده احساس کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5845,'همیشه پس از آن که روز از راه برسد، مثل همیشه دیر خواهد شد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5846,'رویای من از قدرت اراده ام آغاز شد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5847,'من اغلب نادانسته به همه چیز شک می کنم، اغلب در جست و جوی خط مستقیمی هستم که با هستی من برابری کند و آن را مستقیم در اندیشه و آرمانم درک کنم، فاصله های کمتر کوتاه میان دو نقطه.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5848,'این کار ما نیست که آشکار بدانیم چه کسی هستیم؛ آن چه ما فکر و احساس می کنیم همواره برگردانی است از اینکه، آنچه می خواهیم، هرگز نخواسته ایم و به احتمال زیاد هرگز کسی نخواسته است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5849,'وقتی می نویسم دردم را فراموش می کنم، مانند کسی که پیش از بهبود بیماری بتواند بهتر نفس بکشد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5850,'اگر به اجبار از چیزی خشنود شوم آن چیز همیشه برایم رخ خواهد داد؛ چرا که برای من قناعت [ =بسنده کاری ] به معنای از دست دادن یک رویا است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5851,'رنج کشیدگان راستین گردهم نمی آیند و گردهمایی تشکیل نمی دهند. هرکس رنج می کشد، به تنهایی رنج می کشد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5852,'تنهایی رفتار مرا رقم می زند نه انسانها.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5853,'هرگز تردیدی به خود راه ندادم که همه بر من خیانت می کنند و باز هر بار هم که خیانت می کردند غرق در شگفت می شدم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5854,'هرگز برای نقص اندیشه همدردی وجود ندارد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5855,'ما با رفتارمان، یعنی خواسته هایمان زندگی می کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5856,'رفتار، درایت [ =دانایی و آگاهی ] راستین است. من همانی خواهم بود که می خواهم باشم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5857,'کامیابی در کامیابی نهفته است، نه آنکه انسان از شرایط کامیابی برخوردار باشد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5858,'هر زمین گسترده ای شرایط قصر شدن را داراست.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5859,'انسان اگر خویشتن خویش را می شناخت بر خود عشق نمی ورزید.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5860,'چون چیزی از هم نمی دانیم، یکدیگر را درک می کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5861,'آنچه که ما زندگی کرده ایم، سوء تفاهمی شایع است، حد میانه ی شادمانه ی عظمتی که وجود ندارد و خوشبختی، که نمی تواند عینیت یابد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5862,'هر کس که در جمع مردم عادی نباشد، فرمانروا است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5863,'کسی که مرزهای روان خود را می شناسد می تواند بگوید من، منم؟((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5864,'آشنایی سطحی برای هنرمند بسیار کم است، او را می آزارد و از تاثیرش می کاهد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5865,'هر انسان امروزی که تندیس اخلاقی و چارچوب فکری اش عقب مانده یا بدوی نباشد، عاشق می شود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5866,'نه عشق، اما اصول تشکیل دهنده ی عشق ارزش زحمت را دارد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5867,'تنها اندیشه می تواند بدون آسیب به شناخت واقعیت نایل آید.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5868,'خالص بودن، نجیب یا قوی بودن نیست، بلکه خود بودن است. کسی که عشق ارزانی می دارد، عشق را می بازد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5869,'زن سرچشمه ی خوبی برای رویاهاست. هرگز او را لمس مکن!((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5870,'رویاها همواره رویا می مانند. پس نیازی نیست که لمس شان کنی. اگر رویای خود را لمس کنی خواهد مرد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5871,'ما هرگز به کسی عشق نمی ورزیم. تنها به پنداری که از کسی داریم عشق می ورزیم. ما به – سرانجام شخصی خود – نظر شخصی خود عشق می ورزیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5872,'دوست ندارم کسی چیزی به من هدیه کند، چرا که طرف مقابل با اهدای آن می خواهد مرا موظف کند تا مانند او چیزی هدیه کنم؛ حال به خود اهدا کننده یا سایرین.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5873,'زندگی مان را چنان سامان دهیم که برای همنوعان خود اسرارآمیز جلوه کند، به گونه ای که اگر کسی ما را بهتر از همه می شناسد، چون سایرین از نزدیک ترین فاصله نشناسد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5874,'چه دردناک است همه چیز، وقتی به مثابه ی انسانی آگاه غرق در تفکر می شویم و به عنوان موجودی اندیشه ور دوباره ادراک در وجودمان گسترش می یابد، به گونه ای که در می یابیم، می دانیم!((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5875,'زندگی یعنی چیزی از خود ندانستن، از خود کم دانستن، یعنی اندیشیدن، به خود آگاه بودن، آن هم ناگهانی مثل این لحظه ی آرام، یعنی یکباره مفهوم وحدت درون و واژه ی جادویی روان را یافتن.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5876,'تمام گذشته ام چیزی است که نمی خواهم بوده باشم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5877,'این قاعده ی زندگی است که می توانیم از همه ی مردم بیاموزیم و باید بیاموزیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5878,'و همه ی ما، انسانها، خانه ها، سنگها، پوستر ها و آسمان مجموعه بزرگی از دوستانیم که در جستجوی جدل با کلمات در حرکت جمعی بزرگ سرنوشت ایم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5879,'ما باید سرنوشت خود را مثل اندام مان بشوییم و شیوه ی زندگی مان را مانند لباس هایمان عوض کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5880,'خوک هایی وجود دارند که از کثافت خود متنفرند، اما نمی توانند آن را از خود دور کنند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5881,'ما با غریزه ی جنسی عشق نمی ورزیم، بلکه تحت شرایط احساسی دیگر عشق می ورزیم و در حقیقت این شرایط، همان احساس دیگر است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5882,'کسی که هرگز تحت فشار نزیسته باشد، آزادی را لمس نمی کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5883,'تمدن، تربیت نهفته در طبیعت است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5884,'در هماهنگی جهان طبیعی و مصنوعی، طبیعی بودن، از روان شایسته و بلند مرتبه ی انسانی شکل گرفته است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5885,'هر روز، روزی است که هست و هرگز در جهان روزهای هم شکلی وجود نداشته است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5886,'برخی برده به دنیا می آیند، دیگران برده می شوند و باز عده ای به سوی برده داری جذب می شوند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5887,'تعریف کردن یعنی اقرار [ = گواهی ]، چیزی که دیگران دوست دارند بشنوند چیزی نیست که دیگران بگویند و آنها تعریف کنند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5888,'کودکان موجودات ادبی هستند، چون همان گونه که احساس می کنند، سخن می گویند و نباید مثل کسی احساس کنند که مثل کس دیگری احساس می کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5889,'وقتی سنگی را از زمین برمی داری، مردم زیر صخره های انتزاعی بزرگ آسمان نامفهوم آبی مثل سوسک درهم می لولند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5890,'هنر ما را به گونه ای رویایی از درد هستی رها می سازد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5891,'رنج های مشترک در پیوند با ما هستند و حقیر اند، چون رنج ها حقیر اند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5892,'لذتی که هنر بر ما ارزانی می دارد، نیازمندیم و چون به گونه ای هنر ما نیست، نه وجهی می پردازیم و نه تاسف می خوریم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5893,'تحت عنوان هنر، همه ی چیزهایی که باعث سرورمان می شود، بی آنکه در ارتباط با ما باشد، درک می کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5894,'متصرف بودن یعنی از دست دادن، احساس کردن بدون متصرف بودن، یعنی پاس داشتن؛ این بدان معنا است که از چیزی هستی اش را بیرون بکشی.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5895,'چیزی را بیان کردن، یعنی نیرویش را پاس داشتن و وحشت اش را زدودن.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5896,'هیچ چیز حقیقی در زندگی وجود ندارد تا از این رو حقیقی باشد که آن را انسان خوب ستوده است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5897,'همه چیز هست چون ما هستیم و همه چیز برای آنانی که ما را در گونه گونی زمان دنبال می کنند، چنین خواهد بود که ما آن را برای خود مشتاقانه ترسیم کرده بودیم، یعنی همان گونه که به پاس خیال مان راستین بوده ایم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5898,'همه ی ما رمان نویس هستیم، وقتی می بینیم تعریف می کنیم، چون دیدن مثل همه ی چیزهای دیگر پیچیده است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5899,'خود را خوشبخت دانستن یعنی پی بردن به اینکه انسان از میان خوشبختی می گذرد و به زودی باید [ آن را ] پشت سر بگذارد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5900,'همین که بتوانیم جهان را چونان خیال و تصاویر موهوم بنگریم، می توانیم همه ی چیزهایی را که برایمان روی می دهد، خیال تلقی کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5901,'انسان هرچه کامل تر، به همان نسبت غنی تر و هرچه غنی تر باشد، به همان نسبت کمتر کس دیگری می شود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5902,'آرزوی هر روان ناب است که کل زندگی را طی کند، تجربه هایی درباره ی همه ی چیزها، همه ی نواحی و همه ی احساس طی شده گرد آورد و چون چنین چیزی غیرممکن است، زندگی فقط به گونه ی ذهنی می تواند به طور کامل طی شود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5903,'خوشبخت کسی که با یاری خیال، شخصیت خود را ترک می کند و با تماشای زندگی غریبه ها خویشتن را مسرور می سازد و حتا نه کل تاثیرات، اما نمایش درونی کل تاثیرات را تجربه می کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5904,'خوشبخت کسی که از همه چیز کناره گیری می کند، چرا که از همه چیز کناره گرفته است و برای خود چیزی نگاه نداشته است که بتوان آن را برگزید.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5905,'من آنچه را که آرزو نمی کنم، آرزو می کنم و از آن چیزی دوری می گزینم که ندارم. من نه می توانم هیچ باشم و نه همه چیز؛ من پل گذر میان آنی که ندارم و آنی که نمی خواهم، هستم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5906,'با دقت باید زیست تا بتوان وجود داشت.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5907,'سپاسگزار ایم، اگر انسان های اندیشه مندی شویم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5908,'همیشه انسان حلزونی بهتر است، چرا که عشق می ورزد و بی خبر است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5909,'زالو بی آنکه خود بداند، دور کننده است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5910,'زندگی، شیب در حال سقوط و جلگه ی پهناور زیر پای ارتفاعات و قله ها است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5911,'هرچه در ما وجود دارد پیشامد گونه و فریبکارانه است و بلندایی که در درونمان داریم، در درونمان نداریم؛ ما در بلندی ها، بلندتر از بلندای خودمان نیستیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5912,'حتا آن چیزی که در زیر پاهایمان لگدمال می کنیم، ما را می خیزاند و اگر بلندتر بایستیم به این سبب است که در جای بلندتری ایستاده ایم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5913,'همه چیز بر بستر افسانه آرمیده، اما حتا افسانه هم کار ما نیست.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5914,'هر کس غرور خود را دارد و غرور هر کس او را به فراموشی می سپرد، چرا که کس دیگری با همان روان وجود دارد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5915,'کسی که چشم های مرا ببندد نابینایم نمی کند، مانع از دیدنم می شود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5916,'وقتی مرگ شباهتی به خواب ندارد، چرا باید مرگ، خواب باشد؟((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5917,'مرگ، شباهتی به خواب ندارد، چرا که انسان در خواب زنده است و می خوابد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5918,'گاهی وقت ها به نظرم می رسد همه چیز نادرست است و زمان فقط فضایی است برای اشتباه گرفتن آرامش؛ چیزی که برای زمان ناشناخته است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5919,'از دو چرخ یک محور همیشه یکی جلوتر حرکت می کند، حتا اگر به بخش های شکننده ای از میلی مترها مربوط باشد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5920,'با مرگ طبیعت، خود ما هم پایان خودمان را رقم می زنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5921,'هر پاییزی که از راه می رسد، به آخرین پاییزی که تجربه خواهیم کرد نزدیک است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5922,'انسان به هر میزان از مقام برتری برخوردار باشد، باید به همان نسبت از بسیاری از چیزها چشم پوشی کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5923,'به چنگ آوردن چیزی که نتوان به چنگ آورد، هنر است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5924,'خود را بیان کردن همیشه به معنای خطا رفتن است. همواره بر خود آگاه باش؛ خود را بیان کردن برای تو مفهومی جز دروغ گفتن ندارد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5925,'کاهلی بیش از هر چیزی آرامش بخش است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5926,'هیچ کس نمی تواند شاه جهان باشد، این یک رویا است و هر یک از ما که به درستی خود را بشناسد، می خواهد شاه جهان باشد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5927,'ما دارنده ی آن چیزی هستیم که از آن کناره می گیریم، چرا که آن را در رویا دست نخورده پاس می داریم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5928,'آزادی، فرصتی برای گوشه گیری است؛ تو اگر بتوانی خودت را از انسانها جدا کنی، آزادی.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5929,'اگر برای تو آسان نیست که تنها زندگی کنی، پس برده به دنیا آمده ای.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5930,'وای بر تو، اگر تنها فشار زندگی وادارت کند که برده شوی.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5931,'مرگ،رهایی است؛ چرا که مردن یعنی به هیچ کس دیگر نیاز نداشتن.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5932,'کسانی که عشق را هدر دادند، خود را از پیروزی ای که دار و ندارشان به شمار می رود، آزاد می بینند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5933,'مرده می تواند بدریخت باشد، اما نیرومندتر است، چرا که مرگ، او را آزاد کرده است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5934,'پول، زیبا است، چون به معنای آزادی است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5935,'دانش ما كُره ای است که هرچه گسترش می یابد، به همان نسبت درمی یابیم که کدام چیزها را نمی دانیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5936,'انسان با وجود شرورهای جهان نمی تواند لطف بی نهایت آفریدگار دانایی جهان را بپذیرد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5937,'فضا، روان اشیاء است. هر چیزی بیان خاص خود را دارد و این بیان از بیرون به سراغ اش می آید.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5938,'همه چیز از بیرون می آید و حتا روان انسان هم شاید چیزی جز تابش خورشید نیست که می درخشد و در زمین دربند شده؛ زمینی که سطح آن انباشته از کثافتی است که اندام ما است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5939,'بسیار شگفت آور و قابل توجه است که انسان به سادگی نمی تواند واژه هایی بیابد که با آنها تفاوت میان انسان و حیوان را تعریف کند، با وجود این، یافتن شکل و شیوه ای که بتوان انسان بلندپایه را از انسان میانه تمییز داد، آسان است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5940,'انسان بلندپایه بسیار بالاتر از انسان عادی است تا انسان عادی از میمون عادی.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5941,'همیشه حقیقت، حتا اگر نادرست هم باشد، آن سوی خیابان دیگر جا می گیرد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5942,'همیشه خشونت به هر شکل ممکن برای من شکل نابخردانه ای از نادانی انسان ها بوده است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5943,'ما هرچه درباره ی چیزی که اراده به دانستن اش را داریم بالاتر برویم، به همان نسبت از چیزی که می دانیم نزول می کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5944,'بین زندگی انسان و حیوانات هیچ تفاوتی وجود ندارد جز در شکل و شیوه ای که اشتباه می کند یا خودش را باز نمی شناسد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5945,'امید برای کسانی وجود دارد که از آرزو برخوردارند و عشق برای کسانی وجود دارد که برایشان دادن به معنای گرفتن است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5946,'چون هیچ زمان نمی توانیم همه ی اساس یک سؤال را بشناسیم، از این رو هیچ زمان هم نمی توانیم آن را پاسخ دهیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5947,'برای رسیدن به حقیقت با کمبود ارقامی مواجه هستیم که برای این مهم کفایت می کرد و رویکرد فکری چیزی است که همواره این ارقام را تکمیل می کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5948,'اصلاح طلب انسانی است که وضعیت بحرانی جهان را در سطح می شناسد و تصمیم می گیرد آن را مداوا کند، اما اساس کار را خراب تر می کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5949,'چیزی که در اساتید و شناسندگان نامرئی ها توجه مرا دامن می زند این است که می خواهند با نوشتن، اسرارشان را با ما در میان بگذارند یا تحمیل کنند، اما جملگی بد می نویسند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5950,'من به استادهایی که حتا نمی توانند ناظم مدرسه شوند بدبین هستم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5951,'ریاضی دان های بزرگی وجود دارند که در تمرین جمع ساده اشتباه می کنند. این رفتار دلیل بر بی دقتی است و برای هر کسی می تواند پیش بیاید.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5952,'هر روان شایسته ی خویشتن خویش میل دارد زندگی را در بی نهایت به پایان برساند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5953,'خشنود بودن نسبت به هر آنچه انسان دریافت می کند، شیوه ی تفکر بردگان است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5954,'در همه ی دروه های تاریخ، آنهایی که می توانستند چشم هایشان را بر روی کل خستگی ای که مجموعه ی کل خستگی ها است – چرا که همه چیز را به همان شکل جنون آمیز دارا است – ببندند، نادر بودند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5955,'درایت [ = دانایی ] را جایگزین قابلیت کردن، شکستن رابطه ی میان اراده و احساس و دلبستگی نداشتن نسبت به همه ی رفتارهای زندگی مادی، چیزهایی هستند که انسان اگر بدان ها دست یازد، ارزشمندتر از خود زندگی است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5956,'من هرگز درباره ی هرچه که زندگی می دهد و می گیرد اشک نمی ریزم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5957,'چیزی که نوشته شده هم یک موجود زنده است، کلمه اگر دیده یا شنیده شود کامل است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5958,'اثری را آفریدن و پس از پایان، بد شمردن، از رنج های روان است؛ به ویژه اگر شخص بپذیرد که این بهترین اثری بود که می توانست بیافریند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5959,'بزرگترین گناه من از دانستن سرچشمه می گیرد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5960,'برای هرچه تلاش می کنیم، تلاش برای تایید شدن است، اما با این تایید شدن کنار نمی آییم و فقیر می مانیم، یا مدعی می شویم تایید شدن حق ما است، در آن صورت دیوانه های توانگری هستیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5961,'همه ی چیزهایی که در عرصه ی هنر یا زندگی انجام می دهیم، رونوشت ناقص چیزهایی است که می خواستیم انجام دهیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5962,'انسانها تنها یاد می گیرند که از اجداد درگذشته ی خود سودی ببرند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5963,'یک اثر کامل حتا اگر بد هم باشد بهتر است، چون در هر صورت یک اثر است، یا سکوت واژه ها، خموشی کل روان، تا نویسنده با آن به ناتوانی رفتار خود را ثابت کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5964,'هنر عبارت از این است که فرصت دهیم تا سایر انسانها آنچه را که ما احساس می کنیم، احساس کنند، آنها را از خودشان رها سازیم و برای این رهایی خاص شخصیت خودمان را به آنها پیشنهاد کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5965,'برای اینکه بتوانم احساسی را که حس می کنم به دیگری بدهم، ناگزیرم احساس خود را به زبان او برگردانم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5966,'انسان از اندیشیدن و حس کردن چیزی درک نمی کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5967,'همه به یکدیگر عشق می ورزیم و دروغ، بوسه ای است که با یکدیگر رد و بدل می کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5968,'نوشتن یعنی فراموش کردن؛ ادبیات خوش آیندترین شیوه ی نادیده گرفتن زندگی به شمار می رود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5969,'در نثر، لطافت لرزانی وجود دارد که بازیگری بزرگ با آن کلمه را در ماده ی هماهنگ اندامش به راز غیر قابل درک آفریدگان مبدل می کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5970,'شعر، بیان آرمان با احساس به زبان دیگر است که هیچ کس به کار می بندد، چرا که هیچ کس به مانند شعر لب به سخن نمی گشاید.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5971,'چیزی که به تندی رشد نکند، در زیر وجود پیشامد گونه ی اندیشه ی ما رنج می برد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5972,'بزرگترین تلاش ما به اندازه ی خود به زمان نیاز دارد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5973,'من برخی شاعران غنا [ =آواز خوش طرب انگیز ] یی سرا را دوست دارم، چون از بینش درست برخوردارند و هرگز حاضر نیستند احساس یا رویا را بیش از یک لحظه به هنر منتقل کنند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5974,'می توان در رویای چیزی زیبا غرق شد که انسان نه در عمل می تواند بدان برسد و نه در گفتار.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5975,'بسیاری از مردم از اینکه نمی توانند بگویند چه می بینند و چه می اندیشند ناخوش می شوند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5976,'وظیفه ی کشتی، دریانوردی نیست، ورود به بندر است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5977,'اگر قلب توان تفکر می داشت از تپیدن باز می ایستاد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5978,'زندگی در نگاه من به مهمانخانه ای می ماند که باید در آن استراحت کنیم تا کالسکه ی غرقاب از راه برسد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5979,'شب برای همه از راه خواهد رسید.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5980,'زندگی یعنی جوراب بافی به قصد هم نوعی. اما در کنارش افکار آزاد است و همه ی شاهزاده های جادو شده می توانند بروند و در پارک های خودشان قدم بزنند. همزمان میل عاج بافتنی از قسمت انتهایی دوباره و دوباره فرو می رود ... فاصله .... و دیگر هیچ ... .((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5981,'آنچه که متفاوت مان می کند نیرویی است که [با آن] بتوانیم نقشه ها و رویاهای خود را عملی سازیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5982,'ما همه بردگان شرایط بیرونی هستیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5983,'هر یک از ما بی شمار است، بسیار است، زیادتی از خویشتن است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5984,'مایه ی خوشبختی انسانیت است که هر انسانی فقط آنی هست که هست.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5985,'هر کس هستی خود را یکنواخت اداره کند خردمند است. چه در آن صورت هر حادثه ی کوچکی از برکت معجزه بر خوردار می شود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5986,'کمک حسابدار می تواند خود را امپراتور روم بپندارد، پادشاه انگلستان نمی تواند، چرا که او پادشاه انگلیس است و در رویا نمی تواند جز آنچه هست، پادشاه دیگری باشد. واقعیت او، از حس کردن وا می داردش.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5987,'بهترین ویژگی در زندگی روزمره، نیروی محرکه ای است که به عمل منتهی می شود، یعنی اراده ی راسخ.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5988,'چه بر سر افسر ستاد ارتش می آمد اگر می اندیشید که هر حرکت او، برای هزار خانواده تیرگی به ارمغان می آورد و قلب سه هزار نفر را رنجور می سازد؟ این جهان چه می شد اگر ما انسان تر می بودیم؟((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5989,'آدم مسرور چیره است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5990,'اگر هر کسی فقط فکر کند که برای پیروزی به چه چیز نیاز دارد، پیروز می شود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5991,'تا می توانید رو به روی آینه جا خوش کنید. آینه ها با ما حرف می زنند و با نگاهشان سرایی برای ما می گشایند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5992,'هیچ چیز روشن تر از این واقعیت، فقر اندیشه را بروز نمی دهد که انسان بتواند به حساب دیگران با فراست [ =با فهم و درک ] باشد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5993,'چیزهایی را که ما را در بر گرفته، به بخشی از خویشتن ما تبدیل می شود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5994,'شادمانه ترین لحظه های من، لحظه هایی است که نمی اندیشم، چیزی نمی خواهم و حتا غرق رویا هم نمی شوم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5995,'هرگز کسی را سراغ نداشتم که بتوانم «سرمشق» صدایش بزنم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5996,'زندگی یعنی دیگری بودن؛ حتا احساس آنچه که امروز دریافتی و آنچه که دیروز احساس می کردی، ممکن نیست.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5997,'ما آن که می پنداریم نیستیم و زندگی، زودگذر و اندوهگین است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5998,'زندگی کلافی است که کسی آن را در هم ریخته است. کلافی که باز و کشیده شده و یا خوب جمع شده، ثمری ندارد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(5999,'لحظه هایی وجود دارند که در تهی بودن آنها انسان خود را زنده احساس می کند و به وضعیت مثبت متراکمی می رسد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6000,'مردان بزرگ اهل عمل، قدیسان، نه با بخشی از احساس شان که با کل شان رفتار می کنند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6001,'احساس، زندگی را که هیچ است به بی نهایت هدایت می کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6002,'اگر انسان با خیالبافی زنده باشد نیرویش را صرف خیالبافی می کند و مقدم بر همه، این قدرت را دارد تا واقعیت موجود را به خود بشناساند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6003,'اگر انسان از نظر فکری از چیزی می زید که وجود خارجی ندارد و نخواهد داشت، در نهایت هرگز نمی تواند تصور کند که چه چیزهایی وجود دارند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6004,'به درد نخورها و بی اهمیت ها فضای بینابینی فروتنانه میانه ای در زندگی حقیقی ما می گشایند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6005,'درک احساس یک راز دشوار است. مثل اینکه کسی ناظر کشتاری باشد و همزمان به درگیری های بی اهمیت اجتماعی بیندیشد، یا هنگام تماشای سنگی کوچک بر سطح خیابان، هیچ تصویری جز هستی خود را دامن نزند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6006,'اندوه تلاش گران سخت کوش بدترین نوع اندوه است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6007,'مجسمه اندامی مرده است که با قلم کنده کاری شده تا مرگ را در ماده ای ابدی نگهداری کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6008,'زندگی به خودی خود یعنی مرگ، چون در زندگی خود روزی نمی یابیم که با گذارآن یک روز از زندگی مان کاسته نشود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6009,'من ادراک شکست را با خود مانند پرچم پیروزی به هر سو می کشانم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6010,'بزرگترین هراس خود را نه فقط در زندگی جهان هستی، بلکه در روان شخص خود مانند رویدادی بی ارزش نگریستن، سرآغاز خردمندی است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6011,'نه رنج انسانی بی نهایت است و نه رنج ما ارزش برتری از خود رنج را دارد که باید تاب آوریم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6012,'اینکه زندگی، دردناک یا اندیشیدن به زندگی، دردناک است، نادرست است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6013,'همه چیز هیچ است، درد ما هم همین طور.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6014,'نجیب زاده کسی است که هیچ وقت فراموش نمی کند تنها است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6015,'هر یک از ما جامعه ای کامل است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6016,'زمانی چیزهایی مرا به خشم می آورد که امروز باعث خنده ام می شوند. برای این کار پایداری لازم است تا با آن، انسان های میانه ی اهل عمل بر شاعران و هنرمندان خنده سر دهند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6017,'مزیت خیال پرداز در این است که، خیال پردازی عملی تر از زندگی است و خیال پرداز از زندگی لذتی گسترده و بسیار گونه گون تر از اهل عمل می برد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6018,'زندگی یک حالت فکری است و همه چیزهایی که انجام می دهیم یا می اندیشیم، برای ما به نسبتی که معتبر می پنداریم، معتبر است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6019,'همه ی چیزها، حال هر قدر هم که حقیر باشند وقتی از آن من نباشند همواره برایم جاذبه ای شاعرانه دارند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6020,'هیچ چشم داشتی آزار دهنده تر از چشم داشت چیزهایی که هرگز وجود نداشته، نیست.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6021,'کسی که بتواند بنویسد، می تواند ویرانی رویاهایش را آشکارا تماشا کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6022,'برای کسی که خیال اش در سطح پوست جای دارد، ماجراجویی یک قهرمان رمان، هیجان شخصی و چه بسا بیشتر است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6023,'تنها آنچه که ما در خیال اش به سر می بریم، به راستی خودمان هستیم و هر آن چه باقی می ماند، چون به حقیقت پیوسته است، به جهان و مردمان بستگی دارد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6024,'ما هیچ چیزی را عملی نمی سازیم؛ زندگی مثل آهن ربا ما را جذب می کند و ما بیهوده می گوییم: «من حرکت می کنم.»((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6025,'زندگی سفری تجربی است که ناخواسته دامن زده می شود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6026,'انسان هیچ وقت به اندازه ای که اندیشیده، زندگی نکرده است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6027,'وقتی شاهد رقص باشم با رقص همراه می شوم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6028,'این همه مدت زیسته ام بی آنکه زیسته باشم! این همه اندیشیده ام بی آنکه اندیشیده باشم!((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6029,'ما در این جهان، حال ارادی یا غیر ارادی، مسافران میان هیچ و هیچ ایم، یا میان همه و همه ی مسافران مخالف سفر، حق نداریم برای دردسر مسیر سفر ارزش چندانی قایل شویم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6030,'همین که انسان کاملاً دور دنیا را بگردد و به همان دریاچه ی مرغابی که از آنجا راه افتاده برسد، آنجا انتهای جهان است؛ در حقیقت، انتهای جهان مثل ابتدای جهان است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6031,'زندگی همانی است که ما از آن می سازیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6032,'آنچه ما می بینیم چیزی نیست که می بینیم، بلکه آنی هست که ما هستیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6033,'تجربه های زندگی به ما چیزی نمی آموزد، همان گونه که تاریخ هم ما را از چیزی آگاه نمی کند. تجربه ی حقیقی زمانی به دست می آید که ارتباط با واقعیت را محدود کنیم و کاهش ارتباط را نیرو بخشیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6034,'هر غروب خورشید، غروب خورشید است؛ ضرورتی وجود ندارد که انسان غروب خورشید را در قسطنطنیه تماشا کند. اگر رهایی در درونم نباشد در هیچ جای دیگری هم نیست.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6035,'ما هرگز نمی توانیم از خویشتن خویش پیاده شویم، هرگز به کس دیگری مبدل نمی شویم، مگر اینکه خودمان را با وهم حساس به دیگری مبدل کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6036,'چشم انداز راستین، چشم اندازی است که خود می آفرینیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6037,'کسی که همه ی دریاها را درنوردیده باشد، تنها یکنواختی خویشتن خویش را درنوردیده است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6038,'اگر بخواهم راهی سفر شوم، در سفر تنها سرنوشت رنگ و رو رفته ی چیزی را می بینم که بدون سفر دیده بودم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6039,'هرجا که باشم خودم هستم، حال چه با مشرق زمین و چه بی مشرق زمین.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6040,'کسی که سفر می کند ناتوان از احساس است. برای همین هم کتابهای سفر در نقش کتابهای تجربه، چنین کم محتوا هستند و ارزش آنها قدردان خیال کسی است که آنها را نوشته است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6041,'صرفنظر از نگاه به درون، همه ی ما نزدیک بین هستیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6042,'برای ما که همیشه از خویشتن خویش می گذریم چشم اندازی جز آنچه خودمان هستیم وجود ندارد، چرا که ما حتی صاحب [ =دارنده ی ] خودمان هم نیستیم. ما هیچ چیز نداریم، چرا که هیچ ایم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6043,'هر کس اطمینان به دست آورد که همنوعان اش او را ارزشمند می شمارند، هراسی از آنها به دل راه نمی دهد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6044,'زندگی دره ی اشک است، اما به ندرت در این دره اشک می ریزند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6045,'اگر از زندگی سر در می آوردیم، تحمل ناپذیر می شد. خوشبختانه چنین نیست. ما با همان ناآگاهی مثل حیوانات زندگی می کنیم و مانند آنها بیهوده و عبث.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6046,'کسی که خود را بزرگ می پندارد، اعتماد چندانی ندارد که همنوعانش او را بزرگ شمارند. او می ترسد نظری که درباره ی خود دارد با نظرهایی ترکیب شود که همنوعان اش نسبت به او دارند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6047,'خوشا به سعادت کسی که نمی اندیشد، چرا که او بر مبنای غریزه و به پاس تعریف روشن اندام خود، آن را عملی می سازد؛ چیزی که همه ی ما باید از بیراهه ها و در نتیجه ی تعریفی ورای اندام یا جامعه عملی سازیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6048,'ما مطلق پرست هستیم، چون به آن دسترسی پیدا نمی کنیم، اگر آن را می داشتیم دورش می انداختیم. مطلق غیر انسانی است، چرا که انسان ناقص است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6049,'آرزوی ما شبیه بیچاره ی تنگدستی است که فکر می کند در آسمان، زمین وجود دارد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6050,'چه بهتر که زمینی در آسمان نباشد، اصلاً هیچ آسمانی وجود ندارد؛ پس همه چیز هیچ است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6051,'تلاش تکاملی هنرمندان بزرگ را گرامی می داریم و حیرت می کنیم. نزدیکی آنها را به تکامل دوست داریم، به هر حال آنها را دوست داریم، چرا که تنها یک نزدیکی است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6052,'به هر میزان که زندگی با انسان میانه، سخت و بی ملاحظه برخورد کند، انسان این نیک بختی را دارد که مجبور نیست مدام زندگی را با اندیشیدن سپری کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6053,'اندیشه کردن یعنی ویرانی؛ خود جریان اندیشیدن، اندیشه را متهم می سازد، چرا که اندیشه تجزیه شده است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6054,'اگر انسانها بخواهند درباره ی اسرار زندگی کاوش کنند، اگر می توانستند هزاران پیچیدگی را احساس کنند که جزء جزء رفتار روان را گوش می ایستد، هرگز رفتاری پیشه نمی کردند و حتا دیگر نمی زیستند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6055,'انسان از بدو تولد تا مرگ به مثابه ی برده ی بیرونی خویشتن خویش، که حیوانات نیز چنین اند، زندگی می کند. او در طول زندگی اش، زندگی نمی کند، بلکه با پیچیدگی های فراخ در ابعاد گسترده به زندگی نباتی دامن می زند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6056,'من هیچ جمله ی اسف باری را نمی شناسم که بتواند انسانیت بشر را تمام و کمال افشا کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6057,'انسان می تواند همسرش را انکار کند، اما پدر و مادرش را نه.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6058,'مطالعه ی روزنامه از نقطه نظر زیبایی شناسی، چه بسا اخلاقی هم همیشه پر دردسر است، حتا برای کسی که اندکی عذاب وجدان اخلاقی را بشناسد پر دردسر است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6059,'جنگ ها و انقلاب ها هنگام مطالعه ی تاثیرشان نه باعث هراس بلکه موجب بی حوصلگی می شوند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6060,'همه چیز بشریت است و بشریت همواره یکسان، تغییرپذیر اما اصلاح ناپذیر، در حال نوسان، اما ناتوان از پیشرفت.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6061,'تاریخ، ثبات را انکار می کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6062,'زندگی باید برای بهترین ها که از مقایسه ی خود سر باز می زنند، یک رویا باشد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6063,'تجربه ی بدون میانجی، بهانه یا مخفی گاه کسانی است که بی خیال هستند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6064,'[ همه ی چیزها ] تنها در وضعیت مناسب ارزشمند هستند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6065,'تعریف کردن یعنی پدید آوردن، چرا که زندگی یعنی فقط زیسته بودن.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6066,'برای خوشبخت بودن، انسان باید بداند که خوشبخت است، در خواب بی رویا به هیچ وجه خوشبختی وجود ندارد.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6067,'بزرگ کسی است که به دانایی دست یابد و به فاصله ی دره تا آسمان یا کوه تا آسمان که با هم متفاوت اند، تفاوتی قائل نشود.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6068,'کسی که امکان رفتن به ارتفاع را در عضلات خود حس کند، به راستی باهوش است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6069,'قصر باشکوه در جنگل برای کسی زیبا است که آن را از دره تماشا کند.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6070,'کل زندگی روان انسان تلاشی در سایه روشن است.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6071,'ما چیزی هستیم که در استراحت میان یک نمایش به پایان می رسیم، گاهی وقت ها به گونه ای فرار از شکاف درهای خاص به درون می نگریم، چیزی که شاید جز تصویر صحنه چیز دیگری نیست.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6072,'من مثل کسی هستم که در روی زمین در جستجوی خوشبختی است و نمی داند این وسیله کجا پنهان شده است و چیزی از آن برایش نگفته اند که چه هست. ما با هیچ کس قایم باشک بازی نمی کنیم.((فرناندو پسوا))',86,0,0,0,0,0);
INSERT INTO mysms VALUES(6073,'اگر هر کس همه کس را دوست می داشت، همه کس صاحب دنیا می شد.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6074,'چرخ های جهان را دو چیز می چرخانند: گرسنگی و عشق.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6075,'کسی که در زمان خود سنگ تمام گذاشته، نامی جاودان یافته است.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6076,'آزادی فقط در دنیای خیال وجود دارد.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6077,'امید، با مرگ هم به گور نمی رود.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6078,'هر کجا مردی یافت شد که به مقامات عالیه رسیده، بدون شک، زنی پاکدامن او را همراهی کرده است.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6079,'هیچ جنگی غیر انسانی تر و ننگ آورتر از جنگ های متعصبانه ی مذهبی و تنفرآور حزبی که در داخل یک کشور برافروخته می شود، نیست.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6080,'در جهان تنها کسی پیروز می شود که به امید دیگران ننشیند و همه چیز را از خود بخواهد.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6081,'کسی که بیش از اندازه فکر می کند، کمتر به نتیجه می رسد.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6082,'اگر سخنان تو را اراده و عمل دنبال نکند، پس از کجا می توان ارج و بهای تو را شناخت؟ دوست عزیزم همیشه این گونه بوده و باز هم این گونه خواهد ماند. ناتوانی برای خود قانونی دارد، اما اراده جز کامیابی قانونی نمی شناسد.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6083,'با مردم زمانه همنشین باش، اما مانند آنان مباش و برای معاصرین خود مطالبی بنویس که به آن محتاجند، نه مطالبی که می پسندند.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6084,'به زنان خود به [ دیده ] احترام بنگرید. آنها گلهای آسمانی را در بستر زمین می کارند و جامه دلپذیر و زیبای عشق را می آرایند و در پرده پاکدامنی، احساسات لطیف را می پرورانند.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6085,'بیکاری نه تنها موجب پریشانی فکر و ناراحتی خیال است، بلکه کدورت و پشیمانی همیشگی و باطنی پدید می آورد.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6086,'پرهیزگاری، هرکسی را در یک لحظه پرخطر می آزماید و درست می کند.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6087,'جوانی ترکیبی از غرور و نشاط است، اما پیری خوابی از الهام و خاطرات است.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6088,'جوانی موج می زند و اقیانوس زندگی به جوش می آید. جوانان برخیزید و بکوشید پیش از آنکه روح شما تاریک شود.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6089,'حسن شهرت و نام نیک بزرگترین سعادت ها است.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6090,'شاد باد آن که روح کودکانه خویش را برای همیشه از گناه به دور می دارد.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6091,'زن، یگانه وجودی است که حقیقت عشق پاک را می شناسد.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6092,'قدرت به هر دستی که رسید، آن را مسموم می کند.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6093,'گناه هرچند افزون تر، زشتی آن در نظر گناهکار کمتر.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6094,'برتر از همه چیز، داشتن پندارهای بزرگ و قلب پاک است.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6095,'مقام عالی انسانی در برابر شما است، آن را به دست آورید.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6096,'انسان آزاد متولد شده و آزاد است. چه می شد اگر با زنجیر به دنیا می آمد؟((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6097,'فردا دیگر نمی توانیم، پس بگذار امروز زندگی کنیم.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6098,'آه کاش دوران زیبای عشق جوان تا ابد سبز می ماند!((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6099,'جدیت و پشتکار، مقصد را نزدیک می کند.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6100,'گردش جهان بر پایه ی گرسنگی و عشق استوار است.((فریدریش شیلر))',87,0,0,0,0,0);
INSERT INTO mysms VALUES(6101,'بازی زندگی، بازی بومرنگ‌هاست؛ اندیشه‌ها، کردارها و سخنان ما دیر یا زود با دقت شگفت‌آوری به سوی ما بازمی‌گردند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6102,'رهنمودهای مشخص را بطلبید تا کامیاب گردید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6103,'جایی که راه نیست خداوند راه می گشاید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6104,'تمام موفقیت های عظیم بر پایه ی شکست بنا شده اند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6105,'خداوند راهها و چاههایی دارد که تو را حیران خواهد کرد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6106,'فقط کارهای جسورانه به حساب می آیند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6107,'هر انسانی با آگاهی کامل به آیین عشق، پا به این سیاره ی خاکی می گذارد. مشکل شما هر چه باشد، امتحان محبت است. اگر بتوانید از راه محبت در این آزمایش پیروز شوید، مسأله شما حل خواهد شد. اگر نه، آنقدر به درازا خواهد کشید تا از راه محبت، مسأله خود را حل کنید. زیرا مشکل شما مجالی برای تشرف به آیین عشق است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6108,'زندگی، بازگشت اندیشه ها، گفتارها و کردارهای ماست که دیر یا زود به ما باز می گردد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6109,'خداوند بخشاینده است و انسان، دریافت کننده.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6110,'قدرت شکست ناپذیر خدا هر مانعی را از سر راه بر می دارد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6111,'همه ی امور مهم به دست کسانی انجام پذیرفته است که از آرمانهای بزرگ خود دست نکشیده اند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6112,'جایی هست که جز تو هیچ کس آن را پر نمی کند و کاری هست که جز تو هیچ کس قادر به انجامش نیست.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6113,'خدا به زمان نیاز ندارد و هرگز دیر نمی کند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6114,'هر آنچه از آدمی در سخن یا کردار، آشکار شود یا بروز کند، به خود او باز خواهد گشت؛ و هرچه بدهد، باز خواهد گرفت.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6115,'هر آنچه آدمی به گونه ای ژرف، احساس، یا به روشنی مجسم کند، بر ذهن نیمه هشیار اثر می گذارد و مو به مو در صحنه ی زندگی پدیدار می شود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6116,'جز تردید و هراس، هیچ چیز نمی تواند میان انسان و بزرگترین آرمانها یا مرادهای دلش، جدایی بیفکند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6117,'زمانی که آدمی بتواند بی هیچ دلهره ای آرزو کند، هر آرزویی بی درنگ برآورده خواهد شد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6118,'ترس، تنها دشمن آدمی است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6119,'اگر کسی در پی کامیابی باشد، ولی شرایط را برای شکست فراهم کند، دچار همان شرایطی خواهد شد که برای آن تدارک دیده است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6120,'بیشتر، پیش از کامیابی بزرگ، اندیشه های عذاب دهنده می آیند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6121,'هر موهبت والا و دلخواهی، پیشاپیش، چشم براه انسان است؛ بادا که آدمی آن را بازشناسد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6122,'انسان، تنها آن چیزی را می تواند به دست آورد که خود را در حال ستاندن آن ببیند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6123,'هر انسانی دارنده ی آن سرزمینی است که با چشم رؤیا می بیند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6124,'کسی که از نفوذ سخن باخبر است، به هنگام گفتگو، دقت بسیاری به خرج می دهد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6125,'اگر انسان برای کسی، بدبختی بخواهد، بی گمان، بدبختی به سراغ خود او خواهد آمد و اگر بخواهد به کسی کمک کند تا به کامیابی برسد، همانا راه کامیابی خود را هموار کرده است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6126,'در بازی زندگی، محبت و نیک خواهی بر هر تدبیری پیروز می شود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6127,'تنها دشمنان انسان، در درون خود او هستند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6128,'شگفتا که اگر آدمی برای کسی برکت بخواهد، توان آزار رسانیدن را از او خواهد گرفت.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6129,'خیرخواهی انسان نیک خواه، پیرامون او، هاله ای بزرگ از پشتیبانی می آفریند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6130,'تا خود را توانگر احساس نکنی، توانگری ات آشکار نخواهد شد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6131,'به محض آرام شدن دریا، کشتی های انسان از راه می رسند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6132,'تا زمانی که آدمی در برابر شرایطی پایداری کند، آن را به سوی خود خواهد کشید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6133,'هر وضعیت ناهماهنگ، نشانه ی ناهماهنگی در درون خود آدمی است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6134,'اگر در باطن انسان، ذره ای واکنش هیجانی نسبت به شرایط ناهماهنگ وجود نداشته باشد، آن شرایط برای همیشه از سر راه او کنار می رود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6135,'چه بسیار پیش آمده که آدمی با دیدن بدیهای خود در دیگران، شفا یافته است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6136,'زندگی، یک آینه است؛ و ما در دیگران، بازتاب چهره ی خودمان را می بینیم.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6137,'گذشته و آینده، دزدان زمان هستند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6138,'انسان باید رها در لحظه [ =دم ] زندگی کند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6139,'آدمی، تنها آنچه را که می دهد، باز می ستاند. بازی زندگی؛ بازی بومرنگها است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6140,'هر اندازه دانش آدمی بیشتر باشد، مسئولیت او افزونتر می گردد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6141,'انسان، تنها می تواند آن باشد که خود را چنان بیند؛ و تنها می تواند به جایی برسد که خود را در آنجا ببیند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6142,'به محض اینکه انسان از قانون پیروی کند، قانون به فرمانبرداری از او درمی آید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6143,'برای شفای تن باید روح را شفا داد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6144,'تا زمانی که در جاسنجاقیت، کبریت سوخته افتاده باشد، توانگر نخواهی شد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6145,'قانون، همواره پشتیبان کسی است که بی باکانه، ولی خردمندانه خرج می کند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6146,'اگر کسی وانمود کند که دارا و کامیاب است، میوه‌اش را در زمان آن درو خواهد کرد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6147,'تا زمانی که انسان، هرچه ترس را از ذهن نیمه هشیار خود نزداید، او را شادی و آرامشی نیست.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6148,'ترس تو است که شیر را درنده می کند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6149,'عشق راستین از خویشتن، آزاد است و از هرچه ترس، رها.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6150,'عشق راستین بدون هیچ چشمداشت یا اندکی امید، خود را بر معشوق فرو می باراند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6151,'عشق راستین، شادمانی‌اش در بخشیدن است، نه ستاندن.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6152,'حسد، بزرگترین دشمن عشق است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6153,'محال [ =نشدنی ] است آدمی بتواند چیزی را به دست آورد که خود هرگز نبخشیده است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6154,'عشقی در حد کمال ببخش تا عشقی در حد کمال بستانی.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6155,'هیچ انسانی تا عاشق کار خود نباشد، کامیاب نمی شود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6156,'تصویری که نقاش، تنها از روی عشق (به هنرش) می آفریند، بزرگترین شاهکار او به شمار می رود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6157,'آثار هنرمندی که برای گذران زندگی کار می کند، همواره فراموش می شود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6158,'زمانی که انسان با خشنودی و بدون ترس خرج کند، راه را برای سرازیر شدن پول بیشتر می گشاید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6159,'آزمندی، ریشه ی همه ی بدیها است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6160,'محبت و خوش قلبی در کسب و کار نیز گرانبها است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6161,'اگر خطای گذشته را نمی توان جبران کرد، دست کم اثر آن را با مهربانی در حق کسی دیگر می توان از بین برد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6162,'بخشیدن، راه ستاندن را می گشاید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6163,'آنچه را که آدمی در دیگران سرزنش می کند، به راستی، به سوی خود می کشد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6164,'انسان به هنگام خرده گیری یا سرزنش دیگران، به سیم لُخت برق دست می زند و هر آن، ممکن است ضربه ای هولناک بر او وارد آید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6165,'تنها راهزنی که دار و ندار آدمی را به تاراج می برد، اندیشه های منفی خود او است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6166,'پدر و مادر هرگز نباید فرزندان خود را به داشتن پیشه ای وادار کنند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6167,'گاه رویدادی بس ناچیز می تواند نقطه ی عطف زندگی انسان باشد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6168,'خشم نمی گذارد که انسان، پیش چشم خود را ببیند و سبب همه ی تصمیم گیری های نادرست است که به شکست می انجامد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6169,'آدمی، تنها با پیش تاختن به سوی چیزی که از آن می هراسد، می تواند بر ترس خود چیره شود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6170,'هر گاه آدمی، [چیزی را] با باور بخواهد، باید بستاند؛ زیرا خدا چاره‌ساز است و تدابیر خود را می آفریند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6171,'برخی از مردم، بخشندگانی خوشدل، ولی گیرنده‌ای ناپسند هستند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6172,'آدمی باید آنچه را که در ازای کار خودش به او بازمی گردد، در نهایت لطف بپذیرد؛ مفت داده‌ای، پس مفت بگیر!((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6173,'انسان با هر دست بدهد، با همان دست می گیرد و همیشه میان داد و ستد، موازنه ای کامل برقرار است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6174,'خداوند، گیرنده‌ی دلنشین را به اندازه‌ی بخشنده‌ی خوشدل دوست دارد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6175,'هر معلولی زاییده ی علتی است و چیزی به نام رویداد وجود ندارد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6176,'محال است که آدمی بتواند بیش از آنچه آسان می انگارد، به دست آورد؛ زیرا آدمی در بند اندک چشم داشت های ذهن نیمه هشیار خویشتن است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6177,'قوه ی تخیل، نیرویی آفریننده است. همیشه باید واژه هایی را برگزید که در آن، بارقه ای از برآورده شدن آرزو باشد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6178,'در ذهن الهی، زمان گمشده وجود ندارد؛ اگر دری بسته شود، دری دیگر می گشاید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6179,'سری که بر پیشانی‌اش اخمی باشد، آسوده بر بالین قرار نمی گیرد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6180,'خشم، بیزاری، بدخواهی، حسد [ =چشم تنگی ] و کین خواهی، شادمانی انسان را می رباید و بیماری و شکست و فقر می آورد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6181,'به راستی که بیزاری، بیش از شرابخواری، خانه‌ها را ویران کرده است و بیش از جنگها، جان آدمیان را بر باد داده است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6182,'جسورانه به شیری که بر سر راه ام قرار گرفته، می تازم و می بینم که سگ کوچک خرمایی رنگ و مهربانی بیش نیست.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6183,'آدمی، همچنان است که در دل خود می پندارد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6184,'هیچ گاه زیر پایت را نگاه نکن، اگر نه می جهی!((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6185,'قدرت، تکان می دهد، ولی تکان نمی خورد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6186,'تنها، کارهای جسورانه به شمار می آید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6187,'زندگی چه باشکوه می شود، اگر تن به استدلال [ =گواه آوردن ] ندهید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6188,'خودخواهی، راهها را می بندد و خیر و خوشی را دور می کند. حال آنکه، هر اندیشه ی مهرآمیز به دور از خودخواهی، نطفه ی کامیابی را در خود می پروراند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6189,'آنچه در حق دیگران می کنید، همانا برای خود می کنید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6190,'هر کنشی که به هنگام خشم و بیزاری انجام پذیرد، واکنشی ناخوشایند خواهد داشت.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6191,'نتیجه ی فریب و ریاکاری، ناامیدی و اندوه؛ و راه خیانتکاران، دشوار است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6192,'ترس و ناشکیبایی، نیروی کشش را از میان می برد؛ حال آنکه توازن، کشش می آورد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6193,'باور، نیرومندتر از خوش بینی است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6194,'امید به پیش می نگرد، حال آنکه ایمان، باور دارد که پیشاپیش ستانده است و هماهنگ با آن به کنش می پردازد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6195,'تا زمانی که خواسته ی آدمی برآورده نشده است، نباید از آن با کسی سخن بگوید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6196,'آنچه را که خدا به من بخشیده است، هیچ کس نمی تواند بازستاند، زیرا بخشش های او جاودانه‌اند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6197,'آدمی همواره در برون، همان را درو می کند که در جهان اندیشه ی خود کاشته است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6198,'باور بدون کنش، باور نیست. باور اگر پویا نباشد، مرده است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6199,'اگر انسان، هدفی یا سرزمین نوید داده شده ای نداشته باشد که او را به پیش براند، به تباهی و نیستی می گراید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6200,'در درون هر انسان، سرزمینی کشف نشده و یک معدن طلا وجود دارد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6201,'آنچه جویای آن هستم، هم اکنون جویای من است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6202,'برای کشیدن هر چیزی به سوی خود، باید با آن در هماهنگی کامل قرار گرفت.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6203,'هیچ چیز آنقدرها شگفت آور نیست که راست نباشد، آنقدرها شگفت آور نیست که پیش نیاید و آنقدرها شگفت آور نیست که دیر نپاید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6204,'اگر شکست خورده باشید و به کامیابی دیگری نفرت بورزید، راه کامیابی خود را می بندید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6205,'آن سخنی که بارقه ای از برآورده شدن آرزو در آن باشد، امورتان را دگرگون می کند؛ زیرا واژه ها و اندیشه ها، گونه ای رادیواکتیویته اند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6206,'شور و شوق نسبت به هر کاری که انجام می دهید، در مخفی کامروایی را به رویتان می گشاید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6207,'راز کامیابی این است که آنچه را انجام می دهید، برای دیگران جالب توجه کنید. خود، دوستدار باشید، دیگران نیز دوستدار خواهند شد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6208,'کامیابی، راز نیست، نظام [ =سامانه ] است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6209,'تنها آن چیزهایی را به سوی خود می کشید که بسیار به آن می اندیشید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6210,'هیچ کس، چیزی به آدمی نمی دهد، مگر خود او و هیچ کس، در دادن چیزی به او کوتاهی نمی کند، مگر خود او.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6211,'هرچه بیشتر نفرت بورزید، بر دوام نفرت خود افزوده اید و در ذهن خود، شیاری از نفرت حک می کنید که خود را در حالت همیشگی چهره تان نشان خواهد داد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6212,'در پهنه ی زندگی، باید با ساز زندگی پایکوبی کرد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6213,'انسانی که به کامیابی می رسد، دیدگان خود را به کامیابی دوخته است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6214,'آنچه را که دارید، خردمندانه به کار گیرید، تا راه را برای دارایی هرچه بیشتر بگشایید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6215,'هر رویداد زندگی تان، تبلور اندیشه ای است که از راه ترس یا باور، به زندگی خود فراخوانده اید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6216,'با چیزهایی که آرزومند یا نیازمند به دست آوردن شان هستید، از در دوستی درآیید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6217,'به هرچه توجه کنید، با آن یکی می شوید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6218,'اگر از زیر نردبانی بگذرید و به این بیندیشید که برایتان بدبختی می آورد، بی گمان، بدبختی خواهد آورد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6219,'بدبختی، تنها به این دلیل می آید که خودتان چشم براه آن بوده اید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6220,'هیچ انسانی برای تندرستی خودش هم که شده، نباید نفرت بورزد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6221,'نفرت، چون بکارگیری پی در پی زهر است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6222,'فراوانی یا کامیابی بزرگ، معمولاً زمانی از راه می رسد که هرگز چشم داشت آن را ندارید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6223,'هرگز نگذارید که آرزوی قلبی شما به بیماری قلبی بدل شود. زمانی که بیش از اندازه چیزی را خواهانید، همه ی کشش خود را از دست می دهد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6224,'گاه کشتی های آدمی از روی دریای بی توجهی به او می رسند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6225,'سخن تو، روشنایی تو است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6226,'هر هدیه ای، هزاران برابر به خودِ بخشنده باز می گردد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6227,'در هر کنش ناشی از ترس، نطفه ی شکست نهفته است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6228,'شخص عادی، کمابیش همه چیز را نشدنی می پندارد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6229,'همه ی آرمانهای بزرگ با مخالفت روبرو می شوند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6230,'به راستی که پیش از رسیدن به سرزمین نوید داده شده باید از صحرا گذشت.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6231,'هر انسانی دارای مراد دل و سرزمین نوید داده شده ای است؛ ولی چنان اسیر اندیشه های منفی خود شده اید که همه ی اینها دور از دسترس و نشدنی یا والاتر از آن می نماید که بتواند پیش بیاید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6232,'شاید همیشه نتوانیم اندیشه هایمان را مهار کنیم، ولی سخن خود را می توانیم.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6233,'مردم گمان می کنند که با فرار از شرایطی ناخوشایند، می توانند از شر آن رهایی یابند؛ بی خبر از اینکه به هر کجا بروند با همان شرایط روبرو خواهند شد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6234,'چیزی که می خواهم، در خانه ی خودم و در حیاط خودم است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6235,'اگر از دشواری های خود بگریزیم، آنها ما را دنبال خواهند کرد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6236,'از هیچ وضعیتی برآشفته نباشید تا وزن و سنگینی خود را از دست بدهد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6237,'زمانی که دیگر از هیچ چیز آزرده نشدید، ناآرامی های برونی هم ناپدید می شوند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6238,'هرگاه به اهمیت هر اندیشه و هر واژه پی ببرید، به این امر خو می گیرید که در هر اندیشه و هر واژه ی خود، دقت به خرج دهید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6239,'زمانی که به اندیشه های منفی توجه می کنید، به آنها پر و بال می دهید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6240,'واژه ها بسان اجسام یا نیروهایی مدام در جنبش اند تا در زمان خود به زندگی ِ آفرینندگان خویش بازگردند و ثمره ی خود را باز گیرند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6241,'مردمانی که پیوسته از تنگدستی و شکست و محدودیت سخن می گویند، همانها را خواهند دروید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6242,'برخی از مردم، آسان به ثروت دست می یابند، ولی شایستگی نگهداری از آن را ندارند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6243,'چه بسیارند مردمانی که با شرایطی که چنگی به دل نمی زند کنار می آیند؛ تنها به این دلیل که (از نظر ذهنی) تنبل تر از آن هستند که خود را بیرون از آن شرایط تصور کنند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6244,'برای به دست آوردن استقلال [ =خودسالاری ] مالی، باید از سر تا پا خواهش و آرزو بود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6245,'چه خوب است که گهگاه انسان به تماشای سحر برود تا با همه ی وجود، باور کند که آفتاب بی گمان خواهد دمید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6246,'هر روز، روزی پر اهمیت است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6247,'اگر کامیابی بخواهید، ولی برای شکست تدارک ببینید، همان چیزی را خواهید ستاند که برایش تدارک دیده اید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6248,'اگر خود را شکست خورده بدانید، بی تردید شکست خواهید خورد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6249,'هر روز، سخن درست و اندیشه های درست را برگزینید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6250,'همه ی ما گنجینه ای داریم که هرچه بخواهیم می توانیم از آن بیرون بکشیم؛ گنجینه ی گمان!((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6251,'راههای خدا خردمندانه و چاره های او حکیمانه است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6252,'هر پیشرفتی، نتیجه ی آرزو است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6253,'به آنچه توجه کنید، با آن یکی می شوید؛ پس اگر پیوسته متوجه امور بازدارنده باشید، آنها بزرگتر و بزرگتر می شوند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6254,'چه بسیارند مردمان کامیابی که دلیل کامیابی آنها، باور همسرشان به آنها بوده است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6255,'آنان که در گذشته زندگی می کنند، تماس خود را با حال شگفت آور از دست می دهند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6256,'چه بی شمارند مردمانی که مدام به انبارداری و اندوختن سرگرمند و هراسان از به کارگیری آنچه که دارند، کمبود و تنگدستی بیشتری را به زندگی خود فرا می خوانند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6257,'تنها در کاری می توانیم کامیاب شویم که به سختی، دوستدار آن باشیم.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6258,'نوآوران بزرگ هرگز از کار خود به ستوه نمی آیند؛ وگرنه نمی توانستند به نوآوری های بزرگ دست بزنند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6259,'هرگز کودکی را وادار نکنید چیزی بشود که نمی خواهد؛ چون جز شکست، نتیجه ای به بار نخواهد آورد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6260,'نخستین گام برای کسب کامیابی این است که از آنچه هستید، شادمان باشید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6261,'چه بسیارند مردمانی که از خود به ستوه آمده اند و آرزو دارند دیگری باشند؛ چنین مردمانی از باور و اتکا به نفس بی بهره اند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6262,'هدیه ی خدا به انسان، نیرو و توان است و چیرگی بر همه ی آفریده ها و ذهن و تن خویش.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6263,'همه ی ناکامی ها از نبود نیرو و توان است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6264,'آدمی پیوسته نتیجه ی سخن خود را می درود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6265,'دشمنان شما همه در درون خودتان هستند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6266,'تنها کسی که باید دگرگون شود، خودتان هستید؛ خودتان که دگرگون شوید، همه ی اوضاع و شرایط پیرامون تان نیز دگرگون می شود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6267,'زندگی هر کس، تصویری عینی از برآورد باورهایی است که در ذهن نیمه هشیار او نقش بسته است؛ پس به هر کجا که برود، درست همان شرایط را با خود می برد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6268,'از امور جزیی درس بگیرید تا برای اداره ی امور بزرگ آماده شوید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6269,'ترس، تنها دشمن آدمی است؛ هرگاه بترسید، شکست می خورید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6270,'هر شرایط ناخوشایندی که در زندگی تان پیش آید، نتیجه ی اندیشه ی منفی خودتان است؛ نتیجه ی گمانهای نادرست.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6271,'کسی که با خدا همراه است، شکست ناپذیر می شود.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6272,'اندیشه های منفی، شما را از اوج موجها به پایین می افکند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6273,'اندیشه ها و آرزوهایتان بسان گردونه ای همواره شما را به جایی می کشاند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6274,'آرزوهای به ستوه آورنده ی آدمی، به گونه ای اندوه بار برآورده می شوند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6275,'در باتری اندیشه ی خود، جریانی دیگرگون را روشن کنید تا بی درنگ در جهان عینی نیز دگرگونی را به چشم ببینید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6276,'سرزنش کنندگان همواره مورد سرزنش قرار می گیرند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6277,'ترس و نگرانی، راهزنان زمان هستند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6278,'ترس، بدترین دشمن آدمی است، زیرا از هرچه بترسید، همان بر سرتان می آید. به راستی، با ترس، آن را به سوی خود می کشانید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6279,'بیزاری و ناشکیبایی، نیرو و توان آدمی را از چنگ او می رباید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6280,'اگر رویایی در سر نداشته باشید، چه بسا در ظاهر، سخت کار کنید و باز هم به جایی نرسید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6281,'اگر به خودتان باور داشته باشید، دیگران نیز شما را باور خواهند داشت.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6282,'هر اندیشه ی منفی، یک دشمن خطرناک است.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6283,'بسیاری از مردم، آنچه را که از هر چیز دیگر عزیزتر می دارند، از دست می دهند، چون پیوسته در بیم از دست دادن آن به سر می برند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6284,'نمی توانید دست به کاری بزنید، اگر خودتان را در حال انجام آن نبینید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6285,'میزان کامیابی و نیک بختی هر کس، بستگی مستقیم به میزان برابری و هم سنگی او دارد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6286,'مهم نیست که چند بار شکست بخورید؛ دیگر بار برخیزید و از نو آغاز کنید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6287,'زندگی، یک جدول واژه های متقاطع است. واژه ی درست، پاسخ مورد نیاز را در دسترس تان قرار می دهد.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6288,'بسیاری از مردم، مطالبی [ =جستارهایی ] را دوست دارند که آنقدر دشوار باشد که نتوانند آن را درک کنند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6289,'مردم، آیین و رسومی را به جا می آورند که تنها نیمی از آن را درک می کنند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6290,'حق را بشناس تا حق، تو را برهاند.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6291,'برای اینکه فروشنده ی خوبی باشید، باید برای کالاهایی که می فروشید، شور و شوق داشته باشید.((فلورانس اسکاول شین))',88,0,0,0,0,0);
INSERT INTO mysms VALUES(6292,'سکوت را بیاموز؛ بگذار ذهن آرام تو گوش کند و بیاموزد.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6293,'به شخصی که لباس گرانمایه داشت و حرفهای بیهوده می زد، گفتند یا لباس خود را مانند سخنانت کن یا سخنان خود را مانند لباست.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6294,'خشم با دیوانگی آغاز می شود و با پشیمانی پایان می پذیرد.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6295,'دوست تو کسی است که هر گاه کلمه حق از تو بشنود، خشمناک نشود.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6296,'اصل حاکم بر زندگی ما انسانها احساس است.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6297,'اگر دوست تو مرتکب خطایی شد، از دوستی او صرفنظر مکن؛ زیرا که انسان در معرض خطا است. تا می توانی دوست برای خودت آماده کن نه از برای مالت.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6298,'اراده انسانی، در کنار سرنوشت او ایستاده و چرخ تکامل او را اداره می کند.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6299,'اراده به گونه ای طبیعی و خود به خود تکمیل می شود؛ به شرط آنکه آموزش و پرورش نادرست سد راهش نشود.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6300,'بهترین زمان برای تربیت اراده، ایام جوانی است.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6301,'بایستی با روح خود آشنا شده و سعادت را در ژرفای روح و قلب خود جستجو کنیم.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6302,'آدمی را آزمودن به کردار باید کرد نه به گفتار؛ چه بیشتر مردم، زشت کردار و نیکو گفتارند.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6303,'انسان، معیار همه ی چیزها است.((فیثاغورث))',89,0,0,0,0,0);
INSERT INTO mysms VALUES(6304,'وقتی به دنیا نگاه می کنم، بدبینم، اما وقتی به مردم نگاه می کنم، خوشبین هستم.((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6305,'تنها انسان فرهیخته کسی است که آموخته باشد چگونه یاد بگیرد و تغییر کند.((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6306,'جوهر و ذات خلاقیت، تازگی و نو بودن آن است و در نتیجه، ما استانداردی برای قضاوت درباره ی آن نداریم.((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6307,'برای کسی که کاملاً در برابر تجربه های جدید راحت (باز) است و حالت تدافعی نمی گیرد، هر لحظه، می تواند سرشار از تازگی باشد.((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6308,'من رفته رفته به یک نتیجه گیری بدبینانه و منفی درباره ی «زندگی خوب» رسیده ام. به نظر می آید که زندگی خوب شکل ثابتی ندارد، بلکه یک فرایند است؛ یک جهت است نه یک هدف.((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6309,'تجربه برای من بالاترین اولویت را دارد. ملاک درستی و منزلت هر چیز، تجربه ی خود من است؛ نظر افراد دیگر و حتی ایده های خودم نیز به اندازه ی تجربیاتم اعتبار ندارند.((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6310,'در ابتدای کارم این سوال را از خود می پرسیدم که: «چگونه می توانم این فرد را درمان کنم یا تغییر دهم؟» اکنون این سوال را بدین صورت تغییر داده ا م که: «چگونه می توانم رابطه ای با این فرد برقرار کنم که او بتواند از این رابطه برای رشد شخصی خودش استفاده کند؟»((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6311,'برای من هیچ چیز با ارزش‌تر از تجربه نیست. ابزار سنجش درستی هر چیز برای من، تجربه ی شخصی خود من است.((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6312,'نه ایده‌های کسان دیگر و نه حتی هیچ یک از ایده‌های خود من، درست تر از تجربه هایم نیستند.((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6313,'این تجربه هاست که باید بارها و بارها به آن بازگردم تا به نزدیکی بیشتری از حقیقت دست یابم. تنها به این ترتیب است که آن حقیقت، بخشی از وجود من می‌شود.((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6314,'چگونه این پیشامد رخ می‌دهد که هر اندازه بیشتر در خویشتن به عنوان موجودی ویژه و بی‌همتا فرو رفته و در پی هویت خویش می‌رویم، بیشتر کل انسان را در خود می‌یابیم؟((کارل راجرز))',90,0,0,0,0,0);
INSERT INTO mysms VALUES(6315,'همه چیز پول است و انسان پیش از آنکه به سیاست، علم، هنر، دین و ... بپردازد؛ باید بخورد ... .((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6316,'آزادی از جمله تجملاتی است که هرکسی توان کسب آن را ندارد.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6317,'هیچ کس مخالف آزادی نیست؛ نهایت امر، مخالف آزادی دیگران است.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6318,'اگر انسان در جسم خود فانی است، در عمل تاریخی خود باقی است.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6319,'انسانها با اشتراک در تولید اجتماعی وارد روابط معینی می شوند که گریزناپذیر و مستقل از اراده هایشان است.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6320,'این آگاهی انسانها نیست که هستی آنها را تعیین می کند بلکه این هستی اجتماعی آنهاست که آگاهی شان را تعیین می کند.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6321,'انسان، تنها از راه آگاهی می تواند به شأن سزاوارش دست یابد.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6322,'شیوه تولید زندگی مادی، ویژگی عام فراگردهای اجتماعی، سیاسی و روانی را تعیین می کند. این آگاهی انسانها نیست که هستی شان را مشخص می سازد، بلکه برعکس، این هستی اجتماعی آنها است که آگاهی شان را تعیین می کند.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6323,'تاریخ تمام جوامع موجود، تاریخ مبارزات طبقاتی بوده است که آزاده و برده، پاترسین و پلیبن، ارباب و سرف، استاد کارگاه و پیشه ور و روزمزد، در یک کلام، ستمگر و ستمدیده با یکدیگر ستیزی همیشگی داشته و به پیکاری بی وقفه، گاه نهان و گاه آشکار دست یازیده اند؛ پیکاری که هر بار منجر به نوسازی انقلابی کل جامعه یا نابودی همزمان طبقات در حال پیکار شده است.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6324,'رادیکال بودن به معنی فهم ریشه ای امور است، اما برای انسان، ریشه خود انسان است.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6325,'تنها با کمک حس آزادی است که جامعه می تواند بار دیگر به اجتماع انسانهایی تبدیل شود که برترین نیاز خود، یعنی تشکیل یک دولت دموکراتیک را متحقق سازند.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6326,'یونانیان، کودکی تاریخی بشریت اند؛ انسان نمی تواند بار دیگر به کودکی بازگردد، [ بلکه ] می تواند از سادگی کودک لذت ببرد و حقیقت آن را در سطحی برتر بازتولید کند.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6327,'اومانیسم، ضرورت انقلاب و رویای انسان رها شده از قید نابرابری و استثمار را تضمین می کند.((کارل هنریش مارکس))',91,0,0,0,0,0);
INSERT INTO mysms VALUES(6328,'برای زنده ماندن باید خود را تطبیق داد و برای تطبیق باید در خود تغییر ایجاد کرد.((کرت لوین))',92,0,0,0,0,0);
INSERT INTO mysms VALUES(6329,'هیچ چیز عملی تر از یک نظریه ی خوب نیست.((کرت لوین))',92,0,0,0,0,0);
INSERT INTO mysms VALUES(6330,'اگر به راستی می خواهید چیزی را درک کنید، بکوشید آن را تغییر دهید.((کرت لوین))',92,0,0,0,0,0);
INSERT INTO mysms VALUES(6331,'تجربه به تنهایی تولید دانش نمی کند.((کرت لوین))',92,0,0,0,0,0);
INSERT INTO mysms VALUES(6332,'فعالیت اجتماعی نیز مثل فعالیت جسمی به وسیله ی درک و احساس، راهبری می شود.((کرت لوین))',92,0,0,0,0,0);
INSERT INTO mysms VALUES(6333,'یک آدم موفق معمولاً هدف بعدی خود را خیلی بالاتر از آخرین دستاوردش قرار نمی دهد.((کرت لوین))',92,0,0,0,0,0);
INSERT INTO mysms VALUES(6334,'به جای بخشوده شدن، بیشتر، چشم داشت دلجویی داریم، تا دوباره بدی ها را ادامه دهیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6335,'هرگز هیچ چیز آن گونه که باید باشد نیست.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6336,'زیباترین زیبایی ها، قرار گرفتن یک دل در صف خورشید است؛ چیزی گریزان از هر چیرگی.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6337,'زمان، آن گونه آراسته نیست که می پنداریم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6338,'یک اثر هنری باید در نهاد قلب آفریده شود.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6339,'برای یک کودک، هیچ ترسی بزرگتر از کابوس نیست. حال آنکه ترس از مرگ، بزرگترین وحشت یک انسان سالخورده است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6340,'همواره کوچکترین ها، نجات بخش ما هستند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6341,'باور داشته باشیم که یک روز و تنها یک روز، دوستمان خواهند داشت و آن، پرواز مشخص قلب در روشنایی است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6342,'ما کم و بیش همیشه به زیبایی، چهره ای فریبنده و گمراه کننده می بخشیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6343,'شاید بهشت جایی است که نه تهدیدی احساس می کنیم و نه نیازی به دفاع.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6344,'طبیعت، آزمونی است که ما در قلب آن قرار گرفته ایم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6345,'آنچه وحشتناک است، تلاش نکردن برای ژرف نگری در زندگی است؛ همان زندگی ای که به ما هدیه داده اند و ما در آن، گم گشتگانی بیش نیستیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6346,'در اسارت جادوی زیبایی، باید آنچه را که زیبا است، با سرانگشت به دیگری نشان دهیم تا به آرامش دست یابیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6347,'زیبایی، چنان با بی بند و باری به بیرون برمی جهد که ممکن است لگدکوب شود.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6348,'گاهی برخی چیزها به اندازه ای به ما نزدیک می شوند که پژواک شان را می شنویم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6349,'به سختی می توان کسی را که عاشق پژوهش است، از کتاب خواندن بازداشت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6350,'طبیعت، کتاب همیشه بازی است که وظیفه ی باد، برگ زدن آن است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6351,'زندگی برای خشن ترین افراد هم دشواری های بسیار می آفریند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6352,'چهره ی زندگی حتی برای یک میلیاردر، آکنده از آزردگی، ترس، نگرانی و چشم داشت است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6353,'جامه ی برازنده ی پارسایان، که نمی توان بهایی بر آن نهاد، از تار و پود جان است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6354,'در کنار نردبانی که تا ستاره ها بالا می رود، از انبوه آرزومندان خبری نیست؛ دشواری کار از شمار مشتاقان کاسته است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6355,'وقتی که اندیشه در برابر سرچشمه ای باشکوه تر از خود قرار می گیرد، از گفتن باز می ماند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6356,'باور نداشتن به گفتار بی آلایش پارسایان، دشنام بر شکوه قهرمانانه ی آنها است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6357,'یک نویسنده بیشتر اسیر وظیفه است تا حق.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6358,'اگرچه پنهان کردن رنج، به نیرنگ و تردستی، روا نیست، این راه در سنجش، شگردی پسندیده تر است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6359,'همه چیز روی زمین، نتیجه ی اراده ی آدمیان است، جز نیکی که تنها نتیجه ی پذیرش کناره گرفتن آدمی است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6360,'شگفت آور است بگویم، ولی در پنجاه سالگی تازه می توانم نخستین گام هایم را بردارم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6361,'راستی، همچون عشق، شورش به پا می کند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6362,'هیچ نوشته ای آنقدر دقیق و درست نیست، مگر آنکه از جریان دگرگونی های همیشگی زندگی جدا نگردد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6363,'دلیری این نیست که زندگی را چون دوزخی در نظر آوریم، زیرا زندگی بیشتر، خود چنین است؛ پس بهتر آنکه برخلاف نماهای دوزخی اش، با هوایی بهشتی، روزگار به سر آوریم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6364,'نوزادان، آموزندگان اندیشه اند که هرگز غم به خود راه نمی دهند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6365,'اندوه، چون شیشه ی عطری بسیار گرانبها است؛ به اندازه ای که نمی توان برایش بهایی در نظر گرفت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6366,'هرچه دلهره بیشتر باشد، گمانها هم بیشتر خواهد شد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6367,'کتاب راستین همان است که پا به تنهایی ما می گذارد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6368,'یک بار زندگی می کنیم و تنها در زمان زیستِ خود می توانیم آن را بنگاریم. خط خوردگی ها، زخم های زندگی است که پابرجا خواهد ماند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6369,'انسان ها نمی دانند چگونه بنگرند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6370,'نویسندگی، نخ ارتباط میان پدر و فرزند است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6371,'هرگز برای آرامش بخشیدن و دلجویی کردن، دیر نیست.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6372,'عشق ورزیدن، بر زبان آوردن آن است. عشق یعنی خواندن کتاب دل دیگری، تا آن را همان گونه که می ستانیم هدیه کنیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6373,'هر کس نبشته ای است که با زبانی بیگانه نگاشته شده است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6374,'خوانش دیگری، آسان کردن تنفس او و به عبارتی، هستی بخشیدن به او است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6375,'شاید هر یک از ما خانه ای پر پنجره است که هر از گاه با شنیدن نام خود، یکی دو پنجره اش روشن می شود.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6376,'با آشکار شدن راستی، عشق آغاز می گردد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6377,'نمی توان به آنهایی که بیدار می کنند و آنهایی که گمراه می کنند، محبتی برابر داشت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6378,'بدترین کردار، چیدن گفته های راست و دروغ در کنار یکدیگر است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6379,'هوش سرشار، به تنهایی، به ویژه هوش سرشار بدخیم، پاسخگوی هیچ دردی نیست.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6380,'در یک متری خویش با کسی گفتگو می کنیم، ولی گویی سال های نوری از ما دور است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6381,'می توانیم از هر چیز، حتی از حقیقت، به شکلی نامناسب بهره بگیریم، همان گونه که می توانیم از همه چیز، ناب ترین ها را بهره مند شویم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6382,'می خواهم آنچه را که مردنی است، بیشتر بكُشم، تا زندگی بخش آنچه زنده است باشم. هر اندازه دنیا بیشتر به سیاهی بگراید، نیاز افزون تری به روشنایی خواهد داشت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6383,'اندیشه ی راستین، زودتر از همه به تکاپو می افتد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6384,'آتش، تصمیم گیرنده است؛ آتش جان هر جا بخواهد سر می کشد. برای برافروخته شدن، تنها به یک چوب خشک نیاز دارد، یعنی تنها به یک قلب استوار.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6385,'سخنان عشق پیوسته برپا دارنده ی صومعه ی کوچکی است که درون اش گفتگویی بی پایان، جریان دارد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6386,'عشق، زمانی است که براده های اندیشه ی ما مثل آهن ربا به درون دل دیگری بشتابد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6387,'طبیعت نیکی چنان است که هر چه از آن داشته باشیم، کمتر خواهیم داشت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6388,'زندگی سرشار از خوشگذرانی، بر چهره ی انسانها همچون مومی بسیار کهنه و زشت اثر می نهد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6389,'خشونت و نرمی از یکدیگر جدایی ناپذیرند. هیچ چیز به اندازه ی پنجه ی یک گربه نرم نیست، ولی یک گربه بدون چنگال اش، دیگر گربه نیست.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6390,'اندیشیدن، نگاهی است در ژرفنای یک چاه، که سطلی را آویخته به زنجیر در آن رها می کنیم و شادمانه می خواهیم آن را در کرانه ی آبهای سیاه، رخشنده از پرتو ستارگان، دوباره باز یابیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6391,'افسوس که نمی توان کسانی را که از پیش در خود احساسی نیافته اند، به درجه ای بالاتر از راستی و درستی رسانید.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6392,'نویسندگی، تجربه ای خنده آور است؛ باید هزاران بار مُرد تا به تازگی آغازین بنفشه ها دست یافت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6393,'اینکه مطلقاً [ =به تمامی ]، خودمان بمانیم، کاری بس دشوار است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6394,'از تیره بختی، نیرویی برتر نیز وجود دارد و آن، امیدواری است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6395,'شاعران، پرتوبخش درخشنده ترین نورها نیستند، ولی، هم اینان، نوری زیباتر از سروده دیده اند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6396,'برای اندیشیدن، می بایست به همه چیز دست یافت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6397,'روشنایی هستی و جهان، قلب های سوزان است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6398,'برخی از بی سوادان می توانند نگاهی بس برتر از قبیله ی سطحی نگر باسوادان داشته باشند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6399,'زندگی، دیوانه ای بیش نیست؛ هر گاه دلش بخواهد با نقشه ها، اهداف، محاسبات و باورهای ما مخالفت می کند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6400,'زندگی، منطق گریز است؛ همه چیز، زندانی زندگی است، به جز منطق بی انتهای دل.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6401,'حتی نادان ترین انسان، از خود در برابر نور یک تکاپو و بیان، نگاهبانی می کند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6402,'ساده اندیشی، همیشه محدودیتی دارد، ولی بی گناهی، سرشار از شادابی است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6403,'مهر ورزیدن به معنای [ =چَم ] آن است که در وجود خود، به شناخت چیزی فراتر از فهم و دانایی دست یابیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6404,'راستی، به خرگوش ها می ماند؛ [آنها] با گوش هایشان اسیر می شوند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6405,'کار هر کسی نیست که چرخ دنده های از هم پاشیده ای را از نو بر هم سوار کند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6406,'هنگامی که هر کس بر جای خویش نشسته، همه چیز دارای نظم است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6407,'اگر نویسندگان را از گذاشتن نام خود بر روی کتابی که نوشته اند، بازدارند، هیچ یک، نوشتن حتی یک جمله را آغاز نخواهند کرد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6408,'کتابها و آثاری که بزرگی و شکوه زندگی را گسترش می دهند، بسیار کمیاب اند و تنها نام دارنده ی اثر را بر جلد کتاب، بزرگ و بزرگتر می بینیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6409,'یک نقاش بزرگ و یا نویسنده ای نیرومند، زمانی که بر قله ی سربلندی ایستاده، شانس کمتری دارد تا دیگران به درک راستین هنر او نائل شوند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6410,'نویسنده ای که تنها در پی حُسن، زیبایی و کمال است، اسیر حُسن شناسی، زیبایی و کمال خواهد ماند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6411,'اگر بر مهربانی چون اسبی لگام بندیم و سایر عضلات او را رها بگذاریم، بدون شک، کار زیبایی خواهد بود، ولی، این هنوز دست یابی بر قلب نیست.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6412,'هیچ شماره ای در قلب جای نمی گیرد، جز یک، یک و همیشه یک.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6413,'سنگی که دانش و حتی الهام را تراش می دهد و آن را تیز می کند، قلب است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6414,'برای اینکه خردمند باشم، به خردمندان نیازمندم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6415,'با دقت در بیان راستی و درستی، می توان به گمان دست یافت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6416,'حقیقت، بازگرداننده ی راستی نخستین به ما است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6417,'چشم داشتی که باید از یک کتاب داشت، این است که شفاف باشد و نور را بازتاباند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6418,'بدبختانه، آن گونه که به آسودگی، گناهان دیگران را می بینیم، بینای گناهان خویش نیستیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6419,'اگر می دانستیم نوشته هایمان افشاگر همه چیز ما است، دیگر یارای نوشتن نداشتیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6420,'راستی، هرچه را که ما به او می دهیم، بی هیچ کم و کاست به ما بازمی گرداند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6421,'هنگامی که خود ناچیزم، آنچه می بینم نیز بی مقدار است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6422,'زمانی که می خوانم، چیزی است که [آن را] می جویم؛ در پی چیزی هستم که هنوز دنیا آن را نیالوده باشد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6423,'هر کس، خود، راه خویش را می یابد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6424,'راستی، حتی هنگامی که خوشبختی به ارمغان می آورد، گران تمام می شود.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6425,'دروغ، تمام سکه های تقلبی را به جریان می اندازد، ولی به هنگام جستجوی طلا، یکه و تنهاییم؛ همان گونه که در سوگ تنهاییم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6426,'شگفت انگیزترین چیز، لبخند است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6427,'زیباترین لبخندها همان است که به ناگهان بر چهره ای خاموش و غمگین نقش می بندد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6428,'لبخند راستین، لبخند کسی است که همه چیز را یافته است: دیگر حساب یا فریبی در کار نیست.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6429,'اگر دانشی در من وجود داشته باشد، چنین است: هنر وجود داشتن به تمامی و با دقتی بی نهایت و استوار.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6430,'درک بسیاری از آدمیان، دشوار است؛ چون یا در چارچوب گفتارشان نمی گنجند، یا روح خود را از دست داده اند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6431,'همدلی یعنی آنکه با شتاب آذرخش، آنچه را که دیگری احساس می کند، دریابیم و آسوده دل باشیم که کار نادرستی نمی کنیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6432,'بهترین راه احساس، نه با دست سودن، بلکه در راه دل پیش رفتن است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6433,'اگر کسی، زندانی همیشگی گذشته ی خود نبوده باشد، برای همیشه رنگی از تبار و دودمان خویش بازگو خواهد کرد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6434,'هنگامی که آدم، کسی یا چیزی را دوست دارد، همیشه چیزی برای گفتن یا نوشتن به او پیدا می‌کند، تا پایان زندگی.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6435,'هنگامی که آدم، کسی را دوست نداشته باشد، نه در این سوی زندگی و نه در آن سو، اهمیتی ندارد که نمی‌تواند او را ببیند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6436,'برای سخن گفتن حتماً [=به ناگزیر] نیاز نیست واژه‌ها را به کار ببری.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6437,'لبخند از اندیشه باور کردنی‌تر است. لبخند، بسیار کارآمدتر و بسیار پُرمعنی‌تر [=پُر چَم‌تر] است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6438,'ما گمان می‌کنیم که عاشق افراد می‌شویم. به راستی، عاشق دنیا هستیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6439,'در همه جا جزئیات اهمیت دارند، فقط جزئیات.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6440,'کودکی، دنیای شگفتی‌‌ست، دوست داشتنی و کسل کننده؛ هم گنج است و هم ابهام.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6441,'لبخند، مانند یک ارتش پیشتاز است. تغییر [=دگرگونی] چهره‌ای‌ست که پس از چهره می‌ماند، از چهره جدا می‌شود و در دوردست‌ها پرواز می‌کند؛ بسیار دورتر از چهره‌ای که لبخند را پدید آورده و لبخند در آن بر لب‌ها نشسته است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6442,'هیچ چیز نگران کننده‌تر از این خُلق و خوی بزرگترها نیست که هرگاه با بچه‌ها سخن می‌گویند، شیر و شکر به گفته‌هایشان می‌افزایند، به جای اینکه بسیار ساده و آسان، همان گونه که بین خودشان سخن می‌گویند، با بچه‌ها نیز به گفتگو بنشینند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6443,'این یک قانون کهن دنیاست، قانونی نامکتوب [=نانوشته]: هر کسی که چیزی بیشتر دارد، در همان دَم، چیزی هم کمتر دارد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6444,'دنیا مانند انباری، یک باغ، یک اتاق پُرهمهمه و یک بازار است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6445,'زندگی، به فیلمی از لورل و هاردی شباهت دارد. زنجیره‌ای از دردهاست که تاب آورده و سپس، به دیگران می‌دهیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6446,'من چیزهایی را که در دنیا وجود ندارند، چیزهایی را که اندکی فراتر از دنیا شناورند، ترجیح [=برتری] می‌دهم. من ترجیح می‌دهم وارد دنیا نشوم، در آستانه‌ی دنیا بمانم، بنگرم، بی‌نهایت بنگرم، عاشقانه بنگرم، فقط بنگرم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6447,'تنهایی، گاه آدم‌ها را پَس می‌زند، گاه نیز تنهایی، آدم‌ها را به سوی خود می‌كِشد؛ چون به همان اندازه که دریاچه وجود دارد، تنهایی هم وجود دارد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6448,'به راستی، کسی که از گفتگو با یک ابلهِ [=نابخرد] مسلم، لذت ببرد، ابله نیست؟((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6449,'اگر آدم، دیرگاهی به چیزی بنگرد، تبدیل به همان چیز می‌شود.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6450,'آدم فقط چیزهایی را به دیگران می‌دهد که خودش دوست دارد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6451,'هر کسی شرم و حیای خودش را دارد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6452,'هیچ چیز دوام ندارد؛ هیچ چیز تا ابد باقی نمی‌ماند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6453,'در جایی که فقط یک دیوانه وجود دارد، همه بی‌گمان می‌دانند که دیوانه نیستند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6454,'اگر نتوانید پیش‌بینی کنید که در آنچه پایان می‌پذیرد، چیز دیگری آغاز می‌شود، چه بسا نگران کننده خواهد بود.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6455,'فروختن آب به کسی که در بیابان مانده، کار آسانی‌ست. هر کسی می‌تواند این کار را انجام دهد. بازاریاب راستین، کسی است که به صحرانشینان، ماسه می‌فروشد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6456,'شیفته کردن با دوست داشتن فرق دارد [=دیگرگون است].((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6457,'زندگی، هدیه‌ای‌ست که من، هر بامداد که از خواب برمی‌خیزم، روبان‌های دُور آن را به آرامی باز می‌کنم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6458,'زندگی، مانند جریان آب یک رودخانه است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6459,'دیروز، با همه‌ی جوانی‌اش، دیگر نیست. امروز، اینجاست و به زیبایی می‌گذرد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6460,'بهتر است آدم کارهای کمی انجام دهد، ولی از آنها لذت کامل ببرد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6461,'اسراری در این سوی زندگی وجود دارند و اسراری در آن سو. همه جا پُر از اسرار است؛ بهتر آنکه اسرار را پذیرا باشیم و چیزی نپرسیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6462,'کسی که بتواند کارهای کوچک‌تر را انجام بدهد، در کارهای بزرگتر هم کامروا می‌شود، یا برعکس.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6463,'به باور من، هیچ چیز را نباید نادیده گرفت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6464,'برخی چیزها را نباید گفت. برخی چیزها را اگر بگویی، رخ می‌دهند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6465,'زمانی که آدم بچه دارد، باید در اندیشه‌ی تربیت [=پرورش] او باشد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6466,'رازها روی نوک زبان ما مانند فلفل قرمزند؛ دیر یا زود، زبان‌مان را آتش می‌زنند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6467,'آدم، همیشه کم و بیش، عاشق است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6468,'حقیقت [=راستی]، باور نکردنی‌ست.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6469,'یک زن به تنهایی، هنگامی که عاشق باشد، زمین و آسمان را پُر می‌کند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6470,'راز، مانند طلاست؛ زیبایی طلا در این است که می‌درخشد و برای اینکه بدرخشد، نباید آن را در نهان‌‌خانه، نگه داشت، باید در روز روشن، آن را بیرون آورد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6471,'اگر راز فقط از آنِ خودمان باشد، ارزشی ندارد. برای اینکه یک راز، راز بماند، باید آن را به کسی گفت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6472,'وابستگی به آدم‌هایی که دو یا سه سال برایشان زیاد دلنشین نبوده‌اید، نگران کننده است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6473,'فقط به ندای کودک درون خویش گوش بسپار نه هیچ ..... .((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6474,'آنچه ترس را برمی‌انگیزد، ناشناخته‌هاست.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6475,'لبخند، حتی زمانی که بر لبان یک مرده می نشیند، باز هم زیباست.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6476,'همه ی ما به نوعی دیوانه ایم. بدترین نوع دیوانگی آن است که موهبت خیال پردازی را از دست بدهیم.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6477,'وقتی نانوا با دقت و وسواس نان را می پزد و به دست مشتری می دهد، خدا با او در کنار تنور ایستاده است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6478,'بیشتر انسان‌ها، هنگام زایش، به همان سادگی که یک کتاب در اسباب‌کشی گُم می‌شود، روح خود را گم می‌کنند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6479,'آدم یا بی‌درنگ از کسی خوشش می‌آید، یا هرگز خوشش نمی‌آید.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6480,'شیرینی‌پزی و عشق، همچون یکدیگرند؛ پای طراوت [=تازگی] در میان است و همه‌ی مواد آن، حتی تلخ‌ترین‌شان، به شیرینی دلپذیری تبدیل می‌شوند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6481,'عشق، همچون سیرک، دایره‌ای می‌سازد، با خاک اره فرش شده و زیر پاهای برهنه، نرم است و زیر پارچه‌ی قرمز ورم کرده از باد، درخشان. دایره‌ی ساده‌ای است؛ هر اندازه دوست داشتنی‌تر باشید، بیشتر دوست‌تان خواهند داشت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6482,'من زندگی‌ای را خواسته‌ام که کسی نتواند آن را خلاصه [=کوتاه] کند؛ زندگی‌ای همچون موسیقی، نه همچون سنگ مرمر یا کاغذ.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6483,'شگفت‌آور است که آدم برای نگه‌داشتن مردم، چه سخنان نابخردانه‌ای می‌تواند بر زبان آورد- و باز چه اندازه شگفت‌آور است که مردم، سخنان نابخردانه‌ای را که به آنها گفته می‌شود، باور می‌کنند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6484,'شادمانی و خوشبختی، در یک نُت تنها نهفته نیست، شادمانی، آن چیزی‌ست که در دو نُتی که با هم برخورد می‌کنند، وجود دارد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6485,'هنگامی که، همه آدم را دوست دارند، دیگر دنیا برایش بی‌اهمیت است، بسیار کمتر نیاز دارد جایی در آن برای خودش دست و پا کند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6486,'همیشه چیزی برای دیدن وجود دارد، همه جا.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6487,'کودکی، مانند قلبی‌ست که تپش‌های زیادی تُند آن، آدم را می‌ترساند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6488,'یک انباری هم می‌تواند خانه‌ای رؤیایی باشد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6489,'شنیدن چیزی که آدم از پیش می‌داند، دشوار است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6490,'فقط یک چیز در زندگی به شمار می‌آید و آن، شادی‌ست؛ هیچ‌گاه نگذار کسی آن را از تو بگیرد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6491,'عشق، امر بسیار کوچکی‌ست، نباید راز بزرگی از آن ساخت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6492,'آدم چگونه می‌تواند عاشق کسی نشود که روی قرن‌ها جدیت و سلیقه، سیل به راه انداخته؟((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6493,'زندان، هر اندازه هم آسان و قشنگ باشد، باز هم زندان است؛ آدم بسیار آسان پا به درون آن می‌گذارد و سپس، بسیار زمان و تلاش لازم است تا بتواند از آن بیرون بیاید.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6494,'راه درست برای بچه‌ها، هرگز همان راه پدر و مادر نیست، هرگز.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6495,'فقط در عشق است که آدم، دوستش را خوب می‌شناسد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6496,'زندگی مشترک، بزرگ و بی‌پایان است؛ از یک سو می‌تواند نابود شود و از سوی دیگر، به آرامی ادامه یابد. زندگی مشترک، حیوان بزرگ پایداری‌‌ست، به دشواری می‌میرد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6497,'فقط یک دنیا وجود دارد، آن هم دنیای توانگران است و کنار آن یا پشت سرش، ساختمان‌های بی‌قواره‌ی پس مانده‌های این دنیا.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6498,'تجربه‌ی خوار شدن، همچون تجربه‌ی عشق است؛ فراموش نشدنی.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6499,'خِرد، به عکس آنچه می‌گویند، با بالا رفتن سن به سراغ آدم نمی‌آید، این امر بسته به دل است و دل هم با زمان، سر و کاری ندارد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6500,'اگر حقیقت [=راستی و درستی]، همیشه و همه جا گفته می‌شد- حقیقتی که در دل‌مان در ترنم است- زندگی بامزه‌تر، شاید از هم گسیخته‌تر و بسیار سرزنده‌تر می‌شد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6501,'هیچ‌کس نمی‌تواند همه چیز را بدهد، هیچ‌کس برای هیچ‌کس بَس نیست، هیچ‌کس خدا نیست.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6502,'گفتار است که آدم‌ها را به هم پیوند می‌دهد و از هم جدا می‌کند. گفتار است که خانواده‌ها را می‌‌سازد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6503,'آنچه آدم نسبت به چیزی احساس می‌کند، به ستوه آورنده‌تر از خودِ آن است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6504,'هنر بزرگ، هنر فاصله‌هاست. آدم، زیادی نزدیک باشد می‌سوزد، زیادی دور، یخ می‌زند؛ باید نقطه‌ی درست را پیدا کرد و در آن ماند.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6505,'برای دانستن، باید بهایش را پرداخت.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6506,'اگر آدم گرفتار رنج عشق است، سبب نمی‌شود اجازه‌ی هر کاری را به خودش بدهد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6507,'کسانی که دوست‌مان دارند، بسیار بیشتر از کسانی که از ما بیزارند، ترسناک هستند؛ ایستادگی در برابرشان هم بسیار دشوار است.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6508,'شاید آدم هیچ‌گاه کاری را برای خودِ آن کار نکند، بلکه برای این باشد که به خودش فرصت انجام کار دیگری را بدهد.((کریستین بوبن))',93,0,0,0,0,0);
INSERT INTO mysms VALUES(6509,'برای ارتباطات، هیچ پایانی نیست.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6510,'رنجهای ناشی از سرکوبی و قوانین بی رحم ناشی از وفادار ماندن به یک الگو، هیچ یک منجر به درک حقیقت نمی شوند.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6511,'اگر تفکر قادر به دریافت حقیقت باشد، نمی توان آن امر را حقیقت نامید.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6512,'ما نتیجه ی انواع نفوذها و تاثیرات هستیم و هیچ چیز تازه ای در ما وجود ندارد؛ چیزی که خودمان آن را کشف کرده باشیم؛ چیزی که بکر و دست نخورده باشد.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6513,'نداشتن بلوغ روانی، تنها به دلیل ناآگاهی از خود است.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6514,'دریافتن و ادراک خود، آغاز حکمت است.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6515,'راستی، هیچ جاده ای ندارد و همین، زیبایی راستی است.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6516,'اگر شما بکوشید خود را بر مبنای [فرد] دیگری مورد بررسی قرار دهید، همواره موجود دست دومی باقی خواهید ماند.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6517,'نظمی که از بیرون تحمیل می شود، همیشه ناگزیر، آبستن بی نظمی است.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6518,'آزادی هرگز کاری به نادرست انجام نمی دهد، آزادی کاملاً با نافرمانی دیگرگون است.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6519,'اگر آموختن درباره ی خود، بر پایه ی گفته های دیگران باشد، ما درباره ی آنها چیز آموخته ایم و نه درباره ی خویش.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6520,'هرگز راهی به واقعیت وجود ندارد، همان گونه که راهی به حقیقت نیز وجود ندارد؛ شما ناگزیرید خود، راهنما و قانون خود باشید.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6521,'با حقیقت روبرو شوید، به آن نگاه کنید، از آن نگریزید؛ زیرا زمانی که فرار می کنید، زمان آغاز ترس است.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6522,'یادگیری، حرکتی است همیشگی و بدون زمان و بدون گذشته.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6523,'هریک از ما تصوری از آنچه که می اندیشیم هستیم یا باید باشیم، در ذهن داریم و آن تصور، ما را از دیدن خود، همان گونه که به راستی هستیم، باز می دارد.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6524,'ساده نگریستن به هر چیز، یکی از دشوارترین کارها در دنیا به شمار می آید. زیرا ذهن ما ذهنی پیچیده است.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6525,'مادام که حیوان، ناز و نوازش می شود، واکنش های زیبایی دارد، ولی به محض اینکه با او مخالفت می شود، همه ی خشونت طبیعتش پدیدار می گردد.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6526,'برای درک یک حقیقت، باید به آن نگاه کنیم، نه اینکه از آن بگریزیم.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6527,'زندگی کردن در همین زمان [اکنون]، یک دریافت آنی و زیبا و شادی بزرگ است، بدون جستجو و کسب لذت از آن.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6528,'بسیاری از ما می خواهیم ذهنمان را پیوسته سرگرم نگهداریم، تا خود را آنگونه که هستیم نبینیم.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6529,'برای کشف، برای یادگیری چیزی بنیادی، شما ناگزیرید گنجایش رفتن به عمق را داشته باشید.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6530,'شما هنگامی می توانید در ژرفا نفوذ پیدا کنید که ذهنتان به تیزی یک سوزن و به استواری و قدرت یک الماس باشد.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6531,'زمانی، تهیدستی، چیز زیبا و شگفت انگیزی خواهد بود که ذهن، از جامعه رها شود.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6532,'آنچه ذهن را از وابستگی رها می کند، دیدن کل ساختار و طبیعت برانگیختن و وابستگی است و دیدن اینکه چگونه آن وابستگی، ذهن را کودن، منگ و کم کار می کند.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6533,'اگر شما خود را با دیگران نسنجید، آنچه که هستید، همان خواهید بود.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6534,'زمان، فریب دهنده ای بیش نیست، بدین جهت که هیچ کاری برای کمک به ما در راه دگرگون ساختن خود نمی کند.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6535,'مسائل، تنها در زمان وجود دارند و هستی پیدا می کنند. یعنی هنگامی که ما با موضوعی به گونه ی کامل برخورد نمی کنیم، خود این عدم برخورد کامل با موضوع، سبب آفریدن مسئله می شود.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6536,'تا زمانی که ما از زندگی می ترسیم، از مرگ نیز هراسان خواهیم بود.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6537,'عشق، زمانی تحقق می یابد که تسلیم و رهایی کامل از خود وجود داشته باشد.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6538,'تنها یک ذهن بی گناه می داند که عشق چیست، این، تنها ذهن بی گناه است که می تواند در دنیایی که پاک نیست، زندگی کند.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6539,'هرگز مرکزی نیست، از این رو عشق هست.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6540,'یکی از دشواریهای بزرگ ما، ندیدن خودمان به گونه ای درست و روشن است.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6541,'تنها، ذهنی که به یک درخت یا به ستارگان یا به تلألوی آب یک رودخانه با واگذاری و گردن نهادن خود می نگرد،می داند زیبایی چیست.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6542,'تنها، زمانی که ما پیوند راستین بین یکدیگر را درک کنیم، امکان عشق ورزیدن و عاشق شدن وجود دارد.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6543,'شما درباره ی زیبایی یا عشق، سخن می گویید و درباره ی آنها بسیار می نویسید، ولی هرگز عشق و زیبایی را نشناخته اید، به جز در زمانهای اندکی از وانهادن خود.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6544,'تا زمانی که مرکزی وجود دارد که در پیرامون خود، فاصله هایی را می آفریند، نه عشق وجود خواهد داشت و نه زیبایی.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6545,'زندگی کردن یعنی همواره در زمان اکنون بودن.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6546,'اگر انسان بخواهد چیزی را به گونه ای روشن و دقیق ببیند، می بایست ذهنش بسیار آرام و به دور از همه ی کینه ورزی ها، فریب ها، گفتگوها، تجسم ها و تصاویر باشد.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6547,'تا زمانی که اندیشه ای از حافظه، تجربه یا دانش، که همه مربوط به گذشته اند، سرچشمه نگرفته باشد، به هیچ وجه، شخصی به نام اندیشمند هم وجود نخواهد داشت.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6548,'ما همواره در حال کشیدن بارهای گوناگون هستیم. ما هیچ گاه با گذشته نمی میریم و هیچ گاه گذشته را پشت سر نمی گذاریم.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6549,'یک ذهن زنده، ذهنی است آرام، ذهنی است که نه مرکزی دارد و نه مکانی و نه زمانی. یک چنین ذهنی نامحدود است، یک چنین ذهنی، تنها حقیقت موجود است.((کریشنا مورتی))',94,0,0,0,0,0);
INSERT INTO mysms VALUES(6550,'دلبستگی و پایبندی با هم فرق دارد. زمانی که به کاری دلبسته هستید، در زمان مناسب، آن را انجام می‌دهید. زمانی که به کاری پایبند هستید، هیچ‌گونه بهانه‌ای را نمی‌پذیرید، جز نتایج را.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6551,'عشق این توانایی را می دهد که بگویید پوزش می خوا هم.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6552,'بسیاری از مشکلات ما، نه در گروی تجهیزات، که در گروی انسان هاست.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6553,'هیچ چیز جای استقامت و پشتکار را نمی گیرد؛ از استعداد و نبوغ هیچ کاری ساخته نیست.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6554,'سیر هر کس به اوج خویشتن است.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6555,'آرزوهای واپسین دم زندگی، خانه بزرگتر و ثروت بیشتر نیست، بلکه دوست داشتن دیگران، رسیدگی به خود و بهبود روابط با دیگران است.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6556,'افراد با هدف مادی در نیمه اول عمر، سلامتی خود را برای کسب ثروت از دست می دهند و در نیمه دوم عمر، ثروت خود را برای کسب سلامتی از دست می دهند.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6557,'گرفتاری کار روزانه مجال بیداری خود درونی و برتری را نمی دهد. برای رسیدن و پیدا کردن خود درونی سعی کنید با خود خلوت کنید.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6558,'تغییر با قانون، قدرت و زور بیرونی میسر نیست؛ تغییر باید پذیرفته شود.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6559,'هدف بدون رسالت، ما را به جایگاه بلند مرتبه ای نمی رساند.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6560,'کار کردن بدون رعایت اصول اخلاقی به فساد و تباهی می انجامد.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6561,'زیستن بدون رفتار بر پایه ارزشها، بی ارزش است.((کن بلانچارد))',95,0,0,0,0,0);
INSERT INTO mysms VALUES(6562,'کسی که مایل است خیر دیگران را تامین کند، خیر خود را هم تامین کرده است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6563,'به جای آنکه به تاریکی لعنت بفرستید یک شمع روشن کنید.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6564,'هر حادثه ای را [ اگر ] در آغاز پیدایش آن بشناسید، می توانید جریان و اثراتش را تا حدودی تغییر بدهید.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6565,'مردی که دارای عزمی نیرومند و اخلاقی متین است، هرگز فضیلت اخلاقی خود را فدای هوس های زندگی نمی کند. آری مردانی در این جهان زندگی کرده اند که برای تکمیل و حفظ فضایل اخلاقی، جسم و جان خود را فدا ساخته اند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6566,'از اشتباهات خود شرمنده نشوید و آن را جرم ندانید.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6567,'زمانی که دانش یک مرد برای موفقیت کافی است، اما تقوای او کافی نیست، هر چه را که او ممکن است به دست آورد دوباره از دست خواهد داد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6568,'اگر یک سال، ثمر از کاری خواستی گندم بکار و اگر دو سال خواستی درخت بکار و اگر صد سال ثمر خواستی مردم را تربیت کن.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6569,'اگر غذایی نامطبوع بخوری، آبی ناگوار بنوشی و بر خم بازوانت بالین بسازی، هنوز هم می توانی سرور و شادمانی را بیابی.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6570,'تو نیکی را از دست مده، دیگران نیز خوب خواهند شد. وجود نیکان همچون باد و [ وجود ] بدان همچون گیاه است. هرجا باد بوزد، گیاه به ناچار سر فرود می آورد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6571,'کسی که می خواهد خیر دیگران را تامین کند، خیر خودش را هم تامین کرده است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6572,'مردی که خصلت نیک دارد، هرگز تنها نمی ماند؛ زیرا همیشه دوستانی برای خود پیدا می کند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6573,'من از یاد دادن آنچه که یاد گرفته ام، هرگز خسته نشده ام. این تنها خدمت ناچیزی است که می توانم آن را به خود نسبت دهم.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6574,'مردی را که آماده آموختن است تعلیم ندادن، انسانی را به هدر دادن است، مردی را که آماده آموختن نیست تعلیم دادن، سخن به هدر دادن است، خردمند نه انسان را به هدر می دهد و نه سخن را.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6575,'میانه روی و حد خود نگاه داشتن، کمال سرشت آدمی است .((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6576,'تمایل برای انجام پرشتاب کارها، از انجام کامل و جامع آن‌ها جلوگیری می‌کند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6577,'نگریستن به سودهای اندک، مانع به فعلیت درآمدن کارهای بزرگ می‌شود.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6578,'کارهای بزرگ، ترسی از زمان ندارند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6579,'آنکه به فکر فردا نیست، به غم فردا گرفتار خواهد شد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6580,'اگر شما آدمی نادان و بی خبر باشید و به این امر پی ببرید، این خود یک نوع آگاهی و دانش است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6581,'در گذشته، مردم به کسب دانش می پرداختند تا خود را به درجه کمال برسانند. امروزه دانش را برای این فرا می گیرند که دیگران را شیفته و فرمانبردار خود سازند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6582,'اگر انسان یک روز از خودخواهی [ اش ] بگذرد، همه او را نیک خواهند گفت.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6583,'در به کار بردن کلمه ها دقت کنیم. چون ممکن است یک کلمه ما را عاقل و کلمه دیگر ما را کم عقل جلوه دهد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6584,'مرد آزاده پیوسته می کوشد که در گفتار خود، آهسته و در کردار خود، تند و تیز باشد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6585,'هدف هر حکومت و دولت تنها بهتر کردن ابزارهای زندگی مادی و راحتی مردم نیست، بلکه حکومت وظیفه دارد سعادت مردم را نیز تامین کند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6586,'من از این اندوهگین نمی شوم که مردم مرا چنانکه هستم نمی شناسند، بلکه از آن غمگینم که من مردم را چنانکه باید و شاید نمی شناسم.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6587,'تماشاکردن ستمکاران و گوش دادن به سخنانشان آغاز ستمکاری است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6588,'از برفی که بر پشت بام همسایه است شکایت مکن؛ در حالی که جلوی خانه خودت کثیف و پر برف است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6589,'خطا کردن و آن را اصلاح ننمودن، خود به منزله ارتکاب خطای دیگری است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6590,'اگر چیزی را می دانی، باید بدانی که آن را می دانی و اگر چیزی را نمی دانی، باید بدانی که آن را نمی دانی؛ دانستن حقیقی همین است و بس.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6591,'آموزگار و دانش آموز باید زندگی خود را نمونه و نشانه درخشان آنچه می آموزند قرار دهند، وگرنه مانند کسانی هستند که راهی را شناخته اند، ولی از آن راه نمی روند و یا مانند بیمارانی هستند که داروی شفا بخش را از طبیب گرفته اند، ولی آن را نمی خورند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6592,'کسب دانش وظیفه اساسی هر انسانی است. در [ راه انجام ] این وظیفه، دانشجو می تواند و حق دارد که از آموزگار خود هم پیشی بگیرد و زودتر به مقصد برسد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6593,'شتاب در یاد گرفتن مثل این است که از پی کسی بدوی و به او نرسی و بترسی که او را گم کنی.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6594,'من یکبار در تمام روز چیزی نخوردم و در تمام شب نخوابیدم تا بتوانم مسئله ای را حل کنم، اما نتیجه نداد. از این رو فهمیدم که بهتر است آنچه را نمی دانم، بپرسم.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6595,'اگر می دانی پایداری کن و اگر نمی دانی بگو نمی دانم؛ این نشان دانش است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6596,'من از کسانی که حیله گری را حکمت، نافرمانی را شجاعت و یاوه گویی را حقیقت می پندارند، نفرت دارم.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6597,'مردان بزرگ کم می گویند و بسیار می کوشند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6598,'کسی که معلومات جامع درباره ادبیات دارد، مطابق با قواعد فضیلت اخلاقی رفتار می کند و می تواند به جایی برسد که از خطا و لغزش به دور بماند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6599,'سه نوع دوستی است که در همه جا پر سود و با ارزش است و سه نوع دوستی دیگر هست که زیان آور و باعث پشیمانی است : دوستی با کسانی که در دوستی خود پایدار و با وفا هستند و با کسانی که درستکار و راستگویند و با آنان که تجربه بسیار دارند، سودمند است، ولی دوستی و رفاقت با چاپلوسان، ریاکاران و یاوه گویان موجب بدبختی و رسوایی است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6600,'اگر می بایست من از میان سیصد شعر حماسی یک جمله را برگزینم که تمام آموزشهایم را در بر داشته باشد، این جمله را بر می گزینم : هیچ اندیشه بدی را در سر یا در دل خود جای مده.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6601,'آهستگی و آرامش در سخن گفتن کار آسانی نیست. در این جهان که بیشتر مردم بر ضد یکدیگر می خروشند، خودداری از هیجان و کینه و پرخاشگری بسیار دشوار است. برای رهایی از این دشواری، راهی جز آرام و نرم سخن گفتن وجود ندارد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6602,'دانش بی اندیشه دام است و اندیشه ی بی دانش بلا.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6603,'زیان و درد برایمان آنقدر مهم نیست؛ آن چیزی که موجب ناراحتی است، به یاد آوردن آنها است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6604,'یک مرد خشمگین وجودش پر از زهر است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6605,'برگ در هنگام نابودی می افتد، میوه در هنگام کمال می افتد؛ بنگر که چگونه می افتی چون برگی زرد و یا سیبی سرخ.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6606,'مرد خشمگین پر از زهر است و زندگی را زهرآگین می کند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6607,'لیاقت و استعداد شخصی، وابسته به هدف عالی هر شخصی بوده و موفقیت، نتیجه مستقیم سعی و کوشش است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6608,'ثروت و افتخاری که از راه نامشروع به دست آمده مانند ابری زودگذر است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6609,'بدی را با عدالت پاسخ دهید، مهربانی را با مهربانی.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6610,'آماده شدن برای دستگیری از پیران و وفاداری نسبت به دوستان و مهر ورزیدن به مردم، آرزوی من است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6611,'آنچه را می شنوم فراموش می کنم، آنچه را می بینم به یاد می سپارم و آنچه را انجام می دهم درک می کنم.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6612,'هر چیزی زیبایی های خاص خودش را دارد، اما هرکسی نمی تواند آنها را ببیند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6613,'اگر مردم مرا نشناسند غصه نخواهم خورد، اما من اگر مردم را نشناسم افسرده خواهم شد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6614,'کار دلخواه برگزین تا رنج نبینی.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6615,'این خوبی که در ما وجود دارد؛ کار خدا است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6616,'دانستنی های کهنه را به کار بردن و از روی آن دانستنی های تازه ای به دست آوردن از اصول عمده ی آموزش است، هر که این کار را انجام دهد می توان او را آموزگار نامید.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6617,'کسب دانش را همیشه ناتمام بدان و از کوشش باز نمان.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6618,'مرد آزاده، پروردن هنرهای عالی را اساس همنشینی با دوستان خود قرار می دهد و از راه همنشینی با دوستان خود، فضایل اخلاقی را در نفس خویش به کمال می رساند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6619,'انسان بزرگ در جستجوی حق است و انسان پست به دنبال منفعت.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6620,'خوب فکر کردن یعنی اینکه بدانی چگونه از ذهن و قلب، از نظم و احساس استفاده کنی. وقتی چیزی را بخواهیم، زندگی، ما را به سوی آن هدایت می کند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6621,'مرد آزاده دقت نظر مردم را به ویژگی های نیک دیگران جلب می کند نه به ویژگی های زشت ایشان و مرد عامی، عکس آن رفتار می کند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6622,'سه راه برای رسیدن به شناخت وجود دارد: نخست، اندیشیدن که بهترین راه است. دومی تقلید که آسانترین است و سومی تجربه که تلخ ترین است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6623,'هرکس باید با تمام قوت مردم را دوست بدارد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6624,'ایده آل مرد نجیب، داشتن خوی پاک است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6625,'یک دقیقه موفـقیت، شکسـت چندین سالـه ی انسان را بی اثر کرده و جبران می کند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6626,'مرد آزاده به هیچ وجه ابزار دست کسی قرار نمی گیرد، یعنی برای وی شایسته نیست که خود را ابزار هدف دیگران سازد؛ او هدف خویشتن است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6627,'اگر به راه خطا رفتی از برگشتن مترس.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6628,'اشتباه را درست نکردن، خود اشتباه دیگری است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6629,'کسی که دور اندیش نباشد، گرفتار خطرهای نزدیک می شود. دور اندیشی کلید پیشگیری بسیاری از سختی ها است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6630,'ایمان به حقیقت را هدف و مقصد اساسی زندگی خود قرار دادن و تکالیف را از روی وجدان به جا آوردن، طبع مرد را بلندتر و کاملتر می سازد. یکی را دوست داشتن و حیات او را آرزو کردن و دیگری را دشمن داشتن و مرگ او را خواستن، ناگزیر طبع آدمی را تاریک می سازد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6631,'وظیفه در درجه اول و ثمره ای که از آن باید چید، در درجه دوم قرار دارد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6632,'خردمند وظیفه را وسیله امرار معاش قرار نمی دهد، بلکه برای او وظیفه، هدف زندگی است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6633,'از زندگی چه می دانیم که از مرگ بدانیم.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6634,'وقتی دوست می داری، دلت می خواهد زنده باشی و وقتی کینه می ورزی، آرزوی مرگ می کنی.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6635,'مرد بزرگ از صفات خوب دیگران استفاده می کند، نه از صفات بدشان سوء استفاده.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6636,'مرد بزرگ دلسوزی و پریشانی نشان نمی دهد، عاقل است و تردید ندارد، شجاع است و نمی ترسد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6637,'مرد بزرگ با روی خوش رد می کند و مرد کوچک با روی ترش می پذیرد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6638,'مرد بزرگ دیر وعده می دهد و زود عمل می کند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6639,'مرد بزرگ به خود سخت می گیرد و مرد کوچک به دیگران.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6640,'مرد عاقل همواره نه نکته را در نظر دارد : روشن دیدن، خوب گوش دادن، مهربان سخن گفتن، آداب داشتن، راستگو بودن، انجام وظیفه کردن، پرسیدن هنگام تردید ، خود را از خشم دور نگاه داشتن و در عین موفقیت، عادل و منصف بودن.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6641,'به چیزهای کوچک خود را گرفتار کردن، از کار های بزرگ باز ماندن است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6642,'مشورت کردن با کسانی که به راه دیگران می روند، بی فایده و نشان بی خردی است.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6643,'کسی که خود درستکار است، نیاز به امر کردن ندارد و کارش بدون فرمان هم پیش می رود. اما کسی که خود درستکار نیست، اگر هم فرمان بدهد، نخواهند شنید.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6644,'کسی که تنها به وسیله نیروی شخصیت خود حکمرانی می کند، شبیه به ستاره قطب شمال است که در جای خود حرکت کنان برقرار می ماند و ستارگان دیگر به دور او می گردند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6645,'مرد بزرگ یکطرفه قضاوت نمی کند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6646,'مرد بزرگ، وقار دارد، ولی مغرور نیست و مرد کوچک غرور دارد، ولی وقار ندارد.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6647,'خودبینی و رشک بردن، هزاران ویژگی خوب را می پوشاند.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6648,'بدترین چیز آن است که بدانی کار درست چیست، ولی آن را انجام ندهی.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6649,'از مرد آزاده، چشم داشت ایستادگی و پایداری را دارند، نه فرمانبرداری کورکورانه.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6650,'هرگاه انسان در آستانه‌ی مرگ است، سخنانش حکیمانه می‌شود.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6651,'انسان والا همیشه به شرافت می اندیشد؛ انسان عادی، به آسایش خود.((کنفسیوس))',96,0,0,0,0,0);
INSERT INTO mysms VALUES(6652,'انسان در سختی و شکست می تواند تولدی دیگر اما نیرومندتر پیدا کند. شوق فروتنانه و از روی راستی، برای ارزیابی کنش های خویش در کانون شکوفایی انسان قرار دارد.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6653,'شیوه مدیریت هر کس برخاسته از فلسفه و تفکر اوست.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6654,'تصمیم درست برآیند فلسفه ای درست و استوار است.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6655,'احساس مسئولیت نسبت به جامعه، هسته اصلی داد و ستد را تشکیل می دهد.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6656,'ماتسوشیتا الکتریک پیش از آنکه لوازم برقی بسازد، آدم می سازد.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6657,'بیاموزید که مردم چه می خواهند و بر پایه آن رفتار کنید.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6658,'فلسفه شرکت باید در دل و جان هر کارمندی چنان جای گیرد که بخشی از خون تن او شود.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6659,'هفتاد درصد فکر مدیر باید متوجه نکات مثبت و تنها سی درصد متوجه نکات منفی افراد باشد.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6660,'کالای فروشگاه شما هم مانند دختری است که به خانه بخت رفته است؛ آن را از یاد نبرید.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6661,'مدیر شایسته و توانا کسی است که اجازه می دهد زیردستانش کار را تا جایی که در قالب نتیجه نهایی است بر پایه تشخیص خود انجام دهند.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6662,'فرمان دادن و فرمان بردن، کار را به پایان نمی رساند. همکاری و همفکری است که شگرد راستین مدیریت کامیاب به شمار می رود.((کونوسوکی ماتسوشیتا))',97,0,0,0,0,0);
INSERT INTO mysms VALUES(6663,'امکانات در هر حیطه‌ی کاری، پنهان هستند و این شما هستید که باید آنها را کشف کنید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6664,'زندگی، درهای خود را به روی کسانی می‌گشاید که خود را باور دارند؛ آنانی که ایمان دارند؛ آنهایی که زندگی را با انرژی و قدرت به مبارزه می‌طلبند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6665,'مردمی که منفی‌باف و ترسو هستند و به آسانی، همه چیز را رها می‌کنند، هرگز کار مهمی انجام نخواهند داد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6666,'هیچ چیز نمی‌تواند زمان و انرژی بیشتری را از کسی بگیرد که شایستگی آن را دارد تا یک موسیقیدان بزرگ شود، ولی آن شایستگی‌ها را نادیده گرفته و در گوشه‌ای می‌نشیند و نق نق می‌کند که چرا نمی‌تواند یک نقاش بزرگ شود.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6667,'هرچه که می‌خواهید انجام دهید، مجسم کنید که انجام داده‌اید و سپس از خرد خود بهره بگیرید؛ خواهید دید که می‌توانید آن کار را انجام دهید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6668,'مهم‌ترین چیزی که در بازی زندگی هست، این است که رقیبی [=هم‌چشم] دادگر و خویشتن‌دار باشیم؛ این حتی از برنده شدن هم مهم‌تر است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6669,'هدف رقابت [=چشم و هم‌چشمی] این نیست که رقیب [=هم‌چشم] را سر به نیست و یا او را به کلی از میدان به در کنیم. رقابت باید سببی باشد که زندگی را برای همه‌ی شرکت کنندگان بهتر کند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6670,'یک رقیب [=هم‌چشم] راستین، حتی می‌تواند بهتر از یک دوست و یا آموزگار باشد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6671,'شما نمی‌توانید به تنهایی و برای خودتان زندگی کنید؛ باید همکاری نموده و به دیگران خدمت کنید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6672,'ما به گونه‌ای بی‌پایان، برای هر چیزی در زندگی به یکدیگر وابسته‌‌ایم.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6673,'ناجوانمردانه بازی کردن، تجاوز از اصول رقابت [=چشم و هم‌چشمی] درست است. آنها که جوانمردانه بازی می‌کنند، برندگان نهایی بازی هستند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6674,'رهبر شدن مانند راه رفتن در جاده‌ای پوشیده از خار است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6675,'پایه و بنیان خوشبختی، یک زندگی خوب خانوادگی‌ست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6676,'از اینکه در زمان‌های ضرورت [=بایستگی]، مبلغ هنگفتی پول خرج کنید، هراس نداشته باشید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6677,'هر کاری را باید کسی انجام دهد که شایستگی آن را دارد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6678,'پول را خردمندانه و درست خرج کردن، مهم‌تر از درآوردن آن است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6679,'هر کس حد و مرز ویژه‌ی خود را دارد؛ حد و مرزی که می‌تواند از عهده‌ی آن برآید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6680,'من احساس آسودگی بیشتری می‌کنم، اگر دست کم یک پله پایین‌تر از حد توان خود زندگی کنم.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6681,'کسی که فقط برای دارایی خود، زندگی می‌کند، هرگز نمی‌تواند لذت راستین زندگی را درک کند، چون طبیعتِ آزمندی مادی، حد و مرز نمی‌شناسد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6682,'زندگی لبریز از ناخرسندی و آزمندی، لذت نمی‌شناسد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6683,'به میزان ثروت و دارایی خود بالیدن، نابخردانه است، چون معنی [=چَم] این کار این است که شما چیز دیگری برای بالیدن ندارید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6684,'کسی که ثروت بسیاری روی هم انباشته و نمی‌داند که چگونه از آنها به سود مردم بهره بگیرد، توانگر نیست، بلکه تهیدست است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6685,'کسی که دارایی ناچیزی دارد و می‌داند که چگونه از آن به سود دیگران بهره بگیرد، توانگر راستین است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6686,'زندگی خانوادگی خوشبخت، کلید همه‌ی دستاوردهای پیروزمندانه‌ است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6687,'حتی اگر بسیار گرفتار باشید، باز هم می‌توانید فرصتی داشته باشید تا با خانواده‌تان سپری کنید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6688,'زندگی پرکار‌تر، بسیاری از مردم را خوشبخت‌تر می‌کند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6689,'هرگاه مشکلاتی بین زن و شوهر بروز می‌کند، شکیبایی می‌تواند همچنان آنها را به یکدیگر نزدیک‌تر کند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6690,'هر کسی شایستگی‌های بی‌پایانی دارد، ولی به جز در شرایط استثنایی، هیچ‌گاه این شایستگی‌ها برانگیخته نمی‌شوند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6691,'اگر کسی به راستی از شایستگی خود، بهره گرفته و سخت کار کند، همه چیز امکان‌پذیر است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6692,'اگر می‌خواهید در اوج بمانید، باید به همان اندازه سخت‌تر کار کنید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6693,'اگر نسبت به شرایط موجود خرسند باشید، فقط کافی‌ست آنهایی که عقب‌تر [=واپس‌تر] از شما هستند، از شما پیروی کنند و به شما برسند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6694,'یک ملت مصرف کننده که بیش از دارایی و توان خود خرج می‌کند، ملتی پُر از دردسر است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6695,'مسئولیت شخص نسبت به جامعه، به نسبت افزایش دارایی‌ او بیشتر می‌شود. هر آنچه که ما داریم، جامعه به ما داده است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6696,'مدیریت، یعنی اینکه شما نمی‌توانید از تلاش کردن بازایستید، بلکه باید سخت‌تر تلاش کنید تا در اوج بمانید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6697,'مفهوم زندگی در از خودگذشتگی یافت می‌شود، نه در خودخواهی و خودپسندی.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6698,'با یکدیگر زندگی کردن، بسیار فراتر از در کنار یکدیگر زنده بودن است. با هم زندگی کردن به معنی [=به چَم] با هم رشد کردن و با هم پیشرفت کردن است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6699,'جامعه‌ای که شایستگی تولید و نوآوری را بستاید، شکست نمی‌خورد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6700,'تاریخ با فداکاری رشد می‌کند؛ یک نسل باید برای رفاه نسل پس از خود، فداکاری کند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6701,'رشد و پیشرفت، از یادگیری سرچشمه می‌گیرد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6702,'زمانی که می‌اندیشید چیزی را می‌دانید، در همان دَم که می‌اندیشید به راستی مهم هستید، رشد بازمی‌ایستد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6703,'رشد و توسعه در فروتنی شما و تلاش برای بیشترین میزان یادگیری است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6704,'درخت چون پُربار شود، بیشتر سر به زیر می‌افکند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6705,'هرچه کسب و تجارت، بزرگتر باشد، رویدادهای بیشتری در پیش رو خواهید داشت و بیشتر باید برای آن، مایه گذاشت.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6706,'اگر کسی در روند کسب و تجارت، دچار ناامیدی و بدبینی شود، آنجا نقطه‌ی پایان پیشرفت اوست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6707,'زمانی که دیگران، شمارش غیرممکن‌ها را آغاز می‌کنند، من شمارش امکانات را آغاز می‌کنم.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6708,'مهم نیست که چه کاری انجام می‌دهید؛ خود را وقف آن کار کنید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6709,'مغز شما هنگامی رشد می‌کند که آن را بکار گیرید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6710,'جرقه برای ایده‌‌های خلاق [=آفریننده]، زمانی ایجاد می‌شود که شما به سختی سرگرم کار و اندیشه هستید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6711,'خلاقیت [=آفرینندگی]، با کار سخت افزایش می‌یابد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6712,'نیروهای بالقوه‌ی بیشتر مردم، فراتر از میزانی‌ست که از آن آگاهی دارند یا آماده‌اند آن را بکار گیرند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6713,'هر کس که خود را وقف کارش کرده، هرگز شکست نخورده است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6714,'آنهایی که از شکست و ربرو شدن با تنش‌ها می‌هراسند، هرگز مزه‌ی کامیابی را نخواهند چشید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6715,'جهان، بزرگ و چشم‌براه شماست و کارهای بسیاری هست که می‌توانید انجام دهید. باید جاهایی را پیدا کنید که دست هیچ‌کس به آنها نرسیده و کارهایی را بکنید که هیچ‌کس تاکنون انجام نداده است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6716,'توان و رفاه ملت‌ها با روحیه‌ی پیشگامی ساخته شده است و ویرانی، نتیجه‌ی بی‌خیالی و گریز از مسئولیت است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6717,'نشانه‌ی نادانی، آن است که بدون اینکه تلاشی کرده باشیم، امری را غیر ممکن بدانیم.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6718,'اگر ما تنها از بیست درصد توانایی‌های بالقوه‌ی خود بهره بگیریم، همه نابغه خواهیم شد و اگر از سی درصد آن بهره بگیریم، می‌توانیم همه قهرمانان بزرگی شویم.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6719,'همواره باید مراقب پیشرفت خود باشید و تلاش بسیار کنید که هر روز باهوش‌تر، تیزتر و بهتر شوید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6720,'تنها کسانی که پیوسته خود را رشد می‌دهند و با اشتیاق [=آرزومندی]، کسب دانش می‌کنند، پیشرفت خواهند کرد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6721,'با خودتان، سخت‌گیر باشید. هرگز گمان نبرید که به هدف‌تان رسیده‌اید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6722,'نیروهای بالقوه‌ی خود را تا جایی که می‌توانید، گسترش دهید؛ چون اگر اراده کنید، شما هم می‌توانید نبوغ [=هوش سرشار] خفته‌ی خود را بیدار سازید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6723,'مسئولیت دانایان این است که فرصت آموختن به دیگران بدهند و این دست و دلبازی نباید فقط محدود به یاد گرفتن باشد. ما باید هرچه را که داریم، با همنوع خود قسمت [=بخش] کنیم.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6724,'خودخواهی فکری و مادی، هر دو به یک اندازه زشت و ناپسند هستند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6725,'کسی که در دنیا همه چیز دارد، ولی نمی‌داند چگونه باید به دیگران بخشش کند، توانگر نیست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6726,'توانگران راستین آنانی هستند که زیاد می‌بخشند؛ آنانی که هرچه در توان دارند، می‌بخشند؛ آنانی که می‌دانند چگونه ببخشند؛ مهم نیست که چه اندازه ناچیز و یا چه اندازه زیاد دارند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6727,'خوشبختی، ربطی به دارایی، توانایی و آوازه ندارد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6728,'هر کسی و هر چیزی در زندگی، جای خود را دارد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6729,'آرامش، هماهنگی و نظم، زمانی وجود دارند که هر چیزی در جای خود باشد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6730,'دشواری‌ها زمانی ایجاد می‌شوند که کاری را که باید بکنید، نمی‌کنید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6731,'همه‌ی ما باید کاری را بکنیم که باید بکنیم.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6732,'هرگاه افراد در جایی هستند که باید باشند، آنگاه جامعه به خوبی عمل می‌کند، همه چیز آرام و هر چیزی روی روال خودش به پیش می‌رود.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6733,'نوآوری در زندگی، یک ضرورت [=بایستگی] است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6734,'به راستی، بیشتر نوآوری‌های باارزش بر پایه‌ی دیدگاه‌های کاملاً ساده بنیان گذاشته‌اند که اغلب، نتایج بزرگی به بار می‌آورند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6735,'افراد آفریننده، تاریخ‌ساز هستند و حرکت رو به جلوی [=پیش] تاریخ را تداوم می‌بخشند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6736,'هر چه بیشتر کندوکاو کنید، نتایج، بهتر و بزرگتر خواهد بود.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6737,'همواره باید با ذهنیت یک مالک، کار و زندگی کنید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6738,'مسئولیت، توجیه‌بردار نیست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6739,'یکی از بدترین کارها در زندگی، دور کردن مسئولیت از خود است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6740,'مهم نیست که چکار می‌کنم، ولی دوست دارم آن را درست و کامل انجام دهم؛ این برای کامیابی، بسیار ضروری [=بایسته] ا‌ست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6741,'هر کس به گونه‌ای، در یک زمانی به کمک نیاز دارد و هیچ‌کس آنقدر بی‌ارزش نیست که نتواند به دیگری کمک کند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6742,'این در زندگی، یک اصل است: کمک به دیگران، کمک به خود است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6743,'یکی از سبب‌های کلیدی در روابط شخصی خوب، ‌باور دوسویه است که در درازمدت به وجود می‌آید و فقط با شکیبایی زیاد به دست می‌آید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6744,'ترس بیش از حد از خارهای گل رز به همان اندازه نابخردانه است که از بوی خوش آن مست شوید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6745,'کامیابی از آنِ کسانی‌ست که از مبارزه با خارها نمی‌هراسند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6746,'فرصت‌ها زاییده‌ی سختی‌ها هستند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6747,'اگر شما تنها چشم به سود و زیان خود داشته باشید و از کمک به نیازمندان، دوری کنید، ممکن است در کوتاه مدت، کامیاب باشید، ولی در درازمدت، زیان خواهید کرد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6748,'اگر روابط شخصی خوبی داشته باشید، می‌توانید در زندگی کامیاب شوید. روابط خوب با اشخاص برجسته، موهبتی‌ست که بسیار باارزش‌تر از دارایی‌ست، چون روابط خوب را نمی‌توان با پول خرید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6749,'زمان مناسب برای انجام کاری، هنگامی‌ست که هیچ‌کس آن کار را نمی‌کند، یا کسان دیگر هنوز آغاز نکرده‌اند و یا از انجام آن بازایستاده‌اند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6750,'شما باید یاد بگیرید که چگونه از بحران‌ها به سود خود بهره بگیرید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6751,'تا زمانی که همه‌ی تلاش خود را بکار می‌گیرید، هرگز برای شکست، سرزنش نمی‌شوید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6752,'آنانی که می‌خواهند بهترین باشند، باید همه‌ی تلاش خود را بکنند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6753,'اگر گمان می‌کنید که نمی‌توانید کاری را انجام دهید، اگر گمان می‌کنید که شایستگی‌‌های انجام کاری را ندارید، پس بدانید که هرگز نمی‌توانید کار مهمی انجام دهید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6754,'آنچه انجام می‌دهید مهم است، ولی چگونگی انجام آن مهم‌تر است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6755,'تلاش‌های تردیدآمیز، هرگز هیچ‌کس را به اوج نرسانده است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6756,'برای گریز از پشیمانی، فقط یک راه وجود دارد و آن، نهایت تلاش در هر کاری‌ست که انجام می‌دهید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6757,'در جهان، باارزش‌ترین چیز، زمان است؛ مانند تیری که از چله‌ی کمان رها می‌شود و هرگز برنمی‌گردد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6758,'زندگی، ارزشمندتر از آن است که به بیکاری و تنبلی بگذرد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6759,'حتی از یک لحظه [=دَم] هم بی‌خبر نمانید؛ چرا که همه چیز از انباشته شدن لحظه‌ [=دَم] ها ساخته می‌شود.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6760,'هر کسی، شایستگی‌های بی‌پایانی دارد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6761,'اگر خود را دربند دنیای کوچک خود کنید، مانند قورباغه‌ای می‌شوید که به دیواره‌ی چاه آب چسبیده و از بقیه‌ی جهان پیرامون خود بی‌خبر می‌مانید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6762,'بکوشید در زمینه‌ی کار خود بهترین باشید؛ هرچه می‌خواهد باشد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6763,'نود و نه درصد نبوغ [=هوش سرشار]، بستگی به تلاش دارد و برای نابغه شدن در رشته‌ی خودتان، باید خود را غرق در کار کنید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6764,'همه‌ی ما توان آن را داریم که سرنوشت خود را دگرگون سازیم.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6765,'کسی که سخت کار می‌کند، به هیچ ورزش دیگری نیاز ندارد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6766,'کار به ستوه آورنده و خسته کننده، برای تندرستی، زیان‌آور است. ولی کاری که از آن لذت می‌برید، انرِژی بیشتری به شما می‌دهد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6767,'عرق ریختن هنگام کار، بسیار بهتر از عرق ریختن در باشگاه است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6768,'فایده‌ی شکم پُر با فکر خالی یا کج‌اندیش چیست؟((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6769,'تجربه، آموزگار بزرگی‌ست. تجربه، حقایق زندگی را به شما می‌آموزد و شما را پخته می‌کند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6770,'تجربه، دقیق‌ترین و کارآمدترین راهِ آموختن درباره‌ی جامعه است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6771,'اگر هر چیزی را یک بار تجربه کنید، بهتر از آن است که هرگز آن را تجربه نکنید؛ چون هر چیزی را باید تجربه کنید تا آن را بشناسید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6772,'اگر از هر سفر، چیزی نیاموزید، فقط زمان‌تان را هدر داده‌اید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6773,'نوآوری، به خودی خود مشکل نیست. اینکه آیا می‌خواهید نوآوری کنید یا نه، این مهم است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6774,'جایی وجود ندارد که ارزش یک بار سفر را نداشته باشد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6775,'دوستان گوناگون از طبقات گوناگون داشتن، دارایی بزرگی‌ست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6776,'یکصد نفر از خویشاوندان، هرگز جای یک دوست صمیمی و نزدیک را نمی‌گیرند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6777,'اقوام و خویشاوندان ممکن است در زمان‌های نیاز، همگی به شما پشت کنند، ولی یک دوست صمیمی و نزدیک هرگز این کار را نخواهد کرد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6778,'هر کاری را با اراده انجام دهید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6779,'هر چیزی می‌تواند آموزگاری بزرگ برای شما باشد و هر مکانی، یک مدرسه که در آنجا چیزی بیاموزید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6780,'نوآوری‌های بزرگ، نتیجه‌ی انباشتن نوآوری‌های کوچكِ بسیار است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6781,'اگر خود را باور داشته باشید، هر کاری، شدنی‌ست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6782,'هر مشکلی که به وجود بیاید، همیشه راهی برای از بین بردن آن وجود دارد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6783,'فرصت‌های بالقوه، نامحدود هستند، ولی شما بدون کار سخت نمی‌توانید آنها را ببینید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6784,'ناامیدی و شکست حتی در نوآورانه‌ترین کارها به وجود می‌آید، ولی مهم این است که اراده کنید و پایان کار خود را در نظر بیاورید، مهم نیست آن کار چه باشد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6785,'هیچ چیز، نشدنی نیست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6786,'شرط زنده بودن، تکاپو و جنبش است و تکاپو، نشان دهنده‌ی زنده بودن شماست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6787,'همیشه خرسندی و خشنودی، نشانه‌ی پایان هر چیزی‌ست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6788,'جوانی، دورانی از زندگی‌ست که باید خود را وقف گسترش زیبایی درون کنید؛ ارزش و آراستگی راستین نمی‌تواند از ظاهر شما سرچشمه بگیرد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6789,'آراستگی ظاهری، سبُک سری‌ست، ولی آراستگی راستین که از باطن شما سرچشمه می‌گیرد، بلندپایه و ارزشمند است و آراستگی این چنینی، پایدار و جاودانه است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6790,'اگر به راستی می‌خواهید کسی سخن شما را باور کند و به شما ایمان بیاورد، باید صادقانه [=از روی راستی] و به گونه‌ای خشنود کننده رفتار کنید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6791,'به راستی، مردم به آنانی که با ظاهری آراسته، ولی بدون توانایی، زندگی می‌کنند، باور ندارند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6792,'به گذشته چسبیدن و به آسایش اکنون، خرسند بودن، شما را از دیدن آینده باز می‌دارد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6793,'قورباغه‌ای که در ته چاه زندگی می‌کند، هیچ درکی از گسترش جهان پیرامون خود ندارد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6794,'هرچه پرنده بیشتر اوج بگیرد، جهان گسترده‌تری در برابر دیدگان خود خواهد داشت.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6795,'اگر افراد به شما ایمان داشته باشند، شما هم تلاش می‌کنید آنها هرگز سرشکسته نشوند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6796,'فریاد کشیدن بر سر شاگردانی که خوب درس نمی‌خوانند و سبب دردسر هم هستند و نیز سرزنش آنها، کاری از پیش نمی‌برد. باید به آنها دلبستگی نشان داد و شایستگی‌های بی‌همتایشان را کشف کرد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6797,'هیچ چیز، مفت و مجانی به دست نمی‌آید و هیچ امری، ناگهانی نیست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6798,'هنگامی که به جایی پُر از ریسک و خطر وارد می‌شوید، به دقت، همه‌ی جوانب کار را در نظر می‌گیرید؛ پس به راستی، دیگر ریسکی در کار نیست.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6799,'این نشانه‌ی خامی‌ست که در گوشه‌ای بنشینیم و احساس خرسندی کنیم.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6800,'رؤیاها، نیروی مورد نیاز برای دگرگون کردن جهان هستند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6801,'تاریخ، از آنِ کسانی‌ست که رؤیاهای بزرگ در سر دارند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6802,'از هر آنچه که جوانی به همراه خود می‌آورد، رؤیاها، مهمترین هستند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6803,'مردمی که رؤیا دارند، تهیدستی را نمی‌شناسند؛ چرا که دارایی هر کس به اندازه‌ی رؤیاهایی‌ست که در سر دارد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6804,'جوانی، دوره‌ای از زندگی‌ست که حتی اگر هیچ چیز در زندگی نداشته باشید، اگر رؤیا داشته باشید، هیچ چیز نمی‌تواند حسادت [=بدخواهی] شما را برانگیزاند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6805,'رؤیاها، در بیشتر موارد، انسان‌ساز هستند. آنها منش، کار و حتی سرنوشت انسان را مهار می‌کنند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6806,'انسانی که رؤیاهایش از رفاه شخصی کنونی‌اش فراتر نرود، به اندازه‌ی یک انسانِ بدون رؤیا، بدبخت و ناکام است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6807,'اگر تنها یک درصد شانس کامیابی در یک پروژه وجود داشته باشد، یک دارنده‌ی کسب و کار راستین، آن یک درصد را به چشم جرقه‌ای می‌نگرد تا آتشی با آن روشن کند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6808,'کسب و کار، فراتر از پول درآوردن است. گاهی، کمتر ضرر دادن نیز مهم است.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6809,'تصمیم‌گیری، نه تنها در کسب و تجارت، بلکه در سراسر زندگی اهمیت دارد.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6810,'زندگی از تصمیم‌گیری‌ها تشکیل شده و یک تصمیم نادرست می‌تواند همه‌ی زندگی را نابود کند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6811,'شما باید ویژگی پنهان هر شرایط و تجربه‌ای را لمس کنید تا آگاهی‌های درست را کسب کرده و بتوانید تصمیم‌های درست را بگیرید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6812,'کسانی که سر راه زندگی شما قرار می گیرند، می توانند نقش مهمی در سرنوشت و آینده شما داشته باشند.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6813,'اداره ی زندگی، همچون اداره ی حرکت و کنترل یک ماشین سواری است. راننده ی باتجربه، هنگام رانندگی، کمتر پشت سر خود را نگاه می کند و اگر این کار را انجام دهد، هر لحظه با خطرهای مَهیب و مُهلکی رو به رو خواهد شد ... پس هرچه ممکن است از زیانها و شکست های گذشته، کمتر یاد کنیم تا شیرینی لذت روزگار آینده را از دست ندهیم.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6814,'شما بدون تسلط بر خود نمی توانید فاتح دیگران باشید.((کیم وو چونگ))',98,0,0,0,0,0);
INSERT INTO mysms VALUES(6815,'بدترین شکل دلتنگی برای کسی آن است که در کنار او باشی و بدانی که هرگز به او نمی رسی.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6816,'هرگز وقت خود را با کسی که حاضر نیست وقتش را با تو بگذراند، نگذران.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6817,'اگر خداوند، لحظه ای فراموشش شود و مرا عروسکی پارچه ای بپندارد و اگر چنین باشد که برای مدت کوتاهی باز عمری ارزانی ام دارد، به یقین حرفی در مورد آنچه که بدان می اندیشم بر زبان نخواهم راند و بی شک، اندیشه خواهم کرد پیرامون سخنانی که باید بگویم.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6818,'برای هر دقیقه ای که چشمانمان را می بندیم، شصت ثانیه روشنایی را از دست می دهیم.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6819,'خدای من؛ اگر قلب داشتم، کینه و نفرتهای خود را بر روی یخ می نوشتم و به امید برآمدن خورشید می ماندم.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6820,'همه ی مردم دوست دارند در اوج و قله ی کوه زندگی کنند، بی آنکه متوجه باشند خوشبختی حقیقی در سراشیبی ای است که به سمت بالای کوه می پیماییم.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6821,'چیزها را نه برای آنچه می ارزند، بلکه برای معنایی که دارند بسنجید.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6822,'مرگ از پیری نمی آید، بلکه با فراموشی می آید.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6823,'زمانی که یک نوزاد برای بار نخست، انگشت پدرش را با مشت کوچک خود می گیرد و می فشارد، آن را برای همیشه گرفته است.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6824,'بالاترین چیز در موسیقی، سخن گفتن درباره ی موسیقی است.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6825,'در حقیقت، والاترین چیز در ادبیات، بحث پیرامون ادبیات است.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6826,'مسأله ی مهم در نوشتن رمان یا داستان کوتاه، جدی نوشتن آن است؛ حتی اگر به فروش نرود و یا جایزه ای به دست نیاورد.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6827,'پاره کردن داستان های کوتاه ضروری است؛ چون نوشتن آنها مانند بتون ریختن است. اما نوشتن رمان به بنایی با آجر می ماند؛ معنایش این است که اگر داستان کوتاه به عنوان کوشش اولیه موفق نبود، نباید بر نوشتن آن پافشاری کرد، در حالی که در مورد رمان بسیار آسان است؛ چون می توان دوباره از نو آغاز کرد.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6828,'هر چیزی باید متعادل باشد تا عشق به اوج تکامل خود برسد.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6829,'تنها چیزی که در شهرهای کوچک شایع است، خود شایعه است... حتی پیش از آنکه پیشامدی رخ دهد، مردم به آن پی می برند.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6830,'برخی از چیزها که خیلی به درد نمی خورند، زمانی استفاده ی زیادی دارند.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6831,'ترسوی حقیقی از پرواز نمی ترسد، بلکه آن کس که با ترس پرواز را یاد می گیرد، ترسو است.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6832,'تله پاتی و روش های گوناگون آن، به گونه ای که برخی می اندیشند، شعبده بازی و کلک نیست؛ بلکه یک نیروی بزرگ ذاتی است که دانش، آن را نمی پذیرد ... چون آن را نمی شناسد ... همان گونه که در آغاز، کروی بودن زمین را نپذیرفت و مردم گمان می کردند زمین پهن است.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6833,'تله پاتی، امری ناخودآگاه است که نمی توان آن را کنترل کرد و هیچ گونه برنامه ریزی را نمی پذیرد. تله پاتی را نمی توان فهمید مگر اینکه به کمال برسد و در غیر این صورت، خود به خود ناکام خواهد ماند.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6834,'من همیشه باور داشته ام که در طبیعت، زیباتر از زن وجود ندارد.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6835,'انسان تنها زمانی حق دارد به انسانی دیگر از بالا به پائین بنگرد که ناگزیر است او را یاری رساند تا روی پای خود بایستد.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6836,'اگر کسی تو را آن گونه که می خواهی دوست ندارد، به این معنا نیست که تو را با تمام وجودش دوست ندارد.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6837,'دوست حقیقی کسی است که دستهای تو را بگیرد، اما قلب تو را لمس کند.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6838,'تو ممکن است در تمام دنیا تنها یک نفر باشی، اما برای برخی از افراد، تمام دنیا هستی.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6839,'دوستت دارم؛ نه به خاطر شخصیت تو؛ بلکه به خاطر شخصیتی که در زمان با تو بودن پیدا می کنم.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6840,'فداکاری، یعنی آسایش خود را فدای آسایش دیگران کردن.((گابریل گارسیا مارکز))',99,0,0,0,0,0);
INSERT INTO mysms VALUES(6841,'در جهان، روشنایی هایی وجود دارد که در عمیق ترین ظلمات نهانند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6842,'ما که آزادی را دوست نداریم نمی توانیم نان آزادی را بخوریم و اگر هم بخوریم، آنرا هضم نخواهیم کرد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6843,'کسی که خود را وقف خدمت می‌کند، کوچک‌ترین اندیشه‌ی رفاه خود را به ذهن، راه نمی‌دهد، بلکه آن را به پروردگارش می‌سپارد و به اراده‌ی او گردن می‌نهد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6844,'من به راه‌های خشونت‌بار، اعتراض دارم؛ حتی اگر در مسیر نیکی، این راه‌ها به کار گرفته شود، نیکی برآمده از آن، موقتی و گذراست؛ ولی شرّی که نتیجه‌ی آن است، ماندگار و همیشگی است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6845,'تجربه به من آموخته است که خیر پایدار هیچ‌گاه نمی‌تواند میوه‌ی خشونت و ناراستی باشد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6846,'نافرمانی مدنی، حق ذاتی همه‌ی شهروندان است. اگر از چنین حقی چشم‌پوشی کنیم، از انسانیت دست شُسته‌ایم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6847,'راستی و درستی، در قلب همه‌ی آدم‌ها وجود دارد؛ هر کسی باید در قلب خود، آن را جستجو کند و آن گونه که خود، می‌فهمدش، چراغ راهش سازد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6848,'انسان نمی‌تواند در گستره‌ای از زندگی خود با درستی و روراستی عمل کند، در حالی که در سایر حوزه‌های زندگی‌اش، آلوده‌ی نادرستی‌هاست. زندگی، واحدی تجزیه‌ناپذیر است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6849,'بدانید که هر ناکامی و شکستی، شما را به اندوخته‌های بیشتری در ژرفای وجودتان راهنمایی می‌کند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6850,'خشونت، هرگز خشونت را از بین نمی‌برد، بلکه تنها بر شدت آن می‌افزاید؛ در حالی که با پایبندی بی‌کم و کاست به عدم خشونت، در اندیشه و گفتار و رفتار، رسیدن به آزادی، گریزناپذیر است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6851,'اگر چیزی در گذشته رخ نداده باشد، به این معنا نیست که در آینده نیز رخ نمی‌دهد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6852,'این تصور که رسیدن به هدف به سبب دشوار بودن راه، ناممکن است، با روح زمانه‌ی ما سازگاری ندارد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6853,'شادمانی، نه در پیروزی، که در مبارزه و تلاش و رنج برآمده از آن نهفته است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6854,'همان گونه که گرما، اگر مهار شود، به انرِژی تبدیل می‌شود، خشم نیز در صورت مهار شدن می‌تواند به نیرویی مبدل گردد که جهان را به لرزه درمی‌آورد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6855,'برآنم که اگر یک فرد به مراتب بالای روحی برسد، همه‌ی جهان با او صعود می‌کنند و اگر فردی سقوط کند، همه‌ی جهان را به سقوط کشانده است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6856,'عدم خشونت راستین، بدون وجود بی‌باکی ناب در روح فرد، دست یافتنی نیست.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6857,'می‌توان امیدوار بود که فردی خشن، روزی دست از خشونت بردارد، ولی چنین امیدی در مورد ترسویان وجود ندارد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6858,'قدرت، برآمده از توانایی جسمی نیست، بلکه از اراده‌ی استوار سرچشمه می‌گیرد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6859,'هر نامی که بر او بگذارید، مهم نیست؛ او کسی‌ست که بیشترین آرامش را در میان شدیدترین شعله‌ها به آدمی می‌دهد؛ او همان پروردگار است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6860,'تنها هنگامی می‌توانید ادعای عشق‌ورزی کنید که رفاه دیگران برایتان اهمیتی بیش از رفاه خودتان داشته باشد و حتی زندگی دیگران را مهم‌تر از زندگی خود بدانید.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6861,'قدرت برآمده از شمار نفرات، مایه‌ی خشنودی ترسویان است. آن که روحاً دلیر است، جز از نفس مبارزه لذت نمی‌برد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6862,'ایمان، تنها ذکر گفتن زبانی نیست؛ بلکه گونه‌ای مبارزه با مرگ است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6863,'هدف، همواره از ما دور می‌شود. هرچه به پیشرفت‌های بزرگ‌تری دست می‌یابیم، بیشتر به بی‌ارزشی خود پی می‌بریم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6864,'شادمانی، در تلاش نهفته است، نه در دستیابی به هدف. تلاش بی‌کم و کاست، همان پیروزی‌ست.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6865,'عشق ورزیدن به آنانی که ما را دوست دارند، عدم خشونت نیست. عدم خشونت یعنی به کسانی که از ما بیزارند، عشق بورزیم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6866,'عشق به کسی که از ما بیزار است، دشوارترین کارهاست. ولی به لطف پروردگار، حتی این دشوارترین کارها نیز اگر بخواهیم انجام‌شان دهیم، آسان و ساده خواهد شد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6867,'برای کسی که اندیشه‌ی عدم خشونت را در خود پرورده است، همه‌ی جهان، یک خانواده است؛ نه ترسی به دل دارد و نه کسی از او می‌ترسد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6868,'گاهی ساده‌ترین چیزها به نظر ما دشوارترین کارها می‌نماید. اگر دل‌های ما باز بود، هیچ‌گاه به مشکلی برنمی‌خوردیم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6869,'زندگی من یک واحد تجزیه‌ناپذیر است؛ همه‌ی تلاش‌هایم به یکدیگر وابسته‌اند و همه‌ی آن‌ها از عشق پایان ناپذیر من به بشریت سرچشمه می‌گیرند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6870,'به یاد داشته باشید تا زمانی که همه‌ی تلاش و کوشش خود را به کار می‌گیرید، سخن گفتن از شکست، نارواست.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6871,'عشق، هیچ‌گاه درخواست نمی‌کند، همواره می‌بخشد. عاشق، همواره رنج می‌برد، هرگز آزرده نمی‌گردد، هرگز تلافی نمی‌کند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6872,'باید به زندگی من بنگرید: اینکه چگونه زندگی می‌کنم، چگونه غذا می‌خورم، می‌نشینم، سخن می‌گویم و به طور کلی، چگونه رفتار می‌کنم؛ مذهب مرا مجموعه‌ی همه‌ی این‌ها تشکیل می‌دهد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6873,'اگر عدم خشونت، قانون هستی ماست، پس آینده از آن زنان است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6874,'تمام فلسفه‌ی من، اگر بتوان از آن با این عنوان پردبدبه یاد کرد، در آنچه که انجام داده‌ام، نهفته است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6875,'حقیقت، هیچ‌گاه و در هیچ شرایطی، نباید فدای چیزی شود.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6876,'کوچک‌ترین شکی ندارم که هر مرد و زنی می‌تواند به جایی که من رسیده‌ام، برسد، به شرطی که همان تلاش‌ها را به کار بندد و همان امید و ایمان را در خود بپروراند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6877,'زمانی فرا می‌رسد که فرد، مقاومت‌ناپذیر و اثر کارهای او یکسره فراگیر می‌گردد. این حالت، زمانی رخ می‌نمایاند که فرد، خود را تا حد صفر فرو کاسته باشد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6878,'سرچشمه‌ی قدرت، شایستگی فیزیکی نیست، بلکه اراده‌ی تزلزل‌ناپذیر است که قدرت را می‌آفریند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6879,'اگر عدم خشونت را در ارتباطات شخصی خود با دیگران به کار نبندیم، سخت دچار اشتباه شده‌ایم. عدم خشونت نیز مانند نیکوکاری، باید از خانه آغاز شود.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6880,'طبیعت همه‌ی مردم یکسان است؛ پس آنچه که برای من امکان‌پذیر است، برای همه امکان‌پذیر است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6881,'تا زمانی که دلیل مناسبی برای باور نداشتن به حریف [=هم‌آورد] وجود ندارد، باید او را باور داشت.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6882,'بی‌اعتمادی، نشانی از ناتوانی‌ست.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6883,'هنگامی که هدف‌مان، نه درهم شکستن حریف [=هم‌آورد]، که کسب پشتیبانی اوست، بی‌اعتمادی نیز معنای خود را از دست می‌دهد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6884,'ارتباط همیشگی بین هدف و وسیله، درست مانند ارتباط درخت با دانه‌ی آن است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6885,'با هر گونه دشواری که روبرو شویم، و هر شکست ظاهری که گریبانگیرمان شود، نباید از جستجوی راستی و درستی، دست برداریم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6886,'من همواره بر این باور بوده‌ام که حتی اگر یک فردِ تنها، پایبندی نسبتاً بی‌چون و چرایی به عدم خشونت داشته باشد، می‌تواند آتش را فرو نشاند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6887,'کوچک‌ترین نگرانی در مورد خود به دل راه ندهید و همه چیز را به خدا بسپارید.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6888,'آن کس که با وجدانی آرام، خود را وقف خدمت به دیگران می‌کند، روز به روز، نیاز بیشتری به این خدمت، در درون خود حس می‌کند و ایمانش بی‌درنگ فزونی می‌گیرد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6889,'کسی که آماده‌ی چشم‌پوشی از دلبستگی‌های شخصی نیست و فراموش کرده که هنگام زایش، چیزی با خود نیاورده است، به سختی می‌تواند در جاده‌ی خدمت به مردم گام نهد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6890,'آن گونه از دگرگونی را که دوست دارید در دیگران ببینید، در خودتان ایجاد کنید.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6891,'اسارت و بندگی مردم به خود آنها و میزان تحمل رنج و قبول فداکاری شان بستگی دارد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6892,'خود را قربانی کنیم بهتر است که دیگران را.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6893,'حقیقت داروی تلخی است که نتایج شیرین دارد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6894,'از گناه تنفر داشته باش نه از گناهکار.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6895,'مایلم که فرهنگ تمام کشورها آزادانه در اطراف خانه ام بوزد، ولی اجازه نمی دهم که بر اثر وزش چنین نسیمی، [ خانه ام ] از جا کنده شود.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6896,'راستی نخستین چیزی است که انسان باید به جستجوی آن بپردازد. ابزار شناخت راستی همان قدر ساده است که سخت به نظر می رسد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6897,'از هر چیزی تازه اش را انتخاب کنید، ولی از دوست کهنه اش را.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6898,'راستی نخستین چیزی است که انسان باید به جستجویش بپردازد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6899,'ابزارهای شناخت راستی همان قدر ساده است که سخت می نماید.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6900,'شاید ثمره کلام دلنشینی را که امروز به زبان می آورید، فردا بچشید.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6901,'باید تمرین کنم که هشتاد درصد از حرفهایم را کسر کنم و تا لازم و واجب نباشد، حرف نزنم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6902,'کاهلی [ = تنبلی ]، از هر بیماری مسری تر است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6903,'می توانید مرا زنجیر کنید، می توانید شکنجه کنید، حتی می توانید تن مرا نیست و نابود سازید، ولی هرگز اندیشه مرا زندانی نخواهید کرد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6904,'کسی که از خردمندی خود بسیار آسوده دل باشد، خردمند نیست. باید به یاد داشت که شاید نیرومندترین افراد، ناتوان شوند و خردمندترین افراد کار نادرستی انجام دهند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6905,'آن دگرگونی‌هایی را که دلتان می‌خواهد در دنیا ببینید، نخست از خودتان آغاز کنید.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6906,'من بر این باورم که انسان‌ها باید کارهای نادرست خویش را بپذیرند و در درست کردن آنها بکوشند. چنین پذیرشی، روح انسان را نیرو و جلا می‌بخشد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6907,'اگر دعا و نماز نبود شاید مدتها پیش به جنون و دیوانگی رسیده بودم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6908,'تمامی گناهان به طور پنهانی صورت می گیرد. آن زمان که درک کنیم خداوندِ حق گواه بر افکار ما است، شاید رها و آزاد شویم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6909,'باطن و سیرت مردم را در هنگام بدبختی آنها می توان شناخت.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6910,'ما باید خود، آن تغییری باشیم که دوست داریم در دنیا ببینیم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6911,'آنچنان زندگی کن که گویی فردا خواهی مرد و آنچنان بیاموز که گویی تا ابد زنده خواهی ماند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6912,'چشم در برابر چشم باعث کوری تمام دنیا می شود.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6913,'آنچه ما را به نابودی خواهد کشاند از این قرار است: سیاست بدون اصول، لذت بدون وجدان، علم بدون شخصیت و تجارت بدون اخلاق.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6914,'هر تغییر و تحولی که برای جهان آرزو می کنی، در وجود خودت ایجاد کن!((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6915,'آزادی، هرگز به معنی مجوز [ =روا دارنده ] برای به کار بستن استبداد نبوده است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6916,'اگر ما نتوانیم همانند مردان و زنان آزاد زندگی کنیم، باید از مردن خوشحال باشیم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6917,'هدف نهایی مبارزه ی ما، دوستی با تمام مردم دنیا است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6918,'جهان مانند آیینه است؛ آیینه همیشه انعکاس صورت خود انسان را نشان می دهد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6919,'شاد کردن قلبی با عمل، بهتر از هزاران سر است که به نیایش خم شده باشد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6920,'مشتری مهمترین بازدیدکننده در محدوده ی ماست. او به کار ما وابسته نیست، ما به کار او وابسته هستیم. او مزاحم کار ما نیست، هدف کار ماست. او بیگانه در کار ما نیست، بلکه بخشی از کار ماست. با انجام کار او، لطفی در حق او نمی کنیم؛ اوست که با فراهم آوردن این فرصت به ما لطف می کند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6921,'اگر اراده ای نباشد عشقی در کار نیست.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6922,'اگر خودمان احترام خویش را به کسی ندهیم، هیچ کس نمی تواند آن را از ما بگیرد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6923,'انسان ترسو به قدرت متکی بر تعداد مسرور می شود و انسان شجاع با قدرت متکی بر خود (راستینش) برانگیخته می شود.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6924,'پیروزی آن نیست که هرگز زمین نخوری، آن است که بعد از هر زمین خوردنی برخیزی.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6925,'قلمرو من، عمل من است و کاری را می کنم که عقلم حکم می کند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6926,'بزرگترین پیروزی آن است که به گونه ای رفتار کنیم تا مبارزان قویتر از آن بشوند که در شروع کار مبارزه بوده اند.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6927,'شاخ پربار سر بر زمین می نهد و شکوه آن همچنان در فروتنی او جلوه گر است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6928,'اولین تکلیف بشر حمایت از افراد ضعیف و جریحه دار نکردن یک وجدان بشری است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6929,'نباید ناامید شوید که حس بشر دوستی از میان رفته است. زیرا نوعدوستی مثل اقیانوس است که اگر چند قطره از آن آلوده گردد، تمام آبهای آن هرگز آلوده نخواهد شد.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6930,'دهقان و دهکده اش قادرند جهانی را به زور آزمایی بخوانند. زیرا قانونی که بر دهقان حکومت می کند این است که او برای دفاع از اعتبار و افتخار زادگاهش، حتی مرگ را به آسانی تحمل خواهد نمود.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6931,'اگر نتوانیم آزاد زندگی کنیم، بهتر است با آغوش باز از مرگ استقبال کنیم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6932,'وقتی کسانی که در اطراف من هستند از بی غذایی در حال مرگند، جایز است که من به گرسنگان غذا بدهم.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6933,'زندگی، بیش از شتابی که دارد، با ارزش است.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6934,'اگر چیزی نخواهی، چیزی به دست نمی آوری.((گاندی))',100,0,0,0,0,0);
INSERT INTO mysms VALUES(6935,'ای مزدا اهوره! ما را نیرو [يِ مینُوی] و شادمانی بخش تا بر ستیزه و آزار دشمنان چیره شویم.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6936,'«اهوره» تنها دادگستر [در جهان] است. پس آنچه خواستِ او باشد، همان خواهد شد.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6937,'دانای روشن‌بین باید دانشجو را بیاگاهاند. مبادا نادان [کسی را] بفریبد و گمراه کند.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6938,'بهروزی از آنِ کسی است که دیگران را به بهروزی برساند.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6939,'ای هوشمندان! بشنوید با گوش‌ها [يِ خویش] بهترین [سخنان] را و ببینید با منش روشن و هر یک از شما - چه مرد، چه زن - پیش از آنکه رویدادِ بزرگ به کام ما پایان گیرد، از میان دو راه، [یکی را] برای خویشتن برگزینید و این [پیام] را [به دیگران] بیاموزید.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6940,'هیچ کدام از این گناهکاران درنمی‌یابند که کامیابی – همان گونه که زندگی به ما آموخته و بدان سان که با [آزمون] آهن گدازان گفته شده – به کار و کوشش [باز بسته است].((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6941,'ای مزدا! منم که ترا می‌پرستم و از تو خواستارم: ناباوری و «تَرمَنشی» از میان برخیزد و خویشاوندان از خیره‌سری، همکاران از فریب نزدیکان و یاوران از نکوهندگان رهایی یابند و جهان از رهبران بد بیاساید.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6942,'کسی که [برای دیگران] خواستار روشنایی است، روشنایی [بدو] ارزانی خواهد شد.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6943,'ای مزدا! تو را توانا و پاک شناختم؛ آنگاه که [دانستم] در پرتو توانایی‌ات، آرزوهای ما برآورده خواهد شد.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6944,'آن کس که به [دین] من بپیوندد، من خود، او را بهترین [یاور]م و در پرتو «منش نیک»، بهترین چیزها را بدو نوید می‌دهد. امّا آن کسی که با [آموزش] ما سر ستیز دارد، ستیز با او رواست.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6945,'آن کس که نیک می‌اندیشد یا بد، بی‌گمان «دین» و گفتار و کردار خود را نیز [چنان خواهد کرد] و خواهش او پیرو گزینش آزادانه‌ی وی خواهد بود.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6946,'باید برای [آبادانی] جهان کوشید و آن را به درستی نگاهبانی کرد و به سوی روشنایی برد.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6947,'تا بدان هنگام که بتوانم به خواست خویش بر سرنوشت فرمان برانم، خواستار نیكْ آگاهی خواهم بود.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6948,'تا بدان هنگام که دل و جان شما سرشار از شور و مهر است و با یکدیگر می‌جوشید – خواه در فراخی، خواه در تنگی – از پاداش «مَگَه» [= انجمن پذیرندگان و پیروان پیام و آموزش زرتشت ] برخوردار خواهید بود.((گاهان))',101,0,0,0,0,0);
INSERT INTO mysms VALUES(6949,'برخی نومیدانه در قید بندگی هستند و به غلط چنین می پندارند که آزادند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6950,'به زودی روزگار انتقاد و عیب جویی به پایان می رسد و به وسیله پیشرفت تمدن، آزادی اندیشه به تمام معنی در جهان حکمفرما می شود و همه کس می تواند به دلخواه خود راجع به فلسفه وجود بیاندیشد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6951,'انسان نه برای حل و فصل مسائل جهان هستی، بلکه برای یافتن اینکه چه باید بکند، پا به گستره‌‌ی وجود نهاده است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6952,'هر تردید و دودلی، یک تاخیر در پی دارد و روزها با افسوس خوردن بر فرصت‌های از دست رفته هدر می‌رود.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6953,'فقط کافی‌ست برخیزید و وارد میدان کارزار زندگی شوید تا کار، کامل شود و به پایان برسد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6954,'از بسیاری چیزهای بزرگ می‌گذریم، ولی به ندرت، در امور جزئی، از خودگذشتگی می‌کنیم.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6955,'عاشق شوید و بلندهمتی را پیشه‌ی خود سازید، زیرا اینها بالهای نیرومندی هستند که شما را به قله‌های بلند کامیابی می‌رسانند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6956,'برای بیشتر داشتن، نخست باید بیشتر بشوید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6957,'ثروت حقیقی چیست؟ آفتابی که بر گدا و توانگر یکسان می تابد. تو ای توانگر، دل رنجه مکن که چرا گدا نیز در دنیای خویش خوشبخت است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6958,'باید پیوسته دگرگون شده، تجدید حیات کرده و جوان گردیم، در غیر این صورت، متعصب و سخت می شویم.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6959,'زندگی اندک اندک به هر کس می فهماند چه کاره است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6960,'این تضادها هستند که ما را به کامیابی می رسانند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6961,'من در تولد خود نقشی نداشتم و در مرگ خود نیز نقشی ندارم؛ نقش من همه در زندگی من است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6962,'کسی که از مرگ می ترسد از زندگی هم می ترسد؛ زیرا مرگ و زندگی از یکدیگر جدایی ناپذیرند و هر دو، جلوه ی یک حقیقت به شمار می روند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6963,'حتی هنگام مرگ نیز وابستگی به قوت اراده و نیروی خواستن در ما وجود دارد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6964,'از همه چیز مهمتر در زندگی آن است که انسان هدفی داشته و برای رسیدن به هدف خود دارای استعداد کافی باشد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6965,'بهتر است پیش از آنکه شخصی ساعتی را تلف شده بداند، سرگرم بی ارزش ترین کار گردد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6966,'هیچ چیز ارزشمندتر از همین امروز نیست.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6967,'هر شک و تردیدی تأخیرهای خاص خود را به همراه دارد و ایام، با افسوس بر روزهای از دست رفته می گذرد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6968,'گرفتن تصمیم، اراده می خواهد و اجرای تصمیم، قدرت؛ اراده و قدرت دو بال نیکبختی هستند و بدون بال نباید پرید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6969,'کاری نیست که نتوان با اراده از پیش برد. همه ی دشواریهایی که برای من پیدا شده به یاری اراده و اقدام، از بین برده ام.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6970,'هیچ کس تا زمانی که نیروهایش را نیازموده، نمی داند که دامنه ی آنها تا چه اندازه گسترده است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6971,'مراقب باش که نیروهایت را بر سر مسایل پراکنده از دست ندهی. همیشه تلاش کن که آنها را متمرکز کنی.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6972,'یا کاری را آغاز نکن یا برای انجام آن از همه ی نیروی خود بهره بگیر.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6973,'پر اهمیت ترین کارها هرگز نباید قربانی کم اهمیت ترین کارها شوند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6974,'به وظیفه ی خود آن گونه که لازم است عمل کنید؛ زیرا در این صورت راه شما آشکار می شود و در آن، تاریکی وجود نخواهد داشت.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6975,'وظیفه ای را که از همه به شما نزدیکتر است، انجام دهید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6976,'هر نویسنده تا حدی چهره ی خویش را در آثارش آشکار می سازد، حتی اگر این کار بر خلاف میل اش باشد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6977,'فقط هنگامی یک نویسنده می تواند اثری مهم به وجود آورد که کاملاً گوشه گیر شود.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6978,'برای اینکه نویسنده ای باشید که ساده و روان بنویسد، سزاوار است که آزاد اندیش باشید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6979,'کسی که شادمانی را در خانه‌اش بیابد، شادترین انسان روی کره‌ی زمین است؛ خواه شاه باشد یا دهقان.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6980,'با زبان شعر بهتر می توان در دلها نفوذ کرد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6981,'سند پاره می شود اما عهد نه.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6982,'اجرای عهد به سواد و زور و ثروت نیست، به ذات و سرشت است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6983,'آن که در جایگاه نخست است، حتماً دارای نقش نخست نیست. چه بسیار پادشاهان که از سوی وزیرانشان و چه بسیار وزرا که از سوی منشی هایشان اداره می شوند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6984,'می توانم عهد کنم که صاف و بی پرده باشم، در عین اینکه ممکن است بی طرف هم نبوده باشم.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6985,'ما می توانیم بخشش های بزرگ داشته باشیم، اما در مورد چیزهای کوچک به ندرت از خودگذشتگی به خرج می دهیم.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6986,'کسی سزاوار آزادی است که بتواند بر هوسهای خود چیره شود.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6987,'جوانی منزلی است که مابین مسافت کودکی و پیری واقع شده است. روزگار نیکبختی و کامیابی زندگی در آنجا می گذرد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6988,'مردمان جهان از خُرد تا بزرگ، تارهای سست از آرزوهای گران بر گرد خویش می تنند و خود، عنکبوت وار میان آنها جای می گیرند. ناگهان ضربت جارویی این تارهای سست را از هم می گسلد. آنگاه همه فریاد بر می آورند که کاخی آراسته به دست ستم ویران شده است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6989,'حتی کودکان نیز آرزو دارند و اگر ما خبر نداریم برای این است که نمی توانند سخن بگویند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6990,'هیچ دلبستگی ای نیست که روزی به نفرت مبدل نشود؛ حتی مهر مادری.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6991,'هیچ تاریخی به دور از حب و بغض و اشتباه نیست.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6992,'به نظر من، هدیه ای که موجب خوشنودی کوچک شود بسیار ارزشمندتر از هدایای پر زرق و برقی است که وسواس و خودنمایی هدیه دهنده، تنها موجب کوچکی ما می شود.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6993,'سفر باید اسباب سرور و شادمانی و تمدد [ =ترنجیدگی ] اعصاب باشد، نه دلهره و خشونت و جنگ اعصاب.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6994,'پژوهش بی آنکه خستگی بشناسد، از پی قانون، علت، چرا و چون، می کوشد و می پوید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6995,'تا زمانی که توان اندیشیدن و امکان دوست داشتن داری، به راستی چیزی از دست نداده ای.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6996,'آنگاه که خویشتن را فراموش کنیم، همه چیز ما را فراموش می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6997,'انسان شریف، شریف ها را جذب می کند و راه ماندن با آنها را می داند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6998,'طبیعت همانند بیماری جسمانی در برابر ضعف اخلاقی واکنش نشان می دهد. زمانی که بر دامنه ی خطر افزوده می شود به همان نسبت بر میزان شهامت فرد نیز افزوده می گردد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(6999,'اگر از نعمتهایی که خداوند هر روز به ما می دهد با آغوش باز لذت بریم، هنگامی که بدی فرا می رسد، نیروی کافی برای تاب آوردنش داریم.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7000,'تشویق پس از نکوهش، همچون آفتاب پس از رگبار باران است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7001,'شما می توانید همه ی آدمهای دنیا را با تمام اندیشه های گوناگون بیازمایید، اما خواهید دید که دوستی آدم های خداشناس پایدارتر است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7002,'گل، شادی آفرین است. بگذار همه از این شادی سهمی داشته باشند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7003,'پرپر کردن یک گل، ستم به همه ی طبیعت است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7004,'عاشق تنها زیبایی چهره را می بیند و دانا زیبایی سیرت را و عارف هر دو زیبایی را.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7005,'زیبایی به هرجا که وارد شود مهمان عزیزی است و پذیرایی خواهد شد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7006,'دشواریهای دنیوی نمی توانند حس زیبایی را که خداوند در روح انسان آفریده است از میان بردارند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7007,'عشق ورزیدن هنری بس بزرگ است که هر چند عاشق را زر و زور نمی بخشد، او را همسنگ بزرگترین قهرمانان جهان می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7008,'عشق تند درمانی ندارد و هر دوایی آن را شدیدتر و تندتر از پیش خواهد کرد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7009,'عشق، پریشانی زندگی است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7010,'اگر نمی توانید قهرمان باشید، دست کم مرد باشید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7011,'مادر، شاهکار طبیعت است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7012,'آنچه فرزند درباره ی پدر می اندیشد مهم نیست؛ چگونه پدر بودن مهم است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7013,'ما باید با فرزندانمان چنان رفتار کنیم که خداوند با ما رفتار می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7014,'به میزان افراد بشر، نیکبختی وجود دارد، اما به دست آوردن همه ی آنها فقط یک کلید دارد؛ اراده.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7015,'هر قدر علل و موجبات اندوه آدمی کمتر باشد، نیک بخت تر است و هر چه میزان خواسته ها و تمایلات کمتر باشد، زندگی او آرامتر و به نیکبختی نزدیکتر خواهد بود.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7016,'چون بیشتر نوابغ و دیوانگان رفتار مشابه دارند، نبوغ و جنون در یک ردیف قرار می گیرند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7017,'نابغه در بند حدود دیگران نیست، قانون او در وجود خود اوست. همچون کوهی که آتشفشان از درون می خروشد و کوهپاره ها را از پیش بر می اندازد و دهانه ای برای فوران خود می سازد. فطرت وی قانون می گذارد و کار خود را با آن می سنجد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7018,'نویسندگان از آن جهت که چیزهای نو می آورند مبتکر نیستند، بلکه از آن جهت چنین اند که آن چه را می خواهند بگویند به گونه ای بیان می کنند که گویی هرگز پیش از آن گفته نشده است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7019,'وفا سن و سال و زمان و مکان نمی شناسد؛ همیشه و همه جا باید وفادار بود.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7020,'تو در حقیقت، همانی که هستی؛ روی سرت هزار تا کلاه گیس هم که بگذاری یا کفش های گران به پا کنی، باز همانی که هستی باقی خواهی ماند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7021,'طمع، همچون آب شوری است که هر چه بیشتر خورده شود، تشنگی را افزونتر می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7022,'از میانه روی، خوش بختی پاک سر می زند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7023,'باید بی چون و چرا اشتهای اشخاصی را که بی جهت وقت شما را می گیرند کور ساخت؛ آنها از شما می خواهند که منفعت و سوی در کار به آنها برسانید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7024,'کسی که می خواهد اثری برای دنیا به وجود آورد، باید متوجه باشد که نگذارد دیگری وقت او را بگیرد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7025,'آه! جوان ها! افسوس که شما قیمت و بهای وقت را نمی دانید!((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7026,'تنها کسانی سزاوار و شایسته ی درک آزادی و زندگانی هستند که بدانند چگونه هر روز بر مشکلات زندگانی و آزادی غلبه کرده و آزادی و زندگانی نوینی را به چنگ آورند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7027,'اندیشیدن آسان است و عمل کردن، سخت و دشوار. عمل کردن به دنبال اندیشه و نقشه و از روی تعمق و اندیشه پیدا کردن اش در جهان بسیار دشوار است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7028,'اگر وجود خار در گل مایه اندوه ماست، وجود گل در کنار خار باید مایه شادی ما باشد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7029,'بزرگترین مشکل ها در جایی نهفته است که ما هرگز انتظارش را نداریم.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7030,'دست طبیعت در هر دل پاک این احساس شریف را گذاشته است که به تنهایی نمی تواند خوشبخت شود و باید سعادت خود را در خوشبختی دیگران بجوید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7031,'روحی که زیبایی را می بیند گاهی تنها می ماند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7032,'مردان کامیاب غالبا از شکست، میوه پیروزی چیده اند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7033,'مردان مقاوم و باتجربه همیشه فرصت دارند کارها را از اول شروع کنند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7034,'خوشبخت ترین فرد کسی است که خوشبختی را در خانه خود جستجو می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7035,'عشق، تاج زندگی و سعادت جاودانی است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7036,'دوستی مثل اسناد کهنه است؛ قدمت تاریخ آن را قیمتی می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7037,'کسی که می خواهد رازی را حفظ کند باید این واقعیت را که رازی دارد، پنهان کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7038,'زن، شریک زندگانی و یار ساعات درماندگی است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7039,'سخن گفتن یک نوع نیاز است، ولی گوش دادن هنر است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7040,'اگر مردم زبان «مقاصد» یکدیگر را می فهمیدند، اختلاف از میان برداشته می شد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7041,'خوشبخت ترین مردم کسی است که برای برتری های دیگران ارزش قائل است و از شادی دیگران آن قدر شاد می شود که گویی این شادی مربوط به خود اوست.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7042,'قله ها را مگر با پیمودن راه های پیچ در پیچ نمی توان فتح کرد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7043,'کوشش، نخستین وظیفه ی انسان است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7044,'آنجا که نور بیشتر است، سایه های سیاه تری می توان یافت.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7045,'در مورد همه ی نو آوریها و خلاقیت ها یک حقیقت ابتدایی وجود دارد: از لحظه ای که شخص به طور قطع مصمم به کاری می شود، همه ی امدادهای غیبی به کمک او می آیند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7046,'من از هیچ مکتبی پیروی نمی کنم، از هیچ استادی چاپلوسی نمی کنم... دیوانه ای هستم که قایقم را خود هدایت می کنم.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7047,'به حقیقت، کسی را می توانیم والا و محترم بدانیم که در جست و جوی خویش نیست.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7048,'به خویشتن اعتماد کن، آنگاه راه زندگی را خواهی یافت.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7049,'قلب مغرور و خودخواه هرگز نمی تواند از سرگیجه و بی حوصلگی بگریزد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7050,'جوانی نیز مانند پاک ترین و بهترین عشقها سرانجامی ندارد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7051,'کسی که دارای عزمی راسخ است، جهان را مطابق میل خویش عوض می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7052,'مرد عمل وجدان ندارد؛ تنها مرد اندیشه است که با وجدان است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7053,'زیاده روی در گفتار برای زنان خیلی طبیعی تر از راستگویی است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7054,'ای پیر زنده دل، از گذشت عمر افسرده مشو؛ هر چند زمانه نیز موی تو را سپید کرده، اما هنوز نیروی عشق که زاینده ی جوانی است از دلت بیرون نرفته است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7055,'رنج و شادی؛ این سخن مرا جز با عاقلان مگویید، زیرا مردم عوام جز نیشخند کاری نمی توانند کرد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7056,'می خواهم زبان به ستایش آن كَس گشایم که در پی آتشی است تا خویشتن را پروانه وار در آن بسوزاند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7057,'بکوش تا از ورای پوست به مغز بنگری و در پس پرده ی جدایی، پیوستگی حقیقی را ببینی، زیرا چون بر سر خوان گسترده ی جهان بنشینی، میان شرق و غرب فرقی نتوانی گذاشت.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7058,'هر که خود و دیگران را بشناسد ناچار بدین نکته پی بَرَد که از این پس، شرق و غرب جدا نمی توانند زیست.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7059,'حافظا هیچ کلامی را دو بار در قافیه نیاورم، مگر آنکه با ظاهری یکسان، معنایی جدا داشته باشد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7060,'جرقه ای برای آتش زدن و سوختن شهر امپراتوران کافی است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7061,'به من بگو دلم چه می خواهد... دلم در کنار تو است، حقیرش نشمار.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7062,'گاه خاموشی، بهتر از سخن، از راز دل خبر می دهد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7063,'ای دلبر من، غزلهایی را که روزگاری در دشت و دمن برایت می سرودم، اکنون در دیوانی فشرده و زندانی کرده ام، زیرا زمانه ناسازگار است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7064,'غزل من از رخدادهای زمان ایمن خواهد ماند و هر بیت آن که ستایشگر عشق است، چون خودِ عشق جاودان خواهد بود.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7065,'تو چون مُشکی که از هر کجا بگذری، از خود نشانی گذاری.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7066,'جوانی، خود، مستی بی شراب است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7067,'گنج عشق، گدایان را توانگر می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7068,'نهالهای درختان بارور را کنار هم با نظم کامل در خاک بنشانید، زیرا تنها آن کشتزاری از رحمت یزدانی بهره خواهد برد که منظم و آراسته باشد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7069,'اگر آب و زمین را چنانکه باید پاک نگهدارید، خورشیدِ آسمان با شوق و صفا بر آن خواهد تافت.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7070,'آنجا که آتش فروزان است، ظلمت شب را یارای بقا نیست.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7071,'تا جهان بوده، بیگناهان قربانی رنج و غم بوده اند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7072,'پنج چیز است که پنج چیز از آن نزاید: دلی که خانه ی غرور است کانون محبت نشود. یاران دوران فرومایگی، نکوخویی ندانند و تنگ نظران ره به بزرگی نبرند. حسودان بر جمال و کمال جز به چشم کین ننگرند و دروغگویان از کسی وفا و اعتماد نبینند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7073,'سلامی را که از ناشناس شنوی پاس دار و چون سلام دوستی دیرین، عزیز شمار؛ با آشنای تازه به گرمی سخن بگو، زیرا پس از کلامی چند هر یک به راه خویش خواهید رفت.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7074,'تو که زیردستی، لطف و خشم خواجه را یکسان گیر؛ چه بسا خواجه آنجا خشمگین آید که جای ستودن باشد و آنجا بستاید که جای خشم گرفتن باشد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7075,'اگر خواهی همیشه خرسند باشی همیشه راضی باش.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7076,'دنیا زادگاه رنج است؛ اگر در آن بمانی، چون خواب و خیالی می گریزد و اگر سفر کنی غم با تو همراه می آید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7077,'همه جا ابلهان را دیدم که بیش از هوشمندان تفاخر می کردند و کوته فکران را یافتم که پیوسته کوشای حکمفرمایی بر دانایان بودند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7078,'دانایان، هماره آماج تیر خشم و کین جاهلانند، اما همین که یکی از آنان بمیرد، مردمان به آه و ناله پردازند و به نامش پول گرد آرند تا بنایی به یادبودش بسازند و نامش را جاودانی کنند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7079,'گویند: خود ستودن خطاست، اما کسی را نتوان گفت که کار نکو کند و خرسند نباشد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7080,'دنیا پر از ابلیسانی است که پیوسته به ما کینه می ورزند، زیرا طاقت آنکه کمال خود را به حال خود گذارند، ندارند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7081,'دل من پیوسته آرزوی پرواز دارد، نه برای آنکه به سوی آسمان رود، بلکه تا از دست خویش بگریزد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7082,'هنوز روز است؛ بکوش تا کاری کنی، زیرا وقتی که شب خاموش فرا رسد هیچ کس کاری نمی تواند کرد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7083,'غم جهان مخور که غم خوردن، سرنوشتت را دگرگون نخواهد کرد، اما تعادل زندگانیت را به هم خواهد زد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7084,'اقبال، در خانه ات را بکوفت و تو غافلانه در به رویش نگشودی. اما نگران مباش، فرشته ی اقبال دلی مهربان دارد و باز هم در خانه ات را خواهد کوفت.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7085,'نیکی را فقط برای نفس نیکوکاری، انجام بده. اگر هم به فرزندانت پاداشی نرسد، به یقین نوادگانت از آن بهره خواهند برد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7086,'چرا از دشمنان شِکوه می کنی؟ مگر توقع داری که ایشان تو را که وجودت خود مایه ی آزار دل پر حسدشان است عزیز شمارند؟((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7087,'از این مصیبتی گرانتر نیست که ابلهان، دانایان را به فروتنی بخوانند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7088,'خداوند بر خلاف ما، بدان را به حال خویش می گذارد و کاری به زشت و زیبای آنان ندارد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7089,'اگر حسد در پی آن است که خویشتن را به چنگ و دندان بدرد؛ او را در نیت خیر خود آزاد بگذار.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7090,'تنها آن کس قهرمانان را به حق تواند ستود که خود دلیرانه جنگیده باشد، زیرا تا کسی سرد و گرم جهان را نچشیده باشد قدر مردان ارزنده را نداند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7091,'اگر خواهی از راهزنان نیمه ی راه آسوده باشی، زر و ایمان و مقصدت را از کسان پنهان دار.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7092,'جوانان عادت دارند که گفته های خردمندانه ی پیشینیان را تکرار کنند و آنها را از خویش شمارند. لاجرم در ردیف سخنان ابلهانه، گاه حرفی بس نکو می توان شنید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7093,'اگر عاقلی، با جاهلان مباحثه مکن، زیرا خود را اسیر جهل خواهی ساخت و آنان را دانایی نخواهی آموخت.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7094,'از خود مپرس که از نکویی تو چه کس بهره خواهد برد. نان خود را به آب فکن، بالاخره کسی آن را خواهد خورد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7095,'می گویید تنگ چشمم؛ آخر چیزی به من دهید تا بتوانم بخشش کنم.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7096,'تا مرد خاموش است بر او خطری نیست، زیرا سرنوشت او به زبانش بسته است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7097,'هر کس به جهان آمد خانه ای نو ساخت و چون مُرد خانه به دیگری گذاشت. دومی، بنا را به سلیقه ی خود تغییر داد، اما هیچ کس در پی اتمام آن برنیامد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7098,'بکوش تا نیک را از بد بشناسی و از خود نام نکو باقی گذاری؛ بیش از این دو، چیزی مخواه که همه چیز را از دست خواهی داد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7099,'به هر عشق ناکام! به آتش پر شرار و بی دریغ دوزخ! ای کاش چیزی بدتر می شناختم که بتوانم بدان سوگند یاد کنم!((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7100,'آنچه از میراث پدرانت داری بطلب تا از آنِ تو شود.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7101,'ما همیشه وقت کافی داریم به شرط آن که هم بخواهیم و هم درست از آن استفاده کنیم.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7102,'شوهر، مغز خانه است و زن، قلب آن.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7103,'زندگی را هر گونه که نگاه می کنی زیبا است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7104,'اگر تو را همان گونه که هستی بپذیرند، بدتر خواهی شد و اگر با تو به گونه ای رفتار کنند که گویی همانی هستی که لایق آنی، همان خواهی شد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7105,'هیچ کس نمی تواند ما را بهتر از خودمان فریب دهد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7106,'هر کسی چیزی را می بیند که آن را در قلب خود حمل می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7107,'چه شادی هایی که زیر پای انسانها سرکوب شدند، زیرا بیشتر افراد به آسمان توجه دارند و آنچه را که زیر پاهایشان است نادیده می گیرند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7108,'دوست من، گذشته ها کتابی مهر و موم شده است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7109,'خوشبخت کسی است که راه قدردانی از خدمت دیگران را می داند و شادی دیگران را به قدر شادی خود حس می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7110,'از محالات است که کسی را برای آنچه که هست، دوست بدارند. بیشتر آدمها چیزی را در دیگران دوست دارند که خود به آنها امانت می دهند: خودشان را، تفسیر و برداشت خودشان را از او ... .((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7111,'نیاسایید، زندگی در گذر است. بروید و دلیری کنید، پیش از آنکه بمیرید، چیزی نیرومند و متعالی از خود بجای گذارید تا بر زمان چیره شوید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7112,'هرکس باید روزانه یک آواز بشنود، یک شعر خوب بخواند و در صورت امکان چند کلمه حرف منطقی بزند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7113,'هر چه نور بیشتر باشد، سایه ژرفتر است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7114,'طوفان های حوادث، اخلاقیات و روحیات انسان را تقویت می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7115,'پیش از آنکه کاری بکنی، باید کسی باشی.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7116,'کوشش تازه و جاندار، تنها راه غلبه بر بدبختی است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7117,'شیفتگی آن است که شما چشمان زنی را دوست بدارید، بی آنکه رنگ آن را به یاد آورید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7118,'مردم پست و دون به سراب مانند که چون کمی به آنان نزدیک شوی، به زودی پستی و ناچیزی خود را بر تو آشکار سازند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7119,'کار ما نمایانگر قابلیت های ما است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7120,'هر کس با استعدادهایی آفریده شده که باید آنها را به کار بندد. به کار بستن آنها، بزرگترین سعادت در زندگی هر فرد است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7121,'آنها که غایبند، کمال مطلوب هستند؛ حاضرین [ =باشنده ها ]، عادی و پیش پا افتاده اند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7122,'اخلاق را طوفان های روزگار تقویت می کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7123,'هر آنچه که باید، انجام بده و به تحقق آرزوهایت ایمان داشته باش؛ هر آنچه که باشند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7124,'به کارگیری دخانیات، بیشتر از تمام جنگها انسان را به خاک سپرده است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7125,'دارو بسیار است، اما تعداد کمی [ از آنها ] تاثیرگذارند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7126,'ای عشق! تو افسر زندگی و بختِ بیداری.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7127,'خدایا! چه خوشبختی بالاتر از این، هم عاشق و هم معشوق بودن.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7128,'عشق و اشتیاق، بالهای پرنده ی بلندپرواز خلاقیت است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7129,'عمل، بیان قدرت عشق است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7130,'خدایا، هنر چقدر فرازمند، عمر چه کوتاه!((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7131,'رؤیاهای کوچک را آرزو نکن؛ زیرا برای تکان دادن قلبت به اندازه ی کافی قدرت ندارند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7132,'رفتار هر انسان، آینه ای است که او چهره ی خود را در آن نشان می دهد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7133,'هر کسی نمی تواند انسان باشد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7134,'از اینکه در کنارم هستی بسیار شادم؛ بودنت به من کمک می کند که دریابم دنیا چقدر زیباست.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7135,'طبیعت، تنها کتابی است که تمام صفحاتش پر از معنی است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7136,'آن که به صبح می اندیشد، همیشه می خندد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7137,'با یک اراده ی نیرومند، شرایط دیگر چندان اهمیت ندارد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7138,'مادام که خود را باور دارید، می دانید چگونه زندگی کنید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7139,'هیچ چیز به اندازه ی زمانی که آدمی مطلبی خنده دار می گوید، شخصیتش را نشان نمی دهد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7140,'زندگی بدون کار، مردن پیش از وقت است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7141,'شخصیت، در جریان زندگی شکل می گیرد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7142,'شرط پیروزی، داشتن یک اراده قوی است و شرایط دیگر اهمیت چندانی ندارد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7143,'با داشتن اراده قوی، مالک همه چیز هستید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7144,'زیبایی ناپایدار و فضیلت جاودانه است.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7145,'اگر به مهمانی گرگ می روید، سگ خود را به همراه ببرید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7146,'استعداد در فضای آرام رشد می کند و شخصیت در جریان کامل زندگی.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7147,'نخستین وظیفه هر فرد آن است که استعدادهای خود را پرورش دهد و از تواناییهای خویش استفاده کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7148,'اگر هنری داری، بیاور. در غیر این صورت، هنرنمایی نکن که رسوایی به بارخواهد آورد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7149,'فکر کردن بسیار آسان است و عمل کردن، بی نهایت دشوار. هیچ چیز در زندگی مشکل تر از این نیست که انسان، افکار خود را به عرصه عمل گذارد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7150,'یک کار نیک گاهی یک شخص، یک ده، یک شهر و یا یک ملت را رهایی بخشیده و به پیشرفت می رساند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7151,'کسی که می خواهد خود را از مصیبت ها برهاند، باید در جستجوی لذات خیالی نباشد. چه اگر باشد، همچون نابینایی است که راه را از چاه تشخیص نمی دهد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7152,'مهربانی رشته زرینی است که افراد اجتماع با آن به هم پیوند می خورند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7153,'اگر انسانی، کتابی را می‌نویسد، بگذارید فقط آنچه را می‌داند بر روی کاغذ بیاورد؛ من به اندازه‌ی خودم از آن برداشت می‌کنم.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7154,'اشخاص بزرگ و با همت به کوه مانند که هر چه به ایشان نزدیک شوی، بزرگی و شکوه آنان بیشتر برای تو روشن می گردد و مردم پست و کم تلاش سراب را مانند که چون کمی به آنان نزدیک شوی، به زودی پستی و ناچیزی خود را بر تو آشکار سازند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7155,'سعادتمند کسی است که هر چه پیرتر شود، مهربانتر گردد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7156,'خوشبخت ترین افراد کسی است که دانش و حکمت دیگران را قدر بداند و از خوشبختی دیگران احساس خوشحالی کند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7157,'هر زمان پیرمردی زیر خاک می رود، یک کتاب تاریخ زیر خاک پنهان می شود.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7158,'روح انسان آب را می‌ماند، از آسمان فرود می‌آید و بر آسمان برمی‌بالد و آنگاه دوباره به زمین باز می‌گردد، یک پیوستگی ابدی.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7159,'هنرمند به تنهایی، ارواح را می‌بیند، ولی زمانی که از او خواسته می‌شود ارواح را پدیدار سازد، همه ی آنها را می‌بینند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7160,'زیبایی، بزرگتر از خوبی است، زیرا دربرگیرنده ی خوبی نیز می‌شود.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7161,'هیچ چیز بیش از امروز ارزش ندارد.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7162,'اگر زمانِ خود را به گونه ی درست به کار گیریم، همیشه زمان کافی خواهیم داشت.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7163,'افراد، ویژگی خود را بیشتر از همه با این نشان می دهند که به چه چیز می خندند.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7164,'اگر می خواهید نزدیکانتان از دیدن شما لذت ببرند، باید از دیدار افرادی که می شناسید، شاد شده و شادمانی خود را نشان دهید.((گوته))',102,0,0,0,0,0);
INSERT INTO mysms VALUES(7165,'از بین بردن دشواری های شخصی، به تنهایی، لذت ویژه ای به همراه دارد.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7166,'تا زمانی که به تمام ریشه های بنیادی زندگی مان باور نداشته باشیم، نمی توانیم به زندگی دلخواه و شادی که در ذهن داریم، دست یابیم و سبک زندگی را آنگونه که خود می خواهیم، شکل دهیم.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7167,'هنگامی که شما می خواهید موقعیت پیشین خود را پاس دارید، هیچ معجزه ی جدیدی برای دگرگونی موقعیت تان رخ نمی دهد.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7168,'هرگز خود را برای شرایطی که دارید، سرزنش نکنید، زیرا جز افسردگی و پریشانی هیچ ثمر دیگری ندارد.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7169,'هر چه سرحال تر و تندرست تر باشید، درک آن گونه از زندگی که خواهانش هستید و می خواهید برایتان پایدار بماند، آسان تر می شود.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7170,'حس مکرر شادی برای کسی که خواهان زندگی دلخواه خود است، بسیار ضروری [ =بایسته ] می باشد.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7171,'افرادی که زندگی را برای خود آسان نموده و تلاش می کنند دنیای پیرامون خود را بهتر از پیش سازند، بسیار بهتر و مفیدتر از کسانی زندگی می کنند که تنها به خودِ زندگی توجه دارند و ژرفای روح زندگی را درک نمی کنند.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7172,'شادمانی، نه تنها پایانِ رسیدن به لذت است، بلکه بهترین راه نیرو دادن به روحیه و داشتن انگیزه است.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7173,'آسوده باشید که در طول زندگی، هر چیزی که نیاز داشته و خواهان آن باشید، زندگی در دسترس تان قرار خواهد داد.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7174,'چیزی که شما را در زندگی سرحال و سر زنده نگه می دارد، به خود هدیه کنید.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7175,'بکوشید از هر پیشامدی که برایتان پیش می آید، بسیار لذت ببرید.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7176,'کاری را انجام دهید که می دانید از آن لذت می برید.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7177,'رایج ترین کار نادرستی که تمام افراد مرتکب می شوند، عشق های ناگهانی است که همه گمان می کنند، سرنوشت، این مسئله را سر راه زندگی افراد قرار می دهد.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7178,'هرگز فراموش نکنید که بدون دستیابی به دلبستگی های باور نکردنی، نمی توانید صد در صد به کسی دلبسته شوید.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7179,'یک زندگی اجتماعی خوب، نه تنها باعث لذت و انگیزش می شود، بلکه سرچشمه ی بزرگی برای پشتیبانی از ما در سختی هایمان است.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7180,'برای داشتن زندگی آسوده باید کارهای حساب شده ای انجام دهیم.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7181,'یک زندگی اجتماعی والا هرگز نمی تواند این توانایی را داشته باشد که تجربه های ژرف پر معنایی [ =چَمی ] را بدون خواست خود در دسترس مان قرار دهد.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7182,'کاری پسندیده است که از آن احساس لذت کنیم و نیازهای روانی ما را بهیود بخشد و این امر، هرگز ارتباطی به میزان دریافت مادی ما ندارد.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7183,'همیشه به خود، یادآوری کنید که از شکست هایی که با آن روبرو می شوید، درس می گیرید.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7184,'بکوشید هر روزتان را با پرداختن به کاری که از آن لذت می برید و دوست دارید آن را انجام دهید، به پایان برسانید.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7185,'برای درک کردن و نیکو شمردن دنیای پیرامون خود، زمان صرف کنید.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7186,'قشنگ ترین و بهترین بخش زندگی هر فرد، زمانی است که به نتیجه ی تلاش های خود دست می یابد.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7187,'خود [ شما ] برمی گزینید که چگونه از زمان و انرژی بهره گیرید تا در زندگی به تعادل [ =ترازمندی ] برسید یا نرسید.((گیل لیندن فیلد))',103,0,0,0,0,0);
INSERT INTO mysms VALUES(7188,'میان آدمیان چیزی نیست، جز دیوارهایی که خود ساخته اند.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7189,'فرق انسان و سگ در آن است که اگر به سگی غذا بدهی، هرگز تو را گاز نخواهد گرفت.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7190,'بشریت به ظاهر نیست، به باطن است.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7191,'بدترین و خطرناک ترین کلمات اینست : همه اینگونه اند.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7192,'ما، آدمیان را بیشتر به سبب خوبی‌هایی که خود، در حق‌شان کرده‌ایم، دوست می‌داریم، تا به سبب خوبی‌هایی که آنان در حق ما کرده‌اند.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7193,'همه می خواهند بشریت را عوض کنند، ولی افسوس هیچ کس در این اندیشه نیست که خود را عوض کند.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7194,'به دوستانت خوبی کن، محبت شان نسبت به تو بیشتر می شود و به دشمنان خوبی کن تا دوست شوند.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7195,'در عالیترین طبقات اجتماع و همچنین در پست ترین طبقات آن، زن آلتی بیش برای کامرانی نیست.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7196,'بشر تا زمانی که زندگی را به عنوان چیزی مقدس باور نداشته باشد و به همنوعان خود به چشم برادر نگاه نکند، زندگی دیگران را تباه خواهد کرد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7197,'برای کشف اقیانوسهای جدید باید شهامت ترک ساحل آرام خود را داشته باشید؛ این جهان، جهان تغییر است نه تقدیر.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7198,'مردان بلندنام و باافتخار هرگز نمی میرند، زیرا که گورشان قلوب نسل های آینده است.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7199,'مالکیت زمین از اهمیت ویژه ای برخوردار است. چنانچه مقرر گردد که زمین متعلق به هیچ کس نباشد، مگر آن کس که روی آن کار می کند، آنگاه می توان آزادی ِ پایدار را تضمین کرد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7200,'دوست داشتن، رحمت است؛ مورد محبت قرار گرفتن، خوشبختی است.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7201,'در زندگی، هرکس راه و روش راستی و درستی را بپیماید، زودتر به هدف می رسد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7202,'آفتاب به گیاهی حرارات می دهد که سر ازخاک بیرون آورده باشد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7203,'عشق گوهری گرانبها است اگر با عزت همراه باشد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7204,'تاریخ نگاران شبیه افراد ناشنوا هستند، [ چرا که ] مدام به پرسش هایی پاسخ می گویند که هیچ کس از آنها نپرسیده است.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7205,'موضوع تاریخ، زندگی مردمان و بشریت است.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7206,'انسان نمی تواند به تنهایی و برای خود زندگی کند؛ این مرگ است نه زندگی.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7207,'در زندگی وضعی نیست که انسان نتواند به آن خو بگیرد، به ویژه هنگامی که ببیند همه ی اطرافیانش آن را پذیرفته اند.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7208,'به هنگام عزیمت و در لحظه های حساس زندگی، انسان هایی که توانایی بررسی کرده های خویش را دارند به اراده ای قوی دست می یابند.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7209,'تصور این موضوع که سرنوشت بشر می تواند با تدبیرهای عقلانی تغییر مسیر یابد و راه درست را در پیش گیرد، زندگی را به معنای واقعی ناممکن می سازد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7210,'تنها هدف زندگی، رسیدن به سعادت و کسب لذت است. رسیدن به لذت و خوشی هدفی شایسته و درخور حیات است. بخشش، صلیب و فداکاری، همه در راستای رسیدن به لذت و شادمانی قرار گرفته اند.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7211,'«عشق ورزیدن کافی است، آنگاه همه چیز لذت بخش است.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7212,'هنر خوب است، اگر احساسات خوب را ترویج کند. احساسات هنگامی خوب است که شعور جداکننده ی نیک و بد به خوبی آنها حکم دهد و این شعور در میان مردم یک عصر مشترک است. هنر جهانی یک محک درونی دارد که پا برجا و بی خطاست و آن هم وجدان پاک نام دارد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7213,'هنر، نتیجه ی تحریک احساسات و هدف آن نیز انتقال امور نفسانی و عواطف انسانی است.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7214,'هیچ پدیده ای تحت تأثیر سنت گرایی، به اندازه ی قلمرو هنر آسیب پذیر نیست.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7215,'این زیبایی نیست که آفریننده ی عشق است، بلکه عشق است که آفریننده ی زیبایی است.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7216,'هر وقت خواستید درباره ی راه رفتن دیگری قضاوت کنید، کمی با کفشهای او راه بروید.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7217,'انسان، تنها برای نیکی کردن آفریده شده است.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7218,'بهترین رویداد زندگی زمانی است که آدمی خود را می شناسد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7219,'جنگ چنان بی عدالتی ای است که همه ی آنهایی که آن را به راه می اندازند، می بایست صدای وجدان را در درون خود خفه کرده باشند.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7220,'نیکی همه چیز را مغلوب می کند و خودش مغلوب نمی شود.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7221,'انسانی که معنا و هدف زندگی را بداند، نه تنها خود را با افراد ملت خویش برابر خواهد دانست، بلکه با تمام ابنای اقوام دیگر نیز مساوی خواهد شمرد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7222,'کاری که تنها سود شخصی در آن منظور شده باشد، شرافتمندانه نیست.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7223,'در دنیا لذتی که با لذت مطالعه برابری کند، وجود ندارد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7224,'نیکی، همه چیز را شکست می دهد و خود، هرگز شکست نمی خورد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7225,'نوشتن ده جلد کتاب فلسفه آسانتر از آن است که انسان تنها یک قاعده و دستور تنها را به مرحله ی اجرا و عمل درآورد.((لئو نیکولایویچ تولستوی))',104,0,0,0,0,0);
INSERT INTO mysms VALUES(7226,'عشق، یعنی زندگی و اگر عشق را از کف دهید، زندگی را از کف می‌دهید.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7227,'اگر حقیقت عشق را دریابیم و بی‌پناهی عام بنی‌آدم را بپذیریم، به کسانی هم که دوست داشتنی نباشند، شفقت [=مهربانی] پیدا می‌کنیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7228,'جالب است کسانی که خود سبب می‌شوند دوست داشتن آنها از هر کس دیگر دشوارتر باشد، از همه نیازمندترند به اینکه دوست‌شان بدارند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7229,'عشق، آدمی را رهایی می‌بخشد و آزاد می‌کند تا بی‌آنکه هراسی به دل راه دهد و بدون آنکه مدام لازم باشد با الگوهای فرد دیگری برابر باشد، به تمامی زندگی کند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7230,'اجبار، آدمی را پوک و توخالی می‌کند. عشق، به آدمی نیرو می‌بخشد تا آگاهانه و با اراده‌ای استوار در مسیر هرچه آزادتر شدن گام نهد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7231,'کسی که گمان می‌کند همه چیز را می‌داند، به هیچ رو نمی‌تواند بفهمد که گمانش نادرست است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7232,'همان هستید که از شما سر می‌زند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7233,'اگر همه‌ی آنان که می‌گویند عاشقند، در کردار هم چنین می‌بودند، دنیا بسیار بهتر از آن که هست، می‌شد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7234,'برای شناخت درست کسی، باید بیشتر به کردار او توجه کرد تا به گفتارش.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7235,'آنچه در بیرون از ما سر می‌زند، فاش کننده‌ی چیزی‌ست که در درون‌مان می‌گذرد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7236,'اگر هیچ نشان آشکاری از عشق ما در کردارمان به چشم نمی‌خورَد، باید در بود و نبود آن شک کرد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7237,'هر آدمی، هر اندازه هم خردمند یا دل‌آگاه باشد، نه تنها اشتباه می‌کند که ممکن است در آینده هم به اشتباه کردن ادامه دهد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7238,'کسی، بهتر از خود آدمی نمی‌داند بنیاد رشد و خوشبختی‌اش چیست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7239,'تا زمانی که باور کنیم خود، سرچشمه‌ی خوشبختی خویش هستیم، می‌توانیم رؤیاهامان را برآورده سازیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7240,'فقط زمانی می‌توانیم آنچه را می‌خواهیم، داشته باشیم، که باور کنیم می‌توانیم به دستش آوریم، نه زمان دیگر.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7241,'تا زمانی که یاد نگیریم احساسات خود را بهتر درک کنیم و بر آنها چیره شویم، بنده‌ی آنها خواهیم ماند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7242,'احساسی که با خرد همراه نباشد، فقط سبب هرج و مرج و ناآرامی خواهد بود و بس.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7243,'عشق، زاده‌ی احساس است، ولی پرورده‌ی خرد است. اگر این دو آمیزه‌ی جوهری به تعادل برسند، بقای عشق و رشد ما در آن، تضمین خواهد شد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7244,'با هم کار کردن، نویدبخش زندگانی دراز‌تر و زندگی بهتر و سالم‌تری است تا جدا از هم کار کردن.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7245,'تعالی بخشیدن به [=رشد و بالا بردن] زندگی دیگران، بی‌آنکه به خود، خدمتی کنیم، ممکن نخواهد بود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7246,'اگر پروای رویارویی با دشواری‌ها را بیابیم، دیگر کنار گود زندگی نخواهیم ماند و خود، راه خود را برمی‌گزینیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7247,'این پندار که شاید زمانی بهتر یا مناسب‌تر از این هم بتوان عشق ورزید، برای بسیاری، یک عمر پشیمانی به بار آورده است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7248,'عشق نمی‌ورزیم که به ما عشق بورزند؛ عشق می‌ورزیم که عشق ورزیده باشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7249,'عشق اگر پیوسته به نمایش گذاشته شود، بیشتر به چشم می‌آید.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7250,'نرمخویی همواره برآمده از قدرت است. قدرت است که سبب می‌شود بیاموزیم کسی را به سبب ناتوانی یا ترس یا خشم، محکوم نکنیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7251,'آزادی در گرو اختیار، مسئولیت و تعهد است؛ در قبال آن، همواره بهایی باید پرداخت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7252,'نمی‌توان عشق ورزید و از خیر چیزی هم نگذشت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7253,'آنجا که عشق، حضوری نیرومند داشته باشد، دلشوره و نومیدی و ترس از میان می‌رود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7254,'امروز بر پایه‌ی خاطره‌ی [=یادبود] دیروز، داوری نکن.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7255,'با عاشق شدن است که ژرف‌ترین شادی، آرامش و امنیت ممکن را تجربه می‌کنیم و به ژرف‌ترین وجه درمی‌یابیم که یکی بودن، چه اندازه شگفت‌آور است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7256,'در هر کاری که انجام می‌دهیم، فرصتی [=زمانی] برای عشق نهفته است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7257,'شاد کردن یا افزودن بر مناعت طبع [=بزرگ‌منشی] آدم‌ها، در عمل بسیار آسان است؛ فقط دلی آگاه می‌خواهد و مهربانی آمیخته با صداقت [=راستی] و کاردانی.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7258,'برنامه‌ریزی، نه تنها شگفتی و شادمانی را از در نمی‌راند، بلکه راهگشای آن هم هست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7259,'امنیت در عشق، در پذیرش طبع دگرگون شونده‌ی آن است. اگر این را بدانیم، دیگر چیزی نخواهد بود که از آن بترسیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7260,'هر چند آموختن چیزهای تازه و دگرگون کردن باورهای کهنه ممکن است دردناک باشد، ولی دردآورتر و هستی‌سوزتر از آن- در درازمدت – ایستا و ناهمدل ماندن است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7261,'زمان، ارزشمندترین دارایی ماست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7262,'خوب است فراموش نکنیم که عشق، همانند زمان، چشم به راه کسی نمی‌ماند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7263,'دل‌نگرانی‌های گذشته را به دوش کشیدن، به همان اندازه بیهوده است که هراس از آینده.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7264,'اکنون، تنها چیزی‌ست که بدان نیازمندیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7265,'هر انسان، موجودی بی‌همتاست، با نیازهایی ویژه و طرز زیستن، عشق ورزیدن و آموختنی که از آنِ خود اوست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7266,'زندگی هر کس از آنِ خود اوست. ما باید از هر کس که می‌کوشد آن را از ما برباید، دوری کنیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7267,'آنان که خود را باور دارند و لحظه [=دَم] را باور می‌کنند، از زندگی لذت فراوان می‌برند. آنان آموخته‌اند که گذشته، انبان خاطره‌هاست نه انبار افسوس‌ها.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7268,'چنین نیست که فقط یک راه برای عشق ورزیدن وجود داشته باشد. به شمار همه‌ی کسانی که تاکنون عشق ورزیده‌اند، شیوه‌ی ابراز هواداری، گرامی‌داشت و ارج‌ نهادن وجود دارد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7269,'زنهار، اگر آغاز روزمان با پایان آن متفاوت [=دیگرگون] نباشد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7270,'بیشتر احساس‌ها، به هر گونه که ابراز شوند، گذرا هستند، ولی باید با پیامدهایشان سر کرد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7271,'هوشمندی و هوشیاری، همواره بهترین رهنمای ما هستند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7272,'احساس، از بنیادهای زندگانی‌ست و ابراز کردن آن به گونه‌ای متناسب، رسیدن به تعادلی‌ [=هم‌سنگی] است که آن را در امان بدارد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7273,'بخشی از یگانگی هر آدمی، در تمایز دیدگاه و چگونگی نگرش او به جهان است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7274,'چنانچه بر حقانیت آنچه هستیم، پافشاری کنیم، سرانجام، احترام [=ارج] خویشتن و دیگران را به دست خواهیم آورد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7275,'به رویاهایتان سفت بچسبید؛ چراکه رویاها از یک نظر، اسباب سازنده‌ی واقعیت هستند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7276,'نمی‌توانیم چیزی را بدهیم که خود، از آن بی‌بهره‌ایم. بدون عشق ورزیدن به خود، نمی‌توان به دیگری عشق ورزید.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7277,'فقط با درک و پذیرش خود و همچنین نیازها و اسباب خوشبختی خود است که خواهیم توانست نیازهای دیگران را دریابیم و گرامی بداریم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7278,'عشق از آن رو به آسیب‌پذیری نام‌آور شده که از دیرباز در کف خامان مانده است، ناآزمودگانی که نه به عشق را باور می‌کنند، نه خویشتن را.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7279,'آدمی، ثمره‌ی همه‌ی تجربه‌هایی‌ست که از سر گذرانده، همه‌ی آدم‌هایی که شناخته و همه‌ی رویاهایی که تحقق بخشیده یا نبخشیده.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7280,'منکر گذشته نمی‌توان شد، ولی گردن به بردگی آن نیز نمی‌باید نهاد. گذشته فقط می‌تواند فردا را تا اندازه‌ای برایمان پیش‌بینی‌پذیر کند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7281,'تا خود را به چیزی نگرفته‌ای، دیگری هم تو را به چیزی نخواهد گرفت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7282,'زندگی و عشقی در کار نیست، جز آنچه خود آفریده‌ایم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7283,'هنگامی که عشق با صمیمیت [=یکدلی] ژرف همراه شود، به مرتبه‌ی والاترین تجربه‌ی انسانی می‌رسد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7284,'دگرگونی‌ها را باید در درون برآورده سازیم، نه در بیرون؛ همین است و بس.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7285,'آنچه محدود کننده و سرکوبگر باشد، عشق نیست. عشق، فقط زمانی، عشق است که آزاد کننده باشد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7286,'اطمینان [=بی‌گمانی]، پایه‌ی باور است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7287,'بازی، به وجود آورنده‌ی گونه‌ای همکاری پاک و بدون ریاست و ما را از آن احساس رقابتی [=چشم و هم‌چشمی] که اجتماع به وجود می‌آورد، می‌رهاند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7288,'عشق راستین، خودخواهانه نیست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7289,'والاترین گونه‌ی عشق، تعالی بخشیدن به [=رشد و بالا بردن] خود است و گذاشتن نیازهای دیگران و جهانی که در آن زندگی می‌کنیم، در کانون توجه‌مان.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7290,'هنگامی که به آدم‌ها عشق می‌ورزیم، شادی آنها، رشد کردن آنها و بهتر زیستن آنها از هر چیز مهم‌تر می‌شود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7291,'دانستن اینکه در شرایطی هستیم که به دست خود باید خوشبختی خود را به وجود آوریم، بسیار ارجمند است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7292,'نخستین گام برای از گِل درآمدن، این است که دست روی دست گذاشتن را کنار بگذاریم و چاره‌ای بیندیشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7293,'بسا زمان‌ها از آنچه بیش از هر چیز بدان نیازمندیم، می‌گریزیم و از همین رو، دچار تنهایی، اندوه و بی‌کسی می‌شویم. رشد کردن، راه بهتری پیش پایمان می‌گذارد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7294,'کامل بودن، لازمه‌ی عشق نیست، ولی راستی، هست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7295,'برتری، در جستن کمال است، نه به دست آوردن آن.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7296,'دروغگویی، یکی از سبب‌های ناکام ماندن مناسبت‌هاست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7297,'چیزی ویرانگرتر از این نیست که دریابیم فریب همان کسانی را خورده‌ایم که باورشان داشته‌ایم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7298,'هنگامی که عشق با صمیمیت [=یک‌رنگی] همراه می‌شود، آدمی به بزرگ‌‌ترین تجربه‌ی انسانی دست می‌یابد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7299,'عشق ما هر اندازه هم که نیرومند باشد، نمی‌توان تنها بود و عشق ورزید.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7300,'هیچ رابطه‌ای بدون تلاش آگاهانه، انگیزش خود را پاس نخواهد داشت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7301,'باید زمان را دریابیم تا زندگی و سرانجام، عشق را هستی بخشیم، وگرنه فقط همزیستی کرده‌ایم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7302,'عشق، پیوسته هست. آدمی‌ست که دگرگون می‌شود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7303,'زمانی، رنج یا نومیدی، بیدار کننده خواهد بود که در پی آن، خویشتن را بهتر بشناسیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7304,'شفایی چون امید وجود ندارد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7305,'با همدردی، شرافت افراد را بهبود می‌بخشیم که بنیادی‌ترین امر در عشق ورزیدن به آنهاست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7306,'کسانی که احساس خوبی نسبت به خود دارند، زیاد از آینده نمی‌ترسند. چه شرایط روبراه باشد، چه همه چیز رو به ویرانی رود، استواری این افراد، آسیب ناخورده می‌ماند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7307,'افسردگی، خشم و احساس مزمن بدبختی، فقط برآمده از یک رخداد یا فرد ویژه نیست، بلکه از شیوه‌ی روبرو شدن آدمی با آن هم ناشی می‌شود [=برمی‌آید].((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7308,'هر کسی را می‌توان دوست داشت، فقط از آن رو که انسان است و یکی از آفریده‌های بی‌همتای خداوند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7309,'اگر بخواهیم کسی را در عشق شریک کنیم، کارآمدترین ترفند، این است که تا آنجا که می‌توانیم عاشق خوبی باشیم و بگذاریم دیگر کارها خود به خود انجام شود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7310,'نگرانی، هرگز اندوه فردا را از بین نخواهد برد؛ فقط امروز را از شادی تهی می‌کند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7311,'نود درصد آنچه نگرانش هستیم، هرگز روی نخواهد داد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7312,'دریافت ما از دیگر آدم‌ها، برمی‌گردد به میزان شناخت‌مان از خویشتن.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7313,'از آن رو که مرگ، در بیشتر زمان‌ها، بی‌خبر در پی ‌‌ما می‌آید، به ندرت، زندگی دیگر و عشقی دیگر در کار است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7314,'کارآمدترین چیز برای خوب زندگی کردن و خوب عشق ورزیدن، پذیرش راستین این حقیقت است که هیچ کس دارنده‌ی همیشه نیست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7315,'هیچ چیز بیشتر از باور، آدمیان را به یگانگی نمی‌رساند و به آنان احساس امنیت نمی‌بخشد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7316,'بدون باور، به هیچ رو عشق نمی‌تواند دوام بیاورد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7317,'ما نگرانیم که باور بیش از اندازه سبب فریب‌مان شود، ولی به پیامدهای کم‌باوری نمی‌اندیشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7318,'سرتاسر جهان، کسی نیست که مو به مو شبیه دیگری باشد؛ هرچه بار و بر داریم، به راستی از آنِ خود ماست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7319,'در برابر عشقی که داریم و آنچه از آن به دست می‌آوریم یا نمی‌آوریم، خود ما مسئول [=مورد بازخواست] هستیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7320,'حتی عشق هم با نگفتن می‌میرد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7321,'عشق را باید پیوسته ابراز کرد، پرورش داد و بدان نیرو بخشید.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7322,'دگرگونی، امری‌ست ناگزیر.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7323,'از چگونگی رویارویی ما با دشواری‌ها و بازدارنده‌هاست که نیرو و توان عشق‌مان آشکار می‌شود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7324,'عشق، سازنده‌ی استوارترین افراد است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7325,'تردیدی نیست که عشق، زیبایی را ارمغان زندگی می‌کند، به همان سان، این هم درست‌ است که زیبایی، عشق را افزون می‌کند. هر دو به کار هم می‌آیند و مایه‌ی اعتبار یکدیگرند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7326,'عادی‌ترین امور هم سرشار از زیبایی و جذابیت [=گیرایی] است؛ فقط باید نگاه کرد و یافت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7327,'کوتاهی کردن در عشق، به هر سبب که باشد، بی‌بهره کردن دیگران از بهترین چیزی‌ست که برای بخشیدن داریم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7328,'آن باشید که هستید؛ برای عشق، همین بس است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7329,'خود بودن در دنیایی که همه می‌خواهند از نو ما را بسازند، همواره بزرگترین مبارزه‌ی ما خواهد بود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7330,'اگر می‌خواهیم درخور آنچه هستیم با ما رفتار شود، باید نخست خودمان را بیان کنیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7331,'رابطه‌ها ممکن است سست شود و از میان برود، ولی زندگی و عشق همچنان ادامه خواهد یافت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7332,'اگر بخواهیم دگرگون شویم، امکان کمی وجود دارد که بتوانیم رابطه‌ای را به مدت زیاد پابرجا نگه داریم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7333,'هرگاه به همان اندازه که پذیرای خوشی‌ها هستیم، درد و اندوه و سوءتفاهم را هم پذیرفتیم، راهی را که در طی آن، عاشقی راستین خواهیم شد، آغاز کرده‌ایم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7334,'عشق بر کامل بودن تاکید نمی‌کند؛ می‌خواهد بفهمیم میان آنچه هستیم و آنچه باورمان است و آنچه رفتار می‌کنیم، پیوندی مهم وجود دارد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7335,'بهترین راه بازگشت به زندگی، زندگی‌بخش بودن است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7336,'عشق، بسیار فراتر از احساسی صِرف است. چیزی است که هر روز باید زیسته شود و به کردار درآید.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7337,'سخنی مهرآمیز که صادقانه [=از روی راستی] بیان شده باشد، می‌تواند افسون کننده‌ باشد، بویژه در روابطی که از افسون خود تهی شده است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7338,'تشویق مهرآمیز، مایه‌ی قوت قلب و زمینه‌ساز رشد است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7339,'تفاوت، چاشنی رفتار آدمی‌ست؛ چیزی که سبب این همه جذابیت [=گیرایی] او می‌شود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7340,'به اندازه‌ی تک‌تک آدم‌ها، رویکردهای گوناگون به زندگی وجود دارد. هرچه از این رویکردها آگاه‌تر باشیم و بیشتر پذیرای‌شان شویم، عشق‌مان سرشارتر و ژرف‌تر خواهد شد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7341,'بیشتر زندگانی خود را در میان مردمی می‌گذرانیم که هر روز همان‌ هستند که روز پیش بوده‌اند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7342,'بسیاری از مسائل ما تنها سایه‌هایی کمرنگ هستند که در پرتو شکیبایی، رنگ خواهند باخت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7343,'عشق بادوام، بزرگترین پاداشی‌ست که در ازای شکیبایی، از آن بهره‌مند می‌شویم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7344,'آدمی که درست و حسابی عشق می‌ورزد و عشقش بی‌دوام نیست، آموخته است زمان‌های پریشانی را هم مانند زمان‌های سرشار از شادی بپذیرد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7345,'عاشقان همواره امور منفی را از کانون توجه خود می‌رانند و زیبایی و خوبی و خوشی را جایگزین آن می‌کنند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7346,'آدمی که شکست‌خورده‌ی نادرستی‌های دنیا باشد، آشکارا در برابر آنچه خوب و بر حق است، کور مانده است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7347,'زیبایی و خوبی، در برابر زشتی و بدی، سلاح‌هایی کارآمدند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7348,'مهم نیست چه برایمان رخ می‌دهد، مهم آن است که آنچه روی می‌دهد، چه تاثیری در ما می‌گذارد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7349,'همه‌ی ما باید مسئولیت همه‌ی باورها، برداشت‌‌ها و رفتارهای خود را بپذیریم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7350,'خود را قربانی سرنوشتی شوم پنداشتن، آسان‌تر از پذیرفتن سهم خود در به وجود آمدن مسآله است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7351,'تا زمانی که کسی هست که دوست‌مان بدارد، زنده خواهیم ماند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7352,'سرچشمه‌ی عشق، احساس تنهایی است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7353,'خوب است به یاد داشته باشیم که با چشم‌پوشی از شمار آدم‌هایی که دوست‌مان دارند و غمخوارمان هستند، واقعیت این است که تنها هستیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7354,'عشق، از آن رو ارزش چندانی در ذهن‌ها ندارد که دیر زمانی‌ست در دست افراد ناآزموده مانده است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7355,'به همان اندازه که به بهروزی و خرسندی خودمان اهمیت می‌دهیم، بلکه هم بیشتر، باید بهروزی و خرسندی کسانی که دوست‌شان داریم را ارج نهیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7356,'آدم پایبند، از دست نمی‌دهد، به دست می‌آورد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7357,'برای آزاد بودن، باید مسئولیت کارهایمان را بپذیریم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7358,'به مجرد اینکه گناه شکست‌مان را به گردن دیگری انداختیم، از توان رشد خود کاسته‌ایم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7359,'در عشق، شکست وجود ندارد؛ مگر آنکه بخواهیم گناه شکست‌مان را به پای کسی دیگر بنویسیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7360,'ارزشمندترین دارایی ما شرافت‌‌مان است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7361,'خوار کردن دیگران با هدف بزرگ نمودن خودمان، نشان ناتوانی‌ست. بیشتر سبب دلسوزی است تا خواری؛ چرا که نشان می‌دهد چه نومیدانه آرزو می‌کنیم که دوست‌مان بدارند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7362,'با عشق می‌توانیم آن گونه ببینیم که تاکنون ندیده‌ایم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7363,'تنها به اندازه‌ای که آموخته‌ایم، می‌توانیم عشق ببخشیم و دریافت کنیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7364,'عشق از خلال عشق ورزیدن آموخته می‌شود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7365,'دلشوره هرگز غم فردا را فرو نمی‌نشاند، فقط خون شادی را از رگ امروز بیرون می‌کشد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7366,'روزی که بدون انجام کاری عاشقانه به شب رسیده باشد، هدر رفته است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7367,'همه‌ی ما هرازگاهی نیازمند آنیم که دیگران ما را ببخشایند، ولی بخشایشی که خود باید ارزانی خود کنیم نیز به همان اندازه مهم است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7368,'عشق، جهان را از چشم دیگری به ما می‌شناساند و از این راه، ما را به گسترده‌تر کردن چشم‌اندازمان می‌خواند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7369,'عشق راستین، گرفتنی نیست، به دست آوردنی‌ست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7370,'تنها درد بی‌درمان، آن است که با ابتلا به آن، دگرگونی مثبتی نیابیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7371,'خوب است بدانیم که در بهترین زمان‌ها همیشه سبب‌هایی برای جشن گرفتن وجود دارد، ولی از آن مهم‌تر، در نظر آوردن این امر در بدترین زمان‌هاست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7372,'نمی‌توانیم مسائل‌مان را پشت سر بگذاریم، مگر آنکه دیگر آن آدم مسئله‌ساز پیشین نباشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7373,'باور داشتن، از بنیانی‌ترین ویژگی‌های عشق است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7374,'تا زمانی که در این دنیا درد و رنج، تنهایی و گرسنگی وجود دارد، ما در برابر آنها پاسخگو هستیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7375,'اینکه چه اندازه دوست‌مان دارند، بستگی به این دارد که چه اندازه دوست داشتنی هستیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7376,'تنها زمانی درمی‌یابیم بنیان عشق انسانی چیست که عشق را کمتر بخواهیم و بیشتر ببخشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7377,'اگر شأن خود را نادیده گرفتیم، دیگر حق نداریم از بی‌شأنی گله کنیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7378,'به ندرت می‌پذیریم که خودمان پاسخگوی بدبختی خودمان هستیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7379,'بخش بزرگی از خوشبختی ما بر پایه‌ی حس شرافت‌‌مان بنا نهاده شده است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7380,'چیرگی بی‌چون و چرا بر انسانی دیگر، نه شدنی است، نه خواستنی؛ در هر شرایطی، ویرانگر است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7381,'خردمند کسی است که بتواند در برابر هر مسأله‌ای، چاره‌های گوناگونی بیندیشد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7382,'روزی که از برای فردایش زیسته شود، همواره با یک روز دیرکرد دریافته خواهد شد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7383,'اگر با دیگران همان کنیم که دوست داریم با ما بکنند، دست کم یک زندگی آشتی‌جویانه را پایه‌گذاری کرده‌ایم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7384,'همان دریافت می‌کنی که می‌بخشی، همان می‌یابی که چشم‌داشت آن را داری.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7385,'اگر خواهان عشق هستیم، بهتر است جویای خوبی‌های مردم باشیم؛ حتی اگر از این راه، گونه‌ای خوشبینی نابخردانه را بر خود هموار کرده باشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7386,'کمابیش همه‌ی ما تشنه‌ی آنیم که پذیرفته شویم و دریافت کننده‌ی والا‌ترین ستایش ممکن باشیم؛ ستایشی گویای اینکه کسی عاشق ماست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7387,'از روی راستی، ستایش کردن، آسان است و هیچ گونه خرجی برنمی‌دارد، ولی نباید ارزش آن را دست کم گرفت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7388,'تا واپسین دَم زندگی، نیازمند آن خواهیم بود که دوست بداریم و دوست‌مان بدارند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7389,'بد نیست بخواهیم آنها که دوست‌شان داریم، بهترین‌ها باشند، ولی این امر را هرگز با جاری کردن سیلی از خرده‌گیری‌های منفی‌نگرانه نمی‌توانیم برآورده کنیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7390,'خرده‌گیری، هنری‌ست پیچیده و دشوار؛ با بی‌باکی، نه باید خرده گرفت و نه خرده‌گیری را پذیرفت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7391,'عشق، همیشه به دست آمدنی‌ست. باید پرسید آیا آنقدر خواهان آن هستیم که رنج جست‌و‌جو و تکاپوی سخت روح را بر خود هموار کنیم، یا فقط خودمان را سر کار گذاشته‌ایم؟((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7392,'کمتر چیزی ویران کننده‌‌تر و خوارکننده‌تر از بیخودی، سرکوفت زدن است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7393,'عشق، اندازه نمی‌شناسد، خوار نمی‌شمارد. شرافت را می‌سازد، از آن نمی‌کاهد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7394,'عشق، نمک پاشیدن به زخم‌های کهنه نیست، مرهم نهادن است و شفا بخشیدن. برخاستن و پیش رفتن است و آشتی کردن با زندگی.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7395,'بخشایش، آسان خواهد بود، اگر دیگران را با خود یکی بدانیم، کاستی‌‌های خود را باور داشته باشیم و بپذیریم همه به یک اندازه آماده‌ی انجام کار نادرست هستند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7396,'عشق را با کسی که آسان شکست می‌خورد و زود، سرخورده می‌شود، کاری نیست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7397,'افراد کامیاب، کسانی هستند که پذیرای مسائل زندگی‌اند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7398,'راه زندگی، به تمامی [=سراسر] زیستن است و کامل‌ترین مکمل آن، به تمامی عشق ورزیدن.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7399,'هر اندازه بگذاریم دیگران نیروی خود را بر ما بکار گیرند، پیوندمان با آنچه هستیم و حق ماست، بیشتر گسسته می‌شود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7400,'همدلی فقط به این معناست [=چَم] که بکوشیم دیدگاه دوست‌‌ خود را درک کنیم، نه اینکه بناگزیر آن را بپذیریم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7401,'نباید امیدوار باشیم که همه، همان گونه جهان را دریابند که خود، درمی‌یابیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7402,'زمانی با دیگران به همدلی راستین خواهیم رسید که از خود به در آییم و بکوشیم بفهمیم دیگران چگونه به امور می‌نگرند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7403,'عشق، پیوسته هست، ماییم که گسسته‌ایم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7404,'فقط به همان اندازه می‌توانید دیگران را دوست بدارید که خودتان را دوست دارید.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7405,'توان برقراری ارتباط، بنیادی‌‌ترین سبب ماندگاری روابط است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7406,'به راستی که امید، برای باورمندان، رودی همیشه جاری‌ست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7407,'زندگی، بزرگترین دارایی ماست و عشق، بزرگترین سند و گواه آن است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7408,'خود راستین‌مان بهتر از هر هویتی‌ست که به خود نسبت می‌دهیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7409,'گاهی، بهترین خدمتی که می‌توانیم به آنها که دوست‌شان داریم، بکنیم، این است که کنارشان بایستیم، خاموش باشیم، شکیبایی به خرج دهیم، امیدوار باشیم، درک کنیم و چشم‌براه بمانیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7410,'عشق، بازدارنده‌ی عشق نیست. عشق، فقط زمانی عشق است که آزاد کند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7411,'عاشقان، فقط دل بستن به بهبود را نمی‌آموزند، تلاش می‌کنند آن را برآورده سازند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7412,'پایه و بنیان آنچه همیشگی خوانده می‌شود، شکیبایی است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7413,'عشق، سبُک‌سر نیست؛ روی ناخوشایند زندگی را هم می‌بیند، ولی به سبب پایندگی خود، منفی‌بینی را سرمنزل نهایی خود نمی‌شمارد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7414,'گاه در عشق می آموزیم که رعایت حال دیگری بهتر از پافشاری در اثبات عقیده است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7415,'بیشتر وقتها ما بدون اینکه آگاه باشیم در گرو عشق هستیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7416,'عشق همیشگی است، این ما هستیم که ناپایداریم. عشق متعهد است و مردم عهد شکن، عشق همیشه قابل اعتماد است، اما مردم نیستند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7417,'عشق و صمیمیت پخته، تهی از هرگونه چشمداشت است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7418,'با پول می توان جسم و حتی کمی از احترام دیگران را خرید، ولی عشق را نمی توان.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7419,'شادمانی و عشق، راههایی ارادی‌اند و بس.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7420,'این حقیقت که می توانم دانه ای بکارم و آن دانه تبدیل به گل شود، دانش خود را با دیگری بخش بخش کنم و آن دانش همان شخص شود، یا به کسی لبخند بزنم و بجای آن، لبخندی دریافت کنم، تمرینات معنوی است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7421,'اینکه می‌توان عشق ورزید، چیزی‌ست و شایستگی عشق ورزیدن، چیز دیگر.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7422,'عشق، هرگز به مرگ طبیعی نمی‌میرد؛ از بی‌تدبیری می‌میرد و از وانهادگی.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7423,'نادانی و بی‌تدبیری، در بیشتر زمان‌ها، تهدیدآمیزتر از کار نادرستی‌ست که مرتکب می‌شویم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7424,'عشق، مانند هر چیز زنده و پویا، برای رستگاری، نیاز به تلاش دارد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7425,'آنجا که عشق در کار است، شکستی نیست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7426,'به نظر می‌رسد که ما از شکست‌هایمان زودتر پند می‌گیریم تا از کامیابی‌هایمان.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7427,'کامیابی، بیشتر، با شکست همسایه است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7428,'اگر به جست‌و‌جوی سرچشمه‌ی شکست‌هایمان برآییم، بر خودمان افزوده خواهد شد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7429,'شیوه‌ی رشد و آگاهی‌‌ست، شیوه‌ی عشق.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7430,'دانش، همیشه از پی نادانی می‌آید.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7431,'آنها که گمان می‌کنند همه چیز را می‌دانند، برای پی بردن به نادرستی کار خود، راهی ندارند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7432,'در بیشتر زمان‌ها، اگر ذره‌ای خواهان بخشیدن باشیم، بیش از آنکه به رویا دیده‌ایم، به دست خواهیم آورد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7433,'عشق، سن و سال نمی‌شناسد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7434,'عشق، تا زمانی که دَم می‌زنیم، به زندگی خود ادامه می‌دهد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7435,'در هر سنی، زیستن با عشق، معنای [=چَم] ساده‌اش این است که آموخته‌ایم عشق را زنده نگاه داریم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7436,'آثار محسوس [=چشم‌گیر] هنری با گذشت زمان از بین می‌روند، از دیوارها کنده می‌شوند و با بکارگیری سلیقه‌های گذرا از اعتبار می‌افتند؛ ولی زندگی عشق، همیشگی‌ست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7437,'در عشق می‌توانیم به جایی برسیم که گذشت و چشم‌پوشی را رواتر از سرزنش و نکته‌سنجی بدانیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7438,'به راستی، خوشبخت بوده‌‌ایم، اگر کسی در زندگی‌مان بوده تا زمان صرف ما کند و از نزدیک، تماشاگر گوهر بی‌مانند درون ما باشد و در پرورش توانایی‌های ویژه‌، ما را یاری‌ دهد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7439,'تنها با پذیرش آسیب‌پذیری‌ست که فرصت کامیابی در عشق را به خود ارزانی می‌کنیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7440,'عشق، پرورده‌ی خوشبینی‌ست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7441,'هیچ چیز به اندازه‌ی پذیرفتن شکست، آن را شدنی نمی‌کند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7442,'حتی کارهای نادرست‌مان می‌توانند سبب خوشبینی باشند، اگر از رهگذر آنها فرصت‌هایی برای بازآموزی و آگاه‌تر شدن فراهم آید.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7443,'عشق، کمال طلب نیست؛ ما هم نباید باشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7444,'عاشقان، فقط به بهروزی دل نمی‌بندند، برای آن تلاش هم می‌کنند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7445,'عشق، ما را با کاستی‌های دیگر آشتی می‌دهد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7446,'برای درک کردن چیزی، باید آن را سراسر شناخت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7447,'هرچه بیشتر خود را بشناسیم، به شناخت بیش‌تر دیگران می‌رسیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7448,'کسی که عشق می‌ورزد، توجهش به آن زیبایی غیرجسمانی‌ است که با گذشت زندگانی، رنگ نمی‌بازد. نه اینکه عشق، کور باشد، نه؛ عشق، ریشه و گوهر وجودی را می‌بیند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7449,'عشق را اسیر خُلق و خوی روزمره کردن به مفهوم دور کردن آن از شور و نیروی درونی‌ست و ناگزیر، نتیجه‌ای جز از دست دادنش برای همیشه در بر نخواهد داشت.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7450,'هیچ چیز نمی‌تواند جبران کننده‌ی دمی باشد که عشق ما خواسته شده است و ما بی‌پاسخ از کنار آن گذشته‌ایم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7451,'دست کم یک شرط ضروری [=بایسته] برای عشق ورزیدن به هر کس وجود دارد و آن این است که او به گونه‌ای آزاد و جدای از ما که عاشقش هستیم، رشد کند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7452,'شگفت به نظر می‌آید، ولی حقیقت این است که اگر به رشد مشترک و همزمان دو نفر بتوان امید بست، این امید فقط در سایه‌ی رشد جداگانه‌ی هریک از آنها فراهم می‌آید.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7453,'زمان‌‌هایی هست که باید بیش از آنچه دریافت می‌کنیم، ببخشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7454,'عشق راستین آن است که بی‌اندیشه‌ی سود بیشتر، خواهان بخشیدن به دیگری باشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7455,'کمتر مانعی‌ست که با پایداری، اراده، شکیبایی و به ویژه عشق بیشتر، از میان برداشته نشود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7456,'هیچ دیواری آنقدر بلند نیست که ما را از تنهایی و نومیدی انسانی دیگر جدا نگه دارد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7457,'عشق، کارآمدترین سبب ایجاد پیوستگی با هر چیز است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7458,'دوام عشق در گرو رشد آن است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7459,'به دور از خطر، بازی کردن، یعنی باختن.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7460,'هیچ دردی ژرف‌تر از پوچی ناشی از خطر نکردن نیست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7461,'رشد عشق درست و بی‌گزند در گرو تفاوت‌ها [=دیگرگونی‌ها] است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7462,'عشق، بوم گسترده‌ی نقاشی‌ است که نقش هنرمندانه‌ی قلم ما را کم دارد.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7463,'بیشتر ما در بازی عشق، چون پیاده‌های شطرنج هستیم که از آن، هیچ سردرنمی‌آوریم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7464,'تو وجودی هستی که پس از این هرگز نخواهد توانست هستی یابد. نادیده گرفتن چنین وجودی، نه فقط برای خودت، بلکه برای همه‌ی جهان، اندوه‌ برانگیز خواهد بود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7465,'اگر بتوانیم اندیشه‌ها و رفتارهای بیشتر پیش‌بینی نشده‌ی خود را بپذیریم، کم‌کم بر ما روشن خواهد شد که دیگران چرا چنین می‌اندیشند و چنین رفتار می‌کنند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7466,'همه به یک اندازه می‌توانیم عشق بورزیم و مورد عشق‌ورزی قرار بگیریم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7467,'ممکن است همه‌ی ما به یک اندازه از دارایی، توان، هوش و آوازه بهره‌مند نباشیم، ولی توانایی عشق ورزیدن در همه‌ی ما به یک اندازه است.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7468,'میزان کامیابی ما در عشق، بسته به این است که چقدر می‌خواهیم آن را بیاموزیم، چقدر در برابر شکست مقاوم هستیم، چقدر در برخوردهایمان انعطاف‌پذیریم و چقدر تن به دگرگونی می‌دهیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7469,'کسی میوه‌ی تلخ را دوست ندارد، آدم تلخ را نیز.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7470,'اگر به هر دلیل، روی تُرش به این جهان نشان دهیم، نباید به امید پاسخی دیگر باشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7471,'اگر حتی در زمان‌هایی که می‌دانیم حق با ماست، کمی گذشت پیشه کنیم، چنین گذشتی، بر احساس احترام [=بزرگ‌داشت] و عشق دیگران به ما خواهد افزود و نیز، خمیرمایه‌ی مناسبت‌های پایا خواهد بود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7472,'اگر دیگران را ابزار رسیدن به آرزوهای خود بدانیم، آنها را تا مقام اشیاء پایین آورده‌ایم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7473,'اگر عاشقیم، همان گونه که از خوشی‌های عشق لذت می‌بریم، باید شوربختی‌های آن را هم پذیرا باشیم؛ چه هر دو، بخشی از حقیقت عشق هستند.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7474,'با عشق می‌توانیم به کمک آن که دوست می‌داریم و با برابر هم نهادن باورها و رفتارهایمان، به دیدگاه‌هایی تازه برسیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7475,'زمانی پیر شده‌ایم که شادی درونی خود را از دست داده باشیم.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7476,'کمتر از یک سده، زمان زیادی برای زندگی کردن نیست، ولی آن اندازه هست که بیاموزیم عشق، پایان و سرانجام هستی‌ست.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7477,'در پی کسی باشید که شما را به سبب تفاوت [=دیگرگونی]‌هایتان دوست بدارد، نه به رغم [=برخلاف] آنها.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7478,'عشق، بایرترین زمین ممکن را ارزانی رشد ما می‌کند. این خاک با چشم‌پوشی از خویشتن کهنه و پیش‌فرض‌های بیهوده‌مان، آبیاری می‌شود.((لئوبوسکالیا))',105,0,0,0,0,0);
INSERT INTO mysms VALUES(7479,'آرزو کردن شرط نیست، شرط تحقق آرزو است.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7480,'کار همیشگی و پی در پی سبب می‌شود که نیروی سنجش و ارزیابی خود را از دست بدهید.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7481,'سرشت آدمهای خوب، گرایش به دانستن است.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7482,'عشق سرشار در صورتی به وجود می‌آید که از آنچه دوست دارید،آگاهی فراوان داشته باشید.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7483,'می‌توان در هر زمینه‌ای کسب دانش کرد.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7484,'درست همان گونه که اگر آهن را بکار نگیریم، زنگ می‌زند، همان‌ گونه که آب راکد می‌گندد و یا به هنگام سرما یخ می‌بندد، اگر از آگاهی و مغز خود بهره نگیریم، آن را از دست می‌دهیم.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7485,'ثابت ماندن در یک‌جا سبب از دست رفتن قوه‌ی ادراک شما می‌شود. اندکی فراتر بروید تا چیزهای دیگری را نیز ببینید که در آنها نبود سامان و تناسب، قابل مشاهده است.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7486,'من از سودمند بودن دست نمی‌کشم.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7487,'تنها سخنگوی جهان طبیعت، آزمایش است، پس باید همیشه به تجربه پرداخت و هرکار را به هزار شکل آزمود.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7488,'نقاش باید نهانخانه داشته باشد؛ زیرا هنگامی که در نهانخانه با خود هستید، خودتان هستید و هنگامی که همراه با شخص دیگری هستید، نیمی از خودتان می‌باشید.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7489,'آنچه را نقاشی می‌کنید باید بشناسید و از آن، آگاهی کافی داشته باشید.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7490,'اگر پیوسته و بدون درنگ کار کنید، توان داوری خود را از دست می‌دهید.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7491,'توانمندی و قدرتِ هنر از محدودیت زاده می شود و در آزادی می میرد.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7492,'انسانها به نسبت ظرفیتی که برای کسب تجربه دارند عاقل به شمار می روند. کسانی خوشبخت هستند که از تجربه ی دیگران استفاده می کنند و تجربه ی دیگران را دوباره تجربه نمی کنند.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7493,'دشواری ها نمی توانند مرا شکست دهند. هر مشکلی در برابر اراده ی بی چون و چرای من تسلیم می شود.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7494,'بازدارنده‌ها و دشواریها، کمر مرا خم نمی‌کند.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7495,'انسانها هرچه حساس‌تر باشند، رنج بیشتری می‌کشند.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7496,'اندیشه‌ها به سوی امید می‌چرخند.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7497,'سبب اندوه است که اشخاص در حد میانه نیز به حواس خود توجه نمی‌کنند. نگاه می‌کنند، ولی نمی‌بینند، گوش می‌دهند، ولی نمی‌شنوند، لمس می‌کنند، ولی احساس نمی‌کنند، غذا می‌خورند، ولی مزه ی آن را حس نمی‌کنند.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7498,'پرها می‌توانند انسانها را مانند پرندگان به پرواز درآورند و به سوی بهشت ببرند. این از ادبیاتی‌ است که با قلم‌پرهایشان نوشته شده است.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7499,'نگذارید خشم و بدخواهی شما سبب نابودی زندگی شود؛ زیرا کسی که به زندگی ارج ننهد، خود، شایسته‌ی آن نیست.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7500,'همان‌گونه که روز خوب، خواب خوش در پی دارد، زندگی هم اگر به ‌خوبی سپری شود، مرگی آمرزیده در پی خواهد داشت.((لئوناردو داوینچی))',106,0,0,0,0,0);
INSERT INTO mysms VALUES(7501,'دانا انتظار ندارد کارهای او مورد ستایش و تحسین قرار گیرد.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7502,'بخت اگر یار باشد، شاید مردی چند صباحی بر جهان فرمان براند، اما به لطف عشق، او می تواند فرمانروای جاودان باشد.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7503,'کسی که بر دیگران برتری جوید، نیرومند است، اما کسی که بر نفس خویش چیره شود، از همه نیرومند تر است.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7504,'یک درخت هرچقدر هم بزرگ باشد، با یک دانه آغاز می شود؛ طولانی ترین سفرها با اولین قدم آغاز می گردد.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7505,'معشوق کسی بودن، به شما نیرو می‌بخشد و عاشق کسی بودن، بی‌باکی و جسارت.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7506,'یک مسافر خوب از خود هیچ ردی بر جا نمی گذارد.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7507,'دانای فرزانه بی آنکه گام بردارد، می داند؛ بی آنکه بنگرد، می بیند و بی عمل، سامان می دهد.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7508,'عشق یعنی اینکه آدمی جزیی از همه چیز باشد.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7509,'اول اندیشه، وانگهی گفتار.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7510,'انتخاب در دو چیز امکان ندارد، یکی تولد و یکی مرگ.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7511,'هنگامی که صمیمیت ناب در درون شما به وجود می آید، بلافاصله به بیرون تراوش می کند و در قلب دیگران احساس می شود.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7512,'درختهای بزرگ با یک دانه و دورترین راهها با یک قدم آغاز می شوند.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7513,'برای هدایت مردم، پشت سرشان حرکت کن.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7514,'طولانى‏ترین سفر هم با یک قدم از سر گرفته مى‏شود.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7515,'گشاینده ی بزرگ آن است که بدون جنگ پیروز شود.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7516,'کسی که بسیار بر باور خود پافشاری می کند، همراهان کمی خواهد داشت.((لائوتسه))',107,0,0,0,0,0);
INSERT INTO mysms VALUES(7517,'دارندگان قلبهای پاک هیچ گاه راحتی و خوشی را به خود نخواهند دید. زیرا خوشی و راحتی خود را فدای پیشرفت و ترقی دیگران کرده اند.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7518,'برای کسی که آهسته و پیوسته قدم بر می دارد، هیچ راهی دور نیست.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7519,'اگر دوستانی نتوانند گناهان کوچک یکدیگر را ببخشند، نخواهد توانست دوستی خود را به جایی برسانند.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7520,'بخشش در زیاد دادن نیست، در به موقع دادن است.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7521,'در این دنیا از دو راه می توان موفق شد؛ یا از هوش خود و یا از نادانی دیگران.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7522,'در این جهان، هر طبع نازک به دست کسی که فاقد آن است تباه می گردد.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7523,'تواضعِ بی جا آخرین حد تکبر است.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7524,'وقتی با دوستان به سر می بریم دیگر آرزویی نداریم.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7525,'زندگی تراژدی است برای آن کس که احساس می کند و کمدی است برای آن که می اندیشد.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7526,'اصیل بودن سعادت است، اما خوب است رفتار ما به گونه ای باشد که نیاز به پرسش از اصل ما نباشد.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7527,'برای کسی که آهسته و پیوسته راه می رود، هیچ راهی دور نیست.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7528,'طلوع و غروب عشق، خود را به وسیله درد تنهایی و جدایی آشکار می سازد.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7529,'مرگ یک بار می آید، ولی ترس از فرا رسیدن آن در طول عمر، آدمی را زجر می دهد.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7530,'تهیدست از برخی نعمت های دنیا، ولی خسیس، از همه ی نعمت های دنیا بی بهره است.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7531,'آسمان بی‌ابر مانند دریای بی‌آب و جنگل بی‌درخت است.((لابرویر))',108,0,0,0,0,0);
INSERT INTO mysms VALUES(7532,'اگر خودپسند نباشیم از خودپسندی دیگران شکایت نخواهیم کرد.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7533,'راز اینکه سخنان کمتر کسی بر دل می نشیند این است که هر کس به آنچه خود می خواهد بگوید بیشتر می اندیشد تا به آنچه دیگران می گویند.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7534,'بیشتر مردم عدالت را از آن جهت دوست دارند که می ترسند روزی به آنها ستم شود.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7535,'انسان هیچ وقت [ بیشتر ] از آن زمان خود را گول نمی زند که فکر می کند دیگران را فریب داده است.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7536,'حیله و خیانت، بیشتر از اشخاص ناتوان سر می زند.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7537,'زندگی با شادی و غم همراه است و نباید از آن بیم داشت.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7538,'وقتی که کینه ما بسیار شدید باشد، ما را پست تر از اشخاصی می کند که طرف کینه و تحقیر ما قرار گرفته اند.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7539,'آنچه مردم دوستی می خوانند، چیزی غیر از نفع شخصی نیست. دوستی خودپسندی انسان است که می خواهد به نام دوستی از دیگران بهره ببرد.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7540,'آنقدر که احساسات و خیالات در زنان اثر دارد، امور عقلانی موثر نیست.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7541,'هواهای نفسانی در بیشتر موارد باعث زد و خورد می شود. برای مثال، حرص گاهی تولید اسراف و اسراف تولید حقارت می کند. ما اغلب از شدت ضعف، قوی و از شدت ترس، شجاع می شویم.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7542,'غرور و خودبینی در همه ی مردم به طور یکسان وجود دارد، تفاوتی که هست در شکل بروز آن است.((لارشفوکولد))',109,0,0,0,0,0);
INSERT INTO mysms VALUES(7543,'زیانکارترین عیبها، عیب خود نادیدن است.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7544,'ای پسر، مراقب باش که به خاموشی عادت کنی. من از سکوت هیچ گونه شرمسار نشدم، لیکن بسیار وقت سخن گفتم و پشیمانی بردم.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7545,'چون دوستی اختیار کردی، در حال خشم، وی را آزمایش کن؛ اگر با انصاف باشد، دوستی را شاید و اگر نه از وی دوری کن.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7546,'تقوا، سرآمد صفت های پسندیده است.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7547,'جوانمردی آن است که نیازمندان را نرنجانی.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7548,'پسرم بزرگترین نفرین آن است که همه چیز را تجربه کنی.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7549,'سخنان سودمند را باید نشر کرد، مانند افشاندن تخم گندم؛ حتی اگر آب و هوا و زمین به رویاندن و بار آوردن دانه ها یاری نرساند، باز در گوشه و کنار خرمن، سنبلها و خوشه های کمی سر دهد و بروید.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7550,'فرزندم! در دنیا چنان وارد نشو که به آخرتت زیان برساند و آن را چنان رها مکن که سربار مردم شوی.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7551,'اگر سخن چون نقره است، خاموشی چون زر پربهاست.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7552,'شر با شر خاموش نمی شود، چنان که آتش با آتش، بلکه شر را خیر فرو می نشاند و آتش را آب.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7553,'آن که خوراکش کم است، عمرش زیاد و طولانی است.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7554,'با پیران مشورت کن و از مشورت با کودکان شرم مدار.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7555,'با زیردستان جدال مکن و آنان را حقیر نشمار.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7556,'بدترین پریشانی، پریشانی عقل است و بزرگترین مصیبت ها از نادانی برخیزد.((لقمان))',110,0,0,0,0,0);
INSERT INTO mysms VALUES(7557,'بهترین لحظات زندگی من لحظاتی بود که در خواب گذراندم.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7558,'اگر می خواهی خوشبخت باشی برای خوشبختی دیگران بکوش؛ زیرا آن شادی که ما به دیگران می دهیم به خود ما بر می گردد.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7559,'زیانی که از بیان حقی متوجه من گردد به مراتب مرا گوارا و دلپذیرتر است تا آنکه پنهان کردن مطلبی، ضرری به حقی برساند.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7560,'برای درمان بدبختی و دردهای بشر، دوایی بهتر از موسیقی وجود ندارد.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7561,'در جهان، چیزی برتر از محبت نمی بینم.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7562,'هنر و دانش، برگزیده ترین و بزرگوارترین انسانها را به هم می پیوندد.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7563,'هنر هم مانند زندگی فقط آزادی و پیشرفت است.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7564,'تکامل باید هدف ابتدایی همه ی هنرمندان حقیقی باشد.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7565,'نمی دانم چه رازی بین هنرمندان حقیقی وجود دارد که هیچ کدام به دنیا روی خوش نشان نمی دهند.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7566,'هنر من باید برای سعادت بیچارگان اختصاص یابد.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7567,'من جز خوبی نشان دیگری که نمودار برتری باشد، نمی شناسم.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7568,'من می خواهم گریبان سرنوشت را بگیرم، او نمی تواند سر مرا در برابر زندگی خم کند.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7569,'من برای شادمانی به دنیا نیامده ام، من آمده ام تا کارهای بزرگ انجام بدهم.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7570,'ای انسان! خود به یاری خود برخیز!((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7571,'موسیقی [ مانند ] کشوری است که روح من در آن حرکت می کند. در آنجا هر چیز، گلهای زیبا می دهد و هیچ علف هرزه ای در آن نمی روید. اما تعداد کمی از افراد می فهمند که در هر قطعه از موسیقی چه شوری نهفته است.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7572,'موسیقی از فکر تراوش می کند و در قلب می نشیند.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7573,'من برای شادمانی به دنیا نیامده ام، [ بلکه ] آمده ام تا کارهای بزرگ انجام بدهم.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7574,'بیشتر از هنرم به دانش خود مدیونم که نگذاشت با خودکشی به زندگی ام خاتمه دهم.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7575,'هرگاه آسایش، درازمدت و بی دلیل باشد، به تنبلی می گراید.((لودویگ وان بتهوون))',111,0,0,0,0,0);
INSERT INTO mysms VALUES(7576,'تحمل و بردباری بالاترین دلیری و جسارت است.((لویی پاستور))',112,0,0,0,0,0);
INSERT INTO mysms VALUES(7577,'شانس تنها متوجه ذهن آماده است.((لویی پاستور))',112,0,0,0,0,0);
INSERT INTO mysms VALUES(7578,'شادمانی های حقیقی در اثر ازدواج به دست می آید.((لویی پاستور))',112,0,0,0,0,0);
INSERT INTO mysms VALUES(7579,'هر آنچه که اکنون هستیم نتیجه افکاری است که پیش از این داشته ایم و حالا داریم.((لویی پاستور))',112,0,0,0,0,0);
INSERT INTO mysms VALUES(7580,'در کارهای دشوار، نشاط بسیار وجود دارد.((لویی پاستور))',112,0,0,0,0,0);
INSERT INTO mysms VALUES(7581,'تمدن، تنها زاییده اقتصاد برتر نیست، در هنر و ادب و اخلاق هم باید متمدن بود و برتری داشت.((لویی پاستور))',112,0,0,0,0,0);
INSERT INTO mysms VALUES(7582,'امروز برای دریافت رهنمود، چه کرده‌اید؟((لویی پاستور))',112,0,0,0,0,0);
INSERT INTO mysms VALUES(7583,'باید کمتر سخن گفت؛ از پند و اندرز کاری ساخته نیست؛ پس چه باید کرد؟ جارویی بردارید و خانه‌ی یک نفر را تمیز و پاکیزه کنید؛ این، به اندازه‌ی کافی گویاست.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7584,'اگر می‌خواهید برای ایجاد آشتی در جهان، کاری انجام دهید، به خانه‌تان بروید و خانواده‌تان را دوست بدارید.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7585,'سالها کار سخت شما ممکن است یک شبه ویران شود، اما این مساله نباید شما را از ادامه سخت کوشی باز دارد.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7586,'عشق، میوه تمام فصل هاست و دست همه کس به شاخسارش می رسد.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7587,'در این جهان نیاز به دوست داشتن و ستایش بیش از نیاز نان است.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7588,'ما در این دنیا نمی توانیم کارهای بزرگ انجام دهیم، بلکه می توانیم کارهای کوچک را با عشق های بزرگ انجام دهیم.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7589,'به همسرتان لبخند بزنید، به شوهرتان لبخند بزنید، به فرزندانتان لبخند بزنید و به یکدیگر لبخند بزنید. مهم نیست که به چه کسی لبخند می‌زنید، مهم این است که این لبخندها به شما کمک خواهد کرد تا میزان عشق‌تان را نسبت به یکدیگر افزایش دهید.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7590,'انسان‌ها را از دور دوست داشتن، کار دشواری نیست. دوست داشتن آنهایی که به ما نزدیک هستند، کار دشواری است. بخشیدن یک کاسه برنج برای سیر کردن یک گرسنه، بسی آسان‌تر از کاهش تنهایی و درد و رنج انسانی رانده شده در خانه‌ی خودمان است. عشق را به خانه‌ی خود بیاورید؛ چرا که عشق ورزیدن به یکدیگر را باید از خانه آغاز کنیم.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7591,'اگر به قضاوت مردم بنشینی، دیگر زمانی برای دوست داشتن آنها نخواهی داشت.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7592,'دعا کردن، خواهش نیست، بلکه قرار دادن خود در دستان خدا و گوش دادن به صدای او از کنه قلب است.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7593,'محبت راستین اندازه نمی گیرد، فقط می بخشد.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7594,'چیزهای کوچک به راستی کوچک هستند، اما امین بودن در چیزهای کوچک، کار بزرگی است.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7595,'برهنگی تنها به یک تکه پارچه بستگی ندارد. برهنگی یعنی بی توجهی به انسانیت و شرافت انسانی و شخصیت انسانها.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7596,'به دیگران آنقدر ببخشید که به خودتان فشار بیاید.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7597,'کارهای بزرگ از دست ما برنمی آید، اما می توانیم با عشقی بزرگ، کارهای کوچکی انجام دهیم.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7598,'اگر دعا کنیم، ایمان خواهیم داشت، اگر ایمان داشته باشیم، محبت خواهیم نمود و اگر محبت کنیم، خدمت نیز خواهیم کرد.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7599,'ممکن است بیان عبارت محبت آمیز، ساده به نظر برسد، اما بازتاب آنها تا بی نهایت ادامه می یابد.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7600,'شادی سبدی از عشق است که با آن می توان به روح دست پیدا کرد... بدون شک یک قلب شاد، نتیجه ی قلبی است که از عشق برافروخته است.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7601,'اجازه نده کسی نزد تو بیاید، مگر اینکه هنگام بازگشت، شادتر و خوشحال تر باشد.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7602,'در پایان زندگی، از روی تعداد مدرک هایی که گرفته ایم، میزان مالی که اندوخته ایم و کار های بزرگی که به انجام رسانده ایم، در باره ی ما داوری نخواهد شد، بلکه از ما خواهند پرسید: آیا گرسنه ای را سیر کردی؛ برهنه ای را لباس پوشاندی و بی خانه ای را پناه بخشیدی؟ گرسنه ی نه فقط لقمه نان که گرسنه ی عشق، برهنه ی نه فقط از تن پوش که برهنه ی از عزت و احترام انسانی و بی خانه ای نه فقط از خشت و گل که بی خانمان به سبب طرد و رانده شدن.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7603,'کار بزرگ عبارت است از انجام کار کوچک با عشق زیاد.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7604,'محبت، توری است که روانها را صید می کند.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7605,'این قانون طبیعت است که کار نیکی که امروز انجام می دهید، فردا فراموش می شود؛ اما کار خوب باید انجام شود.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7606,'کشورهای پیشرفته از فقر درک و فهم، فقر اراده، تنهایی و فقدان عشق و معنویت رنج می برند. امروزه بیماری وحشتناک تر از این در جهان وجود ندارد.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7607,'اندازه کردارهای ما مهم نیست، بلکه میزان عشق و دقتی که در آن وجود دارد مهم است.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7608,'نمی توانیم بدون واسطه در حضور خداوند قرار بگیریم، مگر آنکه سکوت درون و بیرون، هر دو را بر خود تحمیل کنیم.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7609,'شناخت خود به معنای ناخدای خود بودن است.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7610,'سکوت ژرف را به مثابه لحظاتی مقدس و گران بها پاس بدار؛ لحظاتی برای پناه جستن در سکوت زنده خانه آفریدگار.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7611,'سکوت پیش از آغاز آفرینش به وجود آمده است و آسمانها در فضا پراکنده شدند، بی هیچ حرفی و سخنی.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7612,'در سکوت گوش فرا ده، چون اگر دلت پر از چیزهای دیگر باشد نمی توانی صدای آفریدگار را بشنوی.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7613,'سکوت، بینشی نوین از زندگی به ما می دهد. در این بینش، وجود ما سرشار از لطف او خواهد شد؛ بارقه ای که امکان می دهد تمام کارها را با خوشی به پیش ببریم.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7614,'اگر شروع به قضاوت کردن در مورد مردم بکنی، وقت پیدا نمی کنی که آنها را دوست داشته باشی.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7615,'مردم غالبا نامعقول، خودخواه و غیر منطقی هستند، با وجود این آنها را ببخشید.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7616,'اگر مهربان باشید ممکن است مردم شما را متهم به چاپلوسی کنند، با وجود این مهربان باشید.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7617,'اگر صادق و روراست باشید ممکن است مردم فریبتان بدهند، با وجود این صادق و روراست باشید.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7618,'چیزی را که طی سالها می سازید ممکن است کسی یک شبه خراب کند، با وجود این بسازید.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7619,'تنهایی و احساس ناخواسته بودن، ترسناک ترین فقر ممکن است.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7620,'محبت‌آمیزترین واژه در دنیا، واژه‌ی نامهربانانه‌ای است که هرگز بازگو نشود.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7621,'کارهای ما چندان نیز بزرگ نیستند، تنها کاری که از ما ساخته است، این است که کارهای کوچک را با عشقی بزرگ انجام دهیم.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7622,'موضوع این نیست که چه کاری می‌کنیم، مهم، میزان عشقی است که به کارمان داریم.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7623,'اگر آرامش نداریم، به این دلیل است که فراموش کرده‌ایم به یکدیگر وابسته‌ایم.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7624,'اگر می‌خواهیم پیام عاشقانه‌مان دریافت شود، باید آن را مخابره کنیم [=بفرستیم]. اگر می‌خواهیم چراغی را روشن نگه داریم، باید مُدام در آن نفت بریزیم.((مادر ترزا))',113,0,0,0,0,0);
INSERT INTO mysms VALUES(7625,'در زندگی، باید به چیزی باور داشته باشی و با چنان اشتیاقی آن را بپذیری که تا پایان زندگی بدان اتکا کنی.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7626,'همه می‌توانند بزرگ باشند.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7627,'پرسش بنیادی و مهم درباره‌ی زندگی این است: برای دیگران چه کار می‌کنید؟((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7628,'عشق، تنها نیرویی‌ست که دشمنی را به دوستی تبدیل می‌کند.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7629,'اگر گام نخست را با ایمان بردارید، ناگزیر نیستید کل پلکان را ببینید؛ فقط گام نخست را بردارید.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7630,'گاهی خداوند دارایی را به دست اُلاغان فربهی می‌دهد که از هر موهبت دیگری بی بهره‌شان داشته است.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7631,'آرزو دارم روزی این حقیقت به واقعیت مبدل شود که همه انسانها برابرند.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7632,'تا خم نشوید کسی نمی تواند سوارتان شود.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7633,'عشق، پایدارترین قدرت جهان است.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7634,'ما آموختیم تا همچون پرندگان در آسمان بال بگشاییم و پهنه ی اقیانوس را چون ماهیان درنوردیم، اما هنر ساده ی زندگی برادرانه را یاد نگرفتیم.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7635,'ما یا باید یاد بگیریم که چگونه با هم مثل برادر زندگی کنیم، یا باید مثل احمق ها نابود شویم.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7636,'آن که مرا نابود نمی کند، نیرومندترم می سازد.((مارتین لوترکینگ))',114,0,0,0,0,0);
INSERT INTO mysms VALUES(7637,'هنگامی که در تردید به سر می‌برید، راستش را بگویید.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7638,'عمر زیادی کردم و نگران دشواری‌ها و دردسرهای زیادی بودم که بیشتر آن‌ها هرگز روی نداد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7639,'چقدر یه چیز کوچیک، وقتی [=زمانی که] بدونی خودت به دستش آوردی، می‌تونه خوشحالت [=شادِت] کنه!((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7640,'باهوش بودن هیچ اهمیتی نداره، ارزش واقعی [=راستین] تو قلبِ انسانه!((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7641,'به قلب مهربونُ عاشق واسه انسان بزرگترین ثروته [=دارایی] وُ بدونِ اون حتی با داشتن هوشِ زیاد، انسان فقیره [=تهیدسته].((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7642,'حس ترس... ای کاش هیچ‌وقت [=هیچ‌گاه] این حسُ کشف نکرده بودم؛ حسی که لحظه‌هامُ خراب می‌کنه، شادیمُ از بین می‌بره وُ سبب می‌شه از وحشت [=ترس] به خودم بلرزم.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7643,'بهترین راه برای درک چیزا، تجربه‌ کردن اوناست. ولی اگه فقط به حدسُ گمون قناعت [=بسنده] کنی، هیچ‌وقت [=هیچ‌گاه] دانا نمی‌شی.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7644,'بعضی [=برخی] چیزا رُ نمی‌تونی بفهمی، ولی همین مسئله رُ هم با حدسُ گمون نمی‌شه فهمید. باید صبور [=شکیبا] باشی وُ به تجربه کردن ادامه بدی تا بفهمی که نمی‌تونی بفهمی! اینجوری زندگی کردن، دنیا رُ برات جذاب می‌کنه.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7645,'اگه چیزی برای کشف کردن نبود، دنیا خیلی یه‌‌نواختُ خسته‌کننده می‌شد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7646,'تلاش کردنُ به نتیجه نرسیدن، درُست به اندازه‌ی تلاش کردنُ به نتیجه رسیدن، لذت‌بخشه.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7647,'اصول [=قوانین]، فقط زمانی مُهمَن که سیر باشی...((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7648,'هوش وُ خرد به مرور زمان پیشرفت می‌کنه، ولی این پیشرفت، ناگهانی نیست.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7649,'همیشه چیزای غیرمنتظره‌ست [=دور از چشم‌داشت] که اتفاق می‌افته [=روی می‌ده].((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7650,'سماجتُ سخت‌کوشی یه [=یک] استعداده [=هنره].((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7651,'باید مراقب تجربه کردن چیزهایی که تنها خرد در آنها نقش دارد، باشیم. دست نگه دارید، مبادا مانند گربه‌ای شوید که روی لوله‌ی بخاری داغ می‌نشیند. او برای بار دوم، روی لوله‌ی بخاری داغ نمی‌نشیند. خوب است که روی لوله‌ی بخاری داغ نمی‌نشیند، ولی او دیگر هرگز روی لوله‌ی بخاری سرد هم نمی‌نشیند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7652,'هرگز اجازه نمی دهم مدرسه رفتن جلوی پیشرفتم را بگیرد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7653,'حقیقت گرانبهاترین چیزی است که ما داریم.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7654,'عشق عینک سبزی است که با آن انسان کاه را یونجه می بیند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7655,'بخشندگی، انتشار بوی گل بنفشه به ته کفش است که لگدمالش کرده است.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7656,'زندگی، چیزی جز مبارزه میان عاطفه و عقل نیست.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7657,'من و مادرم، ناسازگاری‌ها و کشمکش‌‌های زیادی با هم داشتیم، ولی به نظرم، او از همه‌ی آنها لذت می‌برد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7658,'چاپلوسها تنها، همانند دوست هستند، همانطوری که گرگ ها به سگ ها شباهت دارند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7659,'هرگز کاری که امروز می توانید آن را درست انجام دهید، به فردا نسپارید.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7660,'یک دروغ ممکن است دنیا را دور بزند و به جای اولش برگردد؛ اما در همین مدت، یک حقیقت هنوز دارد بند کفش های خود را می بندد تا حرکت کند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7661,'زمانی که شما گناهکار هستید، دوست‌تان باید کنارتان باشد؛ چرا که هرگاه حق با شماست، همه در کنارتان هستند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7662,'همواره ذهن خود را خانه تکانی کنید تا دچار جمود نشوید. خود را از زندگانی تکراری و ملالت بار نجات دهید.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7663,'ازدواج قرارداد دونفره ای است که در همه دنیا اعتبار دارد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7664,'من از عشق بدم می آید، زیرا یک بار عاشق شدم و مادرم را فراموش کردم.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7665,'تمام چیزی که در این زندگی لازم دارید بی خبری و اعتماد به نفس است و موفقیت حتمی است.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7666,'مردی که فکر نو دارد مادام که فکرش به ثمر نرسیده است آرام و قرار ندارد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7667,'بهتر است دهانت را ببندی و احمق بنظر برسی، تا اینکه بازش کنی و همه بفهمند که به راستی احمقی!((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7668,'کسی که نتواند خود را مهار کند، بدون شک قادر به مهار دیگران هم نخواهد بود.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7669,'من نه به رنگ پوست تعصب دارم، نه به طبقه و نه به کیش. تنها چیزی که به آن اهمیت می دهم این است که کسی انسان هست یا خیر؛ بالاتر از این نمی توان بود.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7670,'چین و چروک ها صرفاً از جایگاه لبخندها حکایت می کنند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7671,'بیست سال بعد، بابت کارهایی که نکرده ای بیشتر افسوس می خوری تا بابت کارهایی که کرده ای. بنابراین، روحیه تسلیم پذیری را کنار بگذار، از حاشیه امنیت بیرون بیا، جستجو کن، بگرد، آرزو کن و کشف کن.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7672,'برخی از افراد هر کجا می روند شادی آفرین می شوند و برخی، هر کجا چنین افرادی قدم می گذارند، همواره دنباله روشان هستند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7673,'اثر کلاسیک اثری است که همه کس آرزو می کند خوانده باشد، اما هیچ کس نمی خواهد آن را بخواند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7674,'با هر ستایش خوب [ که از من می شود ]، می توانم دو ماه زندگی کنم.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7675,'باید هوشیار باشیم و از هر تجربه، فقط حکمتی را که در آن نهفته است کسب کنیم.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7676,'اگر می خواهید خودتان شاد باشید، نخست دیگران را شاد کنید.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7677,'وقتی جوانتر بودم همه چیز را به یاد می آوردم، می خواست اتفاق افتاده باشد یا نه!((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7678,'اگر دوستی ها نبود، دشمنی ها، دنیا را جهنم می کرد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7679,'راز پیشرفت در آغاز کردن است. راز آغاز کردن در آن است که وظایف سخت و پیچیده ی خود را به وظایف کوچکی که قابل مدیریت کردن باشند، بشکنید و سپس از نخستین آنها آغاز کنید.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7680,'هیچ منظره ای غم انگیزتر از دیدن یک جوان بدبین نیست.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7681,'راز موفقیت آن است که شغل خود را جزء سرگرمی های خویش قرار دهید.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7682,'انسان برای کسب محبت دست به خیلی کارها می زند؛ اما برای تحریک حسادت دیگران هر کاری که از دستش ساخته باشد، انجام می دهد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7683,'تفاوت اساسی بین یک گربه و یک دروغ در این است که گربه نه تا جان دارد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7684,'اگر آنچه را که می پوشانید چیزی جز یک وجود پاک نیست، زیاد در بند چند و چون جامه نباشید.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7685,'جلوگیری از تکرار تاریخ کاری بیهوده است. زیرا شخصیت آدمی به گونه ای است که اجتناب از تکرار را امکان ناپذیر می سازد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7686,'سعی نکن به یک خوک آواز خواندن یاد بدهی؛ هم وقت خودت را هدر می دهی و هم خوک را اذیت می کنی.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7687,'راستگوترین، بی توقع ترین، مفیدترین و همیشگی ترین رفیق برای هر کسی کتاب است.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7688,'انسان تنها حیوانی است که چهره اش از شرم سرخ می شود و یا باید بشود.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7689,'زن عاقل به تربیت همسرش همت می گمارد و مرد عاقل اجازه می دهد که زنش او را تربیت کند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7690,'گرسنگی، خدمتکار هوش سرشار است.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7691,'تختخواب، خطرناک‌ترین جای دنیاست، چون صدی نود مردم در آن می‌میرند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7692,'یک گفتار درست، ابزاری توانمند است.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7693,'زمانی که پسری چهارده ساله بودم، پدرم آن چنان بی‌سواد بود که به سختی می‌توانستم وجودش را تاب آورم. ولی زمانی که بیست و یک ساله شدم، از اینکه پیرمرد در طی این هفت سال، چقدر آموخته، شگفت زده شدم.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7694,'در برابر یورش خنده، هیچ امری نمی‌تواند پایداری کند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7695,'آسمان هرکس به اندازه ی روشنی و تاری قلب او، روشن یا تار است.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7696,'انسان، تنها حیوانی‌ست که شرم می‌کند؛ یا باید شرم کند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7697,'شکم پر در برابر کله ی خالی و قلب تهی ارزشی ندارد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7698,'تنها آموزش و پرورش است که دل آدمی را نرم می سازد و ایجاد مهربانی و شفقت و نیکی می کند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7699,'بزرگان به کارهای کوچک و بی اهمیت بسیار زود رسیدگی می کنند.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7700,'در عمل ثابت شد که گفتن آسان و عمل دشوار است.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7701,'وقتی که انسان توانایی نداشته باشد باید به کم خشنود باشد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7702,'هر چیزی را پایانی است.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7703,'اگر انسان گاهی در کشور وهم و خیال، شوالیه باشد بهتر از این نیست که در جهان واقع، کنت باشد؟((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7704,'در دنیا تنها شما نیستید که حقوق حقه تان را به مسخره گرفته و در هویت تان تردید کرده اند!((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7705,'[ به ] راستی کارهای آدمی چه خنده آور و ضد و نقیض و ناپایدار است!((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7706,'به یقین در دنیا چیزی لذت بخش تر از این نیست که انسان پرستش کرده شده و دوست داشته شده ی ملتی باشد.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7707,'پادشاهان بایستی گاهگاهی در برابر قوانینی که بر آن تکیه زده اند انعطاف پذیر باشند تا از این راه پی به مفهوم مهربانی و مروت ببرند و در دل آنها عاطفه به وجود آید.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7708,'هرچند که مادرم برای من دچار سختی های فراوانی شده است، ولی فکر می کنم از آنها لذت نیز برده است.((مارک تواین))',115,0,0,0,0,0);
INSERT INTO mysms VALUES(7709,'باری که نمی کشی، نمی دانی چقدر سنگین است.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7710,'حقیقت تلخ بهتر از یک دروغ شیرین است.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7711,'همیشه حق با کسی نیست که بهتر سخن می گوید.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7712,'اگر پالان زرین هم بر الاغ بنهی باز هم الاغ است.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7713,'وقتی نه تا احمق را برای کاری نزد تو می فرستند مراقب باش که تو احمق دهمی نگردی.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7714,'کرهای حقیقی کسانی هستند که نمی خواهند بشنوند.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7715,'اگر می خواهی عاقل شوی، بنشین و گوش کن.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7716,'اگر نمی خواهی سگ ترا گاز بگیرد، با دمش بازی مکن.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7717,'تدبیر، ارزنده تر از زور است.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7718,'مادام که از رودخانه بیرون نیامده ای، سر به سر تمساح مگذار.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7719,'اگر چه دریا آرام به نظر می رسد، ممکن است تمساحی در زیر آب کمین کرده باشد.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7720,'کسی که در برابر مورچه ناتوان است، بیهوده به جنگ فیل می رود.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7721,'با چوبهایی که در جوانی گرد آوری در پیری گرم خواهی شد.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7722,'وجدان بد، انسان را ترسو بار می آورد.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7723,'یک ساعت بامداد بهتر از دو ساعت شب است.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7724,'حتی مورچگان از میهمانی که به موقع برنخیزد، خوششان نمی آید.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7725,'میهمان پررو را کباب کن و بخور.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7726,'رفتار خوب تو، همسایه خوب به وجود می آورد.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7727,'از هر کس که از تو نفرت دارد، دوری کن؛ حتی اگر او مگس و تو فیل باشی.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7728,'تنها چاره نفرت، جدایی است.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7729,'میمون هرگز میمون دیگری را مسخره نمی کند.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7730,'هر اشتباهی را علتی است.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7731,'هر درختی ریشه ای دارد و هر اختلافی علتی.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7732,'زن بد مرداب خوشرنگی است که مرد در آن غرق می شود.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7733,'حتی بهترین چوب ممکن است گرفتار موریانه گردد.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7734,'دروغ شکوفه می آورد، اما میوه ندارد.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7735,'در زیباترین هلو ممکن است کرم وجود داشته باشد.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7736,'دریای بی تلاطم، هیچگاه دریانورد ورزیده تربیت نمی کند.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7737,'کسی که منتظر می ماند به همه چیز می رسد.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7738,'یک دوست خوب را با هر دو دستت نگاهدار.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7739,'تخم مرغ از رقصیدن با سنگ پرهیز می کند.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7740,'آیا خودت وفادار هستی که انتظار وفا داری؟((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7741,'برای کشتن پشه، شمشیر لازم نیست.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7742,'افکار هر انسان دنیای اوست.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7743,'کاری که حتی فکرش تو را ناراحت می کند، انجام نده.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7744,'اگر در غربت طلا ببارد و در وطن سنگ، بهتر است در وطن بمانیم.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7745,'کار کردن خوب است، اما زندگی کردن را فراموش مکن.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7746,'یک نفر نمی تواند در آن واحد در دو قایق پارو بزند.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7747,'آنکه اشکهای یک کودک را نخشکاند، خود خواهد گریست.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7748,'خطاهای کودکان به گردن والدین است.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7749,'کسی که پاروزنی بداند، همیشه جایی در قایق پیدا می کند.((مثل آفریقائی))',116,0,0,0,0,0);
INSERT INTO mysms VALUES(7750,'هرچه قفس تنگتر باشد، آزادی شیرین تر به نظر می رسد!((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7751,'زن و شوهر اگر یکدیگر را بخواهند، در کلبه خرابه هم زندگی می کنند.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7752,'به امید شانس نشستن، همان و در بستر مرگ خوابیدن، همان.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7753,'طالع هر کس به دست خودش است.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7754,'کسی که کار نکند، حق خوردن هم ندارد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7755,'باید حرف دو طرف را شنید.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7756,'نباید از ظاهر امور قضاوت کرد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7757,'خون تمام افراد بشر یکرنگ است، ولی لیاقت آنها.......((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7758,'از آنچه به آسانی به دست آمده، بد نگهداری می شود.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7759,'کسی که در بیرون از خانه خود را عاقلتر از محیط خانه نشان دهد، احمقی بیش نیست.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7760,'داماد زشت و باشخصیت بهتر از داماد خوش صورت و بی لیاقت است.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7761,'بهتر است دوباره سوال کنی، تا اینکه یک راه اشتباه بروی.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7762,'هنگام ازدواج بیشتر با گوشهایت مشورت کن تا با چشمهایت.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7763,'گاهی دروغ همان کاری را می کند که یک چوب کبریت با انبار باروت.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7764,'انسان همچون رودخانه است؛ هرچه عمیق تر باشد آرام تر و متواضع تر است.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7765,'کار، ریشه های تلخ و میوه شیرین دارد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7766,'همه کسانی که با تو می خندند دوستان تو نیستند.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7767,'کسی که خوب فکر می کند لازم نیست زیاد فکر کند.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7768,'هر چه میوه بهتر باشد، زنیورهای بیشتری را به سوی خود می کشد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7769,'دوستی مانند گیاهی است که هر چند وقت یکبار باید آبیاری شود.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7770,'دوست و مگس هر دو در تابستان پدیدار می شوند((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7771,'دوستی وصله شده به ندرت به کیفیت سابق باز می گردد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7772,'با قرض اگر داماد شدی با خنده خداحافظی کن.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7773,'زناشویی، غصه های خیالی و موهوم را به غصه ی نقد و موجود تبدیل می کند.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7774,'خنده بهترین داروها است.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7775,'عشق رقاصی می آموزد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7776,'عشق، کوه را جا به جا می کند.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7777,'عشق، نیازی به آموزگار ندارد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7778,'گذشت زمان بیشتر از کتاب می آموزد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7779,'هنر برتر از پول است.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7780,'یک بار حساب نیست، یک بار چون هیچ است؛ تنها یک بار زندگی کردن مانند هرگز زندگی نکردن است.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7781,'آدم نادان در همه کار نظر می دهد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7782,'پول، غلام مرد عاقل است و ارباب مرد احمق.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7783,'افتادن در گل و لای ننگ نیست، ننگ در این است که همانجا بمانی.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7784,'در بیشتر موارد چنین به نظر می رسد که حتی مرگ نیز به قدرت اراده و خواست ما بستگی دارد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7785,'برای آدم بهانه گیر همیشه بهانه وجود دارد.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7786,'جهنم حقیقی، وجدان مخرب است.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7787,'همسایه ات را دوست بدار، اما دیوار مابین خود را از بین نبرید.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7788,'احمق در کار همه دخالت می کند، جز کار خودش.((مثل آلمانی))',117,0,0,0,0,0);
INSERT INTO mysms VALUES(7789,'آهسته برو همیشه برو.((مثل آمریکایی))',118,0,0,0,0,0);
INSERT INTO mysms VALUES(7790,'در جای خودت بنشین؛ کسی بلندت نخواهد کرد.((مثل آمریکایی))',118,0,0,0,0,0);
INSERT INTO mysms VALUES(7791,'برای خوردن زندگی مکن؛ بلکه بخور برای زندگی کردن.((مثل آمریکایی))',118,0,0,0,0,0);
INSERT INTO mysms VALUES(7792,'احمق و پولش به زودی از هم جدا می شوند.((مثل آمریکایی))',118,0,0,0,0,0);
INSERT INTO mysms VALUES(7793,'تاسف خوردن، جبران قرض را نمی کند.((مثل آمریکایی))',118,0,0,0,0,0);
INSERT INTO mysms VALUES(7794,'دو فکر بهتر از یک فکر است.((مثل آمریکایی))',118,0,0,0,0,0);
INSERT INTO mysms VALUES(7795,'کسی که شرم ندارد، وجدان هم ندارد.((مثل آمریکایی))',118,0,0,0,0,0);
INSERT INTO mysms VALUES(7796,'زود رسیده زود فاسد می شود.((مثل آمریکایی))',118,0,0,0,0,0);
INSERT INTO mysms VALUES(7797,'عدالت، مجموعه وظایف اخلاقی است.((مثل آمریکایی))',118,0,0,0,0,0);
INSERT INTO mysms VALUES(7798,'مهربانی مانند کاشتن بذر گندم است که با گذشت زمان افزایش می یابد.((مثل آمریکایی))',118,0,0,0,0,0);
INSERT INTO mysms VALUES(7799,'اگر ما آنچه را که می بایست انجام می دادیم، به آنچه بایستی داشته باشیم می رسیدیم.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7800,'بگذار کسی که سردش است، آتش را خاموش کند.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7801,'گدا هر قدر گردنش را کج تر نگاه دارد، بیشتر دروغ می گوید.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7802,'غاز باید خیلی احمق باشد که روباه را از دور نشناسد.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7803,'عادت، اول دوست انسان است و بعد دشمن او.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7804,'تپه ای وجود ندارد که سراشیبی نداشته باشد.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7805,'هیچ زنی در راه رضای خدا با مرد ازدواج نمی کند.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7806,'دوام ازدواج یک قسمت آن از روی محبت است و نُه قسمتش از روی چشم پوشی از خطا.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7807,'کسی که یک بار مرا فریب می دهد برای او ننگ است، ولی کسی که دو بار فریبم می دهد برای من ننگ است.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7808,'کم‌خردان همه چیز را می‌خواهند و خردمندان، گزیده‌خواه هستند.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7809,'برای میهمانی هر قدر هم خرج کنی، بالاخره از یاد میهمان خواهد رفت.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7810,'هر وقت از سر میز قمار بلند شدی، به موقع بلند شده ای.((مثل اسکاتلندی))',119,0,0,0,0,0);
INSERT INTO mysms VALUES(7811,'زن مطیع فرمانروای قلب شوهر است.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7812,'یک متر یک متر سخت است، ولی یک سانت یک سانت مثل آب خوردن است.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7813,'مرد عمل شاید آدم پولداری نباشد، ولی شخص با اراده ای است که می‌داند در پی چیست و کجا باید برود.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7814,'هر کار ارزشمند، ارزش آن را دارد که آغاز بدی داشته باشد.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7815,'کسی که خردش به کار خودش نرسد، خردمند نیست.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7816,'برای اینکه پیش قاضی نروی، پشت سر قانون راه برو.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7817,'از حسادت تنها یک نتیجه عاید حسود می شود و آن از بین رفتن تدریجی خود اوست.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7818,'درستکاری بهترین سیاست است.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7819,'یک قهرمان گناهکار در مقابل یک ترسوی بیگناه به زانو در می آید.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7820,'عالی ترین سلاح برای مغلوب کردن دشمن، خونسردی است.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7821,'اگر اسرار خود را به زن لال هم بسپاری، فاش می شود.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7822,'طمع به همه چیز، از دست دادن همه چیز است.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7823,'شوهر به مرد کن نه به پول.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7824,'به جز بذرهایی که کاشته ای، چیزهای دیگری هم در باغ می روید.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7825,'نمی گویم دستخوش هیجان نشو، اما خودت را به آتش هیجانها نسوزان.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7826,'اول ازدواج کن، عشق خودش به وجود می آید!(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7827,'اگر کسی از خانه بیرون بیاید و نداند به کجا می رود نادان است.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7828,'قرض، حلقه اسارتی است که به دست خود بر گردن خود می افکنیم.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7829,'حیوان به پایش بسته می شود، انسان به قولش.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7830,'صبحانه را تنها بخور، نهار را با دوست خود بخور و شام را بده به دشمن خود.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7831,'وجدان محکمه ای است که در آن احتیاجی به وکیل دادگستری نیست.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7832,'دوست، دزد زمان است.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7833,'اگر دیر برسید، بهتر از آن است که غایب باشید.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7834,'هر قدر بیشتر ماهی بخواهید باید بیشتر به دریا نزدیک شوید.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7835,'قانون اغلب دندان خود را نشان می دهد، ولی گاز نمی گیرد.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7836,'شخص قوی و آبشار هر دو راه خود را باز می کنند.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7837,'کسی که قدرت را با پول بخرد، عدالت را هم به پول می فروشد.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7838,'کاری که گرگ به زور می کند، روباه با حیله انجام می دهد.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7839,'قمار، مشروب و زن بد، خنده را از لبان مرد دور می کند.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7840,'بین زن کور و شوهر كَر، همیشه آرامش برقرار است.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7841,'زیبایی در چشمهای بیننده است.(( مثل انگلیسی))',120,0,0,0,0,0);
INSERT INTO mysms VALUES(7842,'دختر عاقل، جوان فقیر را به پیرمرد ثروتمند ترجیح می دهد.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7843,'گاهی ترحم ستم است و ستم ترحم.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7844,'پنجاه دوست دارید؟ این کافی نیست، یک دشمن دارید؟ بسیار زیاد است.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7845,'از دشمنی که دشمنی خود را در برابر تو ابراز می کند، زیاد نترس.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7846,'معنی همه چیز دانستن، هیچ ندانستن است.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7847,'باغبان هیچ گاه باغش را نابود نمی کند.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7848,'تا زمانی که میوه هایم سبز نشده، مرا درخت زیتون خطاب نکن.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7849,'موفقیت برای اشخاص کم ظرفیت مقدمه ی گستاخی است.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7850,'دو نوع زن وجود دارد؛ با یکی ثروتمند می شوی و با دیگری فقیر.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7851,'پای دروغ کوتاه است.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7852,'انسان نمی‌تواند خرد یا تجربه‌اش را برای وارثان به جا گذارد.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7853,'کسانی که در انتظار زمان نشسته اند، آن را از دست داده اند.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7854,'کسی که در حضورت از تو می ترسد، در غیابت از تو متنفر است.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7855,'همه ی آرزوها، دم مرگ زنده می‌شوند.((مثل ایتالیایی))',121,0,0,0,0,0);
INSERT INTO mysms VALUES(7856,'دل به دل راه دارد.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7857,'آدم باید کلاهش را پیش خودش قاضی کند.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7858,'زبان سرخ، سر سبز را می دهد بر باد.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7859,'قبل از فرا رسیدن تاریکی، چراغت را روشن کن.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7860,'کلیدی که همه دشواریها را در زندگی خواهد گشود محبت است.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7861,'رگ خواب ایرانی، چاپلوسی است.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7862,'از این ستون به آن ستون فرج است.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7863,'میهمان روز اول زر باشد، روز دوم نقره، روز سوم آهن، روز چهارم سفال و روز پنجم خاکستر که در کوچه ریزند.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7864,'شکرگزار باش تا سزاوار نیکی باشی.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7865,'شمع با آب شدن به دیگران نور می دهد.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7866,'برای کسی بمیر که برایت تب کند.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7867,'ابر پر صدا باران ندارد.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7868,'محبت را از ماهی باید آموخت که اگر از آب خارجش کنی، می میرد.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7869,'خودستایی جان من دلیل نادانی بود.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7870,'به هر کجا که روی آسمان همین رنگ است.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7871,'استاد بودن در یک فن، بهتر از آن است که شخص در صد کار دخالت کند و هیچ یک را به خوبی نداند.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7872,'روزی را در آغاز صبح تقسیم می‌کنند.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7873,'کس نخارد پشت من جز ناخن انگشت من.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7874,'هر سخن جایی و هر نکته مقامی دارد.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7875,'یک دیوانه سنگی به چاه می اندازد که صد عاقل نمی توانند [ آن را ] بیرون آورند.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7876,'گریه کردن هم دل خوش می خواهد.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7877,'دشمن دانا از دوست نادان بهتر است.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7878,'پیکان از زخم به در آید و آزار در دل بماند.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7879,'مرد حکیم خرده نگیرد بر آینه.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7880,'صدقه، راه به خانه ی صاحبش می برد.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7881,'به کارهای زشت عادت مکن، زیرا ترک آن دشوار است.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7882,'دانش، از بهر دین پروردن است نه از بهر دنیا خوردن.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7883,'دوصد گفته چون نیم کردار نیست.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7884,'جوان را نفرست به زن گرفتن، پیر را نفرست به خر خریدن!((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7885,'سری که درد نمی کند دستمال نمی بندند.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7886,'شراب زده را شراب درمان است.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7887,'عروس نمی توانست برقصد، می گفت اطاق کج است.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7888,'سیاست، پدر و مادر ندارد.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7889,'از عنوان، کتاب پدید آید!((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7890,'عشق پیری گر بجنبد سر به رسوایی زند.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7891,'عشق و مشک پنهان نمی ماند.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7892,'بهتر ز کتابخانه جایی نبود!((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7893,'هنر بهتر از ملک و مال پدر.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7894,'هرکس کتاب را به امانت (عاریه) داد، یک دستش را باید برید و هرکس پس بدهد، هر دو دستش را.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7895,'چو میوه درخت خوردی شاخه مشکن.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7896,'هر که بامش بیش برفش بیشتر.((مثل ایرانی))',122,0,0,0,0,0);
INSERT INTO mysms VALUES(7897,'تاک را از خاک خوب و دختر را از مادر خوب و اصیل انتخاب کنید.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7898,'در دنیا دو نوع کور وجود دارد : یکی آنکه نابیناست و دیگری آنکه شهوت جلوی چشمش را بگیرد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7899,'ذهن خود را تیره نکنید تا هر چیز برای شما روشن گردد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7900,'دلت را چنان تمیز کن که ظرفت را.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7901,'از آهسته رفتن نترس، از بی حرکت ایستادن بترس.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7902,'برای اینکه انسان کمال یابد صد سال کافی نیست، ولی برای بد نامی او یک روز کافی است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7903,'با صبر و بردباری، برگ توت تبدیل به پیراهن ابریشمی می شود.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7904,'کسی که آهسته رود، دور رود.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7905,'سازنده بت هرگز بت پرست نمی شود.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7906,'مگسی که بر پیشانی دوست‌تان نشسته، با تبر نرانید.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7907,'هر کسی دیوار پوسیده و ناچیز را هل می دهد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7908,'روباه نباید هنگام محاکمه مرغ، قاضی دادگاه باشد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7909,'کسی که تنها به فکر سیر کردن شکم است، عقل را گرسنه گذاشته است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7910,'دانش این است که بدانید و بدانید که می دانید و بدانید که نمی دانید.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7911,'با اینکه رو در روی هم سخن می گویند، قلبشان فرسنگها از هم دور است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7912,'هنگامی که خشم حرف می زند، عقل چهره خود را می پوشاند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7913,'بدبختی چیزی نیست که بتوان از آن برحذر بود، بلکه چیزی است که گریزی از آن نیست.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7914,'مردان در اوقات خوشبختی نسبت به سایر افراد همدردی نشان می دهند و زنان در اوقات بدبختی.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7915,'هیچ وقت به اندازه زمانی که سر و کارمان با احمق است، نیاز به هوش زیاد نداریم.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7916,'راستی یگانه سکه ای است که همه جا قیمت دارد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7917,'هزار بار گوش بده و بیش از یکبار سخن مگو.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7918,'شمشیر تیز به دست سرباز شجاع داده می شود.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7919,'آن کس که در هر جا دوستانی دارد، همه جا را دوست داشتنی می یابد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7920,'کسی که زن خوب به دست آورد، بزرگترین سرمایه را به دست آورده است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7921,'دانایان سکوت می کنند،با استعدادها گفتگو می کنند و ابلهان اهل مجادله اند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7922,'هنگام خشم اگر درنگ پیشه کنید، از صدها روز اندوه خوردن رسته اید.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7923,'کسی که می‌پرسد، تنها برای پنج دقیقه نادان است، ولی آن که نمی‌پرسد، برای همه‌ی عمر نادان می‌ماند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7924,'یک تصور، خیلی بهتر از ده هزار کلمه با انسان حرف می زند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7925,'همیشه بوی عطر به آن دستی که گل هدیه می دهد باقی خواهد ماند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7926,'آدم خشمگین نمی تواند حقیقت را بگوید.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7927,'نمی توانیم کاری کنیم که مرغان غم، بالای سرِ ما پرواز نکنند، اما می توانیم نگذاریم که روی سرِ ما آشیانه بسازند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7928,'دانشی که تو را اصلاح نکند گمراهی است و مالی که تو را سود ندهد عذاب است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7929,'برنج سرد را می توان خورد، چای سرد را می توان نوشید، اما نگاه سرد را نمی توان تاب آورد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7930,'کسی که از رنج زندگی بترسد، از ترس در رنج خواهد بود.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7931,'اگر سوار پلنگ شدی، پیاده شدنت دشوار خواهد بود.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7932,'اگر از آینده باخبر باشی، امروز کار نمی کنی.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7933,'گُرز برزگ زندگی ممکن است سرم را بشکند اما گردنم را خرد نمی کند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7934,'جواهر بدون تراش و مرد بدون زحمت ارزش پیدا نمی کنند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7935,'تا زمانی که امروز مبدل به فردا شود، انسان ها از سعادتی که در این دم نهفته است غافل خواهند بود.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7936,'تا زمانی که به یک دره ی عمیق نرسیدی، ضخامت زمین را درک نمی کنی.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7937,'باران بهاری به اندازه ی نفت ارزشمند است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7938,'تا زمانی که ریشه عمیق است ترسی از باد وجود ندارد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7939,'یک شادی، صدها غصه را پراکنده می سازد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7940,'انسان موفق سهل انگار نیست و انسان سهل انگار موفق نیست.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7941,'لیاقت داماد به قدرت بازوی او است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7942,'برا ی یافتن زن، می ارزد که یک کفش بیشتر پاره کنی.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7943,'اگر می خواهی برای یک روز در شکنجه باشی مهمان دعوت کن. اگر می خواهی یک سال عذاب بکشی پرنده نگه دار و اگر می خواهی تمام عمر در شکنجه باشی ازدواج کن!(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7944,'پیش از اینکه تشنه شوید، چاه را بکنید.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7945,'وقتی کمتر سزاوارم به من عشق بورز که آن زمان نیازمندترم.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7946,'خوش زبان‌‌ترین انسان، کسی است که بتواند شنونده‌ای خوب باشد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7947,'پشت سر هر مرد توانایی، مردان توانای دیگری وجود دارند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7948,'درختی سبز را در قلب خویش نگه دارید، شاید که پرنده‌ای آوازخوان از راه برسد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7949,'به جایی که زمین خورده ای نگاه نکن، به جایی نگاه کن که پایت سُر خورده است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7950,'وجود یک مدیر بی ارزش در محیط کار مانند وجود یک میمون در شیشه فروشی است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7951,'فرزانگان سکوت اختیار می کنند، با استعدادها سخن می گویند و نادانها جر و بحث می کنند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7952,'فتح ستارگانی که هزاران فرسنگ با ما فاصله دارند با برداشتن اولین گام ممکن شده است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7953,'اگر گلی سالم باشد میوه آن هم سالم خواهد بود.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7954,'تمام گلهایی که در آینده خواهند رویید در دل تخم های امروز نهفته اند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7955,'اگر می خواهی بدانی چه بوده ای، بنگر که چه هستی و اگر می خواهی بدانی که چه خواهی شد، بنگر که چه می کنی.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7956,'اگر برای خاموش کردن آتش می روی، لباس علفی بر تن مکن.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7957,'چه بر حق باشی چه نباشی، اهمیتی ندارد. اگر پول نداشته باشی، خطاکار به شمار خواهی رفت.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7958,'با ثروت می توان بر شیاطین فرمانروایی کرد، بدون ثروت حتی نمی توان بره ای را احضار کرد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7959,'مردم همیشه به سخنان مرد پولدار گوش می دهند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7960,'همیشه دزدی وجود دارد که دزد دیگر را غارت کند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7961,'فرد چاپلوس، یا نادانی است که به فکر تحقیر من است و یا شیادی است که می خواهد فریبم دهد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7962,'زمانی که که دهانت می خورد بگذار با شکمت مشورت کند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7963,'مردی که کوه را از میان برداشت، کسی بود که شروع به برداشتن سنگ ریزه ها کرد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7964,'اگر وجدان زخمی شد، خوب شدنی نیست.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7965,'یک لحظه اشتباه، یک عمر پشیمانی به بار می آورد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7966,'با یک متر طلا نمی توان یک متر وقت را خرید.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7967,'برای آدم خوش زبان هیچ کس دشمن نیست.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7968,'پرهیز از نیکوکاری بسی بهتر از نیکی کردن به امید دریافت پاداش است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7969,'آنکه تهمت می زند، هزار بار می کشد و قاتل، یکبار.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7970,'برای اینکه انسان کمال یابد صد سال کافی نیست، ولی برای بدنامی او یک روز کافی است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7971,'کفش گشاد که پوشیدی، منتظر زمین خوردن هم باش.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7972,'همه مردان بزرگ در پشت دانایی خود دارای دلی کودکانه هستند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7973,'کمرنگ ترین جوهر بهتر از قوی ترین حافظه است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7974,'عجله، کاری را انجام می دهد که احتیاط [ آن را ] منع کرده است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7975,'هر سفر هزار فرسنگی هم با یک گام آغاز می شود.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7976,'اگر می خواهی کاری را خوب انجام دهی، با سه تن از سالخوردگان مشورت کن.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7977,'طلا که حکمران شد، عموم احمقها فرمانبردار می شوند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7978,'وقتی متهم قاضی شود، فاتحه قانون خوانده شده است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7979,'کسی که به دادگاه می رود، گربه خود را به دست می آورد، ولی به طور حتم گاوش را از دست خواهد داد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7980,'قمار باز کور است، ولی کسی که کنار دست او نشسته خوب می بیند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7981,'اگر چه در کنار جنگل زندگی می کنی، قدر چوب را بدان.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7982,'برای [ شخص ] قانع حتی فقر و گمنامی شیرین است و برای ناراضی حتی ثروت و افتخار، [ مایه ] رنج است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7983,'همه چیز چون بسیار شود، خوار و ارزان گردد، مگر علم و دانش که هر چه بیشتر شود، عزیزتر گردد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7984,'فرد خردمند، اهانت‌ها [=سبک‌داشت‌ها] را فراموش می‌کند، همان گونه که فرد ناسپاس، خوبی‌ها را.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7985,'کسانی که زهر زندگی را نچشیده اند، شهد زندگانی را نیز نچشیده اند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7986,'خانواده‌ای که شخص کهنسالی در آن باشد، دارای جواهری است.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7987,'اگر می‌خواهید یک ساعت شادمان باشید، یک شیشه نوشیدنی خنک بنوشید. اگر می‌خواهید یک سال شادمان باشید، ازدواج کنید. اگر می‌خواهید در همه‌ی زندگی، شادمان باشید، یک باغچه درست کنید.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7988,'اگر یک فنجان چای داغ می خواهی، نخست باید فنجانت را خالی کنی.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7989,'مردی که گشاده رو نباشد، نباید مغازه باز کند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7990,'آدم خردمند، دوخت و دوز لباسش را به یک خیاط چیره دست می سپارد.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7991,'شخصی که می خواهد مرغابی سرخ کرده، پرواز کند و وارد دهانش بشود، مدت زیادی باید بردباری و شکیبایی پیشه کند.(( مثل چینی))',123,0,0,0,0,0);
INSERT INTO mysms VALUES(7992,'کسی نمی‌تواند ثروتی را که در قلب شماست، بدزدد.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(7993,'دعا کن و از خدا بخواه، ولی به سوی ساحل هم پارو بزن.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(7994,'در مرداب دروغ چیزی جز ماهیهای مرده شناور نیست.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(7995,'برای کسی که شکمش خالی است، هر نوع باری سنگین است.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(7996,'بدون باختن برنده نمی شوی.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(7997,'سعادت مانند اسب نیست؛ نمی توانی آن را مهار کنی.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(7998,'همسفری خردمند داشتن، به این می‌ماند که نیمی از راه را رفته باشی.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(7999,'آینده به کسی تعلق دارد که می داند چگونه منتظر ننشیند.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(8000,'وقتی پول حرف می زند، حقیقت سکوت می کند.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(8001,'عیب در این نیست که انسان اشتباه کند، بلکه عیب در این است که به اشتباه خود پی نبرد.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(8002,'خوشبختی ها زمان نمی شناسند.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(8003,'اگر به میهمانی گرگ می روی، سگ به همراه ببر.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(8004,'امکان دارد گاو سیاه باشد، ولی شیرش سفید است.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(8005,'قمار باز شریک دزد است.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(8006,'نبودن آزادی از زیادی اش بهتر است.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(8007,'دارایی هایی که در قلب جای دارند، هرگز دزدیده نمی شوند.((مثل روسی))',124,0,0,0,0,0);
INSERT INTO mysms VALUES(8008,'زنها مانند کوههای خوش منظره ای هستند که باید از دور تماشایشان کرد و از مناظرشان لذت برد.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8009,'وقتی از اخلاق یک فرد سر در نمی آوری به دوستانش نگاه کن.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8010,'اگر به پسرت ماهی بدهی، برای یک روز، او را سیر می‌کنی، ولی اگر ماهی‌گیری را به او بیاموزی، او را برای همیشه سیر خواهی کرد.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8011,'هفت بار زمین بخور، هشت بار بلند شو.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8012,'در عشق، سکوت بهتر از نطق و بیان، ادای مقصود می کند.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8013,'هفت بار که افتادی، برای هشتمین بار برخیز.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8014,'از این نترس که آهسته حرکت می کنی، از این بترس که هنوز حرکت نکرده ای.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8015,'دیدار، آغاز جدایی است.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8016,'باید حریف را به کمک حریف دیگری به دام انداخت.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8017,'افعی ترسناک را دسته ای مورچه از پای درمی آورند.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8018,'سکوت هرگز اشتباه نمی کند.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8019,'آرمان بدون عمل گمانی باطل و عمل بدون آرمان کابوس است.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8020,'راهی که طول آن هزار کیلومتر باشد، قدم به قدم پیموده می شود.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8021,'کسی که به پشتکار خود اعتماد دارد، ارزشی برای شانس قایل نیست.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8022,'یک کلمه محبت آمیز می تواند در تمام طول زمستان انسان را گرم کند.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8023,'در جریان آب می توان بی حرکت ماند، اما در دنیای مردان هرگز.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8024,'از پیروزی، کمتر می توان آموخت و از شکست، بسیار.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8025,'دانش بدون خرد، تنها همچون کشیدن انبوهی کتاب بر پشت است.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8026,'هرچه آهسته تر بروی زودتر می رسی.((مثل ژاپنی))',125,0,0,0,0,0);
INSERT INTO mysms VALUES(8027,'کسی که به امید شانس زنده باشد، سالها قبل مرده است.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8028,'کسی که برای کامروا شدن به امید شانس نشسته باشد، سالها پیش مرده است.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8029,'اگر یک مقصر، بیگناه دانسته شود بهتر از این است که یک بیگناه، محکوم گردد.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8030,'سگ بیدار بهتر از شیر خفته است.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8031,'مردی که برای «پول» زن می گیرد به نوکری می رود.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8032,'اگر [ تا به حال ] داماد نشده ای، از یک شب شادمانی و عمری بداخلاقی محروم گشته ای.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8033,'بمیر، اما گدا مباش.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8034,'عادل باش، گرچه گدا باشی.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8035,'کسی که از احمق تعریف کند، احمق تر از اوست.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8036,'کسی که پند ارزان را رد کند، طولی نمی کشد که پشیمانی را با بهای زیادی خریداری خواهد کرد.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8037,'زن لال هرگز از شوهرش کتک نخورده است.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8038,'تو نان می خوری و زنده هستی و من نان می دهم و زندگی می کنم.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8039,'انسان در هر کار می بایست بداند کجا باید ترمز کرد.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8040,'هر کسی نگهبان شرافت خویش است.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8041,'کسی که فقط با کمک چشم دیگران می بیند فریب می خورد.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8042,'ازدواج، زودش اشتباهی بزرگ و دیرش اشتباه بزرگتری است.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8043,'در همه چیز، همه چیز هست.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8044,'در حال رقص هم می توان به جلو رفت.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8045,'رقص ِآدم ِپیر خیلی گَرد و خاک به پا می کند.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8046,'کسی خوب می رقصد که [ انسان ] نیک بختی برایش آواز بخواند.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8047,'خانه ات را برای ترساندن موش آتش مزن.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8048,'کسی که کم فکر می کند، زیاد حرف می زند.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8049,'کسی که وجدانش پاک باشد، زود خوابش می برد.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8050,'در مهمانی آدم خسیس همه می خورند و در دل به میزبان ناسزا می گویند.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8051,'بسیاری از افراد در نتیجه زیاده روی در خوردن داروها می میرند، نه در نتیجه بیماری هایشان.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8052,'نبوغ یعنی حوصله.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8053,'اگر به میان گرگها روی، از آنها زوزه کشیدن خواهی آموخت.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8054,'خوشبختی از قمار باز فرار می کند. ((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8055,'سخن ملایم، به زبان آسیب نمی رساند.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8056,'نیک نامی از کمربند زرین بهتر است.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8057,'کسی که توان تصور دارد، ولی آموزش ندیده، بال دارد، ولی پر ندارد.((مثل فرانسوی))',126,0,0,0,0,0);
INSERT INTO mysms VALUES(8058,'اگر کسی را تحقیر کردی، معنایش این است که خودت بی شخصیت هستی.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8059,'جایی که عشق حکمفرمایی می‌کند، غیر ممکن‌ها ممکن می‌شود.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8060,'عاقل همان کاری را ابتدا انجام می دهد که نادان در آخر.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8061,'مانند خردمندان بنویس و مانند توده ی مردم حرف بزن.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8062,'سکوت هرگز اشتباه نمی کند و هر چه طولانی تر باشد، بهتر قضاوت می کند.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8063,'زمین به کسی که او را از آن خودش می دانست، خندید.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8064,'حتی اگر کسی آن‌ اندازه فرمانبردار شماست که از دست‌تان غذا می‌خورد، باز خردمندانه این است که مراقب انگشتان خود باشید.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8065,'اگر وجدان پاکی داری، پس چرا غصه می خوری؟ چرا که چیزی کم نداری.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8066,'هر کس برای یک و یا دو روز میهمان به شمار می رود و روز سوم، فضول و مزاحم.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8067,'برای اینکه بزرگ باشی، اول کوچک باش.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8068,'آخرین عضوی که در وجود یک زن می میرد، زبان اوست.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8069,'اندوه، نخست مغز را از بین می برد و سپس جسم را.((مثل هندی))',127,0,0,0,0,0);
INSERT INTO mysms VALUES(8070,'ماده‌ی سگ خانه مانند بانوی آن خانه رفتار می‌کند.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8071,'همه چیز زاده ی نابخردی است.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8072,'زمان درباره همه چیز قضاوت می کند.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8073,'آتش را نمی توان با آتش خاموش کرد.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8074,'هر کس می تواند خودکشی کند، اما هر فردی لیاقت ندارد زندگی کند.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8075,'اگر نمی توانی دوست پیدا کنی، برای خود دشمن نتراش.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8076,'اگر پول در جیب نداری، عسل دردهان داشته باش.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8077,'بهترین چیزها سخت ترین آنها است.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8078,'همه کس، قوم و خویش آدم ثروتمند است.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8079,'بدی اشخاص احمق، برابر با نیکی اشخاص عاقل است.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8080,'شگفتی، آغاز خردمندی است.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8081,'اگر می خواهید قدرتمند شوید، وانمود کنید که قدرتمند هستید.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8082,'آدم عاقل فرصت نیک را به بخت نیک تبدیل می سازد.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8083,'هرکس هر چقدر هم که ناتوان باشد، دست کم می تواند بر خودش حکومت کند.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8084,'هرگز درباره هیچ کس قضاوت نکنید، مگر آنکه خود را به جای او بگذارید.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8085,'هر مردی می تواند داماد شود، اما هر دامادی نمی تواند مرد باشد.((مثل یونانی))',128,0,0,0,0,0);
INSERT INTO mysms VALUES(8086,'از هر گره که به ابرو می زنیم، صد گره به کارمان می خورد.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8087,'غالباً از نادانی وحشتی نداریم، از اینکه ما را نادان بدانند می ترسیم.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8088,'سکوت بر دانا و نادان هر دو زیبنده است؛ عیب نادان را می پوشاند و دانا را فرزانه تر جلوه می دهد.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8089,'آن اندازه که ما خود را فریب می دهیم و گمراه می کنیم، هیچ دشمنی نمی تواند.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8090,'اگر کسی در انتخاب همسرش دقت نکند، دو نفر را بدبخت کرده است.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8091,'گذشته، سرمایه ای است که اندوخته ایم؛ می توان آن را به تأسف و اندوه صرف کرد یا برای موفقیت آینده به کار برد.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8092,'اگر آنچه از گذشته تجربه آموخته ایم در آینده به کار بریم، مثل این است که دو بار عمر کرده باشیم.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8093,'برای درست اندیشیدن باید نخست دل را از کینه و بدی پاک کرد.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8094,'در گرفتاری باید اندیشه را به جنب و جوش در آورد نه اعصاب را.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8095,'آن که خیال می کند شاید فردا گرفتار شود، از اینکه الان گرفتار است بیشتر رنج می برد.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8096,'جهان هر کس به اندازه وسعت فکر اوست.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8097,'اگر روزگار یک موجب حلال فراهم می کند، ما خود به دست خیال، ده موجب دیگر می سازیم.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8098,'داروی تمام دردها نیکی است؛ به شرط آنکه ندانند شما نیک هستید، وگرنه نمی گذارند نیک بمانید.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8099,'شادی نعمت همیشه هماهنگ با رنجی است که برده ایم.((محمد حجازی))',129,0,0,0,0,0);
INSERT INTO mysms VALUES(8100,'انسان مانند رودخانه است؛ هر چه ژرفتر باشد، آرامتر است.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8101,'زمانی می رسد که سکوت، بیش از همه گفته ها مقصود را می رساند.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8102,'گاهی سکوت بیش از هر استدلالی به ما کمک می کند.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8103,'اوج کارآمدی مدیریت این است که فرد بداند به چه میزان و در کجا باید از قدرت خود بهره گیرد.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8104,'آزادی عبارت است از حق انجام هرکاری که قوانین اجازه می دهد و انجام ندادن آنچه قانون منع کرده است.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8105,'اگر قدرت قضاوت از قدرت قانون گذاری و اجرایی جدا نباشد، باز هم از آزادی نشانی نخواهد بود.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8106,'تنها قدرت است که می تواند جلوی قدرت را بگیرد.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8107,'برای آنکه نتوان از قدرت سوء استفاده کرد، باید دستگاهها طوری تنظیم شوند که قدرت، قدرت را متوقف کند.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8108,'پریشان نمودن اوضاع مستلزم هوشمندی نیست، اما ایجاد نظم و تعادل به نیروی عقل وابسته است.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8109,'جمهوری ها در نتیجه تجمل و پادشاهی ها در اثر فقر سقوط می کنند.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8110,'عاشقان دلباخته معمولأ در سکوت به سر می برند.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8111,'من مخالف مراسم پرخرج خاکسپاری هستم. برای انسان باید هنگام تولد عزاداری کرد، نه وقتی که از این دنیا می رود.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8112,'باید زیاد مطالعه کنید تا بدانید که هیچ نمی دانید.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8113,'در میان کشورهای استبدادی، بینواتر از همه آن کشوری است که پادشاهش تاجر باشد.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8114,'سکوت، گاه هزار معنی در بر دارد که از گفتن به دست نمی آید.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8115,'کسی اجازه ندارد خود را بکشد و اگر کشت، وجود خود را از میهن خود دزدیده است.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8116,'کسی که بدون فکر کردن حرف می زند، صیادی را می ماند که بدون نشانه گرفتن، تیر خالی می کند.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8117,'کشور در صورتی دارای آزادی و استقلال است که افرادش آزاد و مستقل باشند.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8118,'من هیچ غمی نداشته ام که خواندن یک صفحه کتاب آن را از بین نبرده باشد.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8119,'همیشه قضایا را پیش از تصمیم گیری به محکمه وجدان تسلیم کنید. وجدان راهنمای بی نظیر و قاضی بیطرفی است.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8120,'یک ساعت از عمر گذشته را به هیچ بهایی نمی توان بازگردانید، اما با دادن چند تکه پول ناچیز می توان تجربه های تمام عمر بزرگترین خردمندان جهان را به دست آورد.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8121,'شکست خوردن به حق، شیرین تر از پیروزی به باطل است.((منتسکیو))',130,0,0,0,0,0);
INSERT INTO mysms VALUES(8122,'کسی که از سرنوشت خود شکایت کند، از حماقت و ناچیزی روح خویش شکایت کرده است.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8123,'تمام بدبختی ها و عذابهایی که خدایتان برای شما می فرستد، از سوی خود شما فرستاده می شود.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8124,'عظمت خدای هر کس به اندازه بزرگی مغز اوست و به بیان دیگر، بزرگی خداوند به قدر شایستگی ماست.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8125,'هیچ کس نمی تواند خدا را برای دیگران توصیف کند. زیرا به محض اینکه توانست خدای خویش را وصف کند، دیگر او خدا نبوده، بلکه مثل من و شماست.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8126,'هرکس ارزش پیروزی را نداند، به شکست خواهد رسید.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8127,'اگر در تمام دنیا یک ذره بی مقدار که خود، جهان دیگری است، از قوانین گیتی پیروی نمی کرد، مدتهای مدیدی بود که جهان ما دیگر این جهان نبود.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8128,'برای چه توقع دارید که خیالات، تصورات، احساسات، اقدامات و به طور خلاصه زندگی ما آزاد باشد و حال آنکه به چشم خود می بینیم کهکشانهای بزرگ که منظومه خورشیدی ما در برابر آنها هیچ است، در حرکات خود آزاد نیستند و مجبور شده اند که خط سیر ویژه ای را پیموده و از قوانین خاصی پیروی کنند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8129,'مهد پرورش خرد، آغوش عقل نیست، بلکه دامان عشق است. نهال خرد باید در کوزه عشق کاشته شود و با آب احساسات آبیاری گردد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8130,'تمام سرمایه فکری و دانش باید تسلیم یک عظمت اخلاقی و روحی گردد، وگرنه دانش مانند رودی خواهد بود که نتوانسته خط سیر خود را بپیماید و به دریا بریزد و با وضعی اندوه بار در صحرا و ریگزارها فرو می رود.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8131,'فداکاری در دو مورد خوب است : اول اینکه تنها راه باقی مانده باشد و از هیچ راه دیگری غیر از جانفشانی نتوان به هدف مورد نظر دست یافت. دوم آنکه بدانیم دیگران با این فداکاری خوشبخت خواهند شد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8132,'آیا به یاد دارید که در دوره حیات از دست شخصیت خودتان و آنچه که نامش را «من» می گذارید، چقدر آسیب دیده اید؟ تمام اوقات تلخی ها، رنجها، زیانها، بدبختی ها و ناکامی های شما در طول مدت حیات از دست همین «من» بوده است.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8133,'هیچ چیز بهتر از کار کردن به جای غصه خوردن، آدمی را به خوشبختی نزدیک نمی کند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8134,'چون مردم کمتر متوجه سعادت هستند، بدبختی ها بیشتر نمایان است. [ آنها ] ستم و ستمگری را عادی فرض می کنند و سعادت و عدالت را جزء اتفاقات می پندارند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8135,'خوشبختی بر سه ستون استوار است : فراموش کردن گذشته، غنیمت شمردن حال و امیدوار بودن به آینده.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8136,'غنچه خوشبختی در جای تاریک، بی صدا و گودی پنهان شده که بسیار نزدیک به ماست، ولی کمتر از آنجا می گذریم و آن، دل خود ماست.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8137,'روزی که بشر کاملاً به نادانی خود پی برد، به موفقیت بزرگی دست یافته است؛ زیرا دانش در این دنیا چیزی جز پی بردن به نادانی نیست.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8138,'وقتی انسان به نادرستی یک نظریه پی برد و آنرا حفظ کرد، دلیل بر حماقت اوست.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8139,'عدالت انتظار پاداش ندارد، بلکه خود عمل دارای زیبایی و سرور است.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8140,'در روی زمین سعادتی بالاتر از یک عشق پاک و بادوام متصور نیست.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8141,'هیچ موجودی در دنیا وجود ندارد که در اثر عشق ورزیدن به یک موجود دیگر، حتی اگر یک عشق پیش پا افتاده و پست هم باشد، اصلاحی در روح خود به عمل نیاورد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8142,'وقتی عشقی در کار است، پای عقل می لنگد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8143,'هیچ چیز به زیبایی یک قفل بسته نیست؛ البته تا زمانی که کسی نداند چگونه می توان آن را باز کرد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8144,'نباید آنقدر در اندیشه فرو رویم که قدرت همه چیز از ما سلب شود؛ چرا که اگر هدف، سلامت اندیشه های ما باشد باید بدانیم که بیشتر اندیشه های نادرست و غلط، در عمل، جبران و اصلاح خواهد شد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8145,'تمام چیزهایی که نوابغ آینده خواهند گفت، هم اکنون در وجود من و شما هست؛ منتها باید آنها را پیدا کرد و روی آنها انگشت گذاشت.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8146,'زندگان، مردگانی هستند که در دوران آسایش به سر می برند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8147,'ما انسانها این گونه آفریده شده ایم که اگر بدانیم میلیونها سال در جهان باقی خواهیم ماند، اما قرین اندوه و بدبختی خواهیم بود، راضی شده و حتی شادمان می گردیم. اما اگر بفهمیم که از بین می رویم و در عوض هیچ شکنجه و رنج نخواهیم کشید اندوهگین و مایوس می شویم !!!.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8148,'هنگام نیکبختی است که باید بیمناک بود؛ هیچ چیز تهدید آمیزتر از سعادت نیست.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8149,'تنهایی جهنم است، اما این جهنم با مطالعه تبدیل به بهشت می شود.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8150,'آینده ها بزرگ به نظر می رسند، ولی وقتی که گذشتند، می فهمیم که ناچیز بوده اند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8151,'اگر تصمیمی داشته باشید، به زودی موفق خواهید شد؛ زیرا آدمی ساخته افکار خویش است و فردا همان خواهد شد که امروز [ به آن ] می اندیشیده است.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8152,'فکر شما هر چه باشد، شما همان هستید و محال است که بتوانید از فکر خود بزرگتر شوید و یا از حدود فکر خویش تجاوز نمایید.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8153,'سرنوشت بشر این است که در تمام عمر زحمت بکشد تا بتواند به زندگی ادامه بدهد و به زندگی ادامه بدهد تا بتواند بمیرد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8154,'آن کس که اراده و استقامت دارد، روی شکست را نمی بیند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8155,'ما نادان هستیم و شاید تا ابد نادان بمانیم، اما بتدریج چیزهایی می آموزیم که به ما نشان می دهد به درستی نادانیم.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8156,'آنچه انسان هرگز نخواهد فهمید این است که چگونه در برابر کسی که ما را آفریده و همه چیز ما از اوست، مسئول خواهیم شد و او از ما بازخواست خواهد کرد ؟!؟!؟((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8157,'اگر در اولین قدم، موفقیت نصیب ما می شد، سعی و عمل دیگر مفهومی نداشت.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8158,'روح کندوست که به زنبور عسل وحی می کند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8159,'ما هیچ دلیلی در دست نداریم که بتوانیم وجود فهم و شعور را در سنگ و درخت و غیره منکر بشویم و در این صورت بعید نیست دنیاهایی وجود داشته باشد که در آن، سنگ ها و سایر جمادات مثل ما زندگی کنند و بلکه زندگی فکری آنها از ما بالاتر باشد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8160,'ارزش اخلاقی بسته به تعداد وظایفی است که انسان انجام می دهد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8161,'آنچه ما به آن مرگ می گوییم، یک زندگی است که هنوز نتوانسته ایم چگونگی آن را درک کنیم.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8162,'با اینکه دوره عمر ما کوتاه است، دو سوم عمر کوتاه ما در این می گذرد که از کندی مرور زمان ناراضی هستیم و شب و روز در نظرمان بلند جلوه می کند و همواره می خواهیم امروز بگذرد و فردا بیاید. گویی به مرگ وعده داده ایم که تا حد امکان زودتر به ملاقات آن برویم.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8163,'بدون مرگ، زندگی اهمیت و ارزشی نداشت. زیرا ترس از مرگ است که زندگی را در نظر ما اینقدر لذت بخش و خواستنی جلوه می دهد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8164,'به نظر من اگر مرگ در دنیا نبود، بشر به آن محتاج بود و می بایست آن را خلق کند تا از چنگال کسالتهای زندگی رهایی یابد. در حقیقت بسیاری از ما پیش از مردن، مرده هستیم؛ برای اینکه همه چیز خود را از دست داده ایم.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8165,'تمام اشتباههای ما درباره مرگ ناشی از این است که فکر می کنیم درد و رنجی که ما قبل از مردن می کشیم مربوط به مرگ است. در صورتی که چنین نیست و تمام درد و رنجها مربوط به زندگی است. زندگی موجب درد و رنج می شود و مرگ، پایان دردهاست.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8166,'زندگان، مردگانی هستند که ایام رهایی شان را در این دنیا می گذرانند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8167,'آدم تنها اگر در بهشت هم باشد به او خوش نمی گذرد. اما کسی که به کتاب یا تحقیق علاقه مند است، هنگامی که به مطالعه یا اندیشه درباره محتوای آن کتاب مشغول است، جهنم تنهایی برای او بهترین بهشت هاست.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8168,'محبت گوهر گرانبهایی است که در برخی از قلبها روی آن خاکستر نشسته است. این پوشش نازک و کوتاه را با تبسم بزدایید تا گوهر گرانبهایی که پنهان است، آشکار شود و با درخشش خود، روح و قلب همگان را روشن سازد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8169,'آنچه باعث آزار ما می شود، زندگی است. یک بیمار وقتی بمیرد، تمام دردهایش از بین می رود.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8170,'وقتی به شعله شمع می دمید و شمع را خاموش می کنید، آن شعله کجا می رود؟ خواهید گفت نیست می شود، ولی چنین چیزی محال است؛ در دنیا یعنی در هستی، هیچ چیز «نیست» نخواهد شد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8171,'خیلی دشوار است که انسان [ به این نکته ] پی ببرد که جهان را برای او نیافریده اند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8172,'ما باید خیلی خودپسند باشیم اگر تصور کنیم که برجسته ترین موجودات دنیا هستیم. در این دنیا نزدیک به صد میلیارد کهکشان وجود دارد که هر یک از آنها چندین میلیون خورشید دارند که شبیه به ما یا بهتر هستند و هر روز تعدادی از آنها با هم برخورد می کنند و در هیچ جای دنیا کسی برایشان غصه نمی خورد و مجلس ترحیم و یادبود برپا نمی کند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8173,'مرگ، پزشک بازپسینی است که هنگام درماندگی پزشکان از درمان، به سراغ بیمار آمده و او را بهبودی ابدی می‌بخشد.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8174,'اگر مرگ نبود، زندگی، زندگی نبود.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8175,'به یاد داشته باشید که شادمانی به اندازه ی غم و غصه واگیردار است. نخستین وظیفه ی افراد شاد این است که دیگران را از شادمانی خود آگاه سازند.((موریس مترلینگ))',131,0,0,0,0,0);
INSERT INTO mysms VALUES(8176,'بزرگترین خطری که بسیاری از ما با آن روبرو هستیم، پایین و دست یافتنی بودن اهداف‌مان است و نه بزرگ بودن و دست نایافتنی بودن آنها.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8177,'بلندنظر باشید و هدف‌های بزرگ تعیین کنید.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8178,'خداوندا بگذار همیشه خواهان انجام آنی باشم که بیش از توان من است.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8179,'بلند نظر باشید و هدفهای بزرگ در پیش گیرید.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8180,'چه غصه هایی برای رویدادهای بدی که هرگز در زندگیم پیش نیامد خوردم.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8181,'صلح ناحق بهتر از جنگ بر حق است.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8182,'عشق، آدمی را به کمال می رساند.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8183,'خدایا! به من آن ده که همیشه شوقی بیش از حد توان خود برای کار کردن داشته باشم.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8184,'بهترین دستور زندگی این است که اعتماد به نفس داشته باشید.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8185,'بزرگترین خطری که ما را تهدید می کند پایین بودن و در دسترس بودن اهدافمان است.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8186,'همه افراد بشر استعداد نقاشی دارند، اما همه حوصله ندارند.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8187,'تمام وعده ها و نویدهای دنیا فریبی بیش نیست و بهترین دستور زندگی این است که اعتماد به نفس داشته باشی و در پرتو سعی و تلاش خود به مقامی برسی.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8188,'اگر مردم می دانستند برای احراز مقام استادی چه رنجها برده ام و چه روزها و شبها جان کنده ام، هرگز از دیدن شگفتی های هنریم متعجب نمی شدند.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8189,'چیزهای کوچک، کمال ایجاد می کند و کمال هم چیز کوچکی نیست.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8190,'هرچه دانش بیشتر و تجربه زیادتر شود، نیاز به پند گرفتن کمتر می‌شود.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8191,'آن که به هوش سرشار خویش ارزش نمی‌نهد و در بند آن است که با گرایش‌های کوته‌فکران سازواری [= سازگاری] داشته باشد، نزد من رتبه و جایگاهی ندارد.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8192,'انسان، با مغز نقاشی می‌کند نه با دست. هر که اندیشه‌هایش گرفتار مسایل دیگر باشد، کارش رسوایی به بار می‌آورد.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8193,'دیدگاه هر نویسنده را از کتاب او و دیدگاه هر هنرمند را از هنرش می‌توان دریافت.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8194,'آسانترین راه رسیدن به آینده، نیندیشیدن به آن است.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8195,'هر کنشی، هرچند هم دشوار، با خونسردی و آرامش، آسانتر به نظر می‌رسد.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8196,'زیبایی، هدیه ای از بهشت است.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8197,'زیبایی، در روان و هوش آدمی تأثیر می‌گذارد و احساس لذت و همت به وجود می‌آورد.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8198,'عشق، سبب رسیدن به خداست.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8199,'مهم‌ترین شرط ازدواج با یک دختر، پاکی نفس و شرافت ذاتی او است.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8200,'ارزش ندارد که آدمی برای مبارزه کردن با کوته‌فکران، خود را آزرده سازد؛ زیرا پیروزی بر آنها هیچ ارزشی ندارد.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8201,'تا زمانی که ننگ و جنایت دوام دارد، هیچ ندیدن و نشنیدن برای من پایان خوشبختی‌ است.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8202,'کار هنری راستین، سایه‌ای از کمال ملکوت است.((میکل آنژ))',132,0,0,0,0,0);
INSERT INTO mysms VALUES(8203,'رمان، عرصه ای است که تخیل نیز در آن، مانند رویا، می تواند فوران کند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8204,'اگر رمان به راستی باید از میان برود، به این دلیل نیست که نیروی اش تمام شده است، بلکه بدان علت است که رمان در جهانی به سر می برد که دیگران از آن او نیست.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8205,'بزرگترین عشق، سرانجام به مجموعه ای از یادگارهای بی رونق کاهش می یابد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8206,'تاریخ نگار، تاریخ جامعه را می نویسد نه تاریخ انسان را.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8207,'هرگز نمی توان عملی را که یک بار از دست ما خارج شده است، دوباره در دست گرفت.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8208,'هستی، آنچه روی داده است نیست؛ هستی، عرصه ی امکانات بشری است؛ هر آنچه انسان بتواند آن شود، هر آنچه انسان بتواند واقعیت بخشد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8209,'لباس نظامی آن چیزی است که ما برنمی گزینیم، بلکه برای ما تعیین می شود و این همانا ثبات کل در برابر بی ثباتی فرد است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8210,'تیره بخت ترین افراد همیشه نقش دژخیم [ =بدخوی ] را در فراشد تنزل ارزشها به عهده می گیرند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8211,'اگر از دست دادن عشقی با دلیل باشد، ما تسلیم می شویم. اما اگر عشقی را بدون دلیل از دست بدهیم، هرگز خود را نخواهیم بخشود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8212,'همه ی آثار بزرگ ( و درست از آن روی که بزرگ اند ) چیزی از انجام نایافته را در بردارند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8213,'در زمان تقسیم بیش از حد کار و تخصص گرایی لگام گسیخته [ =بی بند و بار ]، رمان یکی از آخرین مواضعی است که در آن انسان هنوز می تواند رابطه با زندگی را در مجموعه اش حفظ کند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8214,'هرکس، اعم از سیاستمدار، فیلسوف و دربان، به درستی سخن خود باور دارد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8215,'رمان اندیشه ای درباره ی وجود است، وجودی که از بین شخصیت های تخیلی دیده می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8216,'قدرت در هر جا که خود را خداگونه بنمایاند، خودبخود الهیات خاص خویش را تولید می کند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8217,'در جهان دیوان سالارانه ی کارمند، اثری از ابتکار، نوآوری و آزادی عمل در میان نیست، تنها چیزی که وجود دارد دستورها و مقررات است و این همانا دنیای فرمانبرداری است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8218,'کارمند، بخش کوچکی از کار عظیم اداری را که از فهم هدف و افق آن به دور است، انجام می دهد؛ این جهانی است که حرکات در آن مکانیکی می شوند و اشخاص معنای آنچه را که انجام می دهند، نمی دانند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8219,'نوشتن برای شاعر به معنای از بین بردن دیوار نازکی است که در پس آن چیزی تغییر ناپذیر («شعر») در تاریکی پنهان است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8220,'هر وضعی ساخته ی انسان است و نمی تواند در بردارنده ی چیزی به جز آنچه در انسان هست، باشد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8221,'شاعر فقط امکانی بشری را «کشف» می کند؛ امکانی که تاریخ هم به نوبه ی خود روزی آن را کشف خواهد کرد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8222,'رمان همه ی آن جنبه های وجود را که کشف می کند، همچون زیبایی کشف می کند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8223,'زیبایی، آخرین پیروزی انسانی است که دیگر امیدی ندارد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8224,'زیبایی در هنر، نوری است که به ناگاه از آنچه هرگز گفته نشده است، می تابد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8225,'تاریخ نمی آفریند، بلکه «کشف» می کند. تاریخ، از طریق موقعیتهای بی سابقه، پرده از آنچه انسان هست، پرده از آنچه «از دیر زمان» در انسان وجود دارد، پرده از آنچه امکانهای انسان را تشکیل می دهد، بر می دارد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8226,'نویسنده ای که می کوشد بر ترجمه ی رمانهایش نظارت کند، همچون چوپانی که در پی گله ای از گوسفندان ناآرام است، به دنبال کلمه های بی شماری می گردد؛ حالت این نویسنده برای خودش غم انگیز است و برای دیگران خنده آور.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8227,'یک حالت آغازین موسیقی وجود دارد، حالتی که پیش از تاریخ موسیقی وجود داشته است؛ حالت پیش از نخستین پرسش، پیش از نخستین اندیشه، پیش از نخستین بازی با دورنمایه یا مضمون.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8228,'هنگامی که سخن بر سر پی بردن به مفهوم و ارزش یک اثر هنری است، زمینه های ملی و منطقه ای به هیچ کار نمی آیند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8229,'زمانه ی آکنده از انبوه ایده ها و بی اعتنا نسبت به آثار [ ادبی و هنری ] ، مرا غرق در نومیدی می سازد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8230,'گرایش به آشتی دادن ماجرای عشق شهوانی با عشق پاک، عین ذات لذت پرستی است و دلیل ناممکن بودن این آشتی است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8231,'از کودکی بیرون می آییم، بی آنکه بدانیم جوانی چیست، ازدواج می کنیم، بی آنکه بدانیم متاهل بودن چیست، و حتی زمانی که قدم به دوره پیری می گذاریم، نمی دانیم به کجا می رویم: سالخوردگان، کودکان معصوم کهنسالی خویش اند. از این جهت، سرزمین انسان سیاره ی بی تجربگی است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8232,'رمان هنری طنز آمیز است؛ «حقیقت» رمان نهفته، ادا نشده و ادا نشدنی است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8233,'طنز، آدمی را برآشفته می کند، نه برای آنکه آدمی را مسخره می کند یا بر او می تازد، بلکه از آن روی که با آشکار ساختن جهان همچون پدیده ای دو گانه، ما را از داشتن یقین ها باز می دارد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8234,'بیهوده است اگر بخواهیم رمانی را با زینت دادن سبک، «دشوار » کنیم، هر رمانی که شایستگی این کلمه را دارد، هر چقدر روشن و شفاف باشد، بر اثر طنز ذاتی خود، به اندازه ی کافی دشوار است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8235,'زشتيِ همه جا گسترده ی دنیای جدید که بر اثر عادت از دیده پوشیده می ماند، در کوچکترین لحظات تیره بختی ما بیرحمانه ظاهر می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8236,'حماسه از تمایل بیش از حد به چیرگی بر عینیت جهان، ناشی می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8237,'تغزل گرایی نوعی سرمستی است و انسان برای اینکه آسان تر با جهان یکی شود، سرمست می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8238,'کمال مطلوب مرد زن گریز، تجرد با داشتن معشوقه های بسیار است، یا ازدواج با زن محبوب بدون کودک.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8239,'آن کس متجدد [ =نوگردیده ] است که خود را متجدد بنامد و همچون متجدد پذیرفته شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8240,'پرسش بنیادی برای هر هنرمند عبارت از این است که اثر «با ارزش» او با کدام کار آغاز می شود؟((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8241,'انسان همواره بر این بوده است که زندگینامه ی خاص خود را باز نویسد، گذشته را تغییر دهد و هم ردپای خودش و هم ردپای دیگران را پاک کند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8242,'طلب فراموشی کردن را نمی توان با وسوسه ی ساده به نیرنگ زدن، یکی پنداشت.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8243,'فراموشی، در عین حال بی عدالتی مطلق و آسایش مطلق است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8244,'مترجمان دیوانه وار لغات مترادف را دوست می دارند (من، مفهوم مترادف را به کلی نفی می کنم: هر کلمه معنای خاص خود را دارد و از لحاظ معنا، جایگزین ناپذیر است).((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8245,'رمان نویس اهمیت زیادی برای ایده های خود قایل نیست. او یک کاشف است که کورمالانه می کوشد تا جنبه ی ناشناخته ای از وجود را آشکار کند. رمان نویس مسحور [ =شیفته] رای و آوای خود نیست، بلکه مسحور شکلی است که دنبال می کند و تنها شکل هایی که به خواست های رویایش پاسخ می دهد، جزیی از اثر او هستند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8246,'انسان تنها هنگامی که سالخورده است می تواند باورهای جماعت، افکار عمومی و آینده را نادیده گیرد. انسان سالخورده با مرگ قریب الوقوع خود تنها است و مرگ نه چشم دارد و نه گوش. انسان سالخورده نیازی ندارد که خوشایند مرگ باشد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8247,'رمان نویس سخنگوی هیچ کس نیست ... او حتی سخنگوی افکار خاص خودش هم نیست.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8248,'رمان نویسانی که هوشمندتر از آثارشان اند، باید حرفه ی خود را تغییر دهند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8249,'انسان می اندیشد و به حقیقت پی نمی برد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8250,'هر چه انسانها بیشتر می اندیشند، اندیشه ی این یک از اندیشه ی آن دیگری دورتر می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8251,'انسان هرگز آن چیزی نیست که می اندیشد هست.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8252,'انسان آرزومند جهانی است که در آن خیر و شر آشکارا تشخیص دادنی باشند، زیرا در او تمایل ذاتی و سرکش داوری کردن پیش از فهمیدن، وجود دارد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8253,'زمان در ترنی که تاربخش می نامند، سوار شده است؛ سوار شدن در این ترن آسان است اما پیاده شدن از آن دشوار.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8254,'روح رمان، روح پیچیدگی است. هر رمان به خواننده می گوید: «چیزها پیچیده تر از آن اند که تو می اندیشی.»((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8255,'آینده همیشه نیرومندتر از اکنون است. در حقیقت، این آینده است که درباره ی ما به داوری خواهد نشست و بی شک بدون هیچ گونه شایستگی.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8256,'با عمل است که انسان از دنیای تکراری روزانه - جایی که همه شبیه یکدیگرند - بیرون می آید، با عمل است که انسان خود را از دیگران متمایز می کند و فرد می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8257,'هر لحظه ای نمایانگر جهانی کوچک است که ناگزیر در لحظه ی بعدی فراموش می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8258,'مهربانی به معنای ایجاد فضایی مصنوعی است که در آن با دیگری، باید همچون کودک رفتار شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8259,'کشفهای رمان نویسان - هر اندازه هم قدیمی باشد - هرگز از به حیرت افکندن ما باز نتواند ایستاد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8260,'نوابغ حقیقی قلمرو کمدی، کسانی نیستند که ما را بیشتر می خندانند. بلکه کسانی هستند که «حوزه ی ناشناخته ی عنصر کمدی» را باز می نمایند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8261,'رمان غالبا چیزی نیست مگر پیاپی شدن درازمدت چند تعریف فرار.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8262,'سرنوشت همچون گلوله ی آهنینی است که بر مچ پای ما بسته شده باشد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8263,'در سلسله مراتب هنرها، این موسیقی است که جای نخست را می گیرد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8264,'آدمی به ضعف خویش آگاهی دارد و نمی خواهد در برابرش مقاومت ورزد، بلکه خود را به آن تسلیم می کند. آدمی خود را از ضعف خویش سرمست می کند، می خواهد هر چه ضعیف تر شود، می خواهد در وسط خیابان جلوی چشمان همگان در هم فرو ریزد، می خواهد بر زمین بیفتد و از زمین پایین تر برود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8265,'انسان و جهان همچون حلزون و صدف اش به یکدیگر پیوسته اند: جهان و انسان تفکیک ناپذیرند، جهان گستره ی انسان است و بتدریج که جهان تغییر می کند، هستی نیز تغییر می یابد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8266,'برای همه ی ما تصورناپذیر است که یگانه عشقمان چیزی سبک و سست باشد، چیزی بدون وزن؛ می پنداریم عشق ما آن چیزی است که ناگزیر باید باشد، که بدون آن زندگی ما از دست رفته است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8267,'آیا یک رویداد هر چه بیشتر اتفاقی باشد، مهمتر و پرمعناتر نیست؟((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8268,'آنچه بر حسب ضرورت روی می دهد، آنچه که انتظارش می رود و روزانه تکرار می شود، چیزی ساکت و خاموش است؛ تنها اتفاق سخنگو است و همه می کوشند آن را تعبیر و تفسیر کنند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8269,'زندگی بشر همچون یک قطعه ی موسیقی ساخته شده است. انسان با پیروی از درک زیبایی، رویداد اتفاقی را پس و پیش می کند تا از آن دورنمایه ای برای قطعه ی موسیقی زندگیش بیابد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8270,'دورنمایه ی فراموش نشدنی وابسته به تولد عشق با زیبایی اضطراب آورش درست در لحظه ی ناامیدی انسان را به سوی خود می کشد. انسان همیشه ندانسته، حتی در ژرف ترین لحظه های پریشانی، زندگیش را بر مبنای قوانین زیبایی می سازد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8271,'براستی، [ می بایست ] انسان را سرزنش کرد که طی زندگی روزمره در برابر [ برخی از ] رویدادها بی اعتنا است و بدین ترتیب، بعد زیبایی را از زندگی خود سلب می کند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8272,'آنچه فرد تحصیلکرده را از فرد خودآموخته مشخص می سازد، وسعت دانش نیست، بلکه مراتب گوناگون نیروی حیات و اعتماد به نفس است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8273,'رؤیا تنها یک ارتباط ( شاید یک ارتباط رمزی ) نیست، بلکه یک کوشش زیبایی شناسی و یک بازی قوه ی تخیل است و این بازی به خودی خود دارای ارزش است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8274,'تخیل، یکی از ژرفترین نیازهای بشری است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8275,'آدمی به ضعف خویش آگاهی دارد و نمی خواهد در برابر آن ایستادگی نماید، بلکه خود را به آن تسلیم می کند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8276,'وقتی مردم هنوز کم و بیش جوانند و آهنگ های موسیقی زندگی شان در حال تکوین است می توانند آن را با کمک یکدیگر بسازند و مایه ها را رد و بدل کنند، اما وقتی در سن کمال به یکدیگر می رسند، آهنگ های موسیقی زندگی آنها کم و بیش تکمیل شده است و هر کلام یا هر شیئی در قاموس موسیقی هر کدام معنای دیگری می دهد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8277,'چیزی را که نتیجه ی یک انتخاب [ =برگزیدگی ] نیست، نمی توان شایستگی یا ناکامی تلقی کرد؛ در برابر چنین وضعی تحمیلی باید رفتار درستی در پیش گرفت.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8278,'وفا از والاترین پارسایی ها به شمار می رود. وفا به زندگی ما وحدت می بخشد و بدون آن، زندگی ما به صورت هزاران احساس ناپایدار پراکنده می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8279,'نخستین خیانت، جبران ناپذیر است و از طریق واکنش زنجیره ای، خیانت های دیگری را بر می انگیزد که هر کدام از آنها ما را بیش از پیش از خیانت پیشین دور می کند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8280,'موسیقی، روزنه هایی در تن باز می کند که روح می تواند برای رسیدن به صفای یکرنگی از آنها بیرون آید.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8281,'هرچه انسان بیشتر در تاریکی درون خویش به سر برد، بیشتر در ظاهر جسمانیش پژمرده می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8282,'دوست داشتن، چشم پوشی از قدرت است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8283,'آنچه به رفتار و کردار ما معنا می بخشد، همیشه برای ما ناشناخته است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8284,'عشق، به یک امپراتوری شبیه است؛ اگر اندیشه ای که بر مبنای آن به وجود آمده از میان برود، خود عشق نیز از میان خواهد رفت.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8285,'اندیشه ها نیز زندگی مردم را نجات می بخشند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8286,'همیشه ساده ترین پرسش ها با اهمیت ترین پرسش ها به شمار می رود و پاسخی برای آنها وجود ندارد و پرسشی که نتوان به آن پاسخ داد، مانعی است که فراتر از آن نمی توان رفت.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8287,'ما بیشتر برای از یاد بردن درد و رنج خویش به آینده پناه می بریم؛ در پهنه ی زمان، خطی را تصور می کنیم که فراسوی آن خط، درد و رنج ما پایان خواهد یافت.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8288,'اگر بی خبر باشیم، بی گناه هستیم؟ آیا آدم ابلهی که بر اریکه ی قدرت تکیه زده است، تنها به بهانه ی نادانی، از هر گونه مسئولیتی به دور است؟((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8289,'وقتی در برابر کسی که مهربان، مؤدب و مبادی آداب است قرار می گیریم، بسیار دشوار است که همه ی حرفهایش را دروغ تصور کنیم و درستی و راستی در او نبینیم.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8290,'اگر بتوان انسانها را به گروههای گوناگون تقسیم کرد، به یقین، این گروه بندی باید بر مبنای گرایش های ژرف و بنیادین آنها باشد؛ گرایش هایی که آنها را به سمت تلاشی می برد که زندگی خود را وقف آن می کنند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8291,'از زمان کودکی، پدر و آموزگار مدرسه برای ما تکرار می کنند که خیانت، نفرت انگیزترین چیزی است که می توان تصور کرد. اما خیانت کردن چیست؟ خیانت، از صف خارج شدن و به سوی نامعلوم رفتن است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8292,'آن کسی بازیگر است که از کودکی می پذیرد تمامی زندگی خود را برای مردم ناشناس به نمایش گذارد. اگر کسی به این کار بنیادی تن ندهد– که هیچ ربطی به استعداد ندارد و چیزی ژرفتر از آن است – نمی تواند بازیگر شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8293,'پزشک، کسی است که می پذیرد تمام عمرش را صرفنظر از نتایج آن، وقف بدن انسان کند. این، یک نوع موافقت بنیادی است - نه استعداد است و نه تبحر - که در سال اول، ورود او را به تالار تشریح فراهم می سازد و به او امکان می دهد که شش سال دیگر پزشک شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8294,'زندگی فقط یکبار است و ما هرگز نخواهیم توانست تصمیم درست را از تصمیم نادرست تمییز دهیم؛ زیرا ما در هر شرایطی فقط یکبار می توانیم تصمیم بگیریم؛ زندگی دوباره، سه باره و چهار باره به ما عطا نمی شود که این را برای ما امکان پذیر سازد تا تصمیم های گوناگون خود را مقایسه کنیم.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8295,'اگر عذاب ابدی و شرایط ممتاز فردی با هم یکسان باشد، اگر هیچ تفاوتی میان عالی و پست وجود نداشته باشد، هستی بشر، حجم و ابعاد خود را از دست می دهد و به گونه ای تحمل ناپذیر سبک می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8296,'زمانی که قلب، لب به سخن می گشاید شایسته نیست که خِرد خُرده بگیرد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8297,'ما هرگز نمی توانیم با قاطعیت بگوییم که روابط ما با دیگران تا چه حدی از احساسات ما، عشق ما، فقدان عشق ما، لطف و مهربانی ما و یا از کینه و نفرت ما سرچشمه می گیرد و تا چه حد از قدرت و ضعف ما در میان افراد تاثیر می پذیرد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8298,'نیکی حقیقی انسان - در کمال خلوص و صفا و بی هیچگونه قید و تکلف - تنها در مورد موجوداتی آشکار می شود که هیچ نیرویی را به نمایش نمی گذارند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8299,'آزمون حقیقی اخلاق بشریت، چگونگی روابط انسان با حیوانات است، به ویژه حیواناتی که در اختیار و تحت تسلط او هستند. اینجاست که بزرگترین ورشکستگی بشر تحقق یافته است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8300,'اگر ما شایستگی دوست داشتن را نداریم، شاید به این دلیل است که خواهانیم تا دوستمان بدارند؛ یعنی چشم داشت چیزی (عشق) را از دیگری داریم؛ به جای آنکه بدون ادعا و توقع به سویش برویم و تنها خواستار حضورش باشیم.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8301,'زمان بشری دایره وار نمی گذرد، بلکه به خط مستقیم پیش می رود و به همین دلیل، انسان نمی تواند خوشبخت باشد، چرا که خوشبختی، تمایل به تکرار است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8302,'وحشت، حالت یک ضربه را دارد؛ لحظه ای است که انسان هیچ چیز نمی بیند. وحشت فاقد هر گونه اثر زیبایی است و انسان تنها پرتو شدید رویداد ناشناخته ای را می بیند که چشم براه آن است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8303,'شک و تردید، امری یکسره طبیعی است؛ آدمی هرگز از آنچه باید بخواهد آگاهی ندارد، زیرا زندگی یک بار بیش نیست و نمی توان آن را با زندگی های گذشته مقایسه و یا در آینده درست کرد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8304,'هیچ وسیله ای برای تشخیص تصمیم درست وجود ندارد، زیرا هیچ مقایسه ای امکان پذیر نیست.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8305,'اگر نخستین تمرین زندگی، خودِ زندگی باشد، پس برای زندگی چه ارزشی می توان قایل شد؟((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8306,'کسی را از روی همدردی دوست داشتن، دوست داشتن حقیقی نیست.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8307,'آن کس که استعداد دشوار همدردی ( احساس مشترک ) را دارا نیست، به سردی رفتار دیگران را محکوم می کند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8308,'هیچ چیز از احساس همدردی دشوارتر نیست. حتی تحمل درد خویشتن به سختی دردی نیست که به گونه ای مشترک با کسی دیگر برای یک نفر دیگر یا به جای شخص دیگری می کشیم و قوه ی تخیل ما به آن صدها بازتاب می بخشد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8309,'بشر - چون تنها یک بار زندگی می کند- به هیچ وجه امکان به اثبات رساندن فرضیه ای را از راه تجربه ی شخصی خود ندارد، به گونه ای که هرگز نخواهد فهمید که پیروی از احساسات، کار درست یا نادرستی بوده است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8310,'مجازات کردن کسی که نمی داند چه می کند، نشانه ی توحش است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8311,'یک بار حساب نیست؛ یک بار مثل هیچ وقت است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8312,'تاریخ به سبکی زندگی یک فرد است؛ بیش از اندازه سبک، به سبکی پَر است، مانند گرد و غبار در هوا معلق است، مانند چیزی است که فردا ناپدید می شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8313,'عشق، آن گونه اشتیاق و تمایلی است که ما به آن نیمه ی از دست رفته ی خود داریم.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8314,'مرگ، یک پیشامد بسیار ساده است که به آسانی رخ می دهد، مثل تمام پیشامدهای دیگر.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8315,'شریف ترین احساسات می تواند به آسانی برای توجیه بزرگترین وحشت ها به کار گرفته شود.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8316,'بی توجهی به زن، کفر کبیر و بی احترامی بزرگی به آفریده های خداوند است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8317,'یکی از حقوق جدایی ناپذیر داستان نویس، این است که بتواند داستان خود را بازنویسی کند؛ اگر از آغاز داستان بدش بیاید، بتواند آن را از نو بنویسد، یا روی همه ی آن خط بکشد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8318,'مردم همیشه فریاد می زنند که می خواهند آینده ی بهتری بسازند. این، حقیقت ندارد. آینده، خلایی است بی عاطفه نسبت به همه.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8319,'مردم، تنها به این دلیل می خواهند ارباب آینده شوند که گذشته را دگرگون سازند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8320,'آدم هنگامی که صف را ترک می کند، دوباره می تواند به آن باز گردد، ولی زمانی که دایره ای بسته می شود، دیگر راه بازگشتی وجود ندارد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8321,'رمان، نتیجه ی تصور بیهوده ی بشر مبنی بر توانایی شناخت همنوعانش است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8322,'آدمی که کتاب می نویسد، یا همه چیز است یا هیچ و چون هرگز همه چیز به هیچ کس داده نمی شود، هریک از ما که کتاب می نویسیم، هیچیم.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8323,'آنگاه که نویسنده ی درون هر فرد پا به هستی می گذارد، به دوره ی ناشنوایی و از دست دادن درک جهانی نزدیک هستیم.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8324,'نام، به چَم پیوند با گذشته است و مردمی که گذشته ای ندارند، مردمی بی نام و نشانند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8325,'یادبودها در سراسر دنیا پراکنده اند. اگر بخواهیم آنها را پیدا کنیم و از مخفیگاه هایشان بیرون بکشیم، باید سفر کنیم.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8326,'آدم زمانی که هدف، برایش اهمیت نداشته باشد، نمی پرسد که به کجا دارد می رود!((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8327,'حتی ساده ترین شنوندگان، آنچه را که می گذرد، کم و بیش تشخیص می دهند.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8328,'موسیقی در پیچیده ترین شکل خود، همچون یک زبان است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8329,'هیچ چیز خوارکننده تر از دویدن با همه ی نیرو از روی ناتوانی نیست.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8330,'هرگز نباید بگذاریم که آینده، زیر بار گذشته فرو بپاشد.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8331,'تاریخ، پیاپی شدن دگرگونی های بی دوام است.((میلان کوندرا))',133,0,0,0,0,0);
INSERT INTO mysms VALUES(8332,'تنها انقلاب خطرناک، انقلاب گرسنگان است. من از شورشهایی که دلیل آن بی نانی باشد، بیش از نبرد با یک ارتش دویست هزار نفری بیم دارم.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8333,'در دنیا خفتگانی هستند که بیداریشان همچون بیداری اژدها وحشتناک است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8334,'ازدواج همیشه به عشق پایان داده است. ((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8335,'انسانها در کل، چیزی جز کودکان بزرگ نیستند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8336,'بزرگترین حوادث دنیا را که تجزیه و تحلیل می کنیم به این نتیجه می رسیم که آنچه باعث بروز آن شده هیچ ارزشی نداشته است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8337,'بزرگترین عمل غیر اخلاقی این است که انسان شغلی را که از انجام آن ناتوان است بر عهده گیرد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8338,'خونسردی بزرگترین صفت یک فرمانده است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8339,'استحکام و پایداری و ثبات و جرأت بر همه چیز فایق می شود.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8340,'بزرگترین خطر در زمان پیروزی رخ می دهد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8341,'هیچ کاری خوب انجام نمی گیرد مگر اینکه انسان خودش آن را به انجام رساند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8342,'فن جنگ، نیرومند بودن در یک نقطه ی مشخص و روشن است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8343,'بین شکست و پیروزی یک قدم بیشتر فاصله نیست و مردم از ترس شکست، شکست می خورند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8344,'[ هر ] پیروزی، پیروزی نیست و هر شکستی، شکست نیست.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8345,'مردی را در نظر بگیرید که در غربت و دور از خویشاوندان و خانواده خود زندگی می کند. چنین مردی به طور حتم نیاز به نوعی دلبستگی دارد تا جای علاقه پدر و برادرش را بگیرد. اینجاست که عشق می آید و این گونه مزایا را به او تقدیم می کند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8346,'آن کس که از اول می داند به کجا می رود، خیلی دور نخواهد رفت.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8347,'کم دانستن و پر گفتن مثل نداشتن و زیاد خرج کردن است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8348,'اولین شرط کامیابی، شهامت و بی باکی است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8349,'کسانی که روح ناامید دارند، گناهکارترین مردم هستند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8350,'عشق گوهری گرانبهاست، اگر با پاکدامنی همراه باشد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8351,'پاکدامنی در زن مانند شجاعت در مرد است؛ من از مرد ترسو آنگونه متنفرم که از زن نانجیب.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8352,'فداکاری در راه وطن از همه دانش ها باارزش تر است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8353,'عشق باید شادی آور باشد نه رنج آور.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8354,'کسی که از شکست می ترسد، حتما شکست خواهد خورد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8355,'به نظر ما خیرخواهی دیگران حماقت جلوه می کند و حماقتهای ما خیرخواهی، نیکی دیگران ضعف و ضعف ما همیشه نیکی است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8356,'رنجها و ناراحتی ها فکر انسان را قوی می کند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8357,'دلیری و شجاعت را به دروغ نمی توان بر خود بست؛ دلیری صفتی است که مکر و فریب نمی پذیرد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8358,'شجاعت مانند عشق از امید تغذیه می کند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8359,'کسی را که دوستش داری، آزادش بگذار؛ اگر قسمت تو باشد بر می گردد، وگرنه بدان که از اول مال تو نبوده است.ازدواج همیشه آنگونه که می پندارید، نتیجه عشق نیست.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8360,'نایاب ترین چیزها در جهان، دوست صمیمی است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8361,'من در جهان یک دوست داشتم و آن هم خودم بوده ام.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8362,'تسخیر یک کشور بزرگ از تسخیر قلب کوچک زن آسانتر است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8363,'زنان مرد صفت را دوست نمی دارم و همچنین از مردان زن صفت بیزارم؛ هر کس باید در دنیا تنها نقش خود را بازی کند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8364,'کم دانستن و پر گفتن مثل پول نداشتن و زیاد خرج کردن است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8365,'سه چهارم پیروزی در صحنه ی نبرد را « روحیه » تشکیل می دهد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8366,'باید در مشاوره، دقیق و خوشرو باشیم و در اجرای آن سخت و جدی.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8367,'نا امیدی، نخستین گامی است که شخص، به سوی گور بر می دارد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8368,'برای اندیشیدن وقت بگذارید، اما هنگامی که زمان عمل رسید اندیشیدن را متوقف کنید و حرکت کنید.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8369,'کسانی که بیش از اندازه فکر می کنند، تهی از اراده و تصمیم اند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8370,'هر مردی را می توان از طریق رفتار او با همسر و زیر دستانش شناخت.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8371,'برخی از مردان اگر بخواهند به فضیلت خود، مردانگی خود و صلابت خود دست یابند به دشمنانی صادق نیاز دارند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8372,'کسی به جز خودم مسئول سقوطم نیست؛ بزرگترین دشمنی که باعث به وجود آمدن سرنوشتی غم انگیز و اندوهبار برایم شده تنها خودم هستم.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8373,'اشکال کارتان این است که تازه روز قبل از نبرد، در حالی که هنوز از تحرکات دشمنتان بی خبرید، نقشه می کشید.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8374,'غیر ممکن کلمه ای است که تنها در فرهنگ لغت انسانهای احمق یافت می شود.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8375,'انسان با استقامت، قدرت و نفوذ عقل و استدلال می تواند حتی بر وبا و طاعون غلبه کند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8376,'هنگامی که دشمنت در حال اشتباه کردن است، در کارش وقفه نینداز.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8377,'تدبیر همیشه بر شمشیر غالب است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8378,'مردم از ترس شکست می بازند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8379,'اگر زن نبود، نوابغ جهان را چه کسی پرورش می داد؟((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8380,'شجاعت حقیقی، پیروز شدن بر سختی های زندگی است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8381,'بسیاری از مردم سعادت و شوکت روزگار پیری خود را مدیون سختی ها و دشواریهای روزگار جوانی هستند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8382,'احمق ها از گذشته حرف می زنند، دیوانه ها از آینده و عاقل ها از حال.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8383,'از کسانی که با شما مخالف هستند نهراسید، از کسانی در هراس باشید که با شما موافق هستند اما آن قدر جرأت ندارند که عدم موافقت خود را آشکارا به شما بگویند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8384,'آن قدر شکست خوردم تا راه شکست دادن را آموختم.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8385,'جنگ روی جنگ تغذیه می کند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8386,'سیاست فاقد قلب است و تنها مغز دارد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8387,'رهبران سوداگر امید هستند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8388,'یک سرباز برای یک تکه روبان رنگی به مدت طولانی و سخت می جنگد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8389,'مرگ حقیقی برای انسان، مرگ امید است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8390,'ندای وجدان را هیچ زمان از خود دور نسازید.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8391,'تصور کن اگر قرار بود هر کس به اندازه ی دانش خود حرف بزند چه سکوتی بر دنیا حکمفرما می شد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8392,'تا زنده ام لحظه ای آرام نخواهم گرفت؛ راحتی کردن و مردن نزد من یکی است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8393,'در دنیا فقط باید از یک چیز ترسید و آن [ خود ] ترس است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8394,'اگر به کسی اعتماد نداری از او دوری کن.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8395,'شرط اصلی موفقیت در تمام مراحل زندگی، داشتن سه چیز است : اول پول، دوم پول و سوم پول.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8396,'اگر به یک انسان فرصت پیشرفت ندهید، لیاقت، تاثیر چندانی در پیشرفت [ او ] نخواهد داشت.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8397,'از پیروزی تا سقوط تنها یک قدم فاصله است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8398,'مرد حقیقی کسی است که در خواری و خوشبختی، خود را تباه نکند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8399,'غرور و خودبینی را ابزار باز شناختن و نشان ترقی قرار ندهید. زیرا در این راه پر پیچ و خم، پرتگاههای وحشتناک زیادی وجود دارد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8400,'کسی که می تواند چاپلوسی کند، تهمت زدن هم برایش بسیار آسان است.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8401,'با به زبان آوردن صلح، دنیا آرام نمی شود.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8402,'راه گریز را پیش از ستیز باید جست.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8403,'هیچ چیز دشوارتر از تصمیم گیری نیست.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8404,'پیروزی یعنی اراده کردن.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8405,'در بین هنرها موسیقی بیش از همه در عواطف انسان تاثیر دارد. یک سمفونی در هر مایه از دست یک استاد، بدون شک بیشتر از یک کتاب اخلاق در دل اثر می کند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8406,'چه بسا اشخاصی که فقط با صدای کلنگ گورکن از خواب بیدار می شوند.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8407,'برای آدمی بهتر است که هرگز به دنیا نیاید، تا اینکه به دنیا بیاید و اثری از خود بر جای نگذارد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8408,'هیچ کس آنقدر ابله نیست که برای هیچ گونه کاری لیاقت نداشته باشد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8409,'مردم مانند فرزندان هستند و ارزش آنها بستگی به موقعیت شان دارد. انگیزه افراد بشر نیز ترس و یا نفع شخصی است؛ برای رهبری مردم باید از این دو انگیزه استفاده کرد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8410,'همیشه دم از صلح بزن، ولی آماده جنگ باش.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8411,'آنجا که قدرت قدم می گذارد، قانون ضعیف خواهد شد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8412,'برای یک مرد هیچ شکنجه ای بالاتر از آن نیست که به او ترحم کنی.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8413,'فرد با اراده در پیچ و خمهای زندگی هیچ گاه با ناامیدی روبرو نخواهد شد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8414,'من همه چیز خود را به گرسنگی و رنجهای جوانی مدیون ام.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8415,'به نظر من، حق ناشناسی، بزرگترین ناتوانی است که ممکن است کسی داشته باشد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8416,'عشق، روح را تواناتر می سازد و انسان را زنده دل نگاه می دارد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8417,'من گمان نمی‌کنم مرگ دارای اهمیتی باشد. آنچه که سرنوشت ما را تعیین می‌کند، به دنیا آمدن است. همه‌چیز بستگی دارد به اینکه به هنگام، به دنیا بیاییم.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8418,'کسی که از شکست خوردن می هراسد، به شکست خود باور دارد.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8419,'چه بسیارند مردانی که گناهکار و مجرم نیستند مگر به دلیل ضعف آنان برای یک زن.((ناپلئون بناپارت))',134,0,0,0,0,0);
INSERT INTO mysms VALUES(8420,'انسان باید یک ویژگی داشته باشد تا پیروز شود؛ آن ویژگی، روشنی هدف، آگاهی از آنچه می‌خواهد، و سوختن در آتش اشتیاق برای به چنگ درآوردن آن است.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8421,'هر آنچه را ذهن بتواند درک و باور کند، می‌تواند آن را به دست آورد.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8422,'هر پیشامد و رویدادی منفی، در درون خود، بذری از کامیابی و شادی بیشتر دارد.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8423,'شکست از آن کسانی است که بی‌تفاوت اجازه می‌دهند ذهنیت شکست در آنها نفوذ کند.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8424,'پندارها، فرماندهان دنیای ما هستند.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8425,'پیش از گرفتن، دهنده و بخشنده باشید.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8426,'اولین مرحله انجام کار، تمایل به انجام آن است.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8427,'برنامه ریزی دقیق و منظم، پیمودن نیمی از راه است.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8428,'شکست یک عامل نیرو بخش است، نه یک باز دارنده؛ هر شکست بذری از موفقیت در دل دارد.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8429,'نقطه شروع هر موفقیتی، وجود یک نظریه یا نقطه نظر است که خود ناشی از تصور می باشد.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8430,'نقطه شروع هر دستاوردی، خواست و اشتیاق است.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8431,'اگر چند کیلومتر بیشتر از آنچه تاکنون به آن خو گرفته‌اید، گام بردارید، دارایی افزونتری به دست خواهید آورد.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8432,'تهیدستی، مایه‌ی آبروریزی نیست، ولی بی‌گمان، مایه‌ی سربلندی و پشتیبانی هم نیست!((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8433,'در همین زمانهای سرخوردگی، چالش و ناامیدی است که انسان با یک جهش می‌تواند برای رهایی خود برنامه‌ریزی کند.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8434,'مهم نیست که چه می خواهید بکنید، مهم این است که در این لحظه چه می کنید.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8435,'دانستن کافی نیست، باید به دانسته خود عمل کنید.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8436,'یک زن قشنگ و نیکو صورت در نظر زیباست، اما یک زن خوب و نیکو سیرت در قلب انسان جای دارد.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8437,'اگر با دشمنی زیاد بجنگی، پس از مدتی تمام استراتژی های تو را فرا می گیرد.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8438,'نادان ها پیوسته از گذشته لاف می زنند، خردمندان در اندیشه حال بوده و دیوانگان، سخن آینده را به زبان می آورند.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8439,'پیروزی با کسانی است که پشتکار بیشتری دارند.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8440,'نبردهای زندگی همیشه به سود قویترین ها و سریعترین ها پایان نمی پذیرد! دیر یا زود، بُرد با کسی است که بردن را باور دارد!((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8441,'همیشه حرفی را بزن که بتوانی آن را بنویسی، چیزی را بنویس که بتوانی امضایش کنی و چیزی را امضا کن که بتوانی پای آن بایستی.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8442,'کسی که همیشه می خواهد اشتباه دیگران را ثابت کند، آنها را از خود دور می سازد.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8443,'اگر باور داشته باشی که می توانی، بی شک می توانی.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8444,'منتظر نمانید، زمانی مناسب تر از اکنون وجود ندارد. از همین نقطه ای که ایستاده اید و با همین ابزار و امکاناتی که در اختیار دارید، کار را ادامه دهید. همین طور که پیش می روید، ابزار ها و امکانات بهتر و مناسب تر را پیدا می کنید.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8445,'در هر رویداد منفی، بذر یک پیشامد پربارتر و سودمندتر کاشته می شود.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8446,'چشم براه مباش؛ زمانه هرگز به مراد دل ما نخواهد بود.((ناپلئون هیل))',135,0,0,0,0,0);
INSERT INTO mysms VALUES(8447,'لحظه پیروزی برای من از آن جهت شیرین است که پیران، زنان و کودکان کشورم را در آرامش و شادان ببینم.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8448,'گاهی سکوتم دشمن را فرسنگها از مرزهای خودش نیز به عقب می نشاند.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8449,'کمربند سلطنت، نشان نوکری برای سرزمین من است. نادرها بسیار آمده اند و باز خواهند آمد، اما ایران و ایرانی باید همیشه در بزرگی و سروری باشد؛ این آرزوی تمام عمرم بوده است.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8450,'هنگامی که برخواستم، از ایران ویرانه ای ساخته بودند و از مردم کشورم بردگانی زبون. سپاه من نشان بزرگی و رشادت ایرانیان در طول تاریخ بوده است؛ سپاهی که تنها به دنبال حفظ کشور و امنیت آن است.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8451,'کیست که نداند مردان بزرگ از درون کاخهای فرو ریخته به قصد انتقام بیرون می آیند؛ انتقام از خراب کننده. ندایی در درونم می گفت : برخیز، ایران تو را فراخوانده است و برخواستم.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8452,'میدان جنگ می تواند میدان دوستی نیز باشد؛ اگر نیروهای دو طرف میدان به حقوق خویش بسنده کنند.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8453,'از دشمن بزرگ نباید ترسید، اما باید از صوفی منشی جوانان واهمه داشت. جوانی که از آرمانهای بزرگ فاصله گرفت، نه تنها کمک جامعه نیست، بلکه [ همچون ] باری به دوش هموطنانش است.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8454,'اگر جانبازی جوانان ایران نباشد نیروی دهها نادر هم به جایی نخواهد رسید.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8455,'شاهنامه ی فردوسی خردمند، راهنمای من در تمام زندگی بوده است.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8456,'فتح هند باعث سرافرازی نبود؛ برای من دستگیری متجاوزین و سرسپردگانی مهم بود که بیست سال کشورم را ویران ساخته و جنایت و غارت را در حد کمال بر مردم سرزمینم روا داشتند. اگر در پی افتخار بودم پادشاهان اروپا را به بردگی می گرفتم که آن هم از جوانمردی و خوی ایرانی من به دور بود.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8457,'هر سربازی که بر زمین می افتد و روحش به آسمان پر می کشد نادر می میرد و به گور سیاه می رود. نادر به آسمان نمی رود، نادر آسمان را برای سربازانش می خواهد و خود بدبختی و سیاهی را. او همه ی این فشارها را برای به ثمر رساندن ایران بزرگ به جان می خرد؛ پیشرفت و به قدرت رسیدن ایران تنها عاملی است که فریاد حمله را از گلوی غمگینم به در می آورد و مرا بی پروا به قلب سپاه دشمن می راند...((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8458,'برای زمینهای کشورم هیچ زمان گفتگو نمی کنم، بلکه آن را با قدرت فرزندان کشورم به دست می آورم.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8459,'سکوت شمشیری است که من همیشه از آن بهره جسته ام.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8460,'تمام وجودم را برای سرفرازی میهن بخشیدم، به این امید که افتخاری ابدی برای کشورم کسب کنم.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8461,'خردمندان و دانشمندان سرزمینم؛ آزادی زمینهای کشور با سپاه من و تربیت نسلهای آینده با شما. اگر سخن شما مردم را آگاهی بخشد دیگر نیازی به شمشیر نادرها نخواهد بود.((نادر شاه افشار))',136,0,0,0,0,0);
INSERT INTO mysms VALUES(8462,'به راستی، انسان رودی است آلوده؛ دریا باید بود تا رودی آلوده را پذیرا شد و ناپاکی را نپذیرفت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8463,'آنچه در انسان بزرگ است این است که او پل است نه غایت؛ آن چه در انسان خوش است این است که او فراشدی است و فروشدی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8464,'کارهای بزرگ را همه دور از بازار و نام آوری کرده اند. پایه گذاران ارزشهای نو همیشه دور از بازار و نام آوری زیسته اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8465,'به خُردان و بیچارگان بس نزدیک زیسته ای. از کین پنهان شان بگریز! آنان در برابر تو سراپا کین اند و بس.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8466,'دوست می دارم آنانی را که برای فرو شدن و فدا شدن، نخست فراپشت ستارگان از پی دلیل نمی گردند، بل خویش را فدای زمین می کنند تا زمین روزی از آن ابرانسان شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8467,'دوست می دارم آن را که فضیلت خویش را خواهش و سرنوشت خویش می سازد و این گونه بَهر فضیلت خویش هنوز زیستن خواهد یا دیگر نزیستن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8468,'دوست می دارم آن را که روان اش خویشتن، بر باد ده است و نه اهل سپاس خواستن است و نه سپاس گزاردن؛ زیرا که همواره بخشنده است و به دور از پاییدن خویشتن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8469,'دوست می دارم آن را که پیشاپیش کردار اش کلام زرین می گستراند و همواره بیش از آن چه نوید می دهد به جای می آورد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8470,'دوست می دارم آن را که آیندگان را بر حق می کند و گذشتگان را نجات می بخشد؛ زیرا می خواهد جان بر سر کار انسانهای کنونی نهد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8471,'دردا، زمانی فرا رسد که از انسان دیگر اختری نزاید. دردا، زمانه ی خوار شمردنی ترین انسان فرا رسد؛ انسانی که دیگر خود را خوار نتواند شمرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8472,'هنوز همسایه را دوست می دارند و خود را بدو می سایند [ =مالیدن ]؛ زیرا انسان به گرما نیاز دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8473,'خطر را پیشه ساختی و درین چیزی نیست که سزاوار سرزنش باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8474,'خرد می گوید: هر که گرسنه ای را سیر کند، روان خویش را تازه می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8475,'رنجور را چشم بر گرفتن از رنج خویش و به فراموشی سپردن خویش لذتی مستانه است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8476,'در پس اندیشه ها و احساس هایت فرمانروایی قدرتمند ایستاده است، دانایی ناشناس که نام اش خود است. او در تن تو خانه دارد: او تن توست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8477,'بگریز، دوست من، به تنهایی ات بگریز! تو را از بانگ بزرگ مردان کر و از نیش خردان زخمگین می بینم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8478,'دیگر بار چونان درختی باش که دوستش می داری؛ همان درخت شاخه گستری که آرام و نیوشا بر دریا خمیده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8479,'پایان تنهایی آغاز بازار است؛ و آنجا که بازار آغاز می شود، همچنین آغاز هیاهوی بازیگران بزرگ است و وز وز مگسان زهرآگین.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8480,'جهان گرد پایه گذاران ارزشهای نو می گردد، با گردشی ناپیدا؛ اما مردم و نام گرد نمایشگران می گردند: چنین است «راه و رسم جهان».((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8481,'زمین هنوز برای روانهای بزرگ گشاده است. هنوز چه بسیار جای ها که برای تنهایان و جفت های تنها تهی است؛ چنان جای هایی که برگردشان عطر خوش دریاهای آرام وزان [ =روان ] است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8482,'نمایشگر را جانی است؛ اما جانی نه چندان با وجدان. ایمان او همواره به چیزی است که بیش از همه دیگران را وادار به ایمان آوردن به آن می کند؛ ایمان به خویشتن خویش.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8483,'بسا مهربانانه به نزد تو می آیند، اما همانا زیرکی ترسویان است. آری، ترسویان زیرک اند!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8484,'از آنجا که مهربان و دادگر هستی، می گویی: «گناهشان چیست اگر که زندگی شان کوچک است!» اما روان تنگشان می اندیشد که «هر زندگی بزرگ گناه است».((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8485,'با شناختن هر چیزی در کسی آن چیز را در او برافروخته می سازیم. پس، از خُردان بپرهیز.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8486,'آن را که پارسایی کاری است دشوار، می باید از آن بر حذر داشت تا که پارسایی او را راه دوزخ نشود؛ یعنی، راه پلیدی و آلودگی روان.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8487,'می خواهی چیزی از خود را در پیش دوستت نپوشی؟ به احترام دوست خویش است که خود را چنان که هستی می نمایانی؟ اما او تو را بدین سبب سر به نیست می خواهد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8488,'دوست می باید در پی بردن و دم فرو بستن استاد باشد: همه چیز را به چشم نباید دید. رویایت باید بر تو فاش کند که دوست ات در بیداری در چه کار است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8489,'ای بسا کس که زنجیر خویش نتواند گسست، اما بندگسل دوست خویش تواند بود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8490,'برده ای؟ پس دوست نتوانی بود. خودکامه ای؟ پس دوستی نتوانی داشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8491,'رفاقت هست؛ ای کاش دوستی نیز باشد!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8492,'تنها با ارزیابی است که ارزشی هست. بی ارزیابی گردوی هستی پوک است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8493,'نخست ملتها آفریدگار بودند و بسی پس از آن فردها. به راستی، فرد، خود، تازه ترین آفریده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8494,'چه بسا اندیشه های بزرگ که کارشان جز کار دَم نیست: باد می کنند و تهی تر می سازند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8495,'فراتر و برتر می روی. اما هرچه بالاتر روی چشم رشک [ =حسد ] تو را کوچک تر می بیند و آن که پرواز می کند از همه بیش نفرت می انگیزد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8496,'هرگاه که دردمندی را هنگام درد کشیدن دیده ام، از شرم اش شرمسار شده ام، زیرا به یاری برخاستن من غرور اش را پایمال کرده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8497,'زیر بار منت های بزرگ بودن کینه توز می کند نه سپاس گزار و چون نیکی کوچکی از یاد نرود به کرمی جونده بدل می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8498,'آدمی درشت ترین پاسخ را آنگاه می دهد که با فرزانگی خویش، حقیقت را در میان می نهد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8499,'اینکه با فرزانگی میانه ی خوب دارم و بسا خوب، از آن روست که بسیار مرا به یاد زندگی می اندازد!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8500,'نادانان، همانا مردم، رودی را مانند زورقی بر آن شناور؛ و در آن زورق، ارزش گذاری ها با وقار و روی پوشیده نشسته اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8501,'به راستی، آن که روزگاری اندیشه ی خود را اینجا در سنگ بر هم انباشت و بر افراشت، از آن خردمند ترین مردمانی بود که راز زندگی را می دانند. او اینجا با ساده ترین مثال به ما می آموزاند که در زیبایی نیز جنگ است و نابرابری و نبرد بر سر قدرت و چیرگی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8502,'خوش تر دارم که «قدیس ستون نشین» باشم تا گردبادی از کین.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8503,'اگر بناست که خدمتکار باشی، کسی را بجوی که به خدمت تو بیش از همه نیازمند باشد!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8504,'ماهیان همه همین می گویند. آنچه را خود نتوانند پیمود ناپیمودنی می دانند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8505,'من هرگز او را و خنده اش را باور ندارم هرگاه که زبان به بدگویی از خود می گشاید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8506,'شما فرزانگان نامدار خدمتگزار مردم و خرافات مردم بوده اید ؛ نه خدمتگزار حقیقت! و ایشان درست به همین دلیل شما را بزرگ می دارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8507,'آن که همیشه ایثار می کند در خطر از کف دادن آزرم است. آن که همیشه قسمت می کند، دل و دست اش از قسمتگری مدام پینه می بندد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8508,'خواهان فضیلت ستون باش که هر چه بالاتر می رود زیباتر می شود و ظریف تر، اما از درون سخت تر و استوار تر.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8509,'آنجا که فداکاری باشد و خدمتگزاری و نگاه های عاشقانه، خواست سَروری نیز هست. ناتوان تر از راههای پنهان به دژ، به اندرون دل قدرتمندتر می خزد تا قدرت را برباید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8510,'دهان را از واژه های نجیب پر می کنید و بر ماست آیا که باور داریم دلهای شما، شما دروغ زنان، سرشار است؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8511,'هر که از خود ایمان نداشته باشد همیشه دروغ می گوید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8512,'شناخت نزد من این است: آن چه ژرف است می باید تا بلندای من بر آید!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8513,'هرگاه مردم از مردان بزرگ دم زده اند هرگز سخن شان را باور نداشته ام و همچنان بر آن ام که او درمانده ای است باژگونه [ =واژگونه ] که از همه چیز بس کم دارد و از یک چیز بس بسیار.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8514,'پرتگاه هولناک است نه بلندی!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8515,'هر آن کس که نخواهد در میان بشر از تشنگی هلاک شود، باید بیاموزد که از هر جام بنوشد و هر آن کس که بخواهد در میان بشر پاک بماند، باید بداند که چگونه خویش را با آب ناپاک نیز بشوید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8516,'برای تماشایی شدن زندگی، نمایش آن را می باید خوب بازی کرد؛ و برای این به بازیگرانی نیکو نیاز است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8517,'نخست می باید گربه های وحشی تان ببر شوند و وزغ های زهر آگین تان سوسمار تا شکارگر خوب را شکاری خوب باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8518,'هر که کوهها را جا به جا کند دره ها و پستی ها را نیز جا به جا می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8519,'در خاموش ترین گاه [ =زمان ] شب است که شبنم بر سبزه فرو می نشیند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8520,'تو راه را از یاد برده ای؛ اکنون راه رفتن را نیز از یاد خواهی برد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8521,'به انجام رساندن کارهای بزرگ دشوار است. اما دشوار تر از آن فرمان دادن به کارهای بزرگ است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8522,'نابخشودنی ترین چیز در تو این است که قدرت داری و نمی خواهی فرمان برانی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8523,'آنجا که دیگر تو را نردبامی نمانده باشد، باید بدانی که چگونه از روی سر خویش بالا روی. از روی سر و از فراز دل خویش! اکنون آنچه در تو نرمترین است باید سخت ترین شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8524,'آن اهل دانشی که چشمانی ظاهربین دارد جز نمای همه چیز چه چیز را تواند دید؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8525,'آنچه من قله ی خویش می نامم، از فراز، بر خویش و نیز بر ستارگان خویش نگریستن است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8526,'در میان مردم کوچک دروغ بسیار است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8527,'فروتنانه در بر گرفتن یک نیکبختی کوچک را «تسلیم و رضا» می نامند! و در همان حال از زیر چشم فروتنانه به دنبال نیک بختی کوچک دیگر اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8528,'همه چیز آزاد است؛ تو می توانی، زیرا می خواهی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8529,'از ته دل ساده لوحانه بیش از همه خواهان آن اند که کسی ایشان را آزار نرساند. از این رو به پیشباز [ =پیشواز ] هر کس می روند و با او کنار می آیند. اما این ترس است، اگر چه «فضیلت» بنامند اش.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8530,'درخت برای آنکه بزرگ شود خواهان آن است که گِردِ صخره ها ی سخت، ریشه های سخت بدواند!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8531,'شما فضیلت مندان کوچک، چنان بر می گیرید که گویی می دزدند. اما شرف در میان ناکسان نیز می گوید: «تنها آن جا باید دزدید که به یغما نتوان برد.»((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8532,'همواره هر چه خواهی بکن؛ اما نخست از آنان باش که توانِ خواستن دارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8533,'همواره به همسایه ات مهر بورز؛ اما نخست از آنان باش که به خود مهر می ورزند؛((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8534,'خوش ترین شرارت و هنر نزدِ من همین است که خاموشی ام آموخته است با خاموش ماندن خود را فاش نکند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8535,'زیرک ترین خاموشان نزدِ من روشنان اند و راستان و شفافان. بُن آنان چندان ژرف است که شفاف ترین آبها نیز آن را فاش نتوانند کرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8536,'پرنده ی خوارداشت [ =توهین ] و هشدار دهی من تنها از درونِ عشق است که پَر می کشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8537,'کلامِ جنون آمیز تو مرا زیان می رساند، حتا آنجا که حق نیز با تو باشد!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8538,'آنجا که دیگر نمی توان عشق ورزید باید آن را گذاشت و گذشت!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8539,'همیشه چه کم اند آنانی که دلهاشان بی باکی و بازیگوشی دیرپای دارد و جانشان نیز شکیبا می ماند. جز اینان دگر همه بیم داران اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8540,'آن که از نوع من است با تجربه های نوع من رویارو می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8541,'سراسر روز، قلاب به دست در کنار برکه می نشینند و خود را ژرف می انگارند. اما من کسی را که در جای بی ماهی ماهیگیری کند سطحی نیز می نامم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8542,'هر که بخواهد همه چیز آدمیان را دریابد، می باید به همه چیزشان دست ساید. اما دستهای من پاک تر از آن اند که به این کار آیند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8543,'می توان فرزانگی خویش را ناقوس وار به صدا در آورد؛ اما دکان داران در بازار با جِرَنگاجِرَنگِ قِران هاشان صدای آن را می كُشند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8544,'همه قُد قُد می کنند، اما کیست که هنوز بخواهد خاموش در لانه بنشیند و تخمها را بپرورد!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8545,'آن که آفرین گفتن آموزانَد نفرین گفتن نیز آموزانَد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8546,'باید خویشتن دوستی آموخت. من چنین می آموزانم! با عشقی سالم و درست، چندان که بتوان نزد خویشتن ماند و سرگردان نگشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8547,'هر چیزی را جویدن و گواریدن، طبع بی گزند خوکانه است! همیشه «آری» گفتن کار خر است و آن کس که جانِ خران دارد!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8548,'برای چیرگی، راه و روشهای بسیار هست: در پی راه و روش خویش باش!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8549,'حقی را که بهر خویش توانی ربود مگذار تو را دهند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8550,'آنان که از نژاد روانها نژاده اند هیچ چیز را رایگان نمی خواهند، بالاتر از همه زندگی را.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8551,'لذت و بی گناهی شرمگین ترین چیزهایند و نمی خواهند کسی خواهانشان باشد. آنان را باید داشت، اما گناه و درد را همان به که بخواهند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8552,'هر آنچه نیکان شر می نامند جمله باید گِرد هم آید تا حقیقتی زاده شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8553,'دلسوزی ام بر همه ی گذشته از آن [جهت] است که آن را بازیچه می بینم. بازیچه ی لطف و عقل و جنون هر نسلی که می آید و هر چه بوده است را پلی بهرِ خود می انگارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8554,'نه جایی که از آن می آیید، بلکه جایی که بدان روان اید شرف شما خواهد بود! اراده ی شما و پای شما که می خواهد از شما برتر و فراتر رود شرف تازه ی شما خواهد بود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8555,'بر سر سفره می نشینند و چیزی با خود نمی آورند، حتی اشتهایی خوب! و حال ناسزا می گویند که «همه چیز نادرست است.»((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8556,'حکمتی هست در اینکه بسی چیزها در جهان بدبوست. دل آشوبه خود بال می آفریند و یارای در آب جَستن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8557,'از آنجا که بد آموخته اند و بهترین چیز را نیاموخته اند و همه چیز را بسی زود و بسی شتابناک آموخته اند؛ آنان از آن جا که بد خورده اند، معده شان آشوب شده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8558,'سرشتِ مردمِ ناتوان همواره چنان است که در راه خود گم می شوند و سرانجام خستگی شان می پرسد: «چرا باید راهی در پیش گرفت؟ همه چیز یکسان است!»((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8559,'هیچ یک از شما نمی خواهد در زورق مرگ پای گذارد! پس از چه رو می خواهید از جهان خسته باشید!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8560,'بر روی زمین نوآوری های خوب فراوان است، برخی سودمند، برخی دل پسند؛ زمین را برای آنها دوست باید داشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8561,'بهترین را سروری باید و بهترین است که سروری خواهد! و آنجا که آموزه ای جز این در میان باشد، آنچه در میان نیست، بهترین است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8562,'دروغ باد ما را هر حقیقتی که با آن خنده ای نکرده ایم!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8563,'بپایید که پیوند زناشویی تان بد بسته نشود؛ زیرا سرانجامِ پیوند شتابکارانه، زناکاری است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8564,'جفت های ناجور را همیشه بدترین کینه توزان یافته ام. آنان از همه ی جهان، تاوان این را می خواهند که دیگر تنها نیستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8565,'آن که در شناخت سرچشمه های کهن استاد شده است، سرانجام به جست و جوی چشمه های آینده و سرچشمه های تازه بر می خیزد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8566,'زمین لرزه همچنان که بسی چاهها را کور می کند و بسی تشنگی می آفریند، نیروهای نهان و اسرار را نیز آشکار می کند. زمین لرزه چشمه های تازه آشکار می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8567,'انسان کوچک، به ویژه شاعر، چه پُرشور با واژه ها از دست زندگی می نالد! به او گوش فرا دهید؛ اما گوش فرا دادن به لذتی را که در هر نالیدنی هست، از یاد مبرید!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8568,'اگر الف تا یای من این است که هر آن چه سنگینی است سبک شود و تن ها همه رقاص و جانها همه پرنده؛ به راستی، این است الف تا یای من!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8569,'تو با حقایقت سخت فرو می کوبی. اما چماقت از من می راند این حقیقت را!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8570,'من خود به راستی مردی بزرگ ندیده ام. برای [ دیدنِ ] آنچه بزرگ است امروز چشمان تیزبین ترین کسان نیز کم سو است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8571,'شرارتم شرارتی خندان است و خود را در زیر چفته های [ = چترهای ] نسترن و چَپَرهای [ =شاخه های درهم ] سوسن در خانه ی خویشتن می یابد؛ زیرا در خنده شرارتها همه نزد یکدیگر گرد می آیند، اما خجستگی خنده آن را قدسی و آمرزیده می دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8572,'غوکی [=وزغی ] که دیری خود را باد كُنَد سرانجام می ترکد و بادش در می رود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8573,'چه کسی تاکنون دانسته است که بزرگ کدام است و کوچک کدام؟ چه کس در جُستن بزرگی خوش بخت بوده است؟ تنها یک دیوانه: بخت یار دیوانگان است!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8574,'آن کس که خوب به دنبال می رود، به آسانی دنباله روی می آموزد؛ زیرا همیشه دنبال دیگران است!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8575,'کشتی ای را که به خشکی بچسبد و به آغوشِ آن پناه برد، [ برای بستن اش ] تار تارتَنَکی [= عنکبوتی ] از خشکی بس! اکنون به ریسمانی سخت تر ازین نیازی نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8576,'شجاع آن کسی است که ترس را می شناسد، اما بر ترس چیره می شود؛ آن که مغاک [= گودنايِ ژرف و هولناک ] را می بیند، اما با غرور؛ آن که مغاک را می بیند، اما با چشمانِ عقاب؛ آن که با چنگالِ عقاب مغاک را می چسبد شجاع است!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8577,'نزد آنان که بیش از توانِ خویش می خواهند، دروغی زشت در میان است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8578,'آنچه را که غوغا روزی بی دلیل باور داشته است، چه کس می تواند با دلیل واژگون کند؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8579,'به خود می بالند که دروغ نمی گویند، اما ناتوانی در دروغ گفتن کجا و عشق به حقیقت کجا! هشیار باشید!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8580,'برای بالا رفتن، پاهای خویش را به کار گیرید! مگذارید شما را بالا کشند. بر سر و بر گُرده ی بیگانگان منشینید!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8581,'هر کس تنها آبستن فرزند خویش است. مگذارید در گوش تان افسانه بخوانند و افسون تان کنند! همسایه ی شما کیست! و اگر «برای همسایه» کاری کنی، هنوز برايِ او نمی آفرینی!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8582,'هر کس هر چه را که با خود به خلوت بَرَد آن چیز آنجا رشد می کند، از جمله ددِ درون. از این رو بسیاری را باید از خلوت نشینی بر حذر داشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8583,'چرا باید نفرین کرد جایی را که دوست نمی داریم؟ این به گمانِ من بی ذوقی است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8584,'گامها می گویند که مرد آیا در راه خویش گام می زند یا نه؛ پس، راه رفتن ام را بنگرید! آن که به هدفِ خویش نزدیک می شود، رقصان است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8585,'گر چه بر روی زمین انبوه مردابها و رنجها هست، آن که سبُک پاست بر سر گِل و لای چنان می دود و می رقصد که بر سر یخ هموار.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8586,'از شادکامی دیوانه گشتن به که از ناکامی!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8587,'باشندگان همه تاکنون چیزی فراتر از خویش آفریده اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8588,'چون راه رفتن آموختم، به دویدن پرداختم. چون پرواز کردن آموختم، دیگر برای جنبیدن نیاز به هیچ فشاری ندارم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8589,'پهلوانی را که در توست فرو مگذار. برترین امید خود را مقدس شمار!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8590,'شما را تنها دشمنانی باد که از ایشان بیزار باشید، نه دشمنانی که خوارشان شمارید. دشمنتان می باید مایه ی سربلندی شما باشد: آن گاه کامیابی او کامیابی شما نیز خواهد بود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8591,'طغیان [ =سرکشی ] شرف برده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8592,'آنان که ملتها را آفریدند و بر فرازشان ایمانی و عشقی آویختند، آفرینندگان بودند و این سان زندگی را خدمت گزاردند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8593,'هر ملت با زبان «نیک و بد» خویش سخن می گوید. همسایه اش این زبان را در نمی یابد. او این زبان را بر پایه ی سنت ها و حقوق خویش بنا کرده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8594,'کوتاهترین راه همیشه لزوما راه تا حد ممکن راست نیست، بلکه آن است که در آن شرطه ترین [ =پسندیده ترین ] بادها، بادبانهایمان را پر می کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8595,'موسیقی مدرن با شُش های نیرومند و اعصاب ضعیف، پیش از همه از خویشتن خود دهشت [ =حیرت ] می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8596,'شاعران و هنرمندانی که از سینه تنگی [ = آسم ] رنج می برند، کاری می کنند که قهرمانانشان بیش از همه نفس نفس بزنند: آنها چیزی از آرام و سبک نفس کشیدن نمی دانند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8597,'اگر همه ی صدقات تنها از سر همدردی داده می شد، همه ی گدایان مدتها پیش یکجا از گرسنگی می مردند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8598,'انسان بی آنکه شنونده باشد می تواند بسیار بشنود، اگر که نیک نگریستن و نیز گاهگاه هیچ نکردن و از نظر دور داشتن را دریابد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8599,'پیشه های برخی از ثروتمندان و نجبا گونه ای خستگی در کردن از کاهلی [ =تن آسانی ] دیرپا و معمول شان است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8600,'پیش از هر چیز می آموزیم که به جای ساده و بی شیله پیله نوشتن، شکوهمند و پر طمطراق [ = کر و فر و خودنمایی ] بنویسیم. دلایل چنین نوشتنی نیز در امر اخلاقی، گم و ناپدید می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8601,'یک هنر مند شریف اساساً مجاز خواهد بود که بداند کارش بر چه کسی تأثیر می گذارد! و آیا هرگز بر مردم! بر ناپختگان! بر احساساتی ها! بر بیماران و اما بیش از همه بر گرانجانان [ =مردم سخت جان ] تاثیر می گذارد؟!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8602,'هر جا که گذشته را بستایند، نباید یکسر پاکی ها و پاکان را سبب آن بدانیم. پرهیزگاری و [ تکریم ] بدون اندکی غبار، کثافت و پلشتی، چیز خوبی از آب در نمی آید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8603,'اساساً فرد نیرومند نه فقط با طبیعت بلکه با جامعه و تک تک افراد ناتوان تر نیز به مثابه ی سوژه ی شکارش رفتار می کند؛ تا آنجا که می تواند از آنها بهره کشی می کند و آنگاه راه خویش می گیرد و می رود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8604,'هر چه فرد [ مقتدر ] زیرک تر باشد، جاه طلبی او بیشتر است؛ زیرا افزایش باور به قدرت به مراتب ساده تر از افزایش نفس قدرت است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8605,'خشم گرفتن و مجازات کردن از انگاره ی حیوانی مان بر جای مانده است. آدمی تازه زمانی سخنگو می شود که این هدیه ی سنگین بدرد نخور را به حیوانات بازپس دهد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8606,'ناخوشنودی و تیره و تار بودن جهان، نزد اقوام کنونی میراث گرسنگی کشیدن های پیشین است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8607,'انسانیت بایستی روزگاری درختی شود که بر سراسر زمین سایه افکند، با میلیاردها شکوفه ای که می باید همه در کنار یکدیگر میوه شوند و زمین، خود نیز برای تغذیه ی چنین درختی فراهم شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8608,'دروره های اصلی در زندگی، آن زمانهای کوتاه سکون اند، در میانه، مابین بر آمدن و فرونشستنِ یک اندیشه یا احساس حاکم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8609,'درست همانند طبیعت، در دانش نیز نخست، بدترین و بی ثمرترین مناطق خوب پرورش می یابند، زیرا برای این حوزه ها ابزارهای دانش مربوط به آن کمابیش کفایت می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8610,'مثل حیوانات از کوه و کمر بالا می روند، احمق وار و عرق ریزان؛ انگار دیگران یادشان رفته بود به ایشان بگویند که میان راه چشم اندازهای زیبایی هم هست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8611,'اکنون همه ی مردم بس بسیار عمر می کنند و بس بسیار اندک می اندیشند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8612,'بسیار پیش می آید که انسان در طول سفر مقصدش را از یاد می برد. ... فراموش کردن نیات، رایج ترین حماقتی است که مردم مرتکب می شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8613,'در زمانه ی ما هر که سخت جدی مبین یک ویژگی یگانه ی اخلاقی باشد، کسانی همانند تئوفراست و مولیر، بیمار شناخته می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8614,'در زمانه ی ما نسبت به کسی که به خود باور دارد، بدگمان می شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8615,'نثار کردن سرمست کننده تر از داشتن است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8616,'کمی تندرستی، هرازگاه نیک ترین دوای درد بیماران است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8617,'از کسی که نمی تواند کاری را درست انجام دهد، باید بخواهیم هرگز دور و بر آن کار نرود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8618,'عشق به آدمها هرچه بیشتر می شود، عاشقان بی پرواتر می شوند، تا آنجا که سرانجام دیگر شایسته ی عشق نیستند و به راستی کدورتی در میانه پدیدار می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8619,'چگونه می تواند کسی اندیشمند شود، اگر دست کم یک سوم هر روزش را به دور از غوغا و شور، بی آدمیان و کتابها سر نکند؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8620,'در گفتگو با آدمهای عامی و میان مایه، می باید این را بفهمیم که با چشمهای بسته ی متفکر بیاندیشیم، تا به عوامانه و میان مایه اندیشیدن دست یابیم و بتوانیم آن را دریابیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8621,'وقتی کسی از ما پوزش می خواهد، بایستی این کار را به بهترین نحو انجام دهد؛ و گرنه خودمان به آسانی در مقام گناهکار جلوه می کنیم و احساس ناگواری به ما دست می دهد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8622,'این اندیشنده نیاز به کسی ندارد که رد و انکارش کند؛ وجود خودش برای این کار کفایت می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8623,'اِعمال قدرت، زحمت دارد و مردانگی می خواهد؛ این است که بسیاری، حق مسلم و ثابت خود را جاری نمی کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8624,'اگر انسان مدام بی اندازه گرامی داشته شود و اندک بخورد، نرم خوترین کس می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8625,'آدمها به سوی روشنایی می شتابند و گرد چراغ جمع می شوند، نه برای اینکه بهتر ببینند، بلکه برای اینکه بهتر بدرخشند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8626,'آدمی در پیشگاه هر که بدرخشد، میل دارد او را چراغ [ روشنایی ] بداند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8627,'آه، بر چه ملال ها باید چیره شد و چه مایه عرق باید ریخت تا آدمی بتواند رنگهای خودش، قلم موی خودش و بوم خودش را پیدا کند!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8628,'ملت خود را به حق رأی همگانی نسپرده، اما با این همه هر جا که این حق اکنون رایج است، به استقبالش رفته و معمولاًً آن را پذیرفته است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8629,'از بین رفتن و نابود شدن، خوشتر از نفرت ورزیدن و هراسیدن است و نابود شدن به مراتب بس خوشتر است از نفرت ورزیدن به خود و هراساندن دیگری.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8630,'انسان مخالف هر چیزی است که موقتاً در تملک اوست، بدون تیمارداری و جانفشانی برای آن ملک، برخوردی سودجویانه با آن دارد، یا در مقام غارتگر و اسراف کاری بی چشم و رو با آن برخورد می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8631,'خطرناکترین هوادار، کسی است که با رویگرداندن وی از حزب، آن حزب به تمامی نابود می شود و البته بهترین هوادار هم اوست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8632,'انسان آگاه را مردم به آسانی ترسو، متأثر و خرده بین می شمرند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8633,'اگر آدمی زیرک باشد، بهترین کاری که می تواند بکند، فرزانه بودن است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8634,'با کمبود خویشتن داری جزئی، توانایی خویشتن داری بزرگتر از میان می رود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8635,'آن که می باید آفریدگار نیک و بد باشد، همانا که نخست می باید نابودگر باشد و ارزش شکن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8636,'حقایقی که بر زبان نیایند، زهر آگین می شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8637,'شکسته باد هر آنچه در برابر حقایق ما شکستنی است! هنوز چه خانه ها که بایدمان ساخت!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8638,'به محض اینکه آدمی خود را یافت، باید این را نیز دریابد که گاه گاهی خود را گم کند و دوباره بیابد؛ به این شرط که اندیشمند باشد. به زیان چنین کسی است که همواره به یک فرد وابسته باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8639,'هر کس که با کمال میل می خواهد به وجد درآید و به آسانی بر جهد بالا رود، باید مراقب باشد بی اندازه سنگین نشود، مثلاً اینکه بسیار نیاموزد و نگذارد از دانش آکنده شود. این کار او را کرخت می کند! ای پر شوران، هوای خودتان را داشته باشید!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8640,'تا آنجا که ممکن است آدمی می بایست نیازهای ضروری خویش را خود برآورد، اگر چه نصفه نیمه و ناقص؛ این است راه آزادی جان و تن [ شخصیت ].((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8641,'غرور، هر جای آسیب دیده و پاره پوره را رفو می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8642,'آن که عادت کرده است خود را همیشه در آینه بنگرد، همواره زشتی اش را از یاد می برد. ... آدمی حتی یک لحظه زشتی تغییر ناپذیر را بر نمی تابد؛ یا آن را فراموش می کند یا در همه ی موارد انکار اش می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8643,'ما از طبیعت سخن می گوییم و در این میان، خودمان را فراموش می کنیم؛ ما خود، طبیعت هستیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8644,'در انسانهای ژرف و چاههای ژرف مدت زیادی طول می کشد تا چیزی را که در آنها می افتد، به ته برسد. نظاره گرانی که معمولاً مدت زیادی تاب نمی آورند، به سادگی چنین انسانهایی را بی حرکت و سخت می پندارند، یا حتی به ستوه آورنده.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8645,'آرامش، عظمت و پرتو آفتاب، این سه، همه ی آنچه را یک اندیشنده آرزو می کند و از خود می خواهد، در بر می گیرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8646,'شاید دلمان نیاید ما را در راه باورهایمان بسوزانند؛ زیرا چندان به آنها یقین نداریم. اما چه بسا برای آنکه بتوانیم آنها را داشته باشیم و دگرگونشان کنیم، بدان راضی شویم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8647,'قهرمان هر جا که می رود بیابان برهوت و محدوده های مرزی مقدس، ناپیمودنی و بکر را با خود می برد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8648,'هر طلایی نمی درخشد. لطیف ترین درخشندگی ها از آنِ ناب ترین فلزهاست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8649,'چرخ و ترمز، وظایف گوناگونی دارند، اما یک وظیفه ی یکسان نیز دارند: به درد آوردن یکدیگر.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8650,'دانایی بسیار داشتن ما را جوان نگه می دارد، اما بایستی این را نیز برتابیم که از این بابت ما را پیرتر از آنچه هستیم بپندارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8651,'در اینجا پهلوانی هست که هیچ نکرده، مگر تکاندن درخت، بی درنگ پس از رسیدن میوه هایش. آیا این را اندک می انگارید؟ پس نخست به درختی بنگرید که وی تکاندش.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8652,'تنها به انسان شریف می توان آزادی جان ارزانی کرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8653,'بر خلوت نشین بیداد روا می دارند و بر او لای و لجن پرتاب می کنند. اما، برادر، اگر ستاره می خواهی بود بدین سبب بر ایشان کمتر متاب!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8654,'سوختن در آتش خویشتن را خواهان باش. بی خاکستر شدن کی نو توانی شد؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8655,'بیش از آن دوست بدارید که دوست تان می دارند و در این کار هرگز از هیچ کس واپس نمانید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8656,'نهاد مرد ژرف است و رود اش در غارهای زیرزمینی می خروشد. زن قدرت او را حس می کند، اما آن را در نمی یابد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8657,'مرد را از زن هراس باید، آنگاه که زن عاشق است. چه آنگاه است که زن همه چیز را فدا می کند و هیچ چیز دیگر را در نظر او ارجی نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8658,'زن از چه کس از همه بیش بیزار است؟ آهنی به آهن ربا چنین گفت: «از تو از همه بیش بیزارم که کشش داری، اما نه چندان که به خود بکشانی.»((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8659,'اگر دشمنی دارید، بدی اش را با نیکی پاسخ نگویید که شرمسار می شود. به جای آن گواهی دهید که در حق شما نیکی کرده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8660,'بی حق دانستن خویش بزرگوارانه تر است از بر حق دانستن، به ویژه آنگاه که حق با تو باشد. بَهر این کار تنها چندان که باید توانگر می باید بود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8661,'گوشه نشین چاهی ژرف را ماند؛ سنگی در آن افکندن آسان است، اما چون به ته چاه فرو رفت، هان، چه کس آن را باز برون خواهد آورد؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8662,'نخواهم چون رسن بافان باشم که هر چه بیش می ریسند واپس تر می روند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8663,'و هر که جویای نام است، باید به هنگام از افتخار کناره گیرد و هنر دشوار به هنگام رفتن را به کار بندد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8664,'آن زمان که خوش ترین مزه ها را داری مگذار تو را تمام بخورند. آنان که می خواهند دیر زمانی در دلها جای داشته باشند این را می دانند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8665,'به راستی، هستند سیب های ترشی که سرنوشت شان این است که تا واپسین روزهای پاییز چشم براه مانند و یکباره رسیده و زرد و پلاسیده شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8666,'برخی را نخست دل پیر می شود و برخی را نخست جان و برخی در جوانی پیر اند؛ اما جوانی که دیر آید، دیر پاید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8667,'راههای نو می نوردم، زیرا کلامی نو به من روی آورده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8668,'خواستن آزادی بخش است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8669,'مرد دانا انسان را چنین می نامد: جانوری با گونه های سرخ.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8670,'مرد بزرگوار هرگز کسی را شرمسار نمی کند و خود از دیدار دردمندان شرمسار نمی شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8671,'اگر بنا باشد که اهل رحم باشم، نخواهم که آن را بر زبان آورم و رحم آوردن ام همان به که از دور باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8672,'هر چه بیش تر خود را شاد کنیم، آزردن دیگران و در اندیشه ی ِآزار بودن را بیشتر از یاد می بریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8673,'خاموش ترین کلام هایند که طوفان می زایند. اندیشه هایی که با گام کبوتر می آیند جهان را رهبری می کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8674,'هر که از خویش فرمان نبرد بر او فرمان می رانند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8675,'من که بخشنده ام با خوشدلی می بخشم، چون دوستی به دوستان. اما غریبان و مسکینان را بهل [ =گذاشتن کسی را به مراد خود ] تا خود از درخت من میوه بچینند. این گونه کمتر شرمسار می شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8676,'گدایان را یکسره باید از میان برداشت! به راستی، ایشان را چیزی دادن مایه ی آشفتگی است و چیزی ندادن نیز.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8677,'خرد اندیشی همچون دمل آگینی [ = آلوده ] است که پنهان می خزد و هیچ جا روی نشان نمی دهد تا آنکه تن سراپا از دملها پر شود و بگندد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8678,'چون دوستی با تو بدی کند، با او بگو: «آنچه با من کرده ای بر تو بخشودم، اما آنچه با خود کرده ای را چه گونه توانم بخشود؟»((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8679,'دل را نگاه دار! دل چون رفت سر نیز چه زود از پی دل می رود!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8680,'آدمی را ارزش های دروغین و کلامهای پوچ، هولناک ترین هیولاست! سرنوشت شوم دیری در آنها می خسبد و چشم براه می ماند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8681,'با حواس سست و خفته با تندر و آتش بازی آسمانی سخن باید گفت. اما زیبایی آوایی آرام دارد که تنها در بیدارترین روانها راه می برد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8682,'فضیلت شما همانا عزیزترین چیز شماست. تشنگی حلقه در شماست؛ هر حلقه از آن رو می گردد و می کوشد که به خود باز رسد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8683,'هستند کسانی که در مرداب شان نشسته اند و از میان نیزار می گویند: «فضیلت، همانا خاموش در مرداب نشستن است! »((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8684,'هستند کسانی که فضیلتی می شمارند گفتن این را که «داشتن فضیلت واجب است»، اما در ته دل تنها بر آن اند که پاسبانان واجب اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8685,'ای بسا کس، بلندی انسان را نتواند دید و اینکه پستی او را فرا چشم می بیند، فضیلت می شمرد. بدین سان، شور چشمی خود را فضیلت نام می نهد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8686,'زندگی چشمه ی لذت است. اما آنجا که فرومایه نیز آب می نوشد، چاهها همه زهرآگین اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8687,'از فرمانروایان رویگردان شدم چون دیدم آنچه را که اکنون فرمان روایی می خوانند؛ یعنی چانه زنی و سوداگری بر سر قدرت با فرومایگان!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8688,'دل مسپارید به آنانی که انگیزه ی کیفر دادن در ایشان نیرومند است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8689,'دل مسپارید به آنانی که بسیار از دادگری خود دم می زنند! به راستی، روان ایشان تنها از انگبین نیست که تهی است!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8690,'عدالت با من چنین می گوید: انسانها برابر نیستند و برابر نیز نخواهند شد!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8691,'دانستن و از مسئولیت فروگذار نکردن و آن را به دیگران محول نکردن از نشانه های والا بودن است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8692,'با دیگران بودن، آلودگی می آورد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8693,'چهار فضیلت انسان والا عبارت است از : دلیری، درون بینی، همدلی و تنهایی که گرایش به آنها سبب پاکی می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8694,'عشق، فریبنده و ویرانگر است نه نجات بخش.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8695,'نادرستی یک حکم باعث نمی شود که آن حکم را رد کنیم. احکام نادرست برای زندگی بشری ضروری است و رد کردن آنها به معنای رد کردن زندگی است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8696,'ضمن مطالعه کتاب سعی کنید همچون صیادی چیره دست به صید افکار خوب بروید و افکار کهنه و پوسیده را یکسره به دور اندازید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8697,'آن که آفرینندگی و نو آفرینی می خواهد، در نیک یا بد، ناگزیر نخست می باید با نیست و نابود کردن ارزشها آغاز کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8698,'زندگی بدون موسیقی، اشتباه است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8699,'در کوهستان کوتاهترین راه از قله ای به قله دیگر است، اما برای گذشتن باید پاهایی دراز داشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8700,'مرد حقیقی خواستار دو چیز است : خطر و بازی. از این رو زن را به عنوان خطرناک ترین بازیچه ها می طلبد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8701,'سعی نکن بر ضد جریان باد، آب دهن اندازی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8702,'اندیشه داشتن؟ باشد! از آن به سروری می رسم! اما اندیشه ورزیدن را خوش دارم فراموش کنم!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8703,'نمی خواهم برده باشم؛ نه اکنون، نه هیچ وقت!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8704,'آن را که خبرهاست برای روز مبادا، بسیار می خموشد در خود، آنکه روزی، می خواهد آذرخشی برافروزد، باید که دیری ابر باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8705,'اصیل ترین غرایز را تامل، شریف می گرداند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8706,'بندی که به دست نمی بُرد، با دندانش ببُر.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8707,'آن مغرور، حتی از اسبی که کالسکه اش را پیش می راند بیزار است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8708,'با شوخ طبعان نیک می توان شوخی کرد؛ آن که غلغلکی است به سادگی می توان غلغلک اش داد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8709,'می خواهی چشم و ذهن خود را مکدر [ =تیره ] نکنی؛ در پی آفتاب بدو در سایه!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8710,'بیزارم از روان های باریک؛ به گمانم هیچ نیکی و بدی بر آن نمی ماند!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8711,'گِرد خویش اگر چون خمره ی غلتانی نگردم؛ چگونه تاب آرم، بی سوختن، از پی خورشید دویدن را ؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8712,'آن که هراسناک نیست، کسی را نمی هراساند؛ و تنها آن که هراس می افکند دیگری را ره می نماید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8713,'می نگارد، می نگارد نابردبار، این یاوه ی [ = چرند و پرند ] حکیمانه اش؛ گویا قاعده چنین است، نخست بنگار، آنگاه فلسفه بافی کن!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8714,'بیگانه با خلق و نیز سودمند برای خلق؛ راهم را این گونه می پویم، گاه آفتاب، گاه ابر و هماره بر فراز همین خلق!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8715,'اگر دارنده ی روحی، دوچندان مراقب خود باش! به خودت می كِشند، دوستت می دارند، بعد هم تو را می درند؛ روان های رمه ای! بدین سان روانت را از کف می دهی!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8716,'همه ی کامیابی ها بر زمین و دوستان، رهاورد نبرد اند! آری، برای دوست شدن به باورت نیاز است! یکی از این سه، دوستانند: برادران به وقت نیاز، بی تفاوتان در برابر دشمن و آزادگان در برابر مرگ!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8717,'آوازه، همه ی هوش و حواس ات را گرفته؟ پس این آموزه را در گوش گیر: هر از گاهی به دلخواه از ستایش روی بگردان.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8718,'تنها با دست نمی نویسم: پا نیز، همواره، همراهی نویسنده را می خواهد. استوار، آزاد و دلیر می دَوَد، گاهی بر دشت، گاهی بر کاغذ.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8719,'این طبیعت كِی به نقش درمی آید؟ بی کرانه است، خردترین جزء آن! و سرانجام، نگارگر آن را می كِشد که می پسندد! چه را می پسندد؟ آنچه می تواند نقاشی کند!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8720,'برایت آرزومند لقمه ای چرب و هاضمه ای [ =گوارنده ] خوبم. کتابم را اگر بگواری، مرا نیز با آن فرو برده ای!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8721,'آن که به فراز می رود، ستایش شایسته ی اوست! اما کسی همواره از فراز می آید! حتی به دور از ستایش می زید؛ فرازنده اوست!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8722,'باید خموشی گزینی یا که سترگ سخن گویی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8723,'بر من خشم مگیرید که خفتم، مرده نبودم، فقط خسته بودم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8724,'ای والا مردمان! از آن هنگام که امروز و دیروزی بوده است؛ روزگارانی اندیشه ور و روزگارانی اندیشه در بوده اند. این روزگار، چون بانوی بیماری است؛ رها کنیدش تا فریاد برآورد، خشم بگیرد، ناسزا گوید و میز و بشقاب را در هم شکند!...((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8725,'بایستی دوباره به انبوهه ی مردمان بازآیی، در میان آنها سُفته و سخت شوی؛ تنهایی تباه می کند...((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8726,'می خواهی بر بلندی ها خانه کنی؛ سنگینی هایت را به دریا فکن. فراموشی هنری ایزدی است!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8727,'کسی خواهد بود که بتواند حق ات را بدهد؟ پس حق ات را بگیر.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8728,'آنچه پیرامونتان می زید به زودی در شما خانه می کند؛ عادت این گونه شکل می بندد. هر جا دیر زمانی بنشینی سنت ها سبز می شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8729,'شکستنی ای؟ پس از دسترس کودکان دور بمان! زندگی، کودک شکستن است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8730,'سطحی زرین باش، اشیاء خود را با نوشته ای زرین بر تو می نگارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8731,'زندان جایی بس امن است برای ناآرامان! چه آسوده به خواب می روند جانهای جانیان زنجیری! تنها وجدان داران از وجدان در عذابند!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8732,'شاعری که دانسته و خواسته می تواند دروغ بگوید، تنها قادر است از حقیقت دم بزند!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8733,'اریب می روند، بزرگان و رودخانه ها راهشان را، اما به سوی هدف خویش. نیکوترین جسارتشان، نهراسیدن از راههای اریب است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8734,'بپا! دهل زنِ سرنوشت ات مباش! از راه پر هیاهوی هر ناموری به در آ!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8735,'همه چیز فرایند شدن را از سر گذرانده است؛ واقعیات جاودانی وجود ندارند، همان گونه که حقایق مطلق وجود ندارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8736,'وجه مشترک جهان و هنرمند این است که هر روز اشیا برای آنها تازه و گیرا هستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8737,'در درون انسان راستین، کودکی پنهان است؛ کودکی که می‌خواهد بازی کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8738,'دشمنان خود را دوست بدارید، زیرا بهترین جنبه‌های شما را به نمایش می‌گذارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8739,'در گفتگوی درازدامنه تر، فرزانه ترین کس نیز یکباره دیوانه می شود و سه باره، نابخرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8740,'جاه طلبی آدمیزاد، تا آنجا که من می شناسم اش، نمی پرسد بار دیگر چگونه رفتار کنم که رخصت [ =اجازه ی ] سخن گفتن داشته باشم: او همیشه سخن می گوید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8741,'برای اینکه زیبایی رخسار، روشنی کلام، نیکی و استواری منش برجا بماند، سایه نیز همان اندازه ضروری است که نور.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8742,'همین که نور ناپدید می شود، پشت سرش سایه نیز غیب اش می زند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8743,'حقیقت مانندی [حقیقت نمایی] نه حقیقت؛ آزادی نمایی، نه آزادی؛ همین دو میوه اند که به واسطه ی آنها نمی توانیم درخت معرفت را با معرفت با درخت زندگانی یکی کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8744,'برای سنجش ارتفاع کوهها، دشواری صعود بر آنها نمی تواند در این میان سنجه ای [ =سنگ ترازویی ] باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8745,'همانا هرکس خود را در آنجایی بیش از همه آزاد می انگارد که بیش از همه احساس زیستن می کند. به تعبیر دیگر، گاهی در شیفتگی، گاهی در وظیفه، گاهی در شناخت و گاهی در خوشباشی و هوس.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8746,'خوب است موضوعی را بی درنگ دوگانه بیان کنیم و به آن، یک پای راست و یک پای چپ بدهیم. بر یک پا حقیقت می تواند بایستد؛ اما با پای دیگر خواهد رفت و گردِ جهان خواهد گشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8747,'غارتگر و حاکم مقتدری که به گروهی وعده می دهد آنها را از غارتگران در امان بدارد، از بنیاد موجوداتی همسان هستند، فقط دومی به روشی متفاوت [ =دیگرگون ] از اولی به سود خود دست می یابد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8748,'اخلاق بازرگان تماماً چیزی جز زیرکی اخلاق دزد دریایی نیست: تا آنجا که ممکن است ارزان خریدن، کمابیش رایگان خریدن و تا آنجا که ممکن است گران فروختن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8749,'سکه ی یک فرانکی در دستِ وارثی رنجدیده، مزدور، بازرگان و دانشجو اشیایی کاملاً متفاوت [ =دیگرگون ] از هم هستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8750,'در دنیای عظیم پول، سکه ی تن پرورترین دارا، سودآورتر از سکه ی مرد بینوا و کارگر است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8751,'شادمانی شریرانه از بدبیاری همسایه، گستاخانه ترین بیان پیروزی و بازتولید همسان سازی [برابری] است؛ حتی در درون والاترین نظام جهان.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8752,'بیشتر بزهکاران چنان به سراغ کیفرهایشان می روند که مادران به سراغ کودکانشان. صدها بار بزه کرده اند، بی آنکه عواقب ناگوار آن را احساس کنند. به ناگهان کشفی روی می دهد و پشت سرِ آن کیفر از راه می رسد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8753,'به هر جا که به راستی دادگری راه یافته و به مرور زمان در آن جا نهادینه شده باشد، رشک ورزی به وجود می آید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8754,'تمامی واژه ها و عبارت ها، واقعیت هایی هستند که گاهی آن مفهوم و گاهی مفاهیم بسیار، به یکیاره در آن چپانده شده است!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8755,'آدمی می خواهد [ بر آسیب رسان ] رنج و اندوه ببارد. به عکس، اندک نشانی از ایمن کردن خویش از زیان های بعدی، در منظر انتقامجو دیده نمی شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8756,'آزادترین و ظریف ترین شعور معمولاً تا بدانجا قد نمی دهد که میان دو امر، آنی را برگزیند که در گزینش آن به ناچار سود بیشتری باشد. ما در چنین مواردی، برمی گزینیم فقط از آن رو که ناگزیر به گزینش هستیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8757,'هر گدایی حیله گر می شود؛ همانند هر کسی که از سر کمبود یا نیازمندی (چه شخصی و چه عمومی) پیشه ای را در پیش می گیرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8758,'آدمی نباید در خطاهایش چنان بدمد که مبدل به شومناکی ابدی شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8759,'گزش وجدان همانند گاز گرفتن سگی است از سنگی و چیزی جز نابخردی نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8760,'اخلاق پیش از هر چیز ابزاری است که اصولاً جامعه را پاس می دارد و آن را از زوال باز می دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8761,'بر حذر باش که آرامش و تامل تو به آرامش و تامل سگی در مقابل دکان قصابی همانند نباشد که از ترس یارا نمی کند پیش رود و حرص و آز پای پس رفتن او را بسته است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8762,'کسی که بر آن است که از همه ی نیکی ها بهره ای برای خود برگیرد، باید در پاره ای اوقات خُرد بودن را درک کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8763,'محتوای وجدانمان درست همان چیزهایی است که از دوران کودکی آنهایی که بزرگشان می داشتیم یا از ایشان می ترسیدیم، بی دلیل و پیاپی از ما می خواستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8764,'انسانی که دردهایش را پشت سر گذاشته باشد، در حکم دارنده، به قلمرو بارآورترین خاک پا نهاده است؛ همچون استعمارگری که بر جنگلها و آبگیرها فرمانروا می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8765,'چیرگی فقط ابزار است، نه هدف.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8766,'همه انسانهای به اصطلاح عملگرا گونه ای مهارت در خدمت گزاری دارند؛ همان چیزی که آنها را عملگرا می سازد، چه برای دیگری و چه برای خود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8767,'هر واژه، یک پیشداوری است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8768,'بیش از همه، ظریفترین و کاری ترین حیوانات آماده ی رنج و سختی هستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8769,'می توانیم هنوز هم به وجود آمدن اخلاق را در رفتارهایمان با حیوانات مشاهده کنیم. آنجا که سود و زیان به چشم نمی آید، احساس بی مسئولیتی کامل داریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8770,'ما چنان کج رفتاریم که حتی برخوردهایمان با گلها و حیوانات کوچک کمابیش همیشه قاتل گونه است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8771,'در میان آدمیان هیچ عامی گرایی ای بزرگتر از مرگ نیست؛ زاده شدن در این مرتبه مقام دوم را دارد، زیرا همه ی آنها که می میرند، زاده نمی شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8772,'آدمیان به مراتب به تصاویر اندیشه ی خود بسیار وابسته تر هستند تا به محبوبترین دلدارشان.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8773,'نیک تر نوشتن همزمان به معنای نیک تر اندیشیدن نیز هست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8774,'هرگز آنچه را همه چیز دانان مغرور و آشفته سران می نویسند، نخوانید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8775,'برخی نویسندگان نه جان اند و نه شراب، اما جان شراب اند؛ آنها می توانند به کام شعله روند و سپس گرما دهند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8776,'سبک نوشتار بر آن است که با کمترین ابزار، خواننده را به همان دریافتی برساند که سبک گفتار می رساند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8777,'اگر بدکاران به راستی می دانستند، چه می کردند، ما را نیز تنها آن زمان حق بخشودن می بود که حق اتهام بستن و کیفر نیز می داشتیم؛ البته که چنین حقی نداریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8778,'آن که چهار حس ظریف تر هنر را ندارد، می کوشد همه چیز را با زمخت ترین حس پنجم خود دریابد؛ این است حس دراماتیک!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8779,'با خود عهد بسته ام [ آثار ] نویسنده ای را نخوانم که از آثارش پی ببرند بر آن بوده کتابی فراهم آورد، بلکه فقط [ آثار ] کسی را بخوانم که اندیشه هایش به ناگهان به صورت کتاب درآمده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8780,'پیمان ها و قراردادها [ی هنری] همانا ابزارهای هنری غالب برای دریافت شنوندگان هستند، زبانی مشترک و فراگرفته با مشقت بسیار، که با یاری آن هنرمند می تواند به راستی خود را به کسی که با او سخن می گوید بشناساند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8781,'کسی که رنج هایش را بر صفحه ی کاغذ آورد، نویسنده ای اندوهگین می شود: اما وقتی به ما بگوید از چه رنج می بُرد و چرا اکنون در شادمانی آرمیده، نویسنده ای جدی به شمار می رود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8782,'دیگر نخوانید کتابی را که در یک زمان زاده و (با مرکب) تعمید داده شد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8783,'ارتقاء (بهسازی) سبک، بهسازی اندیشه هاست و جز این هیچ نیست! آنکه بی درنگ به این امر اقرار نکند، هیچگاه از آن مجاب [ = پاسخ داده شده ] نخواهد شد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8784,'ضعیف ترین وجه هر کتاب کلاسیک در این است که به گونه ای ژرف به زبان مادری نویسنده اش نوشنه شده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8785,'کتاب باید در اشتیاق کلک [ =نی ]، مرکب و میز تحریر باشد، اما معمولاً کلک [ =نی ]، مرکب و میز تحریر مشتاق کتاب اند. از این رو اکنون چنین کم مایه اند کتابها.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8786,'مخاطب وقتی در یک تابلو نقاشی تامل می کند، در همان حال شاعر می شود و وقتی یک شعر را با دقت بررسی می کند، پژوهشگر.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8787,'اگر بخواهیم دریافت حسی بیشتری از آنچه به راستی داریم نسبت به چیزی نشان دهیم، سبک، در زبان و در همه پیشه های آن تباهی می گیرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8788,'اگر مقایسه های بی پروا دلیل سبک سری ( خواست کینه توزانه ) نویسنده نباشند، پس به یقین، دلیل خیال خسته و درمانده ی او هستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8789,'بی اعتمادی، سنگ محکی برای یقین پیدا کردن از وجود طلاست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8790,'برای کسی که نمی بیند بر صحنه چه رخ می دهد، موسیقی دراماتیک چیزی نابخردانه است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8791,'حقیقت نیازمند نقد است نه ستایش.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8792,'انسان موجود عجیبی است! اگر به او بگویید در آسمان خدا یکصد میلیارد و نهصد و نود و نه ستاره وجود دارد، بی چون و چرا می پذیرد. اما اگر در پارکی ببیند روی نیمکتی نوشته اند رنگی نشوید، بی درنگ انگشت خود را به نیمکت می کشد تا مطمئن شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8793,'استعداد، آدمی را می پوشاند و وقتی استعدادش رو به کاستی نهاد، آنچه هست نمایان می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8794,'اکنون به شما می گویم که مرا گم کنید و خود را بیابید و تنها آنگاه که همگی مرا انکار کردید، نزد شما باز خواهم آمد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8795,'از خردمندان می خواهم که به دنبال حقیقت نروند، چون حقیقت نیاز به پشتیبان ندارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8796,'از آنچه با عظمت است یا باید هیچ نگفت یا با عظمت سخن گفت و با عظمت سخن گفتن یعنی دور بودن از آرایش و آلایش.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8797,'بدترین خوانندگان کتاب، کسانی هستند که چون سربازان غارتگر عمل می کنند؛ یعنی از هر جا که دستشان برسد، تکه ای بر می دارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8798,'انسان های آزاده، دل شکسته و پر غرور، خود را از تیررس نگاه دیگران پنهان می کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8799,'آن کس که نمی تواند فرمان دهد بایستی فرمان ببرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8800,'آنچه آدمی را والا می سازد، مدت زمان احساس های والا در اوست، نه شدت آن احساس ها.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8801,'بشر برای فرار از خدا، طبیعت را عامل همه چیز می داند و در طبیعت، قانونی در کار نیست، بلکه پدیده های طبیعی به دلیل قدرت به وجود می آیند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8802,'با رنج عمیق درون، آدمی از دیگران جدا می شود و سرافراز می گردد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8803,'باید در محدوده امکانات زمینی بیافرینیم و در آفرینش به زمین وفادار بمانیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8804,'اراده زندگی برتر و نیرومندتر در مفهوم ناچیز نبرد برای زندگی نیست، بلکه در اراده جنگ، اراده قدرت و اراده مافوق قدرت است!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8805,'آدمی به دلیل نیاز به مراقبت و کمک دیگران با آنها ارتباط برقرار می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8806,'ارزش یابی هر چیز بستگی به توان خواهی آن دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8807,'جهان تنها همین دنیای ظاهر است و دنیای حقیقی، دروغی بیش نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8808,'آرزو ریشه حیات ماست. اگرچه این ریشه بتدریج حیات ما را می سوزاند، اما همین ریشه مایه زندگی است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8809,'شاید من بهتر می دانم که چرا بشر تنها حیوانی است که می خندد؛ تنها انسان است که به شدت رنج می برد و ناگزیر است خنده را بیافریند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8810,'انسان چیزی است که بر او چیره می باید شد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8811,'آرامش همیشگی نیز کسل کننده است؛ گاهی طوفان هم لازم است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8812,'آرامترین کلماتند که طوفانی را با خود به همراه می آورند. افکاری که با پای کبوتران پیش می آیند، جهان را تسخیر می کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8813,'از ابر انسان است که انسان های برتر، دلگرمی و شجاعت می گیرند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8814,'آدمهای حقیر، انسانهای والا را دیوانه می پندارند. چرا که این انسانها سرشت نامعقول تری داشته و به سمت چیزهای استثنایی جذب می شوند؛ چیزهایی که هیچ جذابیتی برای بسیاری از مردم ندارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8815,'حقیقت مانند آب دریا است؛ چون نمک آب دریا زیاد است، تشنگی را رفع نمی کند. اگر حقیقت آدمی تحریف شود، مثل آب شور دریا خواهد بود که تشنگی اش را رفع نخواهد کرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8816,'زمانی قلبا به او (دوست) نزدیکتری که در برابر او مقاومت و ایستادگی کنی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8817,'ارزیابی یعنی آفرینش.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8818,'هیچ انگاری، آرمانی متعلق به بالاترین درجه قدرتمندی روح است و سرشارترین زندگی، گاهی ویرانگر و گاهی ریشخندآمیز است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8819,'انسان باید بهتر و شریرتر شود، من چنین می آموزانم! شریرانه ترین چیز برای بهترین چیز در ابر انسان نیاز است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8820,'از آنجا که جهان حقیقی وجود ندارد، به ضرورت هر باور و هر فرضی بر درستی، نادرست است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8821,'نمی توان از همساز بودن با طبیعت، یک اصل اخلاقی برای خود ساخت؛ باید بر فریب حواس خود پیروز شویم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8822,'علم، جهان را توضیح نمی دهد، بلکه تفسیر می کند و در حقیقت، معنایی برای وجود نباید در نظر گرفت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8823,'ذهن و اندیشه مسئول به خطا افتادن بشر است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8824,'فیلسوف باید از ایمان به زبان فراتر رود، زیرا مفاهیم در چارچوب زبان اسیر می شوند و نمی توان آنها را به طور کامل با زبان بیان کرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8825,'کسی که جنگجو است، باید همواره در حال جنگ باشد؛ چون در زمان صلح با خودش درگیر خواهد شد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8826,'کسی که آرمان نداشته باشد، کمتر بی بند و بار است تا کسی که راه رسیدن به آرمانش را نمی داند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8827,'نسبت به فرد پایین تر از خود نفرت نداریم، بلکه از فرد برابر با خود یا بهتر از خود متنفریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8828,'هر اخلاق و دستور اخلاقی، طبیعت بردگی و حماقت را پرورش می دهد؛ زیرا روح را با انضباط تحمیلی خود خفه و نابود می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8829,'کسانی که مردم از آنها به صاحبان اخلاق یاد می کنند، اگر ما اشتباهاتشان را ببینیم از ما به بدی یاد خواهند کرد؛ حتی اگر دوست ما باشند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8830,'پاکی نفس، جدایی می آورد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8831,'من می خواهم"من دوست می دارم.',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8832,'بی نیازی ارزان است، بخت بهایی ندارد؛ پس به جای نشستن بر طلا بر سرین خویش می نشینیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8833,'مرگ، خود، بهترین دلیل آسمانی بودن انسان است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8834,'هنری جدی است خندیدن؛ باید که فردا نکوتر به انجامش رسانم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8835,'سر که شوخ طبعی را به چیزی نگیرد، در دل، اخگری نمی افروزد!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8836,'خوش دارم هوشیار باشم نه به بانگی بیگانه.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8837,'فضیلت های ما نیز باید آرام بخرامند؛ چون شعرهای هومر، که سبک می خرامند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8838,'تندرست آن کسی است که فراموش کرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8839,'بر دشت های هموار درنگ مکن! بسیار بلندتر از آن نیز مرو! از نیم فراز زیباتر به دیده می آید، جهان!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8840,'نور دورترین ستارگان، دیرتر از همه به انسان می رسد و تا زمانی که نرسیده است، انسان انکار می کند که در آنجا ستارگانی وجود دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8841,'آدمی با رنج عمیق درونی از دیگران جدا می شود و والا می گردد. انسان های آزاده، دلِ شکسته و پر غرور خود را پنهان می کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8842,'فرد خلوت نشین می گوید که حقیقت در کتابها نیست و فیلسوف آن را پنهان می کند. فرد والا از فهمیده شدن توسط دیگران در هراس است نه از بد فهمیده شدن؛ چون می داند کسانی که او را بفهمند به سرنوشت او یعنی رنج کشیدن در دنیا دچار خواهند شد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8843,'لذت بیرحمی در دیدن رنج دیگران است، اما فردی که بیرحم است این بیرحمی گریبانگیر خودش هم می شود و به ایشان نیز آزار خواهد رسید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8844,'باید در تضادهای دوگانه شک کرد. از کجا معلوم که این تضادهای دوگانه وابسته به هم و یکی نباشند؟ در فلسفه، معین، ارزشی بیشتر از نامعین دارد؛ همان گونه که ارزش نمود کمتر از حقیقت است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8845,'نمی توان گفت آنچه برای یک نفر سزاوار است برای فرد دیگر هم سزاوار می باشد. برای مثال، انکار نفس و افتادگی سزاوار یک فرمانده نیست و برایش فضیلت محسوب نمی شود. حکم یکسان صادر کردن برای همه غیر اخلاقی است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8846,'کسانی که در خود احساس حقارت می کنند به دیگران رحم می کنند، اما به دلیل غرورشان دم نمی زنند! یعنی درد می کشند و می خواهند با دیگران همدردی کنند. کسانی که با دیگران همدردی می کنند به دلیل دردمند بودن خودشان است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8847,'اندیشه های ما، آری و نه گفتن های ما و اگر و اما گفتن های ما، همه با همان ضرورتی از درون ما رشد می کنند که میوه از دل درخت؛ به هم مربوط و با هم خویشاوندند و همه از یک اراده، یک وضع جسمانی، یک خاک و یک خورشید نشان دارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8848,'اجحاف نکردن و آسیب نرساندن به دیگران برای رسیدن به برابری، ریشه و بنیاد جامعه است، اما این خواست نفی زندگی است؛ چون زندگی بهره کشیدن از دیگران است که ناتوان ترند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8849,'جنبش تساوی خواهی زنان موجب می گردد که زن از زنانه ترین غرایز خود دور شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8850,'بشر را مشتاق زندگی ساده و همراه با ریاکاری اخلاق گرایانه می بینم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8851,'بشر به دلیل کسب قدرت به دنبال شناخت است، نه به دلیل تشنگی عقل ناب.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8852,'براستی؛ ای کاش جنون شان را حقیقت نام بود، یا وفاداری و یا عدالت؛ اما دریغ که فضیلت شان در خدمت دراز زیستن و آسودگی نکبت بار است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8853,'انسان باید والاترین آرمانهایش را دنبال نموده و هر لحظه به آنها عمل کند، چرا که آنچه شخص اینک انجام می دهد، بازگشتی مکرر در سراسر ابدیت خواهد داشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8854,'اگر دانش ما تا این اندازه به بی طرف بودن خود می نازد، از آن رو است که جز کنجکاوی ناسالمِ ناتوانان چیز دیگری ندارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8855,'افزونی دانش گذشته و افزونی تاریخ، انسان را پژمرده و بزدل خواهد ساخت. در حالی که انسان باید قادر باشد گذشته را به خدمت حال دربیاورد. البته اگر بتوانیم خوب یاد بگیریم که تاریخ را وسیله ای برای زندگانی قرار دهیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8856,'کمابیش هر چیزی که وجود دارد در معرض نابودی است؛ زندگی، خود چیزی جز ستیزه و جدال ارزشها و مبارزه برای نابودی اندیشه ها و آرمانها نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8857,'خودستایی مایل است که به وسیله ی شما اعتماد به خود را بیاموزد. او از نگاههای شما تغذیه می کند و در دستهای شما تعریف و تمجید نسبت به خود را می بلعد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8858,'در مدارس، اندک نشانی از آموزش اندیشه کردن نمی توان یافت؛ حتی در دانشگاه ها؛ حتی در میان شناخته ترین فیلسوفان؛ آری می بینم که منطق به عنوان یک نظریه، کار و تکنیک دارد به سستی می گراید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8859,'با هر که بدی کردی تا دم مرگ از او بترس.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8860,'شاد ماندن به هنگامی که انسان در گیرودار کارهای ملال آور و پر مسئولیت است، هنر کوچکی نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8861,'باری؛ بدترین چیز خرد اندیشی است. به راستی؛ شرارت به که خرداندیشی!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8862,'این تاج مرد خندان، این تاج گل سرخ را من خود بر سر نهادم. من خود خنده ی خویش را مقدس خواندم. بهر چنین کاری هیچ کس دیگر را امروز چندان که باید نیرومند نیافته ام.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8863,'باور چیست؟ از کجا سرچشمه می گیرد؟ هر باور، چیزی را حقیقی انگاشتن است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8864,'حتی از میان دلیرترین کسان مان، چه اندک اند آنان که دانایی خویش را تاب توانند آورد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8865,'آیا می کوشی که ده بار یا صد بار دو چندان شوی؟ نکند بَهرِ خود مریدان بسیار می جویی؟ پس می باید ظرفهای تهی ( هیچ ) را بجویی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8866,'خسته دلان خورشید را سرزنش می کنند، برای آنها ارزش درخت به سایه است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8867,'آن کس که همیشه شاگرد باقی می ماند، زحمت های آموزگارش را به خوبی جبران نمی کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8868,'ما در رویدادهای زندگی خود نقشی نداریم، اما در اینکه چگونه آنها را تعبیر کنیم تاثیرگذار هستیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8869,'اگر زمانی دراز به اعماق نگاه کنی، آنگاه اعماق هم به درون تو نظر می اندازند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8870,'آرزو کردن چه قدر فرحبخش است، اما وقتی به آرزوی خود رسیدیم، شادی از درون ما رخت برمی بندد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8871,'برای گله ی انسانی تمام نشانه های ابرانسان، چون نشانه های بیماری یا دیوانگی پدیدار می شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8872,'شاعران همه باور دارند که هر گاه کسی بر چمن یا بر دامنه ای خلوت دراز بکشد و گوش کند، از آن چیزهایی که در میان زمین و آسمان است چیزی دستگیرش نخواهد شد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8873,'ارزشها و تغییرات آن به افزایش قدرت کسانی که ارزشها را مقرر می دارند؛ مربوط می شوند. میزان ناباوری؛ میزان آزادی اندیشه ی مجاز بیانی از افزایش قدرت است. هیچ انگاری، آرمانی متعلق به بالاترین درجه ی قدرتمندی روح است و سرشارترین زندگی، گاهی ویرانگر و گاهی ریشخند آمیز.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8874,'آموزه ای پدید آمد و باوری در کنارش: همه چیز پوچ است؛ همه چیز یکسان؛ همه چیز رو به پایان!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8875,'آن کس که نداند خواست خویش را در چیزها چگونه بگمارد؛ دست کم معنایی بر آن خواهد افزود ... و از آن، این پندار زاده می شود که گاهی در آن خواست بنیاد باوری بوده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8876,'آیا برده هستی؟ پس دوست نتوانی بود، آیا خودکامه هستی؟ پس دوستی نتوانی داشت. دیر زمانی است که بردگی و خودکامگی در زن نهان گشته اند، از این رو زن را توانِ دوستی نیست؛ او عشق را می شناسد و بس.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8877,'افراطی ترین صورت هیچ انگاری می تواند این دیدگاه باشد که همه باورها؛ همه چیزی را حقیقی انگاشتن ها، لزوماً به خطا می روند؛ به این علت ساده که هیچ دنیای حقیقی در کار نیست. چنین است یک ظاهر دورنمایی که از درون ما سرچشمه گرفته است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8878,'آزادی ابر انسان تنها در صورتی حقیقی است که در زمان فراشد [ =فرا رفتن ] و در ساختن آینده ای بر پایه ی گذشته عملی شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8879,'بینوایان دارویی جز امید ندارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8880,'یک ساعت کوهنوردی، از یک قدیس و یک ابلیس، دو برادر می سازد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8881,'انسان ستمکارترین حیوان است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8882,'در دنیا زندگیهای تلخ و مرارت باری هم هست که مرگ برای صاحبانش سعادت است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8883,'هزار تلاش انسان به اندازه ی یک تقدیر کارساز نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8884,'فرزانه ترین انسانها برای آسایش خاطر من چنین گفته اند: دریغا کاش امروز نیز چنین می بود! زیرا شر بهترین نیروی انسان است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8885,'مردم دنیا دو دسته اند: یکی زیر دستان و دیگر، بزرگان اصالت. شرف متعلق به بزرگان است و آنها غایت وجود بوده و زیردستان، ابزار اجرای هدفهای ایشان هستند. پیشرفت دنیا و بسط زندگی انسان به وسیله ی بزرگان و سرداران صورت می پذیرد که اندک شمارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8886,'آیا بهتر نیست که انسان گرفتار یک قاتل و جانی شود تا اینکه در رویاهای یک زن شهوت پرست فرو رود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8887,'هر چقدر اوج بگیری از نظر مردمی که پرواز را نمی فهمند کوچکتر به نظر می رسی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8888,'هنگامی که قصد انجام کاری را کردید، باید درهای تردید را به طور کامل ببندید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8889,'مردم خودشان را با هر چیز خسته می کنند، مگر با فهم و اندیشه.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8890,'بشر امروز در پرستش بتان می زید؛ بتان عرصه ی اخلاق، بتان گستره ی سیاست، بتان عرصه ی فلسفه. خدایانی کاملاً باطل که خود آنها را بر ساخته آنگاه پرستیده اند. از این روی از راه راست بیراه گشته و همواره بر این باور پا می فشرده و از آن چنان جانبداری کرده تا اینکه به فرجامش رسیده اند؛ جایگاهی که از همان ابتدا به نیستی و فنا چشم داشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8891,'همو که با اهریمنان به نبرد برمی خیزد باید بر حذر باشد که در این فرآیند خود به اهریمنی تبدیل نشود. در هنگامه ای که تو به خاک می نگری، خاک نیز به تو می نگرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8892,'عشق، خطری است در کمین تنهاترین کس، عشق به هر چیزی که زنده باشد و بس! براستی؛ خنده آور است جنون و فروتنی من در عشق!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8893,'جنگ، قانون ابدی زندگی است و صلح، آسایش میان دو جنگ است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8894,'با آدمیان زیستن دشوار است، زیرا خاموش ماندن بسی دشوارتر است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8895,'اگر بگویند که من انسانم این پیشداوری است. اما در میان انسانها نیز زندگی کرده ام و با هرچه که انسانها احساس می کنند آشنا هستم، از چیزهای کوچک تا بزرگ.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8896,'از زمانی که جستجو مرا به ستوه آورد، یافتن آموختم و از زمانی که باد از من روی گرداند، در همه ی بادها بادبان می گشایم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8897,'بدترین آموزگار آن است که شاگردانش تا ابد شاگرد باقی بمانند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8898,'بین مردمان کوچک، دروغگویی فراوان است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8899,'چیزی که ویرانم کند، نیرومندترم خواهد ساخت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8900,'بسیاری از افراد در پیگیری راهی که برگزیده اند، لجاجت و یکدندگی می کنند و تنها تعداد بسیار اندکی در پی هدف هستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8901,'انسان برتر از ابرانسان بسیار دور است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8902,'در حقیقت، غیبت تجلی رشک و نفرت فرو خورده ی گروههای محروم اجتماعی است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8903,'زمین روزی از آن ابر انسان می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8904,'پریشانی من از این نیست که به من دروغ گفته ای، از این آشفته ام که دیگر نمی توانم تو را باور کنم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8905,'بشر امروز در پرستش بتان می زید، بتان عرصه ی اخلاق، بتان گستره ی سیاست، بتان عرصه ی فلسفه. خدایانی کاملاً باطل که خود آنها را بر ساخته آنگاه پرستیده اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8906,'آسودگی، مادر هر روانشناسی است؛ آنگاه آیا هر روانشناسی تباهی است؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8907,'تکبر، آسانترین راه برای از بین بردن سرافرازی ها است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8908,'بطن هستی با انسان جز به صورتِ انسان سخن نمی گوید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8909,'سیاستمدار انسانها را به دو دسته تقسیم می کند: ابزار و دشمن؛ یعنی تنها یک طبقه را می شناسند و آن هم دشمن است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8910,'برادران آیا من سنگدلم؟ باری من می گویم هرچه را که افتادنی است می باید بیشتر زور داد! هر چه امروزین است می افتد و بر می افتد! چه کسی می خواهد آن را نگاه دارد؟ باری من می خواهم آن را بیشتر زور دهم!... به آن کس که پرواز نمی آموزید تندتر افتادن آموزید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8911,'یک فرد رمانتیک، هنرمندی است که ناخشنودی والای او نسبت به خودش، وی را آفرینش گر می سازد؛ کسی که از خودش و جهانش به دور می نگرد؛ به پشت سر می نگرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8912,'مبادا شما را چنین در نظر آید که در کارهایتان دون پایه و خوارید؛ بلکه چنین سزد که آن را به زمان رخدادنش واگذارید! ...که نکوهش وجدان، خاموش گردانیدن اندرون بایسته نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8913,'درست است که ما عاشق زندگی هستیم؛ اما نه از آن رو که بدان خو کرده ایم؛ بل از آن رو که خو کرده ی عشقیم. عشق هیچ گاه بی بهره از جنون نیست، اما جنون نیز هیچ گاه بی بهره از خرد نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8914,'نیرومند، پیروزمند است؛ این هنجاری جهان گستر است. از حق و باطل به معنای عام حرف زدن، سخن پوچ است. هیچ خشونت، ناموس ربایی، بهره کشی یا ویرانگری در ذات ستمگرانه نیست، چرا که زندگی در ذات خشونت بار و ناموس ربا و بهره کش و ویرانگر است و جز آن گمان پذیر نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8915,'دوست می دارم آن را که چون تاس به سودش افتد؛ شرمسار شود و پرسد: نکند قماربازی فریبکار باشم؟ زیرا که خواهان فناست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8916,'دوست می دارم آن را که فضایل بسیار نمی خواهد. زیرا که یک فضیلت به است از دو فضیلت؛ زیرا که یک فضیلت، چنبری استوارتر برای در آویختن سرنوشت است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8917,'خود را جایی درگیر کن که فضیلتِ دروغین به کار نیاید، چنان جایی که آدمی در آن، همچون بندباز بر رويِ بند، یا می افتد یا سرِ پا می ماند، یا راه به بیرون می برد... .((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8918,'اگر دشمن دارید، بدی او را با خوبی پاسخ ندهید؛ زیرا این امر موجب شرمساری او می گردد، بلکه به او وانمود کنید که با این عمل خود به شما خدمت کرده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8919,'بشر حق انتخاب ندارد، چون محکوم و مجبور به سرنوشت است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8920,'پیشداوری درباره اخلاق به این معناست که نیت اعمال را منشاء آنها می دانیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8921,'جرم این است که ندانیم زندگی خیلی ساده تر از اینهاست که ما فکر می کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8922,'تکبر، زاییده قدرت مادی است و فروتنی، زاییده ضعف معنوی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8923,'خیر نباید همگانی باشد، وگرنه دیگر خیر نیست؛ زیرا چیزهای همگانی ارزشی ندارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8924,'باید بر فریب حواس خود پیروز شویم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8925,'جهان هیولای انرژی است که آغاز و پایان ندارد و تنها خود را دگرگون می سازد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8926,'بدترین پاداش یک استاد این است که دانشجویانش تا ابد شاگرد وی باقی بمانند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8927,'آینده از آن کسانی است که به استقبالش می روند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8928,'بلندپروازی من آن است که در ده جمله چیزی را بگویم که کسی دیگر در یک کتاب می گوید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8929,'کسانی که دلایل منطقی را به شهود متصل می کنند راه به خطا رفته اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8930,'گونه ای از بی گناهی در شگفت زدگی هم وجود دارد و کسی دستخوش آن می شود که فکر نکرده باشد او نیز ممکن است شگفت زده شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8931,'انزجار از کثیفی ممکن است چنان شدید باشد که ما خود را پاک نکنیم، یعنی توجیه نکنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8932,'شهوت اغلب چنان روییدن گیاه را تسریع می کند که ریشه ضعیف می ماند و به آسانی می توان آن را از زمین بیرون کشید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8933,'شادکامی از ستایش در برخی از افراد، گونه ای از احترام قلبی و در حقیقت اقدامی بر ضد خودپسندی عقل است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8934,'آن کس که شادمانه گام بر هیزم گردآورده برای سوزاندنش می نهد، بر درد غلبه نمی کند، بلکه بر احساس درد در جایی چیره می شود که انتظارش می رود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8935,'اگر ناگزیر شویم نظر خود را در مورد کسی تغییر دهیم، به خاطر این زحمتی که فراهم کرده است، انتقام سختی از او خواهیم گرفت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8936,'حقیقتی که تو قصد آموختن آن را داری، هر چه انتزاعی تر باشد، باید حواس خود را بیشتر جلب آن کنی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8937,'مرد و زن در باب یکدیگر اشتباه می کنند و در نتیجه به خویشتن احترام می گذارند و عشق می ورزند (یا درست تر آن است که بگوییم به آرمان خویش عشق می ورزند). از این رو، مرد به دنبال زنی آرام است، اما زن درست بسان گربه ای که خوب حفظ ظاهر آرام را تمرین کرده است، بس ناآرام است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8938,'ما را برای فضیلت ها بیشتر کیفر می دهند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8939,'آن کس که نتواند راه دستیابی به آرمان خویش را بیابد، راحت تر و گستاخانه تر از کسی زندگی می کند که آرمانی ندارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8940,'تمام امور باور کردنی، وجدان راحت و ظاهر حقیقت از راه حواس پدید می آید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8941,'یکی به دنبال قابله ای برای زایش اندیشه هایش است و دیگری به دنبال کسی که به او یاری رساند و به این ترتیب، گفتگویی خوب پدید می آید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8942,'تنها به آنهایی که دردشان را به طور کامل درک می کنی، کمک کن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8943,'واقعیت غیر الهی هرگز زیبایی را به ما نمی دهد یا تنها یکبار به ما نشان می دهد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8944,'در آمد و شد با دانشمندان و هنرمندان به آسانی مرتکب خطا می شویم، زیرا در پس وجود دانشمندانی جالب توجه، اغلب انسانی میان مایه را می یابیم و در پشت هنرمند میان مایه، اغلب انسانی بسیار جالب توجه.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8945,'ما در خواب و رویا انسانی را می آفرینیم و به او جان می دهیم و بعد هم در آمد و شد با او، این موضوع را از یاد می بریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8946,'اگر بند گسسته نشد، نخست باید آن را با دندان گشود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8947,'مهمترین سخن تربیتی که شنیده ام: « در عشق حقیقی، روح، جسم را در آغوش می کشد. »((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8948,'آن کس که با هیولاها می جنگد، باید مراقب باشد که خود به هیولا بدل نشود. اگر مدتی طولانی به پرتگاهی بنگری، پرتگاه نیز به تو چشم می دوزد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8949,'برای دستیابی به هر جهان متعالی باید پرورده شده باشیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8950,'خوشا به حال فراموشکاران؛ زیرا آنان از پس حماقت های خویش نیز برمی آیند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8951,'گفتن چنین عبارتی که «هر آنچه حق یکی باشد، حق دیگران هم هست»، کاری غیر اخلاقی است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8952,'خودپسندی ما از آنچه به بهترین نحو انجام می دهیم، می طلبد که آن را سخت ترین کار بدانیم و این، سرمنشاء بسیاری از امور اخلاقی است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8953,'در باب مقایسه کلی زن و مرد می توان گفت که اگر زن غریزه ایفای نقش دوم را نداشت، هرگز در آرایش خود به این سان نبوغ نمی یافت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8954,'آنچه را زمانی بد می دانیم، معمولا پس مانده امری نابهنگام است که زمانی آن را نیک می دانسته ایم؛ نیاکان آرمانی کهن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8955,'آموختن ما را دگرگون می کند و همان کاری را انجام می دهد که تغذیه می کند؛ یعنی تنها ما را حفظ نمی کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8956,'کسی دوستدار حقیقی زنان است که امروزه به آنها بگوید : زنان در باب زنان سکوت کنند!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8957,'این نشان هوشیاری هر ملتی است که خویشتن را ژرف، ناشی، نیکخواه، صادق و به دور از هوشیاری بداند یا چنین وانمود کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8958,'هر قومی، دورویی خاص خود را دارد و آن را فضیلت می نامد؛ یعنی بهترین ویژگی خویش را که دارند، نمی شناسند و نمی توانند بشناسند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8959,'دوری از صدمه، زورگویی، چپاول متقابل و همسویی خواست های خود با دیگران ممکن است در مفهوم کلی بین افراد بدل به رسمی نیکو شود، به شرط آنکه شرایط آن فراهم باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8960,'بدیهی است که نام ارزش های اخلاقی را در همه جا ابتدا بر انسانها و بعدها بر رفتارها اطلاق کرده اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8961,'انسان والا در وجود خویش برای فرد قدرتمند و نیز آن کسی ارزش قایل است که قدرت چیرگی بر خویشتن را دارد و زمان سخن گفتن و لب فرو بستن را می شناسد، با علاقه بر خود سخت می گیرد و به سختگیری بسیار احترام می گذارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8962,'باور به خویشتن، افتخار به خویشتن و خصومت و مسخرگی نسبت به ایثار، از جمله بدیهی ترین ویژگی های اخلاق والاست، درست همانند کم ارزش دانستن و احتیاط نسبت به همدردی و دلی مهربان.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8963,'فرد خودپسند از هر نظر نیکی که درباره خود می شنود، خوشحال می شود (بدون توجه به سودمندی و یا اصولا درستی یا نادرستی آن) و از هر نظر بدی ناراحت می شود؛ زیرا خود را تسلیم هر دو می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8964,'آن کس که نمی خواهد بلندای انسانی را ببیند، با تیزبینی به امور پست و ظاهری در این انسان می نگرد و راز دل خویش را فاش می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8965,'هر بار که ساخت خانه خود را به پایان می رسانیم، می بینیم که ناخودآگاه، کاری را آموخته ایم که باید پیش تر می آموختیم؛ یعنی پیش از شروع به ساخت خانه.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8966,'توان خواهی می تواند بر گذشت زمان پیروز شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8967,'برای بشر بنیادی تر از بررسی حقیقت، جستجوی ارزش آن بوده است و متافیزیسین ها همگی به دنبال ارزش گذاری مفاهیم متضاد بوده اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8968,'اصولا زمانی پوچی برای چیزی معنا پیدا می کند که برای آن، هدفی تعریف کنیم و اگر بدانیم هدفی در کار نیست، به پوچی هم نخواهیم رسید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8969,'او مرا می ستاید، پس به من حق می دهد. این خريّت در نتیجه گیری، نیمی از زندگی ما گوشه نشینان را تباه می کند. زیرا خران را به همسایگی و دوستی با ما وا می دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8970,'باید بر چهار فضیلت خویش یعنی دلاوری، نگرش ژرف، همدردی و تنهایی چیره ماند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8971,'تنهایی از دیدگاه ما فضیلت، گرایش و اجباری متعالی به پاکی است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8972,'اینجا چشم انداز باز است و جان بر بلندا! اما انسانهایی خلاف این وضعیت هم هستند که آنها هم بر بلندی اند و چشم اندازی باز دارند، اما به پایین می نگرند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8973,'روح والا به خویشتن احترام می گذارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8974,'از نوشته های هر گوشه نشینی، بخشی از پژواک بیابان و نجواها و نگرش های هراسان به تنهایی شنیده می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8975,'هر اندیشمند ژرفی از سوءتفاهم در باب خویشتن بیشتر می هراسد تا فهمیده شدن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8976,'فیلسوف موجودی است که اغلب از خود می گریزد و می هراسد، اما کنجکاوتر از آن است که دیگر به حال خویش باز نگردد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8977,'سرخورده سخن می گوید: « تنها به پژواک سخن خویش گوش فرا داده بودم و ستایش ها را می شنیدم. »((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8978,'غریزه، خانه که می سوزد، حتی نهار را فراموش می کنیم. آری، اما بعد بر خاکسترها نهار را خواهیم خورد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8979,'کسی که می داند چرا زندگی می کند، چگونه زندگی کردن را هم می شناسد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8980,'باید از دگم گرایی ها در اندیشه ی فلسفی دوری کرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8981,'برای بشر بنیادی تر از بررسی حقیقت، جستجوی ارزش آن بوده است و متافیزیسین ها همگی به دنبال ارزش گذاری مفاهیم متضاد بوده اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8982,'نمی توان از همساز بودن با طبیعت، یک اصل اخلاقی برای خود ساخت. زیرا طبیعت بیرحم است و اگر آدمی بخواهد مطابق با طبیعت زندگی کند باید بیرحم باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8983,'فیلسوفی که درصدد آفرینش جهان بنا بر تصور خویش است می خواهد همه به فلسفه اش ایمان بیاورند و این، همان روا داشتن استبداد بر دیگران است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8984,'فلسفه همان خواست قدرت است؛ همان خواست علت نخستین.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8985,'قدرت خواهی بشر و نه میل به شناخت، اولین عامل برای گرایش او به فلسفه بوده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8986,'انسان نمی تواند از غرایز خود فرار کند؛ وقتی از خطر جانی دور شود دوباره به غرایزش روی می آورد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8987,'کسی که دلش را به بند بکشد جانش را آزاد کرده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8988,'مرد، خواهان حقیقت است، اما زن موجودی سطحی نگر می باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8989,'یک دانشمند حتی برای عشق زمینی هم وقت ندارد! او نه رهبر است نه فرمانبردار؛ او کمال بخش نیست، سرآغاز هم نیست؛ او فردی بی خویشتن است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8990,'همه به چیزی دلبستگی دارند و افراد والاتر به چیزهای والاتر، اما افراد فرومایه فکر می کنند که افراد والاتر به چیزی دلبستگی ندارند و ظاهربینيِ افراد فرومایه از سطحی نگری و ریاکاری آنهاست و بر پایه هیچ شناخت اخلاقی نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8991,'حرف کسانی که می گویند عشق به دور از خودخواهی است خنده دار است؛ زیرا همه چیز طبق خواست قدرت ما است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8992,'اختلاف طبقاتی از ضرورتهای جامعه است، چون عامل اشتیاق به پرورش حالت های والاتر، کمیاب تر، دورتر و عامل چیرگی بر نفس می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8993,'در هر دوره مردان خوب آنهایی هستند که افکار قدیمی خود را در عمق می کارند تا برایشان ثمر دهد؛ اینان کشت کاران روح هستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8994,'ما همه در درون خود کشتزارها و باغهایی پنهان داریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8995,'ما همگی آتشفشان فعالی هستیم که ساعت فورانی برای خود داریم، اما هیچ کس از زمان دقیق آن اطلاعی ندارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8996,'مردان استثنایی یک عصر، بیشتر فرزندان فرهنگهای قدیمی و نهالهای دیررس نیروهای گذشته اند. من در آنها میراث یک ملت و آداب و رسوم آن را می بینم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8997,'درد و رنج همیشه علت خود را جستجو می کند، در حالی که لذت به خود توجه دارد و به پشت سر خود نگاه نمی کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8998,'اینکه برای انجام خوبی یا بدی، فداکاریهایی انجام می دهیم ارزش نهایی اعمال ما را تغییر نخواهد داد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(8999,'برای سرشت های مغرور، طعمه آسان چیز تحقیر آمیزی است. آنان تنها در مقابل مردانی که هنوز مهار نشده اند و می توانند به دشمنان آنها مبدل شوند و یا در برابر ملکی که به سختی تسخیر می شود، احساس آسایش و خشنودی می کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9000,'انگیزه ها و نیاتی که در پس عادت پنهان شده اند همیشه بعداً و به محض اینکه کسی شروع به مبارزه با آن و بررسی دلایل و اهداف آن نموده، به دورغ ابداع شده اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9001,'اگر توجه کنیم که نیروی جوانان بی صبرانه در انتظار انفجار و آزاد شدن است، دیگر از اینکه تصمیم گیریهای آنان فاقد ظرافت کافی و بصیرت لازم است دچار تعجب نخواهیم شد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9002,'آنچه را که ما از خود می دانیم و در حافظه نگاه داشته ایم، کمتر از آنچه که فکر می کنیم، در خوشبختی زندگی ما تاثیر دارد. روزی فرا خواهد رسید که آنچه دیگران درباره ما می دانند (یا فکر می کنند که می دانند) در زندگی ما دخالت می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9003,'آنچه موجب بیشترین زحمت من شده و هنوز هم می شود این است که متوجه شده ام شناخت نام چیزها بسیار مهمتر از شناخت خود آنهاست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9004,'تمام زنها در زیاده روی و گزافه گویی در مورد ضعف خود مهارت دارند و با زبردستی خاصی ضعفهایی غیر واقعی برای خود عنوان می کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9005,'سلیقه بد هم مانند سلیقه خوب برای خود حقی دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9006,'این هنرمندان و به خصوص هنرمندان تئاتر بودند که اولین بار به انسانها چشم و گوش دادند تا هر کس آنچه هست، آنچه بر دیگران گذشته و آنچه می خواهد را با نوعی لذت ببیند و بشنود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9007,'شاعری را می شناسم که مانند بسیاری از کسان دیگر، جذابیت کمبودها و ضعفهایش نسبت به کارهایی که تمام می کند و به کمال می رساند بیشتر است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9008,'شور و هیجان طبیعی در زندگی بی اندازه خساست کلام نشان می دهد! گنگ و دستپاچه است! اگر کلماتی هم برای گفتن پیدا کند کلماتی مبهم و نابخردانه بوده و گویی بی اندازه از خود شرمنده است!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9009,'مردان برتر تنها با ظرافت فوق العاده زیادی که در دیدن و شنیدن دارند از دیگران متمایز می شوند و آنها جز به تعمق نمی بینند و نمی شنوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9010,'جهان در نظر آن کس که به مقام رفیع انسانی نایل گردد، غنی تر می شود؛ وسوسه و جذبه های حرص و ولع افزایش یافته و برای انسان دام می گسترند. همچنین، انواع گوناگون تحریکها، لذتها و دردها بیشتر می شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9011,'ما؛ که اهل تعمق و احساس هستیم، همین ما آن چیزی را که از قبل وجود نداشته تولید می کنیم و براستی از تولید آن باز نمی ایستیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9012,'اگر ما بخواهیم اندکی بدانیم چه چیزی نیستیم باید بدانیم چگونه خویشتن را برای مدتی فراموش کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9013,'ما جزو کسانی نیستیم که تنها در میان کتابها فکر می کنند و تراوشهای فکری آنها نیاز به محرکهای آثار چاپی دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9014,'ما نفی می کنیم و باید نفی کنیم؛ زیرا چیزی در ما می خواهد زندگی کند و خود را اثبات نماید؛ چیزی که ما هنوز آن را نمی شناسیم و نمی بینیم. این است آن چیزی که من در دفاع از انتقاد در نظر دارم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9015,'ما همیشه شجاع نیستیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9016,'بسیاری از ما هنگام ناتوانی این چنین ناله سر می دهیم که: نیازردن مردم چه دشوار است!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9017,'اگر نمی خواهیم چیزی را که رسوایی به بار می آورد در خود مخفی کنیم، زندگی در انزوا به چه درد می خورد؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9018,'آیا برای آنکه نسوزم، سرانجام شعله خود را فوت خواهم کرد؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9019,'حکمت درد کمتر از حکمت لذت نیست. درد نیز مانند لذت، یکی از نیروهای بنیادین بقای نوع است؛ زیرا اگر جز این بود، نیروی درد مدتها پیش از بین رفته بود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9020,'بعید است که بتوانیم کسی را تغییر دهیم و اگر زمانی در این تغییر کامیاب شدیم دلیل آن، بی آنکه خود از آن آگاه باشیم چیز دیگری بوده است؛ یعنی ما خود توسط آن شخص تغییر کرده ایم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9021,'بزرگترین نشانی که سرنوشت می تواند به ما هدیه کند این است که اجازه دهد ما مدتی در کنار مخالفان خود بجنگیم، زیرا از این راه پیروزی بزرگی را برای خود رقم می زنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9022,'زندگی می تواند برای آنهایی که در جستجوی دانش هستند یک تجربه باشد و نه یک وظیفه.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9023,'زندگی وسیله ای برای شناخت است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9024,'آن کس که در آغاز با مبارزه و پیروزی آشنایی نداشته باشد چگونه می تواند خوب بخندد و خوب زندگی کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9025,'هرگاه انسان رنجی عظیم را در خود ایجاد کند و فریاد آن را بشنود و از یأس و ناامیدی درونی نمیرد و جان بدر ببرد، عظمت یافته است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9026,'هرگاه این جانور ظریف، انسان، خوش اخلاقی خود را از دست بدهد می گویند که او جدی شده است و هرگاه که اندیشه را با خنده و شادی تلفیق کند می گویند این اندیشه بی ارزش است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9027,'ما همچون آدمهایی زندگی می کنیم که دائماً در هراسند که مبادا فرصت از دست برود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9028,'یکی از اصولی که به انهدام هر نوع فرهنگ و سلیقه متعالی می انجامد آن است که انجام هر کاری بهتر از این است که هیچ کار نکنی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9029,'اندیشمند نیاز به تایید یا تجلیل دیگران ندارد، به شرط آنکه او از تایید و تجلیل خود مطمئن باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9030,'در گذشته انسان می خواست شهرت پیدا کند، اما امروز این کار دیگر کافی نیست، زیرا بازار بیش از حد گسترده شده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9031,'اکنون باید با جار و جنجال چیزی را فروخت و در نتیجه، بهترین حنجره ها پاره می شوند تا بهترین کالاها با فریاد و سر و صدای خراشیده عرضه شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9032,'بخش بزرگ فعالیت فکری ما بی خبر از ما و ناآگاهانه رخ می دهد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9033,'در اعماق وجود ما قهرمانی نهفته است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9034,'عشق نیز باید آموخته شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9035,'هیچ کس به اندازه خودش برای خود غریبه نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9036,'زنده باد صداقت ما.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9037,'ماهیت احساس دلسوزی آن است که رنج دیگران را از حالت کاملاً خصوصی آن خارج می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9038,'ترسها، محرومیتها، فقر، نیمه شبهای جانکاه روح، ماجراها، مخاطره ها و اقدامهای نافرجام برای من و تو به همان اندازه ضروری هستند که نقیض آنها.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9039,'پنهان زندگی کن تا بتوانی برای خود زندگی کنی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9040,'نسبت به چیزی که برای زمانه تو بسیار بااهمیت به نظر می رسد، بی خبر باش.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9041,'جهان گرچه پر از چیزهای زیباست، اما در ارائه لحظات زیبا و بهره برداری از چیزهای زیبا بسیار بخیل است. شاید همین بُخل، بزرگترین لطف زندگی باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9042,'در قلمرو دانش، یقین و اعتقادهای بی چون و چرا جایی ندارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9043,'هیچ قدرت و اعتلای انسانی وجود ندارد که نوع جدیدی از برده داری را طلب نکند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9044,'خواست حقیقت"خواست فریب نخوردن"خواست فریب ندادن دیگران و فریب ندادن خویشتن',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9045,'مسایل بزرگ نیاز به عشقی بزرگ دارند و تنها روحیه های قوی، شفاف، مطمئن و با بینایی مستحکم قادر به داشتن این عشق بزرگ هستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9046,'دنیا آنگونه که خیال کرده ایم نمی ارزد؛ این تقریباً مطمئن ترین حقیقتی است که بدگمانی ما در نهایت به دست آورده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9047,'هر اندازه کمتر توان فرماندهی داشته باشیم، بیشتر می خواهیم فرمان پذیر و تحت تسلط باشیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9048,'دانشمند اساساً و ناخواسته نماینده اندیشه دمکراتیک است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9049,'هیچ چیز دمکراتیک تر و مردمی تر از منطق نیست؛ ملاحظه هیچ کس را نمی کند و ریز و درشت را درون یک کیسه می ریزد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9050,'مفاهیم خاص از دل یکدیگر بیرون می آیند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9051,'آه که پرهیزکاری چه خوب آنچه را که نداریم پنهان می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9052,'آیا حکمت مخفیگاهی نیست که فیلسوف برای گریز از... اندیشه خود را در آن پنهان می کند؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9053,'من خیال می کنم که هیچ چیز مانع از آن نیست که یک دیپلمات خوب تبدیل به بازیگر عالی شود، جز نگرانی از مرتبه و وقار.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9054,'مسئله شعور و آگاهی (یا به طور دقیق تر شناخت خویشتن) از زمانی برای ما مطرح می شود که متوجه می شویم از چه راهی می توانستیم از آن فرار کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9055,'مفهوم کلیسا نمی تواند به قوت خود باقی باشد، مگر آنکه بپذیریم روح الهام بخشی که این کلیسا را بنا نهاده زنده است، آن را می سازد و همواره در آن سکنی می گزیند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9056,'هنر معاشرت با انسانها عمدتاً بر مهارتی استوار است (نیاز به آموزشی طولانی دارد) که به کمک آن قادریم غذایی را که به طبخ آن هیچ اعتمادی نداریم بپذیریم و آن را ببلعیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9057,'ما هم با آدمها معاشرت می کنیم و با هم متواضعانه لباسی را بر تن می کنیم که با آن ما را می شناسند، درباره ما قضاوت و ارزیابی می کنند و به دنبال ما می گردند و چنین ملبس وارد اجتماع می شویم؛ یعنی به سرزمین نقابداران که دوست هم ندارند آنها را برملا کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9058,'کسی که بی وقفه خلق می کند، کسی که چیزی جز آبستن شدنها و زایشهای روح خود نمی شناسد، دیگر فرصت فکر کردن به خود و مقایسه خود و اِعمال سلیقه خود ندارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9059,'هر هنر و فلسفه ای می تواند دارویی شفابخش برای زندگی، یاور و نیرویی برای رشد آن و مرهمی برای مبارزه ها باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9060,'ما ثروتمندان و گشاده دستانِ ذوق و اندیشه، چشمه سارانی در گذرگاه ها را می مانیم که نمی توانیم مانع شوبم عابران از آب ما برندارند و متاسفانه هر بار هم که بخواهیم، توان دفاع از خویش را نداریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9061,'دانش رشد می کند و دانشمندترین افراد در میان ما در آستانه کشف این حقیقت قرار گرفته اند که چیز زیادی نمی دانند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9062,'وظیفه ما پیش از هر چیز آن است که خود را با دیگری اشتباه نگیریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9063,'آنچه می شنوید، دست کم آوایی تازه است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9064,'اگر بنا بود که دلایل خود را همراه داشته باشم، آیا نمی بایست اکنون توشه دانی از یادبودها می بودم؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9065,'در هنگام لزوم نه تنها مشکلات و حوادث ناگوار را صبورانه بپذیر و تحمل کن، بلکه آنها را دوست بدار.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9066,'جنایتکار اغلب به کفایت، شایسته کار خویش نیست و به همین دلیل، آن را خوار می دارد و بدنام می کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9067,'وکلای جنایتکار به ندرت هنرپیشگی کافی را برای آن دارند که از جنبه هراس انگیز و زیبای جرم به نفع مجرم بهره جویند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9068,'خودپسندی ما زمانی به نهایت جریحه دار می شود که غرور ما زخم خورده باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9069,'می خواهی دل او را به دست آوری؟ تظاهر کن که دچار تشویش می شوی... .((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9070,'آنجا که عشق یا نفرت اهمیتی ندارد، زن نقشی میانه بر عهده می گیرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9071,'دوره های بزرگ زندگی ما آن زمانی است که جسارت می یابیم و بدترین حالتها را نیک ترین می نامیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9072,'خواست چیرگی بر حسی عاطفی در نهایت خود خواسته یک یا چند حس عاطفی دیگر است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9073,'به نظر من اولین موسیقیدان کسی است که فقط اندوه عمیق ترین خوشبختی را درک کند، نه اندوهی دیگر را. تاکنون چنین موسیقیدانی پیدا نشده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9074,'سلیقه من آن است که به جای برپا داشتن مترسک در پیرامون خود، بگذاریم غارت شویم؛ باز هم پای سلیقه در میان است، فقط سلیقه.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9075,'امروز او فقیر است، اما نه به این دلیل که همه چیزش را گرفته اند، بلکه به این دلیل که همه چیز را دور ریخته است. برایش چه اهمیتی دارد! او به یافتن عادت دارد. این فقرا هستند که فقر داوطلبانه او را به خوبی درک نمی کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9076,'اکنون او تنها کارهای عاقلانه و صحیح انجام می دهد؛ اما وجدانش آسوده نیست؛ زیرا رسالت او در کارهای خارق العاده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9077,'او یک اندیشمند است؛ یعنی می داند چیزها را چگونه ساده تر از آنچه که هستند در نظر بگیرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9078,'انسان جز از طریق همردیف های خود ستایش نمی شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9079,'فریبکارانه ترین روش در تباه کردن یک آرمان این است که از آن با دلایل سفسطه آمیز به عمد حمایت کنید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9080,'او از مردم گریزان است، اما مردم به دنبال او می دوند، زیرا او پیشاپیش آنها است... چقدر مردم گلّه صفت هستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9081,'انسان فقط پرسشهایی را می شنود که قادر به یافتن پاسخی برای آنها است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9082,'ما بیش از هر چیز دوست داریم هر آنچه را که سرّی است به دیگران بگوییم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9083,'آدم مغرور حتی نسبت به موجوداتی که او را به پیش می برند آزرده خاطر می شود؛ او اسبهای ارابه خود را با بدبینی می نگرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9084,'سخاوت ثروتمندان غالباً چیزی جز گونه ای شرمندگی نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9085,'خندیدن یعنی استقبال ناملایمات با خاطری آسوده.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9086,'در تشویق و ستایش، همیشه گونه ای صدا وجود دارد؛ حتی زمانی که خودمان را تشویق و ستایش می کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9087,'همه گدایان و نیازمندان بر این باورند که کوفتن در با سنگ هنگامی که زنگ وجود ندارد، بی نزاکتی نیست، اما کسی به آنها حق نمی دهد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9088,'مردم فکر می کنند که نیاز علت چیزها است، اما در حقیقت نیاز غالباً معلول چیزها است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9089,'فضیلت، خوشبختی و سعادت را تنها برای کسانی به ارمغان می آورد که به فضیلت خود ایمان دارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9090,'انسان با صدای بلند از اندیشیدن درباره ی چیزهای ظریف، کمابیش عاجز است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9091,'آنک یک حسود؛ برای او آرزو نکنید که بچه دار شود. او حتی نسبت به بچه ها حسادت خواهد کرد، زیرا دیگر نمی تواند مانند آنها شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9092,'قربانیان، نظر دیگری سوای نظر تماشاگران نسبت به قربانی و قربانی کردن دارند، اما هیچ گاه به آنها اجازه اظهار نظر داده نشده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9093,'کسانی که دید ضعیف دارند می کوشند همه چیز را مشابه و یکسان ببینند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9094,'او به آرمانی که ضعف آن مشهود است هنوز با سماجت پایبند است... و این امر را وفاداری می نامد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9095,'رفتار او برای ما قانع کننده نیست، زیرا هرگاه کار خوبی انجام داده است آن را با سر و صدای زیادی به رخ همه کشیده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9096,'در خواب، یا رؤیایی جالب ببین یا اصولاً رؤیا نبین؛ به همین ترتیب باید بیداری صحیح را بیاموزیم، یعنی باید تنها برای دلیلی جالب بیدار باشیم و بس.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9097,'آنچه را که من در این لحظه انجام می دهم یا نمی دهم به اندازه بزرگترین رویداد گذشته، برای هر آنچه که در آینده قرار است رخ دهد مهم است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9098,'بسیاری از مردم از نظر شخصیت مادی به اوج کمال می رسند، اما عقل آنها به این مقام عادت ندارد و بسیاری از آدمهای دیگر برعکس.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9099,'کسی که می خواهد توده مردم را بسیج کند، آیا نباید ابتدا بازیگر نقش خویش باشد؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9100,'وجود تنها یک آدم بی نشاط کافی است تا کل یک خانه را مسموم ساخته و فضای آن را تیره و تار سازد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9101,'من در شناخت هر اندازه حریص باشم نمی توانم جز از طریق نگرش خود چیزی کشف کنم و به نگرش دیگران دست نمی یابم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9102,'تنها آن کسی تحول ایجاد می کند که بتواند احساس کند که فلان چیز خوب نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9103,'انسان حتی نمی تواند اندیشه های خویش را به طور کامل به گفتار درآورد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9104,'هنرمند موضوعاتش را انتخاب می کند و این خود، شکلی از ستایش است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9105,'ما می خواهیم ظرافت و دقت ریاضیات را تا آنجا که ممکن است در همه علوم تسری دهیم. البته با این کار قصد نداریم چیزها را بهتر بشناسیم، بلکه تنها می خواهیم با اشیاء رابطه ای انسانی برقرار کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9106,'عادت، دستان ما را هوشمندتر و هوش ما را بی دست و پاتر می سازد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9107,'کتابی که نتواند ما را به فراسوی کتابها ببرد به چه درد می خورد؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9108,'بهتر است بدهکار باشیم، تا اینکه با پولی که مهر و نشان ما بر آن نیست بدهی خود را بپردازیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9109,'انسان زمانی که همواره به گونه ای عمیق مشغول است از هر گونه گرفتاری رها است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9110,'شخصیتهای ضعیف چون بدون برده شدن نمی توانند خدمت کنند، از خدمت کردن بیزارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9111,'همه انسانهای عمیق از اینکه توانسته اند همانند ماهیان جهنده در بالای امواج جست و خیز نمایند، احساس رستگاری می کنند! آنها تصور می کنند که بهترین جای هر چیز سطح آن است؛ یعنی بشره ی آن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9112,'هیچ فاتحی به اتفاق و حادثه عقیده ندارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9113,'یک، هرگز حق ندارد؛ با دو، حقیقت آغاز می شود. یک، نمی تواند خود را اثبات کند، اما دو در ورای هر گونه انکار است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9114,'نوآوری یعنی مشاهده چیزی که گرچه در برابر چشم همگان است، اما هنوز نامی ندارد و نمی تواند نامیده شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9115,'اصولاً مردم از روی عادت، خواهان اسمی هستند تا اشیاء برایشان قابل رؤیت باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9116,'ما هنگامی که عاشق می شویم دوست داریم ضعف هایمان پنهان بمانند، البته نه به دلیل تکبر، بلکه به دلیل اینکه معشوق رنج نبرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9117,'آنچه که انجام می دهیم هرگز فهمیده نمی شود، بلکه تنها ستایش یا تقبیح می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9118,'تحلیل نهایی حقایق انسان چیست؟ خطاهای انکارناپذیر وی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9119,'آن کس که بزرگ است نسبت به فضایل و قضاوتهای ثانویه خود بیرحم است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9120,'شما با هدفی بزرگ نه تنها نسبت به اعمال و قضاوت خود، بلکه نسبت به خودِ عدالت برتر می شوید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9121,'چه چیز موجب قهرمانی است؟ همزمان به استقبال بزرگترین رنج و بزرگترین امید خود رفتن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9122,'باید اوزان همه چیز را از نو تعیین کرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9123,'بزرگترین خطر در کجاست؟ در دلسوزی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9124,'چه چیز را در دیگران دوست داری؟ امیدهایم را.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9125,'چه کسی را بد می دانی؟ آن کس که همیشه می خواهد دیگران را شرمسار می سازد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9126,'انسانی ترین کار چیست؟ شخص را از شرمساری دور ساختن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9127,'نشان آزادی به دست آمده چیست؟ دیگر از خود شرمسار نبودن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9128,'من هنوز زنده ام و هنوز فکر می کنم. باید باز زندگی کنم تا باز فکر کنم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9129,'باید بیش از پیش بیاموزم که ضرورت چیزها عین زیبایی ذاتی است. بدینسان، من در شمار کسانی خواهم بود که چیزها را زیبا می سازند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9130,'من نمی خواهم متهم کنم؛ حتی نمی خواهم متهم کنندگان را متهم کنم. من نمی خواهم با زشتی ها سر جنگ داشته باشم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9131,'ما مشاهده می کنیم که هر چیزی که بر سر ما آمده همیشه به نفع ما تمام شده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9132,'دیگران باید ابتدا ایمان به خویشتن را فرا گیرند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9133,'تنها کاری که می توانم بکنم آن است که کمک کنم تا شما به یاد آورید و نه چیز دیگر.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9134,'آدم شرور، آدم بدبخت و آدم استثنایی نیز هر کدام باید فلسفه خود، حقوق خود و خورشیدی مخصوص به خود را داشته باشند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9135,'در یک جا، زشتی را که نتوانسته ایم محو کنیم می پوشانیم و در جایی دیگر آن را به گونه ای تغییر می دهیم که معنایی متعالی پیدا کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9136,'چیزی وجود دارد که گهگاه با ما بازی می کند و آن اتفاق و تصادف است. این اتفاق گاه دست ما را چنان حرکت می دهد که زیباترین آهنگها از زیر انگشتان ما نواخته می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9137,'من خوشحالم از اینکه می بینم انسانها به طور مطلق از اندیشه مرگ سر باز می زنند. من دوست دارم به آنها کمک کنم تا اندیشه زندگی هزاران بار بیشتر از این برای آنها درخور تامل شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9138,'ای جویندگان دانش، ای عارفان، تا زمانی که نمی توانید بر خود مسلط شوید و مالک خویش باشید، باید به غارت و فتح بپردازید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9139,'افکارم باید مرا آگاه کند که در چه وضعیتی هستم، نه آنکه فاش سازد به کجا روانم. من بی خبری از آینده را دوست دارم و نمی خواهم به بی صبری و ملاحت چیزهای دیده و شناخته مبتلا گردم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9140,'در حقیقت، تنها یک چیز لازم است و آن اینکه انسان با توسل به هر نوع هنر یا شعری به خرسندی و ارضای خود دست یابد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9141,'آنهایی که همیشه از خود راضی و خشنودند، همواره آماده اند که انتقام بگیرند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9142,'اگر می خواهید هر گونه ارزش و اعتباری را از بهترین چیزها بگیرید مانند گذشته همچنان درباره آنها سخن بگویید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9143,'بگذار کسانی که نمی دانند چگونه خود را با تاثیر آتش ما گرم و روشن سازند، از ما بترسند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9144,'به احتمال زیاد، دانش طی هزاران سال اسیر وجدان کاذب بوده است و زندگی متفکران بزرگ با تحقیر خود و عقده های پنهانی بسیار همراه بوده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9145,'امروزه همه می دانند که تحمل انتقاد نشانه بارز فرهنگ است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9146,'توان انتقاد کردن و حفظ وجدان صادق در مخالفت با آنچه که همیشگی، سنتی و مقدس است، هنری والاتر از تحمل و تحریک انتقاد است و این هنر در فرهنگ ما به راستی بزرگ، نو و شگفت آور است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9147,'ما باید راز هنر را گسترش داده و آن را به هنر زیستن تبدیل کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9148,'خنده یعنی دیدن درون چیزها.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9149,'فیلسوفی که حالتهای متعدد سلامتی را پیوسته از سر گذرانده و می گذراند، به همان اندازه، فلسفه های متعدد را نیز پشت سر می گذارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9150,'برای ما زیستن یعنی تبدیل پیوسته آنچه که هستیم و آنچه را که به ما مربوط می شود، به نور و شعله.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9151,'اکنون برای هنرمند بودن باید تلاش کرد که فراموش کنیم، نادیده بگیریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9152,'شاید طبیعت، (حقیقت) زنی باشد که حق دارد نخواهد اساس وجودش برملا شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9153,'حالا می فهمم که بهبود یافته ای، زیرا آن کس که فراموش کرد بهبود یافت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9154,'تحمل دردی مضاعف آسانتر از دردی واحد است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9155,'شاید شریرترین انسان باز هم در نهایت، مفیدترین آنها در جهت حفظ نوع انسان باشد، زیرا با عمل خود غرایزی را در خویش و یا دیگران فعال نگاه می دارد که بدون آنها بشریت از مدتها پیش سست و تباه شده بود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9156,'زندگی چیز مهمی است، در پس آن چیزی هست، ظاهرش آن چیز را پنهان می کند؛ مراقب باشید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9157,'اغلب مردم فاقد وجدان فکری هستند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9158,'ترحم، خوشایندترین احساس برای کسانی است که غرور زیادی ندارند و امکان دستیابی به موفقیت های بزرگی هم برایشان فراهم نیست: طعمه آسان- هر موجودی که رنج می برد یک طعمه آسان است - چیزی است که آنان را شیفته می سازد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9159,'عشق شاید طبیعی ترین و خودجوش ترین تجلی خودخواهی انسان باشد. این برداشت عامیانه باید توسط کسانی انجام شده باشد که کامروا نشده و چیزی جز اشتیاق در تملک خود نداشته اند؛ این عده احتمالاً همیشه زیاد بوده اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9160,'به محض اینکه فساد به جایی رخنه کند می بینیم خرافات چندگانه ای حاکم می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9161,'خرافات نوعی تفکر آزاداندیشی از درجه دوم است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9162,'مردم دورانهای فاسد، مردمی زیرک و اندیشمند و اهل تهمت و افترا هستند؛ آنها می دانند که می توان بدون استفاده از خنجر و غافلگیری کسی را کشت و همچنین می دانند که مردم آنچه را که خوب بیان شود باور می کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9163,'مثل همه نیندیشیدنِ کمتر، نتیجه فهم و شعور برتر است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9164,'ما با عذاب وجدان خود بهتر کنار می آییم تا با سابقه و شهرت بد خود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9165,'عاشق حتی امیال هوس آلود محبوب خود را هم می بخشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9166,'نیاز به کسب اطمینان در برابر نوسان هول انگیز ثروت باعث می شود در برابر مرد ثروتمند و صاحب نفوذی که آماده است تا سکه ای طلا پرتاب کند دستهای آزاده ترین مردمان هم دراز شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9167,'تحمل انسان دارای نبوغ ناممکن است، مگر آنکه در وجودش دست کم دو ویژگی وجود داشته باشد : سپاسگزاری و پاکی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9168,'در زمان صلح، انسان جنگجو به جان خودش می افتد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9169,'آن کس که خویشتن را تحقیر می کند، در مقام تحقیرگر به خویشتن احترام می گذارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9170,'روانی که می داند دوستش دارند، اما خودش کسی را دوست ندارد، ژرفای وجودش را آشکار می کند، یعنی آن رسوبها بالا می آیند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9171,'هر امری که روشن شده باشد، دیگر برای ما جذاب نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9172,'مردن در اقیانوس از شدت تشنگی هراس انگیز است. آیا ناگزیرید بر حقیقت چنان نمک بپاشید که دیگر تشنگی را رفع نکند؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9173,'آنچه از سر عشق انجام می شود، فراسوی نیک و بد است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9174,'ایراد، خیانت، سوءظنی شادمانه و میل به تمسخر، همگی نشانی از سلامت است و هر امر بی قید و شرطی، نشانی از بیماری.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9175,'دیوانگی به ندرت در فردی تنها پدید می آید، اما در گروه، احزاب، جمعیت ها و دوره ها امری معمول است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9176,'فکر به خودکشی راهی موثر برای تسلی خاطر است و با آن می توان به خوبی از پس برخی شبهای سخت برآمد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9177,'در برابر قویترین رانه ی وجودی ما، آن ستمگر وجود ما، نه تنها خرد، بلکه وجدان هم تسلیم می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9178,'ای فایده باوران، حتی شما نیز هر فایده ای را به مثابه ارابه خواست خویش دوست می دارید. آیا شما نیز از سر و صدای چرخهای آن ناراحت نیستید؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9179,'در نهایت، عاشق هوس خویش هستیم و نه آنچه هوس کرده ایم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9180,'خودپسندی دیگران زمانی خلاف ذوق ما است که خلاف خودپسندی ما باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9181,'حماقت های هوشمندان را باور نمی کنیم؛ چه لطمه هایی به حقوق بشر!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9182,'نوعی بی گناهی در دروغ نهفته است که نشانه ای از باور نیک است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9183,'سخن گفتن در جایی که کسی ما را نفرین می کند، کاری غیر انسانی است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9184,'دوستی با افراد برتر تلخ است، زیرا نمی توان چیزی در قبال این دوستی به فرد داد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9185,'نه اینکه دیگر به من دروغ نمی گویی، بلکه چون دیگر به تو اعتماد ندارم، این موضوع برایم تکان دهنده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9186,'گونه ای از نخوت در نیکی وجود دارد که همانند بدی، موردی استثنایی است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9187,'«از چشم من افتاد». چرا؟ «من همسان او نیستم»؛ آیا انسانی تاکنون چنین پاسخ داده است؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9188,'اخلاقیات گوناگون زبان اشاره عواطف است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9189,'پیوسته در طول زمان امری پدید آمده که برای آن، زندگی بر زمین ارزشمند شده است. برای مثال، فضیلت، هنر، موسیقی، رقص، خرد، معنویت؛ خلاصه امری درهم، متعالی، عالی و الهی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9190,'شکوه اصلی نژادهای کوشا تحمل بیکاری است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9191,'هر جا که می خواهی مالک چیزی شوی، نباید دیگران را بفریبی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9192,'عشق به همنوع در مقایسه با هراس از همنوع پیوسته امری بی اهمیت، تا حدودی قراردادی و ظاهری بوده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9193,'ناخودآگاه هنگام شنیدن سخنان فردی به زبان دیگر می کوشیم آواهای شنیده را در قالب عبارتهایی درآوریم که برای ما طنین آشناتر و معمول تری دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9194,'هیچ مادری هرگز در ژرفای دل خویش تردید نمی کند که با کودک خود صاحب کسی شده است و هیچ پدری نیست که از حق خویش بگذرد و فرزند را تسلیم ارزش گذاری و مفاهیم خویش نکند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9195,'تا زمانی که چشم تنها به حفظ امت دوخته شده باشد و رفتار غیر اخلاقی را دقیقا و صرفا در اموری جستجو کنند که موجودیت امت را به خطر می اندازد، اخلاق نوعدوستی دیگر وجود نخواهد داشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9196,'مجازات، کاری بس هراس انگیز است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9197,'دردی شدیدتر از آن نیست که زمانی شاهد و آگاه باشیم و حس کنیم انسانی فوق العاده از راه خویش منحرف و به تباهی کشیده می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9198,'آن کسی بزرگترین است که بتواند تنهاترین باشد، پنهان ترین، رهپویی متفاوت از دیگران، انسانی فراسوی نیک و بد، سرور فضیلت های خویش و لبریز از اراده.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9199,'احساس های عاطفی بین زن و مرد از نظر سرعت متفاوت است و به همین دلیل، سوء ظن بین زن و مرد هرگز پایان نخواهد پذیرفت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9200,'هر بار دل خویش را سخت به بند کشیم و اسیر کنیم، می توانیم به جان خویش، آزادی های فراوانی بدهیم. کسی حرف مرا باور نمی کند، مگر آنکه از پیش آن را بداند... .((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9201,'هر بار که انسان های بس هوشمند دستخوش پریشانی می شوند، بدبینی دیگران به سخن آنان آغاز می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9202,'انسان های سنگین جان و سختگیر دقیقا با آنچه دیگری را سنگین تر می کند، یعنی عشق و نفرت، سبکتر می شوند و گاهی به سطح می آیند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9203,'چنان سرد و یخ زده است که انگشت در برخورد با آن می سوزد! هر دستی که بر آن بخورد، می هراسد! و مردم به همین دلیل او را شعله ور می پندارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9204,'کیست که تاکنون خویشتن را برای نیک نامی خویش قربانی نکرده باشد؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9205,'در مردم داری هیچ نشانی از نفرت انسانی نیست، اما به همین دلیل هم تحقیر انسانها بسیار است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9206,'پختگی مرد یعنی کشف دوباره همان جدیتی که در کودکی و به هنگام بازی داشته ایم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9207,'شرم از کاری غیر اخلاقی، پله ای از پلکانی است که در پایان آن از اخلاق گرایی خود شرم می کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9208,'اگر وجدان خویش را همانند اسبان آموزش داده باشیم، به هنگام گاز گرفتن، بوسه ای نیز بر ما خواهد زد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9209,'ما همگی در پیشگاه خویش خود را ساده تر از آنچه هستیم، می انگاریم و با این کار، خود را از شر دیگران راحت می کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9210,'به برکت موسیقی، شورها نیز از وجود خویش کامیاب می شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9211,'اگر روزی تصمیم بگیریم گوش را حتما بر بهترین استدلالها ببندیم، نشانه ای از شخصیتی قوی را در وجود خویش داریم، یعنی خواست گاه و بی گاه حماقت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9212,'هیچ پدیده ای اخلاقی وجود ندارد، بلکه تعبیرهای اخلاقی از پدیده ها وجود دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9213,'هنر، بیان عدم حضور خوی طبیعی و تجلی پیوندی برتر و قهرمان پرور است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9214,'در جامعه سطح بالا هیچ گاه آنگونه که منطق ناب می خواهد، نباید ادعا کرد که تنها ما به طور کامل حق داریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9215,'وزن شعر همان قدرتی است که تمام ذرات جمله را دوباره منظم ساخته و انتخاب کلمات را ایجاب کرده، اندیشه را از نو رنگ آمیزی کرده و آن را عجیب تر، غریب تر و مبهم تر می سازد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9216,'هنرمندان پیوسته شکل و عظمت می بخشند و جز این کاری نمی کنند. آنها به ویژه به موقعیت ها و اشیایی شکل و عظمت می بخشند که اصولاً انسان در آنها خود را خوب، بزرگ، سرمست، شاد، مقدس و عاقل می پندارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9217,'وقتی آدم در خودش به اندازه کافی کمدی و تراژدی دارد ترجیح می دهد که از رفتن به تئاتر پرهیز کند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9218,'من فکر می کنم که هنرمندان غالباً نمی دانند بهترین استعداد آنها در کجاست، زیرا بسیار پر مدعا هستند و برای مثال با مشاهده گیاهان سربلند از توجه به گیاهان فروتن غافل می شوند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9219,'جدیت ما نشان نمی دهد که سنگینی ما در کجاست و در چه مواردی از سبکباری بی بهره ایم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9220,'فراموش نکنیم که استادان نثر تقریبا همیشه، خواه در میان همگان یا تنها برای نزدیکان خود، شاعر بوده اند. در حقیقت تنها از دریچه شعر است که می توان نثری خوب نوشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9221,'از میان دو سخنور، تنها آن کس که عنان اختیار به دست شور و شوق می دهد می تواند براستی منطق آرمانی خویش را به کرسی بنشاند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9222,'در حقیقت، بیهوده گویی ها و ضعفها به آسانی مورد تقلید قرار گرفته و نیاز چندانی به تمرین قبلی نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9223,'اصولاً حالتها، رفتار و شور و شوق فلاسفه همیشه فریبنده ترین عنصر اندیشه آنها بوده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9224,'ما نیز باید زندگی و حیاتی داشته باشیم که نسبت به خود ما حق داشته باشد. ما نیز باید آزاد و بدون ترس باشیم و با بذری که در خویشتن خویش داریم رشد کنیم و شکوفا شویم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9225,'ارزش شور و احساسات بهتر از زهد و ریا است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9226,'صادق بودن، حتی در شر، بهتر از گمراه شدن در اصول اخلاقی سنت است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9227,'آن کس که می خواهد آزاده باشد، تنها از طریق توانایی های خود می تواند آزاده شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9228,'آزادگی هرگز با معجزه از آسمان نصیب ما نمی شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9229,'دربار در هر کجا که وجود داشته است قانون سخنوری و در نتیجه قانون سبک را برای نویسندگان تعیین کرده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9230,'باید گهگاه به کمک هنر از خود بدر آییم و فاصله بگیریم و از بالا بر خود بگرییم یا بخندیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9231,'انسان آزاده می تواند خوب یا بد باشد، اما انسان غیر آزاده ننگ طبیعت است و کوچکترین آرامش، خواه آسمانی یا زمینی، وجود ندارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9232,'آدم با اصوات می تواند مردم را به راه کج یا راه درست هدایت کند، زیرا کسی به فکر انکار صدا نمی افتد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9233,'برای آنکه بتوانیم از حکمت خود برخوردار گردیم باید گهگاه از دیوانگی خود بهره مند شویم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9234,'انسان با خطاهایش تربیت شده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9235,'در تمام دوران طولانی تاریخ بشر چیزی وحشتناک تر از احساس تنهایی وجود نداشته است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9236,'هرجا انسان عمل کرده است، انسان خواسته است؛ تنها از طریق موجوداتی که اراده دارند می توان عمل کرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9237,'خواستن تنها، مکانیزمی آنقدر رایج است که تقریباً از نظر پوشیده می ماند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9238,'یک قاضی حتی اگر بخشنده هم باشد مورد عشق قرار نمی گیرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9239,'بر فرض که من تو را دوست داشته باشم؛ این به تو چه ربطی دارد؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9240,'هر اندازه یک فرد یا اندیشه وی، کلی و مطلق عمل کند به همان اندازه توده ای که تحت تاثیر این اندیشه قرار می گیرد باید همگون تر و پایین تر باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9241,'انسان ممکن است در برابر یک فضیلت، چاپلوس و متملق باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9242,'کشیش خلع لباس شده و جانيِ آزاد شده همیشه نقابی بر چهره دارند. آنچه که آنها نیاز دارند چهره ای بدون گذشته است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9243,'خودخواهی، قانون نمایش (پرسپکتیو) ادراک ما است. مطابق این قانون چیزهای نزدیک، بزرگتر و سنگین تر به نظر می رسند، در حالی که وزن و اندازه همه چیزهایی که دور هستند کاهش می یابد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9244,'بهترین چیز در یک پیروزی بزرگ آن است که فاتح را از ترس شکست آزاد می سازد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9245,'من اکنون به اندازه کافی مقتدر هستم که شکست را تحمل کنم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9246,'من افرادی را که در میان اشیاء تاریک پیرامون خود در جستجوی آرامش هستند می شناسم؛ یعنی کسانی که در زمان خواب، اتاق خود را تاریک می کنند یا به درون دخمه می خزند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9247,'وقتی کسی از چیزی به طور کامل و برای مدتی طولانی صرفنظر کند، اگر تصادفاً آن را دوباره ببیند تقریباً تصور می کند که آن را دوباره کشف کرده است و شادی هر کاشفی چه باشکوه است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9248,'هر آنچه که در طبیعت و در تاریخ برایم آشنا است با من حرف می زند، مرا می ستاید، به پیش می راند و تسکین می دهد. اما من بقیه را نمی شنوم یا آن را فوری فراموش می کنم. در حقیقت، ما همیشه چیزی را که در میان ما آشنا است می خواهیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9249,'ما تنها زمانی از مردم سیر می شویم که دیگر نتوانیم آنها را هضم کنیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9250,'شوکت و جلال زمانی زاده می شود که گروهی از مردم در ستایش از یک فرد، حجب و حیا را به کلی کنار بگذارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9251,'آن کس که خود را عمیق می داند تلاش می کند که واضح و شفاف باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9252,'آن کس که دوست دارد به نظر توده مردم عمیق بیاید تلاش می کند که مبهم و کدر باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9253,'توده مردم کف هر جایی را که نتوانند ببینند عمیق می پندارند و از غرق شدن بسیار واهمه دارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9254,'تاکنون قاطع ترین گشاده زبانی از آن که بوده است؟ از آنِ طبل. سلاطین تا زمانی که طبل را در اختیار دارند همیشه بهترین سخنور و بهترین برانگیزاننده توده ها باقی خواهند ماند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9255,'در تنهایی، صداها طنین دیگری دارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9256,'می ترسم که حیوانات، انسان را به صورت موجودی همنوع خود که عقل سلیم را گم کرده است بدانند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9257,'پدر و پسر بیش از مادر و دختر، نسبت به یکدیگر ملاحظه کارند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9258,'انسان با چشمهایش نیز می شنود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9259,'در میان نابینایان، آن کس که درازترین گوشها را دارد، پادشاه است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9260,'آموزش را در خانواده، دانش را در جامعه و بینش را در تفکرات تنهایی می آموزند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9261,'آنچه والا بودن یک فرد را ثابت می کند، کرده های او نیست چون بیخ و بن آنها روشن نیست و معانی گوناگونی دارند؛ والایی در ایمان به هدف و آرمان است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9262,'بشر در این دنیا بیشتر از همه موجودات، گرفتاری و عذاب کشیده است. بهترین دلیلش هم این است که در بین تمام آنها تنها او می تواند بخندد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9263,'آنچه در انسان بزرگ است، این است که او پل است نه غایت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9264,'آن که بر فراز بلندترین کوه رفته باشد، خنده می زند بر همه نمایش های غمناک و واقعیت های غمناک.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9265,'اینها ضعفا هستند که اراده به سوی قدرت خودشان را به این صورت مخفی می کنند که جهان دیگری بسازند؛ در حقیقت جهان افلاطونی همین است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9266,'در حقیقت نکته جالب هر نظریه، همان کمترین محرکی است که می توان بر آن اساس این نظریه را مردود شمرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9267,'زن نفرت را تا به آن پایه می آموزد که دلبری را از یاد می برد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9268,'پیرترین و جوانترین مارها چنین می گویند : «هر جا درخت معرفت است، همانجا بهشت است».((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9269,'آموختن آنچه فیلسوف است، کار دشواری است، زیرا نمی توان آن را یاد داد. باید آن را از سر تجربه دانست یا باید به این ناآگاهی افتخار کرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9270,'امکان ندارد شما به راه خطا نرفته باشید، اما چرا باز هم آن را حقیقت می پندارید؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9271,'هر انسانی که بخواهد ...، در وجودش به دیگری فرمانی می دهد که اطاعت می کنند یا او باور می کند که از این فرمان پیروی کرده اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9272,'اراده ناآزاد، همان اسطوره است. زیرا در زندگی حقیقی، تنها اراده های قوی و ضعیف وجود دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9273,'به طور مطلق، هیچ قانونی وجود ندارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9274,'کل روانشناسی تاکنون وابسته به پیشداوری ها و هراس های اخلاقی مانده و هرگز بی پروا به ژرفاها نرفته است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9275,'روانشناسی بار دیگر راهی است برای رسیدن به مسایل بنیادین.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9276,'چه نیک می توانیم حواس خویش را گذرگاهی برای امور سطحی و اندیشه خویش را شهوتی خدایی برای جهش ها و خطاها بدانیم! از همان سر منزل کار دریافته ایم که ناآگاهی خویش را پاس بداریم تا از خود زندگی لذت ببریم!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9277,'هیچ کس به اندازه فردی خشمگین دروغ نمی گوید.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9278,'بسیار انگشت شمارند کسانی که مستقل باشند، زیرا این امتیاز افراد قدرتمند است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9279,'باید خویشتن را بیازماییم تا دریابیم مهیای استقلال و فرماندهی هستیم یا خیر و این کار را باید به هنگام انجام دهیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9280,'والاترین نگرش های ما، هر بار که غیر منتظره به گوش کسانی برسد که برای آنها مهیا و معین نشده اند، باید و مقرر چنین است که بسان حمایت و شاید هم جنایت طنین انداز شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9281,'احساس خشم و احترام که از ویژگیهای جوانی است، به نظر می رسد تا زمانی که انسانها و اشیاء را به درستی جعل کند و راه گریزی برای خویش بیابد، فرد را راحت نمی گذارد، زیرا جوانی در حقیقت همان جعل و فریب است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9282,'نباید به وطن وابسته شد، حتی اگر رنجورترین و نیازمندترین پاره از جهان باشد؛ زیرا دل کندن از وطنی پیروز چندان دشوار نیست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9283,'رفتار شاعران با تجربه های خویش بی شرمانه است، زیرا آنها را استثمار می کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9284,'در نهایت، جهان باید همان وضعی را داشته باشد که همیشه داشته است؛ یعنی امور سترگ برای بزرگان می ماند و پرتگاهها برای ژرف اندیشان، ظرافت و هراس برای ظریف اندیشان و در نهایت، تمام امور نادر برای نادران.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9285,'آن کس که به گونه ای ژرف به جهان نگریسته باشد، بی تردید می داند چه حکمتی در سطحی بودن انسانها نهفته است. این غریزه حفظ خویشتن است که به او می آموزاند، سطحی، آسان نگر و دروغین باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9286,'اگر آن دستی را نبینیم که با مهربانی می کشد، نگرش ما به زندگی نادرست است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9287,'اگر دارای شخصیت ثابتی باشیم، تجربه ای معمول داریم که پیوسته تکرار می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9288,'حکیم در مقام منجم، مادام که ستارگان را بر فراز سر خویش حس نمی کند، نگرش اهل شناخت را ندارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9289,'نه قدرت، بلکه احساس تعالی، انسان های متعالی را پدید می آورد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9290,'آن کس که به آرمان خویش دست یابد، از آن نیز فراتر خواهد رفت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9291,'برخی از طاووس ها دم خویش را از دید دیگران پنهان می دارند و این کار را غرور می نامند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9292,'باید نیک و بد را جبران کنیم، اما چرا در حق آن کس که در حق ما نیکی یا بدی روا کرده است؟((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9293,'همین که معرفت خویش را به دیگری منتقل کنیم، دیگر آن را به کفایت دوست نداریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9294,'عشق، ویژگی والا و پنهان عاشق را آشکار می کند، یعنی همان ویژگی های نادر و استثنایی را و به این ترتیب، معشوق به آسانی در باب ویژگی های معمول او مرتکب اشتباه می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9295,'در باب هر حزبی، هر چوپانی به قوچی راهنما نیاز دارد، یا خودش باید گاه و بیگاه به قوچی بدل شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9296,'با دهان دروغ می گوییم، اما با شکل و پوزه خویش به هنگام بیان دروغ، حقیقت را بر زبان می آوریم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9297,'برای انسانهای سرسخت، همدلی شرم آور است، اما باارزش.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9298,'زیاده از خویش سخن گفتن، راهی است برای پنهان کردن خویشتن.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9299,'در ستایش، گستاخی بیش از سرزنش است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9300,'دلسوزی، خنده بر لب پوینده ی راه شناخت می نشاند، درست مانند دستانی ظریف که به بدن غولها می خورد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9301,'گاهی از سر انسان دوستی، کسیرا در آغوش می گیریم (چون نمی شود تمام انسانها را در آغوش گرفت)، اما نباید این نکته را بر آن کسی فاش کنیم که در آغوش گرفته ایم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9302,'تا زمانی که امری یا کسی را کم ارزش می پنداریم، نفرت نمی ورزیم، بلکه تازه زمانی چنین می کنیم که آن را همسان یا برتر می دانیم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9303,'در انتقام و عشق، زن وحشی تر از مرد است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9304,'زن هنوز قابل دوستی نیست؛ او تنها عشق را می شناسد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9305,'باید بدانیم که بزرگترین آزمون استقلال، همان حفظ خویشتن است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9306,'پاداش عشق به حقیقت هم در آسمان است و هم در زمین.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9307,'زمانی انسان می خواهد همگام با نظم جاودانه باشد که خودش خوب است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9308,'همواره پرتو اعمال نیک، شما را می پیماید؛ حتی اگر مدت زیادی از انجام آن عمل گذشته باشد. هر چقدر هم که اعمال شما فراموش و دفع شود، اشعه آن همیشه فروزان خواهد ماند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9309,'آن که پرنده نیست نباید بر پرتگاهها آشیان سازد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9310,'آنکه می خواهد روزی پریدن را بیاموزد، نخست باید ایستادن، راه رفتن، دویدن و بالارفتن را یاد بگیرد. پرواز را با پرواز آغاز نمی کنند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9311,'اگر بت ها را واژگون کرده باشی کاری نکرده ای، وقتی واقعاً شهامت خواهی داشت که خوی بت پرستی را در درون خویش از میان برداشته باشی.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9312,'مردان بزرگ فقط آرمانهای خود را نمایش داده اند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9313,'آنچه هستی باش.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9314,'دنیا نه موافق انسان است و نه مخالف [ او ]، بلکه بیطرف است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9315,'مرغان دیگری هم هستند که بالاتر می پرند.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9316,'غم خودش ما را پیدا می کند، باید دنبال شادیها گشت.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9317,'بس نیک و عالی است که مخالفان خاص خویش را داشته باشیم!((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9318,'فیلسوف چنان که ما، جانهای آزاده، درکش می کنیم، انسانی است که بیشترین مسئولیت و وجدان را برای تکامل کلی انسان دارد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9319,'آن کس که از اساس آموزگار باشد، تمام امور را تنها در رابطه با شاگردانش جدی می پندارد، حتی خودش را.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9320,'شناخت به خاطر خود شناخت؛ این واپسین دامی است که اخلاق می گسترد و آدمی نیز سراپا اسیر آن می شود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9321,'اگر مجبور نبودیم در راه شناخت بر بسیاری از شرم ها چیره شویم، کشش آن بسیار اندک بود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9322,'بهتر است انسان چیزی نداند، تا اینکه بسیاری چیزها را نیمه تمام بداند. بهتر است که با عقاید خودمان یک ابله کم عقل باشیم، تا آنکه با عقاید دیگران یک مرد دانشمند به حساب آییم.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9323,'خدا زن را خلق کرد و از آن زمان دلتنگی رخت بربست، ولی چیزهای دیگر هم با دلتنگی از بین رفت! خلقت زن اشتباه دوم خدا بود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9324,'زمستان بدون بهار، قلب عاشق شکست خورده است.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9325,'زیبایی جز اینکه نعمت خداست، دام شیطان نیز هست.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9326,'اراده یک احساس نیست، بلکه شامل احساس های گوناگون است و نمی توان آن را از اندیشه جدا کرد. در عین حال، اراده یک شور است و کسی که اراده می کند، به اشتباه اراده را با عمل یکسان می شمرد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9327,'خطر خوشبختی در این است که آدمی در هنگام خوشبختی هر سرنوشتی را می پذیرد و هرکسی را نیز.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9328,'کسی که «دلیلی» برای زیستن دارد، با هر «وضعیتی» می سازد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9329,'نباید وابسته به ترحم شد، زیرا انسانهای به نهایت والایی هستند که رنج و درماندگی آنان را از سر تصادف شاهد خواهیم بود.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9330,'نباید به علم وابسته شد، حتی اگر با ارزشمندترین یافته های خاص خود، قصد فریفتن ما را داشته باشد.((نیچه))',137,0,0,0,0,0);
INSERT INTO mysms VALUES(9331,'هر اینچ از فضا در نزد من یک معجزه است؛ هر عضو و اندیشه‌ی من خوشامد می‌گوید؛ نه یک اینچ و نه ذره‌ای از یک اینچ، دارای خدشه و خلل نیست.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9332,'ابرهای غارتگر دیرزمانی پیروز نخواهند ماند، دیر زمانی صاحب آسمان نخواهند بود و اختران را تنها به ظاهر در کام خود فرو می برند.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9333,'قدرت تهدید آمیز زمین یکسره بر گرد من فرا ایستاده بود، اما من با روح خود تغذیه کردم، قانع و سربلند تغذیه می کردم.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9334,'به پیش می رویم تا آنچه را زمین و دریا هرگز به ما نداده اند به دست آوریم.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9335,'تا کسی شهامت و تندرستی را با خود نیاورد به عرصه ی آزمایش پا نتواند گذاشت.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9336,'تو نباید آنچه را ثروت و مال می نامند گرد کنی، باید آنچه به دست می آری و هر کامیابی که نصیب می شود با دستهای گشاده به گرد خود بپراکنی.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9337,'به هیچ شوهری و هیچ زنی و هیچ یاری اعتماد نیست که اعترافات آدمی را بشنود.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9338,'در جوهر چیزها چنین نهاده اند که از ثمره ی هر توفیقی، هر چه باشد، چیزی پدید آید که مبارزه ی عظیم تری را واجب سازد.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9339,'غرق در اندیشه جهان، دیدم ذره ای که نام آن « نیکی » است استوار بسوی ابدیت می شتابد، و دریای بی کرانی که « بدی » نام دارد خود را به هر سوی می کشاند، تا ناپدید شود و بمیرد.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9340,'چه لوحه ی یادبود و چه سنگ نبشته ای بر مزار توست، ای میلیونر؟ ما ندانیم که چه سان زیستی، اما این دانیم که عمر خود را در دادوستد و در میان گروه دلالان بسر آوردی، نه رادمردی و قهرمانی از آن تست، نه نبردی و نه افتخاری.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9341,'تمام وجود آدمی بسی شایسته تر است، من درباره ی زن و مرد یکسان می سرایم.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9342,'دیگر در دل جنگل های عظیم راه نخواهیم سپرد، بلکه از میان شهرهای عظیم تر خواهیم گذشت، اکنون چیزی عظیم تر از ریزش نیاگارا در برابر ما خروشان است، سیل آدمیان.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9343,'آزادی نه چنان است که یک یا دو بار شکست، یا بارها شکست، یا بی اعتنایی و حق ناشناسی مردم، یا پیمان شکنی، و یا صف آرایی سر نیزه های زور و قدرت و سربازان و توپها و قوانین جزایی، آن را سرکوب کند و از میان بردارد.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9344,'هنگامی که آزادی جایی را ترک می کند، نخستین کسی نیست که پا بیرون می نهد و حتی دومین و سومین کس هم نیست، درنگ می کند تا همه بیرون روند، و او آخرین کس است.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9345,'هنگامی که دیگر خاطره ای از قهرمانان و شهیدان نماند، و هنگامی که حیات و جان های کردان و زنان یکسره از سرزمین رانده شوند، آنگاه آزادی یا اندیشه ی آزادی از آنجا رانده خواهد شد، و پیمان شکنان اقتدار مطلق خواهند یافت.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9346,'آیا ما پیروزی را بزرگ انگاشتیم؟ آری چنین نیز هست – اما اکنون چنین می بینیم که شکست هم، اگر از آن گریزی نباشد، بزرگ است، و مرگ و بیم نیز بزرگ اند.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9347,'خیال می کنم که همه قهرمان ها و تمامی شعرهای آزاد در هوای آزاد به تصور درآمده است.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9348,'کسی که مرا از خود براند آزرده ام نخواهد ساخت.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9349,'اکنون راز پرورانیدن برترین کسان را در می یابم، باید که در هوای آزاد نشو و نما کرد و با زمین خورد و خفت.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9350,'خرد را آخرین بار در مدرسه ها نمی آزمایند، خرد را نمی توان از کسی که صاحب آن است به کسی که از آن محروم است منتقل ساخت.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9351,'خرد از آن روح است، اثبات نمی پذیرد، خود دلیل خویشتن است.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9352,'در منظره ی اشیاه چیزی است که خرد را از درون روح بر می انگیزد.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9353,'گذشته، آینده، عظمت و عشق – اگر آنها از تو تهی هستند تو خود از آنها تهی هستی.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9354,'زمین در ابتدا گستاخ و خاموش و درک ناشدنی است، طبیعت در ابتدا گستاخ و درک ناشدنی است، نومید مشوید، راه خود را دنبال گیرید، در این راه بسی چیزهای آسمانی است که مستور و نهفته است.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9355,'پیشتر رویم! انگیزه ها بزرگتر خواهند بود.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9356,'برای روبرو شدن با طوفان، گرسنگی، بلایای طبیعی و حادثه ها باید از روش حیوانات و درختان استفاده کرد.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9357,'زندگی به من آموخت؛ بودن با کسانی که دوستشان دارم، از همه چیز با ارزش تر است.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9358,'شیفتگی ذاتی هر اتم این است که به سرچشمه ی الهی و بنیادی‌‌اش بازگردد.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9359,'من بر این باورم که دیدن یک برگ علف، کمتر از سفر به ستارگان نیست.((والت ویتمن))',138,0,0,0,0,0);
INSERT INTO mysms VALUES(9360,'امروزه ساختار زندگی، بیش از آنکه برآمده از باورها باشد، در اراده‌ی رخدادهاست؛ رخدادهایی که هیچ‌گاه بنیان آن باورها قرار نگرفته‌اند.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9361,'امروزه کار ادبی به شرطی کارآمد است که عمل [=کار و تلاش] و نوشتن پیوسته به یکدیگر تبدیل شوند.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9362,'نبوغ [=هوش سرشار]، خود را به کار سپردن است.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9363,'بدون استثنا، نویسندگان بزرگ، آثار خود را در جهانی می‌پرورانند که تازه پس از آنها فرا می‌رسد.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9364,'نیروی پنهان در یک جاده‌ی روستایی، زمانی که در آن گام برداریم، متفاوت [=دیگرگون] است با زمانی که با هواپیما از رویش بگذریم.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9365,'نیروی پنهان در یک متن، زمانی که آن را بخوانیم، متفاوت [=دیگرگون] است با زمانی که از رویش نسخه‌برداری کنیم.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9366,'فقط، کسی که روی جاده گام برمی‌دارد، از نیرویی که در آن است، باخبر می‌شود.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9367,'یک نفر هرچه بیشتر در برابر امور سنّتی جبهه‌گیری کند، بی‌رحمانه‌تر [=نامهربانانه‌تر]، زندگی خصوصی‌اش را تابع [=پیرو] آن معیارهایی [=سنجه] می‌سازد که آرزو می‌کند قوانین جامعه‌ی آینده گردند.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9368,'برای اینکه شرایطی، باثبات [=پایدار] باشد، لزومی ندارد خوشایند هم باشد.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9369,'موضوع [=جستار] هر گفتگو به گونه‌ای اجتناب‌ناپذیر [=گریز‌ناپذیر]، وضع زندگی و پول شده است: گفتگو، هیچ‌گاه به نگرانی و درد و رنج افراد نمی‌کشد – اگر چنین می‌شد، دست‌کم می‌توانستند به یکدیگر کمک کنند – و نه به بیان کلّی این وضعیت.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9370,'برای منتقد [=خُرده‌گیر]، همکارانش، برترین مرجعیت را دارا هستند و نه مردم. و از آن کمتر، نسل‌های آینده.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9371,'جدل [=یک و دو کردن]، یعنی کتابی را با بکار بردن چند سخن آن، نابود کردن. هرچه کتاب، کمتر بررسی شود، بهتر است. فقط کسی که بتواند کتابی را ویران کند، می‌تواند آن را نقد کند [=بر آن خرده گیرد].((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9372,'آن که مقيّد [=در بند] به آداب همنشینی و مخالفِ دروغ گفتن است، مانند کسی است که از مُد پیروی می‌کند، ولی جلیقه نمی‌پوشد.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9373,'خوشبختی، یعنی توانایی شناختِ خود، بدون ترسیدن.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9374,'هیچ اراده‌ی سالمی [=درست و بی‌گزند] وجود ندارد که تخيّل دقیق از تصویری، به همراهش نباشد.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9375,'تنها راه شناخت یک نفر، دوست داشتن او بدون هیچ امیدی است.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9376,'دو عاشق، بیش از هر چیز، به نام‌های یکدیگر وابسته‌اند.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9377,'معشوق در چشم عاشق، همیشه تک و تنهاست.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9378,'مداخله‌ها [=دست‌اندازی]، مخاطره‌ [=به خطر انداختن] کردن‌ها و آهنگِ کار سیاستمداران، اموری تکنیکی است – و نه سلحشورانه.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9379,'به تنهایی غذا خوردن، آدم را سخت و خشن می‌کند.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9380,'کشتن یک جنایتکار، شاید کاری اخلاقی باشد- ولی مشروعیت دادن [=روا دانستن] به آن، هرگز.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9381,'اگر بناست غذا ثمری داشته باشد، باید تقسیم و توزیع [=بخش و پخش] شود: مهم نیست با چه کسی: در گذشته، یک گدای سر میز، سفره را غنی می‌کرد. تنها چیزی که مهم است، بخش کردن و بخشیدن است، و نه بهانه برای گپ و گفت.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9382,'برای کسی که به انبوه نامه‌های کهن می‌نگرد، تمبری که دیرگاهی‌ست از رواج افتاده، بر پاکتی فرسوده، از خواندن ده‌ها برگ، سخن بیشتری برای گفتن دارد.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9383,'کسی که از راه فال‌گیری، جویای آینده می‌شود، نادانسته، آگاهی قلبی خود از رویدادهای آینده را که هزار بار درست‌‌تر از پیشگویی هر فال‌گیری است، نادیده می‌گیرد.((والتر بنیامین))',139,0,0,0,0,0);
INSERT INTO mysms VALUES(9384,'هرگز در میان موجودات، مخلوقی که برای کبوتر شدن آفریده شده، کرکس نمی شود. این ویژگی در میان هیچیک از مخلوقات نیست جز آدمیان.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9385,'هر آموزشگاهی را که باز کنید، در زندانی را بسته اید.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9386,'خوشبخت کسی است که خداوند دلی پر احساس به او ارزانی کرده باشد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9387,'زندگی شما از زمانی آغاز می‌شود که افسار سرنوشت خویش را در دست گیرید.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9388,'چقدر عاقلند آنهایی که در عشق احمق اند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9389,'بهشت دولتمندان از دوزخ فقرا پدید آمده است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9390,'گاهی کار فقر و بیچارگی به جایی می رسد که رشته ها و پیوندها را می گسلد. این مرحله ای است که تیره بختان و سیاهکاران چون بدانجا رسند، درهم آمیخته و در یک کلمه که کلمه شومی است، شریک می شوند؛ این کلمه «بینوایان» است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9391,'امید در زندگانی بشر به همان میزان اهمیت دارد که بال برای پرندگان.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9392,'لطف زن مانند ماسه خطرناک است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9393,'عشق عبارت است از وجود یک روح در دو کالبد؛ عاملی است که دو تن را مبدل به فرشته واحدی می کند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9394,'آدم ها تهی از توانایی نیستند، تهی از اراده اند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9395,'هر چه از کوه بالاتر رویم، چشم انداز گسترده تری را می بینیم.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9396,'هیچ چیز به اندازه رویا در ساختن آینده موثر نیست، مدینه های فاضله ی امروز، واقعیت های فردا هستند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9397,'انسان در جهان همچون شبح سرگردانی است که در عبور از این راه حیاتی، سایه ای از خود به یادگار نمی گذارد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9398,'الماس را جز در ژرفای زمین نمی توان یافت و حقایق را جز در اعماق اندیشه نمی توان کشف کرد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9399,'بدیهای اجتماع به دست ما ساخته شده است و به جای ناله بهتر است درصدد رفع آن برآییم.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9400,'در میان جانداران، هیچ حیوانی که برای کبوتر شدن به دنیا آمده، کرکس نخواهد شد؛ مگر انسان که کبوتر زاده می شود و ممکن است کرکس شود.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9401,'به کسی عشق بورز که لایق عشق تو باشد نه تشنه عشق ... چون تشنه عشق روزی سیراب می شود ...((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9402,'لبخند زن در دو جا آسمانی و فرشته مانند است: یکی، هنگامی که برای اولین بار با لبخند به معشوق خود می گوید دوستت دارم و دیگر، هنگامی که برای اولین بار به روی نوزادش لبخند می زند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9403,'شعر، ارکستر پرهیجانی است که آواز طبیعت و بشر و حوادث را به هم آمیخته است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9404,'آزادی ما از نقطه ای شروع می شود که آزادی دیگران پایان می یابد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9405,'کینه و تنفر را به کسانی واگذار کنید که نمی توانند دوست بدارند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9406,'بدبختی، مربی استعداد است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9407,'به مرگ راضی شدن، به پیروزی دست یافتن است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9408,'تعارف و خوش آمدگویی، چیزی مانند بوسیدن از روی چادر است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9409,'جسد دشمنی را که تشییع می کنی سنگین نیست.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9410,'خدا فقط آب را آفرید، انسان شراب را.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9411,'در بینوایی همچنان که در سرما نیز دیده می شود، افراد به یکدیگر فشرده می گردند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9412,'شاید بتوان از هجوم سیل آسای یک ارتش ممانعت کرد، اما از هجوم اندیشه ها و باورها نمی توان جلوگیری نمود.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9413,'فقر و مسکنت، مردها را به جنایت و زنها را به فحشا سوق می دهد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9414,'فکر کردن، شغل ذهن است، خواب دیدن، تفریح آن.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9415,'فلسفه، میکروسکوپ اندیشه ها است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9416,'وقتی نتیجه ی انتخابات آشکار شد، یعنی رأی بالاترین مرجع آشکار شده است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9417,'همه جا شادمانی قشر نازکی است که روی رنج و بیچارگی کشیده اند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9418,'آینده ی کودکان بسته به تربیت پدر و مادر است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9419,'ادبیات، راز پنهانی تمدن و شعر، راز پوشیده ی آرزوها است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9420,'از آن در شگفتم که در سینه دلی دارند و می پندارند که آسایش و سعادت بشر جز مهر و صفا راه دیگری هم دارد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9421,'باید درهای دانش به روی همه باز باشد، هرجا مزرعه هست، هرجا بشر هست، آنجا کتاب هم باید باشد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9422,'بدتر از مرگ چیست؟ آن که پس از آمدنش مرگ را می جویی.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9423,'خدمت به وطن نیمی از وظیفه است و خدمت به انسانیت، نیم دیگر آن.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9424,'خوشبخت کسی است که به یکی از این دو چیز دسترسی دارد، یا کتاب های خوب و یا دوستانی که اهل کتاب باشند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9425,'دانشمندان، علماء و بزرگان، هر یک نردبانی برای پیشرفت دارند، اما شاعران و هنرمندان، این مدارج را پروازکنان می پیمایند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9426,'دروغ جلوه ای از شیطان است زیرا شیطان دو نام دارد: یکی شیطان و دیگری دروغ.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9427,'در هر ملت چراغی هست که به همه ی افراد نور می دهد و آن، آموزگار است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9428,'صالح ترین فرزندان آنهایی هستند که با اعمال خود باعث افتخار پدر و مادر خود شوند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9429,'عذاب وجدان، بدتر از مرگ در بیابان سوزان است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9430,'علت اینکه ما از موسیقی خوشمان می آید این است که در دنیای رؤیاهای خود فرو می رویم. سرشت های عالی، موسیقی را دوست می دارند، اما بهتر می دانند که از آن به عنوان ابزاری برای رسیدن به رؤیاهای خویش استفاده کنند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9431,'خشک سالی و بیماری در مقام مقایسه با جنگ، چیزی نیست زیرا خود این دو نیز ناشی از پیامدهای دشوار و ناهنجار جنگ هستند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9432,'کتاب، جام جهان نما است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9433,'گسیختن رشته ی علایق فرزندی، غریزه ی برخی از خانواده های بینوا است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9434,'مانند پرنده باش که روی شاخه سست و ضعیف لحظه ای می نشیند و آواز می خواند و احساس می کند که شاخه می لرزد، اما به آواز خواندن خود ادامه می دهد زیرا مطمئن است که بال و پر دارد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9435,'مرگ مهم نیست، خوشبخت نبودن مهمترین چیزها است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9436,'مسافرت به منزله ی هر لحظه مردن و زنده شدن است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9437,'وقتی که کیسه خالی شد، دل پر می شود.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9438,'هدف هنر امروز، زندگی است نه زیبایی.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9439,'یک پرنده ی کوچک که زیر برگها آواز می خواند برای اثبات خدا کافی است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9440,'یک زن کامل کسی است که بداند چگونه فرمانروایی کند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9441,'دانشمند کسی است که بکوشد تا از درد و رنج خود بکاهد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9442,'هیچ عاملی همچون رؤیا آینده ساز نیست؛ آرزوهای امروز حقایق فردایند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9443,'مردم، توان و نیرو دارند؛ اراده ندارند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9444,'هر قدر روح آدمی لطیف تر و منش او عالی تر باشد، میزان یاری بیشتر خواهد بود.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9445,'ایمان داشته باش که کوچکترین محبت ها از ضعیف ترین حافظه ها پاک نمی شوند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9446,'همیشه با زیر دستان خود با مهربانی و دوستی رفتار کنید، زیرا قلب بخشنده و مهربان، بزرگترین سرمایه است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9447,'آنان که نمی توانند خود را اداره کنند، ناچار به پیروی از دیگرانند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9448,'از کوچکی می خواستم بزرگ باشم.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9449,'من اشخاص زنده را کسانی می دانم که مبارزه می کنند؛ بدون مبارزه، زندگی، مرگ است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9450,'هیچ چیز مثل بدبختی، کودکان را ساکت نمی کند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9451,'زندگی، گلی است که عشق، شهد آن است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9452,'سراسر وجود انسان طرحی از خداست که در زمین گام برمی‌دارد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9453,'هرچه خدایی نیست، فرو ریختنی است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9454,'کمک زمینی، ممکن است کمک آسمانی باشد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9455,'یکی از دشوارترین کارها، بیرون کشیدن پیوسته ی ناپاکی از روح است [و این] کاری‌ است بس دشوار.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9456,'زمانی که دریچه ی قلب را باز کنید، روح، روشن و تابناک می‌گردد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9457,'به همان اندازه که نسبت به شکم‌های گرسنه مهربان هستیم، برای روح‌هایی که غذا نمی‌خورند نیز دل بسوزانیم.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9458,'اندیشه به سقوط [ =ریزش ]، اندیشیدنی، غمگین‌تر از این نیست.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9459,'اندیشه ها باید همیشه به سوی آینده پیش رود. اگر خدا می‌خواست که انسان به گذشته بازگردد، یک چشم، پشت سر او می گذاشت.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9460,'انسان اندیشمند، بیشتر، انسانی بردبار است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9461,'در تاریخی که خوبی، مروارید کمیابی‌ است، کسی که خوب بوده است از کسی که بزرگ بوده است، کمابیش، پیش می‌افتد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9462,'روزی جهانیان، همه، دست برادری به ‌یکدیگر خواهند داد و آن، روزی ا‌ست که بدبختی و تیره روزی در گستره‌ی جهان یافت نخواهد شد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9463,'برخی از افراد، هر چیز پاک و ناب و شادی‌آفرین را به پستی می‌کشانند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9464,'دلیری، به کارگیری مثبت زور است و ستم، به کار بردن منفی آن.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9465,'همیشه مردم بدکار، خودشان سبب وجود بدبختی می‌شوند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9466,'آزمندی، زنجیری‌ است که دست و پای هر آزاده‌ای را خواهد بست.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9467,'اختیار آینده‌ی آدمی هنوز بسی بیشتر به‌دست دلهاست تا به دست جانها. دوست داشتن، یگانه چیزی است که می‌تواند ابدیت را فراگیرد و سرشار سازد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9468,'سرافرازی، ستاره‌ای‌ است که نورش دیر به زمین می‌رسد، ماهی ا‌ست که اشعه‌ی آرام و کمرنگ آن تنها بر گورها می‌تابد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9469,'باید به طبقه‌ی پست جامعه کمک کرد، از خُرد کردن و بیزاری از آنها دست شست و به ویژه باید در آموزش و پرورش این گروه کوشید.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9470,'شکست، دایه‌ی غرور است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9471,'رشک بردن را نادیده بگیرید و به کلی آن را فراموش کنید؛ زیرا این حس، همیشه شما را شکنجه خواهد داد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9472,'لغزش انسان همواره تدریجی است. بدیها در وجود ما، پای حاضر آماده و نامرئی دارند. حتی کسانی از ما با ظاهر پاک و آراسته، چنین ویژگی هایی دارند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9473,'هنگامی که بیش از اندازه به زشتی می گراییم، سنگینی وجودمان، ترازمندی، از دست داده و ما را به ژرفای پرتگاه می‌کشاند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9474,'بزرگترین آزمون گیرنده، خداست و کوچکترین آزمون دهنده، بنده‌ی خدا.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9475,'اگر نمی‌خواهی تو را بیازمایند، کار خود را درست انجام بده.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9476,'آنچه می‌گویی بکن و آنچه می‌کنی بگو.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9477,'ای انسان، آیا می‌خواهی راستی را بیابی؟ راستی را جست و جو کن.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9478,'از لابه‌لای شدیدترین تاریکی ها، نور راستی برافروخته می شود.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9479,'برای کسی که در تصور زندگی می‌کند، راستی، بدبو است. آن که تشنه‌ی چاپلوسی‌ است، حیرت می‌آشامد و راستی، قی می کند [=برمی گرداند].((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9480,'کسی که در زندگی، هدف والا و ارجمند دارد، هرگز به دامن تسلیم و ناپاکی نخواهد غلتید.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9481,'پیش از آنکه خزان از راه برسد، از هر بهار بهره مند شو.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9482,'مردن، ناگوار نیست، بلکه راه زندگی را ندانستن ناگوارتر از مرگ است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9483,'نمی‌دانید تا چه اندازه، خودباوری سبب باور به هر چیز دیگر می‌شود.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9484,'اگر چشم و هم چشمی در زندگی بشر نبود، نه نوآوری‌ای می‌شد و نه کشفی.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9485,'چشم و هم چشمی درست، سبب درست کردن افراد و در نتیجه، سامان یافتن جامعه است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9486,'هرکس ارزش خود را خود، تعیین می‌کند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9487,'انسان راستگو، خوش سیماتر است، سرنوشت، او را چنین نقش کرده و خورشید شامگاهی تا حد خداوندی، سزاوار ستایش.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9488,'من مردی اندیشنده‌ام که درد را می‌شناسد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9489,'زندگی، بدبختی، گوشه نشینی، سرگردانی و تهیدستی، میادین کارزاری‌اند که قهرمانان آن، قهرمانان نادیدنی آن، گاه به مراتب نیرومندتر از قهرمانان نامی هستند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9490,'زیبایی که با دانش همراه نباشد، به گلی بی‌عطر و بو می‌ماند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9491,'بدون زشتی، زیبایی معنایی [ =چمی ] ندارد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9492,'شما یک ستاره را به دو دلیل می‌نگرید: برای آنکه درخشان است و برای آنکه نفوذناپذیر است. نزدیک به خود درخشندگی‌ای دلنوازتر و رازی بزر‌تر دارید و آن، زن است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9493,'زندگی بدون زن در نظر من گلستانی بدون گل و عطری بدون بو و خواب آسوده‌ای بدون رویای خوش، بیش نیست.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9494,'هر زن پاکدامنی، زیبا و دلپسند است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9495,'زندگانی، گل است و عشق، عسل آن.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9496,'عشق، یک نفس آسمانی از هوای بهشت است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9497,'خدا پایان شکوه جهان آفرینش است، عشق، پایان شکوه آدمی.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9498,'انسان، نیازمند عشق است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9499,'خلاصه [ =کوتاه ] کردن جهان آفرینش در یک نفر و بزرگ کردن یک نفر تا مقام خدایی، عشق یعنی این.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9500,'عشق، قانون و شهوت، دام است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9501,'برای عشق هیچ چیز کفایت نمی‌کند. عاشق، نیک بختی دارد، بهشت می‌خواهد، بهشت دارد، آسمان می‌خواهد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9502,'عشق، زشت و زیبا نمی‌شناسد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9503,'عشق پاک، می تواند نیکبخت‌ترین و دل‌انگیزترین زندگی را به وجود آورد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9504,'جان آدمی چه اندوهگین است، هنگامی که اندوه‌اش از عشق است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9505,'عشق راستین هرگز فرو نمی‌نشیند. روی هیزم سوخته را خاکستر می‌گیرد، ولی ستارگان، تابناکی خود را از دست نمی‌دهند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9506,'ازدواج چیز شگفت آوری‌ است، گاه شیران را روباه و گاه روبهان را شیر می‌کند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9507,'گل شادی‌بخش زندگی، تنها با تبسم ملکوتی [ =آسمانی ] کودک شکفته می‌شود.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9508,'ما فرزندان خود را بسیار بیش از آنکه آنها ما را دوست می‌دارند، دوست داریم.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9509,'بهترین دوستان من کسانی هستند که پیشانی و ابروهای آنها باز است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9510,'کسی که مورد توجه مردم قرار گیرد، ممکن نیست سبب برانگیختن خشم و کینه ی دشمنان نشود.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9511,'دوستان از ستایش بسیار زود خسته می‌شوند، ولی دشمنان از دشنام دادن هرگز خسته نمی‌شوند. دشنام زیانی نمی‌رساند و دشمنان از این بی‌خبرند. آنها نمی‌توانند از ناسزاگویی خودداری کنند و این، زیبایی کار است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9512,'درد و رنج، کوتاه است و زودگذر؛ برای فراموشی، اینها لازمه‌ی زندگی‌اند و گرانترین گورها جز حفره‌ای تاریک نیست.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9513,'اگر انسان بتواند رنج را نیز به‌مانند شهری ترک گوید، می‌تواند خوشبختی را از سر گیرد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9514,'سرمشق [ =نمونه‌ی ] چکامه سرا یکی است، طبیعت و رهبر او یکی‌ است، راستی.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9515,'در نوشتن از آنچه دیگران نوشته‌اند، نباید یاری خواست، بلکه از جان و دل خویشتن است که باید یاری جست.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9516,'تاریخ می‌آید و می‌رود، ولی هنر باقی می‌ماند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9517,'از دیده ی هنر، هیچ چیزی زشت نیست.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9518,'هنر می‌تواند نگهدار و نگهبان هر گوهری باشد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9519,'بدون دادگری، هیچ گونه پیشداوری درست نیست.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9520,'به باور من، هر چکامه سرای راستین باید با دیدگاههایی که از حقایق ابدی در او به ‌وجود می‌آید، بر همه‌ی اندیشه‌های زمان خود، چیره و توانا باشد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9521,'هنر، سرافرازی و شادمانی‌ است.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9522,'هنر، آتشی‌ است که در طوفان، برافروخته است و آسمان آبی را روشن می‌سازد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9523,'خنده، خورشیدی است که زمستان را از نژاد انسانی دور می‌کند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9524,'بدون امید، هزار آسمان فراوانی هم دیناری نمی ارزد.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9525,'تلاش و کوشش دو گونه است: تلاش درونی، که همانا اندیشیدن است و تلاش بیرونی، که همانا کردار است. این دو چیز همراه با یکدیگر، انسان را به نتیجه ی دلخواه می رساند.((ویکتور هوگو))',140,0,0,0,0,0);
INSERT INTO mysms VALUES(9526,'دنیای گمان، دنیایی راستین و جاودانه است و این دنیای فانی و بی‌ارزش، تنها سایه‌ی ناچیزی از آن.((ویلیام بلیک))',141,0,0,0,0,0);
INSERT INTO mysms VALUES(9527,'فراموش کردن و بخشودن، تنها هنر زیستن در بهشت است.((ویلیام بلیک))',141,0,0,0,0,0);
INSERT INTO mysms VALUES(9528,'گرانقدرترین کارها این است که اجازه دهیم دیگری از روبروی ما بگذرد.((ویلیام بلیک))',141,0,0,0,0,0);
INSERT INTO mysms VALUES(9529,'بی‌نهایت را در کف دست‌تان و ابدیت را در یک ساعت نگاه دارید.((ویلیام بلیک))',141,0,0,0,0,0);
INSERT INTO mysms VALUES(9530,'دنیا را در یک سنگریزه و بهشت را در یک شاخه، تنها یک شاخه گل صحرایی تماشا کن. بی‌کرانگی را در کف دستانت بگذار، که ابدیت، همین یک ساعت است.((ویلیام بلیک))',141,0,0,0,0,0);
INSERT INTO mysms VALUES(9531,'برای به دست آوردن گلهای کوچک باید سالها تلاش کرد.((ویلیام بلیک))',141,0,0,0,0,0);
INSERT INTO mysms VALUES(9532,'می توان دنیا را در یک ذره ی ماسه مشاهده نمود و بهشت را در یک گل وحشی؛ می توان ابدیت را دست گرفت و جاودانگی را در یک ساعت جا داد.((ویلیام بلیک))',141,0,0,0,0,0);
INSERT INTO mysms VALUES(9533,'والاترین عمل این است که دیگران را بر خود مقدم بدانیم.((ویلیام بلیک))',141,0,0,0,0,0);
INSERT INTO mysms VALUES(9534,'کسی که باورهای خود را هرگز تغییر نمی دهد همچون آب راکدی است که در آن، کرمها لانه می کنند.((ویلیام بلیک))',141,0,0,0,0,0);
INSERT INTO mysms VALUES(9535,'اگر بخواهیم احساسات خود را در زندان وجودمان پنهان کنیم، یک روز سرانجام ریشه‌های عشق، خودشان را از ژرفای گور بیرون خواهند کشید.((ویلیام بلیک))',141,0,0,0,0,0);
INSERT INTO mysms VALUES(9536,'نخستین تلاش من در مورد آزادی اراده، باور کردن این موهبت خواهد بود.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9537,'اصلی در روانشناسی می‌گوید که اگر تصویری از خواسته‌ی خود را در ذهن ترسیم کنید و آن تصویر را به اندازه‌ی کافی در آنجا نگاه دارید، دیری نمی‌پاید که خواسته‌ی شما به طور دقیق، برآورده می‌شود.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9538,'بنیادی‌ترین اصل در طبیعت آدمی، اشتیاق [=آرزو] فراوان اوست به گرامی داشته شدن.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9539,'بزرگترین کشف نسل امروز این است که انسان می تواند با تغییر ذهنیت خود، زندگی خود را تغییر دهد.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9540,'مردان شجاع فرصت می آفرینند، ترسوها و ضعیف ها منتظر فرصت می نشینند.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9541,'عاقل کسی است که می داند چه کارهایی را باید کنار بگذارد.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9542,'بزرگترین کشف عصر ما این است که انسانها می توانند با اصلاح نگرشهای ذهنی خود، زندگانی خویش را اصلاح کنند.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9543,'وقتی که تصمیمی گرفته شد و بدون معطلی به مرحله اجرا درآمد، به طور کلی نگرانی و استرس نسبت به نتیجه و سرانجام کار از بین می رود.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9544,'اتفاقات ناگوار را همان گونه که رخ داده بپذیر، زیرا پذیرفتن آنچه رخ داده اولین قدم برای پیروز شدن و غلبه بر نتایج دردناک است.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9545,'ما تنها به وسیله گرفتن تصمیم قادر به تغییر احساسات خود نیستیم، بلکه اگر در عین حال که تصمیم می گیریم قادر به دگرگون کردن اعمال خود باشیم، نتیجه خواهیم گرفت.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9546,'شاد و بانشاط باشید، بگویید و بشنوید و به غم و غصه مجالی برای خودنمایی ندهید.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9547,'با کمی مبارزه قادریم آن چیزهایی را که شر نامیده می شوند تبدیل به خیر کنیم.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9548,'هر انسان عادی تنها از یک دهم استعدادها و قدرت فکری خود استفاده می کند.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9549,'بشر امروزی هنور نتوانسته به آنچه که باید باشد و می تواند باشد دست یابد و این تنها به دلیل قانع بودن انسان به آن قسمتی از سعادت است که به نظرش کافی می آید.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9550,'ناخوشی ها و بیماریهای ما قادرند به گونه ای پیش بینی ناپذیر به ما کمک کنند!((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9551,'اعتقاد و باور دینی یکی از نیروهایی است که بشر به کمک آن زندگی می کند و اگر آن را به طور کامل از دست بدهد نتیجه ای جز سقوط ابناء بشری به همراه نخواهد داشت.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9552,'امواج خروشان سطح اقیانوس هرگز سکون و آرامش اعماق آن را بر هم نمی زند.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9553,'ایمان و باور ما در ابتدای هر مسئولیت دشواری تنها عاملی است که موفقیت نهایی مان را تضمین می کند.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9554,'زمانی که انتظار بهترین پیشامد را دارید، نیرویی مغناطیسی از مغزتان خارج می شود که بهترین ها را جذب می کند. اگر انتظار بدترین ها را داشته باشید از مغز قدرت دافعه ا ی رها می کنید که سبب می شود بهترین ها از شما بگریزد؛ حیرت انگیز است.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9555,'بسیاری از مردم می اندیشند که در حال اندیشیدن هستند، در حالی که سرگرم بازآرایی و تنظیم پیشداوری ها و تعصباتشان می باشند.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9556,'پذیرش آنچه رخ داده است، نخستین گام در غلبه بر پیامدهای هر گونه بدشانسی است.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9557,'فلسفه، والاترین و روشن ترین خواسته ی بشر است.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9558,'هر کس باید برای تمرین، هر روز دست کم دو کار را که از آن نفرت دارد انجام دهد.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9559,'انگیزه نهانی تمام کارهایی که بیشتر مردم انجام می دهند این است که چگونه شادی را به دست آورند، چگونه آن را نگه دارند و چگونه آن را بهبود بخشند.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9560,'هنر عاقل بودن عبارت است از هنر آگاهی از اینکه نسبت به چه چیزهایی چشم پوشی کنید.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9561,'تنها یک دلیل برای شکست انسان وجود دارد و آن کمبود ایمان فرد به خودِ واقعی اش است.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9562,'همه ما آمادگی داریم تا به دلایلی، کارهای وحشیانه و به دور از تمدن انجام دهیم. تفاوت بین یک آدم خوب و یک آدم بد در برگزیدن آن دلیل است.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9563,'ژرف ترین اصل طبیعت انسان، اشتیاق و میل شدید او به قدردانی شدن است.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9564,'تا زمانی که قلب شما نخواهد، به طور یقین مغزتان هرگز به چیزی باور پیدا نمی کند.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9565,'مغز ما یک دینام هزار ولتی است که متاسفانه بیشتر از یک چراغ موشی از آن استفاده نمی کنیم.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9566,'خوشبختی در جستجوی خوشبختی است نه یافتن آن.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9567,'خوشبختی، نشانه ی تندرستی روح است.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9568,'پیشامدهای باور نکردنی و بحرانهای بزرگ به ما نشان می دهند که سرچشمه های زندگی ما از آنچه می پنداشتیم باشکوه ترند.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9569,'از زندگی نترسید، باور داشته باشید، زندگی ارزش زیستن را دارد و باورتان در آفرینش این حقیقت به شما کمک خواهد کرد.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9570,'آواز خواندن، نتیجه ی شادمانی ما نیست، بلکه چون آواز می خوانیم، شادمانیم.((ویلیام جیمز))',142,0,0,0,0,0);
INSERT INTO mysms VALUES(9571,'اگر ملتی چیزی را بر آزادی ترجیح دهند، همه چیز را از دست خواهند داد.((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9572,'پول مانند حس ششم است که در صورت نبود آن، دیگر حواس پنجگانه به خوبی کار نمی‌کنند.((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9573,'مردم حتی زمانی هم که از شما می خواهند معایب شان را آشکارا به آنها بگویید، انتظار ستایش و تحسین دارند.((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9574,'درد پیری، انحطاط روحی و جسمانی آن نیست، بلکه بار خاطرات آن است.((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9575,'تا پیش از ازدواج تنها مرگ می تواند دو عاشق دلداده را از هم جدا کند، اما بعد از ازدواج کمابیش هر چیزی می تواند سبب جدایی آنان شود!((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9576,'با کمی عقل، اندکی گذشت و مقداری اخلاق خوب خوشبخت ترین فرد دنیا خواهید بود.((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9577,'پول مانند حس ششم است که بدون آن نمی توان از پنج حس دیگر به گونه ای کامل استفاده کرد. بدون درآمد کافی، نیمی از امکانات زندگی از ما گرفته خواهد شد.((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9578,'کمی عقل سلیم، اندکی گذشت و قدری خوش خلقی داشته باشید، آن وقت خواهید دید در این دنیا چقدر آسوده و خوشبخت هستید.((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9579,'فاجعه بزرگ زندگی در مرگ انسان نیست، در دست شستن از عشق ورزیدن است.((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9580,'زندگی، جالب و در عین حال، مسخره است. اگر از پذیرش بهترین چیزهای آن سر باز بزنید، چه بسا همین، روزی شما می شود.((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9581,'یک چیز جالب در مورد زندگی این است که بیشتر زمانها، چیزهایی را که از آنها دوری می کنید، به اندازه ی فراوان به شما می دهد.((ویلیام سامرست موام))',143,0,0,0,0,0);
INSERT INTO mysms VALUES(9582,'آن کس که جرات انجام کارهای شایسته را دارد، انسان است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9583,'من همیشه تمایل دارم که از اشخاص نجیب پیروی کرده و از آنان چیز بیاموزم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9584,'از دست دادن امیدی پوچ و محال، خود موفقیت و پیشرفتی بزرگ است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9585,'مردم بینوا و تیره بخت، درمانی جز امید ندارند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9586,'گذشت، خیر و برکتی‌ست دو جانبه، که هم دهنده و هم دریافت کننده را بهره‌مند می‌کند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9587,'گذشت، نیرومندترین مولد نیروی جهان است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9588,'هنگامی که گفتگو کردن، کاری از پیش نمی‌برد، سکوت بهتر است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9589,'افراد پرشتاب و ناشکیبا، بسیار ناتوان و بیچاره‌اند. کدام زخم تاکنون با شتاب و بی‌تابی بهبود یافته است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9590,'ما به سبب بی‌خبری از شیوه‌ی کار جهان، بیشتر، چیزهایی را خواستاریم که به زیان ماست و کردگار یکتا به سبب مصلحت‌مان، از برآورده ساختن آن، سر باز می‌زند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9591,'در جستجوی گناهکار در ستارگان نباشید، سبب در خود ماست.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9592,'صورت شما کتابی است که مردم می توانند از آن چیزهای عجیب بخوانند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9593,'آدم تبهکار می رود، ولی زیانهایش بعد از او می ماند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9594,'دیوانه خودش را عاقل می پندارد و عاقل هم می داند که دیوانه ای بیش نیست.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9595,'حقیقت را همانطور که هست، بپذیر.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9596,'هنگامی که فقر از در وارد شود، عشق از پنجره می گریزد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9597,'آنچه را داریم و از آن لذت می بریم، چنان که باید ارج نمی نهیم و قدر آن را نمی شناسیم و چون از دست برود به ارزش آن پی می بریم. در این هنگام است که به این حقیقت دست می یابیم که تا مالک چیزی هستیم از مالکیت خود بی خبریم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9598,'بودن یا نبودن؛ مساله اینست.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9599,'دنیا مانند یک تماشاخانه است؛ هر کس نقش خود را بازی می کند و سپس مخفی می شود.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9600,'دنیا یکسره صحنه بازی است و همه بازیگران آن به نوبت می آیند و می روند و نقش خود را به دیگری می سپارند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9601,'گریه ما در هنگام تولد از آن است که به صحنه بزرگ جنون و حماقت وارد شده ایم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9602,'زنبور هر قدر زیاد باشد، گل از آن بیشتر است و دلهای ماتم زده هر اندازه باشند، قلبهای شاد زیادترند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9603,'تشویق ها، رویاها، آه ها، آرزوها و اشکها از همراهان جدایی ناپذیر عشق اند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9604,'عشق در حقیقت عذاب است، ولی محروم بودن از عشق، مرگ است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9605,'دشمنان بسیار دارید که نمی دانند چرا دشمن شما هستند؛ همچون سگهای ولگرد هنگامی که رفیقشان بانگ بردارد، آنان نیز بانگ بر می دارند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9606,'کینه پنهان نمی ماند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9607,'بالاتر از همه چیز این است که با خودمان صادق باشیم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9608,'چیزی که در زن مرا تسخیر می کند مهربانی اوست، نه روی زیبایش؛ من زنی را بیشتر دوست دارم که مهربانتر باشد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9609,'یک زن چیزی جز شوهر نمی خواهد، ولی وقتی به او رسید همه چیز می خواهد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9610,'چاپلوسی خوراک ابلهان است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9611,'هرگز از شنیدن آنچه که شرافتمندانه انجام داده ای، شرم نداشته باش.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9612,'باید ترسید؛ آنگاه که مستبدان مهربان می شوند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9613,'آنان که پیروز می شوند، همان کسانی هستند که از مشورت دوستان بهره می برند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9614,'به همه عشق بورز، به تعداد کمی اعتماد کن و به هیچ کس بدی نکن.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9615,'عده ای بزرگ زاده می شوند، عده ای بزرگی را به دست می آورند و عده ای بزرگی را بدون آنکه بخواهند با خود دارند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9616,'برای آنچه که انجام داده ای، غم خوردن بی فایده است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9617,'اگر شرافتم را از دست بدهم، وجودم را از دست داده ام.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9618,'شرافتم را از من بگیر و بنگر که چگونه زندگی من تباه می شود.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9619,'عادل باش و هراسی به دل راه نده.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9620,'عشق ما را می کشد تا دوباره حیاتمان بخشد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9621,'از تیز ترین چاقو هم اگر استفاده نشود، کند می گردد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9622,'فصیح ترین زبان، عمل است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9623,'نصایح دوستانه، پایان بخش اندوه ماست.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9624,'جنبش، اهرم دگرگونی است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9625,'خدا عشق را به شما هدیه نداد تا آن را در قلب خویش نگه دارید؛ عشقی که ابراز نشود، عشق نیست.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9626,'کوره را برای سوزاندن دشمنان خود آنقدر شعله ور نکن که شعله اش خودت را بسوزاند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9627,'داشتن فرزندانی ناسپاس بسیار بدتر از دندان تیز افعی است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9628,'سر بر گریبان فرو بر، از دل خویش بپرس آنچه را که می داند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9629,'کدامین کس عاشق گشته، لیک نه در نگاه نخست؟((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9630,'در آن زمان که به شدت احساس تنهایی می کنی، مطمئن باش که یکی برای دیدنت لحظه شماری می کند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9631,'هیچ چیز خوب یا بد نیست، بلکه شیوه اندیشیدن ما است که آنها را نیک یا ناپسند جلوه گر می کند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9632,'یقین است که رفتار حکیمانه با وضع جاهلانه همچون بیماری از یک شخص به شخص دیگر سرایت می کند؛ پس لازم است که مردمان مراقب انتخاب همنشینان خود باشند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9633,'من وقت را تلف کرده ام و اینک وقت، مرا تلف می کند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9634,'تا به حال هیچ فیلسوفی قدم به عرصه خاک نگذاشته که بتواند دندان درد را تاب آورد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9635,'عشق حقیقی هیچگاه یکنواخت و آرام پیش نمی رود.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9636,'آنان که عشق خود را آشکار نکنند معشوق نخواهند بود.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9637,'کسی را که دوست داری رهایش کن؛ اگر سوی تو بازگشت از آن توست و اگر بازنگشت، از ابتدا برای تو نبوده است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9638,'وجود ما به منزله باغی است که اراده ی ما باغبان آن است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9639,'دوستانی که داری و آنان را آزموده ای، با قلاب های پولادین به جان و دل خود پیوسته نگاه دار.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9640,'دوستی نعمت گرانبهایی است، خوشبختی را دو برابر می کند و بدبختی را کاهش می دهد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9641,'یک بار دیگر، دوستان عزیز، یک بار دیگر برق آسا حمله کنید!((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9642,'شکوه دنیوی همچون دایره ای است بر سطح آب که لحظه به لحظه به بزرگی آن افزوده می شود و سپس در نهایتِ بزرگی، هیچ می شود.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9643,'بر این باورم که سرنوشت انسان را تنها محبت مشخص می کند و بس.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9644,'لبخند می تواند جراحت اخمی را بهبود بخشد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9645,'به شخصی که به هیچ کس اعتماد ندارد اطمینان نداشته باشید!((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9646,'مرگ را فراخوان و فتنه ی خفته جنگ را بیدار کن.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9647,'داشتن دانش بهتر از داشتن ثروت است، اما نداشتن ثروت بدتر از نداشتن دانش است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9648,'دیرگاهی که مرگش فرارسید، ذره ذره از پیکرش ستاره بساز! آنگاه طاق نیلگون آسمان را چراغانی خواهد کرد تا جهان عاشق به شب تبدیل شود و هیچ انسانی به ستایش خورشید خودپرست ننشیند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9649,'عشق و فروتنی، جامه ای برازنده ی مرشدی پرهیزگار باشد، نه سزاوار جاه طلبی مغرور.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9650,'من از خوشبختی های این جهان بهرمند گردیده ام زیرا در زندگی عاشق شده ام.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9651,'فقر در کوی هنرمندان و رنجبران راه ندارد و شادمانی در خانه ای است که مهربانی در آنجا حکومت می کند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9652,'فروتنی، نردبان بزرگی و بلندی است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9653,'افرادی که توانایی لبخند زدن و خندیدن دارند، موجوداتی برتر هستند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9654,'عشق، بیشتر گونه ای عذاب است، اما بی بهره بودن از آن مرگ است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9655,'سقوط ژرفتر، آن گونه که رایج است، به خوشبختی بیشتر منتهی می شود.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9656,'صبر عبارت است از حفظ آرامش برای رسیدن به نتیجه.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9657,'هیچ چیز وجود ندارد، مگر اندیشه ای که آن را می سازد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9658,'زمانی که گفتم تا آخر عمر مجرد می مانم نمی دانستم آنقدر عمر می کنم که ازدواج کنم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9659,'اشتباه را محکوم کن نه آنکه را اشتباه از او سر زده است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9660,'اگر تمام شب برای دیدن خورشید گریه کنی لذت دیدن ستاره ها را از دست خواهی داد!((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9661,'زمانی که فکر می کنی توی آسمون یک ستاره هم نداری، یکی یه گوشه ی دنیا هست که برای دیدنت لحظه شماری می کنه!((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9662,'عشق مثل آبه، می تونی تو دستات قایمش کنی، اما یه روز دستاتو باز می کنی می بینی همش چکیده بی اینکه بفهمی دستت پر از خاطرست.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9663,'عشق، فریب زمان را نمی خورد، حتی اگر لبان و گونه ی سرخ او زیر تیغ داس قرار گیرد. عشق با گذشت ساعتها و هفته های کوتاه تغییر نمی یابد، بلکه تا پایان هستی ادامه خواهد داشت.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9664,'سه جمله برای دستیابی به موفقیت: 1) بیشتر از دیگران بدان 2) بیشتر از دیگران کار کن 3) کمتر از دیگران توقع داشته باش((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9665,'اگر فضیلتی ندارید، وانمود کنید که [ آن را ] دارید.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9666,'آنان که توان آزردن دارند و هیچ کس را نمی آزارند به درستی که وارثان شکوه ملکوتند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9667,'کسی را که دوست داری آزادش بگذار، اگر از آن تو باشد باز می گردد، وگرنه بدان که از ابتدا از آن تو نبوده است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9668,'تردیدها به ما خیانت می کنند، ما را از تلاش به دور می سازند و از پیروزی هایی که به احتمال زیاد نصیب ما خواهد شد محروم می سازند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9669,'عدالت قابل تربیت [ برای منافع خودمان ] نیست. هر کاری که انجام دهیم نتیجه آن را می بینیم و خطاهایی که از ما سر می زند تازیانه ای را به وجود می آورد که با آن مجازات می شویم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9670,'پول کالایی است که می توان با آن به همه جا وارد شد و همه چیز را به دست آورد، جز شرافت و سعادت.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9671,'هر کس فقیر و قانع باشد، ثروتمند است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9672,'ای فتنه و فساد، تو چه زود در اندیشه مردان ناامید رخنه می کنی.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9673,'ما از جنس رویاهایمان هستیم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9674,'این قانون طبیعت است که هیچ کس به تنهایی نمی تواند خوشبخت باشد، بلکه خوشبختی و سعادت را باید در سعادت و خوشبختی دیگران جستجو کرد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9675,'اگر قرار است زندگی خود را برای چیزی خرج کنیم، بهتر است که آن را خرج لطافت یک لبخند و یا نوازشی عاشقانه کنیم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9676,'روزگار [ مانند ] دریایی است که کشتی زندگی ما بر روی آن به طرف ساحل هدف می رود. این دریای بزرگ همیشه در [ حال ] جزر و مد است. اگر امروز آرام باشد، به طور حتم فردا طوفانی خواهد بود. بنابراین وقتی آرام است، [ باید ] فرصت را غنیمت شماریم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9677,'هیچ میراثی گرانبهاتر از راستی و درستی نیست. ((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9678,'ما در طول عمرمان مثل بازیگرانی هستیم که چند لحظه ای روی صحنه ظاهر می شویم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9679,'از دست دادن امیدی پوچ و آرزویی دست نایافتنی، خود، کامیابی و پیشرفت بزرگی است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9680,'فرد خردمند و هوشیار، هرجا گام نهد، نیک بختی و آسودگی خاطر، همراه او است؛ زیرا در جهان به جز خوبی و زیبایی، چیزی نمی بیند.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9681,'چه شاهکاری است هر انسان.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9682,'یک قلب سرزنده، زندگی دراز دارد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9683,'عشق جستجو شده خوب است، ولی عشق داده شده بدون جستجو بهتر است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9684,'اگر بمب‌گذار با بمب خودش کشته شود، دیگر احساس بدی نخواهیم داشت.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9685,'تردیدهای ما خائنانی هستند که با ایجاد ترس در درونمان، ما را از تلاش باز می دارند و در نتیجه، سبب می شوند که نتایج دلخواه را به دست نیاوریم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9686,'باید توانایی پذیرش رویدادها را داشته باشیم.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9687,'هرآنچه با نادرستی به دست آید، سازواری ناپسندی خواهد داشت.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9688,'بُرد، با آن کنشی است که برای انجام اش همت گماشته شده است. شادی و مسرت روح، در گرو کردار و کنش است.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9689,'آوایی که از ژرفای وجودمان می شنویم، روح امیدوار را ناامید نخواهد کرد.((ویلیام شکسپیر))',144,0,0,0,0,0);
INSERT INTO mysms VALUES(9690,'خوشبختی، چیزی‌ست که در وجود شماست و از دیدگاه شما سرچشمه می‌گیرد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9691,'کمبود فرصتی وجود ندارد که با آنچه عاشق آن هستیم، زندگی خود را بسازیم، فقط کمبود اراده وجود دارد تا سبب روی دادن آن شود.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9692,'امکانات در دسترس یا دسترسی‌ناپذیر، سطح کامیابی و خوشبختی شما را تعیین نمی‌کند؛ بلکه آنچه که درست می‌دانید، تعیین کننده‌ی سطح کامیابی و خوشبختی شماست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9693,'از داوری درباره‌ی اهمیت یا ارزش دیگران بر اساس آنچه شما آن را طبیعی و عادی به شمار می‌آورید، بپرهیزید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9694,'همواره در اکنون و اینجا به سر ببرید؛ جای دیگری برای بودن وجود ندارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9695,'هنگامی که با شور و اشتیاق، با کاری که به شدت آن را دوست داریم، درگیر می‌شویم، اندیشه‌های ما دگرگون می‌شود و محدودیت‌ها را به هر شکل ممکن از خود می‌راند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9696,'در درون شما فردی بزرگ‌‌تر که قدر و اهمیت او هرگز برای شما قابل تصور نیست، آشیان دارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9697,'به باور من، به جای ترس از گذشت زمان، می‌توان هر روز را با راستی، سازش و آرامش و عشق سپری کرد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9698,'از چشم‌انداز ابدیت به همه چیز بنگرید تا مشاهدات شما از زمان تاثیر نپذیرد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9699,'تا می‌توانید نیروی عشق را در وجود خود و دیگران، گسترش دهید و اندیشه‌های برگرفته از خشم و دشمنی و داروی را مهار کنید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9700,'به جای تردید و دودلی و یا تلاش برای کشف اسرار نظام هستی، آن را باور کنید و از خشنودی و خرسندی‌ای که میوه و ثمره‌ی این باور است، بهره‌مند شوید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9701,'هیچ بانگی شیرین‌تر از بانگ خنده، به ویژه خنده‌ی کودک نیست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9702,'من از گفتگو و همنشینی با افرادی که بتوانند بیشتر بخندند و همین واکنش را در من برانگیزند، بسیار لذت می‌برم.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9703,'با تلاشی آگاهانه، خنده را به کردار و رفتار هر روزتان بیامیزید. نگذارید که روزی بدون خنده و آرامش برآمده از آن، سپری شود.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9704,'خودتان باشید و با پیروی از آنچه می‌دانید، هماهنگ با ذات معنوی خویش، درست زندگی کنید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9705,'خودتان باشید؛ برای خود، احترام قائل شوید و بین انسجام و یکپارچگی شخصیت خویش و رفتار روزانه‌تان، هماهنگی بیافرینید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9706,'هر بار که شراره‌های خشم در درون‌تان جرقه می‌زند، تردید نکنید که به سوی باتلاقی زمین‌گیر کننده گام برمی‌دارید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9707,'گذشت، بار سنگین دشمنی و کینه‌توزی را از دوش شما برمی‌دارد و فضای ذهن‌تان را برای اندیشه‌های مثبت و سازنده، پاک و روشن می‌سازد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9708,'زیستن در پرتو بخشش و گذشت و بهره‌گیری از آن در هر روز از زندگی، راهی‌ست برای مهار امیال نفسانی و پرورش عشق و مهربانی.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9709,'مدار جهان، بر گذشت و بخشودگی می‌گردد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9710,'آنچه را که بتوانیم در دنیای پندار ببینیم، در هشیاری نیز دست یافتنی است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9711,'همواره به یاد داشته باشید که شما همان می‌شوید که می‌پندارید باید بشوید. بنابراین، اندیشه‌هایی که بذر شک و دودلی را در کشتزار ذهن‌تان می‌کارند، از خود برانید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9712,'از یاد نبرید که چاشنی زندگی، انجام کارهای تازه است که فقط با برانگیختن نیروی تخیل، عملی می‌شود.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9713,'بین دانستن یک نکته و دانستن درباره‌ی آن نکته، تفاوت بسیار است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9714,'به راستی، همه‌ی معجزه‌ها نتیجه‌ی تغییر جهت از تردید به سوی آگاهی‌ست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9715,'شما نمی‌توانید با تلاش و کوشش دیگران، چیزی بیاموزید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9716,'تا هنگامی که دیدگاه‌ دیگران را بر پایه‌ی آگاهی خویش نپذیرید، حتی بزرگترین استادان جهان هم نمی‌توانند چیزی به شما بیاموزند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9717,'پیش از بیان درستی یک چیز و پیروی از آن، بکوشید ذهن خود را به تجربه‌ی مستقیم هر چیز متمرکز کنید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9718,'از نیرو بخشیدن به چیزهایی که به آن باور ندارید و می‌دانید که برای شما مفید و سودمند نیست، بپرهیزید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9719,'رهبران راستین، با نام و نشان شناخته نمی‌شوند. نام‌ها و نشان‌ها برانگیخته از خودخواهی و خودپرستی است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9720,'رهبران راستین، از باور مردم لذت می‌برند، در حالی که دروغگویی، چرب‌زبانی و قدرت که زاییده‌ی خودپرستی است، از نشانه‌های رهبران دروغین به شمار می‌رود.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9721,'پیش از کسب اعتماد دیگران، اعتماد خودتان را به آنان نشان دهید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9722,'هرچه خانه‌ی دل را از آرامش بیشتر لبریز کنید، آسیب‌پذیری‌تان در برابر دشمنی و بدخواهی دیگران کمتر می‌شود.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9723,'هر روز، سازش و آرامش بیشتری را به زندگی‌تان فراخوانید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9724,'از یاد نبرید که در برابر هر کار غیر انسانی، هزاران کار برآمده از مهربانی و گذشت و خیرخواهی وجود دارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9725,'سنجش خود با دیگران در هر زمان و در هر شرایط، کاری پوچ و نابخردانه است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9726,'انسان برای دستیابی به آسودگی خیال، رفاه، آسایش و همه‌ی چیزهای نیکو، تنها باید خودش باشد و مسئولیت همه‌ی احساسات خویش را بر دوش خود بگذارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9727,'به منش و ویژگی‌های خاص خود ارج نهید و با اندیشه‌هایی که شما را به سنجش فرا می‌خواند، به رویارویی برخیزید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9728,'فراموش نکنید که شما با سنجش، افسار زندگی‌تان را به دستان افرادی که خود را با آنان می‌سنجید، می‌سپارید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9729,'اگر آن اندازه که می‌خواهید بااستعداد و یا کارآمد نیستید، به خود یادآور شوید که این وضعیت، به دلیل کمبود و کاستی‌تان نیست، بلکه به سبب واکنش خاص و یا تجربه‌ی شما نسبت به آن رویداد است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9730,'روشن‌بینی؛ چیزی نیست که در موقعیت نیلوفر آبی در غاری بر فراز قله‌ی هیمالیا یافت شود و یا آن را از یک مرشد و با یک کتاب و یا یک دوره‌ی آموزشی، کسب کنید؛ بلکه صرفاً نگرشی‌ست نسبت به هر کاری که می‌کنید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9731,'به باور من، آگاهی از زمان‌های بدون سازش و آرامش، یکی از روش‌های روشن‌بین شدن است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9732,'هرگز ناگزیر نیستید آنچه را که در درون و یا بیرون خود می‌بینید، دگرگون سازید؛ فقط کافی است زاویه‌ی دیدتان را دگرگون کنید و راه گذر ضمیر برتر خود را بگشایید؛ این است روشن‌بینی.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9733,'شناخت لحظه‌‌هایی که با روشن‌بینی، فرسنگ‌ها فاصله دارد، آغاز تغییر مسیر به سوی روشن‌بینی است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9734,'بخشودن، آسان، شادی‌بخش و از همه مهم‌تر، رهایی‌بخش است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9735,'هنگامی که بخشش و گذشت را می‌آموزیم، نسبت به افرادی که اسباب آشفتگی‌مان را فراهم آوردند و دشنام و نفرین، پیشکش‌مان کردند، در مرتبه‌ی بالاتری هستیم.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9736,'تلاش کردن و نتیجه نگرفتن، بهتر از هرگز تلاش نکردن است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9737,'پریدن در گرداب زندگی و تجربه کردن آشوب آن، بهتر از آن است که از ترس غرق شدن، کنار بایستیم و فقط به آن بنگریم.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9738,'هرگاه سراپا مشتاق هدفی باشید و همه جوره باور داشته باشید که می‌توانید آن را انجام دهید و از ارتکاب خطا ترس به دل راه ندهید، خواسته‌ی شما نمی‌تواند از تحقق باز ایستد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9739,'راه گریز از ترس و شکست، رویارویی و به مسخره گرفتن آن است، و نه خودباختگی در برخورد با نخستین پیامدهای آن.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9740,'هر ناکامی و عقب‌ماندگی، چنانچه با اندیشه‌ی مثبت ارزیابی شود، به جای اینکه از بزرگی ما بکاهد، آن را نیرو می‌بخشد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9741,'شکیبایی، میوه و ثمره‌ی باور، قاطعیت و خرسندی است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9742,'اگر برخلاف تمایلات ژرف نسبت به یک چیز، هیچ کاری برای رسیدن به آن نکنید، زهرآگین‌ترین دانه‌های افسوس و پشیمانی را در کشتزار قلب خود می‌کارید و راه را بر جریان نیروهای آفریننده‌‌تان می‌بندید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9743,'فراموش نکنید که تصویری که از خود بر ذهن‌تان ترسیم می‌کنید، از وجود خودتان سرچشمه می‌گیرد و هیچ کس دیگر در این زمینه مسئول نیست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9744,'ما بدون توجه به اینکه تا چه اندازه گمان می‌کنیم باید غمگین باشیم، آه بکشیم و گریه کنیم، نسبت به میزان خنده و شادمانی و سرخوشی زندگی‌مان، اختیار تام داریم.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9745,'آرمان‌ها، اندیشه‌ها و مفاهیمی که ما درباره‌ی خود داریم، جوانی، شادابی و تندرستی ما را رقم می‌زند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9746,'اگر بخواهید، می‌توانید در هر زمینه‌ای خوب و ماهر باشید و از آن، لذت ببرید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9747,'شور و اشتیاق خود را در زندگی نشان دهید و به هر شکل ممکن، این نیرو را بر جهان و انسان‌های پیرامون‌تان، بازتاب دهید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9748,'شما همواره می‌توانید با کسالت و سستی، روح خود را به خوابی ژرف فرو ببرید و یا با تلاش و اشتیاق، روح‌تان را تازگی ببخشید و از حضورش در این کالبد فیزیکی لذت ببرید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9749,'جهان، مکانی نیکوست که همه می‌توانند در آن خوشبخت شوند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9750,'تا زمانی که بذر مهر و محبت را در کشتزار دل پرورش ندهیم، احساس کامروایی نمی‌کنیم.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9751,'خواندن و مطالعه کردن، سبب می‌شود که تجربه‌های زندگی شما در همه‌ی زمینه‌ها، ژرف‌تر و غنی‌تر شود.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9752,'هرگاه با مساله‌ای که حل شدنی به نظر نمی‌رسد، روبرو می‌شوید، به خود یادآور شوید که حل و فصل آن در گرو یک واکنش مناسب است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9753,'هنگام انجام کارهای بزرگ، برخی از رفتارهای ناشی از خودبینی‌تان را مهار کنید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9754,'کردار، کارآمدترین شیوه برای آموختن است و نه پند و اندرز.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9755,'سخنی که با کردار، پشتیبانی نشود، اندرزی پوچ و ناکارآمد است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9756,'با رفتار و کردار خود، به دیگران درس می‌دهید و هدف‌های خود را بیشتر برآورده می‌سازید؛ در حالی که همه‌ی واژه‌های یک فرهنگ لغت، چنین کارآیی‌ای ندارند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9757,'شما در ذهن‌تان همواره از این آزادی برخوردارید که در سازش و آرامش، غوطه‌ور شوید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9758,'ذهن شما مسئول تندرستی و آرامش شماست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9759,'هیچ چیز و هیچ کس، بیرون از وجودتان نمی‌تواند بدون خواست شما دلگیرتان کند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9760,'انسان در دل طبیعت، همه‌ی داوری‌ها و ادعاهای پوچ و توخالی خود را رها می‌کند؛ زیرا طبیعت با داوری میانه‌ای ندارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9761,'یکی از ویژگی‌های نوابغ آن است که چون کودکان، کنجکاوند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9762,'بچه‌ها و نوابغ، هر دو بدون راه دادن اندیشه‌ی شکست به ذهن و یا دل‌نگرانی از انتقاد و سرزنش دیگران، آرزومند اکتشاف و یافتن پدیده‌های بکر و پویا هستند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9763,'هیچ‌گاه درباره‌ی به پایان رساندن هرچه پرشتاب‌تر کارها و یا حجم و گستردگی آنها نیندیشید؛ تنها کافی‌ست که از همین زمان‌ها بهره گیرید و برخیزید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9764,'برای شکوفایی نبوغ نهفته در درون‌تان، می‌بایست همت کنید و الهام خود را عینیت و فعلیت بخشید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9765,'همت و جسارت برای برداشتن نخستین گام، نیرویی‌ست شگرف که می‌تواند زندگی و هستی شما را از جهات گوناگون روحی، معنوی، مادی، مالی و اجتماعی دگرگون سازد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9766,'سستی و کاهلی برای درگیر شدن در کارها سبب می‌شود که در گرداب سکون و بی‌تحرکی فرو روید و به ذهن خویش، اجازه‌ی تکاپو ندهید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9767,'این دست و آن دست کردن کارها سبب می‌شود که زمان گرانمایه‌ی امروز را نیز از دست بدهید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9768,'هر بهانه‌ای، جز بهانه، چیز دیگری نیست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9769,'سروده، زبان دل است، نه گونه‌ای سرگرمی و یا چیزی که می‌بایست در مدرسه آموخته شود.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9770,'در برابر تاثیرات محیطی، گردن ننهید و نگذارید شما را به زور انضباط به قالب الگوی کار ویژه‌ای درآورند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9771,'بکوشید در روابط خویش، دست از داوری بشویید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9772,'برای پیش رفتن در مسیر ایده‌آل خویش، فقط به ندای باطن‌تان گوش فرا دهید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9773,'ثمره و نتیجه‌ی استقلال رای نسبت به همرنگی با جماعت، قابل سنجش نیست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9774,'افرادی که از کارآمدی بالایی برخوردارند، در ایجاد توازن و هماهنگی مورد نیاز بین جنبه‌های گوناگون زندگی‌شان نیز مهارت دارند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9775,'برای اینکه زندگی گرانمایه‌تان در راه دستیابی به چیزی هدر نرود، می‌بایست گاه از آن چیز فاصله بگیرید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9776,'هرچه برای انجام و یا تکمیل یک کار، کمتر دچار استرس و تنش شویم، نیروی بیشتری برای به ثمر رساندن آن خواهیم داشت.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9777,'فراموش نکنید که مرگ، یک بار تجربه می‌شود؛ ولی ترس از مرگ، در هر دَم، شما را می‌كُشد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9778,'احساس نیاز به تکبر و فخرفروشی را در وجودتان سرکوب کنید و در برابر، دستاوردهای دیگران را نیکو شمارید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9779,'رویدادهای زندگی هر فرد، تابعی از اندیشه‌ی اوست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9780,'گردن نهادن به معیارهای خُرد و چشم‌داشت‌های کوچک، نیرومندترین خطری‌ست که ما با آن روبرو هستیم.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9781,'عشق، انگیزه‌ی دستیابی انسان به درجات عالی و متعالی‌ست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9782,'هرگز به پند و اندرز افرادی که می‌کوشند با بدبینی‌هایشان شما را تحت نفوذ درآورند، گوش فرا ندهید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9783,'به این حقیقت دست یافته‌ام که شکست، مایه و برانگیزاننده‌ی اصلی انسان است؛ زیرا فرصتی به ما می‌دهد تا نیروی مورد نیاز را برای حرکت به سوی هشیاری برتر پدید آوریم.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9784,'آنچه شما به راستی، به راستی، به راستی، به راستی بخواهید، آن را به دست خواهید آورد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9785,'آنچه را که در دنیای گمان، خواستارش هستید، در تنهایی با پژواکی بلند بخواهید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9786,'خواسته‌ی خود را به میل و آرزو تبدیل کنید؛ شما تجلی همه‌ی برکت‌های بی‌کرانه‌اید و سزاوارید که از وفور نعمت جهان هستی بهره‌مند باشید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9787,'افرادی که به راستی، به راستی، در دستیابی به خواسته‌ها و آرزوهایشان، کامیاب‌ هستند، به بخت و سرنوشت خود متکی نیستند و کاسه و کوزه‌ی بی‌تحرکی خود را بر سر دنیای فانی نمی‌شکنند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9788,'برای امیدها و آرزوهای خود، محدودیت قائل نشوید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9789,'اختصاص همه‌ی نیروی زندگی به کار، جز آشفتگی، خشم و دلزدگی و وارونه کردن اولویت‌های راستین زندگی، فرجامی ندارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9790,'دیدگاه شما درباره‌ی کارتان، نود و نه درصد منش راستین شماست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9791,'اگر شما سرگرم کاری هستید که آن را دوست ندارید، و یا آنچه را که دوست دارید، انجام نمی‌دهید، تردید نکنید ناهنجاری را بر سکون و آرامش، برتری داده‌اید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9792,'برای اینکه میزان شکوه و شادمانی هر کس را بسنجیم، باید ببینیم که برای مهار آرزوهای خود تا چه اندازه توانایی دارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9793,'باور به این نکته که رویدادهای گذشته، قایق زندگی ما را به پیش می‌راند و یا آن را از حرکت در شرایط کنونی‌ باز می‌دارد، فقط یک پندار نادرست است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9794,'کمبودها و نابسامانی‌های کنونی زندگی را به گذشته نسبت ندهید. شما نتیجه و ثمره‌ی تصمیم‌های کنونی زندگی‌تان هستید و هیچ چیز گذشته در شرایط کنونی‌تان تاثیر ندارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9795,'از افسوس خوردن نسبت به رویدادهای گذشته دست بشویید. غم و اندوه و افسوس، کوچک‌ترین تغییر در گذشته‌ی شما پدید نمی‌آورد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9796,'بدون آنکه بگذارید یادبودهای دردناک گذشته بر شما چیره گردند، به آرامی در ذهن، مرورشان کنید و از آن تجربه‌ها، درس بیاموزید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9797,'برای رسیدن به خوشبختی، راهی وجود ندارد، چرا که خوشبختی، خود یک راه است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9798,'با گناهکار دانستن دیگران، جز هدر دادن زمان، کاری نمی‌کنید. مهم نیست دیگری را چه اندازه گناهکار بدانید یا به چه میزان، او را سرزنش کنید؛ هیچ کدام از این‌ها شما را تغییر نخواهد داد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9799,'آنچه هستید نتیجه فکر شما است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9800,'در انجام هر کاری، نیاز به خداوند و خدمت به مردم را در نظر داشته باشید، که این شیوه ی زندگی معجزه آفرینان است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9801,'راهی به سوی خوشبختی وجود ندارد؛ خوشبختی خود راه است، راهی که از درون شما آغاز می شود و به کمک توانایی و قابلیت شما در جهان برون تجلی می یابد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9802,'بهتر است مخالفت خود را با چیزی به صورت موافقت خود با چیزی دیگر بیان کنید! به جای مبارزه با جنگ، برای صلح بکوشید، به جای مبارزه با فقر، برای فراوانی تلاش کنید، و به جای تکیه ی افراطی بر منع کردن از بدی، انجام کارهای نیک را تبلیغ کنید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9803,'به جای اینکه به باغ دیگران سرک بکشید و ذهن خود را به کار ایشان متمرکز کنید، به کار خود بپردازید و هر چه می خواهید در باغ خود بکارید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9804,'اگر دو عبارت «خسته ام» و «حالم خوب نیست» را از زندگی خود پاک کنید، نیمی از بی حالی و بیماری خود را درمان کرده اید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9805,'جدل بر سر کمبودها و محدودیت ها، حاصلی جز کمبود و محدودیت ندارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9806,'مشکلات و موانع جزء فرصت ها هستند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9807,'در پی هر دانشی، اندیشه ای نهفته است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9808,'مبارزه با مواد مخدر، فقر و گرسنگی برای جوانی فرهیخته و روشن بین به همان کارآمدی تلاش برای ارتقای سطح زندگی و احترام گذاشتن به حقوق و اموال یکدیگر نیست..((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9809,'تو از جسم و فکر تشکیل شده ای. قوانین مربوط به جسم به وجود می آیند، اجرا می شوند و پایان می پذیرند، اما فکر ما مرز نمی شناسد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9810,'این شمایید که به مردم می آموزید که چگونه با شما رفتار کنند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9811,'برنامه ریزی لزوماً بد نیست، اما عاشق شدن طبق برنامه دیوانگی محض است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9812,'با قضاوت کردن درباره ی دیگران، در حقیقت به بیان و شرح خود می پردازید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9813,'اگر بتوانی خواسته ات را در ذهن [ خود ] تصور کنی، می توانی آن را در دنیای مادی ات متجلی سازی.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9814,'روزها جریان باارزش زندگی هستند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9815,'اگر گمان کنی که دوران تغییر به درازا می کشد، همین گونه خواهد شد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9816,'اگر باور داری که احساس بد یا نگرانی زیاد، در آنچه در گذشته اتفاق افتاده یا در آینده رخ می دهد تغییری ایجاد می کند، بدان که گویی بر روی این کره زندگی نمی کنی و از واقعیت های آن سر در نمی آوری.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9817,'هر گاه بفهمی که اهدافت را خودت تعیین می کنی، می فهمی که زندگی ات را هم خودت شکل می دهی.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9818,'تنها تفاوت کسی که زیبا به نظر می رسد و کسی که جذاب نیست، در نوع پیش داوری ما است. هیچ کس روی این کره زشت نیست؛ مردم تصمیم می گیرند برخی چیزها را زشت بپندارند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9819,'اگر از زندگی ات خشنود نیستی، حتما به دلیل وسواسی است که نسبت به پیشرفت امور داری.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9820,'وقتی اندیشه ای به خوبی تغذیه شود و ملکه ذهن گردد، در دنیای مادی واقعیت می یابد؛ اندیشه، بسیار قدرتمند است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9821,'تو نمی توانی مطابق میل دیگران زندگی کنی؛ این روشی نیست که برای تو خوشحالی و آسایش درونی به ارمغان آورد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9822,'وقتی اجازه داده ای که عدم تایید شخص دیگری مانع تو شود، بخشی از خودت را نادیده گرفته ای.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9823,'رمز فراوانيِ نعمت، تمرکز نکردن بر آن چیزی است که نیستی. اندیشه هایت را به سمتی سوق بده که تمام آنچه را هستی و باید باشی، درک کند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9824,'اینکه برخی می ترسند یا افسرده می شوند، به این دلیل است که آنان سر رشته امور را از دست داده اند؛ بابت احساس بدی که دارند مسئولیت را از سر خود باز می کنند چون برای شان راحت تر است که دیگران را مسئول زندگی خود بدانند تا اینکه بگویند: باعث بروز چنین احساساتی خودم هستم.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9825,'مرگ، واژه ای است که به مفهوم پایان یافتن به کار برده می شود. محدودیت ها پایان پذیرند؛ روح هیچ محدودیتی ندارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9826,'آنچه که هستید نتیجه ی افکارتان است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9827,'دل قوی دارید که او هرگز ما را رها نمی کند و هر لحظه در درون ما زنده است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9828,'آنچه می کوشد تو را پایین بکشد حقیقت ندارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9829,'احساس جدایی از خداوند، تنها کمبودی است که شما براستی نیازمند برطرف شدن آن هستید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9830,'میزان کمک خداوند به شما بسیار بیشتر از تصور ما است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9831,'هر قدر که به طبیعت بیشتر خدمت کنید، همکاری طبیعت در به ثمر رساندن زحمت شما بیشتر خواهد بود.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9832,'بکوشید تا در برخورد با هر کس، او را به دیده ی یک آموزگار بنگرید و چیزی از او بیاموزید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9833,'حضور در زمان حال، یعنی دور کردن حواس پرتی ها و توجه به آنچه در همین لحظه وجود دارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9834,'از پیشداوری خشم آلود در مورد خویشتن بپرهیزید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9835,'جایی که ترس دامن بگسترد نشانی از عشق نیست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9836,'ما هریک بخشی از این دنیای پهناوریم.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9837,'تو جدا از دیگران نیستی؛ قضاوت در مورد کارهای خوب دیگران هم دست کمی از قضاوت در مورد کارهای بدشان ندارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9838,'اگر مشکلی داری، به دلیل طرز فکر توست و تنها از یک راه می توانی مشکلات را برای همیشه حل کنی؛ اینکه طرز فکرت را تغییر دهی.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9839,'بزرگترین نشانه ی نادانی انسان، نپذیرفتن دانسته های دیگران درباره ی چیزی است که خود، سر رشته ای از آن ندارد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9840,'آزادترین مردم روی زمین کسانی هستند که به آرامش و صفای درون دست یافته اند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9841,'برای دستیابی به آرامش خیال و صفای باطن، روح را بر جسم خویش برتری دهید، نه جسم را بر روح.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9842,'انسان‌هایی که از روح بزرگ برخوردارند، همواره با مخالفت‌های شدید افراد ترسو و میانه‌رو روبرو می‌شوند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9843,'روح از عشق بیکران و بی پایان، مایه می‌گیرد؛ و تنها آرزویش دستیابی به آرامش و سازش است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9844,'با محبت کردن به کودکان، می توانید به آنها کمک کنید تا سرشار از عشق و پذیرش خود شوند و این، همان هدیه ای است که آنها می توانند به دیگران بدهند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9845,'هر کس به دیگری زیانی برساند و یا ضربه ای به کسی وارد کند، بیشترین زیان را خود از آن خواهد دید، چرا که هرکس در دادگاه عدل الهی در برابر کردارهای ناروای خویش مسئول است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9846,'تنها راه دگرگون ساختن آنچه که به آن خو گرفته ایم، تکرار رفتارهای تازه است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9847,'درستکارترین مردم جهان، بیشترین احترام [ =بزرگداشت ] را به سوی خود جلب شده می بینند، حتی اگر هدف بیشترین بدرفتاریها و ناروایی‌ها قرار گیرند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9848,'برای آغاز هر دگرگونی در خود، ابتدا سرچشمه ی پرورش ترس و نفرت را در وجود خود شناسایی و ریشه کن کنید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9849,'از مهمترین کارهایی که به عنوان یک آدم بزرگ می توانید انجام دهید، این است که گهگاه به شادمانی دوران کودکی برگردید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9850,'درون تو مُشتی گوشت قرمز است که دیدن اَن، تو را با خودت روبرو نمی‌کند. تو لابلای آن گوشتهای قرمز درون اَن نیستی، آنجا را نگرد، خودت را در آرزوهایت خواهی یافت.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9851,'اگر می‌توانید بین حق به جانب بودن و مهربانی، یکی را برگزینید، مهربانی را برگزینید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9852,'دروغ، انفجاری است در خودباوری تو.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9853,'زندگی شما را با اندوخته هایتان نمی سنجند، بلکه ملاک سنجش، بخشایش و ازخودگذشتگی شماست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9854,'زمانی که تعطیلات را آغاز می کنید، در برابر آینه بایستید و به خود بگویید: کسی حق ندارد این روزهای خوش را از من بگیرد، حتی تو!((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9855,'شما می توانید به پرستشگاهی با شکوه بروید و ساعت ها به نیایش و دعا بپردازید و بهترین پاکی ها و قداست ها را برای خود آرزو کنید؛ ولی اگر پیش از آن، در قلب خود پرستشگاهی نساخته باشید، با هیچ نیایشی، حضور قلب خود را در آن پرستشگاه احساس نخواهید کرد.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9856,'رمز و راز دستیابی به دارایی و فراوانی، آن است که ذهن را بر آنچه ندارید، متمرکز نکنید و از هر فرصتی برای شکرگزاری و سپاسداری از آنچه که دارید بهره گیرید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9857,'برای دستیابی به کسب و کار دلخواه و داشتن درآمد زیاد، محدودیتی وجود ندارد. آنچه کمیاب است، اراده ی پابرجا برای رسیدن به آنهاست.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9858,'آسانسوری که بتواند شما را به بالاترین طبقه ی کامیابی برساند، از کار افتاده است و شما ناگزیرید که راهروی کامیابی را پله پله بالا بروید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9859,'همه ی ما در طول زندگی خود به کارهایی دست می زنیم که به درستی از آن سر رشته داریم و با شرایط ما سازگار است.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9860,'بزرگترین بازدارنده بر سر راه زندگی لذت بخش، این است که کمابیش همه به اندوه با اندوه پاسخ می گویند، به خشم با خشم و به کینه با کینه.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9861,'سبب ساز آرامش باش و هرجا هستی، شادی و تکاپو را با خود به آنجا ببر و این چنین است که همه ی موانع برداشته می شوند.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9862,'گذشته ی شما بهبود نخواهد یافت، پس آن را رها کنید.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9863,'بگذارید اندیشه های شما در عشق بمانند و کردارهای شما از این عشق جان بگیرند. این تشخیص مسیر پاک شماست. شما براستی می توانید ترس و تردید را رها کنید. آزادی‌ای از این بزرگتر وجود نخواهد داشت!((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9864,'هرجا ممکن شد، خطر یکدل شدن را بپذیر.((وین دایر))',145,0,0,0,0,0);
INSERT INTO mysms VALUES(9865,'عیب جامعه این است که همه می خواهند آدم مهمی باشند و هیچ کس نمی خواهد فرد مفیدی باشد.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9866,'بدون پول، آرامش به دشواری به دست خواهد آمد.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9867,'رو به پیشرفت، یعنی دگرگونی، رو به تکامل، یعنی دگرگونی های بسیار.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9868,'سرسخت و سمج و پایدار باشید و هرگز تسلیم نشوید.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9869,'با خوردن حرف هرگز سوء هاضمه نمی گیرید.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9870,'بزرگترین درس زندگی این است که گاهی احمقها هم درست می گویند.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9871,'آیین دموکراسی بدترین شکل حکومت است. با وجود این از تمام آیین هایی که تاکنون آزمایش شده بهتر است.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9872,'سیاستمدار کسی است که حوادث فردا و یک ماه و یکسال بعد را پیش بینی کند و سپس بتواند دلایلی بیاورد که چرا اتفاق نیفتاد.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9873,'آدم بدبین، سختی را در هر فرصتی می بیند، آدم خوش بین فرصت را در هر سختی.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9874,'تا هنگامی که پایانهای بزرگ در جهان وجود دارد، می دانیم که روح و معنی هستیم نه جسم و جانور.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9875,'اشخاص همیشه گناه را به گردن شرایط و بی نظمی می اندازند؛ من به شرایط و بی نظمی باور ندارم. مردان کامیاب، شرایط و نظم را جستجو می کنند و آن را می آفرینند.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9876,'امپراتورهای آینده، امپراتورهای فکر و اندیشه اند.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9877,'شجاعت را به حق در صدر صفات برجسته انسان دانسته اند... زیرا این صفت تکیه گاه صفت های دیگر است.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9878,'کافی نیست حداکثر تلاش خود را بکنیم و در بهترین حد خود ظاهر شویم، گاهی لازم است کاری را در حد کفایت انجام دهیم.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9879,'موفقیت، توانایی رفتن از شکستی به شکست دیگر بدون از دست دادن شور و حرارت است.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9880,'ماجراجو و خطرناک باشید، با روزگار به مقابله برخیزید و ترس به خود راه ندهید؛ زیرا هر چه پیش آید خوش آید.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9881,'از مخالفت نهراسید؛ بادبادک تنها زمانی می تواند بالا رود که با باد مخالف مواجه شود.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9882,'همه از مردن در راه باورشان سخن می گویند، اما هیچ کس از کشته شدن در راه باورش دم نمی زند.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9883,'حقیقت، انکارناپذیر است. بدخواهی ممکن است به آن حمله کند. ممکن است نادانی آن را به مسخره بگیرد، اما سرانجام حقیقت پایدار خواهد بود.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9884,'من شخصأ دوست دارم بیاموزم، اگرچه همیشه نفرت دارم از این که به من بیاموزند.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9885,'قرنهای تاریک باز خواهد گشت، روزگار حجر بر بالهای درخشان دانش باز خواهد گشت، چه بسا امروز برکت های بی شمار به بشر ارزانی داشته تا یک روز مایه ی نابودی وی شود، آگاه باش! فرصت از دست نرود.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9886,'هرگز ناامید نشو.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9887,'بدون تردید، افراد بشر حاضر نیستند که همگی بطور یکسان و برابر از گرسنگی بمیرند و بی گمان برای آخرین تکه نانی که می ماند و باید تقسیم شود، درگیری شدیدی به وجود خواهد آمد.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9888,'مهمترین کار ما این نیست که ببینیم در دور دستهای مبهم و ناپیدا چه چیزهایی وجود دارد. کار ما این است که به آنچه آشکارا در پیش رو داریم، بپردازیم.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9889,'شهامت چیزی است که باعث می شود انسان بایستد و سخن بگوید. البته این هم که انسان بنشیند و گوش دهد، شهامت است.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9890,'بهای هیچ چیز گزاف تر از انتقامجویی [ =کینه كِشی ] نیست.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9891,'بدون انگیزه و شوق، همه ی خوبیهای دیگر، مفهوم خود را از دست خواهند داد.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9892,'ما از جهانیان، هیچ نمی خواهیم، جز احترام.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9893,'تجربه همیشه به سود انسان نیست، زیرا، هیچ رویدادی دوبار به یک شکل رخ نمی‌دهد.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9894,'درستکاری اهمیت دارد، ولی روراستی نیز مهم است.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9895,'من آدم خوشبینی هستم؛ فکر نمی کنم اگر جز این باشم، حال و روز ام بهتر از این باشد.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9896,'من شخصاً همیشه آماده ی چیز یاد گرفتن هستم، هر چند همیشه هم دوست ندارم به من چیزی یاد بدهند.((وینستون چرچیل))',146,0,0,0,0,0);
INSERT INTO mysms VALUES(9897,'هنگامی که انسان، جدیت بچه‌ها را در هنگام بازی کسب می‌کند، با جوهره‌ی راستین خویش به نزدیکترین فاصله می‌رسد.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9898,'چیزی پایدار نمی ماند، مگر دگرگونی.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9899,'دگرگونی، قانون جهان است.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9900,'در یک رودخانه نمی توان دو بار آب تنی کرد.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9901,'چیزهایی که دیده و شنیده و دانسته می شوند در نظر من بیشترین ارزش را دارند.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9902,'زمان، کودکی است که شاه و وزیر بازی می کند؛ قدرت شاه در دست یک کودک است.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9903,'نزد خدا همه ی چیزها عادلانه و خوب و درست است، اما آدمها برخی را درست و برخی را نادرست می دانند.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9904,'سیرت انسان سرانجام اوست.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9905,'هر جانوری به ضرب چوب به چراگاه رانده می شود.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9906,'جنگیدن با خواسته ی دل دشوار است. هر چه دل بخواهد به بهای روح به دست می آورد.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9907,'حکمت، یک چیز است و دانستن، اندیشه ای است که به وسیله ی آن همه چیز از خلال همه چیز گذرانده می شود.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9908,'مردمان نمی دانند که چگونه از هم جداشدگی، عین به هم پیوستگی است: هماهنگی کششهای متضاد همچون کمان و چنگ.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9909,'خورشید هر روز تازه است.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9910,'اگر خوشبختی در خوشی های جسمانی بود، باید گاوان نر را هنگامی که ماش سبز برای خوردن می یابند، خوشبخت بخوانند.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9911,'بهترین [ انسان ] از همه چیز برای یک چیز دست می کشد.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9912,'بسیاری از مردم شکم خود را مانند چارپایان پر می کنند.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9913,'تناقض، توافق است و از چیزهای ناموافق، زیباترین هماهنگی پدید می آید.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9914,'آنچه ما در بیداری می بینیم مرگ است، اما آنچه که در خواب می بینیم رویاست.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9915,'نام عدل هم نمی دانستند اگر این بی عدالتی ها نمی بود.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9916,'آدمیان را پس از مرگشان چیزهایی در انتظار است که نه امید آن را دارند و نه تصور توانند کرد.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9917,'دانایی یک چیز است، دانستن آنچه بر همه فرمانرواست.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9918,'غرورِ سرکش را باید بیشتر از آتش خاموش کرد.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9919,'آدمیان در شناسایی چیزهای آشکار فریب می خورند.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9920,'افتخارها، خدایان و آدمیان را برده می سازد.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9921,'کسانی که دوست دارند خردمند باشند، باید خود را در بسیاری زمینه‌ها آگاه سازند.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9922,'ای جویندگان خرد! کاری را بکنید که من کرده‌ام؛ در مورد همه چیز پژوهش کنید.((هراکلیت))',147,0,0,0,0,0);
INSERT INTO mysms VALUES(9923,'دانش را می‌توان مبادله کرد، ولی خرد را نه. خرد را می‌توان یافت، با آن زندگی کرد، مجهز به آن شد، به کمک آن به شگفتی‌ها دست یافت؛ ولی هیچ‌کس نمی‌تواند آن را مبادله کند و یاد بدهد.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9924,'اگر از کسی متنفری، از قسمتی از خودت در او متنفری؛ چیزی که از ما نیست نمی تواند افکار ما را آشفته کند.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9925,'پاکدل را کسی باور نمی کند، مگر پاکدل.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9926,'شادی قابل تعریف نیست، بلکه قابلیتی است که در وجود هر انسان نهفته و هدف به شمار نمی رود.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9927,'اگر معنای عشق را می فهمم، همه از برای تو است.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9928,'انسانهای بزرگ در نظر جوانان مانند کشمش های روی کیک تاریخ هستند.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9929,'لطافت، نیرومندتر از سختی، آب قوی تر از صخره و عشق تواناتر از بیرحمی است.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9930,'ما باید طبیعت را به چشم مادرمان بنگریم و با آرامش، خودمان را به او تسلیم کنیم تا بتوانیم بسیار آسوده احساس کنیم که به جهان باز می گردیم؛ همان گونه که همه ی موجودات دیگر باز می گردند. همه ی ما در حقیقت جزء جدایی ناپذیر این کل هستیم. نافرمانی و سرکشی، بیهوده است؛ باید خودمان را به این جریان بزرگ واگذار کنیم.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9931,'آنان که نمی توانند مسئولیت بپذیرند، به رهبر نیاز دارند و برای داشتن رهبر، داد و فریاد راه می اندازند.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9932,'اوج همه‌ی بذله‌گویی‌‌ها به آنجا می رسد که انسان، خویشتن خویش را نیز به مسخره می‌گیرد.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9933,'در غروب جوانی، نفس‌پرستی به پایان می‌انجامد؛ در زمان پیری، خدمت به مردم آغاز می‌گردد.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9934,'بهره ی ما از جامعه ی بشری، بیماری روح‌خراش تکنیک و ملی گرایی است.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9935,'افراد شجاع و باشخصیت، همیشه از نظر دیگران گمراه‌کننده و زبون هستند.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9936,'یک آموزگار، بودن ده تا كُره‌خر را در کلاس درس، به یک دانش‌آموز باهوش برتری می‌دهد. براستی، حق با اوست، چون وظیفه ی او پرورش روح هوش و استعداد نیست، بلکه باید حساب دان، لاتین‌شناس و افراد با ایمان تربیت کند.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9937,'بر گامهای برداشته شده و بر مرگهای رخ داده، نباید افسوس خورد.((هرمان هسه))',148,0,0,0,0,0);
INSERT INTO mysms VALUES(9938,'ما به تنهایی کاری از پیش نمی‌بریم، با یکدیگر می‌توانیم کارهای بسیاری بکنیم.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9939,'زندگی بدون دل و جرأت، زندگی نیست.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9940,'به هر کاری که اراده کنیم تواناییم، اگر آن گونه که سزاوار است پیگیر باشیم.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9941,'اگر آفتاب را به نظاره بنشینی، سایه را نتوانی دید.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9942,'چهره ی خود را به سوی خورشید بگردان، آن گاه تمام سایه ها پشت سر تو خواهند افتاد.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9943,'خوشبختی، لذت مشترکی است که حاصل یاری بدون چشم داشت به دیگران است.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9944,'با مرگ هر دوست ... جزیی از وجود من نیز دفن می شود ... اما سهم آنان در خوشی ها و لذتهایم، مرا وا می دارد تا در این دنیای فانی باقی بمانم.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9945,'تا زمانی که توده مردم برای بهبود حال یکدیگر احساس مسئولیت نکنند، عدالت اجتماعی تحقق نمی یابد.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9946,'زندگی، پیاپی شدن درس هایی است که می بایست زنده بود تا درک شان کرد.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9947,'هنگامی که دری از خوشبختی به روی ما بسته می شود، دری دیگر باز می گردد، اما اغلب چنان به درِ بسته چشم می دوزیم که درهای باز را نمی بینیم.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9948,'چنانچه به خورشید بنگری، سایه ای نخواهی یافت.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9949,'بیچاره ترین انسان در دنیا، فرد بینایی است که فاقد یک چشم انداز آرمانی است.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9950,'در ایام کریسمس تنها کسی که نابیناست، شخصی است که قلبش کریسمسی نیست.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9951,'زندگی، تاریخ شمار روابط دوستانه است. دوستان، هر روز دنیا را از نو می سازند. بدون عشقِ دوستان، قلبها قدرت تپیدن برای زندگی را ندارند.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9952,'زمانی که دری از شادی بسته می شود، در دیگری گشوده می گردد؛ اما اغلب آن قدر به آن در بسته توجه داریم که دری را که برایمان گشوده است نمی بینیم.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9953,'من در آرزوی انجام خدمتی بزرگ و پرشکوه زندگی می کنم، اما سخت ترین وظیفه من انجام خدمات کوچکی است که به گونه ای بزرگ و شکوهمند پدیدار شوند.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9954,'قشنگ ترین چیزهای دنیا نه قابل دیدن و نه حتی قابل لمس کردن هستند؛ بلکه باید آنها را با قلب خود حس کنید.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9955,'من می توانم ادعا کنم که در هنر تحمل بار سرنوشت، خواه شادی و خواه محنت، آنقدر مسلط شده ام که سختی ها و رنجها در نهاد من وجد و نشاطی ایجاد می کند که به سعادت نزدیک است.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9956,'ما بیشتر زمانها به درهایی که شادی را بر ما بسته است، نگاه می کنیم، اما هیچ گاه کسی را که برایمان درهای شادی را بگشاید نمی بینیم.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9957,'زندگانی را بیش از حد تصورم زیبا یافتم.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9958,'تا ایمان به هدف، امید و سخت کوشی نباشد کاری از پیش نمی رود.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9959,'گرامی ترین ها و زیباترین ها در جهان نه دیده می شوند و نه حتی لمس می گردند؛ آنها را تنها باید در دل حس کرد.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9960,'خوشبختی شکل ظاهری ایمان است. تا زمانی که ایمان و امید و سخت کوشی نباشد، هیچ کاری را نمی توان انجام داد.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9961,'همیشه به نور خورشید بنگرید، در این صورت، تاریکی ها و سایه ها را نخواهید دید.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9962,'کامیابی و شادمانی تو در درونت قرار دارد، اراده کن که شاد بمانی، آن زمان، تو و شادمانی‌ات، میزبان نامریی دشواریها خواهید بود.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9963,'من فقط یکی هستم، ولی هنوز هم یکی هستم. هر کاری نمی‌توانم بکنم، ولی هنوز می‌توانم کاری بکنم. از انجام آنچه می‌توانم، سر باز نخواهم زد.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9964,'زندگی، توانایی رویارویی با پیشامدها است و چیزی بیش از این نمی باشد.((هلن کلر))',149,0,0,0,0,0);
INSERT INTO mysms VALUES(9965,'ارزش هر چیزی، آن مقدار از زندگی ماست که هم‌اکنون یا در آینده، آن را می‌ستایند.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9966,'اگر تنها برای پول کار کنید، هیچ‌گاه خوشبخت نمی‌شوید و احتمالاً پول چندانی هم به دست نمی‌آورید.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9967,'بسیاری از انسانها یک عمر به ماهیگیری می‌روند، ولی هرگز نمی‌فهمند که در جستجوی ماهی نیستند.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9968,'زمانی به آگاهی می رسیم که بیدار باشیم.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9969,'هر احمقی می تواند قانونی بر جای نهد که احمق دیگری به آن اهمیت بدهد.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9970,'پرداختن به جزئیات، زندگی را تباه و زشت می کند؛ ساده شوید، ساده کنید.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9971,'چیزی تغییر نمی کند، ماییم که دگرگون می شویم.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9972,'زبان دوستی واژه نیست، معناست.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9973,'حقیقتی تشویق کننده تر از این سراغ ندارم که انسان با تلاش آگاهانه و منظم می تواند زندگی خود را متعادل سازد.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9974,'قیمت هر چیزی عبارت از آن مقدار زندگی است که باید برای آن تلف کنیم!((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9975,'برای آن آفریده نشده ایم که همه کارها را انجام بدهیم. برای آن متولد شده ایم که یک کاری بکنیم و منشا اثری باشیم.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9976,'مهمترین کاری که می توانم برای دوستم انجام دهم، این است که دوستش باشم.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9977,'اگر کسی با اطمینان به سمت رویاهایش به پیش برود و بکوشد تا آنچنان زندگی کند که همیشه تصورش را کرده است، با چنان موفقیتی به آنها خواهد رسید که در زمانهای عادی، دست نایافتنی به نظر می رسد.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9978,'به خوشبختی ایمان دارم، برای همین سخت تلاش می کنم.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9979,'از این نکته مشوق تر نمی توان یافت که بشر دارای قدرت انکار ناپذیری است که می تواند با تلاش و کوشش، آزادی زندگی خود را افزایش دهد.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9980,'اگر کسی در جهت رویا و گمان های خویش گام بردارد و کوشش کند و آن گونه زندگی را که در دنیای خیال برای خود مجسم کرده است فراهم سازد، با موفقیتی که در لحظه های عادی به دور از انتظار است مواجه خواهد شد.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9981,'موفقیت به سراغ کسانی می آید که آنقدر در تلاشند که وقت نمی کنند دنبال آن بروند.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9982,'زمانی که به موسیقی گوش فرا می‌دهم، از هیچ خطری نمی‌هراسم. آسیب‌پذیر نیستم. دشمنی نمی‌بینم. با دوران کهن و زمان کنونی پیوند می‌خورم.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9983,'بهترین سنگ شکن‌ها، ابزارهای سمی یا فولادی نیستند، بلکه اثر ملایم و آب و هوا هستند که با آسودگی و در هر زمان سرگرم کارند.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9984,'آن گونه که باور دارید، زندگی کنید. آنگاه می توانید دنیا را زیر و رو نمایید.((هنری تورئو))',150,0,0,0,0,0);
INSERT INTO mysms VALUES(9985,'اگر شما در اندیشه‌ی آینده‌تان نباشید، آینده‌ای نخواهید داشت.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9986,'اگر بیندیشید که می توانید کاری را انجام دهید، یا می اندیشید که نمی توانید آن را انجام دهید، در هر دو صورت، حق با شما است.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9987,'کسی وجود ندارد که توانایی انجام بیش از آنچه می اندیشد را نداشته باشد.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9988,'شکست، فرصتی برای شروع دوباره و هوشمندانه تر است.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9989,'اندیشیدن، یکی از دشوارترین کارهای انسان است. ولی با همین فرایند است که انسان می‌تواند به کشفیات و دریافت‌های ذهنی باارزشی برسد و زندگی خود را پُربار‌تر کند. به این ترتیب، ما بازیگر زندگی می‌شویم نه تماشاگر آن.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9990,'چنانچه نتوانم حوادث را از بین ببرم، آنها را به حال خود می گذارم تا به خودی خود حل شوند.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9991,'من هیچ وقت در جایی که می توانم بنشینم نمی ایستم و آنجا که می توانم بخوابم نمی نشینم.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9992,'باور من این است که همه کارها را خداوند روبراه می کند و آن خدای قادر و متعال هیچ نیازی به توصیه و راهنمایی من ندارد؛ در این صورت دلیلی برای نگرانی وجود ندارد.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9993,'بیش از هر چیز دیگری، آمادگی، رمز و راز پیروزی است.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9994,'اشتباه فرصتی است [ برای این ] که هوشمندتر باشیم.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9995,'شما نمی توانید با قصد انجام یک کار به شهرت برسید.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9996,'در هر شرایطی، با تمام توان و با بکارگیری تمام امکانات خود، کار کنید. کسی که از کار می گریزد، در حقیقت از زندگی گریزان است.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9997,'بهتر است به جای لگد کوبیدن به در، کلید آن را پیدا کنید.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9998,'راز موفقیت در این است که بتوانیم ایده های دیگران را کسب کنیم و به مسائل، هم از دید خود و هم از دید دیگران بنگریم.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(9999,'به جای ایرادگیری، چاره اندیشی کنید.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(10000,'قبل از هر چیز دیگر، آماده شدن راز موفقیت است.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(10001,'موانع، آن چیزهای وحشتناکی هستند که وقتی چشمتان را از روی هدف بر می دارید، به نظرتان می رسند.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(10002,'اندیشیدن، دشوارترین کارها است؛ شاید به این دلیل است که کمتر کسی زحمت آن را به خود می دهد.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(10003,'کارها را با آرامش و خونسردی انجام دهید؛ زیاد دربند شتاب در انجام دادن کاری نباشید؛ مهم آن است که آن را آغاز کرده‌اید.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(10004,'بسیاری از کارهای دشوار، به نظر هیچ‌گاه به پایان نمی‌رسند، ولی اگر به آنها بیندیشیم، انرژی ما را خواهند گرفت.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(10005,'نقش کار در زندگی، مهمتر از گذران زندگی است؛ کار، خودِ زندگی است.((هنری فورد))',151,0,0,0,0,0);
INSERT INTO mysms VALUES(10006,'به آینده، هرچند شیرین و دلپسند، اعتماد مکن؛ بگذار گذشته‌ی از دست رفته، به خاک سپرده شود؛ عمل کن، عمل در زمان زنده‌ی حال.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10007,'زندگی مردان بزرگ، به ما یادآور می‌شود که ما نیز می‌توانیم چون آنان، برجسته و شاخص باشیم و هنگام سفر (از این دنیا) ردپاهایی بر شن‌های زمان به جا گذاریم.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10008,'ثابت قدمی و داشتن پشتکار، یکی از عناصر کامیابی است.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10009,'بگذار تا به پا خیزیم و کار کنیم، با دلی که آماده ی مقابله با هر گونه سرنوشتی است؛ پیوسته بکوشیم و پیوسته پایداری کنیم، کوشش و بردباری را بیاموزیم.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10010,'ای ستاره ی نیرو، تو را می بینم که ایستاده ای و بر درد من لبخند می زنی؛ تو با دست فولادین خود اشاره می کنی و من نیروی خویش را باز می بابم.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10011,'از بهار عشق و جوانی بهره بگیر و باقی را هر چه هست به فرشته ی نیکوکاری واگذار؛ چرا که زمان به زودی این حقیقت را به تو خواهد آموخت که در لانه ی سال پیش، مرغان بجای نمانده اند!((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10012,'آرام باش ای دل غمگین! از شِکوه بس کن؛ پشت ابرها هنوز خورشید می درخشد؛ سرنوشت تو همان است که دیگران دارند. در هر زندگی باید بارانهایی فرو ریزد و برخی روزها تیره و حزن انگیز باشند.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10013,'کودکی شاخساری است که پرندگان و شکوفه های بسیار بر آن آرام می گیرند؛ و پیری شاخه ای است که برف بر آن می نشیند.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10014,'در میان اندوه و خطا و غم، شبنم جوانی را در دل خود نگهدار و بر لبان خود لبخند حقیقت را.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10015,'ای دلهای کوچک که سخت تب آلود و ناشکیبا می تپید و می زنید، با آرزوهایی سخت نیرومند و بی انتها؛ قلب من که زمانی بس دراز از هیجانها درخشیده و افروخته مانده، اکنون به خاکستر بدل شده، و آتشهای خود را می پوشاند و پنهان می کند.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10016,'بدین گونه است رفتار طبیعت با ما؛ بازیچه های ما را یک به یک از ما می رباید و دست ما را می گیرد و چنان به نرمی ما را به آرامگاه خود می برد.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10017,'به دشواری می توان دانست که مایل به رفتن هستیم یا نه، زیرا چنان خواب آلوده ایم که نمی فهمیم ناشناخته ها، از شناخته ها تا چه پایه برتر هستند.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10018,'دلهای خانه نشین خوشبخت ترند، زیرا دلهای آواره نمی دانند به کجا می روند، گرانبارند از تشویش و گرانبارند از اندیشه.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10019,'تنبه [ =هوشیاری ] و پیری، بی خبر می رسند!((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10020,'هدف مورد نظر یا روش ما نه شادی است و نه اندوه؛ کار کردن است و بس، بدان گونه که هر روز از روز گذشته گامی پیش تر باشیم.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10021,'هنر، دیریاب و زمان، شتابان است.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10022,'دلهای ما گرچه مردانه و دلیراند، با اینهمه، چون طبل های گلو گرفته ای آهنگهای عزا را در راه گورستان می نوازند.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10023,'به آینده دل مبند، هر چند نویدبخش! بگذار تا گذشته ی معدوم نعش خود را به خاک سپارد! کار کن، کار در زمان حال که نقد است؛ با دل امیدوار و توکل به کردگار!((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10024,'زندگی مردان بزرگ به ما می آموزد که می توانیم زندگی خود را برتری بخشیم و چون آهنگ سفر کردیم، در قفای [ =غیبت ] خود نشانه ی پایی بر خاک زمان بگذاریم. نشانه پایی، تا مگر دیگری، برادری کشتی شکسته و بی پناه، که بر دریای بزرگ زندگی رو به راه نهاده است، با دیدن آن از نو قوت قلبی بیابد.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10025,'در این جهان هراس به دل راه مده، بزودی خواهی دریافت چه لطیفه ای در خود دارد رنج کشیدن و نیرومند بودن.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10026,'[ در بهار ] از قالب نرم خاک نهال نیرو می گیرد و می روید و درخت خمیده از نو جان می یابد؛ هر چند سرمای زمستان بر قلب او ضربتی نواخته.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10027,'چه شکوهی دارد این جهان، در چشم کسی که با دلی آرزومند در زیر آسمان پر جبروت و درخشان به پیش می رود.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10028,'در کوره ی شعله ور زندگی، نیکبختی ما پرداخته می شود.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10029,'زندگی مردان بزرگ به ما می آموزد که ما نیز بزرگوار زندگی کنیم.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10030,'در آوردگاه پهناور دنیا، در اردوی زندگی، چون گوسفندانی مباش که بی اراده رانده می شوند؛ قهرمانی باش در تکاپو.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10031,'کسی که برای خود احترام قایل است، از گزند دیگران در امان است. او کتی را بر تن دارد که کسی را یارای پاره کردن آن نیست.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10032,'زمانی که روحیه ی شما سست می شود و ناامید می گردید، به یاد آورید: پایین تر جزر است که مد را ایجاد می کند.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10033,'اگر ذهن، که بر بدن فرماندهی می‌کند، به گونه ای خود را فراموش کند که انگار بر برده‌ی خود لگد می‌زند، برده که هرگز آن اندازه سخاوتمند نیست که بتواند آسیب و خستگی را ببخشد، شورش می‌کند و ستمگر را در هم می‌کوبد.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10034,'موسیقی، زبان جهانی نوع بشر است.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10035,'عشق، بهتر از یک ردا یا عبا از ما در برابر سرما نگاهبانی می‌کند.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10036,'کسی که به خود ارج می نهد، از آسیب دیگران در امان است؛ زرهی بر تن دارد که به هیچ وجه دریده نمی شود.((هنری وادزورث لانگ‌فلو))',152,0,0,0,0,0);
INSERT INTO mysms VALUES(10037,'matn','cat_id',0,0,0,0,0);
INSERT INTO mysms VALUES(10038,'‏‎‏‏ای آزادی! من از ستم بیزارم، از بند بیزارم، از زنجیر بیزارم، از زندان بیزارم، از حکومت بیزارم، از باید بیزارم، از هر چه و هر که، تو را، در بند می‌کشد، بیزارم  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10039,' ای آزادی! چه زندان‌ها برایت کشیده‌ام! و چه زندان‌ها خواهم کشید، چه شکنجه‌ها تحمل کرده‌ام، و چه شکنجه‌ها تحمل خواهم کرد. اما، خود را به استبداد نخواهم فروخت. من پرورده‌ی آزادی‌ام، استادم علی است، مردِ بی‌بیم و بی‌ضعف و پر صبر، و پیشوایم مصدق، مردِ آزاد، مردی که، هفتاد سال برای آزادی نالید. با من، هرچه کنند، جز در هوای تو دم نخواهم زد.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10040,' ای آزادی! با من هرچه کنند، جز در هوای تو دم نخواهم زد. اما من، به دانستن از تو نیازمندم، دریغ مکن، بگو! هر لحظه کجایی، چه می‌کنی؟ نا بدانم، آن لحظه، کجا باشم، چه کنم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10041,' ای آزادی! مرغکِ پَر شکسته‌ی زیبای من، کاش می‌توانستم، تو را، از چنگِ پاسدارانِ وحشت، سازندگانِ شب و تاریکی و سرما، سازندگانِ دیوارها و مرزها، و زندان‌ها و قلعه‌ها، رهایت کنم. کاش قفس‌ات را می‌شکستم، و در هوای پاکِ بی‌ابر و بی‌غبار بامدادی، پروازت می‌دادم، اما…، دست‌های مرا نیز شکسته‌اند، زبانم را بریده‌اند، پاهایم را در غل و زنجیر کرده‌اند، و چشمانم را نیز بسته‌اند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10042,' ای آزادی! مرا با تو سرشته‌اند، تو را در خویش، در آن صمیمی‌ترین و راستین‌ترین منِ خویش، می‌یابم، احساس می‌کنم. عمقِ طعمِ تو را، هر لحظه، در خویش می‌چشم. بوی تو را، همواره، در فضای خلوتِ خویش، می‌بویم. آوای زنگ‌دار و دل‌انگیزت را، که به سایش بال‌های فرشته‌ای، در دلِ ستاره‌ریزِ آسمانِ شب‌های تابستانِ کویر می‌ماند، همواره می‌شنوم  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10043,' ای آزادی! خدا، تو را در من سرشته است. آنگاه که خدا کالبدم را ساخت، تو را، ای آزادی، به جای روح،در من دمید، و بدین گونه، با تو زنده شدم، با تو دم زدم، با تو به جنبش آمدم، با تو دیدم و گفتم و شنفتم، و حس کردم و فهمیدم و اندیشیدم… و تو، ای روح ِگرفتارِ من! می‌دانی، می‌دانی که در همه‌ی آفرینش، چه نیازی دشوارتر و دیوانه‌تر، از نیازِ کالبدی است به روح‌اش؟ اما…، تو را که، میر‌غضب‌های استبداد، و فراشانِ خلافت، از من باز گرفتند، و مرا به “تنهایی‌ی دردمندم” تبعید کردند، و به زنجیر بستند، چگونه می‌توانند از یکدیگر بگسلند؟ که نگاه را، از چشم، باز نمی‌توانند گرفت، و چشم را، از نگاه‌اش، باز نمی‌توانند گرفت. و من، ای آزادی! با تو می‌بینم.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10044,' ای آزادی! کاش با تو زندگی می‌کردم، با تو جان می‌دادم، کاش در تو می‌دیدم، در تو دم می‌زدم، در تو می‌خفتم، بیدار می‌شدم، می‌نوشتم، می‌گفتم، حس می‌کردم، بودم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10045,' ای آزادی! به مِهرِ تو پرورده‌ام، ای آزادی! قامتِ بلند و آزادِ تو، مِناره‌ی زیبای معبدِ من است. ای آزادی! کبوترانِ آزاد و رنگینِ تو، دوستانِ همراز و آشنای من‌اند، کبوترانِ صلح و آشتی‌اند، پیک‌های همه‌ی مژده‌ها، و همه‌ی پیام‌های نوید و امید و نوازش من‌اند  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10046,' خدایا! این کلامِ مقدسی را، که به “روسو” الهام کرده‌ای، هرگز از یاد من مبر، که : “من، دشمنِ تو، و عقایدِ تو هستم، امّا، حاضرم جانم را، برای آزادی‌ی تو، و عقایدِ تو، فدا کنم!”.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10047,' دل‌هائی که رنجِ اسارت را چشیده‌اند، می‌توانند پرورنده‌ی مژده‌ی بزرگِ آزادی باشند. آزادی، همواره، در دامنِ اسارت می‌زاید، و در زنجیر رشد می‌کند، و از ستم تغذیه می‌کند، و با غصب بیدار می‌شود. های!، این، سرنوشتِ آزادی است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10048,' آزادی‌ی انسانی را، تا آنجا حرمت نهیم، که مخالف را، و حتی، دشمنِ فکری‌ی خویش را، به خاطرِ تقدسِ آزادی، تحمل کنیم، و تنها، به خاطرِ این‌که می‌توانیم، او را، از آزادی‌ی تجلی‌ی اندیشه‌ی خویش، و انتخابِ خویش، با زور، باز نداریم، و به نامِ مقدس‌ترین اصول، مقدس‌ترین اصل را، که آزادی‌ی رشدِ انسان، از طریقِ تنوعِ اندیشه‌ها، و تنوعِ انتخاب‌ها، و آزادی‌ی خلق و آزادی‌ی تفکر، و تحقیق و انتخاب است، با روش‌های پلیسی و فاشیستی، پایمال نکنیم. زیرا، هنگامی که “دیکتاتوری” غالب است، احتمالِ این‌که عدالتی در جریان باشد، باوری فریبنده و خطرناک است. و هنگامی که “سرمایه‌داری” حاکم است، ایمانِ به دموکراسی و آزادی‌ی انسان، یک ساده‌لوحی است. و اگر به تکاملِ نوعِ انسان اعتقاد داریم، کم‌ترین خدشه به آزادی‌ی فکری‌ی آدمی، و کم‌ترین بی‌تابی، در برابرِ تحملِ تنوعِ اندیشه‌ها و ابتکارها، یک فاجعه است  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10049,' من، هرگز از مرگ نمی‌هراسیده‌ام. عشقِ به آزادی، سختیِ جان دادن را، بر من هموار می‌سازد. عشقِ به آزادی، مرا، همه‌ی عمر، در خود گداخته است. آزادی، معبودِ من است، به خاطرِ آزادی، هر خطری، بی‌خطر است، هر دردی، بی‌درد است، هر زندانی، رهایی است، هر جهادی، آسودگی است، هر مرگی، حیات است. مرا، این چنین پرورده‌اند. من، این چنین‌ام.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10050,' من معتقدم : هرچه درباره‌ی انسان گفته‌اند، فلسفه و شعر است، و آنچه حقیقت دارد، جز این نیست که، انسان، تنها آزادی است و شرافت و آگاهی. و این‌ها، چیزهایی نیست که بتوان فدا کرد، حتی در راهِ خدا. و اگر کسی، این سه را، از انسانی گرفت، در برابر، چه چیزی در این جهان هست، که به او ببخشد؟  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10051,' انسان، با آزادی آغاز می‌شود، و تاریخ، سرگذشتِ رقت‌بارِ انتقالِ اوست از این زندان، به آن زندان! و هر بار، که زندان‌اش را عوض می‌کند، فریادِ شوقی برمی‌آورد که : آزادی!.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10052,' آزادیِ سیاسی، فکری، هنری، اعتقادی، آزادیِ چگونه زیستن، و آزادیِ انتخاب کردن، دست آوردِ عزیزِ انسان، در طولِ تکاملِ خویش است  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10053,' آدمی، یعنی آزادی و رشد، یعنی تنوع و درگیری. اگر آزادی و تنوع و درگیری و تضاد را از زندگی برداریم، نه تکامل خواهیم داشت، و نه معنیِ بودن و زندگی کردن، که ویژه‌ی آدمی است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10054,' فرار، فرار برای باز یافتنِ آنچه که، از عدالت عزیزتر است، و آنچه که، از سیری والاتر : “آزاد” بودن. انسان، در گرسنگی، ناقص است. انسان، در استثمار شدن، ناقص است. انسان، در محرومِ از سواد بودن، ناقص است. انسان، در محروم ماندن از نعماتی، که خداوند بر سرِ سفره‌ی طبیعت نهاده است، ناقص است، اما، “انسان” است. حال آن که، انسانی که از “آزادی” محروم است، انسان نیست. زیرا، انسان، حیوانِ آزاد است، یعنی اختیار و اراده دارد، و این اختیار و اراده است که، او را با طبیعت بیگانه می‌کند، و از حیوانات جدا می‌سازد، و با خدا، اراده‌ی مطلقِ آفرینش، “همانند”. آن‌که آزادی را از من می‌گیرد، دیگر، هیچ چیز ندارد که، عزیزتر از آن، به من ارمغان دهد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10055,' لیبرالیسم و سرمایه‌داری می‌گوید : برادر! حرفت را خودت بزن، نانت را من می‌خورم. . مارکسیسم، برعکس، می‌گوید : رفیق ! نانت را خودت بخور، حرف‌ات را من می‌زنم. . و فاشیسم می‌گوید : نانت را من می‌خورم، حرف‌ات را هم، من می‌زنم، تو فقط برای من کف بزن! . اما، نانت را خودت بخور، حرف‌ات را هم خودت بزن، من، برای اینم، تا این دو حق، برای تو باشد. من، آنچه را حق می‌دانم، بر تو تحمیل نمی‌کنم، من، خود را “نمونه” می‌سازم، تا بتوانی سرمشق گیری  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10056,' روشنفکران، در نبردِ با این خفقانِ ناشی از دیکتاتوریِ فکری، و تحمیلِ یک فکر بر همه، مبارزه‌ی مقدسی را، به سوی آزادی، و به سوی رشدِ متنوع و آزادِ استعدادها، و اعطای فرصتِ ابتکار و انتخاب، و تحملِ تضاد و جدالِ فکری و علمی و عقیدتی، و امکانِ تحقیق و ابرازِ وجود و شکوفاییِ نبوغ و…، آغاز کردند. شعارها چه بود؟ لائیک شدنِ (غیرِ مذهبی شدنِ) حکومت، و نیز، تعلیم و تربیت، استقلالِ علوم، فلسفه، ادب و هنر و اقتصاد، از سلطه‌ی احکام و عقایدِ مذهبِ حاکم، تا خود، آزادانه رشد کنند.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10057,' اما، این آزادی، کم‌کَمَک، ضعف‌های خود را نشان داد. پیشرفت… آری، اما به کدام سو ؟ ثروت… آری، اما به سودِ کدام طبقه؟ علم… آری، اما در خدمتِ چی؟ تکنولوژی… آری، اما در دستِ کی؟ آزادی! … می‌بینی که در آن، همه، به تساوی، حق تاختن دارند، و مسابقه‌ای به راستی آزاد، و بی‌ظلم و تبعیض و تقلب، در جریان است، اما، طبیعی است که، فقط آنها که سواره‌اند، پیش می‌افتند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10058,' ای آزادی! تو را دوست دارم، به تو نیازمندم، به تو عشق می‌ورزم، بی‌تو زندگی دشوار است، بی‌تو، من هم نیستم، هستم، اما “من” نیستم، یک موجودی خواهم بود توخالی، پوک، سرگردان، بی امید، سرد، تلخ، بیزار، بدبین، کینه دار، عقده‌دار، بیتاب، بی‌روح، بی‌دل، بی‌روشنی، بی‌شیرینی، بی‌انتظار، بیهوده، منی بی تو، یعنی هیچ! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10059,' من هرگز تعطیل و بی‌پناه نخواهم ماند. هیچ‌گاه “تنهایی و کتاب و قلم”، این سه روح و سه زندگی و سه دنیای مرا، کسی از من نخواهد گرفت. قصرِ بلند و زیبای تنهائی، و از آن دو یارِ همیشگی‌ام، کتاب و قلم. دیگر چه می‌خواهم؟ آزادی، چهارمین بود، که به آن نرسیدم، و آن را از من گرفتند. اما، این سه را، که نمی‌توانند گرفت. حتی به زندان هم که می‌روم، این‌ها وفادارتر می‌شوند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10060,' برقِ امید، در چشمانِ خسته‌ام، می‌درخشد، و از خوشبختی‌ی توست، که هوای پاکِ سعادت را، در ریه‌هایم، احساس می‌کنم. نمی‌توانم خوب حرف بزنم، نیروی شگرفی را، که در زیرِ این کلماتِ ساده، و جمله‌های ضعیف و افتاده، پنهان کرده‌ام، دریاب! دریاب! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10061,' او، علی، حاکمی بود که، بر پهنه‌های بزرگی در آفریقا حکم می‌راند، اما، زندانِ سیاسی نداشت. حتی یک زندانی‌ی سیاسی و قتلِ سیاسی. و طلحه و زبیر، قدرت‌مندترین شخصیت‌های بانفوذ و خطرناکی، که در رژیمِ او توطئه کرده  بودند، هنگامی که آمدند و بر خروج از قلمروِ حکومت‌اش اجازه خواستند، و می‌دانست که به یک توطئه‌ی خطرناک می‌روند، اما اجازه داد، زیرا نمی‌خواست این سنت را برای قداره‌بندان و قلدران به جای گذارد، که به خاطرِ سیاست،  آزادی‌ی انسان را پامال کنند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10062,' این کلمه (آزادی)، در مذهب، یک بُعدِ بی‌نهایت عمیق دارد، اما، در تاریخ، و در فلسفه‌های ماتریالیستی‌ی جدید، معنای خیلی خلاصه‌شده‌ی سطحی و بی‌دامنه دارد… در اسلام، آرمان، فلاح است، البته، با همان اندازه اختلافِ  معنائی که بینِ “لیبرته” و فلاح وجود دارد. لیبرته، آزاد شدن از یک بند است، فقط، اما فلاح، در بردارنده‌ی یک آزادی‌ی تکاملی‌ی وجودی است، نه آزاد شدنِ یک فرد از یک زندان. یک برداشتنِ مانع نیست، بلکه، یک نوع رشد است، یک  شکوفایی است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10063,' خدایا! به من زیستنی عطا کن، که در لحظه‌ی مرگ، بر بی‌ثمری‌ی، لحظه‌ای که، برای زیستن گذشته است، حسرت نخورم. و مُردنی عطا کن، که بر بیهودگی‌اش، سوگوار نباشم. بگذار، تا آن را، من، خود انتخاب کنم، امّا، آن چنان که، تو دوست می‌داری. . خدایا! چگونه زیستن را، تو به من بیاموز، چگونه مردن را، خود، خواهم آموخت! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10064,' خدایا! رحمتی کن، تا ایمان، نام و نان، برایم نیاورد. قوتم بخش، تا نان‌ام را، و حتی نام‌ام را، در خطرِ ایمان‌ام افکنم، تا از آنها باشم، که پولِ دنیا را می‌گیرند، و برای دین کار می‌کنند، نه از آنها که، پولِ دین می‌گیرند، و برای دنیا کار می‌کنند!  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10065,' خدایا! مسئولیت‌های شیعه بودن را، که علی‌وار بودن، و علی‌وار زیستن، و علی‌وار مردن، و علی‌وار پرستیدن، و علی‌وار اندیشیدن، و علی‌وار جهاد کردن، و علی‌وار کار کردن، و علی‌وار سخن گفتن، و علی‌وار سکوت کردن است، تا آنجا که، در توانِ این بنده‌ی ناتوانِ علی است، همواره فرایادم آر .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10066,' خدایا! به من، توفیقِ تلاش، در شکست، صبر، در نومیدی، رفتن، بی‌ همراه، جهاد، بی سلاح، کار، بی پاداش، فداکاری، در سکوت، دین، بی دنیا، مذهب، بی عوام، عظمت، بی نام، خدمت، بی نان، ایمان، بی ریا، خوبی، بی نمود، گستاخی، بی خامی، مناعت، بی غرور، عشق، بی هوس، تنهایی، در انبوهِ جمعیت، و دوست داشتن،بی آنکه دوست بداند، روزی کن.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10067,' خدایا! مرا به خاطرِ حسد، کینه، و غرض، عمله‌ی آماتورِ ظلمه مگردان  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10068,' خدایا! خودخواهی را چندان در من بکُش که خودخواهی دیگران را، احساس نکنم و از آن، در رنج نباشم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10069,' خدایا! جهلِ آمیخته با خودخواهی و حسد، مرا، رایگان، ابزارِ قتاله‌ی دشمن، برای حمله‌ی به دوست نسازد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10070,' خدایا! مرا همواره، آگاه و هوشیار دار، تا پیش از شناختنِ “درست” و “کاملِ” کسی یا فکری، مثبت یا منفی، قضاوت نکنم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10071,' خدایا! مرا، در ایمان، اطاعتِ مطلق بخش، تا در جهان، عصیانِ مطلق باشم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10072,' خدایا! مرا از این فاجعه‌ی پلیدِ “مصلحت‌پرستی”، که چون همه‌کس‌گیر شده است، وقاحت‌اش از یاد رفته، و بیماری‌ای شده است، که از فرطِ عمومیت‌اش، هر که از آن سالم مانده باشد، بیمار می‌نماید، مصون بدار، تا : به رعایتِ مصلحت، حقیقت را، ذبحِ شرعی نکنم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10073,' خدایا! رشدِ عقلی و علمی، مرا، از فضیلتِ تعصب، احساس، و اشراق، محروم نسازد.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10074,' خدایا! مگذار، که : ایمان‌ام به اسلام، و عشق‌ام به خاندانِ پیامبر، مرا، با کسبه‌ی دین، با حَمَله‌ی تعصب، و عَمَله‌ی ارتجاع، هم‌آواز کند. که : آزادی‌ام، اسیرِ پسندِ عوام گردد. که : دین‌ام، در پسِ “وجهه‌ی دینی”ام، دفن شود. که : عوام‌زدگی، مرا مقلّدِ تقلیدکنندگان‌ام سازد. که : آن چه را “حق می‌دانم”، به خاطرِ آن که “بد می دانند”، کتمان کنم  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10075,' خدایا! می‌دانم که، اسلامِ پیامبرِ تو، با “نه” آغاز شد، و تشیعِ دوستِ تو نیز، با “نه” آغاز شد. مرا، ای فرستنده‌ی محمد، و ای دوستدارِ علی! به “اسلامِ آری”، و به “تشیعِ آری”، بی‌ایمان گردان! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10076,' خدایا! در برابرِ هر آنچه که، انسان ماندن را، به تباهی می‌کشاند، مرا، با “نداشتن”، و “نخواستن”، روئین‌تن کن .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10077,' ای خداوند! تو که به بنی‌آدم کرامت بخشیده‌ای، تو که امانتِ خاصِ خویش را، بر دوشِ بنی‌آدم نهاده‌ای. تو که همه‌ی پیامبرانت را، برای تعلیمِ کتاب، و تحققِ عدالت، مبعوث کرده‌ای. تو که عزت را، از آنِ خود می‌خوانی، و از آنِ پیامبرانِ خود، و از آنِ انسان‌هایی، که ایمان دارند، ما انسانیم، به تو، و پیامِ پیامبرانِ تو، ایمان داریم، آزادی و آگاهی و عدالت و عزت را، از تو می‌خواهیم. ببخش، که سخت محتاجیم، و دردناکانه‌تر از همه وقت، قربانی‌ی اسارت و جهل و ذلّت‌ایم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10078,' ای خداوندِ مستضعفان! تو که “اراده کرده‌ای، تا بر بیچاره‌شدگانِ زمین، منت نهی، و توده‌های محکومِ ضعف، و محرومِ از حیات را _ که در بندکشیدگانِ تاریخ، و قربانیانِ ستم و غارتِ زمان، و مغضوبانِ دوزخِ زمین‌اند _ به رهبری‌ی انسان‌شان برکشی، و به وراثتِ جهان‌شان برداری!” اینک، هنگام در رسیده است، و مستضعفانِ زمین، وعده‌ی تو را، انتظار می‌کشند. ای مظهرِ غیرت، مستضعفانِ زمین، دراین زمان، تنها پرستندگانِ تواند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10079,' ای خداوند! تو که، “همه‌ی فرشتگان‌ات را، در پای آدم، به سجده افکندی”، اینک نمی‌بینی، که بنی‌آدم را، در پای دیوان، به خاکِ سجود افکنده‌اند؟ آنان را، از بندِ عبودیتِ بت‌های این قرن، که خود تراشیده‌ایم، به بندگی‌ی آزادی‌بخشِ عبادتِ خویش، آزادی ببخش!.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10080,' ای خداوند! “آن‌ها که به آیاتِ تو کفر می‌ورزند، پیامبرانِ تو را به ناحق می‌کشند، و نیز مردانی را، که از میانِ مردم برخاسته، و به عدالت و برابری می‌خوانند، نابود می‌کنند”، هنوز بر جهان مسلط‌اند. عذابی را، که مژده داده بودی، بر آنان فرو فرست!  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10081,' ای خداوند! به علمای ما، مسئولیت، و به عوامِ ما، علم، و به مومنانِ ما، روشنایی، و به روشنفکرانِ ما، ایمان، و به متعصبینِ ما، فهم، و به فهمیدگانِ ما، تعصب، و به زنانِ ما، شعور، و به مردانِ ما، شرف، و به پیرانِ ما، آگاهی، و به جوانانِ ما، اصالت، و به اساتیدِ ما، عقیده، و به دانشجویانِ ما نیز، عقیده، و به خفتگانِ ما، بیداری، و به بیدارانِ ما، اراده، و به مبلغانِ ما، حقیقت، و به دیندارانِ ما، دین، و به نویسندگانِ ما، تعهد، و به هنرمندانِ ما، درد، و به شاعرانِ ما، شعور، و به محققانِ ما، هدف، و به نومیدانِ ما، امید، و به ضعیفانِ ما، نیرو، و به محافظه‌کارانِ ما، گستاخی، و به نشستگانِ ما، قیام، و به راکدانِ ما، تکان، و به مردگانِ ما، حیات، و به کورانِ ما، نگاه، و به خاموشانِ ما، فریاد، و به مسلمانانِ ما، قرآن، و به شیعیانِ ما، علی، و به فرقه‌های ما، وحدت، و به حسودانِ ما، شفا، و به خودبینانِ ما، انصاف، و به فحاشانِ ما، ادب، و به مجاهدانِ ما، صبر، و به مردمِ ما، خودآگاهی، و به همه‌ی ملتِ ما، همتِ تصمیم، و استعدادِ فداکاری، و شایستگی‌ی نجات و عزت، ببخش.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10082,' ای خدای کعبه! این مردمی را، که همه‌ی عمر، هر صبح و شام، در جهان، رو به خانه‌ی تو دارند، رو به خانه‌ی تو می‌زیند، و رو به خانه‌ی تو می‌میرند، این مردمی را، که بر گردِ خانه‌ی ابراهیمِ تو طواف می‌کنند، قربانی‌ی جهلِ شرک و در بندِ جُورِ نمرود مپسند!  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10083,' و تو، ای محمد! پیامبرِ “بیداری” و “آزادی” و “قدرت”! در خانه‌ی تو، حریقی دامن‌گستر درگرفته است، و در سرزمینِ تو، سیلی بنیان‌کن از غرب تاختن آورده است، و خانواده‌ی تو، دیری است که، در بسترِ سیاهِ ذلت، به خواب رفته است. بر سرشان فریاد زن: قُم فَأَنذِر! بیدارشان کن!.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10084,' و تو، ای علی! ای شیر! مردِ خدا و مردم، رَبّ‌النوعِ عشق و شمشیر! ما شایستگی‌ی “شناختِ تو” را از دست داده‌ایم. شناختِ تو را، از مغز‌های ما برده‌اند، اما، “عشقِ تو را”، علی‌رغمِ روزگار، در عمقِ وجدانِ خویش، در پسِ پرده‌های دلِ خویش، هم‌چنان مشتعل نگاه داشته‌ایم. چگونه تو، عاشقانِ خویش را، در خواری رها می‌کنی؟ تو، ستمی را، بر یک زنِ یهودی _ که در ذِمّه‌ی حکومت‌ات می‌زیست _ تاب نیاوردی، و اکنون، مسلمانان را، در ذمه‌ی یهود، ببین! و ببین که بر آنان چه می‌گذرد! ای صاحبِ آن بازو _ که یک ضربه‌اش، از عبادتِ هر دو جهان برتر است _ ضربه‌ای دیگر!  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10085,' و شما دو تن : ای خواهر! ای برادر! ای شما! که به انسان بودن، معنی دادید، و به آزادی، جان، و به ایمان و امید، ایمان و امید، و با مرگِ شکوه‌مندِ خویش، به حیات، زندگی بخشیدید! . آری، ای دو تن! از آن روزِ دردناک _ که خیال نیز، از تصورش، می‌هراسد، و دل از دردش، پاره می‌شود _ چشم‌های این ملت، از اشک، خشک نشده است. توده‌ی ما، قرن‌هاست که، در غمِ شما، و در عشقِ به شما، می‌گرید، مگر نه عشق، تنها با اشک سخن می‌گوید؟ . یک ملت، در طولِ یک تاریخ، در اندوهِ شما، ضجه می‌کند. به جرمِ این عشق، تازیانه‌ها خورده، و قتلِ عام‌ها دیده، و شکنجه‌ها چشیده، و هرگز، برای یک لحظه، نامِ شما دو تن، از لب‌اش، و یادِ شما، از خاطرش، و آتشِ بی‌تابِ عشقِ شما، از قلبش، نرفته است. هر تازیانه‌ای که، از دژخیمی خورده است، داغِ مهرِ شما را، بر پشت و پهلویش، نقش کرده است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10086,' خدایا! مرا به ابتذالِ آرامش و خوشبختی، مکشان. اضطراب‌های بزرگ، غم‌های ارجمند، و حیرت‌های عظیم را، به روحم عطا کن. لذت‌ها را، به بندگانِ حقیرت بخش، و دردهای عزیز، بر جان‌ام ریز! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10087,' خدایا! اندیشه و احساس مرا، در سطحی پائین میاور که زرنگی‌های حقیر و پستی‌های نکبت‌بار و پلیدِ “شبه آدم‌های اندک” را، متوجه شوم، چه، دوست‌تر می‌دارم، “بزرگ‌واری گول‌خور” باشم، تا، هم‌چون اینان، “کوچک‌واری گول‌زن” .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10088,' خدایا! آتشِ مقدسِ “شک” را آن‌چنان در من بیفروز تا همه‌ی “یقین”هایی را، که در من نقش کرده‌اند، بسوزد. و آنگاه، از پسِ توده‌ی این خاکستر، لبخندِ مهراوه، بر لب‌های صبحِ یقینی، شسته از هر غبار، طلوع کند.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10089,' خدایا! جامعه‌ام را، از بیماری‌ی تصوف، و معنویت‌زدگی، شفابخش، تا به زندگی، و واقعیت، بازگردد. و مرا، از ابتذالِ زندگی، و بیماری‌ی واقعیت‌زدگی، نجات بخش، تا به آزادی‌ی عرفانی، و کمالِ معنوی، برسم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10090,' خدایا! مرا از چهار زندانِ بزرگِ انسان : “طبیعت”، “تاریخ”، “جامعه” و “خویشتن”، رها کن، تا آن‌چنان که، تو، ای آفریدگار من، مرا آفریده‌ای، خود، آفریدگارِ خود باشم، نه که، هم‌چون حیوان، خود را با محیط، که، محیط را با خود، تطبیق دهم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10091,' خدایا! مرا، از فقرِ ترجمه، و زبونی‌ی تقلید، نجات بخش، تا قالب‌های ارثی را بشکنم، تا در برابرِ “قالب‌ریزی”ی غرب، بایستم، تا، هم‌چون این‌ها و آن‌ها، دیگران حرف نزنند، و من فقط دهن‌ام را تکان دهم!  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10092,' خدایا! در تمامی‌ی عمرم، به ابتذالِ لحظه‌ای گرفتارم مکن که، به موجوداتی برخورم، که در تمامی‌ی عمر، لحظه‌ای را، در ترجیحِ “عظمت”، “عصیان”، و “رنج”، بر “خوشبختی”، “آرامش”، و “لذت”، اندکی تردید کرده‌اند.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10093,' خدایا! پارسایانِ بزرگی را، که در انزوای ریاضت، و عزلتِ پاکِ عبادت، یا علم، یا هنر، به “کشتنِ نفس”، کمر بسته‌اند، هرچه زودتر توفیق‌شان ده! تا در این طبیعتِ خوبِ خدا، که هر موجودی را، جز این‌ها، معنائی است، و در این اندامِ زنده‌ی جهان، هر ذره‌ای، جز این انگل‌ها، سلولی، و در این نظامِ هماهنگِ خلقت، هر مخلوقی را، جز این پاکانِ پوک، یا، پوکانِ پاک، نقشی است، جایی برای حشره‌ای، که می‌داند چرا زندگی می‌کند، تنگ نباشد، و خلقت از بیهودگی و عبث پاک گردد، و در چشمِ ظاهربینِ پیرِ حافظ، خطائی بر قلمِ صنع نیاید  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10094,' خدایا! به من “تقوای ستیز” بیاموز، تا در انبوهِ مسئولیت نلغزم. و از “تقوای پرهیز” مصون‌ام دار، تا در خلوتِ عُزلت نپوسم.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10095,' خدایا! مرا، از همه‌ی فضائلی، که به کارِ مردم نیاید، محروم ساز! و به جهالتِ وحشیِ معارفِ لطیفی، مبتلا مکن که، در جذبه‌ی احساس‌های بلند، و اوجِ معراج‌های ماوراء، برقِ گرسنگی را، در عمقِ چشمی، و خطِ کبودِ تازیانه را، بر پُشتی، نتوانم دید! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10096,' خدایا! مرا یاری ده، تا جامعه‌ام را، بر سه پایه‌ی، کتاب، ترازو، و آهن، استوار کنم. و دلم را، از سه سرچشمه‌ی، حقیقت، زیبایی، و خیر، سیراب سازم  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10097,' خدایا! این آیه‌ای را، که بر زبانِ داستایوفسکی رانده‌ای، بر دل‌های روشنفکران فرودآر که : “اگر خدا نباشد، همه چیز مجاز است”.  جهان، فاقدِ معنی، و زندگی، فاقدِ هدف، و انسان، پوچ است. و انسانِ فاقدِ معنی، فاقدِ مسئولیت نیز هست .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10098,' خدایا! در روحِ من اختلافِ در “انسانیت” را با اختلافِ در “فکر” و اختلافِ در “رابطه” با هم میامیز، آن‌چنان که نتوانم این “سه” اقنومِ جدا از هم را بازشناسم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10099,' خدایا! شهرت، من‌ی را که می‌خواهم باشم، قربانی‌ی من‌ی که می‌خواهند باشم، نکند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10100,' خدایا! مرا، از نکبتِ دوستی‌ها و دشمنی‌های ارواحِ حقیر، در پناهِ روح‌های پُر شکوه، و دل‌های همه‌ی قرن‌ها، از گیلگمش تا سارتر، و از سیدارتا تا علی، و از لوپی تا عین‌القضات، و از مهراوه تا رزاس، پاک گردان.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10101,' به هر حال، سه ره پیداست : “پلیدی”، “پاکی”، و “پوچی” این، سه راهی است که، پیشِ پای هر انسانی گشوده است، و تو، یک کلمه‌ی نامفهومی، و “وجود”ی بی‌”ماهیت”ی، و هیچ‌ای، که بر سرِ این سه راه، ایستاده‌ای، تا ایستاده‌ای، هیچی، چون ایستاده‌ای، هیچی. یکی را انتخاب می‌کنی، براه می‌افتی، و با انتخابِ راهِ “رفتن”ات، “خود”ت را انتخاب می‌کنی، معنی می‌شوی .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10102,' این موجودِ انسانی، چه شگفت مخلوقی است! گاهی، در پَستی، چنان می شود، که هیچ جانورِ کثیفی به او نمی‌رسد، و گاه، در عظمت، تا آنجا اوج می‌گیرد، که در خیال نیز نمی‌گنجد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10103,' مگر نمی‌دانی، که بزرگ‌ترین دشمنِ آدمی، فهمِ اوست؟ پس، تا می‌توانی خر باش، تا خوش باشی!! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10104,' با همه چیز درآمیز!، و با هیچ چیز آمیخته مشو! که در “انزوا” پاک ماندن، نه دشوار است، و نه با ارزش!  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10105,' انسان، درختی است که، از خاکِ فرهنگِ خویش تغذیه می‌کند، و با تاریخِ خویش رشد می‌نماید، و وارثِ تمامی‌ی دست‌آوردهای گذشته‌ی ملتِ خویش، در توالی‌ی قرون، و توارثِ نسل‌ها است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10106,' برای سیاست‌بازی، برای خدمتِ به مملکت، و برای تفریح، و لذت بردنِ از زندگی، همیشه وقت هست، اما، برای فراگیری، وقت، همین الان است، و می گذرد. وانگهی، بی‌مایه فطیر است. آدمِ بی‌سواد، سیاست‌اش، قیل‌و‌قال‌های بی‌ریشه است، و خدمت‌اش، پوچ و حقیر، و زندگی‌اش و لذت‌اش، گَند، سطحی، عامیانه، و بی‌ارزش. ارزش و عمق و اصالتِ هر کاری، به : میزانِ خودآگاهی، رشدِ شخصیت، و سرمایه و غنای فرهنگ و فکرِ آدمی وابسته است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10107,' انسان، نهالی است که، در زیرِ نورِ ایمان، و هوای فرهنگ، و بر روی خاکِ ملیتِ خویش، می‌روید، و ریشه در عمقِ تاریخ می‌دواند، و از ذخائرِ تجربه‌ها، عاطفه‌ها، ارزش‌ها، عشق‌ها و ایمان‌ها، شکست‌ها و پیروزی‌ها، و خلاصه، ساخته‌ها و اندوخته‌های عقلی و علمی، و احساسی و هنری و ادبی، و آرمانِ نسل‌ها و عصرهای مستمری، که تا آن سوی تاریخ می‌رود، و به خلقتِ نوعی‌ی خویش می‌پیوندد، تغذیه می‌کند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10108,' به نظرِ من، آنچه که واقعاً انسان را از همه‌ی حیوانات جدا می‌کند، آن دغدغه‌ی در برابرِ “غیب” است. یعنی، آن نارسایی‌ی طبیعت، نسبت به وجود و نیازِ او، و گریزِ وی، از “آن چه که هست”، به سوی “آن چه که باید باشد”  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10109,' انسان، این خداگونه‌ی آزاد و آگاه و آفریننده، که فرشتگان، همه، در پایش به سُجود افتاده‌اند، و زمین و آسمان، و هرچه در این میانه است، مُسَخّر اویند.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10110,' راست می‌گوید برتراند راسل، که: ریشه‌ی همه‌ی غرایضِ آدمی، خودخواهی است. باور می‌کنم. . نه سخنِ مارکس درست است، که می‌گوید: اقتصاد است، و نه سخنِ فروید، که می‌گوید: عشق است. نه، خودخواهی است، خودخواهی! . گویی، همین سخنِ راسل است که، در روایتِ پیغمبر می‌خوانیم، که: آخرین چیزی که، از قلبِ راستان (صدیقین)، بیرون رود، جاه‌طلبی است” .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10111,' سخن از انسان است. انسان، چرا، برخلافِ همه‌ی جانوران، بر روی دو پای‌اش ایستاده است؟ ایستاده است تا، به آسمان بنگرد. انسان، حیوانی است که، دست‌هایش را، از خاک بلند کرده، تا در دامنِ آسمان زند. اندامِ انسان، نمودارِ زمین‌گریزی، و آسمان‌پیوندی‌ی او است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10112,' انسان، به میزانِ برخورداری‌هایی که در زندگی دارد، انسان نیست، بلکه، درست به اندازه‌ی نیازهایی که در خویش احساس می‌کند، انسان است. سطحِ تعالی و درجه‌ی کمالِ هر انسانی را، با درجه‌ی تعالی، و کمالِ نیازهایی که  دارد، و کمبودهایی که در خود احساس می‌کند، دقیقاً می‌توان اندازه‌گیری کرد. یعنی، هر انسانی، به میزانی انسان‌تر است، که نیازهای کامل‌تر، متعالی‌تر، و متکامل‌تری دارد. آدم‌های اندک، نیازهای اندک دارند، و انسان‌های بزرگ،  نیازهای بزرگ .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10113,' انسان، یک حیوانِ ایستاده است، یک حیوانِ برخاسته از زمین است. این دست‌ها و کفِ دست‌ها و انگشت‌های انسان را نگاه کنید! این‌ها را برای راه رفتن بر روی زمین نساخته‌اند، این‌ها را برای گرفتنِ دستی، و چنگ زدن بر دامنی  ساخته‌اند. چرا شگفت‌ترین و شورانگیزترین آرزو و خیالِ انسان، همیشه، پرواز بوده است؟ چرا هرگز هوسِ فرو رفتن ندارد؟ فرو رفتن در زمین را، مرگ و دفن و زوال و پستی احساس می‌کند، و فرا رفتن و پریدن و صعود را، حیاتِ جاویدان و  وصال و تعالی و کمال می‌یافته است. داستان‌ها و قصه‌ها را نگاه کنید! قارونِ پلیدِ پول‌پرست، به زمین فرو می‌رود، و مسیحِ پاکِ عشق‌پرست، به آسمان صعود می‌کند!  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10114,' هنگامی که یک انسانِ بزرگ را می‌شناسیم، که در زندگی موفق زیسته است، روحِ او را در کالبدِ خویش می‌دمیم، و با او زندگی می‌کنیم، و این، ما را حیاتی دوباره می‌بخشد.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10115,' آدمی‌زاده‌های سرزمینِ ما، هنوز، از اکثرِ میوه‌های باغ محروم‌اند، و میوه‌های “مجاز”، برای‌شان “ممنوع” شده است. . ابتدا باید در این باغ، دارای حقوقِ آدمیت شوند، از همه‌ی میوه‌های این باغ برخوردار شوند، در بهشت، آدم شوند، “میوه‌های مجاز” را بدست آرند، بچشند، آنگاه، نوبت به “میوه‌ی ممنوع” خواهد رسید! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10116,' رها! این کلمه‌ی شورانگیزی، که همواره، روحِ انسان را، وسوسه می‌کرده است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10117,' هرکسی باید هر چندی یکبار، مدتی تنها باشد، تنهایی هم یکی از احتیاجاتِ آدمی است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10118,' چه سخت است تنهایی، در انبوهِ جمعیتی، که از همه سو، مرا احاطه کرده‌اند  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10119,' و از این میان، تنهایی! چه حصارِ استوار، پناهگاهِ امن، انیسِ خوب، و اقلیمی، عظیم، زیبا، و پُر از هر چه دلخواه است، بود.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10120,' از تنهایی، به میانِ مردم می‌گریزم، و از مردم، به تنهایی پناه می‌برم. راست می‌گفتی نیما! “به کجای این شبِ تیره بیاویزم، قبای ژنده‌ی خود را؟” .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10121,' تنهایی، نه به آن معنی‌ی اجتماعی‌ی خنک‌اش، تنهایی، به معنی: “انسان، در غربتِ این هستی، می‌هراسد، می‌گریزد، ناآرام است، در جستجوی آشنایی با “خود”ش، در انتظارِ یافتنِ کسی، هم‌چون خودش، که در این دنیا، با هم، یک “بودنِ” مطمئن و مأنوس، و متکی به یکدیگری، داشته باشند” .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10122,' انسان، وقتی به تنهایی می‌رسد، که احساس می‌کند، دیگر جنس‌اش، از جنسِ طبیعتِ مادی نیست، و هنگامی احساس می‌کند که اینجائی نیست، که احساس کند، نوعِ ساختمانِ فطری‌ی او، با نوعِ ساختمانِ حیواناتِ دیگر، اختلاف دارد، و احساس کند که، ایده‌آل‌هایی، او را، به طرفِ خودش می‌کشاند، که آن ایده‌آل‌ها، در طبیعت وجود ندارد  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10123,' مسئولیت، زاده‌ی “توانایی” نیست، زاده‌ی “آگاهی” است، و زاده‌ی انسان بودن.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10124,' در هر حال، ولو همه‌ی امکاناتِ فکری و اجتماعی، از یک فکر گرفته شود، و حتی اگر فردی ببیند، که برای دفاعِ از حقیقت، هیچ کس نیست، باز مسئول است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10125,' مسئولیتِ تراشیدنی، به دردِ همان تراشکارها می‌خورد، مسئولیت را، باید من، در عمقِ سرشت‌ام، در عمقِ وجدان‌ام، روح‌ام، سلول سلولِ اندام‌ام، مغزم، اعصابم، در سراپای درونم، احساس کنم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10126,' فرد، به میزانی که، از رشد و فرهنگ و خودآگاهی‌ی بیش‌تری برخوردار است، قدرت و امکانِ تغییرِ محیط، و اصلاح و انقلابِ اجتماعی را، بیش‌تر دارا می‌شود، و توانایی‌ی آن را، که خود، سرنوشتِ خود، و جامعه‌ی خود را، بسازد، و یا در ساختنِ آن سهیم باشد، بیش‌تر به دست می‌آورد. و از همین جاست که، مسئولیت و تعهد، برای او بیش‌تر مطرح است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10127,' چرا هیچ کس دوست ندارد، که به قیمتِ تمامِ لذت‌ها، و خوشبختی‌های عالم، یک گام، در فهمیدن، عقب‌تر برود. هیچ سوداگری، درباره‌ی “فهمیدن” چانه نمی‌زند، و هیچ آدمِ نفهمی هم، حاضر نیست، به هیچ قیمتی، تخفیف بدهد. . این فرشته‌ی گنهکارِ عاصی، آدم، نمی‌فهمد که، هر چه بیش‌تر بفهمد، و چشم و گوش‌اش بازتر شود، بیش‌تر به خود ستم کرده است، و نمی‌داند که، هر چه بیش‌تر بداند، به زیرِ بارِ سنگین‌ترِ مسئولیت، کشیده می‌شود، و کویرِ آتش‌خیزِ رنج! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10128,' خدایا! به من زیستنی عطا کن، که در لحظه‌ی مرگ، بر بی‌ثمری‌ی، لحظه‌ای که، برای زیستن گذشته است، حسرت نخورم. و مُردنی عطا کن، که بر بیهودگی‌اش، سوگوار نباشم. بگذار، تا آن را، من، خود انتخاب کنم، امّا، آن چنان که، تو دوست می‌داری. . خدایا! چگونه زیستن را، تو به من بیاموز، چگونه مردن را، خود، خواهم آموخت! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10129,' در شبی، از آن “ششصد شبِ تنهایی”، در زندانِ قزل قلعه، خواب بودم. خواب دیدم که، تالارِ بزرگی است بی سَر و پایان، و تمامی‌ی چهره‌های آشنایم جمع‌اند، و من، از انسان و زندگی و عُمر، و فلسفه‌ی زیستن و بودن، حرف  می‌زدم. یکی از میانِ جمع برخاست، و پرسید: شما، که همیشه، از انسان و فلسفه‌ی وجود و معنی‌ی زندگی و این مسائل حرف می‌زنید، قبلاً باید یک حقیقتِ اولیه و اساسی را روشن کنید، و آن، موضوعِ اصلی‌ی همه‌ی این مباحث و تمامی‌ی این نظریات است. اصلاً می‌توانید بگویید: زندگی، خود، چیست؟ آدم، در خواب، توانایی‌هایی دارد، که در بیداری فاقد است. بی‌درنگ، و با اطمینان، گفتم: “یادداشت کنید”!  نان، آزادی، فرهنگ، ایمان، و دوست داشتن .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10130,' همه چیز در جهان، برای بودنِ آدمی است، و درد این است که، بودن، خود برای چیست؟ چه خنده‌آورند آنها که، بودنِ خویش را در جهان، ابزارِ چیزی کرده‌اند، که خود، ابزارِ بودنِ آنهاست .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10131,' چه خانه‌ی سرد و احمق و بی‌روحی است طبیعت، که “خدا” از آن رفته باشد. چه شبِ دراز و تاریکِ زمستانی است تاریخ، که “علی” در آن مُرده باشد. و چه قبرستانِ عزادار و غم‌زده‌ای است زمین، که در آن “معبد” نباشد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10132,' چه رنجی است، لذت‌ها را تنها بُردن، و چه زشت است، زیبایی‌ها را تنها دیدن، و چه بدبختی‌ی آزاردهنده‌ای است، تنها خوشبخت بودن! در بهشت تنها بودن، سخت‌تر از کویر است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10133,' مردن نیز خود هنری است، و هم‌چون هر هنری، باید آن‌را آموخت. نمایشی سخت زیبا و عمیق. تماشایی‌ترین صحنه‌ی زندگی است. بسیار کم‌اند مردانی که زیبا مرده‌اند. یکی از مشهورترینِ مردن‌ها، از آنِ وسپاسین، امپراطورِ روم است. وی در بستری افتاده بود، و جان می‌کند. افسران‌اش بر بالین ایستاده بودند. همین که احساس کرد که مرگ تا حلقوم‌اش بالا آمده است، ناگهان از جا پرید، و فریاد زد:  “یک امپراطور باید ایستاده بمیرد”. و سپس، در آغوش افسران‌اش، سرِ پا، جان داد  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10134,' زندگی را، چون سوسمار، در سوراخِ خود خزیدن، و مشغولِ سعادتِ خانوادگی بودن، بد است. تلاش در جستجوی حقیقت و کسبِ آزادی و فلاحِ انسان، نفسِ زندگی، و عینِ سعادت است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10135,' رشک بردن،بر زندگی‌ی قارونی، با ننگ شمردنِ آن، یکی نیست! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10136,' ای کاش، ای کاش، ای کاش! زندگی کردنِ من، زندگی‌ی ای کاش است! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10137,' کسی که می‌بیند در ده، بیست سالِ آینده، قسطِ ابزار و کالاهایی را که قبلاً خریده، و اکنون اثری از آن‌ها نیست، باید بدهد، محکوم به “چپه” زندگی کردن است! چپه زندگی کردن یعنی چه؟ یعنی: آینده را برای گذشته زندگی  کردن، برای مصرف‌ها و نیازهائی، که زمان آن سپری شده، و دیگر وجود ندارد، کار کردن! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10138,' اکثریتِ مردم زندگی می‌کنند، بی‌آنکه نیازی داشته باشند به این‌که بدانند “چرا”؟ در این‏‌ها، زندگی کارِ خویش را می‌کند، و می‌داند که چه می‌کند، و هرگز از آنها نمی‌پرسد که دوست می‌دارند؟، یا نه، طرح دیگری را می‌پسندند.  این‌ها، وسایلِ جاندارِ طبیعت‌‏اند، و از “وسیله”، کسی نظر نمی‌خواهد! و اقلیتی هستند، که می‌خواهند بدانند، و گاه بی گاه، گریبانِ زندگی را می‌چسبند، که: تو چه‏‌ای؟ چه می‌جویی؟ کجا می‌روی؟ بالاخره چه؟ با ما چه کار داری؟ .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10139,' اگر کسی، در این دنیایی که، هیچ عشوه‌ای او را نمی‌فریبد، و هیچ جاذبه‌ای او را نمی‌گیرد، و برای آن منِ پنهانی‌ی اثیری‌ی خود، ندیمی و پاسخی نمی‌یابد، آیا انسانی‌ترین سرگرمی‌ها، برای گذراندنِ حیاتِ بی‌معنی و پوچ، این  نیست که، خود را به لذت بردن از خوب بودن، پاک ماندن، فداکار بودن، اندیشیدن، چیز نوشتن، و از زیبایی‌های علم و فضیلت و هنر و عرفان و مردم دوستی و شرافت گفتن، عادت دهد، و عمرِ بیهوده‌ای را، که چنین بیهوده‌تر می‌گذرد،  حال که از رضایتِ روح محروم‌ایم، از رضایتِ وجدان نیز محروم کنیم؟ من تاکنون به چه چیز‌ها می‌توانستم زندگی را پُر کنم؟ آیا آنچه که دارم، مقدس‌ترین و ارجمند‌ترین فریب‌ها نیست؟! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10140,' آدمی، بوی مرگ را که می شنود، صمیمی می شود. بر بسترِ احتضار، هر کس، “خودش” است!!! وحشتِ مرگ، او را، چنان سراسیمه می‌سازد، که مجالِ تظاهر نمی ماند. حادثه چنان بزرگ است، که بزرگان، همه کوچک می  شوند  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10141,' آدمی، در زندگی، همواره خود را می‌پوشاند، همواره در زیرِ نقابی، که به چشمِ دیگران زیبا می‌آید، مخفی است. تنها دو جا است که، هر کسی خودش است، بسترِ مرگ، و سلولِ زندان.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10142,' اگرتنهاترینِ تنهاها شوم،باز خدا هست.او،جانشینِ همه‌ی نداشتن‌هاست.نفرین‌ها و آفرین‌ها بی‌ثمر است..اگرتمامی‌ی خلقگرگ‌های هار شوندو از آسمانهول و کینه بر سَرم بارد،تو،مهربانِ جاودانِ آسیب‌ناپذیرِ من هستی..ای پناهگاهِ ابدی!تو می‌توانیجانشینِ همه‌ی بی‌پناهی‌ها شوی.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10143,' خدایا!این آیه‌ای را،که بر زبانِ داستایوفسکی رانده‌ای،بر دل‌های روشنفکران فرودآر،که :“اگر خدا نباشد،همه چیز مجاز است”.جهان،فاقدِ معنی،و زندگی،فاقدِ هدف،و انسان،پوچ است.و انسانِ فاقدِ معنی،فاقدِ مسئولیت نیز هست.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10144,' ای خدای بزرگ!تو،چه باشی،و چه نباشی،من اکنون سخت به تو نیازمندم.تنها،به این نیازمندم که،تو باشی.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10145,' چه خانه‌ی سرد و احمق و بی‌روحی است طبیعت،که “خدا” از آن رفته باشد.چه شبِ دراز و تاریکِ زمستانی است تاریخ،که “علی” در آن مُرده باشد.و چه قبرستانِ عزادار و غم‌زده‌ای است زمین،که در آن “معبد” نباشد.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10146,' عالی‌ترین، کامل‌ترین، عمیق‌ترین، و مطمئن‌ترین ایمان، ایمانی است که، در جانِ انسانی طلوع کرده است، که با آتشِ شک، آنچه را به نامِ دین، از گذشته، با خود داشته، و وراثت و محیط، در او کاشته، پیش‌تر، سوزانده است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10147,' ایمانی که، از خلاءِ بی‌مذهبی، عبور کرده باشد، و در تنورِ شک، گداخته باشد، و بوی حقیقت را، خود، استشمام کرده باشد، از هر خطری، مصون است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10148,' چه زشت است از قربانی‌های خویش در راه ایمان خویش سخن گفتن! چه زشت.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10149,' چه لذت بزرگ و زلال و زیبایی! ایمانِ به خویشتن! “به خاطرِ حقیقت، و به خاطر دیگران، از نام و ننگ، و نفرین و آفرینِ خلق گذشتن، و حیثیتِ خویش را فدا کردن، در مرحله‌ای است که، کسانی که، جز تا بامِ کوتاهِ جان دادن، بالا  نرفته‌اند، آن را نمی‌توانند احساس کنند”، و من، به این مقام رسیده بودم، و چه ایمانی! ایمان از عشق برتر است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10150,' ایمان،هر چه پنهان‌تر است،پاک‌تر است،و عشق،هر چهدر پناهِ کتمانمخفی‌تر است،زلال‌تر است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10151,' مگر نه بهشت پاداشِ دوزخی است که در زندگی داریم، و چه دوزخی، در زندگی، دردناک‌تر و گدازان‌تر از کتمانِ ایمان؟ و کتمانِ آتش، در عمقِ روح، در پرده‌های دل، در رشته‌های اعصاب، در راستین‌‌ترین و حقیقی‌ترین خودِ  خویش.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10152,' وای! که چه زشت و سرد است،روحِ عالِمی،که بی‌درد است،اندیشه‌ی خردمندی،که نمی‌پرستد،و نسلِ جوانی،که ایمان ندارد!.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10153,' ابراهیم‌وار زندگی کن، و معمارِ کعبه‌ی ایمانِ خویش باش! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10154,' مذهب، تلاشِ انسانی است به “هست آلوده”، تا خود را پاک سازد، و از خاک، به خدا، بازگردد. طبیعت و حیات را، که “دنیا” می بیند، “قداست” بخشد، و “اُخری” کند، چه، قُدس، به گفته‌ی دورکهیم، فصلِ مذهب است، و  شاخصه‌ی جوهری‌ی آن.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10155,' دشمنانِ انسانیت، بزرگ‌ترین عملی که انجام دادند، این بود که، مذهب را، که یک احساسِ ماوراءِ انسانی، و یک عاملِ ترقی و آگاهی و فخر و کمالِ بشری بود، و عاملی بود که، انسان را، از این چهارچوبِ زندگی‌ی روزمره،  می‌توانست نجات بدهد، به صورتِ یک عاملِ تخدیر درآوردند، و وسیله‌ی توجیهِ وضعِ موجود کردند. بدین ترتیب که، مذهب را، منحصر کردند به آخرت، بطوری‌که، فقط به دردِ این می‌خورد، که وقتی سرت را به قبر گذاشتی، برایت  فایده داشته باشد. خب، ما قبل از آن‌که سرمان را به قبر بگذاریم، چه کسی باید به دردمان بخورد؟! آخر این عمل چه فایده دارد؟ این سنت و این عقیده چه نتیجه‌ای می‌تواند داشته باشد؟.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10156,' پیشرفتِ هر مذهبی، بستگی به پیشرفتِ پیروانِ آن مذهب دارد.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10157,' اگر مذهب را، از انحصارِ همین قالب‌های تکراری‌ی “طبقِ معمولِ سنواتی”، نجات ندهیم، و آن را، به یک “حرکت”، بدل نسازیم، مذهب، با مرگِ نسلِ فرتوت، و تیپِ فرسوده‌ای، که هم‌چون عادتی، بدان وفادار مانده‌اند، خواهد  مُرد.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10158,' این را به روشنفکران، به آنهایی که همیشه می‌پرسند که: “تو به نامِ یک روشنفکر، چگونه این همه به دین تکیه می‌کنی؟”، می‌گویم که: “من اگر از دین سخن می گویم، از دینی سخن نمی‌گویم که در گذشته تحقق داشته، و در جامعه  حاکم بوده است، بلکه، از دینی سخن می گویم، که هدف‌اش، از بین بردنِ دینی بوده است، که در طولِ تاریخ، بر جامعه‌ها حکومت داشته است. و از آن دین سخن می‌گویم، که پیغمبران‌اش، برای نابودی‌ی اشکالِ گوناگونِ دینِ “شرک”،  قیام کردند، و در هیچ زمانی، به‌طورِ کامل، از نظرِ جامعه و زندگی‌ی اجتماعی‌ی مردم، آن دینِ توحید، تحقق پیدا نکرده، بلکه، مسئولیتِ ما، این است که، برای تحققِ آن دین، دینِ توحید، در آینده، بکوشیم. و این، مسئولیتِ بشری است،  تا در آینده، دینِ توحید، آن‌چنان که به وسیله‌ی پیغمبرانِ توحید اعلام شده، در جامعه‌ی بشری، جانشینِ ادیانِ تخدیری‌ی توجیهی‌ی “شرک” شود. بنابراین، تکیه‌ی ما به دین، بازگشت به گذشته نیست، بلکه، ادامه‌ی راهِ تاریخ است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10159,' ادبیات ما، بیش‌تر در شعر تجلی دارد، و شعرِ ما، بیش‌تر در غزل، و غزلِ ما، مجموعاً، عبارت است از، آه و ناله‌ها، و زاری و زوزه‌های ذلت‌آور و رقت‌بارِ عاشق، برای جلبِ نظرِ معشوق. یک بار هم، در شعرِ ما، سابقه ندارد که،  یک معشوقی، برای عاشقِ خویش، کوچک‌ترین ارزشِ انسانی _ حتی در حدِ یک موجودِ زنده، در حدِ یک جانور _ قائل باشد، و در نقطه‌ی مقابل، یک عاشقی هم، در سراسرِ ادبیاتِ وسیعِ فارسی، وجود ندارد که، در راهِ طلب، بر زیبائی،  جاذبه، شخصیت، ارزش، قدرت، و یا، تلاش و اراده‌ی خود، تکیه‌ای داشته باشد، یعنی، در آرزوی وصال، به شخصیتِ خودش، کوچک‌ترین امیدی داشته باشد. عاشق، همیشه، یک پفیوزِ بدبختِ روزگار سیاهی است، که تنها و تنها، از  طریقِ استرحام، می‌خواهد در دلِ سنگ و بیزارِ معشوق، راهی پیدا کند، و بس ناله کند، و عجز و لابه کند، و مثلِ گدا، مثلِ سگ، سَرِ کوی معشوق بایستد، و خاکِ راه‌اش شود، و گردِ پایش گردد، تا شاید، بر کفش‌اش بنشیند!… و مذهبِ ما  نیز چنین است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10160,' نقطه‌ی ضعفِ مذهبِ موجودِ رسمی، این است که، واقعاً انسان را از انسان بودن خارج می‌کند، و به صورتِ یک بنده‌ی گدای ملتمس، برای نیروهای غیبی، که خارج از قدرتِ اویند، در می‌آورد. انسان را از اراده‌ی خویش بیگانه و  خلع می‌کند. این، مذهب رسمی ‌است، که ما الآن می‌شناسیم.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10161,' مذهبِ الانِ ما، ارزش‌ها و کرامت‌های انسانی را، که قرآن تثبیت می‌کند، نفی می‌کند. ما، هیچ چیز، غیر از یک ولایتِ توسل و گریه نداریم. در دنیا هم، بینشِ مذهبی، همین‌طوری است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10162,' یک شراب‌خوارِ بی بندوبار، اما مرد و وفادار و باشعور، مثل…، به همه‌ی این “جماداتِ” انسان‌گونه‌ی مقدس‌مآبِ بی عقل، می ارزد. خدا، از آدم‌هایی که، ضعف و زبونی‌ی خود را، می‌خواهند با خداپرستی جبران کنند، بیزار  است. از آن‌ها که، یک تخته‌شان کم است، و جای خالی‌ی آن را، با مذهب پُر می‌کنند، نفرت دارد. می‌دانم.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10163,' خدایا! به مذهبی‌ها بفهمان که : آدم از خاک است. بگو که: یک پدیده‌ی مادی، به همان اندازه، خدا را معنی می‌کند، که یک پدیده‌ی غیبی! در دنیا، به همان اندازه خدا وجود دارد، که در آخرت. و مذهب، اگر پیش از مرگ، به کار نیاید،  پس از مرگ، به هیچ کار نخواهد آمد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10164,' تشیع، در داستانِ کربلا، در اوجِ شکوه‌اش، تجسمِ عینی پیدا می‌کند. اما، یکی از وسایلِ بی‌روح شدن و فلج شدنِ فکرِ شیعی، و گم شدنِ اصلِ قضیه، این است که، ما، همه چیز را گذاشتیم، و به داستانِ کربلا پرداختیم. و به شکلی  پرداختیم، که اولین قربانی‌ی تجلیل و تعظیمِ ما از حسین، و یادآوری‌ی داستانِ کربلا، خودِ حسین، و خودِ داستانِ کربلا است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10165,' در همه‌ی قرن‌ها و عصرها، هنگامی که پیروانِ یک ایمانی، و یک اعتقادی، قدرت دارند، با جهاد، عزت‌شان و حیات‌شان را، تضمین می‌کنند، و وقتی که، به ضعف دچار شدند، و همه‌ی امکاناتِ مبارزه را از آنان گرفتند، با شهادت،  حیات و حرکت و زندگی و ایمان و عزت و آینده و تاریخِ خودشان را، تضمین می‌کنند. که: شهادت، دعوتی است به همه‌ی عصرها، و به همه‌ی نسل‌ها، که: اگر می‌توانی، بمیران، و اگر نمی‌توانی، بمیر! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10166,' خواهران، برادران! اکنون، شهیدان مرده‌اند، و ما مرده‌ها، زنده هستیم. شهیدان سخن‌شان را گفتند، و ما، کرها، مخاطب‌شان هستیم. آنها که گستاخی‌ی آن‌ را داشتند، که وقتی نمی‌توانستند زنده بمانند، مرگ را انتخاب کنند، رفتند، و  ما، بی‌شرمان، ماندیم، صدها سال است که مانده‌ایم. و جا دارد که، دنیا بر ما بخندد، که ما، مظاهرِ ذلت و زبونی، بر حسین و زینب، مظاهرِ حیات و عزت، می‌گرییم، و این، یک ستمِ دیگرِ تاریخ است، که ما زبونان، عزادار و سوگوارِ  آن عزیزان باشیم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10167,' شهادت، در یک کلمه، برخلافِ فرهنگ‌های دیگر، که حادثه است، و درگیری است، و مرگِ تحمیل‌شده بر قهرمان است، و تراژدی است، در فرهنگِ ما، یک “درجه” است، وسیله نیست، خود، هدف است، اصالت است، خود، یک  تکامل، یک عُلو است. خود، یک مسئولیتِ بزرگ است. خود، یک راهِ نیم‌بر به طرفِ صعود به قله‌ی معراجِ بشریت است، و یک فرهنگ است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10168,' حسین، وارثِ آدم _ که به بنی‌آدم زیستن داد ‌‌_ و وارثِ پیامبرانِ بزرگ‌ _ که به انسان، “چگونه باید زیست” را آموختند _ اکنون آمده است تا، در این روزگار، به فرزندانِ آدم، “چگونه باید مرد” را بیاموزد! حسین آموخت که،  “مرگِ سیاه”، سرنوشتِ شومِ مردمِ زبونی است که، به هر ننگی تن می‌دهند، تا “زنده بمانند”. چه، کسانی که، گستاخی‌ی آن را ندارند، که “شهادت” را انتخاب کنند، “مرگ” آنان را انتخاب خواهد کرد! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10169,' آموزگارِ بزرگ “شهادت”، اکنون، برخاسته است، تا به همه‌ی آنها که، جهاد را، تنها در “توانستن” می‌فهند، و به همه‌ی آنها که، پیروزی‌ی بر خصم را، تنها در “غلبه”، بیاموزد که: “شهادت”، نه یک “باختن”، که یک “ انتخاب” است، انتخابی که، در آن، مجاهد، با قربانی کردنِ خویش، در آستانه‌ی معبدِ آزادی، و محرابِ عشق، پیروز می‌شود .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10170,' در فرهنگِ ما، شهادت، مرگی نیست، که دشمنِ ما، بر مجاهد تحمیل کند. شهادت، مرگِ دل‌خواهی است که، مجاهد، با همه‌ی آگاهی، و همه‌ی منطق و شعور و بیداری و بینایی‌ی خویش، خود، انتخاب می کند! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10171,' ما، مسئولِ این هستیم که، با این میراثِ عزیزِ شهدا و مجاهدان‌مان و امامان و راهبران‌مان و ایمان‌مان و کتاب‌مان، امتی نمونه بسازیم، تا برای مردمِ جهان، شاهد باشیم، و شهید باشیم، و پیامبر، برای ما، نمونه و شهید باشد. رسالتی به این سنگینی، رسالتِ حیات و زندگی و حرکت بخشیدن به بشریت، بر عهده‌ی ماست، که زندگی‌ی روزمره‌مان را عاجزیم!  خدایا! این چه حکمت است؟ .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10172,' ما، که در پلیدی و منجلابِ زندگی‌ی روزمره‌ی جانوری‌مان غرق‌ایم، باید سوگوار و عزادارِ مردان و زنان و کودکانی باشیم، که در کربلا، برای همیشه، شهادت‌شان و حضورشان را، در تاریخ، و در پیشگاهِ خدا، و در پیشگاهِ  آزادی، به ثبت رسانده‌اند. خدایا! این باز چه مظلومیتی است بر خاندانِ حسین؟ .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10173,' اکنون شهیدان کارشان را به پایان رسانده‌اند، و ما، شبِ شامِ غریبان می‌گیریم، و پایان‌اش را اعلام می‌کنیم. و می‌بینی چگونه در جامه‌ی گریستن بر حسین، و عشقِ به حسین، با یزید هم‌دست و هم‌داستانیم؟ او، که می‌خواست این  داستان به پایان برسد  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10174,' یکی از بهترین و حیات‌بخش‌ترین سرمایه‌هایی که در تاریخِ تشیع وجود دارد، شهادت است. ما، از وقتی که، به گفته‌ی جلال، “سنت شهادت” را فراموش کرده‌ایم، و به مقبره‌داری‌ی شهیدان پرداخته‌ایم، “مرگِ سیاه” را ناچار  گردن نهاده‌ایم، و از هنگامی که، به جای شیعه‌ی علی بودن، و از هنگامی که، به جای شیعه‌ی حسین بودن، و شیعه‌ی زینب بودن، یعنی، “پیروِ شهیدان بودن”، زنان و مردان ما، “عزادارِ شهیدان شده‌اند و بس”، در عزای همیشگی  مانده‌ایم!.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10175,' شهید یعنی حاضر! کسانی که، “مرگِ سرخ” را، به دستِ خویش، به عنوانِ نشان دادنِ عشقِ خویش، به حقیقتی که دارد می‌میرد، و به عنوانِ تنها سلاح، برای جهاد در راهِ ارزش‌های بزرگی که دارد مسخ می‌شود، انتخاب می‌کنند،  شهیدند، حی و حاضر و شاهد و ناظرند، نه تنها در پیشگاهِ خدا، که در پیشگاهِ خلق نیز، و در هر عصری و قرنی و هر زمان و زمینی. و آنها که، تن به هر ذلتی می‌دهند، تا زنده بمانند، مرده‌های خاموش و پلیدِ تاریخ‌اند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10176,' آنها نشان دادند، شهید نشان می‌دهد، و می‌آموزد، و پیام می‌دهد، که در برابرِ ظلم و ستم، ای کسانی که می‌پندارید: “نتوانستن، از جهاد معاف می‌کند”، و ای کسانی که می‌گویید: “پیروزی‌ی بر خصم، هنگامی تحقق دارد، که بر  خصم غلبه شود”، نه! شهید، انسانی است که، در عصرِ نتوانستن و غلبه نیافتن، با مرگِ خویش، بر دشمن پیروز می‌شود، و اگر دشمن‌اش را نمی‌شکند، رسوا می‌کند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10177,' وقتی از صحنه‌ی حق و باطلِ زمانِ خویش غایبی، هرکجا که خواهی باش! وقتی در صحنه حق و باطل نیستی، وقتی ک شاهدِ عصرِ خودت، و شهیدِ حق و باطلِ جامعه‌ات نیستی، هرکجا که می‌خواهی باش، چه به نماز ایستاده  باشی، چه به شراب نشسته باشی، هر دو یکی است. شهادت، “حضور در صحنه‌ی حق و باطلِ همیشه‌ی تاریخ” است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10178,' هرانقلابی دو چهره دارد: خون و پیام. و هر کسی، اگر مسئولیت پذیرفتنِ حق را انتخاب کرده است، و هر کسی که می‌داند مسئولیتِ شیعه بودن یعنی چه، مسئولیتِ آزاده انسان بودن یعنی چه، باید بداند که، در نبردِ همیشه‌ی تاریخ،  و همیشه‌ی زمان، و همه جای زمین – که همه‌ی صحنه‌ها کربلاست، و همه‌ی ماه‌ها محرم، و همه‌ی روزها عاشورا -، باید انتخاب کند: یا خون را، یا پیام را، یا حسین بودن را، یا زینب بودن را، یا آن‌چنان مُردن را، یا این چنین ماندن  را. اگر نمی‌خواهد از صحنه غائب باشد  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10179,' آنچه می‌خواستم بگویم، حدیثِ مفصلی است، که در این مجمل می‌گویم، به عنوانِ رسالتِ زینب، “پس از شهادت”، که: آنها که رفتند، کاری حسینی کردند، و آنها که ماندند، باید کاری زینبی کنند، وگرنه یزیدی‌اند!.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10180,' حسین، در اتصالِ به آن تسلسلِ نهضتی، که در تاریخِ ابراهیمی وجود دارد، معنی می‌دهد، و باید معنی شود، و انقلاب‌اش باید تفسیر و توجیه گردد. به صورتِ مجرد حسین را در تاریخ مطرح کردن، و جنگِ کربلا را، به صورتِ یک  حادثه‌ی تاریخی‌ی مجرد عنوان کردن، و آن را از پایگاه‌های تاریخی و اجتماعی‌اش جدا کردن، موجب می‌شود که، آنچه را معنی‌ی ابدی دارد، و همواره زنده است، به صورتِ یک “حادثه‌ی غم‌انگیزِ گذشته” در بیاوریم‌، چنان‌که در  آوردیم‌، و در برابرش فقط بگرییم، چنان‌که می گرییم!  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10181,' ما داستانِ کربلا را، از روزِ تاسوعا می دانیم، و عصرِ عاشورا ختم‌اش می‌کنیم، بعد دیگر نمی‌دانیم چه شد! همین طور هستیم تا اربعین. آنجا شله‌ای می‌دهیم، و بعد قضیه دیگر بایگانی است! و بعد، سالِ دیگر باز همین طور، و سالِ  دیگر، و سالِ دیگر، باز همین طور. داستانِ کربلا، نه از تاسوعا، و یا محرم، شروع می شود، و نه به عصرِ عاشورا یا اربعین تمام می شود. این است که، از دو طرف قیچی‌اش کردیم، و آن را از معنی انداختیم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10182,' خدایا! به هر کس، که دوست می‌داری، بیاموز که : عشق، از زندگی کردن بهتر است.  و به هر کس، که دوست‌تر می‌داری، بچشان که : دوست داشتن، از عشق برتر! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10183,' دوست داشتن از عشق برتر است. و من، هرگز خود را، تا سطحِ بلند‌ترین قله‌ی عشق‌های بلند، پایین نخواهم آورد.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10184,' دریغم آمد، که آن را نیز، “عشق” بنامم، که شاعران آلوده‌اش کرده‌اند. خواستم “ارادت” بخوانم، ملاها به حماقت‌اش کشانده‌اند. گفتم بهترین کلمه، در اینجا، “خویشاوندی” است، خویشاوندی‌ی دو روح، دو بیگانه، با لطافتِ  زیبایی، که در ساختمانِ این کلمه است: “خویش” و “وند”! ترسیدم که نفهمند. به‌هرحال، می‌گویم: “دوست داشتن”. و مقصودم، عشق و ارادت و ایمانِ دو روحِ آشنای خویشاوند است. دو “انسانی” که، جز آن خمیره‌ی صمیمی و  ناب و منزهی، که “منِ انسانی‌ی خالصِ” هر کسی را می‌سازد، هیچ مصلحتی، و ضرورتی، آنان را به یکدیگر نمی‌پیوندد، پیوندی که، نه طبیعت، نه خلقت، بلکه، تنهایی، میانِ دو تنهای خویشاوند، بسته است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10185,' دوست داشتن، از عشق، برتر است. عشق، یک جوششِ کور است، و پیوندی از سرِ نابینایی، اما، دوست داشتن، پیوندی خودآگاه، و از روی بصیرتِ روشن و زلال. عشق، بیش‌تر از غریزه آب می‌خورد، و هرچه، از غریزه سر  زند، بی‌ارزش است. و دوست داشتن، از روح طلوع می‌کند، و تا هرجا که، یک روح ارتفاع دارد، دوست داشتن نیز، همگام با آن، اوج می‌یابد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10186,' عشق، در غالبِ دل‌ها، در شکل‌ها و رنگ‌های تقریباً مشابهی، متجلی می‌شود، و دارای صفات و حالات و مظاهرِ مشترکی است، اما، دوست داشتن، در هر روحی، جلوه‌ای خاصِ خویش دارد، و از روح رنگ می‌گیرد، و چون روح‌ها،  برخلافِ غریزه‌ها، هرکدام، رنگی و ارتفاعی و بُعدی، و طعم و عطری ویژه‌ی خویش دارند، می‌توان گفت که، به شماره‌ی هر روحی، “دوست داشتن”ی هست .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10187,' عشق، با شناسنامه بی‌ارتباط نیست، و گذرِ فصل‌ها، و عبورِ سال‌ها، بر آن اثر می‌گذارد، اما، دوست داشتن، در ورای سن و زمان و مزاج زندگی می‌کند، و بر آشیانه‌ی بلندش، روز و روزگار را، دستی نیست… ” .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10188,' عشق، در هر رنگی و سطحی، با “زیبایی”ی محسوس، در نهان یا آشکار، رابطه دارد. چنان‌که، شوپنهاور می‌گوید: “شما بیست سال بر سنِ معشوق‌تان بیفزایید، آنگاه، تأثیرِ مستقیمِ آن را، بر روی احساس‌تان، مطالعه کنید”!  اما، دوست داشتن، چنان در روح غرق است، و گیج و جذبِ “زیبایی‌های روح”، که زیبایی‌های محسوس را، به گونه‌ای دیگر، می‌بیند  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10189,' عشق، طوفانی و متلاطم و بوقلمون‌صفت است، اما، دوست داشتن، آرام و استوار و پروقار و سرشارِ از نجابت .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10190,' عشق، با دوری و نزدیکی، در نوسان است. اگر دوری به‌طول انجامد، ضعیف می‌شود، اگر تماس دوام یابد، به ابتذال می‌کشد، و تنها، با بیم و امید، و تزلزل و اضطراب، و “دیدار و پرهیز”، زنده و نیرومند می‌ماند. اما، دوست  داشتن، با این حالات، ناآشنا است، دنیایش، دنیای دیگری است.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10191,' عشق، جنون است، و جنون، چیزی جز خرابی و پریشانی‌ی “فهمیدن” و “اندیشیدن” نیست. اما، دوست داشتن، در اوجِ معراج‌اش، از سرحدِ عقل فراتر می‌رود، و فهمیدن و اندیشیدن را نیز، از زمین می‌کَند، و با خود به قُله‌ی  بلندِ اشراق می‌برد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10192,' عشق، زیبایی‌های دلخواه را، در “معشوق” می‌آفریند، و دوست داشتن، زیبایی‌های دلخواه را، در “دوست” می‌بیند، و می‌یابد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10193,' عشق، یک فریبِ بزرگ و قوی است، و دوست داشتن، یک صداقتِ راستین و صمیمی، بی‌انتها و مطلق .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10194,' عشق، در دریا غرق شدن است، و دوست داشتن، در دریا شنا کردن  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10195,' عشق، بینایی را می‌گیرد، و دوست داشتن می‌دهد.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10196,' عشق، خشن است و شدید، و در عینِ حال، ناپایدار و نامطمئن، و دوست داشتن، لطیف است و نرم، و در عینِ حال، پایدار و سرشارِ اطمینان .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10197,' عشق، همواره با شک آلوده است، و دوست داشتن، سراپا یقین است و شک‌ناپذیر. از عشق، هرچه بیش‌تر می‌نوشیم، سیراب‌تر می‌شویم، و از دوست داشتن، هرچه بیش‌تر، تشنه‌تر. عشق، هرچه دیرتر می‌پاید، کهنه‌تر می‌شود، و  دوست داشتن، نوتر. عشق، نیرویی است در عاشق، که او را به معشوق می‌کشاند. و دوست داشتن، جاذبه‌ای است در دوست، که دوست را به دوست می‌برد. عشق، تملکِ معشوق است، و دوست داشتن، تشنگی‌ی محو شدنِ در  دوست .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10198,' عشق، معشوق را، مجهول و گمنام می‌خواهد، تا در انحصارِ او بماند، زیرا، عشق، جلوه‌ای از خودخواهی و روحِ تاجرانه یا جانورانه‌ی آدمی است، و چون، خود، به بدی‌ی خود، آگاه است، آن را، در دیگری که می‌بیند، از او بیزار  می‌شود، و کینه برمی‌گیرد. اما، دوست داشتن، دوست را محبوب و عزیز می‌خواهد، و می‌خواهد که، همه‌ی دل‌ها، آنچه را او از دوست در خود دارد، داشته باشند. که دوست داشتن، جلوه‌ای از روحِ خدایی و فطرتِ اهورایی‌ی آدمی  است، و چون، خود، به قداستِ ماورایی‌ی خود بینا است، آن را، در دیگری که می‌بیند، دیگری را نیز دوست می‌دارد، و با خود آشنا و خویشاوند می‌یابد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10199,' در عشق، رقیب، منفور است، و در دوست داشتن است که، “هوادارانِ کوی”اش را، چو جانِ خویشتن دارند”، که حسد، شاخصه‌ی عشق است، چه، عشق، معشوق را طعمه‌ی خویش می‌بیند، و همواره، در اضطراب است که،  دیگری، از چنگش نرباید، و اگر ربود، با هر دو دشمنی می‌ورزد، و معشوق نیز، منفور می‌گردد. و دوست داشتن، ایمان است، و ایمان یک روحِ مطلق است، یک ابدیتِ بی‌مرز است، از جنسِ این عالم نیست .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10200,' عشق، لذت جستن است، و دوست داشتن، پناه جستن. عشق، غذا خوردنِ یک گرسنه است، و دوست داشتن، “هم‌زبانی، در سرزمینِ بیگانه یافتن” است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10201,' عشق، گاه جا به جا می‌شود، و گاه سرد می‌شود، و گاه می‌سوزاند. اما، دوست داشتن، از جای خویش، از کنارِ دوستِ خویش، برنمی‌خیزد. سرد نمی‌شود، که داغ نیست، نمی‌سوزاند، که سوزاننده نیست .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10202,' عشق، رو به جانبِ خود دارد. خودخواه است و “خودپا” و حسود، و معشوق را، برای خویش می‌پرستد و می‌ستاید. اما، دوست داشتن، رو به جانبِ دوست دارد، دوست‌خواه است و دوست‌پا، و خود را، برای دوست می‌خواهد، و او را، برای او دوست می‌دارد، و خود در میانه نیست .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10203,' انقلابی شدن، بیش از هر چیز، مستلزمِ یک انقلابِ ذهنی است، یک انقلابِ در بینش، و یک انقلابِ در شیوه‌ی تفکرِ ماست. انقلابی توام با این آگاهی که: خداپرستی را، آخوندیسم به ابتذال کشاند، و سوسیالیسم را، مارکسیسم به  اکونومیسم جبری‌ی کورِ مادی بدل کرد، و آزادی را، سرمایه‌داری نقابی برای نفاق و فریب کرد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10204,' پیروزی‌ی قبل از خودآگاهی، خود، آغازِ فاجعه است  .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10205,' ما، باید بیش‌تر “فداکاری” کنیم، و کم‌تر “توقع” داشته باشیم، برخلافِ امروز، که روشنفکر، بیش‌تر توقع دارد، و کم‌تر فداکاری. من ترجیح می‌دهم، دو نسل، سه نسل کار کنند، و بعد به نتیجه برسند. اما، اگر در عرض ده سال،  به نتیجه برسیم، باز برمی‌گردیم به صد سال عقب‌تر. همیشه، یک تجربه‌ی عجیب در افریقا و آسیا شده: کسانی که به سرعت به نتیجه رسیده‌اند، بعد، امتیازاتِ قبل از انقلاب‌شان را هم، از دست داده‌اند. من، همه‌ی انقلاباتِ زودرس را  نفی می‌کنم.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10206,'وقتی کبوتری شروع به معاشرت با کلاغها می کند پرهایش سفید می ماند، ولی قلبش سیاه میشود. دوست داشتن کسی که لایق دوست داشتن نیست اسراف محبت است((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10207,'دل های بزرگ و احساس های بلند، عشق های زیبا و پرشکوه می آفرینند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10208,'اما چه رنجی است لذت ها را تنها بردن و چه زشت است زیبایی ها را تنها دیدن و چه بدبختی آزاردهنده ای است تنها خوشبخت بودن! در بهشت تنها بودن سخت تر از کویر است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10209,'اکنون تو با مرگ رفته ای و من اینجا تنها به این امید دم میزنم که با هر نفس گامی به تو نزدیک تر میشوم . این زندگی من است .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10210,'وقتی خواستم زندگی کنم، راهم را بستند.وقتی خواستم ستایش کنم، گفتند خرافات است.وقتی خواستم عاشق شوم گفتند دروغ است.وقتی خواستم گریستن، گفتند دروغ است.وقتی خواستم خندیدن، گفتند دیوانه است.دنیا را نگه دارید، میخواهم پیاده شوم .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10211,'اگر قادر نیستی خود را بالا ببری همانند سیب باش تا با افتادنت اندیشه‌ای را بالا ببری .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10212,'به سه چیز تکیه نکن ، غرور، دروغ و عشق.آدم با غرور می تازد،با دروغ می بازد و با عشق می میرد .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10213,'خدایا به هر که دوست میداری بیاموز که عشق ورزیدن بهتر از زندگی کردن است و به هر که دوست‌تر میداری بچشان که دوست داشتن بهتر از عشق ورزیدن است(از نظره دکتر شریعتی عشق تب تندی هست که خیلی زود خاموش میشه و به خاطر همینه‌که دوست داشتن برتر است)((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10214,'من هرگز نمی نالم، قرنها نالیدن بس است. من میخواهم فریاد بزنم، اگر نتوانستم سکوت میکنم، خاموش بودن بهتر از نالیدن است، نالیدن فرزندان ماکیوالیسم را مغرور میکند.......((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10215,'خدایا زیستنی به من بیاموز که از بیثمری لحظه‌ی مرگ حسرت نخورم. خدایا: تو چگونه زیستن را به من بیاموز، چگونه مردن را خود خواهم آموخت!!((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10216,'زندگی یعنی  نان ، آزادی ، فرهنگ ، ايمان و دوست داشتن .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10217,'عشق با دوری و نزدیکی در نوسان است. اگر دوری به طول انجامد ضعیف میشود و اگر تماس تداوم یابد به ابتذال کشیده میشود، و تنها با بیم، امید، تزلزل، اضطراب و دیدار و پرهیز زنده و نیرومند میماند.امّا دوست داشتن با این حالات نا آشناست. دنیایش دنیای دیگریست..........!((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10218,'خدایا همواره تو را سپاس میگزارم که هرچه در راه تو و پیمان تو پیشتر میروم، بیشتر رنگ میبرم، آنها که باید مرا بنوازند میزنند، آنها که باید همگامم شوند ساده راهم میشوند، آنها که باید امیدوارم کنند و تبرئه‌ام کنند، سرزنشم میکنند نومیدم میکنند......تا چشم انتظارم تنها به روی تو باز ماند، تنها از تو یاری طلبم، در حسابی که با تو دارم شریکی نباشد تاتکلیفم با تو رشن شود.تا تکلیفم با خودم معلوم گردد!!((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10219,'خداوندا من در کلبه حقیرانه خود چیزی را دارم که تو در عرشه کبریایی خود نداری! من خدای چون تو دارم و تو چون خودی نداری!((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10220,'دیروز همسایه ام از گرسنگی مرد ، در عزایش گوسفندها سربریدند .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10221,'از خداوند چیزی برایت میخواهم که جز خدا در باور هیچکس نگنجد! دکتر شریعتی.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10222,'در بیکرانه زندگی دو چیز افسونم کرد ، آبی آسمان که می بینم و میدانم نیست و خدایی که نمی بینم و میدانم که هست.. .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10223,'روزگاریست که شیطان فریاد می زند: آدم پیدا کنید! سجده خواهم کرد. .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10224,'در دردها دوست را خبر نکردن ، خود نوعی عشق ورزیدن است! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10225,'گاهی گمان نمیکنی ولی میشود ، گاهی نمی شود که نمی شود! گاهی هزار دوره دعا بی اجابتست! گاهی نگفته قرعه به نام تو می شود! گاهی گدای گدایی و بخت یار نیست! گاهی تمام شهر گدای تو می شود!!! .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10226,'ساعتها را بگذارید بخوابند! بیهوده زیستن را نیاز به شمردن نیست. .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10227,'مشکلات انسانهای بزرگ را متعالی می سازد و انسانهای کوچک را متلاشی. .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10228,'خدایا،به من توفیق تلاش در شکست،صبر در نومیدی،رفتن بی همراه،جهاد بی سلاح،کار بی پاداش،فداکاری در سکوت،دین بی دنیا،عظمت بی نام،خدمت بی نان،ایمان بی ریا،خوبی بی نمود،مناعت بی غرور،عشق بی هوس،تنهایی در انبوه جمعیت،و دوست داشتن بی آنکه دوست بداند،روزی کن..((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10229,'بغض بزرگترین نوع اعتراض در برابر آدم هاست اگر بشکنه دیگه اعتراض نیست التماسه.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10230,'زخمی بر پهلویم هست روزگار نمک می پاشد و من پیچ و تاب میخورم و همه گمان میکنند که من میرقصم. .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10231,'نامم را پدرم انتخاب کرد ، نام خانوادگی ام را یکی از اجدادم! دیگر بس است ، راهم را خودم انتخاب می کنم. .((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10232,'دلی که ازبی کسی تنها است،هرکس رامیتواندتحمل کند.!((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10233,'به دکتر شریعتی گفتند استاد سیگار طول زندگی رو کوتاه میکنه ، دکتر در جواب گفتند من به عرض زندگی فکر میکنم.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10234,'در دشمنی دورنگی نیست ، کاش دوستانم هم در موقع خود چون دشمنان بی ریا بودند.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10235,'اگر روزی تهدیدت کردن بدان در برابرت ناتوانند..اگر روزی خیانت دیدی بدان قیمتت بالاست..اگر روزی ترکت کردن بدان لیاقت با تو بودن را نداشته اند..((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10236,'مذهب شوخی سنگینی با من کرد .سالها مذهبی بودم بی آنکه خدایی داشته باشم...((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10237,'فلسفه زندگی انسان در این جمله خلاصه میشود.......فدا کردن آسایش زندگی برای فراهم آوردن وسایل آسایش زندگی((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10238,'خدایا....بر اراده.دانش.عصیان.بی نیازی.حیرت.لطافت روح.شهامت و تنهایی من بیفزا((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10239,'عاقلانهازدواج کنتاعاشقانهزندگی کنی((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10240,'آزادی!در دامن اسارت می زاید؟در زنجیر رشد می کند؟از ستم تغذیه می کند:؟با غصب بیدار می شود…های… این سرنوشت آزادی است!((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10241,'وقتی که در صحنه حق و باطل نیستی، هر جا که میخواهی باش، چه به نماز ایستاده باشی، چه به شراب نشسته باشی، هر دو یکیست.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10242,'به سه چیز تکیه نکنغرور، دروغ و عشقآدم با غرور می تازدبا دروغ می بازدو با عشق می میرد((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10243,'خداوندا، مگذار آنچه را که حق می دانم،به خاطر آنچه که بد می دانند، کتمان کنم((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10244,'برایت دعا میکنم که خدا از تو بگیرد هر آنچه خدا را از تو میگیرد.  دل های بزرگ و احساس های بلند، عشق های زیبا و پرشکوه می آفرینند.((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10245,'کاش خدا سه چیز را نمی آفرید .......غرور،دروغ،عشقتا هیچ گاه کسی از سر غرور به دروغ دم از عشق نزند((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO mysms VALUES(10246,'خوب بودن ! کلمه هیجان آوری نیست .خوبی ، در فارسی شکوه و عظمت خارق العاده ای ندارد ،با متوسط بودن و بی بو و خاصیت بودن هم صف استخوب بودن از نظر ما یعنی بد نبودن ! و این معنی مبتذلی است !((دکتر شریعتی))',153,0,0,0,0,0);
INSERT INTO bio VALUES(1,replace(replace('آبراهام لینکلن (به انگلیسی: Abraham Lincoln) (زاده ۱۲ فوریهٔ ۱۸۰۹ – درگذشته ۱۵ آوریل ۱۸۶۵) شانزدهمین رئیس‌جمهور ایالات متحدهٔ آمریکا و به عنوان اولین رئیس‌جمهور ایالات متحده آمریکا که از حزب جمهوری‌خواه بود.\r\nآبراهام لینکلن به عنوان نخستین رئیس جمهور از حزب جمهوری خواه آمریکا انجام وظیفه نمود. امروز او بدین جهت مشهور است که به تاریخ برده‌داری در آمریکا خاتمه داد و ایالات متحده آمریکا را با نظارت بر مسائل مربوط به جنگ در دوران جنگ داخلی آمریکا پابرجا نگه داشت. او پایه‌گذار خردورزی، آزادی، برابری، دموکراسی و احیاگر اندیشه‌های والای انسانی بود. او همچنین ژنرالها را انتخاب کرد و استراتژی آنها را تایید نمود، افسران ارشد غیر نظامی را برگزید، بر دیپلماسی، عزل و نصب و فعالیتهای حزب نظارت کرد، و از طریق پیام‌ها و سخنرانی‌ها نظرات عموم را جلب نمود. توان بالای او در سخنوری قدرت تأثیر گذاری او را افزایش می‌داد. نطق گتیسبرگ وی تأثیر ماندگاری بر ارزشهای آمریکا داشت.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(2,'(۱۷۸۸–۱۸۶۰ میلادی) فیلسوف آلمانی یکی از بزرگترین فلاسفهٔ اروپا و فیلسوف پرنفوذ تاریخ در حوزه اخلاق، هنر، ادبیات معاصر و روانشناسی جدید است.',0);
INSERT INTO bio VALUES(3,replace(replace('آلبر کامو (فرانسوی: Albert Camus; زادهٔ ۷ نوامبر ۱۹۱۳ - درگذشتهٔ ۴ ژانویه ۱۹۶۰). نویسنده، فیلسوف و روزنامه‌نگار فرانسوی‌ بود. او یکی از فلاسفهٔ بزرگ قرن بیستم و از جمله نویسندگان مشهور و خالق کتاب بیگانه است.\r\n\r\nکامو در سال ۱۹۵۷ به خاطر «آثار مهم ادبی که به روشنی به مشکلات وجدان بشری در عصر حاضر می‌پردازد»[۴] برندهٔ جایزهٔ نوبل ادبیات شد. آلبر کامو پس از رودیارد کیپلینگ جوان‌ترین برندهٔ جایزهٔ نوبل و همچنین نخستین نویسندهٔ زادهٔ قارهٔ آفریقا است که این عنوان را کسب کرده‌است.[۵] همچنین کامو در بین برندگان نوبل ادبیات، کمترین طول عمر را دارد و دو سال پس از بردن جایزهٔ نوبل در یک سانحهٔ تصادف درگذشت.\r\n\r\nبا وجود اینکه کامو یکی از متفکران مکتب اگزیستانسیالیسم شناخته می‌شود او همواره این برچسب خاص را رد می‌کرد.[۶] در مصاحبه‌ای در سال ۱۹۴۵ کامو هرگونه همراهی با مکاتب ایدئولوژیک را تکذیب می‌کند و می‌گوید: «نه، من اگزیستانسیالیست نیستم. هم سارتر و هم من همیشه متعجب بوده‌ایم که چرا نام ما را پهلوی هم می‌گذارند»[۷]','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(4,'آلبرت آینشتاین (به آلمانی: Albert Einstein) (زاده ۱۴ مارس ۱۸۷۹ - درگذشته ۱۸ آوریل ۱۹۵۵) (در آلمانی آینشتاین و در انگلیسی آینستاین[۷] و در فارسی معروف به انیشتن، انیشتین یا اینشتین) فیزیک‌دان نظری آلمانی بود. او بیشتر به خاطر نظریّه نسبیت و بویژه برای هم‌ارزی جرم و انرژی (E=mc۲) (که از معروف‌ترین روابط فیزیک بین غیرفیزیک‌دان‌هاست) شهرت دارد. علاوه بر این، او در بسط تئوری کوانتوم و مکانیک آماری سهم عمده‌ای داشت. اینشتین جایزه نوبل فیزیک را در سال ۱۹۲۱ برای خدماتش به فیزیک نظری و به خصوص به خاطر کشف قانون اثر فوتوالکتریک دریافت کرد. او به دلیل تأثیرات چشمگیرش، به عنوان یکی از بزرگ‌ترین فیزیکدانانی شناخته می‌شود که به این جهان پا گذاشته‌اند.[۸][۹] در فرهنگ عامه، نام «اینشتین» مترادف هوش زیاد و نابغه شده‌است.',0);
INSERT INTO bio VALUES(5,replace(replace('آناتول فرانس (به فرانسوی: Anatole France)‏ (۱۶ آوریل ۱۸۴۴ - ۱۲ اکتبر ۱۹۲۴) تخلص ژاک آناتول فرانسوا تیبو از نویسندگان برجسته فرانسوی است.\r\n\r\nنام اصلی او آناتول فرانسوا تیبو (به فرانسوی: François-Anatole Thibault) است. این نویسنده را پادشاه نثر فرانسه لقب داده‌اند. او در ۱۸۴۴ در پاریس متولد شد و در سال ۱۹۲۴ درگذشت. وی پسر کتاب فروشی به نام "فرانسوا" بود که نام مستعار "فرانس" را از او گرفته است. او تحصیلات خود را به طور رسمی ادامه نداد و شخصاً مطالعه می‌کرد. فرانس در سال ۱۸۷۳ یعنی در ۲۹ سالگی اشعار طلایی خود را منتشر ساخت. او در انجمن‌های ادبی پاریس رفت‌وآمد می‌کرد و به جریانات ادبی علاقه‌ی وافری داشت. فرانس تدریجاًً هم به داستان‌نویسی و نگارش آثار منثور علاقه‌مند شد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(6,replace(replace('آنتوان دو سنت‌اگزوپری (به فرانسوی: Antoine de Saint-Exupéry) ‏ (۲۹ ژوئن ۱۹۰۰ - ۳۱ ژوئیهٔ ۱۹۴۴) نویسنده و خلبان اهل فرانسه بود.\r\n\r\nپس از تسلیم کشور فرانسه به آلمان، اگزوپری به آمریکا مسافرت کرد و کتابهای زیبایی چون «قلعه»، «شازده کوچولو» و «خلبان جنگی» را در این دوران منتشر کرد. او کتابی درباره تجربه جنگی خودش بنام "خلبان جنگ" Pilote de guerre منتشر کرد که گزارشی بود از ناامیدی‌ها و واکنشهای روحی خلبانی در برابر خطرهای مرگ و اندیشه‌ها و القای شهامت و پایداری. اثر کوچکی که در آمریکا نوشته شده بود به نام "نامه به یک گروگان" Letter a un otage در ۱۹۴۳ منتشر شد. حوادث این داستان در ۱۹۴۰ به هنگامی می‌گذرد که نویسنده از لیسبون، پایتخت پرتغال، پرواز می‌کند و آخرین نگاهش را به اروپای تاریک که درچنگال بمب و دیوانگی اسیر است، می‌افکند. به چشم او حتی کشورهایی چون پرتغال که ظاهراً از جنگ برکنار است، غمزده‌تر از کشورهای مورد تهدید یا ویران شده به نظر می‌آید و با آنکه مردمش آسایشی دارند، همه چیز در شبحی غم‌انگیز فرورفته‌است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(7,'تونی رابینز (به انگلیسی: Tony Robbins) با نام کامل آنتونی رابینز (به انگلیسی: Anthony Robbins) نویسنده، سخنران، مربی و بازیگر آمریکایی است. وی ۲۹ فوریه ۱۹۶۰در شمال هالیوود در کالیفرنیا به دنیا آمد. شهرت عمده او در ایران به خاطر سلسله کتاب‌هایش در مورد موفقیت می‌باشد، کتابهای چون قدرت نامحدود در سال ۱۹۸۷ و به سوی کامیابی در سال ۱۹۹۴. تونی رابینز موفق‌ترین، مشهورترین و پرطرفدارترین نویسنده و سخنران انگیزشی در جهان است. هر چند که خود اصرار دارد به جای سخنران انگیزشی، مربی منتهای کارایی نامیده‌شود. او در مدت کوتاهی به بیشترین آرزوها و رویاهای جوانی خود جامه عمل پوشانید و توانست درآمد سالیانه خود را از ۳۸ هزار دلار به بیش از یک میلیون دلار افزایش دهد.',0);
INSERT INTO bio VALUES(8,replace(replace('پل گیوم آندره ژید (به فرانسوی: Paul Guillaume André Gide) ‏ (۱۸۶۹ - ۱۹۵۱) نویسندهٔ فرانسوی و برنده جایزه ادبی نوبل در سال ۱۹۴۷ بود.\r\n\r\nآندره ژید در بیست و دو سالگی فعالیت ادبی را آغاز کرد.\r\n\r\nدوستی با «استفان مالارمه» باعث روی آوردن به مکتب «نمادگرایی» و پدید آوردن آثاری مثل «یادداشت‌های روزانه آندره والتر»، «شعرهای آندره والتر»، «رسالهٔ نرگس» و «سفر اورین» شد. اما پس از مدتی از این مکتب روی گرداند و به تجزیه و تحلیل و تامل در پیچیدگی‌های زندگی درونی انسان پرداخت.\r\n\r\nسفر ژید به آفریقا باعث تغییرات بسیاری در وی شد. آثاری هم چون «مائده‌های زمینی»، «ضد اخلاق»، «در تنگ» و «دخمه‌های واتیکان» متأثر از این تغییرات است. مائده‌های زمینی کتابی است در ستایش شادی، شوق به زندگی و غنیمت شمردن لحظات. آندره ژید در این کتاب خداوند را در همه موجودات هستی متجلی می‌بیند و آزدانه و برخلاف قید و بندهای مذهب، عشق به هستی را مترادف عشق به خداوند می‌داند. او کتابش را «ستایشی از وارستگی» می‌نامد.\r\nتمام کتاب‌های آندره ژید در لیست کتاب‌های ممنوعه کلیسای کاتولیک قرار دارد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(9,replace(replace('آندره موروا (Andre Maurios)نویسنده و بیوگرافی‌نویس بزرگ فرانسوی اصلاً یهودی است و نام اصلی او امیل هرزوگ (Emile Herzog) است. در سال 1885 در شهر البوف متولد شد. وی پس از اتمام تحصیلاتش در البوف و سپس در شهرهای روان و کان، مسئولیت نظارت بر کارخانه نساجی خانواده‌اش را به مدت ده سال به عهده گرفت.\r\n\r\nدر جنگ جهانی اول از طرف ارتش فرانسه به سمت رابط و مترجم بین دسته‌های نیروی فرانسه و انگلیس تعیین شد. بدین‌جهت با روحیات انگلیس‌ها از نزدیک آشنایی یافت و این آشنایی ایجاد علاقه‌ای کرد که در طول سی‌ سال آثار ادبی و تاریخی و تحقیقی متعددی درباره انگلستان بنویسد. بهترین این آثار دو رمان : خاموشیهای سرهنگ برامبل Les Silences du Colonel Bramble (1918)، مذاکرات دکتر اُ. گریدی Les Discours du Docterur O’Grady (1921)- توصیفهایی طنزآمیر و سرگرم کننده از یاران زمان جنگ که موروا را به مردم شناساند','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(10,'سِر ایزاک نیوتن (به انگلیسی: Sir Isaac Newton) (زاده ۲۵ دسامبر ۱۶۴۲ – درگذشته ۲۰ مارس ۱۷۲۷) فیزیک‌دان، ریاضی‌دان، ستاره‌شناس، فیلسوف و شهروند انگلستان بوده‌است.∗ وی در سال ۱۶۸۷ میلادی شاهکار خود «اصول ریاضی فلسفهٔ طبیعی» را به نگارش درآورد. در این کتاب، او مفهوم گرانش عمومی را مطرح ساخت و با تشریح قوانین حرکت اجسام، علم مکانیک کلاسیک را پایه گذاشت. از دیگر کارهای مهم او بنیان‌گذاری حساب دیفرانسیل و انتگرال است.',0);
INSERT INTO bio VALUES(11,replace(replace('حکیم ارد بزرگ نابغه عرصه فلسفه و حکمت ایرانی با شهرت جهانی است. او مشهورترین "فیلسوف ایرانی" است فیلسوفی که با سخنان قصارش محبوب جهانیان شده است هیچ فیلسوفی در تاریخ ایران به اندازه او سخنانش مورد استقبال عام و خاص نشده است .\r\n- زادگاه حکیم ارد بزرگ شهر مشهد است خانواده حکیم در محله عشرت آباد این شهر زندگی می کردند.\r\nاین فیلسوف و نابغه بزرگ توانست فلسفه مرده و مهجور را با اندیشه های تازه خود جانی دوباره بخشد. کمپین دیوار مهربانی بر اساس سخنان حکیم ارد بزرگ شکل گرفت سخنانی که در سال 1390 گفته شد اما در سال 1394 در سراسر ایران موجی گشت که جوانان خیرخواه و نیکوکار کشورمان بر آن اساس بر دیوارها نقش دیوار مهربانی کشیدند و پوشاک نیازمندان را برایشان مهیا نمودند سخنان حکیم ارد بزرگ در سال 1390 را با هم بخوانیم :\r\n\r\n(( دیوار های سرزمینم ایران، باید رنگ مهر و مهربانی بر خود بگیرند.\r\nایرانیان همواره در جهان پرچمدار مهر و مهربانی بوده اند... آنهایی که مهر ورزیده اند، جاودانه شده اند... و چه زیباست فصل رویش مهر و دوستی... دیوارهای ایران شهرمان را با مهر پرشکوفه کنیم و آنچه داریم را بر آن بیاویزیم و به هم ببخشیم ... این گام کوچک امروز ما ، یادگاری باشکوه خواهد بود برای فرزندانمان و دودمانهای پسین، از مردم مهرورز زمانه ما ... همدیگر را عاشقانه دوست بداریم که سفر زندگی کوتاه است ... ))\r\n','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(12,'ابوعلی حسین بن عبدالله بن سینا، مشهور به ابوعلیِ سینا و ابن سینا و پور سینا (زادهٔ ۳۵۹ ه‍. خ. در بخارا –درگذشتهٔ ۲ تیر ۴۱۶ در همدان، ۹۸۰–۱۰۳۷ میلادی)،پزشک و شاعر ایرانی و از مشهورترین و تاثیرگذارترینِ فیلسوفان و دانشمندان ایران‌زمین است که به ویژه به دلیل آثارش در زمینه فلسفه ارسطویی و پزشکی اهمیت دارد. وی نویسنده کتاب شفا یک دانشنامه علمی و فلسفی جامع است و القانون فی الطب یکی از معروف‌ترین آثار تاریخ پزشکی است.',0);
INSERT INTO bio VALUES(13,'احمد شاملو (۲۱ آذر ۱۳۰۴ - ۲ مرداد ۱۳۷۹) متخلص به الف. بامداد یا الف. صبح، شاعر، نویسنده، روزنامه‌نگار، پژوهشگر، مترجم، فرهنگ‌نویس ایرانی و از بنیان‌گذاران و دبیران کانون نویسندگان ایران در پیش و پس از انقلاب بود. شاملو تحصیلات کلاسیک نامرتبی داشت؛ زیرا پدرش افسر ارتش بود و پیوسته از این شهر به آن شهر اعزام می‌شد و از همین روی، خانواده اش هرگز نتوانستند برای مدتی طولانی جایی ماندگار شوند. زندانی شدنش در سال ۱۳۲۲ به سبب فعالیت‌های سیاسی، پایانِ همان تحصیلات نامرتب را رقم می‌زند.شهرت اصلی شاملو به خاطر نوآوری در شعر معاصر فارسی و سرودن گونه‌ای شعر است که با نام شعر سپید یا شعر شاملویییا شعر منثورشناخته می‌شود. شاملو که هر شاعر آرمانگرا را در نهایت امر یک آنارشیست تام و تمام می‌انگاشت، در سال ۱۳۲۵ با نیما یوشیج ملاقات کرد و تحت تأثیر او به شعر نیمایی روی آورد.',0);
INSERT INTO bio VALUES(14,'اَرَسطو (به یونانی: Αριστοτέλης، تلفظ: آریستوتِلِس) (زادهٔ ۳۸۴ ق م. -درگذشتهٔ ۳۲۲ ق م)[۱] از فیلسوفان یونان باستان بود. او یکی از مهم‌ترین فیلسوفان غربی به حساب می‌آید. او در سن هیجده سالگی به آکادمی افلاطون راه یافت و به مدت بیست سال در مکتب افلاطون کسب دانش کرد و آموزگار اسکندر مقدونی بود. تالیفات او در زمینه‌ها و رشته‌های گوناکون من‌جمله فیزیک، متافیزیک، شعر، زیست‌شناسی، منطق، علم بیان، سیاست، دولت و اخلاق بوده‌اند. ارسطو به همراه سقراط و افلاطون از تأثیر گذارترین و بزرگترین فیلسوفان یونان باستان بوده‌است. این سه تن فلسفه غربی (آنطور که برای ما آشنا است) را بر اساس فلسفه ماقبل سقراط یونان بنیاد نمودند. ارسطو فلسفه را به‌عنوان «دانش هستی» تعریف می‌کرد',0);
INSERT INTO bio VALUES(15,replace(replace('ارنِست میلر هِمینگوی (زاده ۲۱ ژوئیه ۱۸۹۹ درگذشت ۲ ژوئیه ۱۹۶۱) (به انگلیسی: Ernest Miller Hemingway) از نویسندگان برجستهٔ معاصر ایالات متحده آمریکا و برندهٔ جایزه نوبل ادبیات است. وی از پایه‌گذاران یکی از تأثیرگذارترین انواع ادبی، موسوم به «وقایع‌نگاری ادبی» شناخته می‌شود\r\nسبک ویژهٔ او در نوشتن او را نویسنده‌ای بی‌همتا و بسیار تأثیرگذار کرده بود. در سال ۱۹۲۵ نخستین رشته داستان‌های کوتاهش، در زمانهٔ ما، منتشر شد که به خوبی گویای سبک خاص او بود. خاطراتش از آن دوران که پس از مرگ او در سال ۱۹۶۴ با عنوان «عید متغیر» انتشار یافت، این کتاب برداشتی شخصی و بی‌نظیر از نویسندگان، هنرمندان، فرهنگ و شیوه زندگی در پاریس دههٔ ۱۹۲۰ است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(16,replace(replace('\r\nاریک نیوتن یک روزنامه نگار آمریکایی، رئیس نوآوری در مدرسه والتر کرونکایت دانشگاه ایالتی آریزونا روزنامه نگاری و ارتباطات جمعی و یک مشاور برای جان و جیمز L. بنیاد نایت، یک سازمان ایجاد شده توسط یکی از خانواده های موسس روزنامه نایت ریدر است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(17,replace(replace('جانسون زمینه ای در پزشکی دارد. او از دانشگاه کالیفرنیای جنوبی مدرک لیسانس روانشناسی و از کالج رویال سرجنز مدرک دکترای پزشکی گرفت. جانسون مدتی مدیر پزشکی ارتباطات در موسسه مدترونیک بود که دستگاه تنظیم کننده ضربان قلب را اختراع کرد. جانسون مشاور دانشکده پزشکی کالیفرنیای جنوبی است.\r\n\r\nجانسون به کمک کنت بلنچارد کتاب پر فروش «مدیر یک دقیقه ای» را به رشته تحریر در آورد. او کتابهای متعددی در زمینه یک دقیقه ای به رشته تحریر در آورده است. او در ضمن خالق سری کتابهای «ولیو تیلز» برای بچه هاست که براساس زندگی بزرگان نوشته می شود. جانسون حکایت «چه کسی پنیر مرا جا به جا کرد؟» را از 20 سال قبل برای همگان بازگو می کرد، اما به اصرار کنت بلنچارد ان را تبدیل به یک کتاب نمود. این کتاب تاکنون 12 میلیون نسخه فروش کرده است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(18,'استیو چندلر نویسنده پرفروش 100 روش زندگى خود را متحول كنيد. او یک سخنران مشهوری است که سخنرانی های  انگیزشی در سراسر دنیا برگزار میکند',0);
INSERT INTO bio VALUES(19,'زندگی نامه ای در دسترس نیست',0);
INSERT INTO bio VALUES(20,replace(replace('اسکار فینگل اُ. فِلاهرتی ویلز وایلد (به انگلیسی: Oscar Fingal O''Flahertie Wills Wilde) که به نام هنری اسکار وایلد معروف است، (۱۶ اکتبر ۱۸۵۴ تا ۳۰ نوامبر ۱۹۰۰)، شاعر، داستان‌نویس، نمایشنامه‌نویس و نویسنده داستان‌های کوتاه ایرلندی بود.\r\n','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(21,replace(replace('\r\nاصلاح‌طلب اجتماعی انگلیسی \r\nتاریخ تولد - وفات: 1812-1904\r\n\r\nسیموئل اسمایلز ، نویسندۀ اخلاق‌گرای اسکاتلندی، در 23 دسامبر 1812 در هدینگتون به دنیا آمد. او در اصل پزشک بود، ولی مطب خود را تعطیل کرد تا سردبیری نشریۀ لیدز تایمز را بر عهده بگیرد. وی از 1845 تا 1860 منشی شرکت‌های مختلف خط‌آهن بود. برخی از نوشته‌های او شهرت بسیار یافت.\r\n\r\nسیموئل اسمایلز در 16 آوریل 1904 از دنیا رفت.\r\nآثار: شخصیت، به خود کمک کن!، راه رسیدن به موفقیت از طریق قدرت خویش، کمک به خود\r\n','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(22,replace(replace('راجنیش چاندرا موهان جین (به هندی: रजनीश चन्द्र मोहन जैन) از عرفای هندی و اساتید باطنی است که بین سالهای ۱۹۳۱ تا ۱۹۹۰ می‌زیست. وی در دهه ۶۰ میلادی به نام آچاریا راجنیش و در دهه ۷۰ و ۸۰ باگوان شری راجنیش الگو:به انگلیسی:bhagwan shree rajneesh نامیده می‌شد اما نهایتاً در سال ۱۹۸۹ میلادی نام اشو را برای خود برگزید. او یک عارف، گورو و فیلسوف هندی بود.\r\n\r\nاشو نیز مانند اکثر اساتید باطنی تاکنون هیچ کتابی ننوشته است. در واقع، کتابهای منتشر شده به نام او که شمار آنها به بیش از ۶۰۰ عنوان می‌رسد نسخه برداری از سخنرانی‌های وی هستند که توسط شاگردانش به انجام رسیده‌اند. حدود هفت هزار سخنرانی از اشو بر روی نوار کاست و هزار و هفتصد سخنرانی بر روی نوار ویدئو ضبط شده است و کتابهای او جزو پرفروش‌ترین کتب در هند به شمار می‌آیند.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(23,replace(replace('آریستوکلس ملقب به افلاطون یا پلاتون (به یونانی باستان: Πλάτων،‏ با تلفظ: Plátōn) ‏(۴۲۸/۴۲۷ پ. م. تا ۳۴۸/۳۴۷ پ. م) دوّمین فیلسوف از فیلسوفان بزرگ سه‌گانه یونانی (سقراط، افلاطون و ارسطو) است. افلاطون نخستین فیلسوفی است که آثار مکتوب از او به جا مانده است. همچنین بسیاری او را بزرگ‍‎ترین فیلسوف تاریخ می‌دانند.[۱]\r\n\r\nافلاطون در سال ۳۸۷ پیش از میلاد "مدرسه علوم، فلسفه و ادبيات" خود را بنام "آکادمی" را در مکانی به همین نام بنا کرد تا به طور فراگیر به آموزش خردورزی و پژوهش در این زمینه بپردازد. ارسطو به مدت ۲۰ سال شاگرد آکادمی و پس از آن استاد بود. در آکادمی، رشته‌های گوناگونی شامل ستاره‌شناسی، زیست‌شناسی، هندسه، اخلاق و کلام آموزانده می‌شد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(24,'الکساندر پوپ شاعر و منتقد انگلیسی(به انگلیسی: Alexander Pope) (زاده ۲۱ مه ۱۶۸۸ - درگذشته ۳۰ مه ۱۷۴۴) از شاعران نامدار سدهٔ هجده بریتانیا بود. شهرت او بیشتر به خاطر اشعار طنزش و ترجمه اثر هومر است. الکساندر پوپ پس از شکسپیر و تنیسون سومین نویسنده‌ای است که فرهنگ گفتاوردهای آکسفورد از او نقل قول کرده‌است. پوپ به خاطر دوبیتی‌هایش که مضمون قهرمانی دارند معروفیت دارد.',0);
INSERT INTO bio VALUES(25,'علی بن ابی‌طالب (به عربی: علیّ بن أبی طالب؛ زادهٔ ۱۳ رجب ۳۰ عام‌الفیل برابر با ۲۳ قبل از هجرت – درگذشتهٔ ۲۱ رمضان ۴۰ قمری) امام اول تمامی شاخه‌های مذهب شیعه، خلیفهٔ چهارم از خلفای راشدین اهل سنت، و پسرعمو و داماد محمد، پیامبر اسلام، است. او فرزند ابوطالب و فاطمه بنت اسد، همسر فاطمهٔ زهرا، و پدر حسن، حسین، و زینب است. وی از سال ۳۵ تا ۴۰ هجری، کمتر از پنج سال، به‌عنوان خلیفه بر خلافت اسلامی جز شام حکومت کرد. او در میان طیف وسیعی از مسلمانان از جایگاه برجسته‌ای برخوردار است.',0);
INSERT INTO bio VALUES(26,'ابومحمد علی بن حسین ملقب و مشهور به سجاد و زین‌العابدین چهارمین امام شیعیان بعد از پدرش حسین ، عمویش حسن و پدر بزرگش علی بن ابی‌طالب است. او در مدینه به دنیا آمد و در جوانی پدرش را در صحرای کربلا همراهی می کرد، اما به دلیل بیماری در نبرد کربلا شرکت نکرد. پس از کشته شدن پدر و خویشانش به همراه اسرا به کوفه و سپس دمشق منتقل شد اما بعدها اجازه پیدا کرد تا به مدینه برگشته در آنجا انزوا اختیار کرده با عده محدودی از اصحابش رابطه داشت. زان پس زندگی زین العابدین به عبادت گذشت و تعلیماتش بیشتر شکل دعا و نیایش داشت.',0);
INSERT INTO bio VALUES(27,'ابوجعفر، محمد بن علی الباقر، مشهور به محمد باقر پنجمین اما شیعیان بعد از پدرش سجاد و قبل از پسرش جعفر صادق می باشد. کنیه‌اش ابوجعفر و لقبش باقرالعلوم و زادهٔ سوم صفر یا نخست رجب سال ۵۷ هجری در مدینه می باشد. مادرش فاطمه بنت حسن بن علی دختر حسن بن علی می باشد و بنابراین او اولین امام شیعه است که از نسل هر دو نوه ی پیامبر حسن و حسین است. روایات و سخنان حکیمانه ی فراوانی از محمد باقر نقل شده است. او به عنوان رهبر دینی مورد احترام شیعیان می باشد و به عنوان یک فقیه و عالم بزرگ اسلامی مورد احترام اهل سنت است',0);
INSERT INTO bio VALUES(28,'موسی بن جعفر الکاظم، یا موسی کاظم ملقب به ابو ابراهیم، ابوالحسن و ابو عبدالله هفتمین امام شیعیان دوازده امامی بعد از پدرش جعفر صادق می باشد. موسی کاظم در روز ۷ صفر سال ۱۲۸ ه. ق. در ابواء (منطقه‌ای در میان مکه و مدینه) به دنیا آمد. مادرش برده ای آزاد شده به نام حمیده بود که نامهای دیگری از قبیل حمیده بربریه و حمیده اندلسیه نیز برای او نقل شده‌است. اهل سنت به عنوان یک عالم دینی به کاظم احترام می گذارند. زندگی کاظم همزمان با زندگی خلفای عباسی منصور، هادی، مهدی و هارون‌الرشید بود. موسی کاظم چندین بار به زندان افتاد و عاقبت در زندان سندی بن شابک در بغداد از دنیا رفت.',0);
INSERT INTO bio VALUES(29,'نویسنده کتاب راز شاد زیستن ',0);
INSERT INTO bio VALUES(30,replace(replace('اُنوره دو بالزاک (به فرانسوی: Honoré de Balzac) ‏ (زادهٔ ۲۰ مه ۱۷۹۹ - درگذشتهٔ ۱۸ اوت ۱۸۵۰) نویسندهٔ نامدار فرانسوی است که او را پیشوای مکتب رئالیسم اجتماعی در ادبیات می‌دانند.\r\n\r\n«کمدی انسانی» نامی است که بالزاک برای مجموعه آثار خود که حدود ۹۰ رمان و داستان کوتاه را دربرمی‌گیرد برگزیده‌است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(31,'ایمانوئل کانت (به آلمانی: Immanuel Kant) (زاده ۲۲ آوریل ۱۷۲۴ - درگذشتهٔ ۱۲ فوریه ۱۸۰۴) چهره محوری در فلسفه جدید است. وی تجربه گرایی و عقل گرایی ابتدایی مدرن را در هم آمیخت، و تا به امروز تأثیر مهم وی در مابعدالطبیعه، معرفت‌شناسی، اخلاق، فلسفه سیاسی، زیبایی شناسی و دیگر حوزه‌ها ادامه دارد. ایده بنیادین «فلسفه نقدی» کانت به ویژه در نقدهای سه‌گانه وی «نقد عقل محض»، «نقد عقل عملی»، و «نقد قوه حکم»، «خودآیینی انسان» است.',0);
INSERT INTO bio VALUES(32,'باربارا دی آنجلیس (به انگلیسی: Barbara De Angelis) روان‌شناس، مشاور روابط، سخنران و نویسنده در زمینه رشد شخصی است. آثار باربار از پرفروش‌ترین کتاب‌های روانشناسی است که بیش از هشت میلیون نسخه از آن فروخته شده‌است و در سراسر جهان به بیشتر از بیست زبان به چاپ رسیده‌است. اولین کتاب او، "چگونه می‌توان عشق ورزید؟ " یکی از پرفروشترین کتاب‌های زمان خود شد؛ و برنده جایزه Infomercial به عنوان بهترین کتاب سال ۱۹۹۴ انتخاب شد.',0);
INSERT INTO bio VALUES(33,'جورج گوردون بایرون (به انگلیسی: George Gordon Byron) معروف به لرد بایرون (۱۸۲۴ - ۱۷۸۸) یکی از بزرگ‌ترین شاعران کشور انگستان بود. مشهورترین اثر وی شعر روایی بلندی به نام دون ژوان است که با مرگ وی ناتمام ماند. از دیگر آثار مهم بایرون می توان به شعر بلند سفر زیارتی چایلد هارولد و نمایشنامه ی خوانشی مانفرد اشاره کرد.',0);
INSERT INTO bio VALUES(34,'رایان تریسی (به انگلیسی: Brian Tracy) (زادهٔ ۱۹۴۴ در شارلوت‌تاون کانادا) از نویسندگان کتابهای خودیاری است که بسیاری از کارهای خود را به صورت کتاب گویا ارائه داده. موضوع سمینارها و ارائه‌های او شامل رهبری، فروش، مدیریت موثر و استراتژی کسب و کار است.',0);
INSERT INTO bio VALUES(35,'برتراند آرتور ویلیام راسل، سومین ارل راسل (به انگلیسی: Bertrand Arthur William Russell, 3rd Earl Russell)، (زادهٔ ۱۸ مه ۱۸۷۲ — درگذشتهٔ ۲ فوریه ۱۹۷۰)، فیلسوف، منطق‌دان، ریاضی‌دان، مورخ، جامعه‌شناس و فعّال صلح‌طلب بریتانیایی بود که در قرن بیستم می‌زیست.',0);
INSERT INTO bio VALUES(36,'بقراط دوم نام بزرگ‌ترین حکیم یونانی است که در جزیره خیوس به‌سال ۴۶۰ (پیش از میلاد) زاده شد و در شهر لاریسا به‌سال ۳۷۵ (پیش از میلاد) درگذشت. وی هم‌زمان با افلاطون می‌زیست. نام پدرش هراکلیدس و نام مادرش فنارته بود. بقراط بعد از آموختن علوم در مکتب پدر، نزد دیمقراطیس (دموکریت)، فیلسوف بزرگ به‌تحصیل پرداخت و مدتی به‌عنوان پزشک دوره‌گرد در سرزمین یونان و آسیای صغیر به سیاحت و طبابت پرداخت. ',0);
INSERT INTO bio VALUES(37,'بلِز پاسکال (به فرانسوی: Blaise Pascal) ‏ (زادهٔ ۱۹ ژوئن ۱۶۲۳ - درگذشتهٔ ۱۹ اوت ۱۶۶۲) ریاضی‌دان، فیزیک‌دان و فیلسوف فرانسوی که در سال‌های پایانی عمر کوتاهش به الهیات روی آورد.',0);
INSERT INTO bio VALUES(38,'امین فرانکلین (به انگلیسی: Benjamin Franklin) (متولد ۱۷ ژانویه ۱۷۰۶، درگذشته ۱۷ آوریل ۱۷۹۰) یکی از پدران بنیانگذار ایالات متحده است. فرانکلین یک دانشمند، نویسنده برجسته و چاپخانه‌دار، طنزنویس، نظریه‌پرداز سیاسی، سیاستمدار، رئیس پست، مخترع، فیزیکدان، فعال مدنی و دیپلمات بود. به عنوان یک دانشمند، او یکی از چهره‌های بزرگ در روشنگری آمریکا و تاریخ فیزیک برای کشف‌هایی که کرده بود و نظریه‌هایش در مورد برق است. میله برق گیر، عینک دو کانونی، اجاق گاز فرانکلین، کیلومتر شمار اتومبیل، و شیشه آرمونیکا از اختراعات او هستند. همچنین او نخستین کتابخانه عمومی در آمریکا و نخستین ایستگاه آتش‌نشانی پنسیلوانیا را ایجاد کرد.',0);
INSERT INTO bio VALUES(39,replace(replace('بودا لقبی است که در آیین بودایی به هر کسی که به «روشنی» (بودهی) رسیده باشد اطلاق می‌شود گرچه از آن بیشتر برای اشاره به سیذارتا گوتاما بودا، بنیانگذار آیین بودا استفاده می‌شود. واژه بودا یعنی بیدار شده یا به عبارت دیگر، کسی که به روشنی رسیده‌است.\r\n\r\nدر سال ۵۶۳ قبل از میلاد مسیح در یک شب مهتابی در وایساکها در سرزمین پادشاهی کاپیلاواستو شاهزاده سادهودانا و همسرش ماهامایا در بیشه سلطنتی لومبینی در زیر درخت Sal صاحب پسری شدند. در روز پنجم تولد نوزاد سیذارتا نامیده شد و در روز هفتم عمر مادرش به پایان رسید','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(40,'ویلیام هنری «بیل» گیتس سوم (به انگلیسی: William Henry "Bill" Gates III) سرشناس به بیل گیتس در ۲۸ اکتبر ۱۹۵۵ میلادی در سیاتل، مرکز ایالت واشنگتن آمریکا زاده شد. او و پل آلن شرکت مایکروسافت را پایه ریزی نمودند. گیتس به‌عنوان مدیر عامل و مدیر معماری نرم‌افزار در شرکت مایکروسافت فعّالیّت کرده‌است و هم اکنون رئیس هیئت مدیره این شرکت است و دارای بیشترین سهام (معادل ۸٪) می‌باشد.',0);
INSERT INTO bio VALUES(41,replace(replace('پائولو کوئلیو (به پرتغالی: Paulo Coelho) (زاده ۲۴ اوت ۱۹۴۷) نویسنده معاصر برزیلی است. رمان‌های او بین مردم عامه در کشورهای مختلف دنیا پرطرفدار است\r\n\r\nچندین کتاب کوئلیو در رسانه‌ها ۱۹۹۸ تلویزیون مانشت ۵۲ قسمت سریال کوتاه بر اساس کتاب بریدا ساخت. این اقتباس توسط جیم کامارگو، سونیا موتا و آجلیکا لوپز نوشته شده و توسط والتر آوانسینی کارگردانی شد. بازیگر برزیلی کارولینا کستینگ نقش بریدا را بازی کرد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(42,'پابلو روئیز وای پیکاسو (به اسپانیایی: Pablo Ruiz y Picasso) نقاش، شاعر، طراح صحنه، پیکرتراش، گراورساز و سرامیک کار اسپانیایی و یکی از برترین و تاثیرگذارترین هنرمندان سده ۲۰ میلادی بود. او به همراه ژرژ براک، نقاش و پیکرتراش فرانسوی، سبک کوبیسم را پدید آورد. از جمله آثار مشهور او می‌توان به دوشیزگان آوینیون و گرنیکا اشاره کرد. پیکاسو بیشتر عمر خود را در فرانسه به سر برد.',0);
INSERT INTO bio VALUES(43,replace(replace('(سپتامبر 1879، اینورنس، اسکاتلند - 1 اکتبر 1951، Redcar را، انگلستان) یک بازیکن فوتبال اسکاتلندی که در سمت چپ نیم برای اینورنس خار، نیوکاسل و اسکاتلند بازی کرده است .\r\nبعدها به عنوان یک مربی سرشناس به موفقیت های چشمگیری دست یافت .','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(44,replace(replace('تئودور روزولت (به انگلیسی: Therodore Roosevelt) (مرگ ۱۹۱۹ -زاده ۱۸۵۸) بیست و ششمین رئیس جمهور ایالات متحده آمریکا از حزب جمهوری‌خواه بود.\r\n\r\nدرخشان‌ترین دستاورد روزولت در میان سایر رئیس‌جمهورهای آمریکا این بود که وی توانست اولین جایزه نوبل را برای آمریکایی‌ها به ارمغان آورد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(45,replace(replace('توماس آلوا ادیسون (انگلیسی: Thomas Alva Edison؛ ۱۱ فوریهٔ ۱۸۴۷ – ۱۸ اکتبر ۱۹۳۱) یک طراح نورپردازی، مهندس، مخترع، دانشمند، و کارآفرین اهل ایالات متحده آمریکا بود.\r\n\r\nوی همچنین برنده جوایزی همچون لژیون دونور (فرمانده) شده است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(46,'توماس جفرسون (به انگلیسی: Thomas Jefferson) یکی از متفکرین اصلی و بنیانگذاران آمریکا (برای ترویج دادن ایده‌آل‌های جمهوری خواهی در آمریکا)، نویسندهٔ اصلی اعلامیه استقلال ایالات متحده آمریکا و سومین رییس جمهور آمریکا است. توماس جفرسون از سال ۱۸۰۱ میلادی دو دوره متوالی چهار ساله رییس جمهوری آمریکا بود. این زمان مصادف است با سالهای ۱۱۷۹-۱۱۸۷ هجری خورشیدی که طی آن فتحعلی شاه قاجار پادشاه ایران بود.',0);
INSERT INTO bio VALUES(47,replace(replace('توماس کارلایل تاریخدان شهیر اسکاتلندی در سوم دسامبر 1795 به دنیا آمد .و در پنجم فوريه ي 1881 چشم از جهان فرو بست.\r\n\r\nمهم‌ترین اثر او تاریخ انقلاب کبیر فرانسه است . کارلایل در تاریخ نگاری انقلاب کرد و تالیفاتش را بدون پانوشت منتشر ساخت که امروز از آن ژورنالیستیک نویسی تاریخ نام می‌برند. و او کتابی را در شرح حال محمد پیامبر اسلام نوشته است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(48,'جان ارنست استاینبک جونیور (به انگلیسی: John Ernst Steinbeck, Jr.) (زاده ۲۷ فوریه ۱۹۰۲ - درگذشته ۲۰ دسامبر ۱۹۶۸) یکی از شناخته شده‌ترین و پرخواننده‌ترین نویسندگان قرن بیستم آمریکاست. از بهترین آثارش می‌توان به خوشه‌های خشم (۱۹۳۹) اشاره کرد.',0);
INSERT INTO bio VALUES(49,replace(replace('جان راسکین (به انگلیسی: John Ruskin) (زاده ۱۸۱۹ - درگذشته ۱۹۰۰) شاعر، فیلسوف، منتقد هنری و نویسنده انگلیسی بود.\r\n\r\nبرداشت او از هنر و معماری بسیار تأثیر پذیرفته از زیبایی‌شناسی ویکتوریایی و زیبایی‌شناسی ادواردی است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(50,replace(replace('جان ماکسول متولد ۱۹۷۴ میشیگان ، نویسنده مسیحی پروتستان و کشیش است. ماکسول به مدت ۳۰ سال به عنوان کشیش در کلیساهای ایندیانا ، اوهایو ، کالیفرنیا و فلوریدا اشتغال داشته است. وی در حال حاضر با همسرش مارگارت ساکن جنوب فلوریدا می باشد. ماکسول تا کنون بیش از ۵۰ کتاب عمدتاً در زمینه رهبری نوشته است.\r\n\r\nماکسول در سطح بین المللی به عنوان متخصص رهبری شناخته می شود و سخنرانیهای بسیاری در این زمینه در سراسر دنیا دارد .\r\n','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(51,replace(replace('جبران خلیل جبران (۶ ژانویه ۱۸۸۳ - ۱۰ آوریل ۱۹۳۱) زاده بشرّی لبنان از نویسندگان لبنانی-آمریکایی و خالق پیامبر است.\r\n\r\nاو در ششم ژانویه سال ۱۸۸۳، در خانواده‌ای مسیحی مارونی (منسوب به مارون قدیس) که به خلیل جبران شهرت داشتند، در البشری، ناحیه‌ای کوهستانی در شمال لبنان به دنیا آمد. مادرش زنی هنرمند بود که «کامله» نام داشت.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(52,replace(replace('جرج برنارد شاو (به انگلیسی: George Bernard Shaw)، (زاده : ۲۶ ژوئیه ۱۸۵۶ -درگذشته: ۲ نوامبر ۱۹۵۰)، نمایش‌نامه‌نویس سبک کُمیک، منتقد ادبی و مبلغ سوسیالیست ایرلندی‌تبار و یک گیاهخوار بود.\r\n\r\nاز او به‌عنوان توانمندترین نمایش‌نامه نویس بریتانیایی پس از شکسپیر و نافذترین رساله‌نویس پس از جاناتان سوییفت، هجونویس ایرلندی، یاد می‌کنند.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(53,' نویسنده  کتاب های متعددی مربوط به مدیریت و مدیران. ',0);
INSERT INTO bio VALUES(54,replace(replace('جواهر لعل نهرو (जवाहरलाल नेहरू) (زاده ۱۴ نوامبر ۱۸۸۹ - درگذشته ۲۷ مه ۱۹۶۴) از رهبران جنبش استقلال هند و کنگره ملی هند بود.\r\n\r\nاو به عنوان اولین نخست‌وزیر هند پس از اعلام استقلال در ۱۵ اوت ۱۹۴۷ (۲۳ مرداد ۱۳۲۶) انتخاب شد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(55,replace(replace('\r\nجرج واشینگتن (به انگلیسی: George Washington) ‏(۱۷۹۹-۱۷۳۲) که از رهبران انقلاب آمریکا و نخستین رئیس‌جمهور آمریکا بود، این سمت را از سال ۱۷۸۹ تا سال ۱۷۹۷ در دست داشت. این هشت سال مصادف است با آخرین سال‌های حکومت سلسله زندیه و به قدرت رسیدن سلسله قاجار در ایران.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(56,' جِیمز رآن»، معروف به جیم رآن(Jim Rohn)(۲۵ سپتامبرِ ۱۹۳۰م.- ۵ دسامبرِ ۲۰۰۹م.) کارآفرین، نویسنده و سخن رانِ انگیزشیِ آمریکایی. کارهایِ او تأثیرِ مستقیمی بر آفرینش و گسترشِ افرادِ دیگری که در صنعتِ رُشدِ شخصی فعّالیّت داشتند، از قبیلِ آنتونی_رابینز، مارک ویکتُر هَنسِن، برایان تریسی و جَک کَنفیلد داشت.',0);
INSERT INTO bio VALUES(57,'جیمز الن مک‌فرسون (به انگلیسی: James Alan McPherson) داستان کوتاه نویس و مقاله‌نویس آمریکایی و برنده جایزه پولیتزر برای داستان در سال ۱۹۷۸',0);
INSERT INTO bio VALUES(58,'ین وبستر (به انگلیسی: Alice Jane Chandler Webster) (زاده ۲۴ ژوئیه ۱۸۷۶ - درگذشته ۱۱ ژوئن ۱۹۱۶) بانوی رمان‌نویس و نمایشنامه‌نویس آمریکایی بود که آثار برجسته‌ای چون بابا لنگ دراز (۱۹۱۲) و دشمن عزیز (۱۹۱۵) به‌رشته تحریر درآورده‌است.',0);
INSERT INTO bio VALUES(59,replace(replace('سِر چارلز اسپنسر چاپلین (به انگلیسی: Sir Charles Spencer Chaplin) (زادهٔ ۱۶ آوریل ۱۸۸۹ - درگذشتهٔ ۲۵ دسامبر ۱۹۷۷)، معروف به چارلی چاپلین و چارلی، یکی از مشهورترین بازیگران و کارگردانان و همچنین آهنگساز برجستهٔ هالیوود و برندهٔ جایزه اسکار است. بیشتر فیلم‌های او کمدی و صامت هستند.\r\n\r\nچاپلین در زمینه‌های سینما و تئاتر ۷۷ سال (۱۸۹۹–۱۹۷۶) فعالیت کرد که شروع فعالیت‌های وی از سالن ویکتوریا انگلستان به عنوان پسربچه‌ای آغاز شد و تا سن ۷۷ سالگی ادامه یافت. وی در سال ۱۹۱۹ همراه با تعدادی از دوستان سینمایی خود اتحادیۀ سینماگران را تأسیس کرد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(60,'فیودور میخاییلوویچ داستایفسکی (روسی: Фёдор Миха́йлович Достое́вский؛ IPA: [ˈfʲodər mʲɪˈxajləvʲɪtɕ dəstɐˈjefskʲɪj] ( بشنوید)) (زادهٔ ۱۱ نوامبر (مطابق تقویم سبک قدیم: ۳۰ اکتبر) ۱۸۲۱ - درگذشتهٔ ۹ فوریه (مطابق تقویم سبک قدیم: ۲۹ ژانویه) ۱۸۸۱ میلادی) نویسندهٔ مشهور و تأثیرگذار اهل روسیه بود. ویژگی منحصر به فرد آثار وی روانکاوی و بررسی زوایای روانی شخصیت‌های داستان است. سوررئالیست‌ها مانیفست خود را بر اساس نوشته‌های داستایوسکی ارائه کرده‌اند.',0);
INSERT INTO bio VALUES(61,replace(replace('دالایی لاما (به تبتی: ཏ་ཱལའི་བླ་མ་) لقبی است که به رهبر دینی بوداییان تبت داده می‌شود. در حال حاضر ابن لقب به تنزین گیاتسو اعطا شده‌است، در سال ۱۹۳۵ متولد شد. دالایی لاما بر رهبران هر چهار مکتب بودایی برتری دارد.\r\nدالایی لاما یک لامای والا در گلوگ یا شاخه "کلاه زرد" از بودیسم تبتی می‌باشد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(62,replace(replace('دانته آلیگیری (به ایتالیایی: Dante Alighieri) (زادهٔ فلورانس در سال ۱۲۶۵ و درگذشتهٔ راونا در سپتامبر ۱۳۲۱ میلادی) شاعر اهل ایتالیا بوده‌است. وی در سیاست زادگاه خود عاملی موثر بود. نوشته‌های دانته در ادبیات ایتالیا و سراسر اروپا موثر افتاده‌است.\r\n\r\nدانته در دوران زندگی خود دست به انتشار کتاب‌های متعددی زد که معروف‌ترین آن‌ها «کمدی الهی»، «زندگانی نو»، «ضیافت»، «سلطنت»، و «آهنگ‌ها» است. کتاب «کمدی الهی» در سه بخش نگاشته شده‌است: دوزخ، برزخ و بهشت.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(63,'رنه دِکارْت (به فرانسوی: René Descartes) (زادهٔ ۳۱ مارس ۱۵۹۶ در دکارت (اندر الوآر) فرانسه — درگذشتهٔ ۱۱ فوریهٔ ۱۶۵۰ در استکهلم سوئد) ریاضی‌دان و فیلسوف فرانسوی بود.',0);
INSERT INTO bio VALUES(64,'دیپاک چوپرا (به انگلیسی: Deepak Chopra) (به هندی: दीपक चोपड़ा) (متولد ۱۹۴۶ در دهلی-نو)، نویسنده و پزشک آمریکایی هندی الاصل است.',0);
INSERT INTO bio VALUES(65,'بنجامین دیزرائیلی (به انگلیسی: Benjamin Disraeli) ‏ (۲۱ دسامبر ۱۸۰۴- ۱۹ آوریل ۱۸۸۱) سیاستمدار و رمان‌نویس بریتانیایی است. او تا به امروز تنها فرد یهودی است که به سمت نخست وزیری انگلستان رسیده است.',0);
INSERT INTO bio VALUES(66,replace(replace('دیل هاریسون کارنگی (به انگلیسی: Dale Haribson Carnegie) یک نویسنده و سخنران آمریکایی توسعه دهنده درسهایی در زمینه پیشرفت شخصی، فروشندگی و سخنرانی در جمع بود.\r\n\r\nاو در یک خانواده فقیر در میزوری به دنیا آمد. او در نوجوانی مجبور بود ساعت ۴ صبح از خواب برخیزد تا شیر گاوهای والدین‌اش را بدوشد. او در خانه‌اش در نیویورک درگذشت.\r\n\r\nیکی از شاخص‌ترین و مهمترین ایده وی در کتابهایش این بود که می‌توان اخلاق عده‌ای را با تغییر دادن رفتارمان در مقابل شان، تغییر داد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(67,'رالف والدو امرسون، (به انگلیسی: Ralph WaldoEmerson) فیلسوف و نویسنده آمریکایی در روز ۲۵ مه ۱۸۰۳ در بوستن ماساچوست زاده شد و در ۲۷ آوریل ۱۸۸۲ در کنکورد ماساچوست درگذشت. امرسون را پدیدآورنده مکتب ترانسندنتال آمریکا می‌دانند.',0);
INSERT INTO bio VALUES(68,replace(replace('رومن رولان (به فرانسوی: Romain Rolland)‏ (۲۶ ژانویه ۱۸۶۶ - ۳۰ دسامبر ۱۹۴۴) نویسنده فرانسوی بود.\r\n\r\nرومن رولان در سال ۱۹۱۰ مفتخر به دریافت نشان لژیون دونور (بالاترین مدال افتخار فرانسه) شد و در سال ۱۹۱۳ جایزه بزرگ آکادمی فرانسه را به دست آورد. در سال ۱۹۱۶ نیز جایزه ادبی نوبل را به دست آورد.\r\n\r\n    نشان لژیون دونور (بالاترین مدال افتخار فرانسه) ۱۹۱۰م.\r\n    جایزه بزرگ آکادمی فرانسه. ۱۹۱۳ م.\r\n    جایزه نوبل ادبیات به رمان جان شیفته. ۱۹۱۵ م.\r\n    مدال گوته. ۱۹۳۳م. این جایزه را از دولت نازی نپذیرفت.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(69,replace(replace('ریچارد دیوید باخ (به انگلیسی: Richard David Bach) خلبان و نویسندهٔ آمریکایی است.\r\nباخ در سال ۱۹۳۶ در اوک پارک ایلینوی به دنیا آمد. او فرزند رولند روبرت و روت هلن شاو باخ و همچنین از نوادگان یوهان سباستین باخ، آهنگساز بزرگ و مشهور آلمانی بود. او در سال ۱۹۵۵ وارد کالج لانگ بیچ استیت شد که در حال حاضر به عنوان دانشگاه ایالتی کالیفرنیا، لانگ بیچ شناخته می‌شود.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(70,replace(replace('ریچارد گری براتیگان (به انگلیسی: Richard Brautigan)‏ (زاده ۳۰ ژانویه ۱۹۳۵ - درگذشته ۲۵ اکتبر ۱۹۸۴) نویسنده و شاعر معاصر آمریکایی. از او ۹ رمان، یک مجموعه داستان و چندین دفتر شعر منتشر شده است. رمان صید قزل آلا در آمریکا اولین و شناخته شده‌ترین اثر اوست. وی عضو جنبش بیت بود. ریچارد براتیگان با تفنگ شکاری کالیبر ۴۴ خودکشی کرد.\r\n\r\n','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(71,'ریچارد تمپلار نامدار یک نویسنده بریتانیایی است که چندین کتاب در خودشناسی و خود پیشرفتی نوشته شده است. در کتاب  مسیر موفقیت ، 100  قوانین  ساده برای رسیدن به موفقیت را ذکر کرده است .',0);
INSERT INTO bio VALUES(72,'زندگی نامه ای در دسترس نیست',0);
INSERT INTO bio VALUES(73,replace(replace('زیگ زیگلار  در سال 1926 در آلاباما به دنیا آمد. او یکی از 12 فرزند خانواده اش بود. پدرش در سال 1932 فوت کرد و در این دوران کسادی با مرگ پدر خانواده اش در مشکلات مالی فرو رفتند. اما زیگ زیگلار توانست در میسی سیپی به کالج برود و بعد از آن در دانشگاه کارولینای جنوبی ثبت نام کرد. زیگ زیگلار سالهای متمادی فروشنده لوازم آشپزخانه بود. او در این کار و در شرکتی که در آن کار می کرد، به بیشترین موفقیتهای ممکن دست یافت.\r\n\r\nدر سال 1946، با جین آبرناتی ازدواج کرد و صاحب فرزندان متعدد شد. در سن 29 سالگی موسسه دیل کارنگی در شهر نیویورک او را استخدام کرد تا به کار تدریس بپردازد.   زیگ زیگلار بخش عمده موفقیت خود را مدیون خواندن کتاب «قدرت فکر مثبت» می داند که وینسنت پیل آن را به رشته تحریر در آورده است.\r\n\r\nزیگ زیگلار 24 کتاب نوشته که از جمله آنها میتوان به «نامزدی بعد از ازدواج» ، «در اوج» و «راز فروش کردن» اشاره کرد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(74,replace(replace('ژان-پل شارل ایمار سارْتْرْ (به فرانسوی: Jean-Paul Charles Aymard Sartre) (زاده ۲۱ ژوئن ۱۹۰۵ - درگذشته ۱۵ آوریل ۱۹۸۰) فیلسوف، اگزیستانسیالیست، رمان‌نویس، نمایش‌نامه‌نویس و منتقد فرانسوی بود.\r\nبطور کلی دو دوره در زندگی حرفه‌ای سارتر وجود داشت. اولین دورهٔ زندگی حرفه‌ای او دورهٔ پس از نوشتن اثر معروف‌اش، هستی و نیستی، بودو نوشتن رمان تهوع. سارتر به آزادی بنیادی انسان اعتقاد داشت و باور داشت که «انسان محکوم به آزادی است. »','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(75,replace(replace('ژان-ژاک روسو (به فرانسوی: Jean-Jacques Rousseau) (زادهٔ ۲۸ ژوئن ۱۷۱۲ - درگذشتهٔ ۲ ژوئیه ۱۷۷۸) متفکر سوئیسی، در سدهٔ هجدهم و اوج دورهٔ روشنگری اروپا می‌زیست.\r\n\r\nاندیشه‌های او در زمینه‌های سیاسی، ادبی و تربیتی، تأثیر بزرگی بر معاصران گذاشت. نقش فکری او که سال‌ها در پاریس عمر سپری کرد، به عنوان یکی از راه‌گشایان آرمان‌های انقلاب کبیر فرانسه قابل انکار نیست. اگر چه روسو، از نخستین روشنگرانی است که مفهوم حقوق بشر را بطور مشخص به کار گرفت، اما نزد او از این مفهوم تنها می‌توان به معنایی ویژه و محدود سخن به میان آورد. ','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(76,replace(replace('ژرژ برنانوس (به فرانسوی: Georges Bernanos) نویسنده قرن نوزدهم میلادی اهل فرانسه است.\r\nبرندگان جایزه فمینا','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(77,replace(replace('سعید نفیسی (۱۸ خرداد ۱۲۷۴ - ۲۳ آبان ۱۳۴۵ خورشیدی)، دانش‌پژوه، ادیب، تاریخ‌نگار، نویسنده، مترجم و شاعر ایرانی است. او جزو نسل اول استادان دانشکدهٔ تاریخ دانشگاه تهران بود.\r\nبه گفته استاد عبدالحسین زرین کوب، نفیسی شخصیتی است، چند بعدی: مورخ، محقق، ادیب، منتقد، نویسنده، مترجم، زبانشناس، روزنامه نگار. همسرش پریمرز نفیسی می­گوید: نفیسی واقعاً عاشق کتاب بود. او مصرف مفید و به جای پول را فقط در خریدن کتاب می‌دانست؛ به طوری که ضروری‌­ترین احتیاجات شخصی خود را در این راه صرف می­‌کرد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(78,replace(replace('سقراط (به یونانی: Σωκράτης) فیلسوف بزرگ یونانی اهل آتن و از تأثیرگذارترین افراد بر فلسفهٔ اسلامی و فلسفهٔ غرب بود.\r\nسقراط هیچ‌گاه زحمت نگارش اندیشه‌هایش را به خود نداد. تقریبآ هر آنچه امروزه از سقراط می‌دانیم، از طریق مشهورترین شاگردش، افلاطون به دست ما رسیده‌است. او در رساله‌های آپولوژی، کریتون و فایدون به شرح زندگی و محاکمهٔ استادش پرداخته‌است','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(79,replace(replace('لوسیوس سنکا «Lucius Annaeus Seneca» (که به سنکای جوان مشهور بود)، (۶۵ پیش از میلاد) اهل رم، فیلسوف رواقی، سیاستمدار، نمایشنامه نویس،معلم و مشاور نرون. که مجبور شد به اتهام همدستی در توطئه قتل امپراتور خودکشی کند، پدر وی سنکای بزرگ نام داشت.\r\n\r\nسنکا (نقل قول):انسان تکلیفی اخلاقی دارد، امّا این تکلیفی‌است در قبال جامعه بزرگ بشری نه صرفاً در قبال شهروندان کشور خود، دولت بازتاب طبیعت شر انسان است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(80,replace(replace('سیسرون (زاده ۱۰۶-درگذشته ۴۳ پیش از میلاد) خطیب معروف رومی بود. مارکوس تولیوس سیسرو خطیب، سیاست مدار و نویسنده رومی، متولد ۰۳٫۰۱٫۱۰۶ قبل از میلاد در «آرپی نوم»(۱) و مقتول در «فورمیا»(۲) به تاریخ ۰۷٫۱۲٫۴۳ قبل از میلاد بود.\r\n\r\nوی در آغاز جوانی ضمن سرودن شعر و ترجمه آثار یونانی، به آموختن فنون نظامی پرداخت و از هفده سالگی در جنگها شرکت جست و بعدها به مدت دو سال به سیاحت در مشرق زمین گذراند و در این سفر، فلسفه و علوم ادبی را از دانشمندان زمان فرا گرفت و در سال ۷۷ قبل از میلاد به رم بازگشت.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(81,'شری کارتر اسکات نویسنده کتاب مشهور قانون ده گانه موفقیت می باشد.',0);
INSERT INTO bio VALUES(82,replace(replace('فرانتس کافکا (به آلمانی: Franz Kafka) (زاده ۳ ژوئیه ۱۸۸۳ - درگذشته ۳ ژوئن ۱۹۲۴) یکی از بزرگ‌ترین نویسندگان آلمانی‌زبان در قرن بیستم بود. آثار کافکا در زمرهٔ تأثیرگذارترین آثار در ادبیات غرب به شمار می‌آیند.\r\nفرانتس کافکا به دوست نزدیک خود ماکس برود وصیت کرده‌بود که تمام آثار او را نخوانده بسوزاند. ماکس برود از این دستور وصیت‌نامه سرپیچی کرد و بیشتر آثار کافکا را منتشر کرد و دوست خود را به شهرت جهانی رساند. پُرآوازه‌ترین آثار کافکا، رمان کوتاه مسخ (Die Verwandlung) و رمان محاکمه و رمان ناتمام قصر هستند.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(83,'ولتر (زادهٔ ۲۱ نوامبر ۱۶۹۴-درگذشتهٔ ۳۰ مه ۱۷۷۸) از نام‌دارترین فیلسوفان و نویسندگان فرانسوی عصر روشنگری است. شهرت او به خاطر ذکاوت خارق‌العاده‌اش، مخالفت‌هایش با کلیسای کاتولیک، حمایتش از آزادی مذهب، آزادی بیان، و جدایی دین از سیاست و شجاعتش در بيان نظريات بى پرده است. تمام شور زندگى او در جست و جوى عدالت بود.ولتر در تمام سبک‌های ادبی و نوشتاری از جمله نمایشنامه، شعر، رمان، مقاله، و نوشته‌های تاریخی و علمی دست داشت. او بیش از ۲۰ هزار نامه و بیش از ۲ هزار کتاب و کتابچه نوشت.',0);
INSERT INTO bio VALUES(84,'فرانسیس بیکن (به انگلیسی: Francis Bacon) (زاده ۲۲ ژانویه ۱۵۶۱ - مرگ ۹ آوریل ۱۶۲۶) فیلسوف، سیاستمدار، دانشمند، حقوقدان و نویسنده انگلیسی بود. بسیاری وی را محور اصلی تحول فکری در قرون وسطی می‌دانند تا جایی که او را از بانیان انقلاب علمی می‌شمارند و پایان سلطه کلیسا بر تفکر را به اندیشه‌های او نسبت می‌دهند.',0);
INSERT INTO bio VALUES(85,'ابوالقاسم فردوسی توسی (زادهٔ ۳۱۹ خورشیدی، ۳۲۹ هجری قمری - درگذشتهٔ پیش از ۳۹۷ خورشیدی، ۴۱۱ هجری قمری در توس خراسان)، سخن‌سرای ایرانی و سرایندهٔ شاهنامه حماسهٔ ملی ایران است. فردوسی را بزرگ‌ترین سرایندهٔ پارسی‌گو دانسته‌اند.',0);
INSERT INTO bio VALUES(86,replace(replace('فرناندو آنتونیو نگورا د سیبرا پسوآ معروف به فرناندو پسوا (به پرتغالی: Fernando António Nogueira Pessoa) شاعر، نویسنده و همچنین مترجم و منتقد پرتغالی است. منتقدان، وی را تاثیرگذارترین نویسنده جریان‌ساز پرتغالی و از بانیان پست مدرنیسم می‌دانند.\r\nکتاب دلواپسی که پسوا بیش از بیست سال زمان برای نوشتن آن سپری کرد، سندی از اندوه هستی گرایانهٔ اوست. این کتاب به اموری چون پیدایش انسان، مفهوم زندگی و اسرار من ِ خویش می‌پردازد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(87,replace(replace('یوهان کریستوف فریدریش فون شیلر (به آلمانی: Johann Christoph Friedrich von Schiller) (زادهٔ ۱۰ نوامبر ۱۷۵۹ در مارباخ آم نکا - درگذشتهٔ ۹ مهٔ ۱۸۰۵ در وایمار) شاعر، نمایشنامه‌نویس، فیلسوف و مورخ آلمانی بود.\r\n\r\nاو مشهورترین نمایشنامه‌نویس آلمانی به‌شمار می‌رود و درکنار گوته، به‌عنوان چهرهٔ اساسیِ کلاسیک وایمار شناخته شده‌است. بسیاری از نمایشنامه‌های او به‌عنوان قطعهٔ تئاترهای استاندارد در سرزمین‌های آلمانی‌زبان مشهور شده‌اند و اشعار افسانه‌ای (Ballads) وی از محبوبترین شعرهای آلمانی‌اند.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(88,'فلورانس اسکاول شین (به انگلیسی: Florence Scovel Shinn)‏ (۲۴ سپتامبر ۱۸۷۱ - ۱۷ اکتبر ۱۹۴۰) تصویرگرکتاب و هنرمند آمریکایی بود که از میانه عمرش به فرقه تفکرنو پیوست و کتبی در ترویج دیدگاههای این فرقه نوشت.او در یک خانواده‌ی کهن "فیلادلفیایی" به دنیا آمد و سالیان بسیار در مقام نقاش و متافیزیسین و سخنران، نامی پرآوازه داشت و با خدمت بزرگ شفا بخشیدن، هزاران تن از مردمان را در گشودن گره‌ها و از بین بردن دشواری‌های زندگی شان، یاری نمود. وی سالها در نیویورک، دانش الهی را تدریس می‌کرد. افراد بی‌شماری در کلاس هایش شرکت می‌کردند و از این راه، پیام معنویت را به بخش گسترده‌ای از مردم می‌رساند.',0);
INSERT INTO bio VALUES(89,replace(replace('پیتاگوراس، فیثاغورس یا فیثاغورث (به یونانی: Πυθαγόρας) فیلسوف و ریاضیدان یونان باستان (۵۶۹–۵۰۰ پیش از میلاد) بود. او نخستین کسی بود که توانست اصول پراکنده‌ای را که ریاضیدانان نخستین عمدتاً با استقرا و آزمون و خطا کشف کرده بودند، بر پایهٔ اصول و براهین قیاسی بنا کند.\r\nفیثاغورس در جزیرهٔ ساموس، نزدیک کرانه‌های ایونی، زاده شد. او در عهد قبل از ارشمیدس، زنون و اودوکس (۵۶۹ تا ۵۰۰) پیش از میلاد می‌زیست.\r\n\r\nاو در جوانی به سفرهای زیادی رفت و این امکان را پیدا کرد تا با افکار مصریان باستان، بابلیان و مغان ایرانی آشنا شود.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(90,replace(replace('کارل راجرز (به انگلیسی: Carl Rogers) (زاده ۸ ژانویهٔ ۱۹۰۲ - مرگ ۴ فوریهٔ ۱۹۸۷)، روانشناس آمریکایی، یکی از نظریه‌پردازان معروف شخصیت و یکی از چهره‌های اصلی در رویکرد روان‌شناسی انسان‌گرایانه و اگزیستانسیالیستی در روانشناسی است.\r\n\r\nراجرز به عنوان یکی از پدرهای تحقیقات روان‌درمانی شناخته می‌شود و به دلیل تحقیقات پیشرویی که انجام داده‌است، نشانِ مشارکت علمی برجسته از سوی انجمن روانشناسی آمریکا در سال ۱۹۵۶ به او تعلق گرفت.\r\n\r\nاو موضعی پرسشگر و جرأت ورزانه به سوی قلمرو ناشناخته هم به عنوان یک فرد متخصص و و هم عادی داشت.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(91,replace(replace('کارل هاینریش مارکس (به آلمانی: Karl Heinrich Marx) (زادهٔ ۵ مه ۱۸۱۸ در تریر، پروس - درگذشتهٔ ۱۴ مارس ۱۸۸۳ در لندن، انگلستان) متفکر انقلابی، فیلسوف، جامعه‌شناس، تاریخ‌دان، اقتصاددان آلمانی و از تأثیرگذارترین اندیشمندان تمام اعصار است.\r\n\r\nاو به همراه فردریش انگلس، مانیفست کمونیست[۴] (۱۸۴۸) که مشهورترین رسالهٔ تاریخ جنبش سوسیالیستی است را منتشر کرده‌است. مارکس همچنین مؤلّف «سرمایه» مهم‌ترین کتاب این جنبش است. این آثار به‌همراه سایر تألیفات او و انگلس، بنیان و جوهرهٔ اصلی تفکّر مارکسیسم را تشکیل می‌دهد. «تاریخ همهٔ جوامع تا کنون، تاریخ مبارزهٔ طبقاتی بوده است.» از جمله مشهورترین جملات مارکس دربارهٔ تاریخ است که در خط اوّل مانیفست کمونیست خلاصه شده‌است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(92,replace(replace('کرت لِوین در سپتامبر 1890 در آلمان به دنیا آمد و در 11 فوریه 1947 در سن 57 سالگی بر اثر حمله قلبی در گذشت. او در خانواده‌ای یهودی زاده شد. در سال 1909 در دانشکده پزشکی دانشگاه فرایبرگ ثبت نام کرد امّا سپس رشته تحصیلی خود را عوض کرد و برای آموختن زیست ‌شناسی به دانشگاه مونیخ رفت. او سرانجام مدرک دکتری خود را از دانشگاه برلین اخذ کرد.\r\n\r\nلوین در سال 1921 تدریس فلسفه و روان‌شناسی را در دانشگاه برلین آغاز کرد. محبوبیت او در بین دانشجویان از یکسو و نوشته‌های متعدد او از سوی دیگر، توجه دانشگاه استنفورد را جلب کرد و در سال 1930 به عنوان استاد میهمان به آن دانشگاه دعوت شد. لوین سرانجام به تابعیت آمریکا درآمد و به تدریس در دانشگاه آیوا پرداخت. او تا سال 1944 به همکاری خود با این دانشگاه ادامه داد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(93,'کریستین بوبن، نویسنده فرانسوی زاده ۲۴ آوریل ۱۹۵۱ است. او پس از تحصیل در رشته فلسفه به نویسندگی روی آورد و تا کنون بیش از ۳۰ اثر از او منتشر شده‌است. کودکی، عشق و تنهایی دستمایه خلق آثار اوست. آثار بوبن مانند زنجیر به هم پیوسته‌اند، هر یک تصویر دیگری را روشن ساخته ودر کنار هم، تابلوی زندگی و افکار نویسنده را شکل می‌دهند. برای او تجربه‌های ساده زندگی ـ کودکی، عشق، تنهایی ـ دستمایه خلق آثاری عاشقانه‌است. بوبن بیش از آن که به مضامین و کلمات اهمیت دهد، به آوا و لحن کلام می‌پردازد؛ با این حال نوشته‌های او سرشار از اندیشه‌اند. اندیشه‌ای که از زندگی عاشقانه اش و از عشقش به زندگی سرچشمه می‌گیرد. برای بوبن نوشتن، سراییدنِ آواهاست.',0);
INSERT INTO bio VALUES(94,'جیدو کریشنامورتی (تلوگو: జిడ్డు కృష్ణ మూర్తి ۱۲ مه ۱۸۹۵ - ۱۷ فوریه، ۱۹۸۶) و یا جی کریشنامورتی (تلوگو: జే కృష్ణ మూర్తి) نویسنده و سخنران هندی در مورد موضوعات فلسفی و معنوی بود. موضوعات او عبارتند از: انقلاب روانی، طبیعت ذهن، مراقبه، روابط انسانی و ایجاد تغییرات مثبت در جامعه. او به طور مداوم بر نیاز به انقلاب در روح هر انسان اشاره کرده و تأکید می‌کرد که چنین انقلابی نمی‌تواند توسط یک ماهیت بیرونی، مذهبی، سیاسی، یا اجتماعی رخ دهد.',0);
INSERT INTO bio VALUES(95,'کنت بلانچارد هارتلی (زاده 6 مه 1939) نویسنده آمریکایی و کارشناس مدیریت است. کتاب او مدیر یک دقیقه ای (با همکاری اسپنسر جانسون) بیش از 13 میلیون نسخه فروخته شده است و به 37 زبان ترجمه شده است.',0);
INSERT INTO bio VALUES(96,'کُنفُوسیوس مشهورترین فیلسوف، نظریه‌پرداز سیاسی و معلم چینی است که در چین باستان زندگی می‌کرد وی در ۵۵۱ پیش از میلاد در ایالت کوچک لو که امروزه بخشی از شهرستان جدید شاندونگ است متولد شد و در ۴۷۹ پیش از میلاد در گذشت. والدینش، که در زمان کودکی او زندگی را بدرود گفتند، او را کونگ - کویی نامیدند. کنفوسیوس از کلمهٔ کونگ فوزی، به معنای «استاد بزرگ، کونگ» گرفته شده‌است.',0);
INSERT INTO bio VALUES(97,replace(replace('کونوسوکی ماتسوشیتا در سال 1894 در دهکده‌ای در کشور ژاپن به نام واسامورا به دنیا آمد. ماتسوشیتا فرزند کوچک خانواده ده نفری خود بود. بر پایه معیار‌های ژاپنی آغاز سده بیستم، افراد خانواده ماتسوشیتا را می‌توان خانواده مرفه به شمار آورد اما در سال 1899 به دلیل ورشکستگی پدر، اوضاع اقتصادی خانواده فروپاشید و مرحله جدیدی از زندگی برای ماتسوشیتا و خانواده اش آغاز شد.\r\n\r\nترک محل سکونت، دوری از خانواده، ترک تحصیل، پرداختن به کار سخت، مصائبی بود که ورشکستگی پدر برای او به ارمغان آورد. به دلیل فقر و بیماری و نداری، ماتسوشیتا در 27 سالگی خود، تمام اعضای خانواده خود را از دست داده بود. این سالها همراه با کونوسوکی ماتسوشیتا در سال 1894 در دهکده‌ای در کشور ژاپن به نام واسامورا به دنیا آمد. ماتسوشیتا فرزند کوچک خانواده ده نفری خود بود. بر پایه معیار‌های ژاپنی آغاز سده بیستم، افراد خانواده ماتسوشیتا را می‌توان خانواده مرفه به شمار آورد اما در سال 1899 به دلیل ورشکستگی پدر، اوضاع اقتصادی خانواده فروپاشید و مرحله جدیدی از زندگی برای ماتسوشیتا و خانواده اش آغاز شد. ترک محل سکونت، دوری از خانواده، ترک تحصیل، پرداختن به کار سخت، مصائبی بود که ورشکستگی پدر برای او به ارمغان آورد. به دلیل فقر و بیماری و نداری، ماتسوشیتا در 27 سالگی خود، تمام اعضای خانواده خود را از دست داده بود. این سالها همراه با ابتلاء وی به نوعی بیماری ریوی بود که تا پایان عمرش گریبان گیر او بود. او در سال 1917، در حالی که در شغل خود موفق بود شرکت محل کار خود را ترک کرد و تصمیم گرفت از آن به بعد برای خود کار کند.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(98,'کیم ووچونگ یا کیم وو جونگ(به کره‌ای: 김우중) (زاده ۱۹ دسامبر ۱۹۳۶) رییس سابق هیئت مدیره و موسس گروه سابق دوو درشهر تااگو کره به دنیا آمد، کیم در دوران کودکی، زمانی که یک پسربچه بود، به فروش روزنامه می‌پرداخت. یاری رساندن به خانواده‌اش درپی ربوده شدن پدرش در آن زمان برای مدت کوتاهی تقریباً تنها برعهده او بود. پدر کیم، معلم رییس جمهور اسبق کره، پارک چونگ هی بود. پارک، کیم را از نظر اعتبار و رقابت تجاری، بسیار کمک کرد.',0);
INSERT INTO bio VALUES(99,'گابریل خوزه گارسیا مارکِز (به اسپانیایی: Gabriel José García Márquez) (زادهٔ ۶ مارس ۱۹۲۷ در دهکدهٔ آرکاتاکا* درمنطقهٔ سانتامارا* در کلمبیا – درگذشته ۱۷ آوریل ۲۰۱۴) رمان‌نویس، نویسنده، روزنامه‌نگار، ناشر و فعال سیاسی کلمبیایی بود. او بین مردم کشورهای آمریکای لاتین با نام گابو یا گابیتو (برای تحبیب) مشهور بود و پس از درگیری با رییس دولت کلمبیا و تحت تعقیب قرار گرفتنش در مکزیک زندگی می‌کرد. مارکز برنده جایزه نوبل ادبیات در سال ۱۹۸۲ را بیش از سایر آثارش به خاطر رمان صد سال تنهایی چاپ ۱۹۶۷ می‌شناسند که یکی از پرفروش‌ترین کتاب‌های جهان است.',0);
INSERT INTO bio VALUES(100,'مهانداس کارامچاند گاندی (به انگلیسی: Mohandas Karamchand Gandhi؛ زاده: ۲ اکتبر ۱۸۶۹ - درگذشت: ۳۰ ژانویه ۱۹۴۸) (۱۰ مهر ۱۲۴۸ - ۹ بهمن ۱۳۲۶) رهبر سیاسی و معنوی هندی‌ها بود که ملت هند را در راه آزادی از استعمار امپراتوری بریتانیا رهبری کرد. او در طول زندگیش استفاده از هر نوع ترور و خشونت برای رسیدن به مقاصد را رد می‌کرد. فلسفهٔ بی‌خشونتی گاندی که خود نام ساتیاگراها (در سانسکریت به معنای تلاش و کوشش برای رسیدن به حقیقت؛ تحت‌اللفظی: محکم گرفتن حقیقت) روی بسیاری از جنبش‌های مقاومت بدون خشونت در سراسر جهان و تا امروز تأثیر گذارده ‌است.',0);
INSERT INTO bio VALUES(101,'گاتاها یا گاهان یا گات‌ها (اوستایی: گاثا، سانسکریت: گاثا، پهلوی: گاسان، گاهان) عبارت از ۱۷ سروده‌ای است که از دیرباز از سخنانِ زرتشت اسپنتمان دانسته می‌شده‌است و در برگیرنده پیام و آموزش‌های وی و بنیادِ مزدیسنا است و کهن‌ترین و سپنتاترین بخشِ کتابِ اوستا شمرده می‌شود.',0);
INSERT INTO bio VALUES(102,replace(replace('یوهان ولفگانگ فون گوته (به آلمانی: Johann Wolfgang von Goethe) (زادهٔ ۲۸ اوت ۱۷۴۹ در فرانکفورت – درگذشتهٔ ۲۲ مارس ۱۸۳۲ در وایمار) شاعر، ادیب، نویسنده، نقاش، محقق، انسان‌شناس، فیلسوف و سیاست‌مدار آلمانی بود. او یکی از کلیدهای اصلی ادبیات آلمانی و جنبش وایمار کلاسیک و همچنین رمانتیسیسم به شمار می‌رود. وی یکی از مردان بزرگ فرهنگی قرون ۱۸ و ۱۹ اروپا و یکی از افراد برجستهٔ ادبیات جهان محسوب می‌شود. گوته آلمانی، شکسپیر انگلیسی، هومر یونانی، و دانته ایتالیایی را چهار رکن ادبیات اروپا می‌دانند.\r\n\r\nخانه یوهان ولفگانگ فون گوته در مجموعه سنت‌گرایی وایمار قرار دارد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(103,'زندگی نامه ای در دسترس نیست',0);
INSERT INTO bio VALUES(104,'لئو(ن) نیکلایویچ تولستوی(تالستوی) (۹ سپتامبر ۱۸۲۸م. در یاسنایا پالیانا - ۲۰ نوامبر ۱۹۱۰م. در یاسنایا پالیانا)، فعال سیاسی-اجتماعی و نویسنده روسی بود. تولستوی از نویسندگان نامی تاریخ معاصر روسیه به شمار می‌آید. رمان‌های جنگ و صلح و آنا کارنینا تولستوی از بهترین‌های ادبیات داستانی جهان هستند. تولستوی در روسیه بسیار محبوب است و سکه طلای یادبود به‌احترام وی ضرب شده است. تولستوی در زمان زنده بودنش شهرت و محبوبیتی جهانی داشت اما به سادگی زندگی می‌کرد. تولستوی در روز ۲۰ نوامبر ۱۹۱۰ درگذشت و در زادگاه خویش به خاک سپرده شد.',0);
INSERT INTO bio VALUES(105,'فلیس لئوناردو بوسکالیا (به ایتالیایی: Felice Leonardo Buscaglia) استاد دانشگاه، نویسنده و سخنران آمریکایی -ایتالیایی‌تبار- است. بوسکالیا در ۳۱ مارس ۱۹۲۴ متولد شد و در ۱۱ ژوئن ۱۹۹۸ در «گلنبروک» ایالت نوادا درگذشت.',0);
INSERT INTO bio VALUES(106,'لئوناردو دی سر پیرو دا وینچی (۱۵ آوریل ۱۴۵۲ - ۲ مه ۱۵۱۹) دانشمند، نقاش، مجسمه‌ساز، معمار، موسیقی‌دان، ریاضی‌دان، مهندس، مخترع، آناتومیست، زمین‌شناس، نقشه‌کش، گیاه‌شناس و نویسنده ایتالیایی دوره رنسانس بود. نبوغ او شاید بیش از هر چیز دیگری مورد توجه بوده است. لئوناردو معمولاً به عنوان نمونهٔ بارز یک مرد رنسانس معرفی می‌شود. از او به طور گسترده بزرگترین نقاش تاریخ یاد می‌کنند. عده‌ای نیز او را با استعدادترین شخصی می‌دانند که تا کنون در این جهان زندگی کرده است.',0);
INSERT INTO bio VALUES(107,replace(replace('لائوتسه، با نام اصلی «لی اره»، معروف به «استاد کهن» یا «پیر استاد» بوده است. تا آنجایی که مدارک تاریخی نشان می‌دهد، او به احتمال زیاد، اولین فیلسوف مشهور چینی در زمان سلسله چو می‌باشد. اما برخی بر این باورند که چنین شخصی هرگز وجود نداشته است.\r\n«لائوتسه» در قرن پنجم یا ششم قبل از میلاد، «آیین تائو» را پایه‌گذاری کرد. وی از فلاسفه بزرگ چین به شمار می‌رود. لائوتسه به معنای حکیم سالخورده است. بنا به افسانه‌های چینی، نام او «وین لی» می‌باشد که در سال ۶۰۴ ق. م در ایالت «چو» متولد شده و در سال ۵۲۴ ق. م نیز وفات یافته است.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(108,replace(replace('ژان دلا برویر، نویسنده فرانسوی. زادروز: (۱۶ اوت، ۱۶۴۵ - ۱۱ مه، ۱۶۹۶)،\r\n','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(109,'زندگی نامه ای در دسترس نیست',0);
INSERT INTO bio VALUES(110,'لُقمان غلام سیاهی بود که در سرزمین سودان چشم به جهان گشود. در برخی کتب از جمله قرآن و گلستان سعدی، او را حکیم دانسته‌اند. گفتنی است که علاوه بر لقمان حکیم از شخصیت دیگری هم با نام لقمان در منابع یاد کرده اند که عمری طولانی داشته است و برخی او را همان لقمان حکیم دانسته اند، اما یقیناً این دو شخص متمایزند.همچنین برخی منابع ازوپ را با لقمان حکیم یکی دانسته‌اند.',0);
INSERT INTO bio VALUES(111,'لودویگ فان بتهوون یا لودویگ فان بیتهوفِن (به آلمانی: Ludwig van Beethoven) (غسل تعمید در ۱۷ دسامبر ۱۷۷۰ – درگذشتهٔ ۲۶ مارس ۱۸۲۷) یکی از موسیقی‌دانان برجستهٔ آلمانی بود که بیشتر زندگی خود را در وین سپری کرد. وی یکی از تأثیر گذارترین شخصیت‌های موسیقی در دوران کلاسیک و آغاز دورهٔ رمانتیسیسم بود. بتهوون، به‌عنوان موسیقی‌دان تاریخ، همیشه مورد ستایش قرار گرفته و آوازهٔ او موسیقی‌دانان، آهنگ‌سازان، و شنوندگانش را در تمام دوران تحت تأثیر عمیق قرار داده است، در ۳۰ سالگی ۱۶ سونات و ۱۳ واریاسیون نوشته بود ولی به این دو فُرمِ موسیقی، با شهامت بسیار یک فُرم سوم اضافه کرد.',0);
INSERT INTO bio VALUES(112,replace(replace('لویی پاستور (به فرانسوی: Louis Pasteur) (زاده ۱۸۲۲ - درگذشته ۱۸۹۵)، از شیمی‌دانان و زیست‌شناسان مشهور فرانسوی است.\r\nشهرت وی مدیون شناخت نقش باکتری‌ها در بروز بیماری و کشف واکسن ضد هاری می‌باشد. همچنین عمل پاستوریزه‌کردن که مأخوذ از نام اوست، از ابداعات این دانشمند نامدار است. پاستور نخستین کسی‌ است که پی به واگیردار بودن سیاه‌زخم گوسفندان برد.\r\n\r\nلویی پاستور در تاریخ (۲۷ دسامبر ۱۸۲۲ (میلادی)) در شاتو ویله نوولتان از ایالت ژورا زاده شد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(113,replace(replace('س گونجا بویاجیو معروف به مادر ترزا (به انگلیسی: Mother Teresa)(زاده ۲۶ اوت ۱۹۱۰ در اسکوپیه امپراطوری عثمانی - درگذشته ۵ سپتامبر ۱۹۹۷ ) راهبهٔ کاتولیک آلبانیایی و تأسیس کنندهٔ ماموران امور خیریه در هند بود. وی در سال ۱۹۲۸ به هندوستان رفت و به گروه "tas" درپیوست. به‌خاطر ارادتی که به "تِرِزا اهل آویلا"، راهبهٔ اسپانیایی و بنیانگذار یکی از جریانهای رهبانیتد و خدمات خیرخواهانه بود، نام ترزا را برای خود برگزید. در سال ۱۹۴۸ صومعه را ترک گفت تا پرستاری بیاموزد.\r\nمادر ترزا به خاطر خدمات انسان‌دوستانه‌اش سال ۱۹۷۹، جایزه نوبل صلح را به خود اختصاص داد و در سال ۲۰۰۳ از طرف پاپ ژان‌پل دوم آمرزیده شناخته شد.','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(114,replace(replace('مارتین لوتر کینگ جونیور (به انگلیسی: Martin Luther King, Jr.) (۱۵ ژانویه ۱۹۲۹ - ۴ آوریل ۱۹۶۸) رهبر جنبش حقوق مدنی آمریکایی‎های آفریقایی‎تبار بود.\r\nمارتین لوتر کینگ در سال ۱۹۶۳ به‌عنوان مرد سال ازسوی مجله تایم برگزیده شد و در سال ۱۹۶۴ به‌عنوان جوان‌ترین فرد جایزه صلح نوبل را دریافت کرد. اوج فعالیت‌های مبارزاتی مارتین لوتر کینگ در دههٔ ۱۹۶۰ و برای تصویب قانون حقوق مدنی بود. وی در سال ۱۹۶۳ در گردهمایی بزرگ طرفداران تساوی حقوق سیاهان که دربرابر بنای یادبود آبراهام لینکلن در واشنگتن دی‌سی برگزار شد، معروفترین سخنرانی خود را به‌نام «رویایی دارم» انجام داد که از مهم‌ترین سخنرانی‌ها در تاریخ آمریکا به‌شمار می‌آید.\r\n','\r',char(13)),'\n',char(10)),0);
INSERT INTO bio VALUES(115,'سمیوئل لنگهورن کلمنز (به انگلیسی: Samuel Langhorne Clemens) (زادۀ ۳۰ نوامبر ۱۸۳۵ - درگذشتۀ ۲۱ آوریل ۱۹۱۰) که بیشتر با نام هنری اش مارک توین (Mark Twain) شناخته می‌شود، نویسنده و طنزپرداز آمریکایی بود. او شهرت خود را مدیون رمان ماجراهای هاکلبری فین (۱۸۸۵) و ماجراهای تام سایر (۱۸۷۶) است.',0);
